(window.webpackJsonp=window.webpackJsonp||[]).push([[1588],{1611:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e03\u8282 \u7384\u9e1f",content:"<article><h2>\u7b2c\u4e09\u5341\u4e03\u8282 \u7384\u9e1f</h2><p>\u9634\u5f71\u5170\u662f\u5e7d\u5f71\u754c\u4e2d\u7684\u4e00\u79cd\u5e38\u89c1\u690d\u7269\uff0c\u82b1\u74e3\u78be\u788e\u9634\u5e72\uff0c\u6839\u830e\u7814\u78e8\u6210\u7c89\u672b\uff0c\u4ee5\u4e09\u70b9\u4e94\u6bd4\u516d\u70b9\u4e94\u7684\u6bd4\u4f8b\u6df7\u5408\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u591a\u79cd\u5e7b\u672f\u7684\u65bd\u6cd5\u6750\u6599\uff0c\u743c\u6069\u5c0f\u65f6\u5019\u5c31\u7ecf\u5e38\u548c\u73ca\u5609\u53bb\u57ce\u5916\u91c7\u96c6\u5b83\u4eec\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5b83\u8fd8\u53ef\u4ee5\u7528\u6765\u51b2\u6ce1\u5f53\u996e\u6599\uff0c\u6e05\u5e7d\u6de1\u96c5\uff0c\u9690\u7ea6\u5e26\u7740\u4e1d\u7f15\u51b0\u51b7\u7684\u751c\u9999\uff0c\u6709\u5b89\u5fc3\u5b9a\u795e\u7684\u6548\u679c\u2014\u2014\u987a\u4fbf\u8bf4\u53e5\uff0c\u73ca\u5609\u7684\u4f53\u9999\u5c31\u975e\u5e38\u8fd1\u4f3c\u9634\u5f71\u5170\u7684\u5473\u9053\uff0c\u5927\u6982\u662f\u559d\u5f97\u592a\u591a\u7684\u7f18\u6545\u3002\u95ee\u9898\u662f\u8fd9\u4e1c\u897f\u662f\u5e7d\u5f71\u754c\u7684\u7279\u4ea7\uff0c\u5728\u5176\u4ed6\u4f4d\u9762\uff0c\u5c24\u5176\u5728\u7269\u8d28\u754c\uff0c\u662f\u4e0d\u53ef\u80fd\u751f\u957f\u7684\u3002\u743c\u6069\u5f53\u5e74\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u8bfb\u4e66\uff0c\u9b54\u836f\u5b66\u662f\u57fa\u7840\u8bfe\u7a0b\uff0c\u9634\u5f71\u5170\u65e2\u7136\u662f\u5e38\u7528\u7684\u65bd\u6cd5\u6750\u6599\uff0c\u81ea\u7136\u4e5f\u4f1a\u8be6\u7ec6\u63d0\u53ca\uff0c\u8bb0\u5f97\u6559\u6750\u4e0a\u6e05\u6e05\u695a\u695a\u8bf4\u8fc7\uff0c\u8fd9\u690d\u7269\u53ea\u751f\u957f\u5728\u5e7d\u5f71\u754c\u3002\u7269\u8d28\u754c\u7684\u5deb\u5e08\u8981\u65bd\u5c55\u90a3\u51e0\u79cd\u5e7b\u672f\uff0c\u90fd\u662f\u7528\u5176\u4ed6\u65bd\u6cd5\u6750\u6599\u4ee3\u66ff\uff0c\u6548\u679c\u5c31\u4e0d\u5982\u9634\u5f71\u5170\u90a3\u4e48\u597d\u3002</p><p>\u90a3\u4e48\u2026\u2026\u8fd9\u5e7d\u6697\u5730\u57df\u91cc\uff0c\u600e\u4e48\u4f1a\u957f\u51fa\u9634\u5f71\u5170\u6765\uff0c\u96be\u9053\u81ea\u5df1\u8ba4\u9519\u4e86\u4e0d\u6210\uff1f</p><p>\u8fd9\u79cd\u53ef\u80fd\u6027\u5fae\u4e4e\u5176\u5fae\uff0c\u6bd5\u7adf\u4e5f\u662f\u5c0f\u65f6\u5019\u5929\u5929\u89c1\u7684\u4e1c\u897f\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\u5f04\u9519\u3002\u8299\u8389\u5a05\u6b64\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u540c\u6837\u786e\u8ba4\u8fd9\u5c31\u662f\u9634\u5f71\u5170\u3002\u56db\u4eba\u4e4b\u4e2d\uff0c\u51db\u548c\u838e\u73de\u514b\u538b\u6839\u6ca1\u89c1\u8fc7\uff0c\u6ca1\u6709\u53d1\u8a00\u6743\uff0c\u5269\u4e0b\u4e24\u4e2a\u90fd\u662f\u66fe\u7ecf\u5728\u5e7d\u5f71\u754c\u5f85\u8fc7\u5341\u51e0\u5e74\u7684\u4eba\uff0c\u603b\u4e0d\u4f1a\u4e00\u8d77\u770b\u8d70\u773c\u4e86\u3002</p><p>\u201c\u8fd9\u786e\u5b9e\u662f\u9634\u5f71\u5170\uff0c\u9634\u5f71\u5170\u662f\u53ea\u80fd\u751f\u957f\u5728\u5e7d\u5f71\u754c\u7684\uff0c\u201d\u743c\u6069\u548c\u8299\u8389\u5a05\u5bf9\u89c6\u4e00\u773c\uff0c\u201c\u5982\u679c\u6309\u7167\u8fd9\u4e2a\u903b\u8f91\u5f80\u4e0b\u63a8\u2026\u2026\u201d</p><p>\u90a3\u8fd9\u91cc\u5c31\u662f\u5e7d\u5f71\u754c\uff1f</p><p>\u8fd9\u4e2a\u7ed3\u8bba\u5f88\u8352\u8c2c\uff0c\u5e7d\u6697\u5730\u57df\u548c\u5e7d\u5f71\u754c\u867d\u7136\u540d\u5b57\u542c\u8d77\u6765\u5f88\u50cf\uff0c\u4e5f\u90fd\u662f\u9ed1\u4e4e\u4e4e\u4e0d\u89c1\u5929\u65e5\u7684\u5730\u65b9\uff0c\u4f46\u6839\u672c\u5c31\u662f\u4e24\u4e2a\u4e0d\u540c\u7684\u4f4d\u9762\u3002\u5982\u679c\u4ece\u5e7d\u6697\u5730\u57df\u8d70\u7740\u8d70\u7740\u5c31\u8d70\u5230\u5e7d\u5f71\u754c\uff0c\u90a3\u4ece\u5730\u7403\u6b65\u884c\u4e5f\u80fd\u5230\u706b\u661f\u4e86\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u88ab\u8fd9\u4e00\u63d0\u9192\uff0c\u743c\u6069\u518d\u770b\u56db\u5468\u666f\u8272\uff0c\u53d1\u73b0\u8fd8\u771f\u548c\u5e7d\u5f71\u754c\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u5c24\u5176\u8fd9\u80a1\u82e5\u6709\u82e5\u65e0\u7684\u7070\u8499\u96fe\u6c14\uff0c\u96be\u602a\u770b\u7740\u8fd9\u4e48\u773c\u719f\u3002</p><p>\u4e8b\u60c5\u53d8\u5f97\u8d8a\u53d1\u8be1\u5f02\u8d77\u6765\uff0c\u4e00\u65f6\u95f4\u8c01\u4e5f\u63e3\u6d4b\u4e0d\u900f\uff0c\u53ea\u80fd\u7ee7\u7eed\u524d\u884c\u3002\u968f\u7740\u9053\u8def\u7684\u6df1\u5165\uff0c\u4ed6\u4eec\u53c8\u53d1\u73b0\u4e86\u66f4\u591a\u7684\u9634\u5f71\u5170\uff0c\u4ee5\u53ca\u5176\u4ed6\u5e7d\u5f71\u754c\u7279\u6709\u7684\u690d\u7269\uff0c\u53cd\u8fc7\u6765\uff0c\u5e7d\u6697\u5730\u57df\u91cc\u5e38\u89c1\u7684\u90a3\u4e9b\u82d4\u85d3\u3001\u771f\u83cc\u3001\u8840\u679c\u6811\u5374\u6e10\u6e10\u7a00\u5c11\uff0c\u5230\u540e\u6765\u5c31\u5168\u9500\u58f0\u533f\u8ff9\u4e86\u3002\u5468\u56f4\u7a7a\u6c14\u7684\u6e29\u5ea6\u8d8a\u6765\u8d8a\u4f4e\u4e86\uff0c\u9634\u5f71\u8ff7\u96fe\u8d8a\u6765\u8d8a\u539a\u91cd\u6d53\u5bc6\uff0c\u98d8\u6d6e\u6e38\u79fb\uff0c\u4eff\u4f5b\u6709\u5f62\u6709\u8d28\uff0c\u9690\u9690\u7684\u4f4e\u6c89\u5636\u543c\u58f0\u4ece\u4e2d\u65ad\u65ad\u7eed\u7eed\u4f20\u51fa\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002</p><p>\u743c\u6069\u53d6\u51fa\u9f99\u9cde\u76fe\u73af\u7ed5\u8eab\u4fa7\uff0c\u53f3\u624b\u63e1\u7740\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u5de6\u624b\u6084\u6084\u6263\u4f4f\u4e00\u679a\u8428\u74e6\u68cb\u5b50\uff1b\u51db\u7684\u53f3\u624b\u4e5f\u4e00\u76f4\u653e\u5728\u8170\u95f4\u53e3\u888b\u4e2d\uff0c\u968f\u65f6\u51c6\u5907\u53d6\u51fa\u5b9d\u77f3\uff0c\u7709\u5fc3\u7684\u9b54\u6cd5\u9635\u5df2\u7ecf\u6e05\u6670\u6d6e\u73b0\uff0c\u95ea\u70c1\u6d41\u8f6c\uff1b\u8299\u8389\u5a05\u52c9\u5f3a\u4fdd\u6301\u7740\u9547\u5b9a\uff0c\u7d27\u968f\u5176\u540e\uff1b\u8868\u73b0\u6700\u8f7b\u677e\u7684\u53cd\u800c\u662f\u838e\u73de\u514b\uff0c\u811a\u6b65\u8f7b\u5feb\uff0c\u72ec\u81ea\u4e00\u4eba\u8d70\u5728\u6700\u524d\uff0c\u5c06\u8eab\u540e\u4e09\u4eba\u90fd\u62c9\u5f00\u4e86\u4e00\u6bb5\u8ddd\u79bb\uff0c\u53e3\u4e2d\u4f3c\u4e4e\u8fd8\u54fc\u7740\u66f2\u8c03\uff0c\u542b\u542b\u7cca\u7cca\u542c\u4e0d\u6e05\u695a\u3002</p><p>\u7136\u540e\u5979\u9661\u7136\u62ac\u624b\uff0c\u706b\u7ea2\u97ad\u5f71\u500f\u5ffd\u5377\u51fa\u3002</p><p>\u201c\u556a\u201d\u5730\u4e00\u58f0\u8f7b\u54cd\uff0c\u9b45\u9b54\u7684\u957f\u97ad\u50cf\u6bd2\u86c7\u822c\u5728\u7a7a\u6c14\u4e2d\u4e00\u4e2a\u7ffb\u8f6c\uff0c\u660e\u660e\u6ca1\u6709\u770b\u89c1\u4efb\u4f55\u76ee\u6807\uff0c\u4f46\u518d\u6536\u56de\u6765\u65f6\uff0c\u97ad\u8eab\u531d\u531d\u73af\u7ed5\uff0c\u4eff\u4f5b\u71c3\u70e7\u7684\u7194\u5ca9\u7ec6\u675f\uff0c\u5df2\u7ecf\u6346\u4f4f\u4e86\u67d0\u6837\u4e1c\u897f\u3002\u201c\u4e00\u4e2a\u5e7d\u7075\u3002\u201d\u5979\u8bf4\u3002</p><p>\u786e\u5b9e\u662f\u4e2a\u5e7d\u7075\uff0c\u900f\u660e\u7684\u8eaf\u4f53\u5728\u5965\u672f\u89c6\u89c9\u4e0b\u65e0\u6240\u9041\u5f62\uff0c\u5c16\u8033\u7ea2\u77b3\uff0c\u94f6\u53d1\u9ed1\u80a4\uff0c\u751f\u524d\u663e\u7136\u662f\u4e2a\u5353\u5c14\uff0c\u773c\u770b\u88ab\u6293\u4f4f\uff0c\u6b63\u6781\u529b\u5f20\u7259\u821e\u722a\u60f3\u8981\u9003\u8131\uff0c\u4f46\u838e\u73de\u514b\u5ff5\u4e86\u4e00\u53e5\u5492\u8bed\uff0c\u957f\u97ad\u4e0a\u7efd\u653e\u51fa\u6735\u6735\u9752\u84dd\u706b\u7130\uff0c\u8fd9\u6765\u81ea\u4e0b\u5c42\u754c\u7684\u90aa\u708e\u953b\u9b42\u70bc\u9b44\uff0c\u51f6\u623e\u65e0\u6bd4\uff0c\u5e7d\u7075\u987f\u65f6\u5c31\u8001\u5b9e\u4e0b\u6765\u3002</p><p>\u743c\u6069\u7b49\u4eba\u4e00\u8def\u8d70\u6765\uff0c\u534a\u4e2a\u4eba\u5f71\u90fd\u6ca1\u89c1\uff0c\u6b63\u81ea\u6478\u4e0d\u7740\u65b9\u5411\uff0c\u5982\u4eca\u770b\u89c1\u4e00\u4e2a\uff0c\u867d\u7136\u662f\u9b3c\u4e0d\u662f\u4eba\uff0c\u4f46\u4e5f\u65e0\u6240\u8c13\u4e86\uff0c\u53ea\u8981\u80fd\u4ea4\u6d41\u5c31\u884c\u3002\u5148\u8be2\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u539f\u6765\u5728\u5927\u7ea6\u4e94\u5343\u5e74\u524d\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u66fe\u7ecf\u6d3e\u9063\u4e00\u652f\u6570\u91cf\u8d85\u8fc7\u767e\u4eba\u7684\u63a2\u9669\u961f\u8fdb\u5165\u6b64\u5730\uff0c\u6210\u5458\u7ec4\u6210\u5305\u62ec\u6218\u58eb\u3001\u5deb\u5e08\u548c\u5973\u796d\u53f8\uff0c\u5176\u4e2d\u4e0d\u4e4f\u4e00\u6d41\u597d\u624b\uff0c\u7ed3\u679c\u662f\u5168\u519b\u8986\u706d\uff0c\u800c\u8fd9\u4e2a\u5e7d\u7075\uff0c\u5c31\u662f\u5f53\u4e8b\u4eba\u4e4b\u4e00\u3002</p><p>\u201c\u662f\u8c01\u6740\u4e86\u4f60\u4eec\uff1f\u201d\u743c\u6069\u95ee\uff0c\u8fd9\u662f\u5173\u952e\u95ee\u9898\u3002</p><p>\u201c\u4e00\u53ea\u602a\u9e1f\u3002\u201d\u5e7d\u7075\u56de\u7b54\u3002</p><p>\u201c\u602a\u9e1f\uff1f\u201d</p><p>\u201c\u5c31\u5728\u524d\u9762\u4e00\u4e2a\u5c71\u6d1e\u91cc\uff0c\u4f4f\u7740\u4e00\u53ea\u602a\u9e1f\uff0c\u201d\u5e7d\u7075\u89e3\u91ca\uff0c\u201c\u5389\u5bb3\u81f3\u6781\uff0c\u6211\u4eec\u6240\u6709\u4eba\u90fd\u662f\u88ab\u5b83\u6740\u4e86\u3002\u201d</p><p>\u636e\u5e7d\u7075\u63cf\u8ff0\uff0c\u524d\u65b9\u518d\u8d70\u5927\u7ea6\u4e24\u5c0f\u65f6\uff0c\u4fbf\u662f\u8fd9\u707e\u5384\u4e4b\u5730\u7684\u4e2d\u5fc3\uff0c\u90a3\u91cc\u6709\u4e00\u5904\u5de8\u5927\u6d1e\u7a74\uff0c\u91cc\u9762\u6816\u606f\u7740\u4e00\u53ea\u5f62\u5bb9\u602a\u5f02\u7684\u5927\u9e1f\uff0c\u5e94\u8be5\u5c31\u662f\u6b64\u5730\u7684\u201c\u9886\u4e3b\u201d\uff0c\u5e7d\u7075\u4fbf\u662f\u88ab\u5b83\u5e72\u6389\u7684\u3002\u800c\u4e14\u8fd9\u5730\u65b9\u4f3c\u4e4e\u53e6\u6709\u53e4\u602a\uff0c\u6b7b\u540e\u7075\u9b42\u90fd\u65e0\u6cd5\u9003\u8131\uff0c\u88ab\u5f3a\u5927\u7684\u529b\u91cf\u675f\u7f1a\u6b64\u5730\uff0c\u6700\u7ec8\u53d8\u6210\u5e7d\u7075\uff0c\u65e0\u6cd5\u9003\u8131\u3002</p><p>\u201c\u90a3\u53ea\u602a\u9e1f\u4ec0\u4e48\u6a21\u6837\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5e7d\u7075\u541e\u541e\u5410\u5410\u7740\uff0c\u201c\u53cd\u6b63\u957f\u5f97\u5f88\u5947\u602a\u2026\u2026\u201d</p><p>\u743c\u6069\u671d\u838e\u73de\u514b\u770b\u4e86\u4e00\u773c\uff0c\u9b45\u9b54\u624b\u8155\u4e00\u6296\uff0c\u6b63\u8981\u518d\u5ea6\u91ca\u653e\u90aa\u708e\uff0c\u5e7d\u7075\u5df2\u7ecf\u62a2\u5148\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u5176\u5b9e\u6211\u4e5f\u6ca1\u770b\u6e05\u695a\uff01\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u88ab\u5b83\u6740\u4e86\u4e48\uff0c\u600e\u4e48\u4f1a\u6ca1\u770b\u6e05\u695a\uff1f\u201d</p><p>\u5e7d\u7075\u614c\u5fd9\u89e3\u91ca\uff0c\u5f53\u65e5\u5353\u5c14\u4eec\u95ef\u8fdb\u5de2\u7a74\uff0c\u6b63\u56db\u5904\u641c\u7d22\uff0c\u5c31\u89c1\u4e00\u56e2\u706b\u5149\u81ea\u865a\u7a7a\u4e2d\u817e\u8d77\uff0c\u5f53\u4e2d\u73b0\u51fa\u4e00\u7269\u3002\u56e0\u4e3a\u5468\u8eab\u5149\u660e\u707f\u70c2\uff0c\u8000\u773c\u523a\u76ee\uff0c\u5353\u5c14\u4eec\u7684\u773c\u775b\u53c8\u6700\u754f\u60e7\u5149\u4eae\uff0c\u6240\u4ee5\u6839\u672c\u5c31\u6ca1\u770b\u6e05\u695a\u5177\u4f53\u7684\u6a21\u6837\uff0c\u53ea\u80fd\u9690\u9690\u7ea6\u7ea6\u5224\u65ad\u51fa\u662f\u4e2a\u98de\u9e1f\u4e4b\u5f62\u3002\u63a5\u7740\u4fbf\u662f\u53cc\u65b9\u5927\u6218\uff0c\u90a3\u602a\u9e1f\u51f6\u623e\u65e0\u6bd4\uff0c\u53cc\u7ffc\u8212\u5f20\uff0c\u53e3\u5410\u70c8\u7130\uff0c\u7247\u523b\u95f4\u5c31\u628a\u5353\u5c14\u4eec\u70e7\u5f97\u7070\u98de\u70df\u706d\uff0c\u534a\u4e2a\u4e0d\u5269\u3002\u5b83\u7684\u5de2\u7a74\u4f3c\u4e4e\u53e6\u6709\u9b54\u6cd5\u7981\u5236\uff0c\u5353\u5c14\u4eec\u6b7b\u540e\u5316\u4f5c\u5e7d\u7075\uff0c\u65e0\u6cd5\u9760\u8fd1\uff0c\u800c\u602a\u9e1f\u53c8\u4ece\u4e0d\u5916\u51fa\uff0c\u4ee5\u540e\u8fde\u9762\u90fd\u6ca1\u89c1\u8fc7\u4e00\u6b21\uff0c\u6240\u4ee5\u8981\u5e7d\u7075\u4ed4\u7ec6\u5f62\u5bb9\uff0c\u5b83\u5c31\u8bf4\u4e0d\u4e0a\u6765\u4e86\u3002</p><p>\u201c\u4f60\u4eec\u4e00\u767e\u591a\u4eba\uff0c\u5168\u662f\u88ab\u90a3\u53ea\u602a\u9e1f\u5e72\u6389\u7684\uff1f\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u3002</p><p>\u5e7d\u7075\u70b9\u5934\u3002</p><p>\u743c\u6069\u5012\u5438\u53e3\u6c14\uff0c\u4e00\u767e\u591a\u540d\u7cbe\u9510\u7684\u5353\u5c14\u6709\u5907\u800c\u6765\uff0c\u5c45\u7136\u88ab\u4e00\u53ea\u602a\u9e1f\u5c60\u6740\u5e72\u51c0\uff0c\u8fd9\u5b9e\u529b\u53ef\u7740\u5b9e\u60ca\u4eba\u5f97\u5f88\u3002\u4ece\u5e7d\u7075\u7684\u63cf\u8ff0\u4e0a\u770b\uff0c\u8fd9\u602a\u9e1f\u80fd\u591f\u55b7\u5410\u6740\u4f24\u529b\u6781\u5f3a\u7684\u70c8\u7130\uff0c\u53ea\u6015\u6bd4\u8d77\u7ea2\u9f99\u90fd\u4e0d\u900a\u8272\uff0c\u751a\u81f3\u72b9\u6709\u8fc7\u4e4b\u3002</p><p>\u201c\u4f60\u4eec\u6765\u8fd9\u91cc\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u5e7d\u7075\u72b9\u8c6b\u7740\uff0c\u4f3c\u4e4e\u4e0d\u592a\u60f3\u8bf4\uff0c\u4f46\u5728\u5a01\u903c\u4e4b\u4e0b\u8fd8\u662f\u8001\u5b9e\u62db\u4f9b\u51fa\u6765\u3002\u539f\u6765\u5f53\u65f6\u74dc\u7406\u5fb7\u65af\u57ce\u51fa\u4e86\u4e00\u4f4d\u53db\u5f92\uff0c\u4e43\u662f\u7b2c\u4e00\u5bb6\u65cf\u7684\u9996\u5e2d\u5927\u5deb\u5e08\uff0c\u4ed6\u5728\u57ce\u5916\u6709\u4e00\u5904\u79d8\u5bc6\u7684\u5c45\u6240\uff0c\u5c31\u662f\u8fd9\u5757\u707e\u5384\u4e4b\u5730\uff0c\u5176\u4e2d\u6709\u8bf8\u591a\u5b9d\u85cf\u3002\u540e\u6765\u9996\u5e2d\u5927\u5deb\u5e08\u5bc6\u8c0b\u53cd\u53db\uff0c\u4e8b\u8d25\u88ab\u5904\u6b7b\uff0c\u4e3b\u6bcd\u4eec\u6d3e\u9063\u58eb\u5175\u8fdb\u5165\u6b64\u5730\uff0c\u662f\u5e0c\u671b\u5bfb\u627e\u63a5\u6536\u4ed6\u7684\u9057\u4ea7\uff0c\u6ca1\u60f3\u5230\u649e\u4e0a\u90a3\u53ea\u602a\u9e1f\uff0c\u5168\u519b\u8986\u706d\uff0c\u4ece\u6b64\u4e4b\u540e\u5c31\u518d\u4e5f\u6ca1\u6709\u4eba\u6562\u518d\u5c1d\u8bd5\u4e86\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u9690\u9690\u4e00\u52a8\uff0c\u4f3c\u4e4e\u628a\u63e1\u5230\u4e86\u4ec0\u4e48\uff0c\u4f46\u4e5f\u65e0\u6687\u591a\u60f3\uff0c\u773c\u4e0b\u6700\u5173\u952e\u7684\uff0c\u662f\u627e\u5230\u8299\u857e\u72c4\u3002</p><p>\u201c\u4f60\u8fd1\u51e0\u65e5\u6709\u6ca1\u6709\u770b\u5230\u4e00\u4e2a\u5973\u5b69\u5b50\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6709\uff0c\u201d\u5e7d\u7075\u7acb\u523b\u70b9\u5934\uff0c\u201c\u524d\u5929\u770b\u89c1\u7684\u3002\u7a7f\u7740\u957f\u888d\uff0c\u50cf\u662f\u4e2a\u5deb\u5e08\uff0c\u548c\u5979\u957f\u5f97\u4e00\u6a21\u4e00\u6837\uff0c\u201d\u5b83\u671d\u7740\u8299\u8389\u5a05\u4e00\u6307\uff0c\u201c\u4e0d\u8fc7\u7ea4\u7626\u82d7\u6761\u4e9b\u3002\u201d</p><p>\u743c\u6069\u548c\u540c\u4f34\u5bf9\u89c6\u4e00\u773c\uff0c\u201c\u5979\u5f80\u54ea\u91cc\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u4ece\u8fd9\u91cc\u7ecf\u8fc7\uff0c\u5e94\u8be5\u662f\u5f80\u90a3\u602a\u9e1f\u7684\u5de2\u7a74\u91cc\u53bb\u4e86\u3002\u201d</p><p>\u4f17\u4eba\u5fc3\u4e2d\u90fd\u662f\u4e00\u60ca\uff0c\u5018\u82e5\u8fd9\u5e7d\u7075\u6240\u8a00\u5c5e\u5b9e\uff0c\u524d\u65b9\u771f\u6709\u90a3\u4ec0\u4e48\u55b7\u706b\u602a\u9e1f\u7684\u8bdd\uff0c\u8299\u857e\u72c4\u6b64\u53bb\u5c82\u4e0d\u662f\u51f6\u591a\u5409\u5c11\u3002\u8299\u8389\u5a05\u6309\u637a\u4e0d\u4f4f\uff0c\u5c31\u8981\u51b2\u8fc7\u53bb\uff0c\u88ab\u743c\u6069\u4e00\u628a\u62c9\u4f4f\u3002\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6025\u4e5f\u6ca1\u7528\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u5982\u679c\u771f\u51fa\u4e8b\uff0c\u73b0\u5728\u53bb\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002\u201d</p><p>\u201c\u4f60\u2026\u2026\u201d</p><p>\u201c\u5c31\u7b97\u8ddd\u79bb\u592a\u8fdc\uff0c\u5fc3\u7075\u611f\u5e94\u5fae\u5f31\uff0c\u4f46\u5982\u679c\u8299\u857e\u72c4\u771f\u7684\u906d\u9047\u4e0d\u6d4b\uff0c\u4f60\u603b\u8be5\u6709\u6240\u5bdf\u89c9\u5427\uff1f\u201d</p><p>\u743c\u6069\u4e00\u53e5\u8bdd\u8ba9\u8299\u8389\u5a05\u6014\u4f4f\uff0c\u968f\u5373\u795e\u60c5\u7f13\u548c\u4e0b\u6765\u3002\u8bda\u5982\u4ed6\u6240\u8a00\uff0c\u53cc\u80de\u80ce\u59d0\u59b9\u4e4b\u95f4\u7684\u5fc3\u7075\u611f\u5e94\uff0c\u867d\u7136\u4f1a\u968f\u7740\u8ddd\u79bb\u7684\u589e\u52a0\u800c\u524a\u5f31\uff0c\u65e0\u6cd5\u4f20\u9012\u6e05\u695a\u7684\u8baf\u606f\uff0c\u4f46\u5018\u82e5\u4e00\u65b9\u906d\u9047\u4e0d\u6d4b\u5267\u53d8\uff0c\u53e6\u4e00\u65b9\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u53ef\u80fd\u6ca1\u6709\u534a\u70b9\u611f\u5e94\u3002\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8299\u8389\u5a05\u8fd8\u6ca1\u63a5\u6536\u5230\u201c\u6b7b\u201d\u7684\u8baf\u606f\uff0c\u90a3\u81f3\u5c11\u8bf4\u660e\u8299\u857e\u72c4\u6682\u65f6\u8fd8\u662f\u5b89\u7136\u65e0\u6059\u7684\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u79cd\u8bf4\u6cd5\u5176\u5b9e\u4e5f\u4e0d\u65e0\u7834\u7efd\uff0c\u5fc3\u7075\u611f\u5e94\u8fd9\u4e1c\u897f\u6bd5\u7adf\u6ca1\u6709\u4e25\u8c28\u7684\u5206\u6790\u8bba\u8bc1\uff0c\u51c6\u4e0d\u51c6\u8fd8\u4e0d\u597d\u8bf4\u3002\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u743c\u6069\u6240\u8bf4\u7684\uff0c\u6b63\u662f\u8299\u8389\u5a05\u8feb\u5207\u5e0c\u671b\u542c\u5230\u7684\uff0c\u987f\u65f6\u4e5f\u5c31\u76f8\u4fe1\u4e86\uff0c\u5c31\u7b97\u5fc3\u5e95\u6709\u4e00\u70b9\u6000\u7591\uff0c\u4e5f\u88ab\u81ea\u52a8\u538b\u5236\u4e0b\u53bb\u3002</p><p>\u7ee7\u7eed\u76d8\u95ee\u5e7d\u7075\uff0c\u5b83\u5374\u4e5f\u63d0\u4f9b\u4e0d\u4e86\u66f4\u591a\u4fe1\u606f\uff0c\u53ea\u662f\u7ffb\u6765\u8986\u53bb\u628a\u90a3\u4e9b\u5df2\u7ecf\u8bf4\u8fc7\u7684\u8bdd\u53c8\u98a0\u5012\u91cd\u8ff0\u3002\u743c\u6069\u4fbf\u547d\u4ee4\u5b83\u5e26\u8def\uff0c\u524d\u5f80\u602a\u9e1f\u7684\u5de2\u7a74\u3002\u5e7d\u7075\u6218\u6218\u5162\u5162\uff0c\u4f46\u53c8\u4e0d\u6562\u8fdd\u6297\u743c\u6069\u7684\u547d\u4ee4\uff0c\u53ea\u5f97\u5728\u524d\u5f15\u8def\u3002\u8d70\u4e86\u5927\u7ea6\u4e24\u4e2a\u5c0f\u65f6\uff0c\u524d\u65b9\u51fa\u73b0\u4e00\u5904\u4e24\u4eba\u9ad8\u7684\u6d1e\u53e3\uff0c\u5e7d\u7075\u8bf4\u8fd9\u5c31\u662f\u90a3\u602a\u9e1f\u7684\u5de2\u7a74\u6240\u5728\u4e86\u3002</p><p>\u743c\u6069\u89c2\u5bdf\u7247\u523b\uff0c\u4e0d\u89c1\u6709\u4ec0\u4e48\u5f02\u6837\uff0c\u56de\u5934\u770b\u770b\u540c\u4f34\uff0c\u5c06\u5fc3\u4e00\u6a2a\uff0c\u5f53\u5148\u8e0f\u5165\u3002\u838e\u73de\u514b\u4e22\u5f00\u5e7d\u7075\u7d27\u8ddf\u5176\u540e\uff0c\u51db\u548c\u8299\u8389\u5a05\u4e5f\u968f\u4e4b\u8fdb\u6765\u3002\u6d1e\u7a74\u9887\u5927\uff0c\u7a7a\u7a7a\u8361\u8361\uff0c\u6709\u4e9b\u77f3\u684c\u77f3\u6905\uff0c\u4f46\u663e\u7136\u8352\u5e9f\u5df2\u4e45\uff0c\u6b63\u4e2d\u662f\u4e00\u5904\u796d\u575b\u6a21\u6837\u7684\u5e73\u53f0\uff0c\u4e0a\u9762\u4f3c\u4e4e\u653e\u7740\u4ec0\u4e48\u4e1c\u897f\uff0c\u9690\u9690\u900f\u7740\u4e94\u8272\u5fae\u5149\u3002\u796d\u575b\u4e0b\u65b9\u9634\u5f71\u4e2d\u8eba\u7740\u4e00\u4e2a\u4eba\uff0c\u4ece\u8eab\u5f62\u8f6e\u5ed3\u4e0a\u770b\uff0c\u6b63\u662f\u8299\u857e\u72c4\u3002\u743c\u6069\u6b63\u8981\u4e0a\u524d\u5bdf\u770b\uff0c\u521a\u521a\u8e0f\u8fd1\u4e24\u6b65\uff0c\u9661\u7136\u4e00\u4e2a\u6e05\u6717\u4e2d\u4e0d\u5931\u5a01\u4e25\u7684\u58f0\u97f3\u5728\u8111\u6d77\u4e2d\u7f13\u7f13\u54cd\u8d77\u3002</p><p>\u201c\u5c14\u3001\u7b49\u3001\u4f55\u3001\u4eba\uff1f\u201d</p><p>\u743c\u6069\u62ac\u5934\u770b\u53bb\uff0c\u5c31\u89c1\u796d\u575b\u4e0a\u5149\u534e\u7efd\u653e\uff0c\u4e00\u56e2\u718a\u718a\u706b\u7130\u4eff\u4f5b\u70c8\u65e5\u817e\u8d77\uff0c\u5f53\u4e2d\u73b0\u51fa\u4e00\u53ea\u98de\u9e1f\u6765\uff0c\u9e70\u5934\u9ad8\u51a0\uff0c\u957f\u5c3e\u66f3\u7fbd\uff0c\u53cc\u7ffc\u6781\u5bbd\uff0c\u5468\u8eab\u706b\u7ea2\uff0c\u53cc\u76ee\u4e4b\u4e2d\u91d1\u5149\u707f\u70c2\uff0c\u987e\u76fc\u95f4\u9690\u7136\u6d41\u9732\u7687\u8005\u6c14\u6d3e\uff0c\u6b63\u5c45\u9ad8\u4e34\u4e0b\u4fef\u89c6\u7740\u796d\u575b\u524d\u7684\u8bf8\u4eba\u3002</p></article>"}}}]);
