(window.webpackJsonp=window.webpackJsonp||[]).push([[1590],{1613:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u7fd4\u9f99\u4e0e\u51e4\u51f0",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u7fd4\u9f99\u4e0e\u51e4\u51f0</h2><p>\u6b63\u5982\u743c\u6069\u6240\u6599\u60f3\u7684\u90a3\u6837\uff0c\u8bbe\u4e0b\u7981\u5236\u7684\u5deb\u5e08\uff0c\u81ea\u7136\u4e5f\u4f1a\u6709\u7834\u89e3\u7684\u65b9\u6cd5\uff0c\u81f3\u5c11\u9884\u7559\u4e86\u201c\u540e\u95e8\u201d\u3002\u6309\u7167\u6b27\u51ef\u7684\u6307\u5f15\uff0c\u743c\u6069\u7b49\u4eba\u987a\u5229\u8d70\u51fa\u4e86\u707e\u5384\u4e4b\u5730\uff0c\u56de\u5230\u4f0a\u5361\u6c99\u57ce\u3002\u800c\u5728\u4e24\u5929\u4e4b\u540e\uff0c\u6b27\u51ef\u4e5f\u767b\u95e8\u62dc\u8bbf\u3002</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u6765\u5386\u7684\uff1f\u201d\u6b27\u51ef\u6709\u4e9b\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u731c\u7684\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u731c\u51fa\u6b27\u51ef\u7684\u8eab\u4efd\uff0c\u5176\u5b9e\u5e76\u4e0d\u7b97\u591a\u4e48\u96be\u7684\u4e8b\u60c5\uff0c\u79cd\u79cd\u7ebf\u7d22\u4ea4\u7ec7\u6c47\u805a\u5728\u4e00\u8d77\uff0c\u81ea\u7136\u5c31\u6307\u5411\u90a3\u4e2a\u6700\u5927\u7684\u53ef\u80fd\u6027\u3002\u743c\u6069\u624b\u4e0a\u90a3\u526f\u8428\u74e6\u68cb\u9b54\u50cf\uff0c\u6b27\u51ef\u66fe\u7ecf\u89e3\u91ca\u5176\u6765\u5386\uff0c\u5c31\u8bf4\u8fc7\u662f\u4e00\u4f4d\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u5b66\u8005\u5947\u68b0\u5e08\uff0c\u56e0\u4e3a\u548c\u6076\u9b54\u52fe\u7ed3\uff0c\u89e6\u72af\u5f8b\u6cd5\uff0c\u9003\u4ea1\u5230\u5e7d\u6697\u5730\u57df\uff0c\u88ab\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u83f2\u5c14\u4f26\u5bb6\u65cf\u6240\u63a5\u7eb3\uff0c\u5316\u540d\u68b5\u94ce\u5c14\uff0c\u4ee5\u5353\u5c14\u7684\u8eab\u4efd\u5f53\u4e0a\u4e86\u9996\u5e2d\u5927\u5deb\u5e08\uff0c\u5236\u4f5c\u4e86\u8fd9\u526f\u8428\u74e6\u68cb\u3002\u540e\u6765\u68b5\u94ce\u5c14\u88ab\u5904\u6b7b\uff0c\u8428\u74e6\u68cb\u843d\u5230\u5353\u5c14\u624b\u4e2d\uff0c\u6700\u540e\u88ab\u6b27\u51ef\u4ece\u4e9a\u5f53\u65af\u624b\u4e2d\u53d6\u5f97\uff0c\u7ed9\u4e86\u743c\u6069\u3002</p><p>\u8fd9\u526f\u8428\u74e6\u68cb\u662f\u5f3a\u529b\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u4f46\u5c31\u76ee\u524d\u6765\u770b\uff0c\u53ea\u6709\u6b27\u51ef\u548c\u743c\u6069\u4e24\u4eba\u80fd\u7528\uff0c\u5bf9\u4e8e\u5176\u4ed6\u4eba\u800c\u8a00\u5168\u90fd\u662f\u6446\u8bbe\u3002\u8fd9\u5176\u4e2d\u7684\u539f\u56e0\uff0c\u743c\u6069\u662f\u4e0d\u77e5\u9053\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u63a8\u6d4b\u51fa\u4e00\u70b9\uff1a\u6b27\u51ef\u548c\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u5173\u7cfb\u532a\u6d45\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u8fd9\u526f\u8428\u74e6\u68cb\u5728\u5353\u5c14\u90a3\u91cc\u5c18\u5c01\u51e0\u5343\u5e74\uff0c\u53c8\u6ca1\u4eba\u80fd\u7528\uff0c\u4f30\u8ba1\u65e9\u5c31\u6254\u5728\u4ed3\u5e93\u89d2\u843d\u91cc\u88ab\u9057\u5fd8\u4e86\uff0c\u6b27\u51ef\u4f55\u81f3\u4e8e\u5355\u5355\u5c31\u70b9\u540d\u8981\u5b83\uff0c\u4e16\u754c\u4e0a\u672a\u5fc5\u6709\u8fd9\u4e48\u51d1\u5de7\u7684\u4e8b\u60c5\u3002</p><p>\u4ece\u5e38\u7406\u63a8\u8bba\uff0c\u6b27\u51ef\u5176\u5b9e\u65e9\u5c31\u77e5\u9053\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u5b58\u5728\uff0c\u5e76\u4e14\u5341\u5206\u6e05\u695a\u81ea\u5df1\u80fd\u591f\u4f7f\u7528\uff0c\u5426\u5219\u4ed6\u4e0d\u4f1a\u6307\u5b9a\u8981\u4e00\u4e2a\u5e9f\u7269\u3002\u5982\u679c\u66f4\u8fdb\u4e00\u6b65\u731c\u6d4b\u7684\u8bdd\uff0c\u6b27\u51ef\u53ea\u6015\u548c\u5f53\u5e74\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u5236\u4f5c\u8005\u5927\u6709\u7275\u8fde\u3002</p><p>\u90a3\u4e48\uff0c\u7275\u8fde\u5230\u4ec0\u4e48\u7a0b\u5ea6\u5462\uff1f</p><p>\u5728\u4f0a\u5361\u6c99\u57ce\u7684\u65f6\u5019\uff0c\u77ee\u4eba\u66fe\u7ecf\u63d0\u53ca\uff0c\u4ee5\u524d\u6709\u4e00\u961f\u5353\u5c14\u8fdb\u5165\u707e\u5384\u4e4b\u5730\uff0c\u6709\u53bb\u65e0\u56de\u3002\u743c\u6069\u7b49\u4eba\u9047\u5230\u7684\u90a3\u4e2a\u5e7d\u7075\u4e5f\u8bc1\u5b9e\u4e86\u8fd9\u70b9\uff0c\u5e76\u4e14\u5f97\u77e5\u539f\u6765\u4e94\u5343\u5e74\u524d\u74dc\u7406\u5fb7\u65af\u57ce\u51fa\u4e86\u4e00\u4f4d\u80cc\u53db\u7684\u9996\u5e2d\u5927\u5deb\u5e08\uff0c\u8fd9\u5730\u65b9\u662f\u4ed6\u7684\u79d8\u5bc6\u5c45\u6240\uff0c\u5927\u5deb\u5e08\u88ab\u5904\u6b7b\uff0c\u8fd9\u4e9b\u5353\u5c14\u4eec\u5c31\u662f\u6765\u641c\u522e\u9057\u4ea7\u7684\uff0c\u7ed3\u679c\u649e\u4e0a\u51e4\u51f0\u5168\u519b\u8986\u706d\u4e86\u3002</p><p>\u5353\u5c14\u867d\u7136\u662f\u4ee5\u80cc\u53db\u8457\u79f0\u7684\u79cd\u65cf\uff0c\u4f46\u8fd9\u79cd\u201c\u80cc\u53db\u201d\u4e3b\u8981\u662f\u6307\u4e2a\u4f53\u4e0e\u4e2a\u4f53\uff0c\u5bb6\u65cf\u4e0e\u5bb6\u65cf\u4e4b\u95f4\uff0c\u771f\u6b63\u8c08\u5f97\u4e0a\u80cc\u53db\u6574\u4e2a\u57ce\u5e02\uff0c\u6574\u4e2a\u65cf\u7fa4\u7684\uff0c\u53ea\u6015\u662f\u5be5\u5be5\u65e0\u51e0\uff0c\u800c\u4e14\u53c8\u662f\u9996\u5e2d\u5927\u5deb\u5e08\uff0c\u6700\u540e\u53c8\u88ab\u5904\u6b7b\u2014\u2014\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u5c31\u662f\u6b27\u51ef\u66fe\u7ecf\u63d0\u53ca\u7684\u90a3\u4f4d\u5947\u68b0\u5e08\uff0c\u8428\u74e6\u68cb\u9b54\u50cf\u7684\u5236\u4f5c\u8005\u68b5\u94ce\u5c14\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u707e\u5384\u4e4b\u5730\uff0c\u5c31\u662f\u68b5\u94ce\u5c14\u66fe\u7ecf\u7684\u5c45\u6240\uff0c\u8ba9\u4eba\u6709\u8fdb\u65e0\u51fa\u7684\u9b54\u6cd5\u7981\u5236\u662f\u4ed6\u5e03\u7f6e\uff0c\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\uff0c\u539f\u672c\u6700\u64c5\u957f\u7684\u4e24\u79cd\u9b54\u6cd5\uff0c\u5c31\u662f\u7a7a\u95f4\u6cd5\u672f\u548c\u70bc\u91d1\u672f\u3002\u800c\u90a3\u53ea\u51e4\u51f0\uff0c\u5f88\u663e\u7136\u548c\u68b5\u94ce\u5c14\u8131\u4e0d\u5f00\u5e72\u7cfb\uff0c\u6216\u8bb8\u662f\u4ed6\u7684\u5ba0\u7269\u4e4b\u7c7b\uff0c\u4e5f\u672a\u53ef\u77e5\uff0c\u603b\u4e4b\u662f\u66ff\u4ed6\u5728\u8fd9\u91cc\u770b\u5bb6\u5b88\u95e8\u7684\u3002</p><p>\u51e4\u51f0\u4e00\u5f00\u53e3\u5c31\u8bf4\u6c49\u8bed\uff0c\u867d\u7136\u5979\u4e5f\u4f1a\u8bf4\u8010\u745f\u8bed\uff0c\u4f46\u5341\u5206\u7684\u4e0d\u719f\u7ec3\uff0c\u663e\u7136\u4e0d\u662f\u6bcd\u8bed\u3002\u5982\u679c\u8bf4\u51e4\u51f0\u548c\u68b5\u94ce\u5c14\u662f\u4e00\u4f19\u7684\u8bdd\uff0c\u90a3\u4e48\u68b5\u94ce\u5c14\u5e94\u8be5\u4e5f\u4f1a\u8bf4\u6c49\u8bed\uff1f</p><p>\u5982\u679c\u662f\u5728\u5730\u7403\u4e0a\uff0c\u4f1a\u8bf4\u6c49\u8bed\u7684\u4eba\u6709\u5341\u51e0\u4ebf\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u8fd9\u91cc\u662f\u8d39\u4f26\uff0c\u90a3\u5c31\u53e6\u5f53\u522b\u8bba\u3002\u743c\u6069\u88ab\u838e\u73de\u514b\u63d0\u9192\uff0c\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u5728\u65ad\u57df\u9547\u7684\u65f6\u5019\uff0c\u9b45\u9b54\u66fe\u7ecf\u63d0\u53ca\u6709\u5173\u7f69\u676f\u5927\u5c0f\u7684\u6807\u51c6\uff0c\u8bf4\u662f\u6b27\u51ef\u6240\u53d1\u660e\uff0c\u7528\u7684\u5206\u660e\u4e5f\u662f\u6c49\u8bed\u3002</p><p>\u4f1a\u540c\u4e00\u79cd\u7f55\u89c1\u7684\u8bed\u8a00\uff0c\u540c\u6837\u548c\u8428\u74e6\u68cb\u9b54\u50cf\u5173\u7cfb\u532a\u6d45\uff0c\u5c31\u6b64\u63a8\u8bba\u7684\u8bdd\uff0c\u6b27\u51ef\u53ea\u6015\u548c\u68b5\u94ce\u5c14\u76f8\u8bc6\uff0c\u800c\u4e14\u5173\u7cfb\u5341\u5206\u5bc6\u5207\u3002\u5982\u679c\u518d\u8fdb\u4e00\u6b65\uff0c\u7ed3\u5408\u9b45\u9b54\u6240\u8bf4\u7684\u5176\u4ed6\u4fe1\u606f\u731c\u6d4b\u7684\u8bdd\uff1a\u68b5\u94ce\u5c14\u6b7b\u4ea1\u65f6\u95f4\u662f\u5728\u4e94\u5343\u5e74\u524d\uff0c\u800c\u6b27\u51ef\u7b2c\u4e00\u6b21\u51fa\u73b0\u5728\u6df1\u6e0a\u4e5f\u662f\u8fd9\u4e2a\u65f6\u5019\uff1b\u68b5\u94ce\u5c14\u662f\u4e2a\u5deb\u5e08\uff0c\u6b27\u51ef\u867d\u7136\u662f\u708e\u9b54\uff0c\u5374\u660e\u663e\u504f\u597d\u4f7f\u7528\u6cd5\u672f\uff1b\u68b5\u94ce\u5c14\u662f\u56e0\u4e3a\u548c\u6076\u9b54\u52fe\u7ed3\uff0c\u6240\u4ee5\u89e6\u72af\u5f8b\u6cd5\u9003\u4ea1\uff0c\u4ed6\u8fd9\u526f\u8428\u74e6\u68cb\u4e5f\u662f\u5f97\u5230\u6076\u9b54\u7684\u5e2e\u52a9\u6240\u4ee5\u624d\u80fd\u5b8c\u6210\uff0c\u90a3\u4e48\u4ed6\u6b7b\u4ea1\u4e4b\u540e\uff0c\u7075\u9b42\u6781\u6709\u53ef\u80fd\u4e5f\u4f1a\u8f6c\u5316\u6210\u6076\u9b54\uff0c\u5c31\u50cf\u838e\u73de\u514b\u4e00\u6837\u2026\u2026</p><p>\u90a3\u4e48\uff0c\u6b27\u51ef\u4f1a\u4e0d\u4f1a\u5c31\u662f\u68b5\u94ce\u5c14\uff0c\u4e24\u8005\u5176\u5b9e\u662f\u4e00\u4e2a\u4eba\uff1f</p><p>\u743c\u6069\u4ee5\u524d\u77e5\u9053\u4e00\u4e2a\u540d\u8bcd\uff0c\u53eb\u505a\u5965\u5eb7\u59c6\u5243\u5200\uff0c\u6240\u8c13\u201c\u5982\u65e0\u5fc5\u8981\uff0c\u52ff\u589e\u5b9e\u4f53\u201d\uff0c\u901a\u4fd7\u5730\u8bf4\u5c31\u662f\u4ee5\u6700\u7b80\u6d01\u7684\u65b9\u5f0f\u641e\u5b9a\u95ee\u9898\u3002\u4ed6\u7684\u601d\u7ef4\u65b9\u5f0f\uff0c\u5f88\u591a\u65f6\u5019\u4e5f\u8fd1\u4f3c\u4e8e\u6b64\uff0c\u8d8a\u7b80\u5355\u8d8a\u6b63\u786e\uff0c\u8d8a\u5c11\u8d8a\u597d\u3002\u4e0a\u6b21\u5728\u65ad\u57df\u9547\uff0c\u4ed6\u80fd\u591f\u731c\u6d4b\u51fa\u683c\u62c9\u5179\u7279\u548c\u7ea2\u8272\u5bff\u8863\u662f\u540c\u4e00\u4eba\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c31\u662f\u8fd9\u4e2a\u7f18\u6545\u2014\u2014\u56e0\u4e3a\u5982\u679c\u4ed6\u4eec\u662f\u540c\u4e00\u4e2a\u4eba\u7684\u8bdd\uff0c\u4e00\u5207\u95ee\u9898\u4f9d\u65e7\u53ef\u4ee5\u5f97\u5230\u5706\u6ee1\u89e3\u91ca\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u5c31\u6ca1\u5fc5\u8981\u518d\u591a\u589e\u52a0\u4e00\u4e2a\u4eba\u3002</p><p>\u540c\u6837\u7684\u9053\u7406\uff0c\u5047\u8bbe\u6b27\u51ef\u5c31\u662f\u68b5\u94ce\u5c14\u7684\u8bdd\uff0c\u4e00\u5207\u95ee\u9898\u4fbf\u8fce\u5203\u800c\u89e3\uff0c\u6574\u4e2a\u7591\u56e2\u8c41\u7136\u5f00\u6717\u3002</p><p>\u201c\u4f60\u539f\u672c\u662f\u4f0a\u739b\u65af\u5361\u7684\u5b66\u8005\u5947\u68b0\u5e08\uff0c\u56e0\u4e3a\u52fe\u7ed3\u6076\u9b54\uff0c\u7814\u7a76\u4ee5\u4eba\u7684\u7075\u9b42\u5236\u4f5c\u9b54\u50cf\u7684\u65b9\u6cd5\uff0c\u89e6\u72af\u5e1d\u56fd\u5f8b\u6cd5\uff0c\u9003\u4ea1\u5230\u5e7d\u6697\u5730\u57df\uff0c\u6210\u4e3a\u5353\u5c14\u7684\u9996\u5e2d\u5927\u5deb\u5e08\u3002\u8eab\u6b7b\u4e4b\u540e\uff0c\u501f\u52a9\u6076\u9b54\u7684\u529b\u91cf\uff0c\u5c06\u81ea\u5df1\u4e5f\u8f6c\u5316\u6210\u6076\u9b54\uff0c\u5760\u5165\u6df1\u6e0a\u3002\u8fd9\u4e00\u6b21\u88ab\u5965\u5580\u65af\u6307\u6d3e\uff0c\u80fd\u591f\u91cd\u8fd4\u7269\u8d28\u754c\uff0c\u8d81\u673a\u53d6\u56de\u81ea\u5df1\u6614\u65e5\u672a\u66fe\u5236\u4f5c\u5b8c\u6210\u7684\u8428\u74e6\u68cb\u9b54\u50cf\uff0c\u540e\u6765\u53c8\u501f\u673a\u9001\u7ed9\u4e86\u6211\u2026\u2026\u6574\u4e2a\u8fc7\u7a0b\u662f\u8fd9\u6837\u6ca1\u9519\u5427\u3002\u201d</p><p>\u6b27\u51ef\u8f7b\u8f7b\u70b9\u5934\u3002</p><p>\u201c\u6ca1\u9519\uff0c\u201d\u4ed6\u8bf4\uff0c\u8138\u4e0a\u7684\u7b11\u610f\u95f4\u5fae\u5fae\u6709\u4e86\u4e9b\u8bb8\u8d5e\u8d4f\uff0c\u201c\u867d\u7136\u8bf4\u7834\u4e86\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u4f46\u80fd\u591f\u4ece\u8fd9\u4e9b\u96f6\u788e\u7247\u6bb5\u62fc\u51d1\u51fa\u7b54\u6848\u51fa\u6765\uff0c\u4e5f\u7b97\u662f\u6709\u51e0\u5206\u806a\u660e\u4e86\u3002\u201d</p><p>\u743c\u6069\u81ea\u5632\u4e00\u7b11\uff0c\u201c\u5982\u679c\u771f\u662f\u806a\u660e\uff0c\u65e9\u5c31\u5e94\u8be5\u80fd\u731c\u51fa\u6765\uff0c\u54ea\u91cc\u8fd8\u8981\u7b49\u5230\u4eca\u5929\u3002\u66f4\u4f55\u51b5\uff0c\u867d\u7136\u73b0\u5728\u5f04\u660e\u767d\u4e86\u4e00\u4e9b\u4e1c\u897f\uff0c\u4f46\u5374\u53c8\u5192\u51fa\u66f4\u591a\u7684\u7591\u56e2\uff0c\u6211\u4e5f\u4e0d\u60f3\u518d\u731c\uff0c\u53ea\u80fd\u8bf7\u6559\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u6bd4\u5982\u8bf4\uff0c\u8fd9\u526f\u8428\u74e6\u68cb\u4e3a\u4ec0\u4e48\u53ea\u6709\u4f60\u548c\u6211\u80fd\u7528\uff1f\u4f60\u80fd\u7528\uff0c\u90a3\u7406\u6240\u5f53\u7136\uff0c\u4f60\u5c31\u662f\u5236\u4f5c\u8005\u2014\u2014\u4f46\u6211\u4e3a\u4ec0\u4e48\u4e5f\u80fd\u7528\uff1f\u201d</p><p>\u201c\u4f60\u731c\u731c\u770b\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u6211\u542c\u8bf4\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u672f\u8bb2\u7a76\u8840\u8109\u9650\u5b9a\uff0c\u6709\u5f88\u591a\u9b54\u6cd5\u7269\u54c1\uff0c\u552f\u6709\u2018\u7687\u5ba4\u2019\u624d\u80fd\u4f7f\u7528\uff0c\u843d\u5230\u5176\u4ed6\u4eba\u624b\u91cc\u5c31\u6beb\u65e0\u4ef7\u503c\u3002\u4f46\u8981\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u90a3\u6211\u548c\u4f60\u5c31\u90fd\u5f97\u662f\u2018\u7687\u5ba4\u2019\u4e86\uff0c\u6211\u8fd9\u8fb9\u4e14\u4e0d\u8bf4\uff0c\u81f3\u5c11\u4f60\u5e76\u4e0d\u662f\u2018\u7687\u5ba4\u2019\uff0c\u53ea\u662f\u2018\u5b66\u8005\u2019\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6211\u5e76\u4e0d\u662f\u7687\u5ba4\u3002\u201d</p><p>\u201c\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u90a3\u5c31\u662f\u4f60\u5728\u5236\u4f5c\u7684\u65f6\u5019\uff0c\u7ed9\u8fd9\u526f\u8428\u74e6\u68cb\u4e0b\u4e86\u4ec0\u4e48\u7981\u5236\uff0c\u8ba9\u5176\u4ed6\u4eba\u65e0\u6cd5\u4f7f\u7528\u3002\u4f60\u662f\u5236\u4f5c\u8005\uff0c\u8981\u505a\u5230\u8fd9\u70b9\u5e94\u8be5\u4e0d\u96be\uff0c\u800c\u4e14\u4ece\u60c5\u7406\u4e0a\u800c\u8a00\u4e5f\u8bf4\u5f97\u901a\uff0c\u4f60\u4e0d\u5e0c\u671b\u5b83\u4e3a\u5353\u5c14\u6240\u7528\u3002\u81f3\u4e8e\u6211\u80fd\u591f\u4f7f\u7528\uff0c\u90a3\u662f\u4f60\u628a\u5b83\u7ed9\u6211\u7684\u65f6\u5019\uff0c\u6697\u4e2d\u4e3a\u6211\u5f00\u653e\u4e86\u6743\u9650\u3002\u201d</p><p>\u201c\u8fd9\u53ef\u4ee5\u8bf4\u5f97\u901a\u554a\u3002\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u8fd9\u662f\u80fd\u591f\u8bf4\u5f97\u901a\uff0c\u4f46\u6211\u603b\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u52b2\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u52b2\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u53ea\u662f\u4e00\u79cd\u76f4\u89c9\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u201c\u6211\u603b\u89c9\u5f97\uff0c\u6211\u5e94\u8be5\u548c\u8fd9\u526f\u8428\u74e6\u68cb\u6709\u67d0\u79cd\u8054\u7cfb\uff0c\u5426\u5219\u7684\u8bdd\u4f60\u6ca1\u5fc5\u8981\u7279\u5730\u628a\u5b83\u9001\u7ed9\u6211\u3002\u201d</p><p>\u6b27\u51ef\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u6211\u5fc3\u60c5\u597d\uff0c\u770b\u4f60\u987a\u773c\uff0c\u968f\u624b\u9001\u4f60\u70b9\u4e1c\u897f\uff0c\u8fd9\u96be\u9053\u90fd\u4e0d\u884c\uff1f\u201d</p><p>\u201c\u5f53\u7136\u884c\uff0c\u201d\u743c\u6069\u4e5f\u7b11\u8d77\u6765\uff0c\u201c\u53ea\u662f\u6211\u4e0d\u76f8\u4fe1\u3002\u201d</p><p>\u201c\u6807\u51c6\u7684\u9634\u8c0b\u8bba\u8005\u3002\u201d\u6b27\u51ef\u8bc4\u4ef7\u3002</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u6ca1\u529e\u6cd5\uff0c\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u6c89\u9ed8\u7740\uff0c\u624b\u6307\u5728\u77f3\u684c\u4e0a\u8f7b\u8f7b\u53e9\u51fb\uff0c\u4eff\u4f5b\u5728\u8e0c\u8e87\uff0c\u743c\u6069\u9759\u9759\u7b49\u5f85\uff0c\u5e76\u4e0d\u50ac\u4fc3\u3002</p><p>\u201c\u4f60\u7684\u4e24\u79cd\u731c\u6d4b\u90fd\u9519\u4e86\uff0c\u201d\u6b27\u51ef\u6162\u6162\u5f00\u53e3\uff0c\u201c\u6216\u8005\u8bf4\uff0c\u90fd\u4e0d\u5b8c\u5168\u6b63\u786e\u3002\u6211\u786e\u5b9e\u662f\u5728\u8fd9\u526f\u68cb\u4e0a\u4e0b\u4e86\u7981\u5236\uff0c\u4e5f\u786e\u5b9e\u662f\u4e3a\u4e86\u907f\u514d\u843d\u5230\u5353\u5c14\u624b\u4e2d\uff0c\u4f46\u4f60\u4e4b\u6240\u4ee5\u80fd\u4f7f\u7528\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u6211\u4e3a\u4f60\u5f00\u8bbe\u4e86\u6743\u9650\uff0c\u800c\u662f\u4f60\u672c\u6765\u5c31\u80fd\u3002\u201d</p><p>\u201c\u6211\u672c\u6765\u5c31\u80fd\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u5c31\u50cf\u4f60\u6240\u77e5\u9053\u7684\u90a3\u6837\uff0c\u6211\u4eec\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u672f\uff0c\u8bb2\u7a76\u8840\u8109\u9650\u5b9a\uff0c\u5f88\u591a\u9ad8\u660e\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u53ea\u6709\u7687\u5ba4\u624d\u80fd\u5b66\u4e60\u638c\u63e1\uff0c\u5f88\u591a\u5389\u5bb3\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u53ea\u6709\u7687\u5ba4\u624d\u80fd\u8fd0\u4f7f\uff0c\u5176\u4ed6\u4eba\u62ff\u4e86\uff0c\u8981\u4e48\u6839\u672c\u65e0\u7528\uff0c\u8981\u4e48\u4e5f\u53d1\u6325\u4e0d\u51fa\u5b8c\u5168\u7684\u6548\u679c\u3002\u4f46\u6240\u8c13\u2018\u8840\u8109\u9650\u5b9a\u2019\uff0c\u8fd8\u6709\u4e00\u5c42\u542b\u4e49\uff0c\u5c31\u662f\u7687\u5ba4\u8840\u8109\u5bf9\u4e8e\u975e\u7687\u5ba4\u8840\u8109\uff0c\u62e5\u6709\u5148\u5929\u6027\u7684\u4f18\u52bf\u3002\u5c31\u50cf\u8fd9\u526f\u8428\u74e6\u68cb\uff0c\u5c3d\u7ba1\u6211\u5728\u5236\u9020\u7684\u65f6\u5019\u5c31\u4e0b\u4e86\u7981\u5236\uff0c\u9664\u6211\u4e4b\u5916\u65e0\u4eba\u80fd\u7528\u2014\u2014\u4f46\u8fd9\u4e2a\u7981\u5236\u5bf9\u4e8e\u7687\u5ba4\u800c\u8a00\u662f\u65e0\u6548\u7684\uff0c\u53ea\u8981\u77e5\u9053\u53e3\u4ee4\uff0c\u4ed6\u4eec\u5c31\u80fd\u540c\u6837\u4f7f\u7528\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u7684\u610f\u601d\u4e0d\u4f1a\u662f\u8bf4\uff0c\u6211\u662f\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u6b27\u51ef\u80af\u5b9a\u5730\u70b9\u5934\uff0c\u201c\u4f60\u662f\u7687\u5ba4\uff0c\u4f60\u6709\u7fd4\u9f99\u5370\u8bb0\u3002\u201d</p><p>\u201c\u522b\uff0c\u522b\u5f00\u73a9\u7b11\uff0c\u201d\u743c\u6069\u4e00\u65f6\u95f4\u88ab\u9707\u4f4f\u4e86\uff0c\u6216\u8005\u8bf4\u4ed6\u8fd8\u6ca1\u505a\u597d\u8fd9\u4e2a\u5fc3\u7406\u51c6\u5907\uff0c\u201c\u6211\u51fa\u751f\u4e8e\u9634\u9b42\u57ce\uff0c\u660e\u660e\u662f\u7eaf\u79cd\u7684\u8010\u745f\u745e\u5c14\u4eba\u2026\u2026\u201d</p><p>\u201c\u6211\u6ca1\u8ddf\u4f60\u5f00\u73a9\u7b11\uff0c\u201d\u6b27\u51ef\u76b1\u7709\uff0c\u201c\u7fd4\u9f99\u5370\u8bb0\u662f\u7687\u5ba4\u7684\u4e24\u5927\u8c61\u5f81\u4e4b\u4e00\uff0c\u4f60\u6709\u7fd4\u9f99\u5370\u8bb0\uff0c\u90a3\u5c31\u80af\u5b9a\u662f\u7687\u5ba4\u3002\u81f3\u4e8e\u4f60\u660e\u660e\u662f\u4e2a\u8010\u745f\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u6211\u4eec\u7687\u5ba4\u624d\u6709\u7684\u5370\u8bb0\uff0c\u5766\u767d\u5730\u8bf4\uff0c\u6211\u4e5f\u6b63\u7eb3\u95f7\u7740\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u4f60\u8bf4\u7684\u4ec0\u4e48\u7fd4\u9f99\u5370\u8bb0\uff0c\u90a3\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6211\u8eab\u4e0a\u4ece\u6765\u5c31\u6ca1\u4ec0\u4e48\u5370\u8bb0\uff0c\u8fde\u80ce\u8bb0\u90fd\u6ca1\u6709\u2026\u2026\u201d</p><p>\u201c\u7fd4\u9f99\u5370\u8bb0\u53c8\u4e0d\u662f\u523b\u5728\u8eab\u4f53\u4e0a\u7684\u3002\u201d</p><p>\u201c\u90a3\u523b\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u7075\u9b42\u3002\u201d</p><p>\u201c\u7075\u9b42\uff1f\u201d</p><p>\u201c\u7075\u9b42\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u77a5\u7740\u743c\u6069\uff0c\u201c\u6211\u4eec\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\uff0c\u5206\u2018\u7687\u5ba4\u2019\u548c\u2018\u5b66\u8005\u2019\u4e24\u79cd\uff0c\u4f60\u5230\u5e95\u77e5\u4e0d\u77e5\u9053\uff0c\u8fd9\u2018\u7687\u5ba4\u2019\u7684\u771f\u6b63\u542b\u4e49\uff1f\u201d</p><p>\u201c\u7687\u5ba4\u2026\u2026\u5f53\u7136\u662f\u6307\u6709\u7687\u5ba4\u8840\u7edf\u7684\u4eba\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u6447\u5934\uff0c\u201c\u9519\u4e86\uff0c\u6240\u8c13\u7687\u5ba4\uff0c\u662f\u6307\u6709\u7687\u5ba4\u7684\u8840\u8109\uff0c\u800c\u2018\u8840\u8109\u2019\u8fd9\u4e2a\u8bcd\uff0c\u5176\u5b9e\u662f\u4e00\u79cd\u5f62\u5bb9\uff0c\u5e76\u4e0d\u662f\u771f\u7684\u6307\u4f60\u4f53\u5185\u6d41\u6dcc\u7684\u8840\u6db2\uff0c\u4e5f\u5e76\u4e0d\u771f\u662f\u6307\u4f60\u7684\u8840\u7edf\uff0c\u5b83\u6307\u7684\u662f\u4e00\u79cd\u8d44\u683c\u3002\u5177\u4f53\u800c\u8a00\uff0c\u5c31\u662f\u62e5\u6709\u7075\u9b42\u5370\u8bb0\u2014\u2014\u7075\u9b42\u5370\u8bb0\u6709\u4e24\u79cd\uff1a\u51e4\u51f0\u548c\u7fd4\u9f99\uff0c\u62e5\u6709\u5176\u4e00\uff0c\u90a3\u5c31\u662f\u7687\u5ba4\u3002\u5982\u679c\u8981\u8bf4\u8840\u7edf\uff0c\u6211\u4e5f\u7b97\u662f\u4e2a\u738b\u5b50\u5462\uff0c\u4f46\u5c31\u6ca1\u6709\u5370\u8bb0\uff0c\u6240\u4ee5\u53ea\u662f\u5b66\u8005\u5947\u68b0\u5e08\u3002\u201d</p><p>\u201c\u90a3\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u624d\u4f1a\u6709\u7075\u9b42\u5370\u8bb0\uff1f\u201d\u743c\u6069\u4f9d\u65e7\u4e0d\u89e3\uff0c\u201c\u96be\u9053\u662f\u968f\u673a\u51fa\u73b0\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u4f1a\u662f\u968f\u673a\uff0c\u7687\u5ba4\u7684\u5b50\u55e3\uff0c\u672a\u5fc5\u4e00\u5b9a\u662f\u7687\u5ba4\uff1b\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u7687\u5ba4\u4e00\u5b9a\u662f\u7687\u5ba4\u7684\u5b50\u55e3\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u53e5\u8bdd\u6211\u6ca1\u542c\u61c2\u3002\u201d</p><p>\u6b27\u51ef\u644a\u5f00\u624b\uff0c\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u4e00\u56e2\u8d64\u7130\u5728\u4ed6\u638c\u5fc3\u817e\u8dc3\u8d77\u6765\uff0c\u75be\u901f\u65cb\u8f6c\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u51e4\u51f0\u4e4b\u5f62\u3002\u201c\u6211\u6253\u4e2a\u6bd4\u65b9\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u770b\u8fd9\u56e2\u706b\u7130\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u6b27\u51ef\u53c8\u968f\u624b\u4e00\u62b9\uff0c\u77f3\u684c\u4e0a\u51fa\u73b0\u4e86\u4e94\u5806\u5c0f\u5c0f\u7684\u67f4\u579b\uff0c\u201c\u73b0\u5728\u7528\u4e94\u6839\u5f15\u706b\u68cd\uff0c\u4ece\u8fd9\u56e2\u706b\u7130\u4e2d\u5f15\u706b\uff0c\u6765\u5206\u522b\u70b9\u71c3\u8fd9\u4e94\u4e2a\u67f4\u579b\u3002\u4f46\u5e76\u4e0d\u662f\u6bcf\u4e00\u4e2a\u90fd\u80fd\u987a\u5229\u70b9\u71c3\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u53ef\u80fd\u6210\u529f\u4e86\uff0c\u5269\u4e0b\u56db\u4e2a\u90fd\u5931\u8d25\u4e86\u3002\u201d</p><p>\u201c\u6211\u638c\u5fc3\u7684\u8fd9\u56e2\u706b\u7130\uff0c\u5c31\u662f\u4e00\u4e2a\u7687\u5ba4\u5947\u68b0\u5e08\uff0c\u4ed6\u6709\u51e4\u51f0\u5370\u8bb0\u3002\u4ed6\u6709\u4e94\u4e2a\u5b50\u5973\uff0c\u5c31\u662f\u8fd9\u4e94\u4e2a\u67f4\u579b\uff0c\u6bcf\u4e2a\u5b50\u5973\u90fd\u6709\u53ef\u80fd\u4ece\u4ed6\u8fd9\u91cc\u7ee7\u627f\u51e4\u51f0\u5370\u8bb0\uff0c\u4e0d\u8fc7\u5e76\u4e0d\u662f\u6bcf\u4e00\u4e2a\u4eba\u90fd\u80fd\u6210\u529f\u3002\u4f46\u53cd\u8fc7\u6765\uff0c\u5982\u679c\u6ca1\u6709\u8fd9\u56e2\u706b\u7130\uff0c\u90a3\u4e48\u65e0\u8bba\u8c01\u4e5f\u6ca1\u529e\u6cd5\u70b9\u71c3\u81ea\u5df1\u2014\u2014\u73b0\u5728\u4f60\u660e\u767d\u6211\u7684\u610f\u601d\u4e86\uff1f\u7687\u5ba4\u7684\u5b50\u55e3\uff0c\u672a\u5fc5\u4e00\u5b9a\u662f\u7687\u5ba4\uff0c\u53cd\u8fc7\u6765\uff0c\u7687\u5ba4\u4e00\u5b9a\u662f\u7687\u5ba4\u7684\u5b50\u55e3\u3002\u201d</p><p>\u201c\u90a3\u8981\u8fd9\u4e48\u8bf4\uff0c\u6211\u7684\u7236\u6bcd\u5f53\u4e2d\uff0c\u5c82\u4e0d\u662f\u80af\u5b9a\u6709\u4e00\u4f4d\u662f\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\u5947\u68b0\u5e08\uff1f\u4e43\u81f3\u6211\u7684\u7956\u7956\u8f88\u8f88\uff0c\u6bcf\u4e00\u4ee3\u90fd\u80af\u5b9a\u6709\u4e00\u4f4d\u7687\u5ba4\u5947\u68b0\u5e08\uff1f\u201d</p><p>\u6b27\u51ef\u63c9\u7740\u989d\u5934\uff0c\u201c\u4ece\u7406\u8bba\u4e0a\u8bf4\u5e94\u8be5\u662f\u8fd9\u6837\uff0c\u7136\u800c\u8fd9\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u2026\u2026\u6211\u4e5f\u641e\u4e0d\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4e0d\u77e5\u9053\u5e03\u96f7\u7eb3\u65af\u5f04\u4e86\u4ec0\u4e48\u624b\u811a\u3002\u201d</p><p>\u201c\u4f60\u4e4b\u6240\u4ee5\u4e00\u8def\u8ddf\u7740\u6211\u6765\u5e7d\u6697\u5730\u57df\uff0c\u4e5f\u662f\u56e0\u4e3a\u8fd9\u4e2a\uff0c\u60f3\u4ece\u6211\u8eab\u4e0a\u627e\u5230\u7b54\u6848\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u5176\u4e2d\u4e00\u4e2a\u539f\u56e0\uff0c\u201d\u6b27\u51ef\u627f\u8ba4\uff0c\u201c\u5766\u767d\u5730\u8bf4\uff0c\u8981\u4e0d\u662f\u4f60\u6709\u610f\u5fd7\u5c4f\u969c\uff0c\u6211\u65e9\u5c31\u76f4\u63a5\u8bfb\u53d6\u4f60\u7684\u8bb0\u5fc6\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e4b\u524d\u5462\uff0c\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u6211\u4eec\u8ba4\u8bc6\u7684\u65f6\u5019\uff0c\u662f\u5728\u5deb\u5e08\u5c71\u8109\u5427\uff0c\u90a3\u65f6\u5019\u6211\u5e94\u8be5\u6ca1\u6709\u610f\u5fd7\u5c4f\u969c\u4fdd\u62a4\u554a\u3002\u201d</p><p>\u201c\u95ee\u9898\u5c31\u51fa\u5728\u8fd9\u91cc\u554a\uff0c\u201d\u6b27\u51ef\u644a\u624b\uff0c\u201c\u90a3\u65f6\u5019\u4f60\u660e\u660e\u662f\u6ca1\u6709\u7fd4\u9f99\u5370\u8bb0\u7684\uff0c\u81f3\u5c11\u6211\u534a\u70b9\u6ca1\u5bdf\u89c9\u51fa\u6765\uff0c\u6240\u4ee5\u4e5f\u6ca1\u5728\u610f\u3002\u76f4\u5230\u540e\u6765\uff0c\u5e03\u96f7\u7eb3\u65af\u9080\u8bf7\u6211\u53bb\u9634\u9b42\u57ce\uff0c\u518d\u6b21\u9047\u5230\u4f60\u7684\u65f6\u5019\uff0c\u6211\u624d\u53d1\u73b0\u4f60\u6709\u7fd4\u9f99\u5370\u8bb0\u2014\u2014\u90a3\u65f6\u5019\u60f3\u4e0b\u624b\u5df2\u7ecf\u8fdf\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u9001\u8fd9\u526f\u8428\u74e6\u68cb\u7ed9\u6211\uff0c\u4e5f\u662f\u60f3\u9a8c\u8bc1\u6211\u7684\u7fd4\u9f99\u5370\u8bb0\u5230\u5e95\u662f\u771f\u662f\u5047\uff1f\u201d</p><p>\u6b27\u51ef\u4e0d\u7b54\uff0c\u7b97\u662f\u9ed8\u8ba4\u3002</p><p>\u201c\u679c\u7136\uff0c\u6211\u5c31\u77e5\u9053\u5929\u4e0a\u4e0d\u4f1a\u5e73\u767d\u65e0\u6545\u6389\u9985\u997c\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u4e0d\u8fc7\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u65e9\u5c31\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u5176\u5b9e\u743c\u6069\u4e5f\u6e05\u695a\u6b27\u51ef\u5e76\u4e0d\u662f\u5355\u7eaf\u7684\u8bd5\u63a2\u2014\u2014\u5982\u679c\u53ea\u662f\u60f3\u8bd5\u8bd5\u770b\uff0c\u76f4\u63a5\u501f\u7ed9\u743c\u6069\u73a9\u4e24\u5929\u5c31\u662f\uff0c\u4e0d\u7528\u5f53\u771f\u7ed9\u3002\u662f\u6177\u6168\u5927\u65b9\u4e5f\u597d\uff0c\u662f\u770b\u5728\u201c\u540c\u65cf\u201d\u7684\u60c5\u5206\u4e0a\u4e5f\u7f62\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u526f\u8428\u74e6\u68cb\u4e5f\u5c61\u6b21\u5e2e\u4e86\u4ed6\u7684\u5927\u5fd9\uff0c\u7b97\u662f\u627f\u4e86\u6b27\u51ef\u4e00\u4efd\u60c5\u3002</p><p>\u201c\u603b\u4e4b\uff0c\u867d\u7136\u539f\u56e0\u4e0d\u660e\uff0c\u4f46\u4f60\u53ef\u4ee5\u80af\u5b9a\u6211\u6709\u90a3\u4ec0\u4e48\u7fd4\u9f99\u5370\u8bb0\u2026\u2026\u662f\u5427\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u5982\u679c\u5355\u51ed\u6211\u81ea\u5df1\u6765\u5224\u65ad\u7684\u8bdd\uff0c\u53ea\u6015\u8fd8\u4e0d\u80fd\u5341\u5206\u80af\u5b9a\uff0c\u56e0\u4e3a\u6211\u81ea\u5df1\u4e0d\u662f\u7687\u5ba4\uff0c\u6ca1\u6709\u5370\u8bb0\uff0c\u65e0\u6cd5\u5171\u9e23\uff0c\u53ea\u80fd\u4ece\u8ff9\u8c61\u4e0a\u63a8\u6d4b\u3002\u4f46\u8fd9\u6b21\u827e\u841d\u5a1c\u8bd7\u4e4b\u6240\u4ee5\u653b\u51fb\u4f60\uff0c\u6b63\u662f\u56e0\u4e3a\u5979\u53d1\u73b0\u4e86\u4f60\u7684\u7fd4\u9f99\u5370\u8bb0\uff0c\u8fd9\u5c31\u786e\u5b9a\u65e0\u7591\u4e86\u3002\u201d</p><p>\u201c\u827e\u841d\u5a1c\u8bd7\uff1f\u201d\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u90a3\u53ea\u51e4\u51f0\uff1f\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u201c\u5979\u662f\u2026\u2026\u201d</p><p>\u201c\u662f\u6211\u7684\u670b\u53cb\uff0c\u201d\u6b27\u51ef\u7b80\u5355\u5730\u8bf4\uff0c\u660e\u663e\u4e0d\u613f\u610f\u591a\u8c08\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u827e\u841d\u5a1c\u8bd7\u548c\u6211\u4e0d\u4e00\u6837\uff0c\u5979\u751f\u524d\u662f\u7687\u5ba4\u5947\u68b0\u5e08\uff0c\u6709\u51e4\u51f0\u5370\u8bb0\u3002\u7fd4\u9f99\u3001\u51e4\u51f0\u5370\u8bb0\uff0c\u5f7c\u6b64\u95f4\u90fd\u662f\u80fd\u591f\u76f8\u4e92\u5171\u9e23\u7684\uff0c\u6240\u4ee5\u5979\u7684\u5224\u65ad\u4e0d\u53ef\u80fd\u51fa\u9519\u3002\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u63c9\u7740\u592a\u9633\u7a74\uff0c\u201c\u5982\u679c\u6211\u6ca1\u5f04\u9519\u7684\u8bdd\uff0c\u4f60\u662f\u8bf4\uff0c\u7fd4\u9f99\u3001\u51e4\u51f0\u4e24\u79cd\u7075\u9b42\u5370\u8bb0\uff0c\u90fd\u662f\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u7684\u8c61\u5f81\u3002\u4f60\u73b0\u5728\u53c8\u8bf4\uff0c\u827e\u841d\u5a1c\u8bd7\u5973\u58eb\u4e4b\u6240\u4ee5\u653b\u51fb\u6211\uff0c\u6b63\u56e0\u4e3a\u8ba4\u51fa\u4e86\u6211\u7684\u7fd4\u9f99\u5370\u8bb0\uff0c\u8fd9\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u6d89\u53ca\u4e00\u6bb5\u5f80\u4e8b\u4e86\uff0c\u201d\u6b27\u51ef\u72b9\u8c6b\u7247\u523b\uff0c\u6700\u7ec8\u8fd8\u662f\u51b3\u5b9a\u5bf9\u743c\u6069\u89e3\u91ca\u6e05\u695a\uff0c\u201c\u5e1d\u56fd\u5386\u53f2\u4e0a\uff0c\u66fe\u7ecf\u7206\u53d1\u8fc7\u4e00\u573a\u60e8\u70c8\u5267\u6218\u3002\u90a3\u4e00\u6218\uff0c\u738b\u57ce\u5937\u4e3a\u5e73\u5730\uff0c\u7687\u5e1d\u6218\u6b7b\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u597d\u50cf\u542c\u8bf4\u8fc7\u3002\u201d\u743c\u6069\u6253\u65ad\u3002</p><p>\u201c\u4f60\u542c\u8bf4\u8fc7\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5728\u65ad\u57df\u9547\u53c2\u52a0\u51b3\u6597\u5927\u8d5b\u7684\u65f6\u5019\uff0c\u6b23\u5e03\u5973\u58eb\u770b\u5230\u8fd9\u526f\u8428\u74e6\u68cb\uff0c\u8ba4\u51fa\u662f\u6709\u72c4\u9b54\u9ad8\u6839\u7684\u529b\u91cf\u5728\u652f\u6301\uff0c\u5f53\u65f6\u66fe\u7ecf\u63d0\u5230\u8fc7\u8fd9\u4ef6\u4e8b\u3002\u201d</p><p>\u201c\u5979\u600e\u4e48\u8bf4\u7684\uff1f\u201d</p><p>\u201c\u5979\u8bf4\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u4ee5\u524d\u662f\u5d07\u62dc\u72c4\u9b54\u9ad8\u6839\u7684\uff0c\u4f46\u540e\u6765\u6709\u4e00\u6b21\uff0c\u5947\u68b0\u5e08\u4eec\u5f00\u4f20\u9001\u95e8\u5931\u8bef\uff0c\u6253\u5f00\u4e86\u901a\u5f80\u72c4\u9b54\u9ad8\u6839\u7684\u738b\u5bab\u7684\u4f4d\u9762\u901a\u9053\u3002\u5927\u91cf\u7684\u6076\u9b54\u95ef\u5165\u4f0a\u739b\u65af\u5361\u738b\u57ce\uff0c\u9020\u6210\u5de8\u5927\u7834\u574f\uff0c\u7687\u5e1d\u4e5f\u6218\u6b7b\u6b64\u5f79\u3002\u4e5f\u6b63\u662f\u56e0\u4e3a\u8fd9\u4e00\u6b21\uff0c\u4f0a\u739b\u65af\u5361\u5236\u5b9a\u6cd5\u5f8b\uff0c\u4e25\u7981\u6076\u9b54\u4fe1\u4ef0\u2026\u2026\u201d</p><p>\u201c\u5f00\u4f20\u9001\u95e8\u5931\u8bef\uff1f\u563f\u563f\uff0c\u201d\u6b27\u51ef\u51b7\u7b11\uff0c\u201c\u7a7a\u95f4\u4f20\u9001\uff0c\u8fd9\u662f\u7fd4\u9f99\u4e00\u8109\u7684\u62ff\u624b\u597d\u620f\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\u51fa\u73b0\u5931\u8bef\u3002\u4e0d\u8fc7\u662f\u5b98\u9762\u6587\u7ae0\uff0c\u63a9\u9970\u771f\u76f8\u7f62\u4e86\u3002\u201d</p></article>"}}}]);
