(window.webpackJsonp=window.webpackJsonp||[]).push([[1593],{1616:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u5b8c\u7f8e\u7684\u738b\u8005",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u5b8c\u7f8e\u7684\u738b\u8005</h2><p>\u201c\u7ec8\u4e8e\u80fd\u56de\u5bb6\u4e86\u3002\u201d</p><p>\u63a5\u5230\u547d\u4ee4\uff0c\u743c\u6069\u957f\u957f\u8212\u4e86\u53e3\u6c14\u3002\u6263\u9664\u53bb\u4e0b\u5c42\u754c\u7684\u4e00\u8d9f\u65c5\u884c\uff0c\u7b97\u8d77\u6765\u5728\u8fd9\u5e7d\u6697\u5730\u57df\u4e2d\u5df2\u7ecf\u5446\u4e86\u5927\u534a\u5e74\uff0c\u867d\u8bf4\u65e0\u8bba\u5728\u74dc\u7406\u5fb7\u65af\uff0c\u5728\u4f0a\u5361\u6c99\uff0c\u8fd8\u662f\u5728\u6668\u70bc\uff0c\u603b\u662f\u53d7\u5230\u793c\u9047\uff0c\u9526\u8863\u7389\u98df\u8c08\u4e0d\u4e0a\uff0c\u4f18\u88d5\u8212\u9002\u662f\u80af\u5b9a\u7684\uff0c\u4f46\u8fd9\u79cd\u6ca1\u6709\u707f\u70c2\u9633\u5149\u7684\u65e5\u5b50\uff0c\u6765\u81ea\u5730\u8868\u7684\u4eba\u7c7b\u7ec8\u7a76\u8fd8\u662f\u96be\u4ee5\u9002\u5e94\uff0c\u611f\u89c9\u6574\u4e2a\u4eba\u90fd\u5feb\u8981\u53d1\u9709\u4e86\u3002</p><p>\u66f4\u91cd\u8981\u7684\u662f\uff0c\u53ef\u4ee5\u56de\u53bb\u89c1\u59d0\u59d0\u73ca\u5609\u4e86\u3002</p><p>\u56de\u9634\u9b42\u57ce\u8ff0\u804c\uff0c\u8fd9\u662f\u9898\u4e2d\u5e94\u6709\u4e4b\u4e49\uff0c\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\u3002\u53c8\u8bf4\u53e6\u6709\u65b0\u7684\u4efb\u547d\uff0c\u867d\u7136\u6ca1\u6709\u5177\u4f53\u660e\u6307\uff0c\u4f46\u5341\u6709\u516b\u4e5d\u662f\u4e0d\u7528\u518d\u56de\u6765\u4e86\u3002\u6bd5\u7adf\u5446\u4e86\u8fd9\u4e48\u4e45\uff0c\u5982\u4eca\u79bb\u804c\u5378\u4efb\uff0c\u514d\u4e0d\u4e86\u8981\u6709\u79cd\u79cd\u7e41\u7410\u5b89\u6392\uff0c\u743c\u6069\u539f\u672c\u5c31\u4e0d\u64c5\u6b64\u9053\uff0c\u53c8\u662f\u5f52\u5fc3\u4f3c\u7bad\uff0c\u54ea\u91cc\u8010\u70e6\u505a\u8fd9\u4e9b\uff0c\u7d22\u6027\u76f4\u63a5\u5c31\u628a\u6743\u529b\u548c\u624b\u4e2d\u7684\u8d44\u6e90\u57fa\u672c\u90fd\u79fb\u4ea4\u7ed9\u4e86\u5fb7\u53e4\u62c9\uff0c\u7531\u4ed6\u4ee3\u7406\uff0c\u53cd\u6b63\u81ea\u5df1\u9a6c\u4e0a\u5c31\u8d70\u4eba\uff0c\u4e5f\u65e0\u6240\u8c13\u4ec0\u4e48\u653e\u5fc3\u4e0d\u653e\u5fc3\u7684\uff0c\u987a\u4fbf\u628a\u4f8f\u5112\u8fea\u74e6\u514b\u8c03\u5230\u6668\u70bc\u53bb\u7ed9\u4ed6\u5e2e\u5fd9\u3002\u81f3\u4e8e\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\uff0c\u6ca1\u6709\u4e00\u4e2a\u613f\u610f\u7ee7\u7eed\u7559\u5728\u8fd9\u91cc\u7684\uff0c\u81ea\u7136\u5168\u90fd\u5e26\u8d70\u3002\u53e6\u5916\u8fd8\u8981\u548c\u77ee\u4eba\u544a\u522b\uff0c\u5411\u9c81\u6587\u544a\u8f9e\uff0c\u8bf8\u5982\u6b64\u7c7b\u4e00\u5806\u5e94\u916c\uff0c\u5c31\u8fd9\u6837\u5fd9\u5fd9\u4e71\u4e71\u6298\u817e\u4e86\u4e03\u516b\u5929\uff0c\u4e00\u5207\u57fa\u672c\u51c6\u5907\u5c31\u7eea\uff0c\u6885\u83f2\u65af\u4e5f\u81ea\u6668\u70bc\u6765\u5230\u4f0a\u5361\u6c99\u57ce\uff0c\u4f17\u4eba\u96c6\u5408\u3002\u63a5\u4e0b\u6765\u6240\u9700\u8981\u9762\u4e34\u7684\u95ee\u9898\uff0c\u4fbf\u662f\u56de\u53bb\u7684\u8def\u600e\u4e48\u8d70\u4e86\u3002</p><p>\u5018\u82e5\u5728\u4ee5\u524d\uff0c\u6839\u672c\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\uff0c\u5927\u5bb6\u53ea\u80fd\u539f\u8def\u8fd4\u56de\uff0c\u8fd8\u662f\u8d70\u5730\u5e95\uff0c\u4e00\u8def\u5317\u4e0a\u76f4\u5230\u6c99\u739b\u65af\u3001\u5965\u56fe\u57ce\uff0c\u7136\u540e\u4e0a\u5730\u8868\uff0c\u5934\u9876\u4e0a\u98d8\u7740\u7684\u5c31\u662f\u9634\u9b42\u57ce\u4e86\u3002\u4f46\u56e0\u4e3a\u8299\u857e\u72c4\u7684\u7814\u7a76\u8fdb\u5c55\uff0c\u73b0\u5728\u591a\u51fa\u4e00\u79cd\u9009\u62e9\u6765\uff0c\u4fbf\u662f\u76f4\u63a5\u7528\u4f20\u9001\u9b54\u6cd5\u4e0a\u5730\u8868\uff0c\u7136\u540e\u518d\u56de\u9634\u9b42\u57ce\u3002</p><p>\u5728\u6700\u8fd1\u4e00\u6b21\u7684\u8bd5\u9a8c\u4e2d\uff0c\u8299\u857e\u72c4\u5df2\u7ecf\u6210\u529f\u628a\u4e00\u5934\u6d1b\u65af\u517d\u4f20\u9001\u5230\u4e86\u4e8c\u5341\u4e94\u82f1\u91cc\u4e4b\u5916\u7684\u5730\u65b9\uff0c\u8bef\u5dee\u5728\u4e94\u82f1\u5c3a\u4e4b\u5185\u3002\u4f0a\u5361\u6c99\u57ce\u8ddd\u79bb\u5730\u8868\u5927\u7ea6\u5341\u51e0\u82f1\u91cc\uff0c\u6700\u591a\u4e0d\u4f1a\u8d85\u8fc7\u4e8c\u5341\u82f1\u91cc\uff0c\u5b8c\u5168\u5c5e\u4e8e\u6cd5\u672f\u53ef\u4ee5\u627f\u53d7\u7684\u8303\u56f4\u4e4b\u5185\u3002\u5353\u5c14\u7684\u8d44\u6599\u4e2d\u8be6\u7ec6\u8bb0\u8f7d\u4e86\u5730\u8868\u7684\u4e00\u5904\u8352\u5e9f\u57ce\u5821\uff0c\u6b63\u597d\u4f5c\u4e3a\u4f20\u9001\u7684\u76ee\u6807\u3002</p><p>\u6beb\u65e0\u60ac\u5ff5\u5730\uff0c\u6240\u6709\u4eba\u90fd\u9009\u62e9\u4e86\u7b2c\u4e8c\u79cd\u65b9\u6848\uff0c\u80fd\u591f\u8d70\u9633\u5149\u5927\u9053\uff0c\u8c01\u613f\u610f\u50cf\u8001\u9f20\u4e00\u6837\u5728\u9ed1\u6697\u6f6e\u6e7f\u7684\u5730\u4e0b\u752c\u9053\u91cc\u6b65\u884c\u3002\u5148\u4e0a\u5730\u8868\uff0c\u8fdb\u5165\u4eba\u7c7b\u57ce\u9547\uff0c\u7136\u540e\u4fbf\u80fd\u96c7\u9a6c\u8f66\uff0c\u8d70\u5546\u8def\uff0c\u65e2\u8212\u9002\u53c8\u5feb\u6377\uff0c\u5927\u5927\u8282\u7ea6\u65f6\u95f4\uff0c\u6700\u91cd\u8981\u7684\u662f\u5b89\u5168\u3002\u867d\u7136\u8def\u4e0a\u53ef\u80fd\u6709\u5f3a\u76d7\uff0c\u4f46\u603b\u6bd4\u5e7d\u6697\u5730\u57df\u91cc\u5c42\u51fa\u4e0d\u7a77\u7684\u602a\u7269\u597d\u5bf9\u4ed8\u591a\u4e86\u3002</p><p>\u4e3b\u610f\u6253\u5b9a\uff0c\u8299\u857e\u72c4\u4fbf\u5f00\u59cb\u51c6\u5907\u6cd5\u672f\uff0c\u5148\u7528\u5927\u91cf\u7684\u8ff7\u96fe\u6676\u6784\u5efa\u6210\u9b54\u6cd5\u9635\uff0c\u963b\u9694\u5730\u8109\u8f90\u5c04\u7684\u626d\u66f2\u5f71\u54cd\uff0c\u7136\u540e\u7528\u9884\u8a00\u672f\u7a7f\u900f\u5ca9\u5c42\uff0c\u9010\u6b65\u5206\u6790\u5b9a\u4f4d\u76ee\u6807\uff0c\u786e\u4fdd\u4e0d\u4f1a\u51fa\u73b0\u610f\u5916\u53d8\u6545\u3002\u6700\u540e\u7531\u743c\u6069\u548c\u51db\u5408\u529b\u5b8c\u6210\u4e34\u65f6\u7684\u4f20\u9001\u95e8\uff0c\u867d\u7136\u53ea\u80fd\u7ef4\u6301\u4e94\u5206\u949f\uff0c\u4f46\u5df2\u7ecf\u8db3\u591f\u4e86\u3002</p><p>\u548c\u77ee\u4eba\u9053\u522b\uff0c\u743c\u6069\u4e00\u884c\u4eba\u9c7c\u8d2f\u8fdb\u5165\u4f20\u9001\u95e8\u3002\u77ed\u6682\u7684\u6655\u7729\u4e4b\u540e\uff0c\u4ed6\u4eec\u518d\u5ea6\u8e0f\u4e0a\u4e86\u575a\u5b9e\u7684\u5730\u9762\uff0c\u707f\u70c2\u7684\u9633\u5149\u4ece\u5934\u9876\u7167\u5c04\u4e0b\u6765\uff0c\u660e\u4eae\u5f97\u8ba9\u4eba\u4e00\u65f6\u95f4\u53ea\u80fd\u7d27\u95ed\u53cc\u773c\uff0c\u67d4\u548c\u7684\u98ce\u5728\u8138\u4e0a\u62c2\u8fc7\uff0c\u8ba9\u8eab\u4f53\u53d8\u5f97\u6696\u6d0b\u6d0b\u7684\uff0c\u539f\u672c\u81ea\u5e7d\u6697\u5730\u57df\u4e2d\u4fb5\u67d3\u6765\u7684\u90a3\u70b9\u9634\u5bd2\u77ac\u95f4\u51b0\u6d88\u96ea\u878d\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u7545\u3002\u743c\u6069\u6162\u6162\u9002\u5e94\u7740\u523a\u773c\u7684\u65e5\u5149\uff0c\u4e00\u70b9\u4e00\u70b9\u7741\u5f00\u773c\u775b\uff0c\u770b\u89c1\u4e86\u4e45\u8fdd\u7684\u9c9c\u8273\u8272\u5f69\uff1a\u84dd\u5929\u3001\u767d\u4e91\u3001\u8fdc\u5904\u82cd\u9752\u8272\u7684\u7fa4\u5c71\u7ef5\u5ef6\u8d77\u4f0f\uff0c\u519c\u7530\u4e2d\u7684\u91d1\u9ec4\u7a3b\u7a57\u8fce\u98ce\u6447\u6446\uff0c\u5373\u4fbf\u662f\u56e0\u4e3a\u51db\u51ac\u5c06\u81f3\u800c\u67af\u9ec4\u7684\u8870\u8349\uff0c\u6b64\u65f6\u5728\u4ed6\u770b\u6765\u90fd\u683c\u5916\u7684\u4eb2\u5207\u8bf1\u4eba\u3002</p><p>\u201c\u554a\u554a\u554a\uff01\u201d\u4ed6\u653e\u7eb5\u5730\u5927\u543c\u4e00\u58f0\uff0c\u4e3e\u8d77\u53cc\u81c2\uff0c\u201c\u7ec8\u4e8e\u53c8\u770b\u5230\u592a\u9633\u4e86\uff01\u201d</p><p>\u201c\u522b\u4eba\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u4f60\u662f\u4e2a\u7f6a\u72af\uff0c\u521a\u4ece\u5730\u7262\u91cc\u9003\u51fa\u6765\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u58f0\u97f3\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u53d6\u7b11\u7740\u743c\u6069\u7684\u5931\u6001\u3002\u4f46\u5176\u4ed6\u4eba\u5374\u6ca1\u5979\u8fd9\u822c\u6c89\u9759\uff0c\u51db\u5012\u8fd8\u7f62\u4e86\uff0c\u5979\u4e00\u5171\u53ea\u5728\u5e7d\u6697\u5730\u57df\u91cc\u5f85\u4e86\u4e24\u4e2a\u6708\u4e0d\u5230\uff0c\u6b63\u597d\u662f\u65b0\u9c9c\u611f\u521a\u8fc7\uff0c\u5c31\u5f53\u662f\u4e00\u6b21\u65c5\u6e38\uff0c\u4f46\u83ab\u5c3c\u5361\u59d0\u59b9\u5374\u5df2\u7ecf\u4f4f\u4e86\u4e00\u5e74\u591a\uff0c\u838e\u73de\u514b\u66f4\u662f\u81ea\u4ece\u201c\u6b7b\u540e\u201d\u5c31\u518d\u4e5f\u6ca1\u56de\u6765\u8fc7\uff0c\u73af\u987e\u56db\u5468\uff0c\u5fc3\u5934\u4e0d\u7531\u5f97\u90fd\u6d8c\u4e0a\u51e0\u5206\u611f\u6168\u6765\u3002</p><p>\u743c\u6069\u6240\u5728\u7684\u5730\u65b9\u662f\u4e00\u5ea7\u8352\u5e9f\u7684\u53e4\u5821\u4e2d\uff0c\u57ce\u5899\u65e9\u5c31\u5012\u584c\u5927\u534a\uff0c\u5efa\u7b51\u4e5f\u7834\u635f\u4e0d\u582a\uff0c\u5730\u52bf\u5012\u9887\u9ad8\uff0c\u4f4d\u4e8e\u4e00\u5ea7\u5c71\u5cf0\u7684\u534a\u8170\u4e0a\u3002\u636e\u8bf4\u5e38\u5e38\u95f9\u9b3c\uff0c\u6240\u4ee5\u65e0\u4eba\u6562\u63a5\u8fd1\uff0c\u518d\u8bf4\u4e5f\u6ca1\u4ec0\u4e48\u503c\u94b1\u4e1c\u897f\uff0c\u8fde\u76d7\u8d3c\u90fd\u4e0d\u5c51\u4e8e\u5149\u987e\u3002\u6b64\u65f6\u662f\u6b63\u5348\uff0c\u6df1\u79cb\u7684\u9633\u5149\u6e29\u6696\u548c\u7166\uff0c\u867d\u7136\u660e\u4eae\u5374\u7edd\u4e0d\u70bd\u70c8\uff0c\u743c\u6069\u7b49\u4eba\u7a0d\u4f5c\u4f11\u6574\uff0c\u5224\u65ad\u4e86\u4e00\u4e0b\u65b9\u4f4d\uff0c\u968f\u5373\u4fbf\u5f00\u59cb\u8d76\u8def\u3002</p><p>\u201c\u6211\u4eec\u73b0\u5728\u662f\u5728\u6cf0\u745f\u5c14\u5883\u5185\uff0c\u201d\u6885\u83f2\u65af\u6307\u7740\u5730\u56fe\u8bf4\uff0c\u201c\u5f80\u5317\u65b9\u8d70\uff0c\u5c31\u662f\u745e\u5854\u6c76\u3002\u201d</p><p>\u745e\u5854\u6c76\u662f\u6cf0\u745f\u5c14\u5e1d\u56fd\u7684\u7b2c\u4e8c\u5927\u57ce\u5e02\uff0c\u62e5\u6709\u8fd1\u4e5d\u4e07\u4eba\u53e3\uff0c\u800c\u4e14\u5360\u636e\u5546\u8def\u8981\u9053\uff0c\u4e43\u662f\u4e00\u5ea7\u8d38\u6613\u91cd\u9547\u3002\u6309\u7167\u8ba1\u5212\uff0c\u53ea\u8981\u5230\u4e86\u745e\u5854\u6c76\uff0c\u4fbf\u80fd\u591f\u96c7\u5230\u9a6c\u8f66\uff0c\u6cbf\u7740\u5546\u8def\u4e00\u76f4\u5317\u4e0a\uff0c\u7a7f\u8fc7\u5b89\u59c6\u5e1d\u56fd\uff0c\u9014\u7ecf\u535a\u5f97\u4e4b\u95e8\uff0c\u4e00\u8def\u524d\u5f80\u5315\u9996\u6ee9\u9547\uff0c\u7136\u540e\u5c31\u50cf\u743c\u6069\u548c\u6885\u83f2\u65af\u4e0a\u6b21\u8d70\u8fc7\u7684\u90a3\u6837\uff0c\u8f6c\u4e0a\u9ed1\u8def\uff0c\u8e0f\u5165\u5927\u6c99\u6f20\u2014\u2014\u9014\u4e2d\u4f1a\u7ecf\u8fc7\u72ec\u89d2\u517d\u4e4b\u6d41\uff0c\u5965\u6c83\u90a3\u4e2a\u80a5\u5deb\u5996\u5c31\u4f4f\u5728\u90a3\u91cc\uff0c\u743c\u6069\u6253\u7b97\u987a\u8def\u53bb\u62dc\u8bbf\u4e00\u4e0b\u3002</p><p>\u4ece\u4ed6\u4eec\u73b0\u5728\u6240\u5728\u7684\u5730\u65b9\u5230\u745e\u5854\u6c76\uff0c\u5927\u7ea6\u9700\u8981\u4e00\u5929\u534a\u7684\u65f6\u95f4\uff0c\u800c\u4e14\u591a\u662f\u4e18\u9675\u5730\u8c8c\uff0c\u9053\u8def\u66f2\u6298\uff0c\u4e5f\u4e0d\u4f1a\u6709\u5546\u65c5\u901a\u8fc7\uff0c\u6ca1\u6cd5\u642d\u8f66\u3002\u5e7d\u6697\u5730\u57df\u7684\u8725\u8734\u56e0\u4e3a\u65e0\u6cd5\u627f\u53d7\u65e5\u5149\u7167\u5c04\uff0c\u6ca1\u529e\u6cd5\u5e26\u4e0a\u5730\u8868\uff0c\u743c\u6069\u7b49\u4eba\u4e8e\u662f\u4fbf\u53ea\u80fd\u6b65\u884c\uff0c\u838e\u73de\u514b\u5219\u5f20\u5f00\u8760\u7ffc\u98de\u4e0a\u9ad8\u7a7a\uff0c\u5bdf\u770b\u7740\u5de6\u8fd1\u5730\u5f62\uff0c\u9632\u6b62\u4ec0\u4e48\u7a81\u53d1\u610f\u5916\u3002\u552f\u4e00\u7684\u5750\u9a91\u9732\u4e1d\u96c5\uff0c\u88ab\u51db\u548c\u8299\u857e\u72c4\u5360\u636e\u4e86\uff0c\u4e24\u4f4d\u7f8e\u5c11\u5973\u4e00\u524d\u4e00\u540e\u5750\u5728\u94f6\u8272\u72ec\u89d2\u517d\u4e0a\uff0c\u770b\u8d77\u6765\u5341\u5206\u7684\u8d4f\u5fc3\u60a6\u76ee\u3002</p><p>\u8299\u857e\u72c4\u8eab\u4f53\u5c1a\u672a\u5b8c\u5168\u5eb7\u590d\uff0c\u9a91\u72ec\u89d2\u517d\u4ee3\u6b65\u5f88\u6b63\u5e38\uff0c\u51db\u662f\u6bd4\u8f83\u61d2\uff0c\u4e5f\u6324\u5728\u4e00\u8d77\u3002\u8fd9\u5012\u7f62\u4e86\uff0c\u53cd\u6b63\u9732\u4e1d\u96c5\u591f\u5927\uff0c\u7136\u800c\u8ba9\u743c\u6069\u6124\u6168\u7684\u662f\uff1a\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u8299\u857e\u72c4\u4e5f\u662f\u90aa\u795e\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u72ec\u89d2\u517d\u5c45\u7136\u4e5f\u6beb\u4e0d\u4ecb\u610f\u5730\u5141\u8bb8\u5979\u9a91\u7740\uff0c\u5374\u5bf9\u81ea\u5df1\u59cb\u7ec8\u62b1\u6709\u660e\u663e\u654c\u610f\uff0c\u8fd9\u672a\u514d\u592a\u4e0d\u516c\u5e73\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u4f60\u786e\u5b9a\u9732\u4e1d\u96c5\u771f\u662f\u96cc\u6027\uff1f\u201d\u743c\u6069\u6253\u91cf\u7740\u72ec\u89d2\u517d\uff0c\u201c\u6211\u73b0\u5728\u4e25\u91cd\u6000\u7591\u5b83\u662f\u96c4\u7684\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u96c4\u6027\u72ec\u89d2\u517d\u6709\u987b\u9aef\uff0c\u96cc\u6027\u6ca1\u6709\uff0c\u8fd9\u4e48\u660e\u663e\u7684\u7279\u5f81\u4f60\u90fd\u5206\u4e0d\u51fa\uff1f\u201d</p><p>\u201c\u90a3\u5979\u4e3a\u4ec0\u4e48\u53ea\u8ba9\u5973\u5b69\u5b50\u9a91\uff0c\u96be\u9053\u548c\u51db\u4e00\u6837\uff0c\u6027\u53d6\u5411\u6bd4\u8f83\u7279\u522b\uff1f\u201d</p><p>\u201c\u2026\u2026\u4f60\u6574\u5929\u90fd\u5728\u60f3\u4e9b\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u201c\u5f53\u7136\u662f\u60f3\u4f60\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u542b\u7b11\u4e0d\u8bed\u3002</p><p>\u8fd1\u4e24\u4e2a\u6708\u6765\uff0c\u5979\u7559\u5728\u6668\u70bc\u6f5c\u5fc3\u953b\u70bc\uff0c\u5df2\u7ecf\u80fd\u591f\u521d\u6b65\u638c\u63e1\u201c\u6df1\u5ea6\u6697\u793a\u201d\u7684\u6280\u5de7\u95e8\u9053\uff0c\u4e50\u89c2\u4f30\u8ba1\u7684\u8bdd\uff0c\u518d\u7528\u4e24\u5230\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u5de9\u56fa\uff0c\u5e94\u8be5\u5c31\u53ef\u4ee5\u5f00\u59cb\u5c1d\u8bd5\u6d88\u5316\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u4e86\u3002\u5979\u5929\u8d4b\u6770\u51fa\uff0c\u53c8\u662f\u795e\u5b50\uff0c\u5bf9\u4e8e\u795e\u529b\u7684\u719f\u6089\u548c\u9002\u5e94\u7a0b\u5ea6\u8fdc\u5728\u743c\u6069\u4e4b\u4e0a\uff0c\u5982\u679c\u4e00\u5207\u987a\u5229\u7684\u8bdd\uff0c\u4e09\u5e74\u4e94\u8f7d\u5c31\u6709\u671b\u6210\u529f\u3002</p><p>\u6362\u53e5\u8bdd\u8bf4\uff0c\u518d\u8fc7\u4e09\u5e74\u4e94\u8f7d\uff0c\u743c\u6069\u5c31\u53ef\u4ee5\u771f\u6b63\u54c1\u5c1d\u5230\u6885\u83f2\u65af\u3002\u542c\u8d77\u6765\u6709\u70b9\u4e45\uff0c\u4f46\u8003\u8651\u5230\u5979\u624d\u521a\u8fc7\u5341\u516d\u5c81\u751f\u65e5\uff0c\u90a3\u4e5f\u5c31\u7b97\u4e0d\u5f97\u4ec0\u4e48\u4e86\u3002</p><p>\u5de6\u53f3\u65e0\u4e8b\uff0c\u519b\u90e8\u7684\u547d\u4ee4\u4e0a\u4e5f\u6ca1\u6709\u6307\u5b9a\u671f\u9650\uff0c\u843d\u5f97\u8f7b\u677e\u3002\u4f17\u4eba\u4e14\u884c\u4e14\u4f4f\uff0c\u6cbf\u9014\u8d4f\u73a9\uff0c\u8c08\u8bba\u6545\u4e8b\uff0c\u5929\u6c14\u53c8\u5341\u5206\u6674\u597d\uff0c\u4eff\u4f5b\u51fa\u5916\u90ca\u6e38\u4e00\u822c\uff0c\u867d\u662f\u6b65\u884c\uff0c\u5374\u4e5f\u4e0d\u89c9\u75b2\u5026\u3002\u6cf0\u745f\u5c14\u5927\u534a\u9762\u79ef\u7684\u56fd\u571f\u90fd\u88ab\u68ee\u6797\u6240\u8986\u76d6\uff0c\u98ce\u666f\u79c0\u4e3d\u6021\u4eba\uff0c\u53e4\u8ff9\u751a\u591a\uff0c\u6885\u83f2\u65af\u548c\u51db\u90fd\u662f\u5c11\u5e74\u65f6\u5c31\u6e38\u5386\u56db\u65b9\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u867d\u7136\u4ece\u6ca1\u6765\u8fc7\u6cf0\u745f\u5c14\uff0c\u4f46\u5bf9\u8fd9\u4e2a\u56fd\u5bb6\u7684\u60c5\u51b5\u8fd8\u662f\u9887\u6709\u4e86\u89e3\u7684\u3002</p><p>\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u6cf0\u745f\u5c14\u5728\u4e0a\u53e4\u65f6\u671f\u662f\u7cbe\u7075\u5efa\u7acb\u7684\u5927\u5e1d\u56fd\u4e4b\u4e00\uff0c\u540e\u6765\u5386\u7ecf\u5de8\u4eba\u3001\u9f99\u65cf\u3001\u5de8\u7075\u548c\u77ee\u4eba\u7684\u7edf\u6cbb\uff0c\u6700\u540e\u53d8\u6210\u4e86\u73b0\u5728\u4ee5\u4eba\u7c7b\u4e3a\u4e3b\u4f53\u7684\u56fd\u5bb6\u3002\u8981\u8bba\u5386\u53f2\u60a0\u4e45\u7684\u8bdd\uff0c\u5b83\u5728\u8d39\u4f26\u5927\u9646\u80fd\u6392\u8fdb\u524d\u5341\u540d\uff0c\u800c\u8981\u8bba\u56fd\u571f\u9762\u79ef\u7684\u8bdd\uff0c\u5b83\u80fd\u6392\u8fdb\u524d\u4e94\u3002\u53ea\u662f\u6cf0\u745f\u5c14\u867d\u5927\uff0c\u5374\u662f\u6218\u4e71\u4e0d\u4f11\u7eb7\u4e89\u4e0d\u6b62\uff0c\u957f\u671f\u5904\u4e8e\u8bf8\u4faf\u5272\u636e\u6df7\u6218\u7684\u72b6\u6001\uff0c\u6c11\u4f17\u751f\u6d3b\u81ea\u7136\u4e5f\u56f0\u82e6\u4e0d\u582a\u3002\u6240\u5e78\u73b0\u4efb\u541b\u4e3b\u83b1\u8335\u987f\u5973\u7687\u5728\u63d0\u5c14\u6559\u4f1a\u5e2e\u52a9\u4e4b\u4e0b\uff0c\u7ecf\u8fc7\u4e09\u5e74\u5185\u6218\uff0c\u6210\u529f\u7ed3\u675f\u4e86\u5e1d\u56fd\u7684\u5206\u88c2\u72b6\u6001\uff0c\u91cd\u65b0\u786e\u7acb\u7687\u5ba4\u7684\u6743\u5a01\u3002\u4f5c\u4e3a\u7edf\u6cbb\u8005\u800c\u8a00\uff0c\u83b1\u8335\u987f\u5973\u7687\u9887\u5177\u82f1\u624d\uff0c\u81ea\u5979\u5373\u4f4d\u4ee5\u6765\uff0c\u6cf0\u745f\u5c14\u56fd\u529b\u6e10\u6e10\u6062\u590d\uff0c\u518d\u4e0d\u590d\u5f80\u65e5\u9893\u5510\uff0c\u9887\u89c1\u5174\u76db\u3002</p><p>\u743c\u6069\u7b49\u4eba\u4e0b\u4e00\u7ad9\u7684\u76ee\u6807\u745e\u5854\u6c76\uff0c\u539f\u672c\u5c5e\u4e8e\u6cf0\u745f\u5c14\u5317\u65b9\u7684\u5b89\u59c6\u5e1d\u56fd\u3002\u4e09\u5e74\u524d\uff081370DR\uff09\uff0c\u5b89\u59c6\u53d1\u751f\u5927\u89c4\u6a21\u5185\u4e71\uff0c\u83b1\u8335\u987f\u5973\u7687\u8d81\u706b\u6253\u52ab\uff0c\u541e\u5e76\u4e86\u745e\u5854\u6c76\u8fd9\u5ea7\u5546\u8d38\u91cd\u9547\u3002\u6b64\u4e3e\u9020\u6210\u4e24\u56fd\u5173\u7cfb\u7684\u6025\u5267\u6076\u5316\uff0c\u4f46\u56e0\u4e3a\u5b89\u59c6\u76ee\u524d\u5185\u4e71\u5c1a\u672a\u5e73\u606f\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u5c40\u9762\u6682\u65f6\u8fd8\u6ca1\u6076\u5316\u5230\u8981\u5f00\u6218\u7684\u5730\u6b65\uff0c\u53ea\u662f\u8fb9\u5883\u6765\u5f80\u4f1a\u6709\u4e9b\u9ebb\u70e6\u3002</p><p>\u63d0\u5c14\u6559\u4f1a\u5728\u6cf0\u745f\u5c14\u5730\u533a\u539f\u672c\u5c31\u6839\u57fa\u9887\u4e3a\u6df1\u539a\uff0c\u83b1\u8335\u987f\u5973\u7687\u80fd\u591f\u767b\u4e0a\u5e1d\u4f4d\uff0c\u7fe6\u706d\u5272\u636e\uff0c\u80cc\u540e\u4e5f\u662f\u5f97\u5230\u4e86\u4ed6\u4eec\u7684\u5927\u529b\u76f8\u52a9\u548c\u652f\u6301\u3002\u5973\u7687\u5373\u4f4d\u4e4b\u540e\uff0c\u63d0\u5c14\u6559\u4f1a\uff08\u5305\u62ec\u63d0\u5c14\u7684\u4ece\u795e\u548c\u76df\u53cb\u6258\u59c6\u3001\u4f0a\u5c14\u739b\u7279\u3001\u6d77\u59c6\u3001\u8d5b\u96c5\u8309\u8299\u7684\u6559\u4f1a\uff09\u76f8\u5e94\u83b7\u5f97\u4e86\u6781\u5927\u53d1\u5c55\uff0c\u5982\u4eca\u5df2\u7ecf\u57fa\u672c\u5784\u65ad\u4e86\u8fd9\u4e2a\u5e9e\u5927\u5e1d\u56fd\u7684\u6c11\u4f17\u4fe1\u4ef0\uff0c\u4fe8\u7136\u6210\u4e3a\u56fd\u6559\u3002\u6cf0\u745f\u5c14\u5728\u56db\u5e74\u524d\u9881\u5e03\u7684\u300a\u5e1d\u56fd\u6c11\u6cd5\u300b\u548c\u300a\u5e1d\u56fd\u5211\u6cd5\u300b\uff0c\u5176\u4e2d\u6709\u8d85\u8fc7\u56db\u6210\u7684\u6761\u6b3e\u5b8c\u5168\u7167\u6284\u6216\u8005\u6a21\u4eff\u81ea\u63d0\u5c14\u6559\u4f1a\u7f16\u64b0\u7684\u300a\u65e0\u5c3d\u6cd5\u5178\u300b\uff0c\u8bae\u4f1a\u3001\u4e2d\u592e\u653f\u5e9c\u548c\u6cd5\u9662\u4e2d\u7684\u91cd\u8981\u5e2d\u4f4d\u5f88\u591a\u90fd\u88ab\u63d0\u5c14\u6559\u4f1a\u7684\u795e\u804c\u4eba\u5458\u548c\u7cbe\u82f1\u4fe1\u5f92\u6240\u5360\u636e\uff0c\u519b\u961f\u4e2d\u7684\u4e0d\u5c11\u519b\u5b98\u4e5f\u51fa\u8eab\u4e8e\u63d0\u5c14\u3001\u6d77\u59c6\u6216\u8005\u6258\u59c6\u6559\u4f1a\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u6cf0\u745f\u5c14\u5176\u5b9e\u5df2\u7ecf\u7b49\u4e8e\u662f\u4f60\u4eec\u6559\u4f1a\u5728\u7edf\u6cbb\u4e86\uff1f\u201d</p><p>\u743c\u6069\u968f\u53e3\u548c\u6885\u83f2\u65af\u5f00\u7740\u73a9\u7b11\uff0c\u4ed6\u5bf9\u63d0\u5c14\u6559\u4f1a\u5e76\u65e0\u53cd\u611f\uff0c\u53ea\u662f\u6f5c\u610f\u8bc6\u91cc\u6bd4\u8f83\u6392\u65a5\u201c\u653f\u6559\u5408\u4e00\u201d\u8fd9\u79cd\u4f53\u5236\uff0c\u8bed\u6c14\u4e2d\u4e0d\u514d\u5c31\u6709\u4e9b\u63f6\u63c4\u7684\u5473\u9053\u3002\u6885\u83f2\u65af\u5fae\u89c9\u4e0d\u5feb\uff0c\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u597d\u5417\uff1f\u201d\u5979\u53cd\u95ee\uff0c\u201c\u5185\u6218\u5e73\u606f\uff0c\u5272\u636e\u6d88\u6cef\uff0c\u56fd\u5bb6\u7edf\u4e00\uff0c\u6c11\u4f17\u5b89\u5c45\u4e50\u4e1a\uff0c\u8fd9\u4e9b\u90fd\u662f\u6446\u5728\u9762\u524d\u7684\u4e8b\u5b9e\u5427\u3002\u201d</p><p>\u201c\u8fd9\u662f\u6ca1\u9519\u5566\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u53ef\u662f\u827e\u5f25\u8587\uff0c\u4f60\u96be\u9053\u4e0d\u89c9\u5f97\uff0c\u5b97\u6559\u4fe1\u4ef0\uff0c\u4e0d\u5e94\u8be5\u8fc7\u591a\u5e72\u6d89\u4e16\u4fd7\u653f\u6743\u4e48\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u53d8\u8d28\u7684\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u6014\u4f4f\uff0c\u5176\u5b9e\u8981\u8bba\u8bc1\u8fd9\u4e2a\u8bdd\u9898\u5e76\u4e0d\u96be\uff0c\u4f8b\u5b50\u4e00\u5927\u5806\u2014\u2014\u7136\u800c\u90a3\u90fd\u662f\u53d1\u751f\u5728\u5730\u7403\u4e0a\u7684\u4e8b\u60c5\uff0c\u6839\u672c\u6ca1\u6cd5\u5bf9\u6885\u83f2\u65af\u8bf4\u3002\u81f3\u4e8e\u8fd9\u4e2a\u4e16\u754c\u7684\u5386\u53f2\uff0c\u4ed6\u53c8\u4e0d\u600e\u4e48\u719f\u6089\uff0c\u4e00\u65f6\u95f4\u627e\u4e0d\u5230\u652f\u6301\u7684\u4f8b\u8bc1\u3002\u201c\u6743\u529b\u4f1a\u4ee4\u4eba\u8150\u5316\uff0c\u201d\u743c\u6069\u6700\u540e\u8bf4\uff0c\u201c\u4ece\u65e0\u4f8b\u5916\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u201d\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u597d\u5427\uff0c\u5c31\u7b97\u662f\u4f60\u8bf4\u5f97\u5bf9\uff0c\u4f46\u90a3\u53c8\u5982\u4f55\u5462\uff1f\u8fd9\u4e16\u754c\u4e0a\uff0c\u603b\u662f\u6709\u56fd\u5bb6\uff0c\u603b\u662f\u6709\u653f\u5e9c\uff0c\u603b\u662f\u6709\u6743\u529b\uff0c\u9700\u8981\u4eba\u6765\u638c\u63e1\u3002\u65e2\u7136\u6743\u529b\u5982\u6b64\u5bb9\u6613\u4ee4\u4eba\u8150\u5316\uff0c\u90a3\u4e48\u4e0e\u5176\u8ba9\u5b83\u843d\u5165\u610f\u5fd7\u8584\u5f31\u7684\u51e1\u4eba\u624b\u4e2d\uff0c\u4e3a\u4f55\u4e0d\u5f52\u4e8e\u6559\u4f1a\uff0c\u5f52\u4e8e\u795e\u660e\uff1f\u6216\u8005\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u201d\u5979\u7a0d\u7a0d\u987f\u4e86\u987f\uff0c\u201c\u6211\u4eec\u8d23\u65e0\u65c1\u8d37\u3002\u201d</p><p>\u201c\u5514\uff0c\u4f60\u8fd9\u4e48\u8bf4\u597d\u50cf\u4e5f\u6709\u70b9\u9053\u7406\uff0c\u4f46\u6211\u8fd8\u662f\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u52b2\uff0c\u201d\u743c\u6069\u82e6\u7b11\uff0c\u8fd9\u4e9b\u653f\u6cbb\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ed6\u5176\u5b9e\u5e76\u4e0d\u611f\u5174\u8da3\uff0c\u53ea\u662f\u968f\u53e3\u8bf4\u8bf4\u7f62\u4e86\u3002\u5982\u4eca\u6885\u83f2\u65af\u53cd\u9a73\uff0c\u518d\u7ec6\u60f3\u8d77\u6765\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u65e0\u9053\u7406\u3002\u5018\u82e5\u662f\u5728\u5730\u7403\u4e0a\uff0c\u653f\u6559\u5408\u4e00\uff0c\u4f3c\u4e4e\u5f88\u5c11\u6709\u6210\u529f\u7684\u4f8b\u5b50\uff0c\u7136\u800c\u8fd9\u662f\u5728\u8d39\u4f26\uff0c\u795e\u660e\u771f\u5b9e\u5b58\u5728\uff0c\u53ef\u4ee5\u5e72\u6d89\u51e1\u95f4\uff0c\u6559\u4f1a\u5f71\u54cd\u6df1\u8fdc\uff0c\u4eba\u4eba\u90fd\u662f\u4fe1\u5f92\uff0c\u60c5\u5f62\u53c8\u6709\u4e0d\u540c\uff0c\u4e0d\u80fd\u7b80\u5355\u5730\u7c7b\u6bd4\u3002</p><p>\u6885\u83f2\u65af\u7684\u610f\u601d\uff0c\u5176\u5b9e\u8bf4\u5f97\u7b80\u5355\u70b9\uff0c\u5c31\u662f\u8bf4\u65e0\u8bba\u4ec0\u4e48\u793e\u4f1a\uff0c\u4ec0\u4e48\u4f53\u5236\uff0c\u603b\u6709\u7edf\u6cbb\u7684\u6743\u529b\uff0c\u603b\u9700\u8981\u6709\u4eba\u53bb\u638c\u63e1\uff0c\u53bb\u884c\u4f7f\u3002\u90a3\u4e48\u4e0e\u5176\u4ea4\u7ed9\u51e1\u4eba\uff0c\u4e0d\u5982\u5f52\u4e8e\u795e\u7947\uff0c\u7531\u795e\u7947\u6765\u4f5c\u4e3a\u81f3\u9ad8\u7684\u201c\u738b\u8005\u201d\u3002\u5f53\u7136\u5979\u6307\u7684\u662f\u63d0\u5c14\uff0c\u4e0d\u4f1a\u662f\u73ed\u6069\u3001\u838e\u5c14\u8fd9\u79cd\u90aa\u795e\u3002</p><p>\u5728\u5730\u7403\u4e0a\uff0c\u6559\u7687\u5b8c\u5168\u6709\u53ef\u80fd\u8150\u8d25\u5815\u843d\uff1b\u4f46\u5728\u8fd9\u4e2a\u4e16\u754c\uff0c\u5927\u4e3b\u6559\u7edd\u4e0d\u53ef\u80fd\u80cc\u53db\u795e\u7947\u3002\u800c\u795e\u7947\u662f\u4fe1\u4ef0\u7684\u805a\u5408\uff0c\u7406\u5ff5\u7684\u5177\u73b0\uff0c\u62e5\u6709\u51e1\u4eba\u65e0\u53ef\u6bd4\u62df\u7684\u667a\u6167\u548c\u5a01\u80fd\uff0c\u53c8\u4e0d\u4f1a\u50cf\u51e1\u4eba\u4e00\u6837\u4efb\u6027\u5984\u4e3a\uff0c\u968f\u5fc3\u6240\u6b32\uff0c\u795e\u7947\u88ab\u795e\u7947\u81ea\u8eab\u6240\u9650\u5236\u3002\u4e2d\u56fd\u7684\u53e4\u4eba\u68a6\u60f3\u5723\u8d24\u541b\u4e3b\uff0c\u67cf\u62c9\u56fe\u5728\u7406\u60f3\u56fd\u4e2d\u5bc4\u5e0c\u671b\u4e8e\u54f2\u4eba\u738b\uff0c\u8fd9\u79cd\u60f3\u6cd5\u5728\u5730\u7403\u4e0a\u81ea\u7136\u662f\u5b8c\u5168\u7684\u68a6\u5453\uff0c\u7136\u800c\u5728\u8fd9\u4e2a\u4e16\u754c\u4e2d\uff0c\u5374\u662f\u5927\u6709\u53ef\u80fd\u7684\u3002\u795e\u660e\u5e76\u975e\u5168\u77e5\u5168\u80fd\uff0c\u5f53\u7136\u4e5f\u6709\u65e0\u6cd5\u89e3\u51b3\u7684\u96be\u9898\uff0c\u65e0\u6cd5\u5316\u89e3\u7684\u56f0\u5883\uff0c\u4f46\u76f8\u6bd4\u8d77\u51e1\u4eba\u800c\u8a00\uff0c\u4f3c\u4e4e\u786e\u5b9e\u662f\u66f4\u597d\u7684\u9009\u62e9\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u4f9d\u65e7\u4e0d\u80fd\u771f\u6b63\u8ba4\u540c\u6885\u83f2\u65af\u7684\u770b\u6cd5\u3002</p><p>\u201c\u795e\u660e\u6e90\u81ea\u51e1\u4eba\uff0c\u4f46\u4ed6\u4eec\u7ec8\u7a76\u5df2\u7ecf\u4e0d\u662f\u51e1\u4eba\uff0c\u6211\u65e0\u6cd5\u76f8\u4fe1\u4ed6\u4eec\u80fd\u591f\u4f5c\u4e3a\u771f\u6b63\u5408\u9002\u7684\u541b\u4e3b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u738b\u8005\u4e0d\u9700\u8981\u662f\u5723\u4eba\u3002\u6216\u8005\u8bf4\uff0c\u8d8a\u5b8c\u7f8e\u7684\u4eba\uff0c\u8d8a\u4e0d\u9002\u5408\u4f5c\u4e3a\u738b\u8005\u3002\u201d</p><p>\u6885\u83f2\u65af\u76b1\u7709\uff0c\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d</p><p>\u743c\u6069\u6c89\u541f\u7740\uff0c\u770b\u770b\u5de6\u53f3\uff0c\u838e\u73de\u514b\u8fd8\u5728\u9ad8\u7a7a\u4e2d\u98de\u7fd4\u5bdf\u770b\uff0c\u51db\u6b63\u5174\u9ad8\u91c7\u70c8\u5730\u548c\u8299\u857e\u72c4\u8bf4\u7740\u4ec0\u4e48\uff0c\u8299\u8389\u5a05\u4e5f\u4e0d\u65f6\u63d2\u4e0a\u51e0\u53e5\u8bdd\uff0c\u90fd\u6ca1\u6ce8\u610f\u743c\u6069\u548c\u6885\u83f2\u65af\u7684\u8c08\u8bba\u3002\u201c\u6253\u4e2a\u6bd4\u65b9\uff0c\u827e\u5f25\u8587\uff0c\u4ec5\u4ec5\u662f\u4e2a\u6bd4\u65b9\uff0c\u201d\u743c\u6069\u6162\u6162\u8bf4\uff0c\u201c\u4f60\u89c9\u5f97\u6211\u548c\u4f60\u4e24\u4eba\u4e4b\u4e2d\uff0c\u8c01\u66f4\u9002\u5408\u4f5c\u4e3a\u738b\u8005\u5462\uff1f\u201d</p><p>\u6885\u83f2\u65af\u5ae3\u7136\u5fae\u7b11\uff0c\u89c9\u5f97\u8fd9\u4e2a\u95ee\u9898\u6839\u672c\u65e0\u9700\u56de\u7b54\u3002</p><p>\u743c\u6069\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u522b\u90a3\u4e48\u81ea\u4fe1\uff0c\u827e\u5f25\u8587\u3002\u6211\u627f\u8ba4\uff0c\u4f60\u6bd4\u6211\u4f18\u79c0\uff0c\u6bd4\u6211\u89c1\u591a\u8bc6\u5e7f\uff0c\u6bd4\u6211\u610f\u5fd7\u575a\u5b9a\uff0c\u52c7\u6562\u679c\u51b3\uff0c\u5f53\u7136\u4e5f\u6bd4\u6211\u516c\u6b63\u65e0\u79c1\uff0c\u6e05\u5ec9\u5be1\u6b32\u3002\u4f60\u662f\u6211\u89c1\u8fc7\u7684\u6700\u5b8c\u7f8e\u7684\u4eba\uff0c\u4f46\u6b63\u662f\u8fd9\u4efd\u5b8c\u7f8e\uff0c\u53ea\u6015\u53cd\u800c\u4f1a\u59a8\u788d\u4f60\u6210\u4e3a\u4f18\u79c0\u7684\u738b\u8005\u5462\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u6885\u83f2\u65af\u79c0\u7709\u4e00\u6311\uff0c\u5979\u5012\u65e0\u610f\u4e8e\u771f\u505a\u4ec0\u4e48\u738b\u8005\uff0c\u65e2\u6ca1\u5174\u8da3\u4e5f\u4e0d\u5c51\u2014\u2014\u5018\u82e5\u771f\u6709\u6b64\u610f\uff0c\u5979\u65e9\u5c31\u662f\u795e\u660e\u4e86\u3002\u4f46\u542c\u743c\u6069\u8fd9\u4e48\u8bf4\uff0c\u5374\u662f\u9887\u6709\u4e9b\u4e0d\u670d\u6c14\uff0c\u597d\u80dc\u5fc3\u8d77\uff0c\u8981\u8bba\u4e2a\u7a76\u7adf\u3002</p><p>\u201c\u5f88\u7b80\u5355\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u592a\u4f18\u79c0\uff0c\u8d85\u51fa\u5bfb\u5e38\uff0c\u7136\u800c\u4f60\u82e5\u662f\u738b\u8005\uff0c\u7edf\u6cbb\u7684\u5374\u4f9d\u65e7\u662f\u666e\u901a\u4eba\u3002\u4f60\u8d8a\u662f\u5b8c\u7f8e\uff0c\u5c31\u8ddd\u79bb\u4ed6\u4eec\u8d8a\u9065\u8fdc\uff0c\u8d8a\u65e0\u6cd5\u771f\u6b63\u5730\u8ba4\u540c\u548c\u7406\u89e3\u4ed6\u4eec\u2014\u2014\u8fd9\u96be\u9053\u4e0d\u662f\u963b\u788d\u5417\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u58f0\u4e00\u7b11\uff0c\u201c\u83ab\u975e\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u56e0\u4e3a\u6211\u81ea\u5df1\u6bcf\u5929\u53ea\u9700\u8981\u7761\u7720\u56db\u4e2a\u5c0f\u65f6\uff0c\u90a3\u4e48\u6211\u5c31\u4f1a\u9881\u5e03\u5f8b\u6cd5\uff0c\u547d\u4ee4\u5168\u56fd\u81e3\u6c11\u7684\u4f11\u606f\u65f6\u95f4\u4e0d\u51c6\u8d85\u8fc7\u56db\u4e2a\u5c0f\u65f6\uff1f\u201d</p><p>\u201c\u4f60\u5f53\u7136\u4e0d\u4f1a\u72af\u8fd9\u79cd\u4f4e\u7ea7\u9519\u8bef\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u53ef\u662f\u827e\u5f25\u8587\uff0c\u6211\u5192\u6627\u5730\u95ee\u4e00\u53e5\uff1a\u4f60\u89c9\u5f97\u81ea\u5df1\u5408\u7fa4\u5417\uff1f\u201d</p><p>\u6885\u83f2\u65af\u4e00\u6014\u3002</p><p>\u201c\u4f60\u7684\u6027\u683c\uff0c\u4ee5\u4e2a\u4eba\u800c\u8a00\u8fd1\u4e4e\u5b8c\u7f8e\uff0c\u6211\u60f3\u5c31\u7b97\u662f\u795e\u7947\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u6bd4\u4f60\u66f4\u4f18\u79c0\u591a\u5c11\u5427\uff0c\u81f3\u5c11\u6211\u662f\u60f3\u50cf\u4e0d\u51fa\u4e86\u3002\u4f46\u4e3a\u4ec0\u4e48\u4f60\u7684\u670b\u53cb\u8fd9\u6837\u5c11\u5462\uff1f\u4f60\u662f\u4e0d\u613f\u4ea4\u53cb\uff1f\u4e0d\u5c51\uff1f\u8fd8\u662f\u4e0d\u80fd\uff1f\u53ea\u6015\u8fd9\u4e16\u754c\u4e0a\uff0c\u771f\u6b63\u80fd\u88ab\u4f60\u770b\u91cd\uff0c\u88ab\u4f60\u653e\u5728\u773c\u91cc\u7684\uff0c\u4e5f\u6ca1\u6709\u51e0\u4e2a\u4eba\u5427\u2014\u2014\u4f60\u7ad9\u5f97\u592a\u9ad8\u4e86\uff0c\u6ca1\u529e\u6cd5\u628a\u81ea\u5df1\u771f\u6b63\u5730\u2018\u653e\u4e0b\u53bb\u2019\u554a\uff0c\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u7406\u60f3\u7684\u738b\u8005\u5e94\u8be5\u662f\u4ec0\u4e48\u6837\uff0c\u4f46\u6211\u76f8\u4fe1\u603b\u4e0d\u4f1a\u662f\u9ad8\u9ad8\u5728\u4e0a\u7684\u4fef\u89c6\u8005\u3002\u4ed6\u5fc5\u987b\u65e0\u6bd4\u7684\u4f18\u79c0\uff0c\u8d85\u51fa\u51e1\u4fd7\uff0c\u4f46\u540c\u65f6\u53c8\u548c\u666e\u901a\u4eba\u65e0\u5f02\uff0c\u548c\u5149\u540c\u5c18\u3002\u795e\u660e\u561b\uff0c\u201d\u4ed6\u7b11\u4e86\u7b11\uff0c\u201c\u6050\u6015\u592a\u5b8c\u7f8e\u4e86\uff0c\u7ad9\u5f97\u592a\u9ad8\u4e86\uff0c\u8ddd\u79bb\u592a\u8fdc\u4e86\uff0c\u5bf9\u4e8e\u8fd9\u79cd\u5b58\u5728\uff0c\u6211\u5b81\u53ef\u656c\u800c\u8fdc\u4e4b\u3002\u201d</p><p>\u201c\u5c31\u8ba9\u795e\u7947\u7684\u5f52\u795e\u7947\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8ba9\u51e1\u4eba\u7684\u5f52\u51e1\u4eba\u3002\u201d</p></article>"}}}]);
