(window.webpackJsonp=window.webpackJsonp||[]).push([[1594],{1617:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e09\u8282 \u95ef\u5165\u5e7b\u5883",content:"<article><h2>\u7b2c\u56db\u5341\u4e09\u8282 \u95ef\u5165\u5e7b\u5883</h2><p>\u8fd9\u573a\u6709\u5173\u795e\u660e\u3001\u738b\u8005\u548c\u653f\u6cbb\u7684\u8fa9\u8bba\u5e76\u6ca1\u6709\u4ec0\u4e48\u7ed3\u679c\uff0c\u56e0\u4e3a\u53cc\u65b9\u5176\u5b9e\u90fd\u6ca1\u6709\u592a\u591a\u7684\u5174\u8da3\uff0c\u672c\u6765\u5c31\u662f\u4e34\u65f6\u8d77\u610f\uff0c\u968f\u53e3\u8c08\u53ca\u3002\u65e0\u8bba\u662f\u743c\u6069\u4e5f\u597d\uff0c\u6885\u83f2\u65af\u4e5f\u597d\uff0c\u90fd\u4ece\u6ca1\u60f3\u8fc7\u81ea\u5df1\u6709\u671d\u4e00\u65e5\u4f1a\u6210\u4e3a\u541b\u4e3b\uff0c\u7edf\u6cbb\u81e3\u6c11\u3002\u540c\u65f6\u4ed6\u4eec\u4e5f\u90fd\u65e0\u6cd5\u8bf4\u670d\u5bf9\u65b9\u3002</p><p>\u5bf9\u4e8e\u6885\u83f2\u65af\u6765\u8bf4\uff0c\u201c\u653f\u6559\u5408\u4e00\uff0c\u53c2\u4e0e\u4e16\u4fd7\u653f\u6743\u63a8\u884c\u6559\u4e49\u201d\uff0c\u8fd9\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u57fa\u672c\u65b9\u7565\u4e4b\u4e00\u3002\u6700\u5178\u578b\u7684\u4f8b\u5b50\u5982\u773c\u524d\u7684\u6cf0\u745f\u5c14\uff0c\u5982\u585e\u65af\u514b\uff0c\u5982\u56e0\u5e03\u56fe\uff0c\u6216\u8005\u7ed3\u675f\u6218\u4e71\uff0c\u91cd\u5f52\u548c\u5e73\uff0c\u6216\u8005\u62b5\u5fa1\u4fb5\u7565\uff0c\u6297\u51fb\u5916\u4fae\uff0c\u6216\u8005\u6587\u660e\u590d\u5174\uff0c\u7e41\u8363\u660c\u76db\uff0c\u63d0\u5c14\u6559\u4f1a\u5728\u5176\u4e2d\u90fd\u8d77\u4e86\u91cd\u8981\u7684\u4f5c\u7528\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u5927\u9646\u4e0a\u5f88\u591a\u56fd\u5bb6\u5728\u5236\u5b9a\u5f8b\u6cd5\u65f6\uff0c\u90fd\u4f1a\u6709\u610f\u65e0\u610f\u5730\u53c2\u7167\u501f\u9274\u63d0\u5c14\u6559\u4f1a\u7f16\u64b0\u7684\u300a\u65e0\u5c3d\u6cd5\u5178\u300b\uff0c\u8bae\u4f1a\u3001\u6cd5\u9662\u548c\u653f\u5e9c\u673a\u6784\u4e2d\u63d0\u5c14\u4fe1\u5f92\u7684\u6bd4\u4f8b\u8d8a\u6765\u8d8a\u5927\uff0c\u751a\u81f3\u76f4\u63a5\u6267\u638c\u6700\u9ad8\u6743\u67c4\u3002\u8fd1\u5341\u5e74\u6765\uff0c\u4e0d\u5c11\u56fd\u5bb6\u76f8\u7ee7\u901a\u8fc7\u6cd5\u6848\uff0c\u7981\u6b62\u6216\u9650\u5236\u5974\u96b6\u8d38\u6613\uff0c\u53d6\u6d88\u5974\u96b6\u5236\u5ea6\uff0c\u8fd9\u4e5f\u6b63\u662f\u63d0\u5c14\u6559\u4f1a\u5728\u80cc\u540e\u63a8\u52a8\u4e4b\u529f\u3002\u51e1\u6b64\u79cd\u79cd\uff0c\u90fd\u662f\u6446\u5728\u9762\u524d\u7684\u4e8b\u5b9e\uff0c\u65e0\u4e0d\u8bc1\u660e\u4e86\u6559\u4f1a\u65b9\u7565\u7684\u6b63\u786e\u6027\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u743c\u6069\u4e5f\u660e\u767d\uff0c\u5728\u8fd9\u4e2a\u6709\u771f\u795e\u7684\u4e16\u754c\u4e2d\uff0c\u6885\u83f2\u65af\u7684\u770b\u6cd5\uff0c\u4e0d\u4f46\u4ee3\u8868\u4e86\u63d0\u5c14\u6559\u4f1a\u7684\u7406\u5ff5\uff0c\u751a\u81f3\u662f\u7edd\u5927\u591a\u6570\u4eba\u7684\u770b\u6cd5\uff0c\u5e76\u4e14\u786e\u5b9e\u662f\u6709\u5176\u9053\u7406\u7684\u3002\u8bf4\u5f97\u900f\u5f7b\u70b9\uff0c\u63d0\u5c14\u5c31\u662f\u4e00\u4f4d\u82f1\u660e\u777f\u667a\u53c8\u6709\u65e0\u4e0a\u5a01\u80fd\u7684\u738b\u8005\uff0c\u5bf9\u5c5e\u4e0b\u7684\u63a7\u5236\u529b\u8fdc\u80dc\u51e1\u4eba\u541b\u4e3b\uff0c\u800c\u4e14\u66f4\u662f\u957f\u751f\u4e0d\u673d\u7684\u5b58\u5728\uff0c\u4e43\u662f\u5b8c\u7f8e\u7684\u201c\u54f2\u4eba\u738b\u201d\u3002\u867d\u7136\u8fd9\u4e16\u754c\u4e0a\u4e0d\u5b58\u5728\u5b8c\u7f8e\u7684\u5236\u5ea6\uff0c\u63d0\u5c14\u4e5f\u4e0d\u53ef\u80fd\u5168\u77e5\u5168\u80fd\uff0c\u6559\u4f1a\u7edf\u6cbb\u4e5f\u4e0d\u53ef\u80fd\u5c31\u5b8c\u5168\u675c\u7edd\u8150\u8d25\uff0c\u4f46\u76f8\u5bf9\u800c\u8a00\u5374\u662f\u66f4\u4f73\u7684\u9009\u62e9\u3002</p><p>\u6ca1\u6709\u6700\u597d\uff0c\u4f46\u5374\u6709\u6700\u4e0d\u574f\uff1b\u4e0d\u6c42\u5b8c\u7f8e\uff0c\u53ea\u6c42\u6709\u6240\u8fdb\u6b65\u3002</p><p>\u53ef\u60dc\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u4ed6\u5728\u6f5c\u610f\u8bc6\u91cc\u5bf9\u4e8e\u795e\u660e\u6709\u4e00\u79cd\u6839\u6df1\u8482\u56fa\u7684\u6392\u65a5\u548c\u53cd\u611f\uff0c\u4e0d\u559c\u6b22\u5b97\u6559\u52bf\u529b\u8fc7\u591a\u63d2\u624b\u4e16\u4fd7\uff0c\u6240\u4ee5\u672c\u80fd\u5730\u5c31\u4f1a\u627e\u5404\u79cd\u7406\u7531\u6765\u5426\u8ba4\u3002\u8fd9\u5df2\u7ecf\u65e0\u5173\u9053\u7406\u7684\u5bf9\u9519\uff0c\u66f4\u591a\u662f\u4e00\u79cd\u6267\u5ff5\u4e86\u3002</p><p>\u201c\u6211\u6e38\u5386\u56db\u65b9\uff0c\u4eb2\u773c\u6240\u89c1\u7684\u4e8b\u5b9e\u662f\u5728\u6559\u4f1a\u7684\u63a8\u52a8\u4e4b\u4e0b\uff0c\u6574\u4e2a\u4e16\u754c\u5728\u5411\u7740\u7406\u60f3\u7684\u65b9\u5411\u9010\u6e10\u524d\u8fdb\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u4e0d\u662f\u5f88\u597d\u7684\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u201c\u4f46\u4e0d\u5e94\u8be5\u4ecb\u5165\u592a\u6df1\u3002\u201d\u743c\u6069\u575a\u6301\u3002</p><p>\u201c\u4ecb\u5165\u4e0d\u6df1\uff0c\u5c31\u6ca1\u6709\u529b\u91cf\u3002\u6ca1\u6709\u529b\u91cf\uff0c\u4e00\u5207\u4fbf\u662f\u7a7a\u8c08\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e0d\u5360\u636e\u4f4d\u7f6e\uff0c\u53c8\u5982\u4f55\u80fd\u63a8\u884c\u5b97\u65e8\uff1f\u4e0d\u638c\u63e1\u529b\u91cf\uff0c\u53c8\u5982\u4f55\u80fd\u4fdd\u969c\u5b9e\u65bd\uff1f\u201d</p><p>\u8bdd\u8bf4\u5230\u8fd9\u91cc\u5c31\u518d\u4e5f\u8fdb\u884c\u4e0d\u4e0b\u53bb\uff0c\u53cc\u65b9\u8fe5\u7136\u76f8\u5f02\u7684\u4eba\u751f\u7ecf\u5386\u3001\u6559\u80b2\u80cc\u666f\uff0c\u6ce8\u5b9a\u4e86\u4ed6\u4eec\u5728\u8fd9\u4e2a\u8bdd\u9898\u4e0a\u5f88\u96be\u8fbe\u6210\u5171\u8bc6\u3002\u7eb5\u7136\u90fd\u662f\u806a\u660e\u800c\u7406\u667a\u7684\u4eba\uff0c\u6709\u4e9b\u9053\u7406\u4e5f\u662f\u600e\u4e48\u90fd\u8bf4\u4e0d\u901a\u7684\u3002</p><p>\u201c\u4f60\u4eec\u771f\u65e0\u804a\uff0c\u201d\u51db\u5728\u80cc\u540e\u8bf4\uff0c\u201c\u8fd9\u4e48\u597d\u7684\u98ce\u666f\uff0c\u8c08\u8fd9\u4e48\u6c89\u95f7\u7684\u8bdd\u9898\u3002\u6709\u7a7a\u626f\u8fd9\u4e9b\u4e0d\u7740\u8fb9\u9645\u7684\uff0c\u8fd8\u4e0d\u5982\u60f3\u60f3\u4eca\u665a\u5728\u54ea\u91cc\u8fc7\u591c\u3002\u201d</p><p>\u4ed6\u4eec\u6b64\u65f6\u662f\u5728\u8352\u91ce\u4e2d\uff0c\u5929\u8272\u5df2\u7ecf\u6e10\u6e10\u6c89\u6697\u4e0b\u6765\uff0c\u5915\u9633\u897f\u5782\uff0c\u653e\u773c\u671b\u53bb\u6ca1\u6709\u4eba\u70df\u3002\u91ce\u5916\u5bbf\u8425\u5012\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53cd\u6b63\u4e5f\u643a\u5e26\u4e86\u7761\u888b\uff0c\u53ea\u662f\u51db\u559c\u6b22\u8212\u9002\uff0c\u7231\u7761\u61d2\u89c9\uff0c\u4e0d\u514d\u5c31\u6709\u4e9b\u6392\u65a5\u3002\u6885\u83f2\u65af\u770b\u4e86\u770b\u5730\u56fe\uff0c\u8bf4\u524d\u9762\u5e94\u8be5\u6709\u4e00\u5904\u6751\u5e84\uff0c\u53ef\u4ee5\u53bb\u501f\u5bbf\u3002</p><p>\u6b63\u8bf4\u8bdd\u95f4\uff0c\u9ad8\u7a7a\u4e2d\u9b45\u9b54\u4f20\u6765\u8baf\u606f\uff0c\u8bf4\u662f\u897f\u5357\u65b9\u5411\u6709\u4e00\u961f\u4eba\u9a6c\u6b63\u671d\u8fd9\u8fb9\u8fc7\u6765\u3002\u5404\u4eba\u8b66\u6212\uff0c\u8fc7\u4e86\u5341\u591a\u5206\u949f\uff0c\u6765\u4eba\u8fd1\u524d\uff0c\u5374\u662f\u516b\u4e2a\u9a91\u58eb\uff0c\u5168\u90fd\u7a7f\u7740\u9501\u7532\uff0c\u643a\u5e26\u5175\u5668\uff0c\u884c\u52a8\u4e4b\u95f4\u660e\u663e\u662f\u8bad\u7ec3\u6709\u7d20\u7684\u6b66\u8005\u3002\u9886\u5934\u7684\u662f\u4e2a\u4f53\u683c\u58ee\u7855\u7684\u4e2d\u5e74\u4eba\uff0c\u5934\u53d1\u6709\u4e9b\u82b1\u767d\uff0c\u770b\u89c1\u743c\u6069\u4e00\u884c\u4fbf\u7b56\u9a6c\u8fc7\u6765\uff0c\u201c\u6253\u6270\u8bf8\u4f4d\uff0c\u201d\u4ed6\u9887\u4e3a\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u8bf7\u95ee\u53bb\u5854\u683c\u6751\u662f\u5f80\u8fd9\u4e2a\u65b9\u5411\u6ca1\u9519\u5427\u3002\u201d</p><p>\u5854\u683c\u6751\u6b63\u662f\u6885\u83f2\u65af\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4e2a\u6751\u5e84\uff0c\u201c\u5730\u56fe\u4e0a\u662f\u8fd9\u6837\uff0c\u201d\u5979\u56de\u7b54\uff0c\u7565\u5e26\u6b49\u610f\u5730\u5fae\u7b11\uff0c\u201c\u6211\u4e5f\u662f\u7b2c\u4e00\u6b21\u5230\u8fd9\u91cc\u6765\uff0c\u4e0d\u6562\u786e\u5b9a\u5462\u3002\u201d</p><p>\u201c\u90a3\u5c31\u5e94\u8be5\u6ca1\u9519\u4e86\uff0c\u591a\u8c22\u3002\u201d</p><p>\u9a91\u58eb\u5728\u9a6c\u4e0a\u6b20\u8eab\u9053\u8c22\uff0c\u968f\u5373\u5e26\u7740\u540c\u4f34\u5954\u9a70\u800c\u53bb\u3002\u743c\u6069\u6ce8\u610f\u5230\u4ed6\u7684\u9501\u7532\u5de6\u80f8\u53e3\u90e8\u4f4d\u4e0a\u7f00\u7740\u4e00\u4e2a\u5fbd\u8bb0\uff1a\u91d1\u8272\u7684\u5929\u5e73\uff0c\u4e00\u8fb9\u6258\u76d8\u4e2d\u653e\u7740\u957f\u5251\uff0c\u4e00\u8fb9\u653e\u7740\u6218\u9524\uff0c\u4e0b\u65b9\u6709\u51e0\u4e2a\u5b57\u6bcd\u7f29\u5199\u3002\u201c\u4ed6\u662f\u4f60\u4eec\u6559\u4f1a\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u201c\u800c\u4e14\u662f\u4e2a\u9ad8\u7ea7\u6cd5\u8b66\uff0c\u4ece\u6765\u7684\u65b9\u5411\u4e0a\u770b\uff0c\u5e94\u8be5\u662f\u8fbe\u52b3\u739b\u57ce\u7684\u3002\u201d</p><p>\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u4e0d\u4ec5\u8981\u6c42\u80fd\u529b\u8d85\u7fa4\uff0c\u529f\u52cb\u5353\u8457\uff0c\u540c\u65f6\u8fd8\u8981\u7cbe\u901a\u5404\u56fd\u7684\u5b98\u50da\u4f53\u7cfb\u3001\u5f8b\u6cd5\u884c\u653f\u77e5\u8bc6\u3002\u6cf0\u745f\u5c14\u6b64\u5730\u7684\u53f8\u6cd5\u7cfb\u7edf\u53c8\u57fa\u672c\u90fd\u88ab\u63d0\u5c14\u6559\u4f1a\u6240\u638c\u63e1\uff0c\u6885\u83f2\u65af\u6240\u4ee5\u80fd\u4ece\u5fbd\u7ae0\u76f4\u63a5\u5224\u65ad\u51fa\u5bf9\u65b9\u8eab\u4efd\u3002\u201c\u5947\u602a\u554a\uff0c\u201d\u5979\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u4ec0\u4e48\u4e8b\u9700\u8981\u516b\u4e2a\u6cd5\u8b66\u3002\u201d</p><p>\u6cd5\u8b66\u4e00\u8d77\u51fa\u52a8\uff0c\u6446\u660e\u4e86\u529e\u516c\u7684\u6837\u5b50\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u662f\u6e38\u5c71\u73a9\u6c34\uff0c\u800c\u662f\u53bb\u6293\u4eba\u3002\u636e\u6885\u83f2\u65af\u89c2\u5bdf\uff0c\u8fd9\u516b\u4e2a\u6cd5\u8b66\u5e94\u8be5\u90fd\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u5b88\u536b\u51fa\u8eab\uff0c\u63a5\u53d7\u8fc7\u4e25\u683c\u7684\u6b66\u6280\u8bad\u7ec3\uff0c\u5e94\u8be5\u80fd\u4f7f\u7528\u7b80\u5355\u7684\u795e\u672f\uff0c\u643a\u5e26\u7684\u6b66\u5668\u548c\u8eab\u4e0a\u7684\u76d4\u7532\u4e5f\u90fd\u662f\u9644\u6709\u9b54\u6cd5\u7684\u7cbe\u5236\u54c1\u3002\u5854\u683c\u6751\u4ece\u8d44\u6599\u4e0a\u770b\u53ea\u4e0d\u8fc7\u662f\u4e2a\u504f\u8fdc\u5c0f\u6751\uff0c\u5c45\u6c11\u4e0d\u8fc7\u767e\u4f59\u4eba\uff0c\u80fd\u72af\u4ec0\u4e48\u6848\u5b50\uff0c\u53c8\u80fd\u6709\u4ec0\u4e48\u68d8\u624b\u7684\u4eba\u7269\uff0c\u9700\u8981\u8fd9\u4e48\u90d1\u91cd\u5bf9\u5f85\uff1f\u96be\u4e0d\u6210\u6cf0\u745f\u5c14\u7684\u8b66\u529b\u5df2\u7ecf\u5bcc\u88d5\u5230\u53ef\u4ee5\u968f\u610f\u6325\u970d\u7684\u7a0b\u5ea6\u4e86\u4e0d\u6210\u3002</p><p>\u201c\u8fd9\u51e0\u4e2a\u4eba\u5f88\u5f3a\u4e48\uff1f\u201d\u743c\u6069\u95ee\uff0c\u4ed6\u773c\u5149\u4e0d\u591f\uff0c\u770b\u4e0d\u51fa\u4e00\u4e2a\u4eba\u7684\u6b66\u6280\u9ad8\u4f4e\u3002</p><p>\u6885\u83f2\u65af\u7565\u7565\u6c89\u541f\uff0c\u201c\u5982\u679c\u9650\u5236\u573a\u5730\uff0c\u6b63\u9762\u5bf9\u654c\uff0c\u8054\u624b\u56f4\u653b\u7684\u8bdd\uff0c\u201d\u5979\u8c28\u614e\u5730\u8bf4\uff0c\u201c\u6211\u672a\u5fc5\u80fd\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d</p><p>\u6362\u53e5\u8bdd\u8bf4\uff0c\u6cd5\u8b66\u4eec\u9884\u8ba1\u8981\u5bf9\u4ed8\u7684\u654c\u4eba\uff0c\u5f88\u6709\u53ef\u80fd\u662f\u548c\u6885\u83f2\u65af\u5927\u81f4\u4e00\u4e2a\u5c42\u7ea7\u7684\u3002\u5f53\u7136\uff0c\u53ef\u4ee5\u80af\u5b9a\u662f\u4eba\u7c7b\u4e0d\u662f\u4ec0\u4e48\u602a\u517d\uff0c\u5426\u5219\u5c31\u4e0d\u662f\u6cd5\u8b66\uff0c\u800c\u662f\u6d3e\u519b\u961f\u6765\u4e86\u3002</p><p>\u8fd9\u4e2a\u65b0\u53d1\u73b0\u9887\u6709\u4e9b\u4ee4\u4eba\u60ca\u8bb6\uff0c\u4f46\u5e76\u6ca1\u6709\u5f71\u54cd\u884c\u7a0b\uff0c\u53cd\u800c\u5f15\u8d77\u4e86\u5927\u5bb6\u7684\u597d\u5947\u5fc3\u3002\u53cd\u6b63\u4e5f\u6ca1\u522b\u5904\u53ef\u53bb\uff0c\u7d22\u6027\u524d\u5f80\u770b\u770b\u3002\u53c8\u8d70\u4e86\u5927\u7ea6\u4e00\u4e2a\u591a\u5c0f\u65f6\uff0c\u524d\u65b9\u5df2\u7ecf\u9690\u7ea6\u80fd\u770b\u89c1\u6751\u5e84\u7684\u8f6e\u5ed3\uff0c\u708a\u70df\u5728\u665a\u971e\u4e2d\u8885\u8885\u5347\u8d77\uff0c\u663e\u7136\u5c31\u662f\u5854\u683c\u6751\u4e86\u3002</p><p>\u201c\u597d\u50cf\u6ca1\u4ec0\u4e48\u52a8\u9759\u554a\uff0c\u90a3\u4e9b\u6cd5\u8b66\u529e\u5b8c\u4e8b\u4e86\uff1f\u201d</p><p>\u743c\u6069\u5600\u5495\u7740\uff0c\u8ba9\u838e\u73de\u514b\u5148\u6253\u63a2\u4e00\u4e0b\u60c5\u51b5\uff0c\u9b45\u9b54\u9886\u547d\u800c\u53bb\uff0c\u534a\u5c0f\u65f6\u540e\u8fd4\u56de\uff0c\u8bf4\u6751\u5185\u5168\u65e0\u534a\u70b9\u5f02\u72b6\uff0c\u5c45\u6c11\u4eec\u57fa\u672c\u90fd\u5728\u51c6\u5907\u665a\u9910\uff0c\u6c14\u6c1b\u5b89\u5b81\u7965\u548c\uff0c\u770b\u4e0d\u51fa\u6709\u4e1d\u6beb\u66fe\u7ecf\u60ca\u52a8\u3001\u6253\u6597\u3001\u6df7\u4e71\u4e4b\u7c7b\u7684\u8ff9\u8c61\uff0c\u4e5f\u6ca1\u53d1\u73b0\u90a3\u51e0\u4e2a\u6cd5\u8b66\u6216\u8005\u4ed6\u4eec\u7684\u5750\u9a91\u4e4b\u7c7b\u3002</p><p>\u201c\u83ab\u975e\u90a3\u4e9b\u6cd5\u8b66\u8d70\u9519\u8def\u4e86\uff1f\u201d\u743c\u6069\u731c\u6d4b\u3002</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u4e00\u8def\u4e0a\u6709\u9a6c\u8e44\u7684\u75d5\u8ff9\uff0c\u662f\u671d\u7740\u90a3\u4e2a\u6751\u5e84\u7684\u65b9\u5411\u53bb\u7684\uff0c\u4ed6\u4eec\u80af\u5b9a\u6ca1\u8d70\u9519\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u8d70\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fc7\u53bb\u770b\u770b\u3002\u201d</p><p>\u5730\u5f62\u9887\u4e3a\u590d\u6742\uff0c\u867d\u7136\u6751\u5e84\u5df2\u7ecf\u8fdc\u8fdc\u51fa\u73b0\u5728\u89c6\u91ce\u4e2d\uff0c\u4f46\u771f\u6b63\u8d70\u8d77\u8def\u6765\u5927\u7ea6\u8fd8\u9700\u8981\u4e00\u4e2a\u5c0f\u65f6\u624d\u80fd\u62b5\u8fbe\uff0c\u4fd7\u8bed\u6240\u8c13\u671b\u5c71\u8dd1\u6b7b\u9a6c\uff0c\u4fbf\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u8d70\u4e86\u4e8c\u5341\u591a\u5206\u949f\uff0c\u4ed6\u4eec\u8fdb\u5165\u4e00\u5904\u72ed\u957f\u7684\u5c71\u8c37\uff0c\u53ea\u8981\u7a7f\u8fc7\u53bb\uff0c\u524d\u65b9\u5c31\u662f\u4e00\u9a6c\u5e73\u5ddd\u7684\u8352\u539f\u4e86\u3002</p><p>\u6885\u83f2\u65af\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u540c\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u4e5f\u6b62\u4f4f\u811a\u6b65\u3002</p><p>\u201c\u9a6c\u8e44\u5370\u7a81\u7136\u6d88\u5931\u4e86\u3002\u201d\u6885\u83f2\u65af\u89c2\u5bdf\u7740\u5730\u9762\u3002</p><p>\u6d45\u6d45\u800c\u6742\u4e71\u7684\u8e44\u5370\u4e00\u76f4\u5ef6\u4f38\u5230\u8c37\u53e3\uff0c\u7136\u540e\u5c31\u51ed\u7a7a\u65ad\u6389\u4e86\uff0c\u4eff\u4f5b\u6c34\u5206\u84b8\u53d1\u4e00\u822c\uff0c\u518d\u4e5f\u770b\u4e0d\u5230\u4e00\u4e1d\u4e00\u6beb\u7684\u75d5\u8ff9\u3002</p><p>\u201c\u524d\u65b9\u4e09\u5341\u82f1\u5c3a\uff0c\u6709\u975e\u5e38\u5f3a\u7684\u9b54\u6cd5\u6ce2\u52a8\uff0c\u201d\u743c\u6069\u4f38\u624b\u81ea\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u6cd5\u6756\uff0c\u540c\u65f6\u5c06\u5965\u672f\u89c6\u89c9\u5b8c\u5168\u5f00\u542f\uff0c\u201c\u597d\u523a\u773c\u7684\u7075\u5149\u2026\u2026\u5e7b\u672f\u2026\u2026\u201d</p><p>\u201c\u4e00\u4e2a\u5f3a\u529b\u7684\u5e7b\u672f\uff0c\u201d\u4ed6\u6700\u540e\u5f97\u51fa\u7ed3\u8bba\uff0c\u201c\u5c01\u4f4f\u4e86\u8c37\u53e3\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u5e7b\u672f\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u80fd\u80af\u5b9a\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u597d\u50cf\u662f\u2018\u6d77\u5e02\u8703\u697c\u2019\u8fd9\u79cd\u5e7b\u5883\u6cd5\u672f\uff0c\u7b3c\u7f69\u4e86\u8fd9\u4e00\u6574\u5757\u533a\u57df\u2026\u2026\u4e0d\uff0c\u4e0d\u5bf9\uff0c\u6bd4\u6d77\u5e02\u8703\u697c\u660e\u663e\u66f4\u5f3a\u66f4\u590d\u6742\uff0c\u201d\u4ed6\u8fdf\u7591\u8d77\u6765\uff0c\u9ed8\u5ff5\u4e00\u53e5\u5492\u8bed\uff0c\u53cc\u773c\u7684\u77b3\u5b54\u53d8\u6210\u900f\u660e\u7684\u6de1\u7d2b\uff0c\u9690\u9690\u900f\u51fa\u661f\u8292\uff0c\u201c\u4e73\u767d\u8272\u7075\u5149\u2026\u2026\u7a7a\u95f4\u2026\u2026\u8ff7\u5bab\u672f\uff1f\u201d</p><p>\u4ed6\u5012\u5438\u4e86\u53e3\u6c14\uff0c\u201c\u8fd9\u597d\u50cf\u662f\u628a\u6d77\u5e02\u8703\u697c\u548c\u8ff7\u5bab\u672f\u878d\u5408\u5728\u4e00\u8d77\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u53ef\u80fd\u5427\uff1f\u201d\u51db\u4e5f\u5df2\u7ecf\u8d70\u8fd1\u524d\uff0c\u6b63\u597d\u542c\u5230\u743c\u6069\u7684\u8bdd\uff0c\u201c\u8fd9\u4e24\u79cd\u6cd5\u672f\u7684\u8fd0\u4f5c\u539f\u7406\u6839\u672c\u5c31\u662f\u5927\u76f8\u5f84\u5ead\uff0c\u600e\u4e48\u80fd\u540c\u65f6\u6df7\u7528\uff1f\u5982\u679c\u5e7b\u672f\u548c\u9644\u9b54\u672f\u878d\u5408\u90a3\u8fd8\u6709\u53ef\u80fd\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002\u4f46\u5206\u6790\u9b54\u6cd5\u7075\u5149\uff0c\u5f97\u51fa\u6765\u7684\u7ed3\u8bba\u2026\u2026\u5e94\u8be5\u662f\u8fd9\u6837\u6ca1\u9519\u3002\u201d</p><p>\u201c\u786e\u5b9e\u6709\u8fd9\u79cd\u624b\u6cd5\u3002\u201d\u8299\u857e\u72c4\u5728\u80cc\u540e\u8bf4\u3002</p><p>\u6240\u6709\u4eba\u90fd\u8f6c\u8fc7\u8138\u6765\u770b\u5979\uff0c\u5c0f\u5973\u5b69\u6709\u4e9b\u7d27\u5f20\uff0c\u4f46\u8fd8\u662f\u602f\u751f\u751f\u5730\u628a\u8bdd\u8bf4\u5b8c\u3002\u201c\u6211\u4ee5\u524d\u542c\u7236\u4eb2\u8bf4\u8d77\u8fc7\uff0c\u662f\u6709\u8fd9\u79cd\u5c06\u6d77\u5e02\u8703\u697c\u548c\u8ff7\u5bab\u672f\u7cc5\u5408\u5728\u4e00\u8d77\u7684\u6cd5\u672f\uff0c\u4ed6\u66fe\u7ecf\u89c1\u739b\u63d0\u514b\u548c\u74e6\u63d0\u514b\u4e24\u4f4d\u6bbf\u4e0b\u4f7f\u7528\u8fc7\u3002\u201d</p><p>\u739b\u63d0\u514b\u548c\u74e6\u63d0\u514b\u4fbf\u662f\u9634\u9b42\u57ce\u70bc\u91d1\u5b66\u9662\u7684\u90a3\u5bf9\u53cc\u80de\u80ce\u9662\u957f\uff0c\u548c\u5e03\u96f7\u7eb3\u65af\u5173\u7cfb\u4eb2\u5bc6\uff0c\u7cbe\u64c5\u70bc\u91d1\u672f\u548c\u5e7b\u672f\u3002\u8299\u857e\u72c4\u7684\u8bf4\u6cd5\uff0c\u95f4\u63a5\u8bc1\u5b9e\u743c\u6069\u7684\u5224\u65ad\uff0c\u4f46\u540c\u65f6\u4e5f\u610f\u5473\u7740\u53c8\u8981\u9762\u4e34\u65b0\u7684\u9ebb\u70e6\u4e86\u3002</p><p>\u5c06\u6d77\u5e02\u8703\u697c\u548c\u8ff7\u5bab\u672f\u7cc5\u5408\uff0c\u786e\u5b9e\u662f\u5f88\u4e86\u4e0d\u8d77\u7684\u624b\u6bb5\uff0c\u743c\u6069\u81ea\u5ea6\u80af\u5b9a\u6ca1\u8fd9\u4e2a\u9020\u8be3\uff0c\u4f46\u5982\u679c\u4ec5\u4ec5\u53ea\u662f\u8981\u907f\u5f00\uff0c\u90a3\u5e76\u4e0d\u96be\uff0c\u65e2\u7136\u5df2\u7ecf\u5bdf\u89c9\uff0c\u90a3\u4e48\u8d70\u8fdc\u8def\u7ed5\u8fc7\uff0c\u4ece\u5929\u4e0a\u98de\u8fc7\uff0c\u751a\u81f3\u76f4\u63a5\u4f20\u9001\u8fc7\u53bb\uff0c\u90fd\u662f\u5f88\u5bb9\u6613\u7684\u4e8b\u60c5\u3002\u5c31\u4ed6\u672c\u4eba\u6765\u8bf4\uff0c\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\uff0c\u4e0d\u5fc5\u62db\u60f9\u9ebb\u70e6\u4e0a\u8eab\uff0c\u7136\u800c\u6709\u6885\u83f2\u65af\u5728\u8eab\u8fb9\uff0c\u90a3\u4e48\u60c5\u51b5\u4fbf\u53c8\u4e0d\u540c\u3002</p><p>\u6276\u5371\u6d4e\u56f0\u662f\u5723\u6b66\u58eb\u7684\u672c\u804c\uff0c\u90a3\u51e0\u4e2a\u6cd5\u8b66\u663e\u7136\u662f\u88ab\u9762\u524d\u7684\u9b54\u6cd5\u56f0\u4f4f\u4e86\uff0c\u65e2\u7136\u9047\u89c1\uff0c\u90a3\u5c31\u6ca1\u6cd5\u7f6e\u8eab\u4e8b\u5916\uff0c\u8896\u624b\u65c1\u89c2\uff0c\u800c\u4e14\u4ed6\u4eec\u8fd8\u662f\u63d0\u5c14\u7684\u4fe1\u5f92\u3002\u6885\u83f2\u65af\u867d\u7136\u5df2\u7ecf\u88ab\u5f00\u9664\u6559\u7c4d\uff0c\u4f46\u65e0\u8bba\u5979\u81ea\u5df1\u8fd8\u662f\u743c\u6069\uff0c\u5fc3\u5e95\u4f9d\u65e7\u90fd\u8fd8\u662f\u9ed8\u8ba4\u5979\u662f\u63d0\u5c14\u5723\u6b66\u58eb\uff0c\u4e0d\u66fe\u6539\u53d8\u3002\u66f4\u4f55\u51b5\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5341\u6709\u516b\u4e5d\u548c\u201c\u7f6a\u72af\u201d\u6709\u5173\u3002</p><p>\u201c\u6d77\u5e02\u8703\u697c\u8fd9\u79cd\u5927\u89c4\u6a21\u6539\u53d8\u533a\u57df\u5f62\u8c8c\u7684\u5e7b\u672f\uff0c\u4e0d\u53ef\u80fd\u4e34\u65f6\u8d77\u610f\u5c31\u80fd\u65bd\u5c55\uff0c\u4e8b\u5148\u8981\u82b1\u957f\u65f6\u95f4\u51c6\u5907\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u4e5f\u4e0d\u53ef\u80fd\u662f\u6c38\u6052\u751f\u6548\uff0c\u6301\u7eed\u65f6\u95f4\u81f3\u591a\u4e0d\u8fc7\u4e09\u56db\u5929\u3002\u201d</p><p>\u8a00\u4e0b\u4e4b\u610f\uff0c\u8fd9\u662f\u4e2a\u8fd1\u51e0\u65e5\u624d\u521a\u5e03\u7f6e\u7684\u9677\u9631\uff0c\u5176\u76ee\u7684\u663e\u7136\u662f\u4e13\u95e8\u9488\u5bf9\u8fd9\u4e9b\u6cd5\u8b66\uff0c\u5426\u5219\u8fd9\u79cd\u7a77\u4e61\u50fb\u58e4\uff0c\u8352\u90ca\u91ce\u5916\uff0c\u54ea\u4e2a\u5bb6\u4f19\u5403\u9971\u4e86\u6ca1\u4e8b\u5e72\uff0c\u5728\u8c37\u53e3\u8d39\u8fd9\u4e48\u5927\u529b\u6c14\u5f04\u8fd9\u4e2a\u3002\u800c\u8c01\u4f1a\u8fd9\u4e48\u505a\uff0c\u7b54\u6848\u4e0d\u95ee\u53ef\u77e5\u3002</p><p>\u771f\u662f\u7684\uff0c\u4e3a\u4ec0\u4e48\u8d70\u5230\u54ea\u91cc\u90fd\u4f1a\u9047\u5230\u9ebb\u70e6\u3002</p><p>\u743c\u6069\u4f4e\u58f0\u53f9\u6c14\uff0c\u7136\u540e\u5c06\u8fd9\u4e2a\u60f3\u6cd5\u629b\u4e4b\u8111\u540e\uff0c\u201c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u95ee\uff0c\u201c\u4f60\u77e5\u9053\u600e\u4e48\u7834\u89e3\u5b83\u5417\uff1f\u201d</p><p>\u5982\u679c\u53ea\u662f\u5355\u7eaf\u7684\u5e7b\u672f\uff0c\u53ef\u4ee5\u8003\u8651\u7528\u771f\u77e5\u672f\u6765\u5c1d\u8bd5\u514b\u5236\uff0c\u4f46\u53c8\u7cc5\u5408\u4e86\u8ff7\u5bab\u672f\u5728\u5185\uff0c\u8fd9\u5c31\u5df2\u7ecf\u8d85\u51fa\u4e86\u743c\u6069\u548c\u51db\u7684\u77e5\u8bc6\u4f53\u7cfb\uff0c\u65e0\u6cd5\u7528\u6240\u5b66\u8fc7\u7684\u9b54\u6cd5\u7406\u8bba\u6765\u5206\u6790\u63a8\u65ad\u3002\u8299\u857e\u72c4\u65e2\u7136\u66fe\u7ecf\u542c\u7236\u4eb2\u8bf4\u8d77\u8fc7\u8fd9\u4e2a\u6cd5\u672f\uff0c\u6216\u8bb8\u6709\u529e\u6cd5\u5e94\u4ed8\u3002\u800c\u4e14\u5979\u662f\u9884\u8a00\u5e08\uff0c\u7b97\u8d77\u6765\u6b63\u662f\u5e7b\u672f\u7684\u514b\u661f\u3002</p><p>\u542c\u5230\u743c\u6069\u7684\u95ee\u9898\uff0c\u5c0f\u5973\u5b69\u9762\u9732\u96be\u8272\uff0c\u4f46\u5728\u52aa\u529b\u56de\u5fc6\u4e86\u534a\u5929\u4e4b\u540e\uff0c\u5979\u7ec8\u4e8e\u8fd8\u662f\u63d0\u4f9b\u4e86\u6709\u4ef7\u503c\u7684\u4fe1\u606f\u3002\u201c\u5bf9\u4e86\uff0c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u201d\u5979\u773c\u524d\u4e00\u4eae\uff0c\u201c\u7236\u4eb2\u5f53\u65f6\u8bf4\u8fc7\uff0c\u8fd9\u4e2a\u6cd5\u672f\u8981\u76f4\u63a5\u6d88\u89e3\u662f\u5f88\u96be\u7684\uff0c\u4f46\u6709\u4e00\u79cd\u65b9\u6cd5\u53ef\u4ee5\u5c1d\u8bd5\uff0c\u5c31\u662f\u6253\u5012\u65bd\u6cd5\u8005\u3002\u201d</p><p>\u201c\u65bd\u6cd5\u8005\u53c8\u4e0d\u5728\u8fd9\u91cc\uff0c\u600e\u4e48\u6253\u5012\u4ed6\u3002\u201d</p><p>\u5728\u53d1\u73b0\u8e4a\u8df7\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u5c31\u5df2\u7ecf\u4ed4\u7ec6\u89c2\u5bdf\u56db\u5468\uff0c\u672a\u89c1\u6709\u4efb\u4f55\u57cb\u4f0f\u8ff9\u8c61\uff0c\u800c\u838e\u73de\u514b\u4e5f\u4e00\u76f4\u5728\u9ad8\u7a7a\u76d1\u89c6\uff0c\u6ca1\u770b\u5230\u6709\u5176\u4ed6\u4eba\u3002\u65bd\u6cd5\u8005\u5e03\u7f6e\u4e86\u8fd9\u4e2a\u9677\u9631\uff0c\u96be\u9053\u8fd8\u4f1a\u7559\u5728\u539f\u5730\u4e0d\u6210\uff0c\u53ea\u6015\u65e9\u5c31\u9003\u4e4b\u592d\u592d\uff0c\u8fdc\u9041\u5343\u91cc\u4e4b\u5916\u4e86\u3002</p><p>\u201c\u4e0d\uff0c\u4e0d\u662f\uff0c\u65bd\u6cd5\u8005\u80af\u5b9a\u5c31\u5728\u5176\u4e2d\u3002\u201d</p><p>\u8299\u857e\u72c4\u89e3\u91ca\uff0c\u8fd9\u4e2a\u6cd5\u672f\u662f\u6d77\u5e02\u8703\u697c\u548c\u8ff7\u5bab\u672f\u7684\u7cc5\u5408\uff0c\u80fd\u591f\u521b\u9020\u51fa\u4e00\u7247\u5f3a\u529b\u5e7b\u5883\uff0c\u8ba9\u4eba\u9677\u5165\u5176\u4e2d\uff0c\u65e0\u6cd5\u8131\u8eab\u3002\u4f46\u5b83\u4e5f\u6709\u5176\u9650\u5236\uff0c\u4fbf\u662f\u65bd\u6cd5\u8005\u5fc5\u987b\u4eb2\u81ea\u5728\u5185\u90e8\u4e3b\u6301\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u6e83\u6563\u3002\u800c\u53ea\u8981\u95ef\u5165\u5e7b\u5883\uff0c\u6253\u5012\u65bd\u6cd5\u8005\uff0c\u6cd5\u672f\u81ea\u7136\u88ab\u6d88\u89e3\uff0c\u8fd9\u662f\u6bd4\u8f83\u86ee\u5e72\uff0c\u4f46\u4e5f\u662f\u6700\u6709\u6548\u7387\uff0c\u6700\u76f4\u622a\u4e86\u5f53\u7684\u65b9\u6cd5\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u65b9\u6cd5\u96be\u5ea6\u5f88\u9ad8\uff0c\u56e0\u4e3a\u5982\u679c\u9009\u62e9\u76f4\u63a5\u201c\u95ef\u5165\u201d\u7684\u8bdd\uff0c\u5c31\u76f8\u5f53\u4e8e\u5bf9\u5e7b\u5883\u7684\u201c\u8ba4\u53ef\u201d\uff0c\u800c\u8fd9\u6837\u4e00\u6765\uff0c\u5bf9\u65b9\u4fbf\u5360\u636e\u4e86\u5f88\u5f3a\u7684\u4e3b\u573a\u4f18\u52bf\u3002\u6253\u4e2a\u6bd4\u65b9\u8bf4\uff0c\u5047\u5982\u65bd\u6cd5\u8005\u64c5\u957f\u6797\u4e2d\u4f5c\u6218\uff0c\u4ed6\u4fbf\u5927\u53ef\u5c06\u5e7b\u5883\u6f14\u5316\u6210\u4e00\u7247\u8302\u5bc6\u68ee\u6797\uff0c\u800c\u95ef\u5165\u8005\u662f\u65e0\u6cd5\u6539\u53d8\u8fd9\u70b9\u7684\u3002</p><p>\u201c\u4f46\u65bd\u6cd5\u8005\u65e2\u7136\u8981\u7ef4\u6301\u5e7b\u5883\uff0c\u90a3\u4e48\u4ed6\u4e5f\u5c31\u4e0d\u53ef\u80fd\u5168\u529b\u51fa\u624b\uff0c\u5bf9\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u5bf9\u6211\u4eec\u662f\u4e00\u4e2a\u4f18\u52bf\u3002\u201d</p><p>\u201c\u55ef\uff0c\u8fd9\u662f\u6ca1\u9519\uff0c\u4f46\u4ed6\u5f88\u53ef\u80fd\u4e5f\u6709\u540c\u4f34\u5728\u5185\u534f\u52a9\u5462\uff0c\u672a\u5fc5\u662f\u5b64\u8eab\u4e00\u4eba\u3002\u201d</p><p>\u8299\u857e\u72c4\u7684\u63d0\u9192\u5f88\u6709\u9053\u7406\uff0c\u4f46\u743c\u6069\u5e76\u4e0d\u5728\u610f\u3002\u957f\u671f\u7684\u5386\u9669\uff0c\u8ba9\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u95f4\u80c6\u6c14\u5df2\u7ecf\u8fdc\u80dc\u4ece\u524d\uff0c\u5c24\u5176\u662f\u6709\u6885\u83f2\u65af\u5728\u8eab\u8fb9\u7684\u65f6\u5019\uff0c\u5f80\u5f80\u5c31\u4f1a\u89c9\u5f97\u5929\u4e0b\u867d\u5927\uff0c\u65e0\u5904\u4e0d\u53ef\u53bb\uff0c\u4ec0\u4e48\u5371\u9669\u5f3a\u654c\u90fd\u4e0d\u5fc5\u653e\u5728\u773c\u91cc\u3002\u8fd9\u6216\u8bb8\uff0c\u7b97\u662f\u4e00\u79cd\u81ea\u4fe1\u5fc3\u7684\u76f2\u76ee\u81a8\u80c0\u5427\u3002</p><p>\u5fc3\u610f\u5df2\u51b3\uff0c\u4fbf\u5f00\u59cb\u7740\u624b\u884c\u52a8\u3002\u8299\u857e\u72c4\u53d6\u51fa\u8c61\u7259\u6cd5\u6756\uff0c\u9876\u7aef\u9576\u5d4c\u7684\u4e09\u679a\u541b\u738b\u4e4b\u6cea\u4f9d\u6b21\u4eae\u8d77\uff0c\u6e05\u6f88\u5149\u8292\u4eff\u4f5b\u6708\u534e\u6d41\u6dcc\uff0c\u9010\u6e10\u6269\u6563\u5f00\u53bb\uff0c\u5728\u865a\u7a7a\u4e2d\u52fe\u52d2\u51fa\u4e00\u6247\u95e8\u6765\u3002\u8fd9\u662f\u5979\u7528\u9884\u8a00\u672f\u5f3a\u884c\u6253\u5f00\u4e86\u4e00\u4e2a\u8fdb\u5165\u5e7b\u5883\u7684\u5b89\u5168\u901a\u9053\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u53d8\u6545\uff0c\u8fd8\u53ef\u4ee5\u7531\u6b64\u9000\u51fa\u6765\uff0c\u6240\u4ee5\u5fc5\u987b\u7ef4\u6301\u4f4f\u3002\u8299\u8389\u5a05\u81ea\u7136\u5728\u65c1\u8fb9\u5b88\u62a4\u59b9\u59b9\uff0c\u838e\u73de\u514b\u4f9d\u65e7\u5728\u9ad8\u7a7a\u5de1\u903b\uff0c\u5bdf\u770b\u56db\u5468\u3002\u51db\u539f\u672c\u4e5f\u60f3\u8fdb\u5165\u5e7b\u5883\uff0c\u4f46\u88ab\u743c\u6069\u963b\u6b62\u4e86\u3002\u201c\u6211\u548c\u827e\u5f25\u8587\u8fdb\u53bb\u5c31\u884c\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u5916\u9762\u5f97\u6709\u4eba\u4fdd\u62a4\u5979\u4eec\u3002\u201d</p><p>\u8fd9\u662f\u5408\u7406\u7684\u5b89\u6392\uff0c\u51db\u6241\u6241\u5634\uff0c\u4e5f\u4fbf\u6ca1\u518d\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u201c\u8d70\u5427\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u81ea\u8170\u95f4\u53d6\u51fa\u94f6\u5251\uff0c\u5f53\u5148\u8fdb\u5165\u3002</p><p>\u743c\u6069\u7d27\u968f\u5176\u540e\uff0c\u4e24\u4eba\u8de8\u8fc7\u865a\u7a7a\u4e4b\u95e8\uff0c\u773c\u524d\u7684\u666f\u8c61\u5239\u90a3\u6d41\u8f6c\uff0c\u65e0\u6570\u5149\u5f71\u500f\u5ffd\u5206\u5408\uff0c\u6700\u7ec8\u6e10\u6e10\u7a33\u5b9a\uff0c\u7f16\u7ec7\u6f14\u5316\u51fa\u4e00\u5e55\u964c\u751f\u7684\u573a\u666f\u6765\u3002\u4ed6\u4eec\u53d1\u73b0\u81ea\u5df1\u6b63\u7ad9\u5728\u4e00\u5ea7\u5c71\u7684\u811a\u4e0b\uff0c\u6574\u9f50\u7684\u7070\u8272\u77f3\u9636\u548c\u4e24\u65c1\u7684\u53c2\u5929\u53e4\u6728\uff0c\u6784\u6210\u4e86\u68ee\u4e25\u9634\u90c1\u7684\u5c71\u9053\uff0c\u5448\u56db\u5341\u4e94\u5ea6\u89d2\u659c\u659c\u5411\u4e0a\uff0c\u873f\u8712\u66f2\u6298\uff0c\u901a\u5f80\u90a3\u9ad8\u4e0d\u53ef\u89c1\u7684\u5c71\u9876\uff0c\u9690\u6ca1\u5728\u832b\u832b\u4e91\u6d77\u4e4b\u95f4\u3002\u6709\u660e\u4eae\u523a\u773c\u7684\u5149\u8f89\u5728\u90a3\u4e91\u96fe\u4e4b\u95f4\u95ea\u70c1\u4e0d\u4f11\uff0c\u9664\u6b64\u4e4b\u5916\u5929\u5730\u95f4\u4fbf\u662f\u4e00\u7247\u7070\u8499\u8499\u7684\u6c89\u6697\u3002</p><p>\u201c\u5514\uff0c\u770b\u6765\u662f\u8981\u722c\u5c71\u4e86\u3002\u201d</p><p>\u65e0\u9700\u591a\u8bf4\uff0c\u81ea\u7136\u800c\u7136\u4fbf\u660e\u767d\u4e86\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u65bd\u6cd5\u8005\u663e\u7136\u4fbf\u5728\u5c71\u9876\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5fc5\u987b\u901a\u8fc7\u8fd9\u6f2b\u957f\u7684\u5c71\u9053\u624d\u80fd\u89c1\u5230\u4ed6\uff0c\u9664\u6b64\u4e4b\u5916\u522b\u65e0\u5176\u4ed6\u9014\u7ecf\u3002\u8fd9\u5c31\u662f\u8fd9\u4e2a\u5e7b\u5883\u7684\u201c\u89c4\u5219\u201d\uff0c\u5df2\u7ecf\u5236\u5b9a\uff0c\u4fbf\u5fc5\u987b\u9075\u5b88\uff0c\u65e0\u8bba\u521b\u9020\u8005\u8fd8\u662f\u95ef\u5165\u8005\u3002</p><p>\u201c\u8d70\uff01\u201d</p><p>\u6885\u83f2\u65af\u7b80\u6d01\u5730\u8bf4\uff0c\u50cf\u730e\u8c79\u4e00\u822c\u5954\u8dd1\u7740\u51b2\u4e0a\u5c71\u9053\uff0c\u957f\u9774\u6572\u51fb\u7740\u77f3\u9636\uff0c\u53d1\u51fa\u6e05\u8106\u800c\u6025\u4fc3\u7684\u54cd\u58f0\u3002\u51db\u51bd\u7684\u5c71\u98ce\u5728\u8033\u8fb9\u547c\u5578\u800c\u8fc7\uff0c\u522e\u5728\u8138\u4e0a\u6709\u4e9b\u751f\u75bc\uff0c\u4f46\u5979\u6beb\u4e0d\u5728\u610f\u3002\u5c71\u9053\u5f88\u957f\uff0c\u82e5\u662f\u5728\u73b0\u5b9e\u4e4b\u4e2d\uff0c\u5373\u4fbf\u4ee5\u6885\u83f2\u65af\u7684\u901f\u5ea6\u4e5f\u8981\u5927\u534a\u4e2a\u5c0f\u65f6\u624d\u80fd\u901a\u8fc7\uff0c\u4f46\u8fd9\u662f\u5728\u5e7b\u5883\u4e2d\uff0c\u65f6\u95f4\u7684\u6d41\u901d\u548c\u5916\u754c\u6709\u6240\u4e0d\u540c\u3002\u4eff\u4f5b\u53ea\u8fc7\u4e86\u5341\u5206\u949f\uff0c\u5979\u4fbf\u5df2\u7ecf\u671b\u89c1\u4e86\u5cf0\u9876\uff0c\u524d\u65b9\u4fbf\u662f\u6c49\u767d\u7389\u77f3\u7b51\u6210\u7684\u5c71\u95e8\uff0c\u79f0\u4e0d\u4e0a\u5b8f\u4f1f\u58ee\u4e3d\uff0c\u53cd\u800c\u6709\u51e0\u5206\u53e4\u65e7\u6b8b\u7834\u7684\u611f\u89c9\uff0c\u6885\u83f2\u65af\u77e5\u9053\u8fd9\u4fbf\u662f\u201c\u5165\u53e3\u201d\u4e86\u3002</p><p>\u80cc\u540e\u4f20\u6765\u7684\u811a\u6b65\u58f0\u544a\u8bc9\u5979\u743c\u6069\u5df2\u7ecf\u8d76\u4e0a\uff0c\u5723\u6b66\u58eb\u6ca1\u6709\u591a\u505a\u505c\u7559\uff0c\u5229\u7bad\u4e00\u822c\u8dc3\u8d77\u5f80\u524d\u51b2\u53bb\uff0c\u7136\u800c\u5c31\u5728\u5979\u8ddd\u79bb\u5c71\u95e8\u8fd8\u6709\u5341\u6b65\u4e4b\u9065\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u7537\u4eba\u9661\u7136\u51fa\u73b0\u3002</p><p>\u201c\u5f88\u62b1\u6b49\uff0c\u6b64\u5730\u7981\u6b62\u901a\u884c\uff0c\u201d\u4ed6\u5fae\u5fae\u7b11\u7740\uff0c\u795e\u6001\u60a0\u95f2\u6d12\u8131\uff0c\u201c\u4e24\u4f4d\uff0c\u8bf7\u56de\u5427\u3002\u201d</p></article>"}}}]);
