(window.webpackJsonp=window.webpackJsonp||[]).push([[1596],{1619:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e94\u8282 \u8d35\u65cf\u7684\u7279\u6743",content:"<article><h2>\u7b2c\u56db\u5341\u4e94\u8282 \u8d35\u65cf\u7684\u7279\u6743</h2><p>\u5360\u636e\u4e0a\u98ce\u7684\u4e00\u65b9\u4e3b\u52a8\u7f62\u624b\uff0c\u63d0\u8bae\u548c\u8c08\uff0c\u603b\u662f\u4f1a\u53d7\u5230\u6b22\u8fce\u7684\uff0c\u8fd9\u4e00\u6b21\u4e5f\u4e0d\u4f8b\u5916\u3002\u73b0\u5728\u5c71\u95e8\u524d\u7684\u5f62\u5f0f\u5df2\u7ecf\u53d8\u6210\u4e86\u4e8c\u5bf9\u4e8c\uff0c\u5251\u58eb\u5bf9\u5251\u58eb\uff0c\u5deb\u5e08\u5bf9\u5deb\u5e08\uff0c\u6885\u83f2\u65af\u65e0\u6cd5\u51fb\u8d25\u4f0a\u65af\u5854\uff0c\u800c\u743c\u6069\u81ea\u5ea6\u4e5f\u672a\u5fc5\u80fd\u80dc\u8fc7\u90a3\u4e2a\u53eb\u601d\u601d\u7684\u767d\u888d\u5973\u5b69\uff0c\u66f4\u4f55\u51b5\u5bf9\u65b9\u7684\u624b\u4e0a\uff0c\u8fd8\u6709\u90a3\u6837\u4e00\u679a\u81f3\u9ad8\u795e\u5668\u7b49\u7ea7\u7684\u6212\u6307\u3002</p><p>\u201c\u4e16\u754c\u4e0a\u7684\u795e\u5668\u867d\u7136\u591a\uff0c\u6212\u6307\u7c7b\u578b\u7684\u5374\u6781\u5c11\u542c\u8bf4\uff0c\u4f55\u51b5\u8fd8\u662f\u8fd9\u4e48\u5389\u5bb3\u7684\u8d27\u8272\u2026\u2026\u56de\u5934\u8981\u627e\u4e2a\u56fe\u4e66\u9986\u67e5\u67e5\u770b\u3002\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\u4e14\u4e22\u5230\u4e00\u8fb9\uff0c\u5148\u89e3\u51b3\u773c\u524d\u7684\u9ebb\u70e6\u518d\u8bf4\u3002\u4f0a\u65af\u5854\u7684\u610f\u601d\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u5927\u5bb6\u840d\u6c34\u76f8\u9022\uff0c\u5f80\u65e5\u65e0\u6028\u8fd1\u65e5\u65e0\u4ec7\uff0c\u6ca1\u5fc5\u8981\u6253\u751f\u6253\u6b7b\u3002\u521a\u624d\u5df2\u7ecf\u5207\u78cb\u4e00\u573a\uff0c\u96be\u5206\u80dc\u8d1f\uff0c\u65e2\u7136\u5982\u6b64\u4f55\u4e0d\u5404\u8d70\u5404\u7684\u8def\uff0c\u4e92\u4e0d\u5e72\u6d89\u7684\u597d\uff0c\u5c31\u5f53\u8fd9\u4e8b\u60c5\u6ca1\u53d1\u751f\u8fc7\u3002</p><p>\u6240\u8c13\u201c\u96be\u5206\u80dc\u8d1f\u201d\uff0c\u5176\u5b9e\u662f\u5ba2\u6c14\u7684\u8bf4\u6cd5\u3002\u8fd9\u91cc\u662f\u5bf9\u65b9\u7684\u4e3b\u573a\uff0c\u5360\u5c3d\u4e86\u5929\u65f6\u5730\u5229\uff0c\u672c\u8eab\u5b9e\u529b\u5f3a\u52b2\uff0c\u53c8\u6301\u6709\u90a3\u7b49\u5f3a\u529b\u795e\u5668\uff0c\u771f\u8981\u52a8\u8d77\u624b\u6765\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5e94\u8be5\u662f\u8d25\u591a\u80dc\u5c11\u3002\u5c31\u7b97\u628a\u51db\u5979\u4eec\u90fd\u53eb\u8fdb\u6765\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u7a33\u64cd\u80dc\u5238\u2014\u2014\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u4e2a\u53eb\u601d\u601d\u7684\u767d\u888d\u5973\u5b69\u624b\u6307\u4e0a\u7684\u9ec4\u91d1\u6212\u6307\uff0c\u743c\u6069\u7740\u5b9e\u5fcc\u60ee\u5f97\u5f88\u3002\u795e\u5668\u4ed6\u4e5f\u4e0d\u662f\u6ca1\u89c1\u8fc7\uff0c\u5965\u6c83\u7684\u85cf\u5b9d\u5e93\u91cc\u5c31\u6709\u4e03\u516b\u4ef6\uff0c\u4f46\u80fd\u591f\u4e0e\u4e4b\u76f8\u6bd4\u7684\uff0c\u90a3\u662f\u4e00\u4ef6\u90fd\u6ca1\u6709\uff0c\u5c31\u8fde\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u5723\u7269\u9ece\u660e\u4e4b\u77f3\uff0c\u6050\u6015\u90fd\u8981\u900a\u8272\u534a\u7b79\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u4e24\u4eba\u8eab\u4e0a\uff0c\u4e5f\u9887\u6709\u51e0\u4ef6\u4ef7\u503c\u4e0d\u83f2\u7684\u9b54\u6cd5\u88c5\u5907\uff0c\u4f46\u5168\u90fd\u52a0\u8d77\u6765\u4ef7\u503c\u53ea\u6015\u8fd8\u6bd4\u4e0d\u4e0a\u90a3\u679a\u6212\u6307\u7684\u5341\u5206\u4e4b\u4e00\u3002</p><p>\u57fa\u4e8e\u8fd9\u79cd\u73b0\u5b9e\u8003\u8651\uff0c\u5bf9\u4e8e\u4f0a\u65af\u5854\u7684\u63d0\u8bae\uff0c\u743c\u6069\u5176\u5b9e\u662f\u5f88\u8d5e\u6210\u7684\uff0c\u4ed6\u672c\u6765\u5c31\u4e0d\u592a\u613f\u610f\u7ba1\u8fd9\u79cd\u95f2\u4e8b\u3002\u5c31\u7b97\u773c\u524d\u7684\u8fd9\u4e24\u4eba\u89e6\u72af\u4e86\u4ec0\u4e48\u5f8b\u6cd5\uff0c\u72af\u4e86\u4ec0\u4e48\u7f6a\uff0c\u53cd\u6b63\u6ca1\u60f9\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u90a3\u53c8\u4f55\u5fc5\u5e72\u6d89\u3002\u5982\u4eca\u5bf9\u65b9\u613f\u610f\u7f62\u624b\u8a00\u548c\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\u60c5\u3002\u95ee\u9898\u7684\u5173\u952e\uff0c\u8fd8\u662f\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u5979\u82e5\u575a\u6301\uff0c\u90a3\u743c\u6069\u4e5f\u53ea\u597d\u548c\u5979\u4fdd\u6301\u7edf\u4e00\u7acb\u573a\u3002</p><p>\u4ee4\u4ed6\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u6885\u83f2\u65af\u7a0d\u4f5c\u8003\u8651\uff0c\u6ca1\u6709\u7acb\u523b\u62d2\u7edd\uff0c\u5374\u53cd\u8fc7\u6765\u63d0\u51fa\u4e86\u4e00\u4e2a\u6761\u4ef6\u3002</p><p>\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8bbe\u8fd9\u4e2a\u5e7b\u5883\u56da\u7981\u6cd5\u8b66\uff1f\u201d\u5979\u95ee\uff0c\u201c\u6211\u5e0c\u671b\u77e5\u9053\u6574\u4e2a\u4e8b\u60c5\u7684\u7f18\u7531\uff0c\u7136\u540e\u518d\u505a\u51b3\u5b9a\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8be7\u5f02\uff0c\u6885\u83f2\u65af\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u5176\u5b9e\u4fbf\u662f\u8bf4\u5982\u679c\u5bf9\u65b9\u80fd\u591f\u7ed9\u51fa\u201c\u5408\u7406\u201d\u7684\u89e3\u91ca\uff0c\u5979\u4fbf\u4f1a\u653e\u5f03\u5e72\u6d89\uff0c\u4e0d\u518d\u63d2\u624b\uff0c\u8fd9\u53ef\u4e0d\u7b26\u5408\u5979\u5723\u6b66\u58eb\u7684\u8eab\u4efd\u3002\u4f46\u5230\u5e95\u4e24\u4eba\u76f8\u5904\u4e45\u4e86\uff0c\u5f7c\u6b64\u5fc3\u6709\u9ed8\u5951\uff0c\u8f6c\u5ff5\u4e00\u60f3\u4e5f\u5c31\u660e\u767d\u8fc7\u6765\u3002</p><p>\u4ee5\u5bf9\u9762\u8fd9\u4e24\u4eba\u5c55\u73b0\u51fa\u6765\u7684\u5b9e\u529b\uff0c\u53c8\u4e8b\u5148\u5728\u6b64\u57cb\u4f0f\uff0c\u771f\u8981\u6740\u6389\u90a3\u51e0\u4e2a\u6cd5\u8b66\uff0c\u5176\u5b9e\u662f\u8f7b\u800c\u6613\u4e3e\uff0c\u6beb\u4e0d\u4e3a\u96be\u3002\u4f46\u4ed6\u4eec\u5374\u5b81\u53ef\u5728\u8fd9\u91cc\u8bbe\u4e0b\u5e7b\u5883\uff0c\u5982\u6b64\u5927\u8d39\u5468\u7ae0\uff0c\u53ea\u4e3a\u4e86\u628a\u4eba\u56f0\u4f4f\u51e0\u5929\u3002\u5c31\u521a\u624d\u7684\u6218\u6597\u6765\u8bf4\uff0c\u5bf9\u65b9\u660e\u660e\u5df2\u7ecf\u5360\u4e86\u4e0a\u98ce\uff0c\u5374\u5728\u6700\u540e\u5173\u5934\u4e3b\u52a8\u7f62\u624b\u8a00\u548c\u2014\u2014\u4ece\u8fd9\u4e9b\u8ff9\u8c61\u4e0a\u6765\u770b\uff0c\u5bf9\u65b9\u5e76\u975e\u7a77\u51f6\u6781\u6076\u4e4b\u8f88\uff0c\u884c\u4e8b\u6e29\u548c\uff0c\u5904\u5904\u7559\u6709\u4f59\u5730\u3002\u4e4b\u6240\u4ee5\u4f1a\u5f15\u6765\u6cd5\u8b66\u8ffd\u6355\uff0c\u53ea\u6015\u5185\u6709\u9690\u60c5\uff0c\u4e5f\u672a\u53ef\u77e5\uff0c\u6bd5\u7adf\u8fd9\u4e16\u754c\u4e0a\u6cd5\u5b98\u5224\u9519\u6848\uff0c\u8b66\u5bdf\u6293\u9519\u4eba\u7684\u4e8b\u60c5\uff0c\u4e5f\u662f\u65f6\u6709\u53d1\u751f\u7684\u3002</p><p>\u542c\u5230\u8fd9\u4e2a\u8981\u6c42\uff0c\u90a3\u4f4d\u53eb\u601d\u601d\u7684\u767d\u888d\u5973\u5b69\u4e0d\u7f6e\u53ef\u5426\uff0c\u671d\u4f0a\u65af\u5854\u671b\u53bb\uff0c\u663e\u7136\u4ee5\u4ed6\u7684\u5224\u65ad\u4e3a\u51c6\u3002\u4f0a\u65af\u5854\u72b9\u8c6b\u7247\u523b\uff0c\u70b9\u5934\u5e94\u5141\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e8b\u60c5\u9700\u8981\u4ece\u4e09\u5929\u524d\u8bf4\u8d77\u3002</p><p>\u636e\u4f0a\u65af\u5854\u8bf4\uff0c\u4ed6\u4eec\u4e24\u4eba\u81ea\u5317\u65b9\u7684\u5b89\u59c6\u5e1d\u56fd\u800c\u6765\uff0c\u6b64\u884c\u662f\u6253\u7b97\u524d\u5f80\u5361\u4e3d\u73ca\u65c5\u6e38\uff0c\u5076\u7136\u8def\u8fc7\u5854\u683c\u6751\uff0c\u5728\u90a3\u91cc\u501f\u5bbf\uff0c\u6b21\u65e5\u51c6\u5907\u542f\u7a0b\u65f6\uff0c\u649e\u4e0a\u4e86\u4e00\u4ef6\u4e8b\u60c5\u3002</p><p>\u539f\u6765\u5c31\u5728\u90a3\u4e00\u65e5\uff0c\u6709\u4e00\u4f4d\u8d39\u90ce\u7f57\u516c\u7235\u5927\u4eba\u6b63\u597d\u5e26\u7740\u968f\u4ece\u90ca\u6e38\uff0c\u7ecf\u8fc7\u5854\u683c\u6751\u3002</p><p>\u8bf4\u8d77\u8fd9\u4f4d\u8d39\u90ce\u7f57\u516c\u7235\uff0c\u5728\u6cf0\u745f\u5c14\u4e5f\u662f\u9f0e\u9f0e\u6709\u540d\u7684\u4eba\u7269\uff0c\u4ed6\u7b97\u8d77\u6765\u8fd8\u662f\u7687\u5ba4\u65c1\u7cfb\uff0c\u6614\u65e5\u5e1d\u56fd\u5206\u88c2\u5272\u636e\u7684\u65f6\u5019\uff0c\u6709\u8fd1\u4e8c\u5341\u591a\u4f4d\u201c\u8bf8\u4faf\u201d\uff0c\u4ed6\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u52bf\u529b\u96c4\u539a\u8db3\u4ee5\u6392\u540d\u524d\u4e09\u3002\u540e\u6765\u83b1\u8335\u987f\u5973\u7687\u8d77\u5175\u5e73\u53db\uff0c\u6717\u8d39\u7f57\u516c\u7235\u7387\u5148\u54cd\u5e94\uff0c\u7b2c\u4e00\u4e2a\u6295\u6548\u9ebe\u4e0b\uff0c\u5728\u4ee5\u540e\u7684\u6218\u4e89\u4e2d\u4e5f\u5c61\u7acb\u529f\u52cb\u3002\u5e1d\u56fd\u590d\u5f52\u7edf\u4e00\u4e4b\u540e\uff0c\u4ed6\u53c8\u8bc6\u8da3\u5730\u4ea4\u51fa\u6240\u6709\u519b\u961f\uff0c\u8f9e\u53bb\u653f\u5e9c\u804c\u4f4d\uff0c\u56de\u5bb6\u517b\u8001\u5b89\u5ea6\u665a\u5e74\u3002\u5bf9\u4e8e\u8fd9\u6837\u7684\u529f\u81e3\uff0c\u5973\u7687\u81ea\u7136\u4e0d\u80fd\u4e8f\u5f85\uff0c\u8d4f\u529f\u916c\u7235\uff0c\u500d\u52a0\u8363\u5ba0\uff0c\u4e00\u5e94\u7235\u4f4d\u3001\u5c01\u5730\uff0c\u5305\u62ec\u5386\u53f2\u4e0a\u4eab\u6709\u7684\u5404\u79cd\u8d35\u65cf\u7279\u6743\uff0c\u5168\u90fd\u4e88\u4ee5\u4fdd\u7559\uff0c\u5e76\u4ee5\u6cd5\u4ee4\u7684\u5f62\u5f0f\u52a0\u4ee5\u786e\u8ba4\u3002\u5854\u683c\u6751\uff0c\u4fbf\u662f\u6717\u8d39\u7f57\u516c\u7235\u7684\u5c01\u5730\u4e4b\u4e00\u3002</p><p>\u6717\u8d39\u7f57\u516c\u7235\u5e74\u4e8b\u5df2\u9ad8\uff0c\u53c8\u6df1\u8c19\u6fc0\u6d41\u52c7\u9000\u4e4b\u9053\uff0c\u6574\u65e5\u53ea\u662f\u60a0\u95f2\u6e38\u730e\uff0c\u8d4f\u82b1\u996e\u9152\uff0c\u5012\u4e5f\u5e76\u65e0\u4ec0\u4e48\u6b3a\u538b\u767e\u59d3\u3001\u6a2a\u5f81\u66b4\u655b\u4e4b\u4e3e\uff0c\u53e3\u7891\u8fd8\u7b97\u4e0d\u9519\u3002\u4ed6\u7ecf\u8fc7\u5854\u683c\u6751\uff0c\u89c1\u8fd9\u91cc\u98ce\u666f\u79c0\u4e3d\uff0c\u4e00\u65f6\u5174\u8d77\u4fbf\u6682\u4e14\u6b47\u9a6c\uff0c\u6253\u7b97\u4f4f\u4e0a\u534a\u65e5\uff0c\u6751\u6c11\u4eec\u5012\u4e5f\u5e76\u4e0d\u60ca\u60f6\uff0c\u6c14\u6c1b\u7965\u548c\u3002</p><p>\u7136\u800c\u5c31\u5728\u6b64\u65f6\uff0c\u51fa\u4e86\u4e00\u4ef6\u4e8b\u60c5\u3002</p><p>\u6751\u4e2d\u6709\u4e00\u5bf9\u9752\u5e74\u7537\u5973\uff0c\u4e24\u60c5\u76f8\u60a6\uff0c\u6b63\u51c6\u5907\u4e3e\u884c\u5a5a\u793c\u3002\u516c\u7235\u5927\u4eba\u4e5f\u88ab\u4f5c\u4e3a\u8d35\u5bbe\u9080\u8bf7\u53c2\u52a0\uff0c\u5f53\u7136\u4e5f\u5c31\u89c1\u5230\u4e86\u65b0\u90ce\u548c\u65b0\u5a18\u2014\u2014\u7136\u540e\u9ebb\u70e6\u5c31\u6765\u4e86\uff0c\u516c\u7235\u5927\u4eba\u770b\u4e0a\u4e86\u65b0\u5a18\u2026\u2026</p><p>\u82e5\u662f\u666e\u901a\u4eba\uff0c\u9047\u5230\u8fd9\u79cd\u60c5\u5f62\uff0c\u4e5f\u53ea\u80fd\u611f\u53f9\u6709\u7f18\u65e0\u5206\uff0c\u6068\u4e0d\u76f8\u9022\u672a\u5ac1\u65f6\uff0c\u8f6c\u8eab\u9ed8\u9ed8\u8d70\u4eba\u3002\u6717\u8d39\u7f57\u5927\u4eba\u662f\u5802\u5802\u516c\u7235\uff0c\u4e16\u88ad\u8d35\u65cf\uff0c\u81ea\u7136\u4e0d\u4f1a\u8fd9\u4e48\u5dee\u52b2\u3002\u4f46\u76f4\u63a5\u5f3a\u62a2\u6c11\u5973\uff0c\u90a3\u5374\u4e5f\u662f\u89e6\u72af\u5f8b\u6cd5\u7684\u3002\u516c\u7235\u5927\u4eba\u82e6\u607c\u4e0d\u5df2\uff0c\u7136\u540e\u5728\u8eab\u8fb9\u4eba\u7684\u63d0\u9192\u4e0b\uff0c\u60f3\u8d77\u4e86\u81ea\u5df1\u539f\u6765\u8fd8\u62e5\u6709\u4e00\u9879\u7279\u6743\u53ef\u4ee5\u884c\u4f7f\u3002</p><p>\u521d\u591c\u6743\uff01</p><p>\u6309\u7167\u6cf0\u745f\u5c14\u5e1d\u56fd\u5386\u53f2\u4e0a\u4e00\u4efd\u53e4\u8001\u7684\u6cd5\u4ee4\uff0c\u9886\u4e3b\u5bf9\u4e8e\u4ed6\u5c01\u5730\u91cc\u7684\u6240\u6709\u65b0\u5a18\uff0c\u662f\u4eab\u6709\u201c\u521d\u591c\u6743\u201d\u7684\uff0c\u901a\u4fd7\u70b9\u8bf4\uff0c\u5c31\u662f\u81e3\u6c11\u4eec\u7ed3\u5a5a\u7684\u65f6\u5019\uff0c\u9886\u4e3b\u6709\u6743\u5360\u6709\u65b0\u5a18\u7684\u521d\u591c\u3002\u8fd9\u81ea\u7136\u662f\u4e00\u79cd\u91ce\u86ee\u6743\u529b\uff0c\u83b1\u8335\u987f\u5973\u7687\u7edf\u4e00\u5e1d\u56fd\u4e4b\u540e\uff0c\u5728\u63d0\u5c14\u6559\u4f1a\u7684\u63a8\u52a8\u4e4b\u4e0b\uff0c\u8bae\u4f1a\u5df2\u7ecf\u9881\u5e03\u6cd5\u4ee4\uff0c\u5e9f\u6b62\u4e86\u201c\u521d\u591c\u6743\u201d\u2014\u2014\u4f46\u6717\u8d39\u7f57\u516c\u7235\u4e0d\u53d7\u6b64\u9650\uff0c\u56e0\u4e3a\u6839\u636e\u53e6\u5916\u4e00\u4efd\u6cd5\u4ee4\uff0c\u4ed6\u5728\u81ea\u5df1\u7684\u5c01\u5730\u4e4b\u5185\uff0c\u4fdd\u7559\u4e86\u201c\u6240\u6709\u7684\u8d35\u65cf\u7279\u6743\u201d\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u6717\u8d39\u7f57\u516c\u7235\u4f9d\u65e7\u62e5\u6709\u201c\u521d\u591c\u6743\u201d\uff0c\u53ea\u8981\u4ed6\u613f\u610f\uff0c\u4fbf\u53ef\u4ee5\u4eab\u7528\u5c01\u5730\u4e2d\u6240\u6709\u65b0\u5a18\u7684\u65b0\u5a5a\u7b2c\u4e00\u591c\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u7406\u8bba\u4e0a\u62e5\u6709\u7684\u6743\u529b\uff0c\u5b9e\u9645\u4e0a\u516c\u7235\u5927\u4eba\u5e76\u6ca1\u6709\u5f53\u771f\u884c\u4f7f\u8fc7\uff0c\u4e5f\u6ca1\u8fd9\u4e2a\u5fc5\u8981\u3002\u60f3\u60f3\u4e5f\u77e5\u9053\uff0c\u4ee5\u4ed6\u516c\u7235\u4e4b\u5c0a\uff0c\u8eab\u8fb9\u7684\u7f8e\u4e3d\u5973\u5b50\u8981\u591a\u5c11\u6709\u591a\u5c11\uff0c\u6574\u65e5\u6574\u591c\u4eab\u7528\u4e0d\u5c3d\uff0c\u5fd9\u90fd\u5fd9\u4e0d\u8fc7\u6765\uff0c\u54ea\u6709\u7a7a\u7406\u776c\u3002\u8bf4\u5f97\u4e0d\u5ba2\u6c14\u70b9\uff0c\u6cbb\u4e0b\u767e\u59d3\u5927\u591a\u662f\u519c\u592b\uff0c\u5e38\u5e74\u52b3\u4f5c\u98ce\u5439\u65e5\u6652\uff0c\u4ee5\u7edd\u5927\u591a\u6570\u65b0\u5a18\u7684\u5bb9\u8c8c\u6c34\u51c6\u800c\u8bba\uff0c\u5c31\u7b97\u767d\u9001\u7ed9\u516c\u7235\u5927\u4eba\uff0c\u4ed6\u53ea\u6015\u4e5f\u656c\u8c22\u4e0d\u654f\uff0c\u5b81\u613f\u63a8\u8131\u6389\u3002\u65f6\u95f4\u4e00\u957f\uff0c\u5927\u5bb6\u65e9\u5c31\u628a\u8fd9\u832c\u7ed9\u5fd8\u4e86\u3002</p><p>\u7136\u800c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u603b\u662f\u6709\u4f8b\u5916\u7684\uff0c\u6751\u5973\u4e4b\u4e2d\uff0c\u4e5f\u4f1a\u6709\u7f8e\u4eba\uff0c\u81f3\u5c11\u8ba9\u516c\u7235\u5927\u4eba\u4e00\u89c1\u949f\u60c5\u3002\u800c\u6743\u529b\u8fd9\u4e1c\u897f\u53c8\u4e0d\u50cf\u6b66\u5668\uff0c\u53ea\u8981\u62e5\u6709\u5b83\uff0c\u5c31\u7b97\u51e0\u5341\u5e74\u4e0a\u767e\u5e74\u4e0d\u884c\u4f7f\uff0c\u5b83\u4e5f\u662f\u4e0d\u4f1a\u751f\u9508\u7684\u3002</p><p>\u5c31\u8fd9\u6837\uff0c\u516c\u7235\u5927\u4eba\u88ab\u65b0\u5a18\u7684\u7f8e\u8c8c\u6240\u5438\u5f15\uff0c\u51b3\u5b9a\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u884c\u4f7f\u521d\u591c\u6743\uff0c\u800c\u6751\u6c11\u4eec\u81ea\u7136\u4e0d\u5e72\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u5927\u90e8\u4efd\u6751\u6c11\u90fd\u5df2\u7ecf\u6839\u672c\u4e0d\u8bb0\u5f97\u8fd8\u6709\u8fd9\u56de\u4e8b\u4e86\u3002\u53cc\u65b9\u51b2\u7a81\u8d77\u6765\uff0c\u6717\u8d39\u7f57\u516c\u7235\u6bd5\u7adf\u662f\u66fe\u7ecf\u4eb2\u5386\u6218\u9635\u7684\u4eba\u7269\uff0c\u8eab\u8fb9\u7684\u968f\u4ece\u867d\u7136\u53ea\u6709\u51e0\u4e2a\uff0c\u5374\u4e5f\u90fd\u662f\u6614\u65e5\u7684\u4eb2\u536b\u7cbe\u9510\uff0c\u8981\u5bf9\u4ed8\u8fd9\u4e9b\u624b\u65e0\u5bf8\u94c1\u7684\u6751\u6c11\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u60ac\u5ff5\u3002</p><p>\u5982\u679c\u6ca1\u6709\u610f\u5916\u7684\u8bdd\uff0c\u516c\u7235\u5927\u4eba\u7684\u5fc3\u613f\u662f\u80fd\u591f\u987a\u5229\u5f97\u507f\u7684\u2026\u2026\u7136\u800c\u6b63\u5982\u541f\u6e38\u8bd7\u4eba\u7684\u6545\u4e8b\u6240\u8bf4\u90a3\u6837\uff0c\u610f\u5916\u603b\u662f\u4f1a\u53d1\u751f\u7684\u3002</p><p>\u4f0a\u65af\u5854\u548c\u601d\u601d\u5f53\u65f6\u4e5f\u5728\u6751\u4e2d\uff0c\u9047\u89c1\u6b64\u4e8b\u4fbf\u51fa\u6765\u63d2\u624b\u3002\u4e00\u6218\u4e4b\u4e0b\uff0c\u516c\u7235\u5927\u4eba\u843d\u8352\u800c\u9003\uff0c\u72fc\u72c8\u56de\u5230\u81ea\u5df1\u7684\u57ce\u5821\u3002\u53d7\u6b64\u5c48\u8fb1\uff0c\u4ed6\u81ea\u7136\u662f\u96f7\u9706\u9707\u6012\uff0c\u53ea\u662f\u624b\u4e2d\u5df2\u7ecf\u6ca1\u6709\u5175\u6743\uff0c\u6ca1\u6cd5\u6d3e\u519b\u961f\u53bb\u628a\u6751\u5e84\u7ed9\u5e73\u4e86\uff0c\u4e8e\u662f\u4fbf\u544a\u4e0a\u4e86\u8fbe\u52b3\u739b\u57ce\u7684\u9ad8\u7ea7\u6cd5\u9662\u3002\u6cd5\u9662\u4e8e\u662f\u6d3e\u51fa\u4e86\u6cd5\u8b66\uff0c\u524d\u6765\u6293\u6355\u5acc\u72af\u3002</p><p>\u4f5c\u4e3a\u4f0a\u65af\u5854\u548c\u601d\u601d\u800c\u8a00\uff0c\u65e2\u7136\u63d2\u624b\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u90a3\u5c31\u5f97\u7ba1\u5230\u5e95\u3002\u4ed6\u4eec\u5018\u82e5\u8981\u4e00\u8d70\u4e86\u4e4b\u7684\u8bdd\uff0c\u5e94\u8be5\u4e5f\u6ca1\u4eba\u80fd\u6293\u5f97\u4f4f\uff0c\u4f46\u90a3\u4e9b\u6751\u6c11\u4e0d\u514d\u5c31\u4f1a\u6709\u9ebb\u70e6\u3002\u4f0a\u65af\u5854\u9884\u6599\u5230\u6717\u8d39\u7f57\u516c\u7235\u4e0d\u4f1a\u5584\u7f62\u7518\u4f11\uff0c\u4fbf\u5728\u8fd9\u5fc5\u7ecf\u4e4b\u8def\u4e0a\u8ba9\u601d\u601d\u8bbe\u4e0b\u4e86\u6cd5\u672f\u9677\u9631\uff0c\u5c06\u8fd9\u51e0\u540d\u6cd5\u8b66\u7ed9\u56f0\u4f4f\u4e86\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u4e8b\u60c5\u8bf4\u5230\u8fd9\u91cc\uff0c\u57fa\u672c\u60c5\u51b5\u5df2\u7ecf\u6e05\u695a\u3002\u4ece\u6cd5\u7406\u4e0a\u8bf4\uff0c\u6717\u8d39\u7f57\u516c\u7235\u662f\u5728\u884c\u4f7f\u4ed6\u7684\u6b63\u5f53\u6743\u5229\uff0c\u76f8\u5bf9\u5e94\u7684\uff0c\u6751\u6c11\u4eec\u5c31\u6709\u4e56\u4e56\u4ea4\u51fa\u65b0\u5a18\u7684\u4e49\u52a1\u3002\u800c\u4f0a\u65af\u5854\u548c\u601d\u601d\u4e24\u4eba\uff0c\u81ea\u7136\u66f4\u662f\u8981\u4f9d\u6cd5\u60e9\u6212\u7684\u66b4\u5f92\u4e86\u2014\u2014\u6574\u4e2a\u903b\u8f91\u65e0\u61c8\u53ef\u51fb\u3002\u4f46\u8981\u8bba\u60c5\u7406\uff0c\u5373\u4fbf\u662f\u6885\u83f2\u65af\uff0c\u4e5f\u6ca1\u529e\u6cd5\u8bf4\u4f0a\u65af\u5854\u548c\u601d\u601d\u505a\u9519\u4e86\u4ec0\u4e48\u3002</p><p>\u9ebb\u70e6\u51fa\u73b0\u4e86\u3002</p><p>\u5723\u6b66\u58eb\u5e94\u8be5\u606a\u5b88\u5f8b\u6cd5\u3001\u7ef4\u62a4\u5584\u826f\uff0c\u5728\u5927\u591a\u6570\u60c5\u51b5\u4e0b\uff0c\u8fd9\u4e24\u8005\u5e76\u4e0d\u51b2\u7a81\uff0c\u4f46\u6709\u4e9b\u65f6\u5019\u5374\u4e5f\u662f\u5b8c\u5168\u5bf9\u7acb\u7684\u3002\u4fbf\u5982\u73b0\u5728\uff0c\u5018\u82e5\u6885\u83f2\u65af\u8981\u575a\u6301\u7ad9\u5728\u6cd5\u5f8b\u7684\u7acb\u573a\uff0c\u90a3\u5c31\u5e94\u8be5\u5e2e\u52a9\u6cd5\u8b66\u6293\u6355\u4f0a\u65af\u5854\u548c\u601d\u601d\u4e24\u4eba\uff0c\u4fdd\u969c\u6717\u8d39\u7f57\u516c\u7235\u5927\u4eba\u987a\u5229\u884c\u4f7f\u4ed6\u7684\u7279\u6743\uff0c\u4f46\u8fd9\u663e\u7136\u548c\u201c\u5584\u826f\u201d\u80cc\u9053\u800c\u9a70\u3002</p><p>\u5979\u6c89\u541f\u7740\uff0c\u7136\u540e\u63d0\u51fa\u4e00\u4e2a\u95ee\u9898\u3002</p><p>\u201c\u4f60\u4eec\u6253\u7b97\u600e\u4e48\u505a\u5462\uff1f\u201d</p></article>"}}}]);
