(window.webpackJsonp=window.webpackJsonp||[]).push([[1599],{1622:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516b\u8282 \u6cbb\u7597\u65b9\u6848",content:"<article><h2>\u7b2c\u56db\u5341\u516b\u8282 \u6cbb\u7597\u65b9\u6848</h2><p>\u8fd9\u4e2a\u8981\u6c42\u5f88\u53e4\u602a\uff0c\u8ba9\u4eba\u6478\u4e0d\u6e05\u610f\u56fe\uff0c\u800c\u4e14\u4e5f\u4e0d\u77e5\u9053\u4ece\u4f55\u8bf4\u8d77\u3002\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u60a8\u662f\u60f3\u77e5\u9053\u2026\u2026\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u5c31\u2026\u2026\u5148\u8bf4\u8bf4\u6cf0\u62c9\u66fc\u7279\u548c\u963f\u62c9\u838e\u5427\uff0c\u201d\u601d\u601d\uff08\u51c6\u786e\u5730\u8bf4\u662f\u65e0\u540d\u8001\u8005\uff09\u8bf4\uff0c\u201c\u4ed6\u4eec\u600e\u4e48\u6837\uff0c\u8fd8\u597d\u5427\u3002\u201d</p><p>\u6cf0\u62c9\u66fc\u7279\xb7\u5766\u8212\u5c14\uff0c\u9634\u9b42\u57ce\u4e3b\u7684\u540d\u5b57\uff0c\u8fd9\u4e2a\u743c\u6069\u662f\u77e5\u9053\u7684\uff0c\u53ef\u662f\u57ce\u4e3b\u5927\u4eba\u6df1\u5c45\u5bab\u4e2d\uff0c\u57fa\u672c\u4e0d\u5728\u516c\u5f00\u573a\u5408\u9732\u9762\uff0c\u81ea\u5df1\u4e00\u4e2a\u5c0f\u89d2\u8272\uff0c\u6253\u8fc7\u4ea4\u9053\u7684\u6700\u9ad8\u7ea7\u522b\u4eba\u7269\u5c31\u662f\u9634\u9b42\u738b\u5b50\u800c\u5df2\uff0c\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u597d\u4e0d\u597d\u3002\u81f3\u4e8e\u963f\u62c9\u838e\uff0c\u8fd9\u540d\u5b57\u542c\u8d77\u6765\u50cf\u662f\u4e2a\u5973\u5b50\uff0c\u5979\u53c8\u662f\u8c01\uff0c\u597d\u50cf\u542c\u8bf4\u8fc7\uff0c\u6709\u70b9\u6a21\u7cca\u5370\u8c61\uff0c\u4f46\u5c31\u662f\u60f3\u4e0d\u8d77\u6765\u2026\u2026</p><p>\u201c\u662f\u5417\uff1f\u201d\u5bf9\u65b9\u76b1\u7709\uff0c\u201c\u90a3\u5bb6\u4f19\u5e94\u8be5\u662f\u5f88\u7231\u70ed\u95f9\uff0c\u559c\u6b22\u629b\u5934\u9732\u9762\u7684\u5440\uff0c\u96be\u9053\u8f6c\u6027\u5b50\u4e86\uff1f\u963f\u62c9\u838e\u662f\u4ed6\u592b\u4eba\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u201c\u57ce\u4e3b\u592b\u4eba\uff1f\u554a\uff0c\u60f3\u8d77\u6765\u4e86\u2026\u2026\u53ef\u662f\u5979\u5df2\u7ecf\u53bb\u4e16\u591a\u5e74\u4e86\u3002\u201d</p><p>\u88ab\u5bf9\u65b9\u4e00\u63d0\u9192\uff0c\u743c\u6069\u7ec8\u4e8e\u60f3\u4e86\u8d77\u6765\uff0c\u9634\u9b42\u57ce\u5386\u53f2\u4e0a\u786e\u5b9e\u662f\u6709\u4e00\u4f4d\u53eb\u505a\u963f\u62c9\u838e\u7684\u201c\u738b\u540e\u201d\u3002\u636e\u8bf4\u662f\u4e00\u4f4d\u98ce\u534e\u7edd\u4ee3\u7684\u5927\u7f8e\u4eba\uff0c\u548c\u9634\u9b42\u57ce\u4e3b\u5728\u5b66\u751f\u65f6\u4ee3\u5c31\u76f8\u8bc6\uff0c\u540e\u6765\u6210\u4e3a\u7231\u4fa3\u3002\u4e24\u4eba\u611f\u60c5\u975e\u5e38\u6df1\u539a\uff0c\u591a\u5c11\u5e74\u6765\u5f7c\u6b64\u6276\u6301\uff0c\u76f8\u4f34\u8d70\u8fc7\u65e0\u6570\u98ce\u96e8\uff0c\u7b80\u76f4\u5c31\u662f\u6d6a\u6f2b\u7684\u7ae5\u8bdd\u6545\u4e8b\u3002\u4ee4\u4eba\u60cb\u60dc\u7684\u662f\uff0c\u738b\u540e\u5df2\u7ecf\u53bb\u4e16\u591a\u5e74\u2026\u2026\u597d\u50cf\u6709\u51e0\u767e\u5e74\u4e86\u5427\uff0c\u53cd\u6b63\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u542c\u8bf4\u8fd8\u662f\u6b7b\u4e8e\u8c0b\u6740\uff0c\u51f6\u624b\u5374\u4e00\u76f4\u6ca1\u6709\u6293\u5230\uff0c\u6210\u4e3a\u4e00\u6869\u60ac\u6848\u3002\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u743c\u6069\u624d\u4f1a\u6709\u4e9b\u5370\u8c61\uff0c\u5426\u5219\u8c01\u4f1a\u8bb0\u5f97\u4e00\u4e2a\u53bb\u4e16\u591a\u5e74\u7684\u4eba\u3002</p><p>\u6cf0\u62c9\u66fc\u7279\u8eab\u4e3a\u57ce\u4e3b\uff0c\u76f8\u5f53\u4e8e\u4e00\u56fd\u4e4b\u541b\uff0c\u8eab\u8fb9\u81ea\u7136\u4e0d\u4f1a\u5c11\u4e86\u5973\u5b50\uff0c\u4e0d\u4ec5\u4ec5\u53ea\u6709\u738b\u540e\u4e00\u4eba\u3002\u5341\u4e8c\u4f4d\u9634\u9b42\u738b\u5b50\uff0c\u5176\u5b9e\u8bf4\u8d77\u6765\u5927\u534a\u90fd\u662f\u975e\u738b\u540e\u6240\u51fa\u7684\u201c\u79c1\u751f\u5b50\u201d\uff0c\u6216\u8005\u8bf4\u5eb6\u51fa\uff0c\u53cd\u6b63\u9634\u9b42\u57ce\u7684\u98ce\u4fd7\u4e0d\u5728\u610f\u8fd9\u4e9b\uff0c\u79c1\u751f\u5b50\u5e76\u4e0d\u53d7\u6b67\u89c6\uff0c\u5927\u5bb6\u4e5f\u65e0\u6240\u8c13\u3002\u4f46\u81ea\u4ece\u963f\u62c9\u838e\u738b\u540e\u53bb\u4e16\u4e4b\u540e\uff0c\u9634\u9b42\u57ce\u4e3b\u5c31\u518d\u6ca1\u6709\u7eed\u5f26\u53e6\u5a36\uff0c\u201c\u738b\u540e\u201d\u7684\u5b9d\u5ea7\u4e0a\u4e00\u76f4\u7a7a\u7740\u4e86\u3002</p><p>\u201c\u53bb\u4e16\u4e86\uff1f\u201d</p><p>\u8001\u8005\u7684\u8bed\u6c14\u4f3c\u4e4e\u6709\u4e9b\u60ca\u8bb6\uff0c\u800c\u4e14\u2026\u2026\u4e0d\u77e5\u9053\u662f\u5426\u662f\u743c\u6069\u7684\u9519\u89c9\uff0c\u4eff\u4f5b\u8fd8\u6709\u4e00\u4e1d\u63a9\u76d6\u4e0d\u4f4f\u7684\u4f24\u611f\uff0c\u5c31\u50cf\u662f\u542c\u5230\u4eb2\u4eba\u6216\u597d\u53cb\u901d\u4e16\u7684\u6d88\u606f\u4e00\u822c\u3002</p><p>\u201c\u4ed6\u8ba4\u8bc6\u9634\u9b42\u57ce\u4e3b\u548c\u592b\u4eba\uff0c\u800c\u4e14\u663e\u7136\u8fd8\u5f88\u719f\u8bc6\u3002\u201d</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u505a\u51fa\u8fd9\u4e2a\u5224\u65ad\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u6562\u6253\u542c\u4ec0\u4e48\u3002\u81f3\u4e8e\u738b\u540e\u662f\u88ab\u8c0b\u6740\u7684\u8fd9\u70b9\uff0c\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u6700\u7ec8\u4e5f\u8fd8\u662f\u6ca1\u6709\u8bf4\u51fa\u53e3\u3002\u6709\u5173\u963f\u62c9\u838e\u738b\u540e\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u4e5f\u53ea\u662f\u5c0f\u65f6\u5019\u542c\u7236\u6bcd\u5076\u7136\u95f4\u8c08\u8d77\uff0c\u8bb0\u5fc6\u90fd\u6709\u4e9b\u6a21\u7cca\u4e86\uff0c\u4e5f\u4e0d\u80fd\u786e\u5b9a\uff0c\u4e07\u4e00\u5f00\u53e3\u7ed3\u679c\u8bf4\u9519\u4e86\u4ec0\u4e48\uff0c\u5c82\u4e0d\u662f\u81ea\u627e\u9ebb\u70e6\uff0c\u8fd8\u662f\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\u5427\u3002</p><p>\u542c\u5230\u963f\u62c9\u838e\u738b\u540e\u53bb\u4e16\u7684\u6d88\u606f\uff0c\u8001\u8005\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u6ca1\u6709\u518d\u8ffd\u7a76\u8fd9\u4e2a\u8bdd\u9898\uff0c\u8f6c\u800c\u95ee\u8d77\u9634\u9b42\u57ce\u8fd1\u4e9b\u5e74\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u5927\u7565\u6574\u7406\u4e86\u4e00\u4e0b\u5934\u7eea\uff0c\u4fbf\u5f00\u59cb\u6162\u6162\u8bf4\u4e86\u8d77\u6765\u3002\u53cd\u6b63\u9634\u9b42\u57ce\u5728\u5e7d\u5f71\u754c\u7684\u65f6\u5019\u6ca1\u4ec0\u4e48\u53ef\u8bb2\u7684\uff0c\u6309\u90e8\u5c31\u73ed\u5730\u751f\u6d3b\uff0c\u81f3\u4e8e\u56de\u5230\u7269\u8d28\u754c\u4e4b\u540e\uff0c\u6240\u5e72\u7684\u51e0\u4ef6\u5927\u4e8b\uff0c\u9634\u5f71\u5927\u5e55\u878d\u5316\u51b0\u5ddd\u3001\u548c\u585e\u5c14\u7ed3\u76df\u3001\u8fdb\u653b\u5e7d\u6697\u5730\u57df\u4e4b\u7c7b\uff0c\u743c\u6069\u6216\u8005\u6709\u4efd\u53c2\u4e0e\uff0c\u6216\u8005\u4e5f\u6709\u8033\u95fb\uff0c\u8bf4\u8d77\u6765\u4e5f\u4e0d\u8d39\u529b\u3002</p><p>\u8001\u8005\u9759\u9759\u5730\u542c\u7740\uff0c\u5076\u5c14\u63d0\u4e00\u4e9b\u95ee\u9898\u3002\u743c\u6069\u4ece\u4e2d\u53d1\u73b0\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4fbf\u662f\u8fd9\u4e2a\u8001\u4eba\u5bf9\u9634\u9b42\u57ce\u663e\u7136\u5e76\u4e0d\u964c\u751f\uff0c\u6216\u8bb8\u4e0d\u66fe\u5728\u5176\u4e2d\u751f\u6d3b\uff0c\u4f46\u81f3\u5c11\u53bb\u8fc7\uff0c\u6709\u5f88\u591a\u7ec6\u8282\u4e0a\u7684\u95ee\u9898\uff0c\u57ce\u5e02\u5efa\u7b51\u3001\u8857\u9053\u8d70\u5411\u3001\u65b9\u4f4d\u7b49\u7b49\uff0c\u4e0d\u662f\u4eb2\u773c\u6240\u89c1\u662f\u4e0d\u53ef\u80fd\u77e5\u9053\u7684\u3002</p><p>\u201c\u4ed6\u8bf4\u7684\u662f\u7eaf\u6b63\u7684\u8010\u745f\u8bed\uff0c\u8ba4\u8bc6\u57ce\u4e3b\u548c\u738b\u540e\uff0c\u53c8\u5bf9\u9634\u9b42\u57ce\u8fd9\u4e48\u719f\u6089\uff0c\u8fd8\u8fd9\u4e48\u5f3a\u2026\u2026\u96be\u4e0d\u6210\u662f\u67d0\u4f4d\u5927\u5965\u672f\u5e08\uff1f\u201d</p><p>\u8fd9\u4e2a\u731c\u6d4b\u6709\u5176\u9053\u7406\uff0c\u7136\u800c\u6709\u4e00\u70b9\u4e0d\u660e\u767d\u7684\uff0c\u4fbf\u662f\u8fd9\u4f4d\u5927\u5965\u672f\u5e08\u660e\u660e\u662f\u7537\u6027\uff08\u81f3\u5c11\u4ece\u55d3\u97f3\u4e0a\u5224\u65ad\u662f\u7537\u6027\uff09\uff0c\u4e3a\u4f55\u5374\u628a\u81ea\u5df1\u53d8\u6210\u4e00\u4f4d\u7f8e\u5c11\u5973\u2026\u2026\u96be\u4e0d\u6210\u4ed6\u662f\u4e2a\u4eba\u5996\uff1f\u7136\u800c\u4ed6\u521a\u624d\u53c8\u8bf4\uff0c\u81ea\u5df1\u5e76\u4e0d\u662f\u201c\u4f60\u73b0\u5728\u770b\u89c1\u7684\u8fd9\u4e2a\u53eb\u601d\u601d\u7684\u5973\u5b69\u5b50\u201d\u3002</p><p>\u4e24\u4e2a\u7075\u9b42\uff1f</p><p>\u6885\u83f2\u65af\u6b64\u524d\u8bf4\u7684\u8bdd\uff0c\u7a81\u7136\u5728\u4ed6\u8111\u4e2d\u5192\u51fa\u6765\u3002\u53cc\u773c\u5f02\u8272\uff0c\u662f\u4e3a\u5996\u77b3\uff0c\u610f\u5473\u7740\u4f53\u5185\u6709\u4e24\u4e2a\u7075\u9b42\u2014\u2014\u4ed6\u672c\u6765\u4ee5\u4e3a\u8fd9\u662f\u80e1\u8bf4\u516b\u9053\uff0c\u8fde\u6885\u83f2\u65af\u81ea\u5df1\u4e5f\u53ea\u5f53\u4f5c\u7b11\u8c08\u3002\u7136\u800c\u5c31\u76ee\u524d\u7684\u60c5\u51b5\u6765\u770b\uff0c\u53ea\u6015\u8fd8\u771f\u6709\u53ef\u80fd\u3002</p><p>\u4ed6\u4e00\u8fb9\u60f3\u7740\uff0c\u4e00\u8fb9\u5c06\u9634\u9b42\u57ce\u7684\u60c5\u51b5\u5927\u81f4\u8bf4\u5b8c\u3002\u5bf9\u65b9\u542c\u4e86\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5fae\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u662f\u8fd9\u6837\u554a\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u4f60\u624b\u4e0a\u7684\u90a3\u4e24\u679a\u6212\u6307\uff0c\u600e\u4e48\u770b\u7740\u50cf\u662f\u5965\u6c83\u90a3\u80d6\u5b50\u7684\u624b\u827a\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u5df2\u7ecf\u53ef\u4ee5\u767e\u5206\u4e4b\u767e\u786e\u5b9a\uff0c\u773c\u524d\u6b63\u5728\u548c\u81ea\u5df1\u8bf4\u8bdd\u7684\u8fd9\u4e2a\u8001\u5bb6\u4f19\uff0c\u7edd\u5bf9\u662f\u6614\u65e5\u8010\u745f\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\u4e4b\u4e00\uff0c\u5426\u5219\u63d0\u8d77\u5965\u6c83\u7edd\u4e0d\u53ef\u80fd\u662f\u8fd9\u79cd\u53e3\u6c14\uff0c\u66f4\u4e0d\u53ef\u80fd\u4ece\u6212\u6307\u7684\u624b\u827a\u4e0a\u5c31\u80fd\u8ba4\u51fa\u4ed6\u662f\u5236\u4f5c\u8005\u3002\u201c\u5965\u6c83\u5148\u751f\u662f\u6211\u7684\u8001\u5e08\uff0c\u201d\u743c\u6069\u5c0f\u5fc3\u56de\u7b54\u8bf4\uff0c\u201c\u8fd9\u4e24\u679a\u6212\u6307\u90fd\u662f\u4ed6\u9001\u7684\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u90a3\u5bb6\u4f19\u539f\u6765\u8fd8\u6ca1\u6b7b\u554a\uff0c\u201d\u8001\u8005\u7528\u4e00\u79cd\u9887\u611f\u5174\u8da3\u7684\u8bed\u6c14\u8bf4\uff0c\u201c\u5230\u5e95\u662f\u2018\u4e0d\u6b7b\u8005\u2019\uff0c\u522b\u7684\u672c\u4e8b\u6ca1\u6709\uff0c\u4fdd\u547d\u7684\u6280\u672f\u662f\u4e00\u6d41\u3002\u4f60\u662f\u4ed6\u5b66\u751f\uff0c\u96be\u602a\uff0c\u4f60\u8001\u5e08\u8fd8\u662f\u6574\u5929\u6302\u7740\u4e00\u4e32\u6212\u6307\u5230\u5904\u5411\u5973\u5b69\u5b50\u6c42\u5a5a\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u5443\uff0c\u4ed6\u73b0\u5728\u57fa\u672c\u4e0d\u8fd9\u4e48\u505a\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5927\u6709\u957f\u8fdb\u554a\u3002\u8fd8\u662f\u8bf4\u4ed6\u5df2\u7ecf\u627e\u5230\u613f\u610f\u5ac1\u7ed9\u4ed6\u7684\u5973\u5b69\u5b50\u4e86\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u6682\u65f6\u8fd8\u6ca1\u6709\u3002\u201d</p><p>\u201c\u610f\u6599\u4e4b\u4e2d\uff0c\u4f60\u4e0b\u6b21\u89c1\u5230\u8f6c\u544a\u4ed6\uff0c\u4e0d\u4e0b\u51b3\u5fc3\u51cf\u80a5\u7684\u8bdd\uff0c\u8fd9\u8f88\u5b50\u4ed6\u662f\u522b\u6307\u671b\u80fd\u6709\u5973\u5b69\u5b50\u559c\u6b22\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u4e00\u5b9a\u8f6c\u544a\u3002\u201d</p><p>\u8001\u8005\u5475\u5475\u4e00\u7b11\uff0c\u201c\u4f60\u73b0\u5728\u662f\u51c6\u5907\u56de\u9634\u9b42\u57ce\u53bb\uff1f\u201d</p><p>\u201c\u662f\u7684\u3002\u201d</p><p>\u201c\u5f88\u597d\uff0c\u201d\u8001\u8005\u8bf4\uff0c\u201c\u4f60\u8eab\u4e0a\u6709\u4e00\u9875\u8010\u745f\u5377\u8f74\u5427\uff0c\u62ff\u51fa\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d</p><p>\u743c\u6069\u6115\u7136\u6014\u4f4f\uff0c\u4ed6\u786e\u5b9e\u662f\u6709\u4e00\u9875\u8010\u745f\u5377\u8f74\uff0c\u5f53\u65e5\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4ece\u4e9a\u5f53\u65af\u624b\u4e2d\u5f97\u6765\uff0c\u56e0\u4e3a\u5b58\u4e86\u79c1\u5fc3\uff0c\u4e00\u76f4\u6ca1\u6709\u4e0a\u7f34\u3002\u4f46\u56e0\u4e3a\u838e\u5c14\u7684\u5f71\u706b\u4f5c\u795f\uff0c\u4ed6\u4e5f\u6ca1\u529e\u6cd5\u770b\u61c2\u5377\u8f74\u5185\u5bb9\uff0c\u53ea\u80fd\u6254\u5728\u6b21\u5143\u888b\u91cc\uff0c\u65f6\u95f4\u4e00\u957f\u51e0\u4e4e\u90fd\u7ed9\u5fd8\u8bb0\u4e86\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u8fd9\u8001\u5bb6\u4f19\u662f\u600e\u4e48\u770b\u51fa\u6765\u7684\uff0c\u4ed6\u518d\u5389\u5bb3\uff0c\u603b\u4e0d\u81f3\u4e8e\u773c\u775b\u80fd\u900f\u89c6\uff0c\u76f4\u63a5\u770b\u89c1\u6b21\u5143\u888b\u91cc\u7684\u4e1c\u897f\u5427\u3002</p><p>\u867d\u7136\u7591\u60d1\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u5426\u8ba4\u62b5\u8d56\u663e\u7136\u662f\u6ca1\u6709\u610f\u4e49\u7684\u3002\u6599\u60f3\u5bf9\u65b9\u4e5f\u4e0d\u81f3\u4e8e\u541e\u6ca1\u4ed6\u7684\u4e1c\u897f\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u771f\u8981\u62a2\u743c\u6069\u4e5f\u6ca1\u529e\u6cd5\u3002\u4ed6\u53d6\u51fa\u90a3\u9875\u9ec4\u91d1\u5377\u8f74\u9012\u8fc7\u53bb\uff0c\u5bf9\u65b9\u63a5\u8fc7\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u77a5\u4e86\u4e24\u773c\uff0c\u4f38\u51fa\u53f3\u624b\u98df\u6307\u5728\u4e0a\u9762\u865a\u865a\u52fe\u52d2\uff0c\u50cf\u662f\u753b\u4e86\u4e2a\u4ec0\u4e48\u7b26\u53f7\uff0c\u7136\u540e\u9012\u8fd8\u7ed9\u743c\u6069\u3002</p><p>\u201c\u4f60\u56de\u5230\u9634\u9b42\u57ce\u4ee5\u540e\uff0c\u628a\u5b83\u4ea4\u7ed9\u5e03\u96f7\u7eb3\u65af\u2014\u2014\u4ed6\u662f\u4f60\u7684\u4e0a\u7ea7\u5bf9\u5427\u3002\u544a\u8bc9\u5e03\u96f7\u7eb3\u65af\uff0c\u8ba9\u5b83\u628a\u8fd9\u9875\u5377\u8f74\u7ed9\u6cf0\u62c9\u66fc\u7279\u770b\u770b\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u4ed6\u4e0d\u60f3\u628a\u8fd9\u9875\u5377\u8f74\u4ea4\u51fa\u53bb\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4e5f\u662f\u81ea\u5df1\u53bb\u74dc\u7406\u5fb7\u65af\u57ce\u4e00\u8d9f\uff0c\u51fa\u751f\u5165\u6b7b\u5f97\u6765\u7684\u6218\u5229\u54c1\u3002\u867d\u7136\u81ea\u5df1\u73b0\u5728\u4e0d\u80fd\u9605\u8bfb\uff0c\u62ff\u7740\u4e5f\u6ca1\u7528\uff0c\u4f46\u8bf4\u4e0d\u5b9a\u5c06\u6765\u53c8\u80fd\u8bfb\u4e86\u5462\u3002\u4e0a\u7f34\u7684\u8bdd\uff0c\u672a\u514d\u592a\u4e8f\u4e86\u3002</p><p>\u8001\u8005\u770b\u51fa\u4e86\u743c\u6069\u7684\u5fc3\u601d\uff0c\u4ed6\u5fae\u5fae\u51b7\u7b11\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u4f60\u6839\u672c\u65e0\u6cd5\u9605\u8bfb\u5b83\uff0c\u62ff\u7740\u4e5f\u6ca1\u7528\uff0c\u53ea\u4f1a\u62db\u81f4\u7978\u60a3\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u5c31\u7b97\u4f60\u80fd\u9605\u8bfb\uff0c\u5bf9\u4f60\u4e5f\u6ca1\u7528\u3002\u4f60\u662f\u53d8\u5316\u5e08\uff0c\u6ca1\u5b66\u8fc7\u4efb\u4f55\u4ea1\u7075\u672f\uff0c\u6211\u6ca1\u770b\u9519\u5427\uff0c\u8fd9\u9875\u5377\u8f74\u4e0a\u8bb0\u8f7d\u7684\u5b8c\u5168\u662f\u4ea1\u7075\u672f\u77e5\u8bc6\uff0c\u4f60\u8981\u5b83\u505a\u4ec0\u4e48\uff0c\u4ea4\u4e0a\u53bb\u7684\u8bdd\uff0c\u81f3\u5c11\u8fd8\u80fd\u6362\u70b9\u5956\u52b1\u3002\u5f53\u7136\uff0c\u4f60\u8981\u613f\u610f\u7684\u8bdd\uff0c\u4e5f\u53ef\u4ee5\u7ed9\u4f60\u8001\u5e08\uff0c\u53cd\u6b63\u4e5f\u6ca1\u5dee\u3002\u201d</p><p>\u743c\u6069\u6ca1\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u8bf4\u7ed9\u5965\u6c83\u201c\u53cd\u6b63\u4e5f\u6ca1\u5dee\u201d\uff0c\u5965\u6c83\u53ef\u662f\u7cbe\u7814\u4ea1\u7075\u672f\u7684\u5927\u884c\u5bb6\uff0c\u8fd9\u9875\u5377\u8f74\u5bf9\u743c\u6069\u800c\u8a00\u662f\u5e9f\u7269\uff0c\u5bf9\u5965\u6c83\u5374\u5e94\u8be5\u662f\u5b9d\u8d1d\uff0c\u4ed6\u96be\u9053\u8fd8\u4f1a\u81ea\u5df1\u4e0d\u8981\uff0c\u9001\u7ed9\u9634\u9b42\u57ce\u4e3b\uff1f\u4e0d\u8fc7\u60f3\u4e0d\u660e\u767d\u7684\u4e8b\u60c5\u5c31\u6682\u65f6\u522b\u60f3\uff0c\u53cd\u6b63\u81ea\u5df1\u4e5f\u6b63\u51c6\u5907\u987a\u8def\u53bb\u62dc\u8bbf\u5965\u6c83\uff0c\u628a\u5377\u8f74\u9001\u4e0a\u53bb\u5f53\u89c1\u9762\u793c\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u80fd\u518d\u635e\u70b9\u597d\u5904\u3002\u800c\u4e14\u8bf4\u8d77\u6765\uff0c\u8fd9\u80a5\u5deb\u5996\u5bf9\u81ea\u5df1\u786e\u5b9e\u8fd8\u771f\u4e0d\u9519\uff0c\u5c3d\u5c3d\u5b66\u751f\u7684\u5fc3\u610f\u4e5f\u662f\u5e94\u8be5\u7684\u3002</p><p>\u62b1\u7740\u8fd9\u79cd\u6253\u7b97\uff0c\u4ed6\u7b54\u5e94\u4e0b\u6765\uff0c\u5c06\u5377\u8f74\u91cd\u65b0\u6536\u8d77\u3002\u5bf9\u65b9\u6ca1\u6709\u518d\u8c08\u6709\u5173\u9634\u9b42\u57ce\u7684\u8bdd\u9898\uff0c\u53cd\u800c\u5f00\u59cb\u8be2\u95ee\u743c\u6069\u81ea\u8eab\u7684\u4e8b\u60c5\uff0c\u540c\u65f6\u4e5f\u6307\u70b9\u4e86\u4ed6\u4e00\u4e9b\u6709\u5173\u9b54\u6cd5\u4e0a\u7684\u6280\u827a\u3002\u743c\u6069\u867d\u7136\u6709\u5965\u6c83\u8fd9\u4e2a\u5927\u5965\u672f\u5e08\u5f53\u8001\u5e08\uff0c\u4f46\u5176\u5b9e\u5728\u4e00\u8d77\u7684\u65f6\u95f4\u5e76\u4e0d\u957f\uff0c\u5f7c\u6b64\u7684\u4e13\u4e1a\u65b9\u5411\u53c8\u4e0d\u4e00\u81f4\uff0c\u5b66\u5230\u7684\u4e1c\u897f\u4e0d\u591a\uff0c\u53cd\u800c\u662f\u8fd9\u4e2a\u8001\u8005\uff0c\u5e94\u8be5\u662f\u5728\u53d8\u5316\u9b54\u6cd5\u4e0a\u9020\u8be3\u6781\u9ad8\uff0c\u968f\u53e3\u70b9\u62e8\u51e0\u53e5\uff0c\u5f80\u5f80\u5c31\u80fd\u8ba9\u743c\u6069\u8c41\u7136\u5f00\u6717\uff0c\u5f88\u591a\u4e00\u76f4\u5904\u4e8e\u74f6\u9888\u9636\u6bb5\u7684\u7591\u96be\u8fce\u5203\u800c\u89e3\u3002</p><p>\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u4e1c\u65b9\u5df2\u7ecf\u6709\u4e9b\u53d1\u767d\uff0c\u6f2b\u6f2b\u957f\u591c\u5373\u5c06\u8fc7\u53bb\u3002\u8001\u8005\u8f6c\u8eab\u51c6\u5907\u79bb\u5f00\uff0c\u8d70\u51fa\u51e0\u6b65\u53c8\u7a81\u7136\u7ad9\u5b9a\u3002\u201c\u5dee\u70b9\u5fd8\u4e86\u4ef6\u4e8b\u60c5\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u90a3\u4f4d\u53eb\u51db\u7684\u670b\u53cb\uff0c\u5979\u662f\u9f99\u8109\u5bf9\u5427\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u5979\u4f53\u5185\u878d\u5408\u4e86\u4e00\u4e2a\u5143\u7d20\u8f6c\u5316\u6cd5\u9635\uff1f\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u8fde\u5fd9\u95ee\uff0c\u201c\u83ab\u975e\u60a8\u77e5\u9053\u2026\u2026\u201d</p><p>\u201c\u5979\u592a\u901e\u5f3a\u4e86\uff0c\u6216\u8005\u8bf4\u592a\u4efb\u6027\u4e86\uff0c\u201d\u8001\u8005\u8bf4\uff0c\u201c\u90a3\u4e2a\u6cd5\u9635\u5f88\u5de7\u5999\uff0c\u5236\u4f5c\u8005\u4e00\u5b9a\u82b1\u8d39\u4e86\u65e0\u6570\u5fc3\u8840\u5728\u5185\uff0c\u4f46\u4e0d\u662f\u5979\u73b0\u5728\u5c31\u53ef\u4ee5\u8fd0\u7528\u7684\u3002\u518d\u8fc7\u4e94\u5e74\uff0c\u6216\u8005\u5341\u5e74\uff0c\u624d\u662f\u6bd4\u8f83\u5408\u9002\u7684\u65f6\u673a\uff0c\u73b0\u5728\u5979\u6839\u672c\u5c31\u627f\u53d7\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u60a8\u662f\u8bf4\uff0c\u5979\u5176\u5b9e\u4e0d\u662f\u751f\u75c5\uff0c\u662f\u8eab\u4f53\u627f\u53d7\u4e0d\u4f4f\u9b54\u6cd5\u9635\uff0c\u51fa\u73b0\u4e86\u53cd\u566c\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u8001\u8005\u8bf4\uff0c\u201c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u6211\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u4e0d\u8fc7\u53ef\u4ee5\u6307\u4e00\u6761\u8def\uff0c\u4f60\u4eec\u53bb\u963f\u65af\u5361\u7279\u62c9\uff0c\u8bd5\u7740\u78b0\u78b0\u8fd0\u6c14\u5427\u3002\u201d</p><p>\u963f\u65af\u5361\u7279\u62c9\uff1f</p><p>\u203b\u203b\u203b</p><p>\u201c\u4ed6\u8ba9\u6211\u4eec\u53bb\u963f\u65af\u5361\u7279\u62c9\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5927\u5bb6\u4e92\u76f8\u544a\u522b\uff0c\u5404\u5954\u4e1c\u897f\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u5357\u4e0b\u53bb\u5361\u4e3d\u73ca\u3002\u7b49\u4ed6\u4eec\u8d70\u540e\uff0c\u743c\u6069\u5c06\u6628\u665a\u53d1\u751f\u7684\u4e8b\u60c5\u7b80\u7565\u5bf9\u6885\u83f2\u65af\u8bf4\u4e86\u4e00\u904d\uff0c\u8bf4\u90a3\u4e2a\u4e0d\u77e5\u540d\u7684\u8001\u5bb6\u4f19\u5efa\u8bae\u4ed6\u4eec\u53bb\u963f\u65af\u5361\u7279\u62c9\u78b0\u78b0\u8fd0\u6c14\uff0c\u6216\u8bb8\u80fd\u6cbb\u597d\u51db\u3002</p><p>\u963f\u65af\u5361\u7279\u62c9\u662f\u6cf0\u745f\u5c14\u5317\u65b9\u5b89\u59c6\u5e1d\u56fd\u7684\u9996\u90fd\uff0c\u53f7\u79f0\u201c\u94f6\u5e01\u4e4b\u90fd\u201d\uff0c\u4e43\u662f\u8d39\u4f26\u5927\u9646\u4e0a\u8457\u540d\u7684\u7e41\u534e\u57ce\u5e02\u4e4b\u4e00\u3002\u5b83\u662f\u4e00\u4e2a\u6e2f\u53e3\u57ce\u5e02\uff0c\u4f4d\u4e8e\u5b89\u59c6\u7684\u6700\u897f\u8fb9\uff0c\u518d\u8fc7\u53bb\u5c31\u662f\u6d69\u701a\u65e0\u9645\u7684\u5b9d\u5251\u6d77\u4e86\u3002\u743c\u6069\u7b49\u4eba\u539f\u672c\u7684\u8def\u7ebf\u5b89\u6392\uff0c\u662f\u5230\u4e86\u745e\u5854\u6c76\u4e4b\u540e\uff0c\u6cbf\u5546\u8def\u5317\u4e0a\uff0c\u4ece\u5b89\u59c6\u5e1d\u56fd\u7684\u4e2d\u95f4\u76f4\u63a5\u7a7f\u8fc7\uff0c\u5e76\u4e0d\u7ecf\u8fc7\u963f\u65af\u5361\u7279\u62c9\uff0c\u5982\u679c\u8981\u53bb\u7684\u8bdd\uff0c\u90a3\u5c31\u5f97\u4e2d\u9014\u53d8\u5411\uff0c\u5f97\u591a\u7ed5\u4e00\u4e2a\u5927\u5f2f\u3002</p><p>\u201c\u8d70\u4e00\u8d9f\u5427\uff0c\u5e0c\u671b\u80fd\u6709\u597d\u8fd0\u6c14\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u770b\u4e86\u770b\u6c89\u7761\u4e2d\u7684\u51db\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u201c\u4e5f\u597d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u963f\u65af\u5361\u7279\u62c9\u662f\u5b89\u59c6\u9996\u5e9c\uff0c\u6709\u4e0d\u5c11\u795e\u6bbf\u548c\u9ad8\u9636\u7267\u5e08\u5728\u90a3\u91cc\uff0c\u5e94\u8be5\u80fd\u5e2e\u4e0a\u5fd9\u5427\u3002\u201d</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u8fd9\u662f\u6709\u4e9b\u81ea\u6211\u5b89\u6170\u7684\u60f3\u6cd5\u3002\u5982\u679c\u771f\u5982\u90a3\u4e2a\u8001\u8005\u6240\u8a00\uff0c\u51db\u5e76\u975e\u751f\u75c5\uff0c\u800c\u662f\u627f\u53d7\u4e0d\u4f4f\u9b54\u6cd5\u9635\u906d\u5230\u53cd\u566c\uff0c\u90a3\u518d\u9ad8\u660e\u7684\u7267\u5e08\u4e5f\u672a\u5fc5\u80fd\u89e3\u51b3\u95ee\u9898\u3002\u6700\u7406\u60f3\u7684\u65b9\u6848\uff0c\u81ea\u7136\u662f\u89e3\u94c3\u8fd8\u987b\u7cfb\u94c3\u4eba\uff0c\u5143\u7d20\u8f6c\u5316\u6cd5\u9635\u60f9\u51fa\u6765\u7684\u9ebb\u70e6\uff0c\u627e\u5f53\u65f6\u5236\u4f5c\u7684\u4eba\u5c31\u597d\u2014\u2014\u7136\u800c\u66ff\u51db\u5236\u4f5c\u6cd5\u9635\u7684\u4eba\u662f\u5979\u7236\u4eb2\uff0c\u5982\u4eca\u65e9\u5c31\u53bb\u4e16\u591a\u5e74\u4e86\uff0c\u6307\u671b\u4e0d\u4e0a\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u5c31\u7b97\u662f\u81ea\u6211\u5b89\u6170\uff0c\u90a3\u4e5f\u5f97\u53bb\u78b0\u78b0\u8fd0\u6c14\u4e86\u3002</p><p>\u5173\u4e8e\u601d\u601d\u4f53\u5185\u5f88\u53ef\u80fd\u9690\u85cf\u7740\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u7075\u9b42\u7684\u4e8b\u60c5\uff0c\u6885\u83f2\u65af\u4e5f\u9887\u89c9\u60ca\u8bb6\uff0c\u6ca1\u60f3\u5230\u201c\u5996\u77b3\u201d\u7684\u4f20\u8bf4\u8fd8\u771f\u662f\u4e8b\u5b9e\u3002\u53ea\u662f\u7ebf\u7d22\u592a\u5c11\uff0c\u4e5f\u4e0d\u8db3\u4ee5\u63a8\u65ad\u4ec0\u4e48\uff0c\u5f88\u96be\u731c\u51fa\u90a3\u4f4d\u5927\u5965\u672f\u5e08\u7684\u8eab\u4efd\u6765\u5386\u3002\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u5b58\u7eed\u4e09\u5343\u4f59\u5e74\uff0c\u9b54\u6cd5\u6587\u660e\u8f89\u714c\u53d1\u8fbe\uff0c\u5927\u5965\u672f\u5e08\u4e0d\u53ef\u8ba1\u6570\uff0c\u4f46\u56e0\u4e3a\u4e00\u671d\u9668\u706d\uff0c\u8bf8\u591a\u53f2\u6599\u90fd\u6ca1\u6709\u6d41\u4f20\u4e0b\u6765\uff0c\u8ba9\u540e\u4eba\u60f3\u7814\u7a76\u90fd\u65e0\u4ece\u7740\u624b\uff0c\u4e5f\u6ca1\u6cd5\u4ece\u4e2d\u63a8\u6572\u7422\u78e8\u3002</p><p>\u201c\u4e0d\u8fc7\u2026\u2026\u5927\u5965\u672f\u5e08\u867d\u591a\uff0c\u80fd\u6bd4\u5965\u6c83\u90fd\u80dc\u4e0a\u4e00\u7b79\u7684\u5374\u5be5\u5be5\u65e0\u51e0\uff0c\u518d\u52a0\u4e0a\u5bf9\u9634\u9b42\u57ce\u7684\u719f\u6089\uff0c\u8fd8\u6709\u5bd2\u51ac\u4e4b\u6212\u2026\u2026\u603b\u4e0d\u4f1a\u662f\u4ed6\u5427\uff1f\u53ef\u662f\u90a3\u5bb6\u4f19\u65e9\u5c31\u5df2\u7ecf\u6b7b\u4e86\u624d\u5bf9\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fc3\u4e2d\u6c89\u541f\u7740\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u4e2a\u6d88\u606f\u5f97\u5c3d\u5feb\u56de\u62a5\u7ed9\u5927\u4e3b\u6559\u3002</p><p>\u201c\u5148\u53bb\u745e\u5854\u6c76\u518d\u8bf4\u5427\uff0c\u201d\u5979\u6700\u540e\u8bf4\uff0c\u201c\u4e0d\u884c\u518d\u53bb\u963f\u65af\u5361\u7279\u62c9\u3002\u201d</p><p>\u4e00\u884c\u4eba\u79bb\u5f00\u5854\u683c\u6751\uff0c\u8d70\u4e86\u534a\u65e5\u62b5\u8fbe\u745e\u5854\u6c76\uff0c\u627e\u5230\u57ce\u4e2d\u7684\u63d0\u5c14\u795e\u6bbf\u8bf7\u7267\u5e08\u6cbb\u7597\uff0c\u7ed3\u679c\u4ee4\u4eba\u5931\u671b\u3002\u51db\u4f9d\u65e7\u5904\u4e8e\u6655\u6c89\u660f\u7761\u72b6\u6001\uff0c\u70e7\u5012\u662f\u6162\u6162\u9000\u4e86\uff0c\u4eba\u5374\u600e\u4e48\u90fd\u4e0d\u9192\uff0c\u7267\u5e08\u4eec\u4e5f\u675f\u624b\u65e0\u7b56\uff0c\u65bd\u5c55\u4e86\u5404\u79cd\u5f3a\u529b\u7684\u6cbb\u7597\u75ca\u6108\u6cd5\u672f\uff0c\u7528\u4f5c\u6750\u6599\u7684\u94bb\u77f3\u90fd\u82b1\u8d39\u4e86\u4e00\u5806\uff0c\u5168\u7136\u6ca1\u6709\u534a\u70b9\u8d77\u8272\u3002</p><p>\u53ea\u80fd\u53bb\u963f\u65af\u5361\u7279\u62c9\u4e86\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u53bb\u963f\u65af\u5361\u7279\u62c9\u7684\u8bdd\uff0c\u6709\u4ef6\u4e8b\u60c5\u8981\u6ce8\u610f\uff0c\u201d\u6885\u83f2\u65af\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u8fde\u5fd9\u63d0\u9192\uff0c\u201c\u51db\u5012\u7f62\u4e86\uff0c\u743c\u6069\u4f60\u548c\u8299\u857e\u72c4\u8981\u5c0f\u5fc3\uff0c\u5343\u4e07\u522b\u5728\u57ce\u91cc\u65bd\u6cd5\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5b89\u59c6\u662f\u4e2a\u654c\u89c6\u5deb\u5e08\u7684\u56fd\u5bb6\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u6cd5\u5f8b\u660e\u6587\u7981\u6b62\uff0c\u4f46\u4e0a\u81f3\u653f\u5e9c\uff0c\u4e0b\u81f3\u5e73\u6c11\uff0c\u57fa\u672c\u90fd\u5bf9\u5deb\u5e08\u62b1\u6709\u5f88\u6df1\u7684\u6210\u89c1\u3002\u5deb\u5e08\u5728\u57ce\u5e02\u4e2d\u7684\u6d3b\u52a8\u4f1a\u53d7\u5230\u76d1\u89c6\u548c\u9650\u5236\uff0c\u5c24\u5176\u963f\u65af\u5361\u7279\u62c9\u8fd9\u79cd\u9996\u5e9c\uff0c\u5018\u82e5\u4e0d\u53d6\u5f97\u2018\u65bd\u6cd5\u8bb8\u53ef\u8bc1\u2019\u7684\u8bdd\uff0c\u64c5\u81ea\u5728\u57ce\u4e2d\u65bd\u6cd5\uff0c\u54ea\u6015\u53ea\u662f\u53d8\u4e2a\u6700\u7b80\u5355\u7684\u620f\u6cd5\uff0c\u90a3\u90fd\u662f\u89e6\u72af\u5f8b\u6cd5\uff0c\u4f1a\u88ab\u7f5a\u6b3e\u751a\u81f3\u56da\u7981\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8fc7\u5206\uff1f\u201d</p><p>\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u4ed6\u53bb\u8fc7\u7684\u5730\u65b9\u4e5f\u4e0d\u5c11\uff0c\u4ece\u6ca1\u542c\u8fc7\u6709\u8fd9\u79cd\u4e25\u82db\u89c4\u77e9\u7684\u3002\u201c\u8fd9\u4e48\u4e00\u6765\u7684\u8bdd\uff0c\u5b89\u59c6\u5c82\u4e0d\u662f\u6ca1\u6709\u5deb\u5e08\u5b9a\u5c45\u4e86\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e5f\u4e0d\u662f\u3002\u201d</p><p>\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u5b89\u59c6\u5e1d\u56fd\u53f7\u79f0\u5546\u4eba\u9886\u5730\uff0c\u4e43\u662f\u5927\u9646\u4e0a\u8457\u540d\u7684\u7e41\u534e\u5bcc\u5eb6\u4e4b\u6240\u3002\u5c31\u5e38\u7406\u800c\u8a00\uff0c\u7ecf\u6d4e\u8d8a\u53d1\u8fbe\u7684\u5730\u533a\uff0c\u5deb\u5e08\u4e5f\u5c31\u8d8a\u591a\uff0c\u4f46\u56e0\u4e3a\u5b89\u59c6\u7684\u6b67\u89c6\u653f\u7b56\uff0c\u5168\u56fd\u5deb\u5e08\u6570\u91cf\u4e0d\u8db3\u767e\u4eba\uff0c\u5176\u4e2d\u5927\u90e8\u4efd\u90fd\u96b6\u5c5e\u4e8e\u4e00\u4e2a\u53eb\u505a\u201c\u515c\u5e3d\u5deb\u5e08\u516c\u4f1a\u201d\u7684\u7ec4\u7ec7\u3002\u8fd9\u4e2a\u516c\u4f1a\u57fa\u672c\u5784\u65ad\u4e86\u5b89\u59c6\u5e1d\u56fd\u7684\u9b54\u6cd5\u7814\u7a76\uff0c\u53d6\u5f97\u653f\u5e9c\u6709\u9650\u5ea6\u7684\u652f\u6301\uff0c\u6392\u65a5\u5916\u6765\u5deb\u5e08\uff0c\u201c\u65bd\u6cd5\u8bb8\u53ef\u8bc1\u201d\u5c31\u662f\u7531\u4ed6\u4eec\u8d1f\u8d23\u9881\u53d1\uff0c\u5018\u82e5\u6709\u4eba\u65e0\u8bc1\u65bd\u6cd5\uff0c\u90a3\u4e48\u4e00\u5207\u9ebb\u70e6\u4e5f\u90fd\u7531\u4ed6\u4eec\u51fa\u9762\u5904\u7406\uff0c\u7b97\u662f\u534a\u5b98\u65b9\u7ec4\u7ec7\u3002</p><p>\u65bd\u6cd5\u8bb8\u53ef\u8bc1\u7684\u4ef7\u683c\u6781\u5176\u6602\u8d35\uff0c\u800c\u4e14\u529e\u7406\u7f13\u6162\uff0c\u624b\u7eed\u7e41\u591a\uff0c\u4eca\u5929\u53bb\u4ea4\u94b1\u7533\u8bf7\u7684\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u8fc7\u4e00\u5e74\u534a\u8f7d\u624d\u4f1a\u53d1\u4e0b\u6765\uff0c\u9ebb\u70e6\u5f97\u5f88\u3002\u743c\u6069\u7b49\u4eba\u53ea\u662f\u8fc7\u8def\uff0c\u81ea\u7136\u6ca1\u5fc5\u8981\u8003\u8651\uff0c\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u5728\u57ce\u4e2d\u5c31\u8981\u683c\u5916\u5c0f\u5fc3\uff0c\u968f\u4fbf\u7528\u4e2a\u4ec0\u4e48\u98de\u884c\u4f20\u9001\u6cd5\u672f\uff0c\u7acb\u523b\u5c31\u4f1a\u6709\u4e00\u5e2e\u515c\u5e3d\u5deb\u5e08\u51fa\u6765\u6293\u4f60\u3002</p><p>\u201c\u5deb\u5e08\u65bd\u6cd5\u4f1a\u6709\u9ebb\u70e6\uff0c\u90a3\u7267\u5e08\u5462\uff1f\u201d</p><p>\u201c\u8bf4\u5f97\u66f4\u51c6\u786e\u70b9\uff0c\u662f\u5965\u672f\u8005\u6709\u9ebb\u70e6\uff0c\u795e\u672f\u8005\u4e0d\u53d7\u5e72\u6d89\u3002\u8299\u8389\u5a05\u5c31\u6ca1\u4e8b\uff0c\u4e0d\u8fc7\u5979\u662f\u838e\u5c14\u7267\u5e08\u2026\u2026\u4e5f\u6ca1\u5173\u7cfb\uff0c\u5728\u6211\u7684\u8bb0\u5fc6\u91cc\uff0c\u5b89\u59c6\u5e76\u4e0d\u7981\u6b62\u90aa\u795e\u4fe1\u4ef0\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5deb\u5e08\u3001\u541f\u6e38\u8bd7\u4eba\u8fd9\u79cd\u4e13\u95e8\u88ab\u6253\u51fb\uff0c\u7267\u5e08\u3001\u5fb7\u9c81\u4f9d\u4e4b\u7c7b\u5c31\u53ef\u4ee5\u5927\u6447\u5927\u6446\uff0c\u884c\u8d70\u65e0\u788d\u3002\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u662f\u56e0\u4e3a\u5b89\u59c6\u5e1d\u56fd\u5b97\u6559\u52bf\u529b\u6781\u76db\uff0c\u5c24\u5176\u662f\u963f\u65af\u5361\u7279\u62c9\uff0c\u5404\u5927\u6559\u4f1a\u90fd\u5efa\u6709\u795e\u6bbf\u3002\u63d0\u5c14\u6559\u4f1a\u6709\u516d\u5927\u6559\u533a\uff0c\u5b89\u59c6\u662f\u5176\u4e2d\u4e4b\u4e00\uff08\u5305\u62ec\u5b89\u59c6\u5e1d\u56fd\u3001\u535a\u5f97\u4e4b\u95e8\u7b49\u5251\u6e7e\u4e00\u5e26\uff09\uff0c\u603b\u90e8\u4fbf\u5728\u963f\u65af\u5361\u7279\u62c9\uff1b\u5546\u4e1a\u5973\u795e\u6c83\u91d1\u7684\u6559\u4f1a\uff0c\u603b\u90e8\u548c\u6559\u7687\u201c\u5723\u5e01\u201d\u4e5f\u5728\u6b64\u3002\u5176\u4ed6\u5982\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u3001\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u3001\u6b7b\u795e\u514b\u5170\u6c83\u3001\u7231\u795e\u6dd1\u5a1c\u7b49\u9ad8\u7b49\u795e\uff0c\u52bf\u529b\u4e5f\u90fd\u9887\u4e3a\u96c4\u539a\u3002\u603b\u4f53\u4e0a\u6765\u8bf4\u5012\u662f\u5448\u73b0\u767e\u82b1\u9f50\u653e\u7684\u67b6\u52bf\uff0c\u4e0d\u50cf\u6cf0\u745f\u5c14\u5e1d\u56fd\uff0c\u63d0\u5c14\u6559\u4f1a\u4e00\u5bb6\u72ec\u5927\uff0c\u5b8c\u5168\u5784\u65ad\u3002\u4f46\u4e0d\u7ba1\u662f\u4ec0\u4e48\u6559\u4f1a\uff0c\u57fa\u672c\u90fd\u6392\u65a5\u5deb\u5e08\uff08\u9b54\u6cd5\u5973\u795e\u7684\u6559\u4f1a\u4f8b\u5916\uff09\uff0c\u6240\u4ee5\u4fbf\u4f1a\u6709\u79cd\u79cd\u82db\u523b\u9650\u5236\u3002</p><p>\u201c\u77e5\u9053\u4e86\uff0c\u8bb0\u5f97\u4e0d\u5728\u57ce\u4e2d\u65bd\u6cd5\u5c31\u884c\u3002\u201d</p><p>\u5deb\u5e08\u53ea\u8981\u4e0d\u65bd\u6cd5\uff0c\u4e0d\u5728\u624b\u4e0a\u63d0\u7740\u6cd5\u6756\u70ab\u8000\uff0c\u4e00\u822c\u4eba\u4e5f\u770b\u4e0d\u51fa\u8eab\u4efd\uff0c\u5deb\u5e08\u888d\u548c\u666e\u901a\u7684\u957f\u888d\uff0c\u4ece\u5916\u8868\u770b\u5f0f\u6837\u662f\u5dee\u4e0d\u591a\u7684\uff0c\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u7279\u5f81\u3002\u867d\u7136\u5728\u540c\u884c\u9762\u524d\u5f88\u96be\u63a9\u9970\u8eab\u4efd\uff0c\u4f46\u7792\u8fc7\u666e\u901a\u5e02\u6c11\u603b\u4e0d\u4f1a\u6709\u95ee\u9898\u3002</p><p>\u5728\u745e\u5854\u6c76\u96c7\u4e86\u9a6c\u8f66\uff0c\u4f17\u4eba\u6cbf\u7740\u5546\u9053\uff0c\u987a\u5229\u901a\u8fc7\u8fb9\u5883\u7684\u5173\u5361\uff0c\u8fdb\u5165\u5b89\u59c6\u5e1d\u56fd\u3002\u4e00\u8def\u75be\u884c\u6beb\u4e0d\u505c\u7559\uff0c\u5728\u7b2c\u4e09\u5929\u4e0a\u5348\u7ec8\u4e8e\u62b5\u8fbe\u4e86\u963f\u65af\u5361\u7279\u62c9\u3002</p><p>\u8fdb\u57ce\u4e4b\u540e\uff0c\u4e5f\u65e0\u6687\u89c2\u8d4f\u6e38\u89c8\uff0c\u76f4\u63a5\u5c31\u5954\u795e\u6bbf\u800c\u53bb\u3002\u51e0\u5e74\u524d\uff0c\u963f\u65af\u5361\u7279\u62c9\u5f00\u53d1\u57ce\u5317\u7684\u8352\u5730\uff0c\u5efa\u7acb\u4e86\u4e00\u5757\u4e13\u95e8\u7684\u201c\u795e\u6bbf\u533a\u201d\uff0c\u91cc\u9762\u6709\u63d0\u5c14\u3001\u5170\u68ee\u5fb7\u5c14\u3001\u6b27\u683c\u739b\u3001\u514b\u5170\u6c83\u3001\u4f0a\u5c14\u739b\u7279\u3001\u6258\u59c6\u3001\u6d77\u59c6\u4e03\u4f4d\u795e\u7947\u7684\u795e\u6bbf\u3002\u5176\u4ed6\u5982\u82cf\u4f26\u3001\u6dd1\u5a1c\u3001\u6c83\u91d1\u3001\u8fea\u5948\u5c14\u3001\u5b89\u535a\u7406\u7b49\u795e\u660e\u7684\u795e\u6bbf\uff0c\u5206\u6563\u5728\u5176\u4ed6\u5404\u57ce\u533a\uff0c\u6797\u6797\u603b\u603b\uff0c\u4e00\u5171\u6709\u8fd1\u4e8c\u5341\u5bb6\u3002</p><p>\u53ef\u60dc\u4e8b\u5b9e\u8bc1\u660e\uff0c\u4e0d\u662f\u6570\u91cf\u591a\u5c31\u4e00\u5b9a\u6709\u7528\u7684\uff0c\u534a\u65e5\u65f6\u95f4\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5e26\u7740\u51db\u628a\u795e\u6bbf\u533a\u7684\u4e03\u5ea7\u795e\u6bbf\u90fd\u8dd1\u904d\u4e86\uff0c\u7ed3\u679c\u4f9d\u65e7\u6ca1\u4ec0\u4e48\u8fdb\u5c55\u3002\u8f6e\u5230\u7597\u4f24\u6cbb\u75c5\u8fd9\u65b9\u9762\u7684\u672c\u4e8b\uff0c\u5404\u5927\u795e\u6bbf\u4e4b\u4e2d\uff0c\u4ee5\u5170\u68ee\u5fb7\u5c14\u7684\u7267\u5e08\u6700\u4e3a\u4e13\u4e1a\uff0c\u7136\u800c\u5c31\u8fde\u4ed6\u4eec\u4e5f\u62ff\u4e0d\u51fa\u4ec0\u4e48\u597d\u529e\u6cd5\u3002</p><p>\u6b63\u5f53\u5927\u5bb6\u90fd\u5df2\u7ecf\u8981\u653e\u5f03\u5e0c\u671b\u7684\u65f6\u5019\uff0c\u5728\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u7684\u795e\u6bbf\u91cc\uff0c\u5012\u662f\u5f97\u5230\u4e86\u4e00\u6761\u76f8\u5bf9\u6709\u4ef7\u503c\u7684\u8baf\u606f\u3002</p><p>\u201c\u8fd9\u79cd\u75c7\u72b6\uff0c\u6211\u66fe\u7ecf\u5728\u4e00\u672c\u53e4\u7c4d\u4e0a\u89c1\u8fc7\u7c7b\u4f3c\u7684\u8bb0\u8f7d\uff0c\u201d\u6b27\u683c\u739b\u4e3b\u6559\u8bf4\uff0c\u4ed6\u662f\u4e00\u4f4d\u5934\u53d1\u82b1\u767d\u7684\u8001\u4eba\uff0c\u6309\u7167\u6559\u4f1a\u7684\u89c4\u77e9\uff0c\u5e94\u8be5\u79f0\u547c\u4ed6\u4e3a\u201c\u547d\u540d\u8005\u201d\uff0c\u201c\u5979\u4e4b\u6240\u4ee5\u660f\u7761\uff0c\u662f\u56e0\u4e3a\u627f\u53d7\u4e0d\u4f4f\u5143\u7d20\u8f6c\u5316\u6cd5\u9635\u7684\u529b\u91cf\uff0c\u906d\u5230\u53cd\u566c\u6240\u81f4\u3002\u6240\u4ee5\u8981\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u6709\u4e24\u6761\u8def\u53ef\u4ee5\u9009\u62e9\uff0c\u6216\u8005\u628a\u8fd9\u4e2a\u6cd5\u9635\u6d88\u53bb\uff0c\u6446\u8131\u8d1f\u62c5\uff1b\u6216\u8005\u8ba9\u5979\u81ea\u8eab\u5f97\u5230\u5927\u5e45\u5ea6\u7684\u5f3a\u5316\uff0c\u8db3\u4ee5\u627f\u53d7\u6cd5\u9635\u3002\u201d</p><p>\u4e4b\u6240\u4ee5\u8bf4\u662f\u201c\u76f8\u5bf9\u201d\u6709\u4ef7\u503c\uff0c\u662f\u56e0\u4e3a\u8fd9\u4e24\u4e2a\u9009\u62e9\u90fd\u5f88\u96be\u529e\u5230\u3002\u51db\u7684\u7236\u4eb2\u5df2\u7ecf\u628a\u5143\u7d20\u8f6c\u5316\u6cd5\u9635\u548c\u5979\u7684\u8eab\u4f53\u878d\u5408\u4e3a\u4e00\uff0c\u743c\u6069\u5b9e\u5728\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u80fd\u591f\u5728\u4e0d\u4f24\u5bb3\u51db\u7684\u524d\u63d0\u4e0b\u6d88\u53bb\u6cd5\u9635\u3002\u81f3\u4e8e\u8bf4\u5927\u5e45\u5ea6\u5f3a\u5316\u5979\u81ea\u8eab\u2026\u2026\u600e\u4e48\u5f3a\u5316\uff0c\u51db\u53c8\u4e0d\u662f\u673a\u5668\u4eba\uff0c\u80fd\u591f\u66f4\u6362\u96f6\u4ef6\u5347\u7ea7\u6362\u4ee3\u3002\u5c31\u7b97\u73b0\u5728\u5f00\u59cb\u6bcf\u5929\u5582\u8865\u54c1\uff0c\u53ea\u6015\u4e5f\u6765\u4e0d\u53ca\u5427\u3002</p><p>\u7136\u800c\uff0c\u547d\u540d\u8005\u7684\u8bdd\u8bed\u4e2d\uff0c\u4eff\u4f5b\u9690\u542b\u7740\u67d0\u79cd\u6697\u793a\uff0c\u800c\u6885\u83f2\u65af\u542c\u61c2\u4e86\u3002\u5979\u76b1\u8d77\u7709\u5934\uff0c\u6c89\u541f\u7247\u523b\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u6ca1\u5f00\u53e3\u3002\u8c22\u8fc7\u547d\u540d\u8005\uff0c\u62b1\u8d77\u51db\uff0c\u548c\u743c\u6069\u8d70\u51fa\u4e86\u795e\u6bbf\u3002</p></article>"}}}]);
