(window.webpackJsonp=window.webpackJsonp||[]).push([[16],{39:function(p){p.exports={chapterTitle:null,title:"\u7b2c16\u7ae0 \u610f\u5916\u7684\u8f6c\u673a",content:"<article><h2>\u7b2c16\u7ae0 \u610f\u5916\u7684\u8f6c\u673a</h2><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ba4\u51fa\u5c0f\u5973\u5b69\u6765\uff0c\u6f02\u4eae\u7684\u8138\u86cb\u4e0a\u5e26\u7740\u505a\u5669\u68a6\u7684\u60ca\u6050\u795e\u60c5\uff0c\u8ba9\u4eba\u770b\u4e86\u8981\u5fc3\u788e\u3002</p><p>\u3000\u3000\u5c0f\u5973\u5b69\u4e5f\u8ba4\u51fa\u5f20\u606a\uff0c\u5634\u5df4\u5f20\u7740\u7a7a\u558a\u4e86\u4e24\u58f0\uff0c\u6ca1\u6709\u58f0\u97f3\u53d1\u51fa\u53bb\uff0c\u624b\u6307\u7740\u5f20\u606a\uff0c\u4ece\u5f90\u5b66\u5e73\u8fd8\u6ca1\u62b1\u7ed3\u5b9e\u7684\u6000\u91cc\u6323\u624e\u4e0b\u6765\uff0c\u5dee\u70b9\u6454\u5012\u5728\u5730\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6211\u90fd\u6ca1\u62b1\u597d\uff0c\u4f60\u5c31\u653e\u4ec0\u4e48\u653e\uff0c\u6454\u7740\u4e86\uff0c\u600e\u4e48\u8ddf\u5979\u5988\u4ea4\u5f85\uff1f\u201d\u5f90\u5b66\u5e73\u5927\u58f0\u62b1\u6028\uff0c\u5374\u770b\u89c1\u5b59\u5973\u8c22\u82b7\u5f64\u671d\u968f\u5f20\u77e5\u884c\u8fc7\u6765\u7684\u5c11\u5e74\u6251\u8fc7\u53bb\uff0c\u51fa\u4e4e\u4efb\u4f55\u4eba\u7684\u610f\u6599\uff0c\u5979\u4e00\u628a\u62b1\u4f4f\u5c11\u5e74\u7684\u8170\uff0c\u624b\u62b1\u4e0d\u8fc7\u6765\uff0c\u7d27\u7d27\u7684\u6293\u4f4f\u5c11\u5e74\u7684\u8863\u670d\uff0c\u597d\u50cf\u6015\u4f1a\u5931\u53bb\u5fc3\u7231\u7684\u4e1c\u897f\u4e00\u6837\uff0c\u7d27\u7d27\u7684\u62b1\u4f4f\u3002</p><p>\u3000\u300018\u65e5\u90a3\u5929\uff0c\u65b0\u5149\u9020\u7eb8\u5382\u8fd1\u767e\u540d\u804c\u5de5\u5230\u5e02\u653f\u5e9c\u524d\u7684\u949f\u697c\u5e7f\u573a\u95f9\u4e8b\uff0c\u6297\u8bae\u57ce\u5357\u533a\u653f\u5e9c\u4e0d\u987e\u804c\u5de5\u5229\u76ca\uff0c\u5c06\u65b0\u5149\u9020\u7eb8\u5382\u8fc1\u5230\u8fdc\u79bb\u5e02\u533a\u7684\u5357\u90ca\u5de5\u4e1a\u56ed\u533a\uff0c\u65b0\u5382\u5b50\u5efa\u6210\u540e\u7f3a\u4e4f\u8d44\u91d1\uff0c\u65e0\u6cd5\u8d2d\u4e70\u539f\u6599\u8fdb\u884c\u751f\u4ea7\uff0c\u533a\u653f\u5e9c\u5374\u6025\u7740\u5173\u505c\u8001\u5382\u3002\u5e02\u91cc\u5c06\u897f\u57ce\u533a\u7684\u5927\u534a\u4ea4\u5de1\u8b66\u4e34\u65f6\u62bd\u8c03\u5230\u949f\u697c\u5e7f\u573a\u7ef4\u6301\u6b21\u5e8f\uff0c\u4ee5\u81f4\u9152\u540e\u9a7e\u8f66\u7684\u53f8\u673a\u5c06\u6e23\u571f\u8f66\u4ece\u5317\u5173\u8fdb\u57ce\uff0c\u4e00\u76f4\u5f00\u5230\u5317\u8857\uff0c\u6ca1\u6709\u4e00\u540d\u4ea4\u8b66\u5c06\u8fd9\u8f86\u8d85\u901f\u53c8\u9152\u540e\u9a7e\u9a76\u7684\u6e23\u571f\u8f66\u62e6\u4e0b\uff0c\u5728\u5317\u8857\u9020\u6210\u7279\u5927\u4ea4\u901a\u4e8b\u6545\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u523018\u65e5\u6b7b\u4e8e\u6d77\u5dde\u7279\u5927\u8f66\u7978\u4e0b\u7684\u9752\u5e74\u7adf\u662f\u5f90\u5b66\u5e73\u7684\u513f\u5b50\uff0c\u770b\u7740\u5c0f\u5973\u5b69\u695a\u695a\u53ef\u601c\u7684\u795e\u60c5\uff0c\u5927\u6982\u662f\u505a\u7740\u4e0e\u8f66\u7978\u6709\u5173\u7684\u5669\u68a6\u5427\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u5c06\u5c0f\u5973\u5b69\u62b1\u8d77\u6765\uff0c\u653e\u5230\u81ea\u5df1\u7684\u819d\u76d6\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6765\uff0c\u82b7\u5f64\uff0c\u6211\u4eec\u4e0a\u697c\u7761\u89c9\u53bb\uff0c\u54e5\u54e5\u8fd8\u6709\u4e8b\u60c5\u2026\u2026\u201d\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u53cc\u624b\u4ece\u5c0f\u5973\u5b69\u7684\u814b\u4e0b\u7a7f\u8fc7\u6765\uff0c\u8981\u5c06\u5c0f\u5973\u5b69\u62b1\u8fc7\u53bb\uff0c\u5c0f\u5973\u5b69\u5374\u6b7b\u6b7b\u6402\u4f4f\u5f20\u606a\u7684\u8116\u5b50\uff0c\u5634\u91cc\u201c\u4e2b\u4e2b\u201d\u7684\u53d1\u51fa\u5636\u54d1\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u4e0d\u80af\u677e\u624b\u3002</p><p>\u3000\u3000\u5ba2\u5385\u91cc\u7684\u4eba\u90fd\u8ba9\u8fd9\u4e00\u53d8\u6545\u641e\u8499\u4e86\uff0c\u5f20\u606a\u6b20\u7740\u8eab\u5b50\u7ad9\u8d77\u6765\uff0c\u8116\u5b50\u7ed9\u5c0f\u5973\u5b69\u52d2\u5f97\u6709\u4e9b\u75bc\uff0c\u4e0e\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u4e00\u8d77\u6258\u4f4f\u5c0f\u5973\u5b69\u7684\u8eab\u4f53\uff0c\u5c31\u6015\u5979\u529b\u6c14\u5c0f\u6389\u4e0b\u6765\uff0c\u4e00\u8138\u5c34\u5c2c\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u662f\u6551\u82b7\u5f64\u7684\u90a3\u4e2a\u5b69\u5b50\uff1f\u201d\u5c0f\u674e\u6700\u5148\u9192\u609f\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6307\u7740\u5f20\u606a\u95ee\uff0c\u201c\u662f\u4e0d\u662f\u4f6018\u65e5\u90a3\u5929\u4ece\u6e23\u571f\u8f66\u4e0b\u6551\u4e86\u82b7\u5f64\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u5f53\u65f6\u600e\u4e48\u5c31\u8d70\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5f53\u65f6\u4e5f\u5413\u8499\u4e86\uff0c\u89c1\u5979\u6ca1\u6709\u4e8b\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\uff0c\u7a00\u91cc\u7cca\u6d82\u7684\u5c31\u5148\u8d70\u4e86\u2026\u2026\u201d\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u677e\u5f00\u624b\uff0c\u5f20\u606a\u5c06\u5c0f\u5973\u5b69\u62b1\u5728\u6000\u91cc\uff0c\u4ed6\u4e5f\u6709\u4e9b\u559c\u51fa\u671b\u5916\uff0c\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u597d\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4f60\u6551\u4e86\u82b7\u5f64\uff1f\u201d\u5f90\u5b66\u5e73\u4e0b\u610f\u8bc6\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u6ca1\u7b49\u5f20\u606a\u56de\u7b54\uff0c\u626d\u5934\u5429\u5490\u4fdd\u59c6\uff0c\u201c\u5c0f\u5468\uff0c\u8d76\u7d27\u7ed9\u82b7\u5f64\u5979\u5988\u5988\u6253\u7535\u8bdd\uff0c\u544a\u8bc9\u5979\uff0c\u4ece\u8f66\u8f6e\u4e0b\u6551\u82b7\u5f64\u7684\u5b69\u5b50\u627e\u5230\u4e86\uff0c\u5c31\u5728\u54b1\u4eec\u5bb6\uff0c\u8ba9\u5979\u8d76\u5feb\u56de\u6765\u3002\u201d\u5f90\u5b66\u5e73\u58f0\u97f3\u5f88\u6fc0\u52a8\uff0c\u8d70\u8fc7\u6765\u8981\u63e1\u5f20\u606a\u7684\u624b\uff0c\u53ea\u662f\u5f20\u606a\u62b1\u7740\u82b7\u5f64\uff0c\u4ed6\u4fbf\u6309\u7740\u4ed6\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u5750\u4e0b\u6765\uff0c\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u597d\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u5728\u65c1\u8fb9\u8bf4\uff1a\u201c\u6211\u4eec\u8ba9\u6d77\u5dde\u7535\u89c6\u53f0\u5e2e\u5fd9\u627e\u4f60\uff0c\u4f60\u8fd9\u51e0\u5929\u6ca1\u6709\u770b\u7535\u89c6\u5417\uff1f\u5fd7\u660e\u5c31\u8fd9\u6837\u8d70\u4e86\uff0c\u6211\u4eec\u5df2\u7ecf\u4f24\u900f\u4e86\u5fc3\uff0c\u8981\u662f\u82b7\u5f64\u518d\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u8c01\u4e5f\u6491\u4e0d\u4f4f\u554a\u3002\u201d\u8bf4\u7740\u8bdd\uff0c\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u5f00\u59cb\u4f38\u624b\u62b9\u8d77\u773c\u6cea\u6765\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5f7b\u5e95\u7ed9\u8fd9\u4e00\u53d8\u6545\u641e\u8499\u4e86\u5934\uff0c\u6709\u4e9b\u532a\u5937\u6240\u601d\uff0c\u770b\u7740\u513f\u5b50\uff1a\u201c\u4f60\u2026\u2026\u4ec0\u4e48\u65f6\u5019\u6551\u4e86\u5f90\u4e66\u8bb0\u7684\u5b59\u5973\u2026\u2026\u600e\u4e48\u6ca1\u542c\u4f60\u8bf4\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u662f18\u65e5\uff0c\u6211\u9ad8\u70e7\u521a\u9000\u7684\u90a3\u5929\uff0c\u6211\u5728\u5bb6\u91cc\u95f7\u5f97\u96be\u53d7\uff0c\u5c31\u5230\u5317\u8857\u8d70\u8d70\uff0c\u5c31\u2026\u2026\u6211\u5f53\u65f6\u62b1\u7740\u5979\uff0c\u8f66\u5b50\u5c31\u64e6\u7740\u6211\u4eec\u4fe9\u7684\u9f3b\u5b50\u5c16\u62d0\u8fc7\u53bb\uff0c\u5413\u8499\u4e86\uff0c\u5c31\u60f3\u628a\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\uff0c\u201d\u5f20\u606a\u626d\u8fc7\u5934\u6765\u5bf9\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u8bf4\uff0c\u201c\u6211\u8ddf\u6211\u7238\u8fd9\u51e0\u5929\u90fd\u6ca1\u6709\u65f6\u95f4\u770b\u7535\u89c6\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f60\u4eec\u5728\u627e\u6211\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u6765\u7740\uff1f\u201d\u5f90\u5b66\u5e73\u62cd\u62cd\u8111\u888b\uff0c\u6307\u7740\u5f20\u606a\uff0c\u4ed6\u773c\u775b\u91cc\u84c4\u7740\u6cea\uff0c\u60c5\u7eea\u6709\u4e9b\u6fc0\u52a8\u3002\u5bf9\u4ed6\u6765\u8bf4\uff0c\u513f\u5b50\u6b7b\u4e8e\u8f66\u7978\uff0c\u5b59\u5973\u6210\u4e3a\u4ed6\u6700\u5927\u7684\u5b89\u6170\uff0c\u5bf9\u4ece\u8f66\u8f6e\u4e0b\u6551\u4e0b\u5b59\u5973\u7684\u5c11\u5e74\uff0c\u5fc3\u91cc\u5341\u5206\u7684\u611f\u6fc0\u3002\u542c\u513f\u5ab3\u5987\u8bf4\uff0c\u5f53\u65f6\u7684\u60c5\u5f62\u5341\u5206\u7684\u5371\u9669\uff0c\u5c11\u5e74\u51e0\u4e4e\u662f\u51b2\u5230\u8f66\u8f6e\u4e0b\u5c06\u4eba\u7ed9\u62a2\u4e86\u51fa\u6765\uff0c\u5c11\u5e74\u5341\u5206\u7684\u52c7\u6562\u3002\u66f4\u52a0\u96be\u5f97\u7684\uff0c\u5c11\u5e74\u5728\u6551\u4e0b\u4eba\u4e4b\u540e\uff0c\u6ca1\u6709\u7559\u4e0b\u540d\u5b57\u5c31\u6084\u6084\u7684\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6b63\u51c6\u5907\u8fc7\u4e86\u8fd9\u51e0\u5929\uff0c\u8ba9\u6d77\u5dde\u5e02\u65e0\u8bba\u5982\u4f55\u90fd\u8981\u628a\u8fd9\u5c11\u5e74\u627e\u5230\uff0c\u8981\u597d\u597d\u7684\u611f\u8c22\u4ed6\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5bf9\uff0c\u5bf9\uff0c\u662f\u53eb\u5f20\u606a\uff0c\u4f60\u7238\u7238\u8fdb\u95e8\u8bf4\u8d77\u8fc7\uff0c\u6211\u6ca1\u542c\u5fc3\u91cc\u53bb\uff0c\u4f60\u4e0d\u8981\u751f\u4f2f\u4f2f\u7684\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8001\u5f90\uff0c\u4eba\u5bb6\u4e0a\u95e8\u662f\u5ba2\uff0c\u4f60\u600e\u4e48\u628a\u4eba\u5bb6\u645e\u5728\u8fd9\u91cc\uff1f\u5e78\u4e8f\u82b7\u5f64\u9192\u4e86\uff0c\u8981\u4e0d\uff0c\u4e0d\u5c31\u53c8\u9519\u8fc7\u53bb\u4e86\u5417\uff1f\u201d\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u62b9\u6389\u8138\u988a\u4e0a\u7684\u6cea\u6c34\uff0c\u201c\u8001\u5f90\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4e0d\u80fd\u5e2e\u4ed6\u4eec\u529e\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5c34\u5c2c\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u2026\u2026\u6d77\u5dde\u6b63\u5728\u8c03\u67e5\u526f\u5e02\u957f\u5510\u5b66\u8c26\u53d7\u8d3f\u7684\u6848\u5b50\uff0c\u6211\u4eec\u53d1\u73b0\u4e00\u4e9b\u6750\u6599\uff0c\u8d76\u5230\u7701\u91cc\u6765\u5411\u5f90\u4e66\u8bb0\u6c47\u62a5\u7684\uff0c\u5f90\u4e66\u8bb0\u8981\u6211\u4eec\u76f4\u63a5\u5411\u7701\u68c0\u5bdf\u9662\u6c47\u62a5\u3002\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6307\u7740\u684c\u4e0a\u90a3\u53e0\u5f20\u77e5\u884c\u5e26\u8fc7\u6765\u7684\u8d44\u6599\uff0c\u8bf4\u9053\uff1a\u201c\u8d81\u7740\u68c0\u5bdf\u9662\u7684\u540c\u5fd7\u8fc7\u6765\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u4f60\u8ddf\u6211\u5230\u4e66\u623f\u6765\uff0c\u5c06\u60c5\u51b5\u518d\u6c47\u62a5\u4e00\u4e0b\uff0c\u8bf4\u8bf4\u4f60\u4eec\u662f\u600e\u6837\u5f97\u5230\u8fd9\u4e9b\u6750\u6599\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f88\u591a\u60c5\u51b5\u662f\u5c0f\u606a\u53d1\u73b0\u7684\u2026\u2026\u201d\u5f20\u77e5\u884c\u6307\u4e86\u6307\u4e86\u513f\u5b50\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u4fa7\u8fc7\u5934\u6765\u770b\u4e86\u770b\u82b7\u5f64\uff0c\u5f20\u606a\u8bf4\uff1a\u201c\u5979\u7761\u89c9\u4e86\uff0c\u6211\u62b1\u7740\u5979\u6ca1\u4e8b\u3002\u201d</p><p>\u3000\u3000\u5c0f\u5973\u5b69\u5373\u4f7f\u662f\u7761\u7740\u4e86\uff0c\u624b\u8fd8\u7d27\u7d27\u73af\u62b1\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u751f\u6015\u4ed6\u4f1a\u79bb\u5f00\uff0c\u8138\u4e0a\u5374\u6ca1\u6709\u60ca\u6050\u7684\u795e\u60c5\uff0c\u6062\u590d\u5c0f\u5b69\u4ece\u5bb9\u7684\u7761\u5bb9\u3002\u5f90\u5b66\u5e73\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff1a\u201c\u8fd8\u662f\u5979\u8fd9\u51e0\u5929\u7b2c\u4e00\u6b21\u7761\u8fd9\u4e48\u6c89\uff0c\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u5c0f\u58f0\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u4fdd\u59c6\u9000\u4e86\u4e0b\u53bb\uff0c\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u4e5f\u5750\u4e86\u4e0b\u6765\u3002</p><p>\u3000\u3000\u521a\u624d\u5c06\u6750\u6599\u4ea4\u7ed9\u5f90\u5b66\u5e73\u770b\u4e86\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u7ec6\u8bf4\u83b7\u5f97\u8fd9\u4e9b\u6750\u6599\u7684\u8fc7\u7a0b\uff0c\u5c31\u8ba9\u5f90\u5b66\u5e73\u6253\u65ad\uff0c\u4e07\u4e07\u6ca1\u60f3\u5230\u4f1a\u9047\u5230\u8fd9\u6837\u7684\u8f6c\u673a\uff0c\u5c31\u5c06\u8bb8\u601d\u3001\u5510\u5b66\u8c26\u3001\u59dc\u660e\u8bda\u3001\u66fe\u5efa\u534e\u4e4b\u95f4\u7684\u5173\u7cfb\u7ec6\u7ec6\u7684\u89e3\u8bf4\u4e86\u4e00\u4e0b\uff0c\u5c06\u513f\u5b50\u5f20\u606a\u5982\u4f55\u65e0\u610f\u4e2d\u53d1\u73b0\u8bb8\u601d\u4e0e\u4e01\u5411\u5c71\u5728\u4e00\u8d77\uff0c\u5982\u4f55\u62cd\u7167\u7559\u8bc1\uff0c\u5982\u4f55\u4ece\u8bb8\u601d\u90a3\u91cc\u83b7\u5f97\u5510\u5b66\u8c26\u4e0e\u8bb8\u601d\u7684\u5408\u5f71\uff0c\u4ee5\u53ca\u6d77\u5dde\u8c23\u8a00\u4e2d\u79cd\u79cd\u5947\u602a\u4e4b\u5904\uff0c\u4ed6\u4eec\u5982\u4f55\u4ece\u5510\u5b66\u8c26\u5bb6\u91cc\u627e\u5230\u66fe\u5efa\u534e\u7684\u8bb0\u4e8b\u672c\uff0c\u53c8\u5982\u4f55\u5230\u66fe\u5efa\u534e\u5bb6\u4e4b\u540e\u624d\u53d1\u73b0\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\u7684\u6d88\u606f\uff0c\u8fd9\u79cd\u79cd\u4e8b\u60c5\uff0c\u5f20\u77e5\u884c\u90fd\u5f88\u8be6\u7ec6\u7684\u8bf4\u4e86\u4e00\u904d\u3002</p><p>\u3000\u3000\u201c\u8001\u5f90\uff0c\u6d77\u5dde\u662f\u6709\u597d\u5e72\u90e8\u7684\uff0c\u201d\u5f90\u5b66\u5e73\u7684\u7231\u4eba\u542c\u5b8c\u5f20\u77e5\u884c\u7684\u9648\u8ff0\uff0c\u5bf9\u5f90\u5b66\u5e73\u8bf4\uff0c\u201c\u5c0f\u5f20\u540c\u5fd7\u5192\u4e86\u8fd9\u4e48\u5927\u98ce\u9669\uff0c\u505a\u4e86\u8fd9\u4e48\u5927\u7684\u5de5\u4f5c\uff0c\u751a\u81f3\u8fd8\u8981\u906d\u53d7\u540c\u4e8b\u7684\u8bef\u89e3\uff0c\u53ea\u5e0c\u671b\u5c06\u95ee\u9898\u67e5\u6e05\u695a\uff0c\u4e0d\u51a4\u6789\u4e00\u4e2a\u597d\u5e72\u90e8\uff0c\u4e5f\u4e0d\u653e\u8fc7\u4e00\u4e2a\u72af\u9519\u8bef\u7684\u4eba\uff0c\u7701\u91cc\u4e00\u5b9a\u8981\u7ed9\u4e88\u652f\u6301\u624d\u884c\u3002\u201d</p><p>\u3000\u3000\u5bf9\u4e00\u4e2a\u4eba\u538c\u6076\u65f6\uff0c\u4ed6\u8bf4\u4ec0\u4e48\u8bdd\u90fd\u89c9\u5f97\u523a\u8033\uff0c\u5bf9\u4e00\u4e2a\u4eba\u559c\u6b22\uff0c\u4e8b\u4e8b\u89c9\u5f97\u4ed6\u8bf4\u7684\u5728\u7406\u3002\u5f20\u606a\u6ce8\u610f\u5230\u5f90\u5b66\u5e73\u8138\u4e0a\u795e\u8272\u51dd\u91cd\uff0c\u4f46\u5df2\u7ecf\u7ed9\u8bf4\u52a8\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u6ca1\u60f3\u5230\u6d77\u5dde\u53ef\u80fd\u5b58\u5728\u8fd9\u4e48\u5927\u7684\u95ee\u9898\uff0c\u201d\u5f90\u5b66\u5e73\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u80fd\u53ea\u542c\u4f60\u4e00\u65b9\u9762\u7684\u8bf4\u8f9e\uff0c\u6211\u8fd8\u8981\u542c\u542c\u68c0\u67e5\u7ec4\u8fc4\u4eca\u4e3a\u6b62\u7684\u8c03\u67e5\u60c5\u51b5\u2026\u2026\u201d\u4e0d\u8fc7\u53c8\u5b89\u6170\u4e86\u5f20\u77e5\u884c\u4e00\u53e5\uff0c\u201c\u4e0d\u7ba1\u5510\u5b66\u8c26\u6709\u6ca1\u6709\u53d7\u8d3f\uff0c\u4f60\u8fd9\u79cd\u6562\u4e8e\u8d28\u7591\u7684\u7cbe\u795e\uff0c\u6070\u6070\u7ef4\u62a4\u4e86\u53f8\u6cd5\u516c\u6b63\u7684\u5a01\u4e25\uff0c\u4f60\u8fd9\u6837\u7684\u597d\u5e72\u90e8\uff0c\u5f88\u96be\u5f97\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u4e86\u4e5f\u662f\u4e00\u6123\uff0c\u5f90\u5b66\u5e73\u7684\u8fd9\u53e5\u8bdd\uff0c\u5206\u91cf\u4e0d\u8f7b\u554a\uff0c\u5b9b\u5982\u5728\u7238\u7238\u8eab\u4e0a\u52a0\u4e86\u4e00\u9053\u62a4\u8eab\u7b26\uff0c\u53ea\u8981\u5f90\u5b66\u5e73\u5728\u4efb\u4e0a\uff0c\u6d77\u5dde\u5e02\u8c01\u4e5f\u4e0d\u6562\u52a8\u7238\u7238\u7684\u4e00\u6839\u6beb\u6bdb\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u52aa\u529b\u4e0d\u8ba9\u81ea\u5df1\u8138\u4e0a\u6d41\u9732\u51fa\u5f97\u610f\u7684\u795e\u8272\uff0c\u5f90\u5b66\u5e73\u8fd9\u662f\u7231\u5c4b\u53ca\u4e4c\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5bf9\u5c0f\u674e\u8bf4\uff1a\u201c\u4f60\u9a6c\u4e0a\u8054\u7cfb\u4e0a\u91d1\u56fd\u6d77\uff0c\u770b\u4ed6\u80fd\u4e0d\u80fd\u628a\u68c0\u67e5\u7ec4\u5728\u6d77\u5dde\u8c03\u67e5\u7684\u60c5\u51b5\u7ed9\u6211\u53d1\u4e00\u4efd\u4f20\u771f\u3002\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u4fa7\u8fc7\u8eab\u6765\u770b\u7740\u5f20\u606a\uff0c\u8fd9\u4e2a\u770b\u8d77\u6765\u6709\u4e9b\u6f02\u4eae\u7684\u5c11\u5e74\u8ba9\u4ed6\u53c8\u60ca\u53c8\u559c\uff0c\u80fd\u51b2\u5230\u8f66\u8f6e\u4e0b\u6551\u4eba\uff0c\u6551\u8fc7\u4eba\u53c8\u4e0d\u7559\u540d\u5b57\uff0c\u5df2\u7ecf\u662f\u5341\u5206\u96be\u5f97\u4e86\uff0c\u9047\u4e8b\u8fd8\u8fd9\u4e48\u673a\u8b66\uff0c\u80fd\u53d1\u73b0\u4e00\u822c\u4eba\u53d1\u73b0\u4e0d\u4e86\u7684\u95ee\u9898\uff0c\u8981\u771f\u67e5\u51fa\u5510\u5b66\u8c26\u6848\u6709\u5176\u4ed6\u5185\u60c5\u7684\uff0c\u8fd9\u4e2a\u5c11\u5e74\u529f\u4e0d\u53ef\u6ca1\u554a\uff0c\u4e5f\u4e0d\u63a9\u9970\u8138\u4e0a\u7684\u6b23\u8d4f\uff0c\u9732\u51fa\u96be\u5f97\u7684\u7b11\u5bb9\uff1a\u201c\u4e0d\u77e5\u9053\u4f60\u7238\u7238\u6709\u6ca1\u6709\u5e2e\u4f60\u5439\u725b\uff0c\u8868\u73b0\u771f\u7684\u4e0d\u9519\uff0c\u4eca\u5e74\u591a\u5927\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5341\u516d\u4e86\uff0c\u521a\u521d\u4e2d\u6bd5\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6bd4\u6211\u5bb6\u4eec\u82b7\u5f64\u5927\u591a\u4e86\uff0c\u662f\u4e2a\u5927\u54e5\u54e5\u3002\u201d\u5f90\u5b66\u5e73\u7ed9\u5f20\u606a\u4ecb\u7ecd\u5ba2\u5385\u91cc\u7684\u4eba\uff0c\u5c0f\u674e\u53eb\u674e\u4e49\u6c5f\uff0c\u662f\u4ed6\u7684\u4e13\u804c\u79d8\u4e66\uff0c\u4ed6\u7231\u4eba\u53eb\u5468\u53d4\u60e0\uff0c\u662f\u7701\u52b3\u52a8\u5385\u7684\u5e72\u90e8\uff0c\u5c0f\u5468\u4fdd\u59c6\u662f\u5468\u53d4\u60e0\u5a18\u5bb6\u7684\u8001\u4eb2\u3002</p><p>\u3000\u3000\u5c0f\u674e\u79d8\u4e66\u653e\u4e0b\u7535\u8bdd\u8d70\u8fc7\u6765\uff1a\u201c\u91d1\u526f\u68c0\u5bdf\u957f\u5df2\u7ecf\u4ece\u5bb6\u91cc\u8d76\u8fc7\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u80fd\u672a\u535c\u5148\u77e5\uff1f\u201d\u5f90\u5b66\u5e73\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u54ea\u91cc\u80fd\u672a\u535c\u5148\u77e5\uff1f</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u79d8\u4e66\u6253\u7535\u8bdd\u8bf4\u6d77\u5dde\u6709\u4eba\u5c06\u4e00\u4e9b\u8d44\u6599\u9001\u5230\u5f90\u5b66\u5e73\u5bb6\uff0c\u8ba9\u68c0\u5bdf\u9662\u6d3e\u4eba\u8fc7\u53bb\u63a5\u6536\uff0c\u4e5f\u6ca1\u6709\u8be6\u7ec6\u8bf4\u662f\u4ec0\u4e48\u4e8b\u3002\u5f90\u5b66\u5e73\u7684\u513f\u5b50\u521a\u5728\u6d77\u5dde\u51fa\u8f66\u7978\uff0c\u503c\u73ed\u4eba\u5458\u5bf9\u8fd9\u6837\u7684\u4e8b\u5c31\u5f88\u654f\u611f\uff0c\u9a6c\u4e0a\u5c31\u7ed9\u5728\u5bb6\u7684\u9662\u9886\u5bfc\u91d1\u56fd\u6d77\u6253\u7535\u8bdd\u8bf7\u793a\u3002\u91d1\u56fd\u6d77\u4e0d\u6562\u6020\u6162\uff0c\u77e5\u9053\u5f90\u5b66\u5e73\u8fd9\u9635\u5b50\u813e\u6c14\u4f1a\u5f88\u5927\uff0c\u4e0d\u7ba1\u5927\u4e8b\u5c0f\u4e8b\uff0c\u4ed6\u51b3\u5b9a\u4eb2\u81ea\u5230\u5f90\u5b66\u5e73\u5bb6\u8d70\u4e00\u8d9f\uff0c\u6ca1\u60f3\u5230\u4e8b\u60c5\u5728\u5f90\u5b66\u5e73\u679c\u771f\u53d1\u751f\u8f6c\u6298\u3002</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u4e0e\u52a9\u624b\u524d\u811a\u5230\u5f90\u5b66\u5e73\u5bb6\uff0c\u8c22\u665a\u6674\u968f\u540e\u8d76\u5230\u3002\u5979\u5728\u516c\u53f8\u6574\u7406\u4e08\u592b\u7684\u9057\u7269\uff0c\u7a81\u7136\u63a5\u5230\u7535\u8bdd\u8bf4\u6551\u82b7\u5f64\u7684\u5c11\u5e74\u5c31\u5728\u516c\u516c\u5f90\u5b66\u5e73\u5bb6\uff0c\u6ca1\u6709\u803d\u8bef\uff0c\u7acb\u5373\u79bb\u5f00\u516c\u53f8\u8d76\u4e86\u8fc7\u6765\uff0c\u8fdb\u95e8\u7b2c\u4e00\u773c\u5c31\u770b\u89c1\u62b1\u7740\u82b7\u5f64\u7684\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u8c22\u8c22\u4f60\u6551\u4e86\u82b7\u5f64\uff0c\u5f53\u65f6\u6ca1\u7559\u4e0b\u4f60\u7684\u59d3\u540d\uff0c\u6b63\u51c6\u5907\u8fc7\u6bb5\u65f6\u95f4\u53bb\u6d77\u5dde\u627e\u4f60\uff0c\u201d\u8fd9\u662f\u8fd9\u4e9b\u5929\u60df\u4e00\u8ba9\u8c22\u665a\u6674\u6b23\u6170\u7684\u4e8b\uff0c\u8f6c\u8fc7\u5934\u95ee\u5979\u516c\u516c\uff0c\u201c\u7238\uff0c\u4f60\u4eec\u600e\u4e48\u627e\u5230\u82b7\u5f64\u7684\u5c0f\u6069\u4eba\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4eba\u53bb\u627e\uff0c\u82b7\u5f64\u81ea\u5df1\u8ba4\u51fa\u6765\u7684\uff0c\u201d\u5f90\u5b66\u5e73\u8bf4\u9053\uff0c\u201c\u4f60\u8ddf\u53d4\u60e0\u5148\u62b1\u82b7\u5f64\u4e0a\u697c\u7761\u89c9\u53bb\uff0c\u8ba9\u53d4\u60e0\u8ddf\u4f60\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u770b\u7740\u91d1\u56fd\u6d77\u8fd9\u4e48\u665a\u4e0a\u95e8\uff0c\u4e00\u5b9a\u6709\u8981\u7d27\u7684\u4e8b\uff0c\u4f38\u8fc7\u624b\u6765\u62b1\u5973\u513f\uff0c\u89c1\u5973\u513f\u7d27\u7d27\u6402\u7740\u5c11\u5e74\u7684\u8116\u5b50\uff0c\u5c31\u662f\u5728\u7761\u68a6\u91cc\u4e5f\u4e0d\u80af\u677e\u624b\uff0c\u8f7b\u58f0\u7684\u8bf4\uff1a\u201c\u82b7\u5f64\uff0c\u6765\uff0c\u5988\u5988\u62b1\u2026\u2026\u201d\u82b7\u5f64\u7741\u5f00\u60fa\u5fea\u7684\u7761\u773c\uff0c\u89c1\u662f\u5988\u5988\uff0c\u624d\u4e0d\u60c5\u4e0d\u613f\u7684\u677e\u5f00\u624b\u3002</p></article>"}}}]);
