(window.webpackJsonp=window.webpackJsonp||[]).push([[160],{183:function(p){p.exports={chapterTitle:null,title:"\u7b2c1138\u7ae0 \u6c79\u6d8c\u7684\u51f6\u9669",content:"<article><h2>\u7b2c1138\u7ae0 \u6c79\u6d8c\u7684\u51f6\u9669</h2><p>\u3000\u3000\u7231\u8fbe\u5927\u53a6\u524d\u90a3\u73bb\u7483\u94a2\u5851\u5f62\u7684\u5de8\u578b\u5e06\u8239\u697c\u6807\u6210\u4e3a\u5927\u53a6\u9c9c\u660e\u7684\u7279\u5f81\uff0c\u8fd9\u91cc\u4e0d\u4ec5\u4ec5\u9526\u6e56\u5546\u4e8b\u5317\u65b9\u516c\u53f8\u7684\u6240\u5728\u5730\uff0c\u4e5f\u662f\u5168\u7403\u7b2c\u4e00\u4e2d\u6587\u95e8\u6237\u7f51\u7ad9\u6606\u817e\u5728\u7ebf\u603b\u90e8\u6240\u5728\u5730\uff0c\u6606\u817e\u5728\u7ebf\u7684\u4f01\u4e1a\u6807\u8bc6\u91cc\uff0c\u5c31\u76f4\u63a5\u91c7\u7528\u84dd\u8272\u8239\u5e06\u5f62\u7684\u56fe\u6807\uff0c\u55bb\u610f\u626c\u5e06\u542f\u822a\u4e4b\u610f\u3002</p><p>\u3000\u3000\u65b0\u95fb\u90e8\u95e8\u8fde\u591c\u5236\u4f5c\u7528\u201c\u7f51\u7edc\u80a1\u707e\u3001\u5371\u673a\u6765\u4e34\u201d\u7684\u65b0\u95fb\u4e13\u9898\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u6606\u817e\u5728\u7ebf\u7684\u9ad8\u5c42\u795e\u8272\u51dd\u91cd\u3001\u5fc3\u60c5\u65e0\u6cd5\u8f7b\u677e\u4e0b\u6765\u3002\u4ed6\u4eec\u5c11\u5e74\u5f97\u5fd7\uff0c\u5728\u5927\u5b66\u8bfb\u4e66\u671f\u95f4\u5c31\u521b\u529e\u7f51\u7ad9\uff0c\u5728\u9526\u6e56\u7684\u652f\u6301\u4e0b\uff0c\u5c06\u7f51\u7ad9\u9010\u6b65\u53d1\u5c55\u6210\u5168\u7403\u7b2c\u4e00\u4e2d\u6587\u95e8\u6237\u7f51\u7ad9\u6606\u817e\u5728\u7ebf\uff0c\u5e76\u5728\u7eb3\u65af\u8fbe\u514b\u6210\u529f\u4e0a\u5e02\u3002\u5728\u4ed6\u4eec\u4e8b\u4e1a\u7684\u5dc5\u5cf0\u65f6\u671f\uff0c\u6574\u4e2a\u7ba1\u7406\u5c42\u6301\u6709\u516c\u53f8\u80a1\u7968\u5e02\u503c\u603b\u989d\u9ad8\u8fbe\u516d\u4ebf\u7f8e\u5143\uff0c\u6210\u4e3a\u8f70\u52a8\u4e00\u65f6\u7684\u8d22\u5bcc\u795e\u8bdd\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u5386\u7ecf\u4e86\u521b\u4e1a\u7684\u8270\u8f9b\u4e0e\u575a\u6301\uff0c\u4eab\u53d7\u8fc7\u6210\u529f\u7684\u559c\u60a6\u4e0e\u6fc0\u60c5\uff0c\u8fd8\u6ca1\u6709\u7ecf\u5386\u632b\u6298\u7684\u78e8\u96be\uff0c\u6606\u817e\u5728\u7ebf\u4e24\u5929\u8dcc\u638920\uff05\u7684\u5e02\u503c\uff0c\u591a\u5c11\u6709\u4e9b\u8ba9\u4eba\u63aa\u624b\u4e0d\u53ca\uff0c\u5bf9\u672a\u6765\u6709\u4e9b\u7684\u8fdf\u7591\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u540e\uff0c\u4e0d\u77e5\u9053\u4eca\u591c\u80fd\u4e0d\u80fd\u6b62\u4f4f\u8dcc\u52bf\u2026\u2026\u201d\u9a6c\u5411\u4e1c\u62cd\u4e86\u62cd\u6881\u6587\u6c5f\u7684\u80a9\u8180\u3002</p><p>\u3000\u3000\u201c\u516c\u53f8\u7684\u8fd0\u8425\u4e00\u5207\u5982\u5e38\uff0c\u4e24\u591c\u4e4b\u95f4\uff0c\u516b\u4e2a\u4ebf\u4e07\u5bcc\u7fc1\u5c31\u7ed9\u7f29\u6c34\u6389\u7684\uff0c\u4e4b\u524d\u771f\u662f\u6ca1\u6709\u80fd\u591f\u60f3\u5230\u3002\u201d\u6881\u6587\u6c5f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e4b\u524d\u6211\u4eec\u4e0d\u662f\u4e5f\u6ca1\u6709\u60f3\u5230\u80fd\u83b7\u5f97\u8fd9\u4e48\u591a\uff01\u201d\u9a6c\u5411\u9f99\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd8\u8bb0\u5f97\u5728\u5b66\u6821\u5c0f\u793c\u5802\u653e\u7535\u5f71\u65f6\u6211\u66fe\u7ecf\u63d0\u8d77\u8fc7\u81ea\u5df1\u7684\u4eba\u751f\u89c4\u5212\uff1f\u6211\u90a3\u65f6\u53ea\u6253\u7b97\u7740\u4e09\u5341\u5c81\u4e4b\u524d\u6398\u53d6\u4eba\u751f\u7684\u7b2c\u4e00\u6876\u91d1\uff0c\u6210\u4e3a\u767e\u4e07\u5bcc\u7fc1\u5c31\u5f88\u77e5\u8db3\u4e86\u3002\u201d</p><p>\u3000\u3000\u5373\u4f7f\u5e02\u503c\u7f29\u6c34\uff0c\u516c\u53f8\u7ba1\u7406\u5c42\u6240\u6301\u80a1\u7968\u5e02\u503c\u603b\u989d\u4f9d\u65e7\u8d85\u8fc7\u56db\u4ebf\u7f8e\u5143\uff0c\u5bf9\u4e8e\u51e0\u5e74\u524d\u8fd8\u662f\u6bdb\u5934\u5c0f\u5b50\u7684\u4ed6\u4eec\uff0c\u6839\u672c\u5c31\u662f\u4e0d\u6562\u5962\u671b\u7684\u8d22\u5bcc\u3002</p><p>\u3000\u3000\u4e0e\u5176\u8bf4\u62c5\u5fc3\u4e2a\u4eba\u8d22\u5bcc\u7684\u7f29\u6c34\uff0c\u8fd8\u4e0d\u5982\u8bf4\u62c5\u5fc3\u7f51\u7edc\u80a1\u707e\u6c14\u52bf\u6c79\u6c79\u5373\u81f3\u3001\u6606\u817e\u5728\u7ebf\u8981\u5982\u4f55\u5e94\u5bf9\u624d\u597d\u3002</p><p>\u3000\u3000\u201c\u89c6\u9891\u4f1a\u8bae\u8fde\u63a5\u4e0a\u4e86\uff01\u201d\u884c\u653f\u79d8\u4e66\u5c0f\u9a6c\u8d70\u8fdb\u6765\u544a\u8bc9\u4ed6\u4eec\u89c6\u9891\u4f1a\u8bae\u51c6\u5907\u597d\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u4e1c\u8d70\u8fdb\u4f1a\u8bae\u5ba4\uff0c\u6606\u817e\u5728\u7ebf\u6240\u6709\u5728\u5317\u4eac\u7684\u9ad8\u5c42\u90fd\u5750\u5728\u4f1a\u8bae\u5ba4\u91cc\u3002\u5927\u5bb6\u5f7c\u6b64\u95f4\u90fd\u5341\u5206\u7684\u719f\u6089\uff0c\u5171\u540c\u521b\u4e1a\uff0c\u5f7c\u6b64\u95f4\u90fd\u4e0d\u751f\u5206\uff0c\u4f1a\u8bae\u4e0a\u90fd\u4e0d\u4f1a\u5341\u5206\u7684\u6c89\u95f7\uff0c\u5373\u4f7f\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\u5927\u5bb6\u4e5f\u90fd\u4ea4\u5934\u63a5\u8033\u7684\u804a\u7740\u5929\uff0c\u4e92\u76f8\u5f00\u73a9\u7b11\uff0c\u4ecd\u96be\u63a9\u9970\u5185\u5fc3\u7684\u5fe7\u8651\uff1a\u8981\u662f\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u4e0b\u5d29\u4e4b\u52bf\u6536\u4e0d\u4f4f\u5f62\u6210\u7f55\u89c1\u7684\u80a1\u707e\uff0c\u4ed6\u4eec\u8981\u600e\u4e48\u529e\uff1f</p><p>\u3000\u3000\u4f1a\u8bae\u5ba4\u5899\u58c1\u4e0a\u6302\u7740\u7684\u8d85\u5bbd\u5c4f\u5e55\u7684\u7b49\u79bb\u5b50\u663e\u793a\u5c4f\u4e0a\u7684\u753b\u9762\u662f\u4e00\u5904\u7565\u663e\u5f97\u51cc\u4e71\u7684\u4e66\u623f\uff0c\u4e66\u623f\u91cc\u7a7a\u65e0\u4e00\u4eba\uff0c\u4e66\u623f\u9760\u5899\u58c1\u653e\u7740\u6446\u6ee1\u7cbe\u7f8e\u74f7\u5668\u7684\u4e66\u67b6\uff0c\u4e66\u67b6\u524d\u662f\u4e00\u5f20\u7ea2\u67ab\u6728\u7684\u4e66\u684c\u4e0a\uff0c\u684c\u9762\u4e0a\u653e\u5728\u7b14\u8bb0\u672c\u7535\u8111\u3001\u624b\u673a\u3001\u9676\u8d28\u7684\u7b14\u7b52\u91cc\u63d2\u6ee1\u5404\u5f0f\u5404\u6837\u7684\u7b14\uff0c\u4e00\u53ea\u76ae\u5939\uff0c\u9664\u4e86\u51e0\u5f20\u9732\u51fa\u6216\u91d1\u8272\u6216\u9ed1\u8272\u8fb9\u7f18\u7684\u5361\u4e4b\u5916\uff0c\u4ece\u663e\u793a\u5c4f\u4e0a\u80fd\u770b\u5230\u76ae\u5939\u91cc\u53ea\u6709\u4e00\u5f20\u5341\u5143\u9762\u503c\u7684\u7eb8\u949e\uff0c\u8fd8\u6709\u4e00\u672c\u4e09\u5c9b\u7531\u7eaa\u592b\u6240\u8457\u7684\u5c0f\u8bf4\u300a\u91d1\u9601\u5bfa\u300b\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u5c06\u6211\u8ddf\u5f20\u606a\u5355\u72ec\u4e22\u5230\u6ca1\u6709\u63d0\u6b3e\u673a\u7684\u8fb9\u8fdc\u5c0f\u9547\uff0c\u8bf4\u4e0d\u5b9a\u6211\u6bd4\u4ed6\u8fd8\u5bcc\u6709\u2026\u2026\u201d\u9a6c\u5411\u4e1c\u62c9\u7740\u6905\u5b50\uff0c\u8ddf\u6881\u6587\u6c5f\u5f00\u73a9\u7b11\u8bf4\u9053\uff0c\u7b49\u5f85\u5f20\u606a\u51fa\u73b0\u5728\u663e\u793a\u5c4f\u4e0a\u3002\u4eca\u5929\u7684\u4f1a\u8bae\u662f\u8ba8\u8bba\u6606\u817e\u5728\u7ebf\u773c\u4e0b\u6240\u9762\u4e34\u7684\u5371\u673a\u4ee5\u53ca\u5e94\u5bf9\u8981\u9009\u62e9\u7684\u7b56\u7565\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u603b\u88c1\u8d75\u6893\u7433\u6ca1\u6709\u53c2\u4e0e\uff0c\u800c\u662f\u5f20\u606a\u4eb2\u81ea\u53c2\u4e0e\uff0c\u6bd5\u7adf\u4e0d\u662f\u6b63\u5f0f\u7684\u8463\u4e8b\u4f1a\u8bae\uff0c\u4e5f\u6ca1\u6709\u901a\u77e5\u5176\u4ed6\u80a1\u4e1c\u4ee3\u8868\u53c2\u52a0\u3002</p><p>\u3000\u3000\u201c\u539f\u6765\u4ed6\u4e5f\u4f1a\u8bfb\u4e09\u5c9b\u7531\u7eaa\u592b\u7684\u5c0f\u8bf4\u554a\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u5d07\u6d0b\u5a9a\u5916\u554a\uff1f\u201d\u8d22\u52a1\u603b\u76d1\u8881\u83c1\u8fd8\u672a\u66fe\u6709\u673a\u4f1a\u8ddf\u5f20\u606a\u63a5\u89e6\u8fc7\uff0c\u4f46\u662f\u4f5c\u4e3a\u6606\u817e\u5728\u7ebf\u7684\u9ad8\u7ea7\u7ecf\u7406\u4eba\uff0c\u8bb8\u591a\u4e8b\u60c5\u90fd\u662f\u6e05\u695a\u7684\uff0c\u4e00\u624b\u7f14\u9020\u9526\u6e56\u7ecf\u6d4e\u5e1d\u56fd\u7684\u4eba\u7269\u5728\u5979\u773c\u524d\u5145\u6ee1\u4e86\u795e\u79d8\u611f\uff0c\u6ca1\u60f3\u5230\u4ed6\u7684\u6848\u724d\u4e0a\u4f1a\u968f\u610f\u6446\u653e\u7740\u4e00\u672c\u65e5\u672c\u6587\u5b66\u3002</p><p>\u3000\u3000\u753b\u9762\u6643\u4e86\u6643\uff0c\u4e00\u53ea\u5de8\u624b\u51fa\u73b0\u5728\u5c4f\u5e55\u4e0a\uff0c\u63a5\u7740\u5c31\u662f\u4e00\u53ea\u633a\u79c0\u7684\u9f3b\u6881\uff0c\u5f53\u9f3b\u6881\u5360\u636e\u56db\u5341\u516d\u5bf8\u5bbd\u5c4f\u5e55\u4e09\u5206\u4e4b\u4e00\u753b\u9762\u65f6\uff0c\u9f3b\u6881\u518d\u633a\u79c0\u4e5f\u5341\u5206\u6709\u9650\uff0c\u4f1a\u8bae\u5ba4\u91cc\u7684\u4eba\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u770b\u7740\u5360\u636e\u663e\u793a\u5c4f\u7684\u90a3\u5f20\u8138\u9010\u6e10\u4e0e\u955c\u5934\u62c9\u5f00\u8ddd\u79bb\uff0c\u6574\u4e2a\u7684\u663e\u793a\u5728\u5c4f\u5e55\u4e0a\uff0c\u624d\u9002\u5e94\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e24\u5929\u5927\u5bb6\u90fd\u6ca1\u6709\u7761\u597d\u5427\uff0c\u770b\u5927\u5bb6\u90fd\u6ca1\u7cbe\u6253\u91c7\u7684\uff1f\u201d\u5f20\u606a\u5728\u8fd9\u8001\u8001\u5b9e\u5b9e\u7684\u5750\u5230\u529e\u516c\u684c\u540e\u9762\u5bf9\u7740\u955c\u5934\u8bf4\u8bdd\uff0c\u753b\u9762\u3001\u58f0\u97f3\u90fd\u6e05\u6670\u65e0\u6bd4\u7684\u4f20\u9012\u5230\u8fd9\u8fb9\u7684\u663e\u793a\u5c4f\u4e0a\uff0c\u4e0d\u8fc7\u5f20\u606a\u7684\u624b\u91cc\u8fd8\u62ff\u7740\u4e00\u53ea\u624b\u673a\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u8ddf\u6881\u6587\u6c5f\u4e92\u6362\u4e86\u4e2a\u773c\u8272\uff0c\u4ed6\u4eec\u77e5\u9053\u5f20\u606a\u4f1a\u6709\u5907\u7528\u624b\u673a\uff0c\u4e0d\u8fc7\u5907\u7528\u624b\u673a\u90fd\u4f1a\u5728\u52a9\u624b\u624b\u91cc\uff0c\u4ed6\u624b\u91cc\u4ece\u6765\u53ea\u6709\u4e00\u53ea\u624b\u673a\u5728\u7528\uff0c\u8981\u662f\u8bf4\u4ed6\u624b\u91cc\u7684\u624b\u673a\u662f\u4ed6\u7684\uff0c\u90a3\u529e\u516c\u684c\u4e0a\u7684\u90a3\u53ea\u624b\u673a\u53c8\u662f\u8c01\u7684\uff1f\u96be\u602a\u89c9\u5f97\u90a3\u53ea\u624b\u673a\u788d\u773c\uff0c\u539f\u6765\u662f\u592a\u79c0\u6c14\u4e86\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u8c03\u4f83\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u606a\u5c11\u4e5f\u6ca1\u6709\u7761\u597d\u5427\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u4e00\u53ea\u7ea4\u7ec6\u5ae9\u82e5\u9752\u8471\u7684\u624b\u6307\u51fa\u73b0\u5728\u5c4f\u5e55\u91cc\uff0c\u90a3\u6839\u624b\u6307\u6307\u4e86\u6307\u684c\u4e0a\u7684\u624b\u673a\uff0c\u5f20\u606a\u62ff\u8d77\u684c\u4e0a\u7684\u624b\u673a\u9012\u51fa\u753b\u9762\uff0c\u771f\u662f\u8ba9\u4eba\u9050\u60f3\uff1a\u624b\u6307\u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u7aa5\u4e00\u6591\u800c\u89c1\u5168\u8c79\uff0c\u770b\u5230\u8fd9\u6839\u5ae9\u82e5\u9752\u8471\u7684\u7389\u6307\uff0c\u5c31\u4e0b\u610f\u8bc6\u7684\u60f3\u5230\u624b\u6307\u7684\u4e3b\u4eba\u5e94\u8be5\u662f\u4e2a\u7edd\u8272\u503e\u57ce\u7684\u7f8e\u4eba\u513f\u3002</p><p>\u3000\u3000\u663e\u7136\u666e\u901a\u5973\u6027\u5de5\u4f5c\u4eba\u5458\u4e0d\u53ef\u80fd\u968f\u4fbf\u5c06\u624b\u673a\u653e\u5728\u5f20\u606a\u7684\u529e\u516c\u684c\u4e0a\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e2a\u8868\u5f1f\u554a\uff0c\u4f60\u4e0b\u8f88\u5b50\u62cd\u9a6c\u8d76\u5427\u3002\u201d\u738b\u5f69\u73b2\u8eab\u5b50\u503e\u8fc7\u6765\u9644\u8033\u8ddf\u6881\u6587\u6c5f\u5c0f\u58f0\u8bf4\u7b11\uff0c\u201c\u6211\u53ef\u8bb0\u5f97\u5510\u5a67\u4e00\u76f4\u90fd\u5728\u9999\u6e2f\u554a\uff0c\u4ed6\u4e24\u4e2a\u5c0f\u79d8\u4e5f\u53bb\u9999\u6e2f\u603b\u90e8\u5b9e\u4e60\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u6587\u6c5f\u65e0\u5948\u7684\u82e6\u7b11\uff0c\u4ed6\u5c0f\u59e8\u8fd8\u7ecf\u5e38\u62b1\u6028\u56f4\u7ed5\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u592a\u6f02\u4eae\u672a\u5fc5\u662f\u4ef6\u597d\u4e8b\u7b49\u7b49\uff0c\u4ed6\u603b\u4e0d\u80fd\u516b\u5366\u7684\u6253\u542c\u955c\u5934\u5916\u7684\u5973\u4eba\u662f\u8c01\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u773c\u775b\u91cd\u65b0\u770b\u5230\u955c\u5934\u4e0a\uff0c\u4ed6\u4f3c\u4e4e\u610f\u8bc6\u7684\u521a\u624d\u7a81\u7136\u51fa\u73b0\u5728\u753b\u9762\u4e0a\u7684\u90a3\u6839\u5973\u6027\u624b\u6307\u4f1a\u5f15\u8d77\u4e00\u4e9b\u8bae\u8bba\uff0c\u4ed6\u76f4\u63a5\u8fdb\u5165\u6b63\u9898\uff1a\u201c\u60c5\u51b5\u4e0d\u662f\u5f88\u4e50\u89c2\uff0c\u8fd9\u4e24\u5929\u7684\u66b4\u8dcc\u5f88\u53ef\u80fd\u53ea\u662f\u4e00\u4e2a\u5f00\u5934\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u5012\u903c\u6bcf\u80a1\u4e09\u5341\u7f8e\u5143\uff0c\u4f60\u4eec\u8981\u505a\u597d\u8fd9\u6837\u7684\u51c6\u5907\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u5c06\u6765\u4e00\u6bb5\u65f6\u95f4\u5f88\u53ef\u80fd\u4f1a\u5012\u903c\u6bcf\u80a1\u5341\u7f8e\u5143\u8fd9\u6761\u7ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u8bdd\u4ee4\u5728\u5ea7\u7684\u4f17\u4eba\u90fd\u5fc3\u91cc\u4e00\u60ca\uff0c\u4e00\u5e74\u524d\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u5f00\u76d8\u4ef7\u5c31\u63a5\u8fd1\u6bcf\u80a1\u5341\u7f8e\u5143\uff0c\u5728\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u4ee5\u53ca\u4e2d\u7f8e\u53cc\u8fb9\u534f\u8bae\u7684\u72c2\u70ed\u63a8\u52a8\u4e0b\u4e0a\u6da8\u4e86\u8fd1\u4e94\u500d\uff0c\u5f20\u606a\u9884\u6d4b\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u4f1a\u5f88\u5feb\u7ed9\u6253\u56de\u539f\u5f62\uff0c\u8ba9\u5728\u5ea7\u8bf8\u4f4d\u5982\u679c\u4e0d\u5fc3\u60ca\uff1f\u6ca1\u6709\u4eba\u518d\u6709\u5fc3\u601d\u5173\u6ce8\u5f20\u606a\u8eab\u4e0a\u7684\u98ce\u6d41\u97f5\u4e8b\uff0c\u9a6c\u5411\u4e1c\u95ee\u9053\uff1a\u201c\u5371\u673a\u771f\u4f1a\u6709\u8fd9\u4e48\u4e25\u91cd\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u9694\u7740\u5343\u91cc\u4e4b\u9065\u80fd\u6ca1\u6709\u969c\u786c\u7684\u4ea4\u6d41\uff0c\u5168\u51ed\u7535\u4fe1\u6280\u672f\u53d1\u5c55\u4e0e\u5927\u89c4\u6a21\u7535\u4fe1\u6295\u8d44\u7684\u798f\uff0c\u53ea\u662f\u8fd9\u79cd\u7a0b\u5ea6\u7684\u901a\u8baf\u9700\u6c42\u8fd8\u5f88\u5c11\uff0c\u5c31\u7a81\u663e\u51fa\u6295\u8d44\u7684\u8fc7\u5ea6\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u7edd\u5927\u591a\u6570\u7f51\u7edc\u516c\u53f8\u90fd\u662f\u4ee5\u8d44\u672c\u8fd0\u4f5c\u7684\u6a21\u5f0f\u53d1\u5c55\u8d77\u6765\u7684\uff0c\u800c\u4e14\u8fd0\u4f5c\u8d44\u672c\u5927\u90fd\u662f\u98ce\u9669\u6295\u8d44\u5546\u63d0\u4f9b\u7684\uff0c\u6211\u4eec\u6240\u6709\u5bf9\u98ce\u9669\u6295\u8d44\u6027\u8d28\u6709\u6240\u4e86\u89e3\u7684\u4eba\u90fd\u77e5\u9053\uff0c\u8fd9\u4e2a\u8fc7\u7a0b\u5f53\u4e2d\u600e\u4e48\u53ef\u80fd\u4f1a\u6291\u5236\u6ce1\u6cab\u7684\u4ea7\u751f\u5462\uff1f\u7cbe\u5fc3\u8bbe\u8ba1\u7684\u8c0e\u8a00\u3001\u534e\u4e3d\u660e\u8273\u7684\u5305\u88c5\uff0c\u91cd\u590d\u53c8\u91cd\u590d\u7684\uff0c\u5c06\u6ce1\u6cab\u5439\u5f97\u65e0\u6bd4\u7684\u5de8\u5927\uff0c\u98ce\u9669\u6295\u8d44\u5546\u4eec\u62fc\u547d\u7684\u5ba3\u4f20\u7f51\u7ad9\u7684\u70b9\u51fb\u91cf\u6216\u8005\u662f\u5b83\u4eec\u6240\u5438\u5f15\u7684\u5173\u6ce8\u7a0b\u5ea6\uff0c\u800c\u4f7f\u6295\u8d44\u4eba\u5ffd\u89c6\u6389\u771f\u6b63\u5e94\u8be5\u5173\u5fc3\u7684\u662f\u5207\u5b9e\u53ef\u884c\u7684\u4e1a\u52a1\u6a21\u5f0f\u4e0e\u672a\u6765\u7684\u76c8\u5229\u80fd\u529b\u2026\u2026\u4e00\u65e6\u51fa\u73b0\u8f6c\u6298\uff0c\u8fd9\u4e9b\u98ce\u9669\u6295\u8d44\u5546\u4f1a\u6e9c\u5f97\u6bd4\u5154\u5b50\u8fd8\u5feb\u2026\u2026\u5c40\u52bf\u8981\u80fd\u904f\u5236\u4f4f\uff0c\u9664\u975e\u8981\u8fd9\u4e9b\u98ce\u9669\u6295\u8d44\u5546\u8ddf\u90a3\u4e9b\u6295\u673a\u8005\u4e0d\u8981\u575a\u5b88\u9635\u5730\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u6ca1\u6709\u8f6c\u673a\u5417\uff1f\u201d\u6881\u6587\u6c5f\u5fcd\u4e0d\u4f4f\u63d2\u5634\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5371\u673a\u53ea\u662f\u8d44\u672c\u5e02\u573a\u5c42\u9762\u7684\uff0c\u6982\u5ff5\u7684\u8fc7\u5ea6\u7092\u4f5c\u915d\u917f\u4e86\u5de8\u5927\u7684\u6ce1\u6cab\uff0c\u4f46\u662f\u5b9e\u9645\u7684\u5e94\u7528\u9700\u6c42\u8fd9\u4e9b\u5e74\u4e00\u76f4\u5728\u6301\u7eed\u589e\u6da8\uff0c\u4e5f\u4f1a\u6301\u7eed\u589e\u6da8\u4e0b\u53bb\uff0c\u6bd5\u7adf\uff0c\u6211\u4eec\u8fd8\u662f\u8d70\u5728\u4e00\u6761\u5bf9\u7684\u9053\u8def\uff0c\u53ea\u662f\u6709\u4e9b\u8d85\u524d\u4e86\u3002\u8f6c\u673a\u8fdf\u65e9\u56de\u6765\uff0c\u6216\u8bb8\u4e24\u5e74\u6216\u8bb8\u4e09\u4e94\u5e74\uff0c\u5f53\u4e2d\u7684\u8d44\u672c\u5e02\u573a\u4f1a\u51f6\u9669\u65e0\u6bd4\uff0c\u4e5f\u4e0d\u4f1a\u4e00\u5473\u7684\u6ed1\u843d\uff0c\u8fc7\u4e8e\u8be6\u7ec6\u7684\u9884\u6d4b\u5e76\u4e0d\u662f\u7ecf\u6d4e\u5206\u6790\u673a\u6784\u80fd\u591f\u5b8c\u6210\u7684\u4efb\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u624b\u62b5\u7740\u5634\u5507\uff0c\u8fd9\u662f\u4ed6\u601d\u8003\u65f6\u60ef\u6709\u7684\u52a8\u4f5c\uff0c\u5f20\u606a\u7684\u9884\u6d4b\u5df2\u7ecf\u591f\u5177\u4f53\u4e86\uff0c\u6606\u817e\u5728\u7ebf\u73b0\u5728\u8fd8\u8fdc\u6ca1\u6709\u5230\u80fd\u591f\u5b9e\u73b0\u76c8\u5229\u7684\u7a0b\u5ea6\uff0c\u8d44\u672c\u5e02\u573a\u53d1\u751f\u5371\u673a\uff0c\u518d\u6b21\u878d\u8d44\u7684\u5927\u95e8\u5c06\u7ed9\u5173\u95ed\uff0c\u6606\u817e\u5728\u7ebf\u53ea\u80fd\u4f9d\u8d56\u73b0\u6709\u7684\u8d44\u91d1\u652f\u6491\u5230\u5b9e\u73b0\u76c8\u5229\u7684\u90a3\u4e00\u523b\u6216\u8005\u7b49\u5f85\u8f6c\u673a\u6765\u4e34\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u653e\u4e0b\u624b\u6765\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8981\u52d2\u7d27\u88e4\u8170\u5e26\u8fc7\u65e5\u5b50\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u9526\u6e56\u7684\u5f71\u54cd\u529b\uff0c\u6606\u817e\u5728\u7ebf\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u6210\u529f\u4e4b\u540e\uff0c\u8fd8\u662f\u6ca1\u6709\u8f6c\u8fdb\u5230\u6fc0\u8fdb\u6295\u8d44\u7684\u65b9\u5411\u4e0a\u53bb\uff0c\u6700\u5927\u7684\u4e00\u7b14\u6295\u8d44\u5c31\u662f\u5bf9\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u5353\u57df\u8fdb\u884c\u6ce8\u8d44\uff0c\u4e5f\u4e0d\u8fc7\u4e24\u4e09\u767e\u4e07\u7f8e\u5143\u800c\u5df2\uff0c\u6b64\u65f6\u7684\u8d22\u52a1\u72b6\u51b5\u8fd8\u76f8\u5f53\u5bbd\u88d5\uff0c\u5e7f\u544a\u4e1a\u52a1\u6536\u5165\u867d\u7136\u4e0d\u80fd\u591f\u5b8c\u5168\u5f25\u8865\u652f\u51fa\uff0c\u4f46\u662f\u4e5f\u5728\u7a33\u5065\u7684\u589e\u6da8\u4e2d\uff0c\u5173\u952e\u662f\u8fd9\u65f6\u5019\u8981\u6536\u7d27\u53e3\u5b50\u51c6\u5907\u6253\u4e00\u573a\u6301\u4e45\u6218\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u673a\u53ef\u4ee5\u9009\u62e9\u589e\u6301\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u8ba8\u8bba\u8fd9\u4e2a\u8fd8\u5acc\u65e9\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u5728\u8fd9\u91cc\u53ef\u4ee5\u660e\u786e\u8bf4\u7684\u662f\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u5bf9\u6606\u817e\u5728\u7ebf\u7684\u6301\u80a1\u9501\u5b9a\u671f\u518d\u5ef6\u957f\u4e24\u5e74\uff0c\u6211\u4f1a\u8ba9\u4eba\u5c06\u4f20\u771f\u4ef6\u53d1\u7ed9\u4f60\u4eec\uff0c\u9002\u65f6\u516c\u5e03\u8fd9\u4e2a\u6d88\u606f\uff0c\u80fd\u7a0d\u7a0d\u633d\u56de\u4e00\u4e0b\u9893\u52bf\uff0c\u81f3\u4e8e\u4ec0\u4e48\u65f6\u673a\u589e\u6301\uff0c\u8fd8\u662f\u770b\u5f62\u52bf\u53d1\u5c55\u518d\u8bae\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u6bd4\u8d77\u673a\u6784\u6295\u8d44\u8005\uff0c\u7ba1\u7406\u5c42\u51cf\u6301\u53d7\u5230\u7ea6\u675f\u66f4\u5927\uff0c\u50cf\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u6bcf\u5e74\u53ea\u80fd\u51cf\u6301\u6781\u5c11\u6bd4\u4f8b\u7684\u80a1\u7968\u3002\u51cf\u6301\u989d\u5ea6\u65e9\u5c31\u5728\u5371\u673a\u7206\u53d1\u4e4b\u524d\u7528\u5b8c\u4e86\uff0c\u8981\u60f3\u5168\u90e8\u51cf\u6301\uff0c\u90a3\u5c31\u53ea\u80fd\u8f9e\u53bb\u6606\u817e\u5728\u7ebf\u7684\u4e00\u5207\u804c\u52a1\uff0c\u7b49\u516d\u4e2a\u6708\u7684\u65f6\u95f4\u9650\u5236\u8fc7\u4e86\u4e4b\u540e\uff0c\u5c31\u53ef\u4ee5\u4e0d\u53d7\u9650\u5236\u5168\u90e8\u51cf\u6301\u2014\u2014\u8fd9\u4e2a\u65b9\u6848\u663e\u7136\u4e0d\u662f\u53ef\u884c\u7684\u65b9\u6848\uff0c\u4ed6\u4eec\u53ea\u80fd\u5750\u770b\u4e2a\u4eba\u8d22\u5bcc\u968f\u7740\u80a1\u4ef7\u7684\u6df1\u8dcc\u800c\u4e25\u91cd\u7f29\u6c34\u3002\u7531\u4e8e\u5728\u9ad8\u4f4d\u51cf\u6301\u4e00\u5b9a\u6bd4\u4f8b\u7684\u4e2a\u4eba\u6301\u80a1\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u4e1c\u7b49\u9ad8\u5c42\u79c1\u4eba\u624b\u5934\u8fd8\u662f\u76f8\u5f53\u5bbd\u88d5\u7684\uff0c\u603b\u6570\u5dee\u4e0d\u591a\u6709\u56db\u5343\u4e07\u7f8e\u5143\u7684\u6837\u5b50\uff0c\u6bd4\u8d77\u5176\u4ed6\u9886\u57df\u7684\u6295\u8d44\uff0c\u4ed6\u4eec\u66f4\u4e13\u6ce8\u81ea\u5df1\u5f00\u62d3\u7684\u4e8b\u4e1a\uff0c\u9002\u5ea6\u9009\u62e9\u589e\u6301\uff0c\u4e5f\u80fd\u591f\u589e\u5f3a\u7ba1\u7406\u5c42\u5bf9\u516c\u53f8\u7684\u63a7\u5236\u529b\uff0c\u4e0d\u8fc7\u589e\u6301\u4e5f\u662f\u8981\u9009\u62e9\u65f6\u673a\u7684\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u5bf9\u6606\u817e\u5728\u7ebf\u6765\u8bf4\u53ea\u662f\u673a\u6784\u6295\u8d44\u8005\uff0c\u53d1\u4e00\u7eb8\u516c\u544a\u5c31\u53ef\u4ee5\u7acb\u5373\u51cf\u6301\uff0c\u552f\u4e00\u7684\u9650\u5236\u5c31\u662f\u516d\u4e2a\u6708\u65f6\u9650\u4e4b\u5185\u7981\u6b62\u53cd\u5411\u64cd\u4f5c\u3002\u7531\u4e8eESS\u4e0e\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u5b58\u5728\u5173\u8054\uff0c\u7531\u4e8eESS\u56db\u6708\u521d\u624d\u5c06\u624b\u91cc\u6606\u817e\u5728\u7ebf\u7684\u80a1\u7968\u5b8c\u5168\u6e05\u7a7a\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u8981\u589e\u6301\uff0c\u4e5f\u8981\u7b49\u5230\u5341\u6708\u4e4b\u521d\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u653e\u5f03\u51cf\u6301\uff0c\u867d\u7136\u7ecf\u6d4e\u4e0a\u4f1a\u8499\u53d7\u4e00\u5b9a\u7684\u635f\u5931\uff0c\u4f46\u662f\u4fdd\u8bc1\u5bf9\u6606\u817e\u5728\u7ebf\u7684\u76f8\u5bf9\u63a7\u5236\u3001\u4fdd\u8bc1\u6606\u817e\u5728\u7ebf\u7ba1\u7406\u56e2\u961f\u7684\u7a33\u5b9a\u6027\u3001\u4fdd\u8bc1\u80fd\u591f\u5f15\u5bfc\u6606\u817e\u5728\u7ebf\u5728\u6b63\u786e\u7684\u53d1\u5c55\u65b9\u5411\u4e0a\uff0c\u8fd9\u4e48\u505a\u662f\u5fc5\u987b\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0a\u6d77\u8bc1\u5238\u62a5\u7b49\u4e3b\u8981\u56fd\u5185\u8d22\u7ecf\u5a92\u4f53\u62a5\u9053\u4e86\u53d1\u751f\u5728\u56fd\u95e8\u4e4b\u5916\u7684\u7f51\u7edc\u80a1\u707e\uff0c\u6caa\u6df1\u4e24\u5e02\u7f51\u7edc\u6982\u5ff5\u80a1\u54c0\u9e3f\u904d\u91ce\u3002</p><p>\u3000\u3000\u300a\u8d22\u7ecf\u65b0\u5468\u520a\u300b\u520a\u51fa\u62a5\u9053\uff0c\u76f4\u63a5\u6307\u51fa\u4e0d\u65ad\u62ff\u5927\u91cf\u8d44\u91d1\u5806\u5792\u8d77\u6765\u7684\u4e2d\u56fd\u7b2c\u4e00\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u4f01\u4e1a8818\u662f\u56fd\u5185\u6700\u5927\u7684\u7f51\u7edc\u6ce1\u6cab\uff0c\u6d77\u7c9f\u79d1\u6280\u62e5\u6709\u5bf98818\u624d30\uff05\u6709\u80a1\u6743\uff0c\u5e02\u503c\u5c31\u7092\u5230\u4e00\u767e\u516d\u4e03\u5341\u4ebf\uff0c\u66f4\u662f\u6ce1\u6cab\u4e2d\u7684\u6ce1\u6cab\u3002</p><p>\u3000\u3000\u300a\u8d22\u7ecf\u65b0\u5468\u520a\u300b\u6bcf\u5468\u56db\u53d1\u884c\uff0c\u7535\u8bdd\u8ffd\u67e5\u8fc7\u53bb\uff0c\u90a3\u7bc7\u62a5\u9053\u65e9\u5728\u4e00\u5468\u4e4b\u524d\u5c31\u5df2\u7ecf\u5b9a\u7a3f\uff0c\u8d76\u5728\u80a1\u707e\u66b4\u53d1\u65f6\u7ed9\u6d77\u7c9f\u79d1\u6280\u4e00\u95f7\u68cd\uff0c\u7eaf\u5c5e\u4ed6\u4eec\u65f6\u8fd0\u4e0d\u6d4e\u3002</p><p>\u3000\u3000\u5e74\u521d\u4ee5\u6765\uff0c\u5173\u4e8e\u6ce1\u6cab\u7684\u4e89\u8bba\u5f88\u591a\uff0c\u53ea\u662f\u80a1\u5e02\u4e00\u76f4\u9ad8\u6b4c\u731b\u8fdb\uff0c\u90a3\u4e9b\u5ba3\u79f0\u7f51\u7edc\u5b58\u5728\u6ce1\u6cab\u7684\u58f0\u97f3\u4e00\u76f4\u90fd\u5927\u4e0d\u8d77\u6765\uff0c\u6ca1\u6709\u591a\u5c11\u5f71\u54cd\u529b\uff0c\u4f46\u662f\u8fd9\u65f6\u5019\u8fd9\u4e48\u4e00\u7bc7\u62a5\u9053\uff0c\u53c8\u7ecf\u591a\u5bb6\u95e8\u6237\u7f51\u7ad9\u8f6c\u8f7d\uff0c\u5f71\u54cd\u4e00\u4e0b\u5b50\u5c31\u5927\u8d77\u6765\u3002</p><p>\u3000\u3000\u6caa\u5e02\u4e00\u5f00\u76d8\uff0c\u6d77\u7c9f\u79d1\u6280\u5c31\u6d8c\u51fa\u5927\u91cf\u7684\u629b\u5355\uff0c\u80a1\u4ef7\u5728\u51e0\u5206\u949f\u4e4b\u5185\u5c31\u7ed9\u6253\u5230\u8dcc\u505c\u677f\u4e0a\uff0c\u79ef\u538b\u7740\u5927\u91cf\u7684\u629b\u5355\uff0c\u5076\u5c14\u4e5f\u6709\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e2d\u5c0f\u6563\u6237\u96f6\u6563\u7684\u4e70\u5165\uff0c\u5374\u6839\u672c\u4e0d\u6210\u89c4\u6a21\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5927\u95ee\u9898\uff01\u201d\u80e1\u91d1\u661f\u5750\u5728\u534a\u5f27\u5f62\u7684\u6d45\u5496\u5561\u8272\u8f6c\u6905\u4e0a\uff0c\u4e0d\u5b89\u5206\u7684\u8f6c\u6765\u8f6c\u53bb\uff0c\u773c\u775b\u5374\u4f3c\u4e4e\u76ef\u7740\u6db2\u6676\u5c4f\u770b\uff0c\u4ed6\u4e0e\u9648\u52c7\u5728\u521b\u57df\u7f51\u5427\u7684\u7ecf\u7406\u5ba4\u91cc\u770b\u80a1\u7968\uff0c\u4ed6\u5634\u91cc\u867d\u7136\u8bf4\u5f97\u8f7b\u677e\uff0c\u4f46\u662f\u90a3\u5e9e\u5927\u7684\u629b\u5355\u8fd8\u662f\u538b\u5728\u4ed6\u7684\u5fc3\u5934\u6709\u4e9b\u5598\u4e0d\u6c14\u6765\u7684\u611f\u89c9\u3002\u6628\u5929\u8fd8\u662f\u597d\u597d\u7684\uff0c\u4ed6\u4eec\u4ece\u4e91\u6797\u8bc1\u5238\u52df\u5f97\u4e09\u5343\u4e07\u7684\u878d\u8d44\u6295\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\uff0c\u6628\u5929\u8fd8\u7d2f\u79ef\u8d62\u5229\u4e24\u767e\u591a\u4e07\uff0c\u4eca\u5929\u5f00\u76d8\u51e0\u5206\u949f\u4e4b\u95f4\u5c31\u5410\u4e86\u5e72\u51c0\uff0c\u8fd8\u5012\u4e8f\u8fdb\u51e0\u5341\u4e07\u3002\u5373\u4f7f\u77e5\u9053\u5b8f\u4fe1\u3001\u7cbe\u5178\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u7684\u5927\u52a8\u4f5c\u8c0b\u5212\u4e86\u5c06\u8fd1\u4e00\u5e74\uff0c\u7edd\u4e0d\u4f1a\u8f7b\u800c\u6613\u4e3e\u7684\u653e\u5f03\uff0c\u80e1\u91d1\u661f\u5fc3\u91cc\u603b\u8fd8\u6709\u4e00\u4e9b\u62c5\u5fc3\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u4f60\u7ea6\u738b\u6d77\u7c9f\u51fa\u6765\u5403\u996d\uff1f\u201d\u9648\u52c7\u5fc3\u91cc\u66f4\u4e0d\u5927\u8e0f\u5b9e\uff0c\u80e1\u91d1\u661f\u8ddf\u90a3\u8fb9\u7684\u5173\u7cfb\u5bc6\u5207\u4e00\u4e9b\uff0c\u4ed6\u8fd8\u662f\u56e0\u4e3a\u6574\u5929\u8ddf\u80e1\u91d1\u661f\u6df7\u5728\u4e00\u8d77\uff0c\u624d\u8ddf\u90a3\u8fb9\u626f\u4e0a\u8054\u7cfb\uff0c\u4ed6\u6ca1\u60f3\u5230\u6d77\u7c9f\u79d1\u6280\u5728\u8fc7\u53bb\u5341\u4e00\u4e2a\u6708\u91cc\u8fde\u7eed\u4e0a\u6da8\u4e86\u4e09\u5341\u500d\uff0c\u5728\u4ed6\u4eec\u51b3\u5fc3\u8981\u5927\u5e72\u4e00\u7b14\u65f6\u5374\u95f9\u51fa\u5168\u7403\u65b0\u7ecf\u6d4e\u80a1\u707e\u6765\u3002\u867d\u7136\u8fd9\u65f6\u5019\u8bf4\u662f\u80a1\u707e\u8fd8\u4e3a\u8a00\u5c1a\u65e9\uff0c\u4f46\u662f\u5a92\u4f53\u6c14\u52bf\u6c79\u6c79\u7684\u62a5\u9053\u8ba9\u4eba\u89e6\u76ee\u60ca\u5fc3\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u6211\u770b\u4ed6\u6709\u6ca1\u6709\u65f6\u95f4\u51fa\u6765\u4e00\u8d77\u5403\u996d\u3002\u201d\u80e1\u91d1\u661f\u62ff\u8d77\u624b\u673a\u8bd5\u7740\u62e8\u6253\u738b\u6d77\u7c9f\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5c41\u80a1\u51e0\u4e4e\u7c98\u5728\u6905\u5b50\u4e0a\uff0c\u6839\u672c\u65e0\u6cd5\u79bb\u5f00\u6d77\u7c9f\u79d1\u6280\u7684\u603b\u90e8\u5927\u53a6\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u603b\u5e02\u503c\u4e00\u5ea6\u63a5\u8fd1\u4e00\u767e\u4e03\u5341\u4ebf\uff0c\u5176\u4e2d\u8fd170\uff05\u7684\u80a1\u7968\u662f\u9650\u5236\u4e0a\u5e02\u6d41\u901a\u7684\u6cd5\u4eba\u80a1\u4e0e\u5b9a\u5411\u589e\u53d1\u80a1\uff0c\u4e0d\u53d7\u9650\u5236\u4e0a\u5e02\u6d41\u901a\u7684\u80a1\u7968\u5e02\u503c\u6709\u4e94\u5341\u4ebf\uff0c\u5b8f\u4fe1\u6295\u8d44\u6240\u638c\u63e1\u7684\u5927\u91cf\u5173\u8054\u8d26\u6237\u6240\u6301\u6d77\u7c9f\u79d1\u6280\u80a1\u7968\u6d41\u901a\u5e02\u503c\u5dee\u4e0d\u591a\u6709\u5341\u4ebf\uff0c\u4e5f\u5c31\u662f\u8bf4\u8fd8\u6709\u56db\u5341\u4ebf\u5e02\u503c\u7684\u6d41\u901a\u80a1\u7968\u5206\u6563\u5728\u5176\u4ed6\u6295\u8d44\u673a\u6784\u4e0e\u6563\u6237\u624b\u91cc\uff0c\u5916\u56f4\u5f62\u52bf\u51f6\u6076\uff0c\u4e0d\u80fd\u8d38\u7136\u52a8\u7528\u8d44\u91d1\u6258\u5e02\uff0c\u603b\u8981\u5148\u8ba9\u538b\u529b\u6cc4\u6389\u4e00\u4e0b\u518d\u8bf4\u2014\u2014\u53ea\u662f\u6ca1\u6709\u9884\u6599\u5230\u538b\u529b\u4f1a\u5982\u6b64\u4e4b\u5de8\uff0c\u8dcc\u505c\u677f\u5728\u65e9\u76d8\u5f00\u76d8\u540e\u8fc5\u901f\u79ef\u538b\u4e86\u8d85\u8fc7\u516d\u4ebf\u5e02\u503c\u7684\u629b\u5355\uff0c\u5230\u4e0b\u5348\u4e09\u70b9\u949f\u65f6\uff0c\u629b\u5355\u7d2f\u79ef\u5230\u5341\u4ebf\u5e02\u503c\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e4b\u524d\u9884\u6d4b\u5230\u4f1a\u5f88\u5927\u7684\u538b\u529b\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u629b\u538b\u4f1a\u5982\u6b64\u4e4b\u5de8\u3002</p><p>\u3000\u3000\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u8bc1\u5238\u5e02\u573a\u505c\u6b62\u4ea4\u6613\uff0c\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u3001\u5218\u5947\u5cfb\u3001\u5f20\u6587\u4fe1\u4ee5\u53ca\u6295\u8d44\u90e8\u7684\u51e0\u540d\u7ecf\u7406\u53c8\u8fde\u8f74\u5728\u4f1a\u8bae\u91cc\u53ec\u5f00\u7b56\u7565\u5e94\u5bf9\u4f1a\u8bae\u3002</p><p>\u3000\u3000\u201c\u518d\u8fc7\u534a\u5c0f\u65f6\uff0c\u6b27\u6d32\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\u5f00\u59cb\u4ea4\u6613\uff0c\u6b27\u6d32\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\u95ed\u5e02\u540e\uff0c\u53c8\u8f6e\u5230\u5317\u7f8e\u8bc1\u5238\u5e02\u573a\u5f00\u76d8\uff0c\u8981\u662f\u4eca\u665a\u6b27\u7f8e\u6ca1\u6709\u4ec0\u4e48\u9006\u8f6c\u6027\u7684\u6d88\u606f\uff0c\u660e\u5929\u7684\u629b\u5355\u4f1a\u66f4\u52a0\u6c79\u6d8c\uff0c\u201d\u5218\u5947\u5cfb\u5206\u6790\u9053\uff0c\u4ed6\u5df2\u7ecf\u8fde\u7eed\u6709\u56db\u5341\u5c0f\u65f6\u6ca1\u6709\u95ed\u773c\u4e86\uff0c\u773c\u775b\u91cc\u90fd\u662f\u8840\u4e1d\uff0c\u8bf4\u8bdd\u58f0\u97f3\u6709\u4e9b\u6c99\u54d1\uff0c\u201c\u6211\u4eec\u73b0\u5728\u80fd\u52a8\u7528\u7684\u8d44\u91d1\u53ea\u6709\u4e94\u4e2a\u4ebf\uff0c\u6ca1\u6709\u5a92\u4f53\u8206\u8bba\u7684\u914d\u5408\uff0c\u53ea\u6015\u96be\u4ee5\u5c06\u629b\u538b\u90fd\u5316\u89e3\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u660e\u5929\u8fd8\u662f\u8981\u6309\u5175\u4e0d\u52a8\uff1f\u201d\u738b\u6d77\u7c9f\u95ee\u9053\u3002</p><p>\u3000\u3000\u6797\u96ea\u5acc\u6076\u7684\u6487\u4e86\u6487\u5634\uff0c\u5979\u90fd\u80fd\u660e\u767d\u7684\u9053\u7406\uff0c\u738b\u6d77\u7c9f\u5374\u8981\u591a\u6b64\u4e00\u95ee\uff0c\u8981\u662f\u5c06\u624b\u5934\u7684\u4e94\u4ebf\u8d44\u91d1\u8017\u5149\uff0c\u4ee5\u540e\u9677\u5165\u5f7b\u5e95\u7684\u88ab\u52a8\uff0c\u73b0\u5728\u51e0\u5927\u8d22\u7ecf\u5a92\u4f53\u90fd\u5927\u8086\u62a5\u9053\u80a1\u707e\uff0c\u5373\u4f7f\u8981\u8425\u9020\u6709\u5229\u7684\u8206\u8bba\u6c1b\u56f4\uff0c\u4e5f\u4e0d\u53ef\u80fd\u786c\u62d7\uff0c\u660e\u5929\u8981\u662f\u629b\u538b\u592a\u51f6\u4e5f\u53ea\u80fd\u653e\u4efb\u4e0d\u7ba1\u3002</p><p>\u3000\u3000\u201c\u8fc7\u4e86\u660e\u5929\u5c31\u662f\u4e94\u4e00\u957f\u5047\uff0c\u6caa\u6df1\u4e24\u5e02\u524d\u540e\u5171\u8981\u4f11\u5e02\u4e5d\u5929\u65f6\u95f4\uff0c\u4e5d\u5929\u65f6\u95f4\u8fc7\u53bb\uff0c\u6b27\u7f8e\u8bc1\u5238\u5e02\u573a\u518d\u52a8\u8361\u4e0d\u4f11\u4e5f\u5e94\u8be5\u7f13\u4e00\u53e3\u6c14\u4e86\uff0c\u201d\u5218\u5947\u5cfb\u4e0d\u7406\u4f1a\u738b\u6d77\u7c9f\u7684\u95ee\u8bdd\uff0c\u5206\u6790\u9053\uff0c\u201c\u6211\u4eec\u4e5f\u5e94\u8be5\u597d\u597d\u5229\u7528\u8fd9\u4e5d\u5929\u7684\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u8fd9\u8fb9\u5c31\u4ea4\u7ed9\u4f60\u4eec\u4e86\uff0c\u6211\u665a\u4e0a\u5c31\u53bb\u5317\u4eac\u3002\u201d\u4e25\u6587\u4ecb\u51b3\u5b9a\u9053\uff0c\u201c\u5373\u4f7f\u4e0d\u80fd\u5c06\u5a92\u4f53\u8206\u8f6e\u9006\u8f6c\u8fc7\u6765\uff0c\u4e5f\u8981\u5c06\u6c34\u6405\u6d51\u4e86\uff0c\u6709\u80fd\u529b\u5c06\u6c34\u6405\u6d51\u7684\u7ecf\u6d4e\u5b66\u5bb6\u4eec\u5927\u591a\u805a\u96c6\u5728\u5317\u4eac\uff0c\u6211\u8981\u6293\u7d27\u65f6\u95f4\u62dc\u8bbf\u51e0\u4e2a\u4eba\u2014\u2014\u5f53\u7136\uff0c\u4e0d\u5e0c\u671b\u770b\u5230\u5c40\u52bf\u6301\u7eed\u6076\u5316\u7684\uff0c\u4e5f\u7edd\u4e0d\u6b62\u6211\u4eec\u4e00\u5bb6\u3002\u661f\u6cb3\u8bc1\u5238\u8fd9\u8fb9\uff0c\u5c31\u8981\u6258\u4ed8\u7ed9\u6797\u603b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u4e8c\u5341\u591a\u5929\u7684\u65f6\u95f4\u4e86\uff0c\u6211\u5929\u5929\u76ef\u7740\u4ed6\u4eec\u5c31\u662f\u3002\u201d\u6797\u96ea\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5218\u5947\u5cfb\u53c8\u8ddf\u4e25\u6587\u4ecb\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5728\u8d44\u91d1\u4e0a\u591a\u505a\u4e00\u4e9b\u51c6\u5907\uff1f\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u542c\u5230\u5218\u5947\u5cfb\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4e5f\u8f6c\u8fc7\u53bb\u770b\u7740\u4e25\u6587\u4ecb\u7684\u8138\uff0c\u89c1\u4ed6\u6478\u7740\u9f3b\u5b50\u601d\u91cf\uff0c\u77e5\u9053\u4ed6\u6ca1\u6709\u89c1\u597d\u5c31\u597d\u7684\u6253\u7b97\uff0c\u4e5f\u5c31\u4e0d\u8bf4\u4ec0\u4e48\u8ba8\u4eba\u5acc\u4e86\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u76f4\u5230\u4f1a\u8bae\u7ed3\u675f\uff0c\u624d\u6ce8\u610f\u770b\u5230\u624b\u673a\u4e0a\u6709\u597d\u51e0\u4e2a\u672a\u63a5\u7535\u8bdd\u90fd\u662f\u80e1\u91d1\u661f\u6253\u6765\uff0c\u5f85\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u79bb\u5f00\u603b\u90e8\u5927\u53a6\uff0c\u4ed6\u624d\u56de\u5230\u529e\u516c\u5ba4\u91cc\u7ed9\u80e1\u91d1\u661f\u56de\u7535\u8bdd\uff0c\u9694\u7740\u843d\u5730\u73bb\u7483\u7a97\u770b\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u7684\u8f66\u5b50\u90fd\u9a76\u5165\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\uff0c\u4ed6\u624d\u4e0b\u697c\u5f00\u8f66\u5230\u5b66\u5e9c\u5df7\u627e\u80e1\u91d1\u661f\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u6bd5\u7adf\u540d\u4e49\u4e0a\u662f\u6d77\u7c9f\u79d1\u6280\u7684\u603b\u88c1\uff0c\u4e0d\u80fd\u6709\u4e8b\u65e0\u4e8b\u8dd1\u5230\u521b\u57df\u7f51\u5427\u53bb\uff0c\u5728\u5185\u5df71978\u5bf9\u9762\u7684\u5496\u5561\u5385\u91cc\u8ddf\u80e1\u91d1\u661f\u3001\u9648\u52c7\u89c1\u9762\uff0c\u4ed6\u4eec\u5750\u5728\u4e8c\u697c\uff0c\u9694\u7740\u73bb\u7483\u7a97\u80fd\u591f\u770b\u5230\u5df7\u5b50\u5bf9\u9762\u76841978\uff0c\u4ed6\u4eec\u85cf\u7740\u5915\u9633\u7167\u8000\u4e0d\u5230\u7684\u5f71\u5b50\u91cc\uff0c1978\u7684\u95e8\u6d1e\u5374\u7ed9\u5915\u9633\u7167\u5f97\u91d1\u7ea2\u6d41\u4e39\u3002</p><p>\u3000\u3000\u201c\u660e\u5929\u4f1a\u600e\u4e48\u6837\uff1f\u8981\u662f\u518d\u6765\u4e00\u4e2a\u8dcc\u505c\u677f\uff0c\u4f60\u501f\u7ed9\u6211\u4eec\u7684\u4fdd\u8bc1\u91d1\u90fd\u8981\u8d54\u8fdb\u53bb\u4e86\u2026\u2026\u201d\u9648\u52c7\u7126\u8651\u7684\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u53ea\u80fd\u53bb\u8df3\u5c0f\u6c5f\u8d54\u7ed9\u4f60\u4e86\u3002\u201d</p><p>\u3000\u3000\u80e1\u91d1\u661f\u7a0d\u9547\u9759\u4e00\u4e9b\uff0c\u8bf4\u767d\u4e86\u4ed6\u8054\u5408\u9648\u52c7\u5728\u5916\u56f4\u7092\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u662f\u60f3\u635e\u4e9b\u5916\u5feb\u7f62\u4e86\uff0c\u5c31\u7b97\u6709\u4e9b\u635f\u5931\uff0c\u4ed6\u4e5f\u80fd\u627f\u53d7\u3002</p><p>\u3000\u3000\u5f53\u521d\u6210\u7acb\u6d77\u7c9f\u79d1\u6280\u6253\u9020\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u65f6\uff0c\u4ed6\u53d4\u53d4\u80e1\u5b97\u5e86\u5c31\u4ee5\u4ed6\u7684\u540d\u4efd\u5728\u6d77\u7c9f\u79d1\u6280\u5360\u4e86\u76f8\u5f53\u6bd4\u4f8b\u7684\u5e72\u80a1\u3002\u6d77\u7c9f\u79d1\u6280\u501f\u58f3\u4e0a\u5e02\uff0c\u4e3a\u4e86\u907f\u514d\u66b4\u9732\uff0c\u4ed6\u540d\u4e49\u4e0b\u7684\u80a1\u4efd\u90fd\u8f6c\u8ba9\u7ed9\u5b8f\u4fe1\u6295\u8d44\uff0c\u9664\u4e86\u6c47\u5916\u6d77\u5916\u8d26\u6237\u7684\u5de8\u989d\u8d44\u91d1\u5916\uff0c\u4e25\u6587\u4ecb\u8fd8\u8ba9\u4ed6\u76f4\u63a5\u8fdb\u5165\u6d77\u7c9f\u79d1\u6280\u5de5\u4f5c\uff0c\u5141\u8bfa\u4ee5\u80a1\u6743\u6fc0\u52b1\u7684\u540d\u4e49\u5206\u7ed9\u4ed6\u4e2a\u4eba\u80a1\u4efd\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5c06\u6765\u6709\u4ebf\u4e07\u8d22\u5bcc\u53ef\u5f97\uff0c\u5948\u4f55\u773c\u4e0b\u624b\u5934\u9887\u7d27\uff0c\u80e1\u91d1\u661f\u624d\u62c9\u62e2\u9648\u52c7\u5728\u5916\u56f4\u7092\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u635e\u4e9b\u5916\u5feb\uff0c\u4e00\u65b9\u9762\u4e5f\u662f\u4e4b\u524d\u6ca1\u6709\u60f3\u5230\u6d77\u7c9f\u79d1\u6280\u4f1a\u7ed9\u5b8f\u4fe1\u7092\u4f5c\u5f97\u7099\u624b\u53ef\u70ed\uff0c\u89c9\u5f97\u5148\u524d\u7684\u5206\u914d\u6709\u4e9b\u5403\u4e8f\uff0c\u624d\u60f3\u7740\u5728\u5916\u9762\u5f97\u5230\u4e9b\u8865\u507f\u3002\u73b0\u5728\u5373\u4f7f\u6709\u635f\u5931\uff0c\u4e5f\u53ea\u662f\u6709\u4e9b\u5fc3\u75db\u7f62\u4e86\uff0c\u4ed6\u73b0\u5728\u4e0d\u8ba4\u4e3a\u4e8f\u6389\u51e0\u767e\u4e07\u7b97\u4ec0\u4e48\u635f\u5931\u3002</p><p>\u3000\u3000\u9648\u52c7\u5374\u662f\u5c06\u4ed6\u53d1\u5bb6\u81f4\u5bcc\u7684\u6240\u6709\u5e0c\u671b\u5bc4\u6258\u5728\u8fd9\u4e0a\u9762\uff0c\u5fc3\u91cc\u7126\u8651\uff0c\u8981\u662f\u660e\u5929\u518d\u6765\u4e00\u4e2a\u8dcc\u505c\u677f\uff0c\u4ed6\u4eec\u5c31\u662f\u60f3\u58ee\u58eb\u65ad\u8155\u90fd\u4e0d\u884c\u3002</p><p>\u3000\u3000\u660e\u5929\u7684\u64cd\u4f5c\u57fa\u8c03\u5df2\u7ecf\u786e\u5b9a\u4e0b\u6765\uff0c\u9664\u975e\u6b27\u7f8e\u80a1\u5e02\u51fa\u73b0\u9006\u8f6c\uff0c\u4e0d\u7136\u660e\u5929\u4ed6\u4eec\u8fd8\u662f\u9009\u62e9\u7ee7\u7eed\u6cc4\u538b\uff0c\u738b\u6d77\u7c9f\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u5b89\u6170\u9648\u52c7\uff0c\u8981\u4e0d\u662f\u9648\u52c7\u7684\u8001\u5b50\u8fd8\u6709\u4e9b\u7528\u5904\uff0c\u4e5f\u61d2\u5f97\u5b89\u6170\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u6b27\u6d32\u5e02\u573a\u5c31\u8981\u5f00\u59cb\u4ea4\u6613\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4ec0\u4e48\u597d\u6d88\u606f\u4f20\u8fc7\u6765\uff0c\u7eb3\u65af\u8fbe\u514b\u8fde\u7eed\u5927\u8dcc\u4e86\u4e24\u5929\uff0c\u4e5f\u5e94\u8be5\u8981\u7f13\u4e00\u7f13\uff1b\u5373\u4f7f\u660e\u5929\u7684\u60c5\u51b5\u4e0d\u5bb9\u4e50\u89c2\uff0c\u56fd\u5185\u4e94\u4e00\u52b3\u52a8\u8282\u8981\u4f11\u5e02\u4e5d\u5929\uff0c\u4e5d\u5929\u4e4b\u540e\u7684\u5c40\u52bf\u4e5f\u4f1a\u7f13\u548c\u4e0b\u6765\u2026\u2026\u201d\u624b\u673a\u6ef4\u6ef4\u7684\u54cd\u4e86\u51e0\u58f0\uff0c\u6709\u77ed\u4fe1\u4f20\u8fdb\u6765\uff0c\u662f\u6295\u8d44\u90e8\u7684\u77ed\u4fe1\u901a\u77e5\uff0c\u6b27\u6d32\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\u5f00\u59cb\u4ea4\u6613\uff0c\u65b0\u79d1\u6280\u7ecf\u6d4e\u677f\u5757\u9634\u4e91\u60e8\u6de1\uff0c\u6709\u9677\u5165\u6076\u6027\u5faa\u73af\u7684\u8d8b\u52bf\uff0c\u8dcc\u52bf\u6bd4\u6628\u5929\u8fd8\u8981\u51f6\u731b\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5728\u5b66\u5e9c\u5df7\u966a\u9648\u52c7\u3001\u80e1\u91d1\u661f\u7b80\u5355\u7684\u7528\u8fc7\u665a\u9910\uff0c\u591c\u91cc\u7ee7\u7eed\u56de\u6d77\u7c9f\u79d1\u6280\u603b\u90e8\u5927\u53a6\u5b88\u7740\uff0c\u591c\u91cc\u5317\u7f8e\u8bc1\u5238\u5e02\u573a\u4f9d\u65e7\u6ca1\u80fd\u7ed9\u4eba\u5e0c\u671b\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u6ca1\u6709\u4e1d\u6beb\u632f\u4f5c\u7684\u8ff9\u8c61\uff0c\u5230\u51cc\u6668\u65f6\uff0c\u53c8\u518d\u6b21\u8dcc\u53bb7\uff05\u3002</p><p>\u3000\u3000\u7f51\u7edc\u80a1\u53c8\u518d\u5ea6\u6210\u4e3a\u91cd\u707e\u533a\uff0c\u8dcc\u5e45\u8d85\u8fc710\uff05\u7684\u7f51\u7edc\u80a1\u6bd4\u6bd4\u7686\u662f\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u7ba1\u7406\u5c42\u4e0e\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u90fd\u516c\u5e03\u4e0d\u51cf\u6301\u8ba1\u5212\uff0c\u4e5f\u4ec5\u4ec5\u4f7f\u5176\u76f8\u6bd4\u8f83\u5176\u4ed6\u7f51\u7edc\u80a1\u8dcc\u5e45\u597d\u770b\u4e00\u4e9b\uff0c\u6bd5\u7adf\u5f53\u521d\u5171\u540c\u53c2\u4e0e\u6606\u817e\u5728\u7ebf\u6295\u8d44\u7684\u9ad8\u76db\u3001\u534e\u767b\u7cfb\u7b49\u98ce\u6295\u57fa\u91d1\u8fd9\u65f6\u5019\u8981\u593a\u95e8\u9003\u547d\u3002\u8c01\u90fd\u7fa1\u6155ESS\u5728\u8fc7\u53bb\u56db\u4e2a\u6708\u65f6\u575a\u51b3\u7684\u5c061700\u4e07\u80a1\u6606\u817e\u5728\u7ebf\u7684\u80a1\u7968\u6e05\u7a7a\uff0c\u4e0d\u7136\u8fd9\u6b21\u4e00\u8d77\u8ddf\u7740\u593a\u8def\u9003\u547d\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u4f1a\u8dcc\u5f97\u66f4\u60e8\u3002</p><p>\u3000\u3000\u4e09\u5929\u65f6\u95f4\uff0c\u5168\u7403\u65b0\u79d1\u6280\u7ecf\u6d4e\u7684\u6307\u6807\u7eb3\u65af\u8fbe\u514b\u8dcc\u53bb20\uff05\uff0c\u8d85\u8fc7\u4e94\u5343\u4ebf\u7f8e\u5143\u7684\u8d22\u5bcc\u5c31\u5728\u4e09\u5929\u7684\u65f6\u95f4\u5185\u84b8\u53d1\u4e00\u7a7a\uff0c\u89e6\u76ee\u60ca\u5fc3\u582a\u6bd4\u4e5d\u4e03\u5e74\u7684\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u3002\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u76f4\u63a5\u8bf1\u53d1\u8fd8\u7ecf\u8fc7\u8fd1\u534a\u5e74\u65f6\u95f4\u7684\u915d\u917f\uff0c\u800c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u51e0\u4e4e\u662f\u5728\u4e00\u591c\u4e4b\u95f4\u503e\u8986\uff0c\u8868\u9762\u7684\u8bf1\u56e0\u7adf\u7136\u662f\u8054\u90a6\u6cd5\u5b98\u6258\u9a6c\u65af\u6770\u514b\u900a\u5bf9\u5a92\u4f53\u7684\u516c\u5f00\u8a00\u8bba\u81f4\u4f7f\u5fae\u8f6f\u80a1\u4ef7\u8d70\u52bf\u7531\u725b\u8f6c\u718a\u3002</p><p>\u3000\u3000\u4e9a\u592a\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\uff0c\u5305\u62ec\u56fd\u5185\u6caa\u6df1\u4e24\u5e02\u7684\u7f51\u7edc\u6982\u5ff5\u80a1\u7ee7\u7eed\u6df1\u53d7\u91cd\u521b\uff0c\u6d77\u7c9f\u79d1\u6280\u4e00\u5f00\u76d8\u5c31\u7ed9\u8d85\u8fc7\u5341\u4ebf\u5e02\u503c\u7684\u629b\u5355\u6b7b\u6b7b\u7684\u6253\u5728\u8dcc\u505c\u677f\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u52c7\u6b32\u54ed\u65e0\u6cea\uff0c\u4ece\u76c8\u5229\u4e24\u767e\u4e07\u5230\u4e8f\u635f\u56db\u767e\u4e07\u4e0d\u8fc7\u4e24\u5929\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5728\u8fc7\u53bb\u5341\u4e00\u4e2a\u6708\u65f6\u95f4\u91cc\uff0c\u4ed6\u5229\u7528\u5185\u5e55\u6d88\u606f\u7092\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u76c8\u5229\u90fd\u6ca1\u6709\u4e00\u767e\u4e07\uff0c\u8fd8\u5dee\u4e0d\u591a\u90fd\u7ed9\u4ed6\u6325\u970d\u6389\u4e86\uff0c\u8fd9\u6b21\u60f3\u73a9\u4e00\u628a\u5927\u7684\uff0c\u5c31\u662f\u4fdd\u8bc1\u91d1\u4e5f\u662f\u501f\u6765\u7684\u3002\u7531\u4e8e\u4ed6\u7092\u4f5c\u6d77\u7c9f\u79d1\u6280\u8fde\u8fde\u5f97\u624b\uff0c\u672c\u91d1\u7ffb\u756a\u6570\u5341\u500d\uff0c\u8bb8\u8bfa\u9ad8\u989d\u5229\u7387\uff0c\u53c8\u62ff\u7740\u4ed6\u8001\u5b50\u7684\u540d\u4e49\u5728\u5916\u9762\u62db\u6447\u649e\u9a97\uff0c\u81ea\u7136\u6709\u5927\u628a\u4eba\u7684\u6562\u5c06\u68fa\u6750\u672c\u501f\u7ed9\u4ed6\uff0c\u62ff\u7740\u4fdd\u8bc1\u91d1\u5411\u4e91\u6797\u8bc1\u5238\u878d\u8d44\u4e5f\u5f88\u65b9\u4fbf\uff0c\u7ed3\u679c\u6eda\u5230\u4e09\u5343\u4e07\u7684\u89c4\u6a21\uff0c\u5c31\u60f3\u8d5a\u8db3\u4e00\u5343\u4e07\u4e0e\u80e1\u91d1\u661f\u4e24\u4eba\u4e8c\u4e00\u6dfb\u4f5c\u4e94\u5206\u4e00\u5206\uff0c\u54ea\u91cc\u4f1a\u60f3\u5230\u5371\u673a\u8bf4\u6765\u5c31\u6765\uff0c\u6839\u672c\u4e0d\u6253\u5546\u91cf\u3002</p><p>\u3000\u3000\u552f\u4e00\u8ba9\u4ed6\u611f\u5230\u5b89\u6170\u7684\uff0c\u5341\u4ebf\u629b\u5355\u538b\u7740\uff0c\u4e91\u6797\u8bc1\u5238\u5373\u4f7f\u60f3\u66ff\u4ed6\u4eec\u5e73\u4ed3\u4e5f\u6ca1\u6709\u53ef\u80fd\uff0c\u4ed6\u4eec\u53ea\u8981\u7b79\u96c6\u5230\u4fdd\u8bc1\u91d1\uff0c\u5c31\u6709\u7ffb\u672c\u7684\u673a\u4f1a\uff0c\u5b8f\u4fe1\u6295\u8d44\u4e00\u5b9a\u4f1a\u91c7\u53d6\u884c\u52a8\u7684\uff0c\u9648\u52c7\u751a\u81f3\u60f3\u8ba9\u80e1\u91d1\u661f\u8bf7\u5e02\u59d4\u526f\u4e66\u8bb0\u80e1\u5b97\u5e86\u51fa\u9762\u5e72\u6d89\u4e00\u4e0b\uff0c\u8ba9\u4e91\u6797\u8bc1\u5238\u4e0d\u6562\u5f3a\u884c\u5e73\u4ed3\u2014\u2014\u8981\u662f\u7ed9\u5f3a\u884c\u5e73\u4ed3\uff0c\u4ed6\u4eec\u8fde\u7ffb\u672c\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u591a\u4fdd\u8bc1\u91d1\uff0c\u9664\u4e86\u738b\u6d77\u7c9f\u501f\u7ed9\u4ed6\u4eec\u7684\u51e0\u5341\u4e07\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u90fd\u662f\u4ed6\u501f\u8fc7\u6765\uff0c\u9648\u52c7\u8fd9\u65f6\u5019\u6709\u4e9b\u62c5\u5fc3\u80e1\u91d1\u661f\u4f1a\u53cd\u53e3\u4e0d\u8ba4\uff0c\u5728\u7ecf\u7406\u5ba4\u91cc\u5750\u4e86\u534a\u5929\uff0c\u6ca1\u770b\u89c1\u80e1\u91d1\u661f\u9732\u8138\uff0c\u90fd\u4e0d\u77e5\u9053\u4ed6\u4eba\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u3000\u3000\u624b\u673a\u54cd\u8d77\u6765\uff0c\u662f\u4e2a\u964c\u751f\u7684\u53f7\u7801\uff0c\u9648\u52c7\u63a5\u901a\u7535\u8bdd\uff1a</p><p>\u3000\u3000\u201c\u9648\u52c7\u554a\uff0c\u4f60\u8bf4\u6d77\u7c9f\u79d1\u6280\u80af\u5b9a\u4f1a\u5927\u6da8\uff0c\u4e3a\u4ec0\u4e48\u8fde\u4e0b\u4e86\u4e24\u4e2a\u8dcc\u505c\u554a\uff1f\u6211\u77e5\u9053\u8fd9\u4e2a\u4e8b\u60c5\u6025\u4e0d\u5f97\uff0c\u5c31\u662f\u6253\u7535\u8bdd\u95ee\u4e00\u95ee\u3002\u8fd8\u6709\u554a\uff0c\u738b\u53d4\u53d4\u6211\u624b\u5934\u6700\u8fd1\u6709\u4e9b\u7d27\uff0c\u4e4b\u524d\u501f\u7ed9\u4f60\u7684\u4e94\u4e07\u5143\u80fd\u4e0d\u80fd\u63d0\u524d\u8fd8\u7ed9\u6211\uff0c\u5229\u606f\u6211\u4e5f\u4e0d\u8981\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u624d\u4e24\u4e2a\u505c\u8dcc\uff0c\u738b\u53d4\u53d4\u4f60\u5c31\u6015\u4e86\uff1f\u4e0d\u628a\u4f60\u4eec\u8fd9\u4e9b\u4e0d\u575a\u5b9a\u7684\u5206\u5b50\u6d17\u51fa\u53bb\uff0c\u7b49\u5230\u5927\u5e45\u62c9\u5347\u8d77\uff0c\u4f60\u4eec\u5c31\u4f1a\u6210\u4e3a\u62d6\u7d2f\u7684\u963b\u529b\uff0c\u6709\u4e9b\u8bdd\u6211\u4e0d\u80fd\u8bf4\u5f97\u592a\u900f\u2014\u2014\u94b1\uff0c\u6211\u90fd\u6295\u8fdb\u53bb\u4e86\uff0c\u738b\u53d4\u53d4\u4f60\u5373\u4f7f\u4e0d\u8981\u5229\u606f\uff0c\u6211\u73b0\u5728\u4e5f\u53d8\u4e0d\u51fa\u73b0\u94b1\u6765\u8fd8\u4f60\u3002\u4e0d\u7ba1\u4f60\u4fe1\u4e0d\u4fe1\u6211\uff0c\u4e0d\u7528\u8fc7\u52b3\u52a8\u8282\u5c31\u80fd\u89c1\u5206\u6653\uff0c\u6211\u73b0\u5728\u80fd\u544a\u8bc9\u4f60\u7684\u662f\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u4f1a\u6709\u91cd\u8981\u4eba\u7269\u7ad9\u51fa\u6765\u529b\u633a\u7f51\u7edc\u80a1\uff01\u201d\u9648\u52c7\u5e94\u4ed8\u7740\u5c06\u7535\u8bdd\u6302\u6389\uff0c\u770b\u7740\u8d70\u52bf\u56fe\u4e0a\u7ea2\u7ebf\u5728\u8dcc\u505c\u4f4d\u7ee7\u7eed\u62c9\u7740\u76f4\u7ebf\uff0c\u5fc3\u70e6\u6c14\u8e81\u7684\u6284\u8d77\u624b\u91cc\u7684\u8336\u676f\u7838\u7535\u8111\u5c4f\u5e55\u4e0a\u3002</p><p>\u3000\u3000\u80e1\u91d1\u661f\u6b63\u597d\u63a8\u95e8\u8fdb\u6765\uff0c\u7ed9\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u770b\u7740\u6c14\u95f7\uff01\u5927\u534a\u5929\uff0c\u4f60\u53bb\u4e86\u54ea\u91cc\uff1f\u201d\u9648\u52c7\u6ca1\u597d\u6c14\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u5fc3\u865a\u4ec0\u4e48\uff01\u4f60\u4ee5\u4e3a\u6211\u5e72\u4ec0\u4e48\u53bb\u4e86\uff0c\u6211\u7ed9\u53eb\u5230\u6211\u53d4\u53d4\u7684\u529e\u516c\u5ba4\u91cc\u7ed9\u8bad\u4e86\u534a\u5929\u3002\u4e91\u6797\u8bc1\u5238\u90a3\u8fb9\uff0c\u6211\u53d4\u53d4\u5e2e\u6211\u4eec\u6253\u8fc7\u62db\u547c\u4e86\uff0c\u5728\u5df2\u7f34\u7eb3\u4fdd\u8bc1\u91d1\u7684\u57fa\u7840\u4e0a\u518d\u653e\u5bbd\u4e00\u500d\uff0c\u53ea\u8981\u6d77\u7c9f\u79d1\u6280\u4e0d\u518d\u6765\u4e24\u4e2a\u8dcc\u505c\uff0c\u6211\u4eec\u5c31\u4e0d\u4f1a\u7ed9\u5f3a\u884c\u5e73\u4ed3\uff0c\u201d\u80e1\u91d1\u661f\u51b7\u773c\u770b\u7740\u9648\u52c7\uff0c\u5f88\u607c\u706b\u9648\u52c7\u6562\u8ddf\u4ed6\u53d1\u813e\u6c14\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff0c\u201c\u738b\u6d77\u7c9f\u90a3\u91cc\u521a\u5f97\u6765\u6d88\u606f\uff0c\u4eba\u5927\u8d22\u7ecf\u59d4\u526f\u4e3b\u4efb\u5434\u6653\u6ce2\u4eca\u5929\u665a\u4e0a\u4f1a\u53d1\u8a00\u529b\u633a\u56fd\u5185\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u2026\u2026\u4f60\u8bf4\u6211\u662f\u5e72\u4ec0\u4e48\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u52c7\u4e5f\u662f\u5e74\u8f7b\u6c14\u76db\uff0c\u8f7b\u6613\u62b9\u4e0d\u5f00\u9762\u5b50\u9053\u8c26\uff0c\u55eb\u5685\u7740\u51e0\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u5750\u5728\u8fd9\u91cc\u5fc3\u6025\u5f97\u5f88\uff0c\u65e2\u7136\u6709\u597d\u6d88\u606f\u4e86\uff0c\u4e5f\u5c31\u7a0d\u7a0d\u5b89\u5b9a\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u90a3\u70b9\u51fa\u606f\uff0c\u5b8f\u4fe1\u3001\u7cbe\u5178\u5728\u80cc\u540e\u51e0\u5341\u4ebf\u7684\u4e70\u5356\u90fd\u4e0d\u62c5\u5fc3\u4ec0\u4e48\uff0c\u5c31\u4f60\u5728\u8fd9\u91cc\u6c89\u4e0d\u4f4f\u6c14\uff01\u201d\u80e1\u91d1\u661f\u538b\u7740\u58f0\u97f3\u8bf4\u9053\uff0c\u5fc3\u91cc\u4e5f\u597d\u4e0d\u5bb9\u6613\u624d\u987a\u8fc7\u6c14\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u4e94\u4e00\u52b3\u52a8\u8282\u7684\u524d\u591c\uff0c\u5168\u56fd\u4eba\u5927\u8d22\u7ecf\u59d4\u526f\u4e3b\u4efb\u5434\u6653\u6ce2\u4ee5\u300a\u50ac\u751f\u6c11\u65cf\u7f51\u7edc\u7ecf\u6d4e\u610f\u8bc6\u3001\u50ac\u5316\u4f20\u7edf\u4ea7\u4e1a\u501f\u7f51\u5347\u7ea7\u3001\u50ac\u4fc3\u4fe1\u606f\u4ea7\u4e1a\u4e0e\u4e16\u754c\u540c\u6b65\u3001\u50ac\u751f\u5168\u6c11\u521b\u4e1a\u6587\u5316\u6c1b\u56f4\u2014\u2014\u8bba\u7f51\u7edc\u6ce1\u6cab\u7684\u79ef\u6781\u4f5c\u7528\u300b\u4e3a\u9898\uff0c\u5426\u8ba4\u6ce1\u6cab\u4f7f\u56fd\u5185\u7f51\u7edc\u53d1\u5c55\u81a8\u80c0\u8fc7\u5feb\u6fd2\u4e34\u5371\u673a\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u5f53\u591c\u5728\u7f51\u7ad9\u9996\u9875\u516c\u5e03\u8d22\u52a1\u6536\u7d27\u653f\u7565\uff0c\u5ba3\u5e03\u5c06\u6267\u884c\u5305\u62ec\u964d\u4f4e\u7ba1\u7406\u5c42\u85aa\u8d44\u3001\u653e\u5f03\u65b0\u62db\u8058\u8ba1\u5212\u3001\u8ba1\u5212\u88c1\u51cf8\uff05\u6bd4\u4f8b\u5458\u5de5\u3001\u538b\u7f29\u884c\u653f\u4e8b\u52a1\u5f00\u652f\u7b49\u591a\u9879\u63aa\u65bd\u5728\u5185\u7684\u65b9\u6848\u6765\u964d\u4f4e\u7f51\u7ad9\u7684\u8fd0\u8425\u6210\u672c\uff0c\u65d7\u5e1c\u66f4\u9c9c\u660e\u7684\u5ba3\u544a\u7f51\u7edc\u6ce1\u6cab\u5371\u673a\u7684\u964d\u4e34\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u4f5c\u4e3a\u4e2d\u6587\u7b2c\u4e00\u95e8\u6237\u7f51\u7ad9\uff0c\u53c8\u662f\u56fd\u5185\u7b2c\u4e00\u5bb6\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u4e2d\u56fd\u7f51\u7edc\u4f01\u4e1a\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u56fd\u5185\u4e92\u8054\u7f51\u4f01\u4e1a\u7684\u98ce\u5411\u6807\uff0c\u6606\u817e\u5728\u7ebf\u7684\u8d22\u52a1\u6536\u7f29\u4e3e\u63aa\u66f4\u5438\u5f15\u5a92\u4f53\u4e0e\u516c\u4f17\u7684\u773c\u7403\uff0c\u8fc5\u901f\u5c06\u5434\u6653\u6ce2\u7684\u5f71\u54cd\u529b\u63a9\u76d6\u4e0b\u53bb\u3002</p></article>"}}}]);
