(window.webpackJsonp=window.webpackJsonp||[]).push([[160],{183:function(p){p.exports={chapterTitle:null,title:"\u7b2c160\u7ae0 \u4eba\u5fc3\u662f\u8981\u62e2\u7edc\u7684",content:"<article><h2>\u7b2c160\u7ae0 \u4eba\u5fc3\u662f\u8981\u62e2\u7edc\u7684</h2><p>\u3000\u3000\u5012\u6ca1\u60f3\u5230\u5c0f\u53d4\u5f20\u77e5\u975e\u5c06\u5e94\u916c\u63a8\u6389\u8d76\u4e86\u8fc7\u6765\uff0c\u5f20\u606a\u521a\u63d0\u8d77\u6c99\u7530\u8857\u5e02\u7684\u706b\u707e\uff0c\u4ed6\u5c31\u63a8\u95e8\u8d70\u4e86\u8fdb\u6765\uff1a\u201c\u72d7\u65e5\u7684\uff0c\u6c99\u7530\u90a3\u91cc\u521a\u5931\u706b\uff0c\u5468\u5bcc\u660e\u5c31\u8981\u6c42\u6709\u5173\u90e8\u95e8\u8981\u5c3d\u5feb\u7a33\u59a5\u7684\u5584\u540e\uff0c\u8981\u4f53\u73b0\u653f\u5e9c\u5bf9\u5e02\u6c11\u7684\u5173\u6000\uff0c\u4ed6\u4eec\u5012\u662f\u4e00\u6b65\u7d27\u8ddf\u7740\u4e00\u6b65\u7684\uff0c\u4e00\u70b9\u4e0d\u653e\u677e\u554a\uff01\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8bed\u6c14\u91cc\u591a\u5c11\u6709\u4e9b\u6c14\u6025\u8d25\u574f\uff0c\u5f20\u606a\u5f80\u65c1\u8fb9\u8ba9\u4e86\u8ba9\uff0c\u8ba9\u5c0f\u53d4\u5750\u81ea\u5df1\u7684\u8eab\u8fb9\uff0c\u8bf4\uff1a\u201c\u8fd9\u4e5f\u4e0d\u80fd\u8bf4\u660e\u4ec0\u4e48\u3002\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u770b\u4e0d\u60ef\u4ed6\u4eec\u8d81\u706b\u6253\u52ab\u7684\u5634\u8138\uff0c\u201d\u5f20\u77e5\u975e\u62ff\u8d77\u773c\u524d\u7684\u5564\u9152\u676f\uff0c\u4e5f\u4e0d\u7ba1\u662f\u8c01\u7684\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u201c\u672c\u6765\u5c31\u6ca1\u591a\u5927\u7684\u6307\u671b\uff0c\u773c\u4e0b\u53ef\u597d\uff0c\u5468\u5bcc\u660e\u53ef\u4ee5\u5149\u660e\u6b63\u5927\u7684\u6307\u4ee4\u90a3\u5757\u5730\u4ece\u6743\u5904\u7f6e\u4e86\uff0c\u53ea\u6015\u8fd9\u4e24\u4e09\u5929\u5c31\u4f1a\u51b3\u5b9a\u90a3\u5757\u5730\u7684\u4e70\u4e3b\u3002\u201d</p><p>\u3000\u3000\u90a3\u5757\u5730\uff0c\u5e02\u91cc\u8981\u62ff\u51fa\u6765\u5f00\u53d1\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u518d\u8ba9\u5931\u706b\u5bb6\u5ead\u539f\u5730\u91cd\u5efa\uff0c\u5468\u5bcc\u660e\u8981\u6c42\u6709\u5173\u90e8\u95e8\u5c3d\u5feb\u7a33\u59a5\u7684\u5584\u540e\u5904\u7f6e\uff0c\u6700\u73b0\u5b9e\u7684\u65b9\u6cd5\u5c31\u662f\u5c3d\u5feb\u62c6\u8fc1\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u8fd9\u6837\u7684\u5c40\u9762\uff0c\u8c01\u90fd\u65e0\u53ef\u5948\u4f55\uff0c\u90b5\u81f3\u521a\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u96be\u5f97\u5468\u4e66\u8bb0\u8fd9\u6b21\u7ad9\u51fa\u6765\u4e3a\u8001\u767e\u59d3\u8bf4\u8bdd\uff0c\u53ea\u6015\u4f1a\u6240\u5411\u62ab\u9761\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u6b47\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u53ea\u80fd\u5982\u6b64\u4e86\uff0c\u8d75\u9526\u8363\u603b\u4e0d\u80fd\u5c06\u6c99\u7530\u897f\u7247\u90fd\u5403\u4e0b\u53bb\uff0c\u90a3\u91cc\u52a8\u8d77\u6765\u4e5f\u5feb\uff0c\u201d\u8f6c\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u5c0f\u606a\uff0c\u4f60\u5728\u9526\u6e56\u80fd\u8bf4\u5f97\u4e0a\u8bdd\uff0c\u9526\u6e56\u5c31\u4e0d\u5fc3\u52a8\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ece\u5bb9\u6de1\u5b9a\u7684\u559d\u7740\u676f\u4e2d\u7684\u5564\u9152\uff0c\u5c0f\u53d4\u8ddf\u90b5\u81f3\u521a\u5bf9\u6c99\u7530\u90a3\u91cc\u7684\u5730\u6bb5\u611f\u5174\u8da3\uff0c\u4e5f\u4e0d\u662f\u4e00\u5929\u4e24\u5929\u4e86\uff0c\u5c0f\u53d4\u4e5f\u6709\u8ddf\u90b5\u81f3\u521a\u5408\u4f5c\u7684\u610f\u601d\uff0c\u4f46\u4ec5\u51ed\u4ed6\u4eec\u4e24\u4eba\u7684\u5b9e\u529b\uff0c\u8981\u8ddf\u9526\u57ce\u96c6\u56e2\u4e89\u5730\uff0c\u90a3\u65e0\u7591\u662f\u4ee5\u5375\u51fb\u77f3\u3002\u6240\u4ee5\u5c0f\u53d4\u8ddf\u90b5\u81f3\u521a\u70ed\u5207\u7740\u8ba8\u8bba\u8fd9\u4e9b\u4e8b\u60c5\u7684\u65f6\u5019\uff0c\u5f20\u606a\u4ece\u5b89\u9759\u7684\u51b7\u773c\u65c1\u89c2\uff0c\u5f88\u5c11\u53d1\u8868\u610f\u89c1\uff0c\u4e5f\u4e0d\u963b\u6b62\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u80fd\u5c06\u9526\u57ce\u96c6\u56e2\u62d6\u5165\u6ce5\u5751\uff0c\u5f20\u606a\u53ef\u6ca1\u6709\u4e00\u70b9\u5185\u759a\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u770b\u7740\u9526\u57ce\u96c6\u56e2\u8feb\u4e0d\u53ca\u5f85\u7684\u8981\u8df3\u8fdb\u5751\u91cc\u53bb\uff0c\u504f\u504f\u5c0f\u53d4\u4ed6\u4eec\u8fd8\u6124\u6124\u4e0d\u5e73\uff0c\u5f20\u606a\u6709\u7740\u4fef\u6291\u5927\u7b11\u7684\u51b2\u52a8\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e5f\u6709\u52a8\u5fc3\u554a\uff0c\u524d\u4e9b\u65f6\u95f4\u8fd8\u8bf7\u90b5\u54e5\u5728\u4e39\u4e95\u5df7\u7269\u8272\u4e86\u4e24\u5904\u5b85\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u55e4\u9f3b\u4e00\u7b11\uff0c\u56de\u5934\u770b\u7740\u5f20\u606a\u5e74\u8f7b\u5374\u663e\u5f97\u6210\u719f\u7684\u8138\u4e0a\u6302\u7740\u6de1\u6de1\u7684\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5fc3\u91cc\u60f3\u7684\uff1f\u201d\u9526\u6e56\u3001\u5b8f\u8fdc\u7ed1\u6210\u4e00\u8d77\uff0c\u8ba9\u90b5\u81f3\u521a\u53bb\u64cd\u4f5c\uff0c\u672a\u5fc5\u4e0d\u80fd\u4ece\u9526\u57ce\u96c6\u56e2\u624b\u91cc\u628a\u8fd9\u5757\u80a5\u8089\u593a\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6316\u7684\u5751\uff0c\u54ea\u6709\u81ea\u5df1\u95ed\u7740\u773c\u775b\u5f80\u91cc\u8df3\u7684\u9053\u7406\uff1f</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\uff0c\u9664\u4e86\u8bb8\u9e3f\u4f2f\uff0c\u5f20\u606a\u8c01\u4e5f\u6ca1\u6709\u544a\u8bc9\uff0c\u4e0d\u8981\u8bf4\u90b5\u81f3\u521a\u4e86\uff0c\u5c0f\u53d4\u90a3\u91cc\u4e5f\u8981\u7792\u7740\uff0c\u5fc3\u60f3\u4ee5\u5b8f\u8fdc\u516c\u53f8\u7684\u5b9e\u529b\uff0c\u4e5f\u641e\u4e0d\u51fa\u4ec0\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u5c31\u4e00\u76f4\u4fdd\u6301\u7740\u6c89\u9ed8\uff0c\u96be\u602a\u5c0f\u53d4\u591a\u5c11\u6709\u4e9b\u4e0d\u4e50\u610f\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u770b\u7740\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u676f\uff0c\u7ec6\u5c0f\u7684\u6c14\u6ce1\u4ece\u676f\u5e95\u4e0d\u95f4\u65ad\u7684\u6d6e\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8bf4\u6211\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u6d88\u606f\uff0c\u90a3\u662f\u9a97\u4f60\u4eec\uff0c\u4e0d\u7ba1\u8c01\u6700\u7ec8\u5c06\u90a3\u5757\u5730\u62ff\u4e0b\u6765\uff0c\u90fd\u4f1a\u9f13\u52a8\u5e02\u91cc\u5bf9\u6c99\u7530\u524d\u8857\u8fdb\u884c\u6539\u9020\u3002\u8bf4\u5b9e\u8bdd\uff0c\u9526\u6e56\u4ee5\u4e3a\u76ee\u524d\u8fd8\u4e0d\u9002\u5408\u5927\u89c4\u6a21\u5708\u5730\uff0c\u5c31\u7b97\u5230\u4e86\u5408\u9002\u7684\u65f6\u673a\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u901a\u8fc7\u884c\u653f\u624b\u6bb5\u62ff\u5730\uff0c\u4f17\u53e3\u94c4\u91d1\u3001\u79ef\u6bc1\u9500\u9aa8\uff0c\u9526\u6e56\u53ef\u62c5\u4e0d\u4e86\u8fd9\u4e2a\u98ce\u9669\u3002\u201d\u5f20\u606a\u77e5\u9053\u81ea\u5df1\u7684\u8fd9\u4e2a\u7406\u7531\u5728\u5c0f\u53d4\u4ed6\u4eec\u773c\u775b\u6709\u4e9b\u4e0d\u5c51\u4e00\u987e\uff0c\u4e5f\u4e0d\u7ba1\u4ed6\u4eec\u5fc3\u91cc\u600e\u4e48\u60f3\uff0c\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u6258\u90b5\u54e5\u5728\u4e39\u4e95\u5df7\u4e70\u7684\u4e24\u680b\u5b85\u5b50\uff0c\u90fd\u4e34\u7740\u6c99\u7530\u524d\u8857\uff0c\u7b49\u6c99\u7530\u524d\u8857\u4e00\u6539\u9020\uff0c\u9526\u6e56\u4f1a\u8bd5\u7740\u5c06\u90a3\u680b\u5b85\u5b50\u6539\u5efa\u4e00\u4e0b\uff0c\u6ca1\u6307\u671b\u53d1\u8d22\uff0c\u5c31\u6253\u7b97\u7ed9\u516c\u53f8\u91cc\u4eba\u4f4f\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u7b11\u4e86\u7b11\uff0c\u6572\u4e86\u6572\u5f20\u606a\u7684\u5934\uff0c\u4ed6\u53ea\u5f53\u5f20\u606a\u4e0d\u80af\u8bf4\u5b9e\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u4ed6\u7238\u5f20\u77e5\u884c\u7684\u610f\u601d\uff0c\u7b49\u5230\u5085\u4fca\u90a3\u51e0\u4e2a\u6d17\u6389\u6666\u6c14\u7684\u4eba\u8fc7\u6765\uff0c\u624d\u5230\u5434\u5929\u5b9d\u7684\u996d\u5e97\u91cc\u5403\u996d\u53bb\u3002</p><p>\u3000\u3000\u996d\u684c\u4e0a\uff0c\u5f20\u606a\u63d0\u5230\u8ba9\u5085\u4fca\u5230\u4ed6\u516c\u53f8\u53bb\u7684\u4e8b\u60c5\uff0c\u53ea\u8981\u76db\u9752\u653e\u4eba\uff0c\u5085\u4fca\u81ea\u7136\u66f4\u5e0c\u671b\u627e\u5bb6\u6b63\u89c4\u7684\u516c\u53f8\u5de5\u4f5c\uff0c\u4ed6\u4e0e\u4ed6\u5cb3\u7236\u6bcd\u5173\u7cfb\u4e00\u76f4\u5f88\u50f5\uff0c\u5c31\u662f\u4ed6\u6ca1\u6709\u4e00\u4efd\u597d\u597d\u7684\u5de5\u4f5c\uff0c\u800c\u4ed6\u4eec\u7684\u5973\u513f\u53c8\u4e3a\u4ed6\u727a\u7272\u4e86\u8fd9\u4e48\u591a\u3002</p><p>\u3000\u3000\u7528\u5085\u4fca\uff0c\u5f20\u606a\u8003\u8651\u4ed6\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u503c\u5f97\u4fe1\u4efb\uff1b\u5f20\u606a\u76f8\u4fe1\u56fd\u5185\u7684\u6cbb\u5b89\u73af\u5883\u4f1a\u8d8a\u6765\u8d8a\u597d\uff0c\u4f46\u662f\u4e5f\u65e0\u6cd5\u5426\u8ba4\u5c11\u6570\u5730\u65b9\u7684\u7ade\u4e89\u73af\u5883\u5341\u5206\u6076\u52a3\uff0c\u9526\u57ce\u96c6\u56e2\u5c31\u662f\u4e00\u5bb6\u559c\u6b22\u8d70\u504f\u95e8\u7684\u516c\u53f8\u3002\u4e07\u4e00\u4eca\u5929\u65e9\u6668\u7684\u4e8b\u60c5\u518d\u6b21\u53d1\u751f\uff0c\u8c22\u5251\u5357\u53c8\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u96be\u9053\u81ea\u5df1\u771f\u80fd\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6076\u6c14\uff1f\u5f20\u606a\u53ef\u4e0d\u5e0c\u671b\u81ea\u5df1\u8eab\u8fb9\u6ca1\u6709\u503c\u5f97\u4fe1\u4efb\u7684\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u91d1\u56fd\u6d77\u8bf4\u5728\u5510\u5b66\u8c26\u7ed9\u5e02\u5c40\u76f4\u63a5\u65bd\u52a0\u538b\u529b\u7684\u60c5\u51b5\uff0c\u5085\u4fca\u8fd8\u6ca1\u6709\u5c06\u81ea\u5df1\u4f9b\u51fa\u6765\uff0c\u5c31\u89c9\u5f97\u4ed6\u503c\u5f97\u4e00\u7528\u3002\u5728\u5ea7\u7684\u4eba\u5f53\u4e2d\uff0c\u9664\u4e86\u5c0f\u53d4\u3001\u90b5\u81f3\u521a\u4e4b\u5916\uff0c\u76db\u9752\u3001\u5434\u5929\u5b9d\uff0c\u5bf9\u5f20\u606a\u771f\u6b63\u7684\u80cc\u666f\u53ea\u6709\u6a21\u7cca\u7684\u6982\u5ff5\uff0c\u5085\u4fca\u66f4\u4e0d\u53ef\u80fd\u8ba4\u8bc6\u5230\u5f20\u606a\u80fd\u8fd9\u4e48\u987a\u5229\u7684\u66ff\u4ed6\u4eec\u5f00\u8131\u3002</p><p>\u3000\u3000\u5f20\u606a\u540c\u65f6\u4e5f\u5e0c\u671b\u901a\u8fc7\u5085\u4fca\uff0c\u4e0e\u76db\u9752\u7684\u5173\u7cfb\u66f4\u5bc6\u5207\u4e00\u4e9b\uff0c\u5341\u51e0\u5e74\u540e\u90a3\u4e9b\u88ab\u8fb9\u7f18\u5316\u7684\u52bf\u529b\uff0c\u4f46\u5728\u56fd\u5185\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\u5374\u5145\u5f53\u7740\u76f8\u5f53\u91cd\u8981\u7684\u89d2\u8272\uff0c\u5f20\u606a\u4e0d\u4f1a\u81ea\u8be9\u6e05\u9ad8\uff0c\u4ed6\u53ea\u662f\u4e0d\u60f3\u5f15\u706b\u70e7\u8eab\u800c\u5df2\uff0c\u5982\u4f55\u5229\u7528\u5e76\u63a7\u5236\u8fd9\u4e9b\u8fb9\u7f18\u52bf\u529b\uff0c\u4e0d\u5bb9\u5f20\u606a\u4e0d\u91cd\u89c6\u3002</p><p>\u3000\u3000\u9152\u5e2d\u4e0a\uff0c\u5f20\u606a\u95ee\u5085\u4fca\uff1a\u201c\u4f60\u4f1a\u4e0d\u4f1a\u5f00\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u90e8\u961f\u91cc\u5b66\u8fc7\uff0c\u5728\u82e6\u7a91\u91cc\u5173\u4e86\u516d\u5e74\uff0c\u4e4b\u540e\u8fd8\u6ca1\u6709\u65f6\u95f4\u91cd\u65b0\u5b66\uff0c\u6ca1\u6709\u9a7e\u7167\uff0c\u9752\u54e5\u6709\u4e8b\u5e38\u8ba9\u6211\u5f00\u8f66\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\uff0c\u60f3\u8981\u62ff\u9a7e\u7167\u5f88\u65b9\u4fbf\uff0c\u4e0d\u9760\u5173\u7cfb\u5b66\u8f66\uff0c\u5374\u8981\u6bd4\u5341\u51e0\u5e74\u590d\u6742\u8bb8\u591a\uff0c\u8fde\u6c7d\u8f66\u4fee\u7406\u90fd\u8981\u5b66\uff0c\u5f53\u7136\u90e8\u961f\u91cc\u66f4\u52a0\u7e41\u7410\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\u8bf4\uff1a\u201c\u90a3\u5148\u628a\u4f60\u7684\u5de5\u4f5c\u5173\u7cfb\u5728\u9526\u6e56\u5b9a\u4e0b\u6765\uff0c\u7136\u540e\u76f4\u63a5\u53bb\u8003\u4e2a\u8bc1\u3002\u542c\u8bf4\u4f60\u5bb6\u91cc\u7684\u5c45\u4f4f\u6761\u4ef6\u4e0d\u662f\u5f88\u597d\uff0c\u8fd9\u4f1a\u5f71\u54cd\u4f60\u5bf9\u5de5\u4f5c\u7684\u4e13\u6ce8\uff0c\u4f60\u660e\u5929\u5230\u9526\u6e56\u628a\u4eca\u5e74\u7684\u5de5\u8d44\u5148\u9886\u51fa\u6765\uff0c\u628a\u5bb6\u91cc\u4e8b\u60c5\u5148\u5b89\u987f\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u5434\u5929\u5b9d\u7b11\u7740\u95ee\uff1a\u201c\u606a\u5c11\u7ed9\u50bb\u4fca\u5f00\u591a\u9ad8\u7684\u5de5\u8d44\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7b11\u7740\u8bf4\uff1a\u201c\u50bb\u4fca\u4ee5\u540e\u5230\u516c\u53f8\u5de5\u4f5c\uff0c\u5927\u5bb6\u8981\u4e60\u60ef\u5524\u4ed6\u7684\u5927\u540d\u3002\u201d</p><p>\u3000\u3000\u201c\u90b5\u54e5\u8fd9\u8bdd\u6211\u7231\u542c\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u5458\u5de5\u7684\u85aa\u8d44\u4e00\u5411\u662f\u9526\u6e56\u7684\u673a\u5bc6\uff0c\u8fd9\u91cc\u4e0d\u8ddf\u5927\u5bb6\u70ab\u8000\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5927\u5bb6\u90fd\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5434\u5929\u5b9d\u8981\u514d\u5355\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u8981\u514d\u5355\uff0c\u4e0d\u5c31\u663e\u5f97\u6211\u7684\u8bda\u610f\u6de1\u4e86\u5417\uff1f\u201d\u4f4e\u5934\u5728\u8d26\u5355\u4e0a\u7b7e\u5b57\uff0c\u8ddf\u5434\u5929\u5b9d\u8bf4\uff0c\u201c\u5434\u7ecf\u7406\uff0c\u5085\u4fca\u4ee5\u540e\u5728\u8fd9\u91cc\u4e5f\u53ef\u4ee5\u7b7e\u5355\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5fae\u5fae\u4e00\u7b11\uff0c\u5f20\u606a\u8981\u662f\u770b\u4e2d\u4e00\u4eba\uff0c\u62e2\u7edc\u4eba\u7684\u624b\u6bb5\u53ef\u8c13\u7528\u4e4b\u4ee5\u6781\u554a\uff0c\u4fa7\u5934\u77a5\u4e86\u5085\u4fca\u4e00\u773c\uff0c\u8fd8\u80fd\u4fdd\u6301\u6de1\u5b9a\uff0c\u786e\u5b9a\u6709\u4e0d\u9519\u7684\u5b9a\u529b\uff0c\u53ea\u53ef\u60dc\u76db\u9752\u4ed6\u635f\u5931\u4e86\u5f97\u529b\u7684\u52a9\u624b\uff0c\u5fc3\u60f3\u5085\u4fca\u7684\u6027\u5b50\u6709\u4e9b\u76f4\uff0c\u5176\u5b9e\u8ddf\u7740\u76db\u9752\u4e5f\u4e0d\u5408\u9002\u3002</p><p>\u3000\u3000\u4eba\u5fc3\u611f\u53d7\u7684\u6e29\u5bd2\uff0c\u5f80\u5f80\u4f53\u73b0\u5728\u7ec6\u8282\u4e0a\uff0c\u65e2\u7136\u51b3\u5b9a\u7528\u5085\u4fca\uff0c\u8981\u5c06\u4ed6\u89c6\u4e4b\u4e3a\u5fc3\u8179\uff0c\u5c31\u8981\u628a\u4ed6\u7684\u5fc3\u601d\u4ece\u76db\u9752\u90a3\u91cc\u62c9\u8fc7\u6765\u3002\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u606a\u8c22\u7edd\u76db\u9752\u5230\u4ed6\u573a\u5b50\u91cc\u6d88\u8c34\u7684\u9080\u8bf7\uff0c\u8ba9\u5c0f\u53d4\u5f20\u77e5\u975e\u5148\u9001\u5085\u4fca\u56de\u5bb6\u3002</p><p>\u3000\u3000\u5085\u4fca\u5bb6\u91cc\u4f4f\u6c99\u7530\u897f\u7247\u7684\u949f\u697c\u89d2\uff0c\u8fd9\u4fbf\u662f\u5728\u6e05\u672b\u4e5f\u5c5e\u4e8e\u6d77\u5dde\u57ce\u7684\u68da\u6237\u533a\uff0c\u5df7\u5b50\u7a84\u5f97\u8fde\u8f7f\u8f66\u90fd\u5f00\u4e0d\u8fdb\u53bb\u3002\u5085\u4fca\u5f88\u5766\u7136\u8bf7\u5f20\u606a\u3001\u5f20\u77e5\u975e\u8fdb\u53bb\u5750\u5750\u3002</p><p>\u3000\u3000\u5085\u4fca\u4e00\u5bb6\u5144\u59b9\u56db\u4eba\u90fd\u7ed3\u4e86\u5a5a\uff0c\u59b9\u59b9\u5acc\u5bb6\u91cc\u7a77\uff0c\u4ece\u6d77\u5dde\u5927\u5b66\u6bd5\u4e1a\u4e4b\u540e\u5374\u53bb\u4e86\u5357\u65b9\u6253\u5de5\uff0c\u4e24\u4e09\u5e74\u90fd\u6ca1\u6709\u56de\u8fc7\u5bb6\u3002\u9662\u5b50\u672c\u6765\u5c31\u5c0f\uff0c\u5085\u4fca\u4e0e\u4ed6\u5973\u4eba\u7ed3\u5a5a\u65f6\uff0c\u5c31\u5728\u9662\u5b50\u642d\u4e86\u95f4\u623f\u3002</p><p>\u3000\u3000\u9662\u5b50\u91cc\u9ed1\u706f\u778e\u706b\uff0c\u5085\u4fca\u558a\u4ed6\u5973\u4eba\u51fa\u6765\u5f00\u95e8\uff0c\u5f20\u606a\u4e5f\u6ca1\u770b\u5979\u5230\u8138\u4e0a\u7ed9\u5212\u82b1\u7684\u6837\u5b50\uff0c\u5012\u662f\u4f53\u6001\u7ef0\u7ea6\uff0c\u8bf4\u8bdd\u58f0\u6e29\u60cb\u60a6\u8033\uff0c\u8fdb\u5c4b\u5934\u5dee\u70b9\u649e\u95e8\u6963\u4e0a\uff0c\u770b\u89c1\u9876\u591a\u6709\u516d\u5e73\u65b9\u7684\u5c0f\u5c4b\u91cc\uff0c\u9760\u91cc\u5899\u67b6\u7740\u4e00\u5f20\u6728\u677f\u5e8a\uff0c\u4e24\u4e2a\u74f7\u5a03\u5a03\u4e00\u6837\u5c0f\u5973\u5b69\u5b50\u6b63\u5750\u5728\u5e8a\u4e0a\uff0c\u7741\u5f20\u6df1\u9083\u7684\u9ed1\u8272\u773c\u7738\u597d\u5947\u7684\u6ce8\u89c6\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b2c\u4e00\u5370\u8c61\u5c31\u662f\u82b7\u5f64\u4ee5\u540e\u518d\u6765\u6d77\u5dde\u5c31\u6709\u73a9\u4f34\u4e86\uff0c\u96be\u602a\u90b5\u81f3\u521a\u90fd\u8981\u5938\u5085\u4fca\u5bb6\u4e24\u4e2b\u5934\u79cd\u597d\u3002</p><p>\u3000\u3000\u5c4b\u5b50\u7ad9\u7740\u4e09\u4e2a\u6210\u5e74\u4eba\u5c31\u8f6c\u4e0d\u5f00\u8eab\uff0c\u5085\u4fca\u8bf7\u5f20\u606a\u3001\u5f20\u77e5\u975e\u5750\u5e8a\u677f\u4e0a\uff0c\u4ed6\u5973\u4eba\u53bb\u5012\u6c34\u8fd8\u6ca1\u6709\u8fdb\u6765\uff0c\u4ed6\u62c9\u4e86\u4e00\u51f3\u5b50\u5750\u4e0b\u6765\uff1a\u201c\u6211\u7238\u5988\u7761\u5802\u5c4b\u2026\u2026\u201d\u6709\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u7537\u5b69\u5b50\u63a2\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u89c1\u6709\u751f\u4eba\u5728\uff0c\u4eba\u5c31\u95ea\u5f00\u4e86\uff0c\u5085\u4fca\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u5927\u54e5\u7684\u5b69\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5085\u4fca\u5bb6\u7684\u60c5\u51b5\uff0c\u8fd9\u4e48\u5927\u7684\u5b69\u5b50\u4e5f\u53ea\u80fd\u8ddf\u7236\u6bcd\u7761\u4e00\u4e2a\u623f\u95f4\uff0c\u751f\u6d3b\u81ea\u7136\u5341\u5206\u7a98\u8feb\uff0c\u62ac\u5934\u770b\u4e86\u770b\u5c4b\u9876\uff0c\u8fd9\u95f4\u623f\u53ea\u6015\u4e5f\u662f\u4e34\u65f6\u8bf7\u4e9b\u670b\u53cb\u642d\u5efa\u7684\u3002</p><p>\u3000\u3000\u5085\u4fca\u7b11\u7740\u8bf4\uff1a\u201c\u9752\u54e5\u4e5f\u4e0d\u4e8f\u6211\uff0c\u6211\u7238\u90a3\u6162\u6027\u75c5\u5403\u94b1\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5ac2\u5b50\u771f\u4e0d\u5bb9\u6613\u2026\u2026\u201d</p><p>\u3000\u3000\u5085\u4fca\u5973\u4eba\u4e00\u624b\u62ff\u7740\u4e00\u645e\u7897\uff0c\u4e00\u624b\u63d0\u7740\u6c34\u58f6\u8fdb\u6765\uff0c\u5de6\u8138\u988a\u6709\u4e24\u9053\u957f\u53ca\u9f3b\u7ffc\u7684\u75a4\u75d5\uff0c\u4e0d\u662f\u90a3\u4e48\u523a\u773c\uff0c\u6c14\u8d28\u6de1\u96c5\uff0c\u8fd8\u5c5e\u4e8e\u90a3\u79cd\u8010\u770b\u7684\u5973\u4eba\uff0c\u53ef\u89c1\u8138\u6ca1\u6709\u7834\u76f8\u4e5f\u662f\u4e3d\u4eba\u4e00\u4e2a\u3002</p><p>\u3000\u3000\u201c\u6211\u7231\u4eba\u674e\u660e\u745c\uff0c\u201d\u5085\u4fca\u8fd9\u624d\u6709\u7a7a\u4ecb\u7ecd\uff0c\u5f20\u606a\u8bb0\u5f97\u76db\u9752\u8bf4\u5085\u4fca\u4ed6\u7231\u4eba\u5a18\u5bb6\u662f\u4e66\u9999\u95e8\u7b2c\uff0c\u8fd9\u624d\u89c9\u5f97\u5085\u4fca\u4ed6\u7231\u4eba\u8138\u719f\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u5927\u54e5\u662f\u674e\u660e\u5b66\uff1f\u201d</p></article>"}}}]);
