(window.webpackJsonp=window.webpackJsonp||[]).push([[160],{182:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u5deb\u5996\u7684\u793c\u7269",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u5deb\u5996\u7684\u793c\u7269</h2><p>\u7b4b\u75b2\u529b\u5c3d\u4e4b\u540e\uff0c\u743c\u6069\u62b1\u7740\u8389\u6cd5\u513f\u6c89\u6c89\u7761\u53bb\u3002</p><p>\u604d\u604d\u60da\u60da\u4e4b\u95f4\uff0c\u4ed6\u53d1\u89c9\u81ea\u5df1\u4f3c\u4e4e\u53c8\u8d70\u5230\u90a3\u4ef6\u56fe\u4e66\u5ba4\u5185\uff0c\u7a7a\u6c14\u4e2d\u4f9d\u65e7\u662f\u51e0\u5341\u6570\u767e\u5f20\u7f8a\u76ae\u7eb8\u5728\u6f02\u6d6e\u65cb\u8f6c\uff0c\u4e00\u5177\u7a7f\u7740\u8934\u891b\u957f\u888d\u7684\u9ab7\u9ac5\u9aa8\u67b6\u6b63\u5728\u4e66\u684c\u540e\u9762\u5237\u5237\u4e66\u5199\u7740\uff0c\u5934\u4e5f\u4e0d\u62ac\u3002</p><p>\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\uff1f</p><p>\u4ece\u8389\u6cd5\u513f\u90a3\u91cc\uff0c\u743c\u6069\u5df2\u7ecf\u5f97\u77e5\u8fd9\u4f4d\u9ad8\u7b49\u5deb\u5996\u7684\u540d\u5b57\uff0c\u4e5f\u77e5\u9053\u4ed6\u66fe\u7ecf\u662f\u6b64\u5730\u7684\u4e3b\u4eba\uff0c\u5982\u4eca\u5219\u662f\u62c9\u6c83\u514b\u7684\u6284\u5199\u5458\u3002\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u5174\u8da3\u53bb\u548c\u4e00\u4e2a\u5deb\u5996\u6253\u4ea4\u9053\uff0c\u4f46\u60f3\u8d77\u8389\u6cd5\u513f\u8bf4\uff0c\u8fd9\u4f4d\u8001\u5deb\u5996\u559c\u6b22\u9001\u4eba\u793c\u7269\uff0c\u65e2\u7136\u8fd9\u6837\u2026\u2026\u7b49\u7b49\uff0c\u8389\u6cd5\u513f\u597d\u8c61\u8bf4\u7684\u662f\uff0c\u4ed6\u7684\u793c\u7269\u53ea\u6709\u5728\u68a6\u91cc\u624d\u4f1a\u7ed9\u3002</p><p>\u96be\u9053\u81ea\u5df1\u73b0\u5728\u662f\u5728\u7761\u68a6\u4e2d\u4e48\uff1f</p><p>\u4e00\u5ff5\u65b9\u8d77\uff0c\u5bf9\u9762\u7684\u5deb\u5996\u5df2\u7ecf\u653e\u4e0b\u7b14\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u4ed6\u7684\u9ab7\u9ac5\u773c\u7736\u4e2d\u94f6\u767d\u8272\u5fae\u5149\u95ea\u70c1\uff0c\u67d4\u548c\u800c\u4e0d\u523a\u773c\uff0c\u76ef\u7740\u743c\u6069\uff0c\u201c\u8bf7\u5750\u3002\u201d\u4ed6\u8bf4\uff0c\u4f38\u624b\u968f\u610f\u6307\u4e86\u6307\uff0c\u743c\u6069\u7684\u8eab\u65c1\u4fbf\u51fa\u73b0\u4e86\u4e00\u628a\u6905\u5b50\u3002</p><p>\u743c\u6069\u770b\u7740\u5deb\u5996\uff0c\u4ed6\u5df2\u7ecf\u660e\u767d\u4e86\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u4e5f\u660e\u767d\u8389\u6cd5\u513f\u6240\u8bf4\u7684\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\u3002\u8fd9\u662f\u5728\u81ea\u5df1\u7684\u68a6\u5883\u4e2d\uff0c\u4f46\u53c8\u4e0d\u5355\u7eaf\u662f\u201c\u81ea\u5df1\u201d\u7684\u68a6\u5883\uff0c\u800c\u662f\u9762\u524d\u7684\u8fd9\u4e2a\u5deb\u5996\u4f7f\u7528\u4e86\u6258\u68a6\u6216\u8005\u6697\u793a\u4e00\u7c7b\u7684\u9b54\u6cd5\uff0c\u4e3a\u743c\u6069\u521b\u9020\u51fa\u4e86\u8fd9\u4e2a\u68a6\u5883\u3002</p><p>\u6084\u65e0\u58f0\u606f\u5730\u4fb5\u5165\u4ed6\u4eba\u610f\u8bc6\uff0c\u51ed\u7a7a\u521b\u9020\u51fa\u4e00\u4e2a\u68a6\u5883\u6765\uff0c\u8fd9\u662f\u6781\u9ad8\u660e\u7684\u9b54\u6cd5\u6280\u5de7\uff0c\u5bf9\u4e8e\u8fd9\u4e00\u70b9\u743c\u6069\u975e\u5e38\u6e05\u695a\u3002\u5982\u679c\u8bf4\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6\u5bf9\u8fd9\u4f4d\u8001\u5deb\u5996\u7684\u8eab\u4efd\u8fd8\u6709\u4e9b\u8bb8\u7591\u8651\u7684\u8bdd\uff0c\u73b0\u5728\u4fbf\u5b8c\u5168\u786e\u5b9a\u4e86\uff0c\u552f\u6709\u6614\u65e5\u8010\u745f\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\uff0c\u624d\u6709\u8fd9\u6837\u7684\u80fd\u529b\u5427\u3002</p><p>\u90a3\u4e48\uff0c\u4ed6\u5230\u5e95\u8981\u505a\u4ec0\u4e48\u5462\uff1f\u8981\u9001\u81ea\u5df1\u793c\u7269\u4e48\uff1f\u7136\u800c\u68a6\u5883\u7eb5\u7136\u518d\u903c\u771f\uff0c\u7ec8\u7a76\u4e5f\u662f\u865a\u5e7b\u800c\u975e\u771f\u5b9e\uff0c\u5c31\u7b97\u6709\u4eba\u5728\u68a6\u91cc\u9001\u4f60\u4e00\u5ea7\u91d1\u5c71\uff0c\u9192\u6765\u4f9d\u65e7\u662f\u4e00\u573a\u865a\u7a7a\uff0c\u8fd9\u662f\u663e\u800c\u6613\u89c1\u7684\u9053\u7406\u2026\u2026\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u5deb\u5996\u8981\u9001\u4ec0\u4e48\u793c\u7269\uff0c\u743c\u6069\u5927\u7ea6\u4e5f\u5c31\u80fd\u9690\u9690\u731c\u5230\u51e0\u5206\u4e86\u3002</p><p>\u4ed6\u52aa\u529b\u538b\u6291\u5fc3\u5934\u7684\u72c2\u559c\uff0c\u5c3d\u91cf\u8ba9\u81ea\u5df1\u9547\u5b9a\u4e0b\u6765\u3002\u201c\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\uff0c\u201d\u4ed6\u5411\u5deb\u5996\u6df1\u6df1\u884c\u793c\uff0c\u7528\u7684\u662f\u53e4\u8010\u745f\u5e1d\u56fd\u7684\u6807\u51c6\u793c\u8282\uff0c\u201c\u6211\u662f\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff0c\u5f88\u8363\u5e78\u80fd\u89c1\u5230\u60a8\u3002\u201d</p><p>\u201c\u8bf7\u5750\u3002\u201d\u5deb\u5996\u91cd\u590d\u3002</p><p>\u743c\u6069\u5750\u4e86\u4e0b\u6765\uff0c\u9759\u9759\u7b49\u5f85\u7740\u3002\u5deb\u5996\u9ed8\u4e0d\u4f5c\u58f0\u5730\u7aef\u8be6\u7740\u4ed6\uff0c\u201c\u4f60\u4ece\u9634\u9b42\u57ce\u6765\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d\u743c\u6069\u95ee\u3002</p><p>\u63a5\u4e0b\u6765\u5deb\u5996\u4eff\u4f5b\u5f88\u968f\u610f\u5730\u8be2\u95ee\u4e86\u51e0\u4e2a\u5173\u4e8e\u9634\u9b42\u57ce\u7684\u95ee\u9898\uff0c\u743c\u6069\u4e00\u4e00\u4f5c\u7b54\uff0c\u4ed6\u53d1\u73b0\u5deb\u5996\u7684\u8bdd\u8bed\u91cc\u5f80\u5f80\u5939\u6742\u7740\u4e00\u4e9b\u964c\u751f\u7684\u8bcd\u6c47\uff0c\u9700\u8981\u52aa\u529b\u5730\u6839\u636e\u4e0a\u4e0b\u6587\u624d\u80fd\u52c9\u5f3a\u731c\u6d4b\u51fa\u610f\u601d\uff0c\u8fd9\u4ee4\u4ed6\u9887\u6709\u4e9b\u5947\u602a\u3002\u540c\u4e3a\u8010\u745f\u4e00\u8109\uff0c\u4ed6\u4eec\u4ea4\u8c08\u7528\u7684\u81ea\u7136\u90fd\u662f\u6807\u51c6\u7684\u8010\u745f\u8bed\uff0c\u4ea4\u6d41\u5e94\u8be5\u4e0d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u969c\u788d\u624d\u5bf9\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u8c08\u8bdd\u4f9d\u7136\u8fd8\u7b97\u662f\u5f88\u987a\u5229\uff0c\u5deb\u5996\u7684\u95ee\u9898\uff0c\u743c\u6069\u5c3d\u91cf\u4f5c\u7b54\u3002\u6709\u4e9b\u4ed6\u5b9e\u5728\u4e0d\u6e05\u695a\u7684\uff0c\u5deb\u5996\u4e5f\u5e76\u4e0d\u8ffd\u95ee\uff0c\u4e3b\u52a8\u8f6c\u6362\u8bdd\u9898\uff1b\u743c\u6069\u5076\u5c14\u8bd5\u63a2\u7740\u63d0\u51fa\u4e00\u4e9b\u95ee\u9898\uff0c\u5deb\u5996\u4e5f\u5e76\u4e0d\u56de\u907f\uff0c\u5f84\u884c\u4f5c\u7b54\u3002\u5982\u679c\u5ffd\u7565\u6389\u53cc\u65b9\u7684\u8eab\u4efd\uff0c\u8fd9\u573a\u8c08\u8bdd\u542c\u8d77\u6765\u5012\u9887\u6709\u4e9b\u50cf\u662f\u6545\u53cb\u95f2\u804a\u3002\u5728\u8c08\u8bdd\u7684\u6700\u540e\uff0c\u5deb\u5996\u7a81\u7136\u8bf4\u4e86\u51e0\u4e2a\u4eba\u540d\uff0c\u95ee\u743c\u6069\u662f\u5426\u77e5\u9053\u4ed6\u4eec\u7684\u6d88\u606f\u3002</p><p>\u8fd9\u51e0\u4e2a\u4eba\u540d\u743c\u6069\u8fde\u542c\u90fd\u6ca1\u542c\u8fc7\uff0c\u81ea\u7136\u66f4\u8c08\u4e0d\u4e0a\u5176\u4ed6\u6d88\u606f\uff0c\u4ed6\u636e\u5b9e\u56de\u7b54\uff0c\u5deb\u5996\u773c\u7736\u4e2d\u5149\u8292\u5fae\u5fae\u9eef\u6de1\uff0c\u4f3c\u4e4e\u7565\u6709\u4e9b\u5931\u671b\uff0c\u4f46\u4e5f\u5e76\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002\u201c\u5f88\u9ad8\u5174\u4f60\u80fd\u966a\u6211\u804a\u5929\uff0c\u201d\u5deb\u5996\u5f88\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u6211\u5f88\u4e45\u90fd\u6ca1\u548c\u4eba\u8bf4\u8bdd\u4e86\u3002\u201d</p><p>\u8fd9\u5f88\u6b63\u5e38\uff0c\u6839\u636e\u8389\u6cd5\u513f\u6240\u8bf4\uff0c\u8fd9\u4f4d\u4e0d\u5e78\u7684\u5deb\u5996\u662f\u5728\u8010\u745f\u5e1d\u56fd\u9668\u706d\u540e\u88ab\u62c9\u6c83\u514b\u51fb\u8d25\u56da\u7981\u7684\uff0c\u7b97\u8d77\u6765\u5728\u8fd9\u91cc\u5df2\u7ecf\u5b85\u5c45\u4e00\u5343\u591a\u5e74\u4e86\u3002\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\u91cc\uff0c\u60f3\u5fc5\u4e5f\u5f88\u5c11\u6709\u5ba2\u4eba\u6765\uff0c\u62c9\u6c83\u514b\u81ea\u5df1\u5fd9\u7740\u7814\u7a76\u201c\u751f\u6b7b\u8f6c\u5316\u201d\uff0c\u8389\u6cd5\u513f\u662f\u6700\u8fd1\u4e00\u5e74\u591a\u624d\u6765\u7684\uff0c\u745e\u54c8\u683c\u62c9\u65af\u81ea\u7136\u627e\u4e0d\u5230\u8bf4\u8bdd\u7684\u5bf9\u8c61\u3002</p><p>\u201c\u8fd9\u662f\u6211\u7684\u8363\u5e78\u3002\u201d\u743c\u6069\u8d76\u7d27\u56de\u7b54\u3002</p><p>\u5deb\u5996\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u4f60\u662f\u4e2a\u5deb\u5e08\u5bf9\u5427\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u6ca1\u770b\u9519\uff0c\u201d\u5deb\u5996\u7ee7\u7eed\u8bf4\uff0c\u201c\u4f60\u5e94\u8be5\u5df2\u7ecf\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u56db\u5c42\u4e86\uff1f\u201d</p><p>\u743c\u6069\u6115\u7136\u4e00\u60ca\uff0c\u672c\u80fd\u5730\u611f\u89c9\u80cc\u4e0a\u5bd2\u6c14\u4e00\u51db\u3002\u89e6\u6478\u9b54\u7f51\u7684\u6df1\u6d45\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c31\u610f\u5473\u7740\u5deb\u5e08\u80fd\u529b\u7684\u9ad8\u4e0b\uff0c\u867d\u7136\u8fd9\u5e76\u975e\u7edd\u5bf9\uff0c\u4f46\u81f3\u5c11\u662f\u5f88\u91cd\u8981\u7684\u53c2\u8003\u6807\u51c6\u3002\u6240\u4ee5\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u80fd\u591f\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u51e0\u5c42\uff0c\u8fd9\u5c5e\u4e8e\u7edd\u5bf9\u7684\u673a\u5bc6\uff0c\u4e0d\u80fd\u8f7b\u6613\u4e3a\u5916\u4eba\u6240\u77e5\u6653\uff0c\u5426\u5219\u6781\u5176\u5bb9\u6613\u88ab\u5224\u65ad\u51fa\u80fd\u529b\u6df1\u6d45\uff0c\u81f4\u4f7f\u88ab\u654c\u4eba\u6240\u4e58\u3002\u743c\u6069\u5728\u524d\u6bb5\u65f6\u95f4\u521a\u521a\u6709\u6240\u7a81\u7834\uff0c\u7cbe\u795e\u80fd\u591f\u8054\u63a5\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff0c\u8fd9\u4ef6\u4e8b\u8fde\u671d\u5915\u76f8\u5904\u7684\u6885\u83f2\u65af\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd9\u8001\u5deb\u5996\u5355\u51ed\u770b\u51e0\u773c\uff0c\u5c45\u7136\u5c31\u80fd\u4f5c\u51fa\u8fd9\u4e48\u51c6\u786e\u7684\u5224\u65ad\uff1f</p><p>\u4ed6\u5fc3\u4e2d\u60ca\u9a87\u7740\uff0c\u52aa\u529b\u5e73\u9759\u4e0b\u6765\uff0c\u201c\u662f\uff0c\u201d\u4ed6\u7b80\u77ed\u5730\u56de\u7b54\uff0c\u201c\u6211\u521a\u521a\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\u3002\u201d</p><p>\u201c\u5f88\u4e0d\u9519\u4e86\uff0c\u201d\u5deb\u5996\u5938\u5956\u7740\uff0c\u201c\u6bd4\u6211\u5f53\u5e74\u5f3a\u5f97\u591a\u4e86\u3002\u5982\u679c\u5e1d\u56fd\u8fd8\u5728\uff0c\u4ee5\u4f60\u8fd9\u6837\u7684\u8fdb\u5883\uff0c\u4e94\u5341\u5c81\u4e4b\u524d\uff0c\u5c31\u6709\u5e0c\u671b\u80fd\u6210\u4e3a\u5927\u5965\u672f\u5e08\u4e86\u3002\u201d</p><p>\u8fd9\u662f\u975e\u5e38\u9ad8\u7684\u8bc4\u4ef7\u4e86\uff0c\u5c31\u7b97\u743c\u6069\u81ea\u5df1\u90fd\u4e0d\u6562\u62b1\u8fd9\u4e2a\u5962\u671b\u3002\u53e4\u8010\u745f\u5e1d\u56fd\u4ee5\u9b54\u6cd5\u8457\u79f0\uff0c\u5deb\u5e08\u4f55\u6b62\u5343\u4e07\uff0c\u5927\u5965\u672f\u5e08\u662f\u5176\u4e2d\u7684\u9876\u7ea7\u4f7c\u4f7c\u8005\uff0c\u4e2a\u4e2a\u90fd\u662f\u4e07\u4e2d\u65e0\u4e00\u7684\u7edd\u9876\u5929\u624d\uff0c\u5c82\u662f\u5bfb\u5e38\u4eba\u80fd\u8f7b\u6613\u68a6\u60f3\u7684\u3002</p><p>\u597d\u5427\uff0c\u522b\u8bf4\u8fd9\u4e9b\u5e9f\u8bdd\uff0c\u4e0d\u662f\u8bf4\u8981\u9001\u793c\u7269\u4e48\uff0c\u4e1c\u897f\u600e\u4e48\u8fd8\u4e0d\u62ff\u51fa\u6765\u5462\u3002</p><p>\u201c\u6211\u8fd9\u91cc\u6709\u4e2a\u5c0f\u6cd5\u672f\uff0c\u201d\u4eff\u4f5b\u542c\u89c1\u4e86\u743c\u6069\u5fc3\u91cc\u7684\u50ac\u4fc3\uff0c\u5deb\u5996\u4e5f\u4fbf\u4e0d\u518d\u5e9f\u8bdd\uff0c\u201c\u5012\u4e5f\u8bf4\u4e0d\u4e0a\u591a\u4e48\u5389\u5bb3\uff0c\u4e0d\u8fc7\u7b97\u662f\u6bd4\u8f83\u7f55\u89c1\u3002\u5982\u679c\u4f60\u6709\u5174\u8da3\uff0c\u6211\u5f88\u4e50\u610f\u4f20\u6388\u7ed9\u4f60\uff0c\u5982\u4f55\u3002\u201d</p><p>\u8fd9\u8fd8\u9700\u8981\u5f81\u6c42\u610f\u89c1\u4e48\uff0c\u7b54\u6848\u662f\u663e\u800c\u6613\u89c1\u7684\u3002\u8001\u5deb\u5996\u867d\u7136\u8bf4\u662f\u201c\u5c0f\u6cd5\u672f\u201d\uff0c\u4f46\u5bf9\u4ed6\u800c\u8a00\u53ef\u80fd\u4e0d\u503c\u4e00\u63d0\uff0c\u5bf9\u743c\u6069\u6765\u8bf4\u5c31\u975e\u540c\u4e00\u822c\u4e86\uff0c\u53cd\u6b63\u5bf9\u5deb\u5e08\u6765\u8bf4\uff0c\u6cd5\u672f\u5c31\u50cf\u94b1\uff0c\u6ca1\u6709\u5acc\u591a\u7684\u9053\u7406\u3002\u82e5\u662f\u6362\u4e86\u70db\u5821\u90a3\u79cd\u5730\u65b9\uff0c\u6284\u5f55\u4e2a\u6cd5\u672f\u90fd\u8981\u82b1\u4e0a\u4e0a\u767e\u91d1\u5e01\u5462\uff0c\u5982\u4eca\u6709\u4eba\u514d\u8d39\u4f20\u6388\uff0c\u5c82\u6709\u4e0d\u5b66\u7684\u9053\u7406\u3002</p><p>\u5deb\u5996\u70b9\u70b9\u5934\uff0c\u5374\u6ca1\u6709\u53d6\u51fa\u4ec0\u4e48\u5377\u8f74\u6765\uff0c\u4e5f\u6ca1\u7ffb\u9b54\u6cd5\u4e66\uff0c\u800c\u662f\u76f4\u63a5\u4f38\u624b\u671d\u743c\u6069\u9065\u9065\u4e00\u6307\u3002\u743c\u6069\u53ea\u89c9\u5fae\u5fae\u4e00\u9635\u6655\u7729\uff0c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8033\u8fb9\u9661\u7136\u4f20\u6765\u4e00\u58f0\u60a0\u957f\u7684\u53f9\u606f\uff0c\u542c\u58f0\u97f3\u6b63\u662f\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\u53d1\u51fa\u6765\u7684\u3002</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u4e0d\u77e5\u9053\u597d\u7aef\u7aef\u7684\u4ed6\u53f9\u606f\u4ec0\u4e48\uff0c\u4f46\u4ed6\u521a\u624d\u88ab\u5deb\u5996\u4e00\u6307\uff0c\u5934\u8111\u9690\u9690\u6655\u7729\u80c0\u75db\uff0c\u4e00\u65f6\u8fd8\u56de\u4e0d\u8fc7\u795e\u6765\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u8033\u8fb9\u7a81\u7136\u53c8\u54cd\u8d77\u4e00\u9635\u9690\u9690\u7ea6\u7ea6\u7684\u51b7\u7b11\u58f0\u3002</p><p>\u8fd9\u51b7\u7b11\u58f0\u4eff\u4f5b\u81ea\u6781\u9065\u8fdc\u5904\u4f20\u6765\uff0c\u53c8\u4eff\u4f5b\u8fd1\u5728\u54ab\u5c3a\uff0c\u9634\u5bd2\u523a\u8033\uff0c\u4ee4\u4eba\u4e0d\u7531\u81ea\u4e3b\u5730\u6709\u6bdb\u9aa8\u609a\u7136\u4e4b\u611f\u3002\u743c\u6069\u539f\u672c\u8111\u4e2d\u6655\u7729\uff0c\u88ab\u8fd9\u51b7\u7b11\u4e00\u523a\uff0c\u53cd\u5012\u7565\u7565\u6e05\u9192\u4e86\u4e9b\uff0c\u6b63\u8981\u67e5\u770b\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u9661\u7136\u95f4\u773c\u524d\u5929\u65cb\u5730\u8f6c\uff0c\u7d27\u63a5\u7740\u5c31\u662f\u4e00\u7247\u9ed1\u6697\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u518d\u5ea6\u6e05\u9192\u8fc7\u6765\uff0c\u53d1\u89c9\u81ea\u5df1\u6b63\u8eba\u5728\u5e8a\u4e0a\uff0c\u6000\u4e2d\u7684\u5438\u8840\u9b3c\u5c11\u5973\u7761\u5f97\u6b63\u9999\u751c\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u4e0d\u77e5\u505a\u4ec0\u4e48\u7f8e\u68a6\uff0c\u53e3\u4e2d\u53d1\u51fa\u8f7b\u5fae\u7684\u5453\u8bed\uff0c\u4e5f\u542c\u4e0d\u771f\u5207\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u56de\u5fc6\u8d77\u521a\u624d\u6240\u53d1\u751f\u7684\u4e00\u5207\uff0c\u77e5\u9053\u8fd9\u5e76\u4e0d\u662f\u5355\u7eaf\u7684\u68a6\uff0c\u800c\u662f\u4e8b\u5b9e\u3002\u4f46\u5deb\u5996\u6700\u540e\u8bf4\u4f20\u6388\u4ed6\u4e00\u4e2a\u6cd5\u672f\u2026\u2026</p><p>\u4e00\u5ff5\u53ca\u6b64\uff0c\u8111\u4e2d\u968f\u5373\u6e05\u695a\u5730\u6d6e\u73b0\u51fa\u4e00\u7bc7\u5197\u957f\u7e41\u590d\u7684\u6cd5\u672f\u8d44\u6599\u6765\uff0c\u5b8c\u5168\u5305\u542b\u4e86\u4e00\u4e2a\u6cd5\u672f\u7684\u6240\u6709\u7ec6\u8282\uff0c\u5de8\u7ec6\u65e0\u9057\u3002\u743c\u6069\u5fae\u89c9\u60ca\u8bb6\uff0c\u8981\u77e5\u9053\u9b54\u6cd5\u662f\u6781\u5176\u8270\u6da9\u6df1\u5965\u7684\u5b66\u95ee\uff0c\u8fde\u4e00\u4e2a\u97f3\u8282\u58f0\u8c03\u7684\u53d8\u5316\u90fd\u53ef\u80fd\u5bfc\u81f4\u8fe5\u7136\u76f8\u5f02\u7684\u6548\u679c\uff0c\u5355\u51ed\u8bb0\u5fc6\u8981\u60f3\u5c06\u4e00\u4e2a\u6cd5\u672f\u8d44\u6599\u6e05\u695a\u80cc\u4e0b\u6765\uff0c\u90a3\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\uff0c\u5426\u5219\u5f53\u65e5\u5728\u70db\u5821\u91cc\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u7528\u82b1\u5927\u4ef7\u94b1\u6284\u5f55\u4e86\u3002\u5deb\u5996\u4e0d\u8fc7\u4f38\u624b\u6307\u4e86\u4e00\u6307\uff0c\u5c45\u7136\u786c\u751f\u751f\u628a\u4e00\u7bc7\u6cd5\u672f\u8d44\u6599\u5c31\u7ed9\u5370\u5728\u4e86\u4ed6\u7684\u8111\u6d77\u91cc\uff0c\u8fd9\u53ef\u7740\u5b9e\u4ee4\u4eba\u4f69\u670d\u5f97\u7d27\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u4e3a\u9632\u6b62\u5fd8\u8bb0\uff0c\u743c\u6069\u8fd8\u662f\u7acb\u523b\u4e0b\u5e8a\uff0c\u70b9\u71c3\u9b54\u6cd5\u706b\u7130\uff0c\u4ece\u957f\u888d\u91cc\u53d6\u51fa\u9b54\u6cd5\u4e66\uff0c\u5c06\u8fd9\u7bc7\u6cd5\u672f\u8d44\u6599\u539f\u539f\u672c\u672c\u6284\u5f55\u4e0b\u6765\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u5f53\u4ed6\u5199\u5b8c\u6700\u540e\u4e00\u4e2a\u5b57\u7b26\u7684\u65f6\u5019\uff0c\u8111\u4e2d\u539f\u672c\u6e05\u695a\u65e0\u6bd4\u7684\u6cd5\u672f\u8d44\u6599\u987f\u65f6\u6d88\u5931\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u4eff\u4f5b\u88ab\u4e00\u53ea\u65e0\u5f62\u4e4b\u624b\u62b9\u53bb\u4e00\u822c\u3002</p><p>\u6697\u81ea\u611f\u53f9\u5deb\u5996\u6cd5\u672f\u7684\u795e\u5947\uff0c\u743c\u6069\u5f00\u59cb\u4ed4\u7ec6\u9605\u8bfb\u8d77\u8fd9\u4e2a\u521a\u83b7\u5f97\u7684\u6cd5\u672f\u3002\u201c\u6885\u65af\u8fea\u5c14\u7684\u5f3a\u9178\u5410\u606f\uff08Mestil's Acid Breath\uff09\u3002\u201d\u4ed6\u91cd\u590d\u7740\u8fd9\u4e2a\u6cd5\u672f\u540d\u5b57\uff0c\u5927\u7ea6\u731c\u51fa\u4e86\u4e9b\u4e1c\u897f\u3002\u8010\u745f\u5e1d\u56fd\u65f6\u4ee3\uff0c\u9b54\u6cd5\u6587\u660e\u9ad8\u5ea6\u53d1\u8fbe\uff0c\u5deb\u5e08\u4f17\u591a\uff0c\u7ecf\u5e38\u53d1\u660e\u51fa\u5404\u79cd\u65b0\u6cd5\u672f\u6765\uff0c\u6309\u89c4\u77e9\u90fd\u662f\u4ee5\u53d1\u660e\u8005\u7684\u540d\u5b57\u4f5c\u4e3a\u524d\u7f00\u6765\u547d\u540d\u7684\uff1b\u8010\u745f\u9668\u706d\u4e4b\u540e\uff0c\u5927\u9646\u4e0a\u7684\u9b54\u6cd5\u6587\u660e\u4e00\u843d\u5343\u4e08\uff0c\u5143\u6c14\u4e00\u76f4\u672a\u80fd\u6062\u590d\uff0c\u6b64\u540e\u4e5f\u6781\u5c11\u6709\u5deb\u5e08\u80fd\u81ea\u521b\u53d1\u660e\u6cd5\u672f\uff0c\u8fd9\u4e2a\u547d\u540d\u89c4\u77e9\u4e5f\u6e10\u6e10\u4e3a\u4eba\u4eec\u6240\u6de1\u5fd8\u4e86\u3002\u5982\u4eca\u5deb\u5e08\u63d0\u5230\u5404\u79cd\u6cd5\u672f\uff0c\u90fd\u662f\u76f4\u547c\u5176\u540d\uff0c\u4e0d\u7528\u53d1\u660e\u8005\u7684\u540d\u5b57\u4f5c\u4e3a\u524d\u7f00\u4e86\u3002</p><p>\u8fd9\u4e2a\u6cd5\u672f\u65e2\u7136\u53eb\u201c\u6885\u65af\u8fea\u5c14\u7684\u5f3a\u9178\u5410\u606f\u201d\uff0c\u4ece\u5b57\u9762\u4e0a\u6765\u770b\uff0c\u201c\u5f3a\u9178\u5410\u606f\u201d\u662f\u6cd5\u672f\u540d\u79f0\uff0c\u201c\u6885\u65af\u8fea\u5c14\u201d\u5219\u662f\u8fd9\u9053\u6cd5\u672f\u7684\u53d1\u660e\u8005\uff0c\u800c\u4e14\u5f88\u53ef\u80fd\u662f\u8010\u745f\u65f6\u4ee3\u7684\u67d0\u4f4d\u5deb\u5e08\u3002\u6309\u7167\u9b54\u6cd5\u539f\u7406\uff0c\u8fd9\u9053\u6cd5\u672f\u5e94\u8be5\u662f\u96b6\u5c5e\u4e8e\u5492\u6cd5\u5b66\u6d3e\uff0c\u5176\u6cd5\u672f\u6548\u679c\u662f\u521b\u9020\u51fa\u4e00\u5757\u9525\u5f62\u533a\u57df\uff0c\u4efb\u4f55\u8303\u56f4\u4e4b\u5185\u7684\u751f\u7269\u90fd\u4f1a\u53d7\u5230\u4e25\u91cd\u7684\u5f3a\u9178\u4f24\u5bb3\uff0c\u9887\u4e3a\u5389\u5bb3\u3002</p><p>\u4ec5\u4ec5\u5982\u6b64\uff0c\u5012\u8fd8\u7f62\u4e86\uff0c\u8fd9\u9053\u6cd5\u672f\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u4e5f\u8bf4\u4e0d\u4e0a\u591a\u4e48\u4e86\u4e0d\u8d77\uff0c\u771f\u6b63\u8ba9\u743c\u6069\u6ce8\u610f\u7684\uff0c\u662f\u80cc\u540e\u9690\u542b\u7684\u4e00\u4e9b\u4e1c\u897f\u3002</p></article>"}}}]);
