(window.webpackJsonp=window.webpackJsonp||[]).push([[1601],{1624:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u8282 \u5353\u5c14\u5c11\u5973\u7684\u6210\u957f",content:"<article><h2>\u7b2c\u4e94\u5341\u8282 \u5353\u5c14\u5c11\u5973\u7684\u6210\u957f</h2><p>\u7ad9\u5728\u743c\u6069\u9762\u524d\u7684\u8d64\u88f8\u5973\u5b50\uff0c\u94f6\u53d1\u3001\u9ed1\u80a4\u3001\u5c16\u8033\uff0c\u65e0\u4e0d\u8868\u660e\u4e86\u5979\u7684\u5353\u5c14\u8eab\u4efd\u3002\u8eab\u6750\u5a07\u5c0f\u5374\u4e30\u6ee1\uff0c\u66f2\u7ebf\u73b2\u73d1\u6d6e\u51f8\uff0c\u8ba9\u4efb\u4f55\u6b63\u5e38\u7684\u7537\u6027\u770b\u5230\u90fd\u4f1a\u672c\u80fd\u5730\u54bd\u53e3\u6c34\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u548c\u743c\u6069\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u6709\u8fc7\u76f8\u5f53\u957f\u4e00\u6bb5\u65f6\u95f4\u7684\u4ea4\u5f80\uff0c\u8fd8\u66fe\u7ecf\u4e0a\u8fc7\u5e8a\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\xb7\u83f2\u5c14\u4f26\u3002</p><p>\u743c\u6069\u5f53\u65e5\u5949\u547d\u524d\u5f80\u4f0a\u5361\u6c99\u57ce\uff0c\u6b63\u649e\u4e0a\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\u4eec\u653b\u57ce\uff0c\u7edf\u5e05\u5c31\u662f\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u88ab\u743c\u6069\u5b64\u8eab\u95ef\u9635\uff0c\u4e8e\u4e71\u519b\u4e2d\u4fd8\u864f\uff0c\u8fd9\u7b97\u662f\u4e24\u4eba\u7684\u521d\u6b21\u76f8\u8bc6\u3002\u540e\u6765\u743c\u6069\u8bf4\u670d\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5e26\u4ed6\u8fdb\u5165\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u5176\u95f4\u7ecf\u5386\u9887\u591a\u66f2\u6298\uff0c\u4e24\u4eba\u51e0\u6b21\u5e76\u80a9\u4f5c\u6218\uff0c\u5c45\u7136\u7b97\u662f\u7ed3\u4e0b\u4e86\u70b9\u60c5\u8c0a\uff0c\u5173\u7cfb\u4e00\u5ea6\u4eb2\u5bc6\uff0c\u6709\u8fc7\u5408\u4f53\u4ea4\u6b22\uff0c\u743c\u6069\u8fd8\u5e2e\u5979\u5f53\u4e0a\u4e86\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\u3002\u53ef\u60dc\u4e8b\u60c5\u5230\u4e86\u6700\u540e\uff0c\u743c\u6069\u7ec8\u7a76\u662f\u6709\u6240\u56fe\u8c0b\u800c\u6765\uff0c\u8bbe\u4e0b\u5708\u5957\u5f15\u8bf1\u5353\u5c14\u4e0a\u5f53\uff0c\u4f0a\u5361\u6c99\u57ce\u4e00\u6218\uff0c\u74dc\u7406\u5fb7\u65af\u5143\u6c14\u5927\u4f24\uff0c\u7cbe\u9510\u5175\u529b\u635f\u5931\u6b86\u534a\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u518d\u5ea6\u88ab\u4fd8\uff0c\u6210\u4e3a\u9636\u4e0b\u56da\u3002</p><p>\u6b64\u6b21\u4e4b\u540e\uff0c\u4e24\u4eba\u7684\u5173\u7cfb\u81ea\u7136\u7b97\u662f\u5f7b\u5e95\u7834\u88c2\uff0c\u539f\u672c\u90a3\u70b9\u8106\u5f31\u60c5\u8c0a\u8361\u7136\u65e0\u5b58\u3002\u5e73\u5fc3\u800c\u8bba\uff0c\u5353\u5c14\u867d\u7136\u662f\u4ee5\u90aa\u6076\u8457\u79f0\u7684\u79cd\u65cf\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5584\u826f\u5c11\u5973\uff0c\u4f46\u5bf9\u743c\u6069\u5374\u8fd8\u7b97\u771f\u8bda\uff0c\u5c31\u50cf\u5979\u81ea\u5df1\u8bf4\u7684\u90a3\u6837\uff0c\u201c\u4f60\u4e00\u76f4\u5728\u6b3a\u9a97\u6211\uff0c\u4ece\u5934\u5230\u5c3e\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u6b3a\u9a97\u8fc7\u4f60\u2014\u2014\u6211\u53ef\u5426\u628a\u8fd9\u79f0\u4e3a\u80cc\u53db\uff1f\u201d</p><p>\u5584\u6076\u662f\u975e\u4e14\u4e0d\u8bba\uff0c\u4ec5\u4ee5\u4e24\u4eba\u4e4b\u95f4\u6765\u8c08\uff0c\u786e\u5b9e\u662f\u743c\u6069\u5bf9\u4e0d\u8d77\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u5927\u7ea6\u4e5f\u662f\u56e0\u4e3a\u5fc3\u6000\u6127\u759a\uff0c\u743c\u6069\u8bf4\u670d\u77ee\u4eba\u91ca\u653e\u4e86\u5979\uff0c\u4efb\u5979\u79bb\u53bb\u3002\u4f5c\u4e3a\u5931\u8d25\u8005\uff0c\u7f57\u4e1d\u7684\u5f03\u6c11\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5df2\u7ecf\u65e0\u6cd5\u8fd4\u56de\u74dc\u7406\u5fb7\u65af\uff0c\u4e5f\u4e0d\u613f\u53bb\u522b\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u6253\u7b97\u524d\u5f80\u5730\u8868\u4e16\u754c\u3002</p><p>\u201c\u56e0\u4e3a\u6211\u60f3\u4e86\u89e3\u4e00\u4e0b\u4eba\u7c7b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u60f3\u770b\u770b\uff0c\u4f60\u4eec\u4eba\u7c7b\u7684\u4e16\u754c\uff0c\u548c\u5353\u5c14\u5230\u5e95\u6709\u4ec0\u4e48\u4e0d\u540c\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u79bb\u53bb\u4e4b\u540e\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u743c\u6069\u6e10\u6e10\u4e5f\u5c31\u5c06\u5979\u6de1\u5fd8\uff0c\u4ee5\u4e3a\u5929\u5357\u5730\u5317\uff0c\u4ece\u6b64\u4fbf\u518d\u65e0\u4ea4\u96c6\uff0c\u4e24\u4eba\u95f4\u66fe\u7ecf\u53d1\u751f\u7684\u4e00\u5207\uff0c\u4e5f\u53ea\u5f53\u4f5c\u6c38\u4e45\u7684\u56de\u5fc6\u3002\u6ca1\u60f3\u5230\u65f6\u9694\u4e00\u5e74\uff0c\u5c45\u7136\u5728\u8fd9\u964c\u751f\u57ce\u5e02\uff0c\u53c8\u9047\u89c1\u4e86\u6545\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u3002</p><p>\u4e16\u4e8b\u65e0\u5e38\uff0c\u83ab\u8fc7\u4e8e\u6b64\u3002</p><p>\u5353\u5c14\u7684\u540d\u58f0\u5728\u4eba\u7c7b\u5f53\u4e2d\u6781\u5176\u6076\u52a3\uff0c\u51e0\u4e4e\u662f\u4eba\u4eba\u558a\u6253\u7684\u5bf9\u8c61\uff0c\u6740\u4e86\u4f30\u8ba1\u90fd\u65e0\u7f6a\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u6562\u5728\u963f\u65af\u5361\u7279\u62c9\u8fd9\u79cd\u4eba\u7c7b\u5927\u90fd\u5e02\u5c45\u4f4f\uff0c\u81ea\u7136\u4e5f\u8981\u7279\u522b\u5c0f\u5fc3\uff0c\u5728\u5916\u9762\u7684\u65f6\u5019\uff0c\u5979\u663e\u7136\u4f7f\u7528\u4e86\u67d0\u79cd\u6cd5\u672f\u4f2a\u88c5\u81ea\u5df1\uff0c\u6539\u53d8\u4e86\u76f8\u8c8c\u2014\u2014\u81f3\u5c11\u6539\u53d8\u4e86\u80a4\u8272\uff0c\u56e0\u4e3a\u5979\u867d\u7136\u7528\u515c\u5e3d\u906e\u4f4f\u4e86\u8138\uff0c\u4f46\u53cc\u624b\u662f\u9732\u5728\u8896\u5916\u7684\uff0c\u800c\u743c\u6069\u5f53\u65f6\u770b\u5230\u7684\u5e76\u975e\u9ed1\u80a4\uff0c\u800c\u662f\u767d\u8272\uff0c\u5426\u5219\u4ed6\u65e9\u5c31\u60f3\u5230\u4e86\u3002\u867d\u7136\u5982\u6b64\uff0c\u4e24\u4eba\u7ec8\u7a76\u66fe\u7ecf\u6709\u8fc7\u4eb2\u5bc6\u5173\u7cfb\uff0c\u743c\u6069\u4e00\u8def\u8ddf\u6765\uff0c\u770b\u8eab\u5f62\u80cc\u5f71\uff0c\u8d70\u8def\u59ff\u6001\uff0c\u7ec8\u4e8e\u8fd8\u662f\u60f3\u4e86\u8d77\u6765\u3002\u72b9\u8c6b\u4e4b\u4e0b\uff0c\u8fd8\u662f\u51b3\u5b9a\u8fdb\u6765\u770b\u770b\uff0c\u5176\u5b9e\u4ed6\u4e5f\u6ca1\u60f3\u597d\u8981\u505a\u4ec0\u4e48\uff0c\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u89c9\u5f97\u65e2\u7136\u89c1\u5230\uff0c\u5c31\u6b64\u9ed8\u9ed8\u8f6c\u8eab\u8d70\u4eba\uff0c\u603b\u662f\u4e0d\u59a5\u3002</p><p>\u56de\u5230\u4f4f\u5904\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6e05\u6d01\u6c90\u6d74\uff0c\u81ea\u7136\u53d6\u6d88\u4e86\u4f2a\u88c5\u6cd5\u672f\uff0c\u6062\u590d\u5353\u5c14\u5f62\u6001\u3002\u4e00\u5e74\u65f6\u95f4\uff0c\u65e0\u8bba\u4eba\u7c7b\u8fd8\u662f\u5353\u5c14\uff0c\u76f8\u8c8c\u7406\u5f53\u90fd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u592a\u5927\u6539\u53d8\uff0c\u4f46\u7ecf\u5386\u7684\u4e8b\u60c5\u591a\u4e86\uff0c\u6c14\u8d28\u81ea\u7136\u800c\u7136\u4fbf\u4e5f\u4f1a\u6709\u5f71\u54cd\u3002\u5353\u5c14\u5c11\u5973\u7684\u5bb9\u989c\uff0c\u867d\u7136\u4f9d\u65e7\u5982\u6614\u65e5\u4e00\u822c\u7cbe\u81f4\u7f8e\u4e3d\uff0c\u4f46\u5374\u5c11\u4e86\u4e9b\u8bb8\u7a1a\u6c14\uff0c\u591a\u4e86\u51e0\u5206\u6c89\u7a33\uff0c\u770b\u8d77\u6765\u660e\u663e\u6210\u719f\u591a\u4e86\u3002</p><p>\u4e24\u4eba\u6014\u6014\u5bf9\u89c6\uff0c\u65e2\u89c9\u610f\u5916\uff0c\u53c8\u60f3\u8d77\u5f80\u4e8b\uff0c\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u6700\u7ec8\u8fd8\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u5148\u56de\u8fc7\u795e\u6765\uff0c\u201c\u662f\u4f60\u554a\uff0c\u201d\u5979\u6de1\u6de1\u4e00\u7b11\uff0c\u201c\u597d\u4e45\u4e0d\u89c1\uff0c\u743c\u6069\u3002\u201d</p><p>\u5979\u6b64\u65f6\u8fd8\u662f\u8d64\u8eab\u88f8\u4f53\uff0c\u4e00\u4e1d\u4e0d\u6302\uff0c\u9762\u5bf9\u7740\u743c\u6069\u548c\u4e00\u4e2a\u964c\u751f\u5973\u5b50\uff08\u838e\u73de\u514b\uff09\uff0c\u795e\u60c5\u5766\u7136\u81ea\u82e5\uff0c\u843d\u843d\u5927\u65b9\uff0c\u6beb\u65e0\u534a\u70b9\u5c40\u4fc3\u4e0d\u5b89\u4e4b\u611f\u3002\u7eb5\u7136\u5353\u5c14\u662f\u4ee5\u98ce\u6c14\u5f00\u653e\u6deb\u8361\u800c\u8457\u79f0\uff0c\u4f46\u5374\u4e5f\u5e76\u4e0d\u662f\u66b4\u9732\u7656\uff0c\u6ca1\u6709\u54ea\u4e2a\u5973\u796d\u53f8\u4f1a\u559c\u6b22\u5728\u8857\u4e0a\u88f8\u5954\uff0c\u8ba9\u522b\u4eba\u968f\u610f\u6b23\u8d4f\u81ea\u5df1\u7684\u8eab\u4f53\u3002\u5018\u82e5\u6362\u4e86\u4ee5\u524d\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u662f\u80af\u5b9a\u4e0d\u4f1a\u8868\u73b0\u5f97\u5982\u6b64\u9547\u5b9a\uff0c\u5341\u6709\u516b\u4e5d\u8981\u7acb\u523b\u7a7f\u8863\uff0c\u5475\u65a5\u743c\u6069\u9000\u4e0b\uff0c\u73b0\u5728\u5374\u80fd\u8a00\u7b11\u81ea\u82e5\uff0c\u4eff\u4f5b\u5168\u7136\u4e0d\u4ee5\u4e3a\u610f\u2014\u2014\u8fd9\u6050\u6015\u5e76\u4e0d\u662f\u8bf4\u5979\u6bd4\u4ee5\u524d\u653e\u8361\uff0c\u6216\u8005\u7f9e\u803b\u5fc3\u4e27\u5931\uff0c\u81ea\u66b4\u81ea\u5f03\uff0c\u800c\u662f\u2026\u2026\u6709\u6240\u89c9\u609f\uff0c\u6709\u6240\u6210\u957f\u4e86\u3002</p><p>\u5353\u5c14\u5c11\u5973\u7684\u88f8\u4f53\u5f88\u6f02\u4eae\uff0c\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u743c\u6069\u8fd8\u771f\u60f3\u5929\u5929\u770b\u3002\u4e0d\u8fc7\u73b0\u5728\u8fd9\u79cd\u573a\u5408\uff0c\u81ea\u5df1\u8863\u51a0\u6574\u9f50\uff0c\u5bf9\u65b9\u4e00\u4e1d\u4e0d\u6302\uff0c\u53c8\u6ca1\u6253\u7b97\u9a6c\u4e0a\u4e0a\u5e8a\uff0c\u611f\u89c9\u603b\u662f\u602a\u602a\u7684\u3002\u743c\u6069\u4e0d\u53ef\u80fd\u628a\u81ea\u5df1\u4e5f\u8131\u5149\uff0c\u6765\u4e2a\u5766\u8bda\u76f8\u5bf9\uff0c\u81ea\u7136\u5c31\u53ea\u80fd\u8bf7\u5bf9\u65b9\u7a7f\u4e0a\u8863\u670d\u4e86\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u968f\u624b\u6311\u8d77\u65c1\u8fb9\u8863\u67b6\u4e0a\u7684\u4e00\u4ef6\u6d74\u888d\u62ab\u5728\u8eab\u4e0a\uff0c\u6563\u6563\u7cfb\u7740\u8170\u5e26\uff0c\u5927\u534a\u4e2a\u6027\u611f\u88f8\u4f53\u4f9d\u65e7\u8fd8\u662f\u66b4\u9732\u5728\u7a7a\u6c14\u91cc\uff0c\u5173\u952e\u90e8\u4f4d\u82e5\u9690\u82e5\u73b0\uff0c\u751a\u81f3\u80fd\u9690\u7ea6\u770b\u89c1\u80f8\u524d\u7684\u4e24\u9897\u5ae3\u7ea2\u84d3\u857e\uff0c\u8fd9\u4e0e\u5176\u8bf4\u662f\u7a7f\u8863\uff0c\u4e0d\u5982\u8bf4\u662f\u5728\u5f15\u8bf1\u4eba\u8131\u8863\u3002\u603b\u7b97\u743c\u6069\u5e73\u65f6\u7f8e\u4eba\u770b\u5f97\u591a\u4e86\uff0c\u8fd8\u7b97\u6709\u4e9b\u5b9a\u529b\uff0c\u4e0d\u81f3\u4e8e\u5931\u6001\u3002</p><p>\u201c\u4e0d\u7528\u4e00\u76f4\u7ad9\u5728\u8fd9\u91cc\u5427\uff0c\u201d\u5353\u5c14\u5c11\u5973\u5fae\u7b11\uff0c\u5728\u65c1\u8fb9\u5899\u4e0a\u8f7b\u8f7b\u6572\u4e86\u6572\uff0c\u4e00\u6247\u6697\u95e8\u6084\u65e0\u58f0\u606f\u5730\u79fb\u5f00\uff0c\u201c\u8fdb\u6765\u5750\u5750\u5982\u4f55\u3002\u201d</p><p>\u743c\u6069\u81ea\u7136\u6ca1\u6709\u62d2\u7edd\u7684\u7406\u7531\uff0c\u968f\u5979\u8fdb\u4e86\u623f\u95f4\uff0c\u8fd9\u5730\u65b9\u5e94\u8be5\u662f\u5367\u5ba4\uff0c\u4e5f\u662f\u5979\u771f\u6b63\u751f\u6d3b\u7684\u5730\u65b9\uff0c\u9648\u8bbe\u5e03\u7f6e\u867d\u7136\u8bf4\u4e0d\u4e0a\u5bcc\u4e3d\u5802\u7687\uff0c\u91d1\u78a7\u8f89\u714c\uff0c\u4f46\u4e5f\u7b97\u5f97\u4e0a\u662f\u534e\u8d35\uff0c\u6bd4\u8d77\u5916\u9762\u7684\u7834\u65e7\u9eef\u6de1\uff0c\u90a3\u662f\u4e00\u4e2a\u5728\u5929\uff0c\u4e00\u4e2a\u5728\u5730\u4e86\u3002\u94bb\u77f3\u540a\u706f\u3001\u771f\u76ae\u6c99\u53d1\uff0c\u8fde\u5730\u4e0a\u94fa\u7740\u7684\u5730\u6bef\u90fd\u662f\u5d2d\u65b0\u7684\uff0c\u5e94\u8be5\u521a\u521a\u6dfb\u7f6e\u4e0d\u4e45\uff0c\u4e2d\u95f4\u6446\u7740\u4e00\u5f20\u53cc\u4eba\u5927\u5e8a\uff0c\u4e00\u770b\u5c31\u8d85\u7ea7\u8212\u9002\u67d4\u8f6f\uff0c\u8ba9\u4eba\u6709\u9677\u8fdb\u53bb\u7684\u6b32\u671b\u3002</p><p>\u4e24\u4eba\u9762\u5bf9\u9762\u5750\u4e0b\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u62cd\u4e86\u62cd\u624b\uff0c\u900f\u660e\u7684\u5f02\u754c\u5974\u4ec6\u81ea\u89d2\u843d\u91cc\u5192\u51fa\u6765\uff0c\u7aef\u4e0a\u4e24\u676f\u8461\u8404\u9152\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u53d6\u8fc7\u4e00\u676f\uff0c\u7aef\u5728\u624b\u4e2d\u5374\u4e0d\u54c1\u5c1d\uff0c\u53ea\u662f\u9759\u9759\u5750\u7740\uff0c\u743c\u6069\u4e00\u65f6\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002</p><p>\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u6545\u4eba\u4e45\u522b\u91cd\u9022\uff0c\u7406\u5f53\u76f8\u4e92\u95ee\u5019\uff0c\u8bf4\u8bf4\u79bb\u522b\u4e4b\u60c5\uff0c\u8c08\u8c08\u81ea\u5df1\u8fd1\u51b5\uff0c\u7136\u800c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5173\u7cfb\u2026\u2026\u670b\u53cb\u7b97\u4e0d\u4e0a\uff0c\u654c\u4eba\u5012\u5dee\u4e0d\u591a\u3002\u5728\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u743c\u6069\u6740\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u6bcd\u4eb2\u548c\u59d0\u59d0\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u5176\u5b9e\u65e0\u6240\u8c13\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u5df1\u5c31\u662f\u540c\u8c0b\uff1b\u743c\u6069\u8fd8\u5f3a\u5978\u4e86\u5979\uff0c\u7ed9\u5979\u540e\u5ead\u5f00\u4e86\u82de\u2014\u2014\u8fd9\u4e5f\u4e0d\u8981\u7d27\uff0c\u5353\u5c14\u4eec\u4e0d\u5728\u4e4e\u8fd9\u4e2a\u3002\u4f46\u743c\u6069\u8bbe\u8ba1\u8ba9\u74dc\u7406\u5fb7\u65af\u57ce\u6218\u8d25\uff0c\u8ba9\u5979\u8fd9\u4e2a\u7b2c\u4e00\u5bb6\u65cf\u7684\u4e3b\u6bcd\u6ca6\u4e3a\u9636\u4e0b\u56da\uff0c\u88ab\u86db\u540e\u9057\u5f03\uff0c\u6d41\u4ea1\u5730\u8868\uff0c\u8fd9\u5c31\u662f\u5f88\u5927\u7684\u4ec7\u6028\u4e86\u3002\u5982\u679c\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u89c1\u5230\u4ed6\u5c31\u51b2\u4e0a\u6765\u780d\uff0c\u90a3\u743c\u6069\u662f\u534a\u70b9\u90fd\u4e0d\u60ca\u8bb6\u7684\uff0c\u5982\u4eca\u5979\u80fd\u8fd9\u4e48\u9547\u5b9a\u7406\u6027\uff0c\u5fc3\u5e73\u6c14\u548c\uff0c\u5df2\u7ecf\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599\u4e86\u3002</p><p>\u6b63\u662f\u56e0\u4e3a\u8003\u8651\u5230\u8fd9\u79cd\u53cd\u5e38\u7684\u7406\u6027\uff0c\u743c\u6069\u624d\u6ca1\u6709\u9063\u9000\u838e\u73de\u514b\uff0c\u800c\u662f\u8ba9\u5979\u8ddf\u81ea\u5df1\u8fdb\u4e86\u5367\u5ba4\uff0c\u7ad9\u5728\u8eab\u540e\u3002\u8fd9\u4e16\u754c\u4e0a\u7684\u5973\u4eba\u5f88\u591a\uff0c\u96be\u7f20\u7684\u4e5f\u4e0d\u5c11\uff0c\u5c31\u743c\u6069\u7684\u8ba4\u77e5\u800c\u8a00\uff0c\u806a\u660e\u7684\u5973\u4eba\u5927\u591a\u90fd\u6bd4\u8f83\u5371\u9669\u2014\u2014\u800c\u65e2\u806a\u660e\u53c8\u7406\u667a\u7684\u5973\u4eba\uff0c\u504f\u504f\u8fd8\u548c\u81ea\u5df1\u6709\u4ec7\uff0c\u90a3\u5c31\u662f\u6700\u5371\u9669\u7684\u5b58\u5728\u4e86\uff0c\u4e0d\u5f97\u4e0d\u9632\u3002\u522b\u770b\u73b0\u5728\u5ba2\u5ba2\u6c14\u6c14\uff0c\u8bf4\u4e0d\u5b9a\u4e0b\u4e00\u79d2\u949f\u5c31\u7ffb\u8138\u52a8\u624b\uff0c\u867d\u8bf4\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5b9e\u529b\u5e94\u8be5\u548c\u81ea\u5df1\u76f8\u5dee\u751a\u8fdc\uff0c\u4f46\u8f7b\u654c\u5927\u610f\u4ece\u6765\u90fd\u662f\u5931\u8d25\u7684\u6839\u6e90\uff0c\u8c01\u77e5\u9053\u5979\u8fd9\u4e00\u5e74\u91cc\u6709\u6ca1\u6709\u4ec0\u4e48\u5947\u9047\uff0c\u4f1a\u4e0d\u4f1a\u7a81\u98de\u731b\u8fdb\u5462\u3002\u8fd9\u4e24\u5e74\u6765\uff0c\u6b7b\u5728\u743c\u6069\u624b\u4e0a\u7684\u5bb6\u4f19\u5f88\u591a\uff0c\u5176\u4e2d\u4e5f\u9887\u6709\u4e00\u4e9b\u5b9e\u529b\u539f\u672c\u662f\u5728\u4ed6\u4e4b\u4e0a\u7684\uff0c\u5f80\u5f80\u5c31\u662f\u4e00\u4e2a\u758f\u5ffd\u4e0d\u614e\uff0c\u7ed3\u679c\u5c31\u4e22\u4e86\u6027\u547d\u3002\u8fd9\u79cd\u4e8b\u60c5\u843d\u5728\u654c\u4eba\u5934\u4e0a\u90a3\u662f\u5f88\u723d\uff0c\u843d\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u53ef\u5c31\u4e0d\u592a\u7f8e\u5999\u4e86\u3002</p><p>\u5e78\u8fd0\u7684\u662f\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4f3c\u4e4e\u5e76\u6ca1\u6709\u8fd9\u4e2a\u6253\u7b97\u3002</p><p>\u201c\u4f60\u521a\u4ece\u5e7d\u6697\u5730\u57df\u56de\u6765\uff1f\u201d\u5979\u5f00\u53e3\u95ee\u3002</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d</p><p>\u201c\u95fb\u5f97\u51fa\u6765\uff0c\u6709\u90a3\u79cd\u5473\u9053\uff0c\u201d\u5353\u5c14\u7b11\u4e86\u7b11\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u6211\u53ef\u662f\u5728\u90a3\u91cc\u751f\u6d3b\u4e86\u4e0a\u767e\u5e74\u5462\u3002\u201d</p><p>\u201c\u54e6\uff0c\u662f\u554a\uff0c\u524d\u51e0\u5929\u624d\u521a\u4e0a\u5730\u8868\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u73b0\u5728\u51c6\u5907\u56de\u9634\u9b42\u57ce\uff0c\u8def\u8fc7\u8fd9\u91cc\u3002\u4f60\u5462\uff0c\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u4f60\u600e\u4e48\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d</p><p>\u4eba\u7c7b\u7684\u5927\u90fd\u5e02\uff0c\u5bf9\u4e8e\u5353\u5c14\u800c\u8a00\u7edd\u975e\u5408\u9002\u7684\u5c45\u6240\uff0c\u5c31\u7b97\u6709\u6cd5\u672f\u4f2a\u88c5\uff0c\u63a9\u76d6\u771f\u9762\u76ee\uff0c\u4e5f\u96be\u4fdd\u4e0d\u4f1a\u6709\u9732\u9985\u7684\u4e00\u5929\u3002\u800c\u4e00\u65e6\u9732\u9985\uff0c\u90a3\u4f30\u8ba1\u5c31\u8981\u4e0a\u706b\u5211\u67b6\u4e86\u3002\u4ece\u5b89\u5168\u89d2\u5ea6\u8003\u8651\uff0c\u8fd8\u662f\u5728\u504f\u8fdc\u7684\u6751\u5e84\u4e2d\u9690\u5c45\u6bd4\u8f83\u597d\u3002\u743c\u6069\u6709\u6b64\u7591\u60d1\uff0c\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\uff0c\u800c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u56de\u7b54\u4e5f\u4ee4\u4ed6\u5927\u5927\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u201c\u6211\u662f\u88ab\u5949\u4e0a\u9762\u7684\u6307\u4ee4\uff0c\u88ab\u6d3e\u8fc7\u6765\u7684\u3002\u201d</p><p>\u201c\u4e0a\u9762\u7684\u6307\u4ee4\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4ece\u5e8a\u5934\u62e3\u8d77\u4e00\u4e2a\u62a4\u8eab\u7b26\u6a21\u6837\u7684\u4e1c\u897f\uff0c\u9012\u7ed9\u743c\u6069\u770b\uff0c\u662f\u4e2a\u9ed1\u8272\u5c0f\u5706\u76d8\uff0c\u5916\u9762\u4e00\u5708\u7d2b\u73af\uff0c\u7528\u94f6\u94fe\u7a7f\u7740\uff0c\u8fd9\u662f\u591c\u5973\u58eb\u838e\u5c14\u7684\u5723\u5fbd\u3002\u201c\u6211\u73b0\u5728\u662f\u591c\u5973\u58eb\u7684\u7267\u5e08\uff0c\u838e\u5c14\u4e4b\u624b\u2014\u2014\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u6211\u4eec\u8fd8\u80fd\u7b97\u662f\u4e00\u5bb6\u4eba\uff1f\u201d\u5979\u683c\u683c\u7b11\u7740\uff0c\u201c\u8bf7\u591a\u5173\u7167\u4e86\u3002\u201d</p><p>\u838e\u5c14\u6559\u4f1a\u795e\u804c\u4eba\u5458\u7684\u4f4d\u9636\u5206\u516d\u7ea7\uff0c\u838e\u5c14\u4e4b\u624b\u662f\u7b2c\u4e09\u7ea7\uff0c\u5c5e\u4e8e\u4e2d\u5c42\u4eba\u5458\uff0c\u8fd9\u5730\u4f4d\u5df2\u7ecf\u4e0d\u7b97\u4f4e\u4e86\uff0c\u5c24\u5176\u5bf9\u4e8e\u4e00\u4e2a\u52a0\u5165\u6700\u591a\u4e0d\u4f1a\u8d85\u8fc7\u4e00\u5e74\u7684\u65b0\u4eba\u800c\u8a00\uff0c\u7b80\u76f4\u5c31\u662f\u7834\u683c\u63d0\u62d4\u3002\u743c\u6069\u65e2\u60ca\u4e14\u6115\uff0c\u201c\u4f60\u600e\u4e48\u4f1a\u53d8\u6210\u591c\u5973\u58eb\u7684\u7267\u5e08\uff1f\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u4f60\u4eec\u9634\u9b42\u57ce\u7684\u529f\u52b3\u5417\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u53cd\u95ee\uff0c\u201c\u4f60\u4eec\u8fdb\u653b\u5e7d\u6697\u5730\u57df\uff0c\u5360\u636e\u4e86\u4e0d\u5c11\u57ce\u5e02\uff0c\u5f3a\u5236\u63a8\u884c\u4fe1\u4ef0\uff0c\u5230\u5904\u5ba3\u4f20\u6559\u4e49\u3002\u6211\u4ece\u4f0a\u5361\u6c99\u57ce\u79bb\u5f00\u4ee5\u540e\uff0c\u4e00\u8def\u5317\u4e0a\uff0c\u6070\u597d\u5c31\u9047\u4e0a\u4e00\u961f\u591c\u5973\u58eb\u7684\u7267\u5e08\uff0c\u62c9\u6211\u52a0\u5165\u3002\u6211\u60f3\u81ea\u5df1\u53cd\u6b63\u5df2\u7ecf\u88ab\u86db\u540e\u9057\u5f03\u4e86\uff0c\u662f\u4e2a\u65e0\u4fe1\u8005\uff0c\u542c\u5979\u4eec\u8bf4\u51e0\u53e5\u89c9\u5f97\u8fd8\u4e0d\u9519\uff0c\u5c31\u6539\u4fe1\u4e86\u3002\u201d</p><p>\u201c\u5c31\u8fd9\u6837\u5417\u2026\u2026\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u8d77\u6765\u8f7b\u63cf\u6de1\u5199\uff0c\u4eff\u4f5b\u5c31\u662f\u5546\u573a\u8d2d\u7269\u6311\u6311\u62e3\u62e3\uff0c\u770b\u4e0a\u65b0\u7684\u5c31\u53c8\u653e\u4e0b\u65e7\u7684\uff0c\u4f46\u743c\u6069\u6e05\u695a\u5b9e\u9645\u60c5\u5f62\u7edd\u6ca1\u8fd9\u4e48\u7b80\u5355\u3002\u5c31\u7b97\u7ef4\u5eb7\u5c3c\u4e9a\u771f\u662f\u4e3b\u52a8\u613f\u610f\u6539\u53d8\u4fe1\u4ef0\uff0c\u52a0\u5165\u838e\u5c14\u6559\u4f1a\uff0c\u5979\u53ef\u539f\u672c\u662f\u7f57\u4e1d\u7684\u7267\u5e08\uff0c\u867d\u7136\u5730\u4f4d\u4e0d\u9ad8\uff0c\u6bd5\u7adf\u4e5f\u662f\u6b63\u5f0f\u7684\u795e\u804c\u4eba\u5458\uff0c\u4e0d\u540c\u4e8e\u90a3\u4e9b\u666e\u901a\u4fe1\u5f92\u3002\u7267\u5e08\u6539\u53d8\u4fe1\u4ef0\uff0c\u8981\u6bd4\u666e\u901a\u4fe1\u5f92\u8270\u96be\u5f97\u591a\uff0c\u75db\u82e6\u5f97\u591a\uff0c\u8089\u4f53\u548c\u7cbe\u795e\u4e0a\u90fd\u4f1a\u53d7\u5230\u6781\u5927\u7684\u6298\u78e8\u548c\u953b\u70bc\uff0c\u54ea\u662f\u90a3\u6837\u8f7b\u8f7b\u677e\u677e\u7684\u4e8b\u60c5\u3002\u8fd9\u5c31\u597d\u50cf\u4e00\u4e2a\u5bb9\u5668\uff0c\u672c\u6765\u5df2\u7ecf\u88ab\u6539\u9020\u6210\u4e86\u6bd4\u8f83\u7b26\u5408\u7f57\u4e1d\u9700\u8981\u7684\u5f62\u6001\uff0c\u73b0\u5728\u53c8\u8981\u5b83\u53d8\u6210\u7b26\u5408\u838e\u5c14\u559c\u6b22\u7684\u6837\u5b50\uff0c\u90a3\u5c31\u4e0d\u662f\u6362\u4e2a\u540d\u5b57\uff0c\u91cd\u8d34\u4e2a\u6807\u7b7e\u5c31\u80fd\u5b8c\u4e8b\u7684\uff0c\u9509\u5200\u4fee\u8865\u3001\u56de\u7089\u91cd\u70bc\uff0c\u90fd\u662f\u610f\u6599\u4e4b\u4e2d\u3002\u5e78\u597d\u7ef4\u5eb7\u5c3c\u4e9a\u5e74\u7eaa\u8f83\u8f7b\uff0c\u4ee5\u5353\u5c14\u800c\u8a00\u8fd8\u662f\u5c11\u5973\uff0c\u524d\u51e0\u5e74\u521a\u4ece\u796d\u53f8\u5b66\u9662\u6bd5\u4e1a\uff0c\u5f53\u7f57\u4e1d\u7267\u5e08\u5e76\u4e0d\u4e45\uff0c\u6539\u53d8\u8fd8\u6765\u5f97\u53ca\uff0c\u6362\u4e86\u8001\u724c\u7267\u5e08\uff0c\u90a3\u5c31\u60f3\u53d8\u90fd\u53d8\u4e0d\u8fc7\u6765\u4e86\u3002</p><p>\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u743c\u6069\u65e2\u662f\u51fa\u4e4e\u610f\u6599\uff0c\u4e00\u65f6\u95f4\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u597d\u3002\u773c\u524d\u7684\u5c11\u5973\uff0c\u867d\u7136\u53ea\u9694\u4e00\u5e74\u672a\u89c1\uff0c\u4f46\u5374\u663e\u7136\u5df2\u7ecf\u7ecf\u5386\u4e86\u5f88\u591a\u4e8b\u60c5\uff0c\u627f\u53d7\u4e86\u5f88\u591a\u75db\u82e6\uff0c\u5fc3\u667a\u6e10\u6e10\u953b\u70bc\u5f97\u575a\u5f3a\uff0c\u6240\u4ee5\u73b0\u5728\u624d\u80fd\u4ee5\u8fd9\u6837\u8f7b\u677e\u7684\u8bed\u6c14\u6765\u63d0\u53ca\u3002\u8fd9\u5176\u4e2d\uff0c\u53ea\u6015\u4e5f\u6709\u81ea\u5df1\u7684\u4e00\u5206\u201c\u529f\u52b3\u201d\u5427\uff0c\u867d\u7136\u743c\u6069\u5e76\u4e0d\u4f1a\u56e0\u6b64\u611f\u5230\u81ea\u8c6a\u4fbf\u662f\u4e86\u3002</p><p>\u201c\u4f60\u5462\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u53cd\u95ee\uff0c\u201c\u4f60\u6765\u963f\u65af\u5361\u7279\u62c9\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u6211\u4e48\u2026\u2026\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u4e0d\u592a\u60f3\u5411\u5bf9\u65b9\u63d0\u8d77\u51db\u7684\u4e8b\u60c5\uff0c\u5979\u4eec\u53c8\u4e0d\u8ba4\u8bc6\uff0c\u89e3\u91ca\u8d77\u6765\u53c8\u9ebb\u70e6\u3002\u6b63\u60f3\u968f\u4fbf\u627e\u4e2a\u7406\u7531\u642a\u585e\u8fc7\u53bb\uff0c\u5353\u5c14\u5c11\u5973\u8f7b\u8f7b\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u521a\u624d\u8bf4\uff0c\u56de\u9634\u9b42\u57ce\uff0c\u8def\u8fc7\u8fd9\u91cc\u662f\u5427\u3002\u53ef\u662f\u963f\u65af\u5361\u7279\u62c9\u8fd9\u4f4d\u7f6e\uff0c\u65e0\u8bba\u4f60\u8d70\u54ea\u6761\u8def\uff0c\u4e5f\u6ca1\u9053\u7406\u4f1a\u8def\u8fc7\u554a\uff0c\u201d\u5979\u7b11\u541f\u541f\u5730\u7aef\u8d77\u9152\u676f\u8f7b\u8f7b\u555c\u996e\u4e00\u53e3\uff0c\u201c\u4f60\u662f\u7279\u5730\u7ed5\u8def\u8fc7\u6765\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u770b\u4f60\u7684\u6837\u5b50\uff0c\u660e\u663e\u662f\u9047\u5230\u4ec0\u4e48\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u743c\u6069\u5b9a\u5b9a\u5730\u770b\u7740\u5979\u3002</p><p>\u201c\u4f60\u770b\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u53f9\u606f\uff0c\u201c\u53ea\u662f\u89c9\u5f97\uff0c\u4f60\u771f\u7684\u957f\u5927\u4e86\u3002\u201d</p><p>\u8fd9\u8bdd\u542c\u8d77\u6765\u771f\u662f\u6709\u591f\u8be1\u5f02\u7684\uff0c\u743c\u6069\u8ddd\u79bb\u5341\u4e03\u5c81\u751f\u65e5\u90fd\u8fd8\u6709\u51e0\u5929\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5df2\u7ecf\u8d85\u8fc7\u767e\u5c81\uff0c\u524d\u8005\u7528\u8fd9\u79cd\u8bed\u6c14\u8bf4\u540e\u8005\uff0c\u5b9e\u5728\u662f\u2026\u2026\u5b9e\u5728\u662f\u6ca1\u6cd5\u5f62\u5bb9\u3002\u867d\u7136\u5982\u6b64\uff0c\u5353\u5c14\u5c11\u5973\u5374\u6ca1\u6709\u5931\u7b11\uff0c\u5979\u6e05\u695a\u660e\u767d\u743c\u6069\u7684\u610f\u601d\u3002</p><p>\u201c\u603b\u662f\u8fc7\u4e86\u4e00\u5e74\uff0c\u957f\u4e86\u4e00\u5c81\uff0c\u7ecf\u5386\u5f97\u591a\u4e86\uff0c\u770b\u4e8b\u60c5\u81ea\u7136\u4e5f\u5c31\u660e\u767d\u70b9\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u6765\u5730\u8868\u8fd9\u4e00\u8d9f\uff0c\u786e\u5b9e\u6ca1\u6709\u8ba9\u6211\u5931\u671b\u3002\u4eba\u7c7b\u7684\u4e16\u754c\uff0c\u6bd4\u5353\u5c14\u8981\u7cbe\u5f69\u767e\u500d\uff0c\u8ba9\u6211\u5b66\u5230\u4e86\u5f88\u591a\u4e1c\u897f\u3002\u201d</p><p>\u8fd9\u542c\u8d77\u6765\u4e0d\u9519\uff0c\u89c1\u8bc1\u4e00\u4f4d\u5c11\u5973\u7684\u6210\u957f\uff0c\u4e5f\u662f\u5f88\u6709\u8da3\u7684\u4e8b\u60c5\u3002\u53ef\u60dc\u53cc\u65b9\u7684\u7acb\u573a\u662f\u654c\u5bf9\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8d8a\u6210\u957f\uff0c\u90a3\u5bf9\u743c\u6069\u5c31\u8d8a\u4e0d\u5229\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u4e5f\u6ca1\u6709\u65a9\u8349\u9664\u6839\uff0c\u65e9\u7edd\u540e\u60a3\u7684\u6253\u7b97\u3002\u4eba\u53ef\u4ee5\u4e0d\u5584\u826f\uff0c\u53ef\u4ee5\u4e0d\u5f3a\uff0c\u4f46\u4e00\u5b9a\u8981\u806a\u660e\uff0c\u806a\u660e\u4e5f\u4e0d\u662f\u8bf4\u601d\u7ef4\u654f\u9510\uff0c\u8fc7\u76ee\u4e0d\u5fd8\uff0c\u80fd\u591f\u89e3\u5f00\u54e5\u5fb7\u5df4\u8d6b\u731c\u60f3\u8fd9\u79cd\uff0c\u800c\u662f\u8bf4\u80fd\u8ba4\u6e05\u81ea\u5df1\uff0c\u660e\u767d\u5f62\u52bf\uff0c\u5766\u7136\u9762\u5bf9\u2014\u2014\u7b80\u800c\u8a00\u4e4b\u4e00\u53e5\u8bdd\uff0c\u8981\u6709\u89c9\u609f\u3002</p><p>\u743c\u6069\u81ea\u5df1\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u4e5f\u7d20\u6765\u6700\u6b23\u8d4f\u8fd9\u6837\u7684\u4eba\u3002\u5f3a\u5927\u7684\u4eba\u503c\u5f97\u754f\u60e7\uff0c\u6f02\u4eae\u7684\u4eba\u503c\u5f97\u503e\u6155\uff0c\u5584\u826f\u7684\u4eba\u503c\u5f97\u5c0a\u91cd\uff0c\u800c\u806a\u660e\u7684\uff0c\u6709\u89c9\u609f\u7684\u4eba\uff0c\u503c\u5f97\u60fa\u60fa\u76f8\u60dc\u3002\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u4ed6\u4ece\u6ca1\u8d77\u8fc7\u534a\u70b9\u6740\u673a\uff0c\u53ea\u6709\u6b23\u8d4f\u548c\u611f\u53f9\u3002</p><p>\u201c\u6211\u786e\u5b9e\u9047\u5230\u4e86\u70b9\u9ebb\u70e6\uff0c\u53d7\u4eba\u6307\u70b9\uff0c\u8fc7\u6765\u78b0\u78b0\u8fd0\u6c14\uff0c\u201d\u4ed6\u627f\u8ba4\uff0c\u201c\u4e0d\u8fc7\u73b0\u5728\u770b\u6765\u4e00\u65e0\u6240\u83b7\u2026\u2026\u4e0d\uff0c\u80fd\u591f\u9047\u89c1\u4f60\uff0c\u4e5f\u5c31\u4e0d\u7b97\u4e00\u65e0\u6240\u83b7\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u4ec0\u4e48\u9ebb\u70e6\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u95ee\uff0c\u201c\u8bf4\u8bf4\u770b\uff0c\u6216\u8bb8\u6211\u80fd\u5e2e\u4e0a\u5fd9\u5462\u3002\u6bd5\u7adf\u5bf9\u8fd9\u57ce\u5e02\uff0c\u6211\u6bd4\u4f60\u5e94\u8be5\u719f\u6089\u5f97\u591a\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8fdf\u7591\uff0c\u5927\u5bb6\u6709\u4ec7\u65e0\u6069\uff0c\u89c1\u9762\u80fd\u591f\u4e0d\u4e92\u76f8\u780d\u5c31\u5f88\u4e0d\u9519\u4e86\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6001\u5ea6\u4e4b\u53cb\u5584\uff0c\u5374\u662f\u5df2\u7ecf\u5927\u5927\u51fa\u4e4e\u4ed6\u7684\u9884\u6599\u3002\u743c\u6069\u81ea\u5ea6\u6ca1\u6709\u80fd\u8ba9\u4eba\u76f8\u9022\u4e00\u7b11\u3001\u6cef\u5c3d\u6069\u4ec7\u7684\u4eba\u683c\u9b45\u529b\uff0c\u4e5f\u6ca1\u6709\u80fd\u8ba9\u4eba\u629b\u53bb\u65e7\u6028\u3001\u4fef\u9996\u81e3\u670d\u7684\u738b\u8005\u6c14\u8c61\uff0c\u8fd9\u4e9b\u5e74\u6765\u90fd\u662f\u8c28\u614e\u5c0f\u5fc3\u5730\u5ea6\u8fc7\uff0c\u5bf9\u4ed6\u4eba\u7684\u8fc7\u5206\u5584\u610f\u548c\u70ed\u60c5\uff0c\u603b\u662f\u62b1\u6709\u672c\u80fd\u7684\u6000\u7591\uff0c\u4e0d\u6562\u8f7b\u6613\u76f8\u4fe1\u3002</p><p>\u7136\u800c\u518d\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53cd\u6b63\u4e8b\u60c5\u5df2\u7ecf\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u518d\u574f\u4e5f\u4e0d\u4f1a\u574f\u5230\u54ea\u91cc\u53bb\u3002\u65e0\u8bba\u7ef4\u5eb7\u5c3c\u4e9a\u662f\u771f\u5fc3\u6709\u610f\u76f8\u52a9\u4e5f\u597d\uff0c\u8fd8\u662f\u6253\u7b97\u501f\u673a\u63a2\u542c\u743c\u6069\u7684\u8fd1\u51b5\u4e5f\u597d\uff0c\u53cd\u6b63\u4e5f\u5c31\u8fd9\u56de\u4e8b\u4e86\u3002\u4e8e\u662f\u70b9\u70b9\u5934\uff0c\u5c06\u51db\u7684\u4e8b\u60c5\u5927\u81f4\u8bb2\u4e86\u4e00\u904d\u3002</p><p>\u201c\u5728\u6cf0\u745f\u5c14\u7684\u5854\u683c\u6751\u91cc\uff0c\u6070\u5de7\u9047\u4e0a\u4e00\u4e2a\u4eba\uff0c\u4e5f\u662f\u4ece\u5b89\u59c6\u8fc7\u6765\u7684\uff0c\u6307\u70b9\u8bf4\u6765\u963f\u65af\u5361\u7279\u62c9\u770b\u770b\uff0c\u6216\u8bb8\u80fd\u6709\u529e\u6cd5\u3002\u53ef\u60dc\u73b0\u5728\u4e24\u5929\u4e86\uff0c\u8fd8\u662f\u4ec0\u4e48\u5934\u7eea\u90fd\u6ca1\u6709\uff0c\u770b\u6765\u6211\u7684\u8fd0\u6c14\u4e0d\u592a\u597d\u5462\u3002\u201d</p><p>\u743c\u6069\u81ea\u5632\u5730\u8bf4\uff0c\u6b63\u51c6\u5907\u559d\u9152\uff0c\u773c\u89d2\u4f59\u5149\u5374\u77a5\u89c1\u5353\u5c14\u5c11\u5973\u7684\u8138\u4e0a\u795e\u60c5\uff0c\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\u3002\u201c\u600e\u4e48\uff0c\u83ab\u975e\u4f60\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u6709\u4eba\u6307\u70b9\u4f60\u6765\u5b89\u59c6\uff0c\u90a3\u4e2a\u4eba\u662f\u8c01\uff0c\u957f\u4ec0\u4e48\u6837\u7684\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6307\u70b9\u6211\u7684\u662f\u4e2a\u53eb\u505a\u601d\u601d\u7684\u5973\u5b69\u5b50\uff0c\u201d\u743c\u6069\u60f3\u4e86\u4e00\u60f3\uff0c\u8fd8\u662f\u6ca1\u6709\u628a\u5979\u4f53\u5185\u8fd8\u6709\u4e2a\u5927\u5965\u672f\u5e08\u7075\u9b42\u7684\u4e8b\u60c5\u8bf4\u51fa\u6765\uff0c\u201c\u5979\u8fd8\u6709\u4e2a\u540c\u4f34\uff0c\u53eb\u505a\u4f0a\u65af\u5854\uff0c\u662f\u4e2a\u5251\u58eb\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u683c\u683c\u5a07\u7b11\u8d77\u6765\uff0c\u201c\u662f\u4ed6\u4eec\u554a\uff0c\u90a3\u5c31\u96be\u602a\u4e86\u3002\u201d</p><p>\u201c\u4f60\u8ba4\u8bc6\uff1f\u201d</p><p>\u201c\u7b97\u662f\u8ba4\u8bc6\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u6253\u8fc7\u4e24\u6b21\u4ea4\u9053\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u2026\u2026\u201d</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u4ed6\u4eec\u6ca1\u8bf4\u9519\uff0c\u4f60\u8fd9\u4e00\u8d9f\u6765\u963f\u65af\u5361\u7279\u62c9\uff0c\u7b97\u662f\u6765\u5bf9\u4e86\uff0c\u201d\u5353\u5c14\u5c11\u5973\u60a0\u7136\u8bf4\uff0c\u201c\u6211\u624b\u4e0a\uff0c\u6070\u597d\u5c31\u6709\u4f60\u9700\u8981\u7684\u4e1c\u897f\u3002\u201d</p></article>"}}}]);
