(window.webpackJsonp=window.webpackJsonp||[]).push([[1603],{1626:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e8c\u8282 \u6c83\u91d1\u6559\u4f1a\u7684\u79d8\u5bc6",content:"<article><h2>\u7b2c\u4e94\u5341\u4e8c\u8282 \u6c83\u91d1\u6559\u4f1a\u7684\u79d8\u5bc6</h2><p>\u5b89\u59c6\u6392\u65a5\u5deb\u5e08\uff0c\u8981\u5728\u57ce\u5e02\u91cc\u4f7f\u7528\u9b54\u6cd5\uff08\u5965\u672f\uff09\uff0c\u5fc5\u987b\u65bd\u6cd5\u8bb8\u53ef\u8bc1\uff0c\u53c8\u79f0\u201c\u5965\u672f\u6267\u7167\u201d\uff0c\u5426\u5219\u5c31\u662f\u8fdd\u6cd5\u3002\u636e\u6885\u83f2\u65af\u6240\u8a00\uff0c\u515c\u5e3d\u5deb\u5e08\u516c\u4f1a\u901a\u8fc7\u9b54\u7f51\u5bf9\u5168\u57ce\u6709\u7740\u4e25\u5bc6\u7684\u76d1\u63a7\uff0c\u4e00\u65e6\u53d1\u73b0\u5f02\u5e38\u6ce2\u52a8\uff0c\u7acb\u523b\u76f4\u63a5\u7528\u4f20\u9001\u672f\u62b5\u8fbe\u73b0\u573a\uff0c\u51b3\u4e0d\u4f1a\u51fa\u73b0\u7f6a\u72af\u5df2\u7ecf\u9003\u8dd1\u534a\u65e5\uff0c\u8b66\u8f66\u624d\u59d7\u59d7\u6765\u8fdf\u7684\u60c5\u5f62\u3002\u800c\u4e14\u515c\u5e3d\u5deb\u5e08\u516c\u4f1a\u7684\u6210\u5458\u867d\u7136\u4e0d\u7b97\u5f88\u591a\uff0c\u4f46\u5b9e\u529b\u9887\u4e3a\u96c4\u539a\uff0c\u81ea\u4ece\u8ba2\u4e0b\u8fd9\u4e2a\u89c4\u77e9\u4ee5\u6765\uff0c\u51e0\u767e\u5e74\u95f4\u4e5f\u9887\u6709\u4e9b\u5916\u6765\u5deb\u5e08\u89e6\u72af\u7981\u4ee4\uff0c\u65e0\u4e00\u4f8b\u5916\u90fd\u88ab\u5173\u8fdb\u76d1\u72f1\u91cc\u53bb\u4e86\u3002</p><p>\u743c\u6069\u81ea\u7136\u4e0d\u60f3\u88ab\u5173\u8fdb\u76d1\u72f1\u91cc\uff0c\u4f55\u51b5\u4ed6\u8fd9\u6b21\u662f\u8981\u53bb\u8c0b\u6740\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u9690\u79d8\uff0c\u5018\u82e5\u91ca\u653e\u4e2a\u6cd5\u672f\uff0c\u7ed3\u679c\u5f15\u6765\u4e00\u7fa4\u515c\u5e3d\u5deb\u5e08\uff0c\u90a3\u5c82\u4e0d\u662f\u73b0\u6210\u7684\u76ee\u51fb\u8bc1\u4eba\u3002\u4f46\u5018\u82e5\u4e0d\u7528\u6cd5\u672f\u7684\u8bdd\uff0c\u4ed6\u53c8\u51ed\u4ec0\u4e48\u53bb\u6740\u4eba\uff1f\u51ed\u81ea\u5df1\u90a3\u4e09\u811a\u732b\u7684\u62f3\u811a\u529f\u592b\u4e48\u3002</p><p>\u57ce\u4e2d\u4e0d\u80fd\u65bd\u6cd5\uff0c\u8fd9\u70b9\u743c\u6069\u65e9\u5c31\u77e5\u9053\uff0c\u5bf9\u4e8e\u5deb\u5e08\u800c\u8a00\u81ea\u7136\u662f\u4e2a\u6781\u5927\u9650\u5236\uff0c\u4f46\u4ed6\u4e00\u5f00\u59cb\u4e5f\u5e76\u6ca1\u6709\u592a\u5728\u610f\u3002\u4e00\u65b9\u9762\u662f\u4e3a\u4e86\u7ed9\u51db\u6cbb\u75c5\uff0c\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\uff0c\u53e6\u4e00\u65b9\u9762\u53cd\u6b63\u6709\u6885\u83f2\u65af\u8fd9\u4e2a\u5f3a\u529b\u5723\u6b66\u58eb\u5728\uff0c\u62c5\u5fc3\u4ec0\u4e48\u3002\u4f46\u73b0\u5728\u9ebb\u70e6\u51fa\u73b0\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6885\u83f2\u65af\u662f\u7edd\u4e0d\u53ef\u80fd\u5e2e\u5fd9\u7684\u2014\u2014\u522b\u8bf4\u5e2e\u5fd9\uff0c\u54ea\u6015\u88ab\u5979\u77e5\u9053\uff0c\u4f30\u8ba1\u90fd\u4f1a\u7acb\u523b\u548c\u743c\u6069\u7ffb\u8138\u3002</p><p>\u743c\u6069\u4e0d\u662f\u597d\u4eba\uff0c\u4f46\u201c\u4e0d\u662f\u597d\u4eba\u201d\u5e76\u4e0d\u7b49\u4e8e\u5c31\u662f\u201c\u574f\u4eba\u201d\uff0c\u8fd9\u4e24\u8005\u4e4b\u95f4\u8fd8\u662f\u6709\u4e00\u6bb5\u8ddd\u79bb\u7684\u3002\u5728\u6885\u83f2\u65af\u9762\u524d\uff0c\u4ed6\u4f1a\u5c3d\u53ef\u80fd\u6536\u655b\uff0c\u5c31\u7b97\u73a9\u4ec0\u4e48\u9634\u8c0b\u8be1\u8ba1\uff0c\u7528\u4ec0\u4e48\u72e0\u8fa3\u624b\u6bb5\uff0c\u5bf9\u4ed8\u7684\u4e5f\u90fd\u662f\u5353\u5c14\u3001\u6076\u9b54\u8fd9\u4e9b\u6076\u540d\u662d\u8457\u7684\u5bb6\u4f19\uff0c\u9ed1\u5403\u9ed1\uff0c\u4e0d\u7b97\u4ec0\u4e48\u5927\u95ee\u9898\u3002\u4f46\u5e2e\u7ef4\u5eb7\u5c3c\u4e9a\u53bb\u8c0b\u6740\u4e00\u4e2a\u65e0\u8f9c\u7684\u82cf\u4f26\u7267\u5e08\uff0c\u8fd9\u5c31\u662f\u9ed1\u5403\u767d\uff0c\u7edd\u5bf9\u8d85\u51fa\u4e86\u6885\u83f2\u65af\u7684\u5bb9\u5fcd\u5e95\u7ebf\u3002</p><p>\u201c\u5c31\u7b97\u8ddf\u5979\u89e3\u91ca\u8bf4\u8fd9\u662f\u4e3a\u4e86\u6551\u51db\u4e5f\u6ca1\u7528\uff0c\u4e3a\u4e86\u6551\u4eba\u5148\u53bb\u6740\u4eba\uff0c\u8fd9\u79cd\u4e8b\u60c5\u5723\u6b66\u58eb\u54ea\u80fd\u505a\u5f97\u51fa\u6765\u3002\u8fd8\u662f\u6211\u81ea\u5df1\u6084\u6084\u89e3\u51b3\uff0c\u522b\u8ba9\u5979\u4e3a\u96be\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u529b\u91cf\u4e0d\u80fd\u501f\u52a9\uff0c\u83ab\u5c3c\u5361\u59d0\u59b9\u672c\u9886\u4f4e\u5fae\uff0c\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\uff0c\u518d\u8bf4\u8299\u857e\u72c4\u4e5f\u662f\u5deb\u5e08\uff0c\u8299\u8389\u5a05\u53c8\u4e0d\u80fd\u5b8c\u5168\u4fe1\u4efb\uff0c\u51db\u660f\u7761\u4e0d\u9192\u2014\u2014\u7b97\u6765\u7b97\u53bb\uff0c\u4e5f\u5c31\u53ea\u6709\u81ea\u5df1\u548c\u7ad9\u5728\u8eab\u540e\u7684\u9b45\u9b54\u838e\u73de\u514b\u4e86\u3002\u65e0\u8bba\u743c\u6069\u8981\u505a\u4ec0\u4e48\u574f\u4e8b\uff0c\u838e\u73de\u514b\u80af\u5b9a\u90fd\u4f1a\u5f88\u4e50\u610f\u5730\u5e2e\u5fd9\uff0c\u751a\u81f3\u79ef\u6781\u5730\u51fa\u8c0b\u5212\u7b56\uff0c\u63a8\u6ce2\u52a9\u6f9c\uff0c\u4ece\u8fd9\u70b9\u4e0a\u6765\u8bf4\uff0c\u4ed6\u4eec\u8fd8\u771f\u662f\u72fc\u72c8\u4e3a\u5978\u2026\u2026</p><p>\u5728\u57ce\u5e02\u91cc\u4e0d\u80fd\u65bd\u6cd5\uff0c\u51fa\u4e86\u57ce\u5e02\u5728\u91ce\u5916\u5c31\u6ca1\u4e8b\uff0c\u7267\u5e08\u4eec\u5012\u4e5f\u672a\u5fc5\u53ea\u5728\u57ce\u5185\u6d3b\u52a8\uff0c\u603b\u4e5f\u6709\u51fa\u57ce\u4e0b\u4e61\u7684\u3002\u7136\u800c\u7ef4\u5eb7\u5c3c\u4e9a\u8981\u6740\u82cf\u4f26\u7267\u5e08\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u53d6\u60a6\u591c\u5973\u58eb\uff0c\u83b7\u53d6\u664b\u5347\u673a\u4f1a\uff0c\u4e3a\u5c06\u6765\u80fd\u591f\u6267\u638c\u5b89\u59c6\u8fd9\u5757\u65b0\u6559\u533a\u505a\u6253\u7b97\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u4e0d\u53ef\u80fd\u968f\u4fbf\u627e\u4e2a\u4f4e\u7ea7\u82cf\u4f26\u7267\u5e08\u6740\u4e86\u5145\u6570\uff0c\u603b\u5f97\u8981\u6709\u8db3\u591f\u7684\u8eab\u4efd\uff0c\u5728\u6559\u4f1a\u91cc\u7684\u4f4d\u9636\u8d77\u7801\u4e5f\u5f97\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u76f8\u5f53\uff0c\u4e5f\u5f97\u662f\u4e2a\u4e2d\u9ad8\u7ea7\u4ee5\u4e0a\u7684\u6210\u5458\u624d\u884c\u3002\u95ee\u9898\u4e5f\u5c31\u5728\u4e8e\u6b64\uff0c\u9ad8\u9636\u7267\u5e08\u4eec\u57fa\u672c\u90fd\u9f9f\u7f29\u5728\u5927\u672c\u8425\u91cc\uff0c\u5e73\u65f6\u4e0d\u4f1a\u8f7b\u6613\u51fa\u52a8\uff0c\u5728\u5916\u9762\u8dd1\u7684\u90fd\u662f\u5c0f\u55bd\u7f57\uff0c\u6740\u4e4b\u65e0\u7528\uff0c\u53ea\u4f1a\u6253\u8349\u60ca\u86c7\u3002</p><p>\u8fd9\u786e\u5b9e\u662f\u4e2a\u5927\u95ee\u9898\uff0c\u743c\u6069\u53ea\u987e\u7740\u5173\u6ce8\u9f99\u8840\uff0c\u4e00\u65f6\u95f4\u6ca1\u60f3\u8d77\u8fd9\u832c\u3002\u5e78\u8fd0\u7684\u662f\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5bf9\u6b64\u4f3c\u4e4e\u65e9\u6709\u6253\u7b97\uff0c\u80f8\u6709\u6210\u7af9\u3002</p><p>\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u5c3d\u7ba1\u5728\u57ce\u4e2d\u65bd\u6cd5\uff0c\u51b3\u4e0d\u4f1a\u6709\u515c\u5e3d\u5deb\u5e08\u6765\u5e72\u6d89\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u5728\u534a\u4e2a\u591a\u6708\u524d\uff0c\u515c\u5e3d\u5deb\u5e08\u516c\u4f1a\u906d\u53d7\u4e86\u4e00\u6b21\u6c89\u91cd\u6253\u51fb\u3002\u4ed6\u4eec\u5728\u5fb7\u963f\u5c3c\u65af\u57ce\u5821\u9644\u8fd1\u8ffd\u6355\u4e00\u4f4d\u9003\u72af\uff0c\u7ed3\u679c\u649e\u4e0a\u4e86\u62c9\u6c83\u514b\uff0c\u5f04\u5f97\u5168\u519b\u8986\u706d\uff0c\u53ea\u6709\u9996\u5e2d\u5deb\u5e08\u5854\u5c3c\u65af\u62c9\u5f17\u9003\u4e86\u56de\u6765\u3002\u73b0\u5728\u5168\u5b89\u59c6\u5883\u5185\u7684\u515c\u5e3d\u5deb\u5e08\uff0c\u4eba\u6570\u53ea\u5269\u4e0b\u4e0d\u5230\u4e8c\u5341\u4eba\uff0c\u963f\u65af\u5361\u7279\u62c9\u8fd9\u91cc\u6700\u591a\u5341\u4e2a\uff0c\u800c\u4e14\u8fd8\u90fd\u5fd9\u5f97\u8981\u547d\uff0c\u54ea\u91cc\u6709\u7a7a\u7ba1\u4f60\u3002\u201d</p><p>\u201c\u62c9\u6c83\u514b\uff1f\u201d\u743c\u6069\u60ca\u8bb6\uff0c\u201c\u535a\u5f97\u4e4b\u95e8\u7684\u90a3\u4e2a\u8001\u5deb\u5996\u62c9\u6c83\u514b\u3002\u201d</p><p>\u201c\u9664\u4e86\u4ed6\uff0c\u4e16\u754c\u4e0a\u8fd8\u6709\u51e0\u4e2a\u62c9\u6c83\u514b\uff1f\u201d</p><p>\u201c\u4ed6\u600e\u4e48\u8dd1\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d</p><p>\u5353\u5c14\u5c11\u5973\u5fae\u5fae\u8038\u80a9\uff0c\u201c\u90a3\u6211\u600e\u4e48\u77e5\u9053\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5728\u5deb\u5e08\u5c71\u8109\u91cc\u95f7\u7740\u65e0\u804a\uff0c\u51fa\u6765\u95f2\u901b\uff0c\u900f\u900f\u6c14\uff0c\u770b\u770b\u98ce\u666f\u3002\u201d</p><p>\u535a\u5f97\u4e4b\u95e8\u8ddd\u79bb\u963f\u65af\u5361\u7279\u62c9\u751a\u8fdc\uff0c\u4e58\u9a6c\u8f66\u8d70\u5546\u8def\u4e5f\u8981\u5341\u5929\u5de6\u53f3\u3002\u62c9\u6c83\u514b\u518d\u600e\u4e48\u95f2\u901b\u4e5f\u4e0d\u4f1a\u901b\u5230\u8fd9\u91cc\uff0c\u53ea\u53ef\u80fd\u662f\u6709\u76ee\u7684\u800c\u6765\u3002\u770b\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u795e\u60c5\uff0c\u53ea\u6015\u5e76\u975e\u662f\u4e0d\u77e5\u60c5\uff0c\u800c\u662f\u4e0d\u80af\u8bf4\u7f62\u4e86\uff0c\u743c\u6069\u4e5f\u5c31\u4e0d\u591a\u95ee\u3002\u201c\u5c31\u7b97\u8fd9\u6837\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e07\u4e00\u4ed6\u4eec\u51fa\u6765\u5e72\u6d89\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u6211\u4f1a\u548c\u5854\u5c3c\u65af\u62c9\u5f17\u5148\u751f\u6253\u62db\u547c\uff0c\u8ba9\u4ed6\u7ea6\u675f\u624b\u4e0b\u5c31\u5f53\u6ca1\u770b\u89c1\u7684\u3002\u201d</p><p>\u743c\u6069\u4e0d\u6613\u5bdf\u89c9\u5730\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u4f60\u548c\u4ed6\u5f88\u719f\uff1f\u201d</p><p>\u201c\u8fd8\u884c\uff0c\u6253\u8fc7\u4e24\u6b21\u4ea4\u9053\uff0c\u6211\u4eec\u6559\u4f1a\u5e2e\u4e86\u4ed6\u4e00\u70b9\u5fd9\uff0c\u662f\u6211\u51fa\u9762\u548c\u4ed6\u8054\u7cfb\u7684\uff0c\u7b97\u662f\u6709\u70b9\u4ea4\u60c5\u5427\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u683c\u683c\u7b11\u7740\uff0c\u201c\u4ed6\u597d\u50cf\u633a\u559c\u6b22\u6211\u7684\u3002\u201d</p><p>\u4e0d\u77e5\u4e3a\u4f55\uff0c\u743c\u6069\u5fc3\u4e2d\u9690\u9690\u6709\u4e9b\u4e0d\u5feb\uff0c\u4f46\u4e5f\u6ca1\u6cd5\u8bf4\u4ec0\u4e48\u3002\u201c\u90a3\u5c31\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d</p><p>\u201c\u771f\u7684\u653e\u5fc3\u4e86\uff1f\u201d\u5353\u5c14\u5c11\u5973\u4e00\u8bed\u53cc\u5173\u5730\u53cd\u95ee\uff0c\u7b11\u76c8\u76c8\u5730\u770b\u7740\u4ed6\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u5a9a\u6001\u8bf1\u4eba\u3002</p><p>\u743c\u6069\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u90fd\u4e00\u6837\uff0c\u6ca1\u533a\u522b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6709\u4e9b\u4e8b\u60c5\uff0c\u5df2\u7ecf\u4e0d\u662f\u6211\u6709\u8d44\u683c\u7ba1\uff0c\u6709\u8d44\u683c\u53bb\u64cd\u5fc3\u7684\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u60f3\u5b83\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u8fd9\u53eb\u4ec0\u4e48\uff0c\u8ba9\u6211\u60f3\u60f3\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u504f\u7740\u5934\u82e5\u6709\u6240\u601d\uff0c\u201c\u6709\u4e2a\u8bcd\u600e\u4e48\u5f62\u5bb9\u6765\u7740\u2026\u2026\u8c41\u8fbe\uff1f\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u8c41\u8fbe\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u662f\u81ea\u5632\u3002\u201d</p><p>\u5373\u4fbf\u662f\u81ea\u5df1\u4e3b\u52a8\u4f24\u5bb3\u7684\uff0c\u81ea\u5df1\u629b\u5f03\u7684\u5973\u4eba\uff0c\u5728\u542c\u5230\u5979\u8c08\u8d77\u548c\u522b\u7684\u7537\u4eba\u5173\u7cfb\u4eb2\u5bc6\u65f6\uff0c\u5fc3\u91cc\u4f9d\u65e7\u8fd8\u662f\u4e00\u9635\u9635\u7684\u4e0d\u8212\u670d\u3002\u5c24\u5176\u662f\u91cd\u9022\u4e4b\u540e\uff0c\u53d1\u73b0\u5979\u5df2\u7ecf\u6bd4\u8bb0\u5fc6\u4e2d\u7684\u5f62\u8c61\u6709\u6240\u6210\u957f\uff0c\u66f4\u52a0\u6210\u719f\uff0c\u8fd9\u79cd\u611f\u89c9\u5c31\u66f4\u52a0\u660e\u663e\u2014\u2014\u8fd9\u4e0d\u77e5\u9053\u5e94\u8be5\u8bf4\u662f\u72ec\u5360\u6b32\uff0c\u8fd8\u662f\u52a3\u6839\u6027\uff0c\u603b\u4e4b\u4e0d\u662f\u4ec0\u4e48\u5149\u5f69\u7684\u4e1c\u897f\u3002\u5018\u82e5\u4e0d\u81ea\u5632\u4e00\u4e0b\uff0c\u53ea\u6015\u8fde\u81ea\u5df1\u90fd\u8981\u8ba8\u538c\u8d77\u81ea\u5df1\u6765\u3002</p><p>\u7b97\u4e86\u7b97\u4e86\uff0c\u8fc7\u53bb\u7684\u4e8b\u60c5\u8fd8\u60f3\u90a3\u4e48\u591a\u505a\u4ec0\u4e48\uff0c\u8d76\u5feb\u8c08\u6b63\u4e8b\u8981\u7d27\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u838e\u73de\u514b\u6084\u6084\u5730\u5728\u80cc\u540e\u78b0\u4e86\u743c\u6069\u4e00\u4e0b\u3002\u743c\u6069\u77e5\u9053\u8fd9\u662f\u9b45\u9b54\u5728\u63d0\u9192\uff0c\u8981\u4ed6\u8b66\u60d5\u8fd9\u53ef\u80fd\u662f\u4e2a\u9677\u9631\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5634\u4e0a\u8bf4\u5f97\u6f02\u4eae\uff0c\u8bf4\u4e0d\u5b9a\u6697\u85cf\u7978\u5fc3\uff0c\u5230\u65f6\u5019\u743c\u6069\u4e00\u65bd\u6cd5\uff0c\u4e00\u7fa4\u515c\u5e3d\u5deb\u5e08\u5c31\u8df3\u51fa\u6765\u6293\u4eba\uff0c\u540d\u6b63\u8a00\u987a\u5730\u62a5\u6614\u65e5\u4e00\u7bad\u4e4b\u4ec7\uff0c\u90a3\u53ef\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u4f46\u743c\u6069\u6ca1\u6709\u7406\u4f1a\u9b45\u9b54\u7684\u63d0\u9192\u3002</p><p>\u57ce\u5185\u65bd\u6cd5\u7684\u95ee\u9898\u5df2\u7ecf\u5f97\u5230\u89e3\u51b3\uff0c\u90a3\u63a5\u4e0b\u6765\u8981\u8003\u8651\u7684\uff0c\u5c31\u662f\u600e\u4e48\u52a8\u624b\u3002\u6bd5\u7adf\u8fd9\u662f\u8c0b\u6740\uff0c\u4e0d\u662f\u8868\u6f14\uff0c\u515c\u5e3d\u5deb\u5e08\u5c31\u7b97\u4e0d\u5e72\u6d89\uff0c\u666e\u901a\u5e02\u6c11\u7684\u8033\u76ee\u4e5f\u662f\u8981\u907f\u5f00\u7684\u3002\u591c\u5973\u58eb\u838e\u5c14\u662f\u9690\u79d8\u5973\u795e\uff0c\u5bf9\u4fe1\u5f92\u7684\u8981\u6c42\u4e4b\u4e00\u5c31\u662f\u505a\u4e8b\u60c5\u8981\u9690\u79d8\uff0c\u8d8a\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u8d8a\u5e94\u8be5\u65e0\u4eba\u5bdf\u89c9\u624d\u5bf9\uff0c\u6740\u4eba\u5c24\u5176\u5982\u6b64\u3002\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e2d\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u6740\u4eba\uff0c\u5c31\u7b97\u4f60\u80fd\u6210\u529f\u9003\u8131\u8ffd\u6355\uff0c\u90a3\u4e5f\u662f\u5927\u5927\u7684\u5931\u8d25\u4e86\u3002\u5018\u82e5\u88ab\u6293\u5230\u4ec0\u4e48\u8bc1\u636e\uff0c\u90a3\u5c31\u66f4\u9ebb\u70e6\uff0c\u6bd5\u7adf\u838e\u5c14\u6559\u4f1a\u8fd8\u60f3\u5728\u8fd9\u91cc\u53d1\u5c55\u5462\u3002</p><p>\u201c\u8981\u6740\u4e00\u4e2a\u9ad8\u9636\u82cf\u4f26\u7267\u5e08\uff0c\u8fd8\u8981\u5077\u5077\u6740\uff0c\u4e0d\u80fd\u8ba9\u4eba\u770b\u89c1\uff0c\u4e0d\u80fd\u7559\u4e0b\u8bc1\u636e\u2026\u2026\u8fd9\u597d\u96be\u554a\u3002\u201d</p><p>\u201c\u5982\u679c\u4e0d\u96be\u7684\u8bdd\uff0c\u6211\u4f1a\u9700\u8981\u4f60\u5e2e\u5fd9\u5417\uff1f\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u7b97\u662f\u9ed8\u8ba4\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u62ff\u51fa\u51e0\u5f20\u7f8a\u76ae\u7eb8\u6765\uff0c\u4e0a\u9762\u5199\u7684\u662f\u5b89\u59c6\u5404\u5927\u6559\u4f1a\u52bf\u529b\u5206\u5e03\u3001\u7ec4\u7ec7\u7ed3\u6784\u3001\u91cd\u8981\u4eba\u5458\u7b49\uff0c\u5176\u4e2d\u82cf\u4f26\u6559\u4f1a\u7684\u8d44\u6599\u5c24\u5176\u8be6\u7ec6\uff0c\u4e43\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u7cbe\u5fc3\u641c\u96c6\u6574\u7406\u6240\u5f97\uff0c\u90fd\u6ca1\u4e0a\u4ea4\u7ed9\u6559\u4f1a\uff0c\u800c\u662f\u79c1\u81ea\u6536\u85cf\u3002\u743c\u6069\u63a5\u8fc7\u4e00\u770b\uff0c\u53d1\u73b0\u7ef4\u5eb7\u5c3c\u4e9a\u5df2\u7ecf\u5728\u4e0a\u9762\u505a\u4e86\u6807\u8bb0\uff0c\u5217\u51fa\u4e09\u4e2a\u4eba\u540d\uff0c\u663e\u7136\u662f\u5979\u7279\u610f\u7b5b\u9009\u51fa\u7684\uff0c\u5730\u4f4d\u591f\u9ad8\uff0c\u4f46\u53c8\u4e0d\u81f3\u4e8e\u9ad8\u5230\u4f1a\u5f15\u8d77\u5927\u52a8\u8361\u7684\u5730\u6b65\uff0c\u80fd\u529b\u4e0d\u4fd7\uff0c\u4f46\u4e5f\u6ca1\u5f3a\u5230\u80af\u5b9a\u641e\u4e0d\u5b9a\u7684\u7a0b\u5ea6\uff0c\u7b97\u662f\u6bd4\u8f83\u9002\u5408\u7684\u523a\u6740\u5bf9\u8c61\u3002</p><p>\u201c\u600e\u4e48\u90fd\u662f\u5973\u6027\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u5f53\u7136\u662f\u5973\u6027\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u82cf\u4f26\u7267\u5e08\u90fd\u662f\u5973\u6743\u4e3b\u4e49\u8005\uff0c\u5730\u4f4d\u9ad8\u7684\u5168\u90fd\u662f\u5973\u6027\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u6211\u660e\u767d\u5979\u4e3a\u4ec0\u4e48\u4f1a\u8870\u5f31\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6bcd\u7cfb\u793e\u4f1a\u5df2\u7ecf\u88ab\u7236\u7cfb\u793e\u4f1a\u53d6\u4ee3\u4e86\u5440\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6ca1\u6709\u542c\u61c2\u4e13\u4e1a\u540d\u8bcd\uff0c\u743c\u6069\u4e5f\u6ca1\u89e3\u91ca\uff0c\u7ee7\u7eed\u7814\u7a76\u8d44\u6599\u3002\u8fd9\u4e09\u4e2a\u7267\u5e08\u90fd\u5c45\u4f4f\u5728\u4f4d\u4e8e\u7801\u5934\u533a\u7684\u201c\u6708\u5bab\uff08Moonhall\uff09\u201d\u795e\u6bbf\u91cc\uff0c\u5e73\u5e38\u6df1\u5c45\u7b80\u51fa\uff0c\u5f88\u5c11\u5728\u5916\u629b\u5934\u9732\u9762\uff0c\u96be\u5f97\u51fa\u6765\u4e00\u8d9f\uff0c\u57fa\u672c\u4e5f\u90fd\u662f\u53c2\u52a0\u4ec0\u4e48\u5e86\u5178\u6216\u8005\u5b98\u65b9\u6d3b\u52a8\uff0c\u524d\u547c\u540e\u62e5\uff0c\u968f\u4ece\u4f17\u591a\uff0c\u90a3\u662f\u80af\u5b9a\u4e0d\u80fd\u52a8\u624b\u7684\u3002\u5982\u679c\u8bf4\u76f4\u63a5\u6f5c\u5165\u6708\u5bab\u53bb\u6740\uff0c\u96be\u5ea6\u53c8\u672a\u514d\u592a\u9ad8\uff0c\u8fd9\u79cd\u5386\u53f2\u60a0\u4e45\u7684\u795e\u6bbf\uff0c\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u91cd\u5f3a\u5927\u7ed3\u754c\u5728\u5b88\u62a4\u7740\uff0c\u743c\u6069\u662f\u838e\u5c14\u4fe1\u5f92\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u662f\u838e\u5c14\u7267\u5e08\uff0c\u838e\u73de\u514b\u662f\u90aa\u9b54\uff0c\u6027\u8d28\u4e0a\u90fd\u6b63\u76f8\u51b2\u7a81\uff0c\u522b\u8bf4\u6f5c\u5165\u6740\u4eba\uff0c\u53ea\u6015\u521a\u521a\u8d70\u8fdb\u95e8\uff0c\u7acb\u523b\u5c31\u4f1a\u89e6\u53d1\u611f\u5e94\uff0c\u53d7\u5230\u4e25\u5bc6\u76d1\u63a7\u4e86\u3002</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u7ffb\u6765\u8986\u53bb\u5730\u770b\u7740\u8d44\u6599\uff0c\u7a81\u7136\u53d1\u73b0\u4e00\u4e2a\u88ab\u5ffd\u7565\u7684\u7ec6\u8282\u3002</p><p>\u201c\u4ece\u8fd9\u4e0a\u9762\u770b\uff0c\u5979\u4eec\u6bcf\u9694\u4e94\u516d\u5929\uff0c\u5c31\u4f1a\u5230\u8fd9\u4e2a\u2018\u661f\u6708\u4e4b\u989c\u2019\u53bb\u4e00\u8d9f\uff0c\u4e00\u53bb\u5c31\u662f\u5927\u534a\u5929\u751a\u81f3\u4e00\u6574\u5929\u2014\u2014\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u8fd9\u662f\u963f\u65af\u5361\u7279\u62c9\u4e43\u81f3\u5b89\u59c6\u6700\u5927\u6700\u8457\u540d\u7684\u7f8e\u5bb9\u9662\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u89e3\u91ca\uff0c\u201c\u5979\u4eec\u5b9a\u671f\u53bb\u4fdd\u517b\u76ae\u80a4\uff0c\u505a\u505a\u5934\u53d1\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u201c\u80fd\u4e0d\u80fd\u4ece\u8fd9\u91cc\u4e0b\u624b\u5462\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u60f3\u8fc7\uff0c\u4f46\u5f88\u96be\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u5982\u679c\u8d70\u6b63\u5e38\u9014\u7ecf\u8fdb\u53bb\uff0c\u627e\u673a\u4f1a\u4e0b\u624b\u7684\u8bdd\uff0c\u90a3\u91cc\u662f\u7f8e\u5bb9\u9662\uff0c\u6211\u6ca1\u529e\u6cd5\u7528\u9b54\u6cd5\u4f2a\u88c5\u5f62\u8c8c\uff0c\u80af\u5b9a\u4f1a\u66b4\u9732\u5353\u5c14\u7684\u8eab\u4efd\uff0c\u90a3\u5c31\u4ec0\u4e48\u90fd\u4e0d\u7528\u8c08\u4e86\u3002\u8981\u5077\u5077\u6f5c\u5165\u7684\u8bdd\uff0c\u90a3\u91cc\u9632\u536b\u53c8\u5f88\u68ee\u4e25\uff0c\u6709\u591a\u5f3a\u529b\u7684\u91cd\u795e\u672f\u7ed3\u754c\u7b3c\u7f69\uff0c\u5f88\u96be\u529e\u7684\u3002\u201d</p><p>\u8d70\u6b63\u5e38\u9014\u7ecf\u81ea\u7136\u662f\u4e0d\u7528\u8003\u8651\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u6210\uff0c\u743c\u6069\u662f\u4e2a\u7537\u6027\uff0c\u8fdb\u53bb\u4e00\u5b9a\u6210\u4e3a\u4f17\u76ee\u777d\u777d\u7684\u7126\u70b9\uff0c\u838e\u73de\u514b\u52c9\u5f3a\u53ef\u884c\uff0c\u4f46\u5979\u6bd5\u7adf\u5df2\u7ecf\u662f\u9b45\u9b54\u3002\u666e\u901a\u4eba\u611f\u89c9\u4e0d\u51fa\u6765\u5979\u7684\u6076\u9b54\u6c14\u606f\uff0c\u53ea\u4ee5\u4e3a\u662f\u4e2a\u6f02\u4eae\u7f8e\u4eba\uff0c\u771f\u9047\u5230\u9ad8\u624b\u5185\u884c\uff0c\u4e00\u773c\u5c31\u8bc6\u7834\u4e86\u3002\u66f4\u4f55\u51b5\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8fd8\u8bf4\u8fd9\u4ec0\u4e48\u661f\u6708\u4e4b\u989c\u7f8e\u5bb9\u9662\u6709\u591a\u91cd\u7ed3\u754c\u795e\u672f\u7b3c\u7f69\u2026\u2026\u7b49\u4e00\u4e0b\uff0c\u7f8e\u5bb9\u9662\u8fd9\u79cd\u5730\u65b9\uff0c\u5c31\u7b97\u505a\u5f97\u518d\u5927\uff0c\u517b\u7684\u4fdd\u5b89\u518d\u591a\uff0c\u5b88\u536b\u68ee\u4e25\u5c31\u7f62\u4e86\uff0c\u53ef\u4ee5\u7406\u89e3\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4f1a\u6709\u5f3a\u529b\u7684\u795e\u672f\u7ed3\u754c\uff1f</p><p>\u201c\u56e0\u4e3a\u5b83\u662f\u6c83\u91d1\u6559\u4f1a\u7684\u4ea7\u4e1a\u554a\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u8fd9\u5ea7\u7f8e\u5bb9\u9662\u6bcf\u5e74\u7ed9\u6559\u4f1a\u4e0a\u7f34\u7684\u5229\u6da6\u6bd4\u4fe1\u5f92\u6350\u732e\u8fd8\u591a\uff0c\u90a3\u5e2e\u62dc\u91d1\u72c2\u5f53\u7136\u8981\u770b\u5b88\u5f97\u7262\u7262\u7684\uff0c\u751f\u6015\u51fa\u4ec0\u4e48\u95ee\u9898\u3002\u522b\u8bf4\u795e\u672f\u7ed3\u754c\uff0c\u8fde\u95e8\u53e3\u5b88\u536b\u90fd\u662f\u76f4\u63a5\u4ece\u795e\u6bbf\u8c03\u6765\u7684\u5462\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8bdd\u63d0\u9192\u4e86\u743c\u6069\uff0c\u8ba9\u4ed6\u60f3\u8d77\u4e24\u4ef6\u4e8b\u3002</p><p>\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u963f\u65af\u5361\u7279\u62c9\u8fd9\u5ea7\u57ce\u5e02\uff0c\u4e43\u662f\u5546\u4e1a\u5973\u795e\u6c83\u91d1\u6559\u4f1a\u7684\u603b\u90e8\u5927\u672c\u8425\u6240\u5728\uff0c\u4f4d\u4e8e\u57ce\u5e02\u897f\u5317\u65b9\u7684\u201c\u91d1\u9876\u201d\u5927\u795e\u6bbf\u5360\u5730\u6781\u5e7f\uff0c\u51e0\u4e4e\u76f8\u5f53\u4e8e\u4e00\u4e2a\u5c0f\u9547\uff0c\u6765\u5f80\u6d77\u8239\u90fd\u628a\u5b83\u5f53\u4f5c\u201c\u94f6\u5e01\u4e4b\u90fd\u201d\u7684\u6807\u5fd7\u5efa\u7b51\u7269\uff0c\u6559\u4f1a\u7684\u6700\u9ad8\u9886\u8896\u201c\u5723\u5e01\u201d\u4fbf\u5c45\u4f4f\u4e8e\u6b64\u3002\u5b89\u59c6\u662f\u5546\u4e1a\u7e41\u534e\u4e4b\u5730\uff0c\u6c83\u91d1\u6559\u4f1a\u7684\u52bf\u529b\u96c4\u539a\uff0c\u5f71\u54cd\u6df1\u8fdc\uff0c\u81ea\u4e0d\u5f85\u8a00\uff0c\u8fde\u743c\u6069\u4e0a\u5348\u53bb\u8fc7\u7684\u5546\u4e1a\u533a\u4e0d\u4e5f\u90fd\u53eb\u505a\u201c\u6c83\u91d1\u6b65\u9053\u201d\u4e48\u3002</p><p>\u6c83\u91d1\u662f\u5546\u4e1a\u5973\u795e\uff0c\u8d38\u6613\u5973\u795e\uff0c\u8d22\u5bcc\u5973\u795e\uff0c\u6559\u4f1a\u81ea\u7136\u4e5f\u4ee5\u8d5a\u94b1\u4e3a\u7b2c\u4e00\u8981\u52a1\uff0c\u6295\u8d44\u5404\u79cd\u4ea7\u4e1a\uff0c\u90a3\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u743c\u6069\u53bb\u8fc7\u535a\u5f97\u4e4b\u95e8\uff0c\u90a3\u91cc\u6709\u4e00\u5ea7\u201c\u707f\u91d1\u4e4b\u5bb6\u201d\uff0c\u4e43\u662f\u535a\u5f97\u4e4b\u95e8\u6700\u5927\u7684\u2026\u2026\u5a31\u4e50\u573a\u6240\uff0c\u743c\u6069\u8fd8\u5728\u91cc\u9762\u8ba4\u8bc6\u4e86\u4e00\u4f4d\u53eb\u505a\u9686\u5947\u7684\u5815\u843d\u6b66\u50e7\uff0c\u540e\u6765\u7528\u201c\u5f3a\u5236\u541e\u91d1\u6cd5\u201d\u628a\u4ed6\u6740\u4e86\u3002\u8fd9\u5ea7\u707f\u91d1\u4e4b\u5bb6\u7684\u4e09\u4e2a\u80a1\u4e1c\u5c31\u5168\u662f\u6c83\u91d1\u4fe1\u5f92\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u7267\u5e08\u3002\u6709\u8fd9\u4e2a\u5148\u4f8b\u5728\uff0c\u743c\u6069\u542c\u8bf4\u8fd9\u201c\u661f\u6708\u4e4b\u989c\u201d\u7f8e\u5bb9\u9662\u662f\u6c83\u91d1\u6559\u4f1a\u7684\u4ea7\u4e1a\uff0c\u5012\u4e5f\u4e1d\u6beb\u4e0d\u89c9\u610f\u5916\u3002</p><p>\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u5219\u662f\u4ece\u7b2c\u4e00\u4ef6\u4e8b\u8054\u60f3\u5ef6\u4f38\u8fc7\u6765\u7684\u3002\u6c83\u91d1\u6559\u4f1a\u7684\u4f4d\u9636\u4f53\u7cfb\uff0c\u6700\u9ad8\u9886\u8896\u662f\u201c\u5723\u5e01\u201d\uff0c\u5176\u4e0b\u662f\u201c\u91d1\u5e01\u201d\u3001\u201c\u94f6\u5e01\u201d\u3001\u201c\u94dc\u5e01\u201d\u4e09\u7ea7\uff0c\u6bcf\u4e00\u7ea7\u53c8\u5206\u201c\u4e0a\u3001\u4e2d\u3001\u4e0b\u201d\u4e09\u7b49\u3002\u743c\u6069\u5728\u535a\u5f97\u4e4b\u95e8\u7684\u65f6\u5019\uff0c\u62db\u52df\u961f\u53cb\u53bb\u5deb\u5e08\u5c71\u8109\u5bf9\u4ed8\u62c9\u6c83\u514b\u90a3\u8001\u5deb\u5996\uff0c\u9664\u4e86\u5728\u707f\u91d1\u4e4b\u5bb6\u9047\u5230\u7684\u6b66\u50e7\u9686\u5947\uff0c\u8fd8\u6709\u4e00\u4e2a\u5e94\u52df\u524d\u6765\u7684\u201c\u4e2d\u91d1\u201d\u7267\u5e08\uff0c\u8fd8\u662f\u4e2a\u91d1\u773c\u4f7f\u8005\u3002</p><p>\u540e\u6765\u7684\u4e8b\u5b9e\u8bc1\u660e\uff0c\u8fd9\u4e2a\u4e2d\u91d1\u7267\u5e08\u6839\u672c\u5c31\u662f\u4e2a\u5367\u5e95\uff0c\u4ed6\u52fe\u7ed3\u838e\u73de\u514b\uff0c\u62c9\u62e2\u9686\u5947\uff0c\u6253\u7b97\u4e2d\u9014\u53cd\u6c34\u3002\u5e78\u597d\u743c\u6069\u548c\u6885\u83f2\u65af\u8b66\u89c9\uff0c\u8bc6\u7834\u9677\u9631\uff0c\u62a2\u5148\u4e0b\u624b\u628a\u4ed6\u4eec\u6740\u4e86\u3002\u5982\u4eca\u65f6\u8fc7\u5883\u8fc1\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u53d8\u6210\u4e86\u9b45\u9b54\uff0c\u5982\u4eca\u6b63\u7ad9\u5728\u743c\u6069\u8eab\u540e\uff0c\u9686\u5947\u7684\u7075\u9b42\u88ab\u6076\u9b54\u6b27\u51ef\u53d6\u8d70\uff0c\u81f3\u4e8e\u90a3\u4e2a\u4e2d\u91d1\u7267\u5e08\uff0c\u6b7b\u540e\u4e0b\u573a\u5c31\u65e0\u4eba\u5173\u5fc3\u4e86\u3002</p><p>\u4f46\u662f\uff0c\u5982\u679c\u743c\u6069\u8bb0\u5f97\u6ca1\u9519\u7684\u8bdd\u2026\u2026</p><p>\u201c\u838e\u73de\u514b\u3002\u201d\u4ed6\u53eb\u9b45\u9b54\u7684\u540d\u5b57\u3002</p><p>\u4e00\u76f4\u4fdd\u6301\u6c89\u9ed8\uff0c\u4eff\u4f5b\u4e00\u5c0a\u96d5\u50cf\u822c\u7684\u9b45\u9b54\u5728\u4ed6\u8eab\u540e\u5fae\u5fae\u6b20\u8eab\uff0c\u201c\u6709\u4f55\u5429\u5490\uff0c\u4e3b\u4eba\u3002\u201d</p><p>\u201c\u6211\u8bb0\u5f97\uff0c\u5728\u6df1\u6e0a\u91cc\uff0c\u6211\u9047\u5230\u4f60\u7684\u65f6\u5019\uff0c\u4f60\u66fe\u7ecf\u8bf4\uff0c\u5f53\u65e5\u5deb\u5e08\u5c71\u8109\u4e00\u6218\uff0c\u90a3\u4e2a\u6c83\u91d1\u7684\u91d1\u773c\u4f7f\u8005\uff0c\u662f\u683c\u62c9\u5179\u7279\u5b89\u6392\u7684\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u4f60\u8fd8\u8bf4\u8fc7\uff0c\u683c\u62c9\u5179\u7279\u53ef\u4ee5\u76f4\u63a5\u5bf9\u5f88\u591a\u6c83\u91d1\u7267\u5e08\u4e0b\u547d\u4ee4\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4e0d\u662f\u8bf4\uff0c\u683c\u62c9\u5179\u7279\u548c\u6c83\u91d1\u7ed3\u76df\u4e86\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d</p><p>\u838e\u73de\u514b\u867d\u7136\u8bf4\u4e0d\u6e05\u695a\uff0c\u4f46\u743c\u6069\u57fa\u672c\u4e0a\u5df2\u7ecf\u53ef\u4ee5\u65ad\u5b9a\u4e86\u3002\u5f53\u65e5\u90a3\u4e2a\u91d1\u773c\u4f7f\u8005\uff0c\u7ed9\u4ed6\u7559\u4e0b\u7684\u5370\u8c61\u9887\u4e3a\u6df1\u523b\uff0c\u65e2\u7528\u5438\u98df\u8111\u6d46\u8fd9\u79cd\u90aa\u9b54\u624b\u6cd5\u6765\u8bfb\u53d6\u8bb0\u5fc6\uff0c\u53c8\u5728\u7948\u7977\u7684\u65f6\u5019\u8bf4\u6076\u9b54\u8bed\u3002\u6885\u83f2\u65af\u548c\u6b23\u5e03\u4e5f\u90fd\u66fe\u7ecf\u63d0\u53ca\uff0c\u8bf4\u6c83\u91d1\u5973\u795e\u5728\u52a8\u8361\u5e74\u4ee3\u4e2d\u5931\u8e2a\uff0c\u5176\u5b9e\u662f\u8bef\u5165\u6df1\u6e0a\uff0c\u88ab\u683c\u62c9\u5179\u7279\u56da\u7981\u4e86\u5341\u51e0\u5e74\u3002\u800c\u4e14\u5973\u795e\u81ea\u4ece\u524d\u5e74\u56de\u5f52\u4e4b\u540e\uff0c\u98ce\u683c\u5927\u6709\u6539\u53d8\u3002\u5f88\u591a\u4eba\u90fd\u6000\u7591\u5979\u662f\u548c\u683c\u62c9\u5179\u7279\u8fbe\u6210\u4e86\u4ec0\u4e48\u534f\u8bae\uff0c\u79d8\u5bc6\u7ed3\u76df\uff0c\u53ea\u662f\u6ca1\u786e\u51ff\u8bc1\u636e\u3002</p><p>\u6ca1\u6709\u786e\u51ff\u8bc1\u636e\uff0c\u90a3\u53ea\u662f\u4e0d\u80fd\u516c\u5f00\u5ba3\u5224\uff0c\u4e0d\u5f71\u54cd\u79c1\u4e0b\u8ba4\u5b9a\u3002\u8fd9\u4e48\u591a\u7ebf\u7d22\u7efc\u5408\u4ea4\u96c6\uff0c\u5018\u82e5\u743c\u6069\u8fd8\u4e0d\u80fd\u5f97\u51fa\u7ed3\u8bba\uff0c\u90a3\u771f\u662f\u4e0d\u7528\u6df7\u4e86\u3002\u5177\u4f53\u7684\u8be6\u60c5\u5012\u662f\u4e0d\u6e05\u695a\uff0c\u6709\u53ef\u80fd\u662f\u5973\u795e\u548c\u683c\u62c9\u5179\u7279\u8fbe\u6210\u534f\u8bae\uff0c\u7b7e\u4e86\u4ec0\u4e48\u4e0d\u5e73\u7b49\u6761\u7ea6\uff0c\u6362\u53d6\u81ea\u7531\uff1b\u4e5f\u6709\u53ef\u80fd\u662f\u8fd9\u4e00\u5973\u795e\u4e00\u6076\u9b54\u60fa\u60fa\u76f8\u60dc\uff0c\u901a\u529b\u5408\u4f5c\uff0c\u643a\u624b\u5171\u521b\u5927\u4e1a\uff1b\u5f53\u7136\u4e86\uff0c\u4ece\u683c\u62c9\u5179\u7279\u8352\u6deb\u597d\u8272\u3001\u7537\u5973\u901a\u5403\u7684\u54c1\u884c\u6765\u5224\u65ad\uff0c\u5973\u795e\u88ab\u4ed6\u56da\u7981\u8c03\u6559\uff0c\u8eab\u5fc3\u6ca6\u9677\uff0c\u4fef\u9996\u79f0\u81e3\u7684\u53ef\u80fd\u6027\u4e5f\u662f\u5f88\u9ad8\u7684\u3002</p><p>\u65e2\u7136\u60f3\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u90a3\u662f\u5426\u53ef\u4ee5\u501f\u6b64\u505a\u6587\u7ae0\uff0c\u6765\u5b8c\u6210\u7ef4\u5eb7\u5c3c\u4e9a\u7ed9\u51fa\u7684\u8fd9\u4e2a\u9ebb\u70e6\u4efb\u52a1\u5462\u3002</p><p>\u743c\u6069\u521a\u624d\u548c\u838e\u73de\u514b\u5bf9\u8bdd\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5728\u65c1\u8fb9\u4e5f\u542c\u51fa\u4e86\u4e9b\u7aef\u502a\uff0c\u5979\u81ea\u7136\u662f\u542c\u8bf4\u8fc7\u6c83\u91d1\u548c\u683c\u62c9\u5179\u7279\u52fe\u7ed3\u8fd9\u79cd\u4f20\u95fb\u7684\uff0c\u53ea\u662f\u4e0d\u66fe\u6df1\u4fe1\uff0c\u4e5f\u4e0d\u77e5\u9053\u838e\u73de\u514b\u7684\u6765\u5386\uff0c\u6240\u4ee5\u6ca1\u60f3\u5230\u8fc7\u8fd9\u65b9\u9762\u3002\u201c\u4f60\u7684\u610f\u601d\u662f\uff0c\u8bf7\u683c\u62c9\u5179\u7279\u7ed9\u6c83\u91d1\u6559\u4f1a\u4e0b\u4ee4\uff0c\u534f\u52a9\u6211\u4eec\u6740\u6389\u82cf\u4f26\u7267\u5e08\uff1f\u201d\u5979\u6709\u4e9b\u4e0d\u592a\u786e\u4fe1\u5730\u95ee\u3002</p><p>\u201c\u4e0d\u7528\u534f\u52a9\uff0c\u53ea\u8981\u9ed8\u8bb8\u5c31\u884c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u8981\u90a3\u5bb6\u7f8e\u5bb9\u9662\u8ba9\u6211\u4eec\u6084\u6084\u6f5c\u5165\uff0c\u52a8\u624b\u6740\u4eba\u7684\u4e8b\u60c5\u81ea\u7136\u6211\u4eec\u6765\u505a\uff0c\u6c83\u91d1\u6559\u4f1a\u9876\u591a\u8d1f\u4e00\u4e2a\u9632\u536b\u4e0d\u4e25\u7684\u8d23\u4efb\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u201d\u838e\u73de\u514b\u72b9\u8c6b\uff0c\u201c\u683c\u62c9\u5179\u7279\u51ed\u4ec0\u4e48\u5e2e\u4f60\uff1f\u201d</p></article>"}}}]);
