(window.webpackJsonp=window.webpackJsonp||[]).push([[1604],{1627:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e09\u8282 \u806a\u660e\u4eba\u548c\u738b\u8005",content:"<article><h2>\u7b2c\u4e94\u5341\u4e09\u8282 \u806a\u660e\u4eba\u548c\u738b\u8005</h2><p>\u683c\u62c9\u5179\u7279\u51ed\u4ec0\u4e48\u8981\u5e2e\u743c\u6069\uff0c\u8fd9\u662f\u4e2a\u597d\u95ee\u9898\u3002\u5176\u5b9e\u8981\u8bf4\u8d77\u6765\uff0c\u53cc\u65b9\u7684\u5173\u7cfb\u4e00\u5ea6\u8fd8\u7b97\u4e0d\u9519\uff0c\u7136\u800c\u9752\u94dc\u57ce\u5821\u4e00\u6218\uff0c\u743c\u6069\u5728\u6700\u540e\u5173\u5934\u628a\u795e\u8eaf\u9b54\u50cf\u9001\u8fdb\u4e86\u65ad\u57df\u9547\uff0c\u72e0\u72e0\u9634\u4e86\u683c\u62c9\u5179\u7279\u4e00\u628a\u3002\u65ad\u57df\u9547\u867d\u7136\u4e0d\u662f\u683c\u62c9\u5179\u7279\u7684\u5927\u672c\u8425\uff0c\u4f46\u81f3\u5c11\u4e5f\u662f\u4e2a\u5206\u90e8\u3002\u4ee5\u795e\u8eaf\u9b54\u50cf\u7684\u5a01\u80fd\uff0c\u8981\u6bc1\u6389\u65ad\u57df\u9547\u4e0d\u592a\u53ef\u80fd\uff0c\u6bd5\u7adf\u6709\u7ea2\u8272\u5bff\u8863\uff08\u683c\u62c9\u5179\u7279\uff09\u4eb2\u81ea\u5750\u9547\uff0c\u4f46\u5fc5\u5b9a\u4e5f\u9020\u6210\u4e86\u4e0d\u5c0f\u7684\u7834\u574f\u3002</p><p>\u743c\u6069\u628a\u795e\u8eaf\u9b54\u50cf\u9001\u53bb\u65ad\u57df\u9547\u4e4b\u540e\uff0c\u8c03\u5934\u5c31\u8d70\uff0c\u5f53\u5929\u4fbf\u56de\u5230\u4e86\u7269\u8d28\u754c\uff0c\u6015\u7684\u5c31\u662f\u88ab\u683c\u62c9\u5179\u7279\u524d\u6765\u8ffd\u6740\u3002\u4ee5\u5e38\u7406\u6765\u8bf4\uff0c\u5403\u4e86\u8fd9\u6837\u7684\u95f7\u4e8f\uff0c\u4e0a\u4e86\u8fd9\u6837\u7684\u6076\u5f53\uff0c\u5018\u82e5\u683c\u62c9\u5179\u7279\u4e0d\u6253\u7b97\u62a5\u590d\u7684\u8bdd\uff0c\u90a3\u4ed6\u5c31\u4e0d\u662f\u6076\u9b54\uff0c\u800c\u662f\u5723\u4eba\u4e86\u3002\u53ea\u4e0d\u8fc7\u743c\u6069\u5982\u4eca\u5728\u7269\u8d28\u754c\uff0c\u6076\u9b54\u5982\u679c\u60f3\u8981\u524d\u6765\uff0c\u5b9e\u529b\u4f1a\u88ab\u4e25\u91cd\u538b\u5236\uff0c\u98ce\u9669\u5f88\u9ad8\uff0c\u4ee3\u4ef7\u5f88\u5927\uff0c\u683c\u62c9\u5179\u7279\u627e\u4e0d\u5230\u673a\u4f1a\u4e0b\u624b\uff0c\u4e5f\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u4e86\u89e3\u5185\u60c5\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u838e\u73de\u514b\u5374\u662f\u5168\u7a0b\u966a\u540c\uff0c\u5bf9\u5176\u4e2d\u7684\u7ecf\u5386\u66f2\u6298\u4e00\u6e05\u4e8c\u695a\uff0c\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u5f53\u5979\u542c\u5230\u743c\u6069\u60f3\u8bf7\u683c\u62c9\u5179\u7279\u5e2e\u5fd9\u7684\u6253\u7b97\u540e\uff0c\u624d\u4f1a\u83ab\u540d\u60ca\u8be7\u3002\u4e0d\u4e45\u524d\u624d\u521a\u521a\u628a\u522b\u4eba\u7684\u5206\u57fa\u5730\u7ed9\u62c6\u4e86\uff0c\u73b0\u5728\u5c45\u7136\u53c8\u8dd1\u8fc7\u53bb\u6c42\u52a9\uff0c\u771f\u5f53\u683c\u62c9\u5179\u7279\u662f\u8036\u7a23\u4e48\uff0c\u88ab\u4eba\u6253\u4e86\u5de6\u8138\uff0c\u53c8\u628a\u53f3\u8138\u4f38\u8fc7\u53bb\u3002</p><p>\u201c\u54e6\uff0c\u6211\u4e5f\u6ca1\u6709\u591a\u5c11\u628a\u63e1\u5566\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd9\u603b\u662f\u4e00\u6761\u8def\uff0c\u53ef\u4ee5\u8bd5\u8bd5\u561b\uff0c\u5c31\u7b97\u4e0d\u884c\u4e5f\u6ca1\u4ec0\u4e48\u635f\u5931\u3002\u5982\u679c\u80fd\u6210\u529f\uff0c\u90a3\u4e00\u5207\u9ebb\u70e6\u5c31\u8fce\u5203\u800c\u89e3\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\u3002\u201d</p><p>\u201c\u6211\u89c9\u5f97\u4f60\u8fd9\u662f\u5728\u5f02\u60f3\u5929\u5f00\u2026\u2026\u201d</p><p>\u795e\u5999\u521b\u610f\u4e5f\u597d\uff0c\u5f02\u60f3\u5929\u5f00\u4e5f\u7f62\uff0c\u743c\u6069\u65e2\u7136\u575a\u6301\uff0c\u838e\u73de\u514b\u4e5f\u53ea\u80fd\u540c\u610f\u3002\u81f3\u4e8e\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5979\u5728\u542c\u743c\u6069\u7b80\u5355\u89e3\u91ca\u4e86\u4e8b\u60c5\u7f18\u7531\u4e4b\u540e\u5927\u529b\u8d5e\u6210\uff0c\u773c\u4e2d\u7b11\u610f\u9887\u6709\u4e9b\u5e78\u707e\u4e50\u7978\u7684\u5473\u9053\uff0c\u5927\u6982\u662f\u5f88\u60f3\u770b\u5230\u743c\u6069\u88ab\u66b4\u6012\u7684\u683c\u62c9\u5179\u7279\u7834\u53e3\u5927\u9a82\u7684\u60c5\u5f62\u3002</p><p>\u201c\u5514\uff0c\u90a3\u4f60\u6050\u6015\u5c31\u8981\u5931\u671b\u4e86\uff0c\u90a3\u4e2a\u516d\u6307\u5f3a\u8feb\u75c7\u5411\u6765\u81ea\u8be9\u662f\u6700\u50cf\u9b54\u9b3c\u7684\u6076\u9b54\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u51b7\u9759\u4ece\u5bb9\u4f18\u96c5\u98ce\u5ea6\uff0c\u522b\u8bf4\u6211\u53ea\u662f\u7ed9\u4ed6\u6dfb\u4e86\u70b9\u5c0f\u9ebb\u70e6\uff0c\u5c31\u7b97\u662f\u628a\u65ad\u57df\u9547\u751a\u81f3\u628a\u4ed6\u7684\u94f6\u5bab\u62c6\u4e86\uff0c\u4ed6\u4e5f\u7167\u6837\u4f1a\u7b11\u563b\u563b\u5730\u548c\u4f60\u5750\u4e0b\u6765\u559d\u5496\u5561\u804a\u5929\u3002\u201d</p><p>\u8fd9\u4e0e\u5176\u8bf4\u662f\u683c\u62c9\u5179\u7279\u7684\u53cd\u5e94\uff0c\u4e0d\u5982\u8bf4\u662f\u743c\u6069\u7684\u5355\u65b9\u9762\u671f\u671b\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u7136\u662f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u201c\u662f\u5417\uff1f\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4f60\u5e72\u561b\u4e0d\u518d\u53bb\u6df1\u6e0a\u627e\u4ed6\u559d\u5496\u5561\uff0c\u8981\u8eb2\u5728\u8fd9\u7269\u8d28\u754c\u5462\u3002\u201d</p><p>\u201c\u9996\u5148\uff0c\u6211\u4e0d\u662f\u8eb2\u5728\u7269\u8d28\u754c\u3002\u6211\u662f\u51e1\u4eba\uff0c\u8fd9\u91cc\u662f\u6211\u5bb6\uff0c\u6ca1\u4e8b\u5e72\u53bb\u6df1\u6e0a\u505a\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u4e49\u6b63\u8bcd\u4e25\u5730\u8bf4\uff0c\u201c\u5176\u6b21\uff0c\u6211\u521a\u624d\u8bf4\u7684\u60c5\u5f62\uff0c\u662f\u6307\u4ed6\u73b0\u5728\u4e0d\u80fd\u62ff\u6211\u600e\u4e48\u6837\uff0c\u6240\u4ee5\u624d\u4f1a\u8bb2\u98ce\u5ea6\u3002\u5982\u679c\u6211\u5c31\u5728\u4ed6\u9762\u524d\uff0c\u90a3\u5c31\u2026\u2026\u90a3\u5c31\u662f\u4ed6\u5f88\u8bb2\u98ce\u5ea6\u5730\u6765\u63d0\u5200\u780d\u6740\u6211\u4e86\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u683c\u683c\u5a07\u7b11\u3002</p><p>\u8054\u7cfb\u683c\u62c9\u5179\u7279\u5e76\u4e0d\u96be\uff0c\u50cf\u4ed6\u8fd9\u79cd\u5927\u6076\u9b54\uff0c\u5728\u7269\u8d28\u754c\u6709\u4e0d\u5c11\u51e1\u4eba\u5d07\u62dc\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u795e\u7947\uff0c\u5374\u4e5f\u80fd\u7b97\u662f\u4e2a\u201c\u4f2a\u795e\u201d\u4e86\uff0c\u901a\u8fc7\u4e00\u5b9a\u7684\u4eea\u5f0f\u3001\u796d\u7940\uff0c\u5c31\u80fd\u548c\u4ed6\u53d6\u5f97\u8054\u7cfb\u3002\u5f53\u7136\uff0c\u683c\u62c9\u5179\u7279\u662f\u5426\u56de\u5e94\uff0c\u662f\u5426\u613f\u610f\u7406\u776c\uff0c\u90a3\u5c31\u8981\u770b\u4ed6\u7684\u5fc3\u60c5\u3002\u5177\u4f53\u600e\u4e48\u505a\uff0c\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u77e5\u9053\uff0c\u838e\u73de\u514b\u662f\u5f88\u6e05\u695a\u7684\u3002</p><p>\u201c\u683c\u62c9\u5179\u7279\u7684\u796d\u7940\u4eea\u5f0f\u5f97\u8981\u5728\u534a\u591c\u4e3e\u884c\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u73b0\u5728\u6ca1\u6cd5\u8054\u7cfb\u3002\u201d</p><p>\u201c\u90a3\u5c31\u7b49\u534a\u591c\u518d\u8bf4\u5427\u3002\u201d</p><p>\u743c\u6069\u5728\u6c83\u91d1\u6b65\u9053\u8f6c\u60a0\u4e86\u4e00\u4e0a\u5348\uff0c\u7136\u540e\u9047\u5230\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u8ddf\u968f\u5979\u6765\u6b64\uff0c\u8bf4\u4e86\u8fd9\u4e48\u4e45\u7684\u8bdd\uff0c\u770b\u770b\u65f6\u95f4\u5df2\u7ecf\u5feb\u5230\u508d\u665a\u4e86\u3002\u4e3a\u514d\u6885\u83f2\u65af\u751f\u7591\uff0c\u743c\u6069\u5148\u8fd4\u56de\u9152\u5e97\uff0c\u838e\u73de\u514b\u5219\u53bb\u6536\u96c6\u4e00\u4e9b\u796d\u7940\u4eea\u5f0f\u5fc5\u5907\u7684\u9053\u5177\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u2026\u2026\u5728\u5bb6\u4f11\u606f\u3002</p><p>\u56de\u5230\u9540\u91d1\u73ab\u7470\u9152\u5e97\uff0c\u5df2\u7ecf\u662f\u665a\u971e\u6ee1\u5929\uff0c\u6885\u83f2\u65af\u548c\u8299\u857e\u72c4\u5e26\u7740\u51db\u4e5f\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u4e0d\u7528\u8be2\u95ee\uff0c\u770b\u795e\u60c5\u5c31\u77e5\u9053\u662f\u65e0\u529f\u800c\u8fd4\u3002\u743c\u6069\u4fe1\u53e3\u80e1\u626f\uff0c\u8bf4\u4eca\u5929\u6709\u4e00\u5bb6\u9b54\u6cd5\u5546\u5e97\u7684\u8001\u677f\u6216\u8bb8\u6709\u95e8\u8def\uff0c\u7b54\u5e94\u5e2e\u5fd9\u627e\u627e\uff0c\u53ef\u80fd\u6709\u5e0c\u671b\uff0c\u660e\u5929\u518d\u53bb\u95ee\u95ee\u770b\u3002</p><p>\u8fd9\u4e2a\u8c0e\u8a00\u5e76\u4e0d\u9ad8\u660e\uff0c\u6885\u83f2\u65af\u5982\u679c\u8f83\u771f\u8d77\u6765\uff0c\u7a77\u6839\u7a76\u5e95\u4e00\u95ee\uff0c\u5f88\u5bb9\u6613\u5c31\u53d1\u73b0\u7834\u7efd\uff0c\u4f46\u5979\u548c\u51db\u60c5\u540c\u59d0\u59b9\uff0c\u611f\u60c5\u81f3\u6df1\uff0c\u5982\u4eca\u51db\u5f04\u6210\u8fd9\u526f\u6837\u5b50\uff0c\u6885\u83f2\u65af\u7126\u8651\u4e0d\u5b89\uff0c\u5fc3\u795e\u70e6\u4e71\uff0c\u4e5f\u6ca1\u7a7a\u53bb\u4ed4\u7ec6\u601d\u7d22\u771f\u5047\uff0c\u88ab\u743c\u6069\u6210\u529f\u542b\u7cca\u8fc7\u53bb\u3002</p><p>\u6309\u7167\u9884\u5148\u7ea6\u5b9a\uff0c\u534a\u591c\u65f6\u5206\uff0c\u743c\u6069\u6765\u5230\u963f\u65af\u5361\u7279\u62c9\u57ce\u5916\u7684\u4e00\u5904\u50fb\u9759\u6811\u6797\u4e2d\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u62b5\u8fbe\uff0c\u5e03\u7f6e\u597d\u4e86\u7b80\u5355\u7684\u796d\u575b\u548c\u4e00\u4e9b\u9053\u5177\uff0c\u56e0\u4e3a\u53ea\u662f\u6253\u7b97\u548c\u683c\u62c9\u5179\u7279\u8054\u7cfb\uff0c\u4e0d\u662f\u5f53\u771f\u796d\u7940\u6216\u8005\u7948\u6c42\u529b\u91cf\u4e4b\u7c7b\uff0c\u4e5f\u65e0\u9700\u796d\u54c1\uff0c\u5426\u5219\u53ea\u6015\u8fd8\u8981\u6293\u51e0\u4e2a\u4eba\u6765\u6740\u6389\u624d\u884c\u3002</p><p>\u6c99\u6c99\u7684\u811a\u6b65\u8f7b\u54cd\uff0c\u6811\u6797\u4e2d\u8d70\u51fa\u4e00\u4e2a\u7a88\u7a95\u8eab\u5f71\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u5230\u4e86\u3002</p><p>\u56e0\u4e3a\u662f\u6df1\u591c\uff0c\u53c8\u662f\u50fb\u9759\u65e0\u4eba\u4e4b\u5904\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u6ca1\u6709\u7528\u6cd5\u672f\u4f2a\u88c5\u9762\u8c8c\uff0c\u4f46\u4f9d\u65e7\u5c06\u5168\u8eab\u88f9\u5728\u957f\u888d\u548c\u6597\u7bf7\u91cc\u3002\u5979\u7684\u201c\u4f8d\u7236\u6597\u7bf7\u201d\u662f\u7236\u4eb2\u7684\u9057\u7269\uff0c\u4e0d\u4f46\u575a\u6108\u7cbe\u94a2\uff0c\u6709\u6297\u9b54\u4e4b\u80fd\uff0c\u800c\u4e14\u8ba9\u7a7f\u6234\u8005\u4e0d\u5fc5\u754f\u60e7\u65e5\u5149\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4f5c\u4e3a\u5353\u5c14\uff0c\u6b63\u662f\u9760\u4e86\u5b83\u624d\u80fd\u5728\u5730\u8868\u4e16\u754c\u7684\u767d\u663c\u968f\u610f\u884c\u8d70\u3002\u5f53\u65e5\u74dc\u7406\u5fb7\u65af\u57ce\u5bb6\u65cf\u5185\u6218\uff0c\u8fd9\u4ef6\u6597\u7bf7\u88ab\u7b2c\u56db\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\u514b\u52b3\u62d3\u7528\u9633\u708e\u5c04\u7ebf\u70e7\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u6709\u6240\u7834\u635f\uff0c\u4f46\u57fa\u672c\u529f\u80fd\u4ecd\u5728\u3002</p><p>\u201c\u4e24\u4f4d\u5df2\u7ecf\u5230\u4e86\u554a\u3002\u201d\u5979\u60a0\u95f2\u5730\u6253\u7740\u62db\u547c\uff0c\u773c\u5149\u968f\u610f\u63a0\u8fc7\uff0c\u770b\u5230\u743c\u6069\u65f6\u5374\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u968f\u5373\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u6765\u3002</p><p>\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u800c\u4e14\u4ed6\u611f\u89c9\u8fd9\u7b11\u5bb9\u6709\u70b9\u719f\u6089\uff0c\u597d\u50cf\u521a\u624d\u838e\u73de\u514b\u770b\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u9732\u51fa\u8fd9\u526f\u5947\u602a\u795e\u60c5\uff0c\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u610f\u601d\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u89c9\u5f97\u4f60\u771f\u662f\u8f9b\u82e6\uff0c\u201d\u5353\u5c14\u5c11\u5973\u7b11\u610f\u76c8\u76c8\uff0c\u201c\u773c\u770b\u5c31\u8981\u51c6\u5907\u7740\u9762\u5bf9\u5927\u6076\u9b54\uff0c\u8fd8\u8981\u5148\u6ee1\u8db3\u8eab\u8fb9\u7684\u7f8e\u4eba\u513f\u3002\u662f\u54ea\u4e00\u4f4d\u5462\uff1f\u90a3\u4f4d\u5723\u6b66\u58eb\uff0c\u8fd8\u662f\u90a3\u5bf9\u59d0\u59b9\u82b1\uff0c\u6216\u8005\u5979\u4eec\u4e00\u8d77\uff1f\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u91ca\u7136\u3002\u4ed6\u56e0\u4e3a\u8981\u534a\u591c\u6e9c\u51fa\u6765\uff0c\u4f46\u8fd9\u4e8b\u60c5\u4e0d\u80fd\u8ba9\u6885\u83f2\u65af\u77e5\u9053\uff0c\u53c8\u6ca1\u529e\u6cd5\u7ed9\u5979\u4e0b\u50ac\u7720\u672f\u4e4b\u7c7b\uff0c\u65e0\u5948\u4e4b\u4e0b\u53ea\u80fd\u81ea\u5df1\u4e0a\u9635\uff0c\u8db3\u8db3\u641e\u4e86\u4e09\u6b21\uff0c\u914d\u5408\u5a9a\u836f\u7684\u6548\u529b\uff0c\u603b\u7b97\u624d\u8ba9\u5979\u6c89\u6c89\u7761\u8fc7\u53bb\uff0c\u7136\u540e\u5c31\u5306\u5fd9\u8d76\u6765\u4e86\u3002\u7537\u4eba\u521a\u505a\u5b8c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u591a\u591a\u5c11\u5c11\u603b\u6709\u4e9b\u8ff9\u8c61\uff0c\u4f55\u51b5\u8fd8\u641e\u5f97\u8fd9\u4e48\u6fc0\u70c8\uff0c\u838e\u73de\u514b\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u90fd\u4e0d\u662f\u6e05\u7eaf\u5904\u5973\uff0c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u773c\u5149\u8001\u5230\uff0c\u81ea\u7136\u5c31\u770b\u4e86\u51fa\u6765\u3002</p><p>\u82e5\u662f\u9762\u5bf9\u522b\u4eba\uff0c\u743c\u6069\u6216\u8bb8\u8fd8\u4f1a\u6709\u51e0\u5206\u5c34\u5c2c\uff0c\u7136\u800c\u773c\u524d\u4e00\u4e2a\u662f\u5353\u5c14\uff0c\u4e00\u4e2a\u662f\u9b45\u9b54\uff0c\u90fd\u4e0d\u662f\u4ec0\u4e48\u6b63\u4e49\u4eba\u58eb\uff0c\u5584\u826f\u5c11\u5973\uff0c\u54ea\u6709\u4ec0\u4e48\u5632\u7b11\u7684\u8d44\u683c\u3002\u73b0\u5728\u8981\u529e\u6b63\u4e8b\uff0c\u6682\u65f6\u6ca1\u5fc3\u60c5\uff0c\u7b49\u4e8b\u60c5\u529e\u5b8c\u4e86\uff0c\u7d22\u6027\u628a\u5979\u4eec\u4e00\u8d77\u5f04\u4e0a\u5e8a\u53bb\u73a9\u53cc\u98de\uff0c\u5c31\u5f53\u662f\u5e86\u795d\u4e86\u3002\u8bf4\u8d77\u6765\uff0c\u8fd9\u4e24\u4eba\u81ea\u5df1\u90fd\u4e0a\u8fc7\uff0c\u4f46\u5374\u90fd\u73a9\u5f97\u4e0d\u591a\uff0c\u5c24\u5176\u662f\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u6b63\u597d\u91cd\u6e29\u4e00\u4e0b\u6ecb\u5473\u3002</p><p>\u6b63\u81ea\u505a\u7f8e\u68a6\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u5c06\u4e00\u5207\u5e03\u7f6e\u597d\uff0c\u62ac\u5934\u770b\u770b\u591c\u8272\uff0c\u4eea\u5f0f\u65f6\u95f4\u4e5f\u5dee\u4e0d\u591a\u5230\u4e86\uff0c\u7b80\u964b\u796d\u575b\u7684\u6b63\u4e2d\u8dc3\u8d77\u4e00\u56e2\u9752\u84dd\u8272\u90aa\u708e\uff0c\u743c\u6069\u81ea\u9b45\u9b54\u624b\u4e2d\u63a5\u8fc7\u4e00\u4e2a\u74f6\u5b50\uff0c\u5c06\u5176\u4e2d\u7684\u94f6\u7070\u8272\u7c89\u672b\u6d12\u8fdb\u706b\u4e2d\uff0c\u4e73\u767d\u8272\u70df\u96fe\u5f25\u6563\u8d77\u6765\uff0c\u5939\u6742\u7740\u4ee4\u4eba\u9ebb\u9189\u7684\u9999\u6c14\uff0c\u6162\u6162\u5347\u817e\uff0c\u6700\u7ec8\u53d8\u5e7b\u51fa\u4e00\u526f\u753b\u9762\u3002</p><p>\u80cc\u666f\u50cf\u662f\u4e00\u5ea7\u534e\u7f8e\u793c\u5802\uff0c\u5730\u677f\u56db\u58c1\u90fd\u662f\u7528\u95ea\u70c1\u94f6\u8272\u5149\u6cfd\u7684\u767d\u8272\u7389\u77f3\u780c\u6210\uff0c\u5176\u4e2d\u9576\u5d4c\u7740\u5343\u767e\u9762\u5927\u5927\u5c0f\u5c0f\u7684\u94f6\u955c\uff0c\u7480\u74a8\u593a\u76ee\uff0c\u4ea4\u76f8\u8f89\u6620\uff0c\u5404\u79cd\u4e0d\u540c\u7684\u5f71\u50cf\u5728\u5176\u4e2d\u4ea4\u9519\u53d8\u5e7b\uff0c\u6d41\u52a8\u4e0d\u606f\u3002\u5728\u753b\u9762\u7684\u6b63\u4e2d\uff0c\u662f\u4e00\u5ea7\u9576\u6ee1\u73e0\u5b9d\u7684\u5e9e\u5927\u738b\u5ea7\uff0c\u683c\u62c9\u5179\u7279\u6b63\u5750\u5728\u5176\u4e2d\uff0c\u5de6\u624b\u968f\u610f\u5730\u642d\u5728\u6276\u624b\u4e0a\uff0c\u53f3\u624b\u7aef\u7740\u4e00\u652f\u9ad8\u811a\u9152\u676f\uff0c\u67b6\u7740\u817f\uff0c\u8eab\u4e0a\u7a7f\u7684\u5e76\u4e0d\u662f\u743c\u6069\u4ee5\u524d\u89c1\u8fc7\u7684\u5e1d\u738b\u888d\u670d\uff0c\u4e5f\u4e0d\u662f\u94f6\u8272\u9501\u7532\uff0c\u800c\u662f\u886c\u886b\u3001\u957f\u88e4\u3001\u9a6c\u7532\u80cc\u5fc3\uff0c\u957f\u53d1\u5728\u8111\u540e\u68b3\u6210\u4e00\u4e2a\u9a6c\u5c3e\uff0c\u770b\u4e0a\u53bb\u65e2\u5e72\u8106\u5229\u843d\u53c8\u6d12\u8131\uff0c\u8fd8\u5e26\u4e86\u51e0\u5206\u843d\u62d3\u7684\u6d6a\u5b50\u6c14\u606f\uff0c\u914d\u4e0a\u90a3\u4fca\u7f8e\u7684\u9762\u5bb9\uff0c\u54ea\u91cc\u50cf\u662f\u5a01\u9707\u6df1\u6e0a\u7684\u5927\u6076\u9b54\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u4e2a\u82b1\u82b1\u516c\u5b50\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u743c\u6069\uff0c\u201d\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u683c\u62c9\u5179\u7279\u5c45\u7136\u4e3b\u52a8\u5f00\u53e3\u6253\u62db\u547c\uff0c\u201c\u597d\u4e45\u4e0d\u89c1\uff0c\u6700\u8fd1\u8fc7\u5f97\u5982\u4f55\u3002\u201d</p><p>\u8001\u670b\u53cb\u7684\u8bed\u6c14\uff0c\u5145\u6ee1\u5584\u610f\u548c\u70ed\u60c5\u7684\u95ee\u5019\uff0c\u8ba9\u838e\u73de\u514b\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u51fa\u73b0\u5e7b\u89c9\uff0c\u5979\u65c1\u8fb9\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u5f53\u573a\u6014\u4f4f\uff0c\u552f\u6709\u743c\u6069\u6700\u662f\u9547\u5b9a\uff0c\u5fae\u5fae\u8eac\u8eab\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u201c\u53c8\u89c1\u9762\u4e86\uff0c\u965b\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u60a8\u770b\u8d77\u6765\u6c14\u8272\u4e0d\u9519\u3002\u201d</p><p>\u201c\u4e00\u822c\u4e00\u822c\uff0c\u201d\u683c\u62c9\u5179\u7279\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8f6c\u52a8\u7740\u624b\u4e0a\u7684\u9152\u676f\uff0c\u201c\u6700\u8fd1\u628a\u5965\u5580\u65af\u90a3\u4e2a\u80a5\u5c71\u7f8a\u72e0\u72e0\u63cd\u4e86\u4e00\u987f\uff0c\u5fc3\u60c5\u6bd4\u8f83\u6109\u5feb\u5427\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u606d\u559c\u606d\u559c\u3002\u201d</p><p>\u683c\u62c9\u5179\u7279\u5475\u5475\u4e00\u7b11\u3002</p><p>\u201c\u5bd2\u6684\u7684\u8bdd\u5c31\u5230\u6b64\u4e3a\u6b62\u5427\uff0c\u743c\u6069\uff0c\u4f60\u7279\u5730\u627e\u6211\uff0c\u603b\u4e0d\u4f1a\u662f\u5c31\u4e3a\u4e86\u8bf4\u51e0\u53e5\u795d\u8d3a\u5427\uff0c\u201d\u4ed6\u653e\u4e0b\u9152\u676f\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u671b\u7740\u743c\u6069\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u8fd8\u771f\u6709\u4e9b\u597d\u5947\uff0c\u4f60\u4e3a\u4f55\u8fd8\u6562\u8fd9\u4e48\u9547\u5b9a\u5730\u7ad9\u5728\u6211\u9762\u524d\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5927\u6982\u56e0\u4e3a\u6211\u4eec\u5176\u5b9e\u8ddd\u79bb\u5f88\u8fdc\u7684\u7f18\u6545\u5427\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u683c\u62c9\u5179\u7279\u53cd\u95ee\uff0c\u201c\u5982\u679c\u6211\u60f3\u6740\u4f60\u7684\u8bdd\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u4f60\u8ba4\u4e3a\u4f60\u771f\u7684\u80fd\u8eb2\u5f97\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5f88\u96be\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u5982\u679c\u60a8\u771f\u7684\u4e0d\u60dc\u4e00\u5207\u4ee3\u4ef7\uff0c\u53ea\u4e3a\u4e86\u8981\u6740\u6211\u7684\u8bdd\uff0c\u90a3\u6211\u60f3\u6211\u5e94\u8be5\u662f\u6b7b\u5b9a\u4e86\u2014\u2014\u524d\u63d0\u662f\uff0c\u60a8\u613f\u610f\u4ed8\u51fa\u4ee3\u4ef7\u5417\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u201d\u683c\u62c9\u5179\u7279\u51b7\u7b11\uff0c\u201c\u4f60\u8fd9\u662f\u5728\u5a01\u80c1\u6211\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u60a8\u8bef\u4f1a\u4e86\uff0c\u201d\u4ed6\u8bda\u6073\u5730\u8bf4\uff0c\u201c\u6211\u6700\u656c\u4f69\u806a\u660e\u4eba\uff0c\u800c\u60a8\u6beb\u65e0\u7591\u95ee\u662f\u6211\u6240\u656c\u4f69\u7684\u5bf9\u8c61\u4e4b\u4e00\u3002\u806a\u660e\u4eba\uff0c\u4f1a\u51c6\u786e\u5730\u6743\u8861\u5229\u5bb3\uff0c\u660e\u667a\u5730\u628a\u63e1\u5206\u5bf8\uff0c\u9009\u62e9\u6700\u6709\u5229\u4e8e\u81ea\u5df1\u7684\u4f5c\u6cd5\uff0c\u800c\u4e0d\u4f1a\u88ab\u4efb\u4f55\u65e0\u804a\u7684\u60c5\u7eea\u6240\u5de6\u53f3\u3002\u5bf9\u4e8e\u60a8\u800c\u8a00\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u5c0f\u89d2\u8272\uff0c\u6216\u8bb8\u7b97\u4e0d\u4e0a\u65e0\u8db3\u8f7b\u91cd\uff0c\u6216\u8bb8\u6709\u90a3\u4e48\u51e0\u5206\u4ef7\u503c\uff0c\u4f46\u76f8\u6bd4\u8d77\u60a8\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u76f8\u6bd4\u8d77\u60a8\u6240\u8ffd\u6c42\u7684\u76ee\u6807\uff0c\u90a3\u5c31\u6e3a\u4e0d\u8db3\u9053\uff0c\u4e0d\u503c\u4e00\u63d0\u4e86\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u53c8\u4f55\u5fc5\u505a\u65e0\u8c13\u7684\u62c5\u5fc3\u5462\u3002\u201d</p><p>\u201c\u5047\u5982\u6211\u4e0d\u8bb2\u4efb\u4f55\u903b\u8f91\uff0c\u4e0d\u5728\u4e4e\u4efb\u4f55\u4ee3\u4ef7\uff0c\u5355\u7eaf\u5730\u5c31\u662f\u60f3\u8981\u6740\u4f60\u4e0d\u53ef\u5462\uff1f\u201d</p><p>\u201c\u5982\u679c\u90a3\u6837\uff0c\u90a3\u6211\u5c31\u66f4\u6ca1\u6709\u4ec0\u4e48\u53ef\u62c5\u5fc3\u4e86\u5440\uff0c\u201d\u743c\u6069\u5fae\u7b11\uff0c\u201c\u56e0\u4e3a\u62c5\u5fc3\u5df2\u7ecf\u6ca1\u6709\u4efb\u4f55\u610f\u4e49\uff0c\u53ea\u9700\u8981\u5766\u7136\u5730\u8fce\u63a5\uff0c\u5168\u529b\u4ee5\u8d74\u5730\u9762\u5bf9\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u683c\u62c9\u5179\u7279\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\u3002</p><p>\u201c\u6709\u70b9\u610f\u601d\uff0c\u6709\u70b9\u610f\u601d\uff0c\u201d\u4ed6\u8bf4\uff0c\u8eab\u4f53\u5fae\u5fae\u524d\u503e\uff0c\u201c\u4f60\u8bf4\u5f97\u6ca1\u9519\uff0c\u6211\u662f\u4e0d\u4f1a\u6765\u6740\u4f60\uff0c\u81f3\u5c11\u6682\u65f6\u662f\u5982\u6b64\u3002\u4f46\u8fd9\u5e76\u975e\u50cf\u4f60\u81c6\u6d4b\u7684\u90a3\u6837\uff0c\u662f\u56e0\u4e3a\u8003\u8651\u4ec0\u4e48\u4ee3\u4ef7\uff0c\u6743\u8861\u4ec0\u4e48\u5229\u5bb3\uff0c\u90a3\u4e9b\u90fd\u662f\u4e0d\u503c\u4e00\u63d0\u7684\u5e9f\u8bdd\uff0c\u53ea\u6709\u4f60\u4eec\u8fd9\u4e9b\u5c0f\u89d2\u8272\u624d\u4f1a\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u624d\u4f1a\u8fd9\u6837\u65a4\u65a4\u8ba1\u7b97\uff0c\u4ee5\u5df1\u5ea6\u4eba\u3002\u5728\u6c38\u6052\u7684\u738b\u8005\u9762\u524d\uff0c\u6240\u6709\u7684\u4e00\u5207\uff0c\u4ef7\u503c\u90fd\u662f\u76f8\u7b49\u7684\uff0c\u56e0\u4e3a\u90fd\u662f\u96f6\u3002\u6211\u53ef\u4ee5\u6d3e\u4e00\u4e2a\u708e\u9b54\u53bb\u6740\u4f60\uff0c\u5982\u679c\u6740\u4e0d\u4e86\uff0c\u90a3\u4e48\u5c31\u6d3e\u5341\u4e2a\uff0c\u5341\u4e2a\u4e0d\u884c\uff0c\u90a3\u4e48\u5c31\u4e00\u767e\u4e2a\uff0c\u4e00\u5343\u4e2a\u3002\u4f60\u4ee5\u4e3a\u6211\u4f1a\u5728\u4e4e\u5417\uff1f\u201d\u4ed6\u7a0d\u7a0d\u505c\u987f\uff0c\u6162\u6162\u555c\u996e\u7740\u676f\u4e2d\u7684\u7f8e\u9152\uff0c\u201c\u6211\u4e4b\u6240\u4ee5\u613f\u610f\u6682\u65f6\u653e\u8fc7\u4f60\uff0c\u662f\u56e0\u4e3a\u4f60\u8fd8\u6709\u70b9\u610f\u601d\u3002\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u4eba\u5f88\u591a\uff0c\u4f46\u767e\u5206\u4e4b\u4e5d\u5341\u4e5d\u70b9\u4e5d\u4ee5\u4e0a\uff0c\u90fd\u662f\u5446\u5934\u5446\u8111\u7684\u5e9f\u7269\uff0c\u611a\u7b28\u65e0\u77e5\u7684\u767d\u75f4\uff0c\u8ba9\u4eba\u671b\u800c\u751f\u538c\uff0c\u4f46\u4f60\u4e0d\u5728\u6b64\u5185\u3002\u4f60\u6709\u70b9\u5c0f\u806a\u660e\uff0c\u6709\u70b9\u4e0d\u540c\u4e8e\u5e38\u4eba\u7684\u4e1c\u897f\uff0c\u6709\u70b9\u50cf\u6a21\u50cf\u6837\u7684\u89c9\u609f\uff0c\u80fd\u5076\u5c14\u8ba9\u6211\u6709\u70b9\u60ca\u559c\uff0c\u7ed9\u8fd9\u67af\u71e5\u6c89\u95f7\u7684\u751f\u6d3b\u5e26\u6765\u70b9\u65b0\u9c9c\u4e50\u8da3\u2014\u2014\u8fd9\uff0c\u624d\u662f\u4f60\u80fd\u6d3b\u5230\u73b0\u5728\u7684\u539f\u56e0\u3002\u201d</p><p>\u743c\u6069\u6df1\u6df1\u8eac\u8eab\u3002</p><p>\u201c\u8bda\u7136\u5982\u6b64\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e3a\u6211\u524d\u9762\u6240\u8bf4\u7684\u8bdd\u800c\u9053\u6b49\u2026\u2026\u201d</p><p>\u683c\u62c9\u5179\u7279\u968f\u610f\u5730\u6325\u4e86\u6325\u624b\uff0c\u201c\u65e0\u9700\u9053\u6b49\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4ee5\u4f60\u7684\u89c1\u8bc6\uff0c\u4e5f\u53ea\u80fd\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u6211\u53ef\u4ee5\u7406\u89e3\u3002\u90a3\u4e48\uff0c\u4f60\u4eca\u5929\u6765\u627e\u6211\uff0c\u76ee\u7684\u662f\u4ec0\u4e48\u5462\uff0c\u76f4\u63a5\u8bf4\u5427\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u539f\u539f\u672c\u672c\u5c06\u4e8b\u60c5\u8bf4\u4e86\u3002\u201c\u5982\u679c\u60a8\u80fd\u6177\u6168\u63f4\u624b\uff0c\u6211\u4e0d\u80dc\u611f\u6fc0\u3002\u201d</p><p>\u201c\u4f60\u7684\u611f\u6fc0\uff0c\u5bf9\u6211\u6beb\u65e0\u4ef7\u503c\uff0c\u201d\u683c\u62c9\u5179\u7279\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u542c\u8d77\u6765\u4f3c\u4e4e\u5f88\u6709\u8da3\u3002\u770b\u5728\u4f60\u8ba9\u6211\u5fc3\u60c5\u6109\u5feb\u7684\u4efd\u4e0a\uff0c\u5e2e\u4e2a\u5fd9\u5012\u4e5f\u65e0\u4e0d\u53ef\u3002\u53ea\u4e0d\u8fc7\uff0c\u201d\u4ed6\u7684\u5507\u89d2\u5fae\u5fae\u4e0a\u6311\uff0c\u9732\u51fa\u4e00\u4e1d\u9887\u4e3a\u8010\u4eba\u5bfb\u5473\u7684\u7b11\u610f\uff0c\u201c\u6211\u5e2e\u7684\u5fd9\uff0c\u4f60\u672a\u5fc5\u6d88\u53d7\u5f97\u8d77\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e00\u6014\uff0c\u6ca1\u660e\u767d\u5bf9\u65b9\u7684\u610f\u601d\uff0c\u6b63\u8981\u8be2\u95ee\uff0c\u683c\u62c9\u5179\u7279\u5df2\u7ecf\u8f7b\u8f7b\u70b9\u5934\uff0c\u201c\u90a3\u597d\u5427\uff0c\u5c31\u8fd9\u6837\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4f1a\u901a\u77e5\u6c83\u91d1\u795e\u6bbf\uff0c\u8ba9\u4ed6\u4eec\u5168\u529b\u534f\u52a9\u4f60\u3002\u90a3\u4e48\uff0c\u518d\u89c1\u4e86\u3002\u201d</p><p>\u6076\u9b54\u6325\u4e86\u6325\u624b\uff0c\u753b\u9762\u9eef\u6de1\u4e0b\u6765\uff0c\u70df\u96fe\u6563\u53bb\uff0c\u6700\u7ec8\u4e00\u5207\u5f71\u50cf\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u548c\u683c\u62c9\u5179\u7279\u7684\u8c08\u8bdd\u5230\u6b64\u7ed3\u675f\uff0c\u4e09\u4eba\u5c06\u73b0\u573a\u75d5\u8ff9\u62b9\u53bb\uff0c\u6084\u6084\u8fd4\u56de\u57ce\u4e2d\u3002\u201c\u6ca1\u60f3\u5230\u4f60\u8fd8\u771f\u731c\u5bf9\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4ed6\u8fd8\u771f\u7b54\u5e94\u5e2e\u5fd9\uff0c\u8fd9\u7b80\u76f4\u592a\u795e\u5947\u4e86\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u968f\u53e3\u7b54\u5e94\u4e86\u4e00\u58f0\uff0c\u660e\u663e\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u7684\u6837\u5b50\uff0c\u838e\u73de\u514b\u5f88\u5947\u602a\uff0c\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u5bf9\u89c6\u4e00\u773c\uff0c\u8f7b\u8f7b\u6447\u5934\u3002\u201c\u600e\u4e48\u4e86\uff0c\u4e3b\u4eba\uff0c\u201d\u9b45\u9b54\u8be2\u95ee\uff0c\u201c\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u6709\u4e9b\u611f\u6168\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7ed3\u679c\u867d\u7136\u5982\u6211\u6240\u6599\uff0c\u4f46\u662f\u4e2d\u95f4\u7684\u8fc7\u7a0b\u5374\u5b8c\u5168\u51fa\u4e4e\u6211\u7684\u9884\u8ba1\u2026\u2026\u5176\u5b9e\uff0c\u8fd9\u6b21\u6211\u662f\u5927\u5927\u5931\u7b97\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u201c\u6211\u672c\u6765\u7684\u6253\u7b97\uff0c\u662f\u548c\u4ed6\u8c08\u5229\u5bb3\uff0c\u8c08\u8ba1\u7b97\uff0c\u8c08\u4ea4\u6613\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u683c\u62c9\u5179\u7279\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u806a\u660e\u4eba\u6ce8\u91cd\u5229\u76ca\uff0c\u61c2\u5f97\u6743\u8861\uff0c\u4e0d\u4f1a\u76f2\u76ee\u51b2\u52a8\u3002\u6211\u786e\u5b9e\u662f\u4e0a\u6b21\u9634\u4e86\u4ed6\u4e00\u628a\uff0c\u4f46\u4e0d\u7b49\u4e8e\u8bf4\u4ed6\u5c31\u4ece\u6b64\u62d2\u6211\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u8fd9\u5c31\u50cf\u68cb\u5c40\uff0c\u6211\u4eec\u4e0b\u4e86\u7b2c\u4e00\u5c40\uff0c\u6211\u8d62\u4e86\uff0c\u5360\u4e86\u70b9\u4fbf\u5b9c\uff0c\u4ed6\u4f5c\u4e3a\u4e00\u4e2a\u806a\u660e\u4eba\uff0c\u5e76\u4e0d\u4f1a\u4ece\u6b64\u5c31\u62d2\u7edd\u548c\u6211\u4e0b\u68cb\uff0c\u6070\u6070\u76f8\u53cd\u7684\u662f\uff0c\u5728\u4ed6\u6ca1\u529e\u6cd5\u76f4\u63a5\u6bb4\u6253\u6211\uff0c\u6559\u8bad\u6211\u7684\u524d\u63d0\u4e0b\uff0c\u4ed6\u4f1a\u5f88\u4e50\u610f\u7ee7\u7eed\u65b0\u7684\u68cb\u5c40\uff0c\u5bfb\u627e\u7ffb\u76d8\u7684\u673a\u4f1a\u3002\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6211\u672c\u6765\u662f\u6253\u7b97\u4ed8\u51fa\u4ec0\u4e48\u4ee3\u4ef7\uff0c\u6362\u53d6\u4ed6\u7684\u5e2e\u5fd9\uff0c\u770b\u4ed6\u5f00\u51fa\u7684\u6761\u4ef6\u6211\u80fd\u4e0d\u80fd\u63a5\u53d7\uff0c\u5f53\u7136\u4e5f\u6ca1\u62b1\u591a\u5927\u5e0c\u671b\uff0c\u53ea\u662f\u8bd5\u8bd5\u770b\u2014\u2014\u7136\u800c\u6211\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4f60\u5fd8\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u6211\u5fd8\u4e86\uff0c\u683c\u62c9\u5179\u7279\u5e76\u4e0d\u4ec5\u4ec5\u662f\u4e00\u4e2a\u806a\u660e\u4eba\uff0c\u4ed6\u8fd8\u662f\u4e2a\u738b\u8005\uff0c\u201d\u743c\u6069\u82e6\u7b11\uff0c\u201c\u4ed6\u662f\u8bde\u751f\u4e8e\u539f\u521d\u65f6\u4ee3\u7684\u5927\u6076\u9b54\uff0c\u6df1\u6e0a\u4e09\u5927\u541b\u4e3b\u4e4b\u4e00\uff0c\u8db3\u4ee5\u548c\u9ad8\u7b49\u795e\u76f8\u5339\u654c\u7684\u5b58\u5728\uff0c\u8fd1\u4e4e\u6c38\u6052\u4e0d\u673d\u7684\u738b\u8005\uff0c\u800c\u6211\u5728\u4ed6\u9762\u524d\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7b97\u3002\u8fd9\u4e2a\u9053\u7406\u6211\u672c\u6765\u662f\u77e5\u9053\u7684\uff0c\u4f46\u5374\u81ea\u5df1\u7ed9\u5ffd\u7565\u4e86\uff0c\u5730\u4f4d\u5982\u6b64\u7684\u60ac\u6b8a\uff0c\u5206\u91cf\u7684\u5982\u6b64\u4e0d\u76f8\u5f53\uff0c\u90a3\u6211\u6709\u4ec0\u4e48\u8d44\u683c\u53bb\u548c\u4ed6\u8c08\u5229\u5bb3\uff0c\u8c08\u4ea4\u6613\uff1f\u5bf9\u4e8e\u738b\u8005\u800c\u8a00\uff0c\u53ea\u6709\u5c45\u9ad8\u4e34\u4e0b\u7684\u6069\u8d50\uff0c\u6ca1\u6709\u5e73\u7b49\u7684\u4ea4\u6362\uff0c\u53ea\u6709\u4ed6\u7684\u559c\u6012\u7231\u618e\uff0c\u6ca1\u6709\u4ec0\u4e48\u5229\u5bb3\u8ba1\u7b97\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u5c51\u4e8e\u5728\u4e4e\u3002\u6211\u81ea\u5df1\u5728\u4e4e\uff0c\u6211\u5c31\u4ee5\u4e3a\u4ed6\u4e5f\u5728\u4e4e\uff0c\u8fd9\u662f\u4ee5\u5df1\u5ea6\u4eba\uff0c\u5374\u5ffd\u7565\u4e86\u6211\u548c\u4ed6\u6839\u672c\u5c31\u4e0d\u5728\u540c\u4e00\u4e2a\u5c42\u6b21\u4e4b\u4e0a\u3002\u6240\u4ee5\u8bf4\uff0c\u8fd9\u6b21\u5176\u5b9e\u6211\u662f\u5931\u8d25\u4e86\u3002\u201d</p><p>\u201c\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u7ed3\u679c\u662f\u4ed6\u7b54\u5e94\u5e2e\u5fd9\u4e86\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4ed6\u6700\u540e\u90a3\u53e5\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u4ec0\u4e48\u53eb\u505a\u2018\u6211\u5e2e\u7684\u5fd9\uff0c\u4f60\u672a\u5fc5\u6d88\u53d7\u5f97\u8d77\u2019\uff1f\u96be\u9053\u4ed6\u60f3\u51fa\u5c14\u53cd\u5c14\uff0c\u9634\u6211\u4eec\u4e00\u628a\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u90a3\u4e0d\u4f1a\uff0c\u683c\u62c9\u5179\u7279\u8fd9\u70b9\u4fe1\u8a89\u8fd8\u662f\u6709\u7684\uff0c\u8bf4\u8bdd\u4ece\u6765\u7b97\u6570\u3002\u4ed6\u8bf4\u8ba9\u6c83\u91d1\u795e\u6bbf\u5168\u529b\u534f\u52a9\uff0c\u90a3\u5c31\u4e0d\u4f1a\u6709\u95ee\u9898\u3002\u81f3\u4e8e\u4ed6\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u2026\u2026\u4e00\u65f6\u95f4\u60f3\u4e0d\u900f\uff0c\u5c0f\u5fc3\u4e3a\u4e0a\u5427\u3002\u201d</p></article>"}}}]);
