(window.webpackJsonp=window.webpackJsonp||[]).push([[1605],{1628:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u56db\u8282 \u6076\u9b54\u7684\u534f\u52a9",content:"<article><h2>\u7b2c\u4e94\u5341\u56db\u8282 \u6076\u9b54\u7684\u534f\u52a9</h2><p>\u683c\u62c9\u5179\u7279\u5e76\u6ca1\u6709\u98df\u8a00\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u4fbf\u6709\u4e00\u4f4d\u6c83\u91d1\u7267\u5e08\u524d\u6765\u9540\u91d1\u73ab\u7470\u9152\u5e97\u627e\u5230\u743c\u6069\uff0c\u4ed6\u662f\u201c\u661f\u6708\u4e4b\u989c\u201d\u7f8e\u5bb9\u9662\u7684\u8d1f\u8d23\u4eba\uff0c\u4e5f\u65e0\u591a\u8bdd\uff0c\u53ea\u662f\u8868\u793a\u63a5\u5230\u4e0a\u9762\u547d\u4ee4\uff0c\u4e00\u5207\u542c\u4ece\u743c\u6069\u7684\u5429\u5490\u884c\u4e8b\u3002</p><p>\u743c\u6069\u4ed4\u7ec6\u8be2\u95ee\uff0c\u5f97\u77e5\u201c\u661f\u6708\u4e4b\u989c\u201d\u7b3c\u7f69\u7740\u91cd\u91cd\u7ed3\u754c\uff0c\u4e43\u662f\u6559\u4f1a\u91cc\u7684\u4e94\u4f4d\u201c\u4e0a\u91d1\u201d\u7267\u5e08\u8054\u624b\u5e03\u4e0b\u7684\uff0c\u7a33\u56fa\u5f02\u5e38\uff0c\u5a01\u529b\u975e\u51e1\u3002\u5ba2\u4eba\u60f3\u8fdb\u5165\u7f8e\u5bb9\u9662\uff0c\u5fc5\u987b\u8d70\u6b63\u5e38\u7684\u9014\u7ecf\uff0c\u7ecf\u7531\u6b63\u95e8\uff0c\u6709\u5e97\u5458\u5f15\u5bfc\u966a\u4f34\uff0c\u6709\u9b54\u6cd5\u5f71\u8c61\u4f5c\u4e3a\u767b\u8bb0\u8bb0\u5f55\uff1b\u5018\u82e5\u7528\u5176\u4ed6\u65b9\u6cd5\u6f5c\u5165\uff0c\u4fbf\u4f1a\u7acb\u523b\u89e6\u53d1\u7ed3\u754c\uff0c\u906d\u53d7\u653b\u51fb\u3002\u4e00\u65e6\u8fdb\u5165\u7f8e\u5bb9\u9662\uff0c\u5404\u79cd\u53d8\u5f62\u3001\u5e7b\u672f\u4f2a\u88c5\u81ea\u52a8\u5931\u6548\uff0c\u663e\u9732\u51fa\u5ba2\u4eba\u7684\u771f\u5b9e\u9762\u8c8c\uff0c\u540c\u65f6\u4e5f\u65e0\u6cd5\u4f7f\u7528\u4efb\u4f55\u4f20\u9001\u9b54\u6cd5\u3002\u5728\u7f8e\u5bb9\u9662\u5185\u7981\u6b62\u6218\u6597\uff0c\u5018\u82e5\u6709\u4eba\u4e3b\u52a8\u653b\u51fb\u6311\u8845\uff0c\u540c\u6837\u4e5f\u4f1a\u88ab\u7ed3\u754c\u7acb\u523b\u611f\u5e94\uff0c\u8fdb\u884c\u76d1\u63a7\u548c\u963b\u6b62\u3002\u6709\u8fd9\u6837\u4e25\u5bc6\u7684\u7ed3\u754c\u5b88\u62a4\uff0c\u518d\u52a0\u4e0a\u5b9e\u529b\u4e0d\u4fd7\u7684\u6559\u4f1a\u9a91\u58eb\u505a\u4fdd\u5b89\uff0c\u81ea\u4ece\u7f8e\u5bb9\u9662\u5f00\u5f20\u4ee5\u6765\uff0c\u8fd8\u6ca1\u4ec0\u4e48\u4eba\u6562\u5728\u91cc\u9762\u95f9\u4e8b\u3002</p><p>\u7267\u5e08\u7ed9\u4e86\u743c\u6069\u4e00\u679a\u9576\u6ee1\u73e0\u5b9d\u7684\u62a4\u8eab\u7b26\uff0c\u53ea\u8981\u6301\u6709\u6b64\u7269\uff0c\u4fbf\u80fd\u65e0\u89c6\u7f8e\u5bb9\u9662\u5185\u7684\u4e00\u5207\u7ed3\u754c\u3002\u743c\u6069\u548c\u540c\u4f34\u4fbf\u53ef\u4ee5\u81ea\u7531\u4f7f\u7528\u4f20\u9001\u672f\u8fdb\u5165\u548c\u79bb\u5f00\uff0c\u4e0d\u5fc5\u7ecf\u7531\u6b63\u95e8\uff0c\u4e5f\u4e0d\u4f1a\u7559\u4e0b\u4ec0\u4e48\u5f71\u8c61\u8bb0\u5f55\uff1b\u4e3b\u52a8\u6311\u8845\u653b\u51fb\u7684\u8bdd\u4e5f\u4e0d\u4f1a\u89e6\u53d1\u7ed3\u754c\uff0c\u4e0d\u4f1a\u60ca\u52a8\u5b88\u536b\u3002\u8fd9\u4e1c\u897f\u5176\u5b9e\u5c31\u8fd1\u4f3c\u4e8e\u8d85\u7ea7\u8d35\u5bbe\u5361\uff0c\u5168\u5b89\u59c6\u5e1d\u56fd\u62e5\u6709\u7684\u4eba\u4e5f\u53ea\u6709\u5341\u4f59\u4f4d\uff0c\u4e0d\u662f\u6559\u4f1a\u5185\u7684\u9ad8\u7ea7\u4eba\u5458\uff0c\u5c31\u662f\u653f\u5c40\u663e\u8981\uff0c\u5018\u82e5\u4e0d\u662f\u6709\u683c\u62c9\u5179\u7279\u7684\u9762\u5b50\u5728\uff0c\u743c\u6069\u53ea\u6015\u4e00\u8f88\u5b50\u4e5f\u4eab\u53d7\u4e0d\u5230\u8fd9\u79cd\u5f85\u9047\u3002</p><p>\u9664\u6b64\u4e4b\u5916\u8fd8\u6709\u4e00\u4e2a\u597d\u6d88\u606f\uff0c\u4fbf\u662f\u4eca\u5929\u4e0b\u5348\uff0c\u5c31\u4f1a\u6709\u4e00\u4f4d\u82cf\u4f26\u7267\u5e08\u8981\u6765\u661f\u6708\u4e4b\u989c\u505a\u7f8e\u5bb9\uff0c\u6b63\u662f\u4e0b\u624b\u7684\u597d\u65f6\u673a\u3002\u743c\u6069\u65c1\u6572\u4fa7\u51fb\u5730\u6253\u542c\u4e86\u534a\u5929\u60c5\u51b5\uff0c\u5ba2\u5ba2\u6c14\u6c14\u5c06\u6c83\u91d1\u7267\u5e08\u9001\u4e86\u51fa\u53bb\uff0c\u521a\u521a\u56de\u5230\u623f\u95f4\uff0c\u838e\u73de\u514b\u6084\u65e0\u58f0\u606f\u5730\u51fa\u73b0\u5728\u8eab\u540e\u3002</p><p>\u201c\u600e\u4e48\u6837\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6211\u53bb\u57ce\u91cc\u6253\u63a2\u4e86\u4e00\u4e0b\uff0c\u53e6\u5916\u53bb\u4e86\u4e00\u8d9f\u5f71\u8d3c\u516c\u4f1a\uff0c\u4ece\u5404\u79cd\u6e20\u9053\u5f97\u6765\u7684\u6d88\u606f\u6c47\u603b\u6765\u770b\uff0c\u515c\u5e3d\u5deb\u5e08\u4e0d\u4e45\u524d\u906d\u53d7\u6c89\u91cd\u6253\u51fb\u7684\u6d88\u606f\u662f\u4e8b\u5b9e\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u867d\u7136\u4e0d\u80fd\u786e\u5b9a\u548c\u62c9\u6c83\u514b\u6709\u5173\uff0c\u4f46\u53ef\u80fd\u6027\u975e\u5e38\u9ad8\u3002\u201d</p><p>\u201c\u62c9\u6c83\u514b\u771f\u8dd1\u5230\u963f\u65af\u5361\u7279\u62c9\u6765\u4e86\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u4ed6\u6765\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u6e05\u695a\uff0c\u4f46\u5728\u4e00\u4e2a\u6708\u524d\uff0c\u8d2b\u6c11\u533a\u7a81\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u7403\u4f53\uff0c\u5341\u5929\u524d\u53c8\u7a81\u7136\u6d88\u5931\uff0c\u88ab\u8fa8\u8ba4\u51fa\u662f\u62c9\u6c83\u514b\u7684\u6b21\u5143\u8bd5\u9a8c\u5ba4\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u867d\u7136\u4e0d\u77e5\u9053\u5176\u76ee\u7684\uff0c\u4f46\u4ed6\u786e\u5b9e\u6765\u8fc7\u8fd9\u5ea7\u57ce\u5e02\uff0c\u505c\u7559\u8fc7\u51e0\u65e5\uff0c\u8fd9\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u5bf9\u4e8e\u62c9\u6c83\u514b\uff0c\u743c\u6069\u5176\u5b9e\u5012\u61d2\u5f97\u5173\u5fc3\uff0c\u53ea\u662f\u63d0\u8d77\u8fd9\u4e2a\u8001\u5deb\u5996\uff0c\u4e0d\u514d\u5c31\u60f3\u8d77\u4ed6\u5f04\u51fa\u6765\u7684\u90a3\u4e2a\u4eba\u9020\u5438\u8840\u9b3c\uff0c\u8389\u6cd5\u5c14\xb7\u94f6\u6708\u3002\u867d\u7136\u4e00\u5171\u4e5f\u624d\u8ba4\u8bc6\u76f8\u5904\u51e0\u5929\uff0c\u6ca1\u6253\u8fc7\u4ec0\u4e48\u4ea4\u9053\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u611f\u60c5\uff0c\u4f46\u5370\u8c61\u5374\u9887\u4e3a\u6df1\u523b\u3002\u5979\u8eab\u4e0a\u90a3\u79cd\u6de1\u5b9a\u4ece\u5bb9\uff0c\u5ba0\u8fb1\u4e0d\u60ca\u7684\u60a0\u7136\u6c14\u8d28\uff0c\u5b9e\u5728\u662f\u6781\u5176\u7f55\u89c1\u7684\uff0c\u6885\u83f2\u65af\u662f\u575a\u5b9a\uff0c\u5374\u592a\u8fc7\u6267\u7740\u521a\u5f3a\uff0c\u950b\u8292\u6bd5\u9732\uff0c\u51db\u662f\u968f\u6027\u800c\u4e3a\uff0c\u9ad8\u5174\u4e86\u4fbf\u6765\uff0c\u5174\u5c3d\u4e86\u4fbf\u6563\uff0c\u4e0d\u59d4\u5c48\u81ea\u5df1\uff0c\u4e0d\u8fc1\u5c31\u522b\u4eba\uff0c\u8389\u6cd5\u5c14\u5374\u662f\u5982\u5979\u81ea\u5df1\u6240\u8bf4\u7684\uff0c\u201c\u968f\u9047\u800c\u5b89\u201d\u3002</p><p>\u76f4\u81f3\u73b0\u5728\uff0c\u743c\u6069\u4f9d\u65e7\u8fd8\u80fd\u6e05\u6e05\u695a\u695a\u5730\u8bb0\u5f97\u5f53\u65e5\u4e24\u4eba\u95f4\u7684\u95f2\u8c08\uff0c\u63d0\u53ca\u8389\u6cd5\u5c14\u88ab\u53d8\u6210\u5438\u8840\u9b3c\uff0c\u4e27\u5931\u795e\u7947\u7684\u7737\u987e\uff0c\u743c\u6069\u4ee5\u4e3a\u5979\u53d8\u6210\u4e86\u65e0\u4fe1\u8005\uff0c\u6216\u8005\u9009\u62e9\u4e86\u65b0\u7684\u4fe1\u4ef0\uff0c\u8389\u6cd5\u5c14\u5374\u6447\u5934\u5426\u8ba4\u3002</p><p>\u201c\u795e\u662f\u629b\u5f03\u4e86\u6211\uff0c\u4f46\u662f\uff0c\u6211\u6ca1\u6709\u629b\u5f03\u4ed6\u554a\u3002\u201d</p><p>\u53ea\u6b64\u4e00\u8bed\uff0c\u4fbf\u8db3\u4ee5\u94ed\u8bb0\u4e0d\u5fd8\u3002</p><p>\u62c9\u6c83\u514b\u6765\u4e86\u963f\u65af\u5361\u7279\u62c9\uff0c\u5979\u4e5f\u8ddf\u7740\u6765\u4e86\u4e48\uff0c\u6216\u8005\u8fd8\u662f\u7559\u5728\u535a\u5f97\u4e4b\u95e8\u7684\u5deb\u5e08\u5c71\u8109\uff1f\u8fd9\u6b21\u56de\u9634\u9b42\u57ce\uff0c\u6b63\u597d\u8981\u8def\u8fc7\u535a\u5f97\u4e4b\u95e8\uff0c\u5012\u662f\u5e94\u8be5\u53bb\u770b\u770b\u5979\uff0c\u6bd5\u7adf\u662f\u66fe\u7ecf\u5e94\u5141\u8fc7\u7684\u3002</p><p>\u653e\u4e0b\u8fd9\u4e2a\u5ff5\u5934\uff0c\u743c\u6069\u548c\u838e\u73de\u514b\u51fa\u95e8\uff0c\u53bb\u627e\u7ef4\u5eb7\u5c3c\u4e9a\u5546\u8bae\u63a5\u4e0b\u6765\u8981\u8fdb\u884c\u7684\u8c0b\u6740\u5927\u8ba1\u3002\u81f3\u4e8e\u6885\u83f2\u65af\u90a3\u8fb9\uff0c\u743c\u6069\u4f9d\u65e7\u662f\u627e\u7406\u7531\u642a\u585e\u8fc7\u53bb\uff0c\u53ea\u8bf4\u9f99\u8840\u5df2\u7ecf\u6709\u4e86\u5934\u7eea\uff0c\u660e\u5929\u5c31\u53ef\u80fd\u5230\u624b\u3002\u6885\u83f2\u65af\u4f3c\u4e4e\u7565\u6709\u7591\u8651\uff0c\u4f46\u4e5f\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u4e0b\u5348\u4e09\u70b9\u949f\u5de6\u53f3\uff0c\u4e00\u4f4d\u8eab\u62ab\u94f6\u888d\u7684\u82cf\u4f26\u7267\u5e08\u8fdb\u5165\u661f\u6708\u4e4b\u989c\uff0c\u7b49\u5f85\u5df2\u4e45\u7684\u743c\u6069\u4e09\u4eba\u968f\u5373\u6f5c\u5165\uff0c\u51ed\u501f\u7740\u6c83\u91d1\u7267\u5e08\u7ed9\u7684\u62a4\u7b26\uff0c\u4ed6\u4eec\u6ca1\u6709\u53d7\u5230\u4efb\u4f55\u963b\u788d\uff0c\u6ca1\u6709\u60ca\u52a8\u4efb\u4f55\u4eba\uff0c\u6084\u65e0\u58f0\u606f\u5730\u4fbf\u8fdb\u4e86\u4e00\u5904\u623f\u95f4\uff0c\u5728\u6697\u5904\u57cb\u4f0f\u4e0b\u6765\uff0c\u8fd9\u662f\u4e00\u4e2a\u8d35\u5bbe\u4e13\u7528\u7684\u4f11\u606f\u5ba4\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5236\u9020\u51fa\u51e0\u7247\u9634\u5f71\uff0c\u5c06\u81ea\u5df1\u4e09\u4eba\u63a9\u85cf\u8d77\u6765\u3002</p><p>\u8fc7\u4e86\u5927\u7ea6\u4e00\u4e2a\u591a\u5c0f\u65f6\uff0c\u8d70\u5eca\u91cc\u7ec8\u4e8e\u4f20\u6765\u4e86\u7b49\u5f85\u5df2\u4e45\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u4f4d\u5973\u6027\u5934\u4e0a\u88f9\u7740\u6bdb\u5dfe\u63a8\u95e8\u8d70\u8fdb\uff0c\u53ea\u7a7f\u7740\u8d34\u8eab\u5185\u8863\uff0c\u8eab\u6750\u5012\u8fd8\u4e0d\u9519\uff0c\u53ea\u662f\u5e74\u7eaa\u5927\u4e86\u70b9\uff0c\u4f30\u8ba1\u5df2\u7ecf\u8d85\u8fc7\u4e09\u5341\uff0c\u8138\u867d\u7136\u8fd8\u7b97\u6f02\u4eae\uff0c\u4fdd\u517b\u6709\u65b9\uff0c\u4f46\u5df2\u7ecf\u9690\u7ea6\u80fd\u770b\u89c1\u5c81\u6708\u7684\u75d5\u8ff9\u3002\u5979\u7684\u8116\u5b50\u4e0a\u6302\u7740\u4e00\u679a\u6708\u7259\u5f62\u4e73\u767d\u8272\u62a4\u7b26\uff0c\u4e2d\u95f4\u753b\u7740\u4e00\u53cc\u773c\u775b\uff0c\u5468\u56f4\u4e03\u9897\u94f6\u661f\u73af\u7ed5\uff0c\u6b63\u662f\u6708\u5973\u795e\u82cf\u4f26\u7684\u5723\u5fbd\u3002</p><p>\u5979\u6ca1\u53d1\u89c9\u6709\u4eba\u57cb\u4f0f\uff0c\u8d70\u5230\u623f\u95f4\u4e2d\u95f4\u7684\u8eba\u6905\u4e0a\uff0c\u6b63\u8981\u8eba\u4e0b\u4f11\u606f\u7247\u523b\uff0c\u9661\u7136\u795e\u60c5\u4e00\u51db\uff0c\u671d\u7740\u89d2\u843d\u91cc\u671b\u8fc7\u6765\uff0c\u90a3\u91cc\u6b63\u662f\u838e\u73de\u514b\u9690\u85cf\u4e4b\u5904\u3002\u5f88\u663e\u7136\uff0c\u4f5c\u4e3a\u5584\u795e\u7267\u5e08\uff0c\u5979\u5bdf\u89c9\u5230\u4e86\u6076\u9b54\u7684\u6c14\u606f\u3002</p><p>\u743c\u6069\u539f\u672c\u662f\u60f3\u7b49\u5979\u8eba\u4e0b\uff0c\u8eab\u4f53\u653e\u677e\u7684\u65f6\u5019\u518d\u7a81\u7136\u52a8\u624b\uff0c\u5982\u4eca\u773c\u89c1\u5f62\u8ff9\u8d25\u9732\uff0c\u4e0d\u5f97\u4e0d\u63d0\u524d\u53d1\u52a8\uff0c\u62a2\u51fa\u4e00\u6b65\u6321\u4f4f\u95e8\u524d\uff0c\u5207\u65ad\u5979\u7684\u9000\u8def\uff0c\u540c\u65f6\u628a\u95e8\u5173\u4e0a\u4e86\u3002</p><p>\u82cf\u4f26\u7267\u5e08\u6b32\u5f85\u558a\u53eb\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53ca\u65f6\u53d1\u51fa\u4e00\u4e2a\u6c89\u9ed8\u672f\uff0c\u62b5\u6d88\u4e86\u5979\u7684\u58f0\u97f3\u3002\u838e\u73de\u514b\u81ea\u9634\u5f71\u4e2d\u4e00\u8dc3\u800c\u8d77\uff0c\u6325\u821e\u7740\u6dec\u6bd2\u77ed\u5251\u76f4\u903c\u8fc7\u6765\u3002\u82cf\u4f26\u7267\u5e08\u4e24\u624b\u7a7a\u7a7a\uff0c\u6b66\u5668\u4e5f\u6ca1\u5e26\u5728\u8eab\u8fb9\uff0c\u4f46\u5979\u5230\u5e95\u4e0d\u662f\u5e38\u4eba\uff0c\u731d\u7136\u9047\u88ad\u5374\u6beb\u4e0d\u60ca\u60f6\uff0c\u5de6\u624b\u6309\u4f4f\u8116\u9888\u4e0a\u7684\u5723\u5fbd\uff0c\u53f3\u624b\u638c\u5fc3\u5ef6\u4f38\u51fa\u4e00\u675f\u6e05\u51b7\u6708\u5149\uff0c\u4eff\u4f5b\u5229\u5251\uff0c\u4e00\u6643\u4e4b\u4e0b\u6574\u4e2a\u623f\u95f4\u90fd\u6ce0\u6ce0\u751f\u8f89\uff0c\u67b6\u4f4f\u4e86\u9b45\u9b54\u7684\u77ed\u5251\uff0c\u987a\u52bf\u53cd\u51fb\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u53cc\u624b\u5408\u63e1\uff0c\u53d1\u51fa\u4e00\u9053\u9eef\u6de1\u4e4c\u5149\uff0c\u5374\u88ab\u82cf\u4f26\u7267\u5e08\u907f\u4e86\u5f00\u53bb\u3002\u743c\u6069\u6321\u4f4f\u95e8\u53e3\uff0c\u624b\u4e0a\u5df2\u7ecf\u63e1\u4f4f\u6cd5\u6756\uff0c\u5374\u5e76\u4e0d\u51fa\u624b\uff0c\u53ea\u662f\u65c1\u89c2\u3002\u838e\u73de\u514b\u8fde\u653b\u51e0\u5251\uff0c\u773c\u770b\u4e0d\u80fd\u5f97\u624b\uff0c\u5de6\u624b\u5f80\u8170\u95f4\u4e00\u63a2\uff0c\u518d\u6325\u51fa\u6765\u65f6\u5df2\u7ecf\u591a\u4e86\u4e00\u6761\u7130\u7ea2\u957f\u97ad\uff0c\u5982\u9f99\u5982\u86c7\uff0c\u671d\u7740\u82cf\u4f26\u7267\u5e08\u5e2d\u5377\u800c\u6765\u3002</p><p>\u8fd9\u4f4d\u82cf\u4f26\u7267\u5e08\u5728\u6559\u4f1a\u4e2d\u4f4d\u9636\u9887\u9ad8\uff0c\u80fd\u529b\u4e0d\u4fd7\uff0c\u4e5f\u9887\u6709\u4e9b\u5b9e\u6218\u683c\u6597\u7ecf\u9a8c\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u4e00\u5bf9\u4e00\u4ea4\u950b\uff0c\u838e\u73de\u514b\u672a\u5fc5\u662f\u5979\u7684\u5bf9\u624b\u3002\u4f46\u6b64\u65f6\u5979\u6beb\u65e0\u51c6\u5907\u4e4b\u4e0b\u906d\u9047\u4f0f\u51fb\uff0c\u8eab\u9677\u91cd\u56f4\uff0c\u5df2\u7ecf\u51fa\u73b0\u7684\u5bf9\u624b\u5c31\u6709\u4e09\u4e2a\uff0c\u4e0d\u77e5\u9053\u6697\u4e2d\u662f\u5426\u8fd8\u6709\u654c\u4eba\u57cb\u4f0f\u3002\u5c24\u5176\u4ee4\u5979\u610f\u5916\u7684\u662f\uff0c\u660e\u660e\u5df2\u7ecf\u53d1\u751f\u6218\u6597\uff0c\u5374\u6ca1\u6709\u89e6\u53d1\u534a\u70b9\u7ed3\u754c\u7684\u611f\u5e94\uff0c\u4e5f\u6ca1\u6709\u4eba\u6765\u5bdf\u770b\u652f\u63f4\uff0c\u8fd9\u8ba9\u5979\u5fc3\u5e95\u5347\u8d77\u4e86\u4e00\u79cd\u4e0d\u7965\u9884\u5146\uff0c\u80c6\u6c14\u987f\u65f6\u5c31\u5f31\u4e86\u51e0\u5206\uff0c\u53cd\u800c\u88ab\u838e\u73de\u514b\u538b\u5728\u4e0b\u98ce\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e3e\u8d77\u838e\u5c14\u5723\u5fbd\uff0c\u540c\u65f6\u53d1\u51fa\u4e94\u9053\u9eef\u5149\u7bad\uff0c\u82cf\u4f26\u7267\u5e08\u907f\u65e0\u53ef\u907f\uff0c\u731b\u7136\u53cc\u76ee\u5706\u7741\uff0c\u5468\u8eab\u94f6\u5149\u5982\u6f6e\u6c34\u822c\u6c79\u6d8c\u9f13\u8361\uff0c\u4ea4\u7ec7\u6210\u4e00\u5f20\u7f51\u72b6\u62a4\u7f69\uff0c\u5c06\u4e94\u53ea\u9eef\u5149\u7bad\u5168\u90fd\u53cd\u5f39\u4e86\u56de\u53bb\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u7136\u4e0d\u4f1a\u88ab\u81ea\u5df1\u7684\u795e\u672f\u6240\u4f24\uff0c\u4f46\u4e00\u65f6\u4e5f\u5f04\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u62db\u67b6\u4e0d\u582a\u3002\u743c\u6069\u773c\u89c1\u65f6\u5019\u5df2\u7ecf\u5dee\u4e0d\u591a\uff0c\u518d\u62d6\u5ef6\u4e0b\u53bb\u53ea\u6015\u751f\u53d8\uff0c\u8e0f\u4e0a\u524d\u6b63\u8981\u65bd\u6cd5\uff0c\u5374\u89c1\u82cf\u4f26\u7267\u5e08\u75be\u9000\u4e24\u6b65\uff0c\u8131\u79bb\u4e86\u6c89\u9ed8\u672f\u7684\u7b3c\u7f69\u8303\u56f4\uff0c\u5de6\u624b\u4e00\u628a\u626f\u4e0b\u8116\u9888\u4e0a\u7684\u94f6\u6708\u5723\u5fbd\uff0c\u63e1\u5728\u638c\u5fc3\u5bf9\u51c6\u838e\u73de\u514b\uff0c\u7070\u8272\u53cc\u76ee\u9661\u7136\u53d8\u6210\u707f\u70c2\u94f6\u767d\uff0c\u53e3\u4e2d\u5389\u559d\u4e00\u58f0\uff1a\u201c\u82cf\u4f26\uff01\u201d</p><p>\u5723\u5fbd\u4e0a\u94f6\u5149\u5927\u76db\uff0c\u5e9e\u5927\u7684\u865a\u5f71\u51fa\u73b0\u5728\u5176\u4e2d\uff0c\u90a3\u662f\u4e00\u4f4d\u7f8e\u4e3d\u7684\u4eba\u7c7b\u5973\u5b50\uff0c\u94f6\u8272\u957f\u53d1\u98d8\u6d12\u5982\u7011\uff0c\u886c\u6258\u51fa\u7cbe\u81f4\u5b8c\u7f8e\u7684\u9762\u5bb9\uff0c\u53cc\u817f\u7b14\u76f4\u4fee\u957f\uff0c\u8eab\u4e0a\u62ab\u7740\u4e00\u4ef6\u6708\u5149\u8272\u7684\u900f\u660e\u7eb1\u888d\uff0c\u6b63\u662f\u6708\u5973\u795e\u82cf\u4f26\u3002\u968f\u7740\u7267\u5e08\u547c\u558a\u51fa\u795e\u540d\uff0c\u82cf\u4f26\u7684\u7fe0\u7eff\u53cc\u773c\u671d\u7740\u838e\u73de\u514b\u6084\u7136\u51dd\u89c6\u8fc7\u53bb\u3002</p><p>\u94ee\uff01</p><p>\u4eff\u4f5b\u662f\u743c\u6069\u7684\u9519\u89c9\uff0c\u4ed6\u8033\u4e2d\u5c45\u7136\u542c\u5230\u4e86\u7434\u5f26\u62e8\u52a8\u7684\u58f0\u97f3\uff0c\u867d\u7136\u8f7b\u5fae\uff0c\u4f46\u6e05\u6670\u65e0\u6bd4\u5730\u4f20\u5165\u8033\u4e2d\u3002\u838e\u73de\u514b\u88ab\u82cf\u4f26\u770b\u4e86\u8fd9\u4e00\u773c\uff0c\u4eff\u4f5b\u906d\u5230\u5343\u65a4\u5de8\u9524\u649e\u51fb\u4e00\u822c\uff0c\u8e09\u8dc4\u540e\u9000\uff0c\u5634\u89d2\u90fd\u6ea2\u51fa\u8840\u4e1d\uff0c\u7d27\u63a5\u7740\u6574\u4e2a\u4eba\u88ab\u6708\u5149\u5305\u88f9\uff0c\u77ac\u95f4\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002</p><p>\u5723\u8a00\uff01</p><p>\u743c\u6069\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u6cd5\u672f\uff0c\u8fd9\u662f\u5584\u795e\u7267\u5e08\u4eec\u62e5\u6709\u7684\u4e00\u79cd\u5f3a\u5927\u795e\u672f\uff0c\u552f\u6709\u975e\u5e38\u9ad8\u9636\u7684\u7267\u5e08\u624d\u80fd\u65bd\u5c55\uff0c\u5728\u8bf4\u51fa\u795e\u540d\u7684\u540c\u65f6\uff0c\u76f8\u5f53\u4e8e\u6682\u65f6\u501f\u6765\u795e\u7947\u7684\u5a01\u80fd\u53d1\u51fa\u4e00\u51fb\u3002\u6240\u8c13\u201c\u5723\u8a00\u201d\uff0c\u662f\u6307\u7267\u5e08\u81ea\u5df1\u547c\u5524\u795e\u660e\uff0c\u5e76\u975e\u501f\u8a00\u8bed\u6765\u6740\u4eba\uff0c\u6240\u4ee5\u65e0\u8bba\u4f60\u542c\u89c1\u6ca1\u542c\u89c1\uff0c\u53ea\u8981\u5728\u6cd5\u672f\u5c04\u7a0b\u4e4b\u5185\uff0c\u54ea\u6015\u662f\u4e2a\u804b\u5b50\uff0c\u7167\u6837\u4e5f\u4f1a\u88ab\u6cd5\u672f\u51fb\u4e2d\u3002\u4e0d\u8fc7\u5723\u8a00\u672f\u7684\u7f3a\u70b9\uff0c\u5728\u4e8e\u5b83\u6709\u592a\u5f3a\u7684\u9635\u8425\u9650\u5236\uff0c\u5bf9\u90aa\u6076\u7684\u5b58\u5728\u6709\u6548\uff0c\u5bf9\u5584\u826f\u4eba\u58eb\u5219\u57fa\u672c\u65e0\u7528\u3002</p><p>\u838e\u73de\u514b\u662f\u9b45\u9b54\uff0c\u5012\u662f\u5c5e\u4e8e\u201c\u5723\u8a00\u201d\u6700\u9002\u5408\u7684\u653b\u51fb\u5bf9\u8c61\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u5979\u4e2d\u4e86\u8fd9\u4e00\u51fb\uff0c\u65e2\u4f1a\u53d7\u4f24\uff0c\u540c\u65f6\u53c8\u4f1a\u88ab\u5f3a\u5236\u9a71\u9010\u56de\u6df1\u6e0a\uff0c\u4e8c\u5341\u56db\u5c0f\u65f6\u4e4b\u5185\u65e0\u6cd5\u518d\u524d\u6765\u7269\u8d28\u754c\u3002\u5e78\u597d\u5979\u662f\u743c\u6069\u7684\u4ec6\u4ece\uff0c\u771f\u540d\u5951\u7ea6\u7684\u81f3\u9ad8\u6548\u529b\u963b\u6321\u4e86\u201c\u5723\u8a00\u201d\u7684\u6b63\u5e38\u8fd0\u7528\uff0c\u6240\u4ee5\u5979\u4e0d\u4f1a\u88ab\u9a71\u9010\u56de\u6df1\u6e0a\uff0c\u800c\u662f\u88ab\u9001\u8fdb\u4e00\u4e2a\u4e34\u65f6\u7684\u534a\u4f4d\u9762\u91cc\u6682\u65f6\u56da\u7981\uff0c\u76f8\u5f53\u4e8e\u4e2d\u4e86\u8ff7\u5bab\u672f\u6216\u8005\u77ed\u6682\u7684\u7981\u9522\u672f\uff0c\u516b\u4e2a\u5c0f\u65f6\u4e4b\u540e\u4fbf\u80fd\u91cd\u65b0\u53ec\u56de\u3002</p><p>\u4e00\u51fb\u5f97\u624b\uff0c\u6253\u53d1\u4e86\u838e\u73de\u514b\u8fd9\u4e2a\u5f3a\u654c\uff0c\u82cf\u4f26\u7267\u5e08\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u5411\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5723\u5fbd\u4e0a\u707f\u7136\u751f\u5149\uff0c\u663e\u7136\u53c8\u8981\u518d\u5ea6\u53d1\u52a8\u201c\u5723\u8a00\u201d\u3002\u623f\u95f4\u72ed\u5c0f\uff0c\u5353\u5c14\u5c11\u5973\u5df2\u7ecf\u6765\u4e0d\u53ca\u9003\u8131\u51fa\u6cd5\u672f\u8303\u56f4\uff0c\u5979\u7684\u80fd\u529b\u4e5f\u4e0d\u8db3\u4ee5\u53d1\u51fa\u201c\u90aa\u8a00\u201d\u76f8\u5bf9\u6297\uff0c\u773c\u770b\u5c31\u8981\u88ab\u91cd\u521b\uff0c\u751a\u81f3\u6027\u547d\u4e0d\u4fdd\uff0c\u9661\u7136\u95f4\u4eba\u5f71\u4e00\u95ea\uff0c\u743c\u6069\u5df2\u7ecf\u77ac\u79fb\u5230\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u9762\u524d\uff0c\u66ff\u5979\u6321\u4e0b\u4e86\u8fd9\u4e00\u51fb\u3002</p><p>\u5723\u8a00\u7684\u6548\u679c\u56e0\u4eba\u800c\u5f02\uff0c\u6253\u4e2a\u6bd4\u65b9\u8bf4\uff0c\u838e\u73de\u514b\u662f\u6076\u9b54\uff0c\u6240\u627f\u53d7\u7684\u5a01\u529b\u4fbf\u662f\u5341\u5206\uff0c\u4f46\u8fd9\u5341\u5206\u5a01\u529b\u4e4b\u4e2d\uff0c\u6709\u4e94\u5206\u7528\u4f5c\u5c06\u5979\u6253\u56de\u6df1\u6e0a\u53bb\u4e86\uff0c\u771f\u6b63\u9020\u6210\u7684\u4f24\u5bb3\u53ea\u662f\u4e94\u5206\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u662f\u838e\u5c14\u7267\u5e08\uff0c\u838e\u5c14\u662f\u82cf\u4f26\u7684\u6b7b\u654c\uff0c\u627f\u53d7\u7684\u5a01\u529b\u4e5f\u662f\u5341\u5206\uff0c\u90a3\u5c31\u662f\u5341\u6210\u5341\u7684\u4f24\u5bb3\uff0c\u771f\u8981\u88ab\u6253\u4e2d\u4e86\uff0c\u4e0d\u6b7b\u4e5f\u8981\u4e22\u6389\u534a\u6761\u547d\u3002\u73b0\u5728\u8fd9\u4e00\u51fb\u6253\u5728\u743c\u6069\u8eab\u4e0a\uff0c\u4ed6\u4e0d\u662f\u7267\u5e08\uff0c\u751a\u81f3\u8fde\u838e\u5c14\u4fe1\u5f92\u90fd\u662f\u201c\u4f2a\u201d\u7684\uff0c\u5723\u8a00\u7684\u5a01\u529b\u4fbf\u987f\u65f6\u5c11\u4e86\u5927\u534a\uff0c\u518d\u52a0\u4e0a\u6597\u7bf7\u3001\u6cd5\u888d\u548c\u8eab\u4e0a\u7684\u9632\u62a4\u9b54\u6cd5\u5c42\u5c42\u62b5\u6d88\uff0c\u771f\u6b63\u627f\u53d7\u7684\u4f24\u5bb3\u4e0d\u5230\u5341\u5206\u4e4b\u4e00\u3002</p><p>\u7eb5\u7136\u5982\u6b64\uff0c\u4ed6\u4e5f\u88ab\u9707\u5f97\u8fde\u9000\u4e24\u6b65\uff0c\u5589\u5934\u4e00\u751c\uff0c\u51e0\u4e4e\u8981\u5f53\u573a\u5410\u51fa\u8840\u6765\uff0c\u5f53\u4e0b\u4e0d\u6562\u518d\u8fdf\u7591\uff0c\u6cd5\u6756\u865a\u6307\uff0c\u516d\u679a\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u4eae\u8d77\uff0c\u8054\u6210\u4e00\u7ebf\uff0c\u6bb7\u7ea2\u5982\u8840\u7684\u7075\u5149\u5305\u88f9\u4f4f\u4ed6\u7684\u53f3\u81c2\uff0c\u4e00\u9053\u5b9a\u8eab\u672f\u914d\u5408\u201c\u6cd5\u672f\u589e\u5e45\u201d\u51fb\u51fa\u3002</p><p>\u5982\u679c\u662f\u8981\u76f4\u63a5\u51fb\u6740\u7684\u8bdd\uff0c\u5176\u5b9e\u7528\u89e3\u79bb\u672f\u6700\u597d\uff0c\u4f46\u4ed6\u7ec8\u7a76\u662f\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u6709\u4e9b\u6127\u759a\uff0c\u60f3\u7740\u9001\u5979\u4e00\u4e2a\u4eba\u60c5\uff0c\u5e2e\u4eba\u5e2e\u5230\u5e95\u3002\u6740\u6b7b\u82cf\u4f26\u7267\u5e08\uff0c\u5bf9\u4e8e\u7ef4\u5eb7\u5c3c\u4e9a\u800c\u8a00\u81ea\u7136\u662f\u529f\u7ee9\uff0c\u4f46\u8fd8\u5c0f\u4e86\u70b9\uff1b\u5982\u679c\u80fd\u591f\u751f\u64d2\u6d3b\u6349\uff0c\u4e3e\u884c\u4eea\u5f0f\u732e\u796d\u7ed9\u591c\u5973\u58eb\uff0c\u90a3\u5c31\u662f\u66f4\u5927\u7684\u8363\u8000\u4e86\u3002</p><p>\u82cf\u4f26\u7267\u5e08\u8fde\u53d1\u4e24\u9053\u5723\u8a00\uff0c\u81ea\u8eab\u4e5f\u5df2\u7ecf\u6fd2\u4e34\u900f\u652f\uff0c\u811a\u6b65\u865a\u6d6e\uff0c\u6447\u6447\u6b32\u5760\uff0c\u88ab\u743c\u6069\u6cd5\u672f\u51fb\u4e2d\uff0c\u987f\u65f6\u4fbf\u52a8\u5f39\u4e0d\u5f97\u3002\u743c\u6069\u773c\u89c1\u5f97\u624b\uff0c\u4e0a\u524d\u4e00\u628a\u6293\u4f4f\u5979\uff0c\u53e6\u4e00\u624b\u63fd\u4f4f\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8170\u80a2\uff0c\u4f20\u9001\u672f\u77ac\u95f4\u53d1\u52a8\uff0c\u8eab\u5f62\u4ece\u7a7a\u6c14\u4e2d\u6d88\u5931\u3002</p><p>\u6574\u4e2a\u6218\u6597\u867d\u7136\u60ca\u9669\uff0c\u5176\u5b9e\u8fd8\u4e0d\u5230\u77ed\u77ed\u5341\u79d2\u949f\uff0c\u623f\u95f4\u91cc\u4fbf\u53c8\u91cd\u65b0\u6062\u590d\u4e86\u5e73\u9759\u3002</p><p>\u743c\u6069\u5f53\u7136\u4e0d\u4f1a\u56de\u9540\u91d1\u73ab\u7470\u9152\u5e97\uff0c\u800c\u662f\u76f4\u63a5\u4f20\u9001\u5230\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u4f4f\u5904\u3002\u5353\u5c14\u5c11\u5973\u7528\u7ef3\u7d22\u5c06\u4fd8\u864f\u7ed1\u8d77\uff0c\u53d6\u8d70\u5979\u7684\u94f6\u6708\u5723\u5fbd\uff0c\u53c8\u7528\u6bd2\u9488\u5c01\u4f4f\u4e86\u5979\u7684\u58f0\u97f3\uff0c\u514d\u5f97\u558a\u53eb\uff0c\u51c6\u5907\u7b49\u5230\u591c\u5e55\u964d\u4e34\uff0c\u4e3e\u884c\u4eea\u5f0f\u5411\u591c\u5973\u58eb\u732e\u796d\u3002\u743c\u6069\u7528\u610f\u8bc6\u8054\u7cfb\u838e\u73de\u514b\uff0c\u5f97\u77e5\u5979\u73b0\u5728\u88ab\u56f0\u5728\u4e00\u4e2a\u534a\u4f4d\u9762\u4e2d\uff0c\u6682\u65f6\u65e0\u6cd5\u8131\u56f0\uff0c\u4f24\u52bf\u5012\u4e5f\u4e0d\u592a\u4e25\u91cd\uff0c\u4fbf\u653e\u4e0b\u5fc3\u6765\u3002</p><p>\u73b0\u5728\u5c06\u8fd1\u4e0b\u5348\u4e94\u70b9\uff0c\u5df2\u7ecf\u662f\u508d\u665a\u65f6\u5206\uff0c\u8ddd\u79bb\u4e3e\u884c\u732e\u796d\u7684\u65f6\u95f4\u4e0d\u8fdc\u3002\u597d\u5728\u591c\u5973\u58eb\u838e\u5c14\u8bb2\u7a76\u9690\u79d8\uff0c\u4e0d\u592a\u6ce8\u91cd\u5f62\u5f0f\uff0c\u4e0d\u641e\u94fa\u5f20\u6d6a\u8d39\uff0c\u5bf9\u4eea\u5f0f\u4e5f\u6ca1\u6709\u592a\u591a\u8981\u6c42\uff0c\u4e5f\u4e0d\u7528\u5fd9\u4e71\u6298\u817e\u4ec0\u4e48\uff0c\u76f4\u63a5\u5728\u8fd9\u5367\u5ba4\u91cc\u5c31\u884c\uff0c\u8fde\u796d\u575b\u90fd\u4e0d\u7528\u3002</p><p>\u743c\u6069\u4e2d\u4e86\u4e00\u51fb\u5723\u8a00\uff0c\u5e78\u597d\u53d7\u4f24\u4e0d\u91cd\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53d6\u51fa\u5723\u5fbd\u66ff\u4ed6\u6cbb\u7597\uff0c\u5f88\u5feb\u4e5f\u5c31\u57fa\u672c\u6062\u590d\uff0c\u6ca1\u6709\u5927\u788d\u4e86\u3002\u201c\u6211\u53ef\u771f\u6ca1\u60f3\u5230\u4f60\u4f1a\u8fd9\u4e48\u505a\uff0c\u201d\u5353\u5c14\u5c11\u5973\u51b7\u51b0\u51b0\u5730\u8bf4\uff0c\u201c\u60f3\u8981\u5411\u6211\u793a\u597d\u4e48\uff0c\u8fd8\u662f\u663e\u793a\u4e00\u4e0b\u82f1\u96c4\u6c14\u6982\uff1f\u6211\u53ef\u628a\u8bdd\u5148\u8bf4\u5728\u524d\u9762\uff0c\u522b\u6307\u671b\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u4eec\u4ee5\u524d\u7684\u65e7\u5e10\u5c31\u4f1a\u4e00\u7b14\u52fe\u9500\u3002\u201d</p><p>\u201c\u90fd\u8c08\u4e0d\u4e0a\uff0c\u5229\u5bb3\u6743\u8861\u7f62\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u8981\u88ab\u6253\u4e2d\u4e86\u5c31\u662f\u6b7b\uff0c\u6211\u88ab\u6253\u4e2d\u4e86\u53ea\u662f\u53d7\u70b9\u4f24\uff0c\u4e24\u76f8\u53d6\u820d\uff0c\u4e0d\u662f\u5f88\u7b80\u5355\u7684\u9009\u62e9\u4e48\u3002\u201d</p><p>\u201c\u6b7b\u90a3\u4e5f\u662f\u6211\u6b7b\uff0c\u5bf9\u4f60\u53c8\u6ca1\u6709\u534a\u70b9\u635f\u5bb3\uff0c\u4f60\u72af\u5f97\u7740\u5728\u610f\u5417\u3002\u201d</p><p>\u201c\u5728\u610f\u554a\uff0c\u4f60\u8981\u662f\u6b7b\u4e86\uff0c\u90a3\u6211\u8981\u7684\u4e1c\u897f\u5c82\u4e0d\u843d\u7a7a\uff0c\u767d\u5fd9\u4e00\u573a\uff0c\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u201c\u8fd9\u79cd\u4e8f\u672c\u7684\u751f\u610f\uff0c\u6211\u5f53\u7136\u4e0d\u4f1a\u505a\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u77aa\u7740\u4ed6\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u8f6c\u8eab\u4ece\u67dc\u5b50\u5e95\u5c42\u7ffb\u51fa\u4e00\u4e2a\u5c0f\u73bb\u7483\u74f6\uff0c\u4e22\u4e86\u8fc7\u6765\uff0c\u743c\u6069\u8fde\u5fd9\u63a5\u4f4f\u3002\u201c\u9f99\u8840\u5c31\u5728\u91cc\u9762\uff0c\u662f\u771f\u662f\u5047\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u201d\u5979\u51b7\u51b7\u8bf4\uff0c\u201c\u4ea4\u6613\u5230\u6b64\u4e3a\u6b62\uff0c\u4f60\u62ff\u4e1c\u897f\u8d70\u4eba\uff0c\u4ece\u6b64\u5927\u5bb6\u5404\u8d70\u5404\u7684\u8def\u3002\u51fa\u53bb\uff01\u201d</p><p>\u201c\u5582\u5582\uff0c\u5e72\u561b\u8fd9\u4e48\u51f6\u2026\u2026\u201d</p><p>\u201c\u51fa\u53bb\uff01\u201d\u5353\u5c14\u5c11\u5973\u5389\u58f0\u8bf4\uff0c\u201c\u6eda\uff01\u201d</p><p>\u201c\u4e0d\u7528\u8fd9\u4e48\u6025\u7740\u8d76\u4eba\u5427\uff0c\u6211\u624d\u521a\u521a\u53d7\u4f24\u554a\u3002\u201d</p><p>\u201c\u4e1c\u897f\u62ff\u5230\u624b\u4e86\uff0c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u56de\u53bb\u6551\u4f60\u7684\u5c0f\u60c5\u4eba\uff0c\u5f85\u5728\u6211\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5353\u5c14\u5c11\u5973\u659c\u7740\u773c\u77a5\u4ed6\uff0c\u201c\u96be\u9053\u8fd8\u60f3\u548c\u6211\u518d\u7eed\u524d\u7f18\u4e48\uff1f\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u8d77\u8eab\u51c6\u5907\u8d70\u4eba\uff0c\u5230\u4e86\u5367\u5ba4\u95e8\u53e3\u4ed6\u505c\u4f4f\u811a\u6b65\uff0c\u8f6c\u56de\u8eab\u6765\u3002\u201c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4ee5\u524d\u9a97\u8fc7\u4f60\u5f88\u591a\u6b21\uff0c\u4e5f\u6ca1\u8d44\u683c\u8981\u4f60\u76f8\u4fe1\u4ec0\u4e48\u3002\u8fd9\u6b21\u80fd\u591f\u91cd\u9022\uff0c\u5df2\u7ecf\u8fdc\u8fdc\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u6211\u60f3\u597d\u8fd0\u6c14\u603b\u4e0d\u4f1a\u91cd\u590d\u53d1\u751f\u3002\u6b64\u6b21\u4e00\u522b\uff0c\u5927\u6982\u662f\u771f\u7684\u4e0d\u4f1a\u518d\u89c1\u4e86\u2026\u2026\u201d</p><p>\u201c\u4f60\u653e\u5fc3\uff0c\u201d\u5353\u5c14\u5c11\u5973\u6076\u72e0\u72e0\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u8fd8\u4f1a\u518d\u89c1\u7684\uff0c\u4f60\u6b20\u6211\u7684\u5e10\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba8\u56de\u6765\uff0c\u4f11\u60f3\u5c31\u8fd9\u4e48\u9003\u6389\u3002\u201d</p><p>\u743c\u6069\u4e00\u7b11\uff0c\u201c\u597d\uff0c\u90a3\u6211\u7b49\u7740\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u60f3\u8bf4\u7684\u662f\uff0c\u521a\u624d\u5bf9\u4ed8\u90a3\u4e2a\u82cf\u4f26\u7267\u5e08\u7684\u65f6\u5019\uff0c\u6211\u6ca1\u60f3\u592a\u591a\uff0c\u4e5f\u6765\u4e0d\u53ca\u60f3\uff0c\u4ec0\u4e48\u5229\u5bb3\u6743\u8861\uff0c\u4ec0\u4e48\u4e8f\u672c\u4e0d\u4e8f\u672c\uff0c\u6211\u90fd\u6ca1\u60f3\u8fc7\u3002\u53ea\u662f\u770b\u5230\u5979\u51c6\u5907\u5411\u4f60\u53d1\u51fa\u5723\u8a00\uff0c\u6211\u77e5\u9053\u4f60\u627f\u53d7\u4e0d\u4f4f\uff0c\u5c31\u672c\u80fd\u5730\u4e0a\u53bb\u66ff\u4f60\u6321\u4e86\u4e00\u4e0b\uff0c\u4ec5\u6b64\u800c\u5df2\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u51b7\u7b11\uff0c\u201c\u53c8\u60f3\u7528\u8fd9\u4e9b\u8bdd\u6765\u9a97\u6211\u5417\uff1f\u4e0a\u8fc7\u4e00\u6b21\u5f53\uff0c\u96be\u9053\u8fd8\u4f1a\u4e0a\u7b2c\u4e8c\u6b21\uff0c\u4f60\u4e5f\u592a\u5c0f\u770b\u6211\u4e86\u3002\u201d</p><p>\u201c\u6211\u6ca1\u5c0f\u770b\u4f60\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u4f60\u5df2\u7ecf\u6210\u957f\u4e86\u5f88\u591a\uff0c\u6240\u4ee5\u6211\u76f8\u4fe1\u4f60\u80fd\u5224\u65ad\u5f97\u51fa\u6765\uff0c\u6211\u8bf4\u7684\u8bdd\u662f\u771f\u662f\u5047\u3002\u6211\u4e0d\u662f\u60f3\u8ba8\u597d\u4f60\uff0c\u4e5f\u4e0d\u662f\u663e\u793a\u4ec0\u4e48\u82f1\u96c4\u6c14\u6982\uff0c\u53ea\u662f\u89c9\u5f97\u81ea\u5df1\u4ee5\u524d\u5bf9\u4e0d\u8d77\u4f60\uff0c\u8fd9\u4e48\u505a\uff0c\u81f3\u5c11\u5b89\u5fc3\u4e9b\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4f9d\u65e7\u51b7\u7b11\uff0c\u5e76\u4e0d\u8a00\u8bed\uff0c\u743c\u6069\u4e5f\u4e0d\u518d\u591a\u8bf4\uff0c\u8f6c\u8eab\u51c6\u5907\u79bb\u5f00\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u4ed6\u9661\u7136\u8138\u8272\u5927\u53d8\uff0c\u81ea\u5df1\u5e03\u7f6e\u5728\u8fd9\u5ea7\u623f\u5b50\u5468\u56f4\u7684\u8b66\u6212\u7ed3\u754c\u9661\u7136\u88ab\u89e6\u52a8\uff0c\u544a\u8bc9\u4ed6\u6709\u4e00\u4e2a\u4eba\u6b63\u4ece\u5916\u9762\u95ef\u5165\uff0c\u753b\u9762\u76f4\u63a5\u6620\u5c04\u5728\u8111\u6d77\u4e2d\uff0c\u91d1\u53d1\u3001\u78a7\u77b3\u3001\u94f6\u7532\u5c11\u5973\u2026\u2026</p><p>\u6885\u83f2\u65af\uff01</p></article>"}}}]);
