(window.webpackJsonp=window.webpackJsonp||[]).push([[1607],{1630:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516d\u8282 \u607c\u7f9e\u6210\u6012\u7684\u63a8\u5012",content:"<article><h2>\u7b2c\u4e94\u5341\u516d\u8282 \u607c\u7f9e\u6210\u6012\u7684\u63a8\u5012</h2><p>\u82b1\u4e86\u5927\u7ea6\u4e94\u5206\u949f\uff0c\u743c\u6069\u7b80\u5355\u5730\u8ba9\u6885\u83f2\u65af\u660e\u767d\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\u3002</p><p>\u6628\u5929\u4e0a\u5348\uff0c\u743c\u6069\u548c\u838e\u73de\u514b\u5728\u6c83\u91d1\u6b65\u9053\u7684\u9b54\u6cd5\u5546\u5e97\u5bfb\u627e\u9f99\u8840\uff0c\u4e00\u65e0\u6240\u83b7\uff0c\u5728\u56de\u6765\u7684\u8def\u4e0a\u9047\u5230\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u5353\u5c14\u5c11\u5973\u9080\u8bf7\u4ed6\u4eec\u5230\u4e86\u4f4f\u5904\uff0c\u8c08\u8d77\u8fd1\u51b5\uff0c\u743c\u6069\u8bf4\u5230\u51db\u7684\u75c5\u60c5\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8868\u793a\u5979\u624b\u4e0a\u6709\u4e00\u74f6\u5bdf\u65af\u8428\u7684\u9f99\u8840\uff0c\u5b8c\u5168\u7b26\u5408\u8981\u6c42\uff0c\u662f\u5979\u673a\u7f18\u5de7\u5408\u4e4b\u4e0b\u5f97\u6765\u7684\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u5c31\u662f\u77e5\u60c5\u8005\uff0c\u6240\u4ee5\u6307\u70b9\u743c\u6069\u7b49\u4eba\u6765\u963f\u65af\u5361\u7279\u62c9\u78b0\u8fd0\u6c14\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u63d0\u51fa\uff0c\u5979\u53ef\u4ee5\u628a\u9f99\u8840\u9001\u7ed9\u743c\u6069\uff0c\u4f46\u6761\u4ef6\u662f\u743c\u6069\u548c\u5979\u4e0a\u5e8a\u3002\u743c\u6069\u8003\u8651\u4e4b\u540e\u540c\u610f\uff0c\u4e8e\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u7ed9\u4e86\u4ed6\u9f99\u8840\uff0c\u4e8e\u662f\u4ed6\u4eec\u8131\u8863\u670d\uff0c\u6070\u597d\u838e\u73de\u514b\u4e5f\u5728\u65c1\u8fb9\uff0c\u7d22\u6027\u52a0\u8fdb\u6765\u4e00\u8d77\u73a9\u2026\u2026\u7136\u540e\u6885\u83f2\u65af\u6765\u4e86\u3002</p><p>\u8fd9\u6bb5\u8bdd\u534a\u771f\u534a\u5047\uff0c\u771f\u5047\u63ba\u6742\uff0c\u4e5f\u7b26\u5408\u5f53\u4e8b\u4eba\u7684\u6027\u683c\uff0c\u66f4\u6709\u624b\u4e0a\u7684\u9f99\u8840\u4f5c\u8bc1\u660e\uff0c\u786e\u5b9e\u5f88\u6709\u8bf4\u670d\u529b\uff0c\u6885\u83f2\u65af\u4e5f\u4e0d\u5f97\u4e0d\u4fe1\u3002\u4f46\u5979\u552f\u4e00\u7684\u7591\u60d1\uff0c\u662f\u90a3\u4e2a\u524d\u53bb\u7ed9\u5979\u62a5\u4fe1\uff0c\u8bf4\u743c\u6069\u5728\u8fd9\u91cc\u9047\u5230\u5371\u9669\uff0c\u8981\u5979\u524d\u6765\u6551\u63f4\u7684\u4eba\uff0c\u53c8\u662f\u4ec0\u4e48\u76ee\u7684\uff1f</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u662f\u6709\u4eba\u4f2a\u9020\u6d88\u606f\uff0c\u9a97\u4f60\u6765\u8fd9\u91cc\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u743c\u6069\u56de\u5934\u671d\u5367\u5ba4\u65b9\u5411\u770b\u4e86\u4e00\u773c\uff0c\u82e5\u6709\u6240\u601d\uff0c\u201c\u90a3\u6211\u5927\u6982\u660e\u767d\u4e86\u3002\u201d</p><p>\u4e8e\u662f\u6885\u83f2\u65af\u4e5f\u660e\u767d\u4e86\u3002</p><p>\u743c\u6069\u7684\u610f\u601d\uff0c\u663e\u7136\u662f\u5728\u6697\u793a\u8fd9\u662f\u4e00\u4e2a\u5708\u5957\uff0c\u8bbe\u8ba1\u8005\u5c31\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u5979\u4ee5\u9f99\u8840\u505a\u8bf1\u9975\uff0c\u8ba9\u743c\u6069\u548c\u5979\u4e0a\u5e8a\uff0c\u540c\u65f6\u53c8\u6545\u610f\u6d3e\u4eba\u901a\u77e5\u6885\u83f2\u65af\u524d\u6765\uff0c\u8ba9\u5723\u6b66\u58eb\u770b\u5230\u8fd9\u573a\u666f\uff0c\u8ba9\u4e24\u4eba\u53d1\u751f\u51b2\u7a81\uff0c\u611f\u60c5\u7834\u88c2\uff0c\u62a5\u6614\u65e5\u7684\u4e00\u7bad\u4e4b\u4ec7\u3002\u8fd9\u5f53\u7136\u662f\u5728\u683d\u8d43\uff0c\u4f46\u5374\u7b26\u5408\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u4efd\uff0c\u6885\u83f2\u65af\u4e5f\u6e05\u695a\u5979\u548c\u743c\u6069\u4e4b\u95f4\u7684\u4ec7\u6028\uff0c\u5982\u679c\u8bf4\u8bbe\u8ba1\u51fa\u8fd9\u6837\u7684\u9677\u9631\u6765\uff0c\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u6ca1\u4ec0\u4e48\u597d\u5947\u602a\u7684\u3002</p><p>\u5982\u6b64\u4e00\u6765\uff0c\u6574\u4ef6\u4e8b\u60c5\u5c31\u5b8c\u5168\u8bf4\u5f97\u901a\u4e86\u3002</p><p>\u5f97\u77e5\u743c\u6069\u662f\u4e3a\u4e86\u6551\u51db\u624d\u6765\u641e\u5973\u4eba\uff0c\u7ed3\u679c\u8fd8\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u9634\u4e86\u4e00\u9053\uff0c\u6885\u83f2\u65af\u6012\u6c14\u7565\u5e73\uff0c\u4f55\u51b5\u4ed6\u6bd5\u7adf\u8fd8\u662f\u62ff\u5230\u4e86\u9f99\u8840\u3002\u201c\u4f60\u80fd\u786e\u5b9a\u8fd9\u771f\u7684\u662f\u9f99\u8840\uff1f\u201d\u5979\u95ee\u743c\u6069\uff0c\u201c\u90a3\u4e2a\u5353\u5c14\u4e0d\u4f1a\u505a\u624b\u811a\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u662f\u771f\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5728\u4e66\u4e0a\u8bfb\u8fc7\u6709\u5173\u9f99\u8840\u7684\u63cf\u8ff0\uff0c\u548c\u8fd9\u5b8c\u5168\u7b26\u5408\uff0c\u800c\u4e14\u7eaf\u5ea6\u660e\u663e\u66f4\u9ad8\u3002\u8fd9\u4e24\u5929\u6211\u57fa\u672c\u90fd\u548c\u5979\u5728\u4e00\u8d77\uff0c\u5c31\u7b97\u5979\u60f3\u9020\u5047\uff0c\u90a3\u4e5f\u6ca1\u65f6\u95f4\uff0c\u9664\u975e\u5979\u80fd\u672a\u535c\u5148\u77e5\uff0c\u63d0\u524d\u505a\u597d\u4e00\u4e2a\u5047\u7684\u7b49\u6211\u4eec\u6765\u3002\u201d</p><p>\u201c\u4e07\u4e00\u5c31\u662f\u5047\u7684\u5462\uff1f\u201d</p><p>\u201c\u90a3\u6211\u6740\u4e86\u5979\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u548c\u6211\u6709\u4ec7\uff0c\u8bbe\u5708\u5957\u9634\u6211\uff0c\u90a3\u662f\u6211\u81ea\u5df1\u7b28\uff0c\u770b\u4e0d\u51fa\u7834\u7efd\uff0c\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\u3002\u5018\u82e5\u5979\u6562\u5bb3\u6211\u7684\u5973\u4eba\uff0c\u90a3\u53ef\u5c31\u522b\u602a\u6211\u4e0d\u5ba2\u6c14\u3002\u8fd9\u4e2a\u9053\u7406\uff0c\u5979\u5e94\u8be5\u660e\u767d\u7684\u3002\u201d</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u884c\u554a\uff0c\u201d\u5979\u5632\u8bbd\uff0c\u201c\u8fd8\u771f\u6709\u70b9\u7537\u4eba\u7684\u6837\u5b50\u3002\u201d</p><p>\u743c\u6069\u5c34\u5c2c\uff0c\u201c\u827e\u5f25\u8587\u2026\u2026\u201d</p><p>\u201c\u522b\u8bf4\u4e86\uff0c\u4f60\u5c31\u5f53\u6211\u8fd9\u51e0\u5929\u5fc3\u60c5\u4e0d\u597d\uff0c\u53d1\u53d1\u7262\u9a9a\uff0c\u201d\u5723\u6b66\u58eb\u5c06\u9f99\u8840\u6536\u5165\u6000\u4e2d\uff0c\u8f6c\u8eab\u5f80\u5916\u8d70\uff0c\u201c\u8be5\u5e72\u561b\u5e72\u561b\u53bb\uff0c\u4e8b\u60c5\u5b8c\u4e86\u65e9\u70b9\u56de\u6765\u3002\u201d</p><p>\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u770b\u7740\u5979\u7684\u8eab\u5f71\u5728\u8d70\u5eca\u8f6c\u89d2\u5904\u6d88\u5931\uff0c\u4e24\u5206\u949f\u540e\uff0c\u8b66\u6212\u7ed3\u754c\u4f20\u56de\u7684\u8baf\u606f\u544a\u8bc9\u4ed6\u6885\u83f2\u65af\u5df2\u7ecf\u79bb\u5f00\u8fd9\u5ea7\u623f\u5b50\u3002\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u8f6c\u8eab\u56de\u5230\u5367\u5ba4\u3002</p><p>\u82cf\u4f26\u7267\u5e08\u53d8\u5f62\u6210\u7684\u9b45\u9b54\u4f9d\u65e7\u8eba\u5728\u5730\u6bef\u4e0a\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5219\u5750\u5728\u6c99\u53d1\u4e2d\uff0c\u5374\u6ca1\u7a7f\u4e0a\u8863\u670d\uff0c\u5168\u8eab\u4f9d\u65e7\u53ea\u6709\u4e00\u4ef6\u7d2b\u8272\u4e01\u5b57\u88e4\u548c\u4e00\u53cc\u9ed1\u8272\u957f\u9774\uff0c\u94f6\u53d1\u63a9\u80f8\uff0c\u8fd1\u4e4e\u8d64\u88f8\uff0c\u6027\u611f\u8bf1\u4eba\uff0c\u6b63\u7b11\u541f\u541f\u5730\u770b\u7740\u743c\u6069\u3002</p><p>\u201c\u591a\u8c22\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6b20\u4f60\u4e00\u4e2a\u4eba\u60c5\u3002\u201d</p><p>\u521a\u624d\u6885\u83f2\u65af\u7a81\u7136\u524d\u6765\uff0c\u743c\u6069\u60c5\u6025\u4e4b\u4e0b\u628a\u82cf\u4f26\u7267\u5e08\u53d8\u6210\u838e\u73de\u514b\uff0c\u53c8\u8ba9\u7ef4\u5eb7\u5c3c\u4e9a\u8131\u6389\u8863\u670d\uff0c\u4f2a\u88c5\u6210\u6b63\u5728\u4e0a\u5e8a\u7684\u6a21\u6837\u3002\u5f53\u65f6\u60c5\u51b5\u7d27\u6025\uff0c\u6839\u672c\u65e0\u6687\u89e3\u91ca\uff0c\u4f46\u5353\u5c14\u5c11\u5973\u5374\u4e5f\u7b97\u662f\u51b0\u96ea\u806a\u660e\uff0c\u5f88\u5feb\u5c31\u53cd\u5e94\u8fc7\u6765\uff0c\u4e88\u4ee5\u914d\u5408\uff0c\u5e76\u5728\u6700\u540e\u5173\u5934\u4e3b\u52a8\u201c\u54ac\u201d\u4f4f\u4e86\u743c\u6069\uff0c\u4fdd\u8bc1\u6574\u573a\u620f\u6ca1\u6709\u7a7f\u5e2e\uff0c\u6210\u529f\u9a97\u4f4f\u4e86\u6885\u83f2\u65af\uff0c\u53ef\u8c13\u662f\u5c45\u529f\u81f3\u4f1f\uff0c\u90fd\u53ef\u4ee5\u53bb\u9886\u6700\u4f73\u5973\u4e3b\u89d2\u5956\u4e86\u3002\u5c31\u8fd9\u70b9\u800c\u8a00\uff0c\u743c\u6069\u5b9e\u5728\u662f\u627f\u4e86\u5bf9\u65b9\u5f88\u5927\u4e00\u4e2a\u4eba\u60c5\u3002</p><p>\u800c\u4e14\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u95ee\u9898\u3002\u4ee5\u524d\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u4e5f\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0a\u8fc7\u5e8a\u3002\u76f4\u63a5\u641e\u5979\u5979\u81ea\u7136\u5f88\u723d\uff0c\u540e\u5ead\u662f\u743c\u6069\u5f3a\u884c\u5f00\u82de\u7684\uff0c\u73a9\u4e86\u51e0\u6b21\u4e5f\u5c31\u6e10\u6e10\u9002\u5e94\uff0c\u4e0d\u518d\u6297\u62d2\uff0c\u4f46\u6bcf\u6b21\u743c\u6069\u60f3\u4eab\u7528\u5979\u7684\u5c0f\u5634\u65f6\uff0c\u5353\u5c14\u5c11\u5973\u90fd\u662f\u575a\u51b3\u53cd\u5bf9\uff0c\u62fc\u6b7b\u4e0d\u4ece\uff0c\u53ea\u5f97\u7f62\u4e86\u3002\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u548c\u5353\u5c14\u7684\u98ce\u4fd7\u6709\u5173\uff0c\u5728\u4ed6\u4eec\u7684\u793e\u4f1a\u4f53\u7cfb\u91cc\uff0c\u5973\u6027\u5730\u4f4d\u81f3\u4e0a\uff0c\u5c24\u5176\u5973\u796d\u53f8\u66f4\u662f\u9876\u5c42\u9636\u7ea7\uff0c\u53ea\u6709\u5979\u4eec\u88ab\u7537\u6027\u8ba8\u597d\u9022\u8fce\uff0c\u6ca1\u6709\u81ea\u5df1\u653e\u4e0b\u8eab\u6bb5\u53bb\u53d6\u60a6\u4f8d\u5949\u7537\u6027\u7684\u9053\u7406\u3002\u743c\u6069\u4e0a\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u65f6\u5019\uff0c\u5979\u4e00\u5f00\u59cb\u662f\u5973\u796d\u53f8\uff0c\u540e\u6765\u6210\u4e86\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\uff0c\u5730\u4f4d\u6446\u5728\u90a3\u91cc\uff0c\u81ea\u7136\u4e5f\u6709\u76f8\u5e94\u7684\u81ea\u5c0a\uff0c\u65e0\u8bba\u743c\u6069\u600e\u4e48\u529d\u8bf1\uff0c\u5979\u90fd\u662f\u6447\u5934\u4e0d\u80af\uff0c\u6ca1\u6709\u4efb\u4f55\u5546\u91cf\u4f59\u5730\u3002</p><p>\u8d8a\u662f\u5f97\u4e0d\u5230\u7684\uff0c\u53cd\u800c\u8d8a\u662f\u60f3\u8981\uff0c\u7537\u4eba\u5927\u6982\u90fd\u6709\u8fd9\u79cd\u5fc3\u7406\u3002\u5982\u679c\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u6162\u6162\u8c03\u6559\u7684\u8bdd\uff0c\u743c\u6069\u6216\u8bb8\u80fd\u591f\u6539\u53d8\u5979\u7684\u575a\u6301\uff0c\u53ef\u60dc\u4e0d\u4e45\u4e4b\u540e\u5c31\u662f\u4f0a\u5361\u6c99\u57ce\u4e4b\u6218\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u6210\u4e3a\u9636\u4e0b\u56da\uff0c\u53cc\u65b9\u5173\u7cfb\u5f7b\u5e95\u7834\u88c2\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7528\u8c08\u4e86\u3002\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u4e00\u76f4\u5f15\u4ee5\u4e3a\u61be\u3002</p><p>\u7136\u800c\u5c31\u5728\u521a\u624d\uff0c\u8fd9\u4e2a\u66fe\u7ecf\u7684\u5e0c\u671b\u7a00\u91cc\u7cca\u6d82\u5730\u5c31\u5f97\u5230\u4e86\u5b9e\u73b0\uff0c\u5728\u743c\u6069\u81ea\u5df1\u90fd\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u4f53\u9a8c\u5230\u4e86\u5353\u5c14\u5c11\u5973\u7684\u6e29\u70ed\u53e3\u8154\uff0c\u4eab\u7528\u4e86\u5979\u7684\u5507\u820c\u4f8d\u5949\u3002\u56de\u60f3\u8d77\u8fd9\u70b9\uff0c\u9664\u4e86\u201c\u4e16\u4e8b\u65e0\u5e38\u201d\uff0c\u8fd8\u771f\u662f\u4e0d\u77e5\u9053\u5e94\u8be5\u8bf4\u4ec0\u4e48\u3002</p><p>\u4e0d\u8fc7\uff0c\u5b9a\u4e0b\u795e\u6765\u4ed4\u7ec6\u56de\u5473\u521a\u624d\u7684\u611f\u53d7\uff0c\u4f3c\u4e4e\u80fd\u591f\u53d1\u73b0\u66f4\u591a\u7684\u8baf\u606f\u3002\u5353\u5c14\u5c11\u5973\u5f88\u52aa\u529b\uff0c\u52a8\u4f5c\u4e5f\u5f88\u79ef\u6781\uff0c\u4f46\u4f9d\u65e7\u662f\u663e\u5f97\u592a\u751f\u6da9\u4e86\uff0c\u660e\u663e\u6ca1\u4ec0\u4e48\u7ecf\u9a8c\u2026\u2026\u5982\u679c\u743c\u6069\u6ca1\u731c\u9519\u7684\u8bdd\uff0c\u5341\u6709\u516b\u4e5d\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002</p><p>\u5514\uff0c\u5982\u679c\u771f\u662f\u8fd9\u6837\uff0c\u90a3\u53ef\u771f\u662f\u723d\u5230\u4e86\u3002</p><p>\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u521a\u624d\u7ef4\u5eb7\u5c3c\u4e9a\u63f4\u624b\u76f8\u52a9\u662f\u4e8b\u5b9e\uff0c\u743c\u6069\u4e0d\u662f\u4e0d\u77e5\u597d\u6b79\u7684\u4eba\uff0c\u6709\u4ec7\u62a5\u4ec7\uff0c\u6709\u6069\u62a5\u6069\uff0c\u57fa\u672c\u7684\u9053\u4e49\u603b\u8981\u8bb2\u3002\u5f80\u6614\u6709\u4ec7\uff0c\u90a3\u662f\u4e24\u519b\u76f8\u4e89\uff0c\u5404\u4e3a\u5176\u4e3b\uff0c\u743c\u6069\u4e5f\u6ca1\u529e\u6cd5\uff0c\u96be\u4e0d\u6210\u8fd8\u5e2e\u5353\u5c14\u5bf9\u4ed8\u77ee\u4eba\uff0c\u963b\u6320\u9634\u9b42\u57ce\u7684\u5927\u8ba1\uff1f\u8fd9\u662f\u516c\u4e8b\uff0c\u8c08\u4e0d\u4e0a\u8c01\u5bf9\u8c01\u9519\uff0c\u743c\u6069\u4e00\u5f00\u59cb\u5c31\u662f\u53bb\u505a\u5367\u5e95\u7684\uff0c\u53ea\u80fd\u8bf4\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u5df1\u89c1\u8bc6\u4e0d\u591f\uff0c\u4fe1\u9519\u4e86\u4eba\u3002\u8fd9\u6b21\u5219\u4e0d\u540c\uff0c\u662f\u743c\u6069\u79c1\u4eba\u627f\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5927\u4eba\u60c5\uff0c\u8fd9\u5c31\u5f97\u62a5\u7b54\u4e86\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u80fd\u62a5\u7b54\u6211\u7684\uff1f\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u6b3a\u9a97\u6211\uff0c\u5f3a\u5978\u6211\uff0c\u5bb3\u5f97\u6211\u5bb6\u7834\u4eba\u4ea1\uff0c\u4ece\u5e7d\u6697\u5730\u57df\u6700\u5927\u7684\u5353\u5c14\u57ce\u5e02\u7684\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u5c0f\u7267\u5e08\u2014\u2014\u4f60\u6b20\u6211\u7684\u5e10\uff0c\u6740\u4e86\u4f60\u90fd\u8fd8\u4e0d\u6e05\uff0c\u4f60\u8fd8\u62ff\u4ec0\u4e48\u62a5\u7b54\u6211\uff1f\u201d</p><p>\u743c\u6069\u9ed8\u7136\u3002</p><p>\u201c\u600e\u4e48\uff0c\u6ca1\u8bdd\u8bf4\u4e86\uff1f\u201d\u5353\u5c14\u5c11\u5973\u51b7\u7b11\uff0c\u201c\u73b0\u5728\u8bf4\u5f97\u597d\u542c\uff0c\u8bf4\u627f\u6211\u7684\u60c5\uff0c\u53ef\u662f\u5728\u51e0\u5206\u949f\u524d\uff0c\u6211\u600e\u4e48\u597d\u50cf\u542c\u5230\u5916\u9762\u8d70\u5eca\u91cc\uff0c\u6709\u4e2a\u4eba\u660e\u91cc\u6697\u91cc\u5730\u5728\u9a82\u6211\uff0c\u8bf4\u6211\u8bbe\u5708\u5957\u5bb3\u4ed6\uff0c\u8fd8\u5a01\u80c1\u8bf4\u8981\u6740\u4e86\u6211\u2014\u2014\u90a3\u4e2a\u4eba\u662f\u8c01\u554a\uff1f\u201d</p><p>\u743c\u6069\u7ee7\u7eed\u9ed8\u7136\u3002\u521a\u624d\u9762\u5bf9\u6885\u83f2\u65af\uff0c\u4e3a\u4e86\u6253\u6d88\u5979\u7684\u7591\u5fc3\uff0c\u65e0\u5948\u4e4b\u4e0b\u53ea\u80fd\u628a\u4e8b\u60c5\u90fd\u63a8\u5230\u7ef4\u5eb7\u5c3c\u4e9a\u5934\u4e0a\uff0c\u8fd9\u6837\u8bf4\u8d77\u6765\u5929\u8863\u65e0\u7f1d\uff0c\u80fd\u591f\u7792\u9a97\u8fc7\u53bb\u3002\u4f46\u7ef4\u5eb7\u5c3c\u4e9a\u521a\u521a\u5e2e\u4e86\u743c\u6069\u7684\u5927\u5fd9\uff0c\u4e00\u8f6c\u8eab\u5c31\u53c8\u80cc\u4e0a\u4e86\u8fd9\u6837\u7684\u9ed1\u9505\u2026\u2026\u8bf4\u53e5\u5b9e\u8bdd\uff0c\u743c\u6069\u81ea\u5df1\u90fd\u89c9\u5f97\u81ea\u5df1\u591f\u5351\u9119\u7684\u3002</p><p>\u201c\u7b97\u4e86\uff0c\u4e0d\u8bf4\u8fd9\u4e2a\u4e86\uff0c\u6ca1\u610f\u601d\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u61d2\u61d2\u8bf4\uff0c\u201c\u4f60\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u65e9\u8be5\u660e\u767d\uff0c\u90a3\u6837\u4e5f\u4e0d\u4f1a\u4e0a\u4f60\u7684\u5f53\uff0c\u4f46\u73b0\u5728\u660e\u767d\u4e5f\u8fd8\u4e0d\u7b97\u665a\u3002\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u743c\u6069\uff0c\u6211\u8fd8\u771f\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u4f60\u8fd9\u4e48\u60ca\u60f6\u5931\u63aa\u7684\u6837\u5b50\u5462\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u201c\u662f\u554a\uff0c\u201d\u5353\u5c14\u5c11\u5973\u70b9\u5934\uff0c\u201c\u6211\u4eec\u5728\u4e00\u8d77\u7684\u65f6\u95f4\u662f\u4e0d\u957f\uff0c\u4f46\u6211\u76f8\u4fe1\u8fd8\u662f\u5bf9\u4f60\u6709\u70b9\u4e86\u89e3\u7684\u3002\u4f60\u7b97\u4e0d\u4e0a\u52c7\u6562\uff0c\u4f46\u5374\u4e5f\u6781\u5c11\u771f\u6b63\u6050\u60e7\u4ec0\u4e48\uff0c\u518d\u5f3a\u5927\u7684\u654c\u4eba\uff0c\u518d\u5371\u9669\u7684\u5904\u5883\uff0c\u518d\u68d8\u624b\u7684\u96be\u9898\uff0c\u4f60\u4f1a\u5934\u75bc\uff0c\u4f1a\u8e0c\u8e87\uff0c\u4f1a\u4ed4\u7ec6\u601d\u7d22\uff0c\u4f1a\u90d1\u91cd\u5bf9\u5f85\uff0c\u4f46\u4f60\u53ef\u4ece\u6765\u6ca1\u6709\u53d1\u81ea\u5185\u5fc3\u5730\u5bb3\u6015\u8fc7\uff0c\u771f\u6b63\u6050\u60e7\u8fc7\u5427\u3002\u6709\u65f6\u5019\uff0c\u6211\u90fd\u89c9\u5f97\u4f60\u50cf\u662f\u4e2a\u8eab\u7ecf\u767e\u6218\u5386\u7ecf\u6ca7\u6851\u7684\u8001\u8005\u4f3c\u7684\uff0c\u751f\u751f\u6b7b\u6b7b\u90fd\u80fd\u770b\u5f97\u6de1\u4e86\uff0c\u4e0d\u5f53\u4e00\u56de\u4e8b\u3002\u4f46\u4f60\u521a\u624d\u7684\u6837\u5b50\u53ef\u662f\u5b8c\u5168\u4e0d\u540c\uff0c\u8138\u8272\u82cd\u767d\uff0c\u773c\u795e\u614c\u4e71\uff0c\u60ca\u60f6\u5931\u63aa\uff0c\u5c31\u597d\u50cf\u770b\u5230\u4e86\u6076\u9b54\u4e2d\u7684\u6076\u9b54\uff0c\u68a6\u9b47\u4e2d\u7684\u68a6\u9b47\u4e00\u6837\u2014\u2014\u8fd9\u53ef\u771f\u662f\u2026\u2026\u771f\u662f\u4ee4\u6211\u51fa\u4e4e\u610f\u6599\u5462\uff0c\u201d\u5979\u6311\u8d77\u79c0\u7709\uff0c\u51dd\u89c6\u7740\u743c\u6069\uff0c\u201c\u90a3\u4e2a\u5973\u5723\u6b66\u58eb\uff0c\u5728\u4f60\u5fc3\u76ee\u4e2d\u7684\u5730\u4f4d\uff0c\u5c31\u771f\u7684\u6709\u90a3\u4e48\u91cd\u8981\u5417\uff1f\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u662f\uff0c\u201d\u4ed6\u627f\u8ba4\uff0c\u201c\u975e\u5e38\u975e\u5e38\u91cd\u8981\u3002\u201d</p><p>\u201c\u91cd\u8981\u5230\u8fd9\u79cd\u5730\u6b65\uff1f\u8ba9\u4f60\u90fd\u4f1a\u5bb3\u6015\u5979\uff0c\u754f\u60e7\u5979\uff1f\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u662f\u5bb3\u6015\u5979\uff0c\u754f\u60e7\u5979\uff0c\u56e0\u4e3a\u2026\u2026\u6211\u7231\u5979\u3002\u201d</p><p>\u201c\u4f60\u7231\u5979\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u53cd\u95ee\uff0c\u201c\u90a3\u4f60\u4f1a\u4e3a\u4e86\u5979\u653e\u5f03\u522b\u7684\u5973\u5b69\u5b50\u5417\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u3002</p><p>\u201c\u4e0d\u4f1a\uff1f\u90a3\u5c31\u522b\u8c08\u4ec0\u4e48\u7231\u4e0d\u7231\u7684\uff0c\u60f9\u4eba\u53d1\u7b11\u4e86\uff0c\u201d\u5353\u5c14\u5c11\u5973\u8f9b\u8fa3\u5730\u8bc4\u4ef7\uff0c\u201c\u4f60\u5c31\u662f\u4e2a\u96c4\u6027\uff0c\u770b\u5230\u6f02\u4eae\u7684\u96cc\u6027\u5c31\u60f3\u4e0a\u3002\u6216\u8bb8\u5979\u786e\u5b9e\u5728\u4f60\u5fc3\u91cc\u5730\u4f4d\u9ad8\u4e9b\uff0c\u5206\u91cf\u91cd\u4e9b\uff0c\u4f46\u8bf4\u5230\u5e95\u4e5f\u5c31\u662f\u4e2a\u53ef\u4ee5\u7528\u6765\u4e0a\u7684\u96cc\u6027\u800c\u5df2\uff0c\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u7528\u201c\u96c4\u6027\u201d\u548c\u201c\u96cc\u6027\u201d\u6765\u4ee3\u66ff\u201c\u7537\u4eba\u201d\u548c\u201c\u5973\u4eba\u201d\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u610f\u601d\u663e\u7136\u662f\u5728\u5632\u8bbd\u743c\u6069\u5c31\u662f\u4e2a\u6b32\u671b\u52a8\u7269\u3002\u201c\u662f\u554a\u3002\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u671d\u5979\u8d70\u8fc7\u6765\u3002</p><p>\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5c31\u50cf\u4f60\u8bf4\u7684\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u6211\u5c31\u662f\u4e2a\u96c4\u6027\uff0c\u770b\u5230\u6f02\u4eae\u7684\u96cc\u6027\u5c31\u60f3\u4e0a\uff0c\u73b0\u5728\u5c31\u6709\u4e00\u4e2a\u6f02\u4eae\u96cc\u6027\u8131\u5149\u4e86\u5728\u6211\u9762\u524d\u2014\u2014\u4f60\u8bf4\u6211\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u203b\u203b\u203b</p><p>\u81ea\u4ece\u56de\u5230\u5730\u8868\u4e4b\u540e\uff0c\u63a5\u8fde\u649e\u4e0a\u9ebb\u70e6\uff0c\u743c\u6069\u5df2\u7ecf\u5c0f\u534a\u4e2a\u6708\u6ca1\u78b0\u8fc7\u5973\u4eba\u4e86\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u4e60\u60ef\u4e86\u5de6\u62e5\u53f3\u62b1\uff0c\u6574\u5929\u5c71\u73cd\u6d77\u5473\u7684\u4eba\u6765\u8bf4\uff0c\u7a81\u7136\u8ba9\u4ed6\u7edd\u98df\u51e0\u5929\uff0c\u90a3\u611f\u89c9\u5b9e\u5728\u7cdf\u7cd5\u5f97\u4e00\u584c\u7cca\u6d82\u3002\u56e0\u4e3a\u62c5\u5fe7\u51db\u7684\u75c5\u60c5\uff0c\u6574\u65e5\u4e3a\u4e4b\u5954\u8d70\uff0c\u5fd9\u7740\u627e\u9f99\u8840\uff0c\u5fd9\u7740\u6740\u4eba\uff0c\u4e00\u65f6\u4e5f\u65e0\u6687\u987e\u53ca\u3002\u7136\u800c\u521a\u624d\u56e0\u4e3a\u8981\u6f14\u620f\u7ed9\u6885\u83f2\u65af\u770b\uff0c\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u8fd9\u4e00\u54ac\uff0c\u672c\u6765\u5c31\u5df2\u7ecf\u706b\u6c14\u5347\u817e\uff0c\u518d\u770b\u5979\u6027\u611f\u5a07\u8eaf\u534a\u88f8\u5750\u5728\u6c99\u53d1\u4e2d\uff0c\u59ff\u6001\u4f18\u96c5\u4eff\u4f5b\u5973\u738b\uff0c\u66f4\u52a0\u6309\u637a\u4e0d\u4f4f\u3002\u73b0\u5728\u53c8\u88ab\u5979\u51b7\u5632\u70ed\u8bbd\uff0c\u867d\u7136\u627f\u8ba4\u5979\u8bf4\u7684\u662f\u4e8b\u5b9e\uff0c\u81ea\u5df1\u6ca1\u4ec0\u4e48\u53ef\u5206\u8fa9\u7684\uff0c\u4f46\u88ab\u4e00\u4e2a\u5973\u4eba\u5c45\u9ad8\u4e34\u4e0b\u5730\u6559\u8bad\u595a\u843d\uff0c\u5fc3\u91cc\u603b\u662f\u4e0d\u723d\u3002\u743c\u6069\u672c\u4e5f\u5c31\u4e0d\u662f\u90a3\u79cd\u8c26\u900a\u793c\u8ba9\u7684\u4eba\uff0c\u8868\u9762\u4e0a\u8fd8\u7b97\u6e29\u548c\uff0c\u9aa8\u5b50\u91cc\u81ea\u8d1f\u5f97\u7d27\uff0c\u4e00\u5f00\u59cb\u5ff5\u7740\u5979\u521a\u624d\u4e3b\u52a8\u63f4\u624b\u76f8\u6551\u7684\u60c5\u5206\uff0c\u4e0d\u60f3\u8ba1\u8f83\uff0c\u6253\u7b97\u8f6c\u8eab\u8d70\u4eba\u4e86\u4e8b\uff0c\u7ed3\u679c\u7ef4\u5eb7\u5c3c\u4e9a\u8d8a\u8bf4\u8d8a\u591a\uff0c\u767b\u65f6\u6709\u4e9b\u607c\u4e86\uff0c\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u63a8\u5012\u518d\u8bf4\uff0c\u53cd\u6b63\u4ee5\u524d\u53c8\u4e0d\u662f\u6ca1\u4e0a\u8fc7\u3002</p><p>\u743c\u6069\u4e00\u628a\u5c06\u5353\u5c14\u5c11\u5973\u62b1\u8d77\uff0c\u7ffb\u8fc7\u8eab\u6765\uff0c\u8ba9\u5979\u53cc\u624b\u6276\u7740\u6c99\u53d1\uff0c\u6446\u6210\u80cc\u5165\u7684\u59ff\u52bf\uff0c\u5f84\u76f4\u523a\u8fdb\u3002\u4ed6\u52a8\u4f5c\u6709\u4e9b\u7c97\u66b4\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4f4e\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u6ca1\u534a\u70b9\u6297\u62d2\uff0c\u4efb\u4ed6\u6446\u5e03\u3002\u8fd9\u6b21\u5374\u4e0d\u540c\u521a\u624d\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u62ff\u4e86\u9f99\u8840\u8d70\u4eba\uff0c\u5a01\u80c1\u8fdc\u53bb\uff0c\u5fc3\u60c5\u653e\u677e\uff0c\u53cc\u65b9\u53c8\u90fd\u6709\u4e86\u5fc3\u7406\u51c6\u5907\uff0c\u8eab\u4f53\u81ea\u7136\u4e5f\u6709\u53cd\u5e94\uff0c\u957f\u77db\u5df2\u7ecf\u5341\u5206\u575a\u5229\uff0c\u82b1\u5f84\u4e5f\u4e0d\u5e72\u6db8\uff0c\u4e00\u5207\u987a\u5229\u3002\u5353\u5c14\u5c11\u5973\u5a07\u541f\u8fde\u8fde\uff0c\u521d\u65f6\u4f3c\u4e4e\u8fd8\u6709\u4e9b\u75db\u695a\uff0c\u6e10\u6e10\u4fbf\u5f00\u59cb\u900f\u7740\u8212\u7545\u5feb\u7f8e\uff0c\u6c89\u8ff7\u5176\u4e2d\u3002\u5012\u662f\u743c\u6069\u5927\u7ea6\u618b\u5f97\u4e45\u4e86\uff0c\u53c8\u6709\u6012\u6c14\uff0c\u4e0d\u514d\u5c11\u4e86\u987e\u60dc\uff0c\u6a2a\u51b2\u76f4\u649e\uff0c\u53ea\u89c9\u5353\u5c14\u5c11\u5973\u4f53\u5185\u7d27\u7a84\u5f02\u5e38\uff0c\u4eff\u4f5b\u5904\u5b50\uff0c\u8d8a\u52a0\u8ba9\u4eba\u5174\u594b\u3002</p><p>\u5904\u5b50\u81ea\u7136\u4e0d\u53ef\u80fd\uff0c\u4f46\u786e\u5b9e\u50cf\u5f88\u4e45\u6ca1\u88ab\u5f00\u53d1\u8fc7\u4f3c\u7684\u2026\u2026\u5514\uff0c\u8fd9\u79cd\u4e8b\u60c5\u600e\u4e48\u53ef\u80fd\uff0c\u5979\u53ef\u662f\u4e2a\u5353\u5c14\uff0c\u5c31\u7b97\u81ea\u5df1\u4e0d\u613f\u610f\uff0c\u4ece\u4f0a\u5361\u6c99\u57ce\u79bb\u5f00\u540e\u5c31\u662f\u4e2a\u65e0\u4f9d\u65e0\u9760\u7684\u5f03\u6c11\uff0c\u52a0\u5165\u838e\u5c14\u6559\u4f1a\u5df2\u7ecf\u5feb\u4e00\u5e74\uff0c\u6765\u5730\u8868\u4e5f\u6709\u5927\u534a\u5e74\u4e86\uff0c\u4e00\u6ca1\u80cc\u666f\u4e8c\u6ca1\u6839\u57fa\uff0c\u957f\u5f97\u53c8\u6f02\u4eae\uff0c\u8981\u8bf4\u6ca1\u88ab\u4e0a\u8fc7\uff0c\u8fd9\u4e5f\u592a\u8352\u5510\u4e86\u3002</p><p>\u9053\u7406\u662f\u5982\u6b64\uff0c\u4f46\u6446\u5728\u9762\u524d\u2026\u2026\u4e0d\uff0c\u6446\u5728\u8eab\u4e0b\u7684\u4e8b\u5b9e\u5374\u4e5f\u6ca1\u6cd5\u8ba9\u4eba\u65e0\u89c6\u3002\u4e00\u5f00\u59cb\u8fd8\u4e0d\u89c9\u5f97\uff0c\u6e10\u6e10\u5730\u4fbf\u663e\u51fa\u8ff9\u8c61\u6765\uff0c\u77ed\u77ed\u534a\u5c0f\u65f6\u4e4b\u5185\uff0c\u5353\u5c14\u5c11\u5973\u5df2\u7ecf\u88ab\u63a5\u8fde\u9001\u4e0a\u4e24\u6b21\u9ad8\u6f6e\uff0c\u8fd9\u5728\u4ee5\u524d\u662f\u65e0\u6cd5\u60f3\u50cf\u7684\uff0c\u8eab\u4f53\u7684\u53cd\u5e94\u6e05\u695a\u8868\u660e\u5979\u6781\u5ea6\u9965\u6e34\uff0c\u662f\u6807\u51c6\u7684\u4e45\u65f7\u4e4b\u8eab\uff0c\u5c11\u8bf4\u4e5f\u6709\u534a\u5e74\u6ca1\u6709\u88ab\u7537\u4eba\u6ecb\u6da6\u8fc7\u4e86\u3002\u5979\u521d\u65f6\u8fd8\u6781\u529b\u60f3\u8981\u4fdd\u6301\u795e\u667a\u6e05\u9192\uff0c\u7136\u800c\u6ca1\u575a\u6301\u591a\u4e45\u4fbf\u88ab\u5982\u6f6e\u5feb\u611f\u51b2\u51fb\u5f97\u4e00\u6e83\u5343\u91cc\uff0c\u5353\u5c14\u6d78\u900f\u5728\u8840\u8109\u91cc\u7684\u6deb\u4e71\u672c\u6027\u66b4\u9732\u65e0\u9057\uff0c\u53d8\u6210\u4e86\u4e0d\u6298\u4e0d\u6263\u7684\u8361\u5987\uff0c\u8eab\u4f53\u4e3b\u52a8\u8fce\u5408\u6536\u7f29\uff0c\u50cf\u4e00\u6735\u566c\u4eba\u7684\u5996\u8273\u82b1\u854a\uff0c\u7d22\u9700\u65e0\u5ea6\u3002</p><p>\u201c\u5e72\uff0c\u8fd9\u4e48\u6d6a\uff0c\u611f\u89c9\u90fd\u5feb\u8981\u4e0d\u662f\u5bf9\u624b\u4e86\u3002\u201d</p><p>\u81ea\u5df1\u6765\u63a8\u5012\u5973\u4eba\uff0c\u5982\u679c\u6700\u540e\u53cd\u800c\u8d25\u4e0b\u9635\u6765\uff0c\u90a3\u5b9e\u5728\u662f\u5f88\u4e22\u8138\u7684\u4e8b\u60c5\u3002\u597d\u5728\u743c\u6069\u4e5f\u662f\u4e45\u7ecf\u953b\u70bc\uff0c\u53c8\u4ece\u5c0f\u5b66\u4e60\u4fb5\u6deb\u6b64\u9053\uff0c\u5012\u8fd8\u80fd\u575a\u6301\u4e0b\u6765\uff0c\u518d\u6084\u6084\u7528\u70b9\u91c7\u8865\u4e4b\u672f\uff0c\u4e00\u5207\u4fbf\u8f7b\u8f7b\u677e\u677e\u3002\u4e24\u4eba\u8fd9\u4e00\u573a\u5927\u6218\u641e\u5f97\u660f\u5929\u9ed1\u5730\uff0c\u8db3\u8db3\u6298\u817e\u4e86\u4e24\u4e2a\u591a\u5c0f\u65f6\uff0c\u743c\u6069\u603b\u7b97\u624d\u628a\u5353\u5c14\u5c11\u5973\u6446\u5e73\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u5168\u8eab\u762b\u8f6f\uff0c\u4eff\u4f5b\u6240\u6709\u529b\u6c14\u90fd\u88ab\u62bd\u7a7a\u4f3c\u7684\uff0c\u61d2\u6d0b\u6d0b\u5730\u8eba\u5728\u5730\u6bef\u4e0a\uff0c\u4e00\u4e1d\u4e00\u6beb\u90fd\u4e0d\u613f\u610f\u518d\u52a8\u5f39\uff0c\u6ee1\u8138\u90fd\u662f\u5fc3\u6ee1\u610f\u8db3\u7684\u5a07\u5a9a\u8868\u60c5\uff0c\u6b8b\u7559\u7740\u521a\u521a\u6fc0\u60c5\u6b22\u597d\u8fc7\u7684\u4f59\u97f5\u3002\u743c\u6069\u5374\u8fd8\u6709\u4e9b\u610f\u72b9\u672a\u5c3d\uff0c\u8f6c\u773c\u770b\u89c1\u8eba\u5728\u65c1\u8fb9\u7684\u82cf\u4f26\u7267\u5e08\uff0c\u56e0\u4e3a\u65f6\u95f4\u7684\u5173\u7cfb\uff0c\u5979\u8eab\u4e0a\u7684\u53d8\u5f62\u672f\u5df2\u7ecf\u89e3\u9664\uff0c\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\uff0c\u58f0\u97f3\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u7528\u6bd2\u9488\u5c01\u4f4f\u4e86\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u4f46\u773c\u4e2d\u5374\u5c3d\u662f\u6050\u60e7\uff0c\u53c8\u5e26\u7740\u4e00\u4e1d\u5014\u5f3a\uff0c\u867d\u7136\u5e74\u9f84\u5927\u4e86\u4e9b\uff0c\u5012\u4e5f\u9887\u6709\u51e0\u5206\u6210\u719f\u98ce\u97f5\u3002\u743c\u6069\u7d22\u6027\u4e5f\u62b1\u4e86\u8fc7\u6765\uff0c\u892a\u4e0b\u8863\u88d9\u76f4\u63a5\u5c31\u4e0a\uff0c\u611f\u89c9\u677e\u677e\u57ae\u57ae\uff0c\u4e0d\u7531\u5f97\u626b\u5174\uff0c\u9a6c\u9a6c\u864e\u864e\u641e\u4e86\u4e00\u4f1a\uff0c\u63a8\u5230\u4e00\u8fb9\uff0c\u4f9d\u65e7\u8fd8\u662f\u56de\u6765\u5bf9\u4ed8\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u5353\u5c14\u5c11\u5973\u539f\u672c\u5df2\u7ecf\u7b4b\u75b2\u529b\u5c3d\uff0c\u4e5f\u65e0\u529b\u6297\u62d2\uff0c\u4efb\u743c\u6069\u5728\u5979\u4f53\u5185\u51b2\u649e\uff0c\u7136\u800c\u6ca1\u8fc7\u7247\u523b\u4fbf\u53c8\u6709\u4e86\u53cd\u5e94\uff0c\u773c\u770b\u5c31\u53c8\u8981\u518d\u6500\u4e0a\u9ad8\u5cf0\u3002\u743c\u6069\u5fc3\u4e2d\u5b9e\u5728\u7591\u60d1\uff0c\u4e0d\u7531\u5f97\u4fbf\u95ee\u51fa\u53e3\uff0c\u201c\u5582\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201d\u4ed6\u6293\u7740\u5353\u5c14\u5c11\u5973\u7684\u94f6\u8272\u957f\u53d1\uff0c\u50cf\u9a91\u9a6c\u4e00\u6837\u9a70\u9a8b\u51b2\u649e\uff0c\u201c\u4f60\u4e0d\u4f1a\u662f\u5f88\u4e45\u6ca1\u548c\u7537\u4eba\u505a\u8fc7\u4e86\u5427\uff1f\u201d</p><p>\u4ed6\u5176\u5b9e\u4e5f\u53ea\u662f\u968f\u53e3\u4e00\u95ee\uff0c\u5e76\u6ca1\u6709\u5f53\u771f\u671f\u5f85\u56de\u7b54\u3002\u6216\u8005\u5c31\u7b97\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u60f3\u5fc5\u4e5f\u662f\u201c\u600e\u4e48\u53ef\u80fd\u201d\u4e4b\u7c7b\u7684\u8bdd\u3002\u7136\u800c\u8ba9\u4ed6\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5353\u5c14\u5c11\u5973\u7741\u5f00\u773c\u775b\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u9690\u7ea6\u6709\u4e00\u4e1d\u9ad8\u6df1\u83ab\u6d4b\u7684\u7b11\u610f\uff0c\u201c\u662f\u554a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u7684\u6559\u4f1a\u53c8\u4e0d\u9f13\u52b1\u6027\u7231\uff0c\u6ca1\u505a\u8fc7\u6709\u4ec0\u4e48\u5947\u602a\u7684\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e0d\u5728\u7109\u5730\u5e94\u4e86\u58f0\uff0c\u7a0d\u7a0d\u540e\u9000\uff0c\u7136\u540e\u7528\u529b\u633a\u8170\uff0c\u5b8c\u5168\u6ca1\u5165\u5c11\u5973\u7684\u8eab\u4f53\u3002\u4ed6\u6b63\u5728\u4eab\u53d7\uff0c\u9661\u7136\u53cd\u5e94\u8fc7\u6765\u521a\u624d\u7ef4\u5eb7\u5c3c\u4e9a\u8bdd\u4e2d\u7684\u542b\u4e49\uff0c\u4e0d\u7531\u5f97\u9661\u7136\u4e00\u60ca\u3002</p><p>\u201c\u4f60\u7684\u6559\u4f1a\uff1f\u201d</p><p>\u521a\u624d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u7684\u662f\u201c\u6211\u7684\u6559\u4f1a\u201d\uff0c\u800c\u4e0d\u662f\u201c\u6211\u4eec\u6559\u4f1a\u201d\uff0c\u6216\u8005\u201c\u6211\u6240\u5728\u7684\u6559\u4f1a\u201d\uff0c\u867d\u7136\u8fd9\u4e48\u8bf4\u4e5f\u4e0d\u662f\u5c31\u4e0d\u884c\uff0c\u53ef\u4ee5\u89e3\u91ca\u6210\u6709\u4ee5\u6559\u4f1a\u4e3a\u5bb6\u7684\u89c9\u609f\u3002\u7136\u800c\u914d\u5408\u4e0a\u521a\u624d\u5979\u7684\u8bed\u6c14\u3001\u795e\u6001\u2026\u2026</p><p>\u201c\u591c\u5973\u58eb\uff1f\u201d</p><p>\u201c\u7ef4\u5eb7\u5c3c\u4e9a\u201d\u683c\u683c\u5a07\u7b11\u8d77\u6765\uff0c\u201c\u53cd\u5e94\u633a\u5feb\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u91cd\u65b0\u6253\u4e2a\u62db\u547c\u5427\uff0c\u597d\u4e45\u4e0d\u89c1\u5462\uff0c\u743c\u6069\u3002\u201d</p></article>"}}}]);
