(window.webpackJsonp=window.webpackJsonp||[]).push([[1609],{1632:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516b\u8282 \u6682\u522b",content:"<article><h2>\u7b2c\u4e94\u5341\u516b\u8282 \u6682\u522b</h2><p>\u83ab\u540d\u5176\u5999\u5730\uff0c\u81ea\u5df1\u591a\u4e86\u4e00\u4e2a\u5973\u795e\u505a\u59d0\u59d0\uff0c\u8fd8\u662f\u4e16\u754c\u4e0a\u6700\u53e4\u8001\u3001\u6700\u5f3a\u5927\u7684\u5973\u795e\u591c\u5973\u58eb\u838e\u5c14\uff0c\u5373\u4fbf\u5728\u5168\u8d39\u4f26\u5927\u9646\u7684\u795e\u7947\u4e2d\uff0c\u5979\u4e5f\u662f\u7b2c\u4e00\u7b49\u7684\u5b58\u5728\u3002\u5f53\u4e86\u59d0\u59d0\u4e0d\u8bf4\uff0c\u8fd8\u8bf4\u8981\u7ed9\u5f1f\u5f1f\u9001\u751f\u65e5\u793c\u7269\u2026\u2026\u5e0c\u671b\u591c\u5973\u58eb\u80fd\u591f\u9ad8\u62ac\u8d35\u624b\uff0c\u5bbd\u5b8f\u5927\u91cf\uff0c\u4e0d\u8981\u9001\u51fa\u4ec0\u4e48\u532a\u5937\u6240\u601d\u7684\u4e1c\u897f\u6765\u624d\u597d\uff0c\u5426\u5219\u743c\u6069\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u548c\u540c\u4f34\u4ea4\u4ee3\u4e86\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u6709\u4e00\u4e2a\u671f\u671b\uff0c\u5c31\u662f\u5973\u795e\u80fd\u591f\u6539\u6539\u55dc\u597d\uff0c\u4e0d\u8981\u603b\u662f\u8fd9\u4e48\u559c\u6b22\u73a9\u5723\u8005\u964d\u4e34\u2014\u2014\u5c31\u7b97\u8981\u73a9\uff0c\u81f3\u5c11\u4e5f\u5f97\u770b\u770b\u573a\u5408\uff0c\u6311\u6311\u65f6\u5019\u3002\u743c\u6069\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u91cc\uff0c\u59d0\u59d0\u73ca\u5609\u548c\u8299\u857e\u72c4\u90fd\u662f\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u8299\u8389\u5a05\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u66f4\u662f\u7267\u5e08\uff0c\u838e\u5c14\u90fd\u6709\u53ef\u80fd\u9644\u4f53\uff0c\u81ea\u5df1\u6b63\u5728\u5e8a\u4e0a\u9a70\u9a8b\u51b2\u950b\uff0c\u8eab\u4f53\u4e0b\u9762\u4e0d\u58f0\u4e0d\u54cd\u5730\u5c31\u6362\u4e86\u4e2a\u4eba\u2026\u2026\u8fd9\u79cd\u4e8b\u60c5\u591a\u6765\u51e0\u6b21\uff0c\u90a3\u662f\u7edd\u5bf9\u4f1a\u9020\u6210\u5fc3\u7406\u969c\u788d\u7684\u3002</p><p>\u4e0d\u8fc7\u603b\u4f53\u6765\u8bf4\uff0c\u743c\u6069\u8fd8\u662f\u975e\u5e38\u611f\u8c22\u591c\u5973\u58eb\u7684\u8fd9\u6b21\u5723\u8005\u964d\u4e34\uff0c\u867d\u7136\u5413\u4e86\u81ea\u5df1\u4e00\u8df3\uff0c\u4f46\u4e5f\u6709\u76f8\u5e94\u7684\u56de\u62a5\u3002\u9996\u5148\u662f\u641e\u6e05\u695a\u4e86\u683c\u62c9\u5179\u7279\u7684\u6253\u7b97\uff0c\u5f97\u77e5\u81ea\u5df1\u548c\u9b45\u9b54\u7684\u771f\u540d\u5951\u7ea6\u7684\u771f\u5b9e\u7528\u9014\uff1b\u5176\u6b21\u662f\u7ed9\u81ea\u5df1\u6572\u4e86\u4e00\u8bb0\u8b66\u949f\uff0c\u4e0d\u8981\u56e0\u4e3a\u8fd9\u6bb5\u65f6\u95f4\u592a\u987a\u5229\u800c\u5f97\u610f\u5fd8\u5f62\uff0c\u5426\u5219\u80af\u5b9a\u683d\u66f4\u5927\u7684\u8ddf\u5934\uff1b\u6700\u540e\u4e5f\u662f\u6700\u91cd\u8981\u7684\uff0c\u5979\u89e3\u9664\u4e86\u743c\u6069\u4e00\u76f4\u4ee5\u6765\u57cb\u85cf\u5728\u5fc3\u5e95\u7684\u62c5\u5fe7\u3002</p><p>\u59d0\u59d0\u73ca\u5609\u4e0d\u662f\u591c\u5973\u58eb\u7684\u5723\u8005\u3002</p><p>\u838e\u5c14\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u5728\u9634\u9b42\u57ce\u4e4b\u5185\uff0c\u5979\u65e0\u6cd5\u5723\u8005\u964d\u4e34\uff0c\u8fd9\u662f\u5979\u548c\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u7684\u534f\u8bae\uff0c\u5982\u679c\u743c\u6069\u4e0d\u76f8\u4fe1\uff0c\u53ef\u4ee5\u5411\u5e03\u96f7\u7eb3\u65af\u6c42\u8bc1\uff0c\u4e0d\u8fc7\u5e94\u8be5\u6ca1\u8fd9\u4e2a\u5fc5\u8981\u3002\u838e\u5c14\u6216\u8bb8\u4f1a\u8bf4\u8c0e\uff0c\u4f46\u5728\u8fd9\u79cd\u592a\u5bb9\u6613\u8bc1\u4f2a\u6233\u7a7f\u7684\u4e8b\u60c5\u4e0a\uff0c\u8bf4\u8c0e\u5c31\u662f\u4fae\u8fb1\u5f7c\u6b64\u7684\u667a\u5546\u4e86\u3002\u5982\u6b64\u4e00\u6765\uff0c\u5c31\u8bc1\u5b9e\u4e86\u743c\u6069\u4f53\u5185\u7684\u5f71\u706b\u6765\u6e90\uff0c\u548c\u73ca\u5609\u65e0\u5173\uff0c\u786e\u5b9e\u662f\u5728\u585e\u5c14\u63a8\u5012\u90a3\u4e2a\u5973\u4ec6\u83f2\u5a05\u65f6\u5f97\u6765\u7684\u3002</p><p>\u5728\u6df1\u6e0a\u65ad\u57df\u9547\u7684\u65f6\u5019\uff0c\u743c\u6069\u63a8\u6d4b\u81ea\u5df1\u4f53\u5185\u7684\u5f71\u706b\u6765\u6e90\uff0c\u731c\u6d4b\u662f\u591c\u5973\u58eb\u9644\u4f53\u83f2\u5a05\uff0c\u800c\u6885\u83f2\u65af\u5219\u63d0\u51fa\u53e6\u5916\u4e00\u79cd\u53ef\u80fd\uff0c\u5c31\u662f\u838e\u5c14\u9644\u4f53\u73ca\u5609\u3002\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u8fd9\u4e24\u79cd\u53ef\u80fd\u90fd\u8bf4\u5f97\u901a\uff0c\u743c\u6069\u81ea\u7136\u975e\u5e38\u4e0d\u5e0c\u671b\u540e\u9762\u4e00\u79cd\u662f\u4e8b\u5b9e\uff0c\u4f46\u53c8\u65e0\u6cd5\u5f7b\u5e95\u5426\u8ba4\uff0c\u6a2a\u4e98\u5728\u5fc3\u91cc\u5341\u5206\u96be\u53d7\uff0c\u5c31\u8fde\u8fd9\u6b21\u8981\u56de\u9634\u9b42\u57ce\uff0c\u4ed6\u90fd\u6709\u4e9b\u80c6\u602f\uff0c\u4e0d\u77e5\u9053\u5e94\u8be5\u4ee5\u4ec0\u4e48\u5fc3\u6001\u6765\u9762\u5bf9\u73ca\u5609\u3002\u5982\u4eca\u838e\u5c14\u66ff\u4ed6\u6d88\u9664\u4e86\u8fd9\u4e00\u5927\u9690\u5fe7\uff0c\u987f\u65f6\u4fbf\u5927\u5927\u8212\u4e86\u53e3\u6c14\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u5378\u4e0b\u5343\u65a4\u91cd\u62c5\uff0c\u4e00\u4e0b\u5b50\u8f7b\u677e\u4e86\u8bb8\u591a\u3002</p><p>\u838e\u5c14\u79bb\u5f00\u4e4b\u524d\uff0c\u987a\u4fbf\u8fd8\u66ff\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u626b\u9664\u4e86\u9762\u4e34\u7684\u4e00\u4e9b\u5c0f\u9ebb\u70e6\u3002\u9996\u5148\u662f\u90a3\u4e2a\u82cf\u4f26\u7267\u5e08\uff0c\u672c\u6765\u8fd8\u8981\u4e3e\u884c\u4eea\u5f0f\uff0c\u7948\u7977\u732e\u796d\uff0c\u5982\u4eca\u838e\u5c14\u76f4\u63a5\u6765\u4e86\uff0c\u4e34\u8d70\u65f6\u987a\u624b\u5c31\u53d6\u8d70\u4e86\u201c\u796d\u54c1\u201d\uff0c\u4e5f\u5c31\u662f\u7075\u9b42\uff0c\u5c38\u4f53\u88ab\u4e00\u53d1\u89e3\u79bb\u672f\u5316\u4f5c\u5fae\u5c18\uff0c\u6d88\u6563\u65e0\u8e2a\uff0c\u4ece\u6b64\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5c31\u4e0d\u5b58\u5728\u4e86\u3002</p><p>\u743c\u6069\u539f\u672c\u8fd8\u62c5\u5fc3\uff0c\u6574\u4ef6\u4e8b\u60c5\u867d\u7136\u505a\u5f97\u9690\u79d8\uff0c\u4f46\u683c\u62c9\u5179\u7279\u548c\u6c83\u91d1\u6559\u4f1a\u5374\u8fd8\u662f\u77e5\u9053\u7684\uff0c\u4e07\u4e00\u8dd1\u53bb\u544a\u53d1\u6216\u8005\u6ee1\u5927\u8857\u5ba3\u626c\uff0c\u90a3\u5c31\u6bd4\u8f83\u9ebb\u70e6\u4e86\uff0c\u81f3\u5c11\u4f1a\u8ba9\u6885\u83f2\u65af\u5fc3\u751f\u6000\u7591\u3002\u4ee5\u4ed6\u539f\u672c\u7684\u6253\u7b97\uff0c\u662f\u628a\u5c38\u4f53\u4e22\u5230\u661f\u6708\u4e4b\u989c\u7f8e\u5bb9\u9662\u53bb\u3002\u4e00\u4e2a\u7267\u5e08\u5931\u8e2a\uff0c\u82cf\u4f26\u6559\u4f1a\u80af\u5b9a\u8981\u8ffd\u67e5\uff0c\u5f88\u5bb9\u6613\u5c31\u67e5\u5230\u7f8e\u5bb9\u9662\u53bb\uff0c\u4e00\u65e6\u53d1\u73b0\u4e86\u5c38\u4f53\uff0c\u90a3\u5c31\u662f\u4e24\u4e2a\u6559\u4f1a\u4e4b\u95f4\u626f\u76ae\u3002\u6c83\u91d1\u6559\u4f1a\u80af\u5b9a\u4f1a\u5426\u8ba4\u548c\u4ed6\u4eec\u6709\u5173\uff0c\u4f46\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u4f9b\u51fa\u743c\u6069\u7b49\u4eba\uff0c\u5426\u5219\u5c31\u5f97\u89e3\u91ca\u4e3a\u4ec0\u4e48\u7ed3\u754c\u65e0\u6548\uff0c\u6ca1\u6709\u76d1\u63a7\uff0c\u5c31\u5f97\u627f\u8ba4\u81ea\u5df1\u4e5f\u662f\u540c\u8c0b\u3002\u53cd\u6b63\u683c\u62c9\u5179\u7279\u65e2\u7136\u8bbe\u5c40\u9634\u4ed6\uff0c\u90a3\u4e48\u4ed6\u4e5f\u5c31\u53cd\u8fc7\u6765\u7ed9\u4ed6\u627e\u70b9\u9ebb\u70e6\uff0c\u6c83\u91d1\u6559\u4f1a\u53d7\u4ed6\u63a7\u5236\uff0c\u90a3\u7ed9\u6c83\u91d1\u6559\u4f1a\u6dfb\u4e71\u5b50\u4e5f\u662f\u4e00\u6837\u3002</p><p>\u4f46\u4ed6\u7684\u8fd9\u4e2a\u6253\u7b97\u88ab\u838e\u5c14\u5236\u6b62\u4e86\uff0c\u591c\u5973\u58eb\u8bf4\u4e0d\u8981\u518d\u548c\u683c\u62c9\u5179\u7279\u53d1\u751f\u4e89\u6267\uff0c\u514d\u5f97\u518d\u751f\u679d\u8282\u3002\u81f3\u4e8e\u6c83\u91d1\u6559\u4f1a\u4f1a\u53bb\u544a\u53d1\u6216\u8005\u5ba3\u626c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u53ef\u80fd\u6027\u4e0d\u5927\uff0c\u5979\u4e5f\u4f1a\u548c\u683c\u62c9\u5179\u7279\u4ea4\u6d89\uff0c\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u6b64\u62b9\u53bb\uff0c\u4e0d\u9732\u534a\u70b9\u98ce\u58f0\u3002\u53e6\u5916\u518d\u5b89\u6392\u4e00\u4e2a\u4eba\u53d8\u5f62\u4f2a\u88c5\u6210\u82cf\u4f26\u7267\u5e08\u7684\u6837\u5b50\uff0c\u79bb\u5f00\u7f8e\u5bb9\u9662\uff0c\u5728\u5927\u8857\u4e0a\u901b\u901b\uff0c\u627e\u4e2a\u522b\u7684\u5730\u65b9\u6d88\u5931\uff0c\u8f6c\u79fb\u89c6\u7ebf\uff0c\u6446\u8131\u5acc\u7591\u3002</p><p>\u6709\u5973\u795e\u505a\u5584\u540e\uff0c\u4e00\u5207\u81ea\u7136\u9ad8\u6795\u65e0\u5fe7\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u5f97\u507f\u6240\u613f\uff0c\u83b7\u5f97\u591c\u5973\u58eb\u7684\u9752\u7750\u548c\u8d5e\u8bb8\u3002\u867d\u7136\u6682\u65f6\u6ca1\u6709\u63d0\u5347\u5979\u7684\u4f4d\u9636\uff0c\u4f46\u4f30\u8ba1\u4e5f\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u5bf9\u4e8e\u5973\u795e\u964d\u4e34\u4e4b\u540e\u7684\u60c5\u5f62\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u65e0\u6240\u77e5\uff0c\u4e5f\u6ca1\u6709\u542c\u5230\u743c\u6069\u548c\u838e\u5c14\u7684\u5bf9\u8bdd\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5728\u838e\u5c14\u79bb\u5f00\u4e4b\u540e\uff0c\u5979\u5728\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u91cc\u8fd8\u662f\u5904\u4e8e\u604d\u604d\u60da\u60da\u7684\u5931\u795e\u72b6\u6001\uff0c\u8fd9\u662f\u88ab\u795e\u7947\u9644\u4f53\u7684\u5fc5\u7136\u53cd\u5e94\uff0c\u6ca1\u529e\u6cd5\u3002</p><p>\u5f53\u5929\u591c\u91cc\uff0c\u743c\u6069\u6ca1\u6709\u76f4\u63a5\u8fd4\u56de\u9540\u91d1\u73ab\u7470\u9152\u5e97\uff0c\u800c\u662f\u7559\u5728\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5e8a\u4e0a\u3002\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u5012\u4e0d\u662f\u8bf4\u8d2a\u604b\u7f8e\u8272\uff0c\u4e3b\u8981\u662f\u9b45\u9b54\u8fd8\u88ab\u56f0\u5728\u534a\u4f4d\u9762\u4e2d\u4e0d\u80fd\u8fd4\u56de\u3002\u743c\u6069\u81ea\u5df1\u4e00\u4e2a\u4eba\u56de\u53bb\uff0c\u6015\u8ba9\u6885\u83f2\u65af\u8d77\u7591\u5fc3\uff0c\u53c8\u8981\u7f16\u7406\u7531\u89e3\u91ca\uff0c\u8fd8\u662f\u514d\u4e86\u8fd9\u9053\u9ebb\u70e6\u4e3a\u597d\u3002</p><p>\u5feb\u5230\u51cc\u6668\u65f6\u5206\uff0c\u838e\u73de\u514b\u7ec8\u4e8e\u6210\u529f\u4ece\u534a\u4f4d\u9762\u4e2d\u8131\u8eab\uff0c\u56de\u5230\u743c\u6069\u8eab\u8fb9\u3002\u5979\u88ab\u5723\u8a00\u8fce\u9762\u51fb\u4e2d\uff0c\u4f46\u56e0\u4e3a\u662f\u5f02\u754c\u5b58\u5728\uff08\u6076\u9b54\uff09\uff0c\u6cd5\u672f\u7684\u5f88\u5927\u4e00\u90e8\u5206\u5a01\u529b\u90fd\u7528\u4e8e\u56da\u7981\uff0c\u771f\u6b63\u4f5c\u7528\u4e8e\u81ea\u8eab\u7684\u90e8\u5206\u5e76\u4e0d\u591a\uff0c\u6076\u9b54\u81ea\u6108\u80fd\u529b\u53c8\u5f3a\uff0c\u4f11\u606f\u4e86\u516b\u4e2a\u5c0f\u65f6\uff0c\u5df2\u7ecf\u5b8c\u5168\u6062\u590d\u3002\u5012\u662f\u5979\u8eab\u4e0a\u7684\u90a3\u4ef6\u708e\u9b54\u76ae\u7532\uff0c\u56e0\u4e3a\u66ff\u5979\u627f\u53d7\u4e86\u5927\u90e8\u4efd\u4f24\u5bb3\uff0c\u88ab\u795e\u5723\u529b\u91cf\u707c\u4f24\u70e7\u7834\u4e86\u597d\u51e0\u4e2a\u5927\u6d1e\uff0c\u867d\u7136\u8fd8\u80fd\u7a7f\uff0c\u6548\u679c\u5374\u5927\u6253\u6298\u6263\u3002\u8fd9\u79cd\u90aa\u9b54\u5236\u54c1\u53c8\u5f88\u96be\u4fee\u8865\u6062\u590d\uff0c\u53ea\u80fd\u5c06\u5c31\u4e86\u3002</p><p>\u56e0\u4e3a\u771f\u540d\u5951\u7ea6\u7684\u5173\u7cfb\uff0c\u5979\u76f4\u63a5\u51fa\u73b0\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5367\u5ba4\u91cc\uff0c\u6b63\u770b\u89c1\u743c\u6069\u5728\u597d\u6574\u4ee5\u6687\u5730\u4eab\u7528\u7740\u5353\u5c14\u5c11\u5973\uff0c\u9b45\u9b54\u81ea\u7136\u6beb\u4e0d\u5403\u60ca\uff0c\u76f4\u63a5\u52a0\u5165\uff0c\u4e09\u4eba\u4e00\u76f4\u73a9\u5230\u7b4b\u75b2\u529b\u5c3d\uff0c\u65b9\u624d\u6c89\u6c89\u7761\u53bb\u3002\u7b49\u518d\u9192\u6765\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u4e2d\u5348\u4e86\u3002</p><p>\u201c\u8fd9\u4e48\u665a\u4e86\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\uff0c\u5306\u5306\u8d77\u8eab\u6d17\u4e86\u4e2a\u6fa1\uff0c\u548c\u838e\u73de\u514b\u8d76\u56de\u9152\u5e97\u3002\u548c\u5973\u4eba\u4e0a\u5e8a\u4e8e\u4ed6\u800c\u8a00\u4e0d\u7b97\u65b0\u9c9c\u4e8b\uff0c\u4f46\u73a9\u5f97\u8fd9\u4e48\u6fc0\u70c8\uff0c\u5f04\u5230\u8fd9\u4e48\u665a\uff0c\u5374\u662f\u5934\u4e00\u906d\u3002\u60f3\u8d77\u6885\u83f2\u65af\u8fd8\u5728\u7b49\u4ed6\uff0c\u5fc3\u4e0b\u4e0d\u7531\u5f97\u4fbf\u6709\u4e9b\u5fd0\u5fd1\u3002\u6ca1\u60f3\u5230\u56de\u5230\u9152\u5e97\uff0c\u521a\u521a\u63a8\u95e8\u8fdb\u623f\u95f4\uff0c\u5c31\u611f\u89c9\u773c\u524d\u7ea2\u5f71\u4e00\u95ea\uff0c\u4e00\u5177\u67d4\u8f6f\u5a07\u8eaf\u5e26\u7740\u9999\u98ce\u6251\u5230\u4ed6\u6000\u91cc\u3002</p><p>\u201c\u65e9\u5b89\uff0c\u743c\u6069\u3002\u201d</p><p>\u94f6\u94c3\u822c\u7684\u5c11\u5973\u55d3\u97f3\uff0c\u751c\u7f8e\u4e2d\u5e26\u7740\u5b69\u5b50\u6c14\u7684\u7b11\u5bb9\uff0c\u78a7\u7eff\u7684\u773c\u7738\u4e2d\u9690\u9690\u6709\u706b\u5149\u8df3\u8dc3\uff0c\u6d53\u5bc6\u7684\u9ed1\u53d1\u7f16\u6210\u4e24\u6761\u6f02\u4eae\u7684\u9a6c\u5c3e\u8fab\uff0c\u5a07\u4fcf\u53ef\u7231\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u660f\u7761\u591a\u65e5\u7684\u51db\u3002</p><p>\u201c\u554a\uff0c\u4f60\u9192\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e5f\u5f88\u662f\u9ad8\u5174\uff0c\u6478\u6478\u5979\u7684\u5c0f\u8111\u888b\u3002\u6c89\u7761\u51e0\u65e5\uff0c\u51db\u7684\u6c14\u8272\u5374\u4e1d\u6beb\u6ca1\u6709\u53d8\u5dee\uff0c\u53cd\u800c\u66f4\u52a0\u5bb9\u5149\u7115\u53d1\uff0c\u8273\u4e3d\u7167\u4eba\uff0c\u987e\u76fc\u4e4b\u95f4\u7075\u6c14\u6d41\u52a8\uff0c\u5ae3\u7136\u751f\u8f89\uff0c\u4e00\u65f6\u95f4\u51e0\u4e4e\u628a\u6885\u83f2\u65af\u90fd\u9690\u9690\u6bd4\u4e86\u4e0b\u53bb\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u7b80\u76f4\u5c31\u662f\u8131\u80ce\u6362\u9aa8\u4e86\u4e00\u822c\u3002\u5f88\u663e\u7136\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7ed9\u7684\u90a3\u74f6\u7ea2\u9f99\u795e\u8840\uff0c\u786e\u5b9e\u662f\u6709\u5947\u6548\u3002</p><p>\u201c\u4f60\u771f\u6f02\u4eae\u3002\u201d\u743c\u6069\u7531\u8877\u8bf4\u3002</p><p>\u201c\u662f\u554a\uff0c\u6211\u73b0\u5728\u53ef\u662f\u771f\u6b63\u7684\u9f99\u5973\u4e86\uff0c\u201d\u51db\u5f97\u610f\u5730\u8bf4\uff0c\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u51db\u8fd9\u4e2a\u540d\u5b57\u624d\u7b97\u662f\u771f\u6b63\u540d\u526f\u5176\u5b9e\u4e86\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u51db\u5176\u5b9e\u662f\u9f99\u540d\uff0c\u201d\u6885\u83f2\u65af\u5750\u5728\u623f\u95f4\u91cc\u7684\u6c99\u53d1\u91cc\uff0c\u624b\u4e2d\u6b63\u62ff\u7740\u4e00\u672c\u4e66\uff0c\u201c\u5de8\u9f99\u7684\u540d\u5b57\uff0c\u4e00\u822c\u90fd\u662f\u50cf\u8428\u83f2\u8389\u5a05\u65af\u3001\u5370\u683c\u7f57\u5361\u65af\u63d0\u5bc6\u9f50\u91cc\u5b89\u3001\u4f0a\u59c6\u74e6\u5c14\u7eb3\u5965\u8fd9\u79cd\uff0c\u65e2\u957f\u53c8\u62d7\u53e3\uff0c\u9664\u4e86\u5b83\u4eec\u81ea\u5df1\u6ca1\u4eba\u613f\u610f\u8bb0\uff0c\u4f46\u5b83\u4eec\u5f80\u5f80\u540c\u65f6\u4e5f\u6709\u7b80\u6d01\u7684\u540d\u5b57\uff0c\u4f8b\u5982\u94f6\u7130\u3001\u70c1\u5f71\u3001\u51b0\u4ea1\u8fd9\u79cd\uff0c\u51db\u5c31\u662f\u6b64\u7c7b\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u743c\u6069\u70b9\u5934\u8868\u793a\u660e\u767d\uff0c\u201c\u90a3\u51db\u4f60\u771f\u6b63\u7684\u539f\u540d\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u51db\u7684\u7236\u6bcd\u7ed9\u5979\u53d6\u7684\u8fd9\u4e2a\u540d\u5b57\u5f88\u8d34\u5207\uff0c\u4e00\u542c\u5c31\u77e5\u9053\u662f\u901f\u5ea6\u6781\u5feb\uff0c\u53cd\u5e94\u8fc5\u6377\u7684\u7c7b\u578b\u3002\u53ef\u60dc\u5979\u8d70\u7684\u662f\u5deb\u5e08\u7684\u9053\u8def\uff0c\u5018\u82e5\u53bb\u5f53\u6740\u624b\uff0c\u90a3\u5c31\u66f4\u5b8c\u7f8e\u4e86\u3002</p><p>\u51db\u8bf4\u4e86\u51e0\u53e5\uff0c\u81ea\u5df1\u5654\u5654\u5654\u8dd1\u5230\u697c\u4e0b\u7528\u9910\u53bb\u4e86\uff0c\u623f\u95f4\u91cc\u53ea\u5269\u4e0b\u743c\u6069\u548c\u6885\u83f2\u65af\u3002\u4e00\u95ee\u624d\u77e5\u9053\uff0c\u539f\u6765\u6885\u83f2\u65af\u62ff\u56de\u9f99\u8840\u4e4b\u540e\uff0c\u72b9\u8c6b\u518d\u4e09\uff0c\u8fd8\u662f\u5c06\u5b83\u7ed9\u51db\u5582\u4e86\u4e0b\u53bb\uff0c\u6ca1\u60f3\u5230\u6548\u679c\u597d\u5f97\u51fa\u4e4e\u610f\u6599\uff0c\u6628\u665a\u5582\u4e0b\uff0c\u4eca\u5929\u4e0a\u5348\u51db\u4fbf\u9192\u4e86\u8fc7\u6765\uff0c\u7cbe\u795e\u5955\u5955\uff0c\u5168\u65e0\u534a\u70b9\u4e0d\u9002\uff0c\u5143\u7d20\u8f6c\u5316\u6cd5\u9635\u8fd0\u4f7f\u8d77\u6765\u4e5f\u8d8a\u53d1\u5f97\u5fc3\u5e94\u624b\uff0c\u6bd4\u4ee5\u524d\u66f4\u8fdb\u4e00\u5c42\u3002\u5979\u4e00\u9ad8\u5174\u4e4b\u4e0b\uff0c\u8fd8\u8dd1\u5230\u57ce\u5916\u53bb\u8bd5\u9a8c\u4e86\u4e00\u4e0b\u201c\u9f99\u5316\u201d\uff0c\u679c\u7136\u6210\u529f\u53d8\u8eab\u6210\u4e86\u4e00\u53ea\u5c11\u5e74\u7ea2\u9f99\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u51db\u8fd9\u6b21\u5012\u7b97\u662f\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d\u6885\u83f2\u65af\u6de1\u6de1\u5e94\u4e86\u4e00\u58f0\u3002</p><p>\u743c\u6069\u4fbf\u4e0d\u77e5\u9053\u518d\u8bf4\u4ec0\u4e48\uff0c\u4e24\u4eba\u90fd\u6ca1\u518d\u51fa\u58f0\uff0c\u623f\u95f4\u91cc\u6c14\u6c1b\u4e00\u65f6\u6709\u4e9b\u6c89\u95f7\uff0c\u8fc7\u4e86\u5927\u7ea6\u5341\u5206\u949f\uff0c\u6885\u83f2\u65af\u62ac\u8d77\u5934\u6765\uff0c\u201c\u743c\u6069\uff0c\u201d\u5979\u5408\u4e0a\u624b\u4e2d\u7684\u4e66\u672c\uff0c\u201c\u4f60\u6253\u7b97\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\u542f\u7a0b\u5462\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u53ef\u80fd\u8fc7\u4e24\u5929\u52a8\u8eab\u5427\uff0c\u53cd\u6b63\u4e0d\u7740\u6025\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u6bb5\u65f6\u95f4\u5927\u5bb6\u90fd\u5fd9\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u4f60\u90fd\u6ca1\u597d\u597d\u4f11\u606f\u8fc7\u3002\u73b0\u5728\u51db\u6062\u590d\u4e86\uff0c\u6b63\u597d\u53c8\u662f\u5728\u94f6\u5e01\u4e4b\u90fd\uff0c\u96be\u5f97\u6765\u4e00\u8d9f\uff0c\u4e0d\u59a8\u4f11\u606f\u4e24\u5929\u518d\u8bf4\uff0c\u4e5f\u6b63\u597d\u5230\u5904\u8d70\u8d70\u3002\u201d</p><p>\u201c\u90a3\u884c\uff0c\u4f60\u5f85\u51e0\u5929\uff0c\u6211\u548c\u51db\u660e\u5929\u5148\u52a8\u8eab\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u4e3a\u4ec0\u4e48\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u53c8\u4e0d\u8d76\u65f6\u95f4\uff0c\u518d\u8bf4\u5982\u679c\u4f60\u771f\u6025\u7740\u8981\u8d70\uff0c\u90a3\u6211\u4eec\u5f53\u7136\u4e00\u8d77\u8d70\u554a\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u4f60\u662f\u8981\u56de\u9634\u9b42\u57ce\uff0c\u5f80\u5317\u8d70\uff1b\u6211\u548c\u51db\u6253\u7b97\u53bb\u4e00\u8d9f\u963f\u683c\u62c9\u9686\uff0c\u90a3\u6b63\u597d\u4ece\u8fd9\u91cc\u76f4\u63a5\u5f80\u4e1c\u8d70\u3002\u53cd\u6b63\u53c8\u4e0d\u540c\u8def\uff0c\u6211\u4eec\u5148\u8d70\u4e5f\u4e00\u6837\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u2026\u2026\u4f60\u8fd8\u5728\u751f\u6211\u7684\u6c14\u554a\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u6ca1\u751f\u6c14\uff0c\u6211\u662f\u8bf4\u771f\u7684\u3002\u6211\u548c\u51db\u5546\u91cf\u4e86\u4e00\u4e0b\uff0c\u5979\u73b0\u5728\u867d\u7136\u9192\u4e86\uff0c\u8fd9\u4e00\u5173\u7b97\u662f\u5ea6\u8fc7\uff0c\u4f46\u63a5\u4e0b\u6765\u7684\u9f99\u72c2\u5374\u662f\u5927\u9ebb\u70e6\uff0c\u773c\u770b\u4e5f\u6ca1\u51e0\u4e2a\u6708\u65f6\u95f4\u4e86\uff0c\u5f97\u53bb\u627e\u5979\u8001\u5e08\u6b23\u5e03\u5546\u91cf\u5bf9\u7b56\uff0c\u6240\u4ee5\u53bb\u963f\u683c\u62c9\u9686\u3002\u6211\u6b63\u597d\u4e5f\u987a\u4fbf\u56de\u4e00\u8d9f\u8ff7\u65af\u5353\u8bfa\uff0c\u6709\u70b9\u4e8b\u60c5\u5f97\u53bb\u627e\u5927\u4e3b\u6559\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u518d\u8bf4\u4e86\uff0c\u6211\u5176\u5b9e\u4e5f\u4e0d\u662f\u5f88\u559c\u6b22\u9634\u9b42\u57ce\uff0c\u4f60\u4e5f\u77e5\u9053\u7684\uff0c\u5728\u90a3\u91cc\u603b\u662f\u4e0d\u81ea\u5728\uff0c\u201d\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u522b\u591a\u60f3\uff0c\u6211\u4e0d\u662f\u8ddf\u4f60\u8d4c\u6c14\uff0c\u771f\u7684\u3002\u80fd\u628a\u51db\u6551\u9192\uff0c\u6211\u5c31\u5f88\u9ad8\u5174\u4e86\uff0c\u5176\u4ed6\u7684\u90a3\u70b9\u4e8b\u60c5\u4e5f\u4e0d\u60f3\u8ba1\u8f83\u3002\u53ea\u662f\u773c\u4e0b\u786e\u5b9e\u90fd\u6709\u4e8b\uff0c\u4f60\u8981\u56de\u9634\u9b42\u57ce\u590d\u547d\uff0c\u6211\u548c\u51db\u53bb\u963f\u683c\u62c9\u9686\u548c\u8ff7\u65af\u5353\u8bfa\uff0c\u5206\u5f00\u8d70\u6bd4\u8f83\u65b9\u4fbf\uff0c\u6ca1\u5fc5\u8981\u786c\u51d1\u4e00\u8d77\u3002\u201d</p><p>\u743c\u6069\u89c1\u5979\u795e\u60c5\uff0c\u5c31\u77e5\u9053\u5fc3\u610f\u5df2\u51b3\uff0c\u591a\u8bf4\u4e5f\u65e0\u76ca\u3002\u201c\u90a3\u597d\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e8b\u60c5\u529e\u5b8c\u4e4b\u540e\u5462\uff1f\u6211\u53bb\u8ff7\u65af\u5353\u8bfa\u627e\u4f60\uff1f\u201d</p><p>\u201c\u884c\uff0c\u6211\u5e94\u8be5\u4f1a\u5728\u8ff7\u65af\u5353\u8bfa\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53cd\u6b63\u6211\u4eec\u6709\u6212\u6307\uff0c\u8ddd\u79bb\u592a\u8fdc\u4e86\u6ca1\u6cd5\u4f20\u9001\uff0c\u81f3\u5c11\u53ef\u4ee5\u4f20\u8baf\u8054\u7cfb\u3002\u201d</p><p>\u201c\u8bb0\u5f97\u4ee3\u6211\u5411\u73ca\u5609\u59d0\u59d0\u95ee\u597d\u3002\u201d\u5979\u6700\u540e\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u51db\u6c89\u7761\u82cf\u9192\uff0c\u800c\u4e14\u987a\u5229\u5b8c\u6210\u201c\u9f99\u5316\u201d\uff0c\u6210\u4e3a\u771f\u6b63\u7684\u9f99\u5973\uff0c\u81ea\u7136\u662f\u503c\u5f97\u9ad8\u5174\u7684\u4e8b\u60c5\u2014\u2014\u81f3\u4e8e\u9f99\u72c2\u7684\u9690\u60a3\uff0c\u5927\u5bb6\u6682\u65f6\u9009\u62e9\u6027\u9057\u5fd8\u4e86\u3002\u7126\u8651\u5fd9\u788c\u4e86\u8fd9\u4e9b\u5929\uff0c\u603b\u7b97\u53ef\u4ee5\u653e\u677e\uff0c\u518d\u52a0\u4e0a\u7b2c\u4e8c\u5929\u5c31\u8981\u5206\u79bb\uff0c\u5f53\u665a\u4fbf\u5f00\u5bb4\u4f1a\u5e86\u795d\u3002\u867d\u7136\u6ca1\u6709\u53d1\u5c55\u6210\u4e71\u4ea4\u6d3e\u5bf9\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u518d\u6b21\u628a\u6885\u83f2\u65af\u548c\u51db\u540c\u65f6\u641e\u4e0a\u5e8a\uff0c\u53ea\u53ef\u60dc\u4ed6\u4e25\u91cd\u4f4e\u4f30\u4e86\u5bf9\u624b\u7684\u5b9e\u529b\uff0c\u6885\u83f2\u65af\u5012\u7f62\u4e86\uff0c\u51db\u5374\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u5979\u53c8\u672c\u6765\u5c31\u653e\u5f97\u5f00\uff0c\u73a9\u5f97\u75af\uff0c\u51e0\u4e4e\u628a\u743c\u6069\u69a8\u6210\u4e86\u4eba\u5e72\u65b9\u624d\u52c9\u5f3a\u7f62\u624b\u3002</p><p>\u77ed\u77ed\u4e8c\u5341\u56db\u5c0f\u65f6\u4e4b\u5185\uff0c\u63a5\u8fde\u4e24\u573a\u201c\u6fc0\u6218\u201d\uff0c\u7b2c\u4e00\u6b21\u7684\u5bf9\u8c61\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u838e\u73de\u514b\uff0c\u5353\u5c14\u52a0\u9b45\u9b54\uff0c\u7b2c\u4e8c\u6b21\u7684\u5bf9\u8c61\u662f\u6885\u83f2\u65af\u548c\u51db\uff0c\u795e\u5b50\u52a0\u9f99\u5973\uff0c\u5168\u90fd\u662f\u6700\u5f3a\u529b\u7684\u7ec4\u5408\u3002\u743c\u6069\u5c31\u7b97\u518d\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u662f\u7d2f\u5f97\u8170\u9178\u80cc\u75db\uff0c\u5934\u8111\u53d1\u6655\uff0c\u4e24\u817f\u8f6f\u5f97\u50cf\u9762\u6761\uff0c\u52c9\u5f3a\u652f\u6491\u7740\u75b2\u5026\u8eab\u4f53\uff0c\u5728\u7b2c\u4e8c\u5929\u65e9\u4e0a\u5c06\u6885\u83f2\u65af\u548c\u51db\u4e24\u4f4d\u5c11\u5973\u9001\u8d70\uff0c\u56de\u5230\u9152\u5e97\u540e\u76f4\u63a5\u6251\u5728\u5e8a\u4e0a\u547c\u547c\u5927\u7761\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7ba1\u4e86\u3002\u5982\u679c\u8fd9\u65f6\u5019\u6709\u4eba\u62ff\u5200\u6765\u780d\u4ed6\uff0c\u743c\u6069\u4f30\u8ba1\u90fd\u61d2\u5f97\u52a8\u5f39\uff0c\u5b9e\u5728\u662f\u592a\u7d2f\u592a\u7d2f\u4e86\u3002</p><p>\u8fd9\u4e00\u89c9\u76f4\u63a5\u7761\u5230\u4e86\u508d\u665a\u65f6\u5206\uff0c\u743c\u6069\u624d\u9192\u4e86\u8fc7\u6765\uff0c\u770b\u770b\u7a97\u5916\u7684\u5929\u8272\u90fd\u5df2\u7ecf\u9ed1\u4e86\u3002\u4ed6\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u6162\u6162\u722c\u8d77\u8eab\uff0c\u611f\u89c9\u808c\u8089\u4f9d\u65e7\u6709\u4e9b\u9178\u75db\uff0c\u800c\u4e14\u54bd\u5589\u5e72\u5f97\u5f88\uff0c\u50cf\u88ab\u706b\u5728\u707c\u70e7\u4e00\u822c\uff0c\u623f\u95f4\u91cc\u53c8\u6ca1\u6709\u6c34\uff0c\u6b63\u60f3\u6309\u94c3\u53eb\u4f8d\u8005\uff0c\u623f\u95e8\u88ab\u8f7b\u8f7b\u63a8\u5f00\uff0c\u8299\u857e\u72c4\u7aef\u7740\u6c34\u676f\u8d70\u8fdb\u6765\u3002</p><p>\u201c\u53e3\u6e34\u5427\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u6765\uff0c\u559d\u70b9\u6c34\u3002\u201d</p><p>\u743c\u6069\u63a5\u8fc7\u6c34\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u54bd\u5589\u90e8\u7684\u707c\u70e7\u611f\u5927\u51cf\uff0c\u987f\u65f6\u8212\u670d\u591a\u4e86\u3002\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7761\u9192\u4e86\uff1f\u201d</p><p>\u201c\u6211\u5728\u9694\u58c1\u542c\u5230\u52a8\u9759\u4e86\uff0c\u201d\u5c0f\u5973\u5b69\u751c\u751c\u5fae\u7b11\uff0c\u201c\u731c\u5230\u4f60\u80af\u5b9a\u6e34\u4e86\uff0c\u5c31\u7aef\u6c34\u8fc7\u6765\u3002\u201d</p><p>\u743c\u6069\u7b11\u7740\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u771f\u4e56\u3002\u201d</p><p>\u201c\u90a3\u6211\u80fd\u4e0d\u80fd\u8981\u70b9\u5956\u52b1\u554a\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u6709\u4e9b\u8be7\u5f02\uff0c\u5012\u4e0d\u662f\u4e0d\u9ad8\u5174\uff0c\u800c\u662f\u4ee5\u8299\u857e\u72c4\u6e29\u987a\u4e56\u5de7\u7684\u6027\u60c5\uff0c\u6781\u5c11\u4f1a\u4e3b\u52a8\u5411\u4ed6\u7d22\u8981\u4ec0\u4e48\uff0c\u6240\u4ee5\u89c9\u5f97\u5947\u602a\u800c\u5df2\u3002\u201c\u4f60\u60f3\u8981\u4ec0\u4e48\u5956\u52b1\u5462\uff1f\u201d\u4ed6\u7b11\u7740\u95ee\uff0c\u201c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u6211\u60f3\u2026\u2026\u201d\u8299\u857e\u72c4\u7ea2\u7740\u8138\uff0c\u201c\u4eca\u665a\u8ba9\u6211\u966a\u4f60\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u5c31\u8fd9\u4e2a\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6211\u5df2\u7ecf\u597d\u51e0\u5929\u90fd\u6ca1\u6709\u2026\u2026\u665a\u4e0a\u7761\u4e0d\u7740\u561b\u3002\u201d</p><p>\u201c\u6ca1\u95ee\u9898\uff0c\u4f46\u4f60\u770b\u6211\u5df2\u7ecf\u7d2f\u5f97\u591f\u545b\uff0c\u4eca\u665a\u4f30\u8ba1\u662f\u6062\u590d\u4e0d\u8fc7\u6765\uff0c\u6050\u6015\u6ca1\u529e\u6cd5\u8ba9\u4f60\u6ee1\u8db3\u2026\u2026\u201d</p><p>\u201c\u4e0d\u662f\u5566\uff0c\u201d\u8299\u857e\u72c4\u8138\u8d8a\u53d1\u7f9e\u7ea2\uff0c\u201c\u6211\u53ea\u662f\u8bf4\uff0c\u5c31\u662f\u60f3\u5728\u4f60\u6000\u91cc\u800c\u5df2\uff0c\u53c8\u6ca1\u8bf4\u8981\u4f60\u2026\u2026\u522b\u8bf4\u5f97\u4eba\u5bb6\u50cf\u4e2a\u5c0f\u8361\u5987\u4f3c\u7684\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u5c0f\u8361\u5987\u5417\uff1f\u201d\u743c\u6069\u8c03\u7b11\uff0c\u201c\u6211\u600e\u4e48\u611f\u89c9\u4e5f\u5feb\u5dee\u4e0d\u591a\u4e86\u3002\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d</p><p>\u5c11\u5973\u7684\u5a07\u7f9e\u5a9a\u6001\uff0c\u90a3\u771f\u662f\u600e\u4e48\u770b\u4e5f\u770b\u4e0d\u538c\uff0c\u743c\u6069\u60f3\u8d77\u8fd9\u6bb5\u65f6\u95f4\uff0c\u56e0\u4e3a\u6709\u6885\u83f2\u65af\u5728\u8eab\u8fb9\uff0c\u53c8\u9047\u4e0a\u51db\u751f\u75c5\uff0c\u786e\u5b9e\u662f\u51b7\u843d\u4e86\u5979\uff0c\u6bd5\u7adf\u81ea\u5df1\u6ca1\u6709\u5206\u8eab\u672f\uff0c\u4e5f\u6ca1\u5f3a\u5230\u80fd\u591c\u5fa1\u5341\u5973\u7684\u5730\u6b65\uff0c\u5bf9\u4ed8\u6885\u83f2\u65af\u548c\u51db\u5c31\u5df2\u7ecf\u591f\u8d39\u52b2\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u4e0a\u5176\u4ed6\u3002\u518d\u8bf4\u5f53\u7740\u6885\u83f2\u65af\u7684\u9762\uff0c\u9876\u591a\u4e5f\u5c31\u80fd\u63a8\u5012\u51db\uff0c\u4e0d\u65b9\u4fbf\u5077\u5403\u5176\u4ed6\u5973\u5b69\u5b50\uff0c\u4f1a\u60f9\u5979\u4e0d\u9ad8\u5174\u3002\u8299\u857e\u72c4\u5341\u5206\u4e56\u5de7\uff0c\u4e5f\u4e0d\u4f1a\u5435\u95f9\u4ec0\u4e48\uff0c\u53ea\u662f\u9ed8\u9ed8\u7b49\u5f85\uff0c\u76f4\u5230\u4eca\u5929\u6885\u83f2\u65af\u548c\u51db\u90fd\u8d70\u4e86\uff0c\u5979\u624d\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8fc7\u6765\u3002</p><p>\u5ff5\u53ca\u6b64\u5904\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u6696\uff0c\u53ea\u89c9\u5bf9\u5979\u9887\u4e3a\u6127\u759a\u3002\u201c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u5c3d\u91cf\u7528\u5e73\u9759\u7684\u8bed\u6c14\u8bf4\uff0c\u201c\u665a\u4e0a\u966a\u6211\u51fa\u53bb\u8d70\u8d70\u5427\uff0c\u542c\u8bf4\u963f\u65af\u5361\u7279\u62c9\u7684\u591c\u666f\u4e0d\u9519\uff0c\u6765\u4e86\u8fd9\u51e0\u5929\u90fd\u6ca1\u7a7a\u89c2\u8d4f\u3002\u201d</p><p>\u201c\u597d\u554a\u3002\u201d\u5979\u5f88\u9ad8\u5174\u5730\u7b54\u5e94\u3002</p><p>\u665a\u9910\u4e4b\u540e\uff0c\u743c\u6069\u4fbf\u548c\u8299\u857e\u72c4\u8d70\u51fa\u9152\u5e97\uff0c\u968f\u610f\u95f2\u901b\u8d77\u6765\uff0c\u838e\u73de\u514b\u6ca1\u6709\u8ddf\u968f\uff0c\u8ba9\u5979\u53bb\u6253\u63a2\u82cf\u4f26\u795e\u6bbf\u7684\u6d88\u606f\u53bb\u4e86\u3002\u53cd\u6b63\u743c\u6069\u73b0\u5728\u53ef\u4ee5\u5728\u57ce\u5185\u968f\u610f\u65bd\u6cd5\uff0c\u5c31\u7b97\u9047\u5230\u5371\u9669\uff0c\u81ea\u4fdd\u9003\u547d\u4e5f\u662f\u6709\u4f59\uff0c\u65e0\u9700\u9b45\u9b54\u505a\u62a4\u536b\u3002\u8299\u8389\u5a05\u4f9d\u65e7\u7559\u5728\u9152\u5e97\u91cc\uff0c\u5979\u662f\u838e\u5c14\u7267\u5e08\uff0c\u591c\u665a\u6b63\u662f\u505a\u7948\u7977\u7684\u65f6\u95f4\u3002</p><p>\u51fa\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u743c\u6069\u8eab\u8fb9\u6709\u4e94\u4e2a\u5973\u5b69\u5b50\uff0c\u73b0\u5728\u6885\u83f2\u65af\u548c\u51db\u8d70\u4e86\uff0c\u53ea\u5269\u4e0b\u4e09\u4e2a\uff0c\u5176\u4e2d\u8fd8\u6709\u4e00\u4e2a\u662f\u5173\u7cfb\u758f\u8fdc\u7684\uff0c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u4ed6\u53cd\u800c\u89c9\u5f97\u81ea\u5728\u4e86\u5f88\u591a\u3002\u5012\u4e0d\u662f\u8bf4\u4e0d\u559c\u6b22\u6885\u83f2\u65af\u548c\u51db\uff0c\u89c1\u5979\u4eec\u8d70\u4e86\u5c31\u9ad8\u5174\uff0c\u53ea\u662f\u5355\u7eaf\u5730\u89c9\u5f97\uff0c\u6709\u65f6\u5019\u5973\u4eba\u592a\u591a\u4e86\uff0c\u4e5f\u672a\u5fc5\u5c31\u662f\u4ef6\u597d\u4e8b\uff0c\u6570\u91cf\u5c11\u70b9\u53cd\u800c\u8212\u5fc3\u3002</p><p>\u771f\u89c1\u9b3c\uff0c\u4e16\u754c\u4e0a\u7684\u7537\u4eba\uff0c\u4ece\u6765\u90fd\u662f\u5e0c\u671b\u5973\u4eba\u8d8a\u591a\u8d8a\u597d\uff0c\u81ea\u5df1\u53cd\u800c\u5e0c\u671b\u5c11\u70b9\uff0c\u7b80\u76f4\u5c31\u662f\u2026\u2026\u7b80\u76f4\u5c31\u662f\u6709\u95ee\u9898\u3002\u7136\u800c\u626a\u5fc3\u81ea\u95ee\uff0c\u786e\u5b9e\u662f\u89c9\u5f97\u5973\u4eba\u5c11\u70b9\u4f3c\u4e4e\u4e5f\u4e0d\u9519\u2026\u2026</p><p>\u597d\u5427\uff0c\u770b\u6765\u81ea\u5df1\u662f\u771f\u6709\u95ee\u9898\u4e86\uff0c\u56de\u5934\u53bb\u627e\u4e2a\u5fc3\u7406\u533b\u751f\u54a8\u8be2\u4e00\u4e0b\u3002</p><p>\u4ed6\u81ea\u5632\u4e00\u7b11\uff0c\u8f6c\u8138\u770b\u4e86\u770b\u8eab\u65c1\u7684\u5c11\u5973\uff0c\u201c\u8299\u857e\u72c4\uff0c\u542c\u8bf4\u57ce\u91cc\u65b0\u6765\u4e86\u4e00\u4e2a\u9a6c\u620f\u56e2\uff0c\u6211\u4eec\u53bb\u770b\u770b\u597d\u4e0d\u597d\u3002\u201d</p></article>"}}}]);
