(window.webpackJsonp=window.webpackJsonp||[]).push([[161],{184:function(p){p.exports={chapterTitle:null,title:"\u7b2c1139\u7ae0 \u7518\u5f53\u5080\u5121",content:"<article><h2>\u7b2c1139\u7ae0 \u7518\u5f53\u5080\u5121</h2><p>\u3000\u3000\u4e94\u4e00\u9ec4\u91d1\u5468\uff0c\u56fd\u5185\u5927\u591a\u6570\u7701\u5e02\u53cc\u4f11\u65e5\u8fdb\u884c\u8c03\u6574\uff0c\u4f01\u4e8b\u4e1a\u5355\u4f4d\u5171\u6709\u4e03\u5929\u7684\u516c\u5f00\u5047\u671f\uff0c\u6caa\u6df1\u4e24\u5e02\u5c06\u4f11\u5e02\u4e5d\u5929\u3002</p><p>\u3000\u3000\u5728\u4e94\u4e00\u9ec4\u91d1\u5468\u524d\u5915\uff0c\u6caa\u6df1\u4e24\u5e02\u65b0\u79d1\u6280\u6982\u5ff5\u80a1\uff0c\u7279\u522b\u662f\u4e92\u8054\u7f51\u6982\u5ff5\u80a1\u6df1\u53d7\u91cd\u632b\uff0c\u666e\u904d\u90fd\u906d\u5230\u8fde\u7eed\u8dcc\u505c\u7684\u6253\u51fb\u3002\u867d\u7136\u6caa\u6df1\u4e24\u5e02\u6682\u65f6\u4f11\u5e02\uff0c\u4f46\u662f\u56f4\u7ed5\u7740\u7f51\u7edc\u6ce1\u6cab\u8fd9\u4e2a\u8bdd\u9898\u7684\u8bba\u6218\u5219\u66f4\u7cbe\u5f69\u7684\u5c55\u5f00\u6765\u3002</p><p>\u3000\u3000\u8054\u901a\u8fdb\u519b\u8fde\u9501\u7f51\u5427\u4ea7\u4e1a\uff0c\u51c6\u5907\u4e86\u4e24\u4e2a\u591a\u6708\u7684\u8054\u901a\u7f51\u82d1\u8ba1\u5212\u4e5f\u6b63\u5f0f\u5411\u516c\u4f17\u63ed\u5f00\u5e37\u5e55\uff0c\u8054\u901a\u7f51\u82d1\u5b66\u5e9c\u5e97\u4ee5\u53ca\u65b0\u5e02\u8857\u5e97\u3001\u65b0\u6d66\u5e97\u3001\u5e9c\u9752\u8def\u5e97\u3001\u5357\u6e56\u5e97\u5171\u4e94\u5bb6\u65d7\u8230\u7f51\u5427\u4e8e\u4e94\u6708\u4e00\u65e5\u8fd9\u5929\u540c\u65f6\u5bf9\u5916\u8425\u4e1a\uff0c\u4e94\u5bb6\u65d7\u8230\u7f51\u5427\u603b\u673a\u53f0\u6570\u8d85\u8fc7\u4e00\u4e07\u4e24\u5343\u53f0\u3002</p><p>\u3000\u3000\u5728\u524d\u591c\uff0c\u5f20\u606a\u63a5\u5230\u8054\u901a\u4e1c\u6d77\u7701\u516c\u53f8\u526f\u603b\u7ecf\u7406\u6210\u6620\u971e\u7684\u7535\u8bdd\uff0c\u9080\u8bf7\u4ed6\u53c2\u52a0\u8054\u901a\u7f51\u82d1\u5b66\u5e9c\u5e97\u7684\u526a\u5f69\u4eea\u5f0f\u3002\u7531\u4e8e\u8054\u901a\u603b\u90e8\u8fd8\u4e0d\u60f3\u5927\u5f20\u65d7\u9f13\u7684\u5ba3\u626c\u7f51\u82d1\u8ba1\u5212\uff0c\u6768\u84c9\u5e73\u4e0d\u4f1a\u5230\u5efa\u90ba\u6765\u53c2\u52a0\u526a\u5f69\u6d3b\u52a8\uff0c\u6210\u6620\u971e\u9080\u8bf7\u5f20\u606a\u7b97\u662f\u4e00\u79cd\u5192\u6627\u7684\u8bf7\u6c42\uff0c\u5f20\u606a\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8ba9\u6210\u6620\u971e\u4e0d\u8981\u4e3a\u4ed6\u505a\u4ec0\u4e48\u7279\u522b\u7684\u5b89\u6392\uff0c\u4ed6\u4f4f\u5f97\u8fd1\uff0c\u7b49\u5230\u526a\u5f69\u65f6\u95f4\u4ed6\u8d70\u8fc7\u53bb\u51fa\u5e2d\u4e00\u4e0b\u5c31\u662f\u3002</p><p>\u3000\u3000\u52b3\u52a8\u8282\uff0c\u9999\u6e2f\u5927\u5b66\u4e5f\u6709\u4e00\u5929\u7684\u516c\u5047\uff0c\u5510\u5a67\u4fbf\u4e0e\u76db\u590f\u3001\u9648\u5983\u84c9\u4e24\u4eba\u4e00\u8d77\u56de\u5185\u5730\u6765\u6e21\u5468\u672b\u3002\u9648\u5983\u84c9\u5728\u5efa\u90ba\u4f4f\u4e86\u4e00\u591c\uff0c\u8d77\u65e9\u5c31\u5750\u8f66\u56de\u6d77\u5dde\u53bb\u4e86\uff1b\u5f20\u606a\u4e0e\u5510\u5a67\u6625\u7761\u8fdf\u8fdf\uff0c\u8fc7\u4e86\u4e5d\u70b9\u949f\uff0c\u624d\u61d2\u6d0b\u6d0b\u7684\u8d77\u5e8a\u6765\uff0c\u8054\u901a\u7f51\u82d1\u5b66\u5e9c\u5e97\u7684\u526a\u5f69\u6d3b\u52a8\u4e5f\u8981\u5341\u70b9\u949f\u624d\u5f00\u59cb\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u5728\u516c\u5bd3\u91cc\u6709\u6761\u4e0d\u7d0a\u7684\u5403\u7740\u65e9\u70b9\u3002</p><p>\u3000\u3000\u675c\u98de\u7684\u7535\u8bdd\u5174\u51b2\u51b2\u7684\u6253\u8fdb\u6765\uff1a\u201c\u6c5f\u654f\u4e4b\u5230\u5b66\u5e9c\u5df7\u6765\u89c6\u5bdf\u4e86\uff0c\u7b49\u4f1a\u513f\u4e5f\u8981\u53c2\u52a0\u8054\u901a\u7f51\u82d1\u5b66\u5e9c\u5e97\u7684\u526a\u5f69\u6d3b\u52a8\u2026\u2026\u8054\u901a\u7684\u6210\u7f8e\u5973\u80fd\u8010\u9887\u5927\u554a\u3002\u201d</p><p>\u3000\u3000\u5317\u4eac\u7684\u4eba\u4e8b\u5173\u7cfb\u590d\u6742\uff0c\u6210\u6620\u971e\u5728\u8054\u901a\u5185\u90e8\u867d\u7136\u53ea\u662f\u5904\u7ea7\u5e72\u90e8\uff0c\u4f46\u662f\u6709\u7740\u6f02\u4eae\u5973\u4eba\u7684\u4f18\u52bf\uff0c\u4e5f\u5c31\u76f8\u5bf9\u6bd4\u5979\u804c\u52a1\u66f4\u91cd\u8981\u3001\u7ea7\u522b\u66f4\u9ad8\u7684\u5b98\u5458\u66f4\u52a0\u6d3b\u8dc3\uff0c\u6307\u4e0d\u5b9a\u6c5f\u654f\u4e4b\u5728\u56fd\u5bb6\u7ecf\u8d38\u59d4\u4efb\u804c\u65f6\u8ddf\u5979\u6709\u8fc7\u63a5\u89e6\uff0c\u60f3\u60f3\u81ea\u5df1\u4e0d\u4e5f\u662f\u968f\u53e3\u7b54\u5e94\u53c2\u52a0\u8054\u901a\u7f51\u82d1\u5b66\u5e9c\u5e97\u7684\u526a\u5f69\u6d3b\u52a8\uff1f</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8868\uff0c\u79bb\u526a\u5f69\u6d3b\u52a8\u8fd8\u6709\u4e0d\u5230\u534a\u5c0f\u65f6\u3002\u4e5f\u6ca1\u6709\u63a5\u5230\u6b63\u5f0f\u7684\u901a\u77e5\u8bf4\u6c5f\u654f\u4e4b\u4eca\u5929\u8981\u89c6\u5bdf\u5b66\u5e9c\u5df7\uff0c\u5373\u4f7f\u6709\u6b63\u5f0f\u7684\u901a\u77e5\uff0c\u4e5f\u662f\u90b5\u81f3\u521a\u4ed6\u4eec\u51fa\u6765\u966a\u540c\uff1b\u4ed6\u60a0\u95f2\u7684\u5c06\u5496\u5561\u559d\u5b8c\uff0c\u624d\u4e0e\u5510\u5a67\u4e0b\u697c\u53bb\u3002</p><p>\u3000\u3000\u8fdb\u5165\u4e94\u6708\uff0c\u5efa\u90ba\u65e9\u665a\u7684\u6e29\u5dee\u5f88\u5927\uff0c\u6e05\u6668\u4ed6\u4e0e\u5510\u5a67\u8d64\u88f8\u7684\u8eba\u5728\u88ab\u7a9d\u91cc\u6b63\u7761\u5f97\u8212\u670d\uff0c\u8fd9\u65f6\u5019\u8d70\u51fa\u95e8\uff0c\u8bb8\u591a\u5973\u5b69\u5b50\u90fd\u82b1\u679d\u62db\u5c55\u7684\u7a7f\u7740\u8f7b\u8584\u7684\u88d9\u88c5\u5438\u5f15\u4eba\u7684\u773c\u7403\uff0c\u7b49\u675c\u98de\u5f00\u8f66\u8f7d\u7740\u76db\u590f\u8fc7\u6765\uff0c\u624d\u7a7f\u8fc7\u5939\u5df7\u5f80\u8054\u901a\u7f51\u82d1\u8d70\u53bb\u3002</p><p>\u3000\u3000\u8054\u901a\u7f51\u82d1\u521d\u8425\u4e1a\u4e5f\u662f\u5b66\u521b\u57df\u7f51\u5427\u6700\u521d\u7684\u90a3\u62db\uff0c\u4ee5\u514d\u8d39\u4e0a\u7f51\u65f6\u6bb5\u6765\u5438\u5f15\u6d88\u8d39\u8005\u3002\u4eca\u5929\u662f\u7b2c\u4e00\u5929\u8425\u4e1a\uff0c\u526a\u5f69\u6d3b\u52a8\u7ed3\u675f\u4e4b\u540e\u5c31\u662f\u4e24\u4e2a\u5c0f\u65f6\u7684\u514d\u8d39\u4e0a\u7f51\u65f6\u6bb5\uff0c\u6709\u521b\u57df\u7f51\u5427\u7684\u524d\u8f66\u4e4b\u9274\uff0c\u8054\u901a\u7f51\u82d1\u7ec4\u7ec7\u6bd4\u8f83\u5f97\u529b\uff0c\u8d34\u7740\u8054\u901a\u7f51\u82d1\u524d\u7684\u5e7f\u573a\u4ee5\u53ca\u556c\u56ed\u8def\u7684\u8def\u7259\uff0c\u5e76\u5217\u4e09\u6392\u961f\u4f0d\u6392\u51fa\u53bb\u8db3\u8db3\u6709\u4e09\u56db\u767e\u7c73\u957f\uff0c\u5012\u662f\u6ca1\u6709\u5835\u585e\u4ea4\u901a\uff1b\u8bb8\u591a\u7a7f\u7740\u8054\u901a\u7f51\u82d1\u6587\u5316\u886b\u7684\u5927\u5b66\u751f\u5728\u5e2e\u5fd9\u7ef4\u6301\u79e9\u5e8f\u3002</p><p>\u3000\u3000\u51e0\u8f86\u6302\u7740\u653f\u5e9c\u8f66\u8f86\u7684\u8f7f\u8f66\u505c\u5728\u7f51\u5427\u524d\uff0c\u6ca1\u6709\u770b\u5230\u6c5f\u654f\u4e4b\u4ee5\u53ca\u5176\u4ed6\u7701\u653f\u5e9c\u5b98\u5458\u7684\u8eab\u5f71\uff0c\u6216\u8bb8\u5df2\u7ecf\u7ed9\u6731\u5168\u5bff\u3001\u6210\u6620\u971e\u7b49\u8054\u901a\u5b98\u5458\u62db\u547c\u5230\u91cc\u9762\u53bb\u6b47\u606f\u4e86\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u8fdb\u53bb\uff1f\u201d\u675c\u98de\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u6211\u4eec\u4e5f\u5c31\u88c5\u6a21\u4f5c\u6837\u7684\u51fa\u5e2d\u4e00\u4e0b\uff0c\u8fdb\u53bb\u4e0d\u5acc\u7d2f\u5f97\u614c\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4ed6\u7684\u8bdd\u97f3\u672a\u843d\uff0c\u5c31\u770b\u89c1\u6210\u6620\u971e\u9886\u7740\u859b\u660e\u697c\u3001\u6c5f\u6e44\u4e24\u4eba\u4ece\u8054\u901a\u7f51\u82d1\u7684\u4e00\u6247\u4fa7\u95e8\u91cc\u8d70\u51fa\u6765\uff0c\u671d\u4ed6\u4eec\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u5df2\u7ecf\u77e5\u9053\u859b\u660e\u697c\u5c06\u8c03\u5230\u4e1c\u6d77\u6765\u62c5\u4efb\u6c5f\u654f\u4e4b\u7684\u79d8\u4e66\uff0c\u6c5f\u6e44\u4e5f\u662f\u5728\u5979\u7684\u5927\u5b66\u5b9e\u4e60\u671f\u5145\u5f53\u5979\u7236\u4eb2\u6c5f\u654f\u4e4b\u7684\u52a9\u624b\uff0c\u4f4e\u58f0\u7ed9\u5510\u5a67\u4ecb\u7ecd\u3002\u770b\u7740\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u53c8\u8ddf\u6210\u6620\u971e\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6210\u603b\u5c06\u6c5f\u7701\u957f\u90fd\u8bf7\u52a8\u4e86\uff0c\u600e\u4e48\u4e0d\u63d0\u524d\u8ddf\u6211\u8bf4\u4e00\u58f0\uff0c\u6211\u8fd9\u4e48\u665a\u624d\u8fc7\u6765\uff0c\u5f88\u5931\u793c\u554a\u2026\u2026\u526a\u5f69\u6d3b\u52a8\u9a6c\u4e0a\u8981\u5f00\u59cb\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7231\u4eba\u558a\u6c5f\u7701\u957f\u53d4\u53d4\uff0c\u6c5f\u7701\u957f\u4e5f\u662f\u4eca\u5929\u65e9\u4e0a\u624d\u4e34\u65f6\u51b3\u5b9a\u53c2\u52a0\u2026\u2026\u201d\u6210\u6620\u971e\u89e3\u91ca\u9053\u3002</p><p>\u3000\u3000\u4ece\u6210\u6620\u971e\u7684\u8bed\u6c14\u91cc\uff0c\u5f20\u606a\u80fd\u591f\u542c\u51fa\u5979\u5bb6\u4e0e\u6c5f\u654f\u4e4b\u7684\u5173\u7cfb\u6bd4\u8f83\u758f\u8fdc\uff0c\u6216\u8bb8\u662f\u6839\u672c\u6ca1\u6709\u8840\u7f18\u5173\u7cfb\u7684\u53d4\u53d4\uff0c\u5fc3\u60f3\uff1a\u6c5f\u654f\u4e4b\u51b3\u5b9a\u53c2\u52a0\u526a\u5f69\u6d3b\u52a8\uff0c\u6216\u8bb8\u662f\u77e5\u9053\u4ed6\u8981\u53c2\u52a0\u800c\u5fc3\u8840\u6765\u6f6e\u5427\uff1f\u5f20\u606a\u770b\u4e86\u4e00\u773c\u8fdc\u5904\u7684\u91c7\u8bbf\u8f66\uff0c\u77e5\u9053\u6709\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u5728\uff0c\u95ee\u9053\uff1a\u201c\u526a\u5f69\u6d3b\u52a8\u5c31\u8981\u5f00\u59cb\u4e86\u5427\uff1f\u90a3\u6211\u4eec\u5c31\u5728\u5916\u9762\u7b49\u6c5f\u7701\u957f\u3001\u6731\u603b\u4ed6\u4eec\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u5728\u5916\u9762\u7b49\u4e00\u4f1a\u513f\uff0c\u6c5f\u7701\u957f\u5728\u91cc\u9762\u63a5\u53d7\u62a5\u793e\u8ddf\u7535\u89c6\u53f0\u7684\u7b80\u77ed\u91c7\u8bbf\uff0c\u4e00\u4f1a\u513f\u5c31\u7ed3\u675f\uff0c\u201d\u6210\u6620\u971e\u70b9\u4e86\u70b9\u5934\uff0c\u77e5\u9053\u5f20\u606a\u4e0d\u60f3\u5728\u5a92\u4f53\u524d\u9732\u8138\uff0c\u8fd9\u4e5f\u662f\u5979\u4e4b\u524d\u5c31\u8003\u8651\u8fc7\u7684\uff0c\u5c31\u4e0d\u575a\u6301\u8bf7\u5f20\u606a\u8d70\u8fdb\u53bb\uff0c\u5979\u7684\u773c\u775b\u53c8\u770b\u5411\u633d\u7740\u5f20\u606a\u80f3\u818a\u4f9d\u5728\u4e00\u65c1\u7684\u5510\u5a67\uff0c\u70ed\u5207\u7684\u62c9\u8fc7\u5979\u7684\u624b\uff0c\u7b11\u7740\u95ee\uff0c\u201c\u4f60\u662f\u5510\u5b66\u8c26\u7701\u957f\u7684\u5343\u91d1\u5427\uff1f\u5938\u4e00\u4e2a\u4eba\u6f02\u4eae\u8bed\u8a00\u603b\u662f\u4e0d\u591f\uff0c\u6211\u8fd9\u65f6\u5019\u7b97\u662f\u771f\u771f\u660e\u767d\u8fc7\u6765\u4e86\uff0c\u4f60\u4e0e\u6c5f\u6e44\u2014\u2014\u6211\u8fd8\u6ca1\u6709\u7ed9\u4f60\u4eec\u4ecb\u7ecd\u5462\uff0c\u8fd9\u4f4d\u662f\u6c5f\u654f\u4e4b\u7701\u957f\u7684\u5343\u91d1\u6c5f\u6e44\uff0c\u7ad9\u5728\u675c\u603b\u8eab\u8fb9\u7684\u662f\u76db\u590f\u5c0f\u59d0\u5427\uff1f\u4f60\u4eec\u4e09\u4e2a\u7ad9\u5728\u4e00\u8d77\u6765\uff0c\u6211\u6068\u4e0d\u5f97\u8981\u5c06\u8138\u8499\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6210\u59d0\u59d0\u4e5f\u5f88\u6f02\u4eae\u554a\uff0c\u6210\u59d0\u59d0\u80fd\u505a\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\u66f4\u4e0d\u7b80\u5355\uff0c\u201d\u5510\u5a67\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u7684\u8bf4\u9053\uff0c\u53c8\u4e0e\u6c5f\u6e44\u62db\u547c\uff0c\u201c\u6c5f\u6e44\u5c0f\u59d0\u624d\u662f\u771f\u6b63\u7684\u6f02\u4eae\uff0c\u53c8\u6709\u6c14\u8d28\uff0c\u6211\u4ece\u5c0f\u5c31\u559c\u6b22\u770b\u6797\u747e\u963f\u59e8\u6f14\u7684\u7535\u5f71\uff0c\u6c5f\u6e44\u5c0f\u59d0\u8ddf\u6797\u747e\u963f\u59e8\u5e74\u8f7b\u65f6\u4e00\u4e2a\u6a21\u5b50\u523b\u51fa\u6765\u4f3c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u6e44\u8d70\u51fa\u6765\u5c31\u5728\u6253\u91cf\u5510\u5a67\uff0c\u89c1\u5979\u5a07\u67d4\u660e\u8273\u7684\u4e0e\u5f20\u606a\u4f9d\u7ad9\u5728\u4e00\u8d77\uff0c\u5b9b\u5982\u91d1\u7ae5\u7389\u5973\uff0c\u5fc3\u91cc\u6709\u4e9b\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\uff0c\u8fd9\u65f6\u5019\u89c1\u5979\u5949\u627f\u7684\u8003\u81ea\u5df1\u6f02\u4eae\uff0c\u957f\u5f97\u8ddf\u5979\u5988\uff0c\u5012\u4e0d\u4fbf\u592a\u51b7\u6f20\uff0c\u7b11\u7740\u8ddf\u5510\u5a67\u3001\u76db\u590f\u62db\u547c\uff0c\u7ec8\u662f\u8981\u6bd4\u6210\u6620\u971e\u51b7\u6de1\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u6c5f\u6e44\u7684\u773c\u775b\u8fd8\u662f\u6709\u610f\u65e0\u610f\u7684\u5f80\u5f20\u606a\u8eab\u4e0a\u8e69\uff0c\u5979\u7238\u7238\u5728\u91cc\u9762\u63a5\u53d7\u5a92\u4f53\u7684\u91c7\u8bbf\uff0c\u5a92\u4f53\u8bb0\u8005\u7684\u63d0\u95ee\u90fd\u96c6\u4e2d\u5728\u4e92\u8054\u7f51\u80a1\u707e\u4e0a\uff0c\u5979\u73b0\u5728\u80fd\u80af\u5b9a\u4e09\u5929\u524d\u5f20\u606a\u63a5\u5230\u7535\u8bdd\u7684\u90a3\u4e00\u523b\u5c31\u5df2\u7ecf\u610f\u8bc6\u5230\u63a5\u4e0b\u6765\u4f1a\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u6beb\u4e0d\u62d6\u6ce5\u5e26\u6c34\u7684\u5728\u80cc\u540e\u7ed9\u6d77\u7c9f\u79d1\u6280\u6345\u4e86\u4e00\u5200\u3002\u4e5f\u8bb8\u5c31\u5728\u8fd9\u4e00\u523b\uff0c\u5979\u624d\u7a0d\u5fae\u7684\u9886\u7565\u5230\u5f20\u606a\u7684\u5389\u5bb3\u4e4b\u5904\uff0c\u6740\u4eba\u6839\u672c\u5c31\u85cf\u5728\u65e0\u5f62\u4e4b\u95f4\u3002</p><p>\u3000\u3000\u6c5f\u6e44\u4e0e\u859b\u660e\u697c\u90fd\u6ca1\u6709\u518d\u8fdb\u53bb\u53bb\uff0c\u5c31\u5728\u5916\u9762\u7b49\u7740\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u6c5f\u654f\u4e4b\u5728\u7701\u653f\u5e9c\u966a\u540c\u5b98\u5458\u4ee5\u53ca\u8054\u901a\u603b\u7ecf\u7406\u6731\u5168\u5bff\u7b49\u4eba\u7684\u7c07\u62e5\u4e0b\u8d70\u4e86\u51fa\u6765\uff0c\u6c5f\u654f\u4e4b\u4e0a\u5348\u4e34\u65f6\u51b3\u5b9a\u89c6\u5bdf\u5b66\u5e9c\u5df7\uff0c\u90b5\u81f3\u521a\u5728\u5efa\u90ba\u81ea\u7136\u8981\u8fc7\u6765\u966a\u540c\u2014\u2014\u5dee\u4e0d\u591a\u6574\u6761\u5b66\u5e9c\u5df7\u90fd\u662f\u4e16\u7eaa\u9526\u6e56\u65d7\u4e0b\u7684\u7269\u4e1a\u3002\u738b\u6d77\u7c9f\u4e5f\u5728\u4eba\u7fa4\u4e2d\uff0c\u738b\u6d77\u7c9f\u540d\u4e49\u4e0a\u6d77\u7c9f\u79d1\u6280\u7684\u603b\u88c1\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u89c4\u6a21\u53c8\u662f\u534e\u4e1c\u5730\u533a\u89c4\u6a21\u6700\u5927\uff0c\u8054\u901a\u7f51\u82d1\u9080\u8bf7\u738b\u6d77\u7c9f\u5217\u5e2d\uff0c\u4e5f\u662f\u4e3a\u4e86\u8868\u793a\u6c14\u5ea6\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u4ed6\u4eec\u4e5f\u65e9\u5c31\u77e5\u9053\u8054\u901a\u8981\u53d1\u5c55\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u3002\u5728\u5371\u673a\u7206\u53d1\u4e4b\u524d\uff0c\u4ed6\u4eec\u6ca1\u6709\u611f\u89c9\u5230\u4e1d\u6beb\u7684\u538b\u529b\uff1b\u5373\u4f7f\u6709\u7ade\u4e89\uff0c\u4e5f\u8981\u4e00\u5e74\u534a\u8f7d\u4e4b\u540e\u624d\u4f1a\u9010\u6e10\u7684\u611f\u89c9\u5230\uff0c\u773c\u4e0b\u7684\u538b\u529b\u53ea\u4f1a\u4f53\u73b0\u5728\u4ea7\u4e1a\u5206\u6790\u62a5\u544a\u4e2d\u3002\u7f51\u7edc\u80a1\u707e\u7206\u53d1\u540e\uff0c\u4efb\u4f55\u4e00\u6839\u7a3b\u8349\u90fd\u4f1a\u8ba9\u4ed6\u4eec\u611f\u89c9\u5230\u589e\u52a0\u4e86\u91cd\u91cf\uff0c\u4f55\u51b5\u8054\u901a\u7f51\u82d1\u8fd9\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u589e\u52a0\u7684\u538b\u529b\u81f3\u5c11\u8981\u6bd4\u4e00\u6839\u7a3b\u8349\u91cd\u591a\u4e86\u3002</p><p>\u3000\u3000\u8054\u901a\u7f51\u82d1\u8fd9\u6b21\u5728\u5efa\u90ba\u65b0\u5f00\u8bbe\u7684\u76f4\u8425\u7f51\u5427\u4e00\u4e0b\u5b50\u5c31\u4f7f\u5efa\u90ba\u7f51\u5427\u4ea7\u4e1a\u603b\u673a\u53f0\u6570\u589e\u52a0\u4e868\uff05\uff0c\u800c\u4e14\u8ba1\u5212\u8981\u5728\u77ed\u671f\u5185\u53d1\u5c55\u56db\u5230\u4e94\u500d\u89c4\u6a21\u7684\u7279\u8bb8\u52a0\u76df\u7f51\u5427\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5efa\u90ba\u7f51\u5427\u4ea7\u4e1a\u603b\u673a\u53f0\u6570\u5c06\u56e0\u4e3a\u8054\u901a\u7684\u8fdb\u519b\u800c\u589e\u6da840-50\uff05\uff0c\u8fd9\u5c06\u4f7f\u5efa\u90ba\u7f51\u5427\u4ea7\u4e1a\u7684\u7ade\u4e89\u73af\u5883\u53d1\u751f\u5267\u70c8\u7684\u53d8\u5316\uff0c\u5f71\u54cd\u6d77\u7c9f\u79d1\u6280\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u7684\u76c8\u5229\u9884\u671f\u2014\u2014\u773c\u4e0b\u6d77\u7c9f\u79d1\u6280\u4e00\u76f4\u90fd\u5728\u9f13\u5439\u65d7\u4e0b\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u7684\u76c8\u5229\u80fd\u529b\uff0c\u76c8\u5229\u9884\u671f\u88ab\u52a8\u6447\u4e4b\u540e\uff0c\u5bf9\u80a1\u4ef7\u7684\u8d1f\u9762\u5f71\u54cd\u6bd4\u8f83\u5927\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u867d\u7136\u8fc7\u6765\u53c2\u52a0\u8054\u901a\u7f51\u82d1\u7684\u526a\u5f69\u4eea\u5f0f\uff0c\u5fc3\u91cc\u5374\u6068\u4e0d\u5f97\u4e00\u628a\u706b\u5c06\u8054\u901a\u7f51\u82d1\u7684\u51e0\u95e8\u65d7\u8230\u5e97\u70e7\u6210\u7070\u70ec\uff0c\u53ea\u662f\u5fc3\u91cc\u60f3\u60f3\u800c\u5df2\u3002</p><p>\u3000\u3000\u7b80\u5355\u7684\u526a\u5f69\u4eea\u5f0f\u4e4b\u540e\uff0c\u7f51\u82d1\u6b63\u5f0f\u5bf9\u5916\u8425\u4e1a\uff0c\u6392\u4e86\u5927\u534a\u5929\u961f\u7684\u4eba\u5f00\u59cb\u8fdb\u53bb\u514d\u8d39\u4f53\u9a8c\u4e0a\u7f51\u51b2\u6d6a\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u8fd8\u662f\u9887\u4e3a\u7167\u987e\u5f20\u606a\u7684\u987e\u5fcc\uff0c\u7b49\u526a\u5f69\u6d3b\u52a8\u4e00\u7ed3\u675f\uff0c\u5c31\u6307\u793a\u8ba9\u7535\u89c6\u53f0\u4e0e\u5a92\u4f53\u8bb0\u8005\u5148\u79bb\u5f00\uff0c\u4ed6\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5230\u8fd9\u91cc\u4e86\uff0c\u6211\u8fd8\u6709\u4e9b\u65f6\u95f4\uff0c\u5c31\u53bb\u7231\u8fbe\u56fe\u4e66\u9986\u53c2\u89c2\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6b63\u597d\u8981\u8bf7\u6c5f\u7701\u957f\u6307\u793a\u5de5\u4f5c\u5462\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u9053\uff0c\u626d\u5934\u770b\u5230\u5efa\u90ba\u6668\u62a5\u7684\u7b80\u5fd7\u5eb7\u5728\u8fdc\u5904\u6324\u7709\u5f04\u773c\uff0c\u671d\u4ed6\u62db\u4e86\u62db\u624b\uff1b\u8d1f\u8d23\u6c5f\u654f\u4e4b\u89c6\u5bdf\u5b89\u6392\u7684\u7701\u653f\u5e9c\u529e\u4e3b\u4efb\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u8ba9\u7b80\u5fd7\u5eb7\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u672c\u6765\u63d0\u524d\u79bb\u5f00\uff0c\u4e0d\u53c2\u52a0\u4e2d\u5348\u7684\u5bb4\u8bf7\uff0c\u6c5f\u654f\u4e4b\u8fd9\u6761\u7ebf\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u4e5f\u6ca1\u6709\u673a\u4f1a\u80fd\u642d\u4e0a\uff1b\u4e0d\u8ba4\u8bc6\u7b80\u5fd7\u5eb7\uff0c\u4f46\u662f\u8bb0\u5f97\u4ed6\u521a\u624d\u5411\u6c5f\u654f\u4e4b\u95ee\u4e86\u597d\u51e0\u4e2a\u5bf9\u6d77\u7c9f\u79d1\u6280\u5f88\u4e0d\u5229\u7684\u95ee\u9898\uff0c\u738b\u6d77\u7c9f\u89c1\u4ed6\u4e0e\u5f20\u606a\u76f8\u8bc6\uff0c\u4fbf\u6309\u8010\u4f4f\u6027\u5b50\u6253\u7b97\u8ddf\u7740\u4e00\u8d77\u53bb\u7231\u8fbe\u56fe\u4e66\u9986\u53c2\u89c2\u3002</p><p>\u3000\u3000\u7231\u8fbe\u56fe\u4e66\u9986\u662f\u9526\u6e56\u6350\u8d44\u4e09\u4ebf\u5efa\u9020\uff0c\u8f70\u52a8\u4e00\u65f6\uff0c\u4e0d\u4ec5\u662f\u4e1c\u6d77\u89c4\u6a21\u6700\u5927\u3001\u89c4\u683c\u6700\u9ad8\u7684\u5355\u4f53\u56fe\u4e66\u9986\uff0c\u4e5f\u662f\u5168\u56fd\u89c4\u6a21\u6700\u5927\u3001\u89c4\u683c\u6700\u9ad8\u7684\u5355\u4f53\u56fe\u4e66\u9986\uff0c\u5c31\u8fde\u738b\u6d77\u7c9f\u770b\u4e86\u4e5f\u5f88\u9057\u61be\u4e3a\u4ec0\u4e48\u4ed6\u5728\u4e1c\u5927\u8bfb\u4e66\u6ca1\u6709\u8fd9\u6837\u7684\u56fe\u4e66\u9986\u80fd\u7ed9\u4ed6\u4eec\u5b89\u9759\u8bfb\u4e66\u7684\u73af\u5883\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4ecb\u610f\u738b\u6d77\u7c9f\u5728\u65c1\uff0c\u7b11\u7740\u95ee\u7b80\u5fd7\u5eb7\uff1a\u201c\u4f60\u4e0d\u662f\u521a\u5f53\u4e0a\u91c7\u7f16\u90e8\u4e3b\u4efb\u4e86\u5427\uff0c\u600e\u4e48\u8fd8\u51b2\u950b\u5728\u7b2c\u4e00\u7ebf\uff1f\u201d</p><p>\u3000\u3000\u201c\u6c5f\u7701\u957f\u662f\u56fd\u5185\u5c11\u6570\u4e13\u5bb6\u578b\u5b98\u5458\uff0c\u8fd8\u62c5\u4efb\u7ecf\u8d38\u59d4\u526f\u4e3b\u4efb\u65f6\uff0c\u6211\u5c31\u60f3\u7ed9\u6c5f\u7701\u957f\u5199\u4e00\u7bc7\u4e13\u8bbf\u4e86\uff0c\u4e00\u76f4\u6ca1\u6709\u5408\u9002\u7684\u673a\u4f1a\uff0c\u73b0\u5728\u6c5f\u7701\u957f\u5230\u4e1c\u6d77\u6765\uff0c\u6211\u5f53\u7136\u66f4\u8981\u4e89\u53d6\u4e00\u4e0b\uff0c\u201d\u7b80\u5fd7\u5eb7\u7ad9\u5728\u5f20\u606a\u4e0e\u6c5f\u654f\u4e4b\u7684\u8ddf\u524d\uff0c\u8ddf\u6c5f\u654f\u4e4b\u81ea\u6211\u4ecb\u7ecd\u9053\uff0c\u201c\u6211\u662f\u6c5f\u7701\u957f\u624b\u4e0b\u7684\u5c0f\u5175\u5352\uff0c\u5efa\u90ba\u6668\u62a5\u793e\u7b80\u5fd7\u5eb7\uff0c\u5e0c\u671b\u6c5f\u7701\u957f\u80fd\u7ed9\u6211\u5199\u7bc7\u4e13\u8bbf\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u770b\u8fc7\u4f60\u5199\u7684\u597d\u51e0\u7bc7\u7ecf\u6d4e\u4e13\u8bbf\uff0c\u5bf9\u9526\u6e56\u51e0\u4e2a\u4f01\u4e1a\u7684\u62a5\u9053\u5f88\u6df1\u5165\u5f88\u5168\u9762\uff0c\u8ba9\u4eba\u8bfb\u540e\u5370\u8c61\u6df1\u523b\uff0c\u521a\u624d\u5728\u91cc\u9762\u4f60\u7684\u95ee\u9898\u4e5f\u5f88\u7280\u5229\uff1a\u662f\u4e0d\u662f\u8981\u7ed9\u5f53\u524d\u7f51\u7edc\u6ce1\u6cab\u6cc4\u4e00\u6cc4\u865a\u706b\uff1f\u6211\u89c9\u5f97\u5f88\u6709\u5fc5\u8981\uff0c\u8981\u4fc3\u8fdb\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u66f4\u5065\u5eb7\u66f4\u6709\u5e8f\u7684\u53d1\u5c55\uff0c\u201d\u6c5f\u654f\u4e4b\u4e3b\u52a8\u4f38\u51fa\u624b\u8ddf\u7b80\u5fd7\u5eb7\u63e1\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5f88\u613f\u610f\u4f60\u6765\u5e2e\u6211\u5199\u4e00\u7bc7\u4e13\u8bbf\u2026\u2026\u201d</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u660e\u6446\u7740\u662f\u9526\u6e56\u7684\u5fa1\u7528\u6587\u4eba\uff0c\u6c5f\u6e44\u4e0d\u660e\u767d\u5979\u7238\u7238\u4e3a\u4ec0\u4e48\u8981\u540c\u610f\u8ba9\u7b80\u5fd7\u5eb7\u66ff\u4ed6\u5199\u5230\u4e1c\u6d77\u540e\u7684\u7b2c\u4e00\u7bc7\u4e13\u8bbf\u3002</p><p>\u3000\u3000\u542c\u5230\u6c5f\u654f\u4e4b\u7684\u8bdd\uff0c\u6210\u6620\u971e\u3001\u6731\u5168\u5bff\u7b49\u4eba\u5fae\u5fae\u5fc3\u60ca\uff0c\u6628\u5929\u4eba\u5927\u8d22\u7ecf\u59d4\u526f\u4e3b\u4efb\u5434\u6653\u6ce2\u53d1\u8868\u6587\u7ae0\u529b\u633a\u4e2d\u56fd\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u6c5f\u654f\u4e4b\u5e94\u8be5\u77e5\u9053\uff0c\u800c\u4e14\u521a\u624d\u5728\u91cc\u9762\u63a5\u53d7\u91c7\u8bbf\u65f6\u6c5f\u654f\u4e4b\u5e76\u6ca1\u6709\u6b63\u9762\u56de\u7b54\u7b80\u5fd7\u5eb7\u7684\u8fd9\u4e2a\u95ee\u9898\uff0c\u8fd9\u65f6\u5019\u65d7\u5e1c\u9c9c\u660e\u7684\u8868\u660e\u89c2\u70b9\u591a\u5c11\u6709\u4e9b\u6478\u4e0d\u7740\u5934\u8111\u3002</p><p>\u3000\u3000\u4ece\u6606\u817e\u5728\u7ebf\u7684\u5e94\u5bf9\u7b56\u7565\u4e0a\uff0c\u53ef\u4ee5\u770b\u51fa\u6606\u817e\u5728\u7ebf\u4ee5\u53ca\u9690\u85cf\u5728\u6606\u817e\u5728\u7ebf\u80cc\u540e\u7684\u9526\u6e56\u5bf9\u5f53\u524d\u4e92\u8054\u7f51\u7ecf\u6d4e\u5371\u673a\u7684\u6001\u5ea6\uff0c\u518d\u770b\u7b80\u5fd7\u5eb7\u5174\u594b\u7684\u795e\u8272\uff0c\u53ea\u6015\u8981\u62ff\u6c5f\u654f\u4e4b\u7684\u8fd9\u6bb5\u8bdd\u5199\u7bc7\u5927\u6587\u7ae0\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5fc3\u91cc\u66f4\u662f\u6ce2\u6f9c\u6c79\u6d8c\uff0c\u518d\u6ca1\u6709\u5fc3\u601d\u8ddf\u7740\u4e00\u8d77\u53bb\u7231\u8fbe\u56fe\u4e66\u9986\u53c2\u89c2\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u966a\u540c\u6c5f\u654f\u4e4b\u7b49\u4eba\u5f80\u8857\u5bf9\u9762\u7684\u7231\u8fbe\u56fe\u4e66\u9986\u8d70\u53bb\u3002\u53c2\u89c2\u7231\u8fbe\u56fe\u4e66\u9986\u4e4b\u540e\uff0c\u6c5f\u654f\u4e4b\u4e0e\u7701\u653f\u5e9c\u5b98\u5458\u5c31\u79bb\u5f00\u4e86\uff1b\u6731\u5168\u5bff\u77e5\u9053\u5f20\u606a\u4e0d\u559c\u6b22\u592a\u70ed\u95f9\uff0c\u53ea\u662f\u6210\u6620\u971e\u4ee3\u8868\u7701\u516c\u53f8\u5bb4\u8bf7\u5f20\u606a\u7b49\u4eba\uff0c\u53e6\u5916\u53ea\u634e\u5e26\u4e0a\u7b80\u5fd7\u5eb7\uff0c\u7701\u516c\u53f8\u5728\u4e1c\u6d77\u53d1\u5c55\uff0c\u5c11\u4e0d\u5f97\u8981\u501f\u52a9\u5230\u5efa\u90ba\u6668\u62a5\u793e\u7684\u529b\u91cf\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u6c5f\u654f\u4e4b\u600e\u4e48\u53ef\u80fd\u8868\u8fd9\u79cd\u6001\uff1f\u201d\u738b\u6d77\u7c9f\u5e26\u56de\u6765\u7684\u6d88\u606f\u8ba9\u6797\u96ea\u50cf\u53ea\u7ed9\u8e29\u4e2d\u5c3e\u5df4\u7684\u732b\uff0c\u201c\u6c5f\u654f\u4e4b\u600e\u4e48\u53ef\u80fd\u7518\u5fc3\u5f53\u9526\u6e56\u7684\u8d70\u72d7\uff1f\u201d</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u5f53\u7136\u4e0d\u4f1a\u5f53\u9526\u6e56\u7684\u8d70\u72d7\uff0c\u4f46\u662f\u5e76\u4e0d\u610f\u5473\u7740\u6c5f\u654f\u4e4b\u4e0d\u4f1a\u501f\u673a\u54ac\u6211\u4eec\u4e00\u53e3\u3002\u201d\u5218\u5947\u5cfb\u5206\u6790\u9053\uff0c\u201c\u6c5f\u654f\u4e4b\u7684\u4e00\u8d2f\u7acb\u573a\u662f\u4ec0\u4e48\uff1f\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u6caa\u6df1\u4e24\u5e02\u56e0\u4e3a\u4e92\u8054\u7f51\u6982\u5ff5\u800c\u80a1\u4ef7\u5927\u5e45\u4e0a\u6da8\u7684\u4e0a\u5e02\u516c\u53f8\u91cc\uff0c\u6709\u591a\u5c11\u5bb6\u662f\u56fd\u6709\u8d44\u672c\u5728\u63a7\u80a1\uff0c\u53c8\u6709\u591a\u5c11\u5bb6\u662f\u6c11\u8425\u8d44\u672c\u5728\u80cc\u540e\u8fd0\u4f5c\uff1f\u6c5f\u654f\u4e4b\u6709\u8fd9\u6837\u7684\u8a00\u8bba\uff0c\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u5947\u602a\uff0c\u4ed6\u5fc3\u91cc\u6216\u8bb8\u66f4\u671f\u5f85\u770b\u5230\u4e92\u8054\u7f51\u7ecf\u6d4e\u80fd\u591f\u5d29\u76d8\uff0c\u624d\u597d\u63a9\u9970\u56fd\u6709\u8d44\u672c\u7684\u65e0\u80fd\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u8981\u8b66\u60d5\u6c5f\u654f\u4e4b\uff1f\u201d\u738b\u6d77\u7c9f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u8ddf\u5434\u6653\u6ce2\u5f88\u591a\u573a\u5408\u90fd\u5531\u53cd\u8c03\uff0c\u201d\u4e25\u6587\u4ecb\u8bf4\u8d77\u53e6\u4e00\u6bb5\u6069\u6028\uff0c\u201c\u6c5f\u9759\u6db5\u516d\u5341\u5e74\u4ee3\u7ed9\u6574\u5012\u65f6\uff0c\u90a3\u6bb5\u65f6\u671f\u7ed9\u6c5f\u9759\u6db5\u62c5\u4efb\u52a9\u624b\u7684\u5434\u6653\u6ce2\u5374\u6ca1\u6709\u53d7\u7275\u7d2f\uff0c\u6c5f\u5bb6\u4e00\u76f4\u90fd\u6000\u7591\u662f\u90a3\u6bb5\u65f6\u95f4\u5434\u6653\u6ce2\u4e5f\u5728\u80cc\u540e\u6574\u6c5f\u9759\u6db5\u7684\u9ed1\u6750\u6599\u2026\u2026\u201d\u4e25\u6587\u4ecb\u8fd9\u6bb5\u65f6\u95f4\u662f\u975e\u5e38\u7684\u8f9b\u82e6\uff0c\u6628\u5929\u4eba\u8fd8\u5728\u5317\u4eac\uff0c\u4e2d\u5348\u53c8\u98de\u56de\u5efa\u90ba\u6765\uff0c\u4e0b\u5348\u518d\u56de\u6587\u821f\u53bb\u3002</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u8ddf\u9526\u6e56\u4f1a\u4e0d\u4f1a\u6709\u9ed8\u5951\uff1f\u201d\u6797\u96ea\u63d0\u5230\u4e00\u4e2a\u5173\u952e\u6027\u7684\u95ee\u9898\uff0c\u8981\u662f\u6c5f\u654f\u4e4b\u662f\u6cdb\u6cdb\u800c\u8bba\u7684\u653b\u51fb\u6574\u4e2a\u4e92\u8054\u7f51\u6982\u5ff5\u80a1\uff0c\u90a3\u5bf9\u6d77\u7c9f\u79d1\u6280\u7684\u76f4\u63a5\u51b2\u51fb\u5c31\u8981\u5c11\u5f97\u591a\uff0c\u8981\u662f\u6c5f\u654f\u4e4b\u4e0e\u9526\u6e56\u3001\u4e0e\u5f20\u606a\u6709\u9ed8\u5951\uff0c\u90a3\u624d\u53eb\u8981\u8001\u547d\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u534a\u7c92\u777e\u4e38\u53ef\u80fd\u8bf4\u662f\u634f\u5728\u6c5f\u654f\u4e4b\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u6709\u9ed8\u5951\u5427\uff1f\u8fd9\u6b21\u5e94\u8be5\u662f\u6c5f\u654f\u4e4b\u8ddf\u5f20\u606a\u7684\u7b2c\u4e8c\u6b21\u63a5\u89e6\uff0c\u201d\u738b\u6d77\u7c9f\u5206\u6790\u9053\uff0c\u201c\u800c\u4e14\u4e24\u6b21\u63a5\u89e6\u7684\u65f6\u95f4\u90fd\u76f8\u5f53\u77ed\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u4e0a\u51e0\u53e5\uff0c\u600e\u4e48\u53ef\u80fd\u6709\u9ed8\u5951\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u80fd\u591f\u4e0a\u9a6c\u53ef\u6709\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u5728\u80cc\u540e\u6d3b\u52a8\u7684\u8eab\u5f71\uff0c\u201d\u5218\u5947\u5cfb\u8bf4\u9053\uff0c\u4ed6\u662f\u4e2a\u9634\u8c0b\u8bba\u8005\uff0c\u201c\u9526\u6e56\u5e94\u8be5\u8ddf\u6c5f\u5bb6\u65e9\u6709\u9ed8\u5951\u3002\u201d</p><p>\u3000\u3000\u201c\u672a\u5fc5\uff0c\u201d\u4e25\u6587\u4ecb\u5426\u8ba4\u5230\u5218\u5947\u5cfb\u7684\u731c\u6d4b\uff0c\u201c\u4e2d\u592e\u4e0d\u4f1a\u6d3e\u4e00\u540d\u8ddf\u9526\u6e56\u6709\u9ed8\u5951\u7684\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u5230\u4e1c\u6d77\u6765\uff0c\u4e00\u65e6\u6c5f\u654f\u4e4b\u518d\u8ddf\u9526\u6e56\u5f62\u6210\u9ed8\u5951\uff0c\u4ed6\u5c31\u4e0d\u53ef\u80fd\u957f\u65f6\u95f4\u7559\u5728\u4e1c\u6d77\u2026\u2026\u8bf4\u8d77\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\uff0c\u6211\u4eec\u4e5f\u4e00\u76f4\u5728\u80cc\u540e\u9ed8\u9ed8\u65bd\u4ee5\u963b\u529b\uff0c\u6c5f\u5bb6\u6216\u8bb8\u5bf9\u6211\u4eec\u4e5f\u6709\u4e9b\u8bb8\u610f\u89c1\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u4e0d\u8fc7\u6c5f\u654f\u4e4b\u4e5f\u4e0d\u662f\u90a3\u79cd\u4f1a\u6495\u7834\u8138\u7684\u5f3a\u52bf\u6027\u683c\u3002\u201d</p><p>\u3000\u3000\u6797\u96ea\u53cc\u624b\u62b1\u5728\u80f8\u524d\uff0c\u5979\u7a7f\u7740\u5355\u8584\u7684\u886c\u886b\uff0c\u9886\u5b50\u5fae\u655e\u7740\uff0c\u53cc\u624b\u7528\u529b\u7684\u62b1\u7740\u80f8\uff0c\u5c06\u4e73\u6c9f\u6324\u5f97\u8d8a\u53d1\u7684\u6df1\u9083\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u6ca1\u6709\u4eba\u6709\u5fc3\u601d\u5077\u7aa5\u5979\u7684\u7f8e\u8272\uff0c\u5979\u60f3\u524d\u56db\u5929\u524d\u7684\u4e1c\u6d77\u5546\u754c\u9886\u8896\u5e74\u4f1a\u9152\u4f1a\u4e0a\u5f20\u606a\u8ddf\u6c5f\u654f\u4e4b\u8bf4\u7684\u8bdd\uff0c\u4f3c\u4e4e\u4e00\u5207\u7684\u53d8\u5316\u8ddf\u53cd\u5e94\u90fd\u5728\u5f20\u606a\u7684\u9884\u6599\u4e4b\u5185\uff0c\u6797\u96ea\u53ea\u89c9\u5f97\u5fc3\u5e95\u5934\u6e17\u51fa\u4e00\u80a1\u5bd2\u6c14\u6765\uff0c\u6709\u8fd9\u6837\u7684\u5bf9\u624b\uff0c\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u627e\u5230\u4f1a\u8d62\u7684\u611f\u89c9\u4e0e\u81ea\u4fe1\uff1f</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u6797\u96ea\u53cd\u5012\u4e0d\u77e5\u9053\u600e\u4e48\u8bf4\u597d\u4e86\uff0c\u4e25\u6587\u4ecb\u624b\u6307\u5934\u8f7b\u6572\u7740\u684c\u9762\uff0c\u76b1\u7709\u60f3\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u654f\u4e4b\u4e0d\u53ef\u80fd\u523b\u610f\u7684\u53ea\u9488\u5bf9\u6211\u4eec\uff0c\u4ed6\u4e0d\u53ef\u80fd\u75d5\u8ff9\u592a\u660e\u663e\uff1b\u8fd8\u6709\u6211\u4eec\u8981\u60f3\u65b9\u6cd5\u63a7\u5236\u5efa\u90ba\u6668\u62a5\u7684\u5f71\u54cd\u529b\uff0c\u6211\u4eec\u5373\u4f7f\u4e0d\u80fd\u9650\u5236\u6606\u817e\u5728\u7ebf\u7684\u65b0\u95fb\u62a5\u9053\u4e0e\u8bc4\u8bba\uff0c\u4f46\u662f\u5176\u4ed6\u4e24\u4e09\u95e8\u6237\u7f51\u7edc\uff0c\u6211\u4eec\u8fd8\u662f\u80fd\u65bd\u52a0\u5f71\u54cd\u7684\uff0c\u4e0d\u8981\u8ba9\u6ce1\u6cab\u5371\u673a\u7684\u8a00\u8bba\u751a\u56a3\u5c18\u4e0a\uff0c\u6211\u4eec\u53ea\u8981\u80fd\u71ac\u8fc7\u4e94\u6708\u5c31\u53ef\u4ee5\u4e86\u3002\u8fd9\u4e5f\u4e0d\u662f\u7279\u522b\u7684\u96be\uff0c\u6628\u5929\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5c31\u5f00\u59cb\u7a33\u4f4f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u4e5f\u671f\u5f85\u6c5f\u654f\u4e4b\u53ea\u662f\u5bf9\u5f53\u524d\u4e92\u8054\u7f51\u7ecf\u6d4e\u5371\u673a\u6cdb\u6cdb\u8bae\u8bba\u3001\u6ca1\u6709\u523b\u610f\u9488\u5bf9\u6d77\u7c9f\u79d1\u6280\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u60f3\u5230\u4e25\u5bb6\u80cc\u540e\u7684\u52bf\u529b\u662f\u5982\u6b64\u7684\u5e9e\u5927\uff0c\u6c5f\u654f\u4e4b\u8ddf\u9526\u6e56\u4e4b\u95f4\u6ca1\u6709\u4ec0\u4e48\u5229\u5bb3\u74dc\u845b\uff0c\u4e0d\u53ef\u80fd\u5b64\u6ce8\u4e00\u63b7\u66ff\u9526\u6e56\u8ddf\u4e25\u5bb6\u6495\u7834\u8138\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u7684\u52a8\u4f5c\u5f88\u5feb\uff0c\u5efa\u90ba\u6668\u62a5\u7b2c\u4e8c\u5929\u6e05\u6668\u5c31\u520a\u767b\u4e86\u5bf9\u6c5f\u654f\u4e4b\u7684\u91c7\u8bbf\u6587\u7ae0\uff0c\u65d7\u5e1c\u9c9c\u660e\u7684\u66ff\u6c5f\u654f\u4e4b\u8868\u8fbe\u4e86\u6caa\u6df1\u4e24\u5e02\u8ddf\u4e92\u8054\u7f51\u6cbe\u8fb9\u7684\u4f01\u4e1a\u8be5\u6cc4\u6cc4\u706b\u7684\u89c2\u70b9\u3002</p></article>"}}}]);
