(window.webpackJsonp=window.webpackJsonp||[]).push([[1615],{1638:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u56db\u8282 \u5965\u6c83\u7684\u63d0\u8bae",content:"<article><h2>\u7b2c\u516d\u5341\u56db\u8282 \u5965\u6c83\u7684\u63d0\u8bae</h2><p>\u8389\u6cd5\u5c14\u5e76\u4e0d\u559c\u6b22\u5deb\u5e08\u4e4b\u5893\uff0c\u8fd9\u70b9\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u4f9d\u65e7\u8fd8\u662f\u62d2\u7edd\u4e86\u743c\u6069\u7684\u63d0\u8bae\u3002\u7b2c\u4e8c\u5929\u65e9\u4e0a\u9192\u6765\u540e\uff0c\u4ed6\u4eec\u4fbf\u518d\u6ca1\u8c08\u53ca\u8fd9\u4e2a\u8bdd\u9898\uff0c\u5c31\u4eff\u4f5b\u6ca1\u53d1\u751f\u8fc7\u4e00\u822c\u3002</p><p>\u767d\u5929\uff0c\u4ed6\u4eec\u8fdb\u5165\u56fe\u4e66\u9986\uff0c\u67e5\u9605\u8d44\u6599\uff0c\u7ffb\u770b\u4e66\u7c4d\uff0c\u5404\u81ea\u5bfb\u627e\u7740\u81ea\u5df1\u611f\u5174\u8da3\u7684\u4e1c\u897f\uff0c\u6284\u5f55\u7b14\u8bb0\uff1b\u665a\u4e0a\uff0c\u4ed6\u4eec\u56de\u5230\u4f4f\u5904\uff0c\u7f20\u7ef5\u6b22\u597d\uff0c\u5171\u5ea6\u6625\u5bb5\u3002\u5e73\u9759\u5b89\u5b81\u7684\u751f\u6d3b\uff0c\u8ba9\u743c\u6069\u6709\u4e00\u79cd\u9519\u89c9\uff0c\u4eff\u4f5b\u53c8\u56de\u5230\u4e86\u5f53\u5e74\u7684\u5b66\u751f\u65f6\u4ee3\u3002</p><p>\u7b2c\u56db\u5929\u4e0b\u5348\uff0c\u4ed6\u4eec\u79bb\u5f00\u70db\u5821\uff0c\u4e00\u8def\u540c\u884c\u56de\u5230\u535a\u5f97\u4e4b\u95e8\uff0c\u8389\u6cd5\u5c14\u8981\u56de\u5deb\u5e08\u4e4b\u5893\uff0c\u743c\u6069\u5219\u8981\u53bb\u548c\u540c\u4f34\u6c47\u5408\uff0c\u4e24\u4eba\u4fbf\u5728\u57ce\u95e8\u53e3\u5206\u624b\u3002</p><p>\u201c\u6211\u4f1a\u5e38\u6765\u770b\u4f60\u3002\u201d\u743c\u6069\u5c06\u5979\u62b1\u5728\u6000\u4e2d\uff0c\u543b\u7740\u7cbe\u81f4\u5a07\u5ae9\u7684\u5634\u5507\uff0c\u6709\u4e9b\u51b0\u51c9\uff0c\u751c\u751c\u7684\uff0c\u4eff\u4f5b\u51b0\u7cd6\u7684\u5473\u9053\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u8389\u6cd5\u5c14\uff0c\u6211\u4eec\u505a\u4e2a\u7ea6\u5b9a\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u7ea6\u5b9a\uff1f\u201d</p><p>\u201c\u7b49\u6211\u3002\u201d</p><p>\u201c\u7b49\u4f60\uff1f\u201d\u8389\u6cd5\u5c14\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u7b49\u4f60\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5c06\u6765\u7684\u67d0\u4e00\u5929\uff0c\u6211\u4e5f\u4f1a\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u6211\u4f1a\u521b\u9020\u4e00\u5ea7\u5c5e\u4e8e\u81ea\u5df1\u7684\u6d6e\u7a7a\u57ce\uff0c\u201d\u743c\u6069\u5b9a\u5b9a\u5730\u51dd\u89c6\u7740\u5979\u7684\u773c\u775b\uff0c\u201c\u6211\u5e0c\u671b\u5b83\u662f\u4e00\u5ea7\u68ee\u6797\u57ce\u5e02\u2014\u2014\u8389\u6cd5\u5c14\uff0c\u5230\u65f6\u5019\u4f60\u80fd\u6765\u5e2e\u6211\u8bbe\u8ba1\u5b83\u5417\uff1f\u201d</p><p>\u5438\u8840\u9b3c\u7f8e\u5973\u6c89\u9ed8\u7247\u523b\uff0c\u9759\u9759\u70b9\u5934\u3002</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u662f\u627f\u8bfa\u3002\u201d</p><p>\u8389\u6cd5\u5c14\u5fae\u7b11\uff0c\u6325\u624b\u4f5c\u522b\u3002\u743c\u6069\u770b\u7740\u5979\u77eb\u5065\u5a40\u5a1c\u7684\u8eab\u5f71\u5728\u89c6\u91ce\u5c3d\u5934\u6d88\u5931\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u538b\u4f4e\u6597\u7bf7\u7684\u515c\u5e3d\uff0c\u8d70\u8fdb\u57ce\u5e02\uff0c\u524d\u5f80\u707f\u91d1\u4e4b\u5bb6\u548c\u8299\u857e\u72c4\u7b49\u4eba\u6c47\u5408\u3002</p><p>\u56e0\u4e3a\u66fe\u7ecf\u5728\u8fd9\u91cc\u72af\u8fc7\u6848\uff08\u743c\u6069\u548c\u6885\u83f2\u65af\u6740\u4e86\u535a\u5f97\u4e4b\u95e8\u7684\u8389\u62c9\u516c\u7235\uff09\uff0c\u76ee\u524d\u8fd8\u5904\u4e8e\u88ab\u901a\u7f09\u72b6\u6001\uff0c\u743c\u6069\u4e0d\u6562\u4e45\u7559\uff0c\u8fde\u51fa\u95e8\u4e0a\u8857\u90fd\u662f\u7528\u9b54\u6cd5\u4f2a\u88c5\u4e86\u76f8\u8c8c\u3002\u5306\u5306\u8fc7\u4e86\u4e00\u591c\uff0c\u4ed6\u4eec\u518d\u5ea6\u542f\u7a0b\u5317\u4e0a\uff0c\u62b5\u8fbe\u5315\u9996\u6ee9\u540e\u91cd\u65b0\u96c7\u4e86\u8f86\u9a6c\u8f66\uff0c\u8f6c\u5f2f\u5411\u4e1c\uff0c\u6cbf\u7740\u5fb7\u6797\u6bd5\u5c14\u6cb3\u4e00\u8def\u62b5\u8fbe\u897f\u5eb7\u5e03\u3002</p><p>\u897f\u5eb7\u5e03\u662f\u4e00\u4e2a\u5c0f\u9547\uff0c\u89c4\u6a21\u4e0d\u5927\uff0c\u5386\u53f2\u4e5f\u4e0d\u957f\uff0c\u4f46\u56e0\u4e3a\u5730\u5904\u5546\u8d38\u8981\u9053\uff0c\u6240\u4ee5\u5f88\u662f\u7e41\u534e\u3002\u743c\u6069\u4e0a\u6b21\u548c\u6885\u83f2\u65af\u8def\u8fc7\u6b64\u5730\u65f6\uff0c\u542c\u8bf4\u524d\u9762\u7684\u661f\u4e4b\u5c71\u8109\u91cc\u5c45\u4f4f\u7740\u4e00\u6761\u4e0a\u53e4\u7eff\u9f99\uff0c\u52a8\u8f84\u8dd1\u51fa\u6765\u88ad\u51fb\u541e\u5403\u6765\u5f80\u7684\u5546\u65c5\u6e38\u5ba2\uff0c\u5341\u5206\u4ee4\u4eba\u5934\u75bc\u3002\u5982\u4eca\u5230\u4e86\u4e00\u95ee\uff0c\u624d\u77e5\u9053\u8fd9\u6761\u7eff\u9f99\u5df2\u7ecf\u6709\u5c0f\u534a\u5e74\u6ca1\u6709\u518d\u51fa\u73b0\u8fc7\uff0c\u4e0d\u77e5\u9053\u662f\u56de\u5de2\u7a74\u7ee7\u7eed\u4f11\u7720\uff0c\u8fd8\u662f\u88ab\u67d0\u4e9b\u5f3a\u5927\u65e0\u654c\u7684\u5192\u9669\u8005\u7ed9\u5e72\u6389\u4e86\uff0c\u603b\u4e4b\u662f\u4ef6\u53ef\u559c\u53ef\u8d3a\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u4e0b\u4e00\u7ad9\u662f\u55a7\u6c34\u57ce\uff0c\u201d\u8299\u857e\u72c4\u770b\u7740\u5730\u56fe\uff0c\u795e\u8272\u95f4\u9887\u6709\u4e9b\u559c\u60a6\uff0c\u201c\u7136\u540e\u662f\u6d1b\u514b\u9547\uff0c\u518d\u7a7f\u8fc7\u7070\u5cf0\u5c71\u8109\uff0c\u8d8a\u8fc7\u5b64\u5bc2\u8352\u539f\uff0c\u5c31\u8fdb\u5165\u5927\u6c99\u6f20\u4e86\u3002\u201d</p><p>\u548c\u743c\u6069\u8fd9\u79cd\u5e26\u6709\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\u8bb0\u5fc6\u7684\u7a7f\u8d8a\u8005\u4e0d\u540c\uff0c\u5bf9\u4e8e\u83ab\u5c3c\u5361\u59d0\u59b9\u800c\u8a00\uff0c\u9634\u9b42\u57ce\u662f\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u201c\u5bb6\u4e61\u201d\uff0c\u5979\u4eec\u751f\u4e8e\u6b64\uff0c\u957f\u4e8e\u6b64\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u51fa\u8fdc\u95e8\uff0c\u4e00\u8d70\u5c31\u662f\u4e00\u5e74\u591a\uff0c\u5982\u4eca\u5373\u5c06\u56de\u5bb6\uff0c\u5fc3\u60c5\u81ea\u7136\u5f88\u6109\u5feb\u3002\u743c\u6069\u4e5f\u5f88\u60f3\u65e9\u4e00\u70b9\u56de\u53bb\u89c1\u73ca\u5609\uff0c\u7136\u800c\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6\u8fd8\u6709\u4e9b\u4e8b\u60c5\u8981\u505a\u3002</p><p>\u4ed6\u5f97\u53bb\u89c1\u5965\u6c83\u3002</p><p>\u5965\u6c83\u5c45\u4f4f\u5728\u72ec\u89d2\u517d\u4e4b\u6d41\uff0c\u5c31\u5728\u4ece\u897f\u5eb7\u5e03\u5230\u55a7\u6c34\u57ce\u7684\u534a\u8def\u4e0a\uff0c\u8003\u8651\u5230\u8fd9\u6b7b\u80d6\u5b50\u7684\u5b85\u7537\u5c5e\u6027\u548c\u6076\u52a3\u524d\u79d1\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u8ba9\u83ab\u5c3c\u5361\u59d0\u59b9\u540c\u884c\uff0c\u800c\u662f\u8ba9\u5979\u4eec\u5148\u53bb\u55a7\u6c34\u57ce\uff0c\u81ea\u5df1\u5219\u5e26\u7740\u838e\u73de\u514b\uff0c\u4e00\u8def\u524d\u5f80\u8001\u5deb\u5996\u7684\u5893\u7a74\u3002</p><p>\u4ed6\u4ee5\u524d\u548c\u6885\u83f2\u65af\u6765\u8fc7\u4e00\u6b21\uff0c\u719f\u95e8\u719f\u8def\uff0c\u6ca1\u8d39\u4ec0\u4e48\u529b\u6c14\u5c31\u627e\u5230\u4e86\u5730\u65b9\u3002\u5965\u6c83\u663e\u7136\u5bf9\u5468\u56f4\u533a\u57df\u5e03\u6709\u4e25\u5bc6\u7684\u76d1\u63a7\uff0c\u4e24\u4eba\u521a\u521a\u8fdb\u5165\u5893\u7a74\uff0c\u4fbf\u6709\u4e00\u53ea\u5e7d\u7075\u8fce\u63a5\u4e0a\u6765\uff0c\u8bf4\u662f\u5927\u5965\u672f\u5e08\u5df2\u7ecf\u77e5\u9053\u743c\u6069\u7684\u5230\u6765\uff0c\u6b63\u5728\u56fe\u4e66\u5ba4\uff0c\u8bf7\u4ed6\u8fc7\u53bb\u3002</p><p>\u743c\u6069\u5148\u8ba9\u5e7d\u7075\u5e26\u7740\u838e\u73de\u514b\u53bb\u4f11\u606f\uff0c\u81ea\u5df1\u524d\u5f80\u56fe\u4e66\u5ba4\u3002\u8001\u5deb\u5996\u6b63\u60a0\u95f2\u81ea\u5f97\u5730\u5750\u5728\u5bbd\u5927\u7684\u8eba\u6905\u91cc\uff0c\u624b\u4e0a\u7ffb\u7740\u4e00\u672c\u4e66\uff0c\u4ed6\u7684\u8eab\u6750\u4f9d\u65e7\u77ee\u80d6\uff0c\u5934\u53d1\u4f9d\u65e7\u84ec\u4e71\uff0c\u809a\u5b50\u6bd4\u4e0a\u6b21\u89c1\u5230\u4f3c\u4e4e\u53c8\u6eda\u5706\u4e86\u51e0\u5206\uff0c\u867d\u7136\u8fd9\u5e94\u8be5\u662f\u9519\u89c9\u3002\u743c\u6069\u4e0a\u524d\u4e24\u6b65\uff0c\u6df1\u6df1\u8eac\u8eab\u884c\u793c\u3002</p><p>\u201c\u8001\u5e08\u3002\u201d</p><p>\u5965\u6c83\u653e\u4e0b\u4e66\uff0c\u793a\u610f\u743c\u6069\u5750\u4e0b\uff0c\u201c\u521a\u4ece\u5e7d\u6697\u5730\u57df\u56de\u6765\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u521a\u56de\u6765\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u51c6\u5907\u56de\u9634\u9b42\u57ce\u590d\u547d\uff0c\u6b63\u597d\u8def\u8fc7\u60a8\u8fd9\u91cc\u3002\u201d</p><p>\u201c\u5514\uff0c\u90a3\u4e2a\u53eb\u827e\u5f25\u8587\u7684\u5c0f\u59d1\u5a18\u5462\uff0c\u201d\u5965\u6c83\u663e\u7136\u6bd4\u8f83\u5173\u5fc3\u8fd9\u4e2a\u95ee\u9898\uff0c\u201c\u5979\u600e\u4e48\u6ca1\u548c\u4f60\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u201c\u2026\u2026\u5979\u6709\u4e8b\u5148\u53bb\u963f\u683c\u62c9\u9686\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u201d\u5965\u6c83\u60cb\u60dc\uff0c\u201c\u592a\u9057\u61be\u4e86\uff0c\u6211\u4ee5\u4e3a\u5979\u4f1a\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u672c\u6765\u8fd8\u7279\u5730\u7ed9\u5979\u51c6\u5907\u4e86\u4e00\u4efd\u793c\u7269\u5462\uff0c\u6253\u7b97\u8fd9\u6b21\u5f53\u9762\u6c42\u5a5a\u2026\u2026\u5f00\u73a9\u7b11\uff0c\u5f00\u73a9\u7b11\u800c\u5df2\uff0c\u201d\u4ed6\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u201c\u8138\u8272\u522b\u90a3\u4e48\u96be\u770b\uff0c\u743c\u6069\uff0c\u8981\u9547\u5b9a\uff0c\u9547\u5b9a\uff0c\u4efb\u4f55\u65f6\u5019\u90fd\u8981\u4ece\u5bb9\u4e0d\u8feb\uff0c\u8fd9\u662f\u4f5c\u4e3a\u4e00\u540d\u5deb\u5e08\u7684\u5fc5\u5907\u7d20\u8d28\u554a\u3002\u201d</p><p>\u201c\u8001\u5e08\uff0c\u6211\u5fc3\u7406\u7d20\u8d28\u5dee\u2026\u2026\u8fd9\u79cd\u73a9\u7b11\u4ee5\u540e\u662f\u4e0d\u662f\u4e0d\u5f00\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u771f\u6ca1\u5e7d\u9ed8\u611f\uff0c\u201d\u5965\u6c83\u626b\u5174\u5730\u6446\u6446\u624b\uff0c\u201c\u7b97\u5566\uff0c\u61d2\u5f97\u548c\u4f60\u8ba1\u8f83\u3002\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u4f60\u8fd9\u6b21\u5e26\u6765\u7684\u90a3\u4e2a\u9b45\u9b54\u4f3c\u4e4e\u4e5f\u4e0d\u9519\u554a\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u633a\u6f02\u4eae\uff0c\u8eab\u6750\u4e5f\u597d\uff0c\u5c31\u662f\u5473\u9053\u8fd8\u4e0d\u591f\uff0c\u4e0d\u7b97\u4e0a\u54c1\u3002\u201d</p><p>\u201c\u5473\u9053\u4e0d\u591f\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4ec0\u4e48\u5473\u9053\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6bd4\u8f83\u9ad8\u6df1\uff0c\u5f88\u96be\u548c\u4f60\u8fd9\u79cd\u7f3a\u4e4f\u9605\u5386\u7684\u5e74\u8f7b\u4eba\u89e3\u91ca\uff0c\u201d\u5965\u6c83\u9887\u4e3a\u77dc\u6301\u5730\u8bf4\uff0c\u201c\u6240\u8c13\u5473\u9053\uff0c\u5c31\u662f\u4e00\u79cd\u611f\u89c9\u2014\u2014\u558f\uff0c\u4e0a\u6b21\u90a3\u4e2a\u53eb\u827e\u5f25\u8587\u7684\u5c0f\u59d1\u5a18\u5c31\u4e0d\u9519\uff0c\u6bd4\u5979\u66f4\u6709\u5473\u9053\u4e9b\uff0c\u4f46\u8fd8\u662f\u5dee\u4e86\u70b9\uff0c\u7f8e\u4e2d\u4e0d\u8db3\u3002\u201d</p><p>\u827e\u5f25\u8587\u8fd9\u79cd\u7edd\u8272\u7f8e\u4eba\u90fd\u201c\u8fd8\u5dee\u4e86\u70b9\u201d\uff1f\u4f60\u8fd8\u771f\u6311\u5254\u2026\u2026</p><p>\u201c\u60a8\u6240\u8c13\u7684\u2018\u5473\u9053\u2019\uff0c\u5230\u5e95\u662f\u6307\u2026\u2026\u201d</p><p>\u5965\u6c83\u6478\u6478\u9f3b\u5b50\uff0c\u201c\u4e0d\u592a\u5bb9\u6613\u5f62\u5bb9\u554a\uff0c\u8fd9\u4e48\u8bf4\u5427\uff0c\u4e00\u4e2a\u771f\u6b63\u4e0a\u7b49\u7684\u5973\u4eba\uff0c\u4e0d\u4ec5\u4ec5\u662f\u8981\u6f02\u4eae\uff0c\u8981\u8eab\u6750\u597d\uff0c\u800c\u4e14\u8fd8\u6709\u90a3\u79cd\u611f\u89c9\u2026\u2026\u5514\uff0c\u6bd4\u5982\u8bf4\uff0c\u51db\u51db\u5a01\u4e25\u7684\u795e\u60c5\u3001\u5c45\u9ad8\u4e34\u4e0b\u7684\u59ff\u6001\u3001\u98de\u626c\u8dcb\u6248\u7684\u6c14\u6d3e\u2026\u2026\u201d</p><p>\u201c\u8001\u5e08\uff0c\u201d\u743c\u6069\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6253\u65ad\uff0c\u201c\u60a8\u6240\u8bf4\u7684\u2026\u2026\u662f\u5973\u738b\u5427\uff1f\u201d</p><p>\u201c\u5973\u738b\uff1f\u5bf9\uff01\u201d\u5965\u6c83\u53cc\u624b\u4e00\u62cd\uff0c\u201c\u6ca1\u9519\uff0c\u5c31\u662f\u5973\u738b\uff0c\u8fd9\u4e2a\u8bcd\u5f62\u5bb9\u5f97\u592a\u8d34\u5207\u4e86\uff0c\u6211\u600e\u4e48\u4ee5\u524d\u5c31\u6ca1\u60f3\u5230\u8fc7\u3002\u743c\u6069\uff0c\u4f60\u679c\u7136\u5f88\u6709\u5929\u8d4b\uff0c\u4e0d\u6127\u662f\u6211\u6700\u4f18\u79c0\u7684\u5b66\u751f\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u539f\u6765\u8fd9\u6b7b\u80d6\u5b50\u5c45\u7136\u559c\u6b22\u5973\u738b\u578b\u7684\uff0c\u771f\u662f\u4eba\u4e0d\u53ef\u8c8c\u76f8\u3002\u743c\u6069\u672c\u6765\u8fd8\u60f3\u95ee\u95ee\u5965\u6c83\u77e5\u4e0d\u77e5\u9053\u6b23\u5e03\u5973\u738b\uff0c\u90a3\u6050\u6015\u662f\u6700\u7b26\u5408\u4ed6\u7684\u8981\u6c42\u4e86\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\u8fd8\u662f\u522b\u63d0\u8fd9\u832c\u3002\u4e07\u4e00\u60f9\u5f97\u8001\u5deb\u5996\u52a8\u4e86\u5174\u81f4\uff0c\u771f\u53bb\u627e\u6b23\u5e03\u9ebb\u70e6\uff0c\u90a3\u51db\u5c31\u8981\u6765\u627e\u81ea\u5df1\u7684\u9ebb\u70e6\u4e86\u2026\u2026</p><p>\u4e0d\u8fc7\u63d0\u8d77\u9b45\u9b54\uff0c\u6b63\u597d\u89e6\u52a8\u743c\u6069\u7684\u5fc3\u4e8b\uff0c\u4ed6\u7d22\u6027\u4fbf\u5c06\u8fd9\u4e2a\u95ee\u9898\u76f4\u63a5\u63d0\u4e86\u51fa\u6765\uff0c\u201c\u8001\u5e08\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u60a8\u77e5\u9053\u771f\u540d\u5951\u7ea6\u5417\uff1f\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u6211\u5f53\u7136\u2026\u2026\u201d</p><p>\u4ed6\u8bdd\u8bf4\u4e86\u534a\u622a\u7a81\u7136\u505c\u4f4f\uff0c\u773c\u795e\u4e00\u51db\uff0c\u671d\u4ed6\u671b\u4e86\u8fc7\u6765\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u4ed4\u7ec6\u6253\u91cf\u4e86\u534a\u5929\uff0c\u8138\u8272\u6e10\u6e10\u53d8\u5f97\u51dd\u91cd\u8d77\u6765\uff0c\u201c\u4f60\u2026\u2026\u201d\u4ed6\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u592a\u786e\u5b9a\uff0c\u201c\u4f60\u96be\u9053\u7b7e\u4e86\u771f\u540d\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u548c\u8c01\u2026\u2026\u548c\u90a3\u4e2a\u9b45\u9b54\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u641e\u7684\uff1f\u201d\u5965\u6c83\u7709\u5934\u76b1\u8d77\uff0c\u201c\u771f\u540d\u5951\u7ea6\u90a3\u79cd\u4e1c\u897f\u600e\u4e48\u80fd\u968f\u4fbf\u4e71\u7b7e\uff0c\u4f60\u4e5f\u662f\u4e2a\u5deb\u5e08\uff0c\u8fde\u8fd9\u70b9\u57fa\u672c\u7684\u5e38\u8bc6\u90fd\u6ca1\u6709\u5417\uff1f\u201d</p><p>\u5c3d\u7ba1\u662f\u88ab\u8d23\u5907\uff0c\u743c\u6069\u5fc3\u4e2d\u8fd8\u662f\u9690\u9690\u5347\u8d77\u4e00\u9635\u611f\u52a8\uff0c\u201c\u4e0d\u662f\uff0c\u8001\u5e08\uff0c\u60a8\u542c\u6211\u89e3\u91ca\uff0c\u201d\u4ed6\u82e6\u7b11\uff0c\u201c\u771f\u540d\u5951\u7ea6\u4e0d\u80fd\u4e71\u7b7e\uff0c\u8fd9\u6211\u81ea\u7136\u77e5\u9053\uff0c\u4f46\u5f53\u65f6\u5b9e\u5728\u662f\u6ca1\u529e\u6cd5\u554a\u3002\u201d</p><p>\u4ed6\u5c06\u4e8b\u60c5\u7684\u7ecf\u8fc7\u5927\u81f4\u5bf9\u5965\u6c83\u8bf4\u4e86\u4e00\u904d\uff0c\u8001\u5deb\u5996\u542c\u4e86\u6c89\u541f\u4e0d\u8bed\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u4ed6\u55ef\u4e86\u4e00\u58f0\uff0c\u6325\u4e86\u6325\u624b\uff0c\u201c\u7b97\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u4e0d\u7528\u591a\u60f3\u3002\u53cd\u6b63\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\u3002\u201d</p><p>\u201c\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\uff1f\u201d</p><p>\u743c\u6069\u6115\u7136\uff0c\u771f\u540d\u5951\u7ea6\u867d\u7136\u6709\u8bf8\u591a\u597d\u5904\uff0c\u4f46\u5b83\u6709\u4e00\u4e2a\u81f4\u547d\u7684\u526f\u4f5c\u7528\uff0c\u4fbf\u662f\u201c\u751f\u547d\u7ed1\u5b9a\u201d\u3002\u7b7e\u7ea6\u53cc\u65b9\u4e2d\u6709\u4e00\u65b9\u6b7b\u4e86\uff0c\u53e6\u5916\u4e00\u65b9\u8ddf\u7740\u5b8c\u86cb\uff0c\u8fd9\u600e\u4e48\u8fd8\u4e0d\u662f\u5927\u95ee\u9898\uff1f\u6b63\u662f\u56e0\u4e3a\u6709\u8fd9\u4e2a\u8981\u547d\u7684\u7f3a\u9677\uff0c\u6240\u4ee5\u771f\u540d\u5951\u7ea6\u81ea\u4ece\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u5c31\u6ca1\u51e0\u4e2a\u4eba\u6562\u7528\u7684\uff0c\u5426\u5219\u53ea\u6015\u5168\u5929\u4e0b\u7684\u5deb\u5e08\u4eec\u51fa\u95e8\uff0c\u90fd\u8981\u5e26\u7740\u4e00\u4f4d\u6f02\u4eae\u706b\u8fa3\u7684\u9b45\u9b54\u4e86\u3002</p><p>\u867d\u7136\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u771f\u540d\u5951\u7ea6\u8fd8\u6ca1\u6709\u7ed9\u743c\u6069\u5e26\u6765\u592a\u5927\u7684\u5371\u9669\uff0c\u4f46\u8fd9\u603b\u662f\u4e2a\u9690\u60a3\u2014\u2014\u800c\u4e14\u662f\u81f4\u547d\u7684\u9690\u60a3\u3002\u4ed6\u8fd9\u6b21\u6765\u62dc\u8bbf\u5965\u6c83\uff0c\u4e3b\u8981\u662f\u770b\u671b\u8001\u5e08\uff0c\u4f46\u4e5f\u62b1\u7740\u4e00\u4e2a\u76ee\u7684\uff0c\u4fbf\u662f\u60f3\u770b\u770b\u6709\u6ca1\u6709\u53ef\u80fd\u89e3\u51b3\u6389\u8fd9\u4e2a\u9ebb\u70e6\u3002\u4ed6\u4e4b\u6240\u4ee5\u5e26\u7740\u838e\u73de\u514b\u4e00\u8d77\u8fc7\u6765\uff0c\u4e5f\u5c31\u662f\u8fd9\u4e2a\u76ee\u7684\u3002</p><p>\u4ece\u9b54\u6cd5\u7406\u8bba\u4e0a\u8bf4\uff0c\u771f\u540d\u5951\u7ea6\u6548\u529b\u81f3\u9ad8\uff0c\u5b83\u501f\u52a9\u7684\u662f\u8fd9\u4e2a\u591a\u5143\u5b87\u5b99\u4e2d\u6700\u672c\u6e90\u7684\u79e9\u5e8f\u529b\u91cf\uff0c\u8d85\u8d8a\u4e00\u5207\u6cd5\u5219\u4e4b\u4e0a\uff0c\u5c31\u7b97\u662f\u795e\u660e\u4e5f\u6ca1\u6cd5\u5e9f\u6b62\u5b83\u3002\u800c\u4e14\u8fd9\u4efd\u5951\u7ea6\uff0c\u56e0\u4e3a\u662f\u4ee5\u201c\u771f\u540d\u201d\u4e3a\u8981\u7d20\u8fdb\u884c\u7ea6\u675f\uff0c\u800c\u771f\u540d\u53c8\u662f\u548c\u7075\u9b42\u878d\u4e3a\u4e00\u4f53\u7684\u2014\u2014\u6362\u53e5\u8bdd\u8bf4\uff0c\u53ea\u8981\u7075\u9b42\u4e0d\u706d\uff0c\u5951\u7ea6\u7684\u6548\u529b\u5c31\u4f1a\u6301\u7eed\uff0c\u54ea\u6015\u743c\u6069\u628a\u81ea\u5df1\u53d8\u6210\u5438\u8840\u9b3c\uff0c\u53d8\u6210\u5deb\u5996\uff0c\u53d8\u6210\u5e7d\u7075\uff0c\u751a\u81f3\u53d8\u6210\u90aa\u9b54\uff0c\u53d8\u6210\u5929\u4f7f\u2014\u2014\u5443\uff0c\u8fd9\u4e2a\u5c31\u7b97\u4e86\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u2014\u2014\u4ed6\u4f9d\u65e7\u8fd8\u662f\u4f1a\u53d7\u5230\u5951\u7ea6\u7684\u9650\u5236\uff0c\u65e0\u6cd5\u9003\u8131\u3002</p><p>\u5c31\u7406\u8bba\u4e0a\u800c\u8a00\uff0c\u8fd9\u5c31\u662f\u4e2a\u65e0\u89e3\u7684\u96be\u9898\u3002\u4f46\u4e8b\u60c5\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u743c\u6069\u4e5f\u53ea\u80fd\u62b1\u7740\u4e00\u7ebf\u5e0c\u671b\u6765\u78b0\u78b0\u8fd0\u6c14\uff0c\u5965\u6c83\u6bd5\u7adf\u662f\u5f53\u4eca\u4e16\u754c\u4e0a\u7b2c\u4e00\u7b49\u7684\u5927\u5deb\u5e08\uff0c\u7cbe\u7814\u7684\u53c8\u662f\u4ea1\u7075\u672f\uff0c\u800c\u771f\u540d\u5951\u7ea6\u4e3b\u8981\u6d89\u53ca\u7684\u662f\u8981\u7d20\u662f\u201c\u7075\u9b42\u201d\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c5e\u4e8e\u4ea1\u7075\u672f\u7684\u8303\u7574\uff0c\u6216\u8bb8\u8001\u5deb\u5996\u80fd\u6709\u529e\u6cd5\u3002</p><p>\u4ee4\u4ed6\u4e07\u4e07\u6ca1\u60f3\u5230\u7684\u662f\uff0c\u5965\u6c83\u5c45\u7136\u8bf4\u201c\u53cd\u6b63\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\u201d\uff1f\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u4ed6\u679c\u7136\u6709\u529e\u6cd5\u89e3\u9664\u771f\u540d\u5951\u7ea6\uff1f</p><p>\u201c\u4e0d\uff0c\u6211\u6ca1\u529e\u6cd5\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u771f\u540d\u5951\u7ea6\u4e00\u65e6\u7b7e\u8ba2\uff0c\u90a3\u5c31\u57fa\u672c\u6ca1\u6709\u53cd\u6094\u7684\u4f59\u5730\uff0c\u6ca1\u6709\u4efb\u4f55\u529e\u6cd5\u80fd\u591f\u64a4\u9500\u5b83\u2026\u2026\u54e6\uff0c\u4e0d\u5bf9\uff0c\u6216\u8bb8\u8fd8\u662f\u6709\u4eba\u80fd\u64a4\u9500\u5b83\u7684\uff0c\u4f46\u4f60\u4e5f\u6307\u671b\u4e0d\u4e86\u3002\u201d</p><p>\u201c\u8c01\uff1f\u201d\u743c\u6069\u8fde\u5fd9\u95ee\uff0c\u201c\u8c01\u80fd\u64a4\u9500\u771f\u540d\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u72c4\u9b54\u9ad8\u6839\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u771f\u540d\u5951\u7ea6\u6240\u51ed\u4f9d\uff0c\u662f\u5b87\u5b99\u95f4\u81f3\u9ad8\u7684\u2018\u79e9\u5e8f\u2019\uff0c\u90a3\u5c31\u552f\u6709\u6700\u5f3a\u7684\u2018\u6df7\u4e71\u2019\u624d\u53ef\u4ee5\u53bb\u5426\u5b9a\u5b83\u3001\u8df5\u8e0f\u5b83\u3001\u6495\u6bc1\u5b83\u3002\u72c4\u9b54\u9ad8\u6839\u662f\u6df1\u6e0a\u4e4b\u4e3b\uff0c\u6076\u9b54\u9886\u8896\uff0c\u6df1\u6e0a\u4e0e\u6076\u9b54\uff0c\u662f\u6574\u4e2a\u4e16\u754c\u2018\u6df7\u4e71\u2019\u7684\u5316\u8eab\u548c\u6295\u5c04\uff0c\u6240\u4ee5\u4ed6\u662f\u6709\u53ef\u80fd\u529e\u5230\u7684\u2014\u2014\u6709\u53ef\u80fd\uff0c\u201d\u8001\u5deb\u5996\u5f3a\u8c03\uff0c\u201c\u6bd5\u7adf\u8fd9\u53ea\u662f\u7406\u8bba\u4e0a\u7684\u63a8\u6d4b\uff0c\u8c01\u4e5f\u6ca1\u5f53\u771f\u8bd5\u8fc7\u3002\u66f4\u4f55\u51b5\uff0c\u81ea\u4ece\u6614\u65e5\u7684\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u9668\u706d\u540e\uff0c\u72c4\u9b54\u9ad8\u6839\u5c31\u5728\u7269\u8d28\u754c\u9500\u58f0\u533f\u8ff9\uff0c\u518d\u4e5f\u4e0d\u56de\u5e94\u51e1\u4eba\u7684\u7948\u7977\u53ec\u5524\uff0c\u624d\u4f1a\u8f6e\u5230\u683c\u62c9\u5179\u7279\u548c\u5965\u5580\u65af\u4e24\u4e2a\u5bb6\u4f19\u98ce\u5149\uff0c\u6240\u4ee5\u8bf4\uff0c\u4f60\u6839\u672c\u6307\u671b\u4e0d\u4e86\u3002\u201d</p><p>\u743c\u6069\u7684\u6ee1\u8154\u671f\u5f85\u987f\u65f6\u50cf\u88ab\u5f53\u5934\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u4f46\u8001\u5deb\u5996\u63a5\u4e0b\u6765\u7684\u8bdd\uff0c\u5374\u53c8\u8ba9\u4ed6\u91cd\u62fe\u5e0c\u671b\u3002</p><p>\u201c\u771f\u540d\u5951\u7ea6\u4e0d\u80fd\u64a4\u9500\uff0c\u4f46\u53ef\u4ee5\u89c4\u907f\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u6253\u4e0d\u8fc7\uff0c\u4f46\u4e0d\u89c1\u5f97\u8eb2\u4e0d\u5f00\u561b\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\u6709\u4ec0\u4e48\u4e0d\u61c2\u7684\uff1f\u201d\u5965\u6c83\u4f3c\u4e4e\u56e0\u4e3a\u743c\u6069\u7684\u53cd\u5e94\u8fdf\u949d\u800c\u6709\u4e9b\u4e0d\u6ee1\uff0c\u201c\u4f60\u4e4b\u6240\u4ee5\u62c5\u5fc3\u771f\u540d\u5951\u7ea6\uff0c\u65e0\u975e\u662f\u6015\u6709\u4eba\u901a\u8fc7\u90a3\u4e2a\u9b45\u9b54\u6765\u5bf9\u4ed8\u4f60\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5176\u5b9e\u4f60\u73b0\u5728\u5c31\u76f8\u5f53\u4e8e\u662f\u4e00\u4e2a\u5deb\u5996\uff0c\u800c\u90a3\u4e2a\u9b45\u9b54\u662f\u4f60\u7684\u547d\u5323\uff1b\u9b45\u9b54\u6b7b\u4e86\uff0c\u4f60\u6b7b\uff0c\u547d\u5323\u6bc1\u4e86\uff0c\u5deb\u5996\u6b7b\u2014\u2014\u662f\u8fd9\u6837\u5427\uff1f\u201d</p><p>\u201c\u5443\uff0c\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u5176\u5b9e\u8001\u5deb\u5996\u7684\u8fd9\u4e2a\u6bd4\u55bb\u5e76\u4e0d\u5341\u5206\u786e\u5207\uff0c\u838e\u73de\u514b\u548c\u743c\u6069\u7684\u5173\u7cfb\uff0c\u5e76\u4e0d\u662f\u547d\u5323\u548c\u5deb\u5996\u90a3\u4e48\u7b80\u5355\u3002\u547d\u5323\u56fa\u7136\u662f\u5deb\u5996\u7684\u81f4\u547d\u5173\u952e\u6240\u5728\uff0c\u4f46\u540c\u65f6\u4e5f\u662f\u4e00\u91cd\u4fdd\u969c\uff0c\u53ea\u8981\u5b83\u4e0d\u6bc1\uff0c\u5deb\u5996\u5c31\u7b97\u88ab\u7c89\u8eab\u788e\u9aa8\u90fd\u80fd\u518d\u5ea6\u91cd\u751f\uff0c\u838e\u73de\u514b\u5c31\u6ca1\u8fd9\u4e2a\u4f5c\u7528\u4e86\u3002\u53cd\u8fc7\u6765\uff0c\u838e\u73de\u514b\u4e5f\u662f\u6709\u5176\u4f18\u52bf\u7684\uff0c\u5979\u662f\u4e2a\u6d3b\u4eba\uff0c\u6709\u77e5\u89c9\uff0c\u6709\u610f\u8bc6\uff0c\u800c\u4e14\u806a\u660e\u3001\u7f8e\u8c8c\uff0c\u6709\u6218\u6597\u529b\uff0c\u80fd\u7ed9\u743c\u6069\u5e2e\u5fd9\uff0c\u66ff\u743c\u6069\u529e\u4e8b\uff0c\u966a\u4ed6\u804a\u5929\uff0c\u751a\u81f3\u548c\u4ed6\u4e0a\u5e8a\uff0c\u547d\u5323\u5c31\u6ca1\u8fd9\u4e9b\u529f\u80fd\u4e86\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u5965\u6c83\u7684\u8bf4\u6cd5\u4e5f\u4e0d\u7b97\u9519\u3002\u547d\u5323\u6bc1\u4e86\uff0c\u5deb\u5996\u4fbf\u6b7b\uff1b\u838e\u73de\u514b\u6b7b\u4e86\uff0c\u743c\u6069\u4fbf\u6b7b\u2014\u2014\u5728\u8fd9\u4e00\u70b9\u4e0a\uff0c\u4e24\u8005\u786e\u5b9e\u662f\u7c7b\u4f3c\u5171\u901a\u7684\u3002\u800c\u8fd9\u4e5f\u6b63\u662f\u95ee\u9898\u7684\u6700\u5173\u952e\u6240\u5728\uff0c\u662f\u743c\u6069\u6700\u5927\u7684\u5fc3\u75c5\uff0c\u53ea\u8981\u89e3\u51b3\u4e86\u5b83\uff0c\u5176\u4ed6\u90fd\u662f\u7ec6\u679d\u672b\u8282\uff0c\u53ef\u4ee5\u65e0\u6240\u8c13\u3002</p><p>\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u90a3\u5c31\u5f88\u597d\u529e\u4e86\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u5c31\u50cf\u5deb\u5996\u85cf\u8d77\u81ea\u5df1\u7684\u547d\u5323\u4e00\u6837\uff0c\u4f60\u4e5f\u628a\u5979\u85cf\u8d77\u6765\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u600e\u2026\u2026\u600e\u4e48\u85cf\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u5979\u662f\u4e2a\u6d3b\u4eba\uff0c\u53c8\u4e0d\u662f\u4e00\u4ef6\u4e1c\u897f\u3002\u201d</p><p>\u201c\u4e5f\u6ca1\u4ec0\u4e48\u96be\u7684\uff0c\u201d\u5965\u6c83\u8f7b\u63cf\u6de1\u5199\u5730\u8bf4\uff0c\u201c\u5148\u628a\u5979\u53d8\u6210\u4e00\u4ef6\u4e1c\u897f\u5c31\u662f\u3002\u201d</p><p>\u201c\u53d8\u5f62\u4e07\u7269\uff1f\u90a3\u5979\u4e0d\u8fd8\u662f\u6b7b\u4e86\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u628a\u4eba\u53d8\u6210\u4e00\u4ef6\u4e1c\u897f\uff0c\u8fd9\u5df2\u7ecf\u8d85\u51fa\u4e86\u901a\u5e38\u7684\u53d8\u5f62\u672f\u80fd\u529b\u8303\u56f4\uff0c\u9700\u8981\u7528\u5230\u4f20\u8bf4\u4e2d\u7684\u201c\u53d8\u5f62\u4e07\u7269\u201d\uff0c\u8fd9\u662f\u9b54\u6cd5\u5b66\u7684\u6700\u9ad8\u6210\u5c31\u4e4b\u4e00\uff0c\u548c\u201c\u65f6\u95f4\u505c\u6b62\u201d\u9f50\u540d\u7684\u53d8\u5316\u5b66\u6d3e\u7ec8\u6781\u6cd5\u672f\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u5982\u679c\u628a\u838e\u73de\u514b\u53d8\u6210\u4e86\u67d0\u4ef6\u4e1c\u897f\uff0c\u90a3\u5979\u5b9e\u9645\u4e0a\u5c31\u7b49\u4e8e\u662f\u6b7b\u4e86\u2014\u2014\u90a3\u743c\u6069\u5c82\u4e0d\u4e5f\u8ddf\u7740\u5b8c\u86cb\uff1f</p><p>\u201c\u8c01\u8bf4\u8981\u7528\u53d8\u5f62\u4e07\u7269\uff1f\u201d\u5965\u6c83\u53cd\u95ee\uff0c\u201c\u6211\u662f\u8bf4\u7528\u6c38\u6052\u9759\u6ede\u3002\u201d</p><p>\u6c38\u6052\u9759\u6ede\u2026\u2026</p><p>\u201c\u7528\u2018\u6c38\u6052\u9759\u6ede\u2019\uff0c\u8ba9\u5979\u505c\u6b62\u4e00\u5207\u751f\u7406\u673a\u80fd\uff0c\u6c38\u8fdc\u8fdb\u5165\u5047\u6b7b\u72b6\u6001\uff0c\u201d\u5965\u6c83\u89e3\u91ca\uff0c\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u65e2\u6ca1\u6b7b\uff0c\u4f46\u53c8\u76f8\u5f53\u4e8e\u5c31\u662f\u4e00\u4ef6\u4e1c\u897f\uff0c\u4e0d\u4f1a\u518d\u7ed9\u4f60\u6dfb\u4efb\u4f55\u9ebb\u70e6\uff0c\u8fde\u81ea\u6740\u90fd\u4e0d\u884c\uff0c\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u7136\u540e\u5462\uff1f\u201d</p><p>\u201c\u7136\u540e\uff1f\u7136\u540e\u5c31\u968f\u4fbf\u4f60\u4e86\uff0c\u201d\u5965\u6c83\u6643\u6643\u8111\u888b\uff0c\u201c\u7528\u68fa\u6750\u628a\u5979\u88c5\u8d77\u6765\uff0c\u627e\u4e2a\u9690\u853d\u5730\u65b9\u57cb\u7740\uff0c\u4e07\u4e8b\u5927\u5409\u3002\u5982\u679c\u4f60\u8fd8\u4e0d\u653e\u5fc3\uff0c\u6211\u53ef\u4ee5\u5e2e\u5fd9\u7528\u7981\u9522\u672f\u628a\u5979\u968f\u673a\u6254\u5230\u67d0\u4e2a\u534a\u4f4d\u9762\u53bb\uff0c\u518d\u914d\u5408\u4e00\u4e9b\u53cd\u9884\u8a00\u6cd5\u672f\uff0c\u867d\u7136\u4e0d\u80fd\u8bf4\u4e07\u65e0\u4e00\u5931\uff0c\u4f46\u5e94\u8be5\u4e5f\u6ca1\u51e0\u4e2a\u4eba\u80fd\u627e\u5230\u4e86\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u3002</p><p>\u5e73\u5fc3\u800c\u8bba\uff0c\u5965\u6c83\u7684\u8fd9\u4e2a\u65b9\u6cd5\u4e0d\u5dee\uff0c\u5148\u7528\u6c38\u6052\u9759\u6ede\u628a\u838e\u73de\u514b\u53d8\u6210\u7761\u7f8e\u4eba\uff0c\u9664\u975e\u6cd5\u672f\u88ab\u7834\u574f\uff0c\u5426\u5219\u5979\u5c31\u6c38\u8fdc\u6c89\u7720\uff0c\u4e0d\u4f1a\u9192\u6765\u3002\u7136\u540e\u518d\u7528\u7981\u9522\u672f\uff0c\u5c06\u5979\u6d41\u653e\u5230\u4e00\u4e2a\u968f\u673a\u7684\u534a\u4f4d\u9762\u4e2d\uff0c\u522b\u8bf4\u522b\u4eba\uff0c\u8fde\u81ea\u5df1\u90fd\u627e\u4e0d\u56de\u6765\u3002\u5f53\u7136\uff0c\u8fd9\u79cd\u65b9\u6cd5\u4e5f\u5e76\u4e0d\u662f\u8bf4\u5c31\u5b8c\u7f8e\u65e0\u7f3a\uff0c\u65e0\u61c8\u53ef\u51fb\uff0c\u7279\u522b\u5f3a\u5927\u7684\u9884\u8a00\u6cd5\u672f\uff0c\u518d\u914d\u5408\u4e0a\u8db3\u591f\u7684\u8baf\u606f\u8d44\u6599\uff0c\u6bd4\u5982\u8bf4\u838e\u73de\u514b\u7684\u771f\u540d\uff0c\u8fd8\u662f\u6709\u53ef\u80fd\u5bfb\u627e\u5b9a\u4f4d\u5230\u76ee\u6807\u7684\uff0c\u4f46\u5965\u6c83\u4e5f\u53ef\u4ee5\u5e2e\u5fd9\u65bd\u52a0\u5404\u79cd\u963b\u9694\u9884\u8a00\u63a2\u6d4b\u7684\u6cd5\u672f\uff0c\u8fd9\u5c31\u770b\u8c01\u66f4\u6280\u9ad8\u4e00\u7b79\u4e86\u3002\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u98ce\u9669\u662f\u5927\u5927\u964d\u4f4e\uff0c\u603b\u6bd4\u743c\u6069\u73b0\u5728\u8fd9\u6837\uff0c\u6210\u5929\u5e26\u7740\u9b45\u9b54\u5230\u5904\u8dd1\u6765\u5f97\u5f3a\u3002</p><p>\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u838e\u73de\u514b\u5c31\u7b49\u4e8e\u662f\u88ab\u5f7b\u5e95\u727a\u7272\u6389\u4e86\u3002\u5979\u6ca1\u6b7b\uff0c\u4f46\u548c\u6b7b\u65e0\u5f02\uff0c\u6c38\u7720\u4e0d\u9192\uff0c\u56da\u7981\u5728\u865a\u65e0\u7684\u534a\u4f4d\u9762\u4e2d\uff0c\u76f4\u81f3\u65f6\u95f4\u7684\u5c3d\u5934\u2014\u2014\u6216\u8005\u743c\u6069\u6b7b\u53bb\uff0c\u5979\u4e5f\u4f1a\u968f\u4e4b\u6084\u7136\u51cb\u8c22\u3002</p><p>\u201c\u5514\uff0c\u96be\u9053\u4f60\u89c9\u5f97\u8fd9\u529e\u6cd5\u4e0d\u597d\uff1f\u201d\u5965\u6c83\u89c1\u743c\u6069\u72b9\u8c6b\uff0c\u9887\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u6211\u89c9\u5f97\u5f88\u7406\u60f3\u554a\u3002\u201d</p><p>\u201c\u662f\u5f88\u7406\u60f3\uff0c\u53ea\u662f\u2026\u2026\u5bf9\u5979\u672a\u514d\u592a\u6b8b\u5fcd\u4e86\u70b9\u5427\u3002\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u6b8b\u5fcd\u7684\uff0c\u201d\u5965\u6c83\u4e0d\u5c51\uff0c\u201c\u5979\u53cd\u6b63\u662f\u5047\u6b7b\u72b6\u6001\uff0c\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u65e0\u610f\u8bc6\uff0c\u65e0\u601d\u8651\uff0c\u65e0\u70e6\u607c\uff0c\u5feb\u5feb\u4e50\u4e50\uff0c\u591a\u597d\u3002\u201d</p><p>\u5feb\u5feb\u4e50\u4e50\u4f3c\u4e4e\u600e\u4e48\u4e5f\u8c08\u4e0d\u4e0a\u5427\u2026\u2026</p><p>\u5965\u6c83\u6240\u8a00\uff0c\u4e0d\u80fd\u8bf4\u5168\u65e0\u9053\u7406\uff0c\u6ca1\u6709\u610f\u8bc6\uff0c\u6ca1\u6709\u77e5\u89c9\uff0c\u4e5f\u5c31\u65e0\u6240\u8c13\u70e6\u607c\u75db\u82e6\uff0c\u4f46\u743c\u6069\u4f9d\u65e7\u8fd8\u662f\u96be\u4ee5\u63a5\u53d7\u8fd9\u79cd\u65b9\u6848\u3002\u751a\u81f3\u8bf4\u5f97\u53ef\u7b11\u70b9\uff0c\u4ed6\u5b81\u53ef\u628a\u838e\u73de\u514b\u6740\u4e86\uff08\u5982\u679c\u4e0d\u4f1a\u5f71\u54cd\u81ea\u5df1\u7684\u8bdd\uff09\uff0c\u4e5f\u4e0d\u559c\u6b22\u8ba9\u5979\u8fd9\u6837\u6c38\u8fdc\u6c89\u7761\u3002\u8fd9\u5927\u7ea6\u662f\u4e00\u79cd\u4f2a\u5584\uff0c\u66f4\u5e94\u8be5\u662f\u4e00\u79cd\u611a\u8822\uff0c\u4f46\u786e\u5b9e\u662f\u4ed6\u6b64\u523b\u771f\u5b9e\u7684\u60f3\u6cd5\u3002</p><p>\u201c\u8001\u5e08\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u522b\u7684\u65b9\u6cd5\u5417\uff1f\u201d\u743c\u6069\u4f4e\u58f0\u95ee\u3002</p></article>"}}}]);
