(window.webpackJsonp=window.webpackJsonp||[]).push([[1617],{1640:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u516d\u8282 \u56de\u5bb6",content:"<article><h2>\u7b2c\u516d\u5341\u516d\u8282 \u56de\u5bb6</h2><p>\u5728\u5965\u6c83\u7684\u5893\u7a74\u91cc\u4f4f\u4e86\u4e00\u5929\uff0c\u7b2c\u4e8c\u65e5\u743c\u6069\u544a\u8f9e\u52a8\u8eab\uff0c\u524d\u5f80\u55a7\u6c34\u57ce\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u6c47\u5408\uff0c\u542f\u7a0b\u524d\u5f80\u9634\u9b42\u57ce\u3002\u81f3\u4e8e\u838e\u73de\u514b\uff0c\u5374\u6682\u65f6\u7559\u5728\u4e86\u5965\u6c83\u90a3\u91cc\uff0c\u6ca1\u6709\u968f\u884c\u3002</p><p>\u5b9d\u77f3\u754c\u57df\u8fd9\u79cd\u5e9e\u5927\u9b54\u6cd5\uff0c\u4e0d\u662f\u7b80\u7b80\u5355\u5355\u5c31\u80fd\u7acb\u523b\u65bd\u5c55\u7684\uff0c\u9700\u8981\u5f88\u957f\u65f6\u95f4\u7684\u51c6\u5907\u5de5\u4f5c\uff0c\u641c\u96c6\u5404\u79cd\u6750\u6599\uff0c\u6784\u5efa\u63cf\u7ed8\u9b54\u6cd5\u9635\uff0c\u4ee5\u53ca\u5929\u65f6\u5730\u7406\u7684\u914d\u5408\u3002\u636e\u5965\u6c83\u4f30\u8ba1\uff0c\u6ca1\u6709\u4e03\u516b\u5929\u529f\u592b\u662f\u6ca1\u6cd5\u5b8c\u6210\u7684\uff0c\u743c\u6069\u7b49\u4e0d\u4e86\u8fd9\u4e48\u4e45\uff0c\u8d76\u7740\u56de\u57ce\u53bb\u89c1\u73ca\u5609\uff0c\u4fbf\u5c06\u838e\u73de\u514b\u7559\u4e86\u4e0b\u6765\u3002</p><p>\u4ece\u5e7d\u6697\u5730\u57df\u521a\u521a\u8fd4\u56de\u5730\u8868\u7684\u65f6\u5019\uff0c\u743c\u6069\u8eab\u8fb9\u6709\u4e94\u4e2a\u5973\u5b69\u5b50\uff0c\u8def\u4e0a\u8fd8\u53c8\u9047\u5230\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u8389\u6cd5\u5c14\uff0c\u7b80\u76f4\u90fd\u53ef\u4ee5\u5efa\u7acb\u540e\u5bab\u4e86\u3002\u7136\u800c\u9646\u9646\u7eed\u7eed\u5730\uff0c\u6885\u83f2\u65af\u548c\u51db\u524d\u5f80\u963f\u683c\u62c9\u9686\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7559\u5728\u963f\u65af\u5361\u7279\u62c9\uff0c\u8389\u6cd5\u5c14\u53c8\u6ca1\u6709\u8bf1\u62d0\u5230\u624b\uff0c\u5982\u4eca\u838e\u73de\u514b\u518d\u79bb\u5f00\uff0c\u8eab\u8fb9\u5c31\u53ea\u5269\u4e0b\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u82b1\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u672a\u5c1d\u4e0d\u662f\u4e00\u4ef6\u597d\u4e8b\u3002</p><p>\u73ca\u5609\u662f\u6e29\u67d4\u7684\u59d0\u59d0\uff0c\u5ba0\u6eba\u7740\u5f1f\u5f1f\uff0c\u4f46\u5979\u540c\u65f6\u4e5f\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u662f\u5973\u5b69\u5b50\uff0c\u5c31\u4f1a\u5ac9\u5992\u7684\u3002\u743c\u6069\u4e0a\u6b21\u56de\u9634\u9b42\u57ce\uff0c\u8eab\u8fb9\u53ea\u5e26\u7740\u4e00\u4e2a\u6885\u83f2\u65af\uff0c\u4fbf\u5df2\u7ecf\u8ba9\u73ca\u5609\u5fc3\u4e2d\u9887\u4e3a\u4e0d\u5feb\u3002\u5982\u679c\u8fd9\u6b21\u53d8\u672c\u52a0\u5389\uff0c\u628a\u6885\u83f2\u65af\u3001\u51db\u3001\u838e\u73de\u514b\u7b49\u4eba\u90fd\u5e26\u56de\u53bb\u7684\u8bdd\uff0c\u90a3\u540e\u679c\u2026\u2026\u80af\u5b9a\u5f88\u4e25\u91cd\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u81ea\u5df1\u7684\u8fd0\u6c14\u8fd8\u771f\u662f\u4e0d\u9519\uff0c\u65e0\u610f\u4e4b\u95f4\u5c31\u8eb2\u907f\u6389\u4e86\u4e00\u4e2a\u5927\u9ebb\u70e6\u3002</p><p>\u4e0d\u8fc7\u60f3\u5230\u6b64\u5904\uff0c\u743c\u6069\u5012\u662f\u53d1\u73b0\u6709\u4ef6\u4e8b\u60c5\u5f88\u5947\u602a\u3002\u73ca\u5609\u4f1a\u5ac9\u5992\u6885\u83f2\u65af\uff0c\u4f1a\u5403\u5979\u7684\u918b\uff0c\u4f1a\u56e0\u6b64\u4e0d\u9ad8\u5174\uff0c\u4f46\u5979\u5374\u4f3c\u4e4e\u5e76\u4e0d\u5982\u4f55\u5ac9\u5992\u8299\u857e\u72c4\uff0c\u867d\u7136\u4e5f\u8bf4\u8d77\u8fc7\uff0c\u62b1\u6028\u8fc7\uff0c\u4f46\u660e\u663e\u6ca1\u6709\u5f53\u771f\u653e\u5728\u5fc3\u4e0a\u3002\u4ece\u5979\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u7684\u5173\u7cfb\u6765\u770b\u5c31\u77e5\u9053\uff0c\u73ca\u5609\u4f1a\u548c\u8299\u8389\u5a05\u53bb\u795e\u6bbf\u53c2\u52a0\u796d\u7940\uff0c\u4f1a\u5411\u8299\u857e\u72c4\u5b66\u4e60\u9b54\u6cd5\uff0c\u5173\u7cfb\u5f88\u878d\u6d3d\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u6b63\u5e38\u7684\uff0c\u56e0\u4e3a\u73ca\u5609\u77e5\u9053\u8299\u857e\u72c4\u548c\u743c\u6069\u7684\u5173\u7cfb\u65f6\uff0c\u4ed6\u4eec\u5df2\u7ecf\u7b97\u662f\u5206\u624b\u4e86\u3002\u5bf9\u4e8e\u73ca\u5609\u800c\u8a00\uff0c\u8299\u857e\u72c4\u662f\u5f1f\u5f1f\u7684\u201c\u524d\u4efb\u201d\u5973\u53cb\uff0c\u662f\u8fc7\u53bb\u5f0f\uff0c\u4e0d\u59a8\u793a\u4ee5\u5927\u5ea6\uff0c\u65e0\u9700\u8ba1\u8f83\uff0c\u800c\u6885\u83f2\u65af\u5219\u662f\u201c\u73b0\u4efb\u201d\u7684\u60c5\u4eba\uff0c\u81ea\u7136\u5c31\u662f\u7ade\u4e89\u5bf9\u624b\u4e86\u3002</p><p>\u7136\u800c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u9ebb\u70e6\u4fbf\u53c8\u6765\u4e86\uff0c\u743c\u6069\u4e0a\u6b21\u79bb\u5f00\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u4ed6\u548c\u8299\u857e\u72c4\u8fd8\u662f\u5904\u4e8e\u5206\u624b\u72b6\u6001\uff0c\u73ca\u5609\u53ef\u4ee5\u4e0d\u8ba1\u8f83\uff0c\u5982\u4eca\u65e7\u60c5\u91cd\u71c3\uff0c\u518d\u5ea6\u590d\u5408\uff0c\u8fd9\u5374\u8981\u5982\u4f55\u5411\u59d0\u59d0\u4ea4\u4ee3\u3002\u4ec5\u4ec5\u5982\u6b64\uff0c\u6216\u8bb8\u8fd8\u7f62\u4e86\uff0c\u66f4\u8981\u547d\u7684\u662f\u81ea\u5df1\u4e0d\u4f46\u91cd\u65b0\u63a8\u5012\u4e86\u8299\u857e\u72c4\uff0c\u8fd8\u8fde\u5979\u59d0\u59d0\u8299\u8389\u5a05\u4e5f\u4e00\u5e76\u5f04\u4e0a\u5e8a\uff0c\u8fd9\u4e9b\u5929\u73a9\u59d0\u59b9\u53cc\u98de\u73a9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u5168\u7136\u90fd\u6ca1\u60f3\u5230\u4e25\u91cd\u540e\u679c\u3002</p><p>\u552f\u4e00\u503c\u5f97\u5e86\u5e78\u7684\uff0c\u662f\u83ab\u5c3c\u5361\u59d0\u59b9\u81ea\u5df1\u5c31\u662f\u9634\u9b42\u57ce\u7684\u5c45\u6c11\uff0c\u5979\u4eec\u53ef\u4ee5\u76f4\u63a5\u56de\u5bb6\uff0c\u4e0d\u9700\u8981\u4f4f\u5728\u743c\u6069\u5bb6\u91cc\uff0c\u907f\u514d\u4e86\u548c\u59d0\u59d0\u76f4\u63a5\u78b0\u9762\u3002\u4f46\u8fd9\u4e5f\u53ea\u80fd\u8eb2\u8fc7\u4e00\u65f6\u800c\u5df2\uff0c\u83ab\u5c3c\u5361\u5bb6\u548c\u5170\u5c3c\u65af\u7279\u5bb6\u662f\u90bb\u5c45\uff0c\u73ca\u5609\u548c\u8fd9\u5bf9\u59d0\u59b9\u4fe9\u5173\u7cfb\u53c8\u751a\u4f73\uff0c\u53ea\u8981\u4e00\u89c1\u9762\uff0c\u4e00\u804a\u5929\uff0c\u5f88\u5bb9\u6613\u5c31\u4f1a\u9732\u9985\u2026\u2026</p><p>\u7b97\u4e86\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u540e\u6094\u4e5f\u65e0\u7528\u2014\u2014\u8bdd\u8bf4\u56de\u6765\uff0c\u81ea\u5df1\u5f53\u771f\u540e\u6094\u4e48\uff1f\u5982\u679c\u65f6\u5149\u80fd\u591f\u5012\u6d41\uff0c\u5982\u679c\u81ea\u5df1\u80fd\u591f\u91cd\u65b0\u9009\u62e9\uff0c\u81ea\u5df1\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u65f6\u5019\u4f1a\u62d2\u7edd\u8299\u857e\u72c4\uff1f\u5728\u963f\u65af\u5361\u7279\u62c9\u4f1a\u653e\u8fc7\u8299\u8389\u5a05\uff1f\u7b54\u6848\u663e\u7136\u90fd\u662f\u5426\u5b9a\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u3002</p><p>\u4ece\u7070\u5cf0\u5c71\u8109\u7684\u5ce1\u8c37\u4e2d\u901a\u8fc7\uff0c\u7a7f\u8d8a\u5b64\u5bc2\u8352\u539f\uff0c\u4e09\u4eba\u8fdb\u5165\u5927\u6c99\u6f20\uff0c\u8e0f\u4e0a\u9ed1\u8def\u3002</p><p>\u201c\u4e0a\u4e00\u6b21\u2014\u2014\u5c31\u662f\u521a\u6bd5\u4e1a\u90a3\u6b21\u2014\u2014\u6211\u88ab\u6d3e\u53bb\u535a\u5f97\u4e4b\u95e8\u7ed9\u62c9\u6c83\u514b\u5148\u751f\u9001\u4fe1\uff0c\u5f53\u65f6\u4e5f\u66fe\u7ecf\u60f3\u8d70\u8fd9\u6761\u9ed1\u8def\uff0c\u201d\u743c\u6069\u9a91\u5728\u9a86\u9a7c\u4e0a\u5bf9\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u7ed3\u679c\u6563\u5854\u6797\u4f1a\u4e0d\u77e5\u9053\u53d1\u4ec0\u4e48\u795e\u7ecf\uff0c\u628a\u8def\u7ed9\u5c01\u4e86\uff0c\u903c\u5f97\u6211\u4e0d\u5f97\u4e0d\u548c\u8d1d\u6234\u86ee\u65cf\u5408\u4f5c\uff0c\u5728\u6c99\u6f20\u91cc\u7ed5\u4e86\u4e2a\u5927\u5f2f\uff0c\u8fd8\u649e\u4e0a\u4e86\u4eba\u9762\u72ee\u548c\u683c\u62c9\u5179\u7279\uff0c\u5dee\u70b9\u6b7b\u5728\u57c3\u65af\u8003\u57ce\u3002\u201d</p><p>\u201c\u4f46\u4f60\u597d\u50cf\u2026\u2026\u5e76\u6ca1\u6709\u663e\u5f97\u4e0d\u9ad8\u5174\u554a\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u6ca1\u6709\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002\u4ed6\u786e\u5b9e\u6ca1\u6709\u4e0d\u9ad8\u5174\uff0c\u751a\u81f3\u53cd\u800c\u5f88\u5e86\u5e78\u3002\u6b63\u662f\u56e0\u4e3a\u6563\u5854\u6797\u4f1a\u7684\u90a3\u6b21\u7a81\u7136\u53d1\u75af\uff0c\u4ed6\u624d\u4f1a\u9047\u4e0a\u6885\u83f2\u65af\uff0c\u4e24\u4eba\u624d\u4f1a\u76f8\u8bc6\u3001\u4e00\u8def\u540c\u884c\uff1b\u4e5f\u6b63\u662f\u56e0\u4e3a\u4eba\u9762\u72ee\u548c\u683c\u62c9\u5179\u7279\uff0c\u4ed6\u624d\u80fd\u628a\u63e1\u673a\u4f1a\u5f97\u5230\u4e86\u8fd9\u4f4d\u5723\u6b66\u58eb\uff0c\u867d\u7136\u624b\u6bb5\u5f88\u4e0d\u5149\u5f69\uff0c\u4f46\u82e5\u662f\u6ca1\u6709\u90a3\u6b21\u534a\u5f3a\u8feb\u7684\u5f00\u59cb\uff0c\u90a3\u4e5f\u5c31\u6ca1\u6709\u4eca\u65e5\u4e86\u3002</p><p>\u7ed5\u70b9\u8fdc\u8def\uff0c\u6253\u51e0\u573a\u67b6\uff0c\u4e00\u70b9\u5c0f\u5c0f\u7684\u98ce\u9669\u2026\u2026\u597d\u5427\uff0c\u786e\u5b9e\u662f\u8d4c\u4e0a\u6027\u547d\u7684\u98ce\u9669\uff0c\u4f46\u73b0\u5728\u4e0d\u4e5f\u6210\u529f\u5ea6\u8fc7\u4e86\uff1f\u6240\u6709\u7684\u8fd9\u4e9b\u201c\u4ed8\u51fa\u201d\uff0c\u6bd4\u8d77\u6885\u83f2\u65af\u8fd9\u4e2a\u201c\u6536\u83b7\u201d\u6765\uff0c\u90a3\u5c31\u534a\u70b9\u4e0d\u503c\u4e00\u63d0\u3002</p><p>\u6240\u4ee5\u743c\u6069\u5f53\u7136\u5f88\u9ad8\u5174\u3002</p><p>\u4fdd\u6301\u7740\u8fd9\u79cd\u6109\u5feb\u5fc3\u60c5\uff0c\u539f\u672c\u96be\u71ac\u7684\u6c99\u6f20\u4e4b\u65c5\u4e5f\u53d8\u5f97\u8f7b\u677e\u8d77\u6765\u3002\u6563\u5854\u6797\u4f1a\u5728\u6cbf\u9014\u90fd\u8bbe\u4e86\u636e\u70b9\uff0c\u7ecf\u8425\u591a\u5e74\uff0c\u4e00\u5757\u4e00\u5757\u7684\u7eff\u6d32\uff0c\u5c31\u76f8\u5f53\u4e8e\u4e00\u5ea7\u4e00\u5ea7\u5c0f\u9547\uff0c\u5b83\u4eec\u4ee5\u5730\u4e0b\u9ed1\u5e02\u8457\u79f0\uff0c\u6765\u5f80\u5546\u65c5\u5e38\u5e38\u4e5f\u4f1a\u5c31\u5730\u8d38\u6613\uff0c\u9c7c\u9f99\u6df7\u6742\uff0c\u6709\u5fc3\u4eba\u5e38\u5e38\u80fd\u4ece\u4e2d\u53d1\u73b0\u5b9d\u8d1d\u2014\u2014\u6563\u5854\u6797\u4f1a\u8fd8\u4f1a\u5b9a\u671f\u53d1\u5e03\u201c\u85cf\u5b9d\u56fe\u201d\u4e4b\u7c7b\u7684\u8baf\u606f\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u6216\u8bb8\u8fd8\u4f1a\u6709\u5174\u81f4\u53bb\u6dd8\u6dd8\u91d1\uff0c\u770b\u4f1a\u4e0d\u4f1a\u53d1\u73b0\u4ec0\u4e48\uff0c\u4f46\u5982\u4eca\u6709\u4e00\u5806\u6b63\u4e8b\u5728\u8eab\uff0c\u4fbf\u6ca1\u8fd9\u4e2a\u5174\u81f4\u3002</p><p>\u7ed9\u59d0\u59d0\u73ca\u5609\u51c6\u5907\u7684\u9b54\u6cd5\u9879\u94fe\uff0c\u6240\u6709\u5b9d\u77f3\u548c\u73cd\u73e0\u7684\u9644\u9b54\u5de5\u4f5c\u5df2\u7ecf\u57fa\u672c\u5b8c\u6210\uff0c\u8fd8\u5dee\u6700\u540e\u7684\u68c0\u67e5\u5b8c\u5584\u3002\u4ece\u70db\u5821\u6284\u5f55\u6765\u7684\u90a3\u4e00\u5927\u5806\u4f0a\u739b\u65af\u5361\u7684\u8d44\u6599\uff0c\u9700\u8981\u4ed4\u7ec6\u9605\u8bfb\u3001\u5206\u6790\uff0c\u6392\u9664\u6389\u90a3\u4e9b\u660e\u663e\u8352\u8bde\u4e0d\u7ecf\u7684\u5938\u5f20\u4f20\u95fb\u3001\u81ea\u76f8\u51b2\u7a81\u77db\u76fe\u7684\u6df7\u4e71\u8bb0\u5f55\uff0c\u4ece\u4e2d\u68b3\u7406\u5f52\u7eb3\u51fa\u771f\u6b63\u6709\u7528\u7684\u4fe1\u606f\u6765\uff0c\u8fd9\u5de5\u4f5c\u5f88\u7e41\u7410\uff0c\u56e0\u4e3a\u53ef\u80fd\u4f1a\u7275\u6d89\u91cd\u5927\uff0c\u743c\u6069\u53c8\u4e0d\u60f3\u5047\u624b\u4e8e\u4eba\uff0c\u53ea\u80fd\u4eb2\u5386\u4eb2\u4e3a\uff0c\u4e8e\u662f\u4fbf\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\u3002</p><p>\u5728\u89c1\u5965\u6c83\u7684\u65f6\u5019\uff0c\u743c\u6069\u4e5f\u8be2\u95ee\u8fc7\u4ed6\u6709\u5173\u4f0a\u739b\u65af\u5361\u7684\u4e8b\u60c5\uff0c\u7136\u800c\u8001\u5deb\u5996\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u4fe1\u606f\u6765\u3002\u4f0a\u739b\u65af\u5361\u5efa\u56fd\u7684\u65f6\u95f4\u4e0d\u8be6\uff0c\u5927\u7ea6\u662f\u8ddd\u4eca\u4e5d\u5343\u591a\u5e74\u524d\uff0c\u8986\u706d\u4e8e-2488DR\uff0c\u8ddd\u4eca\u8d85\u8fc7\u4e09\u5343\u516b\u767e\u591a\u5e74\uff0c\u800c\u8010\u745f\u745e\u5c14\u5efa\u56fd\u4e8e-3859DR\uff0c\u8ddd\u4eca\u4e94\u5343\u4f59\u5e74\uff0c\u800c\u4e14\u4e00\u5f00\u59cb\u4e5f\u53ea\u4e0d\u8fc7\u662f\u51e0\u4e2a\u5c0f\u9547\u7684\u8054\u76df\uff0c\u771f\u6b63\u6210\u4e3a\u5927\u5965\u672f\u5e08\u7edf\u6cbb\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u90a3\u662f\u51e0\u767e\u5e74\u540e\u7684\u4e8b\u60c5\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5dee\u4e0d\u591a\u662f\u4f0a\u739b\u65af\u5361\u5373\u5c06\u8986\u706d\u7684\u665a\u671f\uff0c\u8010\u745f\u745e\u5c14\u624d\u521a\u521a\u5174\u8d77\uff0c\u4e24\u5927\u9b54\u6cd5\u5e1d\u56fd\u4e4b\u95f4\u7684\u91cd\u5408\u671f\u4e0d\u8fc7\u51e0\u767e\u5e74\uff0c\u800c\u4e14\u4e00\u4e2a\u5728\u8d39\u4f26\u5927\u9646\u7684\u6700\u4e1c\u8fb9\uff0c\u518d\u8fc7\u53bb\u5c31\u662f\u65e0\u5c3d\u8352\u91ce\u548c\u4e1c\u65b9\u5927\u9646\uff0c\u53e6\u5916\u4e00\u4e2a\u5728\u8d39\u4f26\u5927\u9646\u7684\u897f\u5317\u90e8\uff0c\u76f8\u9694\u4e07\u91cc\u4e4b\u9065\uff0c\u5f7c\u6b64\u95f4\u7684\u4ea4\u6d41\u81ea\u7136\u662f\u6709\uff0c\u4f46\u4e5f\u7b97\u4e0d\u4e0a\u591a\u4e48\u5bc6\u5207\u3002\u5965\u6c83\u51fa\u751f\u4e8e\u8010\u745f\u745e\u5c14\u7684\u201c\u9ec4\u91d1\u5e74\u4ee3\u201d\uff0c\u6210\u540d\u4e8e\u201c\u53d1\u73b0\u5e74\u4ee3\u201d\uff0c\u5df2\u7ecf\u7b97\u662f\u5e1d\u56fd\u665a\u671f\u4e86\uff0c\u6240\u4ee5\u4ed6\u5bf9\u4f0a\u739b\u65af\u5361\u7684\u4e86\u89e3\u4e5f\u5e76\u4e0d\u591a\u3002\u800c\u4e14\u4f0a\u739b\u65af\u5361\u4ee5\u7a7a\u95f4\u9b54\u6cd5\u548c\u70bc\u91d1\u9020\u7269\u8457\u79f0\uff0c\u5965\u6c83\u7cbe\u7814\u7684\u5374\u662f\u4ea1\u7075\u672f\uff0c\u5927\u5bb6\u7684\u4e13\u4e1a\u65b9\u5411\u534a\u70b9\u4e0d\u5bf9\u8def\uff0c\u81ea\u7136\u4e5f\u5c31\u61d2\u5f97\u5173\u5fc3\u3002</p><p>\u4e0d\u8fc7\u8001\u5deb\u5996\u5411\u6765\u81ea\u8be9\u662f\u201c\u7b2c\u4e00\u6d41\u7684\u5b9d\u7269\u6536\u85cf\u5bb6\u201d\uff0c\u5bf9\u4e8e\u5404\u79cd\u73cd\u8d35\u8457\u540d\u7684\u9b54\u6cd5\u7269\u54c1\u90fd\u611f\u5174\u8da3\uff0c\u5bf9\u4e8e\u4f0a\u739b\u65af\u5361\u4e03\u79d8\u5668\u8fd9\u79cd\u81f3\u5b9d\uff0c\u4ed6\u4e5f\u77e5\u9053\u70b9\u76f8\u5173\u4fe1\u606f\u3002</p><p>\u201c\u4f0a\u739b\u65af\u5361\u7684\u4e8b\u60c5\u6211\u4e0d\u592a\u4e86\u89e3\uff0c\u4e5f\u6ca1\u600e\u4e48\u5173\u5fc3\u8fc7\uff0c\u4e0d\u8fc7\u4e03\u79d8\u5668\u4e48\uff0c\u5012\u662f\u6709\u6240\u8033\u95fb\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u4ed6\u4eec\u7684\u7687\u5ba4\u597d\u50cf\u662f\u5206\u4e24\u5927\u6d3e\uff0c\u5176\u4e2d\u4e00\u6d3e\u6301\u6709\u7b2c\u4e00\u3001\u7b2c\u4e09\u548c\u7b2c\u4e94\u79d8\u5668\uff0c\u53e6\u5916\u4e00\u6d3e\u6301\u6709\u7b2c\u4e8c\u3001\u7b2c\u56db\u548c\u7b2c\u516d\u79d8\u5668\uff0c\u6700\u91cd\u8981\u7684\u7b2c\u4e03\u79d8\u5668\u5219\u662f\u5171\u540c\u638c\u63e1\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e03\u79d8\u5668\u5206\u522b\u662f\u4ec0\u4e48\u5462\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u5965\u6c83\u6447\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u4f0a\u739b\u65af\u5361\u628a\u8fd9\u4e03\u79d8\u5668\u770b\u5f97\u6781\u91cd\uff0c\u5177\u4f53\u7684\u8d44\u6599\u522b\u8bf4\u6211\u4eec\u5916\u4eba\uff0c\u5c31\u8fde\u4ed6\u4eec\u672c\u56fd\u4eba\u90fd\u672a\u5fc5\u4e86\u89e3\u3002\u5c31\u6211\u770b\u8fc7\u7684\u4e00\u4e9b\u8d44\u6599\uff0c\u7b2c\u4e00\u3001\u7b2c\u4e8c\u79d8\u5668\uff0c\u4f3c\u4e4e\u90fd\u662f\u67d0\u79cd\u2018\u51ed\u8bc1\u2019\u6216\u8005\u8bf4\u2018\u4fe1\u7269\u2019\uff0c\u80fd\u591f\u53ec\u5524\u5f3a\u5927\u7684\u5f02\u754c\u751f\u7269\uff1b\u7b2c\u4e09\u79d8\u5668\u636e\u8bf4\u662f\u4e00\u67c4\u5251\uff0c\u7b2c\u56db\u79d8\u5668\u636e\u8bf4\u662f\u4e00\u672c\u4e66\uff0c\u4f5c\u7528\u4e0d\u660e\uff1b\u7b2c\u4e94\u3001\u7b2c\u516d\u79d8\u5668\u5f88\u5c11\u9732\u9762\uff0c\u5c31\u66f4\u4e0d\u77e5\u9053\u4e86\u3002\u201d</p><p>\u201c\u90a3\u7b2c\u4e03\u79d8\u5668\u5462\uff1f\u201d</p><p>\u201c\u7b2c\u4e03\u79d8\u5668\u6700\u795e\u79d8\uff0c\u636e\u8bf4\u81ea\u94f8\u6210\u4ee5\u6765\uff0c\u4ece\u4e0d\u5728\u516c\u5f00\u573a\u5408\u51fa\u73b0\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u88ab\u4f7f\u7528\u7684\u8bb0\u5f55\uff0c\u6240\u4ee5\u5b8c\u5168\u65e0\u6cd5\u63a8\u65ad\u2014\u2014\u4e0d\u8fc7\u6211\u5012\u662f\u542c\u8fc7\u4e00\u79cd\u5f88\u6709\u8da3\u7684\u8bf4\u6cd5\uff1a\u7b2c\u4e03\u79d8\u5668\u5176\u5b9e\u4e0d\u662f\u67d0\u4e2a\u7269\u4f53\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d\u743c\u6069\u6ca1\u542c\u660e\u767d\u3002</p><p>\u201c\u7b2c\u4e03\u79d8\u5668\u4e0d\u662f\u67d0\u4e2a\u7269\u4f53\uff0c\u5b83\u5176\u5b9e\u662f\u4e00\u4e2a\u9b54\u6cd5\u9635\uff0c\u201d\u5965\u6c83\u89e3\u91ca\uff0c\u201c\u5f53\u7136\u8fd9\u4e5f\u53ea\u662f\u4e2a\u8bf4\u6cd5\uff0c\u6216\u8005\u8bf4\u8c23\u4f20\uff0c\u6ca1\u6709\u4efb\u4f55\u771f\u51ed\u5b9e\u636e\uff0c\u542c\u8fc7\u5c31\u7b97\u4e86\u2026\u2026\u8bdd\u8bf4\u4f60\u600e\u4e48\u5bf9\u4f0a\u739b\u65af\u5361\u8fd9\u4e48\u611f\u5174\u8da3\uff1f\u201d</p><p>\u743c\u6069\u6ca1\u8bf4\u5b9e\u8bdd\uff0c\u627e\u4e86\u4e2a\u7406\u7531\u906e\u63a9\u8fc7\u53bb\u3002\u4ed6\u662f\u70bc\u91d1\u5e08\uff0c\u4f0a\u739b\u65af\u5361\u53c8\u4ee5\u70bc\u91d1\u672f\u8457\u79f0\uff0c\u611f\u5174\u8da3\u5f88\u6b63\u5e38\uff0c\u5965\u6c83\u4e5f\u6ca1\u6709\u592a\u5728\u610f\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u8001\u5e08\uff0c\u201d\u743c\u6069\u6700\u540e\u63d0\u51fa\u4e00\u4e2a\u95ee\u9898\uff0c\u201c\u65e2\u7136\u6211\u4eec\u548c\u4f0a\u739b\u65af\u5361\u4e5f\u662f\u6709\u6765\u5f80\uff0c\u90a3\u4e48\u4ed6\u4eec\u706d\u56fd\u7684\u65f6\u5019\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u5947\u68b0\u5e08\u9003\u5230\u8010\u745f\u6765\u5417\uff1f\u201d</p><p>\u8fd9\u662f\u4e2a\u5f88\u5408\u7406\u7684\u8003\u8651\uff0c\u5965\u6c83\u76b1\u7709\u56de\u5fc6\u8d77\u6765\uff0c\u201c\u597d\u50cf\u662f\u6709\u51e0\u4e2a\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u4e5f\u90fd\u4e0d\u662f\u4ec0\u4e48\u5389\u5bb3\u4eba\u7269\uff0c\u4e09\u6d41\u89d2\u8272\u800c\u5df2\u3002\u4f0a\u739b\u65af\u5361\u771f\u6b63\u7684\u7cbe\u82f1\u5168\u5728\u7687\u5ba4\uff0c\u800c\u7687\u5ba4\u90fd\u6218\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u5168\u519b\u8986\u706d\uff0c\u4e00\u4e2a\u5e78\u5b58\u8005\u90fd\u6ca1\u6709\uff1f\u201d\u743c\u6069\u603b\u89c9\u5f97\u8fd9\u6709\u70b9\u96be\u4ee5\u7f6e\u4fe1\u3002</p><p>\u201c\u5e94\u8be5\u662f\u5427\uff0c\u201d\u5965\u6c83\u4e5f\u4e0d\u592a\u80af\u5b9a\uff0c\u201c\u53cd\u6b63\u4ece\u6b64\u5c31\u518d\u4e5f\u6ca1\u6709\u542c\u8bf4\u8fc7\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u5947\u68b0\u5e08\u7684\u6d88\u606f\u2026\u2026\u5514\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u5012\u662f\u60f3\u8d77\u6765\uff0c\u6709\u4e2a\u5bb6\u4f19\u5176\u5b9e\u633a\u50cf\u7684\uff0c\u4f46\u65f6\u95f4\u5bf9\u4e0d\u4e0a\u53f7\u3002\u201d</p><p>\u201c\u8c01\uff1f\u201d</p><p>\u201c\u6b27\u8d1d\u4f26\u3002\u201d</p><p>\u201c\u65c5\u8005\u201d\u6b27\u8d1d\u4f26\uff0c\u8010\u745f\u745e\u5c14\u5386\u53f2\u4e0a\u6700\u6770\u51fa\u7684\u5927\u5965\u672f\u5e08\u4e4b\u4e00\uff0c\u4ee5\u5f02\u754c\u548c\u7a7a\u95f4\u9b54\u6cd5\u8457\u79f0\uff0c\u4e43\u662f\u201c\u53d1\u73b0\u5e74\u4ee3\u201d\u7684\u5f00\u521b\u8005\uff0c\u8981\u8bba\u5730\u4f4d\u548c\u58f0\u671b\uff0c\u6bd4\u773c\u524d\u7684\u5965\u6c83\u8fd8\u8981\u80dc\u4e0a\u4e00\u7b79\u3002\u8fd9\u79cd\u9f0e\u9f0e\u5927\u540d\u7684\u4eba\u7269\uff0c\u743c\u6069\u5f53\u7136\u662f\u77e5\u9053\u7684\u3002</p><p>\u201c\u5e1d\u56fd\u5386\u53f2\u4e0a\uff0c\u5bf9\u5f02\u754c\u3001\u7a7a\u95f4\u8fd9\u65b9\u9762\u7684\u7814\u7a76\u4e00\u76f4\u5f88\u4e0d\u53d1\u8fbe\uff0c\u5927\u5bb6\u4e5f\u90fd\u4e0d\u91cd\u89c6\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u6b27\u8d1d\u4f26\u5374\u662f\u6a2a\u7a7a\u51fa\u4e16\uff0c\u4e00\u624b\u5f00\u521b\u4e86\u53d1\u73b0\u5e74\u4ee3\uff0c\u5982\u679c\u8981\u8bf4\u7684\u8bdd\uff0c\u5012\u662f\u548c\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\u6709\u70b9\u76f8\u4f3c\uff0c\u90fd\u64c5\u957f\u7a7a\u95f4\u9b54\u6cd5\u2014\u2014\u4f46\u65f6\u95f4\u5c31\u5b8c\u5168\u5bf9\u4e0d\u4e0a\uff0c\u6b27\u8d1d\u4f26\u662f\u548c\u6211\u540c\u4e00\u65f6\u4ee3\u7684\u4eba\u7269\uff0c\u4f0a\u739b\u65af\u5361\u90a3\u65f6\u5019\u65e9\u5c31\u5df2\u7ecf\u5b8c\u86cb\u4e24\u5343\u5e74\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d\u743c\u6069\u6709\u4e9b\u5931\u671b\u3002</p><p>\u4ece\u5965\u6c83\u53e3\u4e2d\uff0c\u743c\u6069\u7b97\u662f\u4e86\u89e3\u5230\u4e00\u4e9b\u65b0\u4e1c\u897f\uff0c\u4f46\u8981\u8bf4\u771f\u6b63\u6709\u4ef7\u503c\u7684\uff0c\u5374\u4e5f\u5be5\u5be5\u65e0\u51e0\uff0c\u4ece\u4e2d\u5206\u6790\u63a8\u65ad\u4e0d\u51fa\u4ec0\u4e48\uff0c\u53ea\u5f97\u7f62\u4e86\uff0c\u4ee5\u540e\u518d\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u56e0\u4e3a\u4ea4\u4e86\u4e70\u8def\u8d39\uff0c\u4fbf\u6709\u6563\u5854\u6797\u4f1a\u7684\u519b\u961f\u62a4\u9001\uff0c\u4e00\u8def\u4e0a\u5012\u662f\u5e73\u5b89\u65e0\u4e8b\u3002\u743c\u6069\u7b49\u4eba\u5728\u534a\u9014\u4e0b\u4e86\u9ed1\u8def\uff0c\u8f6c\u5411\u5357\u884c\uff0c\u53c8\u8fc7\u4e86\u4e00\u5929\u65f6\u95f4\uff0c\u4fbf\u770b\u89c1\u4e86\u98d8\u6d6e\u5728\u9ad8\u7a7a\u4e2d\u7684\u9634\u9b42\u57ce\u3002</p><p>\u548c\u8bb0\u5fc6\u4e2d\u4e0d\u540c\uff0c\u5982\u4eca\u7684\u9634\u9b42\u57ce\uff0c\u663e\u7136\u5df2\u7ecf\u5c06\u52bf\u529b\u9010\u6b65\u6269\u5f20\u5230\u5730\u9762\uff0c\u4ee5\u5e7d\u5f71\u6d77\u4e3a\u4e2d\u5fc3\uff0c\u519c\u573a\u3001\u623f\u820d\u548c\u519b\u8425\u5df2\u7ecf\u6b21\u7b2c\u5efa\u8d77\uff0c\u751a\u81f3\u8fd8\u6709\u6b63\u5728\u7b51\u9020\u7684\u4e24\u5ea7\u57ce\u5821\uff0c\u7b97\u662f\u7c97\u5177\u89c4\u6a21\uff0c\u518d\u4e0d\u662f\u6614\u65e5\u4e00\u5ea7\u7a7a\u4e2d\u5b64\u57ce\u4e86\u3002\u743c\u6069\u867d\u7136\u5bf9\u9634\u9b42\u57ce\u6ca1\u6709\u5f88\u5f3a\u70c8\u7684\u5f52\u5c5e\u611f\uff0c\u4f46\u6bd5\u7adf\u4e5f\u662f\u751f\u6d3b\u4e86\u5341\u51e0\u5e74\u7684\u5730\u65b9\uff0c\u81ea\u5df1\u4e5f\u5728\u91cc\u9762\u6df7\u996d\u5403\uff0c\u591a\u5c11\u6709\u4e9b\u611f\u60c5\uff0c\u773c\u89c1\u4e00\u6d3e\u7e41\u76db\u666f\u8c61\uff0c\u4e5f\u8fd8\u662f\u6bd4\u8f83\u9ad8\u5174\u7684\u3002</p><p>\u65b9\u5706\u767e\u91cc\u4e4b\u5185\uff0c\u5e94\u8be5\u90fd\u5df2\u7ecf\u88ab\u9634\u9b42\u57ce\u5212\u4e3a\u52bf\u529b\u8303\u56f4\u2014\u2014\u867d\u7136\u5927\u6c99\u6f20\u91cc\u4e5f\u6ca1\u4eba\u6765\u4e89\u593a\uff0c\u743c\u6069\u7b49\u4e09\u4eba\u521a\u521a\u9760\u8fd1\uff0c\u4fbf\u6709\u5de1\u903b\u58eb\u5175\u4e0a\u6765\u76d8\u67e5\u3002\u743c\u6069\u4eae\u660e\u8eab\u4efd\uff0c\u88ab\u5b89\u6392\u4e0a\u4e86\u4e00\u5ea7\u6d6e\u7a7a\u8247\uff0c\u56de\u5230\u9634\u9b42\u57ce\u4e2d\u3002</p><p>\u6b64\u6b21\u56de\u57ce\u662f\u4e3a\u4e86\u8ff0\u804c\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u5fd9\u4e8e\u4e00\u65f6\uff0c\u5728\u519b\u8425\u91cc\u767b\u8bb0\u62a5\u5230\uff0c\u7136\u540e\u5148\u56de\u5bb6\u4f11\u606f\uff0c\u7b49\u5019\u901a\u77e5\u4fbf\u662f\u3002\u743c\u6069\u548c\u8299\u8389\u5a05\u3001\u8299\u857e\u72c4\u540c\u884c\u56de\u5230\u5b9d\u77f3\u533a\uff0c\u5728\u83ab\u5c3c\u5361\u5b85\u9662\u524d\u5206\u624b\u3002</p><p>\u201c\u5728\u5bb6\u597d\u597d\u4f11\u606f\uff0c\u6211\u4f1a\u6765\u770b\u4f60\u4eec\u3002\u201d\u743c\u6069\u5bf9\u8299\u857e\u72c4\u8bf4\uff0c\u5728\u5979\u5507\u4e0a\u8f7b\u8f7b\u4e00\u543b\u3002</p><p>\u201c\u55ef\uff0c\u201d\u5c0f\u5973\u5b69\u4f4e\u58f0\u7b54\u5e94\uff0c\u201c\u6211\u7b49\u4f60\u3002\u201d</p><p>\u743c\u6069\u56de\u5230\u81ea\u5df1\u5bb6\u4e2d\uff0c\u9662\u5b50\u548c\u623f\u5c4b\u7684\u95e8\u90fd\u662f\u5f00\u7684\uff0c\u4ed6\u76f4\u63a5\u8d70\u5165\uff0c\u4f46\u5374\u6ca1\u770b\u89c1\u4eba\u5f71\uff0c\u66f4\u6ca1\u770b\u89c1\u73ca\u5609\u3002\u697c\u4e0a\u697c\u4e0b\u90fd\u8f6c\u4e86\u5708\uff0c\u4e00\u65e0\u6240\u83b7\uff0c\u6b63\u81ea\u5947\u602a\uff0c\u6070\u597d\u4e00\u4e2a\u5973\u4ec6\u4ece\u540e\u5385\u8d70\u51fa\uff0c\u770b\u89c1\u743c\u6069\u5148\u662f\u6123\u4e86\u4e0b\uff0c\u968f\u5373\u8ba4\u4e86\u51fa\u6765\uff0c\u8fde\u5fd9\u4e0a\u524d\u884c\u793c\uff0c\u201c\u60a8\u56de\u6765\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u6211\u59d0\u59d0\u4e0d\u5728\u5bb6\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u65f6\u95f4\uff0c\u5c0f\u59d0\u5e94\u8be5\u8fd8\u5728\u5b66\u9662\u91cc\u3002\u201d\u5973\u4ec6\u56de\u7b54\u3002</p><p>\u201c\u5b66\u9662\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u4ec0\u4e48\u5b66\u9662\uff1f\u201d</p><p>\u201c\u5c0f\u59d0\u5728\u97f3\u4e50\u5b66\u9662\u91cc\u5b66\u4e60\uff0c\u201d\u5973\u4ec6\u8fde\u5fd9\u89e3\u91ca\uff0c\u201c\u53bb\u5e74\u5e74\u5e95\u5165\u5b66\u7684\uff0c\u90a3\u65f6\u5019\u60a8\u4e0d\u5728\u5bb6\u3002\u201d</p><p>\u743c\u6069\u54e6\u4e86\u4e00\u58f0\uff0c\u793a\u610f\u77e5\u9053\uff0c\u4ed6\u672c\u6765\u8003\u8651\u8981\u4e0d\u8981\u53bb\u97f3\u4e50\u5b66\u9662\u627e\u73ca\u5609\uff0c\u8f6c\u5ff5\u4e00\u60f3\u65e2\u7136\u5979\u5728\u4e0a\u8bfe\uff0c\u81ea\u5df1\u8fd8\u662f\u4e0d\u8981\u6253\u6270\u7684\u597d\u3002\u53c8\u95ee\u4e86\u4e0b\u5973\u4ec6\uff0c\u5f97\u77e5\u97f3\u4e50\u5b66\u9662\u548c\u5deb\u5e08\u5b66\u9662\u4e0d\u540c\uff0c\u89c4\u77e9\u5bbd\u677e\uff0c\u4e5f\u4e0d\u641e\u4ec0\u4e48\u519b\u4e8b\u5316\u7ba1\u7406\uff0c\u66f4\u4e0d\u7528\u5f3a\u5236\u4f4f\u6821\uff0c\u73ca\u5609\u6bcf\u5929\u4e0b\u5348\u56db\u70b9\u949f\u653e\u5b66\uff0c\u56db\u70b9\u534a\u4e4b\u524d\u5c31\u80fd\u5230\u5bb6\uff0c\u73b0\u5728\u624d\u521a\u521a\u4e24\u70b9\uff0c\u8fd8\u6709\u4e24\u4e2a\u591a\u5c0f\u65f6\u3002</p><p>\u4ed6\u521a\u521a\u8fdc\u884c\u5f52\u6765\uff0c\u5c24\u5176\u662f\u6700\u540e\u4e00\u6bb5\u8def\uff0c\u6ca1\u6709\u8212\u9002\u9a6c\u8f66\u53ef\u5750\uff0c\u53ea\u80fd\u9a91\u7740\u9a86\u9a7c\u5728\u6c99\u6f20\u91cc\u8270\u96be\u8dcb\u6d89\uff0c\u98ce\u5c18\u6ee1\u8eab\uff0c\u6c57\u6e7f\u91cd\u8863\uff0c\u4e5f\u4e0d\u60f3\u5c31\u8fd9\u6837\u53bb\u89c1\u73ca\u5609\uff0c\u4e8e\u662f\u4fbf\u5429\u5490\u5973\u4ec6\u51c6\u5907\u70ed\u6c34\uff0c\u6253\u7b97\u5148\u6d17\u4e2a\u6fa1\uff0c\u7a0d\u4f5c\u4f11\u606f\uff0c\u7b49\u4e09\u70b9\u534a\u5de6\u53f3\u51fa\u53d1\u53bb\u97f3\u4e50\u5b66\u9662\u63a5\u73ca\u5609\u3002</p><p>\u8212\u8212\u670d\u670d\u5730\u5728\u6d74\u7f38\u91cc\u6ce1\u4e86\u534a\u5929\uff0c\u6362\u4e0a\u5e72\u51c0\u8863\u670d\uff0c\u743c\u6069\u987f\u65f6\u89c9\u5f97\u4e00\u8eab\u8f7b\u677e\uff0c\u8fde\u65e5\u5954\u6ce2\u7684\u75b2\u52b3\u4e5f\u90fd\u6d88\u89e3\u4e86\u51e0\u5206\u3002\u5973\u4ec6\u6b63\u5728\u4e3a\u4ed6\u6536\u62fe\u623f\u95f4\uff0c\u743c\u6069\u770b\u770b\u65f6\u95f4\u8fd8\u65e9\uff0c\u4fbf\u4e0a\u4e86\u697c\uff0c\u8d70\u5230\u73ca\u5609\u7684\u623f\u95f4\u524d\u63a8\u95e8\u8fdb\u5165\u3002</p><p>\u548c\u8bb0\u5fc6\u4e2d\u4e00\u6837\uff0c\u73ca\u5609\u7684\u623f\u95f4\u4f9d\u65e7\u9648\u8bbe\u7b80\u5355\uff0c\u98ce\u683c\u6734\u7d20\uff0c\u68b3\u5986\u53f0\u4e0a\u6446\u653e\u7740\u4e00\u4e9b\u5973\u5b69\u5b50\u5e38\u7528\u7684\u5316\u5986\u54c1\uff0c\u5e8a\u4e0a\u7684\u6bdb\u6bef\u53e0\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u6795\u5934\u8fb9\u653e\u7740\u4e09\u672c\u4e66\uff0c\u743c\u6069\u62ff\u8d77\u6765\u7ffb\u4e86\u7ffb\uff0c\u53d1\u73b0\u5168\u90fd\u662f\u5173\u4e8e\u97f3\u4e50\u65b9\u9762\u7684\u6559\u6750\uff0c\u5176\u4e2d\u6709\u4e00\u672c\u5c31\u662f\u4e13\u95e8\u8bb2\u957f\u7b1b\u6f14\u594f\u7684\u3002</p><p>\u201c\u770b\u6765\uff0c\u59d0\u59d0\u8fd8\u771f\u662f\u559c\u6b22\u4e0a\u4e86\u97f3\u4e50\u5462\u3002\u201d</p><p>\u743c\u6069\u7565\u89c9\u610f\u5916\uff0c\u540c\u65f6\u4e5f\u4e3a\u73ca\u5609\u6b23\u6170\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u53d1\u73b0\u81ea\u5df1\u9690\u85cf\u7684\u5929\u8d4b\u624d\u80fd\u548c\u5174\u8da3\u7231\u597d\uff0c\u603b\u662f\u503c\u5f97\u9ad8\u5174\u7684\u4e8b\u60c5\u3002\u73ca\u5609\u53c8\u4e0d\u50cf\u81ea\u5df1\u6574\u65e5\u5728\u5916\u5954\u6ce2\uff0c\u5979\u4e00\u4e2a\u5973\u5b69\u5b50\u5b64\u96f6\u96f6\u7559\u5728\u5bb6\u4e2d\uff0c\u9634\u9b42\u57ce\u53c8\u662f\u8fd9\u7b49\u6b7b\u6c14\u6c89\u6c89\u7684\u5730\u65b9\uff0c\u6beb\u65e0\u534a\u70b9\u5a31\u4e50\u6d3b\u52a8\uff0c\u5bc2\u5bde\u70e6\u95f7\u5728\u6240\u96be\u514d\uff0c\u80fd\u591f\u5bc4\u60c5\u97f3\u4e50\uff0c\u6392\u9063\u65f6\u5149\uff0c\u81ea\u7136\u662f\u4ef6\u6c42\u4e4b\u4e0d\u5f97\u7684\u597d\u4e8b\u3002</p><p>\u4ed6\u968f\u610f\u8eba\u5728\u5e8a\u4e0a\uff0c\u95fb\u7740\u73ca\u5609\u7559\u5728\u6795\u4e0a\u7684\u6de1\u6de1\u53d1\u9999\uff0c\u4ee5\u53ca\u623f\u95f4\u91cc\u5e7d\u5e7d\u7684\u9634\u5f71\u5170\u5473\u9053\uff0c\u90a3\u79cd\u719f\u6089\u7684\u201c\u59d0\u59d0\u201d\u6c14\u606f\uff0c\u8ba9\u4ed6\u89c9\u5f97\u5341\u5206\u5b89\u5fc3\uff0c\u604d\u60da\u95f4\u4fbf\u6709\u4e86\u4e9b\u5026\u610f\u3002\u8fde\u65e5\u8d76\u8def\uff0c\u539f\u672c\u5c31\u6709\u4e9b\u75b2\u7d2f\uff0c\u53c8\u521a\u521a\u6d17\u4e86\u6fa1\uff0c\u5168\u8eab\u653e\u677e\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4fbf\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u8fd9\u4e00\u7761\u4fbf\u8fc7\u4e86\u5934\uff0c\u7b49\u5230\u9192\u6765\u65f6\u7a97\u5916\u5df2\u7ecf\u66ae\u8272\u7b3c\u7f69\uff0c\u623f\u95f4\u91cc\u6ca1\u6709\u5f00\u706f\uff0c\u9ed1\u6c89\u6c89\u7684\u3002\u743c\u6069\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5fc3\u60f3\u803d\u8bef\u4e86\u53bb\u63a5\u59d0\u59d0\u7684\u65f6\u95f4\uff0c\u6b63\u8981\u5306\u5fd9\u8d77\u8eab\uff0c\u7a81\u7136\u53d1\u89c9\u8eab\u65c1\u4f3c\u4e4e\u8fd8\u7761\u7740\u4e00\u4e2a\u4eba\u3002\u4ed6\u5b9a\u795e\u770b\u53bb\uff0c\u5ffd\u7136\u8f7b\u58f0\u7b11\u4e86\u8d77\u6765\uff0c\u4fef\u4e0b\u8eab\u53bb\u8f7b\u8f7b\u4e00\u543b\uff0c\u201c\u59d0\u59d0\u3002\u201d</p><p>\u73ca\u5609\u8ff7\u8ff7\u7cca\u7cca\u5730\u5514\u4e86\u4e00\u58f0\uff0c\u611f\u89c9\u81ea\u5df1\u88ab\u4eba\u62b1\u5728\u6000\u4e2d\uff0c\u5a07\u5ae9\u5507\u74e3\u88ab\u6eda\u70eb\u800c\u719f\u6089\u7684\u6c14\u606f\u5c01\u4f4f\uff0c\u820c\u5c16\u7075\u6d3b\u5730\u6e38\u8fdb\u6765\uff0c\u6311\u9017\u7740\u8d1d\u9f7f\u3002\u5979\u4e0b\u610f\u8bc6\u5730\u5fae\u5fae\u5f20\u53e3\uff0c\u8fce\u5408\u7740\u7537\u6027\u7684\u4fb5\u5165\uff0c\u4efb\u4ed6\u7ec6\u7ec6\u542e\u5438\u54c1\u5c1d\uff0c\u53cc\u81c2\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u7ecf\u73af\u6263\u4e0a\u4e86\u5bf9\u65b9\u7684\u8116\u9888\u3002\u623f\u95f4\u91cc\u9759\u6084\u6084\u7684\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4e24\u4eba\u6162\u6162\u5206\u5f00\uff0c\u73ca\u5609\u7741\u5f00\u773c\u775b\uff0c\u773c\u795e\u4e2d\u6ee1\u662f\u6e29\u67d4\u7b11\u610f\u3002</p><p>\u201c\u56de\u6765\u4e86\uff0c\u5c0f\u5f1f\u3002\u201d\u5979\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u5c06\u5979\u62b1\u7d27\uff0c\u201c\u6211\u56de\u6765\u4e86\uff0c\u59d0\u59d0\u3002\u201d</p></article>"}}}]);
