(window.webpackJsonp=window.webpackJsonp||[]).push([[162],{184:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u987a\u8def",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u987a\u8def</h2><p>\u662f\u4e0d\u662f\u5f00\u73a9\u7b11\uff0c\u743c\u6069\u5fc3\u91cc\u81ea\u7136\u4e5f\u6e05\u695a\uff0c\u4ed6\u5728\u5fc3\u4e2d\u5fae\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u987a\u4fbf\u8bc5\u5492\u4e0a\u8f88\u5b50\u770b\u7684\u90a3\u4e9b\u5947\u5e7b\u5c0f\u8bf4\u4f5c\u8005\uff0c\u4e3a\u4ec0\u4e48\u90a3\u4e9b\u5c0f\u8bf4\u91cc\u7684\u4e3b\u89d2\u4e2a\u4e2a\u5de6\u62e5\u53f3\u62b1\u987a\u5229\u81f3\u6781\uff0c\u81ea\u5df1\u624d\u649e\u4e0a\u4e24\u4e2a\u5c31\u8981\u5f00\u59cb\u5934\u75bc\u2026\u2026\u6216\u8bb8\u8fd9\u8bf4\u660e\u81ea\u5df1\u8fd8\u662f\u5f88\u5584\u826f\u7684\uff0c\u4ed6\u8fd9\u6837\u81ea\u6211\u5b89\u6170\u7740\uff0c\u7136\u540e\u89c9\u5f97\u8fd9\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u4e2a\u5b89\u6170\u3002</p><p>\u552f\u4e00\u503c\u5f97\u5e86\u5e78\u7684\uff0c\u662f\u8fd9\u4e24\u4e2a\u5973\u4eba\u90fd\u8fd8\u806a\u660e\uff0c\u6216\u8005\u8bf4\u7406\u667a\uff0c\u7eb5\u7136\u5076\u5c14\u800d\u70b9\u5c0f\u6027\u5b50\uff0c\u4e5f\u4f1a\u6e05\u695a\u5730\u77e5\u9053\u5206\u5bf8\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u4e0d\u4f1a\u628a\u5c40\u9762\u5f04\u50f5\uff0c\u5426\u5219\u5c31\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u6536\u573a\u4e86\u3002</p><p>\u4ed6\u53f9\u6c14\u7740\uff0c\u987a\u7740\u697c\u68af\u5f80\u4e0a\uff0c\u7136\u540e\u4ed6\u53d1\u73b0\u8fd9\u6b21\u53c8\u6362\u4e86\u5730\u65b9\uff0c\u65e2\u4e0d\u662f\u5b9e\u9a8c\u5ba4\uff0c\u4e5f\u4e0d\u662f\u56fe\u4e66\u5ba4\uff0c\u5012\u6709\u70b9\u50cf\u662f\u4ed6\u81ea\u5df1\u7684\u4e66\u623f\u3002</p><p>\u8001\u5deb\u5996\u6b63\u5750\u5728\u4e00\u5f20\u5bbd\u5927\u7684\u8eba\u6905\u4e0a\uff0c\u624b\u91cc\u62ff\u7740\u672c\u4e66\uff0c\u6162\u6162\u7ffb\u9605\uff0c\u89c1\u743c\u6069\u8fdb\u6765\uff0c\u4e5f\u4e0d\u62ac\u5934\uff0c\u201c\u8bf7\u5750\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u6253\u8fc7\u4e24\u6b21\u4ea4\u9053\uff0c\u53c8\u542c\u8389\u6cd5\u513f\u8bf4\u8fc7\uff0c\u743c\u6069\u4e5f\u77e5\u9053\u8fd9\u8001\u5deb\u5996\u7684\u4e60\u60ef\uff0c\u4e0d\u7231\u591a\u8bf4\u5e9f\u8bdd\u3002\u4ed6\u7b54\u5e94\u4e00\u58f0\uff0c\u627e\u628a\u6905\u5b50\u5750\u4e86\u4e0b\u6765\uff0c\u9759\u9759\u7b49\u5f85\u7740\u3002</p><p>\u4ea1\u7075\u679c\u7136\u662f\u6ca1\u6709\u65f6\u95f4\u6982\u5ff5\u7684\u5b58\u5728\uff0c\u62c9\u6c83\u514b\u628a\u743c\u6069\u53eb\u6765\uff0c\u81ea\u5df1\u5374\u597d\u6574\u4ee5\u6687\u5730\u770b\u4e66\uff0c\u5168\u7136\u628a\u4ed6\u667e\u5230\u4e00\u8fb9\uff0c\u4eff\u4f5b\u9057\u5fd8\u4e86\u4f3c\u7684\u3002\u743c\u6069\u5012\u4e5f\u5f88\u6709\u8010\u5fc3\uff0c\u9759\u9759\u7b49\u5f85\uff0c\u4e0d\u8bf4\u534a\u53e5\u8bdd\u3002\u6700\u7ec8\uff0c\u62c9\u6c83\u514b\u624b\u4e2d\u7684\u4e66\u7ffb\u5230\u4e86\u6700\u540e\u4e00\u9875\uff0c\u4ed6\u55ef\u4e86\u4e00\u58f0\uff0c\u628a\u4e66\u4e22\u5230\u684c\u5b50\u4e0a\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u743c\u6069\u3002</p><p>\u743c\u6069\u606d\u656c\u5730\u4f4e\u5934\u3002</p><p>\u62c9\u6c83\u514b\u5f39\u4e86\u5f39\u624b\u6307\uff0c\u4e00\u5c01\u88ab\u725b\u76ae\u4fe1\u5c01\u5c01\u597d\u7684\u4fe1\u4ef6\u5c31\u51fa\u73b0\u5728\u743c\u6069\u9762\u524d\uff0c\u60ac\u6d6e\u5728\u7a7a\u6c14\u4e2d\u3002\u743c\u6069\u4f38\u624b\u63a5\u8fc7\uff0c\u4ed6\u77e5\u9053\u8fd9\u80af\u5b9a\u5c31\u662f\u62c9\u6c83\u514b\u5199\u7ed9\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7684\u56de\u4fe1\u4e86\u3002</p><p>\u201c\u628a\u5b83\u5e26\u56de\u53bb\u7ed9\u5e03\u96f7\u7eb3\u65af\u3002\u201d\u62c9\u6c83\u514b\u8bf4\u3002</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u53e6\u5916\uff0c\u6211\u8fd9\u91cc\u6709\u70b9\u5c0f\u4e8b\uff0c\u4f60\u5e2e\u6211\u53bb\u987a\u8def\u53bb\u529e\u4e86\u3002\u201d</p><p>\u987a\u8def\uff1f</p><p>\u201c\u585e\u5c14\u7684\u8428\u624e\u65af\u5766\uff0c\u501f\u4e86\u6211\u4e24\u6837\u4e1c\u897f\uff0c\u201d\u8001\u5deb\u5996\u81ea\u987e\u81ea\u5730\u8bf4\uff0c\u5b8c\u5168\u61d2\u5f97\u7406\u776c\u743c\u6069\u7684\u53cd\u5e94\uff0c\u201c\u5176\u4e2d\u4e00\u4ef6\u6492\u5bc7\u5e2d\u5c14\u4e4b\u5ea7\uff0c\u4ed6\u5df2\u7ecf\u5f52\u8fd8\u4e86\uff1b\u4f46\u53e6\u5916\u4e00\u4ef6\uff0c\u6b7b\u6708\u6cd5\u73e0\uff0c\u4ed6\u8fdf\u8fdf\u6ca1\u52a8\u9759\u3002\u6211\u9700\u8981\u627e\u4e2a\u4eba\u53bb\u50ac\u50ac\u4ed6\uff0c\u201d\u62c9\u6c83\u514b\u76ef\u7740\u743c\u6069\uff0c\u4ed6\u7684\u9ab7\u9ac5\u773c\u7736\u91cc\u95ea\u70c1\u7740\u5371\u9669\u7684\u7ea2\u5149\uff0c\u201c\u4f60\u53bb\u5e2e\u6211\u8dd1\u4e00\u8d9f\uff0c\u628a\u6b7b\u6708\u6cd5\u73e0\u66ff\u6211\u8981\u56de\u6765\uff0c\u53cd\u6b63\u4e5f\u987a\u8def\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5177\u4f53\u60c5\u5f62\uff0c\u62c9\u6c83\u514b\u61d2\u5f97\u591a\u8d39\u53e3\u820c\uff0c\u4ed6\u76f4\u63a5\u62db\u6765\u4e2a\u5e7d\u9b42\uff0c\u8ba9\u5b83\u6765\u5411\u743c\u6069\u4ecb\u7ecd\u8be6\u7ec6\u60c5\u51b5\u3002</p><p>\u4fbf\u5982\u8389\u6cd5\u513f\u6240\u8bf4\uff0c\u62c9\u6c83\u514b\u5f53\u5e74\u7684\u6d6e\u7a7a\u57ce\u5760\u6bc1\u5728\u7eb3\u83f2\u5c14\u5e73\u539f\uff0c\u6240\u6709\u7684\u85cf\u5b9d\u88ab\u7eb3\u83f2\u5c14\u7f1a\u9b54\u5e08\u6240\u5f97\uff0c\u540e\u6765\u7eb3\u83f2\u5c14\u5e1d\u56fd\u6bc1\u706d\uff0c\u8fd9\u4e9b\u5b9d\u85cf\u8f97\u8f6c\u6d41\u843d\u5230\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\u624b\u91cc\u3002\u5927\u7ea6\u5728\u4e24\u4e09\u5e74\u524d\uff08\u5177\u4f53\u65f6\u95f4\u8bb0\u4e0d\u6e05\u695a\u4e86\uff0c\u4ea1\u7075\u5bf9\u65f6\u95f4\u7684\u6982\u5ff5\u6bd4\u8f83\u6a21\u7cca\uff09\uff0c\u7ea2\u888d\u5deb\u5e08\u7684\u9886\u8896\uff0c\u8428\u624e\u65af\u5766\u5148\u751f\uff0c\u524d\u6765\u5deb\u5e08\u4e4b\u5893\u62dc\u8bbf\uff0c\u4ed6\u9001\u8fd8\u4e86\u4e00\u4e9b\u7269\u54c1\uff0c\u5e76\u501f\u8d70\u4e86\u62c9\u6c83\u514b\u7684\u4e24\u4ef6\u5b9d\u7269\uff1a\u6492\u5bc7\u5e2d\u5c14\u4e4b\u5ea7\u3001\u6b7b\u6708\u6cd5\u73e0\u3002</p><p>\u7136\u800c\u8428\u624e\u65af\u5766\u4e00\u53bb\uff0c\u4ece\u6b64\u4fbf\u518d\u6ca1\u6d88\u606f\uff0c\u76f4\u5230\u524d\u6bb5\u65f6\u95f4\u7ec8\u4e8e\u6d3e\u4eba\u6765\u9001\u8fd8\u4e86\u6492\u5bc7\u5e2d\u5c14\u4e4b\u5ea7\uff0c\u4f46\u95ee\u53ca\u6b7b\u6708\u6cd5\u73e0\uff0c\u5374\u8a00\u8bed\u63a8\u8131\uff0c\u95ea\u70c1\u5176\u8bcd\u3002\u62c9\u6c83\u514b\u4f7f\u7528\u5360\u535c\u672f\uff0c\u7ed3\u679c\u53d1\u73b0\u2026\u2026\u8428\u624e\u65af\u5766\u597d\u50cf\u662f\u628a\u6b7b\u6708\u6cd5\u73e0\u7ed9\u5f04\u4e22\u4e86\u3002</p><p>\u8fd9\u4e24\u4ef6\u5b9d\u7269\u90fd\u662f\u8010\u745f\u65f6\u4ee3\u7684\u9876\u7ea7\u9b54\u6cd5\u7269\u54c1\uff0c\u5c31\u7b97\u8bf4\u662f\u795e\u5668\u4e5f\u4e0d\u8fc7\u5206\uff0c\u5c82\u662f\u80fd\u8f7b\u6613\u5f04\u4e22\u6389\u3002\u62c9\u6c83\u514b\u81ea\u7136\u5f88\u4e0d\u9ad8\u5174\uff0c\u4f46\u8fd9\u4f4d\u8001\u5deb\u5996\u5fd9\u7740\u505a\u4ed6\u7684\u8bd5\u9a8c\uff0c\u61d2\u5f97\u51fa\u95e8\u50ac\u503a\uff0c\u6070\u597d\u743c\u6069\u8fd9\u4e2a\u5012\u9709\u5bb6\u4f19\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\uff0c\u4e8e\u662f\u8fd9\u5dee\u4f7f\u5c31\u843d\u5230\u4ed6\u5934\u4e0a\u4e86\u3002</p><p>\u4e8b\u60c5\u5c31\u662f\u8fd9\u4e2a\u4e8b\u60c5\uff0c\u743c\u6069\u542c\u5728\u5fc3\u91cc\u5c31\u5f88\u60f3\u780d\u4eba\u3002\u4ed6\u5988\u7684\uff0c\u96be\u9053\u6211\u662f\u8dd1\u817f\u6253\u6742\u7684\u4e48\uff0c\u5148\u662f\u7ed9\u4eba\u6765\u9001\u4fe1\uff0c\u73b0\u5728\u53c8\u8981\u53bb\u5e2e\u4eba\u8ba8\u503a\uff0c\u5982\u679c\u8bf4\u56e0\u4e3a\u81ea\u5df1\u603b\u4e5f\u7b97\u662f\u9634\u9b42\u57ce\u7684\u516c\u52a1\u5458\uff0c\u524d\u4e00\u4e2a\u4efb\u52a1\u65e0\u6cd5\u63a8\u8131\uff0c\u53ef\u62c9\u6c83\u514b\u548c\u81ea\u5df1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u51ed\u4ec0\u4e48\u4e5f\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u5730\u6307\u624b\u753b\u811a\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u8bdd\u53ea\u80fd\u5728\u5fc3\u91cc\u60f3\u60f3\uff0c\u82e5\u662f\u60f9\u6012\u4e86\u8001\u5deb\u5996\uff0c\u81ea\u5df1\u7684\u6027\u547d\u5c31\u5f88\u582a\u5fe7\u4e86\u3002\u65e2\u7136\u8001\u5deb\u5996\u5429\u5490\uff0c\u743c\u6069\u4e5f\u53ea\u5f97\u5148\u7b54\u5e94\u4e0b\u6765\uff0c\u6b63\u51c6\u5907\u5148\u884c\u544a\u9000\uff0c\u56de\u53bb\u6162\u6162\u518d\u60f3\u529e\u6cd5\uff0c\u8001\u5deb\u5996\u5374\u53eb\u4f4f\u4e86\u4ed6\u3002</p><p>\u201c\u6212\u6307\u3002\u201d\u8001\u5deb\u5996\u8bf4\u3002</p><p>\u743c\u6069\u4e00\u65f6\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u76f4\u5230\u8001\u5deb\u5996\u770b\u5411\u81ea\u5df1\u7684\u5de6\u624b\uff0c\u4ed6\u624d\u60f3\u8d77\u6765\u3002\u51fa\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u9001\u4e86\u4e9b\u4e1c\u897f\uff0c\u9664\u4e86\u836f\u6c34\u3001\u5377\u8f74\u3001\u9632\u5fa1\u80f8\u9488\u548c\u9f99\u9cde\u76fe\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u679a\u7528\u9014\u4e0d\u660e\u7684\u6212\u6307\uff0c\u4e00\u76f4\u6234\u5728\u81ea\u5df1\u5de6\u624b\u98df\u6307\u4e0a\uff0c\u4e5f\u6ca1\u89c1\u53d1\u6325\u4ec0\u4e48\u4f5c\u7528\uff0c\u7b80\u76f4\u5c31\u662f\u4e2a\u5e9f\u7269\u3002\u62c9\u6c83\u514b\u82e5\u662f\u4e0d\u63d0\u8d77\u6765\uff0c\u81ea\u5df1\u5dee\u70b9\u90fd\u7ed9\u5fd8\u4e86\u3002</p><p>\u4ed6\u5c06\u6212\u6307\u8131\u4e86\u4e0b\u6765\uff0c\u9012\u7ed9\u62c9\u6c83\u514b\uff0c\u4e0d\u77e5\u9053\u8001\u5deb\u5996\u60f3\u5e72\u4ec0\u4e48\u3002</p><p>\u62c9\u6c83\u514b\u63a5\u8fc7\u6212\u6307\uff0c\u7528\u98df\u6307\u548c\u62c7\u6307\u634f\u4f4f\uff0c\u7f13\u7f13\u8f6c\u52a8\u4e00\u5708\uff0c\u4ed6\u4f3c\u4e4e\u5728\u770b\u6212\u6307\u5185\u4fa7\u7684\u5947\u602a\u5b57\u7b26\uff0c\u7136\u540e\u6162\u6162\u8bfb\u4e86\u51fa\u6765\u3002</p><p>\u65e2\u975e\u9f99\u8bed\uff0c\u4e5f\u975e\u8010\u745f\u8bed\uff0c\u4e5f\u975e\u901a\u7528\u8bed\uff0c\u4e43\u662f\u67d0\u79cd\u743c\u6069\u5b8c\u5168\u4e0d\u66fe\u542c\u8fc7\u7684\u8bed\u8a00\uff0c\u4f46\u5176\u4e2d\u8574\u542b\u7740\u663e\u800c\u6613\u89c1\u7684\u5f3a\u5927\u9b54\u529b\uff0c\u743c\u6069\u654f\u9510\u5730\u5bdf\u89c9\u5230\u65e0\u5f62\u7684\u9b54\u6cd5\u7f51\u7edc\u4eff\u4f5b\u90fd\u88ab\u632f\u52a8\u8d77\u6765\u3002\u968f\u7740\u62c9\u6c83\u514b\u5ff5\u5b8c\u6700\u540e\u4e00\u4e2a\u5b57\u7b26\uff0c\u9576\u5d4c\u5728\u6212\u6307\u4e0a\u7684\u90a3\u679a\u5c0f\u5c0f\u7684\u84dd\u8272\u83f1\u5f62\u5b9d\u77f3\u4e5f\u95ea\u70c1\u8d77\u6765\uff0c\u71a0\u71a0\u751f\u8f89\u3002</p><p>\u9b54\u6cd5\u80fd\u91cf\u5728\u6084\u65e0\u58f0\u606f\u5730\u6c47\u805a\uff0c\u62c9\u6c83\u514b\u7684\u53e6\u5916\u4e00\u53ea\u767d\u9aa8\u624b\u638c\u4e2d\uff0c\u6e10\u6e10\u51dd\u6210\u4e00\u4e2a\u534a\u900f\u660e\u7684\u6c34\u6676\u7403\u4f53\uff0c\u5927\u7ea6\u62f3\u5934\u5927\u5c0f\u3002\u8001\u5deb\u5996\u76ef\u7740\u6212\u6307\uff0c\u4eff\u4f5b\u5728\u7b49\u5f85\u7740\u4ec0\u4e48\u65f6\u673a\uff0c\u7136\u540e\u7a81\u7136\u4f38\u624b\u4e00\u6309\uff0c\u5c45\u7136\u5c06\u90a3\u4e2a\u534a\u900f\u660e\u7684\u6c34\u6676\u7403\u4f53\u795e\u5947\u5730\u201c\u6309\u201d\u8fdb\u4e86\u6212\u6307\u4e2d\u3002</p><p>\u6212\u6307\u4e0a\u7684\u84dd\u8272\u83f1\u5f62\u5b9d\u77f3\u5149\u8292\u731b\u76db\uff0c\u5239\u90a3\u95f4\u8000\u773c\u523a\u76ee\uff0c\u968f\u5373\u5feb\u901f\u9eef\u6de1\u4e0b\u6765\uff0c\u6700\u7ec8\u5b8c\u5168\u5e73\u606f\u3002\u62c9\u6c83\u514b\u5c06\u6212\u6307\u8fd8\u7ed9\u743c\u6069\uff0c\u5e76\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u4f46\u743c\u6069\u5df2\u7ecf\u770b\u660e\u767d\u4e86\u3002</p><p>\u62c9\u6c83\u514b\u8d60\u9001\u4e86\u4ed6\u4e00\u4ef6\u793c\u7269\uff0c\u6216\u8005\u8bf4\uff0c\u793c\u7269\u672c\u6765\u5c31\u5df2\u7ecf\u83b7\u5f97\uff0c\u53ea\u662f\u743c\u6069\u4e00\u76f4\u61f5\u61f5\u61c2\u61c2\u7684\u4e0d\u77e5\u9053\uff0c\u5982\u4eca\u62c9\u6c83\u514b\u5e2e\u4ed6\u6253\u5f00\u4e86\u5305\u88c5\u76d2\u3002</p><p>\u8010\u745f\u6cd5\u672f\u62a4\u7f69\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u4eca\u4e16\u4e0a\u73b0\u6709\u7684\u9b54\u6cd5\u6280\u672f\uff0c\u8db3\u4ee5\u5236\u9020\u51fa\u5404\u79cd\u5404\u6837\u7684\u9b54\u6cd5\u7269\u54c1\u6765\uff0c\u743c\u6069\u8eab\u4e0a\u5c31\u6709\u4e0d\u5c11\uff0c\u867d\u7136\u88ab\u6b27\u51ef\u62ff\u8d70\u4e86\u51e0\u4ef6\u3002\u6240\u8c13\u9b54\u6cd5\u7269\u54c1\uff0c\u4ece\u9053\u7406\u4e0a\u6765\u8bf4\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u662f\u5c06\u6cd5\u672f\u9644\u7740\u6052\u5b9a\u5230\u67d0\u4e9b\u7269\u4f53\u4e0a\u3002\u4f46\u5c31\u4e00\u822c\u7684\u7406\u8bba\u800c\u8a00\uff0c\u4e0d\u540c\u6cd5\u672f\u4e4b\u95f4\u5f80\u5f80\u5bb9\u6613\u76f8\u4e92\u51b2\u7a81\uff0c\u5f88\u96be\u540c\u65f6\u5e76\u5b58\u4e8e\u540c\u4e00\u4ef6\u7269\u54c1\u4e0a\uff0c\u6240\u4ee5\u4e16\u754c\u4e0a\u5404\u79cd\u9b54\u6cd5\u7269\u54c1\uff0c\u57fa\u672c\u4e0a\u90fd\u662f\u529f\u80fd\u5355\u4e00\u7684\u3002</p><p>\u4f46\u662f\uff0c\u5728\u6614\u65e5\u7684\u8010\u745f\u5e1d\u56fd\uff0c\u60c5\u5f62\u5e76\u975e\u5982\u6b64\u3002</p><p>\u8f89\u714c\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u662f\u53e3\u5934\u4e0a\u8bf4\u8bf4\u7684\uff0c\u9664\u4e86\u6d6e\u7a7a\u57ce\u3001\u5927\u5965\u672f\u5e08\u8fd9\u4e9b\u201c\u5178\u578b\u201d\u4e4b\u5916\uff0c\u8010\u745f\u65f6\u4ee3\u7684\u9b54\u6cd5\u9020\u7269\u5b66\uff0c\u4e5f\u662f\u7b2c\u4e00\u7b49\u7684\u53d1\u8fbe\u3002\u5deb\u5e08\u4eec\u751a\u81f3\u80fd\u5236\u4f5c\u51fa\u4e00\u79cd\u65e0\u5f62\u65e0\u8d28\u7684\u201c\u4e1c\u897f\u201d\u6765\uff0c\u53ef\u4ee5\u9644\u7740\u5728\u4efb\u4f55\u5b9e\u7269\u4e0a\uff0c\u4f8b\u5982\u957f\u888d\u3001\u9879\u94fe\u3001\u6212\u6307\u4e4b\u7c7b\u3002\u8fd9\u79cd\u65e0\u5f62\u65e0\u8d28\u4e4b\u7269\uff0c\u80fd\u591f\u517c\u5bb9\u591a\u79cd\u4e0d\u540c\u6548\u679c\u7684\u9b54\u6cd5\u4e8e\u4e00\u4f53\uff0c\u79f0\u5f97\u4e0a\u662f\u6781\u9ad8\u660e\u7684\u6210\u5c31\u4e86\u3002</p><p>\u8fd9\u5c31\u662f\u201c\u8010\u745f\u6cd5\u672f\u62a4\u7f69\u201d\u3002</p><p>\u9634\u9b42\u57ce\u7ee7\u627f\u8010\u745f\u6587\u660e\u4e00\u8109\uff0c\u81ea\u7136\u4e5f\u6709\u4eba\u80fd\u5236\u4f5c\u8010\u745f\u6cd5\u672f\u62a4\u7f69\uff0c\u4f46\u8fd9\u79cd\u73cd\u8d35\u7684\u4e1c\u897f\uff0c\u743c\u6069\u4e5f\u53ea\u80fd\u5728\u4e66\u4e0a\u770b\u770b\uff0c\u4ece\u4e0d\u6562\u5962\u671b\u3002\u6ca1\u60f3\u5230\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5c45\u7136\u8fd9\u4e48\u5927\u65b9\uff0c\u81ea\u5df1\u6789\u81ea\u6574\u5929\u628a\u5b83\u6234\u5728\u624b\u4e0a\uff0c\u5374\u4e0d\u77e5\u9053\u5c45\u7136\u662f\u8fd9\u7b49\u597d\u4e1c\u897f\u3002</p><p>\u5e72\uff0c\u9001\u5c31\u9001\u4e86\uff0c\u504f\u504f\u8fd8\u641e\u5f97\u8fd9\u4e48\u795e\u79d8\uff0c\u5dee\u70b9\u4ee5\u4e3a\u662f\u5e9f\u7269\u7ed9\u6254\u4e86\u3002</p><p>\u743c\u6069\u63a5\u8fc7\u6212\u6307\uff0c\u968f\u5373\u8d77\u8eab\u544a\u8f9e\uff0c\u4ed6\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u52c9\u5f3a\u538b\u6291\u4f4f\u72c2\u559c\uff0c\u4ed4\u7ec6\u7814\u7a76\u8d77\u8fd9\u679a\u6212\u6307\u6765\u3002\u521a\u51fa\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u4ed6\u66fe\u7ecf\u8bd5\u7740\u53bb\u611f\u5e94\u5176\u4e2d\u8574\u542b\u7684\u529b\u91cf\uff0c\u7ed3\u679c\u662f\u5f92\u52b3\u65e0\u529f\uff0c\u4f46\u8fd9\u6b21\u4e0d\u540c\u4e86\uff0c\u610f\u8bc6\u987a\u5229\u5730\u6c89\u5165\uff0c\u5f88\u5feb\u5f97\u51fa\u4e86\u60f3\u8981\u7684\u7b54\u6848\u3002</p><p>\u201c\u9632\u62a4\u80fd\u91cf\u4f24\u5bb3\u2026\u2026\u6b21\u7ea7\u6cd5\u672f\u65e0\u6548\u7ed3\u754c\u2026\u2026\u8fd8\u6709\u9632\u62a4\u7bad\u77e2\uff0c\u5514\uff0c\u8fd9\u4e2a\u8010\u745f\u6cd5\u672f\u62a4\u7f69\u91cc\uff0c\u5bb9\u7eb3\u7684\u5168\u662f\u9632\u62a4\u7c7b\u578b\u7684\u6cd5\u672f\u554a\u2026\u2026\u6b21\u7ea7\u94a2\u94c1\u5b88\u536b\uff1f\u201d</p><p>\u5deb\u5e08\u8106\u5f31\uff0c\u6240\u4ee5\u5f04\u4e9b\u9632\u5fa1\u7c7b\u578b\u7684\u9b54\u6cd5\u7269\u54c1\u6765\u4fdd\u62a4\u81ea\u5df1\uff0c\u4e5f\u662f\u5f88\u6b63\u786e\u7684\u9009\u62e9\u3002\u201c\u9632\u62a4\u80fd\u91cf\u4f24\u5bb3\u201d\u80fd\u591f\u5e2e\u52a9\u5deb\u5e08\u62b5\u5fa1\u67d0\u79cd\u80fd\u91cf\uff0c\u5f53\u65e5\u5728\u70db\u5821\u5730\u7a74\u91cc\uff0c\u5e7d\u7075\u94f6\u9f99\u51b2\u5411\u6885\u83f2\u65af\uff0c\u743c\u6069\u5c31\u662f\u7528\u8fd9\u4e00\u62db\u62b5\u6d88\u4e86\u94f6\u9f99\u8eab\u4e0a\u643a\u5e26\u7684\u90e8\u5206\u8d1f\u80fd\u91cf\uff1b\u201c\u6b21\u7ea7\u6cd5\u672f\u65e0\u6548\u7ed3\u754c\u201d\u5219\u4f1a\u5728\u5deb\u5e08\u8eab\u5468\u5f62\u6210\u65e0\u5f62\u529b\u573a\uff0c\u963b\u6b62\u4e00\u4e9b\u4f4e\u9636\u6cd5\u672f\u7684\u653b\u51fb\uff1b\u201c\u9632\u62a4\u7bad\u77e2\u201d\u662f\u8ba9\u5deb\u5e08\u4e0d\u518d\u60e7\u6015\u7bad\u77e2\u7684\u8fdc\u7a0b\u653b\u51fb\uff0c\u5f53\u7136\u8fd9\u6cd5\u672f\u5bf9\u9b54\u6cd5\u7bad\u65e0\u6548\u3002</p><p>\u8fd9\u4e9b\u5012\u4e5f\u90fd\u7f62\u4e86\uff0c\u4ee5\u743c\u6069\u76ee\u524d\u7684\u80fd\u529b\uff0c\u81ea\u5df1\u4e5f\u80fd\u65bd\u5c55\u8fd9\u4e9b\u6cd5\u672f\uff0c\u4f46\u6700\u540e\u90a3\u4e2a\u201c\u6b21\u7ea7\u94a2\u94c1\u5b88\u536b\u201d\uff0c\u5374\u662f\u975e\u6bd4\u5bfb\u5e38\uff0c\u9700\u8981\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u4e94\u5c42\u624d\u80fd\u529e\u5230\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u5b66\u4f4d\u8bc4\u5b9a\u59d4\u5458\u4f1a\u4e4b\u7c7b\u7684\u673a\u6784\uff0c\u5deb\u5e08\u81ea\u7136\u4e5f\u65e0\u6240\u8c13\u9636\u7ea7\u4e4b\u5206\uff0c\u4ec5\u51ed\u89e6\u6478\u9b54\u7f51\u7684\u6df1\u6d45\u4f5c\u4e3a\u5927\u81f4\u7684\u80fd\u529b\u53c2\u8003\u3002\u6309\u7167\u901a\u884c\u7684\u8bf4\u6cd5\uff0c\u9b54\u7f51\u7684\u7b2c\u4e94\u5c42\uff0c\u548c\u7b2c\u56db\u5c42\u867d\u7136\u4ec5\u4ec5\u662f\u4e00\u6b65\u4e4b\u5dee\uff0c\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\u5374\u662f\u4e00\u4e2a\u5927\u5173\u53e3\uff0c\u80fd\u591f\u505a\u5230\uff0c\u90a3\u4e48\u4fbf\u662f\u8d28\u7684\u7a81\u7834\u548c\u98de\u8dc3\uff0c\u4ece\u6b64\u6d77\u9614\u5929\u7a7a\uff0c\u4efb\u610f\u9068\u6e38\uff1b\u53ea\u662f\u5927\u591a\u6570\u5deb\u5e08\u90fd\u51b2\u4e0d\u8fc7\u53bb\uff0c\u6b62\u6b65\u4e8e\u6b64\uff0c\u7ec8\u5176\u4e00\u751f\u4e5f\u5c31\u53ea\u80fd\u5bc2\u6ca1\u65e0\u95fb\u4e86\u3002</p><p>\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e94\u5c42\uff0c\u5c31\u53ef\u4ee5\u7b97\u662f\u901a\u5e38\u6240\u8bf4\u7684\u201c\u9ad8\u9636\u201d\u5deb\u5e08\uff0c\u5018\u82e5\u505a\u4e0d\u5230\uff0c\u90a3\u5c31\u53ea\u80fd\u7b97\u201c\u4f4e\u9636\u201d\u4e86\uff0c\u8fd9\u4e5f\u7b97\u662f\u79cd\u9ed8\u8ba4\u7684\u5212\u5206\u65b9\u6cd5\u3002</p><p>\u6211\u65e2\u6765\u6b64\u4e16\u754c\uff0c\u6b64\u751f\u4fbf\u4e0d\u80fd\u5eb8\u5eb8\u788c\u788c\uff0c\u5c31\u6b64\u57cb\u6ca1\u3002\u90a3\u4e48\uff0c\u8981\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u624d\u80fd\u8d70\u8fc7\u8fd9\u5173\u53e3\u5462\u3002</p><p>\u743c\u6069\u5c06\u6212\u6307\u91cd\u65b0\u6234\u56de\u5de6\u624b\u98df\u6307\u4e0a\uff0c\u9ed8\u7136\u601d\u91cf\u7740\uff0c\u76f4\u5230\u8fc7\u4e86\u5f88\u4e45\u624d\u56de\u8fc7\u795e\u6765\u3002\u4ed6\u8003\u8651\u7740\u4e0b\u9762\u5e94\u8be5\u600e\u4e48\u529e\uff0c\u62c9\u6c83\u514b\u8981\u81ea\u5df1\u53bb\u585e\u5c14\u627e\u4ec0\u4e48\u8428\u624e\u65af\u5766\u50ac\u503a\uff0c\u8fd9\u4e8b\u60c5\u4e14\u653e\u4e0b\u518d\u8bf4\uff0c\u76ee\u524d\u6700\u8981\u7d27\u7684\u662f\u56de\u9634\u9b42\u57ce\u590d\u547d\uff0c\u628a\u8fd9\u4e00\u8d9f\u5dee\u4f7f\u4e86\u7ed3\u4e86\u3002\u56de\u53bb\u5012\u4e5f\u4e0d\u96be\uff0c\u539f\u8def\u8fd4\u56de\u5c31\u662f\uff0c\u4e0d\u77e5\u9053\u9ed1\u8def\u662f\u4e0d\u662f\u8fd8\u88ab\u6563\u5854\u6797\u4f1a\u5c01\u7740\uff0c\u4f46\u5c31\u7b97\u5982\u6b64\u4e5f\u6ca1\u5173\u7cfb\uff0c\u81ea\u5df1\u624b\u91cc\u8fd8\u6709\u90a3\u5f20\u80fd\u5b9a\u4f4d\u7eff\u6d32\u7684\u5730\u56fe\uff0c\u518d\u4ece\u6c99\u6f20\u897f\u5317\u90e8\u7ed5\u4e2a\u5f2f\u5b50\u5c31\u662f\u4e86\u3002</p><p>\u4f46\u662f\uff0c\u6885\u83f2\u65af\u600e\u4e48\u529e\u5462\u3002</p><p>\u743c\u6069\u81ea\u7136\u60f3\u5e26\u5979\u56de\u9634\u9b42\u57ce\uff0c\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u5f88\u591a\u4e8b\u60c5\u5c31\u7792\u4e0d\u4f4f\u5979\u4e86\u3002\u522b\u7684\u5012\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u9634\u9b42\u57ce\u53ef\u5e76\u4e0d\u662f\u4ec0\u4e48\u826f\u5584\u4e4b\u5730\uff0c\u6885\u83f2\u65af\u8eab\u4e3a\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u53ea\u6015\u4e0d\u80af\u53bb\u3002</p><p>\u5bf9\u4e86\uff0c\u5979\u8fd9\u6b21\u51fa\u6765\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\uff0c\u5982\u4eca\u535a\u5f97\u4e4b\u95e8\u7684\u761f\u75ab\u662f\u89e3\u9664\u4e86\uff0c\u5143\u51f6\u4e5f\u6b63\u6cd5\u4e86\uff0c\u4f46\u795e\u5668\u5374\u7ed9\u6bc1\u4e86\uff0c\u5979\u4e5f\u5f97\u5148\u56de\u53bb\u590d\u547d\u5427\u3002\u8bb0\u5f97\u5979\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u6765\u81ea\u8ff7\u65af\u5353\u8bfa\uff0c\u4f46\u8fd9\u5730\u65b9\u5728\u54ea\u91cc\uff1f</p><p>\u743c\u6069\u53d6\u51fa\u5730\u56fe\uff0c\u4ed4\u7ec6\u7814\u7a76\u8d77\u6765\uff0c\u8d39\u4f26\u5927\u9646\u5730\u57df\u5e7f\u9614\uff0c\u57ce\u90a6\u4f17\u591a\uff0c\u8fd9\u5f20\u5730\u56fe\u53c8\u6781\u5c3d\u8be6\u5b9e\u7ec6\u81f4\u4e4b\u80fd\u4e8b\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\u7684\uff0c\u8ba9\u4eba\u770b\u4e0a\u53bb\u5c31\u773c\u775b\u53d1\u6655\u3002\u743c\u6069\u627e\u4e86\u534a\u5929\uff0c\u786e\u5b9a\u4e86\u51e0\u4e2a\u5730\u540d\uff0c\u7136\u800c\u2026\u2026</p><p>\u201c\u9760\uff0c\u201d\u743c\u6069\u6709\u4e9b\u60f3\u53d1\u6012\u4e86\uff0c\u201c\u8fd9\u4e5f\u53eb\u505a\u987a\u8def\u4e48\u2026\u2026\u8fd9\u5230\u5e95\u662f\u8c01\u987a\u8c01\u7684\u8def\u554a\uff01\u201d</p><p>\u62c9\u6c83\u514b\u8ba9\u4ed6\u53bb\u8d9f\u585e\u5c14\uff0c\u8bf4\u662f\u53cd\u6b63\u56de\u9634\u9b42\u57ce\u987a\u8def\uff0c\u743c\u6069\u5f53\u65f6\u4e5f\u4e0d\u77e5\u9053\u585e\u5c14\u5728\u54ea\u91cc\uff0c\u5e76\u6ca1\u5728\u610f\u3002\u5982\u4eca\u4e00\u770b\u5730\u56fe\u624d\u77e5\u9053\uff0c\u585e\u5c14\u662f\u9634\u9b42\u57ce\u4e1c\u8fb9\u7684\u4e00\u4e2a\u56fd\u5bb6\uff0c\u800c\u81ea\u5df1\u5982\u4eca\u8eab\u5904\u535a\u5f97\u4e4b\u95e8\uff0c\u662f\u9634\u9b42\u57ce\u7684\u897f\u5357\u8fb9\u3002\u6309\u8def\u5f84\u8d70\uff0c\u81ea\u5df1\u662f\u5e94\u8be5\u5148\u56de\u5230\u9634\u9b42\u57ce\uff0c\u7136\u540e\u7ee7\u7eed\u5f80\u4e1c\uff0c\u624d\u4f1a\u5230\u585e\u5c14\u3002\u8001\u5deb\u5996\u5c45\u7136\u6562\u8bf4\u8fd9\u53eb\u201c\u987a\u8def\u201d\uff1f\u8fd9\u90fd\u987a\u5230\u5ef6\u957f\u7ebf\u4e0a\u53bb\u4e86\u3002</p><p>\u8ff7\u65af\u5353\u8bfa\u4e5f\u5728\u9634\u9b42\u57ce\u7684\u4e1c\u90e8\uff0c\u8ddd\u79bb\u6bd4\u585e\u5c14\u5012\u8fd8\u8fd1\u4e9b\u3002</p><p>\u201c\u770b\u6837\u5b50\uff0c\u8fd9\u4e00\u8d9f\u8bf4\u4e0d\u5b9a\u8fd8\u8fd9\u5f97\u8981\u8dd1\uff0c\u201d\u743c\u6069\u601d\u5fd6\u7740\uff0c\u5fc3\u4e2d\u9690\u9690\u6709\u4e86\u8fd9\u79cd\u8bf4\u4e0d\u51fa\u7f18\u7531\u7684\u611f\u89c9\uff0c\u201c\u7b97\u4e86\uff0c\u53cd\u6b63\u90fd\u5728\u9634\u9b42\u57ce\u4e1c\u8fb9\u2026\u2026\u5148\u56de\u57ce\u590d\u547d\u518d\u8bf4\u3002\u8001\u5deb\u5996\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u542c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u600e\u4e48\u8bf4\u5427\u3002\u201d</p><p>\u81f3\u4e8e\u600e\u4e48\u548c\u6885\u83f2\u65af\u8bf4\uff0c\u743c\u6069\u51b3\u5b9a\u6682\u4e14\u5148\u653e\u4e0b\u8fd9\u4e2a\u95ee\u9898\uff0c\u7b49\u79bb\u5f00\u5deb\u5e08\u4e4b\u5893\u518d\u8bf4\u3002\u867d\u8bf4\u8001\u5deb\u5996\u4f3c\u4e4e\u5bf9\u81ea\u5df1\u6ca1\u4ec0\u4e48\u6076\u610f\uff0c\u8389\u6cd5\u513f\u7684\u5e8a\u4e0a\u529f\u592b\u4e5f\u8d8a\u6765\u8d8a\u597d\uff0c\u4f46\u743c\u6069\u5230\u5e95\u662f\u4e2a\u6d3b\u4eba\uff0c\u8fd8\u662f\u4e0d\u60f3\u5728\u8fd9\u4ea1\u7075\u4e4b\u5730\u591a\u5446\u3002\u4e07\u4e00\u8001\u5deb\u5996\u8fd9\u4e24\u5929\u53c8\u53d1\u660e\u4e86\u4ec0\u4e48\u65b0\u65b9\u6cd5\uff0c\u8981\u6293\u4eba\u6765\u505a\u8bd5\u9a8c\uff0c\u628a\u81ea\u5df1\u62ff\u53bb\u5145\u6570\uff0c\u5c82\u4e0d\u662f\u7cdf\u7cd5\u900f\u9876\u3002</p><p>\u603b\u4e4b\uff0c\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\u3002</p><p>\u6253\u5b9a\u4e86\u8fd9\u4e2a\u4e3b\u610f\uff0c\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\u4e5f\u5c31\u5bb9\u6613\u5f97\u591a\u4e86\u3002\u743c\u6069\u53bb\u627e\u6885\u83f2\u65af\uff0c\u53d1\u73b0\u5c11\u5973\u5df2\u7ecf\u8d77\u5e8a\uff0c\u4ed6\u628a\u60c5\u51b5\u7b80\u8981\u5e76\u4e14\u6390\u5934\u53bb\u5c3e\u5730\u8bf4\u4e86\u4e00\u904d\uff0c\u5c31\u8bf4\u6b64\u524d\u6885\u83f2\u65af\u6c89\u7761\u4e0d\u9192\uff0c\u6240\u4ee5\u5728\u8fd9\u91cc\u803d\u6401\uff1b\u5982\u4eca\u65e2\u7136\u5df2\u7ecf\u9192\u4e86\uff0c\u81ea\u7136\u4e5f\u5e94\u8be5\u544a\u8f9e\u4e86\u3002\u6885\u83f2\u65af\u663e\u7136\u4e5f\u4e0d\u613f\u610f\u5728\u8fd9\u79cd\u5730\u65b9\u591a\u5446\uff0c\u6240\u4ee5\u5168\u65e0\u5f02\u8bae\u3002</p><p>\u505a\u5ba2\u51e0\u5929\uff0c\u8981\u8d70\u4e86\u7406\u5f53\u548c\u4e3b\u4eba\u6253\u62db\u547c\uff0c\u7136\u800c\u62c9\u6c83\u514b\u8fd9\u8001\u5bb6\u4f19\u795e\u51fa\u9b3c\u6ca1\uff0c\u4ece\u6765\u90fd\u662f\u4ed6\u627e\u743c\u6069\uff0c\u6ca1\u6709\u743c\u6069\u627e\u4ed6\u7684\uff0c\u8fde\u8389\u6cd5\u513f\u90fd\u4e0d\u77e5\u9053\u4ed6\u6b64\u523b\u5728\u505a\u4ec0\u4e48\u3002\u4e0d\u8fc7\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u5012\u4e5f\u4e0d\u7528\u60ca\u52a8\u5deb\u5996\u5927\u4eba\uff0c\u8389\u6cd5\u513f\u4e5f\u7b97\u662f\u534a\u4e2a\u4e3b\u4eba\uff0c\u76f4\u63a5\u5411\u5979\u544a\u8f9e\u5c31\u662f\u4e86\u3002</p><p>\u8389\u6cd5\u513f\u5ba2\u6c14\u5730\u9053\u522b\uff0c\u9001\u4ed6\u4eec\u51fa\u4e86\u5deb\u5e08\u4e4b\u5893\uff0c\u6b64\u65f6\u6b63\u662f\u9ece\u660e\u7834\u6653\uff0c\u4e1c\u65b9\u5c06\u767d\u3002\u743c\u6069\u5728\u5deb\u5e08\u5854\u91cc\u95f7\u4e86\u51e0\u5929\uff0c\u51fa\u6765\u547c\u5438\u5230\u8fd9\u6e05\u6668\u7684\u65b0\u9c9c\u7a7a\u6c14\uff0c\u4e0d\u7531\u5f97\u7cbe\u795e\u4e00\u632f\u3002</p><p>\u6b63\u51c6\u5907\u542f\u7a0b\u79bb\u5f00\uff0c\u80cc\u540e\u8389\u6cd5\u513f\u7a81\u7136\u8f7b\u8f7b\u95ee\u4e86\u53e5\u8bdd\u3002</p><p>\u201c\u743c\u6069\uff0c\u201d\u5979\u5fae\u7b11\u7740\uff0c\u867d\u7136\u4f9d\u65e7\u6de1\u5b9a\u4ece\u5bb9\uff0c\u5374\u6709\u4e00\u4e1d\u63a9\u9970\u4e0d\u53bb\u7684\u843d\u5bde\uff0c\u201c\u4ee5\u540e\u2026\u2026\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u8fd8\u80fd\u6765\u770b\u770b\u6211\u4e48\uff1f\u201d</p></article>"}}}]);
