(window.webpackJsonp=window.webpackJsonp||[]).push([[1620],{1643:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u6700\u5371\u9669\u7684\u5c40\u9762",content:"<article><h2>\u7b2c\u4e8c\u8282 \u6700\u5371\u9669\u7684\u5c40\u9762</h2><p>\u743c\u6069\u7684\u6545\u4e8b\u5f88\u957f\uff0c\u7275\u6d89\u590d\u6742\uff0c\u66f2\u6298\u591a\u53d8\uff0c\u800c\u4e14\u56e0\u4e3a\u73ca\u5609\u5bf9\u5f88\u591a\u76f8\u5173\u77e5\u8bc6\u90fd\u6bd4\u8f83\u7f3a\u4e4f\uff0c\u6240\u4ee5\u8bb2\u8d77\u6765\u5c31\u66f4\u6162\u3002\u4e00\u76f4\u8fc7\u4e86\u51cc\u6668\u96f6\u70b9\uff0c\u743c\u6069\u603b\u7b97\u624d\u5927\u81f4\u8bf4\u5b8c\uff0c\u4ed6\u6000\u7591\u5176\u4e2d\u5f88\u591a\u5730\u65b9\u73ca\u5609\u6839\u672c\u5c31\u6ca1\u61c2\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5c11\u5973\u8fd8\u662f\u542c\u5f97\u5f88\u8ba4\u771f\u3002</p><p>\u81f3\u5c11\u5979\u8bb0\u4f4f\u4e86\u6240\u6709\u7684\u5973\u5b69\u5b50\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u795e\u5b50\uff1b\u51db\uff0c\u9f99\u5973\uff1b\u8389\u6cd5\u5c14\uff0c\u5438\u8840\u9b3c\uff1b\u838e\u73de\u514b\uff0c\u9b45\u9b54\uff1b\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u9ed1\u6697\u7cbe\u7075\u2014\u2014\u5c0f\u5f1f\uff0c\u6211\u771f\u5947\u602a\u4e00\u4ef6\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4f60\u9047\u5230\u7684\u5973\u5b69\u5b50\uff0c\u90fd\u662f\u8fd9\u4e48\u2026\u2026\u201d\u5979\u52aa\u529b\u5bfb\u627e\u4e86\u4e00\u4e2a\u5408\u9002\u7684\u8bcd\u6c47\uff0c\u201c\u8fd9\u4e48\u7684\u2018\u975e\u540c\u5bfb\u5e38\u2019\u5462\uff0c\u9664\u4e86\u9694\u58c1\u5bb6\u7684\u90a3\u5bf9\u59d0\u59b9\u4e4b\u5916\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u6216\u8bb8\u6211\u6709\u51c9\u5bab\u6625\u65e5\u7684\u7279\u8d28\uff1f\u201d</p><p>\u201c\u51c9\u5bab\u6625\u65e5\uff1f\u201d</p><p>\u201c\u5443\uff0c\u73a9\u7b11\uff0c\u5f00\u73a9\u7b11\u800c\u5df2\u3002\u201d</p><p>\u73ca\u5609\u4e5f\u6ca1\u6709\u8ffd\u95ee\u51c9\u5bab\u6625\u65e5\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u201c\u770b\u6765\u4f60\u771f\u5e94\u8be5\u8981\u597d\u597d\u953b\u70bc\u8eab\u4f53\u4e86\uff0c\u201d\u5979\u8bc4\u4ef7\uff0c\u201c\u4e00\u4e8c\u4e09\u56db\u4e94\uff0c\u518d\u52a0\u83ab\u5c3c\u5361\u59d0\u59b9\u4fe9\uff0c\u4e00\u5171\u662f\u4e03\u4e2a\u3002\u4e00\u5468\u6709\u5341\u5929\uff0c\u4f60\u6bcf\u5929\u966a\u4e00\u4e2a\uff0c\u9700\u8981\u4e03\u5929\uff0c\u53ea\u6709\u4e09\u5929\u80fd\u4f11\u606f\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u7b97\u9519\u4e86\uff0c\u201d\u743c\u6069\u7ea0\u6b63\uff0c\u201c\u662f\u516b\u4e2a\uff0c\u4e0d\u662f\u4e03\u4e2a\u3002\u201d</p><p>\u201c\u8fd8\u6709\u8c01\uff1f\u201d</p><p>\u201c\u8fd8\u6709\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u8fd8\u6709\u59d0\u59d0\u4f60\u554a\u3002\u201d</p><p>\u73ca\u5609\u77a5\u7740\u4ed6\uff0c\u201c\u6211\u548c\u5979\u4eec\u4e00\u6837\u5417\uff1f\u201d</p><p>\u611f\u53d7\u5230\u773c\u5149\u4e2d\u51db\u51db\u7684\u5bd2\u610f\uff0c\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u8737\u7f29\u8eab\u4f53\uff0c\u4eff\u4f5b\u60f3\u8981\u628a\u81ea\u5df1\u53d8\u77ee\u53d8\u5c0f\uff0c\u8eb2\u5f00\u59d0\u59d0\u7684\u89c6\u7ebf\u3002</p><p>\u201c\u4e0d\u7528\u8737\u4e86\uff0c\u4f60\u8fd9\u4e48\u5927\uff0c\u518d\u600e\u4e48\u8737\u4e5f\u8eb2\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u201d\u73ca\u5609\u62cd\u4e86\u62cd\u4ed6\u7684\u5927\u817f\uff0c\u201c\u5750\u597d\uff0c\u522b\u4e71\u52a8\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u4e56\u4e56\u5c65\u884c\u7740\u6795\u5934\u7684\u4f5c\u7528\uff0c\u8ba9\u59d0\u59d0\u6362\u4e86\u4e2a\u6bd4\u8f83\u8212\u670d\u7684\u59ff\u52bf\u3002\u201c\u90a3\u4f4d\u53eb\u8389\u6cd5\u5c14\u7684\u5973\u5b69\u5b50\u5f88\u6709\u610f\u601d\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u5979\u5e26\u56de\u6765\u5462\u3002\u201d</p><p>\u201c\u6211\u662f\u60f3\uff0c\u4f46\u5979\u4e0d\u613f\u610f\u5440\u3002\u201d</p><p>\u201c\u5979\u54ea\u91cc\u4e0d\u613f\u610f\uff1f\u201d\u73ca\u5609\u53cd\u95ee\uff0c\u201c\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u9664\u975e\u8111\u7b4b\u4e0d\u6b63\u5e38\uff0c\u5426\u5219\u8c01\u771f\u613f\u610f\u4f4f\u5728\u4e00\u7fa4\u4ea1\u7075\u4e2d\u95f4\uff1f\u660e\u660e\u662f\u4f60\u6ca1\u8bda\u610f\uff0c\u5bf9\u65b9\u81ea\u7136\u5c31\u8bf4\u7b97\u4e86\u3002\u201d</p><p>\u201c\u6211\u54ea\u91cc\u6ca1\u8bda\u610f\u4e86\u2026\u2026\u201d</p><p>\u201c\u90a3\u5979\u95ee\u4f60\u662f\u4e0d\u662f\u5728\u8ffd\u6c42\u5979\u7684\u65f6\u5019\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u2018\u662f\u2019\u5462\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\uff0c\u65e0\u8a00\u4ee5\u5bf9\u3002</p><p>\u73ca\u5609\u8bf4\u5f97\u6ca1\u9519\uff0c\u5f53\u65f6\u5728\u70db\u5821\uff0c\u4ed6\u53d1\u51fa\u9080\u8bf7\uff0c\u8389\u6cd5\u5c14\u539f\u672c\u5e94\u8be5\u662f\u5df2\u7ecf\u6709\u6240\u52a8\u5fc3\u7684\u3002\u4e4b\u6240\u4ee5\u62d2\u7edd\uff0c\u5173\u952e\u5c31\u5728\u4e8e\u6700\u540e\u90a3\u53e5\u8bdd\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u743c\u6069\uff1f\u7b97\u662f\u4f60\u5728\u8ffd\u6c42\u6211\u5417\uff1f\u201d</p><p>\u5982\u679c\u743c\u6069\u5f53\u65f6\u8bf4\u201c\u662f\u201d\uff0c\u90a3\u4e48\u8389\u6cd5\u5c14\u5e94\u8be5\u6709\u5f88\u5927\u7684\u53ef\u80fd\u4f1a\u70b9\u5934\u7b54\u5e94\uff0c\u4f46\u4ed6\u5374\u72b9\u8c6b\u4e86\uff0c\u7ed9\u51fa\u53e6\u5916\u7684\u7b54\u6848\uff0c\u4e00\u4e2a\u5ba2\u6c14\u3001\u793c\u8c8c\u4f46\u5374\u9690\u9690\u5e26\u7740\u758f\u8fdc\u7684\u7b54\u6848\u3002\u4ee5\u8389\u6cd5\u5c14\u7684\u806a\u660e\u548c\u81ea\u5c0a\uff0c\u81ea\u7136\u4e0d\u5c51\u4e8e\u63a5\u53d7\u8fd9\u79cd\u201c\u5584\u610f\u201d\uff0c\u62d2\u7edd\u4fbf\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002</p><p>\u743c\u6069\u4e0d\u662f\u7b28\u86cb\uff0c\u4e0d\u81f3\u4e8e\u8fde\u5973\u5b69\u5b50\u7684\u8fd9\u70b9\u5fc3\u601d\u90fd\u4e0d\u61c2\uff0c\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u77e5\u9053\u4e0d\u7b49\u4e8e\u5c31\u80fd\u505a\u5f97\u5230\u3002\u4ed6\u5f88\u6e05\u695a\u8389\u6cd5\u5c14\u5f53\u65f6\u5e0c\u671b\u542c\u5230\u7684\u56de\u7b54\u662f\u4ec0\u4e48\uff0c\u7136\u800c\u4ed6\u6ca1\u6cd5\u8bf4\u51fa\u53e3\u2026\u2026</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\uff0c\u5c0f\u5f1f\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u5e94\u8be5\u633a\u559c\u6b22\u5979\u7684\u5440\uff0c\u8bf4\u8ffd\u6c42\u5979\u6709\u4ec0\u4e48\u8bf4\u4e0d\u51fa\u53e3\u7684\u3002\u201d</p><p>\u201c\u4e0d\u4e00\u6837\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u540c\u6837\u7684\u8bcd\uff0c\u5728\u4e0d\u540c\u7684\u65f6\u5019\uff0c\u548c\u4e0d\u540c\u7684\u4eba\u8bf4\uff0c\u90a3\u662f\u6709\u4e0d\u540c\u610f\u601d\u7684\u3002\u6211\u5982\u679c\u8bf4\u8ffd\u6c42\u5979\uff0c\u90a3\u610f\u601d\u5e94\u8be5\u5c31\u662f\u8bf4\u6211\u559c\u6b22\u5979\uff0c\u628a\u5979\u770b\u5f97\u6bd4\u522b\u4eba\u90fd\u91cd\u8981\uff0c\u72ec\u4e00\u65e0\u4e8c\u2014\u2014\u81f3\u5c11\u662f\u5e26\u6709\u8fd9\u6837\u7684\u610f\u5473\u5728\u5176\u4e2d\u3002\u4f46\u5b9e\u9645\u4e0a\u2026\u2026\u5b9e\u9645\u4e0a\u5e76\u4e0d\u662f\u8fd9\u6837\u3002\u201d</p><p>\u201c\u4f46\u4f60\u53ef\u4ee5\u8bf4\u662f\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5973\u5b69\u5b50\u672c\u6765\u5c31\u662f\u5e94\u8be5\u8981\u54c4\u7684\u3002\u522b\u544a\u8bc9\u6211\u8bf4\uff0c\u4f60\u4ece\u6765\u6ca1\u9a97\u8fc7\u5973\u5b69\u5b50\u3002\u201d</p><p>\u201c\u9a97\u8fc7\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u800c\u4e14\u9a97\u8fc7\u5f88\u591a\uff0c\u4f46\u5979\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u6211\u6ca1\u529e\u6cd5\u9a97\u5979\uff0c\u4e0d\u60f3\u5bf9\u5979\u8bf4\u8c0e\u3002\u201d</p><p>\u201c\u4e0d\u60f3\u9a97\u5979\uff0c\u90a3\u5c31\u5766\u767d\u627f\u8ba4\u554a\u3002\u4f60\u8fd8\u6709\u5176\u4ed6\u559c\u6b22\u7684\u5973\u5b69\u5b50\uff0c\u4f46\u4f60\u4e5f\u559c\u6b22\u5979\uff0c\u95ee\u5979\u613f\u4e0d\u613f\u610f\u63a5\u53d7\u2014\u2014\u8fd9\u4e48\u8bf4\u4e0d\u884c\u4e48\uff1f\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\uff0c\u522b\u53d6\u7b11\u6211\u5566\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u6211\u5982\u679c\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u7b97\u662f\u4fae\u8fb1\u5979\u5462\uff0c\u8fd8\u662f\u5728\u5632\u7b11\u81ea\u5df1\u5462\u3002\u201d</p><p>\u73ca\u5609\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u7ee7\u7eed\u8ffd\u7a76\uff0c\u201c\u827e\u5f25\u8587\u53bb\u4e86\u963f\u683c\u62c9\u9686\uff1f\u201d\u5979\u8f6c\u79fb\u4e86\u8bdd\u9898\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u751f\u4f60\u7684\u6c14\u4e86\uff1f\u201d</p><p>\u201c\u5443\uff0c\u5e94\u8be5\u4e0d\u662f\u5427\uff0c\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u592a\u786e\u5b9a\u5730\u8bf4\uff0c\u201c\u5979\u8bf4\u6709\u4e8b\u2026\u2026\u201d</p><p>\u201c\u5f88\u6025\u7684\u4e8b\u60c5\uff1f\u8feb\u5728\u7709\u776b\uff0c\u5206\u79d2\u5fc5\u4e89\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e5f\u4e0d\u81f3\u4e8e\uff0c\u9f99\u72c2\u7684\u53d1\u4f5c\u6bd5\u7adf\u8fd8\u6709\u51e0\u4e2a\u6708\u5462\u3002\u201d</p><p>\u201c\u90a3\u5979\u4e3a\u4ec0\u4e48\u4e0d\u518d\u7b49\u4e24\u5929\uff0c\u81f3\u5c11\u7b49\u4f60\u7684\u751f\u65e5\u8fc7\u4e86\u518d\u8d70\u5462\uff1f\u201d\u73ca\u5609\u8f7b\u58f0\u8bf4\uff0c\u201c\u597d\u50cf\u4e5f\u6ca1\u6709\u4e3a\u4f60\u51c6\u5907\u751f\u65e5\u793c\u7269\u5427\u3002\u6216\u8005\uff0c\u5979\u5fd8\u4e86\uff1f\u201d</p><p>\u201c\u59d0\u59d0\uff01\u201d</p><p>\u743c\u6069\u7684\u53e3\u6c14\u6709\u4e9b\u57cb\u6028\uff0c\u73ca\u5609\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u201c\u600e\u4e48\uff0c\u4e0d\u9ad8\u5174\u4e86\uff1f\u201d\u5979\u53cd\u95ee\uff0c\u201c\u4f60\u5728\u5916\u9762\u5230\u5904\u52fe\u5f15\u5973\u5b69\u5b50\uff0c\u505a\u4e86\u90a3\u4e48\u591a\u8ba9\u4eba\u751f\u6c14\u7684\u4e8b\u60c5\uff0c\u6709\u6ca1\u6709\u8003\u8651\u8fc7\u59d0\u59d0\u7684\u611f\u53d7\uff1f\u5982\u4eca\u56de\u5230\u5bb6\u6765\uff0c\u59d0\u59d0\u8bf4\u51e0\u53e5\uff0c\u4f60\u5c31\u53d7\u4e0d\u4e86\u4e86\uff1f\u201d</p><p>\u201c\u2026\u2026\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u7b97\u5566\uff0c\u201d\u73ca\u5609\u8212\u5c55\u8eab\u4f53\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u65e9\u70b9\u7761\u5427\uff0c\u660e\u5929\u8fd8\u6709\u4e8b\u60c5\u5462\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u8eba\u4e0b\u6765\uff0c\u5c06\u59d0\u59d0\u62b1\u5728\u6000\u4e2d\uff0c\u73ca\u5609\u6795\u5728\u4ed6\u7684\u81c2\u5f2f\u91cc\uff0c\u8138\u8d34\u7740\u5f1f\u5f1f\u7684\u80f8\u53e3\u3002\u201c\u5bf9\u4e86\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u95ed\u7740\u773c\u775b\uff0c\u4eff\u4f5b\u4f4e\u58f0\u5462\u5583\uff0c\u53e3\u9f7f\u6709\u4e9b\u4e0d\u592a\u6e05\u695a\uff0c\u201c\u4f60\u8fd9\u6b21\u51fa\u95e8\uff0c\u9047\u5230\u8fc7\u7684\u6700\u5371\u9669\u7684\u5c40\u9762\uff0c\u662f\u54ea\u4e00\u6b21\uff1f\u201d</p><p>\u201c\u6700\u5371\u9669\u7684\u5c40\u9762\uff1f\u201d</p><p>\u743c\u6069\u56de\u5fc6\u4e86\u4e00\u4e0b\uff0c\u81ea\u5df1\u8fd9\u4e00\u5e74\u591a\u6765\u56db\u5904\u5954\u6ce2\uff0c\u8981\u8bf4\u5371\u9669\u786e\u5b9e\u906d\u9047\u4e86\u4e0d\u5c11\uff0c\u751f\u6b7b\u60ac\u4e8e\u4e00\u7ebf\u7684\u60c5\u5f62\u90fd\u6709\u597d\u51e0\u6b21\u3002\u4f8b\u5982\u5728\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u8fdb\u653b\u7b2c\u56db\u5bb6\u65cf\u7684\u65f6\u5019\uff0c\u4e00\u8def\u5371\u9669\u91cd\u91cd\uff0c\u5176\u95f4\u4e2d\u4e86\u5bf9\u65b9\u5deb\u5e08\u7684\u4e00\u8bb0\u6697\u591c\u629a\u6478\uff0c\u8981\u4e0d\u662f\u6709\u5f71\u706b\u5728\uff0c\u4f30\u8ba1\u5f53\u573a\u5c31\u6302\u4e86\uff1b\u5728\u4f0a\u5361\u6c99\u57ce\u7684\u65f6\u5019\uff0c\u7f57\u4e1d\u4ee5\u4f0a\u8389\u96c5\u4e3a\u5bb9\u5668\u5723\u8005\u964d\u4e34\uff0c\u9669\u4e9b\u88ab\u62d6\u8fdb\u5979\u7684\u6df1\u5751\u9b54\u7f51\u91cc\u53bb\uff1b\u5728\u6df1\u6e0a\u65ad\u57df\u9547\u7684\u65f6\u5019\uff0c\u9047\u4e0a\u8428\u9a6c\u65af\u7279\uff0c\u8001\u5deb\u5996\u6293\u4e86\u6885\u83f2\u65af\u505a\u4eba\u8d28\uff0c\u743c\u6069\u8bbe\u8ba1\u53cd\u51fb\uff0c\u4fa5\u5e78\u5f97\u624b\uff0c\u4e5f\u7b97\u662f\u4e5d\u6b7b\u4e00\u751f\u3002\u5982\u6b64\u79cd\u79cd\uff0c\u53ef\u4ee5\u5217\u4e3e\u4e00\u5806\u51fa\u6765\uff0c\u4f46\u6240\u6709\u7684\u8fd9\u4e9b\uff0c\u4f3c\u4e4e\u90fd\u8fd8\u7b97\u4e0d\u4e0a\u201c\u6700\u201d\u5371\u9669\u3002</p><p>\u201c\u6700\u5371\u9669\u7684\u5c40\u9762\uff0c\u5e94\u8be5\u662f\u5728\u963f\u65af\u5361\u7279\u62c9\u90a3\u6b21\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5f53\u65f6\u6211\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u6293\u4e86\u90a3\u4e2a\u82cf\u4f26\u7267\u5e08\uff0c\u7ed3\u679c\u88ab\u827e\u5f25\u8587\u5835\u5728\u95e8\u53e3\u2026\u2026\u59d0\u59d0\u4f60\u522b\u7b11\u8bdd\uff0c\u6211\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u80cc\u540e\u90fd\u8fd8\u9690\u9690\u5192\u51b7\u6c57\uff0c\u5fc3\u6709\u4f59\u60b8\u3002\u201d</p><p>\u201c\u90a3\u6b21\u554a\uff0c\u201d\u73ca\u5609\u542b\u542b\u7cca\u7cca\u5730\u8bf4\uff0c\u201c\u90a3\u6b21\u4f60\u4e0d\u662f\u9a97\u8fc7\u53bb\u4e86\u4e48\u3002\u201d</p><p>\u201c\u6025\u4e2d\u751f\u667a\uff0c\u4fa5\u5e78\u8eb2\u8fc7\uff0c\u201d\u743c\u6069\u82e6\u7b11\uff0c\u201c\u5982\u679c\u80fd\u591f\u9009\u62e9\u7684\u8bdd\uff0c\u6211\u5b81\u53ef\u53bb\u548c\u8428\u9a6c\u65af\u7279\u51b3\u6597\uff0c\u4e5f\u4e0d\u60f3\u518d\u9047\u5230\u8fd9\u79cd\u60c5\u51b5\u4e86\u3002\u201d</p><p>\u201c\u770b\u4f60\u8bf4\u7684\uff0c\u827e\u5f25\u8587\u6709\u90a3\u4e48\u53ef\u6015\u4e48\uff0c\u201d\u73ca\u5609\u683c\u683c\u5a07\u7b11\uff0c\u201c\u743c\u6069\uff0c\u90a3\u59d0\u59d0\u95ee\u4e2a\u95ee\u9898\u554a\u3002\u5047\u5982\u5f53\u65f6\u4e0d\u662f\u827e\u5f25\u8587\u627e\u4e0a\u95e8\u6765\uff0c\u662f\u59d0\u59d0\u7684\u8bdd\uff0c\u4f60\u4f1a\u5bb3\u6015\u4e48\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u4e0d\u4f1a\u5427\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u7684\u58f0\u97f3\u4e2d\uff0c\u4e00\u7f15\u5bd2\u610f\u51db\u8d77\u3002</p><p>\u201c\u56e0\u4e3a\u59d0\u59d0\u5e94\u8be5\u4e0d\u4f1a\u56e0\u4e3a\u6211\u505a\u8fd9\u79cd\u4e8b\u60c5\u800c\u751f\u6c14\u5427\uff0c\u81f3\u5c11\u4e0d\u4f1a\u7279\u522b\u751f\u6c14\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u548c\u827e\u5f25\u8587\u4e0d\u4e00\u6837\u561b\u3002\u201d</p><p>\u201c\u90a3\u5982\u679c\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u59d0\u59d0\u77e5\u9053\u4e86\u4f1a\u7279\u522b\u7279\u522b\u751f\u6c14\uff0c\u4f46\u4f60\u8fd8\u662f\u5077\u5077\u505a\u4e86\u3002\u73b0\u5728\u773c\u770b\u8981\u88ab\u59d0\u59d0\u53d1\u73b0\u4e86\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u5f88\u5bb3\u6015\u554a\u3002\u201d</p><p>\u201c\u5f53\u7136\u4f1a\u3002\u201d</p><p>\u201c\u4f1a\u5bb3\u6015\u5230\u4ec0\u4e48\u7a0b\u5ea6\u5462\uff1f\u201d</p><p>\u201c\u4f1a\u2026\u2026\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f1a\u5bb3\u6015\u5f97\u5e0c\u671b\u6b7b\u6389\u5427\u3002\u201d</p><p>\u201c\u5bb3\u6015\u5f97\u5e0c\u671b\u6b7b\u6389\uff1f\u201d\u73ca\u5609\u4e0d\u89e3\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u53bb\u9762\u5bf9\uff0c\u53ea\u6709\u6b7b\u6389\u624d\u80fd\u9003\u907f\u3002\u201d</p><p>\u73ca\u5609\u5657\u55e4\u4e00\u7b11\uff0c\u201c\u60f3\u5f97\u7f8e\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u743c\u6069\u4ee5\u4e3a\u73ca\u5609\u8fd8\u6709\u4e0b\u6587\uff0c\u7b49\u4e86\u534a\u5929\u6ca1\u53cd\u5e94\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53d1\u73b0\u59d0\u59d0\u5df2\u7ecf\u7761\u7740\uff0c\u5747\u5300\u7684\u547c\u5438\u58f0\u5728\u5bc2\u9759\u623f\u95f4\u91cc\u542c\u8d77\u6765\u683c\u5916\u5b89\u8be6\uff0c\u6de1\u6de1\u7684\u4f53\u9999\u6c81\u5165\u5fc3\u813e\uff0c\u6e05\u5e7d\u4e2d\u5e26\u7740\u4e00\u4e1d\u4e1d\u51b0\u751c\uff0c\u662f\u9634\u5f71\u5170\u7684\u5473\u9053\u3002\u73ca\u5609\u5bb9\u989c\u79c0\u4e3d\uff0c\u4e94\u5b98\u6e05\u6670\uff0c\u5728\u9ed1\u6697\u4e2d\u770b\u8d77\u6765\u9762\u90e8\u8f6e\u5ed3\u5c24\u5176\u9c9c\u660e\uff0c\u5634\u89d2\u4e0a\u7fd8\u626c\u8d77\uff0c\u542b\u7740\u6d45\u6d45\u7b11\u610f\uff0c\u795e\u60c5\u6e29\u5a49\u606c\u9759\uff0c\u7709\u5b87\u4e4b\u95f4\u5374\u9690\u9690\u900f\u7740\u521a\u5f3a\u575a\u6bc5\uff0c\u6b63\u662f\u8fd9\u79cd\u59a9\u5a9a\u4e0e\u82f1\u6b66\u517c\u5177\u7684\u6c14\u8d28\uff0c\u8ba9\u743c\u6069\u6c89\u8ff7\u81f3\u4eca\uff0c\u604b\u604b\u4e0d\u5fd8\u3002</p><p>\u4ed6\u4f4e\u4e0b\u5934\uff0c\u5728\u73ca\u5609\u7684\u5507\u4e0a\u8f7b\u8f7b\u4eb2\u543b\uff0c\u7136\u540e\u4e5f\u6c89\u6c89\u7761\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u743c\u6069\u9192\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u73ca\u5609\u5df2\u7ecf\u7a7f\u597d\u8863\u670d\uff0c\u6b63\u5750\u5728\u68b3\u5986\u53f0\u524d\u6253\u626e\uff0c\u7a97\u5916\u624d\u521a\u521a\u53d1\u767d\uff0c\u770b\u770b\u5e8a\u5934\u7684\u6c99\u6f0f\uff0c\u624d\u521a\u8fc7\u516d\u70b9\u949f\u800c\u5df2\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u5e72\u561b\u8d77\u8fd9\u4e48\u65e9\uff0c\u201d\u4ed6\u542b\u7cca\u4e0d\u6e05\u5730\u8bf4\uff0c\u201c\u518d\u591a\u7761\u4f1a\u5427\u3002\u201d</p><p>\u201c\u6211\u4e60\u60ef\u4e86\u8fd9\u65f6\u5019\u9192\uff0c\u201d\u73ca\u5609\u8d70\u8fc7\u6765\uff0c\u6e29\u67d4\u629a\u6478\u7740\u4ed6\u7684\u8138\uff0c\u201c\u4f60\u7ee7\u7eed\u7761\uff0c\u65e9\u6668\u597d\u4e86\u6211\u6765\u53eb\u4f60\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u5176\u5b9e\u4e5f\u8be5\u8d77\u5e8a\u4e86\uff0c\u4f46\u5982\u4eca\u56de\u5230\u5bb6\u4e2d\uff0c\u7761\u5728\u59d0\u59d0\u7684\u5e8a\u4e0a\uff0c\u76d6\u7740\u59d0\u59d0\u7684\u6bdb\u6bef\uff0c\u6574\u4e2a\u4eba\u90fd\u88ab\u73ca\u5609\u7684\u6c14\u606f\u5305\u88f9\u7740\uff0c\u4fbf\u6709\u4e9b\u820d\u4e0d\u5f97\u79bb\u5f00\u3002\u8d56\u4e86\u4e00\u4f1a\uff0c\u73ca\u5609\u4e0b\u697c\u53bb\u51c6\u5907\u65e9\u6668\uff0c\u743c\u6069\u4e5f\u722c\u4e86\u8d77\u6765\uff0c\u7a7f\u8863\u6d17\u6f31\uff0c\u540c\u65f6\u5f00\u59cb\u6bcf\u5929\u4f8b\u884c\u7684\u51c6\u5907\u9b54\u6cd5\u3002</p><p>\u65e9\u6668\u7684\u65f6\u5019\uff0c\u743c\u6069\u95ee\u8d77\u73ca\u5609\u6709\u5173\u97f3\u4e50\u5b66\u9662\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u4f60\u8d70\u4e86\u4ee5\u540e\uff0c\u6211\u4e00\u4e2a\u4eba\u5728\u5bb6\u91cc\uff0c\u53c8\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u95f2\u5f97\u65e0\u804a\uff0c\u201d\u73ca\u5609\u89e3\u91ca\uff0c\u201c\u540e\u6765\u5728\u53bb\u5e74\u6708\u4eae\u8282\u7684\u65f6\u5019\uff0c\u6211\u53bb\u795e\u6bbf\u53c2\u4e0e\u5e86\u5178\uff0c\u88ab\u97f3\u4e50\u5b66\u9662\u7684\u4e00\u4f4d\u6559\u6388\u770b\u4e2d\uff0c\u529d\u6211\u8fdb\u5b66\u9662\u5b66\u4e60\u3002\u6211\u60f3\u5b66\u70b9\u4e1c\u897f\u4e0d\u9519\uff0c\u4e5f\u6b63\u597d\u6253\u53d1\u65f6\u95f4\uff0c\u5c31\u7b54\u5e94\u4e86\u3002\u201d</p><p>\u6708\u4eae\u8282\u662f\u838e\u5c14\u6559\u4f1a\u7684\u5723\u65e5\uff0c\u6bcf\u5e74\u7684\u8fd9\u4e2a\u65f6\u5019\uff0c\u7267\u5e08\u4eec\u90fd\u8981\u4e3e\u884c\u540d\u4e3a\u201c\u5149\u660e\u7ec8\u7ed3\u201d\u7684\u76db\u5927\u4eea\u5f0f\u53d6\u60a6\u5973\u795e\uff0c\u97f3\u4e50\u6f14\u594f\u81ea\u7136\u662f\u5fc5\u4e0d\u53ef\u5c11\u3002\u73ca\u5609\u6b64\u524d\u968f\u8299\u8389\u5a05\u53c2\u52a0\u8fc7\u51e0\u6b21\u6559\u4f1a\u7684\u5c0f\u89c4\u6a21\u5e86\u5178\uff0c\u5c55\u73b0\u51fa\u4e86\u4f18\u79c0\u7684\u97f3\u4e50\u5929\u8d4b\uff0c\u88ab\u5b89\u6392\u8fdb\u4e86\u4e50\u961f\u5439\u594f\u957f\u7b1b\uff0c\u6070\u597d\u88ab\u524d\u6765\u53c2\u52a0\u5e86\u5178\u7684\u97f3\u4e50\u5b66\u9662\u6559\u6388\u770b\u4e2d\uff0c\u6167\u773c\u8bc6\u91d1\uff0c\u63a8\u8350\u5979\u8fdb\u4e86\u5b66\u9662\u3002</p><p>\u97f3\u4e50\u5b66\u9662\u548c\u5deb\u5e08\u5b66\u9662\u4e0d\u540c\uff0c\u89c4\u77e9\u76f8\u5bf9\u5bbd\u677e\u5f88\u591a\uff0c\u65e0\u9700\u4f4f\u6821\uff0c\u4e0a\u8bfe\u65f6\u95f4\u662f\u4e0a\u5348\u5341\u70b9\u5230\u4e0b\u5348\u56db\u70b9\uff0c\u5348\u9910\u662f\u5b66\u9662\u514d\u8d39\u4f9b\u5e94\u3002\u6bcf\u5468\u4e00\u81f3\u5468\u516b\u4e0a\u8bfe\uff0c\u5468\u4e5d\u548c\u5468\u5341\u4e24\u5929\u4f11\u606f\u2014\u2014\u5176\u5b9e\u8fd9\u4e9b\u90fd\u53ea\u662f\u7eb8\u9762\u4e0a\u7684\u89c4\u77e9\uff0c\u8fdf\u5230\u3001\u65e9\u9000\u3001\u65f7\u8bfe\u4e00\u6982\u6ca1\u5173\u7cfb\uff0c\u6821\u65b9\u5e76\u4e0d\u5f3a\u5236\u4f60\u5b66\u4e60\uff0c\u53cd\u6b63\u4ed6\u4eec\u53c8\u4e0d\u6536\u5b66\u8d39\u3002\u4e0d\u8fc7\u73ca\u5609\u662f\u597d\u5b66\u751f\uff0c\u81ea\u4ece\u5165\u6821\u4ee5\u6765\uff0c\u4ece\u65e0\u7f3a\u52e4\u8bb0\u5f55\uff0c\u582a\u79f0\u6a21\u8303\u3002</p><p>\u201c\u90a3\u59d0\u59d0\u5b66\u7684\u662f\u957f\u7b1b\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u73ca\u5609\u70b9\u70b9\u5934\uff0c\u201c\u8bd5\u8fc7\u5f88\u591a\u79cd\u4e50\u5668\uff0c\u76f8\u6bd4\u8d77\u6765\u6700\u559c\u6b22\u957f\u7b1b\uff0c\u800c\u4e14\u6559\u6388\u4eec\u4e5f\u90fd\u8fd9\u4e48\u8bf4\uff0c\u8bf4\u6211\u5728\u957f\u7b1b\u4e0a\u6781\u6709\u5929\u8d4b\uff0c\u201d\u5979\u6709\u4e9b\u7f9e\u6da9\u5730\u8138\u7ea2\uff0c\u201c\u6211\u81ea\u5df1\u4e5f\u8fd9\u4e48\u89c9\u5f97\uff0c\u957f\u7b1b\u63e1\u5728\u624b\u4e2d\uff0c\u5c31\u6709\u79cd\u7279\u522b\u719f\u6089\u7684\u611f\u89c9\uff0c\u5f88\u591a\u7279\u522b\u6df1\u5965\u7684\u66f2\u8c31\uff0c\u540c\u5b66\u5f80\u5f80\u8981\u5b66\u4e0a\u51e0\u4e2a\u661f\u671f\uff0c\u6211\u53ea\u8981\u626b\u4e00\u773c\u5c31\u80fd\u6d41\u7545\u5730\u5439\u594f\u51fa\u6765\uff0c\u5206\u6beb\u4e0d\u9519\uff1b\u5f88\u591a\u8270\u6df1\u7684\u6280\u5de7\uff0c\u6211\u660e\u660e\u4ece\u6ca1\u7ec3\u8fc7\uff0c\u4f46\u4e00\u5b66\u5c31\u901a\uff0c\u4e00\u8bf4\u5c31\u61c2\uff0c\u5c31\u597d\u50cf\u2026\u2026\u5c31\u597d\u50cf\u751f\u6765\u5c31\u4f1a\u4f3c\u7684\u3002\u201d</p><p>\u201c\u8fd9\u53eb\u2018\u5bbf\u6167\u2019\u3002\u201d\u743c\u6069\u8bf4\uff0c\u7528\u7684\u81ea\u7136\u662f\u6c49\u8bed\u3002</p><p>\u201c\u5bbf\u6167\uff1f\u201d\u73ca\u5609\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\u7684\u53d1\u97f3\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4f0a\u739b\u65af\u5361\u8bed\u91cc\u7684\u4e00\u4e2a\u8bcd\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u610f\u601d\u5c31\u662f\u6307\u4e0e\u751f\u4ff1\u6765\u7684\u5929\u8d4b\u3002\u4ed6\u4eec\u8fd8\u6709\u4e00\u53e5\u8c1a\u8bed\uff0c\u8bf4\u2018\u4e66\u5230\u4eca\u751f\u8bfb\u5df2\u8fdf\u2019\uff0c\u4e5f\u662f\u6307\u8fd9\u4e2a\u610f\u601d\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u548c\u5f1f\u5f1f\u8c08\u8d77\u81ea\u5df1\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u73ca\u5609\u81ea\u7136\u5174\u81f4\u9887\u9ad8\uff0c\u53c8\u8bf4\u4e86\u4e9b\u5b66\u6821\u91cc\u7684\u60c5\u51b5\uff0c\u201c\u5bf9\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4ef2\u51ac\u8282\u7684\u65f6\u5019\uff0c\u5b66\u6821\u91cc\u4f1a\u4e3e\u884c\u671f\u672b\u8003\u8bd5\uff0c\u5230\u65f6\u5019\u8bb0\u5f97\u6765\u7ed9\u59d0\u59d0\u52a0\u6cb9\u54e6\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u59d0\u59d0\u4e00\u5b9a\u4f1a\u62ff\u7b2c\u4e00\u7684\u3002\u201d</p><p>\u73ca\u5609\u751c\u751c\u7b11\u7740\uff0c\u4f4e\u5934\u53bb\u5207\u725b\u6392\uff0c\u743c\u6069\u72b9\u8c6b\u4e86\u4e0b\uff0c\u6700\u7ec8\u8fd8\u662f\u628a\u95ee\u9898\u95ee\u4e86\u51fa\u6765\uff0c\u201c\u59d0\u59d0\uff0c\u4f60\u4eec\u5b66\u6821\u91cc\uff0c\u6709\u5173\u4e8e\u541f\u6e38\u8bd7\u4eba\u8fd9\u65b9\u9762\u7684\u8bfe\u7a0b\u6216\u8005\u8bad\u7ec3\u4e48\uff1f\u201d</p><p>\u201c\u541f\u6e38\u8bd7\u4eba\uff1f\u201d\u73ca\u5609\u6014\u4e86\u6014\uff0c\u5979\u5f53\u7136\u4e5f\u662f\u61c2\u8fd9\u4e2a\u8bcd\u7684\u610f\u601d\u7684\uff0c\u201c\u597d\u50cf\u6ca1\u6709\u5427\uff0c\u90a3\u5e94\u8be5\u5c5e\u4e8e\u5deb\u5e08\u5b66\u9662\u7684\u8bfe\u7a0b\u624d\u5bf9\uff0c\u6211\u4eec\u53ea\u662f\u6559\u5f88\u5e73\u5e38\u7684\u97f3\u4e50\uff0c\u4e0d\u6d89\u53ca\u9b54\u6cd5\u7684\u3002\u201d</p><p>\u201c\u6ca1\u6709\u4e48\u3002\u201d\u743c\u6069\u6709\u4e9b\u5931\u671b\uff0c\u4ece\u8299\u857e\u72c4\u53e3\u4e2d\uff0c\u4ed6\u5f97\u77e5\u838e\u5c14\u6559\u4f1a\u4e2d\u6709\u4e00\u4f4d\u591c\u89c6\u8005\u8bc4\u4ef7\u73ca\u5609\u6709\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\u7684\u5929\u8d4b\uff0c\u539f\u672c\u662f\u62b1\u4e86\u5f88\u5927\u671f\u5f85\u7684\uff0c\u5e0c\u671b\u59d0\u59d0\u80fd\u591f\u5728\u8fd9\u65b9\u9762\u6709\u6240\u53d1\u5c55\u3002\u7136\u800c\u5982\u4eca\u770b\u6765\uff0c\u97f3\u4e50\u5b66\u9662\u5e76\u6ca1\u6709\u8fd9\u65b9\u9762\u7684\u57f9\u8bad\u548c\u6559\u80b2\uff0c\u73ca\u5609\u867d\u7136\u5df2\u7ecf\u5b66\u4e60\u4e86\u4e00\u5e74\uff0c\u4f46\u638c\u63e1\u7684\u53ea\u662f\u201c\u6b63\u5e38\u201d\u7684\u97f3\u4e50\u6280\u80fd\uff0c\u5e76\u4e0d\u6d89\u53ca\u8d85\u81ea\u7136\u7684\u9b54\u6cd5\u8303\u7574\u3002</p><p>\u541f\u6e38\u8bd7\u4eba\u8981\u6c42\u6781\u9ad8\u7684\u97f3\u4e50\u5929\u8d4b\uff0c\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u5e76\u4e0d\u662f\u6709\u4e86\u4e00\u6d41\u7684\u97f3\u4e50\u5929\u8d4b\uff0c\u5c31\u4e00\u5b9a\u80fd\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\uff0c\u9006\u547d\u9898\u662f\u672a\u5fc5\u6210\u7acb\u7684\u3002\u800c\u4e14\u5c31\u7b97\u6709\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\u7684\u6f5c\u8d28\uff0c\u90a3\u4e5f\u5f97\u8981\u5f15\u5bfc\uff0c\u8981\u8bad\u7ec3\u7684\uff0c\u518d\u5b8c\u7f8e\u7684\u749e\u7389\uff0c\u4e5f\u5f97\u9002\u5f53\u96d5\u7422\u624d\u80fd\u6210\u5668\u2026\u2026\u4f46\u95ee\u9898\u662f\uff0c\u73b0\u5728\u8fde\u600e\u4e48\u5165\u95e8\u90fd\u4e0d\u77e5\u9053\u554a\u3002</p><p>\u5982\u679c\u8bf4\u6b63\u5e38\u7684\u5deb\u5e08\u6559\u5b66\uff0c\u743c\u6069\u90fd\u80fd\u52c9\u5f3a\u62c5\u4efb\uff0c\u4ed6\u597d\u6b79\u4e5f\u662f\u9ad8\u9636\u5deb\u5e08\u4e86\uff0c\u867d\u7136\u9b54\u7f51\u8fdb\u5883\u901f\u5ea6\u592a\u5feb\uff0c\u6709\u201c\u62d4\u82d7\u52a9\u957f\u201d\u7684\u5acc\u7591\uff0c\u6839\u57fa\u5e95\u8574\u975e\u5e38\u4e0d\u7262\u56fa\uff0c\u4ec5\u4ee5\u638c\u63e1\u7684\u6cd5\u672f\u6765\u8bf4\uff0c\u4ed6\u8fde\u81ea\u5df1\u4e13\u4e1a\u7cbe\u7814\u7684\u53d8\u5316\u5b66\u6d3e\u90fd\u8fd8\u6ca1\u5b66\u5168\uff0c\u66f4\u522b\u63d0\u5176\u4ed6\u5b66\u6d3e\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u743c\u6069\u603b\u662f\u7ecf\u53d7\u8fc7\u4e25\u683c\u7cfb\u7edf\u7684\u8bad\u7ec3\uff0c\u662f\u79d1\u73ed\u51fa\u751f\uff0c\u8981\u5f15\u5bfc\u521d\u5b66\u8005\u5165\u95e8\u8fd8\u662f\u6ca1\u95ee\u9898\u7684\u3002\u7136\u800c\u541f\u6e38\u8bd7\u4eba\u867d\u7136\u4e5f\u5c5e\u4e8e\u201c\u5deb\u5e08\u201d\uff0c\u4f46\u5374\u662f\u975e\u5e38\u7279\u6b8a\u7684\u4e00\u7c7b\uff0c\u4f7f\u7528\u7684\u65b9\u5f0f\uff0c\u8fd0\u8f6c\u7684\u9014\u7ecf\uff0c\u64cd\u4f5c\u7684\u624b\u6cd5\uff0c\u90fd\u548c\u666e\u901a\u5deb\u5e08\u5927\u76f8\u8fe5\u5f02\uff0c\u743c\u6069\u81ea\u5df1\u90fd\u662f\u534a\u70b9\u4e0d\u61c2\uff0c\u66f4\u6ca1\u6cd5\u6559\u73ca\u5609\u3002</p><p>\u201c\u4ece\u54ea\u91cc\u627e\u4e2a\u541f\u6e38\u8bd7\u4eba\u6765\u6559\u59d0\u59d0\u5462\uff0c\u201d\u743c\u6069\u601d\u7d22\u7740\uff0c\u201c\u81ea\u5df1\u8ba4\u8bc6\u7684\u4eba\u91cc\u9762\uff0c\u5deb\u5e08\u4e00\u5927\u7fa4\uff0c\u672f\u58eb\u4e5f\u6709\uff0c\u541f\u6e38\u8bd7\u4eba\u5374\u534a\u4e2a\u90fd\u4e0d\u89c1\u2026\u2026\u6216\u8005\u4ece\u54ea\u91cc\u5f04\u70b9\u6559\u6750\u6765\u8ba9\u59d0\u59d0\u81ea\u5b66\uff1f\u201d</p><p>\u9b54\u6cd5\u8fd9\u79cd\u6df1\u5965\u590d\u6742\u7684\u6280\u827a\uff0c\u5f53\u7136\u662f\u9762\u5bf9\u9762\u7684\u6307\u5bfc\u4f20\u6388\u6bd4\u8f83\u597d\uff0c\u770b\u6559\u6750\u81ea\u5b66\u6548\u679c\u5f88\u5dee\uff0c\u5f80\u5f80\u8fd8\u6709\u4e00\u5927\u5806\u95ee\u9898\uff0c\u751a\u81f3\u4f1a\u6709\u5371\u9669\uff0c\u4f46\u8fd9\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u529e\u6cd5\u4e86\u3002\u95ee\u9898\u662f\uff0c\u5c31\u7b97\u662f\u6559\u6750\uff0c\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u54ea\u91cc\u4f1a\u6709\u2026\u2026</p><p>\u6b63\u76d8\u7b97\u7740\uff0c\u73ca\u5609\u8bf4\u8bdd\u4e86\uff0c\u201c\u4f60\u600e\u4e48\u7a81\u7136\u95ee\u8fd9\u4e2a\uff1f\u201d\u5979\u5947\u602a\uff0c\u201c\u541f\u6e38\u8bd7\u4eba\u2026\u2026\u96be\u9053\u4f60\u8ba4\u4e3a\u6211\u6709\u8fd9\u79cd\u5929\u8d4b\uff1f\u201d</p><p>\u201c\u6211\u662f\u6bd4\u8f83\u671f\u671b\u5566\uff0c\u201d\u743c\u6069\u542b\u7cca\u5730\u8bf4\uff0c\u4e8b\u60c5\u8fd8\u6ca1\u5b8c\u5168\u786e\u5b9a\u4e4b\u524d\uff0c\u4ed6\u4e0d\u60f3\u7ed9\u59d0\u59d0\u592a\u9ad8\u7684\u671f\u671b\uff0c\u514d\u5f97\u4e07\u4e00\u843d\u7a7a\u53cd\u800c\u6cae\u4e27\uff0c\u201c\u6211\u662f\u60f3\uff0c\u59d0\u59d0\u65e2\u7136\u5728\u97f3\u4e50\u4e0a\u8fd9\u4e48\u6709\u5929\u8d4b\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u591f\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u4ee5\u540e\u6211\u6709\u4ec0\u4e48\u4efb\u52a1\u8981\u51fa\u95e8\uff0c\u59d0\u59d0\u5c31\u80fd\u966a\u5728\u6211\u8eab\u8fb9\u4e86\u3002\u201d</p><p>\u73ca\u5609\u8138\u8272\u4e00\u51dd\uff0c\u5fae\u5fae\u70b9\u5934\uff0c\u663e\u7136\u88ab\u743c\u6069\u8fd9\u53e5\u8bdd\u6253\u52a8\u4e86\uff0c\u201c\u6709\u7a7a\u6211\u5411\u6559\u6388\u6253\u542c\u4e00\u4e0b\u770b\u770b\uff0c\u201d\u5979\u6700\u540e\u8bf4\uff0c\u770b\u4e86\u770b\u65f6\u95f4\uff0c\u201c\u5dee\u4e0d\u591a\u6211\u5f97\u53bb\u5b66\u6821\u4e86\uff0c\u4f60\u4e56\u4e56\u5728\u5bb6\u4f11\u606f\u3002\u201d</p><p>\u201c\u6211\u9001\u59d0\u59d0\u3002\u201d</p><p>\u201c\u4e0d\u7528\uff0c\u201d\u73ca\u5609\u8d77\u8eab\u7a7f\u4e0a\u5916\u5957\uff0c\u201c\u4f60\u4e0d\u4e5f\u6709\u81ea\u5df1\u7684\u4e8b\u60c5\u4e48\u3002\u771f\u6709\u7a7a\u7684\u8bdd\uff0c\u53bb\u591a\u966a\u966a\u8299\u857e\u72c4\u3002\u201d</p><p>\u201c\u554a\u3002\u201d</p><p>\u201c\u554a\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u662f\u4e2a\u7537\u5b69\u5b50\uff0c\u65e2\u7136\u8981\u4e86\uff0c\u90a3\u5c31\u5f97\u8d1f\u8d23\uff0c\u5bf9\u4eba\u5bb6\u597d\u70b9\uff0c\u522b\u603b\u90a3\u4e48\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u201d\u5979\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u743c\u6069\uff0c\u6211\u529d\u4e00\u53e5\uff0c\u518d\u67d4\u987a\u7684\u5973\u5b69\u5b50\uff0c\u90a3\u4e5f\u662f\u6709\u813e\u6c14\u7684\uff0c\u6211\u53ef\u4e0d\u60f3\u54ea\u4e00\u5929\u770b\u5230\u81ea\u5df1\u5f1f\u5f1f\u88ab\u4eba\u8ffd\u6740\u3002\u201d</p></article>"}}}]);
