(window.webpackJsonp=window.webpackJsonp||[]).push([[1621],{1644:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u610f\u5916\u7684\u76f8\u9047",content:"<article><h2>\u7b2c\u4e09\u8282 \u610f\u5916\u7684\u76f8\u9047</h2><p>\u542c\u5230\u59d0\u59d0\u7684\u8b66\u544a\uff0c\u743c\u6069\u8fde\u5fd9\u4f4e\u5934\u8868\u793a\u865a\u5fc3\u63a5\u53d7\u2026\u2026\u4e8b\u5b9e\u4e0a\u4e5f\u6ca1\u6709\u4e0d\u63a5\u53d7\u7684\u9053\u7406\u3002\u73ca\u5609\u8fd9\u5176\u5b9e\u662f\u7ed9\u4e86\u4ed6\u673a\u4f1a\uff0c\u5141\u8bb8\u4ed6\u540d\u6b63\u8a00\u987a\u5730\u53bb\u5077\u5403\uff0c\u5018\u82e5\u8fd8\u4e0d\u8bc6\u76f8\uff0c\u96be\u4e0d\u6210\u8981\u5979\u5927\u53d1\u96cc\u5a01\uff0c\u52d2\u4ee4\u81ea\u5df1\u548c\u8299\u857e\u72c4\u65ad\u7edd\u6765\u5f80\u624d\u6b23\u7136\u4ece\u547d\u4e48\uff0c\u90a3\u81ea\u5df1\u5c31\u771f\u662f\u72af\u8d31\u4e86\u3002</p><p>\u62ac\u8d77\u5934\u6765\uff0c\u73ca\u5609\u5df2\u7ecf\u51c6\u5907\u51fa\u53d1\uff0c\u5979\u7a7f\u7740\u9ed1\u8272\u5929\u9e45\u7ed2\u886c\u886b\u548c\u957f\u88e4\uff0c\u56e0\u4e3a\u5929\u6c14\u6bd4\u8f83\u51b7\uff0c\u5916\u9762\u53c8\u5957\u4e86\u4e00\u4ef6\u98ce\u8863\uff0c\u8116\u9888\u4e0a\u6302\u7740\u5b9d\u77f3\u9879\u94fe\uff0c\u957f\u53d1\u7528\u4e1d\u5e26\u7cfb\u7740\uff0c\u522b\u7740\u767d\u8272\u53d1\u5361\uff0c\u624b\u4e2d\u63d0\u7740\u888b\u5b50\uff0c\u91cc\u9762\u88c5\u7740\u6559\u6750\u548c\u4e00\u652f\u957f\u7b1b\uff0c\u743c\u6069\u626b\u4e86\u4e00\u773c\uff0c\u53d1\u73b0\u662f\u6628\u5929\u770b\u89c1\u7684\u90a3\u652f\u591c\u6c89\u6728\u957f\u7b1b\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u600e\u4e48\u4e0d\u7528\u6211\u9001\u7684\u90a3\u652f\u591c\u98ce\u4e4b\u7b1b\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u96be\u9053\u4e0d\u559c\u6b22\uff1f\u6211\u89c9\u5f97\u5b83\u6bd4\u4f60\u8fd9\u652f\u97f3\u8d28\u66f4\u597d\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u8fd9\u4e2a\u554a\uff0c\u201d\u73ca\u5609\u4f4e\u5934\u770b\u4e86\u770b\uff0c\u201c\u4e0d\u662f\u5566\uff0c\u8fd9\u652f\u957f\u7b1b\u662f\u5b66\u9662\u91cc\u914d\u53d1\u7684\uff0c\u4e0a\u8bfe\u5fc5\u987b\u7528\u5b83\uff0c\u4e0d\u8ba9\u5b66\u751f\u81ea\u5df1\u5e26\u4e50\u5668\uff0c\u5426\u5219\u4e0d\u65b9\u4fbf\u7edf\u4e00\u6559\u5b66\u561b\uff0c\u201d\u5979\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u9001\u7684\u4e1c\u897f\uff0c\u59d0\u59d0\u600e\u4e48\u53ef\u80fd\u4e0d\u559c\u6b22\uff1f\u559c\u6b22\u5f97\u4e0d\u5f97\u4e86\u5462\u3002\u665a\u4e0a\u59d0\u59d0\u7528\u5b83\u5439\u66f2\u5b50\u7ed9\u4f60\u542c\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u201c\u90a3\u5c31\u597d\uff0c\u5728\u5bb6\u4e56\u4e56\u7684\u3002\u201d</p><p>\u73ca\u5609\u51fa\u95e8\u53bb\u97f3\u4e50\u5b66\u9662\uff0c\u5bb6\u91cc\u987f\u65f6\u5b89\u9759\u4e0b\u6765\uff0c\u743c\u6069\u56de\u5230\u81ea\u5df1\u623f\u95f4\u91cc\uff0c\u53d6\u51fa\u9b54\u6cd5\u4e66\u6e29\u4e60\u4e86\u4e00\u4f1a\uff0c\u53c8\u7ffb\u4e86\u7ffb\u4ece\u70db\u5821\u6284\u5f55\u6765\u7684\u90a3\u4e9b\u8d44\u6599\uff0c\u6e10\u6e10\u89c9\u5f97\u6709\u4e9b\u65e0\u804a\u3002\u4ed6\u8fd9\u6b21\u662f\u56de\u57ce\u8ff0\u804c\uff0c\u5df2\u7ecf\u53bb\u62a5\u8fc7\u5230\uff0c\u4f46\u4e00\u65f6\u4e5f\u6ca1\u4eba\u6765\u62db\u4ed6\u524d\u53bb\uff0c\u4f30\u8ba1\u4f1a\u8ba9\u4ed6\u5728\u5bb6\u4f11\u606f\u4e24\u5929\u518d\u8bf4\uff0c\u53cd\u6b63\u5df2\u7ecf\u662f\u5e74\u5e95\u4e86\uff0c\u518d\u8fc7\u51e0\u5929\u5c31\u662f1374DR\u3002\u4ed6\u8eba\u5728\u5bbd\u5927\u7684\u6905\u4e2d\uff0c\u671b\u7740\u7a97\u5916\u7684\u6811\u51a0\uff0c\u9ed8\u9ed8\u5bfb\u601d\u7740\u81ea\u5df1\u7684\u672a\u6765\uff0c\u8003\u8651\u4e0b\u4e00\u6b65\u5e94\u8be5\u505a\u4ec0\u4e48\uff0c\u4f46\u5168\u65e0\u534a\u70b9\u5934\u7eea\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u81ea\u5df1\u8fd8\u771f\u4e0d\u662f\u4e00\u4e2a\u80fd\u6210\u5927\u4e8b\u7684\u4eba\uff0c\u743c\u6069\u5982\u6b64\u81ea\u5632\u7740\u3002\u4ed6\u6709\u806a\u660e\u624d\u667a\uff0c\u4e5f\u6709\u80fd\u529b\u672c\u4e8b\uff0c\u601d\u7ef4\u654f\u9510\uff0c\u8bb0\u5fc6\u529b\u4e0d\u9519\uff0c\u548c\u4eba\u76f8\u5904\u6253\u4ea4\u9053\u4e5f\u6ca1\u4ec0\u4e48\u969c\u788d\uff0c\u5fc3\u6001\u603b\u662f\u4fdd\u6301\u5f97\u5f88\u597d\uff0c\u65e0\u8bba\u4f55\u65f6\u4f55\u5730\uff0c\u4e0d\u6025\u4e0d\u71e5\uff0c\u4e0d\u614c\u4e0d\u4e71\uff0c\u518d\u70e6\u6742\u7684\u5c40\u9762\u518d\u6df7\u4e71\u7684\u5904\u5883\uff0c\u4ed6\u4e5f\u80fd\u7acb\u523b\u9547\u5b9a\u4e0b\u6765\uff0c\u6c89\u7740\u5e94\u4ed8\u2014\u2014\u6240\u6709\u7684\u8fd9\u4e9b\uff0c\u90fd\u80fd\u7b97\u5f97\u4e0a\u662f\u4f18\u79c0\u7684\u54c1\u8d28\uff0c\u4f46\u5374\u8fd8\u6b20\u7f3a\u4e86\u6700\u5173\u952e\u7684\u90e8\u5206\u3002</p><p>\u5c31\u662f\u5f3a\u70c8\u800c\u660e\u786e\u7684\u76ee\u6807\u3002</p><p>\u6709\u4e9b\u4eba\u628a\u5b83\u53eb\u68a6\u60f3\uff0c\u6709\u4e9b\u4eba\u628a\u5b83\u53eb\u91ce\u5fc3\uff0c\u8fd8\u6709\u4eba\u628a\u5b83\u53eb\u6267\u7740\uff0c\u8bf8\u5982\u6b64\u7c7b\uff0c\u53cd\u6b63\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u8981\u60f3\u6210\u5c31\u70b9\u4e8b\u4e1a\uff0c\u8fd9\u662f\u5fc5\u4e0d\u53ef\u5c11\u7684\u8981\u7d20\uff0c\u5426\u5219\u7ec8\u7a76\u96be\u6210\u6c14\u5019\uff0c\u8fd9\u4e2a\u9053\u7406\u743c\u6069\u81ea\u7136\u662f\u61c2\u7684\uff0c\u65e0\u5948\u61c2\u5f52\u61c2\uff0c\u80fd\u4e0d\u80fd\u505a\u5230\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u3002</p><p>\u5c31\u672c\u8d28\u800c\u8a00\uff0c\u743c\u6069\u5176\u5b9e\u662f\u4e2a\u968f\u6ce2\u9010\u6d41\u7684\u4eba\uff0c\u6ca1\u6709\u4ec0\u4e48\u771f\u6b63\u7684\u68a6\u60f3\u548c\u6267\u7740\u3002\u5c31\u7b97\u62e5\u6709\u8d85\u5353\u7684\u5929\u8d4b\u548c\u624d\u534e\uff0c\u4ed6\u4e5f\u672a\u5fc5\u6709\u5174\u8da3\u53bb\u52aa\u529b\u628a\u5b83\u4eec\u53d1\u6325\u5230\u6781\u81f4\uff0c\u8d62\u5f97\u6700\u591a\u7684\u56de\u62a5\u2014\u2014\u4ed6\u66f4\u6709\u53ef\u80fd\u662f\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u6d6a\u8d39\u6389\u3002\u6253\u4e2a\u7b80\u5355\u7684\u6bd4\u65b9\uff0c\u5047\u8bbe\u743c\u6069\u62e5\u6709\u5546\u4e1a\u4e0a\u7684\u5929\u624d\uff0c\u5018\u82e5\u7aed\u5c3d\u5168\u529b\uff0c\u4ed8\u51fa\u5341\u4e8c\u5206\u7684\u52aa\u529b\uff0c\u4ed6\u4fbf\u80fd\u6210\u4e3a\u4e16\u754c\u9996\u5bcc\uff1b\u90a3\u4e48\u4ed6\u5f80\u5f80\u53ea\u4f1a\u4ed8\u51fa\u4e03\u516b\u5206\u7684\u52aa\u529b\uff0c\u80fd\u591f\u8ba9\u81ea\u5df1\u751f\u6d3b\u8212\u9002\u3001\u8863\u98df\u65e0\u5fe7\uff0c\u90a3\u4e5f\u5c31\u5dee\u4e0d\u591a\u8db3\u591f\u4e86\u3002</p><p>\u4eba\u751f\u4e00\u4e16\uff0c\u8349\u6728\u4e00\u79cb\uff0c\u9002\u610f\u4fbf\u53ef\uff0c\u4f55\u5fc5\u8fc7\u5206\u5f3a\u6c42\u3002</p><p>\u8fd9\u79cd\u5fc3\u6001\u4e0d\u80fd\u8bf4\u6709\u95ee\u9898\uff0c\u4f46\u663e\u7136\u4e0d\u9002\u5408\u6210\u5c31\u771f\u6b63\u7684\u4e8b\u4e1a\u3002\u53e4\u5f80\u4eca\u6765\uff0c\u6240\u6709\u80fd\u6210\u5c31\u4f1f\u4e1a\u7684\uff0c\u65e0\u4e00\u4e0d\u662f\u5fc3\u5fd7\u575a\u6bc5\u5982\u94c1\uff0c\u76ee\u6807\u786e\u5b9a\u4e0d\u79fb\uff0c\u72c2\u70ed\u3001\u6267\u7740\u3001\u52c7\u5f80\u76f4\u524d\uff0c\u867d\u4e5d\u6b7b\u800c\u4e0d\u6094\u2014\u2014\u743c\u6069\u6070\u6070\u5c31\u7f3a\u4e4f\u8fd9\u79cd\u7d20\u8d28\uff0c\u4ed6\u592a\u201c\u51b7\u6de1\u201d\u4e86\u3002</p><p>\u4ee5\u76ee\u524d\u7684\u72b6\u51b5\u800c\u8bba\uff0c\u4ed6\u4e5f\u7b97\u662f\u5c0f\u6709\u6210\u5c31\uff0c\u8eab\u4efd\u3001\u5730\u4f4d\u3001\u5b9e\u529b\u3001\u7f8e\u4eba\uff0c\u6837\u6837\u90fd\u8fd8\u4e0d\u9519\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u51fa\u7c7b\u62d4\u8403\uff0c\u4f46\u4e5f\u8fdc\u8d85\u5bfb\u5e38\u4e86\uff0c\u751a\u81f3\u5df2\u7ecf\u8fdc\u8fdc\u8d85\u51fa\u4ed6\u6700\u521d\u7684\u9884\u671f\u3002\u5982\u679c\u6362\u4e86\u4e2a\u4eba\uff0c\u6216\u8bb8\u4fbf\u4f1a\u518d\u63a5\u518d\u52b1\uff0c\u7ee7\u7eed\u524d\u8fdb\uff0c\u4e0d\u8bf4\u5f00\u56fd\u79f0\u5e1d\uff0c\u96c4\u9738\u5929\u4e0b\uff0c\u81f3\u5c11\u4e5f\u8981\u6210\u4e3a\u4e3e\u4e16\u65e0\u53cc\uff0c\u7b2c\u4e00\u82f1\u96c4\u4e4b\u7c7b\uff0c\u751a\u81f3\u5c01\u795e\u6210\u5723\uff0c\u6c38\u6052\u4e0d\u673d\u3002\u7136\u800c\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u5982\u679c\u8981\u95ee\u4ed6\u6b64\u523b\u6700\u5927\u7684\u68a6\u60f3\u662f\u4ec0\u4e48\uff0c\u4ed6\u5927\u6982\u4fbf\u4f1a\u56de\u7b54\u8bf4\u201c\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u9020\u4e00\u5ea7\u81ea\u5df1\u7684\u6d6e\u7a7a\u57ce\uff0c\u8ba9\u6240\u6709\u559c\u6b22\u7684\u5973\u5b69\u5b50\u90fd\u4f4f\u5728\u91cc\u9762\uff0c\u60a0\u95f2\u5ea6\u8fc7\u6b64\u751f\u3002\u201d</p><p>\u5982\u6b64\u800c\u5df2\u3002</p><p>\u4fbf\u662f\u8fd9\u4e2a\u68a6\u60f3\uff0c\u4ed6\u4e5f\u5e76\u6ca1\u6709\u5f53\u771f\u5c31\u8feb\u5207\u5730\u8ffd\u6c42\uff0c\u53bb\u7740\u624b\u5b9e\u65bd\u3002\u4ed6\u60f3\u5efa\u4e00\u5ea7\u81ea\u5df1\u7684\u6d6e\u7a7a\u57ce\uff0c\u4f46\u4ed6\u4e5f\u6ca1\u6709\u53bb\u57cb\u5934\u94bb\u7814\u5177\u4f53\u7684\u65b9\u6cd5\u6b65\u9aa4\uff0c\u6ca1\u6709\u73b0\u5728\u5c31\u7740\u624b\u505a\u51c6\u5907\u3002\u53cd\u6b63\u8981\u60f3\u5efa\u7acb\u6d6e\u7a7a\u57ce\uff0c\u5148\u51b3\u6761\u4ef6\u662f\u81ea\u5df1\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u8fd9\u70b9\u662f\u786e\u5b9a\u7684\u2014\u2014\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u7b49\u81ea\u5df1\u6210\u4e86\u5927\u5965\u672f\u5e08\u518d\u8bf4\uff0c\u53cd\u6b63\u73b0\u5728\u8fd8\u65e9\uff0c\u4e0d\u5fc5\u7740\u6025\u3002</p><p>\u201c\u5e72\uff0c\u81ea\u5df1\u8fd9\u4e2a\u6837\u5b50\u53ef\u4e0d\u884c\u554a\uff0c\u201d\u4ed6\u81ea\u8d23\u7740\uff0c\u201c\u592a\u61d2\u6563\u4e86\u3002\u201d</p><p>\u597d\u5427\uff0c\u90a3\u5c31\u53d8\u5f97\u79ef\u6781\u70b9\uff0c\u5e74\u8f7b\u4eba\u4e48\uff0c\u5c31\u8981\u6709\u5e74\u8f7b\u4eba\u7684\u671d\u6c14\u548c\u6d3b\u529b\u3002</p><p>\u592a\u957f\u8fdc\u7684\u76ee\u6807\uff0c\u6682\u65f6\u5148\u4e0d\u7528\u53bb\u7ba1\uff0c\u89e3\u51b3\u773c\u524d\u9762\u4e34\u7684\u95ee\u9898\u518d\u8bf4\u3002\u81f3\u4e8e\u81ea\u5df1\u73b0\u5728\u9700\u8981\u89e3\u51b3\u7684\u95ee\u9898\u2026\u2026\u9996\u5148\u81ea\u7136\u662f\u8981\u63a8\u5012\u59d0\u59d0\uff0c\u4f46\u8fd9\u6839\u672c\u5c31\u4e0d\u662f\u95ee\u9898\uff0c\u6216\u8005\u8bf4\u53ea\u662f\u4e2a\u65f6\u95f4\u95ee\u9898\u800c\u5df2\uff0c\u743c\u6069\u771f\u60f3\u52a8\u624b\u7684\u8bdd\uff0c\u4eca\u5929\u665a\u4e0a\u4f30\u8ba1\u5c31\u80fd\u771f\u6b63\u5360\u6709\u73ca\u5609\uff0c\u59d0\u59d0\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u62d2\u7edd\uff0c\u4f46\u8fd9\u6837\u6bd4\u8f83\u6ca1\u610f\u601d\uff0c\u5f97\u627e\u4e2a\u5408\u9002\u7684\u65f6\u673a\uff0c\u8981\u6709\u60c5\u8c03\uff0c\u6709\u6c14\u6c1b\uff0c\u6bd5\u7adf\u662f\u6700\u73cd\u7231\u7684\u59d0\u59d0\uff0c\u4e0d\u80fd\u8349\u7387\u9a6c\u864e\u3002\u5176\u6b21\u4e48\uff0c\u662f\u8981\u6446\u5e73\u59d0\u59d0\u548c\u5176\u4ed6\u5973\u5b69\u5b50\u7684\u5173\u7cfb\uff0c\u6362\u53e5\u8bdd\u8bf4\u5c31\u662f\u7ef4\u62a4\u540e\u5bab\u7684\u548c\u8c10\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u81ea\u7136\u5f88\u91cd\u8981\uff0c\u4f46\u6709\u4e9b\u65e0\u4ece\u7740\u624b\uff0c\u53ea\u80fd\u987a\u5176\u81ea\u7136\uff0c\u9759\u89c2\u5176\u53d8\u3002\u5176\u4ed6\u7684\u2026\u2026\u5514\uff0c\u522b\u7684\u5176\u5b9e\u90fd\u53ef\u4ee5\u6682\u4e14\u653e\u5728\u4e00\u8fb9\uff0c\u76ee\u524d\u6700\u6700\u8981\u7d27\u7684\uff0c\u5e94\u8be5\u5c31\u662f\u8d76\u5feb\u8ba9\u73ca\u5609\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\uff0c\u4e86\u5374\u6389\u4e00\u76f4\u4ee5\u6765\u7684\u5fc3\u75c5\u3002</p><p>\u53ef\u662f\uff0c\u4ece\u54ea\u91cc\u5f00\u59cb\u7740\u624b\u5462\u3002</p><p>\u743c\u6069\u5934\u75bc\u8d77\u6765\uff0c\u8981\u505a\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u6015\u5b83\u8270\u96be\u7e41\u7410\uff0c\u5c31\u6015\u832b\u65e0\u5934\u7eea\uff0c\u6beb\u65e0\u95e8\u9053\u53ef\u5faa\u3002\u5018\u82e5\u662f\u5728\u522b\u5904\uff0c\u8fd8\u53ef\u4ee5\u8003\u8651\u53bb\u9152\u9986\u6253\u542c\u4f20\u95fb\uff0c\u53bb\u6e38\u8361\u8005\u516c\u4f1a\u8d2d\u4e70\u4fe1\u606f\uff0c\u6216\u8005\u53bb\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u7684\u795e\u6bbf\u91cc\u54a8\u8be2\u4e4b\u7c7b\uff0c\u7136\u800c\u9634\u9b42\u57ce\u662f\u4e2a\u5c01\u95ed\u3001\u68ee\u4e25\u3001\u6c89\u5bc2\u5e76\u4e14\u6b7b\u677f\u7684\u5730\u65b9\uff0c\u65e2\u6ca1\u6709\u5192\u9669\u8005\u805a\u96c6\u7684\u9152\u9986\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6e38\u8361\u8005\u516c\u4f1a\uff0c\u66f4\u6ca1\u6709\u6b27\u683c\u739b\u795e\u6bbf\uff0c\u53ea\u6709\u591c\u5973\u58eb\u7684\u201c\u9634\u5f71\u4e4b\u5385\u201d\u2014\u2014\u838e\u5c14\u662f\u9690\u79d8\u4e4b\u795e\uff0c\u60f3\u4ece\u5979\u7684\u6559\u4f1a\u90a3\u91cc\u83b7\u53d6\u4fe1\u606f\u2026\u2026\u7b97\u4e86\u5427\uff0c\u522b\u5f00\u73a9\u7b11\u4e86\u3002</p><p>\u8981\u60f3\u83b7\u53d6\u77e5\u8bc6\uff0c\u5168\u5927\u9646\u7684\u6700\u4f73\u5730\u70b9\u65e0\u8fc7\u4e8e\u70db\u5821\u3002\u7136\u800c\u81ea\u5df1\u5728\u70db\u5821\u90a3\u51e0\u5929\uff0c\u5fd9\u7740\u6536\u96c6\u4f0a\u739b\u65af\u5361\u7684\u8d44\u6599\uff0c\u600e\u4e48\u5c31\u628a\u8fd9\u832c\u7ed9\u5fd8\u4e86\uff0c\u5dee\u52b2\uff0c\u592a\u5dee\u52b2\u4e86\u3002</p><p>\u5514\uff0c\u4e0d\u8fc7\u8fd9\u4e48\u4e00\u8bf4\uff0c\u743c\u6069\u5012\u662f\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u9634\u9b42\u57ce\u81f3\u5c11\u8fd8\u662f\u6709\u56fe\u4e66\u9986\u7684\uff0c\u6bd5\u7adf\u4e5f\u662f\u4f20\u627f\u5343\u5e74\u7684\u6587\u660e\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u70db\u5821\u90a3\u6837\u85cf\u4e66\u4e30\u5bcc\uff0c\u8d44\u6599\u6d69\u701a\uff0c\u4f46\u4e5f\u4e0d\u59a8\u53bb\u7ffb\u7ffb\u770b\uff0c\u67e5\u67e5\u8d44\u6599\uff0c\u6216\u8bb8\u80fd\u6709\u4ec0\u4e48\u53d1\u73b0\u3002\u8fd8\u53ef\u4ee5\u95ee\u95ee\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\uff0c\u6bd5\u7adf\u83ab\u5c3c\u5361\u662f\u5deb\u5e08\u4e16\u5bb6\uff0c\u53ef\u80fd\u4f1a\u63d0\u4f9b\u70b9\u7ebf\u7d22\u4e4b\u7c7b\u3002</p><p>\u8fd9\u4e48\u4e00\u60f3\uff0c\u65b9\u5411\u4fbf\u6e10\u6e10\u660e\u6670\uff0c\u743c\u6069\u4e5f\u4e0d\u8fdf\u7591\uff0c\u7acb\u523b\u7a7f\u4e0a\u5916\u5957\uff0c\u52a8\u8eab\u51fa\u95e8\u3002</p><p>\u203b\u203b\u203b</p><p>\u9634\u9b42\u57ce\u6709\u56db\u5ea7\u56fe\u4e66\u9986\uff0c\u5176\u4e2d\u7b2c\u4e00\u56fe\u4e66\u9986\u662f\u738b\u5ba4\u4e13\u7528\uff0c\u743c\u6069\u6ca1\u8d44\u683c\u8fdb\u53bb\uff0c\u7b2c\u56db\u56fe\u4e66\u9986\u5411\u5168\u57ce\u5e02\u6c11\u5f00\u653e\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u771f\u6b63\u6709\u4ef7\u503c\u7684\u8d44\u6599\uff0c\u81f3\u5c11\u80af\u5b9a\u4e0d\u4f1a\u6709\u5173\u4e8e\u541f\u6e38\u8bd7\u4eba\u7684\u6559\u7a0b\uff0c\u7b2c\u4e8c\u3001\u7b2c\u4e09\u56fe\u4e66\u9986\u5012\u662f\u4e3a\u8d35\u65cf\u5f00\u8bbe\u7684\uff0c\u6216\u8bb8\u80fd\u6709\u4e9b\u4e1c\u897f\u3002\u8bf4\u8d77\u6765\uff0c\u81ea\u5df1\u5728\u8fd9\u57ce\u91cc\u4f4f\u4e86\u5341\u51e0\u5e74\uff0c\u8fd8\u4ece\u6ca1\u53bb\u8fc7\u5462\uff0c\u662f\u4e0d\u662f\u5e94\u8be5\u627e\u4e2a\u5411\u5bfc\u3002</p><p>\u73ca\u5609\u53ef\u80fd\u6709\u541f\u6e38\u8bd7\u4eba\u7684\u5929\u8d4b\uff0c\u8fd9\u79cd\u4e8b\u60c5\u81ea\u7136\u4e0d\u80fd\u5230\u5904\u58f0\u5f20\uff0c\u4e0d\u8fc7\u83ab\u5c3c\u5361\u59d0\u59b9\u5df2\u7ecf\u662f\u77e5\u60c5\u8005\uff0c\u4e5f\u5c31\u65e0\u6240\u8c13\u4e86\u3002\u743c\u6069\u8d70\u5230\u9694\u58c1\u5b85\u9662\uff0c\u8299\u857e\u72c4\u6b63\u5728\u9662\u4e2d\u6d47\u82b1\uff0c\u770b\u89c1\u4ed6\u8fc7\u6765\uff0c\u9ad8\u9ad8\u5174\u5174\u5730\u653e\u4e0b\u6c34\u58f6\uff0c\u6251\u5230\u6000\u91cc\u3002</p><p>\u201c\u4f60\u59d0\u59d0\u5462\uff1f\u201d\u743c\u6069\u63fd\u7740\u5979\uff0c\u8f7b\u8f7b\u629a\u6478\u79c0\u53d1\uff0c\u968f\u53e3\u95ee\u4e86\u53e5\u3002</p><p>\u201c\u5979\u53bb\u795e\u6bbf\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u4f60\u73b0\u5728\u6709\u7a7a\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u966a\u6211\u53bb\u4e00\u8d9f\u56fe\u4e66\u9986\u3002\u201d</p><p>\u201c\u597d\u554a\uff0c\u201d\u8299\u857e\u72c4\u70b9\u5934\uff0c\u201c\u7b49\u4e94\u5206\u949f\u884c\u4e48\uff0c\u6211\u53bb\u6362\u4ef6\u8863\u670d\uff0c\u9a6c\u4e0a\u5c31\u597d\u3002\u201d</p><p>\u201c\u4e94\u5206\u949f\u600e\u4e48\u591f\uff0c\u201d\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u201c\u5973\u5b69\u5b50\u51fa\u95e8\uff0c\u81f3\u5c11\u9700\u8981\u534a\u5c0f\u65f6\u7684\u51c6\u5907\u3002\u6211\u53c8\u4e0d\u7740\u6025\uff0c\u4f60\u53bb\u5427\u3002\u201d</p><p>\u5341\u4e94\u5206\u949f\u4e4b\u540e\uff0c\u8299\u857e\u72c4\u6362\u597d\u8863\u670d\uff0c\u968f\u7740\u743c\u6069\u51fa\u95e8\uff0c\u56e0\u4e3a\u662f\u5728\u57ce\u91cc\uff0c\u5979\u6ca1\u6709\u50cf\u5e73\u65f6\u90a3\u6837\u7a7f\u5deb\u5e08\u888d\uff0c\u4e0a\u8eab\u662f\u4e00\u4ef6\u6df1\u7070\u8272\u7f8a\u7ed2\u886c\u886b\uff0c\u4e0b\u8eab\u9ed1\u767d\u65b9\u683c\u77ed\u88d9\u548c\u9ed1\u8272\u957f\u9774\uff0c\u5916\u9762\u518d\u5957\u7740\u767d\u8272\u98ce\u8863\uff0c\u770b\u8d77\u6765\u65e2\u7d20\u51c0\u53c8\u5e72\u7ec3\uff0c\u5012\u662f\u5e73\u6dfb\u4e86\u51e0\u5206\u82f1\u6c14\u3002</p><p>\u201c\u597d\u770b\u5417\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u597d\u770b\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u4ece\u6ca1\u89c1\u4f60\u8fd9\u4e48\u7a7f\u8fc7\u5462\uff0c\u6709\u70b9\u773c\u524d\u4e00\u4eae\u7684\u611f\u89c9\u3002\u201d</p><p>\u201c\u65b0\u5b66\u7684\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u6211\u770b\u73ca\u5609\u59d0\u59d0\u7ecf\u5e38\u5c31\u8fd9\u4e48\u7a7f\uff0c\u6240\u4ee5\u6a21\u4eff\u4e00\u4e0b\u3002\u201d</p><p>\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u5e72\u561b\u6a21\u4eff\u5979\u554a\u3002\u201d</p><p>\u201c\u77e5\u9053\u4f60\u559c\u6b22\u561b\uff0c\u4f60\u4e00\u5411\u559c\u6b22\u82f1\u6c14\u7684\u5973\u5b69\u5b50\u3002\u201d</p><p>\u743c\u6069\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u8c01\u8bf4\u7684\uff0c\u6211\u4e5f\u559c\u6b22\u6e29\u67d4\u4e56\u5de7\u7684\uff0c\u5c31\u50cf\u4f60\u8fd9\u6837\u3002\u201d</p><p>\u8299\u857e\u72c4\u7b11\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u8fd9\u4e2a\u8bdd\u9898\u3002\u201c\u4f60\u53bb\u56fe\u4e66\u9986\u5e72\u561b\u5462\uff0c\u201d\u5979\u95ee\uff0c\u201c\u67e5\u8d44\u6599\uff1f\u201d</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u201c\u54ea\u65b9\u9762\u7684\u8d44\u6599\uff1f\u201d</p><p>\u201c\u6709\u5173\u541f\u6e38\u8bd7\u4eba\u8fd9\u65b9\u9762\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u60f3\u591a\u4e86\u89e3\u70b9\u3002\u201d</p><p>\u8299\u857e\u72c4\u54e6\u4e86\u4e00\u58f0\uff0c\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\uff0c\u201c\u73ca\u5609\u59d0\u59d0\u4e0d\u662f\u5728\u97f3\u4e50\u5b66\u9662\u91cc\u4e48\uff0c\u201d\u5979\u6709\u4e9b\u5947\u602a\uff0c\u201c\u96be\u9053\u4e0d\u6559\u8fd9\u4e9b\uff1f\u201d</p><p>\u201c\u4e0d\u6559\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u8bf4\u5b66\u9662\u91cc\u6559\u7684\u90fd\u662f\u666e\u901a\u7684\u97f3\u4e50\u77e5\u8bc6\uff0c\u4e0d\u6d89\u53ca\u9b54\u6cd5\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u6211\u597d\u50cf\u4ee5\u524d\u542c\u8bf4\u97f3\u4e50\u5b66\u9662\u91cc\u4e5f\u662f\u6709\u8fd9\u79cd\u8bfe\u7a0b\u7684\uff0c\u201d\u8299\u857e\u72c4\u5fae\u5fae\u76b1\u7709\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u201c\u6ca1\u4ec0\u4e48\u5370\u8c61\u4e86\uff0c\u4e0d\u8bb0\u5f97\u542c\u8c01\u8bf4\u7684\u2026\u2026\u53ef\u80fd\u6211\u8bb0\u9519\u4e86\u5427\u3002\u4e5f\u662f\uff0c\u8fd9\u5e94\u8be5\u5c5e\u4e8e\u5deb\u5e08\u5b66\u9662\u7684\u6559\u5b66\u8303\u7574\uff0c\u6bd5\u7adf\u5176\u5b9e\u662f\u9b54\u6cd5\u4e86\u3002\u201d</p><p>\u201c\u6211\u4eec\u5728\u5b66\u6821\u91cc\u7684\u65f6\u5019\uff0c\u597d\u50cf\u4e5f\u6ca1\u542c\u8bf4\u6709\u5f00\u8fd9\u95e8\u8bfe\u7a0b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e9b\u6559\u6388\u91cc\uff0c\u4e5f\u6ca1\u542c\u8bf4\u6709\u8c01\u662f\u541f\u6e38\u8bd7\u4eba\u7684\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d\u8299\u857e\u72c4\u70b9\u5934\u3002</p><p>\u201c\u4f60\u77e5\u9053\u57ce\u91cc\u6709\u8c01\u662f\u541f\u6e38\u8bd7\u4eba\u4e48\uff1f\u201d\u743c\u6069\u968f\u53e3\u95ee\uff0c\u201c\u5c31\u7b97\u4e0d\u80fd\u767b\u95e8\u6c42\u6559\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u80fd\u591a\u4e86\u89e3\u70b9\u7ebf\u7d22\u3002\u201d</p><p>\u8299\u857e\u72c4\u6447\u6447\u5934\uff0c\u201c\u6ca1\u6709\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u57ce\u91cc\u6bd4\u8f83\u8457\u540d\u7684\u5deb\u5e08\u4e16\u5bb6\uff0c\u6211\u591a\u5c11\u4e5f\u90fd\u4e86\u89e3\u4e9b\uff0c\u6ca1\u542c\u8bf4\u6709\u8c01\u662f\u541f\u6e38\u8bd7\u4eba\u7684\u3002\u672f\u58eb\u6211\u90fd\u77e5\u9053\u51e0\u4e2a\uff0c\u541f\u6e38\u8bd7\u4eba\u2026\u2026\u6ca1\u6709\u3002\u201d</p><p>\u8fd9\u4e2a\u7b54\u6848\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u541f\u6e38\u8bd7\u4eba\u548c\u672f\u58eb\u90fd\u662f\u5c5e\u4e8e\u5deb\u5e08\u4e2d\u7684\u5f02\u7c7b\u548c\u7279\u4f8b\uff0c\u800c\u4e14\u76f8\u6bd4\u8d77\u6765\uff0c\u541f\u6e38\u8bd7\u4eba\u6bd4\u672f\u58eb\u66f4\u52a0\u7f55\u89c1\u3002\u672f\u58eb\u7684\u5929\u8d4b\uff0c\u4f9d\u8d56\u7684\u662f\u201c\u795e\u79d8\u201d\u7684\u8840\u8109\uff0c\u4f8b\u5982\u5de8\u9f99\uff0c\u800c\u4e14\u4e5f\u662f\u53ef\u4ee5\u4ee5\u201c\u8840\u8109\u201d\u4e3a\u51ed\u4f9d\u800c\u4f20\u627f\u7684\uff0c\u4fbf\u5982\u51db\u6709\u9f99\u8109\uff0c\u662f\u672f\u58eb\uff0c\u90a3\u4e48\u5979\u7684\u540e\u4ee3\u4e5f\u6709\u9f99\u8109\uff0c\u4e5f\u5c31\u6709\u5f88\u9ad8\u7684\u53ef\u80fd\u6027\u4f1a\u6210\u4e3a\u672f\u58eb\uff0c\u6240\u4ee5\u5386\u53f2\u4e0a\u4e5f\u4f1a\u5f62\u6210\u6240\u8c13\u7684\u201c\u672f\u58eb\u5bb6\u65cf\u201d\uff1b\u4f46\u541f\u6e38\u8bd7\u4eba\u7684\u5929\u8d4b\uff0c\u5374\u66f4\u52a0\u795e\u79d8\uff0c\u65e0\u4ece\u6349\u6478\uff0c\u548c\u8840\u8109\u5173\u7cfb\u4e0d\u5927\uff0c\u4e5f\u6839\u672c\u65e0\u6cd5\u9057\u4f20\u7ee7\u627f\uff0c\u5b8c\u5168\u5c31\u662f\u770b\u8fd0\u6c14\u3002</p><p>\u9634\u9b42\u57ce\u5982\u4eca\u4eba\u53e3\u8fd1\u56db\u4e07\uff0c\u5deb\u5e08\u8d85\u8fc7\u5343\u540d\uff0c\u957f\u671f\u7684\u201c\u7cbe\u82f1\u5316\u201d\u57f9\u8bad\u548c\u6dd8\u6c70\uff0c\u9ad8\u9636\u5deb\u5e08\u4e5f\u6709\u8fd1\u4e24\u767e\u540d\uff0c\u653e\u773c\u6574\u4e2a\u5927\u9646\uff0c\u9b54\u6cd5\u5b9e\u529b\u9996\u5c48\u4e00\u6307\u3002\u7eb5\u7136\u5982\u6b64\uff0c\u5168\u57ce\u60f3\u8981\u627e\u51fa\u4e00\u4e2a\u9b54\u6cd5\u5b66\u610f\u4e49\u4e0a\u7684\u201c\u541f\u6e38\u8bd7\u4eba\u201d\uff0c\u5374\u4e5f\u662f\u96be\u4e4b\u53c8\u96be\u3002</p><p>\u201c\u73b0\u5728\u662f\u6ca1\u6709\uff0c\u4e0d\u8fc7\u6211\u8bb0\u5f97\u5c0f\u65f6\u5019\u542c\u7236\u4eb2\u65e0\u610f\u95f4\u8bf4\u8d77\uff0c\u9634\u9b42\u57ce\u5386\u53f2\u4e0a\uff0c\u4e5f\u662f\u66fe\u7ecf\u51fa\u73b0\u8fc7\u541f\u6e38\u8bd7\u4eba\u7684\uff0c\u800c\u4e14\u662f\u5f88\u4f18\u79c0\u7684\u90a3\u79cd\uff0c\u201d\u8299\u857e\u72c4\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u5e94\u8be5\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u6211\u5f53\u65f6\u8fd8\u5c0f\uff0c\u4e5f\u6ca1\u592a\u5728\u610f\uff0c\u6ca1\u6709\u591a\u95ee\u3002\u201d</p><p>\u201c\u662f\uff0c\u6211\u4e5f\u597d\u50cf\u542c\u8fc7\u8fd9\u79cd\u8bf4\u6cd5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u66fe\u7ecf\u6709\u541f\u6e38\u8bd7\u4eba\uff0c\u90a3\u603b\u8be5\u4f1a\u7559\u4e0b\u4e9b\u8bb0\u8f7d\uff0c\u6240\u4ee5\u6211\u8003\u8651\u53bb\u56fe\u4e66\u9986\u7ffb\u7ffb\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u53d1\u73b0\u70b9\u4ec0\u4e48\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u5728\u7b2c\u4e8c\u56fe\u4e66\u9986\u91cc\u57cb\u5934\u7ffb\u627e\u4e86\u6574\u6574\u4e00\u4e0a\u5348\uff0c\u6700\u7ec8\u5374\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u4e1c\u897f\u90fd\u6ca1\u53d1\u73b0\u3002\u65e2\u6ca1\u6709\u627e\u5230\u541f\u6e38\u8bd7\u4eba\u7684\u57f9\u8bad\u6559\u6750\uff0c\u4e5f\u6ca1\u627e\u5230\u5386\u53f2\u8bb0\u8f7d\uff0c\u751a\u81f3\u6ca1\u6709\u4efb\u4f55\u4e00\u4efd\u8d44\u6599\u4e0a\u63d0\u53ca\u201c\u9634\u9b42\u57ce\u66fe\u7ecf\u51fa\u73b0\u8fc7\u541f\u6e38\u8bd7\u4eba\u201d\uff0c\u7b80\u800c\u8a00\u4e4b\uff0c\u5c31\u662f\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u201c\u771f\u5947\u602a\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u96be\u4e0d\u6210\u6211\u4eec\u90fd\u8bb0\u9519\u4e86\uff1f\u201d</p><p>\u4e00\u4e2a\u4eba\u8bb0\u5fc6\u51fa\u9519\u5f88\u6b63\u5e38\uff0c\u4f46\u4e24\u4e2a\u4eba\u90fd\u6709\u76f8\u540c\u7684\u9519\u8bef\u5370\u8c61\uff0c\u90a3\u5c31\u6709\u70b9\u8e4a\u8df7\u4e86\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u4eec\u4e5f\u65e0\u5904\u67e5\u8bc1\uff0c\u8299\u857e\u72c4\u8bb0\u5f97\u662f\u5c0f\u65f6\u5019\u542c\u7236\u4eb2\u8bf4\u7684\u2014\u2014\u5979\u7236\u4eb2\u65e9\u5df2\u7ecf\u53bb\u4e16\u4e86\uff0c\u6ca1\u6cd5\u518d\u6765\u5bf9\u8bc1\uff0c\u81f3\u4e8e\u743c\u6069\uff0c\u4ed6\u81ea\u5df1\u90fd\u4e0d\u8bb0\u5f97\u662f\u5728\u54ea\u91cc\uff0c\u5728\u4ec0\u4e48\u65f6\u5019\u542c\u8c01\u8bf4\u7684\uff0c\u53ea\u662f\u6a21\u6a21\u7cca\u7cca\u6709\u8fd9\u4e2a\u5370\u8c61\u800c\u5df2\u3002</p><p>\u7b80\u5355\u5f04\u4e86\u70b9\u5348\u9910\uff0c\u4e24\u4eba\u7ee7\u7eed\u594b\u6218\uff0c\u76f4\u5230\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u4ed6\u4eec\u51e0\u4e4e\u628a\u7b2c\u4e8c\u56fe\u4e66\u9986\u5168\u7ffb\u904d\u4e86\uff0c\u6700\u7ec8\u4e5f\u8fd8\u662f\u4e24\u624b\u7a7a\u7a7a\uff0c\u743c\u6069\u4e0d\u514d\u6709\u4e9b\u6cae\u4e27\u3002\u201c\u6ca1\u5173\u7cfb\uff0c\u201d\u8299\u857e\u72c4\u5b89\u6170\uff0c\u201c\u8fd8\u6709\u7b2c\u4e09\u56fe\u4e66\u9986\uff0c\u6211\u4eec\u518d\u53bb\u627e\u3002\u201d</p><p>\u201c\u4eca\u5929\u5c31\u7b97\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u660e\u5929\u5427\uff0c\u53cd\u6b63\u4e5f\u4e0d\u6025\u4e8e\u8fd9\u4e00\u65f6\u534a\u523b\u3002\u201d</p><p>\u8d70\u51fa\u56fe\u4e66\u9986\uff0c\u743c\u6069\u7ad9\u5728\u53f0\u9636\u4e0a\u770b\u4e86\u770b\u5929\u8272\uff0c\u201c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u5bf9\u8eab\u65c1\u5c11\u5973\u8bf4\uff0c\u201c\u6211\u4eec\u53bb\u901b\u8857\u597d\u4e0d\u597d\u3002\u201d</p><p>\u8299\u857e\u72c4\u6014\u4e86\u6014\uff0c\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\uff0c\u201c\u771f\u7684\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u771f\u7684\u5047\u7684\uff0c\u201d\u743c\u6069\u7b11\u7740\u6478\u6478\u5979\u7684\u5934\uff0c\u201c\u8bf4\u5f97\u6211\u597d\u50cf\u4ece\u6ca1\u966a\u8fc7\u4f60\u4f3c\u7684\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5566\uff0c\u201d\u5c0f\u5973\u5b69\u4f4e\u58f0\u8bf4\uff0c\u201c\u4eba\u5bb6\u4ee5\u4e3a\u2026\u2026\u4ee5\u4e3a\u4f60\u96be\u5f97\u56de\u5bb6\uff0c\u8981\u591a\u966a\u73ca\u5609\u59d0\u59d0\u561b\u3002\u201d</p><p>\u743c\u6069\u7a81\u7136\u89c9\u5f97\u5fc3\u91cc\u4e00\u9635\u5fae\u5fae\u75bc\u75db\u3002</p><p>\u201c\u50bb\u4e2b\u5934\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u522b\u8bf4\u8fd9\u79cd\u8bdd\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u2026\u2026\u201d</p><p>\u5979\u7684\u5634\u5507\u88ab\u8f7b\u8f7b\u5c01\u4f4f\uff0c\u540e\u9762\u7684\u8bdd\u5c31\u6ca1\u80fd\u8bf4\u51fa\u53e3\u3002\u534a\u5206\u949f\u540e\uff0c\u743c\u6069\u7ed3\u675f\u4e86\u8fd9\u4e2a\u957f\u957f\u7684\u543b\uff0c\u53cc\u624b\u6367\u7740\u5c11\u5973\u7684\u4fcf\u8138\uff0c\u5b9a\u5b9a\u5730\u51dd\u89c6\u7740\u5979\uff0c\u201c\u6211\u8981\u5bf9\u4f60\u597d\u4e00\u70b9\uff0c\u201d\u4ed6\u50cf\u662f\u81ea\u5632\u5730\u8bf4\uff0c\u201c\u5426\u5219\u81ea\u5df1\u90fd\u4f1a\u89c9\u5f97\u81ea\u5df1\u662f\u4e2a\u6df7\u86cb\u3002\u201d</p><p>\u201c\u73b0\u5728\u5df2\u7ecf\u5f88\u597d\u4e86\u5440\u3002\u201d</p><p>\u201c\u90a3\u5c31\u66f4\u597d\u4e00\u70b9\u3002\u201d</p><p>\u8299\u857e\u72c4\u76ee\u5149\u4f4e\u5782\uff0c\u201c\u55ef\u3002\u201d\u5979\u8f7b\u58f0\u7b54\u5e94\u3002</p><p>\u519b\u4e8b\u5316\u7ba1\u7406\u7684\u9634\u9b42\u57ce\uff0c\u5176\u5b9e\u5e76\u4e0d\u662f\u4e2a\u901b\u8857\u7684\u597d\u5730\u65b9\uff0c\u867d\u7136\u6709\u5e02\u573a\u6709\u5546\u5e97\uff0c\u4f46\u534a\u70b9\u8c08\u4e0d\u4e0a\u7e41\u534e\uff0c\u98ce\u666f\u867d\u7136\u8fd8\u4e0d\u9519\uff0c\u6bd5\u7adf\u662f\u5343\u5e74\u53e4\u57ce\uff0c\u4f46\u5c31\u8fd9\u4e48\u5927\u5730\u65b9\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u90fd\u662f\u4ece\u5c0f\u5728\u6b64\u957f\u5927\uff0c\u751f\u6d3b\u4e86\u5341\u51e0\u5e74\uff0c\u65e9\u5c31\u770b\u4e60\u60ef\u4e86\uff0c\u719f\u6089\u5f97\u4e0d\u80fd\u518d\u719f\u6089\uff0c\u6beb\u65e0\u65b0\u9c9c\u611f\u3002\u597d\u5728\u4e24\u4eba\u90fd\u4e0d\u600e\u4e48\u5728\u610f\u8fd9\u4e9b\uff0c\u7275\u7740\u624b\u968f\u610f\u8d70\u8d70\uff0c\u8bf4\u8bf4\u5f80\u4e8b\uff0c\u81ea\u7136\u5fc3\u60c5\u6109\u5feb\u3002</p><p>\u201c\u56fe\u4e66\u9986\u91cc\u67e5\u4e0d\u5230\u8d44\u6599\u4e5f\u4e0d\u8981\u7d27\u3002\u6211\u8001\u5e08\u8bf4\u8981\u6765\u4e00\u8d9f\u9634\u9b42\u57ce\uff0c\u5e94\u8be5\u4e5f\u5c31\u5728\u8fd9\u51e0\u5929\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5230\u65f6\u5019\u6b63\u597d\u95ee\u95ee\u4ed6\u3002\u201d</p><p>\u201c\u4ed6\u662f\u541f\u6e38\u8bd7\u4eba\uff1f\u201d</p><p>\u201c\u4ed6\uff0c\u7b97\u4e86\u5427\uff0c\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u4f30\u8ba1\u534a\u70b9\u97f3\u4e50\u7ec6\u80de\u90fd\u6ca1\u6709\uff0c\u4f46\u4ed6\u603b\u662f\u6d3b\u4e86\u51e0\u5343\u5e74\u7684\u5927\u5965\u672f\u5e08\uff0c\u89c1\u8bc6\u5e7f\u535a\uff0c\u6216\u8bb8\u80fd\u7ed9\u4e9b\u6307\u70b9\u3002\u201d</p><p>\u8299\u857e\u72c4\u70b9\u70b9\u5934\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u7a81\u7136\u4e00\u6014\uff0c\u50cf\u662f\u770b\u89c1\u4e86\u4ec0\u4e48\u3002\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u987a\u7740\u5979\u7684\u89c6\u7ebf\u671b\u53bb\uff0c\u53d1\u73b0\u524d\u65b9\u6709\u4e2a\u9752\u5e74\u7537\u5b50\u6b63\u671d\u4ed6\u4eec\u8fce\u9762\u8d70\u8fc7\u6765\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u83ab\u5c3c\u5361\u5c0f\u59d0\uff0c\u201d\u4ed6\u5f6c\u5f6c\u6709\u793c\u5730\u5411\u8299\u857e\u72c4\u70b9\u5934\u884c\u793c\uff0c\u7136\u540e\u5c06\u76ee\u5149\u8f6c\u5411\u743c\u6069\uff0c\u201c\u597d\u4e45\u4e0d\u89c1\uff0c\u5170\u5c3c\u65af\u7279\u3002\u201d</p><p>\u743c\u6069\u626c\u4e86\u626c\u7709\uff0c\u9732\u51fa\u5fae\u5fae\u7b11\u610f\uff0c\u201c\u4e0b\u5348\u597d\uff0c\u5e93\u80af\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u786e\u5b9e\u5f88\u4e45\u4e0d\u89c1\u4e86\u3002\u201d</p></article>"}}}]);
