(window.webpackJsonp=window.webpackJsonp||[]).push([[1623],{1646:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u5965\u6c83\u7684\u81ea\u8c6a",content:"<article><h2>\u7b2c\u4e94\u8282 \u5965\u6c83\u7684\u81ea\u8c6a</h2><p>\u743c\u6069\u56de\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u8def\u8fc7\u72ec\u89d2\u517d\u4e4b\u6d41\uff0c\u62dc\u8bbf\u8fc7\u5965\u6c83\uff0c\u628a\u838e\u73de\u514b\u4e5f\u7559\u5728\u4e86\u90a3\u91cc\uff0c\u8bf7\u8001\u5deb\u5996\u5e2e\u5fd9\u65bd\u5c55\u5b9d\u77f3\u754c\u57df\u9b54\u6cd5\u3002\u5f53\u65f6\u5965\u6c83\u8bf4\u4ed6\u8fd1\u671f\u4e5f\u8981\u53bb\u4e00\u8d9f\u9634\u9b42\u57ce\uff0c\u987a\u4fbf\u628a\u838e\u73de\u514b\u5e26\u7ed9\u743c\u6069\u3002\u7b97\u7b97\u65f6\u95f4\uff0c\u743c\u6069\u79bb\u5f00\u72ec\u89d2\u517d\u4e4b\u6d41\u540e\uff0c\u8def\u4e0a\u82b1\u4e86\u4e94\u5929\uff0c\u56de\u5230\u57ce\u4e2d\u4e5f\u8fc7\u4e86\u6574\u6574\u4e00\u5929\uff0c\u5965\u6c83\u5e94\u8be5\u662f\u5df2\u7ecf\u5b8c\u6210\u9b54\u6cd5\u4e86\u3002</p><p>\u8001\u5e08\u6709\u547d\uff0c\u4e0d\u80fd\u4e0d\u4ece\uff0c\u5f97\u9a6c\u4e0a\u8d76\u8fc7\u53bb\uff0c\u8001\u5deb\u5996\u53ef\u6ca1\u6709\u7b49\u4eba\u7684\u4e60\u60ef\u3002\u800c\u4e14\u6839\u636e\u4ee5\u5f80\u7684\u7ecf\u9a8c\uff0c\u8fd9\u4e00\u53bb\u6ca1\u6709\u4e24\u4e09\u4e2a\u5c0f\u65f6\u4e0d\u80fd\u8131\u8eab\u3002\u201c\u8299\u857e\u72c4\uff0c\u5e2e\u6211\u548c\u73ca\u5609\u8bf4\u4e00\u58f0\uff0c\u665a\u9910\u4e0d\u7528\u7b49\u6211\u56de\u6765\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u5965\u6c83\u5728\u9634\u9b42\u57ce\u7684\u5730\u4f4d\u7b97\u662f\u5ba2\u537f\uff0c\u6709\u4e00\u5ea7\u9887\u4e3a\u6c14\u6d3e\u7684\u5c45\u6240\uff0c\u4e3a\u4e86\u7167\u987e\u4ed6\u7684\u7279\u6b8a\u7231\u597d\uff0c\u91cc\u9762\u66f4\u6709\u8d85\u7ea7\u8c6a\u534e\u7684\u5927\u6d74\u5ba4\uff0c\u743c\u6069\u8d76\u5230\u7684\u65f6\u5019\uff0c\u8001\u5deb\u5996\u6b63\u8212\u8212\u670d\u670d\u5730\u8eba\u5728\u6d74\u7f38\u91cc\u6ce1\u6fa1\uff0c\u4e00\u8fb9\u6ee1\u610f\u5730\u54fc\u7740\u67d0\u9996\u66f2\u5b50\uff0c\u4f46\u5f88\u660e\u663e\u5df2\u7ecf\u8dd1\u8c03\u5230\u5341\u4e07\u516b\u5343\u91cc\u4e4b\u5916\u4e86\u3002</p><p>\u770b\u89c1\u743c\u6069\u8fdb\u6765\uff0c\u4ed6\u626c\u4e86\u626c\u624b\uff0c\u4e22\u8fc7\u4e00\u6837\u4e1c\u897f\uff0c\u201c\u63a5\u7740\u3002\u201d</p><p>\u743c\u6069\u8fde\u5fd9\u63a5\u4f4f\uff0c\u53d1\u73b0\u662f\u4e00\u9897\u88ab\u6253\u78e8\u6210\u516b\u89d2\u661f\u5f62\u7684\u8d1d\u88d8\u91cc\u5b9d\u77f3\uff0c\u5728\u638c\u5fc3\u4e2d\u53d1\u7740\u7eff\u8272\u7130\u5149\uff0c\u5b83\u88ab\u9576\u5d4c\u5728\u767d\u8272\u7684\u91d1\u5c5e\u73af\u4e2d\uff0c\u7528\u4e00\u6761\u94f6\u8272\u7ec6\u94fe\u7cfb\u7740\uff0c\u505a\u6210\u540a\u5760\u6a21\u6837\u3002\u201c\u90a3\u4e2a\u9b45\u9b54\u5c31\u5728\u91cc\u9762\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u4f60\u81ea\u5df1\u8bbe\u5b9a\u5bc6\u8bed\uff0c\u7136\u540e\u5c31\u53ef\u4ee5\u968f\u65f6\u53ec\u5524\u5979\u51fa\u73b0\u3002\u201d</p><p>\u201c\u8c22\u8c22\u8001\u5e08\u3002\u201d</p><p>\u743c\u6069\u6b63\u597d\u6ca1\u6709\u9879\u94fe\uff0c\u987a\u624b\u5c06\u5b83\u6302\u5728\u8116\u4e0a\uff0c\u4e00\u65f6\u4e5f\u4e0d\u5fd9\u7740\u8bd5\u9a8c\u3002\u548c\u5965\u6c83\u95f2\u8c08\u51e0\u53e5\uff0c\u770b\u4ed6\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u522b\u7684\u4e8b\u60c5\uff0c\u6b63\u8981\u544a\u8f9e\uff0c\u8001\u5deb\u5996\u7a81\u7136\u53eb\u4f4f\u4e86\u4ed6\uff0c\u201c\u5bf9\u4e86\uff0c\u6709\u4ef6\u4e8b\u8fd8\u5fd8\u4e86\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u4f60\u5f04\u6765\u7684\u90a3\u5f20\u8010\u745f\u5377\u8f74\uff0c\u6211\u66ff\u4f60\u8f6c\u9001\u7ed9\u590f\u591a\u4e86\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\uff0c\u5012\u4e0d\u662f\u820d\u4e0d\u5f97\uff0c\u53cd\u6b63\u90a3\u5f20\u5377\u8f74\u5bf9\u4ed6\u53c8\u6ca1\u7528\uff0c\u800c\u4e14\u5df2\u7ecf\u9001\u7ed9\u4e86\u5965\u6c83\uff0c\u5965\u6c83\u613f\u610f\u8f6c\u9001\u7ed9\u522b\u4eba\uff0c\u90a3\u662f\u4ed6\u7684\u81ea\u7531\u3002\u4ed6\u662f\u6bd4\u8f83\u5947\u602a\uff0c\u8001\u5deb\u5996\u5c45\u7136\u4f1a\u8fd9\u4e48\u6177\u6168\u5927\u65b9\uff0c\u800c\u4e14\u542c\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u8fd8\u662f\u4ee5\u743c\u6069\u7684\u540d\u4e49\u9001\u7684\u3002</p><p>\u201c\u8001\u5e08\uff0c\u60a8\u8fd9\u662f\u2026\u2026\u201d</p><p>\u5965\u6c83\u6446\u6446\u624b\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u201c\u743c\u6069\uff0c\u4f60\u5bf9\u8010\u745f\u5377\u8f74\u4e86\u89e3\u591a\u5c11\uff1f\u201d</p><p>\u201c\u4e0d\u591a\u3002\u201d</p><p>\u5bf9\u4e8e\u8010\u745f\u5377\u8f74\uff0c\u743c\u6069\u76ee\u524d\u6240\u77e5\u9053\u7684\uff0c\u9996\u5148\u5b83\u662f\u8010\u745f\u745e\u5c14\u9b54\u6cd5\u6587\u660e\u7684\u8d77\u6e90\uff0c\u6309\u7167\u5965\u6c83\u7684\u8bf4\u6cd5\uff0c\u6b63\u662f\u6709\u4e86\u5377\u8f74\uff0c\u624d\u6709\u4e86\u9b54\u6cd5\u5e1d\u56fd\uff1b\u5176\u6b21\u5b83\u552f\u6709\u5927\u5965\u672f\u5e08\u6216\u8005\u5373\u5c06\u6210\u4e3a\u5927\u5965\u672f\u5e08\u7684\u4eba\u624d\u80fd\u9605\u8bfb\uff0c\u5176\u4ed6\u4eba\u65e0\u6cd5\u770b\u89c1\u5185\u5bb9\uff0c\u6216\u8005\u50cf\u743c\u6069\u4e00\u6837\u770b\u4e86\u7acb\u523b\u5934\u75bc\u6b32\u88c2\uff1b\u518d\u6b21\u5b83\u6781\u5176\u7684\u91cd\u8981\u5b9d\u8d35\uff0c\u5965\u6c83\u6709\u51e0\u5f20\u6b8b\u9875\uff0c\u5c31\u73cd\u800c\u91cd\u4e4b\u5730\u653e\u5728\u85cf\u5b9d\u5e93\u7684\u6838\u5fc3\uff0c\u548c\u5404\u79cd\u795e\u5668\u5e76\u5217\uff0c\u9634\u9b42\u57ce\u534f\u52a9\u8428\u624e\u65af\u5766\u5e73\u5b9a\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u5185\u4e71\uff0c\u53c8\u51fa\u529b\u5e2e\u5fd9\u51fb\u6740\u5deb\u5996\u4e4b\u795e\u7ef4\u6c99\u4f26\u7684\u5316\u8eab\uff0c\u7d22\u53d6\u7684\u62a5\u916c\u4e5f\u6b63\u662f\u4e24\u9875\u8010\u745f\u5377\u8f74\u3002\u800c\u4e14\u5982\u679c\u4ed6\u6ca1\u8bb0\u9519\uff0c\u4e0a\u6b21\u56de\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u5965\u6c83\u66fe\u7ecf\u8bf4\u8981\u548c\u5e03\u96f7\u7eb3\u65af\u7b49\u4eba\u4e00\u8d77\u884c\u52a8\u524d\u5f80\u8ff7\u65af\u5353\u8bfa\uff0c\u76ee\u6807\u4e5f\u6b63\u662f\u8010\u745f\u5377\u8f74\u3002\u5927\u81f4\u4e0a\u5c31\u8fd9\u4e9b\uff0c\u5176\u4ed6\u4fbf\u4e0d\u751a\u6e05\u695a\u4e86\u3002</p><p>\u201c\u5dee\u52b2\uff0c\u592a\u5dee\u52b2\u4e86\uff0c\u201d\u5965\u6c83\u6447\u5934\uff0c\u201c\u9634\u9b42\u57ce\u7684\u6559\u80b2\u641e\u5f97\u5b9e\u5728\u592a\u5dee\u4e86\uff0c\u4f60\u597d\u6b79\u4e5f\u662f\u4e2a\u9ad8\u9636\u5deb\u5e08\u4e86\uff0c\u8fde\u8010\u745f\u5377\u8f74\u90fd\u4e0d\u77e5\u9053\uff0c\u8bf4\u51fa\u53bb\u80af\u5b9a\u4f1a\u88ab\u4eba\u7b11\u8bdd\u2026\u2026\u5750\u597d\uff0c\u201d\u4ed6\u6307\u4e86\u6307\u65c1\u8fb9\u7684\u8eba\u6905\uff0c\u201c\u6211\u6765\u7ed9\u4f60\u4e0a\u4e0a\u8bfe\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u4e56\u4e56\u5750\u4e0b\uff0c\u5f00\u59cb\u542c\u8001\u5deb\u5996\u4e0a\u5386\u53f2\u8bfe\u3002</p><p>\u201c\u8010\u745f\u745e\u5c14\u5e1d\u56fd\uff0c\u521a\u521a\u5efa\u7acb\u7684\u65f6\u5019\uff0c\u5176\u5b9e\u53ea\u662f\u4e03\u4e2a\u6e14\u6751\u7684\u8054\u76df\uff0c\u7b2c\u4e00\u4efb\u56fd\u541b\u662f\u5927\u8010\u745f\uff08Nether the Elder\uff09\uff0c\u201d\u5965\u6c83\u4e00\u8fb9\u8212\u8212\u670d\u670d\u5730\u6ce1\u6fa1\u4e00\u8fb9\u8bf4\uff0c\u201c\u90a3\u65f6\u5019\uff0c\u5176\u5b9e\u8fd8\u4e0d\u53eb\u8010\u745f\u745e\u5c14\uff0c\u53eb\u505a\u585e\u6c76\u987f\uff08Seventon\uff09\uff0c\u610f\u601d\u5c31\u662f\u4e03\u9547\u4e4b\u5730\u3002\u53c8\u8fc7\u4e86\u51e0\u5e74\uff0c\u5927\u8010\u745f\u628a\u8d5b\u6c76\u987f\u6539\u540d\u4e3a\u8010\u745f\u745e\u5c14\uff0c\u610f\u601d\u662f\u4ed6\u8010\u745f\u56fd\u738b\u6240\u7edf\u6cbb\u7684\u571f\u5730\u3002\u201d</p><p>\u201c\u5927\u8010\u745f\u662f\u4e2a\u66b4\u541b\uff0c\u7edf\u6cbb\u6b8b\u9177\uff0c\u6fc0\u8d77\u6c11\u4f17\u7684\u4e25\u91cd\u4e0d\u6ee1\u3002\u7ed3\u679c\u6709\u4e00\u5929\uff0c\u4ed6\u88ab\u4eba\u8c0b\u6740\u4e86\uff0c\u56fd\u541b\u7684\u4f4d\u7f6e\u7531\u4ed6\u7684\u513f\u5b50\u7ee7\u627f\uff0c\u8fd9\u5c31\u662f\u5c0f\u8010\u745f\uff08Neter the Younger\uff09\u3002\u5b9e\u9645\u4e0a\uff0c\u6709\u5927\u91cf\u7684\u8bc1\u636e\u8868\u660e\uff0c\u8c0b\u6740\u5927\u8010\u745f\u7684\u51f6\u624b\uff0c\u5c31\u662f\u5c0f\u8010\u745f\uff0c\u4f46\u5927\u5bb6\u90fd\u88c5\u4f5c\u6ca1\u770b\u89c1\u3002\u800c\u540e\u6765\u7684\u4e8b\u5b9e\u4e5f\u8bc1\u660e\u8fd9\u4e2a\u9009\u62e9\u662f\u6b63\u786e\u7684\uff0c\u76f8\u6bd4\u8d77\u7236\u4eb2\uff0c\u5c0f\u8010\u745f\u786e\u5b9e\u662f\u4e2a\u66f4\u79f0\u804c\u7684\u56fd\u738b\u3002\u201d</p><p>\u201c\u8fd9\u4e9b\u6211\u4e5f\u90fd\u5b66\u8fc7\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u522b\u6253\u65ad\uff0c\u201d\u5965\u6c83\u4e0d\u9ad8\u5174\u5730\u6446\u6446\u624b\uff0c\u201c\u6211\u8fd8\u6ca1\u8fdb\u5165\u6b63\u9898\u5462\uff0c\u8fd9\u662f\u5728\u915d\u917f\u6c14\u6c1b\u2014\u2014\u6211\u521a\u624d\u8bf4\u5230\u54ea\u4e86\uff1f\u201d</p><p>\u201c\u5c0f\u8010\u745f\u7ee7\u4f4d\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\uff0c\u5c0f\u8010\u745f\u7ee7\u4f4d\uff0c\u4ed6\u5e72\u5f97\u4e0d\u9519\uff0c\u5927\u5bb6\u4e5f\u90fd\u5f88\u6ee1\u610f\u3002\u7136\u540e\u7684\u67d0\u4e00\u5929\uff0c\u7cbe\u7075\u6765\u5230\u4e86\u8010\u745f\u745e\u5c14\uff0c\u548c\u6211\u4eec\u8fdb\u884c\u8d38\u6613\u3002\u4ee5\u6b64\u4e3a\u5951\u673a\uff0c\u8010\u745f\u4eba\u5f00\u59cb\u5411\u7cbe\u7075\u65cf\u5b66\u4e60\u9b54\u6cd5\u7684\u6280\u827a\u3002\u201d</p><p>\u743c\u6069\u95ed\u7740\u5634\uff0c\u7b49\u5f85\u4e0b\u6587\u3002</p><p>\u201c\u5c0f\u8010\u745f\u7684\u670b\u53cb\u745f\u745e\u6069\xb7\u5409\u5c14\u65af\uff0c\u4ed6\u662f\u7b2c\u4e00\u4e2a\u6210\u529f\u5b66\u4f1a\u9b54\u6cd5\u7684\u8010\u745f\u4eba\uff0c\u662f\u6211\u4eec\u8010\u745f\u5386\u53f2\u4e0a\u7b2c\u4e00\u4e2a\u5deb\u5e08\u3002\u201d</p><p>\u201c\u745f\u745e\u6069\xb7\u5409\u5c14\u65af\u7b2c\u4e00\uff08Therion the First of Gers\uff09\u3002\u201d\u743c\u6069\u8bf4\uff0c\u8fd9\u662f\u5bf9\u90a3\u4f4d\u5148\u9a71\u8005\u7684\u5c0a\u79f0\u3002</p><p>\u201c\u5bf9\uff0c\u201d\u5965\u6c83\u70b9\u5934\uff0c\u201c\u867d\u7136\u7ec8\u5176\u4e00\u751f\uff0c\u5409\u5c14\u65af\u4e5f\u5c31\u53ea\u5b66\u4f1a\u4e86\u51e0\u4e2a\u620f\u6cd5\uff0c\u80fd\u591f\u653e\u653e\u7130\u706b\u3001\u95ea\u95ea\u5149\u5413\u552c\u4eba\u800c\u5df2\uff0c\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4ee5\u4ed6\u4e3a\u5f00\u7aef\uff0c\u8010\u745f\u745e\u5c14\u5f00\u59cb\u63a5\u89e6\u9b54\u6cd5\uff0c\u5f00\u59cb\u51fa\u73b0\u5deb\u5e08\u2014\u2014\u6211\u4eec\u79f0\u4e4b\u4e3a\u2018\u5965\u672f\u5e08\u2019\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\u8868\u793a\u5e94\u5408\uff0c\u201c\u662f\u7684\u3002\u201d</p><p>\u201c\u4ece\u7b2c\u4e00\u4e2a\u5965\u672f\u5e08\u5409\u5c14\u65af\u5f00\u59cb\uff0c\u5927\u6982\u8fc7\u4e86\u56db\u767e\u5e74\uff0c\u8010\u745f\u745e\u5c14\u5386\u53f2\u4e0a\uff0c\u7ec8\u4e8e\u8bde\u751f\u4e86\u7b2c\u4e00\u4e2a\u5927\u5965\u672f\u5e08\uff0c\u5eb7\u6770\u8bfa\xb7\u827e\u6069\uff0c\u201d\u5965\u6c83\u7ee7\u7eed\u8bf4\uff0c\u201c\u4ece\u6b64\u4e4b\u540e\uff0c\u76f4\u5230\u706d\u56fd\uff0c\u4e09\u5343\u4e09\u767e\u4f59\u5e74\u7684\u5386\u53f2\u4e2d\uff0c\u8010\u745f\u745e\u5c14\u8bde\u751f\u4e86\u8fd1\u5343\u540d\u5927\u5965\u672f\u5e08\uff0c\u521b\u9020\u4e86\u53e4\u5f80\u4eca\u6765\u6700\u8f89\u714c\u707f\u70c2\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u5c31\u8fde\u6614\u65e5\u7684\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u4e5f\u671b\u5c18\u83ab\u53ca\uff0c\u4ed6\u4eec\u5168\u56fd\u7684\u5deb\u5e08\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5168\u90fd\u52a0\u8d77\u6765\uff0c\u6700\u9f0e\u76db\u65f6\u671f\u4e5f\u4e0d\u4f1a\u8d85\u8fc7\u4e00\u5343\u2014\u2014\u743c\u6069\uff0c\u4f60\u6709\u6ca1\u6709\u60f3\u8fc7\uff0c\u8fd9\u6837\u7684\u8f89\u714c\u4ece\u4f55\u800c\u6765\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u81ea\u7136\u662f\u901a\u8fc7\u5411\u7cbe\u7075\u5b66\u4e60\u9b54\u6cd5\uff0c\u9010\u6e10\u53d1\u626c\u5149\u5927\uff0c\u7ecf\u5386\u56db\u767e\u5e74\u7684\u79ef\u7d2f\uff0c\u6700\u7ec8\u6709\u6b64\u6210\u5c31\u2026\u2026\u201d</p><p>\u201c\u80e1\u626f\uff01\u201d\u5965\u6c83\u5288\u5934\u6253\u65ad\uff0c\u201c\u5411\u7cbe\u7075\u5b66\u4e60\u9b54\u6cd5\uff1f\u7cbe\u7075\u80fd\u6559\u4ec0\u4e48\uff1f\u201d\u4ed6\u4e0d\u5c51\u5730\u8bf4\uff0c\u201c\u7cbe\u7075\u6587\u660e\u65e9\u4e8e\u4eba\u7c7b\uff0c\u4ed6\u4eec\u7684\u9b54\u6cd5\u662f\u6709\u4e9b\u95e8\u9053\uff0c\u8fd9\u4e2a\u6211\u4e0d\u5426\u8ba4\uff0c\u4f46\u548c\u4eba\u7c7b\u7ec8\u7a76\u4e0d\u751a\u76f8\u5408\u3002\u66f4\u4f55\u51b5\u7cbe\u7075\u9b54\u6cd5\u771f\u6b63\u9ad8\u6df1\u7684\u79d8\u827a\uff0c\u90a3\u662f\u638c\u63e1\u5728\u7cbe\u7075\u738b\u5ef7\u624b\u4e2d\uff0c\u4e5f\u662f\u8840\u8109\u9650\u5b9a\u7684\uff0c\u552f\u6709\u65e5\u7cbe\u7075\u624d\u80fd\u8fd0\u7528\uff0c\u8fde\u666e\u901a\u7684\u7cbe\u7075\u90fd\u5b66\u4e0d\u4f1a\uff0c\u66f4\u522b\u8bf4\u6559\u7ed9\u6211\u4eec\u4e86\u3002\u6211\u4eec\u4ece\u7cbe\u7075\u90a3\u91cc\u662f\u5b66\u5230\u4e86\u4e9b\u4e1c\u897f\uff0c\u4f46\u4e5f\u5c31\u662f\u70b9\u76ae\u6bdb\uff0c\u5982\u679c\u5355\u51ed\u8fd9\u70b9\u76ae\u6bdb\uff0c\u522b\u8bf4\u56db\u767e\u5e74\uff0c\u5c31\u7b97\u56db\u4e07\u5e74\uff0c\u4e5f\u522b\u60f3\u80fd\u8bde\u751f\u4e00\u4e2a\u5927\u5965\u672f\u5e08\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u662f\u56e0\u4e3a\u2026\u2026\u8010\u745f\u5377\u8f74\uff1f\u201d</p><p>\u201c\u8010\u745f\u5377\u8f74\uff0c\u201d\u5965\u6c83\u65ad\u7136\u8bf4\uff0c\u201c\u8010\u745f\u745e\u5c14\u7684\u5168\u90e8\u8f89\u714c\uff0c\u6b63\u662f\u8d77\u6e90\u4e8e\u8fd9\u90e8\u5377\u8f74\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>326NY\uff08\u8010\u745f\u7eaa\u5143326\u5e74\uff09\uff0c\u5728\u4e00\u4e2a\u5076\u7136\u7684\u673a\u7f18\u4e4b\u4e0b\uff0c\u8010\u745f\u4eba\u53d1\u73b0\u4e86\u8fd9\u5377\u5171\u4e00\u767e\u9875\u7684\u9ec4\u91d1\u5377\u8f74\uff0c\u4ece\u6b64\u4e4b\u540e\uff0c\u8010\u745f\u745e\u5c14\u7684\u9b54\u6cd5\u6280\u827a\u83b7\u5f97\u4e86\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\u7684\u98de\u8dc3\u3002\u5965\u672f\u5e08\u4eec\u629b\u5f03\u4e86\u7cbe\u7075\u9b54\u6cd5\uff0c\u4ee5\u8010\u745f\u5377\u8f74\u4e3a\u57fa\u7840\u548c\u7406\u8bba\u6307\u5bfc\uff0c\u72ec\u7acb\u53d1\u5c55\u51fa\u4e00\u5957\u5168\u65b0\u7684\u9b54\u6cd5\u5b66\uff0c\u4e5f\u5c31\u662f\u4eca\u65e5\u4eba\u7c7b\u9b54\u6cd5\u7684\u539f\u578b\u3002</p><p>\u201c\u8010\u745f\u5377\u8f74\u5206\u4e3a\u4e0a\u4e0b\u4e24\u5377\uff0c\u5404\u4e94\u5341\u9875\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u5176\u4e2d\u5185\u5bb9\u5305\u7f57\u4e07\u8c61\uff0c\u56ca\u62ec\u4e86\u4e00\u5207\u9b54\u6cd5\u77e5\u8bc6\u548c\u6280\u827a\u3002\u800c\u4e14\u5b83\u662f\u2018\u6c38\u65e0\u6b62\u5c3d\u2019\u7684\uff0c\u65e0\u8bba\u662f\u518d\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u6bcf\u6b21\u9605\u8bfb\u5b83\uff0c\u90fd\u80fd\u591f\u518d\u5ea6\u83b7\u5f97\u65b0\u7684\u9886\u609f\uff0c\u6709\u6240\u8fdb\u6b65\uff0c\u65e0\u8bba\u662f\u518d\u6e0a\u535a\u7684\u5b66\u8005\uff0c\u4e5f\u4e0d\u53ef\u80fd\u7a77\u5c3d\u5b83\u7684\u5185\u5bb9\uff0c\u56e0\u4e3a\u5b83\u4f1a\u6c38\u8fdc\u53d8\u5316\uff0c\u4e0d\u65ad\u6df1\u5165\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b83\u76f8\u5f53\u4e8e\u4e00\u672c\u65e0\u6240\u4e0d\u5305\u7684\u9b54\u6cd5\u767e\u79d1\u5168\u4e66\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u4e0d\uff0c\u4e0d\u662f\u767e\u79d1\u5168\u4e66\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u5b83\u53ea\u662f\u9b54\u6cd5\u7406\u8bba\uff0c\u5176\u4e2d\u4e0d\u6d89\u53ca\u5177\u4f53\u7684\u6280\u827a\u3002\u8010\u745f\u5377\u8f74\u4e0d\u4f1a\u6559\u4f60\u4efb\u4f55\u4e00\u79cd\u6cd5\u672f\uff0c\u54ea\u6015\u662f\u6700\u7b80\u5355\u7684\u620f\u6cd5\uff0c\u5b83\u91cc\u9762\u4e5f\u6ca1\u6709\u8bb0\u8f7d\u3002\u5e1d\u56fd\u5386\u53f2\u4e0a\u51fa\u73b0\u7684\u6240\u6709\u9b54\u6cd5\uff0c\u5168\u90fd\u662f\u5927\u5965\u672f\u5e08\u4eec\u81ea\u5df1\u53d1\u660e\u7684\uff0c\u6ca1\u6709\u4e00\u4e2a\u76f4\u63a5\u6765\u81ea\u5377\u8f74\u3002\u4f46\u5018\u82e5\u6ca1\u6709\u5377\u8f74\uff0c\u90a3\u4e48\u4e5f\u5c31\u4e0d\u4f1a\u6709\u4efb\u4f55\u4e00\u4e2a\u65b0\u6cd5\u672f\uff0c\u4e5f\u4e0d\u4f1a\u6709\u5927\u5965\u672f\u5e08\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u793a\u610f\u660e\u767d\uff0c\u4f46\u4ed6\u7d27\u63a5\u7740\u53c8\u63d0\u51fa\u4e00\u4e2a\u95ee\u9898\uff0c\u201c\u4f46\u8010\u745f\u5377\u8f74\u4e0d\u662f\u5fc5\u987b\u8981\u6709\u5927\u5965\u672f\u5e08\u6216\u8005\u63a5\u8fd1\u7684\u9020\u8be3\u624d\u80fd\u7ed9\u9605\u8bfb\u5417\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u5728\u8010\u745f\u5377\u8f74\u4e4b\u524d\uff0c\u6ca1\u6709\u5927\u5965\u672f\u5e08\uff0c\u5eb7\u6770\u8bfa\u90fd\u8fd8\u6ca1\u51fa\u751f\u3002\u90a3\u5927\u5bb6\u662f\u5982\u4f55\u9605\u8bfb\u5b83\u7684\uff1f\u201d</p><p>\u201c\u6700\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u5377\u8f74\u5e76\u6ca1\u6709\u8fd9\u79cd\u9650\u5236\uff0c\u201d\u5965\u6c83\u89e3\u91ca\uff0c\u201c\u4efb\u4f55\u5deb\u5e08\u90fd\u53ef\u4ee5\u9605\u8bfb\u5b83\uff0c\u90fd\u53ef\u4ee5\u4ece\u4e2d\u6c72\u53d6\u77e5\u8bc6\uff0c\u83b7\u5f97\u9886\u609f\u3002\u4f46\u8010\u745f\u5377\u8f74\u6709\u4e2a\u9ebb\u70e6\uff0c\u5b83\u81ea\u8eab\u4e5f\u662f\u53ef\u4ee5\u2018\u8fdb\u5316\u2019\u7684\uff0c\u968f\u7740\u9605\u8bfb\u8005\u8d8a\u6765\u8d8a\u591a\uff0c\u6c34\u5e73\u8d8a\u6765\u8d8a\u9ad8\uff0c\u57fa\u7840\u7684\u9b54\u6cd5\u5b66\u4e5f\u5df2\u7ecf\u5efa\u7acb\u8d77\u6765\uff0c\u5b83\u5c31\u4e0d\u518d\u63d0\u4f9b\u8fd9\u65b9\u9762\u7684\u77e5\u8bc6\u4e86\uff0c\u751a\u81f3\u63d0\u9ad8\u4e86\u9605\u8bfb\u6743\u9650\u3002\u5c31\u597d\u50cf\u8bf4\uff0c\u5982\u679c\u60f3\u8981\u4e86\u89e3\u90a3\u4e9b\u2018\u666e\u901a\u2019\u7684\u9b54\u6cd5\u77e5\u8bc6\uff0c\u76f4\u63a5\u53ef\u4ee5\u53bb\u5b66\u6821\uff0c\u53bb\u627e\u5bfc\u5e08\uff0c\u5377\u8f74\u53ea\u5bf9\u9020\u8be3\u9ad8\u6df1\u7684\u7814\u7a76\u8005\u5f00\u653e\uff0c\u4e5f\u5c31\u662f\u5927\u5965\u672f\u5e08\u4e86\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u901a\u8fc7\u5965\u6c83\u7684\u89e3\u91ca\uff0c\u743c\u6069\u5927\u81f4\u5bf9\u8010\u745f\u5377\u8f74\u6709\u4e86\u4e00\u5b9a\u7684\u4e86\u89e3\uff0c\u4e5f\u660e\u767d\u4e3a\u4ec0\u4e48\u9634\u9b42\u57ce\u3001\u5965\u6c83\u90fd\u5bf9\u5b83\u5982\u6b64\u770b\u91cd\uff0c\u5fd7\u5728\u5fc5\u5f97\u3002\u4e00\u672c\u5305\u7f57\u4e07\u8c61\u5e76\u4e14\u80fd\u591f\u65e0\u9650\u8fdb\u5316\uff0c\u5185\u5bb9\u6c38\u65e0\u7a77\u5c3d\u7684\u9b54\u6cd5\u5178\u7c4d\uff0c\u867d\u7136\u53ea\u662f\u7406\u8bba\u6307\u5bfc\uff0c\u4e0d\u6d89\u53ca\u4efb\u4f55\u5177\u4f53\u7684\u64cd\u4f5c\uff0c\u5bf9\u4e8e\u8fd9\u4e9b\u5927\u5965\u672f\u5e08\u4eec\u800c\u8a00\uff0c\u4e5f\u7edd\u5bf9\u662f\u65e0\u4ef7\u4e4b\u5b9d\u3002\u5b9e\u9645\u4e0a\uff0c\u5230\u4e86\u4ed6\u4eec\u8fd9\u79cd\u5730\u6b65\uff0c\u5df2\u7ecf\u5dee\u4e0d\u591a\u662f\u7531\u201c\u6280\u201d\u5165\u201c\u9053\u201d\uff0c\u5c31\u7b97\u6559\u4ed6\u4eec\u4e00\u767e\u4e2a\u65b0\u9b54\u6cd5\uff0c\u4e5f\u4e0d\u5982\u51e0\u53e5\u7406\u8bba\u4e0a\u7684\u7a0d\u7a0d\u70b9\u62e8\uff0c\u5f80\u5f80\u5c31\u80fd\u8c41\u7136\u5f00\u6717\uff0c\u8fdb\u5165\u5168\u65b0\u7684\u5883\u754c\u5929\u5730\u3002</p><p>\u53ef\u662f\u2026\u2026</p><p>\u201c\u8fd9\u4e48\u73cd\u8d35\u7684\u4e1c\u897f\uff0c\u4e3a\u4ec0\u4e48\u73b0\u5728\u53d8\u6210\u4e86\u6b8b\u7ae0\u65ad\u9875\u5462\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u96be\u9053\u8bf4\u5927\u5965\u672f\u5e08\u4eec\u628a\u5b83\u62c6\u5f00\u74dc\u5206\u4e86\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u201d\u5965\u6c83\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u54ea\u6709\u8fd9\u79cd\u8352\u5510\u7684\u4e8b\u60c5\u3002\u8010\u745f\u5377\u8f74\u662f\u4e00\u4e2a\u6574\u4f53\uff0c\u62c6\u5f00\u4ef7\u503c\u53ea\u4f1a\u964d\u4f4e\u3002\u6bd4\u5982\u8bf4\u4f60\u62ff\u6765\u7684\u90a3\u5f20\u5377\u8f74\uff0c\u56e0\u4e3a\u53ea\u6709\u5355\u72ec\u7684\u4e00\u9875\uff0c\u6240\u4ee5\u4e0a\u9762\u4f1a\u51fa\u73b0\u7684\uff0c\u5168\u90fd\u662f\u57fa\u7840\u7684\u4ea1\u7075\u672f\u7406\u8bba\uff0c\u5bf9\u6211\u4eec\u6839\u672c\u6ca1\u7528\uff1b\u53ea\u6709\u96c6\u9f50\u66f4\u591a\uff0c\u8fd8\u5fc5\u987b\u662f\u76f8\u4e92\u914d\u5957\u7684\u7ae0\u8282\uff0c\u5b83\u624d\u80fd\u663e\u73b0\u51fa\u66f4\u6df1\u5965\u7684\u5185\u5bb9\u3002\u6240\u4ee5\u6211\u4eec\u600e\u4e48\u53ef\u80fd\u4f1a\u628a\u5377\u8f74\u7ed9\u62c6\u4e86\u74dc\u5206\uff0c\u90a3\u6837\u5c82\u4e0d\u662f\u5927\u5bb6\u8c01\u4e5f\u5f97\u4e0d\u5230\u3002\u201d</p><p>\u201c\u90a3\u5b83\u7531\u8c01\u4fdd\u7ba1\uff1f\u53c8\u600e\u4e48\u6210\u4e86\u73b0\u5728\u8fd9\u6837\u5b50\uff1f\u201d</p><p>\u5965\u6c83\u8138\u8272\u53d8\u5f97\u4e25\u8083\u8d77\u6765\uff0c\u201c\u8fd9\u5c31\u662f\u95ee\u9898\u7684\u5173\u952e\u6240\u5728\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8010\u745f\u5377\u8f74\u88ab\u53d1\u73b0\u7684\u65f6\u5019\uff0c\u5c0f\u8010\u745f\u5df2\u7ecf\u901d\u4e16\u591a\u5e74\uff0c\u4ed6\u6ca1\u6709\u7ee7\u627f\u4eba\uff0c\u738b\u4f4d\u5c31\u6b64\u7a7a\u60ac\uff0c\u540e\u6765\u7d22\u6027\u5c31\u5e9f\u9664\u4e86\u5e1d\u5236\uff0c\u7531\u5965\u672f\u5e08\u8054\u5e2d\u4f1a\u8bae\u6765\u6267\u638c\u5927\u6743\uff0c\u8010\u745f\u5377\u8f74\u4e5f\u7531\u8054\u5e2d\u4f1a\u8bae\u5171\u540c\u6267\u638c\u3002\u540e\u6765\u5eb7\u6770\u8bfa\xb7\u827e\u6069\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u5377\u8f74\u4fbf\u7531\u4ed6\u4fdd\u7ba1\uff0c\u518d\u540e\u6765\u4ed6\u4f20\u7ed9\u4e86\u4f0a\u5965\u52d2\u59c6\u3002\u201d</p><p>\u201c\u8010\u745f\u4e4b\u7236\u4f0a\u5965\u52d2\u59c6\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u6d6e\u7a7a\u57ce\u7684\u53d1\u660e\u8005\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u5c31\u662f\u4ed6\uff0c\u5e1d\u56fd\u5386\u53f2\u4e0a\u516c\u8ba4\u7684\u4e24\u5927\u5929\u624d\u4e4b\u4e00\u3002\u8010\u745f\u5377\u8f74\u7684\u83b7\u5f97\uff0c\u5f00\u59cb\u4e86\u8010\u745f\u7684\u5d1b\u8d77\uff0c\u5eb7\u6770\u8bfa\u7684\u51fa\u73b0\uff0c\u9884\u793a\u7740\u8010\u745f\u7684\u8f89\u714c\uff0c\u4f46\u771f\u6b63\u8981\u8bf4\u540e\u6765\u9b54\u6cd5\u6587\u660e\u7684\u5f00\u521b\u8005\uff0c\u90a3\u8fd8\u662f\u4f0a\u5965\u52d2\u59c6\u3002\u201d</p><p>\u4f0a\u5965\u52d2\u59c6\u4f5c\u4e3a\u8010\u745f\u5386\u53f2\u4e0a\u6700\u8457\u540d\u7684\u5927\u5965\u672f\u5e08\uff0c\u53d1\u660e\u4e86\u6d6e\u7a7a\u57ce\uff0c\u4ee5\u53ca\u4ee5\u4ed6\u7684\u540d\u5b57\u547d\u540d\u7684\u201c\u4f0a\u5965\u52d2\u59c6\u4e4b\u957f\u751f\u672f\u201d\uff0c\u524d\u8005\u8ba9\u4eba\u7c7b\u5f81\u670d\u5929\u7a7a\u548c\u5927\u5730\uff0c\u540e\u8005\u5219\u8ba9\u5927\u5965\u672f\u5e08\u4eec\u80fd\u591f\u501f\u52a9\u9b54\u6cd5\u8fd1\u4e4e\u6c38\u4e45\u6027\u5730\u5ef6\u957f\u5bff\u547d\u3002\u6b63\u662f\u8fd9\u4e24\u5927\u6210\u5c31\uff0c\u771f\u6b63\u5f00\u521b\u4e86\u8f89\u714c\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u88ab\u5c0a\u79f0\u4e3a\u201c\u8010\u745f\u4e4b\u7236\u201d\u3002</p><p>\u201c\u8010\u745f\u5377\u8f74\u7531\u5eb7\u5409\u6069\u4f20\u7ed9\u4e86\u4f0a\u5965\u52d2\u59c6\uff0c\u4ece\u6b64\u4fbf\u7531\u4ed6\u4fdd\u7ba1\uff0c\u5176\u4ed6\u5927\u5965\u672f\u5e08\u60f3\u8981\u9605\u8bfb\u5377\u8f74\uff0c\u5c31\u5f97\u5148\u63d0\u51fa\u7533\u8bf7\uff0c\u83b7\u5f97\u8bb8\u53ef\u624d\u884c\u3002\u4f0a\u5965\u52d2\u59c6\u58f0\u671b\u6781\u76db\uff0c\u65e0\u4eba\u53ef\u6bd4\uff0c\u53c8\u662f\u5927\u5965\u672f\u5e08\u8054\u5e2d\u4f1a\u8bae\u7684\u9886\u8896\uff0c\u6267\u638c\u5377\u8f74\u540d\u6b63\u8a00\u987a\uff0c\u8c01\u4e5f\u6ca1\u6cd5\u63d0\u51fa\u5f02\u8bae\u3002\u53ef\u662f\u5462\uff0c\u201d\u5965\u6c83\u563f\u563f\u7b11\u4e86\u51e0\u58f0\uff0c\u201c\u5377\u8f74\u540e\u6765\u5931\u7a83\u4e86\u3002\u201d</p><p>\u201c\u5931\u7a83\u4e86\uff1f\u201d</p><p>\u201c\u5931\u7a83\u4e86\uff0c\u201d\u5965\u6c83\u70b9\u5934\uff0c\u201c\u6700\u521d\u5931\u7a83\u7684\u662f\u4e0a\u5377\uff0c\u4e0d\u77e5\u9053\u662f\u8c01\u4e0b\u624b\uff0c\u4e5f\u4e0d\u77e5\u9053\u5377\u8f74\u4e0b\u843d\uff0c\u751a\u81f3\u4e0d\u77e5\u9053\u5177\u4f53\u7684\u4f5c\u6848\u624b\u6cd5\u3002\u5377\u8f74\u597d\u7aef\u7aef\u5730\u4fdd\u5b58\u5728\u4f0a\u5965\u52d2\u59c6\u7684\u5bc6\u5ba4\u91cc\uff0c\u81f3\u5c11\u6709\u8d85\u8fc7\u4e00\u767e\u79cd\u5f3a\u529b\u9b54\u6cd5\u5b88\u62a4\uff0c\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u9664\u4e86\u4f0a\u5965\u52d2\u59c6\u4e4b\u5916\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u591f\u6084\u65e0\u58f0\u606f\u5730\u95ef\u5165\uff0c\u5c31\u8fde\u795e\u7947\u4e5f\u529e\u4e0d\u5230\u2014\u2014\u4f46\u4e8b\u5b9e\u5c31\u662f\uff0c\u5377\u8f74\u7684\u4e0a\u5377\u5931\u8e2a\u4e86\u3002\u201d</p><p>\u201c\u53ea\u5931\u8e2a\u4e86\u4e0a\u5377\uff1f\u201d</p><p>\u201c\u4e3a\u4e86\u5b89\u5168\u8d77\u89c1\uff0c\u4e0a\u4e0b\u4e24\u5377\u662f\u5206\u5f00\u653e\u7684\uff0c\u201d\u5965\u6c83\u89e3\u91ca\uff0c\u201c\u4e0a\u5377\u88ab\u5077\u8d70\u4e86\uff0c\u4e0b\u5377\u8fd8\u5728\u3002\u4f46\u540e\u6765\u5728\u5e1d\u56fd\u672b\u671f\uff0c\u5b83\u4e5f\u5931\u8e2a\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u662f\u7a81\u7136\u5931\u8e2a\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u662f\u4f0a\u5965\u52d2\u59c6\u5148\u7a81\u7136\u5931\u8e2a\u4e86\uff0c\u4ed6\u653e\u5f03\u4e86\u6d6e\u7a7a\u57ce\uff0c\u4e0d\u77e5\u9053\u53bb\u4e86\u54ea\u91cc\uff0c\u5927\u5bb6\u4e00\u7247\u6050\u614c\uff0c\u5377\u8f74\u5c31\u5f97\u518d\u627e\u4e2a\u4eba\u6765\u4fdd\u7ba1\u3002\u7ecf\u8fc7\u6fc0\u70c8\u7684\u4e89\u5435\u626f\u76ae\uff0c\u6700\u7ec8\u9009\u5b9a\u4e86\u4eba\u9009\uff0c\u90a3\u5c31\u662f\u5361\u5c14\u8428\u65af\u3002\u201d</p><p>\u5361\u5c14\u8428\u65af\uff0c\u5e1d\u56fd\u5386\u53f2\u4e0a\u6700\u5e74\u8f7b\u7684\u5929\u624d\uff0c\u4e8c\u5341\u4e8c\u5c81\u4fbf\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u8fd9\u4efd\u6210\u5c31\u65f7\u53e4\u7edd\u4eca\uff0c\u65e0\u4eba\u80fd\u6bd4\u3002\u7531\u4ed6\u6765\u63a5\u638c\u5377\u8f74\uff0c\u5012\u4e5f\u7b97\u662f\u4f17\u671b\u6240\u5f52\u3002</p><p>\u201c\u53ef\u662f\u5462\uff0c\u5c31\u5728\u5c06\u5377\u8f74\u4e0b\u5377\u4ece\u4f0a\u5965\u52d2\u59c6\u7684\u5bc6\u5ba4\u4e2d\u53d6\u51fa\uff0c\u9001\u5f80\u5361\u5c14\u8428\u65af\u7684\u6d6e\u7a7a\u57ce\u7684\u8def\u4e0a\uff0c\u5b83\u5931\u8e2a\u4e86\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u62a4\u536b\u8005\u8d85\u8fc7\u767e\u540d\uff0c\u5168\u90fd\u662f\u4f18\u79c0\u7684\u5965\u672f\u5e08\u548c\u6b66\u58eb\uff0c\u5176\u4e2d\u751a\u81f3\u5305\u62ec\u4e24\u540d\u5927\u5965\u672f\u5e08\uff0c\u4ed6\u4eec\u90fd\u662f\u5361\u5c14\u8428\u65af\u7684\u5b66\u751f\u3002\u4f46\u5c31\u5728\u534a\u8def\u4e0a\uff0c\u4ed6\u4eec\u88ab\u6740\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u5377\u8f74\u4e5f\u88ab\u62a2\u8d70\uff0c\u4ece\u6b64\u4e0b\u843d\u4e0d\u660e\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\u4e0d\u8bed\u3002</p><p>\u201c\u5377\u8f74\u5931\u8e2a\uff0c\u81ea\u7136\u5c55\u5f00\u5927\u641c\u6355\uff0c\u4f46\u6700\u7ec8\u4e5f\u662f\u4e00\u65e0\u6240\u83b7\u3002\u518d\u5f80\u540e\u2026\u2026\u8d39\u6797\u9b54\u8475\u5165\u4fb5\uff0c\u5e1d\u56fd\u5782\u5782\u6b32\u5760\uff0c\u5927\u5bb6\u90fd\u5fd9\u7740\u9003\u547d\uff0c\u8c01\u4e5f\u6ca1\u5fc3\u601d\u6765\u7ba1\u8fd9\u4e9b\u3002\u6700\u540e\u7684\u7ed3\u679c\u4f60\u77e5\u9053\u7684\uff0c\u5e1d\u56fd\u9668\u706d\uff0c\u6240\u6709\u7684\u6d6e\u7a7a\u57ce\u5c3d\u6570\u5760\u6bc1\uff0c\u5927\u5965\u672f\u5e08\u4eec\u6b7b\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u5f53\u7136\u6211\u662f\u4f8b\u5916\u2014\u2014\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u6ca1\u4e8b\u5417\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\u3002</p><p>\u201c\u5e1d\u56fd\u9668\u706d\u4e4b\u65f6\uff0c\u9b54\u7f51\u5b8c\u5168\u5d29\u6e83\uff0c\u6240\u6709\u7684\u9b54\u6cd5\u5168\u90fd\u5931\u6548\uff0c\u8fd9\u5c31\u662f\u6d6e\u7a7a\u57ce\u5760\u6bc1\u7684\u539f\u56e0\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u6d6e\u7a7a\u57ce\u4e0a\u7684\u5927\u5965\u672f\u5e08\u4eec\uff0c\u56e0\u4e3a\u4e27\u5931\u4e86\u9b54\u6cd5\uff0c\u548c\u5bfb\u5e38\u4eba\u65e0\u5f02\uff0c\u6240\u4ee5\u4e5f\u5168\u90fd\u6454\u6b7b\u4e86\u3002\u62c9\u6c83\u514b\u80fd\u591f\u6d3b\u5230\u73b0\u5728\uff0c\u90a3\u662f\u4ed6\u6070\u597d\u517b\u4e86\u6761\u9f99\uff0c\u9a91\u7740\u9f99\u9003\u8dd1\u4e86\uff1b\u590f\u591a\u80fd\u591f\u8eb2\u8fc7\u4e00\u52ab\uff0c\u90a3\u662f\u4ed6\u6070\u597d\u628a\u6d6e\u7a7a\u57ce\u79fb\u5230\u4e86\u9634\u5f71\u4f4d\u9762\uff0c\u838e\u5c14\u7528\u795e\u529b\u6258\u8d77\u57ce\u5e02\uff0c\u6551\u4e86\u4ed6\u4e00\u547d\uff1b\u4ed6\u4eec\u90fd\u662f\u8fd0\u6c14\u597d\uff0c\u4f46\u6211\u4e0d\u662f\uff0c\u6211\u662f\u9760\u81ea\u5df1\u7684\u667a\u6167\u548c\u806a\u660e\uff0c\u201d\u5965\u6c83\u6d0b\u6d0b\u5f97\u610f\u5730\u8bf4\uff0c\u201c\u6211\u65e9\u5c31\u77e5\u9053\u98de\u5728\u5929\u4e0a\u4e0d\u5b89\u5168\uff0c\u6240\u4ee5\u6211\u538b\u6839\u5c31\u6ca1\u6709\u5b66\u4ed6\u4eec\u5efa\u4ec0\u4e48\u6d6e\u7a7a\u57ce\uff0c\u4e00\u76f4\u5446\u5728\u5730\u4e0a\uff0c\u81ea\u7136\u6ca1\u4e8b\u3002\u201d</p><p>\u201c\u8001\u5e08\uff0c\u60a8\u679c\u7136\u6709\u5148\u89c1\u4e4b\u660e\u3002\u201d\u743c\u6069\u8d76\u7d27\u62cd\u9a6c\u5c41\u3002</p><p>\u201c\u90a3\u5f53\u7136\uff0c\u5426\u5219\u5927\u5bb6\u600e\u4e48\u90fd\u5c0a\u79f0\u6211\u4e3a\u2018\u4e0d\u6b7b\u8005\u2019\uff0c\u201d\u5965\u6c83\u77dc\u6301\u5730\u70b9\u5934\uff0c\u201c\u743c\u6069\uff0c\u6211\u8ddf\u4f60\u8bf4\uff0c\u8bba\u53ca\u9003\u751f\u4fdd\u547d\u7684\u672c\u4e8b\uff0c\u4f60\u8001\u5e08\u6211\u5982\u679c\u81ea\u79f0\u7b2c\u4e8c\uff0c\u90a3\u5c31\u6ca1\u4eba\u6562\u8bf4\u7b2c\u4e00\u554a\u3002\u201d</p><p>\u8001\u5e08\uff0c\u8fd9\u6050\u6015\u4e0d\u662f\u4ec0\u4e48\u503c\u5f97\u5439\u5618\u7684\u4e8b\u60c5\u5427\u2026\u2026</p></article>"}}}]);
