(window.webpackJsonp=window.webpackJsonp||[]).push([[163],{186:function(p){p.exports={chapterTitle:null,title:"\u7b2c163\u7ae0 \u4e0d\u6b7b\u5fc3",content:"<article><h2>\u7b2c163\u7ae0 \u4e0d\u6b7b\u5fc3</h2><p>\u3000\u3000\u5728\u9526\u6e56\u6301\u80a1\u7684\u4e8b\u60c5\u7792\u4e0d\u4e86\u591a\u4e45\uff0c\u7231\u8fbe\u7535\u5b50\u6b63\u5728\u8fdb\u884c\u8bd5\u751f\u4ea7\u524d\u7684\u51c6\u5907\uff0c\u4e00\u65e6\u542f\u52a8\u5e02\u573a\u8fd0\u4f5c\uff0c\u53ef\u80fd\u8fde\u8d76\u56de\u6d77\u5dde\u53c2\u52a0\u671f\u672b\u8003\u8bd5\u7684\u65f6\u95f4\u90fd\u6ca1\u6709\uff0c\u5f20\u606a\u51b3\u5b9a\u8fd9\u65f6\u5019\u7a0d\u5fae\u8ddf\u5bb6\u91cc\u9732\u4e00\u4e0b\u5e95\uff0c\u514d\u5f97\u4ed6\u4eec\u5230\u771f\u76f8\u66b4\u5149\u7684\u90a3\u4e00\u5929\u4e0d\u77e5\u6240\u63aa\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u6881\u683c\u73cd\u5230\u5e95\u6ca1\u80fd\u4ece\u5f20\u606a\u5634\u91cc\u95ee\u51fa\u5177\u4f53\u7684\u6570\u989d\uff0c\u4f46\u89c1\u4ed6\u62ff\u81ea\u5df1\u8ddf\u8c22\u5251\u5357\u6bd4\uff0c\u5f20\u77e5\u884c\u4e0d\u4ee5\u4e3a\u662f\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u56a3\u5f20\u81ea\u6709\u56a3\u5f20\u7684\u9053\u7406\uff0c\u4e5d\u4e94\u5e74\u7684\u4ebf\u4e07\u5bcc\u7fc1\u53ef\u6bd4\u91ce\u751f\u7684\u9e8b\u9e7f\u8fd8\u7a00\u5c11\uff0c\u8c22\u5251\u5357\u4e5d\u4e09\u5e74\u51fa\u56fd\u524d\u5c06\u4e39\u6d25\u516c\u53f8\u51fa\u552e\u7ed9\u6b63\u6cf0\u96c6\u56e2\uff0c\u6362\u6301\u6b63\u6cf0\u96c6\u56e2\u8fd18\uff05\u7684\u80a1\u4efd\uff0c\u6210\u4e3a\u8c22\u6c0f\u5bb6\u65cf\u5185\u90e8\u5bf9\u6b63\u6cf0\u96c6\u56e2\u6301\u80a1\u7b2c\u4e8c\u591a\u7684\u4e2a\u4eba\uff0c\u662f\u540c\u8f88\u91cc\u4ed6\u90a3\u4e9b\u5802\u5144\u5f1f\u8c22\u665a\u5c71\u90a3\u4e9b\u4eba\u6240\u4e0d\u80fd\u6bd4\u7684\u3002</p><p>\u3000\u3000\u8c22\u5bb6\u7684\u6b63\u6cf0\u96c6\u56e2\u8d77\u4e8e\u65b0\u6cf0\u3001\u5174\u4e8e\u60e0\u5c71\uff0c\u867d\u7136\u653f\u5546\u80cc\u666f\u6df1\u539a\uff0c\u4f46\u662f\u5728\u653f\u754c\u6700\u76f4\u63a5\u7684\u52a9\u529b\u8fd8\u662f\u8c22\u5251\u5357\u7684\u6bcd\u4eb2\u90a3\u4e00\u7cfb\u5468\u5bb6\uff0c\u5468\u747e\u745c\u4ece\u5916\u7ecf\u8d38\u90e8\u8c03\u804c\u5230\u60e0\u5c71\uff0c\u4ece\u526f\u5e02\u957f\u3001\u5e38\u59d4\u526f\u5e02\u957f\u3001\u517c\u5de5\u59d4\u4e66\u8bb0\u5230\u5e02\u957f\u53ea\u7528\u4e86\u516b\u5e74\u7684\u65f6\u95f4\uff0c\u8fd9\u516b\u5e74\u4e5f\u662f\u6b63\u6cf0\u96c6\u56e2\u5728\u60e0\u5c71\u7acb\u8db3\u6839\u57fa\u7684\u516b\u5e74\u3002</p><p>\u3000\u3000\u9274\u4e8e\u4e2d\u592e\u6709\u5173\u5730\u65b9\u515a\u653f\u5e72\u90e8\u56de\u907f\u6761\u4f8b\u7684\u89c4\u5b9a\uff0c\u5468\u747e\u745c\u5230\u60e0\u5c71\u5c31\u4efb\u4e4b\u540e\uff0c\u5176\u4e08\u592b\u8c22\u6c49\u660e\u4ece\u6b63\u6cf0\u96c6\u56e2\u64a4\u8d44\uff0c\u4f46\u5b9e\u9645\u662f\u5bf9\u6b63\u6cf0\u96c6\u56e2\u6700\u6709\u5f71\u54cd\u529b\u7684\u4eba\u7269\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u8c22\u5bb6\u867d\u7136\u6ca1\u6709\u4eba\u76f4\u63a5\u5728\u5b98\u573a\u91cc\u6eda\u722c\uff0c\u4f46\u901a\u8fc7\u59fb\u4eb2\u5173\u7cfb\uff0c\u5012\u4e5f\u7ed3\u4e0b\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u5173\u7cfb\u7f51\uff0c\u8c22\u6c49\u660e\u4e4b\u4e8e\u5468\u747e\u745c\uff0c\u8c22\u665a\u6674\u4e4b\u4e8e\u5f90\u5fd7\u660e\uff0c\u7b49\u7b49\u4e0d\u4e00\u800c\u8db3\u3002\u8bf4\u8d77\u8c22\u665a\u6674\u4e0e\u5f90\u5b66\u5e73\u4e4b\u5b50\u5f90\u5fd7\u660e\u7684\u7ed3\u5408\uff0c\u8fd8\u662f\u5f90\u6c34\u5e73\u59bb\u5b50\u5468\u53d4\u60e0\u4eb2\u81ea\u6413\u548c\u7684\u7ed3\u679c\uff0c\u5468\u53d4\u60e0\u4e0e\u5468\u747e\u745c\u8fdc\u5802\u59d0\u59b9\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u3001\u5510\u5b66\u8c26\u89c6\u5f90\u5b66\u5e73\u4e3a\u6700\u5927\u7684\u9760\u5c71\uff0c\u8fd9\u91cc\u9762\u7684\u5173\u7cfb\u65e9\u5c31\u7814\u7a76\u900f\u4e86\uff0c\u6240\u4ee5\u8c22\u5251\u5357\u4e0a\u6b21\u5728\u6d77\u5dde\u9047\u632b\uff0c\u63a5\u5230\u5468\u747e\u745c\u7684\u7535\u8bdd\uff0c\u5e02\u91cc\u5982\u4e34\u5927\u654c\uff0c\u6ca1\u60f3\u5230\u6700\u540e\u8fd8\u662f\u8c22\u5251\u5357\u81ea\u5df1\u64a4\u6d88\u7533\u8bc9\uff0c\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u77e5\u9053\u662f\u5f20\u606a\u5728\u91cc\u9762\u6363\u9b3c\u3002</p><p>\u3000\u3000\u65e2\u7136\u5f20\u606a\u53c2\u4e0e\u9526\u6e56\u7684\u7ecf\u8425\u5df2\u7ecf\u662f\u4e8b\u5b9e\uff0c\u5f20\u77e5\u884c\u53ea\u80fd\u5bbd\u6170\u81ea\u5df1\u3002\u8bf4\u9053\uff1a\u201c\u5347\u5b98\u8fd8\u662f\u6ca1\u5f71\u7684\u4e8b\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u5230\u8feb\u5728\u7709\u776b\u65f6\u518d\u89e3\u51b3\u4e0d\u8fdf\u2026\u2026\u201d</p><p>\u3000\u3000\u6700\u7ec8\u53ea\u6709\u4e24\u4e2a\u53ef\u80fd\uff0c\u8981\u4e48\u5f20\u606a\u4ece\u9526\u6e56\u64a4\u8d44\uff0c\u8981\u4e48\u5f20\u77e5\u884c\u5f02\u5730\u5c31\u4efb\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u6bd5\u7adf\u5728\u6d77\u5dde\u6709\u57fa\u7840\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u4ed6\u786e\u5b9e\u4e0d\u60f3\u5f02\u5730\u8d74\u4efb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u7ec6\u8bf4\u7231\u8fbe\u7535\u5b50\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4ece\u526f\u5904\u63d0\u6b63\u5904\u5c5e\u4e8e\u7834\u683c\u63d0\u62d4\uff0c\u56fd\u5185\u8fde\u7eed\u4e24\u6b21\u7834\u683c\u63d0\u62d4\u7684\u4f8b\u5b50\u4e0d\u80fd\u8bf4\u7edd\u65e0\u4ec5\u6709\uff0c\u81f3\u5c11\u4e5f\u4f1a\u5341\u5206\u7684\u5f15\u4eba\u77a9\u76ee\uff0c\u4e5f\u5c31\u662f\u8bf4\u5f20\u77e5\u884c\u4ece\u6b63\u5904\u63d0\u526f\u5385\uff0c\u81f3\u5c11\u8981\u71ac\u4e09\u5e74\u7684\u5e74\u9650\uff0c\u90a3\u4e5f\u662f\u5f20\u606a\u9ad8\u4e2d\u6bd5\u4e1a\u4e4b\u540e\u7684\u4e8b\u60c5\uff0c\u8fd9\u65f6\u5019\u8003\u8651\u8fd9\u4e9b\u6709\u7684\u6ca1\u7684\u8fd8\u65e9\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u60f3\u8d77\u5173\u952e\u7684\u4e8b\u60c5\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u5f90\u7701\u957f\u77e5\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u201c\u4ece\u5934\u5230\u5c3e\u90fd\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u6709\u4e9b\u4e0d\u4e50\u610f\u4e86\uff1a\u201c\u5916\u4eba\u6bd4\u5bb6\u91cc\u4eba\u8fd8\u8981\u65e9\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7b11\u7740\u8bf4\uff1a\u201c\u8c01\u8ba9\u4f60\u4e00\u76f4\u5c06\u5f20\u606a\u5f53\u6210\u5c0f\u5b69\u5b50\u770b\uff1f\u201d\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u8d8a\u662f\u4eb2\u8fd1\u719f\u6089\u7684\u4eba\uff0c\u8d8a\u96be\u4ee5\u7f6e\u4fe1\uff0c\u4eb2\u6635\u611f\u4e5f\u5bb9\u6613\u4ea7\u751f\u8f7b\u89c6\u5fc3\uff0c\u5c31\u50cf\u5f20\u77e5\u884c\u5728\u5916\u9762\u4e00\u8a00\u4e5d\u9f0e\uff0c\u56de\u5230\u5bb6\u5c11\u6709\u8bf4\u8bdd\u7684\u673a\u4f1a\uff1b\u8981\u4e0d\u662f\u8fd9\u4e9b\u5929\u5fc3\u91cc\u6709\u4e9b\u731c\u6d4b\uff0c\u5f20\u77e5\u884c\u4e5f\u4e00\u65f6\u63a5\u53d7\u4e0d\u4e86\u5f20\u606a\u5728\u9526\u6e56\u6301\u80a1\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u201c\u4e4b\u524d\u4f60\u5c0f\u53d4\u8ddf\u6211\u63d0\u8d77\u8fc7\u5e0c\u671b\u8054\u5408\u9526\u6e56\u4ece\u6c99\u7530\u62ff\u5730\u7684\u4e8b\u60c5\uff0c\u6211\u5dee\u70b9\u540c\u610f\u4ed6\u4ece\u4e2d\u6413\u548c\u2026\u2026\u201d\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u201c\u65e2\u7136\u4f60\u5728\u9526\u6e56\u6709\u4efd\u989d\uff0c\u6c99\u7530\u7684\u4e8b\u60c5\uff0c\u5c31\u4e0d\u8981\u53c2\u4e0e\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5bf9\u6c99\u7530\u65e7\u57ce\u6539\u9020\u9879\u76ee\u4e0d\u6b7b\u5fc3\u7684\u4e0d\u53ea\u5c0f\u53d4\u5f20\u77e5\u975e\u4e00\u4eba\uff0c6\u6708\u4e0a\u65ec\u7684\u4e00\u5929\u508d\u665a\uff0c\u5f20\u606a\u5750\u8f66\u4ece\u6587\u5c71\u996d\u5e97\u95e8\u53e3\u7ecf\u8fc7\uff0c\u65e0\u610f\u95f4\u77a5\u89c1\u90b5\u81f3\u521a\u7ad9\u5728\u6587\u5c71\u996d\u5e97\u7684\u5165\u53e3\u5904\u5f20\u671b\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5c5e\u4e8e\u8349\u521b\u671f\uff0c\u5f20\u606a\u5c06\u5468\u6587\u658c\u4e0e\u90a3\u8f86\u519b\u724c\u5965\u8fea\u8c03\u7ed9\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u6491\u95e8\u9762\uff0c\u51fa\u95e8\u529e\u4e8b\u60c5\uff0c\u51ed\u90a3\u8f86\u5965\u8fea\u5c31\u80fd\u8ba9\u95e8\u69db\u964d\u4e09\u5206\u3002</p><p>\u3000\u3000\u5085\u4fca\u5de5\u4f5c\u5173\u7cfb\u8c03\u5165\u9526\u6e56\u4e4b\u540e\uff0c\u8c22\u665a\u6674\u5c06\u65b0\u4e70\u7684\u90a3\u8f86\u5e15\u8428\u7279B4\u7ed9\u5f20\u606a\u7528\uff0c\u8fd9\u6b3e\u7ecf\u5178\u4e2d\u9ad8\u6863\u8f66\u578b\uff0c\u4e0a\u6d77\u5927\u4f17\u8981\u7b49\u5230\u4e5d\u4e5d\u5e74\u624d\u6709\u63a8\u51fa\uff0c\u4eae\u68d5\u8272\u7684\u5e15\u8428\u7279B4\u8fdb\u5173\u4ef7\u5927\u7ea6\u4e09\u5341\u4e07\u5de6\u53f3\uff0c\u4f46\u662f\u5728\u4e5d\u4e94\u5e74\u7684\u56fd\u5185\uff0c\u5374\u7ed9\u4eba\u9ad8\u6863\u8f66\u7684\u5962\u534e\u4e4b\u611f\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u66ae\u8272\u665a\u972d\u4e2d\u7684\u90b5\u81f3\u521a\uff0c\u539f\u5148\u8ddf\u4ed6\u6253\u58f0\u62db\u547c\uff0c\u62ff\u8d77\u624b\u673a\uff0c\u770b\u89c1\u4e00\u8f86\u5c3c\u6851\u653f\u5e9c\u516c\u52a1\u8f66\u7f13\u6162\u5728\u4ed6\u8ddf\u524d\u505c\u4e0b\u6765\u3002\u6d77\u5dde\u5e02\u653f\u5e9c\u516c\u52a1\u8f66\u5c3d\u7528\u65e5\u4ea7\u8f66\uff0c\u6ca1\u770b\u6e05\u8f66\u8f86\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u3002\u770b\u7740\u90b5\u81f3\u521a\u77ee\u8eab\u53bb\u5f00\u8f66\u95e8\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5c06\u8f66\u505c\u5728\u8def\u9762\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u94bb\u8fdb\u8f66\u91cc\u9762\uff0c\u5c3c\u6851\u8f66\u8f6c\u53d8\u671d\u6587\u5c71\u996d\u5e97\u91cc\u9762\u5f00\u53bb\uff0c\u624d\u770b\u6e05\u8f66\u5c3e\u6302\u7740\u8f66\u724c\uff0c\u662f\u4e07\u52c7\u7684\u8f66\u3002\u4e07\u52c7\u53ea\u662f\u4f17\u591a\u526f\u5e02\u957f\u4e2d\u7684\u4e00\u4eba\uff0c\u5e02\u653f\u5e9c\u6ca1\u7ed9\u4ed6\u914d\u4e13\u8f66\uff0c\u4ed6\u8fd8\u662f\u7528\u4ed6\u5728\u5916\u7ecf\u8d38\u59d4\u7684\u8f66\u5b50\uff0c\u8f66\u724c00088\u7684\u9ed1\u8272\u5c3c\u6851\uff0c\u5728\u6d77\u5dde\u5e02\u76f8\u5f53\u7684\u8000\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u58f0\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u77e5\u4ec5\u51ed\u4e16\u7eaa\u9910\u996e\u8fd9\u5bb6\u516c\u53f8\u8fd8\u4e0d\u80fd\u5c06\u90b5\u81f3\u521a\u7684\u91ce\u5fc3\u675f\u7f1a\u4f4f\uff0c\u90b5\u81f3\u521a\u5411\u4e0a\u94bb\u8425\u7684\u91ce\u5fc3\u53ef\u4e0d\u4f1a\u8f7b\u6613\u56e0\u4e3a\u522b\u4eba\u7684\u610f\u5fd7\u800c\u8f6c\u79fb\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u8ddf\u90b5\u54e5\u6253\u58f0\u62db\u547c\uff1f\u201d\u5085\u4fca\u5728\u524d\u9762\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5f80\u8f66\u4f4d\u4e2d\u95f4\u79fb\u4e86\u79fb\uff0c\u8ba9\u5085\u4fca\u8f6c\u8eab\u6765\u65b9\u9762\u770b\u5230\u4ed6\uff0c\u65e2\u7136\u7528\u4ed6\uff0c\u6709\u4e9b\u4e8b\u60c5\u7684\u66f2\u6298\u81ea\u7136\u8981\u8bf4\u7ed9\u4ed6\u542c\uff1a\u201c\u4e07\u52c7\u4e0d\u8fc7\u662f\u53ea\u6076\u72ac\uff0c\u867d\u7136\u4ed6\u8fd8\u6ca1\u6709\u5c06\u722a\u7259\u9732\u51fa\u6765\uff0c\u603b\u6539\u4e0d\u4e86\u4ed6\u8981\u54ac\u4eba\u7684\u6027\u5b50\uff0c\u867d\u7136\u8bf4\u6211\u5bb6\u5728\u6d77\u5dde\u4e5f\u4e0d\u5fcc\u60ee\u8c01\uff0c\u8fd9\u79cd\u4eba\u8fd8\u6ca1\u6709\u5230\u60f9\u4ed6\u4eec\u7684\u65f6\u5019\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90b5\u54e5\u5fc3\u91cc\u4e0d\u6e05\u695a\uff1f\u201d\u5085\u4fca\u89c1\u5f20\u606a\u8138\u4e0a\u4e1d\u63a9\u4e0d\u906e\u63a9\u5bf9\u4e07\u52c7\u7684\u538c\u6076\uff0c\u62c5\u5fe7\u7684\u770b\u4e86\u770b\u7a97\u5916\uff0c\u4e0d\u6653\u5f97\u5f20\u606a\u5fc3\u91cc\u5bf9\u6b64\u65f6\u7684\u90b5\u81f3\u521a\u6709\u4ec0\u4e48\u611f\u60f3\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u90b5\u9b3c\u7cbe\u4e00\u6837\u7684\u4eba\uff0c\u4ed6\u80fd\u4e0d\u6e05\u695a\uff1f\u53ea\u662f\u554a\uff0c\u4ed6\u5fc3\u998b\u6c99\u7530\u90a3\u8fb9\u7684\u5730\uff0c\u73b0\u5728\u8c01\u60f3\u4ece\u6c99\u7530\u65e7\u57ce\u6539\u9020\u9879\u76ee\u91cc\u5206\u4e00\u676f\u7fb9\uff0c\u90fd\u7ed5\u4e0d\u8fc7\u4e07\u52c7\uff0c\u4e07\u52c7\u554a\uff0c\u53ef\u662f\u6d77\u5dde\u5e02\u7684\u65b0\u8d35\u2026\u2026\u4e3a\u5b98\u7684\u987a\u9006\uff0c\u53ea\u5728\u4e8e\u7ecf\u8425\u4e8c\u5b57\uff0c\u653f\u7ee9\u4e0d\u8fc7\u662f\u70b9\u7f00\uff0c\u4ed6\u5bb6\u8001\u5934\u4e0a\u8c03\u5230\u7701\u91cc\uff0c\u867d\u7136\u662f\u4e2a\u95f2\u804c\uff0c\u5374\u7ed9\u4e86\u4ed6\u4e07\u5bb6\u66f4\u597d\u7684\u94bb\u8425\u673a\u4f1a\uff0c\u4e07\u52c7\u521a\u63d0\u4e0a\u6765\uff0c\u8fd8\u770b\u4e0d\u51fa\u591a\u5927\u6076\u8ff9\uff0c\u4f60\u518d\u7b49\u4e9b\u65f6\u5019\u770b\u770b\uff0c\u770b\u4ed6\u7684\u722a\u7259\u5c31\u4f1a\u6162\u6162\u9732\u51fa\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u90b5\u81f3\u521a\u4e86\uff0c\u5c0f\u53d4\u60f3\u4ecb\u5165\u6c99\u7530\u65e7\u57ce\u6539\u9020\u4e5f\u8981\u8ddf\u4e07\u52c7\u6253\u4ea4\u9053\uff0c\u5f20\u606a\u867d\u7136\u5bf9\u4e07\u52c7\u6df1\u538c\u75db\u7edd\uff0c\u603b\u4e0d\u81f3\u4e8e\u8fc1\u6012\u5230\u90b5\u81f3\u521a\u5934\u4e0a\u53bb\u3002\u4f46\u662f\u8981\u5c06\u90b5\u81f3\u521a\u5b8c\u5168\u6298\u670d\u6536\u4e3a\u5df1\u7528\uff0c\u4e5f\u4e0d\u662f\u77ed\u65f6\u95f4\u5185\u80fd\u505a\u5230\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u76d8\u7b97\u7740\uff0c\u90b5\u81f3\u521a\u8981\u5728\u6c99\u7530\u62ff\u5730\uff0c\u5fc5\u5b9a\u4f1a\u503e\u5bb6\u8361\u4ea7\u9677\u8eab\u5176\u4e2d\u65e0\u6cd5\u8131\u8eab\u51fa\u6765\uff0c\u5230\u65f6\u518d\u52a9\u4ed6\u8131\u8eab\uff0c\u8fd8\u6015\u4ed6\u80fd\u9003\u51fa\u81ea\u5df1\u7684\u624b\u638c\u5fc3\uff1f\u60f3\u5230\u8fd9\u91cc\u5f20\u606a\u5634\u89d2\u6302\u7740\u6de1\u6de1\u7684\u7b11\u610f\uff0c\u6ca1\u6709\u6c99\u7530\u7684\u5999\u68cb\u8fd8\u6709\u8fd9\u4e48\u591a\u7684\u597d\u5904\u3002</p><p>\u3000\u3000\u5085\u4fca\u89c1\u5f20\u606a\u5634\u89d2\u7684\u5fae\u610f\u9690\u6709\u4e9b\u5f97\u610f\uff0c\u5b9e\u5728\u60f3\u4e0d\u901a\u770b\u5230\u90b5\u81f3\u521a\u4e0e\u4e07\u52c7\u63a5\u89e6\u6709\u4ec0\u4e48\u8ba9\u4ed6\u5f97\u610f\u7684\u3002\u4ed6\u6b63\u5f0f\u5230\u5f20\u606a\u8eab\u8fb9\u529e\u4e8b\u8fd8\u4e0d\u5230\u5341\u5929\u7684\u65f6\u95f4\uff0c\u4e4b\u524d\u662f\u901a\u8fc7\u76db\u9752\u3001\u90b5\u81f3\u521a\u5bf9\u5f20\u606a\u6709\u95f4\u63a5\u7684\u4e86\u89e3\uff0c\u6240\u77e5\u6709\u9650\uff0c\u53ea\u6653\u5f97\u4ed6\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u867d\u7136\u8fd8\u5728\u8bfb\u9ad8\u4e2d\u751f\uff0c\u5fc3\u673a\u3001\u624b\u6bb5\u5374\u4e1d\u6beb\u4e0d\u5dee\u6210\u5e74\u4eba\uff0c\u8fde\u90b5\u81f3\u521a\u3001\u76db\u9752\u90fd\u8981\u5bf9\u4ed6\u754f\u60e7\u4e09\u5206\u3002</p><p>\u3000\u3000\u4e4b\u524d\u76db\u9752\u8ba8\u597d\u5df4\u7ed3\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u8fd9\u4e24\u5b50\uff0c\u4e0d\u8fc7\u56fe\u8d75\u53f8\u660e\u53e3\u888b\u91cc\u6709\u94b1\uff0c\u53ef\u662f\u6253\u5fc3\u773c\u91cc\u6ca1\u77a7\u5f97\u8d77\u8fc7\u8fd9\u4e24\u4e2a\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u4f46\u662f\u5f20\u606a\u7684\u51fa\u73b0\u5374\u5b8c\u5168\u4e0d\u540c\u3002</p><p>\u3000\u3000\u9152\u5427\u95f9\u4e8b\u8fd8\u662f\u5176\u6b21\uff0c\u8ba9\u76db\u9752\u8f6c\u5230\u56db\u51e4\u6865\u7684\u573a\u5b50\uff0c\u5e76\u7acb\u4e0b\u90a3\u4e48\u591a\u7684\u89c4\u77e9\uff0c\u8fd9\u624d\u662f\u5e2e\u4e86\u76db\u9752\u7684\u5927\u5fd9\uff0c\u8ba9\u4ed6\u8f7b\u8f7b\u677e\u677e\u7684\u4ece\u8fd9\u524d\u7684\u6ce5\u6dd6\u91cc\u8131\u8eab\u3002</p><p>\u3000\u3000\u770b\u7740\u5c3c\u6851\u8f66\u6d88\u5931\u5728\u6587\u5c71\u996d\u5e97\u7684\u5c0f\u5f84\u6df1\u5904\uff0c\u5bc6\u6797\u6df1\u5904\u63a2\u51fa\u6587\u5c71\u996d\u5e97\u91cc\u5c0f\u697c\u7684\u6a90\u89d2\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u5085\u4fca\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u7ee7\u7eed\u5f00\u8f66\u3002</p><p>\u3000\u3000\u4eca\u5929\u662f\u8bb8\u601d\u5c0f\u8205\u65bd\u536b\u5fe0\u56db\u5341\u4e5d\u5c81\u751f\u65e5\uff0c\u6d77\u5dde\u4eba\u8fc7\u751f\u65e5\u5e86\u4e5d\u4e0d\u5e86\u5341\uff0c\u65bd\u536b\u5fe0\u751f\u65e5\u672c\u6765\u4e0e\u5f20\u606a\u6ca1\u591a\u5927\u5173\u7cfb\uff0c\u4f46\u662f\u60f3\u5230\u4ed6\u4e0e\u8bb8\u601d\u7684\u5173\u7cfb\u53ef\u80fd\u7ed9\u5979\u5c0f\u8205\u65bd\u536b\u5fe0\u77e5\u9053\uff0c\u8ba8\u597d\u5df4\u7ed3\u81ea\u7136\u8981\u5c11\u4e0d\u4e86\uff0c\u8bb8\u601d\u4e5f\u53ea\u5f00\u53e3\u63d0\u4e86\u4e00\u4e0b\uff0c\u5f20\u606a\u5c31\u6ee1\u53e3\u5e94\u627f\u4e0b\u6765\u3002</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u4e5f\u8bf7\u4e0d\u4e86\u591a\u5c11\u4eb2\u621a\u670b\u53cb\uff0c\u4e5f\u6ca1\u94b1\u5728\u597d\u4e00\u70b9\u7684\u9152\u5e97\u91cc\u8bf7\u5ba2\uff0c\u5c31\u9009\u62e9\u9648\u5947\u7684\u9152\u9986\u529e\u4e94\u516d\u684c\u83dc\uff0c\u4ed6\u90a3\u5929\u4e00\u6e05\u65e9\u770b\u89c1\u8bb8\u601d\u8ddf\u5f20\u606a\u5728\u4e00\u8d77\uff0c\u8bb8\u601d\u7ed9\u83bd\u649e\u7684\u8f66\u5b50\u5413\u7740\uff0c\u5f20\u606a\u52a8\u624b\u6253\u4eba\u7684\u60c5\u5f62\u7ed9\u4ed6\u7684\u5370\u8c61\u5f88\u6df1\u523b\uff0c\u5230\u519c\u673a\u5382\u542c\u8bb8\u601d\u5979\u7238\u8bf4\u8d77\u8bb8\u601d\u524d\u5929\u665a\u4e0a\u7559\u5728\u516c\u53f8\u52a0\u73ed\uff0c\u624d\u8054\u60f3\u5f20\u606a\u4e4b\u524d\u5bf9\u8bb8\u601d\u5bb6\u7684\u5e2e\u52a9\uff0c\u4ed6\u624d\u6562\u80af\u5b9a\u3002\u5f20\u606a\u8fd9\u5c11\u5e74\u867d\u7136\u5e74\u7eaa\u5c0f\uff0c\u4f46\u7ed9\u4eba\u7684\u611f\u89c9\u5374\u5341\u5206\u7684\u8001\u6210\uff0c\u65bd\u536b\u5fe0\u77e5\u9053\u8fd9\u4e8b\u60c5\u95f9\u5f00\u6765\uff0c\u5bf9\u8bb8\u601d\u53c8\u662f\u5927\u7684\u4f24\u5bb3\uff0c\u867d\u7136\u6709\u96be\u4ee5\u63a5\u53d7\uff0c\u5374\u6ca1\u6709\u8ddf\u4ed6\u59d0\u3001\u8bb8\u601d\u7684\u5988\u5988\u8bf4\u5b9e\u8bdd\u3002</p></article>"}}}]);
