(window.webpackJsonp=window.webpackJsonp||[]).push([[1630],{1653:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u51b3\u6597\u5f00\u59cb",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u51b3\u6597\u5f00\u59cb</h2><p>\u5728\u5973\u5b69\u5b50\u9762\u524d\uff0c\u5e94\u8be5\u8868\u73b0\u5f97\u6c14\u5ea6\u6062\u5b8f\uff0c\u5fc3\u80f8\u5bbd\u5e7f\uff0c\u743c\u6069\u77e5\u9053\u8fd9\u4e2a\u9053\u7406\uff0c\u56e0\u4e3a\u6ca1\u6709\u54ea\u4e2a\u5973\u5b69\u5b50\u4f1a\u559c\u6b22\u5c0f\u5fc3\u773c\u7684\u7537\u4eba\u3002\u9002\u5f53\u5730\u8868\u793a\u81ea\u5df1\u7684\u5ac9\u5992\uff0c\u8ba9\u5973\u5b69\u5b50\u89c9\u5f97\u4f60\u73cd\u89c6\u5979\uff0c\u770b\u91cd\u5979\uff0c\u5728\u610f\u5979\uff0c\u90a3\u81ea\u7136\u662f\u6ca1\u9519\uff0c\u4f46\u201c\u8fc7\u72b9\u4e0d\u53ca\u201d\u7684\u9053\u7406\uff0c\u4e5f\u662f\u8981\u660e\u767d\u7684\u3002</p><p>\u6240\u4ee5\u5f53\u73ca\u5609\u627f\u8ba4\u5979\u786e\u5b9e\u7ed3\u8bc6\u4e86\u4e00\u4f4d\u7537\u6027\u670b\u53cb\u4e4b\u540e\uff0c\u743c\u6069\u4fbf\u5236\u6b62\u4e86\u5979\u8fdb\u4e00\u6b65\u7684\u89e3\u91ca\u3002\u8bdd\u8bf4\u5230\u8fd9\u79cd\u5730\u6b65\u4fbf\u5df2\u7ecf\u8db3\u591f\uff0c\u518d\u7ee7\u7eed\u90a3\u5c31\u201c\u8fc7\u201d\u4e86\u3002\u65e2\u7136\u73ca\u5609\u5df2\u7ecf\u8bf4\u4e86\u201c\u53ea\u662f\u666e\u901a\u670b\u53cb\u800c\u5df2\u201d\uff0c\u90a3\u4e48\u743c\u6069\u5c31\u5e94\u8be5\u7acb\u523b\u8868\u73b0\u51fa\u5bf9\u59d0\u59d0\u7684\u5b8c\u5168\u4fe1\u4efb\u624d\u5bf9\u3002</p><p>\u81f3\u5c11\uff0c\u8868\u9762\u4e0a\u5e94\u8be5\u5982\u6b64\u3002</p><p>\u743c\u6069\u4ece\u6765\u4e0d\u76f8\u4fe1\u7537\u6027\u548c\u5973\u6027\u4e4b\u95f4\u4f1a\u6709\u7eaf\u6d01\u7684\u53cb\u8c0a\uff0c\u53ea\u6709\u201c\u60f3\u4e0a\u5979\u201d\u548c\u201c\u4e0d\u60f3\u4e0a\u5979\u201d\u7684\u533a\u522b\u800c\u5df2\u3002\u6216\u8005\u8bf4\uff0c\u4ed6\u53ef\u4ee5\u76f8\u4fe1\u73ca\u5609\uff0c\u4f46\u4ed6\u5374\u4e0d\u53ef\u80fd\u76f8\u4fe1\u53e6\u5916\u90a3\u4e2a\u5bb6\u4f19\u3002\u4e5f\u5c31\u50cf\u838e\u73de\u514b\u6240\u8bf4\u7684\uff0c\u54ea\u6015\u90a3\u4eba\u771f\u662f\u4e2a\u6b63\u4eba\u541b\u5b50\uff0c\u743c\u6069\u4e5f\u8981\u8003\u8651\u9632\u60a3\u4e8e\u672a\u7136\u3002</p><p>\u6240\u4ee5\uff0c\u51b3\u6597\u52bf\u5728\u5fc5\u884c\u3002</p><p>\u743c\u6069\u548c\u5e93\u80af\u7ea6\u7684\u5730\u70b9\u662f\u201c\u591c\u5e55\u201d\uff0c\u8fd9\u662f\u4e2a\u8fd1\u4f3c\u5bc6\u5ba4\u7684\u5730\u65b9\uff0c\u5fc5\u987b\u901a\u8fc7\u9b54\u6cd5\u4f20\u9001\u95e8\u624d\u80fd\u8fdb\u5165\uff0c\u573a\u5730\u662f\u5706\u5f62\uff0c\u76f4\u5f84\u5927\u7ea6\u516d\u5341\u82f1\u5c3a\uff0c\u56db\u5468\u77f3\u58c1\u73af\u7ed5\uff0c\u6ca1\u6709\u95e8\u7a97\uff0c\u4f46\u5934\u9876\u7684\u5929\u82b1\u677f\u662f\u900f\u660e\u73bb\u7483\u6240\u5236\uff0c\u9633\u5149\u80fd\u591f\u76f4\u900f\u4e0b\u6765\uff0c\u5e76\u4e0d\u9634\u6697\u3002</p><p>\u201c\u591c\u5e55\u201d\u662f\u9634\u9b42\u57ce\u5185\u4e13\u95e8\u7528\u4e8e\u5deb\u5e08\u8f83\u91cf\u3001\u5207\u78cb\u548c\u51b3\u6597\u7684\u573a\u6240\uff0c\u56e0\u4e3a\u591f\u201c\u5b89\u5168\u201d\uff0c\u5b83\u88ab\u5f3a\u5927\u7684\u9b54\u6cd5\u7ed3\u754c\u6240\u7b3c\u7f69\u7740\uff0c\u51e1\u662f\u5728\u6b64\u8303\u56f4\u4e4b\u5185\uff0c\u53ea\u8981\u662f\u7f14\u7ed3\u4e86\u201c\u591c\u5e55\u534f\u8bae\u201d\u7684\u51b3\u6597\u8005\uff0c\u5c31\u4e0d\u4f1a\u51fa\u73b0\u201c\u6b7b\u4ea1\u201d\u8fd9\u79cd\u72b6\u51b5\u3002\u4efb\u4f55\u539f\u672c\u4f1a\u81f4\u547d\u7684\u653b\u51fb\u548c\u4f24\u5bb3\u90fd\u4f1a\u88ab\u81ea\u52a8\u963b\u6321\uff0c\u6539\u53d8\u6210\u201c\u9759\u6ede\u201d\u6548\u679c\uff0c\u4e3e\u4f8b\u800c\u8a00\uff0c\u5018\u82e5\u743c\u6069\u5bf9\u5e93\u80af\u53d1\u5c04\u4e00\u51fb\u89e3\u79bb\u672f\uff0c\u5728\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u5982\u679c\u6cd5\u672f\u6210\u529f\u7684\u8bdd\uff0c\u5e93\u80af\u4f1a\u88ab\u53d8\u6210\u4e00\u62b9\u5fae\u5c18\uff0c\u4f46\u5728\u201c\u591c\u5e55\u201d\u4e2d\uff0c\u4ed6\u53ea\u4f1a\u88ab\u6b7b\u6b7b\u5b9a\u4f4f\uff0c\u4e0d\u80fd\u79fb\u52a8\uff0c\u4e0d\u80fd\u8bf5\u5492\uff0c\u4e0d\u80fd\u518d\u7ee7\u7eed\u6218\u6597\uff0c\u4f46\u540c\u65f6\u4e5f\u4e0d\u4f1a\u88ab\u7ee7\u7eed\u653b\u51fb\uff0c\u76f4\u63a5\u5224\u8d1f\u3002\u800c\u4e14\u5728\u80dc\u8d1f\u5df2\u5206\u4e4b\u540e\uff0c\u201c\u591c\u5e55\u201d\u4e2d\u7684\u9b54\u6cd5\u7ed3\u754c\u8fd8\u80fd\u81ea\u52a8\u5bf9\u53d7\u4f24\u8005\u8fdb\u884c\u6cbb\u7597\uff0c\u5404\u79cd\u5f3a\u529b\u795e\u672f\u62cd\u4e0b\u6765\uff0c\u54ea\u6015\u4f60\u539f\u672c\u5df2\u7ecf\u5944\u5944\u4e00\u606f\uff0c\u53ea\u8981\u8fd8\u6ca1\u65ad\u6c14\uff0c\u90a3\u5c31\u4fdd\u8bc1\u80fd\u6551\u6d3b\u3002</p><p>\u9b54\u6cd5\u5e76\u4e0d\u662f\u4e13\u95e8\u4e3a\u6740\u4eba\u800c\u53d1\u660e\u7684\u6280\u827a\uff0c\u4f46\u65e0\u53ef\u5426\u8ba4\u7684\u662f\uff0c\u6740\u4eba\u662f\u9b54\u6cd5\u7684\u6700\u5927\u7528\u9014\u4e4b\u4e00\u3002\u8d8a\u9ad8\u9636\u7684\u5deb\u5e08\uff0c\u8d8a\u5f3a\u529b\u7684\u9b54\u6cd5\uff0c\u6740\u4f24\u6548\u679c\u5c31\u8d8a\u5f3a\uff0c\u5f7c\u6b64\u95f4\u7684\u8f83\u91cf\u5207\u78cb\u4e5f\u5c31\u8d8a\u5371\u9669\uff0c\u5f88\u53ef\u80fd\u539f\u672c\u6ca1\u6709\u6740\u610f\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u4e5f\u5c31\u917f\u6210\u4eba\u547d\u3002\u743c\u6069\u548c\u51db\u5e73\u65f6\u953b\u70bc\u6bd4\u8bd5\u7684\u65f6\u5019\uff0c\u5f7c\u6b64\u90fd\u675f\u624b\u675f\u811a\uff0c\u539f\u56e0\u5c31\u5728\u8fd9\u91cc\uff0c\u743c\u6069\u4e0d\u6562\u7528\u89e3\u79bb\u3001\u77f3\u5316\u8fd9\u79cd\u6709\u53ef\u80fd\u4e00\u51fb\u6bd9\u547d\u7684\u6cd5\u672f\uff0c\u51db\u4e5f\u5fc5\u987b\u523b\u610f\u538b\u4f4e\u5404\u79cd\u5851\u80fd\u9b54\u6cd5\u7684\u5a01\u529b\uff0c\u6015\u743c\u6069\u63a5\u4e0d\u4e0b\u6765\u3002\u4f46\u5728\u201c\u591c\u5e55\u201d\u4e2d\uff0c\u8fd9\u79cd\u95ee\u9898\u5c31\u4e0d\u5b58\u5728\u4e86\u3002</p><p>\u9634\u9b42\u57ce\u8bb2\u6c42\u7cbe\u82f1\u5316\uff0c\u6dd8\u6c70\u4e25\u683c\uff0c\u743c\u6069\u5f53\u5e74\u5728\u5deb\u5e08\u5b66\u6821\u7684\u65f6\u5019\uff0c\u4e00\u767e\u4e2a\u5b66\u751f\u53ea\u6709\u5341\u4e2a\u80fd\u6bd5\u4e1a\uff0c\u4f46\u4e00\u65e6\u4f60\u6210\u4e3a\u201c\u7cbe\u82f1\u201d\uff0c\u90a3\u4e48\u9634\u9b42\u57ce\u4e5f\u5c31\u4e0d\u4f1a\u62ff\u4f60\u7684\u6027\u547d\u5f53\u513f\u620f\uff0c\u6bd5\u7adf\u4eba\u624d\u96be\u5f97\uff0c\u6b7b\u4e00\u4e2a\u5c31\u5c11\u4e00\u4e2a\uff0c\u9700\u8981\u7231\u60dc\u3002\u201c\u591c\u5e55\u201d\u7684\u5efa\u9020\u5c31\u662f\u4e3a\u6b64\uff0c\u4e13\u4f9b\u9ad8\u9636\u5deb\u5e08\u4eec\u5b89\u5168\u5730\u8f83\u91cf\u5207\u78cb\u6240\u7528\u3002</p><p>\u4e4b\u6240\u4ee5\u4e13\u4f9b\u9ad8\u9636\u5deb\u5e08\u4f7f\u7528\uff0c\u662f\u56e0\u4e3a\u8981\u8fdb\u5165\u201c\u591c\u5e55\u201d\uff0c\u5fc5\u987b\u62e5\u6709\u201c\u771f\u540d\u201d\u624d\u884c\uff0c\u5426\u5219\u5c31\u4f1a\u88ab\u9b54\u6cd5\u963b\u6321\u5728\u5916\u3002\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u9664\u4e86\u743c\u6069\u548c\u5e93\u80af\u4e4b\u5916\uff0c\u73ca\u5609\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u90fd\u65e0\u6cd5\u4eb2\u4e34\u73b0\u573a\uff0c\u5979\u4eec\u6ca1\u6709\u771f\u540d\uff0c\u4e0d\u80fd\u8fdb\u5165\uff0c\u53ea\u80fd\u5728\u5bb6\u7b49\u5019\u6d88\u606f\u3002\u8fd9\u662f\u4ef6\u597d\u4e8b\uff0c\u743c\u6069\u6c42\u4e4b\u4e0d\u5f97\u3002</p><p>\u51fa\u53d1\u4e4b\u524d\uff0c\u8299\u8389\u5a05\u72b9\u8c6b\u518d\u4e09\uff0c\u7ec8\u4e8e\u8fd8\u662f\u53eb\u4f4f\u4e86\u743c\u6069\uff0c\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6216\u8bb8\u4f60\u5e94\u8be5\u6ce8\u610f\uff0c\u201d\u5979\u6de1\u6de1\u5730\u8bf4\uff0c\u201c\u4ed6\u5728\u9ed1\u6697\u6216\u8005\u9634\u5f71\u4e2d\uff0c\u4f1a\u683c\u5916\u7684\u5f3a\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u201c\u4e3a\u4ec0\u4e48\uff0c\u4ed6\u662f\u5492\u6cd5\u5e08\uff0c\u53c8\u4e0d\u662f\u5e7b\u672f\u5e08\uff1f\u201d</p><p>\u5e7b\u672f\u5e08\u662f\u501f\u52a9\u5404\u79cd\u903c\u771f\u5e7b\u50cf\u6765\u7792\u9a97\u5bf9\u624b\uff0c\u6240\u4ee5\u5bf9\u5468\u56f4\u73af\u5883\u3001\u5149\u7ebf\u660e\u6697\u6bd4\u8f83\u5728\u610f\uff0c\u8bf4\u5f97\u901a\u4fd7\u70b9\uff0c\u8d8a\u9634\u6697\u7684\u5730\u65b9\uff0c\u81ea\u7136\u8d8a\u9002\u5408\u7528\u6765\u88c5\u795e\u5f04\u9b3c\uff0c\u5018\u82e5\u70c8\u65e5\u5f53\u7a7a\uff0c\u6717\u6717\u4e7e\u5764\uff0c\u96be\u5ea6\u5c31\u4f1a\u76f8\u5e94\u63d0\u9ad8\u4e86\u3002\u4f46\u743c\u6069\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u5e93\u80af\u662f\u5492\u6cd5\u5e08\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u662f\u5426\u653e\u5f03\u4e86\u5e7b\u672f\u5b66\u6d3e\uff0c\u4f46\u80af\u5b9a\u4e5f\u8c08\u4e0d\u4e0a\u591a\u4e48\u7cbe\u901a\u5c31\u662f\u3002</p><p>\u518d\u95ee\u8be6\u60c5\uff0c\u8299\u8389\u5a05\u5374\u4e0d\u80af\u89e3\u91ca\uff0c\u743c\u6069\u4e5f\u4fbf\u7f62\u4e86\u3002\u65e2\u7136\u6709\u6b64\u63d0\u9192\uff0c\u90a3\u4e48\u7559\u610f\u4e9b\u5c31\u662f\uff0c\u771f\u77e5\u672f\u8fd9\u79cd\u4e13\u95e8\u514b\u5236\u9ed1\u6697\u3001\u5e7b\u672f\u7684\u9b54\u6cd5\u591a\u51c6\u5907\u51e0\u4e2a\uff0c\u6709\u5907\u65e0\u60a3\u3002</p><p>\u5230\u4e86\u5730\u65b9\uff0c\u5e93\u80af\u5df2\u7ecf\u5728\u7b49\u5f85\uff0c\u89c1\u743c\u6069\u524d\u6765\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u4e5f\u4e0d\u591a\u8bf4\u5e9f\u8bdd\uff0c\u62bd\u51fa\u6cd5\u6756\uff0c\u7a0d\u7a0d\u4e0a\u626c\uff0c\u8fd9\u662f\u9634\u9b42\u57ce\u4e2d\u5deb\u5e08\u8f83\u91cf\u7684\u793c\u8282\u3002\u743c\u6069\u5374\u6447\u4e86\u6447\u5934\uff0c\u201c\u7a0d\u7b49\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\u6211\u8981\u5148\u8bf4\u6e05\u695a\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u573a\u51b3\u6597\uff0c\u4e0d\u4e3a\u8299\u8389\u5a05\xb7\u83ab\u5c3c\u5361\uff0c\u201d\u743c\u6069\u4e00\u5b57\u4e00\u5b57\u5730\u8bf4\uff0c\u201c\u4e3a\u73ca\u5609\xb7\u5170\u5c3c\u65af\u7279\u3002\u201d</p><p>\u5e93\u80af\u6014\u4e86\u51e0\u79d2\u949f\uff0c\u603b\u7b97\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4f60\u8bef\u4f1a\u4e86\u5427\uff0c\u201d\u4ed6\u76b1\u7709\uff0c\u201c\u6211\u548c\u4f60\u59d0\u59d0\u6839\u672c\u5c31\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u4f60\u8ba4\u8bc6\u6211\u59d0\u59d0\u5bf9\u5427\u3002\u201d</p><p>\u201c\u8ba4\u8bc6\u3002\u201d</p><p>\u201c\u7ecf\u5e38\u89c1\u5230\uff1f\u201d</p><p>\u201c\u662f\uff0c\u4f46\u90a3\u662f\u56e0\u4e3a\u6211\u53bb\u97f3\u4e50\u5b66\u9662\u770b\u671b\u6211\u5f1f\u5f1f\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5e93\u80af\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u8fd9\u7406\u7531\u6bd4\u8f83\u7275\u5f3a\uff0c\u7d22\u6027\u5c06\u540e\u9762\u534a\u622a\u8bdd\u7701\u7565\u6389\u4e86\uff0c\u201c\u603b\u4e4b\uff0c\u6211\u548c\u4f60\u59d0\u59d0\u4e0d\u8fc7\u662f\u8ba4\u8bc6\u800c\u5df2\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u7b97\u662f\u666e\u901a\u670b\u53cb\uff0c\u4f60\u662f\u4e0d\u662f\u8bef\u4f1a\u4e86\u4ec0\u4e48\uff1f\u201d</p><p>\u563f\uff0c\u53c8\u4e00\u4e2a\u201c\u666e\u901a\u670b\u53cb\u201d\uff0c\u6211\u59d0\u59d0\u8fd9\u4e48\u8bf4\uff0c\u90a3\u6211\u76f8\u4fe1\uff0c\u4f60\u5c0f\u5b50\u5c45\u7136\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u771f\u5f53\u6211\u662f\u7b28\u86cb\u4e48\u3002</p><p>\u4e0d\u8fc7\u65e2\u7136\u5e93\u80af\u58f0\u79f0\u53ea\u662f\u201c\u666e\u901a\u670b\u53cb\u201d\uff0c\u743c\u6069\u6b63\u597d\u987a\u6c34\u63a8\u821f\uff0c\u5835\u4f4f\u5bf9\u65b9\u7684\u5634\uff0c\u5426\u5219\u4ed6\u8981\u771f\u627f\u8ba4\u81ea\u5df1\u5728\u8ffd\u6c42\u73ca\u5609\uff0c\u90a3\u4e8b\u60c5\u53cd\u800c\u4e0d\u592a\u597d\u529e\u4e86\u3002\u6bd5\u7adf\u4ece\u540d\u5206\u4e0a\u8bf4\uff0c\u73ca\u5609\u548c\u743c\u6069\u662f\u59d0\u5f1f\uff0c\u5e93\u80af\u8981\u8ffd\u6c42\u73ca\u5609\uff0c\u743c\u6069\u8fd8\u771f\u6ca1\u4ec0\u4e48\u8d44\u683c\u5e72\u6d89\u2026\u2026\u5f1f\u5f1f\u603b\u4e0d\u80fd\u59a8\u788d\u59d0\u59d0\u7684\u7231\u60c5\u5427\u3002</p><p>\u201c\u6211\u4e0d\u60f3\u591a\u8bf4\u5e9f\u8bdd\uff0c\u201d\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\uff0c\u201c\u603b\u800c\u8a00\u4e4b\u4e00\u53e5\u8bdd\uff0c\u6211\u4eca\u5929\u6765\u53c2\u52a0\u8fd9\u573a\u51b3\u6597\uff0c\u4e3a\u7684\u662f\u6211\u59d0\u59d0\u2014\u2014\u5982\u679c\u4f60\u8d62\u4e86\uff0c\u90a3\u4e48\u6211\u6ca1\u8bdd\u8bf4\uff0c\u968f\u4f60\u7684\u4fbf\uff1b\u4f46\u5982\u679c\u6211\u8f93\u4e86\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u8bf7\u4f60\u79bb\u6211\u59d0\u59d0\u8fdc\u70b9\u3002\u201d</p><p>\u5e93\u80af\u8138\u4e0a\u5fae\u5fae\u6cdb\u8d77\u6012\u8272\uff0c\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u8d28\u95ee\uff0c\u201c\u90a3\u8299\u8389\u5a05\u5462\uff1f\u201d</p><p>\u201c\u4f60\u5f88\u5173\u5fc3\u8299\u8389\u5a05\u4e48\uff1f\u201d\u743c\u6069\u53cd\u5507\u76f8\u8ba5\uff0c\u201c\u5982\u679c\u771f\u90a3\u4e48\u5728\u610f\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8fd9\u4e48\u6025\u7740\u628a\u5979\u63a8\u51fa\u53bb\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u662f\u2026\u2026\u201d\u5e93\u80af\u8bdd\u8bf4\u4e86\u534a\u622a\uff0c\u6700\u540e\u6447\u4e86\u6447\u5934\uff0c\u201c\u7b97\u4e86\uff0c\u6211\u6ca1\u5174\u8da3\u548c\u4f60\u89e3\u91ca\uff0c\u6211\u4e5f\u4e0d\u7ba1\u4f60\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u8bef\u89e3\uff0c\u65e2\u7136\u5230\u4e86\u8fd9\u91cc\uff0c\u90a3\u5c31\u4ee5\u51b3\u6597\u6765\u5b9a\u80dc\u8d1f\u597d\u4e86\uff0c\u201d\u4ed6\u626c\u8d77\u6cd5\u6756\uff0c\u9065\u9065\u6307\u5411\u743c\u6069\uff0c\u201c\u4f60\u82e5\u80dc\u4e86\uff0c\u90a3\u4e48\u6211\u7b54\u5e94\u4f60\u6240\u6709\u6761\u4ef6\uff1b\u4f60\u82e5\u8f93\u4e86\uff0c\u4f60\u4e5f\u5f97\u542c\u6211\u5b89\u6392\u2014\u2014\u5982\u4f55\uff1f\u201d</p><p>\u201c\u597d\uff01\u201d</p><p>\u5927\u5bb6\u90fd\u662f\u7537\u4eba\uff0c\u6ca1\u5fc5\u8981\u7f57\u7f57\u55e6\u55e6\u7275\u626f\u4e0d\u6e05\uff0c\u4e09\u8a00\u4e24\u8bed\u505a\u4e2a\u7b80\u5355\u4ea4\u4ee3\uff0c\u76f4\u63a5\u624b\u5e95\u4e0b\u89c1\u771f\u7ae0\u3002\u8c01\u62f3\u5934\u5927\uff0c\u672c\u4e8b\u5f3a\uff0c\u8c01\u5c31\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\u3002</p><p>\u4e24\u4eba\u540c\u65f6\u4e3e\u8d77\u6cd5\u6756\uff0c\u5728\u7a7a\u4e2d\u8f7b\u8f7b\u4e00\u78b0\uff0c\u5f7c\u6b64\u8eac\u8eab\u884c\u793c\uff0c\u8fd9\u5c31\u7b97\u662f\u5b8c\u6210\u4e86\u201c\u591c\u5e55\u534f\u8bae\u201d\uff0c\u53ef\u4ee5\u53d7\u5230\u9b54\u6cd5\u7ed3\u754c\u7684\u4fdd\u62a4\uff0c\u4e0d\u4f1a\u6709\u6027\u547d\u4e4b\u5fe7\u4e86\u3002\u4ed6\u4eec\u540c\u65f6\u540e\u9000\uff0c\u62c9\u5f00\u8ddd\u79bb\uff0c\u4e3e\u8d77\u6cd5\u6756\u9065\u9065\u6307\u5411\u5bf9\u65b9\u7136\u540e\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eec\u540c\u65f6\u91ca\u653e\u51fa\u4e86\u81ea\u5df1\u7684\u7b2c\u4e00\u4e2a\u6cd5\u672f\u3002</p><p>\u201c\u77f3\u5f39\uff01\u201d</p><p>\u201c\u5f71\u76fe\uff01\u201d</p><p>\u743c\u6069\u53f3\u624b\u63e1\u7740\u6cd5\u6756\u865a\u6307\uff0c\u4e00\u76f4\u5782\u5728\u8170\u9645\u7684\u5de6\u624b\u731d\u7136\u62ac\u8d77\uff0c\u4e00\u679a\u77f3\u5f39\u53d1\u51fa\u7206\u88c2\u706b\u5149\u671d\u7740\u5e93\u80af\u75be\u5c04\u8fc7\u6765\u3002\u5e93\u80af\u6cd5\u6756\u5e73\u4e3e\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7d2b\u9ed1\u8272\u900f\u660e\u9634\u5f71\u62a4\u7f69\u81ea\u6756\u5934\u5f39\u51fa\uff0c\u77ac\u95f4\u6269\u5927\uff0c\u7b3c\u7f69\u4f4f\u5168\u8eab\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u5c06\u77f3\u5f39\u5f39\u4e86\u5f00\u53bb\u3002</p><p>\u5deb\u5e08\u4e4b\u95f4\u7684\u51b3\u6597\uff0c\u6309\u7167\u60ef\u4f8b\uff0c\u4e00\u4e0a\u6765\u90fd\u662f\u5148\u7ed9\u81ea\u5df1\u6784\u5efa\u9632\u5fa1\u518d\u8bf4\uff0c\u50cf\u743c\u6069\u8fd9\u6837\u76f4\u63a5\u62a2\u653b\uff0c\u5176\u5b9e\u5f88\u4e0d\u6b63\u5e38\uff0c\u5e93\u80af\u7684\u4f5c\u6cd5\u624d\u662f\u6807\u51c6\u7684\u5b66\u9662\u6d3e\u8d77\u624b\u5f0f\u3002\u4f46\u743c\u6069\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u4e5f\u662f\u6709\u5176\u9053\u7406\u7684\uff0c\u4ed6\u6709\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u5728\u624b\uff0c\u65e9\u5c31\u5df2\u7ecf\u6697\u4e2d\u542f\u52a8\uff0c\u65e0\u8bba\u5e93\u80af\u4f7f\u7528\u4ec0\u4e48\u9b54\u6cd5\uff0c\u53ea\u8981\u662f\u76f4\u63a5\u9488\u5bf9\u4ed6\u7684\uff0c\u90fd\u80fd\u53cd\u5f39\u56de\u53bb\uff0c\u6240\u4ee5\u6709\u6043\u65e0\u6050\u3002</p><p>\u6b64\u6b21\u51b3\u6597\uff0c\u548c\u4ee5\u524d\u5deb\u5e08\u5b66\u9662\u7684\u6bd5\u4e1a\u8003\u8bd5\u3001\u7ea2\u888d\u5deb\u5e08\u7684\u664b\u9636\u8d5b\u4e0d\u540c\uff0c\u5b83\u662f\u4e0d\u9650\u5236\u5deb\u5e08\u4f7f\u7528\u9b54\u6cd5\u88c5\u5907\u548c\u7269\u54c1\u7684\uff0c\u56e0\u4e3a\u672c\u6765\u5c31\u4e0d\u662f\u8981\u6bd4\u8f83\u8861\u91cf\u5deb\u5e08\u7684\u81ea\u8eab\u6c34\u51c6\uff0c\u800c\u662f\u4ee5\u83b7\u80dc\u4e3a\u552f\u4e00\u76ee\u6807\u3002\u51c6\u5907\u7684\u5145\u8db3\u4e0e\u5426\uff0c\u8d44\u4ea7\u5b9d\u7269\u7684\u5360\u6709\u591a\u5be1\uff0c\u539f\u672c\u4e5f\u5c31\u662f\u53ef\u4ee5\u4f5c\u4e3a\u7b79\u7801\u800c\u5b58\u5728\u3002\u743c\u6069\u4e4b\u6240\u4ee5\u6562\u4e8e\u548c\u5e93\u80af\u51b3\u6597\uff0c\u9664\u4e86\u5bf9\u81ea\u5df1\u7684\u81ea\u4fe1\u4e4b\u5916\uff0c\u5176\u4e2d\u4e00\u4e2a\u539f\u56e0\u4e5f\u5728\u4e8e\u6b64\u3002\u8bba\u5bb6\u4e16\uff0c\u8bba\u8d22\u529b\uff0c\u4ed6\u81ea\u7136\u4e0d\u5982\u5e93\u80af\uff0c\u4f46\u4ed6\u6709\u4e2a\u597d\u8001\u5e08\uff0c\u5965\u6c83\u8fd9\u79cd\u6d3b\u4e86\u51e0\u5343\u5e74\u7684\u5927\u5965\u672f\u5e08\uff0c\u624b\u4e2d\u73cd\u85cf\u5c82\u540c\u5c0f\u53ef\uff0c\u968f\u4fbf\u62ff\u4e00\u6837\u51fa\u6765\u90fd\u4ef7\u503c\u8fde\u57ce\uff0c\u8fd9\u679a\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u5df2\u7ecf\u63a5\u8fd1\u795e\u5668\uff0c\u6599\u60f3\u5e93\u80af\u5c31\u7b97\u5168\u8eab\u90fd\u585e\u6ee1\u9b54\u6cd5\u7269\u54c1\u4e5f\u62b5\u4e0d\u4e0a\uff0c\u53c8\u6709\u4f55\u60e7\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u5e93\u80af\u8fd9\u9634\u5f71\u62a4\u7f69\u2026\u2026\u597d\u773c\u719f\uff0c\u81ea\u5df1\u5df2\u7ecf\u89c1\u8fc7\u51e0\u6b21\u4e86\u3002</p><p>\u743c\u6069\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6beb\u4e0d\u8fdf\u7591\uff0c\u6cd5\u6756\u865a\u5212\u4e86\u534a\u5706\uff0c\u53c8\u91ca\u653e\u51fa\u4e00\u4e2a\u8fdf\u7f13\u672f\u6765\uff0c\u4ed6\u65e0\u9700\u8bf5\u5492\uff0c\u65e0\u9700\u505a\u7cbe\u7ec6\u590d\u6742\u7684\u52a8\u4f5c\uff0c\u8fde\u6750\u6599\u90fd\u4e0d\u7528\u53d6\uff0c\u65bd\u6cd5\u901f\u5ea6\u6bd4\u5bfb\u5e38\u5deb\u5e08\u5feb\u4e0d\u5c11\u3002\u5e93\u80af\u663e\u7136\u6ca1\u6709\u9884\u6599\u5230\u8fd9\u70b9\uff0c\u88ab\u6253\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u4ed6\u6b63\u5728\u51c6\u5907\u4e00\u4e2a\u6cd5\u672f\uff0c\u773c\u770b\u5c31\u8981\u53d1\u51fa\uff0c\u5374\u88ab\u8fdf\u7f13\u672f\u51fb\u4e2d\uff0c\u8eab\u5f62\u4e00\u987f\uff0c\u52a8\u4f5c\u8d70\u5f62\uff0c\u539f\u672c\u5df2\u7ecf\u5373\u5c06\u84c4\u79ef\u5b8c\u6210\u7684\u9b54\u529b\u4e1d\u4e1d\u7f15\u7f15\uff0c\u5c3d\u6570\u6d88\u6563\u5728\u7a7a\u6c14\u4e2d\u53bb\u4e86\u3002</p><p>\u6cd5\u672f\u51b3\u6597\uff0c\u4e0d\u50cf\u6b66\u8005\u62f3\u811a\u6bb4\u51fb\uff0c\u66f4\u591a\u662f\u201c\u6587\u6bd4\u201d\uff0c\u4f60\u6765\u6211\u5f80\uff0c\u653b\u9632\u4ea4\u9519\uff0c\u5982\u679c\u4e00\u65b9\u7684\u8282\u594f\u88ab\u6253\u4e71\uff0c\u90a3\u4fbf\u662f\u5931\u8d25\u7684\u5148\u5146\u3002\u743c\u6069\u51fa\u5176\u4e0d\u610f\uff0c\u5df2\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u4ed6\u4e58\u52bf\u800c\u8fdb\uff0c\u6cd5\u6756\u4e0a\u516d\u679a\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u4eae\u8d77\uff0c\u4e00\u9053\u767d\u5149\u81ea\u6756\u7aef\u8f70\u7136\u7206\u51fa\uff0c\u6f6e\u6c34\u822c\u6d8c\u5411\u5e93\u80af\u3002</p><p>\u201c\u9ad8\u7b49\u89e3\u9664\uff01\u201d</p><p>\u7ecf\u8fc7\u5f3a\u5316\u7684\u89e3\u9664\u9b54\u6cd5\u51b2\u6cfb\u800c\u8fc7\uff0c\u7b3c\u7f69\u5e93\u80af\u7684\u9634\u5f71\u62a4\u7f69\u987f\u65f6\u6d88\u6563\u65e0\u5f62\uff0c\u8fde\u5e26\u4ed6\u8eab\u4e0a\u7684\u9b54\u6cd5\u7075\u5149\u90fd\u9eef\u6de1\u4e86\u51e0\u5206\uff0c\u663e\u7136\u987a\u5e26\u8fd8\u89e3\u6d88\u6216\u538b\u5236\u4e86\u5176\u4ed6\u7684\u9632\u62a4\u6cd5\u672f\u3002\u743c\u6069\u81ea\u5df1\u90fd\u6ca1\u9884\u8ba1\u5230\u4f1a\u6709\u8fd9\u7b49\u6210\u679c\uff0c\u5927\u559c\u4e4b\u4e0b\u8e0f\u524d\u534a\u6b65\uff0c\u4e00\u70b9\u7fe0\u7eff\u5fae\u5149\u5728\u6cd5\u6756\u9876\u7aef\u84c4\u79ef\uff0c\u75be\u901f\u65cb\u8f6c\u6da8\u5927\uff0c\u51dd\u805a\u6210\u7403\uff0c\u5c31\u5f85\u51fb\u51fa\u3002</p><p>\u8fd9\u662f\u89e3\u79bb\u672f\uff0c\u743c\u6069\u76ee\u524d\u6240\u638c\u63e1\u7684\u6700\u5f3a\u6740\u4f24\u9b54\u6cd5\uff0c\u867d\u7136\u9650\u4e8e\u201c\u591c\u5e55\u201d\u7684\u89c4\u5219\uff0c\u4e0d\u53ef\u80fd\u6740\u6b7b\u5e93\u80af\uff0c\u4f46\u5374\u6709\u5f88\u9ad8\u7684\u6982\u7387\u76f4\u63a5\u83b7\u80dc\u3002\u5e93\u80af\u7684\u9632\u62a4\u9b54\u6cd5\u521a\u521a\u88ab\u6467\u7834\uff0c\u4e00\u65f6\u95f4\u80af\u5b9a\u6765\u4e0d\u53ca\u91cd\u65b0\u6784\u5efa\uff0c\u573a\u5730\u5f88\u5c0f\uff0c\u4ed6\u60f3\u95ea\u907f\u4e5f\u662f\u8270\u96be\u2014\u2014\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u4ed6\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u53cc\u624b\u5f80\u5185\u865a\u865a\u4e00\u5408\uff0c\u7d27\u63a5\u7740\u743c\u6069\u773c\u524d\u4e00\u6697\uff0c\u53ea\u89c9\u6574\u4e2a\u573a\u5730\u5185\u7684\u5149\u7ebf\u5239\u90a3\u95f4\u9eef\u6de1\u4e86\u4e0b\u6765\u3002</p><p>\u6b64\u65f6\u662f\u4e0a\u5348\u5341\u70b9\u949f\uff0c\u9633\u5149\u900f\u8fc7\u5c4b\u9876\u7167\u5c04\u8fdb\u6765\uff0c\u56fa\u7136\u8bf4\u4e0d\u4e0a\u707f\u70c2\uff0c\u5374\u4e5f\u7b97\u662f\u6bd4\u8f83\u4eae\u5802\uff0c\u4f46\u5e93\u80af\u4e0d\u77e5\u9053\u505a\u4e86\u4ec0\u4e48\uff0c\u5c45\u7136\u51ed\u7a7a\u53ec\u51fa\u5de8\u5927\u7684\u9634\u5f71\uff0c\u5c06\u6574\u4e2a\u573a\u5730\u90fd\u7b3c\u7f69\u5728\u5176\u4e2d\uff0c\u800c\u4ee5\u4ed6\u672c\u4eba\u6240\u7ad9\u7acb\u7684\u4f4d\u7f6e\u6700\u4e3a\u9634\u6697\uff0c\u5149\u7ebf\u51e0\u4e4e\u90fd\u900f\u4e0d\u8fdb\u53bb\u3002\u5e93\u80af\u4eba\u5728\u9634\u5f71\u4e4b\u4e2d\uff0c\u987f\u65f6\u4fbf\u7cbe\u795e\u4e86\u51e0\u5206\u4f3c\u7684\uff0c\u82e5\u6709\u82e5\u65e0\u5730\u5439\u4e86\u58f0\u53e3\u54e8\uff0c\u6597\u7bf7\u80a9\u90e8\u7ed8\u5236\u7684\u5bb6\u5fbd\u56fe\u6848\u9661\u7136\u95ea\u4e86\u4e00\u95ea\uff0c\u4ece\u4e2d\u8dc3\u51fa\u4e24\u53ea\u72f0\u72de\u602a\u7269\uff0c\u5168\u8eab\u706b\u7ea2\uff0c\u957f\u89d2\u7360\u7259\uff0c\u671d\u7740\u743c\u6069\u731b\u51b2\u800c\u6765\u3002</p><p>\u743c\u6069\u7684\u89e3\u79bb\u672f\u6b63\u597d\u53d1\u51fa\uff0c\u51fb\u4e2d\u4e00\u53ea\u602a\u7269\uff0c\u5c06\u5b83\u5316\u4f5c\u7c89\u672b\uff0c\u4f46\u53e6\u5916\u4e00\u53ea\u7d27\u8ddf\u7740\u6251\u4e0a\u3002\u743c\u6069\u4e0d\u5f97\u4e0d\u540e\u9000\u95ea\u907f\uff0c\u540c\u65f6\u63b7\u51fa\u9f99\u9cde\u76fe\u5c06\u5b83\u5f15\u5f00\u3002\u5e93\u80af\u4e58\u673a\u53d1\u52a8\u4e86\u53cd\u51fb\uff0c\u4f4e\u6c89\u7684\u5492\u6587\u6025\u4fc3\u5ff5\u51fa\uff0c\u5404\u79cd\u9b54\u6cd5\u7269\u54c1\u63a5\u8fde\u4f7f\u7528\uff0c\u4e00\u53ea\u53ea\u72f0\u72de\u53ef\u6016\u7684\u602a\u7269\u81ea\u865a\u7a7a\u4e2d\u88ab\u53ec\u5524\u51fa\u6765\uff0c\u5411\u743c\u6069\u53d1\u52a8\u4e86\u56f4\u653b\u3002</p><p>\u4f5c\u4e3a\u6614\u65e5\u7684\u540c\u5b66\uff0c\u5e93\u80af\u5f88\u6e05\u695a\u5bf9\u624b\u7684\u5f31\u70b9\u6240\u5728\u3002\u743c\u6069\u662f\u53d8\u5316\u5e08\uff0c\u53d8\u5316\u5b66\u6d3e\u9b54\u6cd5\u7684\u957f\u5904\u662f\u8f85\u52a9\u3001\u5f3a\u5316\uff0c\u501f\u52a9\u73af\u5883\u5f71\u54cd\uff0c\u4e5f\u4e0d\u7f3a\u4e4f\u4e00\u51fb\u5b9a\u80dc\u8d1f\u7684\u5f3a\u529b\u653b\u51fb\u624b\u6bb5\uff0c\u4f8b\u5982\u53d8\u5f62\u3001\u89e3\u79bb\u3001\u77f3\u5316\u7b49\u7b49\uff0c\u4f46\u5b83\u7684\u7f3a\u9677\u5c31\u5728\u4e8e\uff1a\u8fd9\u4e9b\u653b\u51fb\u6cd5\u672f\u90fd\u662f\u9488\u5bf9\u5355\u4eba\u7684\u3002\u89e3\u79bb\u672f\u80fd\u628a\u4e00\u4e2a\u5de8\u4eba\u5316\u4f5c\u7c89\u672b\uff0c\u5374\u6ca1\u529e\u6cd5\u540c\u65f6\u5bf9\u4ed8\u4e09\u53ea\u5730\u7cbe\uff0c\u77f3\u5316\u672f\u80fd\u628a\u6076\u9f99\u53d8\u6210\u96d5\u5851\uff0c\u5374\u6ca1\u529e\u6cd5\u540c\u65f6\u77f3\u5316\u4e24\u4e2a\u666e\u901a\u4eba\u7c7b\uff0c\u5355\u5bf9\u5355\u7684\u8bdd\uff0c\u5c31\u7b97\u78b0\u4e0a\u5f3a\u624b\u4e5f\u6709\u8d8a\u7ea7\u6311\u6218\u4e4b\u529b\uff0c\u6700\u6015\u7684\u53cd\u800c\u662f\u9677\u5165\u6742\u5175\u5305\u56f4\u3002</p><p>\u800c\u5e93\u80af\u662f\u5492\u6cd5\u5e08\uff0c\u7cbe\u7814\u7684\u66f4\u662f\u5492\u6cd5\u5b66\u6d3e\u4e2d\u7684\u201c\u53ec\u5524\u201d\u5206\u652f\uff0c\u6700\u64c5\u957f\u7684\u5c31\u662f\u53ec\u5524\u5927\u6279\u70ae\u7070\uff0c\u7528\u4eba\u6d77\u6218\u672f\u6765\u5806\u6b7b\u5bf9\u624b\u3002\u4ed6\u53ec\u5524\u7684\u602a\u7269\u90fd\u4e0d\u5f3a\uff0c\u51c6\u786e\u5730\u8bf4\u662f\u5f88\u5f31\uff0c\u4f46\u80dc\u5728\u591f\u591a\uff0c\u591f\u5feb\uff0c\u6e90\u6e90\u4e0d\u65ad\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u6253\u5f00\u4e86\u52a8\u7269\u56ed\u7684\u7b3c\u5b50\u2026\u2026</p><p>\u5018\u82e5\u6362\u4e86\u51db\u5728\uff0c\u90a3\u5c31\u6b63\u662f\u5f97\u5c55\u6240\u957f\uff0c\u5404\u79cd\u706b\u7403\u95ea\u7535\u8c6a\u5feb\u8f70\u6740\uff0c\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u5851\u80fd\u5e08\u539f\u672c\u5c31\u6709\u201c\u79fb\u52a8\u70ae\u53f0\u201d\u7684\u8bf4\u6cd5\uff0c\u6700\u64c5\u957f\u7684\u5c31\u662f\u72c2\u8f70\u6ee5\u70b8\uff0c\u6e05\u626b\u6742\u5175\uff0c\u7136\u800c\u743c\u6069\u504f\u504f\u653e\u5f03\u4e86\u5851\u80fd\u672f\u3002</p><p>\u53ec\u5524\u5e08\u81ea\u7136\u4e5f\u6709\u5f31\u70b9\uff0c\u4fbf\u662f\u5728\u53ec\u5524\u7684\u65f6\u5019\u9700\u8981\u201c\u51dd\u795e\u201d\uff0c\u4e0d\u80fd\u5206\u5fc3\uff0c\u4e0d\u80fd\u79fb\u52a8\uff0c\u65e0\u6cd5\u9632\u5fa1\u95ea\u907f\uff0c\u5f88\u5bb9\u6613\u88ab\u653b\u51fb\u6253\u5012\u3002\u4f46\u5e93\u80af\u663e\u7136\u65e9\u6709\u51c6\u5907\uff0c\u4ed6\u53ea\u8981\u7ad9\u5728\u9634\u5f71\u4e4b\u4e2d\uff0c\u8eab\u5f62\u4fbf\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u82e5\u9690\u82e5\u73b0\uff0c\u4eff\u4f5b\u7a7f\u4e86\u4e00\u4ef6\u4f4d\u79fb\u6597\u7bf7\u4f3c\u7684\uff0c\u8ba9\u743c\u6069\u6839\u672c\u65e0\u6cd5\u628a\u63e1\u4ed6\u7684\u51c6\u786e\u4f4d\u7f6e\uff0c\u81ea\u7136\u4e5f\u5c31\u5f88\u96be\u8fdb\u884c\u653b\u51fb\u3002\u66f4\u8ba9\u743c\u6069\u60ca\u8bb6\u7684\u662f\uff0c\u5e93\u80af\u9041\u5165\u9634\u5f71\u4e4b\u540e\uff0c\u8fde\u65bd\u6cd5\u7684\u901f\u5ea6\u4eff\u4f5b\u90fd\u53d8\u5feb\u4e86\u4e9b\uff0c\u51e0\u4e4e\u53ef\u4ee5\u548c\u81ea\u5df1\u76f8\u5f53\uff0c\u60f3\u8981\u50cf\u4e00\u5f00\u59cb\u90a3\u6837\u6293\u4f4f\u7a7a\u9699\u6253\u7834\u8282\u594f\uff0c\u5df2\u7ecf\u5f88\u96be\u529e\u5230\u4e86\u3002</p><p>\u201c\u8299\u8389\u5a05\u8bf4\u5f97\u6ca1\u9519\uff0c\u4ed6\u5728\u9634\u5f71\u91cc\u4f1a\u683c\u5916\u5f3a\uff0c\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u7591\u60d1\uff0c\u4f46\u6b64\u65f6\u4e5f\u6ca1\u6cd5\u5f00\u53e3\u8be2\u95ee\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u56db\u679a\u8428\u74e6\u68cb\u5b50\u63b7\u4e0b\u5730\u6765\uff0c\u5316\u4f5c\u56db\u4e2a\u624b\u6301\u72fc\u7259\u68d2\u7684\u98df\u4eba\u9b54\uff0c\u4eff\u4f5b\u5766\u514b\u4f3c\u7684\u671d\u56db\u9762\u51b2\u649e\u51fa\u53bb\uff0c\u5c06\u5e93\u80af\u53ec\u5524\u7684\u90a3\u4e9b\u602a\u7269\u98ce\u5377\u6b8b\u4e91\u822c\u5c3d\u6570\u7838\u5f00\u3002\u5e93\u80af\u663e\u7136\u6ca1\u60f3\u5230\u743c\u6069\u5c45\u7136\u4f1a\u643a\u5e26\u6709\u8fd9\u6837\u5f3a\u529b\u7684\u9b54\u50cf\u62a4\u536b\uff0c\u7565\u7565\u4e00\u60ca\uff0c\u4f38\u624b\u53d6\u4e0b\u81ea\u5df1\u5de6\u8033\u4e0a\u7684\u91d1\u73af\uff0c\u4f4e\u58f0\u5ff5\u4e86\u4e00\u4e2a\u53e3\u4ee4\u3002</p><p>\u91d1\u73af\u4e0a\u5149\u8292\u707f\u70c2\uff0c\u8000\u773c\u523a\u76ee\uff0c\u4e00\u53ea\u901a\u4f53\u6f06\u9ed1\uff0c\u957f\u7740\u56db\u53ea\u624b\u81c2\u7684\u5de8\u5927\u7329\u7329\u8df3\u4e86\u51fa\u6765\uff0c\u8377\u8377\u602a\u53eb\u7740\u671d\u743c\u6069\u51b2\u53bb\u3002\u743c\u6069\u6307\u6325\u56db\u53ea\u98df\u4eba\u9b54\u8fce\u4e0a\uff0c\u53cc\u65b9\u567c\u91cc\u556a\u5566\u6253\u6210\u4e00\u56e2\uff0c\u7329\u7329\u4ee5\u4e00\u654c\u56db\uff0c\u867d\u7136\u63a5\u8fde\u88ab\u72fc\u7259\u68d2\u7838\u4e2d\uff0c\u53cd\u800c\u8d8a\u52a0\u51f6\u608d\uff0c\u6012\u543c\u8fde\u8fde\uff0c\u56db\u53ea\u624b\u81c2\u4e0d\u65ad\u53d8\u5e7b\u6210\u68cd\u3001\u9524\u3001\u5200\u3001\u621f\u7b49\u5404\u79cd\u6b66\u5668\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u52c7\u4e0d\u53ef\u5f53\u3002</p><p>\u201c\u70bc\u72f1\u56db\u81c2\u7329\u7329\u2026\u2026\u5e93\u80af\u5bb6\u65cf\u5c45\u7136\u80fd\u62d8\u5f79\u8fd9\u79cd\u90aa\u7269\u4f9b\u4f7f\u9a71\u9063\uff0c\u679c\u771f\u662f\u6709\u4e24\u4e0b\u5b50\uff0c\u8fd9\u5e94\u8be5\u662f\u4ed6\u6700\u5927\u7684\u5e95\u724c\u4e86\u5427\u3002\u201d</p><p>\u70bc\u72f1\u56db\u81c2\u7329\u7329\u867d\u7136\u52c7\u731b\uff0c\u5df2\u7ecf\u5360\u636e\u4e0a\u98ce\uff0c\u4f46\u743c\u6069\u7684\u98df\u4eba\u9b54\u80dc\u5728\u662f\u9b54\u50cf\uff0c\u5e76\u975e\u8840\u8089\u4e4b\u8eaf\uff0c\u6297\u6253\u51fb\u80fd\u529b\u4e00\u6d41\uff0c\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u843d\u8d25\uff0c\u6682\u65f6\u8fd8\u80fd\u652f\u6491\uff0c\u53cc\u65b9\u6df7\u6218\u6210\u4e00\u56e2\u3002\u5e93\u80af\u8f6c\u52a8\u624b\u4e0a\u7684\u6212\u6307\uff0c\u53ec\u51fa\u4e00\u53ea\u706b\u5de8\u7075\uff0c\u6b63\u8981\u547d\u4ee4\u5b83\u8fdb\u653b\uff0c\u5374\u89c1\u743c\u6069\u4f38\u624b\u5165\u6000\uff0c\u4eff\u4f5b\u63e1\u4f4f\u4e86\u4ec0\u4e48\u4e1c\u897f\uff0c\u6084\u4e0d\u53ef\u95fb\u5730\u5730\u5ff5\u51fa\u4e00\u4e2a\u8bcd\u3002</p><p>\u4e00\u56e2\u90aa\u708e\u5728\u743c\u6069\u9762\u524d\u7684\u7a7a\u6c14\u91cc\u8f70\u7136\u817e\u8d77\uff0c\u70c8\u70c8\u71c3\u70e7\uff0c\u9b45\u9b54\u838e\u73de\u514b\u4e00\u8dc3\u800c\u51fa\uff0c\u6325\u821e\u7740\u7194\u5ca9\u97ad\u671d\u7740\u5e93\u80af\u649e\u53bb\u3002</p><p>\u743c\u6069\u4e5f\u7ffb\u51fa\u4e86\u81ea\u5df1\u7684\u5e95\u724c\u3002</p></article>"}}}]);
