(window.webpackJsonp=window.webpackJsonp||[]).push([[164],{187:function(p){p.exports={chapterTitle:null,title:"\u7b2c1142\u7ae0 \u8c22\u5b50\u5609\u5f53\u4e0d\u901f\u4e4b\u5ba2",content:"<article><h2>\u7b2c1142\u7ae0 \u8c22\u5b50\u5609\u5f53\u4e0d\u901f\u4e4b\u5ba2</h2><p>\u3000\u3000\u5bf9\u6709\u4e9b\u4eba\u6765\u8bf4\uff0c\u4e94\u4e00\u9ec4\u91d1\u5468\u8fc7\u4e8e\u6f2b\u957f\u4e86\uff0c\u5f53\u6caa\u6df1\u4e24\u5e02\u4e94\u6708\u7b2c\u4e00\u4e2a\u4ea4\u6613\u65e5\u5f00\u76d8\u4ea4\u6613\u3001\u4e92\u8054\u7f51\u6982\u5ff5\u80a1\u518d\u6b21\u906d\u5230\u6295\u8d44\u8005\u7684\u629b\u5f03\uff1b\u8fd9\u4e9b\u4eba\u53c8\u5e0c\u671b\u4e94\u4e00\u9ec4\u91d1\u5468\u80fd\u518d\u957f\u4e00\u4e9b\uff0c\u4e5f\u8bb8\u7b49\u5669\u68a6\u8fc7\u53bb\u518d\u7ed3\u675f\u9ec4\u91d1\u5468\u957f\u5047\u4e0d\u8fdf\u3002</p><p>\u3000\u3000\u4e94\u6708\u7684\u7b2c\u4e00\u4e2a\u4ea4\u6613\u65e5\uff0c\u6d77\u7c9f\u79d1\u6280\u518d\u5ea6\u8dcc\u53bb8\uff05\uff0c\u867d\u7136\u518d\u6765\u4e00\u4e2a\u8dcc\u505c\uff0c\u4e5f\u591f\u96be\u770b\u7684\uff0c\u5e02\u503c\u4ece\u63a5\u8fd1170\u4ebf\u7684\u5dc5\u5cf0\u8dcc\u5230\u4e0d\u8db3125\u4ebf\u3002\u4e5f\u662f\u4eca\u5929\uff0c\u8bc1\u76d1\u4f1a\u6b63\u5f0f\u6279\u51c6\u4e86\u6d77\u7c9f\u79d1\u6280\u63d0\u4ea4\u7684\u589e\u53d1\u7533\u8bf7\u3002</p><p>\u3000\u3000\u6a80\u6728\u5236\u7684\u4f1a\u8bae\u5ba4\u684c\u4e0a\uff0c\u653e\u7740\u4eca\u5929\u7684\u65b0\u534e\u65e5\u62a5\uff0c\u5934\u7248\u5934\u6761\u7684\u8bc4\u8bba\u5458\u6587\u7ae0\u5c31\u4ee5\u300a\u4fc3\u8fdb\u4e92\u8054\u7f51\u4ea7\u4e1a\u5065\u5eb7\u6709\u5e8f\u53d1\u5c55\u300b\u53d1\u9898\uff0c\u5b9e\u9645\u4e0a\u6279\u8bc4\u4e86\u5f53\u524d\u6295\u8d44\u8005\u5728\u6caa\u6df1\u4e24\u5e02\u5bf9\u4e92\u8054\u7f51\u6982\u5ff5\u80a1\u7684\u7092\u4f5c\u884c\u4e3a\u3002\u5c31\u662f\u8fd9\u7bc7\u8bc4\u8bba\u5458\u6587\u7ae0\u518d\u52a0\u4e0a\u8d22\u7ecf\u5a92\u4f53\u4eca\u5929\u5c31\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u4ea4\u6613\u7684\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u4e8e\u8fc7\u53bb\u516b\u4e2a\u4ea4\u6613\u65e5\u5185\u51e0\u4e4e\u906d\u5230\u8170\u65a9\u7684\u6d88\u606f\u8fdb\u884c\u4e86\u5e7f\u6cdb\u7684\u62a5\u9053\uff0c\u5bf9\u4eca\u5929\u6caa\u6df1\u4e24\u5e02\u7684\u4e92\u8054\u7f51\u6982\u5ff5\u80a1\u5f62\u6210\u57fa\u672c\u9762\u7684\u538b\u5236\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u7709\u5934\u6df1\u9501\uff0c\u4ed6\u4e8b\u5148\u6709\u9884\u6599\u5230\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u5439\u8d77\u7684\u6ce1\u6cab\u6709\u4e00\u5929\u4f1a\u6491\u4e0d\u4f4f\uff0c\u53ea\u662f\u6ca1\u6709\u60f3\u5230\u4f1a\u6765\u5f97\u5982\u6b64\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u5b89\u6392\u5728\u4e00\u5468\u4e4b\u540e\u516c\u5f00\u589e\u53d1\u5df2\u7ecf\u4e0d\u518d\u53ef\u80fd\uff0c\u6d41\u901a\u76d8\u77ed\u65f6\u95f4\u91cc\u589e\u52a0\u4e00\u500d\uff0c\u5728\u5f53\u524d\u53d7\u538b\u5236\u7684\u5c40\u52bf\u4e0b\uff0c\u80a1\u4ef7\u5f88\u6709\u53ef\u80fd\u4f1a\u5448\u96ea\u5d29\u5f0f\u4e0b\u632b\uff0c\u661f\u6cb3\u8bc1\u5238\u4e0d\u6562\u62c5\u8fd9\u4e48\u5927\u7684\u98ce\u9669\uff0c\u5df2\u7ecf\u6d41\u9732\u51fa\u8981\u9000\u7f29\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u201c\u62ff\u94b1\u3001\u73a9\u5973\u4eba\u65f6\u5c31\u6ca1\u6709\u89c1\u4ed6\u4eec\u8f6f\u8fc7\uff0c\u8fd9\u65f6\u5019\u5374\u8f6f\u852b\u852b\u7684\u8ddf\u53ea\u7f29\u5934\u4e4c\u9f9f\u4f3c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u773c\u5e18\u95ea\u4e86\u95ea\uff0c\u62ff\u773c\u89d2\u4f59\u5149\u77a5\u4e86\u6797\u96ea\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u8bf4\u8fd9\u8bdd\u65f6\u674f\u76ee\u5fae\u5f20\uff0c\u8138\u816e\u5fae\u7ea2\uff0c\u4f9d\u65e7\u90a3\u6837\u7684\u8ff7\u4eba\uff0c\u53ea\u662f\u5979\u5fae\u5fae\u8d77\u7740\u76b1\u7684\u9f3b\u7ffc\u66b4\u9732\u51fa\u5979\u5185\u5fc3\u7684\u6012\u6c14\uff0c\u5973\u4eba\uff0c\u7279\u522b\u662f\u81ea\u8d1f\u7684\u6f02\u4eae\u5973\u4eba\uff0c\u8fd9\u65f6\u5019\u662f\u4e0d\u80fd\u60f9\u7684\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u6ca1\u6709\u542d\u58f0\u2026\u2026</p><p>\u3000\u3000\u661f\u6cb3\u8bc1\u5238\u867d\u7136\u603b\u90e8\u5728\u5efa\u90ba\uff0c\u4e4b\u524d\u4e5f\u662f\u4e1c\u6d77\u7701\u5c5e\u4f01\u4e1a\uff0c\u4e5d\u516d\u5e74\u65f6\uff0c\u56fd\u6709\u91d1\u878d\u4f01\u4e1a\u8fdb\u884c\u91cd\u7ec4\u65f6\uff0c\u8d22\u653f\u90e8\u63a5\u66ff\u4e1c\u6d77\u7701\u7ecf\u8d38\u59d4\u6210\u4e86\u661f\u6cb3\u8bc1\u5238\u80cc\u540e\u7684\u51fa\u8d44\u4eba\uff0c\u4ed6\u4e8b\u5148\u5c31\u63a5\u5230\u8d22\u653f\u90e8\u5185\u90e8\u7684\u901a\u878d\u7535\u8bdd\uff0c\u5373\u4f7f\u661f\u6cb3\u8bc1\u5238\u613f\u610f\u627f\u62c5\u98ce\u9669\u5f3a\u884c\u589e\u53d1\uff0c\u5931\u8d25\u7684\u53ef\u80fd\u6027\u4e5f\u76f8\u5f53\u7684\u9ad8\uff0c\u4e8b\u60c5\u6345\u5927\u4e86\uff0c\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u727a\u7272\u4e2a\u8c01\u5c31\u515c\u4f4f\u7684\u2014\u2014\u8fd9\u663e\u7136\u8d22\u653f\u90e8\u5728\u80cc\u540e\u4e5f\u53d7\u5230\u67d0\u4e9b\u4eba\u65bd\u52a0\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u201c\u8bc1\u76d1\u4f1a\u7684\u6838\u51c6\u6587\u4ef6\u6709\u516d\u4e2a\u6708\u7684\u65f6\u6548\uff0c\u6211\u4ee5\u4e3a\u8fd8\u662f\u6682\u7f13\u589e\u53d1\u4e3a\u597d\uff0c\u201d\u5218\u5947\u5cfb\u8bf4\u9053\uff0c\u201c\u5728\u5c40\u52bf\u6ca1\u6709\u5f97\u5230\u5f7b\u5e95\u6539\u5584\u4e4b\u524d\uff0c\u5f3a\u884c\u589e\u53d1\u7684\u98ce\u9669\u592a\u5927\uff0c\u9996\u5148\u662f\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u4ece\u6700\u9ad8\u6bcf\u80a145\u5143\u4e0b\u632b\u5230\u73b0\u5728\u7684\u6bcf\u80a132\u5143\uff0c\u5df2\u7ecf\u8dcc\u7834\u6bcf\u80a134\u5143\u7684\u589e\u53d1\u5e95\u4ef7\uff0c\u6211\u4eec\u6b64\u65f6\u80fd\u505a\u7684\u5c31\u662f\u80a1\u4ef7\u7ef4\u6301\u572830\u5143\u5de6\u53f3\uff0c\u5f3a\u884c\u9006\u5e02\u6258\u523034\u5143\u4ee5\u4e0a\u7684\u98ce\u9669\u4e0e\u538b\u529b\u90fd\u592a\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u71d5\u56ed\uff0c\u675c\u98de\u6240\u4f4f\u7684\u522b\u5885\u4e5f\u662f\u71d5\u56ed\u6700\u5357\u4fa7\u4e34\u6e56\u7684\u516b\u680b\u522b\u5885\u4e4b\u4e00\uff0c\u4e8c\u697c\u7684\u89c6\u91ce\u5f00\u9614\uff0c\u53ef\u4ee5\u773a\u671b\u78a7\u6ce2\u8361\u6f3e\u7684\u71d5\u5f52\u6e56\uff0c\u53ea\u662f\u671b\u5411\u71d5\u5f52\u5c71\u7684\u89c6\u91ce\u7ed9\u4fa7\u524d\u65b9\u7684\u79d1\u738b\u9ad8\u79d1\u7814\u53d1\u5927\u697c\u906e\u4f4f\uff0c\u6709\u7a0d\u7a0d\u7684\u9057\u61be\u3002</p><p>\u3000\u3000\u4eca\u5929\uff0c\u675c\u98de\u5728\u71d5\u56ed\u7684\u522b\u5885\u91cc\u805a\u96c6\u4e86\u597d\u4e9b\u4eba\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u8fd9\u6b21\u4ece\u5317\u4eac\u5230\u5efa\u90ba\u6765\uff0c\u675c\u98de\u4e0a\u6b21\u8bf4\u8fc7\u8981\u62db\u5f85\u4ed6\u4eec\uff0c\u5c31\u5c06\u5927\u5bb6\u90fd\u8bf7\u5230\u522b\u5885\u91cc\u6765\u529e\u4e2a\u70e7\u70e4\u81ea\u52a9\u9910\u4f1a\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u5728\u8fc7\u53bb\u516b\u4e2a\u4ea4\u6613\u65e5\u91cc\u51e0\u4e4e\u906d\u5230\u8170\u65a9\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u4e1c\u7b49\u9ad8\u5c42\u8981\u627f\u62c5\u8d77\u8d23\u4efb\uff0c\u6240\u8c13\u7684\u8d23\u4efb\u5e76\u4e0d\u662f\u8981\u81ea\u7f5a\u85aa\u6c34\u3001\u5f15\u548e\u8f9e\u804c\u4ec0\u4e48\u7684\uff0c\u8fd8\u662f\u8981\u5c3d\u5feb\u7684\u7ed9\u6606\u817e\u5728\u7ebf\u5bfb\u627e\u65b0\u7684\u76c8\u5229\u70b9\uff0c\u5728\u73b0\u6709\u8d44\u91d1\u8017\u5b8c\u4e4b\u524d\u8fbe\u5230\u76c8\u4e8f\u5e73\u8861\u3002</p><p>\u3000\u3000\u4eca\u5929\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u5a92\u4f53\u8d22\u7ecf\u7248\u9762\u90fd\u62a5\u9053\u4e86\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u51e0\u4e4e\u906d\u8170\u65a9\u7684\u6d88\u606f\uff0c\u867d\u8bf4\u9a6c\u5411\u4e1c\u4ed6\u4eec\u90fd\u6709\u610f\u7eb5\u5bb9\u8fd9\u4e9b\u62a5\u9053\uff0c\u771f\u6b63\u770b\u5230\u8fd9\u4e9b\u6d88\u606f\u94fa\u5929\u76d6\u5730\u7684\u5728\u5a92\u4f53\u4e0a\u4f20\u64ad\uff0c\u5fc3\u60c5\u81ea\u7136\u4e0d\u597d\u53d7\uff0c\u611f\u53d7\u5230\u7684\u538b\u529b\u4e5f\u5f88\u5927\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u8d76\u5230\u5efa\u90ba\u6765\uff0c\u56e0\u4e3a\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u4e0e\u4e1c\u5927\u521b\u57df\u5728\u4e92\u8054\u7f51\u4e0a\u4e5f\u6709\u8bb8\u591a\u521b\u65b0\u578b\u7684\u6295\u8d44\uff0c\u5927\u5bb6\u805a\u5728\u4e00\u8d77\uff0c\u770b\u6709\u6ca1\u6709\u627e\u5230\u5f7c\u6b64\u4e4b\u95f4\u4e1a\u52a1\u878d\u5408\u3001\u63d0\u9ad8\u8425\u6536\u7684\u95e8\u5f84\u3002</p><p>\u3000\u3000\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u5353\u57df\u521b\u59cb\u4eba\u4e5f\u7ed9\u773c\u524d\u7eb3\u65af\u8fbe\u514b\u7684\u54c0\u9e3f\u904d\u91ce\u3001\u5c38\u9ab8\u72fc\u7c4d\u4e4b\u60e8\u72b6\u72e0\u72e0\u7684\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u57284\u670826\u65e5\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u66b4\u8dcc\u4e4b\u524d\uff0c\u6bcf\u9022\u76f8\u805a\u90fd\u8981\u9ad8\u58f0\u9614\u8c08\u8fbe\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\uff0c\u4e00\u65e6\u80fd\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\uff0c\u4ed6\u4eec\u4e5f\u5c06\u6210\u4e3a\u50cf\u9a6c\u5411\u529b\u90a3\u6837\u88ab\u5a92\u4f53\u56f4\u8ffd\u5835\u622a\u7684\u8d22\u5bcc\u82f1\u96c4\uff1b\u8fd9\u51e0\u5929\u5374\u662f\u7edd\u53e3\u4e0d\u63d0\u4e0a\u5e02\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5728\u5927\u5e45\u632f\u8361\u4e2d\u6709\u4f01\u7a33\u7684\u8ff9\u8c61\uff0c\u4f46\u662f\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u7f51\u7edc\u80a1\u5374\u9971\u53d7\u91d1\u878d\u6d77\u5578\u7684\u6467\u6b8b\uff0c\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u516b\u4e2a\u4ea4\u6613\u65e5\u51e0\u906d\u8170\u65a9\uff0c\u8fd8\u4e0d\u7b97\u8dcc\u5e45\u6df1\u7684\u3002\u5728\u6606\u817e\u5728\u7ebf\u4e4b\u540e\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u7f51\u666f\u3001\u6d6a\u6f6e\u7b49\u4e2d\u6587\u95e8\u6237\u7f51\u7ad9\u5f88\u5e78\u8fd0\u8d76\u5728\u5371\u673a\u7206\u53d1\u4e4b\u524d\u6210\u529f\u4e0a\u5e02\uff0c\u6d6a\u6f6e\u7684\u80a1\u4ef7\u8dcc\u5e45\u751a\u81f3\u4e00\u5ea6\u8d85\u8fc760\uff05\u3002</p><p>\u3000\u3000\u60f3\u60f3\u90a3\u4e9b\u5728\u4e0a\u5e02\u4e4b\u524d\u5bf9\u6d6a\u6f6e\u3001\u7f51\u666f\u7b49\u4e2d\u6587\u95e8\u6237\u7f51\u7ad9\u8fdb\u884c\u6ce8\u8d44\u7684\u98ce\u9669\u57fa\u91d1\uff0c\u8fd9\u6b21\u4e70\u5356\u53ea\u6015\u8981\u5c06\u5185\u88e4\u90fd\u8f93\u6389\u4e86\uff1b\u98ce\u9669\u6295\u8d44\u5546\u4eec\u53ea\u6015\u5df2\u6210\u4e86\u60ca\u5f13\u4e4b\u9e1f\uff0c\u98ce\u5411\u9661\u5411\uff0c\u4e0d\u8981\u5962\u671b\u518d\u6709\u5927\u89c4\u6a21\u7684\u8d44\u91d1\u4f1a\u6d41\u5411\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u4e5f\u8bb8\u518d\u8fc7\u4e24\u4e09\u4e2a\u6708\u5c31\u4f1a\u56e0\u4e3a\u8d44\u91d1\u4f9b\u7ed9\u4e0d\u8db3\u51fa\u73b0\u7f51\u7ad9\u5012\u95ed\u6f6e\u2026\u2026</p><p>\u3000\u3000\u5353\u57df\u524d\u540e\u4e24\u6b21\u63a5\u53d7\u4e1c\u5927\u521b\u57df\u3001\u4e1c\u5927\u79d1\u6280\u5f00\u53d1\u603b\u516c\u53f8\u4ee5\u53ca\u6606\u817e\u5728\u7ebf\u7684\u6ce8\u8d44\uff0c\u524d\u540e\u5171\u83b7\u5f97\u7ea6\u4e94\u767e\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u3002\u4e0d\u6bd48818\u4e00\u4e0b\u5b50\u4ece\u6d77\u7c9f\u79d1\u6280\u83b7\u5f97\u8fd1\u56db\u4ebf\u5143\u7684\u6ce8\u8d44\uff0c\u5353\u57df\u73b0\u6709\u7684\u8d44\u91d1\u53ea\u80fd\u652f\u6491\u8fd0\u8425\u5230\u5e74\u5e95\uff0c\u800c\u5728\u5e74\u5e95\u4e4b\u524d\u5b9e\u65bd\u76c8\u5229\u663e\u7136\u662f\u4e0d\u53ef\u80fd\u5b8c\u6210\u7684\u4efb\u52a1\u3002</p><p>\u3000\u3000\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u77e5\u9053\u675c\u98de\u4ed6\u4eec\u4e0d\u4f1a\u8f7b\u6613\u653e\u5f03\u5353\u57df\u3001\u653e\u5f03\u7535\u5b50\u5546\u52a1\uff0c\u53ea\u662f\u5728\u4ed6\u4eec\u6ca1\u6709\u6b63\u5f0f\u8868\u6001\u4e4b\u524d\uff0c\u4ec0\u4e48\u90fd\u8bf4\u4e0d\u51c6\uff0c\u96be\u514d\u4f1a\u601d\u524d\u60f3\u540e\u3001\u5fe7\u5fc3\u91cd\u91cd\u3002</p><p>\u3000\u3000\u5c31\u7b97\u4e1c\u5927\u521b\u57df\u7684\u4e00\u7fa4\u4eba\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u7b49\u4eba\u4e5f\u4e0d\u662f\u975e\u5e38\u7684\u8f7b\u677e\uff0c\u4e1c\u5927\u521b\u57df\u5c06\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u8f6c\u624b\u7ed9\u6d77\u7c9f\u79d1\u6280\u3001\u878d\u5165\u9526\u6e56\u4e4b\u540e\uff0c\u8d26\u6237\u4e0a\u8fd8\u51c0\u6709\u4e24\u4ebf\u5143\u7684\u4f59\u989d\u3002\u867d\u7136\u4e00\u76f4\u90fd\u5f88\u514b\u5236\u7740\u82b1\u94b1\uff0c\u4f46\u662f\u5904\u4e8e\u6574\u4f53\u7684\u6c1b\u56f4\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u5c31\u5728\u6e38\u620f\u5e73\u53f0\u3001\u7f51\u7edc\u6e38\u620f\u5f00\u53d1\u3001\u6570\u5b57\u56fe\u4e66\u9986\u3001\u5e94\u7528\u8f6f\u4ef6\u7b49\u9886\u57df\u6295\u5165\u5927\u91cf\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u4ed6\u4eec\u8fd8\u597d\u4e00\u4e9b\uff0c\u77e5\u9053\u9526\u6e56\u7684\u5bb6\u5e95\uff0c\u4e1c\u5927\u521b\u57df\u518d\u600e\u4e48\u6298\u817e\uff0c\u90fd\u6380\u4e0d\u8d77\u5927\u7684\u6c34\u82b1\u6765\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u9526\u6e56\u65d7\u4e0b\u4f01\u4e1a\u90fd\u662f\u72ec\u7acb\u6838\u7b97\u7684\uff0c\u4e1c\u5927\u521b\u57df\u5c31\u7b97\u662f\u540e\u8d77\u4e4b\u79c0\uff0c\u6682\u65f6\u4e0d\u8ddf\u5176\u4ed6\u4f01\u4e1a\u6bd4\u4e1a\u7ee9\uff0c\u4f46\u662f\u6df1\u9677\u8fdb\u6ce1\u6cab\u5371\u673a\u65e0\u6cd5\u8131\u8eab\uff0c\u8138\u9762\u4e0a\u81ea\u7136\u4f1a\u5f88\u96be\u770b\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u4ed6\u4eba\u5462\uff1f\u201d\u8d75\u6893\u7433\u5e26\u7740\u5973\u513f\u8ddf\u7231\u4eba\u8d70\u4e0a\u697c\u6765\uff0c\u6ca1\u770b\u5230\u5f20\u606a\u4ed6\u4eba\uff0c\u5c31\u770b\u89c1\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u3001\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u7ed9\u76db\u590f\u3001\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u3001\u738b\u5f69\u73b2\u51e0\u4e2a\u5973\u5b69\u5b50\u6307\u6325\u7740\u56e2\u56e2\u8f6c\uff0c\u5728\u9732\u53f0\u4e0a\u51c6\u5907\u70e7\u70e4\u7684\u4e1c\u897f\uff0c\u8fd9\u4f1a\u513f\u90fd\u51c6\u5907\u4e86\u5dee\u4e0d\u591a\uff0c\u90fd\u5750\u5728\u90a3\u91cc\u6b47\u529b\uff0c\u7b49\u7740\u4eba\u5230\u9f50\u540e\u5c31\u6b63\u5f0f\u5f00\u59cb\u3002</p><p>\u3000\u3000\u697c\u4e0b\u7684\u5ead\u9662\u8fc7\u4e8e\u7cbe\u81f4\u800c\u7e41\u5197\uff0c\u6ca1\u6709\u8db3\u591f\u7684\u7a7a\u5730\u529e\u70e7\u70e4\u9910\u4f1a\uff0c\u5012\u662f\u4e8c\u697c\u7684\u5927\u9732\u53f0\u4e0e\u5ba2\u5385\u8fde\u5728\u4e00\u8d77\uff0c\u7a7a\u95f4\u8db3\u591f\u5927\u3002\u8d75\u6893\u7433\u7ad9\u5728\u9732\u53f0\u4e0a\uff0c\u9694\u7740\u73bb\u7483\u7a97\u770b\u89c1\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4e09\u4eba\u5728\u4e66\u623f\u91cc\uff0c\u90fd\u4e00\u8138\u7684\u51dd\u91cd\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u8ba8\u8bba\u4ec0\u4e48\u4e25\u8083\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u6ca1\u8bf4\u8981\u8fc7\u6765\u554a\uff0c\u4ed6\u8bf4\u8981\u8fc7\u6765\u5417\uff1f\u201d\u65f6\u5b66\u658c\u8be7\u5f02\u7684\u95ee\u9053\uff0c\u8e22\u4e86\u8e22\u8463\u8dc3\u534e\u7684\u811a\uff0c\u8bf4\u9053\uff0c\u201c\u8fd8\u4e0d\u7ed9\u54b1\u4eec\u7684\u7f8e\u5973\u8d22\u795e\u7237\u8ba9\u5ea7\uff1f\u201d</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u6295\u56e0\u6295\u8d44\u6606\u817e\u5728\u7ebf\u800c\u540d\u95fb\u5929\u4e0b\uff0c\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u9ad8\u4f01\u65f6\uff0c\u6a61\u6811\u56ed\u521b\u6295\u6301\u6709\u6606\u817e\u5728\u7ebf\u80a1\u7968\u5e02\u503c\u5c31\u9ad8\u8fbe\u516d\u4e03\u4ebf\u7f8e\u5143\u3002\u5373\u4f7f\u73b0\u5728\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u51e0\u4e4e\u906d\u5230\u8170\u65a9\uff0c\u6a61\u6811\u56ed\u521b\u6295\u6240\u6301\u6709\u7684\u90a3\u4e00\u90e8\u5206\u6606\u817e\u5728\u7ebf\u80a1\u7968\u5e02\u503c\u4e5f\u8981\u63a5\u8fd1\u56db\u4ebf\u7f8e\u5143\u3002\u6263\u9664\u6606\u817e\u5728\u7ebf\u7684\u80a1\u6743\u8d44\u4ea7\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u7ba1\u7406\u7684\u8d44\u4ea7\u4e5f\u8d85\u8fc7\u4e8c\u5341\u4ebf\u5143\uff0c\u8fde\u7eed\u51e0\u5e74\u8749\u8054\u56fd\u5185\u521b\u6295\u57fa\u91d1\u51a0\u519b\u7684\u5b9d\u5ea7\u3002\u5bf9\u4e8e\u90a3\u4e9b\u6e34\u671b\u521b\u4e1a\u83b7\u5f97\u4e8b\u4e1a\u6210\u529f\u7684\u9752\u5e74\u6765\u8bf4\uff0c\u7ba1\u7406\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u7684\u603b\u8d1f\u8d23\u4eba\u8d75\u6893\u7433\u5f53\u771f\u79f0\u5f97\u4e0a\u5973\u8d22\u795e\uff0c\u5efa\u90ba\u521b\u4e1a\u5708\u5b50\u5185\u7684\u4eba\u4e5f\u62ff\u8fd9\u4e2a\u540d\u8bcd\u620f\u79f0\u8d75\u6893\u7433\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u6295\u5728\u8fc7\u53bb\u8fd1\u4e09\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u4e13\u6ce8\u4e8e\u63a8\u52a8\u56fd\u5185\u8f6f\u786c\u4ef6\u6280\u672f\u7684\u53d1\u5c55\uff0c\u5f88\u5c11\u76f4\u63a5\u6d89\u53ca\u4e92\u8054\u7f51\u7684\u6295\u8d44\uff0c\u8fd9\u6b21\u4e92\u8054\u7f51\u4ea7\u4e1a\u6ce1\u6cab\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u672c\u4e0a\u4e5f\u5904\u4e8e\u5b89\u5168\u533a\u57df\u3002</p><p>\u3000\u3000\u8d75\u6893\u7433\u4f38\u624b\u4f5c\u52bf\u8981\u6247\u65f6\u5b66\u658c\u7684\u540e\u8111\u52fa\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u63a5\u5230\u4ed6\u7684\u7535\u8bdd\u624d\u8fc7\u6765\u8e6d\u996d\u5403\u7684\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u4eba\u65e9\u5230\u4e86\u5462\u3002\u201d</p><p>\u3000\u3000\u8d75\u6893\u7433\u4e5f\u4f4f\u5728\u71d5\u56ed\u91cc\uff0c\u8d70\u8fc7\u6765\u4e5f\u65b9\u4fbf\uff0c\u5979\u770b\u7740\u76db\u590f\u63d0\u7740\u996e\u6599\u74f6\u8d70\u8fc7\u6765\uff0c\u95ee\u9053\uff1a\u201c\u6709\u6ca1\u6709\u51b3\u5b9a\u597d\u6bd5\u4e1a\u540e\u7559\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u76db\u590f\u867d\u7136\u8ddf\u5f20\u606a\u3001\u675c\u98de\u540c\u9f84\uff0c\u5374\u8981\u6bd4\u4ed6\u4eec\u9ad8\u4e00\u5c4a\uff0c\u5979\u518d\u6709\u4e00\u4e2a\u6708\u5c31\u8981\u4ece\u9999\u6e2f\u5927\u5b66\u6bd5\u4e1a\u3002\u9999\u6e2f\u5927\u5b66\u52b3\u52a8\u8282\u516c\u5047\u8fc7\u540e\uff0c\u5979\u6ca1\u6709\u6025\u7740\u56de\u9999\u6e2f\u53bb\uff0c\u8fd9\u4e9b\u5929\u4e00\u76f4\u7559\u5728\u5efa\u90ba\u3002</p><p>\u3000\u3000\u201c\u8fd8\u6ca1\u6709\u51b3\u5b9a\u5462\uff0c\u201d\u76db\u590f\u7b11\u9053\uff0c\u201c\u9009\u62e9\u592a\u591a\u4e5f\u662f\u5934\u75bc\uff0c\u60f3\u7740\u5b9e\u5728\u4e0d\u884c\uff0c\u5c31\u53bb\u5e2e\u6211\u54e5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e16\u7eaa\u9526\u6e56\u5a31\u4e50\u53c8\u4e0d\u662f\u76db\u6c0f\u5bb6\u65cf\u4f01\u4e1a\uff0c\u4f60\u8fc7\u53bb\u51d1\u4ec0\u4e48\u70ed\u95f9\uff1f\u8fd8\u4e0d\u5982\u5230\u4e1c\u5927\u521b\u57df\u6765\u3002\u201d\u675c\u98de\u4ece\u4e66\u623f\u91cc\u8d70\u8fc7\u51fa\uff0c\u6276\u7740\u76db\u590f\u7684\u80a9\u8180\uff0c\u4e0e\u8d75\u6893\u7433\u53ca\u5979\u7231\u4eba\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6837\u4e1c\u5927\u521b\u57df\u4e0d\u5c31\u6210\u592b\u59bb\u5e97\u4e86\uff1f\u201d\u5f20\u606a\u5728\u697c\u4e0b\u5c31\u542c\u89c1\u675c\u98de\u8bf4\u76db\u590f\uff0c\u8d70\u4e0a\u697c\u6765\uff0c\u4ed6\u4eba\u7ad9\u5728\u5ba2\u5385\u91cc\u4f38\u624b\u671d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u627e\u6761\u6bdb\u5dfe\u7ed9\u6211\u64e6\u64e6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u675c\u98de\u3001\u8d75\u6893\u7433\u4ed6\u4eec\u770b\u7740\u5f20\u606a\u5939\u514b\u4e0b\u6446\u3001\u5927\u817f\u4e0a\u6e7f\u4e86\u4e00\u5927\u7247\uff0c\u7591\u60d1\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u55e8\uff0c\u534a\u751f\u7684\u6e05\u767d\u5dee\u70b9\u7ed9\u90b5\u5fc3\u6587\u4ed6\u513f\u5b50\u6bc1\u4e86\uff0c\u6211\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u6ca1\u6709\u7ed9\u5c0f\u5b69\u5b50\u5305\u5c3f\u7247\uff1f\u521a\u62b1\u4e0a\u624b\u5c31\u4e2d\u4e86\u5f69\uff0c\u5dee\u70b9\u7ed9\u8fd9\u5c0f\u5bb6\u4f19\u5c3f\u5934\u4e0a\u2026\u2026\u8981\u4e0d\u662f\u6015\u53f6\u5c0f\u6850\u8fd9\u5a46\u5a18\u8ddf\u6211\u62fc\u547d\uff0c\u65e9\u5c06\u90a3\u5c0f\u5bb6\u4f19\u4e22\u8f66\u5b50\u5916\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u697c\u4e0b\u4e5f\u5e94\u666f\u7684\u4f20\u6765\u5a74\u513f\u7684\u557c\u54ed\u58f0\uff0c\u5927\u5bb6\u90fd\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u4e00\u4f1a\u513f\uff0c\u53f6\u5c0f\u6850\u4e3e\u7740\u5979\u6ca1\u6ee1\u534a\u5468\u5c81\u7684\u513f\u5b50\u4e0a\u697c\u6765\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u5e74\u5934\u6562\u5f80\u606a\u5c11\u5934\u4e0a\u6492\u5c3f\u7684\u4eba\u4e0d\u591a\u89c1\u4e86\uff0c\u5927\u5bb6\u90fd\u8fc7\u6765\u77bb\u4ef0\u4e00\u4e0b\u6211\u5bb6\u7684\u53f6\u9676\u3002\u201d\u90b5\u5fc3\u6587\u5e78\u707e\u4e50\u7978\u7684\u7b11\u7740\u8ddf\u7740\u540e\u9762\u4e0a\u697c\u6765\u3002</p><p>\u3000\u3000\u5973\u5b69\u5b50\u4eec\u90fd\u559c\u6b22\u5c0f\u5b69\u5b50\uff0c\u90fd\u56f4\u4e86\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u82e6\u7b11\uff0c\u770b\u7740\u4e66\u623f\u91cc\u7535\u8111\u6db2\u6676\u663e\u793a\u5c4f\u4e0a\u8fd8\u663e\u793a\u7740\u80a1\u7968\u8d70\u52bf\u56fe\uff0c\u95ee\u675c\u98de\uff1a\u201c\u6211\u4eba\u6ca1\u6765\uff0c\u4f60\u4eec\u5728\u8ba8\u8bba\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e78\u798f\u662f\u4ec0\u4e48\uff0c\u65e0\u8c13\u662f\u770b\u5230\u522b\u4eba\u6bd4\u81ea\u5df1\u66f4\u5012\u9709\uff01\u201d\u9a6c\u5411\u4e1c\u5f00\u73a9\u7b11\u7684\u8bf4\u9053\uff0c\u201c\u8bc1\u76d1\u4f1a\u4eca\u5929\u706b\u901f\u6279\u51c6\u6d77\u7c9f\u79d1\u6280\u7684\u589e\u53d1\u65b9\u6848\uff0c\u6211\u4eec\u5728\u8ba8\u8bba\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u9009\u62e9\u7b2c\u4e00\u65f6\u95f4\u5c31\u5f3a\u884c\u589e\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u4eca\u5929\u6bd4\u6211\u90fd\u5e78\u798f\uff0c\u201d\u5f20\u606a\u644a\u5f00\u7ed9\u5c3f\u6e7f\u7684\u8863\u6446\u7ed9\u5927\u5bb6\u770b\uff0c\u8d70\u8fdb\u4e66\u623f\u91cc\uff0c\u70b9\u5f00\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u8d70\u52bf\u56fe\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u4e0d\u4f1a\u8fd9\u4e48\u5192\u9669\u3002\u4eca\u5929\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u8dcc\u5e45\u5f88\u6df1\uff0c\u4f46\u662f\u8dcc\u7834\u589e\u53d1\u9650\u5e95\u4ef7\u4e4b\u540e\u7ee7\u7eed\u63a2\u5e95\u65f6\uff0c\u4ea4\u6613\u975e\u5e38\u6d3b\u8dc3\uff0c\u4e34\u5c3e\u53c8\u6ca1\u6709\u6536\u5728\u589e\u53d1\u5e95\u4ef7\u4e4b\u4e0a\uff0c\u53ef\u89c1\u4eca\u5929\u8fdb\u573a\u7684\u5927\u591a\u662f\u6295\u673a\u8005\u3002\u5728\u5c40\u52bf\u6ca1\u6709\u9006\u8f6c\u4e4b\u524d\uff0c\u5b8f\u4fe1\u8981\u60f3\u5728\u80cc\u540e\u5f3a\u884c\u62c9\u62ac\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u7ad9\u5728\u589e\u53d1\u5e95\u4ef7\u4e4b\u4e0a\u4ee5\u4fbf\u589e\u53d1\u80fd\u987a\u5229\u8fdb\u884c\uff0c\u52bf\u5fc5\u8981\u5148\u5316\u89e3\u8fd9\u4e9b\u6295\u673a\u8005\u6240\u5e26\u6765\u7684\u538b\u529b\uff0c\u5dee\u4e0d\u591a\u8981\u4e00\u6b21\u52a8\u7528\u5341\u4ebf\u5143\u8d44\u91d1\u624d\u6709\u628a\u63e1\uff0c\u8fd8\u8981\u4fdd\u8bc1\u6d88\u606f\u9762\u662f\u4ed6\u4eec\u7684\u64cd\u4f5c\u662f\u57fa\u672c\u6709\u5229\u7684\uff0c\u54ea\u6709\u8fd9\u4e48\u5bb9\u6613\u7684\u4e8b\u60c5\uff1f\u53e6\u5916\uff0c\u6211\u521a\u63a5\u5230\u6d88\u606f\u542c\u8bf4\u661f\u6cb3\u8bc1\u5238\u4e5f\u6709\u6253\u9000\u5802\u9f13\u7684\u610f\u601d\u3002\u201d</p><p>\u3000\u3000\u201c\u661f\u6cb3\u8bc1\u5238\u4e0d\u662f\u8ddf\u4e25\u5bb6\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\u7684\u5417\uff1f\u201d\u9a6c\u5411\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\u4e0d\u5047\uff0c\u4f46\u662f\u51fa\u4e86\u95ee\u9898\u4e5f\u8981\u4ed6\u4eec\u80fd\u5168\u515c\u4e0b\u6765\u518d\u8bf4\u2026\u2026\u4e0d\u8fc7\u4e5f\u96be\u8bf4\uff0c\u4ed6\u4eec\u7684\u5403\u76f8\u5386\u6765\u90fd\u4e0d\u542b\u84c4\uff0c\u201d\u5f20\u606a\u53c8\u770b\u4e86\u51e0\u773c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u8d70\u52bf\u56fe\uff0c\u5c06\u9f20\u6807\u4e22\u5728\u684c\u4e0a\uff0c\u8ddf\u9a6c\u5411\u4e1c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u843d\u6c34\u72d7\u81ea\u7136\u8981\u6253\uff0c\u4e0d\u8fc7\u8fd8\u662f\u5148\u8ba8\u8bba\u4f60\u4eec\u4e0a\u5cb8\u7684\u4e8b\u60c5\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u6478\u7740\u540e\u8111\u52fa\u5e72\u7b11\u4e86\u4e24\u4e0b\uff0c\u6606\u817e\u5728\u7ebf\u6b64\u65f6\u4e5f\u6389\u5728\u6c34\u91cc\u5462\uff0c\u771f\u662f\u6ca1\u6709\u8d44\u683c\u770b\u522b\u4eba\u7684\u597d\u620f\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u592a\u6324\u4e86\uff0c\u70e7\u70e4\u662f\u4e0d\u662f\u53ef\u4ee5\u5f00\u59cb\u4e86\uff1f\u6211\u4eec\u8fde\u5403\u8fb9\u804a\u5427\u2026\u2026\u201d\u5f20\u606a\u63a2\u5934\u770b\u770b\u9732\u53f0\u4e0a\u90fd\u5df2\u7ecf\u51c6\u5907\u5dee\u4e0d\u591a\u4e86\uff0c\u53c8\u95ee\u675c\u98de\uff0c\u201c\u4f60\u8bf7\u7684\u5ba2\u4eba\u90fd\u5230\u9f50\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u4eca\u5929\u7684\u70e7\u70e4\u9910\u4f1a\u4e3b\u8981\u662f\u4e3a\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u5230\u5efa\u90ba\u6765\uff0c\u4e5f\u517c\u4fbf\u8ba8\u8bba\u5f53\u524d\u4e92\u8054\u7f51\u56f0\u5883\u7684\u5e94\u5bf9\u7b56\u7565\uff0c\u675c\u98de\u4e3b\u8981\u9080\u8bf7\u4e1c\u5927\u521b\u57df\u6709\u5173\u7684\u4e00\u4e9b\u4eba\u8fc7\u6765\uff0c\u8981\u662f\u5c06\u9526\u6e56\u5728\u5efa\u90ba\u7684\u4e2d\u9ad8\u5c42\u90fd\u9080\u8bf7\u8fc7\u6765\uff0c\u5728\u4ed6\u8fd9\u680b\u522b\u5885\u91cc\u529e\u70e7\u70e4\u9910\u4f1a\u5c31\u6709\u4e9b\u62e5\u6324\u4e86\uff0c\u4ed6\u4e5f\u6ca1\u6709\u9080\u8bf7\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u592b\u5987\uff0c\u4eba\u662f\u5f20\u606a\u7ed9\u62c9\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u5927\u5bb6\u6682\u65f6\u5c06\u5fe7\u8651\u629b\u4e0b\uff0c\u70ed\u70ed\u95f9\u95f9\u7684\u62e5\u5230\u9732\u53f0\u4e0a\uff0c\u8fd9\u65f6\u5019\u697c\u4e0b\u7684\u95e8\u94c3\u53c8\u54cd\u4e86\u8d77\u6765\uff0c\u675c\u98de\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u71d5\u56ed\u91cc\u4f4f\u7684\u719f\u4eba\u592a\u591a\u4e86\uff0c\u8c01\u770b\u5230\u8fd9\u8fb9\u70ed\u95f9\u8dd1\u8fc7\u6765\u51d1\u70ed\u95f9\u4e5f\u518d\u6b63\u5e38\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u4e3e\u9152\u6c34\u62ff\u51fa\u6765\u6253\u5f00\uff0c\u5c31\u770b\u89c1\u8c22\u5b50\u5609\u549a\u549a\u549a\u7684\u8dd1\u4e0a\u697c\u6765\uff0c\u9648\u9759\u8ddf\u5728\u540e\u9762\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u8fd9\u4e24\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u5927\u5bb6\u90fd\u89c9\u5f97\u5f88\u610f\u5916\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf7\u6765\u7684\uff1f\u201d\u675c\u98de\u7591\u60d1\u7684\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9648\u9759\u4e00\u773c\uff0c\u9648\u9759\u660e\u4eae\u7684\u7738\u5b50\u8f6c\u4e86\u4e00\u5708\u540e\u77a5\u5728\u8c22\u5b50\u5609\u8eab\u4e0a\uff0c\u793a\u610f\u662f\u5b50\u5609\u786c\u62c9\u5979\u8981\u95ef\u8fdb\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\uff0c\u4e0d\u6b22\u8fce\u5417\uff1f\u201d\u8c22\u5b50\u5609\u624b\u91cc\u62ff\u7740\u4e00\u74f6\u7ea2\u9152\uff0c\u6367\u5728\u80f8\u524d\uff0c\u602f\u602f\u7684\u95ee\u9053\uff0c\u201c\u770b\u7740\u8fd9\u8fb9\u8fd9\u4e48\u70ed\u95f9\uff0c\u5c31\u89c9\u5f97\u6211\u4eec\u90a3\u91cc\u592a\u51b7\u6e05\u4e86\u2026\u2026\u201d\u4e24\u53ea\u4eae\u6676\u6676\u7684\u5927\u773c\u775b\u6251\u95ea\u6251\u95ea\u7684\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u5927\u5bb6\u8fd9\u526f\u8be7\u5f02\u7684\u6837\u5b50\u8ba9\u5979\u5df2\u7ecf\u53d7\u5230\u5f88\u5927\u7684\u4f24\u5bb3\u4e86\u3002</p><p>\u3000\u3000\u8981\u662f\u4e0d\u77e5\u9053\u8c22\u5b50\u5609\u7684\u5e95\u7ec6\uff0c\u65f6\u5b66\u658c\u8bf4\u4e0d\u5b9a\u5c31\u8981\u51fa\u53e3\u8c03\u620f\u6837\u8c8c\u770b\u4e0a\u53bb\u50cf\u7f8e\u5c11\u5973\u521d\u4e2d\u751f\u7684\u5979\u4e86\uff0c\u8fd9\u65f6\u5019\u53ea\u662f\u4e0d\u81ea\u7136\u7684\u5c06\u89c6\u7ebf\u8f6c\u5230\u522b\u5904\u53bb\u3002\u522b\u770b\u4e1c\u5927\u521b\u57df\u58f0\u8a89\u6b63\u9686\uff0c\u9664\u4e86\u5f20\u606a\u4e4b\u5916\uff0c\u4e1c\u5927\u771f\u6b63\u8ba9\u4ed6\u89c9\u5f97\u8be1\u8273\u7684\u4eba\u7269\u8fd8\u662f\u5e73\u65e5\u91cc\u5728\u5b66\u6821\u88c5\u90bb\u5bb6\u5973\u5b69\u5b50\u7684\u8c22\u5b50\u5609\u2014\u2014\u4e1c\u5927\u56fd\u5546\u9662\u5f88\u591a\u5b66\u751f\u90fd\u77e5\u9053\u5979\u662f\u80e1\u6da6\u767e\u5bcc\u699c\u4e1c\u6d77\u9996\u5bcc\u8c22\u6c49\u9756\u7684\u5973\u513f\uff0c\u5979\u7684\u54e5\u54e5\u8c22\u610f\u662f\u56fd\u5185\u7b2c\u56db\u5927\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u6d77\u6cf0\u7535\u5668\u7684\u603b\u88c1\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u662f\u79d1\u738b\u9ad8\u79d1\u7684\u521b\u59cb\u4eba\u4e4b\u4e00\uff0c\u800c\u4e14\u5979\u4ee4\u4eba\u60ca\u8273\u7684\u5546\u4e1a\u624d\u534e\u4f7f\u79d1\u738b\u9ad8\u79d1\u4e5d\u4e5d\u5e74\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u7684\u51c0\u5229\u6da6\u8fbe\u5230\u4e94\u4e2a\u4ebf\u2014\u2014\u65f6\u5b66\u658c\u5e73\u65f6\u53ef\u662f\u8ddf\u675c\u98de\u4e00\u8d77\u88ab\u8a89\u4e3a\u5927\u5b66\u751f\u521b\u4e1a\u7684\u9886\u8896\u4eba\u7269\uff0c\u611f\u89c9\u5728\u770b\u4e0a\u53bb\u6e05\u7eaf\u53ef\u7231\u7684\u8c22\u5b50\u5609\u9762\u524d\u8ddf\u6e23\u4f3c\u7684\uff0c\u7279\u522b\u662f\u4ece\u5f20\u606a\u3001\u675c\u98de\u5634\u91cc\u77e5\u9053\u4e86\u8c22\u5b50\u5609\u7684\u4e00\u4e9b\u4f20\u95fb\uff0c\u624d\u77e5\u9053\u8868\u9762\u4e0a\u5f97\u5230\u7684\u6e05\u7eaf\u53ef\u7231\u7684\u5370\u8c61\u5b9e\u9645\u4e0a\u8ddf\u8c22\u5b50\u5609\u6ca1\u6709\u4e00\u4e01\u70b9\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u8981\u8bf4\u8c22\u5b50\u5609\u56e0\u4e3a\u5bc2\u5bde\u3001\u56e0\u4e3a\u51b7\u6e05\u624d\u95ef\u8fdb\u6765\uff0c\u65f6\u5b66\u658c\u8111\u5b50\u91cc\u5f97\u8981\u88c5\u4e24\u6876\u6d46\u7cca\u624d\u4f1a\u76f8\u4fe1\u5979\u7684\u9b3c\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u5373\u4f7f\u60f3\u5c06\u8c22\u5b50\u5609\u8d76\u51fa\u53bb\uff0c\u4e5f\u6ca1\u6709\u80c6\u5c06\u9648\u9759\u8d76\u51fa\u53bb\uff0c\u4ece\u8c22\u5b50\u5609\u624b\u91cc\u5c06\u7ea2\u9152\u63a5\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4eba\u7559\u4e0b\u53ef\u4ee5\uff0c\u8fd9\u74f6\u9152\u561b\u2026\u2026\u5305\u88c5\u597d\u50cf\u662f\u5b8c\u597d\u7684\uff0c\u559d\u559d\u5e94\u8be5\u6ca1\u6709\u5173\u7cfb\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u6015\u6211\u8bf7\u9152\u5382\u7279\u5236\u4e24\u74f6\u52a0\u6599\u7684\u7ea2\u9152\u7559\u5728\u5bb6\u91cc\u6709\u5907\u65e0\u60a3\uff1f\u201d\u8c22\u5b50\u5609\u659c\u7738\u770b\u7740\u5f20\u606a\uff0c\u7738\u5b50\u4e00\u8f6c\uff0c\u770b\u5411\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\uff0c\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u771f\u662f\u597d\u95f2\u60c5\u9038\u81f4\u554a\uff0c\u770b\u4e86\u4eca\u5929\u8d22\u7ecf\u62a5\u9053\u7684\u4eba\u90fd\u4f1a\u4ee5\u4e3a\u4ed6\u4eec\u95ed\u95e8\u5173\u5728\u529e\u516c\u5ba4\u91cc\u7ede\u5c3d\u8111\u6c57\u60f3\u5bf9\u7b56\u5462\uff0c\u54ea\u91cc\u4f1a\u60f3\u5230\u4ed6\u4eec\u79bb\u5f00\u5317\u4eac\u5230\u5efa\u90ba\u6765\u900d\u9065\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9648\u9759\u4e00\u773c\uff0c\u9648\u9759\u7f8e\u7738\u91cc\u85cf\u7740\u7b11\u610f\uff0c\u793a\u610f\u8fd9\u4fbf\u662f\u8c22\u5b50\u5609\u95ef\u4e0a\u95e8\u6765\u7684\u7528\u610f\uff1b\u63a5\u7740\u5979\u53c8\u8f6c\u8eab\u53bb\u770b\u53f6\u5c0f\u6850\u62b1\u5728\u6000\u91cc\u7684\u5c0f\u5b69\u5b50\uff0c\u634f\u7740\u5c0f\u5b69\u5b50\u6c34\u7075\u7075\u7684\u8138\u86cb\uff0c\u7b11\u7740\u95ee\u53f6\u5c0f\u6850\uff1a\u201c\u505a\u5988\u5988\u5f88\u8f9b\u82e6\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u591c\u91cc\u90fd\u4e22\u7ed9\u4fdd\u59c6\u5e26\uff0c\u6211\u53ef\u6ca1\u6709\u597d\u5fc3\u60c5\u4f3a\u5019\u4ed6\uff0c\u6211\u73b0\u5728\u62b1\u7740\u4e5f\u5c31\u56fe\u65b0\u9c9c\u3002\u201d\u53f6\u5c0f\u6850\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u5c0f\u5b69\u5b50\u6bd4\u8f83\u70e6\u4eba\uff0c\u8981\u4e0d\u4f60\u751f\u4e00\u4e2a\u8bd5\u8bd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u627e\u8c01\u751f\u53bb\u554a\uff1f\u201d\u9648\u9759\u5ae3\u7136\u800c\u7b11\uff0c\u4ece\u53f6\u5c0f\u6850\u6000\u91cc\u5c06\u5c0f\u5b69\u63a5\u8fc7\u6765\u9017\u7740\u73a9\u3002</p><p>\u3000\u3000\u5f20\u606a\u6320\u4e86\u6320\u8111\u888b\uff0c\u8ddf\u8c22\u5b50\u5609\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u54ea\u91cc\u662f\u6765\u900d\u9065\u7684\uff1f\u6211\u521a\u624d\u8fd8\u8bf4\u4ed6\u4eec\u5728\u6c34\u91cc\u6323\u624e\u7740\u6ca1\u4e0a\u5cb8\u5462\u3002\u5f97\uff0c\u5f97\uff0c\u4f60\u8981\u662f\u80fd\u7ed9\u4ed6\u4eec\u6307\u70b9\u4e00\u6761\u660e\u8def\uff0c\u8ba9\u4f60\u8ddf\u9648\u9759\u53c2\u52a0\u4eca\u5929\u7684\u70e7\u70e4\u9910\u4f1a\u4e5f\u7b97\u662f\u7269\u6709\u6240\u503c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u80fd\u6709\u4ec0\u4e48\u597d\u7684\u5efa\u8bae\uff0c\u201d\u8c22\u5b50\u5609\u624b\u80cc\u5728\u8eab\u540e\uff0c\u811a\u5c16\u637b\u7740\u5730\u677f\uff0c\u773c\u775b\u76ef\u7740\u811a\u5c16\uff0c\u770b\u4e0a\u53bb\u50cf\u662f\u817c\u8146\u7684\u5c0f\u5973\u751f\uff0c\u5634\u91cc\u8bf4\u9053\uff0c\u201c\u6606\u817e\u5728\u7ebf\u3001\u4e1c\u5927\u521b\u57df\u4ee5\u53ca\u5353\u57df\u7684\u4eba\u90fd\u7ad9\u5728\u8fd9\u91cc\uff0c\u96be\u9053\u4e0d\u662f\u60f3\u7740\u5c3d\u53ef\u80fd\u5c06\u5df2\u6709\u7684\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\u5728\u4e00\u8d77\uff0c\u627e\u51fa\u4e00\u6761\u6269\u5927\u7ade\u4e89\u4f18\u52bf\u3001\u5c3d\u5feb\u5b9e\u73b0\u76c8\u5229\u7684\u6377\u5f84\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u8c22\u5b50\u5609\u8be5\u4e0d\u4f1a\u5b88\u5728\u7a97\u5b50\u53e3\u770b\u7740\u5927\u5bb6\u9646\u7eed\u7684\u8d70\u8fdb\u8fd9\u91cc\u5427\uff1f\u5979\u5012\u662f\u80fd\u4ece\u53c2\u52a0\u70e7\u70e4\u9910\u4f1a\u7684\u4eba\u8eab\u4e0a\u770b\u51fa\u4eca\u5929\u76f8\u805a\u7684\u4e3b\u8981\u76ee\u7684\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u90fd\u4e0d\u8ba4\u8bc6\u8c22\u5b50\u5609\uff0c\u675c\u98de\u7ed9\u4ed6\u4eec\u4ecb\u7ecd\u8c22\u5b50\u5609\u7684\u201c\u5149\u8f89\u4e8b\u8ff9\u201d\uff0c\u8fd9\u4e48\u4e2a\u4eba\u7269\uff0c\u8ba9\u7ad9\u5728\u4e00\u65c1\u7684\u738b\u5f69\u73b2\u542c\u4e86\u5012\u5438\u51c9\u6c14\uff0c\u8ddf\u6881\u6587\u6c5f\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u6ca1\u6709\u8ddf\u4f60\u8868\u5f1f\u540c\u7ea7\u522b\u7684\u5996\u5b7d\u4eba\u7269\u4e86\uff0c\u4ed6\u4eec\u8fd8\u8ba9\u4e0d\u8ba9\u522b\u4eba\u6d3b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u522b\u4eba\u6700\u591a\u7b97\u662f\u7cbe\u82f1\u4e2d\u7684\u7cbe\u82f1\uff0c\u4ed6\u4eec\u5374\u662f\u5929\u624d\u2026\u2026\u8fd8\u662f\u6709\u4e9b\u533a\u522b\u7684\u3002\u201d\u9a6c\u5411\u4e1c\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u770b\u7740\u9a6c\u5411\u4e1c\u4e0e\u6881\u6587\u6c5f\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u5f20\u606a\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8c08\u5427\uff0c\u6211\u809a\u5b50\u997f\u4e86\uff0c\u5148\u8981\u586b\u4e9b\u4e1c\u897f\u2026\u2026\u201d\u8f6c\u8fc7\u8eab\u95ee\u9648\u9759\uff0c\u201c\u4f60\u559d\u4ec0\u4e48\u9152\uff0c\u8fd9\u91cc\u5e94\u8be5\u90fd\u6709\u51c6\u5907\uff1f\u201d</p><p>\u3000\u3000\u4e0d\u8bf4\u4e24\u4eba\u5077\u60c5\u7684\u5173\u7cfb\uff0c\u9648\u9759\u7684\u8eab\u4efd\u6bd5\u7adf\u7279\u6b8a\uff0c\u5f20\u606a\u4eb2\u81ea\u62db\u5f85\uff0c\u522b\u4eba\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\u6765\uff0c\u53ea\u662f\u53f6\u5c0f\u6850\u6ca1\u6709\u5fc3\u601d\u8ddf\u675c\u98de\u4ed6\u4eec\u51d1\u5230\u4e00\u8d77\u53bb\uff0c\u62c9\u7740\u62b1\u7740\u513f\u5b50\u7684\u90b5\u5fc3\u6587\uff0c\u8ddf\u5f20\u606a\u3001\u9648\u9759\u4ed6\u4eec\u51d1\u5728\u4e00\u8d77\u804a\u5929\u3002</p><p>\u3000\u3000\u201c\u4e1c\u5927\u521b\u57df\u524d\u6bb5\u65f6\u95f4\u4e13\u6ce8\u4e8e\u6295\u8d44\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u5b50\u5609\u5fc3\u6027\u6d6e\u52a8\uff0c\u8fd9\u6bb5\u65f6\u5019\u4e5f\u6709\u90e8\u5206\u6ce8\u610f\u529b\u8f6c\u79fb\u5230\u8fd9\u4e0a\u9762\u3002\u4e92\u8054\u7f51\u80a1\u707e\u5df2\u6210\u5b9a\u5c40\uff0c\u5c06\u4f7f\u4f17\u591a\u98ce\u9669\u6295\u8d44\u5546\u6210\u4e3a\u60ca\u5f13\u4e4b\u9e1f\uff0c\u4e0d\u8fc7\u4e92\u8054\u7f51\u4ea7\u4e1a\u8fd8\u662f\u4f1a\u4fdd\u6301\u9ad8\u901f\u589e\u957f\uff0c\u5979\u8bf4\u8fd9\u65f6\u5019\u6b63\u662f\u2018\u4eba\u5f03\u6211\u53d6\u2019\u7684\u65f6\u673a\uff0c\u5012\u4e0d\u77e5\u9053\u5979\u600e\u4e48\u5c31\u80af\u5b9a\u4f60\u4eec\u4eca\u5929\u805a\u5728\u8fd9\u91cc\u5c31\u662f\u5546\u8bae\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\u7684\u4e8b\u60c5\u2026\u2026\u201d\u9648\u9759\u7b11\u7740\u8ddf\u5f20\u606a\u4ecb\u7ecd\u5979\u7ed9\u5b50\u5609\u62c9\u8fc7\u6765\u7684\u7f18\u7531\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u7740\u8c22\u5b50\u5609\u4e0e\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u4ed6\u4eec\u804a\u5f97\u6b63\u6295\u5165\uff0c\u6447\u5934\u800c\u7b11\u3002\u8c22\u5bb6\u5973\u9b54\u5934\u5728\u5546\u4e1a\u4e0a\u7684\u5929\u8d4b\u654f\u9510\u662f\u5f88\u7f55\u89c1\u7684\uff0c\u6bd4\u8f83\u8d77\u4f20\u7edf\u884c\u4e1a\u5dee\u4e0d\u591a\u90fd\u5df2\u56fa\u5b9a\u7684\u8fd0\u8425\u6a21\u5f0f\uff0c\u65b0\u5174\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u80fd\u8ba9\u4e2a\u4eba\u7684\u5546\u4e1a\u624d\u534e\u8ff8\u53d1\u51fa\u66f4\u8000\u773c\u7684\u5149\u8292\uff0c\u773c\u4e0b\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u8c8c\u4f3c\u906d\u53d7\u6709\u53f2\u4ee5\u6765\u6700\u4e25\u91cd\u7684\u5371\u673a\uff0c\u5374\u4e5f\u8574\u85cf\u7740\u66f4\u5927\u3001\u66f4\u591a\u7684\u5546\u4e1a\u673a\u4f1a\u3002\u8c22\u5b50\u5609\u5fc3\u6027\u6d6e\u52a8\uff0c\u6216\u8bb8\u662f\u4e2a\u7f3a\u70b9\uff0c\u4f46\u4e5f\u662f\u5979\u6700\u5927\u7684\u4f18\u52bf\u6240\u5728\uff0c\u5979\u8feb\u4e0d\u53ca\u5f85\u7684\u95ef\u8fdb\u6765\uff0c\u5927\u6982\u662f\u6709\u8bb8\u591a\u5947\u601d\u5999\u60f3\u7684\u5546\u4e1a\u6784\u601d\u8981\u627e\u4eba\u8bc9\u8bf4\u5427\u3002</p></article>"}}}]);
