(window.webpackJsonp=window.webpackJsonp||[]).push([[1641],{1664:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u518d\u56de\u9996",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u518d\u56de\u9996</h2><p>\u201c\u4ee5\u524d\uff1f\u201d</p><p>\u201c\u5c31\u662f\u6211\u4eec\u8ba4\u8bc6\u7684\u90a3\u6b21\u561b\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u4e5f\u662f\u8fd9\u6837\u4e00\u4e2a\u665a\u4e0a\uff0c\u9759\u6084\u6084\u7684\uff0c\u4f60\u966a\u7740\u6211\u5750\u5728\u82b1\u56ed\u91cc\u3002\u521a\u624d\u604d\u60da\u4e4b\u95f4\uff0c\u6211\u6709\u79cd\u9519\u89c9\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u53c8\u56de\u5230\u4e86\u90a3\u65f6\u5019\u4f3c\u7684\uff0c\u201d\u5979\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u201c\u5982\u679c\u2026\u2026\u5982\u679c\u65f6\u5149\u771f\u7684\u80fd\u591f\u5012\u6d41\uff0c\u90a3\u8be5\u6709\u591a\u597d\u554a\u3002\u6709\u4e9b\u9009\u62e9\u5c31\u53ef\u4ee5\u91cd\u65b0\u505a\uff0c\u4e0d\u4f1a\u518d\u505a\u9519\uff0c\u6211\u4eec\u4e5f\u5c31\u80fd\u2026\u2026\u6211\u60f3\u5e94\u8be5\u5c31\u80fd\u6c38\u8fdc\u5728\u4e00\u8d77\u5427\u3002\u201d</p><p>\u201c\u7b28\u86cb\uff01\u201d\u743c\u6069\u677f\u8d77\u8138\uff0c\u201c\u6211\u4eec\u73b0\u5728\u96be\u9053\u4e0d\u5c31\u662f\u5728\u4e00\u8d77\u5417\uff1f\u8fd8\u8981\u4ec0\u4e48\u91cd\u65b0\u9009\u62e9\u3002\u201d</p><p>\u201c\u80fd\u591f\u91cd\u65b0\u505a\u9009\u62e9\uff0c\u6216\u8bb8\u5c31\u4f1a\u505a\u5f97\u6bd4\u73b0\u5728\u66f4\u597d\u561b\uff0c\u201d\u867d\u7136\u88ab\u743c\u6069\u8d23\u5907\uff0c\u8299\u857e\u72c4\u4f9d\u65e7\u7b11\u76c8\u76c8\u7684\uff0c\u201c\u522b\u751f\u6c14\u5566\uff0c\u743c\u6069\uff0c\u6211\u5e76\u4e0d\u662f\u540e\u6094\uff0c\u4e5f\u4e0d\u662f\u57cb\u6028\u3002\u6211\u53ea\u662f\u5e0c\u671b\u6211\u4eec\u4e4b\u95f4\u80fd\u6709\u66f4\u52a0\u7f8e\u597d\u7684\u56de\u5fc6\uff0c\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u60f3\u8d77\u6765\uff0c\u65e0\u8bba\u60f3\u8d77\u54ea\u4e00\u6bb5\uff0c\u90fd\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u9057\u61be\u3002\u6211\u5fc3\u91cc\u60f3\u7684\uff0c\u53ea\u662f\u8fd9\u6837\u800c\u5df2\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u771f\u662f\u4e2a\u7b28\u86cb\uff01\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u8bf4\u554a\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u5c31\u662f\u4e2a\u7b28\u86cb\uff01\u201d\u743c\u6069\u4fa7\u8fc7\u8138\uff0c\u4e0d\u6562\u78b0\u89e6\u5979\u7684\u89c6\u7ebf\uff0c\u201c\u5c3d\u8bf4\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u8bdd\uff0c\u6709\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u60f3\u6cd5\uff0c\u8ba9\u4eba\u90fd\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u56de\u7b54\u4f60\u2026\u2026\u603b\u4e4b\u4f60\u5c31\u662f\u4e2a\u7b28\u86cb\u6ca1\u9519\u5566\u3002\u201d</p><p>\u201c\u90a3\u4f60\u559c\u6b22\u4e0d\u559c\u6b22\u7b28\u7b28\u7684\u5973\u5b69\u5b50\u554a\u3002\u201d</p><p>\u201c\u5207\uff0c\u6211\u624d\u4e0d\u559c\u6b22\u7b28\u86cb\u5462\uff0c\u6211\u559c\u6b22\u806a\u660e\u4eba\u2026\u2026\u4e0d\u8fc7\u5982\u679c\u662f\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u90a3\u5012\u53ef\u4ee5\u8003\u8651\u8003\u8651\u3002\u201d</p><p>\u201c\u90a3\u6211\u6f02\u4eae\u4e0d\u6f02\u4eae\u554a\u3002\u201d</p><p>\u743c\u6069\u634f\u634f\u5979\u7684\u8138\u988a\uff0c\u201c\u5f53\u7136\u6f02\u4eae\uff0c\u6f02\u4eae\u6781\u4e86\uff0c\u201d\u4ed6\u8f7b\u58f0\u5728\u5979\u8033\u8fb9\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u559c\u6b22\u4f60\u3002\u201d</p><p>\u201c\u6709\u591a\u559c\u6b22\uff1f\u201d\u5979\u8ffd\u95ee\u3002</p><p>\u201c\u6709\u591a\u559c\u6b22\u2026\u2026\u8fd9\u4e2a\u8fd8\u771f\u4e0d\u597d\u5f62\u5bb9\uff0c\u53cd\u6b63\u5f88\u559c\u6b22\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u5982\u679c\u51e0\u5929\u4e0d\u89c1\uff0c\u4f1a\u4e0d\u4f1a\u60f3\u6211\u554a\u3002\u201d</p><p>\u201c\u5f53\u7136\u4f1a\u60f3\u3002\u201d\u743c\u6069\u7acb\u523b\u56de\u7b54\u3002</p><p>\u201c\u55ef\uff0c\u6211\u4e5f\u5f88\u60f3\u4f60\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u60f3\u5f97\u5389\u5bb3\uff0c\u665a\u4e0a\u7761\u4e0d\u7740\u89c9\u2026\u2026\u201d</p><p>\u5979\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u4f4e\uff0c\u540e\u534a\u622a\u8bdd\u5df2\u7ecf\u7ec6\u82e5\u6e38\u4e1d\uff0c\u6084\u4e0d\u53ef\u95fb\u3002\u743c\u6069\u6709\u4e9b\u5947\u602a\u5730\u770b\u7740\u5979\uff0c\u89c1\u5979\u5bb3\u7f9e\u5730\u4f4e\u7740\u5934\uff0c\u767d\u7389\u822c\u7684\u8138\u988a\u4e0a\u7ea2\u6655\u5982\u971e\uff0c\u5a07\u8273\u6b32\u6ef4\uff0c\u4e24\u53ea\u624b\u5728\u817f\u95f4\u4ea4\u9519\u7ede\u626d\u7740\uff0c\u4eff\u4f5b\u4e0d\u77e5\u9053\u8be5\u5f80\u54ea\u91cc\u653e\u3002\u4ed6\u5148\u662f\u6014\u4e86\u6014\uff0c\u968f\u5373\u4fbf\u660e\u767d\u8fc7\u6765\uff0c\u4f38\u624b\u5c06\u5c11\u5973\u63fd\u5165\u6000\u4e2d\uff0c\u8ba9\u5979\u5750\u5728\u81ea\u5df1\u817f\u4e0a\u3002</p><p>\u5341\u516d\u5c81\u7684\u5c11\u5973\uff0c\u8eab\u4f53\u5df2\u7ecf\u53d1\u80b2\u5f97\u975e\u5e38\u826f\u597d\uff0c\u6d0b\u6ea2\u7740\u52c3\u52c3\u7684\u9752\u6625\u6d3b\u529b\u3002\u5979\u7684\u81c0\u90e8\u4e30\u8174\u67d4\u8f6f\uff0c\u540c\u65f6\u53c8\u5f39\u6027\u5341\u8db3\uff0c\u9694\u7740\u88e4\u5b50\u7684\u5e03\u6599\u90fd\u80fd\u611f\u89c9\u5f97\u6e05\u6e05\u695a\u695a\u3002\u743c\u6069\u5de6\u624b\u6402\u7740\u5979\u7684\u7ea4\u8170\uff0c\u53f3\u624b\u5df2\u7ecf\u6084\u6084\u94bb\u8fdb\u88d9\u5e95\uff0c\u5728\u5979\u7684\u7f8e\u81c0\u4e0a\u6469\u6332\u6e38\u79fb\uff0c\u8f7b\u8f7b\u63c9\u634f\u3002\u201c\u5f88\u60f3\u6211\u554a\uff1f\u201d\u4ed6\u5728\u5973\u5b69\u8033\u8fb9\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d\u5973\u5b69\u7684\u58f0\u97f3\u6709\u4e9b\u53d1\u98a4\u3002</p><p>\u201c\u60f3\u5f97\u665a\u4e0a\u90fd\u7761\u4e0d\u7740\u89c9\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u53c8\u9700\u8981\u2018\u6cbb\u7597\u2019\u4e86\uff1f\u201d\u743c\u6069\u542b\u7740\u5973\u5b69\u67d4\u8f6f\u7684\u8033\u73e0\uff0c\u54c1\u5c1d\u7740\uff0c\u64a9\u62e8\u6311\u9017\u5979\u7684\u60c5\u6b32\uff0c\u201c\u50cf\u4ee5\u524d\u4e00\u6837\uff1f\u201d</p><p>\u5c11\u5973\u4f4e\u7740\u5934\uff0c\u8eab\u4f53\u5728\u7537\u4eba\u6000\u4e2d\u60c5\u4e0d\u81ea\u7981\u5730\u8f7b\u8f7b\u98a4\u6296\u7740\uff0c\u4e0d\u6562\u505a\u58f0\u3002\u743c\u6069\u6240\u8bf4\u7684\u201c\u6cbb\u7597\u201d\uff0c\u7b97\u662f\u4ed6\u4eec\u4e4b\u95f4\u7684\u4e00\u79cd\u6697\u8bed\u3002\u4ee5\u524d\u5728\u5b66\u6821\u7684\u65f6\u5019\uff0c\u8299\u857e\u72c4\u6709\u5931\u7720\u75c7\uff0c\u665a\u4e0a\u603b\u662f\u65e0\u6cd5\u5165\u7761\uff0c\u548c\u743c\u6069\u5728\u4e00\u8d77\u4e4b\u540e\uff0c\u7ecf\u8fc7\u4ed6\u7684\u201c\u6cbb\u7597\u201d\u5c31\u518d\u6ca1\u6709\u8fd9\u79cd\u95ee\u9898\u3002\u81f3\u4e8e\u6cbb\u7597\u7684\u5177\u4f53\u65b9\u6cd5\u2026\u2026\u7b80\u5355\u6765\u8bf4\uff0c\u7ecf\u8fc7\u67d0\u79cd\u5267\u70c8\u7684\u6fc0\u60c5\u8fd0\u52a8\u540e\uff0c\u76f8\u4fe1\u518d\u987d\u56fa\u7684\u5931\u7720\u75c7\u90fd\u65e0\u6cd5\u963b\u6321\u7761\u9b54\u7684\u4fb5\u88ad\u4e86\u3002</p><p>\u201c\u662f\u4e0d\u662f\u5462\uff1f\u201d\u743c\u6069\u770b\u7740\u5979\u7f9e\u602f\u7684\u6837\u5b50\uff0c\u4e0d\u7531\u5f97\u8d77\u4e86\u6349\u5f04\u7684\u5fc3\u601d\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u8bf4\u7684\u8bdd\uff0c\u90a3\u6211\u5c31\u5f53\u662f\u81ea\u5df1\u731c\u9519\u4e86\u54e6\u3002\u201d</p><p>\u201c\u2026\u2026\u662f\u3002\u201d</p><p>\u52c9\u5f3a\u53d1\u51fa\u7ec6\u82e5\u6e38\u4e1d\u7684\u58f0\u97f3\uff0c\u8299\u857e\u72c4\u611f\u89c9\u8138\u86cb\u706b\u8fa3\u8fa3\u5730\u4e00\u9635\u9635\u53d1\u70e7\uff0c\u51e0\u4e4e\u8981\u65e0\u5730\u81ea\u5bb9\uff0c\u4f5c\u4e3a\u5973\u5b69\u5b50\uff0c\u4e3b\u52a8\u793a\u610f\u6c42\u6b22\u5df2\u7ecf\u8db3\u591f\u7f9e\u4eba\uff0c\u4f55\u51b5\u8fd8\u8981\u8bf4\u51fa\u53e3\u3002\u7136\u800c\u743c\u6069\u5374\u6ca1\u6709\u5c31\u6b64\u653e\u8fc7\u5979\uff0c\u201c\u539f\u6765\u5982\u6b64\u554a\uff0c\u201d\u4ed6\u53d6\u7b11\uff0c\u201c\u96be\u602a\u4e00\u770b\u5230\u6211\u5c31\u8fd9\u4e48\u5f00\u5fc3\u2026\u2026\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\u662f\u5566\uff01\u201d\u8299\u857e\u72c4\u7f9e\u4e0d\u53ef\u6291\uff0c\u6025\u6025\u8fa9\u89e3\uff0c\u201c\u6211\u662f\u771f\u7684\u770b\u5230\u4f60\u5fc3\u91cc\u5c31\u7279\u522b\u9ad8\u5174\uff0c\u548c\u90a3\u4e2a\u2026\u2026\u548c\u90a3\u4e2a\u6839\u672c\u6ca1\u5173\u7cfb\u7684\u3002\u201d</p><p>\u201c\u548c\u54ea\u4e2a\u6839\u672c\u6ca1\u5173\u7cfb\u5462\uff1f\u201d\u743c\u6069\u9017\u5979\u3002</p><p>\u201c\u90a3\u4e2a\u2026\u2026\u8ba8\u538c\uff0c\u90a3\u4e2a\u5c31\u662f\u90a3\u4e2a\u5566\uff0c\u4f60\u660e\u660e\u77e5\u9053\u7684\u2026\u2026\u201d</p><p>\u201c\u55ef\u55ef\uff0c\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u67d4\u58f0\u54c4\u7740\u5979\uff0c\u201c\u4f60\u770b\u5230\u6211\u5c31\u5fc3\u91cc\u5f88\u9ad8\u5174\uff0c\u548c\u2018\u90a3\u4e2a\u2019\u6ca1\u6709\u5173\u7cfb\u2026\u2026\u4f46\u662f\u4f60\u4e5f\u5f88\u60f3\u548c\u6211\u2018\u90a3\u4e2a\u2019\uff0c\u8fd9\u4e2a\u4e5f\u6ca1\u9519\u5427\uff1f\u201d</p><p>\u5c11\u5973\u7d27\u7d27\u54ac\u7740\u5634\u5507\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u201c\u4e0d\u80af\u8bf4\uff1f\u90a3\u6211\u6362\u4e2a\u8bf4\u6cd5\u2026\u2026\u8299\u857e\u72c4\uff0c\u4f60\u770b\u5230\u6211\uff0c\u5fc3\u91cc\u5f88\u9ad8\u5174\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u8eab\u4f53\u662f\u4e0d\u662f\u4e5f\u5f88\u9ad8\u5174\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u6ca1\u6709\u5566\uff0c\u201d\u5979\u6342\u7740\u8138\uff0c\u201c\u8ba8\u538c\uff0c\u88ab\u4f60\u8bf4\u5f97\u597d\u50cf\u6211\u662f\u4e2a\u6deb\u8361\u7684\u5973\u5b69\u5b50\u4f3c\u7684\u2026\u2026\u201d</p><p>\u201c\u554a\uff1f\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\u5730\u53cd\u95ee\uff0c\u201c\u96be\u9053\u4e0d\u662f\u4e48\uff1f\u201d</p><p>\u201c\u54ea\u6709\uff01\u201d</p><p>\u5979\u6325\u8d77\u7c89\u62f3\uff0c\u4f5c\u52bf\u8981\u6253\u743c\u6069\uff0c\u5374\u88ab\u4ed6\u7d27\u7d27\u62b1\u4f4f\uff0c\u201c\u597d\u4e86\uff0c\u4e0d\u9017\u4f60\u4e86\uff0c\u5c0f\u4e2b\u5934\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u6700\u8fd1\u9ebb\u70e6\u6bd4\u8f83\u591a\uff0c\u51b7\u843d\u4f60\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u4e0d\u662f\u5566\uff0c\u6211\u77e5\u9053\u4f60\u6700\u8fd1\u4e3a\u73ca\u5609\u59d0\u59d0\u7684\u4e8b\u60c5\u70e6\u3002\u672c\u6765\u4e0d\u60f3\u8ddf\u4f60\u8bf4\u7684\uff0c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u55ef\uff0c\u6211\u77e5\u9053\u3002\u201d</p><p>\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u8299\u857e\u72c4\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u77e5\u9053\u4f60\u662f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u201d\u743c\u6069\u5c06\u5634\u5507\u8d34\u5728\u5979\u8033\u8fb9\uff0c\u201c\u4f60\u5750\u7740\u7684\u5730\u65b9\uff0c\u6211\u7684\u88e4\u5b50\u90a3\u5757\u5df2\u7ecf\u88ab\u4f60\u5f04\u6e7f\u900f\u4e86\uff0c\u81ea\u5df1\u6ca1\u611f\u89c9\u5230\u4e48\uff1f\u201d</p><p>\u5c11\u5973\u4e00\u6014\uff0c\u968f\u5373\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u201c\u8ba8\u538c\u8ba8\u538c\u8ba8\u538c\u8ba8\u538c\uff01\u201d</p><p>\u5979\u6376\u6253\u7740\u743c\u6069\u7684\u80a9\u8180\uff0c\u4f46\u5f88\u5feb\u5c31\u6c14\u5598\u5401\u5401\uff0c\u743c\u6069\u4e58\u673a\u543b\u4e0a\u5979\u7684\u5634\u5507\u3002\u8fd9\u4e2a\u7b80\u5355\u7684\u52a8\u4f5c\u7acb\u523b\u74e6\u89e3\u4e86\u5c11\u5973\u6240\u6709\u7684\u62b5\u6297\u610f\u5fd7\uff0c\u5979\u7684\u5c0f\u62f3\u5934\u4e0d\u7531\u81ea\u4e3b\u5730\u677e\u5f00\uff0c\u53cc\u81c2\u73af\u7ed5\u62b1\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u67d4\u8f6f\u7684\u820c\u5c16\u4e3b\u52a8\u4ece\u6a31\u5507\u4e2d\u5410\u51fa\uff0c\u52aa\u529b\u8fce\u5408\u7740\uff0c\u534a\u7741\u534a\u95ed\u7684\u773c\u7738\u91cc\u5df2\u7ecf\u5145\u6ee1\u6e7f\u6e7f\u7684\u6c34\u6c7d\uff0c\u900f\u7740\u8bf1\u4eba\u7684\u60c5\u6b32\u3002</p><p>\u743c\u6069\u542e\u5438\u7740\u5c11\u5973\u9999\u6ed1\u820c\u5c16\uff0c\u54c1\u5c1d\u5979\u53e3\u8154\u4e2d\u751c\u751c\u7684\u6d25\u6db2\uff0c\u8299\u857e\u72c4\u4f3c\u4e4e\u662f\u521a\u6f31\u8fc7\u53e3\uff0c\u6709\u79cd\u6de1\u6de1\u7684\u8584\u8377\u5473\u9053\u3002\u8fc7\u4e86\u5f88\u4e45\uff0c\u4e24\u4eba\u624d\u604b\u604b\u4e0d\u820d\u5730\u5206\u5f00\uff0c\u201c\u6211\u4eec\u56de\u5bb6\u53bb\u505a\uff1f\u201d\u743c\u6069\u8f7b\u58f0\u95ee\uff0c\u201c\u8fd8\u662f\u5c31\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u5973\u5b69\u4e0d\u5b89\u5730\u626d\u52a8\u7740\u8eab\u4f53\uff0c\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d</p><p>\u201c\u5c31\u5728\u8fd9\u91cc\u597d\u4e0d\u597d\uff1f\u201d\u743c\u6069\u53c8\u95ee\u3002</p><p>\u201c\u4f1a\u2026\u2026\u4f1a\u88ab\u4eba\u770b\u89c1\u2026\u2026\u201d\u5973\u5b69\u8f7b\u58f0\u53cd\u5bf9\u7740\uff0c\u4f46\u8bed\u6c14\u534a\u70b9\u90fd\u4e0d\u575a\u6301\u3002</p><p>\u201c\u4e0d\u4f1a\u7684\uff0c\u4f60\u770b\u56db\u5468\u90fd\u6ca1\u4eba\u3002\u73b0\u5728\u5927\u5bb6\u90fd\u5728\u6559\u5802\u91cc\u5462\uff0c\u54ea\u6709\u591c\u91cc\u8dd1\u51fa\u6765\u901b\u82b1\u56ed\u7684\u3002\u201d</p><p>\u201c\u55ef\uff0c\u55ef\u3002\u201d</p><p>\u8f7b\u6613\u5c31\u8bf4\u670d\u4e86\u8299\u857e\u72c4\uff0c\u743c\u6069\u5148\u89e3\u5f00\u81ea\u5df1\u7684\u8170\u5e26\uff0c\u62c9\u5f00\u88e4\u5934\uff0c\u5c06\u4e00\u76f4\u786c\u633a\u7740\uff0c\u5df2\u7ecf\u80c0\u75db\u96be\u53d7\u7684\u5bb6\u4f19\u91ca\u653e\u51fa\u6765\uff0c\u7136\u540e\u4f38\u624b\u5c06\u5979\u7684\u88d9\u5b50\u7ffb\u5230\u8170\u4e0a\uff0c\u9732\u51fa\u7c89\u7ea2\u8272\u7684\u5185\u88e4\uff0c\u4ed6\u6293\u7740\u82b1\u8fb9\uff0c\u6b63\u51c6\u5907\u892a\u4e0b\uff0c\u5374\u88ab\u5979\u4f4e\u58f0\u963b\u6b62\u3002</p><p>\u201c\u4e0d\u2026\u2026\u4e0d\u7528\u8131\u2026\u2026\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u5e95\u90e8\uff0c\u201d\u5979\u5598\u606f\u7740\uff0c\u201c\u5185\u88e4\u5e95\u90e8\uff0c\u53ef\u4ee5\u62c9\u5f00\u7684\u2026\u2026\u201d</p><p>\u743c\u6069\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u5c06\u624b\u63a2\u5165\u5973\u5b69\u96ea\u5ae9\u7684\u53cc\u817f\u6839\u90e8\u4e4b\u95f4\uff0c\u629a\u6478\u5185\u88e4\u5e95\u90e8\u90a3\u5757\u5c0f\u5c0f\u7684\u5e03\u7247\u3002\u7ecf\u8fc7\u521a\u624d\u7684\u7231\u629a\u4eb2\u70ed\uff0c\u90a3\u91cc\u65e9\u5c31\u5df2\u7ecf\u88ab\u5973\u5b69\u6e29\u6696\u7684\u4f53\u6db2\u5b8c\u5168\u6d78\u900f\uff0c\u624b\u6307\u6478\u4e0a\u53bb\u90fd\u662f\u6e7f\u6e7f\u6ed1\u6ed1\u7684\u3002\u743c\u6069\u4ed4\u7ec6\u6478\u7d22\u7740\uff0c\u7136\u540e\u53d1\u73b0\u5f02\u6837\uff0c\u67d0\u5904\u4f3c\u4e4e\u6bd4\u522b\u5904\u9ad8\u8d77\u4e00\u70b9\u3002\u4ed6\u7528\u6307\u5c16\u6309\u4e86\u6309\uff0c\u8f7b\u8f7b\u637b\u4f4f\u4e00\u626f\uff0c\u201c\u55e4\u201d\u5730\u8f7b\u54cd\uff0c\u90a3\u5757\u5e03\u7247\u5f53\u771f\u4fbf\u88ab\u6495\u4e86\u5f00\u6765\uff0c\u9732\u51fa\u6c34\u871c\u6843\u822c\u7684\u9971\u6ee1\u82b1\u623f\u3002</p><p>\u201c\u60c5\u8da3\u5185\u8863\u554a\uff0c\u4e0d\u9519\u4e0d\u9519\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u54ac\u7740\u5979\u7684\u8033\u5782\uff0c\u201c\u4ec0\u4e48\u65f6\u5019\u4e70\u7684\uff1f\u201d</p><p>\u201c\u4eca\u5929\u2026\u2026\u4eca\u5929\u4e0a\u5348\uff0c\u8d81\u8299\u8389\u5a05\u6ca1\u6ce8\u610f\u7684\u65f6\u5019\u5077\u5077\u4e70\u7684\u3002\u201d</p><p>\u201c\u7136\u540e\u5c31\u4e00\u76f4\u7a7f\u5728\u8eab\u4e0a\uff1f\u201d\u743c\u6069\u53c8\u95ee\uff0c\u201c\u7a7f\u7740\u8fd9\u4e48\u77ed\u7684\u88d9\u5b50\uff0c\u8fd8\u7a7f\u7740\u8fd9\u79cd\u5185\u8863\uff0c\u5728\u5916\u9762\u5f85\u4e86\u4e00\u6574\u5929\uff1f\u4ee5\u524d\u6ca1\u770b\u51fa\u6765\u4f60\u8fd9\u4e48\u80c6\u5927\u5462\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\u5566\uff0c\u201d\u8299\u857e\u72c4\u6025\u5fd9\u5206\u8fa9\uff0c\u201c\u6211\u662f\u6765\u795e\u6bbf\u4e4b\u540e\u624d\u6362\u7684\u3002\u56e0\u4e3a\u542c\u8299\u8389\u5a05\u8bf4\uff0c\u4eca\u665a\u73ca\u5609\u59d0\u59d0\u4f1a\u6765\uff0c\u6211\u60f3\u4f60\u80af\u5b9a\u4e5f\u4f1a\u6765\u2026\u2026\u4f60\u559c\u6b22\u5417\uff1f\u201d</p><p>\u743c\u6069\u4eb2\u543b\u5979\u7684\u8138\u988a\uff0c\u201c\u559c\u6b22\uff0c\u5f53\u7136\u559c\u6b22\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u5973\u5b69\u987f\u65f6\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u6211\u8fd8\u4e00\u76f4\u62c5\u5fc3\u5462\uff0c\u6015\u4f60\u4e0d\u559c\u6b22\u2026\u2026\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u6211\u559c\u6b22\u5f97\u8981\u547d\uff0c\u6240\u4ee5\u6211\u8981\u597d\u597d\u5956\u8d4f\u4f60\u3002\u201d</p><p>\u743c\u6069\u8bf4\u7740\uff0c\u53cc\u624b\u63e1\u7740\u5973\u5b69\u7684\u96ea\u5ae9\u7f8e\u81c0\uff0c\u5c06\u5979\u6574\u4e2a\u4eba\u6258\u4e86\u8d77\u6765\uff0c\u5bf9\u51c6\u4f4d\u7f6e\u7136\u540e\u6162\u6162\u653e\u4e0b\uff0c\u8ba9\u81ea\u5df1\u7f13\u6162\u800c\u575a\u51b3\u5730\u4fb5\u5165\u5979\u4f53\u5185\u3002\u8299\u857e\u72c4\u6b7b\u6b7b\u54ac\u7740\u5634\u5507\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u5c3d\u529b\u6291\u5236\u7740\u81ea\u5df1\u4e0d\u53d1\u51fa\u58f0\u97f3\uff0c\u8138\u4e0a\u795e\u60c5\u65e2\u662f\u75db\u695a\uff0c\u53c8\u5939\u6742\u7740\u4e00\u4e1d\u751c\u871c\u3002\u5979\u662f\u7cbe\u81f4\u7ea4\u7ec6\u7684\u5973\u5b69\u5b50\uff0c\u4e00\u5f00\u59cb\u6709\u4e9b\u96be\u4ee5\u627f\u53d7\u7537\u4eba\u7684\u7c97\u66b4\uff0c\u597d\u5728\u4e24\u4eba\u76f8\u8bc6\u6570\u8f7d\uff0c\u6709\u8fc7\u65e0\u6570\u6b22\u597d\u7ecf\u9a8c\uff0c\u5bf9\u5f7c\u6b64\u7684\u8eab\u4f53\u90fd\u662f\u719f\u6089\u65e0\u6bd4\uff0c\u4e8b\u5148\u53c8\u6709\u5145\u5206\u7684\u7231\u629a\uff0c\u6e10\u6e10\u4fbf\u82e6\u5c3d\u7518\u6765\uff0c\u6c89\u9189\u5176\u4e2d\u3002</p><p>\u4e24\u4eba\u9762\u5bf9\u7740\u9762\uff0c\u8299\u857e\u72c4\u8de8\u5750\u5728\u743c\u6069\u8170\u4e0a\uff0c\u5979\u62ab\u7740\u5927\u8863\uff0c\u5b8c\u5168\u906e\u4f4f\u4e86\u8170\u81c0\u90e8\u4f4d\uff0c\u53ea\u9732\u51fa\u5c0f\u534a\u622a\u96ea\u5ae9\u7389\u817f\uff0c\u6625\u5149\u4e0d\u81f4\u5916\u6cc4\u3002\u5982\u679c\u6b64\u65f6\u6709\u4eba\u4ece\u8fdc\u5904\u770b\uff0c\u53ea\u4f1a\u4ee5\u4e3a\u662f\u4e00\u5bf9\u60c5\u4fa3\u5728\u4eb2\u6635\uff0c\u5973\u5b69\u5750\u5728\u7537\u5b69\u817f\u4e0a\u2014\u2014\u4e8b\u5b9e\u4e0a\u4e5f\u786e\u5b9e\u662f\u5728\u4eb2\u6635\uff0c\u53ea\u4e0d\u8fc7\u662f\u6bd4\u8f83\u201c\u6df1\u5165\u201d\u7684\u4eb2\u6635\u7f62\u4e86\u3002\u8fd9\u79cd\u59ff\u52bf\u6ca1\u6cd5\u5267\u70c8\u52a8\u4f5c\uff0c\u4f46\u66f4\u52a0\u6e29\u60c5\u6b3e\u6b3e\uff0c\u522b\u6709\u4e00\u756a\u60c5\u8da3\u3002</p><p>\u201c\u5514\u2026\u2026\u5514\u2026\u2026\u201d</p><p>\u8299\u857e\u72c4\u9762\u8272\u6f6e\u7ea2\uff0c\u989d\u89d2\u6c81\u6c57\uff0c\u9f3b\u5b50\u91cc\u53d1\u51fa\u53ef\u7231\u7684\u95f7\u54fc\uff0c\u53cc\u81c2\u7d27\u7d27\u62b1\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u5426\u5219\u6574\u4e2a\u4eba\u4fbf\u90fd\u8981\u762b\u8f6f\u4e0b\u6765\u4e86\u3002\u743c\u6069\u4eca\u665a\u539f\u672c\u662f\u966a\u73ca\u5609\u8fc7\u6765\u7684\uff0c\u5982\u4eca\u5374\u4e22\u4e0b\u59d0\u59d0\uff0c\u5728\u8fd9\u91cc\u548c\u8299\u857e\u72c4\u6b22\u597d\uff0c\u611f\u89c9\u5c31\u50cf\u662f\u80cc\u7740\u59bb\u5b50\u5077\u60c5\u4f3c\u7684\uff1b\u800c\u4e14\u8fd9\u79cd\u91ce\u5916\u6b22\u597d\uff0c\u8f83\u4e4b\u5e73\u5e38\u5728\u5ba4\u5185\u5e8a\u4e0a\uff0c\u522b\u6709\u4e00\u79cd\u683c\u5916\u7684\u523a\u6fc0\uff0c\u4e0d\u5230\u534a\u5c0f\u65f6\uff0c\u8299\u857e\u72c4\u5df2\u7ecf\u63a5\u8fde\u88ab\u9001\u4e0a\u4e86\u4e09\u6b21\u5dc5\u5cf0\uff0c\u800c\u743c\u6069\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5728\u5979\u8eab\u4f53\u91cc\u75db\u75db\u5feb\u5feb\u5730\u53d1\u6cc4\u51fa\u6765\u3002</p><p>\u4e91\u96e8\u4e4b\u540e\uff0c\u4e24\u4eba\u5e76\u6ca1\u6709\u7acb\u523b\u5206\u5f00\uff0c\u800c\u662f\u4f9d\u65e7\u4fdd\u6301\u7740\u539f\u672c\u7684\u59ff\u52bf\uff0c\u4eab\u53d7\u7740\u4f59\u97f5\u7684\u751c\u871c\u3002\u8fc7\u4e86\u597d\u534a\u664c\uff0c\u4ed6\u4eec\u7684\u5598\u606f\u6e10\u6e10\u5e73\u7f13\uff0c\u5fc3\u8df3\u4e5f\u6062\u590d\u4e86\u6b63\u5e38\uff0c\u56e0\u4e3a\u5267\u70c8\u8fd0\u52a8\u800c\u6d41\u51fa\u7684\u6c57\u4e5f\u88ab\u98ce\u5439\u5e72\u3002\u743c\u6069\u6276\u8d77\u8299\u857e\u72c4\uff0c\u7528\u968f\u8eab\u5e26\u7740\u7684\u7eb8\u5dfe\u5c06\u79fd\u7269\u64e6\u62ed\u5e72\u51c0\uff0c\u6574\u7406\u597d\u8863\u88f3\u3002\u201c\u4eca\u665a\u4e0d\u4f1a\u5931\u7720\u4e86\u5427\u3002\u201d\u743c\u6069\u53d6\u7b11\u5979\u3002</p><p>\u201c\u53ef\u662f\u8fd8\u6709\u660e\u665a\u5462\u3002\u201d\u5979\u5e7d\u5e7d\u5730\u8bf4\u3002</p><p>\u8bdd\u4e00\u51fa\u53e3\uff0c\u4e24\u4eba\u90fd\u6014\u4e86\u6014\uff0c\u6c14\u6c1b\u9661\u7136\u51b7\u5bc2\u4e86\u51e0\u79d2\u949f\uff0c\u968f\u5373\u6062\u590d\u6b63\u5e38\u3002\u201c\u554a\u5440\uff0c\u8fd9\u4e48\u665a\uff0c\u796d\u5178\u5e94\u8be5\u5feb\u8981\u5f00\u59cb\u4e86\uff0c\u201d\u8299\u857e\u72c4\u770b\u7740\u5929\u8272\uff0c\u201c\u8be5\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4ed6\u4eec\u80a9\u5e76\u80a9\u8d70\u4e0b\u571f\u4e18\uff0c\u6cbf\u7740\u8bb0\u5fc6\u4e2d\u6765\u65f6\u7684\u8def\u5f80\u56de\u8d70\u3002\u77f3\u677f\u8def\u539f\u672c\u5c31\u5d0e\u5c96\u96be\u884c\uff0c\u56e0\u4e3a\u521a\u521a\u6b22\u597d\u8fc7\u7684\u7f18\u6545\uff0c\u8299\u857e\u72c4\u8d70\u8def\u660e\u663e\u6709\u4e9b\u4e0d\u65b9\u4fbf\uff0c\u518d\u52a0\u4e0a\u6b64\u65f6\u5df2\u7ecf\u5165\u591c\uff0c\u5929\u7a7a\u4e2d\u90a3\u5f2f\u65b0\u6708\u88ab\u4e4c\u4e91\u906e\u4f4f\uff0c\u534a\u70b9\u5149\u4eae\u90fd\u900f\u4e0d\u4e0b\u6765\u3002\u8fd9\u91cc\u662f\u838e\u5c14\u795e\u6bbf\uff0c\u800c\u591c\u5973\u58eb\u538c\u6076\u5149\u4eae\u2014\u2014\u6240\u4ee5\u8fd9\u5ea7\u82b1\u56ed\u91cc\u8fde\u4e2a\u8def\u706f\u90fd\u6ca1\u6709\uff0c\u73b0\u5728\u662f\u9ed1\u6f06\u6f06\u7684\u4e00\u7247\u3002\u743c\u6069\u867d\u7136\u6709\u9ed1\u6697\u89c6\u89c9\uff0c\u5374\u4e5f\u53ea\u80fd\u52c9\u5f3a\u8fa8\u8ba4\u51fa\u5468\u56f4\u7269\u4f53\u7684\u5927\u81f4\u8f6e\u5ed3\uff0c\u4e0d\u81f3\u4e8e\u649e\u5899\u6216\u8005\u8d70\u9519\u5230\u82b1\u4e1b\u91cc\u53bb\u3002\u4ed6\u5c1d\u8bd5\u60f3\u7528\u4f20\u9001\u672f\u76f4\u63a5\u56de\u5230\u9634\u5f71\u4e4b\u5385\uff0c\u7ed3\u679c\u6cd5\u672f\u867d\u7136\u91ca\u653e\u51fa\u6765\uff0c\u4f20\u9001\u95e8\u5374\u65e0\u6cd5\u5f62\u6210\uff0c\u767d\u767d\u6d6a\u8d39\u4e86\u4e00\u4e2a\u6cd5\u672f\uff0c\u731c\u6d4b\u662f\u795e\u6bbf\u91cc\u8bbe\u4e86\u4ec0\u4e48\u5c01\u9501\u7ed3\u754c\u7684\u7f18\u6545\u3002\u98de\u884c\u672f\u4f30\u8ba1\u53ef\u7528\uff0c\u7136\u800c\u4ed6\u4eca\u5929\u6070\u597d\u6ca1\u51c6\u5907\u3002</p><p>\u53ea\u80fd\u8d70\u8def\u4e86\u3002</p><p>\u4e24\u4e2a\u4eba\u624b\u7275\u7740\u624b\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u524d\u8fdb\u7740\uff0c\u8d70\u4e86\u534a\u5929\u53d1\u73b0\u8d8a\u8d70\u8d8a\u4e0d\u5bf9\u52b2\uff0c\u5468\u906d\u7684\u666f\u7269\u8d8a\u6765\u8d8a\u964c\u751f\u3002\u66f4\u8981\u547d\u7684\u662f\uff0c\u5728\u4ed6\u4eec\u4f4e\u5934\u8ba4\u8def\u7684\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u796d\u5178\u5e94\u8be5\u662f\u5df2\u7ecf\u6b63\u5f0f\u5f00\u59cb\uff0c\u6240\u4ee5\u539f\u672c\u8fd8\u6709\u70b9\u706f\u5149\u7684\u5927\u6559\u5802\u4e0d\u77e5\u4f55\u65f6\u4e5f\u9eef\u6de1\u4e0b\u6765\uff0c\u9690\u6ca1\u5728\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u4e4b\u4e2d\u2014\u2014\u8fd9\u4e0b\u5b50\u4ed6\u4eec\u8fde\u76ee\u7684\u5730\u7684\u65b9\u4f4d\u90fd\u4e0d\u77e5\u9053\u4e86\u3002</p><p>\u201c\u5b8c\u86cb\uff0c\u8ff7\u8def\u4e86\u3002\u201d</p><p>\u5f53\u7136\u4e86\uff0c\u8ff7\u8def\u5176\u5b9e\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u95ee\u9898\uff0c\u8fd9\u91cc\u662f\u795e\u6bbf\u7684\u82b1\u56ed\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u539f\u59cb\u68ee\u6797\uff0c\u65e2\u6ca1\u6709\u8001\u864e\u4e5f\u6ca1\u6709\u9cc4\u9c7c\uff0c\u66f4\u6ca1\u6709\u64c5\u7528\u5439\u7bad\u559c\u6b22\u5265\u4eba\u76ae\u7684\u5c0f\u77ee\u4eba\uff0c\u5b9e\u5728\u4e0d\u884c\u7684\u8bdd\uff0c\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u8fc7\u4e00\u591c\uff0c\u7b49\u660e\u5929\u5929\u4eae\u4e86\u518d\u8bf4\u3002\u53ea\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u73ca\u5609\u548c\u8299\u8389\u5a05\u4e24\u4eba\u5c31\u4e0d\u77e5\u9053\u6709\u591a\u7126\u6025\u4e86\u3002</p><p>\u6b63\u5934\u75bc\u95f4\uff0c\u4e00\u70b9\u8367\u5149\u7a81\u7136\u5728\u524d\u65b9\u4eae\u8d77\uff0c\u7f13\u6162\u53d8\u5927\uff0c\u6700\u7ec8\u5f62\u6210\u4e00\u4e2a\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u7c73\u9ec4\u8272\u5149\u7403\uff0c\u8ddd\u79bb\u743c\u6069\u548c\u8299\u857e\u72c4\u6240\u5728\u7684\u4f4d\u7f6e\u4f3c\u4e4e\u4e5f\u4e0d\u8fdc\u3002\u743c\u6069\u51dd\u795e\u770b\u4e86\u770b\uff0c\u201c\u6211\u4eec\u8fc7\u53bb\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u662f\u4e2a\u821e\u5149\u672f\u3002\u201d</p><p>\u821e\u5149\u672f\u662f\u4e2a\u6700\u57fa\u7840\u7684\u6cd5\u672f\uff0c\u662f\u4e2a\u5deb\u5e08\u5b66\u5f92\u90fd\u80fd\u7528\u2014\u2014\u4f46\u8fd9\u81f3\u5c11\u8bc1\u660e\u90a3\u91cc\u6709\u4e2a\u4eba\uff0c\u6709\u4eba\u5c31\u53ef\u4ee5\u95ee\u8def\uff0c\u81f3\u4e0d\u6d4e\u4e5f\u80fd\u591a\u4e2a\u804a\u5929\u5bf9\u8c61\u3002\u62b1\u7740\u8fd9\u79cd\u5ff5\u5934\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u671d\u7740\u5149\u7403\u8fdb\u53d1\u3002\u4ed6\u4eec\u7a7f\u8fc7\u82b1\u4e1b\uff0c\u8d70\u8fdb\u4e00\u7247\u5c0f\u6811\u6797\uff0c\u6cbf\u7740\u5f2f\u5f2f\u66f2\u66f2\u7684\u6797\u4e2d\u5c0f\u5f84\u8d70\u4e86\u5927\u7ea6\u4e03\u516b\u5206\u949f\uff0c\u524d\u65b9\u7a81\u7136\u5f00\u9614\uff0c\u663e\u51fa\u4e00\u7247\u7a7a\u5730\u6765\u3002</p><p>\u7a7a\u5730\u7684\u4e2d\u592e\uff0c\u662f\u4e00\u4e2a\u5927\u5927\u7684\u6df1\u5751\uff0c\u76f4\u5f84\u8d85\u8fc7\u516d\u5341\u82f1\u5c3a\uff0c\u5751\u5e95\u8ddd\u79bb\u5730\u9762\u4e5f\u8d85\u8fc7\u4e8c\u5341\u82f1\u5c3a\uff0c\u56db\u58c1\u65e2\u9661\u4e14\u76f4\uff0c\u6ca1\u6709\u4efb\u4f55\u53ef\u4f9b\u4e0a\u4e0b\u7684\u53f0\u9636\u6216\u8005\u68af\u5b50\uff0c\u90a3\u4e2a\u9b54\u6cd5\u5149\u7403\u5c31\u60ac\u6d6e\u5728\u6df1\u5751\u7684\u4e0a\u65b9\u3002\u743c\u6069\u8d70\u5230\u5751\u8fb9\uff0c\u53d1\u73b0\u5e95\u4e0b\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7ad9\u7740\uff0c\u4e00\u4e2a\u8e72\u7740\uff0c\u56e0\u4e3a\u5149\u7ebf\u4e0d\u4f73\uff0c\u8ddd\u79bb\u53c8\u8fdc\uff0c\u770b\u4e0d\u6e05\u695a\u76f8\u8c8c\uff0c\u53ea\u80fd\u4ece\u8eab\u5f62\u8f6e\u5ed3\u4e0a\u5224\u65ad\uff0c\u7ad9\u7740\u7684\u662f\u4f4d\u5973\u6027\uff0c\u8e72\u5728\u5979\u9762\u524d\u7684\u5219\u662f\u4f4d\u7537\u6027\uff0c\u624b\u6309\u5728\u5979\u7684\u5c0f\u817f\u4e0a\uff0c\u4e0d\u77e5\u9053\u5728\u505a\u4e9b\u4ec0\u4e48\u3002</p><p>\u743c\u6069\u8d70\u5230\u5751\u8fb9\uff0c\u6b63\u8981\u51fa\u58f0\u53d1\u95ee\uff0c\u90a3\u4e2a\u8e72\u7740\u7684\u7537\u4eba\u7a81\u7136\u8dea\u4e86\u4e0b\u6765\uff0c\u4ee5\u4e00\u79cd\u9876\u793c\u819c\u62dc\u7684\u59ff\u52bf\u530d\u5310\u5728\u5973\u6027\u7684\u811a\u8fb9\u2014\u2014\u7136\u800c\u548c\u8fd9\u8c26\u5351\u59ff\u6001\u76f8\u914d\u5408\u7684\uff0c\u662f\u4ed6\u53f3\u624b\u638c\u5fc3\u4e2d\u63e1\u7740\u7684\u4e00\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u5c0f\u5200\u3002\u7537\u4eba\u5c06\u8138\u51d1\u8fd1\u5973\u4eba\u7684\u811a\uff0c\u4eff\u4f5b\u975e\u5e38\u4ed4\u7ec6\u5730\u89c2\u5bdf\u4e86\u4e00\u4f1a\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5200\u5149\u98de\u63a0\uff0c\u5237\u5237\u5237\u5730\u5c31\u5728\u5973\u4eba\u7684\u811a\u9762\u4e0a\u5207\u524a\u8d77\u6765\u3002</p><p>\u6ca1\u6709\u60f3\u8c61\u4e2d\u7684\u9c9c\u8840\u98de\u6e85\uff0c\u53ea\u6709\u767d\u8272\u7684\u77f3\u5c51\u7c89\u672b\u7eb7\u7eb7\u6563\u843d\uff1b\u4e5f\u6ca1\u6709\u610f\u6599\u4e2d\u7684\u60ca\u547c\u60e8\u53eb\uff0c\u5973\u4eba\u7ad9\u7acb\u539f\u5730\u7eb9\u4e1d\u4e0d\u52a8\u3002\u743c\u6069\u8be7\u5f02\u4e4b\u6781\uff0c\u7d22\u6027\u63fd\u7740\u8299\u857e\u72c4\u7684\u8170\uff0c\u542f\u52a8\u4e00\u4e2a\u7fbd\u843d\u672f\u8df3\u4e0b\u5751\u6765\uff0c\u8d70\u5230\u8fd1\u524d\u624d\u53d1\u73b0\uff0c\u539f\u6765\u8fd9\u4e2a\u5973\u4eba\u5176\u5b9e\u662f\u4e2a\u96d5\u5851\uff0c\u53ea\u662f\u7684\u786e\u96d5\u5f97\u60df\u5999\u60df\u8096\uff0c\u6829\u6829\u5982\u751f\uff0c\u4e0d\u8fd1\u770b\u7684\u8bdd\u6839\u672c\u5c31\u53d1\u89c9\u4e0d\u4e86\u3002\u96d5\u5851\u7684\u4e0a\u534a\u8eab\u5e94\u8be5\u5df2\u7ecf\u5b8c\u5de5\uff0c\u4e0b\u534a\u8eab\u5c24\u5176\u662f\u817f\u811a\u90e8\u4f4d\uff0c\u4f3c\u4e4e\u8fd8\u6709\u4e9b\u7c97\u7cd9\uff0c\u90a3\u4e2a\u62ff\u5c0f\u5200\u7684\u7537\u4eba\u5e94\u8be5\u662f\u96d5\u5851\u5e08\uff0c\u6b63\u5728\u505a\u6700\u540e\u7684\u4fee\u9970\u52a0\u5de5\u3002\u4ed6\u662f\u5982\u6b64\u7684\u4e13\u5fc3\u81f4\u5fd7\uff0c\u805a\u7cbe\u4f1a\u795e\uff0c\u4ee5\u81f3\u4e8e\u743c\u6069\u548c\u8299\u857e\u72c4\u4ece\u80cc\u540e\u8d70\u8fc7\u6765\uff0c\u53d1\u51fa\u5f88\u6e05\u695a\u7684\u811a\u6b65\u58f0\uff0c\u4ed6\u90fd\u50cf\u662f\u5145\u8033\u4e0d\u95fb\uff0c\u5934\u4e5f\u4e0d\u62ac\u3002</p><p>\u201c\u53ef\u80fd\u662f\u795e\u6bbf\u91cc\u8981\u505a\u65b0\u7684\u5851\u50cf\uff0c\u50ac\u5f97\u6bd4\u8f83\u6025\uff0c\u6240\u4ee5\u5728\u8fd9\u91cc\u8fde\u591c\u8d76\u5de5\u5427\u3002\u4e0d\u8fc7\u5947\u602a\u554a\uff0c\u8fd9\u96d5\u5851\u2026\u2026\u600e\u4e48\u603b\u89c9\u5f97\u770b\u7740\u6709\u70b9\u773c\u719f\uff0c\u50cf\u662f\u4e2a\u8ba4\u8bc6\u7684\u4eba\u3002\u201d</p><p>\u743c\u6069\u5fc3\u91cc\u5600\u5495\u7740\uff0c\u7136\u800c\u53c8\u600e\u4e48\u90fd\u60f3\u4e0d\u51fa\u5230\u5e95\u50cf\u8c01\uff0c\u6b63\u5728\u7591\u60d1\uff0c\u8299\u857e\u72c4\u62c9\u4e86\u62c9\u4ed6\u7684\u8863\u8896\u3002\u201c\u743c\u6069\uff0c\u201d\u5979\u6084\u58f0\u8bf4\uff0c\u201c\u5979\u600e\u4e48\u611f\u89c9\u6709\u70b9\u50cf\u73ca\u5609\u59d0\u59d0\u3002\u201d</p><p>\u88ab\u5979\u8fd9\u4e00\u63d0\u9192\uff0c\u743c\u6069\u518d\u4ed4\u7ec6\u770b\uff0c\u8fd8\u771f\u89c9\u5f97\u6709\u51e0\u5206\u76f8\u4f3c\u3002\u5f53\u7136\uff0c\u8fd9\u79cd\u76f8\u4f3c\u5e76\u4e0d\u662f\u6307\u5916\u8c8c\uff0c\u73ca\u5609\u662f\u5e74\u8f7b\u5c11\u5973\uff0c\u7a1a\u6c14\u5c1a\u5b58\uff0c\u8fd9\u4e2a\u96d5\u5851\u5219\u662f\u4e2a\u4e8c\u5341\u516d\u4e03\u5c81\u5de6\u53f3\u7684\u5973\u5b50\uff0c\u660e\u663e\u6210\u719f\u5f97\u591a\uff0c\u800c\u4e14\u773c\u775b\u662f\u7d2b\u8272\u7684\uff0c\u5e76\u4e0d\u662f\u5f88\u76f8\u50cf\u3002\u5979\u4eec\u771f\u6b63\u76f8\u4f3c\u7684\uff0c\u662f\u5728\u7709\u5b87\u95f4\uff0c\u90fd\u6709\u4e00\u80a1\u82f1\u534e\u4e4b\u6c14\u9690\u7136\u6f5c\u85cf\uff0c\u542b\u800c\u4e0d\u9732\uff0c\u5374\u80fd\u663e\u5f97\u6574\u4e2a\u4eba\u795e\u91c7\u98de\u626c\uff0c\u5149\u534e\u593a\u76ee\u3002</p><p>\u5979\u662f\u8c01\u554a\uff1f</p><p>\u4e0d\u77e5\u600e\u5730\uff0c\u743c\u6069\u5fc3\u4e2d\u83ab\u540d\u5176\u5999\u5730\u6709\u4e9b\u7d27\u5f20\uff0c\u611f\u89c9\u5589\u5499\u6709\u4e9b\u53d1\u5e72\u3002\u4ed6\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u60f3\u51fa\u8a00\u8be2\u95ee\uff0c\u8c01\u6599\u90a3\u4e2a\u96d5\u5851\u5e08\u538b\u6839\u628a\u4ed6\u5f53\u4f5c\u7a7a\u6c14\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u95e8\u5fc3\u601d\u7cbe\u96d5\u7ec6\u7422\u7740\u4ed6\u7684\u4f5c\u54c1\u3002\u5de6\u8db3\u4fee\u9970\u5b8c\u6bd5\uff0c\u96d5\u5851\u5e08\u79fb\u52a8\u4e86\u4e0b\u8eab\u4f53\uff0c\u53c8\u5728\u96d5\u5851\u7684\u53f3\u8db3\u4e0a\u5c0f\u5fc3\u7ffc\u7ffc\u7528\u5200\u5203\u6469\u632b\u8d77\u6765\u3002</p><p>\u743c\u6069\u7d22\u6027\u4e5f\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5c31\u5728\u4e00\u65c1\u770b\u7740\u3002\u53c8\u8fc7\u4e86\u5927\u7ea6\u4e00\u523b\u949f\uff0c\u96d5\u5851\u5e08\u7ec8\u4e8e\u5b8c\u6210\u4e86\u5de5\u4f5c\uff0c\u4ece\u5730\u4e0a\u722c\u8d77\u8eab\u6765\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u62cd\u4e86\u62cd\u624b\uff0c\u5f00\u59cb\u6ee1\u610f\u5730\u6b23\u8d4f\u81ea\u5df1\u7684\u4f5c\u54c1\u3002\u201c\u4f60\u7ec8\u4e8e\u80af\u6b63\u773c\u770b\u6211\u4e86\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u50cf\u662f\u5728\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u6211\u7b49\u8fd9\u4e00\u5929\u5df2\u7ecf\u7b49\u4e86\u5f88\u4e45\u3002\u201d</p><p>\u96d5\u5851\u5e08\u4f38\u51fa\u624b\uff0c\u629a\u6478\u7740\u96d5\u50cf\u7684\u8138\uff0c\u53e3\u4e2d\u5583\u5583\u81ea\u8bed\uff0c\u52a8\u4f5c\u8f7b\u67d4\u81f3\u6781\uff0c\u4eff\u4f5b\u662f\u5728\u629a\u6478\u5fc3\u4eea\u5df2\u4e45\u7684\u604b\u4eba\uff0c\u7136\u800c\u770b\u5728\u743c\u6069\u773c\u4e2d\uff0c\u4e0d\u77e5\u600e\u7684\u5374\u900f\u7740\u4e00\u79cd\u83ab\u540d\u7684\u8be1\u5f02\uff0c\u50cf\u662f\u88ab\u4e00\u6761\u51b0\u51b7\u7684\u86c7\u7f20\u7ed5\u4e0a\u4e86\u8eab\u4f53\uff0c\u8d34\u4e0a\u4e86\u76ae\u80a4\uff0c\u8ba9\u4ed6\u5168\u8eab\u53d1\u5bd2\uff0c\u6709\u4e00\u79cd\u60f3\u53cd\u80c3\u7684\u6076\u5fc3\u5455\u5410\u611f\uff0c\u96be\u53d7\u4e4b\u6781\u3002\u4ed6\u6b63\u60f3\u62d4\u817f\u9003\u5f00\u6b64\u5730\uff0c\u7a81\u7136\u542c\u89c1\u4e00\u76f4\u67d4\u58f0\u4f4e\u8bed\u7684\u96d5\u5851\u5e08\u7528\u4e00\u79cd\u6050\u6016\u7684\u58f0\u97f3\u6012\u543c\u8d77\u6765\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u8981\u80cc\u53db\u6211\uff0c\u963f\u62c9\u838e\uff01\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u731b\u5730\u4e00\u8df3\uff0c\u8f6c\u8138\u770b\u5411\u8299\u857e\u72c4\uff0c\u6b63\u597d\u5c0f\u5973\u5b69\u4e5f\u671d\u4ed6\u770b\u8fc7\u6765\u3002\u4e24\u4eba\u90fd\u4ece\u5bf9\u65b9\u7684\u773c\u795e\u4e2d\u770b\u5230\u4e86\u540c\u4e00\u4e2a\u610f\u601d\u3002</p><p>\u4ed6\u521a\u624d\u8bf4\u7684\u662f\u201c\u963f\u62c9\u838e\u201d\uff1f</p><p>\u4e16\u754c\u4e0a\u540c\u540d\u540c\u59d3\u7684\u4eba\u5f88\u591a\uff0c\u4f46\u9634\u9b42\u57ce\u91cc\u53eb\u963f\u62c9\u838e\u7684\u4eba\u4f30\u8ba1\u6ca1\u51e0\u4e2a\u3002\u5982\u679c\u743c\u6069\u548c\u8299\u857e\u72c4\u6ca1\u540c\u65f6\u542c\u9519\u7684\u8bdd\u2014\u2014\u8fd9\u79cd\u53ef\u80fd\u6027\u5b9e\u5728\u4e0d\u9ad8\u2014\u2014\u90a3\u4e48\u8fd9\u4e2a\u5973\u6027\u96d5\u5851\uff0c\u96d5\u523b\u7684\u5e94\u8be5\u5c31\u662f\u6614\u65e5\u7684\u9634\u9b42\u57ce\u738b\u540e\u963f\u62c9\u838e\xb7\u5766\u8212\u5c14\u3002</p><p>\u4e00\u60f3\u5230\u8fd9\u70b9\uff0c\u743c\u6069\u987f\u65f6\u80cc\u4e0a\u53d1\u5bd2\uff0c\u963f\u62c9\u838e\u738b\u540e\u88ab\u8c0b\u6740\uff0c\u8fd9\u662f\u9634\u9b42\u57ce\u6709\u53f2\u4ee5\u6765\u6700\u5927\u7684\u4e00\u6869\u60ac\u6848\uff0c\u5176\u95f4\u53ea\u6015\u7275\u6d89\u4e86\u65e0\u6570\u89c1\u4e0d\u5f97\u5149\u7684\u9690\u79d8\u3002\u6df1\u66f4\u534a\u591c\u6708\u9ed1\u98ce\u9ad8\u7684\uff0c\u8fd9\u4e2a\u5bb6\u4f19\u8eb2\u5728\u8fd9\u91cc\u6084\u6084\u96d5\u523b\u963f\u62c9\u838e\u738b\u540e\u7684\u96d5\u50cf\uff0c\u539f\u672c\u5c31\u5df2\u7ecf\u5f88\u8be1\u5f02\uff0c\u518d\u542c\u4ed6\u8bf4\u8bdd\uff0c\u5206\u660e\u5c31\u4e0d\u662f\u4e2a\u4ec0\u4e48\u7eaf\u6d01\u5584\u826f\u4e4b\u8f88\u3002\u743c\u6069\u662f\u4e2a\u80c6\u5c0f\u7684\u4eba\uff0c\u4e0d\u60f3\u5377\u8fdb\u8fd9\u79cd\u9ad8\u5c42\u9634\u8c0b\uff0c\u8fd9\u79cd\u662f\u975e\u4e4b\u5730\uff0c\u8fd8\u662f\u5c3d\u65e9\u79bb\u5f00\u6bd4\u8f83\u597d\u3002</p><p>\u96d5\u5851\u5e08\u4eff\u4f5b\u9677\u5165\u4e86\u795e\u5fd7\u4e0d\u6e05\u7684\u75af\u72c2\u72b6\u6001\uff0c\u4ed6\u5bf9\u7740\u81ea\u5df1\u521a\u521a\u5b8c\u6210\u7684\u96d5\u5851\uff0c\u6012\u543c\u7740\uff0c\u5486\u54ee\u7740\uff0c\u8dfa\u7740\u811a\u9ad8\u58f0\u5492\u9a82\uff0c\u6325\u821e\u7740\u624b\u91cc\u7684\u5c0f\u5200\u3002\u96d5\u50cf\u81ea\u7136\u53ea\u80fd\u6c89\u9ed8\uff0c\u800c\u8fd9\u4f3c\u4e4e\u66f4\u6fc0\u53d1\u4e86\u4ed6\u7684\u6012\u6c14\uff0c\u201c\u5a4a\u5b50\uff01\u6742\u79cd\uff01\u8a00\u800c\u65e0\u4fe1\u7684\u5a3c\u5987\uff01\u201d\u4ed6\u7834\u53e3\u5927\u9a82\u7740\uff0c\u97f3\u8c03\u8d8a\u6765\u8d8a\u9ad8\uff0c\u8d8a\u6765\u8d8a\u5c16\u9510\uff0c\u523a\u5f97\u4eba\u8033\u819c\u9635\u9635\u751f\u75bc\uff0c\u4eff\u4f5b\u88ab\u9488\u624e\u4e86\u4e00\u822c\u3002</p><p>\u201c\u8fd9\u5bb6\u4f19\u539f\u6765\u662f\u4e2a\u75af\u5b50\u3002\u201d\u743c\u6069\u5fc3\u60f3\u3002</p><p>\u4ed6\u62c9\u4e86\u62c9\u8299\u857e\u72c4\uff0c\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u6084\u6084\u5c31\u5f80\u540e\u9000\u3002\u521a\u9000\u51fa\u4e24\u6b65\uff0c\u5c31\u542c\u5f97\u94ff\u5730\u4e00\u58f0\uff0c\u53ea\u89c1\u90a3\u4e2a\u75af\u72c2\u7684\u96d5\u5851\u5e08\u4e0d\u77e5\u4ece\u54ea\u91cc\u53d6\u51fa\u4e00\u628a\u5de8\u5927\u7684\u65a7\u5b50\uff0c\u4e00\u65a7\u5934\u5c31\u5288\u5728\u96d5\u5851\u7684\u8116\u9888\u90e8\u4f4d\uff0c\u5c06\u5973\u4eba\u7684\u5934\u7ed9\u780d\u4e86\u4e0b\u6765\uff0c\u5495\u565c\u5495\u565c\u6eda\u843d\u5728\u4e00\u65c1\u3002\u4ed6\u4f59\u6012\u672a\u6d88\uff0c\u6325\u7740\u65a7\u5b50\u7ee7\u7eed\u780d\u5288\uff0c\u7247\u523b\u95f4\u5c31\u5c06\u521a\u624d\u8f9b\u8f9b\u82e6\u82e6\u96d5\u6210\u7684\u4f5c\u54c1\u53d8\u6210\u4e86\u5e9f\u6e23\u3002</p><p>\u8299\u857e\u72c4\u5df2\u7ecf\u88ab\u4ed6\u8fd9\u75af\u72c2\u7684\u4e3e\u52a8\u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff0c\u5168\u8eab\u98a4\u6296\u3002\u743c\u6069\u4e5f\u6709\u4e9b\u5fc3\u60ca\u80c6\u6218\uff0c\u62c9\u7740\u5979\u9000\u5230\u5751\u8fb9\uff0c\u6b63\u51c6\u5907\u7528\u6d6e\u7a7a\u672f\u4e0a\u53bb\u3002\u811a\u8ddf\u5904\u4f20\u6765\u4e00\u58f0\u8106\u54cd\uff0c\u50cf\u662f\u8e29\u4e2d\u4e86\u4e00\u8282\u67af\u679d\u3002</p><p>\u201c\u5494\u5693\uff01\u201d</p><p>\u4eff\u4f5b\u7a81\u7136\u88ab\u60ca\u9192\u4e00\u822c\uff0c\u90a3\u4e2a\u75af\u5b50\u96d5\u5851\u5e08\u731b\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u77aa\u7740\u743c\u6069\u548c\u8299\u857e\u72c4\u4e24\u4eba\u3002\u4ed6\u62ab\u5934\u6563\u53d1\uff0c\u6d51\u8eab\u767d\u8272\u77f3\u5c51\uff0c\u53f3\u624b\u62ff\u7740\u96ea\u4eae\u7684\u5c0f\u5200\uff0c\u5de6\u624b\u63d0\u7740\u5de8\u5927\u7684\u6218\u65a7\uff0c\u9ed1\u8272\u7684\u773c\u73e0\u4e2d\u8840\u4e1d\u5982\u86db\u7f51\u5bc6\u5e03\uff0c\u51f6\u5149\u76f4\u9732\uff0c\u8981\u591a\u5413\u4eba\u5c31\u6709\u591a\u5413\u4eba\u3002\u5e78\u597d\u4ed6\u6234\u7740\u4e00\u53ea\u9ed1\u8272\u94c1\u9762\u5177\uff0c\u770b\u4e0d\u5230\u8138\uff0c\u4f46\u4e5f\u53ef\u4ee5\u60f3\u89c1\u5fc5\u5b9a\u662f\u808c\u8089\u626d\u66f2\uff0c\u72f0\u72de\u53ef\u6016\u4e4b\u81f3\u3002</p><p>\u7b49\u7b49\uff0c\u9ed1\u8272\u94c1\u9762\u5177\u2026\u2026</p><p>\u743c\u6069\u5012\u5438\u4e00\u53e3\u51b7\u6c14\uff0c\u5c06\u8299\u857e\u72c4\u62a4\u5728\u8eab\u540e\uff0c\u53f3\u624b\u5df2\u7ecf\u6084\u6084\u4ece\u6000\u4e2d\u6b21\u5143\u888b\u91cc\u62bd\u51fa\u4e86\u6cd5\u6756\u3002\u201c\u591c\u89c6\u8005\uff1f\u201d\u4ed6\u63a2\u8be2\u5730\u95ee\u3002</p><p>\u5bf9\u65b9\u7684\u56de\u7b54\u662f\u4e00\u58f0\u9707\u8033\u6b32\u804b\u7684\u6012\u543c\uff0c\u4eff\u4f5b\u9b54\u795e\u822c\u76f4\u51b2\u8fc7\u6765\u3002</p></article>"}}}]);
