(window.webpackJsonp=window.webpackJsonp||[]).push([[1648],{1671:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u8282 \u5173\u4e8e\u53d8\u88c5\u7684\u8ba8\u8bba",content:"<article><h2>\u7b2c\u4e09\u5341\u8282 \u5173\u4e8e\u53d8\u88c5\u7684\u8ba8\u8bba</h2><p>\u4e0b\u5348\u4e24\u70b9\u949f\uff0c\u743c\u6069\u4e00\u884c\u4eba\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u56e0\u4e3a\u662f\u516c\u6d3e\u51fa\u5dee\uff0c\u9634\u9b42\u57ce\u8c03\u4e86\u4e00\u5ea7\u6d6e\u7a7a\u8247\uff0c\u76f4\u63a5\u9001\u4ed6\u4eec\u5230\u4e86\u9ed1\u8def\u4e0a\uff0c\u514d\u53bb\u4e86\u5728\u6c99\u6f20\u91cc\u8270\u96be\u8dcb\u6d89\u7684\u8f9b\u82e6\u3002</p><p>\u65f6\u9694\u534a\u4e2a\u6708\uff0c\u9ed1\u8def\u4e0a\u660e\u663e\u7e41\u534e\u4e86\u4e0d\u5c11\uff0c\u6765\u6765\u5f80\u5f80\u7684\u5546\u65c5\u7edc\u7ece\u4e0d\u7edd\uff0c\u80cc\u7740\u884c\u56ca\uff0c\u7275\u7740\u9a86\u9a7c\uff0c\u4e00\u4e2a\u4e2a\u884c\u8272\u5306\u5306\u3002\u5728\u5411\u6563\u5854\u6797\u4f1a\u4ea4\u4e86\u4fdd\u62a4\u8d39\u4e4b\u540e\uff0c\u743c\u6069\u7b49\u4eba\u79df\u4e86\u51e0\u5339\u9a86\u9a7c\uff0c\u8ddf\u968f\u5927\u5bb6\u5411\u4e1c\u65b9\u8fdb\u53d1\u3002\u4e24\u5929\u4e4b\u540e\uff0c\u4ed6\u4eec\u8d70\u51fa\u4e86\u5927\u6c99\u6f20\uff0c\u8fdb\u5165\u5315\u9996\u7011\u5e03\u9547\u3002</p><p>\u5315\u9996\u7011\u5e03\u9547\u662f\u5315\u9996\u8c37\u7684\u9996\u5e9c\u6240\u5728\uff0c\u89c4\u6a21\u4eba\u53e3\u5374\u8fdc\u8fdc\u65e0\u6cd5\u4e0e\u9634\u9b42\u57ce\u76f8\u6bd4\uff0c\u5168\u9547\u4e0d\u8fc7\u51e0\u5343\u5c45\u6c11\uff0c\u800c\u4e14\u56e0\u4e3a\u8fed\u906d\u6218\u4e71\u7684\u7f18\u6545\u2014\u2014\u6700\u8fd1\u4e00\u6b21\u6218\u4e89\u5728\u4e94\u5e74\u524d\u521a\u7ed3\u675f\uff0c\u9887\u6709\u4e9b\u6b7b\u6c14\u6c89\u6c89\u3002\u8857\u9053\u4e0a\u7684\u884c\u4eba\u5927\u90e8\u5206\u90fd\u662f\u4e34\u65f6\u8def\u8fc7\u7684\u5916\u5730\u5546\u65c5\uff0c\u672c\u5730\u4eba\u53cd\u800c\u5c11\u89c1\u3002\u743c\u6069\u7b49\u4eba\u5165\u9547\u7684\u65f6\u5019\uff0c\u5929\u8272\u5df2\u66ae\uff0c\u968f\u4fbf\u627e\u4e86\u5bb6\u65c5\u5e97\u4f4f\u4e0b\uff0c\u665a\u4e0a\u4e5d\u70b9\u949f\u7684\u65f6\u5019\uff0c\u6240\u6709\u4eba\u805a\u96c6\u5230\u73ca\u5609\u7684\u623f\u95f4\u91cc\uff0c\u8ba8\u8bba\u4e0b\u4e00\u6b65\u7684\u884c\u7a0b\u3002</p><p>\u743c\u6069\u6b64\u6b21\u51fa\u884c\uff0c\u4e4b\u6240\u4ee5\u5982\u6b64\u6025\u5207\uff0c\u6700\u4e3b\u8981\u7684\u539f\u56e0\u5176\u5b9e\u662f\u4e3a\u4e86\u8eb2\u907f\u90a3\u4f4d\u591c\u89c6\u8005\u827e\u5361\u8bfa\uff0c\u76f4\u89c9\u544a\u8bc9\u4ed6\u5982\u679c\u518d\u548c\u8fd9\u5bb6\u4f19\u591a\u6253\u51e0\u6b21\u4ea4\u9053\u7684\u8bdd\uff0c\u4f1a\u5377\u5165\u5230\u96be\u4ee5\u60f3\u8c61\u7684\u9ebb\u70e6\u4e2d\u53bb\uff0c\u800c\u4e14\u4e0d\u4ec5\u4ec5\u662f\u4ed6\u7684\u9ebb\u70e6\uff0c\u73ca\u5609\u4e5f\u4f1a\u6709\u5927\u9ebb\u70e6\u3002\u56e0\u4e3a\u592a\u6025\u5207\uff0c\u6240\u4ee5\u51fa\u95e8\u524d\u5176\u5b9e\u6ca1\u6709\u8ba4\u771f\u8ba1\u5212\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u538b\u6839\u5c31\u6ca1\u6709\u8ba1\u5212\uff0c\u53ea\u662f\u60f3\u7740\u5148\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u8d70\u51fa\u5927\u6c99\u6f20\u518d\u8bf4\u3002\u81f3\u4e8e\u5982\u4f55\u62b5\u8fbe\u76ee\u7684\u5730\uff0c\u9009\u62e9\u54ea\u6761\u8def\u5f84\uff0c\u4f7f\u7528\u4f55\u79cd\u4ea4\u901a\u65b9\u5f0f\u2026\u2026\u8fd9\u5c31\u662f\u73b0\u5728\u8981\u8ba8\u8bba\u7684\u95ee\u9898\u4e86\u3002</p><p>\u5728\u8fd9\u4e24\u5929\u7684\u8def\u4e0a\uff0c\u743c\u6069\u5df2\u7ecf\u628a\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u4ed6\u7684\u6750\u6599\u770b\u4e86\u4e00\u904d\uff0c\u57fa\u672c\u4e86\u89e3\u4e86\u60c5\u51b5\u3002\u539f\u6765\u5728\u53bb\u5e74\u516b\u6708\u4efd\uff0c\u6851\u6bd4\u4e9a\u7684\u5927\u7edf\u9886\u80af\u5fb7\u745e\u514b\u6d3e\u4f7f\u8005\u524d\u6765\u9634\u9b42\u57ce\uff0c\u5bfb\u6c42\u7ed3\u76df\uff0c\u9634\u9b42\u57ce\u6b23\u7136\u540c\u610f\uff0c\u53cc\u65b9\u968f\u5373\u7b7e\u7f72\u4e86\u5408\u4f5c\u534f\u8bae\u3002\u80af\u5fb7\u745e\u514b\u5927\u7edf\u9886\u7684\u76ee\u7684\uff0c\u662f\u8981\u501f\u9634\u9b42\u57ce\u4e4b\u529b\u6765\u9547\u538b\u56fd\u5185\u5df2\u7ecf\u8822\u8822\u6b32\u52a8\u7684\u53db\u4e71\u52bf\u529b\uff0c\u800c\u9634\u9b42\u57ce\u63d0\u51fa\u7684\u4ea4\u6362\u6761\u4ef6\uff0c\u5219\u662f\u8981\u6c42\u83b7\u51c6\u5728\u6851\u6bd4\u4e9a\u6cbf\u6d77\u533a\u57df\u8fdb\u884c\u201c\u6355\u635e\u5de5\u4f5c\u201d\u3002\u8c01\u6599\u534f\u8bae\u521a\u521a\u7b7e\u8ba2\u7684\u7b2c\u4e8c\u5929\uff0c\u6851\u6bd4\u4e9a\u5185\u6218\u4fbf\u7206\u53d1\u4e86\uff0c\u80af\u5fb7\u745e\u514b\u9047\u523a\u8eab\u4ea1\uff0c\u6709\u4e24\u6d3e\u52bf\u529b\u89d2\u9010\u65b0\u4efb\u5927\u7edf\u9886\u7684\u5b9d\u5ea7\uff0c\u4e00\u6d3e\u9996\u9886\u662f\u80af\u5fb7\u745e\u514b\u7684\u513f\u5b50\uff0c\u7ef0\u53f7\u201c\u94f6\u9e26\u201d\uff0c\u53e6\u4e00\u6d3e\u9996\u9886\u5219\u662f\u80af\u5fb7\u745e\u514b\u7684\u5802\u59b9\uff0c\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\uff0c\u4ed6\u4eec\u5f7c\u6b64\u90fd\u5ba3\u5e03\u81ea\u5df1\u662f\u5408\u6cd5\u7684\u7ee7\u627f\u4eba\uff0c\u6307\u8d23\u5bf9\u65b9\u662f\u53db\u519b\u3002\u9634\u9b42\u57ce\u7ecf\u8fc7\u8c28\u614e\u7684\u89c2\u5bdf\u4e4b\u540e\uff0c\u6700\u540e\u9009\u62e9\u652f\u6301\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\uff0c\u5728\u53bb\u5e74\u5341\u6708\u4efd\u7684\u4e00\u573a\u5173\u952e\u6218\u5f79\u4e2d\uff0c\u745e\u74e6\u5170\u5927\u796d\u53f8\u7387\u9886\u4e09\u4f4d\u9634\u9b42\u738b\u5b50\u4eb2\u4e34\u524d\u7ebf\uff0c\u534f\u52a9\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\u7684\u519b\u961f\u51fb\u6e83\u4e86\u5bf9\u624b\u3002\u6b64\u540e\u53c8\u7ecf\u8fc7\u4e24\u4e2a\u4f59\u6708\u7684\u6218\u6597\uff0c\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\u7ec8\u4e8e\u6210\u529f\u5730\u767b\u4e0a\u4e86\u5927\u7edf\u9886\u7684\u5b9d\u5ea7\uff0c\u5e76\u4e14\u6293\u4f4f\u4e86\u201c\u94f6\u9e26\u201d\uff0c\u5c06\u4ed6\u7ede\u6b7b\u5728\u585e\u5c14\u521a\u7279\u57ce\u7684\u4e2d\u5fc3\u5e7f\u573a\u4e0a\uff0c\u7ed3\u675f\u4e86\u8fd9\u573a\u5185\u6218\u3002</p><p>\u5728\u6851\u6bd4\u4e9a\u5185\u6218\u671f\u95f4\uff0c\u9634\u9b42\u57ce\u5bf9\u6cbf\u6d77\u533a\u57df\u8fdb\u884c\u4e86\u4ed4\u7ec6\u641c\u5bfb\uff0c\u6700\u7ec8\u786e\u5b9a\u76ee\u6807\u5728\u897f\u90e8\u5927\u90fd\u5e02\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u9644\u8fd1\u7684\u6d77\u57df\u4e2d\uff0c\u743c\u6069\u6b64\u6b21\u51fa\u5dee\u7684\u76ee\u7684\u5730\u5c31\u662f\u90a3\u91cc\u3002\u81f3\u4e8e\u5230\u5e95\u662f\u8981\u5728\u6d77\u91cc\u201c\u6355\u635e\u201d\u4ec0\u4e48\uff0c\u8d44\u6599\u91cc\u5374\u6ca1\u8bf4\uff0c\u6216\u8bb8\u662f\u9634\u9b42\u57ce\u81ea\u5df1\u90fd\u6ca1\u80fd\u5341\u5206\u786e\u5b9a\u3002\u4f46\u8fd9\u65e0\u6240\u8c13\uff0c\u5230\u4e86\u5730\u65b9\u81ea\u7136\u5c31\u77e5\u9053\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u743c\u6069\u53e6\u6709\u81ea\u5df1\u7684\u4e8b\u60c5\u8981\u505a\u3002</p><p>\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5f53\u5e74\u6740\u6b7b\u743c\u6069\u7236\u6bcd\u7684\u4e24\u4e2a\u5deb\u5e08\u4e4b\u4e00\u7684\u5f17\u6717\u897f\u65af\u79d1\uff0c\u5982\u4eca\u5c31\u5728\u6851\u6bd4\u4e9a\u3002\u800c\u5728\u90a3\u53ea\u6863\u6848\u888b\u91cc\u6709\u4e00\u4efd\u540d\u5355\uff0c\u8bb0\u5f55\u7684\u662f\u9634\u9b42\u57ce\u6d3e\u9063\u5230\u6851\u6bd4\u4e9a\u7684\u4eba\u5458\uff0c\u4e5f\u5370\u8bc1\u4e86\u5e03\u96f7\u7eb3\u65af\u7684\u8bf4\u6cd5\u3002\u743c\u6069\u5e76\u4e0d\u662f\u6253\u7b97\u5c31\u76f4\u63a5\u53bb\u6740\u4eba\u62a5\u4ec7\uff0c\u4f46\u4e8b\u5173\u7236\u6bcd\u8eab\u6b7b\u7684\u771f\u76f8\uff0c\u603b\u8981\u67e5\u4e2a\u6e05\u695a\uff0c\u5f04\u4e2a\u5206\u6653\u3002\u8fd9\u4e5f\u5c31\u662f\u4ed6\u548c\u73ca\u5609\u8981\u53bb\u6851\u6bd4\u4e9a\u7684\u771f\u6b63\u539f\u56e0\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u5230\u5e95\u600e\u4e48\u53bb\u5462\uff1f</p><p>\u5728\u5ea7\u7684\u4e00\u7537\u56db\u5973\u4e94\u4e2a\u4eba\u4e2d\uff0c\u73ca\u5609\u662f\u4ece\u6ca1\u51fa\u8fc7\u95e8\u7684\uff0c\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u4e5f\u4e0d\u6d88\u8bf4\uff0c\u743c\u6069\u867d\u7136\u8fd1\u51e0\u5e74\u5728\u5916\u9762\u5954\u6ce2\uff0c\u4f46\u5bf9\u5730\u7406\u4e5f\u4e0d\u662f\u5f88\u901a\u6653\u2014\u2014\u56e0\u4e3a\u4ed6\u7edd\u5927\u591a\u6570\u65f6\u95f4\u90fd\u662f\u548c\u6885\u83f2\u65af\u5728\u4e00\u8d77\uff0c\u5723\u6b66\u58eb\u9605\u5386\u4e30\u5bcc\uff0c\u542c\u5979\u5b89\u6392\u884c\u7a0b\u5c31\u662f\u4e86\uff0c\u4e0d\u5fc5\u81ea\u5df1\u64cd\u5fc3\u3002\u6240\u4ee5\u5f04\u5230\u6700\u540e\uff0c\u53ea\u80fd\u7531\u9b45\u9b54\u838e\u73de\u514b\u6765\u505a\u8ba1\u5212\u3002</p><p>\u201c\u8981\u53bb\u6851\u6bd4\u4e9a\u7684\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\uff0c\u6211\u4eec\u5e94\u8be5\u8d70\u8fd9\u6761\u8def\uff0c\u201d\u838e\u73de\u514b\u7684\u4fee\u957f\u7389\u6307\u5728\u5730\u56fe\u4e0a\u79fb\u52a8\u7740\uff0c\u9010\u4e2a\u6307\u70b9\uff0c\u201c\u6211\u4eec\u96c7\u9a6c\u8f66\uff0c\u660e\u5929\u4ece\u8fd9\u91cc\u51fa\u53d1\uff0c\u6cbf\u7740\u6cf0\u65af\u6cb3\u4e00\u76f4\u5f80\u4e1c\uff0c\u7ecf\u8fc7\u6cf0\u65af\u5a01\u3001\u4fde\u62c9\u65af\uff0c\u8fdb\u5165\u8fdc\u5c71\u57ce\u3002\u7136\u540e\u6cbf\u7740\u6708\u6d77\u516c\u8def\u5f80\u5357\uff0c\u8fdb\u5165\u79d1\u66fc\u7d22\uff0c\u8f6c\u5230\u7f57\u8428\u7ef4\u5c14\u8def\uff0c\u518d\u7a7f\u8fc7\u963f\u6c99\u5df4\u6cb3\uff0c\u8fdb\u5165\u6851\u6bd4\u4e9a\u7684\u9996\u90fd\u6b27\u675c\u6797\u57ce\u3002\u7136\u540e\u6211\u4eec\u518d\u6cbf\u7740\u56fd\u5883\u5927\u9053\u4e00\u8def\u5411\u897f\u5357\u8d70\uff0c\u7ecf\u8fc7\u585e\u5c14\u5188\u7279\u57ce\uff0c\u6700\u540e\u62b5\u8fbe\u76ee\u7684\u5730\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u8fd9\u6761\u8def\u5462\uff1f\u201d\u743c\u6069\u6307\u7740\u5730\u56fe\u4e0a\u7684\u53e6\u4e00\u6761\u8def\u7ebf\uff0c\u63d0\u51fa\u5f02\u8bae\uff0c\u201c\u4e0d\u662f\u66f4\u8fd1\u4e00\u4e9b\u5417\u3002\u201d</p><p>\u838e\u73de\u514b\u7528\u4e00\u79cd\u201c\u4f60\u662f\u767d\u75f4\u201d\u7684\u773c\u795e\u770b\u7740\u4ed6\uff0c\u201c\u5982\u679c\u4f60\u8981\u8d70\u8fd9\u6761\u8def\uff0c\u90a3\u5c31\u5f97\u7a7f\u8fc7\u5927\u7247\u5927\u7247\u7684\u8fdc\u53e4\u68ee\u6797\u548c\u504f\u50fb\u5c71\u533a\uff0c\u8d70\u7684\u90fd\u662f\u7f8a\u80a0\u5c0f\u9053\uff0c\u53ea\u80fd\u9760\u6b65\u884c\uff0c\u800c\u4e14\u8fd8\u8981\u9014\u7ecf\u9634\u5f71\u8c37\u548c\u79d1\u7c73\u5c14\u2014\u2014\u4f60\u771f\u60f3\u8fd9\u4e48\u5e72\uff1f\u201d</p><p>\u201c\u5443\uff0c\u90a3\u7b97\u4e86\u3002\u201d</p><p>\u9634\u5f71\u8c37\u662f\u9b54\u6cd5\u5973\u795e\u7684\u5927\u672c\u8425\u4e4b\u4e00\uff0c\u805a\u96c6\u4e86\u4e00\u5927\u7968\u9009\u6c11\uff0c\u5176\u4e2d\u6709\u51e0\u4e2a\u5c31\u662f\u4e0a\u6b21\u6f5c\u5165\u9634\u9b42\u57ce\u7834\u574f\u9634\u5f71\u5927\u5e55\u8ba1\u5212\u7684\u53c2\u4e0e\u8005\uff1b\u79d1\u7c73\u5c14\u5219\u5728\u524d\u5e74\u521a\u521a\u548c\u9634\u9b42\u57ce\u53d1\u751f\u4e86\u4e00\u573a\u5267\u6218\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u7684\u7236\u4eb2\u5c31\u662f\u6218\u6b7b\u6b64\u5f79\u3002\u8fd9\u4e24\u4e2a\u5730\u65b9\u90fd\u5c5e\u4e8e\u654c\u65b9\u52bf\u529b\u8303\u56f4\uff0c\u743c\u6069\u7b49\u4eba\u867d\u7136\u53ef\u4ee5\u88c5\u6210\u6e38\u5ba2\u5feb\u901f\u901a\u8fc7\uff0c\u4f46\u4e07\u4e00\u8981\u662f\u8eab\u4efd\u88ab\u63ed\u7a7f\uff0c\u90a3\u5c31\u6709\u5927\u9ebb\u70e6\u3002\u8fd9\u79cd\u98ce\u9669\u8fd8\u662f\u4e0d\u5192\u7684\u597d\uff0c\u53cd\u6b63\u53c8\u4e0d\u6025\u7740\u8d76\u65f6\u95f4\u3002\u518d\u8bf4\u8d70\u838e\u73de\u514b\u6240\u8bf4\u7684\u8def\u7ebf\uff0c\u57fa\u672c\u4e0a\u90fd\u662f\u5bbd\u9614\u5927\u9053\uff0c\u53ef\u4ee5\u4e58\u9a6c\u8f66\u5954\u9a70\uff0c\u867d\u7136\u4ece\u5730\u56fe\u4e0a\u770b\u8ddd\u79bb\u8fdc\u4e9b\uff0c\u5176\u5b9e\u6240\u9700\u65f6\u95f4\u5e94\u8be5\u53cd\u800c\u4f1a\u5c11\u5f88\u591a\u3002</p><p>\u201c\u800c\u4e14\uff0c\u4f60\u4e0d\u662f\u8fd8\u6253\u7b97\u53bb\u4e00\u8d9f\u8ff7\u65af\u5353\u8bfa\u4e48\uff1f\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6b63\u597d\u6309\u6211\u8bf4\u7684\u8fd9\u6761\u8def\u7ebf\u8d70\uff0c\u5230\u4e86\u8fdc\u5c71\u57ce\u4e4b\u540e\u5f80\u5357\uff0c\u534a\u8def\u4e0a\u7a0d\u5fae\u7ed5\u4e00\u4e0b\uff0c\u5c31\u80fd\u5230\u8ff7\u65af\u5353\u8bfa\u4e86\uff0c\u6216\u8005\u56de\u6765\u7684\u65f6\u5019\u7ecf\u8fc7\u4e5f\u53ef\u4ee5\u3002\u201d</p><p>\u201c\u597d\u5427\u3002\u201d</p><p>\u838e\u73de\u514b\u63d0\u8bae\uff0c\u743c\u6069\u62cd\u677f\uff0c\u5c31\u6b64\u51b3\u5b9a\u4e86\u884c\u7a0b\u8def\u7ebf\u3002\u65f6\u5019\u4e5f\u4e0d\u65e9\uff0c\u8bf8\u4eba\u5404\u81ea\u56de\u623f\u95f4\u4f11\u606f\uff0c\u7b49\u5927\u5bb6\u90fd\u79bb\u5f00\u540e\uff0c\u73ca\u5609\u95ee\u743c\u6069\uff1a\u201c\u4f60\u8054\u7cfb\u827e\u5f25\u8587\u4e86\u5417\uff1f\u201d</p><p>\u201c\u6211\u8bd5\u8fc7\u51e0\u6b21\u4e86\uff0c\u4e00\u76f4\u8054\u7cfb\u4e0d\u4e0a\uff0c\u201d\u743c\u6069\u76b1\u7740\u7709\uff0c\u201c\u4f20\u8baf\u9b54\u6cd5\u88ab\u963b\u65ad\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u73ca\u5609\u6709\u4e9b\u62c5\u5fc3\u8d77\u6765\u3002</p><p>\u201c\u4e0d\u6e05\u695a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u5e94\u8be5\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u6709\u53ef\u80fd\u662f\u5979\u6b63\u5728\u795e\u6bbf\u4e4b\u7c7b\u7684\u5730\u65b9\uff0c\u6709\u6cd5\u672f\u7ed3\u754c\u963b\u6321\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u51c6\u5907\u5148\u53bb\u8ff7\u65af\u5353\u8bfa\u5417\uff1f\u201d</p><p>\u201c\u4ece\u6851\u6bd4\u4e9a\u56de\u6765\u518d\u53bb\u5427\uff0c\u5979\u73b0\u5728\u5e94\u8be5\u8fd8\u5728\u963f\u683c\u62c9\u9686\uff0c\u6216\u8005\u521a\u521a\u52a8\u8eab\u51fa\u53d1\uff0c\u8d77\u7801\u8981\u5341\u5929\u534a\u6708\u624d\u80fd\u5230\u8ff7\u65af\u5353\u8bfa\uff0c\u6211\u4eec\u73b0\u5728\u53bb\u4e86\u4e5f\u6ca1\u7528\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u59d0\u59d0\u597d\u50cf\u5f88\u60f3\u5ff5\u827e\u5f25\u8587\u7684\u6837\u5b50\uff1f\u201d\u743c\u6069\u7b11\u563b\u563b\u5730\u95ee\u3002</p><p>\u201c\u54fc\uff0c\u624d\u6ca1\u6709\u5462\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u5c31\u5e0c\u671b\u5979\u4e00\u76f4\u90fd\u4e0d\u56de\u6765\uff0c\u514d\u5f97\u548c\u6211\u62a2\u5f1f\u5f1f\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u6211\u8fd8\u633a\u671f\u5f85\u80fd\u628a\u59d0\u59d0\u548c\u5979\u4e00\u8d77\u2026\u2026\u201d</p><p>\u201c\u4f11\u60f3\uff01\u201d\u73ca\u5609\u607c\u7f9e\u6210\u6012\u5730\u7528\u957f\u7b1b\u5728\u4ed6\u5934\u4e0a\u6572\u4e86\u4e00\u8bb0\uff0c\u201c\u59d0\u59d0\u6ca1\u548c\u4f60\u7b97\u5e10\u5c31\u4e0d\u9519\u4e86\uff0c\u5c45\u7136\u8fd8\u6562\u5f97\u5bf8\u8fdb\u5c3a\u3002\u4eca\u665a\u7f5a\u4f60\u4e0d\u51c6\u4e0a\u6211\u7684\u5e8a\uff01\u201d</p><p>\u201c\u90a3\u6211\u7761\u54ea\uff1f\u201d</p><p>\u201c\u7761\u5730\u677f\u2014\u2014\u6216\u8005\u522b\u7761\u4e86\uff0c\u627e\u4f60\u7684\u827e\u5f25\u8587\u53bb\uff0c\u201d\u73ca\u5609\u6c14\u9f13\u9f13\u7684\uff0c\u201c\u771f\u662f\u7684\uff0c\u4e00\u5929\u5230\u665a\u5c31\u77e5\u9053\u5728\u6211\u9762\u524d\u63d0\u5979\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u521a\u624d\u660e\u660e\u662f\u4f60\u5148\u63d0\u8d77\u827e\u5f25\u8587\uff0c\u95ee\u6211\u6709\u6ca1\u6709\u8054\u7cfb\u4e0a\u5979\uff0c\u600e\u4e48\u53c8\u602a\u5230\u6211\u5934\u4e0a\u3002\u201d</p><p>\u201c\u5c31\u662f\u602a\u4f60\uff0c\u4e0d\u670d\u6c14\u554a\u2014\u2014\u8981\u4e0d\u662f\u4f60\u628a\u4eba\u5bb6\u52fe\u642d\u4e0a\u624b\uff0c\u4e0d\u5c31\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u5012\u4e5f\u662f\u3002\u201d</p><p>\u6709\u5173\u827e\u5f25\u8587\u7684\u8c08\u8bdd\u5230\u6b64\u7ed3\u675f\uff0c\u743c\u6069\u54c4\u4e86\u534a\u5929\uff0c\u7ec8\u4e8e\u6362\u5f97\u4e86\u73ca\u5609\u7684\u56de\u5fc3\u8f6c\u610f\uff0c\u4e0d\u9700\u8981\u5f53\u771f\u7761\u51b0\u51c9\u7684\u5730\u677f\u3002\u4e3a\u4e86\u8868\u793a\u6b49\u610f\uff0c\u4ed6\u5728\u5e8a\u4e0a\u683c\u5916\u5356\u529b\u4e86\u4e00\u70b9\uff0c\u7ed3\u679c\u5c31\u662f\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u65e5\u4e0a\u4e09\u7aff\u4e86\uff0c\u73ca\u5609\u624d\u8ff7\u8ff7\u7cca\u7cca\u5730\u4ece\u7f8e\u68a6\u4e2d\u9192\u6765\u3002</p><p>\u201c\u65e9\u5b89\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u201c\u65e9\uff0c\u5c0f\u5f1f\u2026\u2026\u73b0\u5728\u4ec0\u4e48\u65f6\u5019\u4e86\uff0c\u9633\u5149\u8fd9\u4e48\u4eae\uff1f\u201d</p><p>\u201c\u73b0\u5728\uff1f\u201d\u743c\u6069\u638f\u51fa\u9b54\u6cd5\u6c99\u6f0f\u770b\u4e86\u770b\uff0c\u201c\u5341\u70b9\u534a\u4e86\u3002\u201d</p><p>\u201c\u554a\u5440\u3002\u201d</p><p>\u73ca\u5609\u614c\u5fd9\u4ece\u88ab\u5b50\u91cc\u5750\u8d77\u6765\u2014\u2014\u7136\u540e\u53c8\u8fc5\u901f\u8eba\u4e0b\u53bb\uff0c\u56e0\u4e3a\u5979\u5168\u8eab\u8d64\u88f8\u7740\u4e00\u4e1d\u4e0d\u6302\u3002\u201c\u600e\u4e48\u8fd9\u4e48\u665a\u4e86\uff0c\u4f60\u4e5f\u4e0d\u53eb\u9192\u6211\uff0c\u201d\u5979\u62b1\u6028\u7740\uff0c\u201c\u4e0d\u662f\u8bf4\u4eca\u5929\u8981\u65e9\u70b9\u51fa\u53d1\u4e48\u2014\u2014\u5feb\u628a\u6211\u7684\u8863\u670d\u9012\u8fc7\u6765\u554a\u3002\u201d</p><p>\u201c\u770b\u59d0\u59d0\u7761\u5f97\u9999\uff0c\u5c31\u6ca1\u820d\u5f97\u53eb\u9192\uff0c\u201d\u743c\u6069\u4e00\u8fb9\u5c06\u73ca\u5609\u7684\u8863\u670d\u9012\u7ed9\u5979\uff0c\u4e00\u8fb9\u89e3\u91ca\uff0c\u201c\u518d\u8bf4\u8fd9\u6b21\u672c\u5c31\u662f\u51fa\u6765\u65c5\u6e38\uff0c\u65f6\u95f4\u53c8\u5145\u88d5\uff0c\u6ca1\u5fc5\u8981\u5306\u5306\u5fd9\u5fd9\u7684\uff0c\u665a\u70b9\u51fa\u53d1\u5c31\u665a\u70b9\u51fa\u53d1\uff0c\u65e0\u6240\u8c13\u5566\u3002\u201d</p><p>\u201c\u53ef\u662f\u4e5f\u592a\u665a\u4e86\u5440\uff0c\u201d\u73ca\u5609\u7a7f\u4e0a\u5185\u8863\uff0c\u7136\u540e\u5f00\u59cb\u7cfb\u886c\u886b\u7684\u7ebd\u6263\uff0c\u201c\u5979\u4eec\u5462\uff1f\u201d</p><p>\u201c\u6211\u8ba9\u5979\u4eec\u53bb\u96c7\u9a6c\u8f66\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f30\u8ba1\u4e5f\u5dee\u4e0d\u591a\u8be5\u56de\u6765\u4e86\u3002\u201d</p><p>\u4ed6\u9884\u8ba1\u7684\u6ca1\u9519\uff0c\u73ca\u5609\u521a\u521a\u7a7f\u4e0a\u8863\u670d\uff0c\u68b3\u6d17\u5b8c\u6bd5\uff0c\u838e\u73de\u514b\u548c\u83ab\u59ae\u5361\u59d0\u59b9\u4fe9\u5c31\u4ece\u5916\u9762\u56de\u6765\uff0c\u5979\u4eec\u96c7\u4e86\u4e24\u8f86\u9a6c\u8f66\uff0c\u56e0\u4e3a\u5168\u7a0b\u592a\u8fdc\uff0c\u6ca1\u6709\u54ea\u4e2a\u8f66\u592b\u613f\u610f\u63a5\u8fd9\u5355\u751f\u610f\uff0c\u6240\u4ee5\u53ea\u7ea6\u5b9a\u4ece\u672c\u9547\u5230\u8fdc\u5c71\u57ce\uff0c\u5230\u65f6\u5019\u743c\u6069\u7b49\u4eba\u518d\u91cd\u65b0\u96c7\u4f63\u9a6c\u8f66\u4fbf\u662f\u3002</p><p>\u6b64\u65f6\u662f\u65b0\u5e74\u521a\u8fc7\uff0c\u5feb\u5230\u4ef2\u51ac\u8282\uff0c\u51db\u51ac\u5c06\u901d\u672a\u901d\uff0c\u91ce\u5916\u5df2\u7ecf\u6709\u4e86\u51e0\u5206\u6625\u65e5\u6c14\u606f\u3002\u6cbf\u7740\u6cf0\u65af\u6cb3\u4e00\u8def\u4e1c\u5411\uff0c\u98ce\u666f\u662f\u6781\u597d\u7684\uff0c\u5927\u7247\u5927\u7247\u7684\u68ee\u6797\u548c\u7530\u91ce\uff0c\u8fdc\u5904\u8fde\u7ef5\u8d77\u4f0f\u7684\u5c71\u8109\uff0c\u6cbf\u9014\u5f00\u59cb\u5316\u51b0\u7684\u6cb3\u6d41\uff0c\u8fd9\u4e00\u5207\u90fd\u8db3\u4ee5\u4ee4\u4eba\u5fc3\u65f7\u795e\u6021\u3002\u73ca\u5609\u4ece\u5c0f\u5230\u5927\uff0c\u90fd\u662f\u5728\u9634\u9b42\u57ce\u91cc\u5ea6\u8fc7\uff0c\u6b64\u524d\u552f\u4e00\u4e00\u6b21\u51fa\u95e8\u7684\u7ecf\u5386\u662f\u4e0a\u6b21\u548c\u743c\u6069\u4e00\u8d77\u53bb\u585e\u5c14\uff0c\u90a3\u4e5f\u662f\u76f4\u63a5\u901a\u8fc7\u4f20\u9001\u95e8\u5230\u4e86\u585e\u5c14\u9996\u90fd\uff0c\u54ea\u91cc\u89c1\u8bc6\u8fc7\u8fd9\u7b49\u7f8e\u4e3d\u666f\u8272\uff0c\u4e0d\u7531\u5f97\u5341\u5206\u9ad8\u5174\u3002\u5728\u5e7d\u6697\u5730\u57df\u91cc\u5f85\u4e86\u4e00\u5e74\u7684\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u4e5f\u5dee\u4e0d\u591a\u3002\u5373\u4fbf\u662f\u838e\u73de\u514b\u8fd9\u79cd\u6740\u624b\uff0c\u4e5f\u60c5\u4e0d\u81ea\u7981\u5634\u89d2\u542b\u7b11\uff0c\u663e\u7136\u5fc3\u60c5\u751a\u4f73\u3002\u81f3\u4e8e\u743c\u6069\u4e48\uff0c\u88ab\u4e00\u7fa4\u7b11\u9010\u989c\u5f00\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u5305\u56f4\u7740\uff0c\u5fc3\u60c5\u4e0d\u597d\u624d\u662f\u5947\u602a\u4e86\u3002</p><p>\u4e00\u8def\u65e0\u4e8b\uff0c\u4e24\u5929\u4e4b\u540e\uff0c\u4ed6\u4eec\u62b5\u8fbe\u4e86\u8fdc\u5c71\u57ce\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u6574\u4e2a\u6708\u6d77\u5730\u533a\uff0c\u8fdc\u5c71\u57ce\u90fd\u7b97\u662f\u7b2c\u4e00\u7b49\u7684\u540d\u57ce\u4e86\uff0c\u5e38\u4f4f\u4eba\u53e3\u8d85\u8fc7\u4e09\u4e07\uff0c\u8fd8\u6709\u4e0d\u5c11\u4e8e\u4e94\u4e07\u7684\u6d41\u52a8\u4eba\u53e3\u3002\u5b83\u662f\u4e2a\u6e2f\u53e3\u57ce\u5e02\uff0c\u56e0\u4e3a\u6b63\u5904\u4e8e\u4ea4\u901a\u8981\u9053\u4e0a\uff0c\u6d77\u8def\u9646\u8def\u8fd0\u8f93\u90fd\u65b9\u4fbf\uff0c\u8d38\u6613\u975e\u5e38\u53d1\u8fbe\uff0c\u5e02\u573a\u7e41\u8363\uff0c\u6709\u5b8c\u5907\u7684\u653f\u5e9c\u673a\u6784\u3001\u519b\u961f\u3001\u76d1\u72f1\u4ee5\u53ca\u6210\u6587\u6cd5\u5178\u3002\u4e0d\u8fc7\u8fdc\u5c71\u57ce\u662f\u4e2a\u79cd\u65cf\u6b67\u89c6\u6781\u5176\u4e25\u91cd\u7684\u5730\u65b9\uff0c\u53ea\u6709\u4eba\u7c7b\u624d\u53d7\u6cd5\u5f8b\u7684\u5e73\u7b49\u4fdd\u62a4\uff0c\u6240\u6709\u975e\u4eba\u7c7b\u79cd\u65cf\uff0c\u4e0d\u7ba1\u662f\u7cbe\u7075\u662f\u77ee\u4eba\u662f\u4f8f\u5112\u662f\u5730\u7cbe\u8fd8\u662f\u5176\u4ed6\u4ec0\u4e48\u7684\uff0c\u751a\u81f3\u5305\u62ec\u90aa\u9b54\uff0c\u4e00\u6982\u90fd\u89c6\u4e3a\u5974\u96b6\u3002\u56e0\u4e3a\u8fd9\u79cd\u7f18\u6545\uff0c\u743c\u6069\u53ea\u597d\u628a\u838e\u73de\u514b\u6682\u65f6\u6536\u8fdb\u5b9d\u77f3\u91cc\uff0c\u5426\u5219\u4ed6\u5c31\u8981\u53bb\u6709\u5173\u90e8\u95e8\u505a\u4e2a\u767b\u8bb0\uff0c\u82b1\u4e8c\u5341\u91d1\u5e01\u7684\u201c\u5de5\u672c\u8d39\u201d\u4e70\u4e2a\u523b\u6709\u8fdc\u5c71\u5e02\u653f\u5e9c\u5fbd\u7ae0\u4ee5\u53ca\u6570\u5b57\u7f16\u53f7\u7684\u94f6\u9879\u5708\u7ed9\u838e\u73de\u514b\u6234\u4e0a\uff0c\u6807\u5fd7\u5979\u7684\u6240\u6709\u6743\u5f52\u5c5e\u2014\u2014\u82e5\u4e0d\u5982\u6b64\uff0c\u838e\u73de\u514b\u5c31\u4f1a\u88ab\u89c6\u4e3a\u65e0\u4e3b\u5974\u96b6\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u51b2\u4e0a\u6765\u62a2\u593a\uff0c\u751a\u81f3\u4f1a\u88ab\u6293\u5230\u89d2\u6597\u573a\u91cc\u53bb\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u5176\u5b9e\u4ece\u743c\u6069\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u4ed6\u8fd8\u771f\u4e0d\u4ecb\u610f\u7ed9\u838e\u73de\u514b\u5957\u4e2a\u9879\u5708\uff0c\u60f3\u60f3\u4e5f\u522b\u6709\u4e00\u756a\u98ce\u60c5\uff0c\u5982\u679c\u518d\u7cfb\u6761\u94fe\u5b50\u5c31\u66f4\u4e0d\u9519\uff0c\u53ef\u4ee5\u7275\u7740\u5979\u4e0a\u8857\u3002\u4f46\u73ca\u5609\u5728\u65c1\u8fb9\uff0c\u8fd9\u79cd\u90aa\u6076\u7684\u4e8b\u60c5\u2026\u2026\u8fd8\u662f\u5148\u7b97\u4e86\u5427\u3002</p><p>\u201c\u4e0d\u8fc7\u4e00\u4e2a\u4ec0\u4e48\u9b54\u6cd5\u6548\u679c\u90fd\u6ca1\u6709\u7684\u94f6\u9879\u5708\uff0c\u5c45\u7136\u8981\u5356\u4e8c\u5341\u91d1\u5e01\uff0c\u8fd8\u53f7\u79f0\u662f\u5de5\u672c\u8d39\u2026\u2026\u8fd9\u53ef\u771f\u662f\u591f\u9ed1\u7684\u554a\u3002\u201d</p><p>\u201c\u6ca1\u529e\u6cd5\uff0c\u201d\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u6709\u516c\u6743\u529b\u7684\u653f\u5e9c\u603b\u662f\u6bd4\u8f83\u725b\u561b\u3002\u201d</p><p>\u5165\u57ce\u7684\u65f6\u5019\uff0c\u536b\u5175\u7684\u76d8\u67e5\u9887\u4e3a\u4e25\u683c\uff0c\u8857\u9053\u4e0a\u4e5f\u80fd\u770b\u5230\u4e00\u961f\u961f\u5168\u526f\u6b66\u88c5\u7684\u58eb\u5175\u6765\u5f80\uff0c\u611f\u89c9\u50cf\u662f\u51fa\u4e86\u4ec0\u4e48\u5927\u4e8b\uff0c\u6216\u8005\u5c06\u8981\u51fa\u4ec0\u4e48\u5927\u4e8b\u4f3c\u7684\u3002\u4e0d\u8fc7\u53cd\u6b63\u81ea\u5df1\u4e5f\u5c31\u662f\u8def\u8fc7\u6b64\u5730\u800c\u5df2\uff0c\u743c\u6069\u867d\u7136\u5bdf\u89c9\u5230\u4e86\u5f02\u72b6\uff0c\u5374\u5e76\u6ca1\u5728\u610f\uff0c\u5f88\u5feb\u629b\u4e4b\u8111\u540e\uff0c\u4e0d\u53bb\u591a\u60f3\u3002</p><p>\u5230\u4e86\u57ce\u4e2d\u7684\u8f66\u9a6c\u884c\uff0c\u743c\u6069\u8fd9\u6b21\u96c7\u4e86\u4e00\u8f86\u6700\u8c6a\u534e\u6700\u5bbd\u655e\u7684\u516d\u8f6e\u5927\u9a6c\u8f66\uff0c\u7ea6\u5b9a\u540e\u5929\u4e0b\u5348\u4e24\u70b9\u949f\u51fa\u53d1\u3002\u73ca\u5609\u7b49\u4eba\u96be\u5f97\u51fa\u95e8\u65c5\u6e38\uff0c\u6ca1\u5fc5\u8981\u5306\u5306\u8d76\u8def\uff0c\u7559\u4e24\u5929\u65f6\u95f4\u4f11\u606f\u4f11\u606f\u3001\u901b\u901b\u8857\u8d2d\u8d2d\u7269\u4e5f\u662f\u5e94\u8be5\u7684\uff0c\u8fdc\u5c71\u57ce\u53ef\u662f\u6574\u4e2a\u6708\u6d77\u5730\u533a\u6570\u4e8c\u6570\u4e09\u7684\u90fd\u5e02\uff0c\u6bd4\u9634\u9b42\u57ce\u7e41\u534e\u597d\u51e0\u500d\uff0c\u81f3\u4e8e\u524d\u9762\u7ecf\u8fc7\u7684\u4ec0\u4e48\u5315\u9996\u7011\u5e03\u9547\u3001\u6cf0\u65af\u5a01\u9547\u3001\u4fde\u62c9\u65af\u9547\u4e4b\u6d41\uff0c\u66f4\u662f\u538b\u6839\u6ca1\u6cd5\u6bd4\u3002\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u4e0d\u8981\u9519\u8fc7\u3002</p><p>\u5b9a\u597d\u9a6c\u8f66\uff0c\u5df2\u7ecf\u5dee\u4e0d\u591a\u662f\u5348\u9910\u65f6\u95f4\uff0c\u4e00\u884c\u4eba\u4fbf\u671d\u57ce\u5357\u8fdb\u53d1\uff0c\u56e0\u4e3a\u542c\u8bf4\u90a3\u91cc\u6709\u5bb6\u6d77\u9c9c\u9910\u9986\u5f88\u4e0d\u9519\u3002\u4e00\u8def\u4e0a\u743c\u6069\u548c\u73ca\u5609\u624b\u7275\u7740\u624b\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u8ddf\u5728\u65c1\u8fb9\uff0c\u8d70\u4e86\u6bb5\u8def\uff0c\u73ca\u5609\u7a81\u7136\u50cf\u662f\u60f3\u8d77\u4ec0\u4e48\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u5657\u54e7\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u59d0\u59d0\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u201d\u73ca\u5609\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u5c0f\u5f1f\uff0c\u4f60\u89c9\u4e0d\u89c9\u5f97\u6211\u4eec\u56db\u4e2a\u4eba\u8fd9\u6837\u8d70\u7740\u6709\u70b9\u5947\u602a\u3002\u201d</p><p>\u201c\u5947\u602a\uff1f\u201d\u743c\u6069\u770b\u770b\u81ea\u5df1\uff0c\u518d\u770b\u770b\u522b\u4eba\uff0c\u201c\u6ca1\u4ec0\u4e48\u5947\u602a\u554a\u3002\u201d</p><p>\u201c\u5f88\u5947\u602a\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5c31\u50cf\u662f\u4e24\u5bf9\u53cc\u80de\u80ce\u4e00\u8d77\u51fa\u95e8\u4f3c\u7684\u3002\u201d</p><p>\u4e24\u5bf9\u53cc\u80de\u80ce\uff1f\u54e6\uff0c\u8fd9\u4e48\u8bf4\u5012\u4e5f\u6709\u9053\u7406\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u81ea\u7136\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u53cc\u80de\u80ce\uff0c\u800c\u743c\u6069\u548c\u73ca\u5609\u867d\u7136\u4e0d\u662f\u53cc\u80de\u80ce\uff0c\u4f46\u5374\u957f\u5f97\u5f88\u50cf\uff0c\u5c24\u5176\u662f\u7709\u773c\u90e8\u5206\u66f4\u795e\u4f3c\uff0c\u4e3a\u6b64\u743c\u6069\u5c0f\u65f6\u5019\u6ca1\u5c11\u88ab\u73ca\u5609\u5632\u7b11\u8fc7\uff0c\u8bf4\u4ed6\u957f\u5f97\u50cf\u5973\u5b69\u5b50\u3002</p><p>\u201c\u5514\uff0c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e5f\u786e\u5b9e\u554a\u2026\u2026\u4e0d\u5982\u8fd9\u6837\u5427\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u5f00\u73a9\u7b11\uff0c\u201c\u4ee5\u540e\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u4f60\u7a7f\u7537\u88c5\u597d\u4e86\uff0c\u8fd9\u6837\u5c31\u662f\u4e00\u5bf9\u53cc\u80de\u80ce\u5144\u5f1f\u5e26\u7740\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\uff0c\u5b8c\u7f8e\u914d\u5bf9\u3002\u201d</p><p>\u73ca\u5609\u9732\u51fa\u4e0d\u6000\u597d\u610f\u7684\u7b11\u5bb9\uff0c\u201c\u8fd9\u6837\u554a\uff0c\u5012\u4e5f\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u6211\u8fd9\u91cc\u6709\u4e2a\u66f4\u597d\u7684\u4e3b\u610f\u5462\uff0c\u4f60\u8981\u4e0d\u8981\u542c\u542c\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u59d0\u59d0\u4f60\u8bf4\u3002\u201d</p><p>\u201c\u4e0e\u5176\u6211\u7a7f\u7537\u88c5\uff0c\u4e0d\u5982\u4f60\u7a7f\u5973\u88c5\u5427\uff0c\u201d\u73ca\u5609\u683c\u683c\u7b11\u7740\uff0c\u201c\u8fd9\u6837\u5c31\u662f\u4e24\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u4e00\u8d77\u53bb\u901b\u8857\uff0c\u4fdd\u8bc1\u80fd\u5438\u5f15\u4e00\u5927\u5806\u4eba\uff0c\u8d70\u5230\u54ea\u91cc\u90fd\u662f\u7126\u70b9\u2014\u2014\u600e\u4e48\u6837\uff0c\u5c0f\u5f1f\uff0c\u6211\u8fd9\u4e2a\u60f3\u6cd5\u4e0d\u9519\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\uff0c\u6211\u4e00\u5411\u4f4e\u8c03\uff0c\u4e0d\u559c\u6b22\u5f15\u4eba\u6ce8\u610f\uff0c\u8fd8\u662f\u7b97\u4e86\u5427\u3002\u800c\u4e14\u6211\u626e\u5973\u5b69\u5b50\u4e5f\u4e0d\u50cf\u2014\u2014\u201d</p><p>\u201c\u8c01\u8bf4\u7684\uff0c\u4f60\u8fd9\u4e48\u79c0\u6c14\uff0c\u626e\u5973\u5b69\u5b50\u6700\u5408\u9002\u4e0d\u8fc7\u4e86\uff0c\u53ea\u8981\u6362\u8eab\u8863\u670d\uff0c\u8138\u4e0a\u7a0d\u5fae\u5316\u5316\u5986\uff0c\u6d82\u70b9\u5507\u5f69\uff0c\u4fdd\u8bc1\u662f\u4e2a\u5927\u7f8e\u4eba\uff0c\u201d\u73ca\u5609\u4f3c\u4e4e\u88ab\u81ea\u5df1\u7684\u60f3\u6cd5\u52fe\u8d77\u4e86\u5174\u8da3\uff0c\u5174\u81f4\u52c3\u52c3\u5730\u8ba1\u5212\u7740\uff0c\u201c\u80f8\u53e3\u518d\u57ab\u70b9\u68c9\u82b1\u2014\u2014\u751a\u81f3\u4e0d\u57ab\u4e5f\u884c\uff0c\u53cd\u6b63\u73b0\u5728\u6d41\u884c\u5e73\u80f8\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u73ca\u5609\u59d0\u59d0\uff0c\u4ed6\u7684\u5589\u7ed3\u600e\u4e48\u529e\u5462\uff1f\u201d\u8299\u857e\u72c4\u4e5f\u53c2\u4e0e\u8fdb\u6765\u8ba8\u8bba\uff0c\u201c\u522b\u4eba\u4e00\u770b\u5c31\u9732\u9985\u4e86\u5440\u3002\u201d</p><p>\u201c\u5514\uff0c\u662f\u4e86\uff0c\u5589\u7ed3\u5012\u662f\u6709\u70b9\u9ebb\u70e6\uff0c\u4e0d\u8fc7\u53ef\u4ee5\u7cfb\u6761\u56f4\u5dfe\u906e\u4f4f\uff0c\u53cd\u6b63\u73b0\u5728\u662f\u51ac\u5929\uff0c\u201d\u73ca\u5609\u7b11\u772f\u772f\u5730\u8bf4\uff0c\u201c\u5c0f\u5f1f\uff0c\u6211\u4eec\u56de\u53bb\u5c31\u8bd5\u8bd5\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u2026\u2026\u6211\u624d\u4e0d\u8981\u53d8\u88c5\u6210\u5973\u4eba\uff01\u201d</p><p>\u201c\u597d\u561b\u597d\u561b\uff0c\u5c31\u8bd5\u8bd5\u770b\uff0c\u5c31\u8bd5\u4e00\u6b21\u597d\u4e0d\u597d\uff1f\u201d\u73ca\u5609\u6293\u7740\u4ed6\u7684\u624b\u81c2\u6447\u6643\u7740\uff0c\u50cf\u5c0f\u5973\u5b69\u4e00\u6837\u6492\u5a07\uff0c\u201c\u771f\u7684\u5f88\u6709\u8da3\u5462\uff0c\u597d\u60f3\u770b\u5230\u5f1f\u5f1f\u4f60\u7a7f\u5973\u88c5\u7684\u6837\u5b50\u2026\u2026\u4e00\u5b9a\u5f88\u53ef\u7231\u5bf9\u4e0d\u5bf9\uff1f\u201d\u5979\u8f6c\u8138\u95ee\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u3002</p><p>\u201c\u55ef\u55ef\uff0c\u5bf9\u5bf9\u3002\u201d\u53cc\u80de\u80ce\u4e00\u8d77\u70b9\u5934\uff0c\u5c31\u8fde\u4e00\u5411\u51b7\u51b0\u51b0\u7684\u8299\u8389\u5a05\u8138\u4e0a\u90fd\u9732\u51fa\u7b11\u610f\uff0c\u663e\u7136\u89c9\u5f97\u73ca\u5609\u7684\u8fd9\u4e2a\u4e3b\u610f\u975e\u5e38\u6709\u8da3\u3002</p><p>\u201c\u4e0d\u884c\u5c31\u662f\u4e0d\u884c\u5566\uff01\u201d\u743c\u6069\u6c14\u6025\u8d25\u574f\u5730\u8bf4\uff0c\u201c\u4f60\u4eec\u8fd9\u5e2e\u5bb6\u4f19\u662f\u600e\u4e48\u56de\u4e8b\u2026\u2026\u603b\u4e4b\u6211\u7edd\u5bf9\u4e0d\u8981\u626e\u5973\u4eba\uff01\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u7edd\u5bf9\u4e0d\u8981\uff01\u201d</p><p>\u201c\u6ca1\u6709\u4efb\u4f55\u5546\u91cf\u7684\u4f59\u5730\uff1f\u201d</p><p>\u201c\u534a\u70b9\u6ca1\u6709\uff01\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u53ea\u597d\u7b97\u4e86\uff0c\u201d\u73ca\u5609\u8c03\u76ae\u5730\u7728\u4e86\u7728\u5927\u773c\u775b\uff0c\u201c\u6211\u672c\u6765\u8fd8\u60f3\u8bf4\uff0c\u5982\u679c\u4f60\u613f\u610f\u7a7f\u5973\u88c5\u7ed9\u6211\u4eec\u770b\u7684\u8bdd\uff0c\u6211\u5c31\u5076\u5c14\u8003\u8651\u4e00\u4e0b\u4f60\u90a3\u5929\u7684\u63d0\u8bae\u3002\u201d</p><p>\u201c\u6211\u4ec0\u4e48\u63d0\u8bae\uff1f\u54ea\u5929\uff1f\u201d</p><p>\u201c\u5c31\u662f\u521a\u5230\u5315\u9996\u7011\u5e03\u9547\u90a3\u5929\u665a\u4e0a\u554a\uff0c\u201d\u73ca\u5609\u63d0\u9192\uff0c\u201c\u4f60\u8bf4\u60f3\u628a\u6211\u548c\u827e\u5f25\u8587\u4e00\u8d77\u2026\u2026\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff1f\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d</p><p>\u201c\u90a3\u2026\u2026\u6211\u518d\u8003\u8651\u8003\u8651\u5427\u3002\u201d</p><p>\u4e09\u4e2a\u5973\u5b69\u5b50\u4e00\u8d77\u7b11\u4e86\u8d77\u6765\uff0c\u743c\u6069\u65e0\u5948\u5730\u8038\u8038\u80a9\uff0c\u201c\u8d70\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5148\u53bb\u5403\u996d\uff0c\u6211\u5feb\u8981\u997f\u6b7b\u4e86\u3002\u201d</p><p>\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u7a81\u7136\u8def\u8fb9\u4e24\u4e2a\u4eba\u7684\u5bf9\u8bdd\u98d8\u8fdb\u8033\u6735\uff0c\u201c\u4ea8\u745e\uff0c\u542c\u8bf4\u4e86\u4e48\uff0c\u6c38\u805a\u5c9b\u7684\u7cbe\u7075\u4eec\u5df2\u7ecf\u5168\u9762\u653b\u5360\u8ff7\u65af\u5353\u8bfa\u4e86\u3002\u201d</p></article>"}}}]);
