(window.webpackJsonp=window.webpackJsonp||[]).push([[1650],{1673:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e8c\u8282 \u6563\u63d0\u519b\u7684\u7edf\u5e05",content:"<article><h2>\u7b2c\u4e09\u5341\u4e8c\u8282 \u6563\u63d0\u519b\u7684\u7edf\u5e05</h2><p>\u4fde\u62c9\u65af\u9547\u9677\u843d\uff0c\u5176\u5b9e\u7b97\u4e0d\u5f97\u4ec0\u4e48\u51fa\u4e4e\u610f\u6599\u7684\u4e8b\u60c5\uff0c\u867d\u7136\u5386\u53f2\u4e0a\u5b83\u4e5f\u66fe\u7ecf\u662f\u8457\u540d\u7684\u575a\u57ce\u8981\u585e\uff0c\u4f46\u5728\u6563\u63d0\u5c14\u5821\u548c\u8fdc\u5c71\u57ce\u591a\u6b21\u5f80\u6765\u7684\u62c9\u952f\u6218\u4e2d\uff0c\u65e9\u5c31\u5df2\u7ecf\u88ab\u7834\u574f\u5f97\u4e0d\u6210\u6837\u5b50\u4e86\u3002\u5982\u4eca\u5168\u9547\u5c45\u6c11\u4e0d\u8db3\u4e24\u5343\uff0c\u9a7b\u519b\u4ec5\u6709\u4e09\u767e\uff0c\u57ce\u5899\u4e5f\u7b97\u4e0d\u5f97\u575a\u56fa\uff0c\u53ea\u662f\u8d77\u5230\u4e00\u4e2a\u524d\u54e8\u7684\u4f5c\u7528\uff0c\u539f\u672c\u5c31\u6ca1\u6307\u671b\u5b83\u771f\u80fd\u6321\u4f4f\u6563\u63d0\u5c14\u5821\u7684\u519b\u961f\u3002\u95ee\u9898\u662f\u5b83\u4e5f\u672a\u514d\u9677\u843d\u5f97\u592a\u5feb\u4e86\u70b9\uff0c\u4ece\u65f6\u95f4\u4e0a\u5224\u65ad\uff0c\u51e0\u4e4e\u662f\u88ab\u654c\u4eba\u4e00\u9f13\u800c\u4e0b\uff0c\u6839\u672c\u6ca1\u6709\u505a\u51fa\u534a\u70b9\u6709\u6548\u62b5\u6297\uff0c\u8fd9\u5728\u4ee5\u524d\u7684\u4e03\u6b21\u6218\u4e89\u4e2d\u662f\u4ece\u672a\u51fa\u73b0\u8fc7\u7684\u3002\u5982\u679c\u6839\u636e\u4ee5\u5f80\u7684\u7ecf\u9a8c\uff0c\u5b83\u5e94\u8be5\u80fd\u62d6\u4f4f\u6563\u63d0\u4eba\u4e00\u81f3\u4e24\u5929\u624d\u5bf9\u3002</p><p>\u6563\u63d0\u5c14\u5821\u7684\u519b\u961f\uff0c\u5389\u5bb3\u5230\u4e86\u8fd9\u79cd\u7a0b\u5ea6\u5417\uff1f</p><p>\u6bd5\u7adf\u5df2\u7ecf\u5341\u5e74\u672a\u7ecf\u6218\u4e89\uff0c\u8fdc\u5c71\u57ce\u4e0a\u4e0b\u90fd\u6709\u4e9b\u61c8\u6020\uff0c\u5bf9\u4e8e\u6563\u63d0\u4eba\u518d\u5ea6\u5165\u4fb5\u8fd9\u4ef6\u4e8b\uff0c\u521d\u65f6\u662f\u8fc7\u5206\u8f7b\u89c6\uff0c\u4e0d\u628a\u5bf9\u65b9\u653e\u5728\u773c\u91cc\uff0c\u5982\u4eca\u5374\u53c8\u8fc7\u5206\u7d27\u5f20\u8d77\u6765\uff0c\u6574\u5ea7\u57ce\u5e02\u987f\u65f6\u53d8\u5f97\u5fd9\u5fd9\u4e71\u4e71\u3002\u4fde\u62c9\u65af\u65e2\u7136\u5df2\u7ecf\u9677\u843d\uff0c\u6563\u63d0\u4eba\u4fbf\u53ef\u4ee5\u76f4\u5954\u8fdc\u5c71\uff0c\u5982\u679c\u4ed6\u4eec\u884c\u519b\u901f\u5ea6\u591f\u5feb\u7684\u8bdd\uff0c\u660e\u5929\u4e2d\u5348\u5c31\u53ef\u4ee5\u62b5\u8fbe\u57ce\u4e0b\uff0c\u5148\u5934\u90e8\u961f\u53ef\u80fd\u66f4\u5feb\u3002\u800c\u8fdc\u5c71\u57ce\u56e0\u4e3a\u6b64\u524d\u7684\u9519\u8bef\u4f30\u7b97\uff0c\u8fd8\u6ca1\u6709\u5b8c\u5168\u505a\u597d\u8fce\u6218\u51c6\u5907\u5462\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u4e5f\u540c\u6837\u662f\u4e2a\u7cdf\u7cd5\u7684\u6d88\u606f\uff0c\u4ed6\u539f\u672c\u662f\u6253\u7b97\u4e00\u65e9\u5c31\u53bb\u8f66\u9a6c\u884c\uff0c\u52a0\u4ed8\u4e00\u4efd\u94b1\uff0c\u8981\u9a6c\u8f66\u7acb\u523b\u52a8\u8eab\u51fa\u53d1\uff0c\u65e9\u65e9\u79bb\u5f00\u8fd9\u4e2a\u662f\u975e\u4e4b\u5730\u3002\u7ed3\u679c\u521a\u6d17\u6f31\u5b8c\u6bd5\u51c6\u5907\u51fa\u65c5\u5e97\uff0c\u5c31\u5f97\u77e5\u5e02\u653f\u5e9c\u53d1\u5e03\u4e86\u63d0\u524d\u6212\u4e25\u7684\u7d27\u6025\u547d\u4ee4\uff0c\u5168\u57ce\u5c01\u95ed\uff0c\u6ca1\u6709\u57ce\u4e3b\u7279\u8bb8\u4ee4\u7684\u8bdd\u4e0d\u51c6\u51fa\u4e5f\u4e0d\u51c6\u8fdb\u3002\u81f3\u4e8e\u6212\u4e25\u4ee4\u4f55\u65f6\u53d6\u6d88\uff0c\u90a3\u5c31\u8981\u770b\u6563\u63d0\u4eba\u4f55\u65f6\u64a4\u8d70\u4e86\u2014\u2014\u5bf9\u4e8e\u8fd9\u70b9\uff0c\u8fdc\u5c71\u4eba\u8fd8\u662f\u6709\u4e9b\u4fe1\u5fc3\u7684\uff0c\u4ed6\u4eec\u76f4\u63a5\u5c31\u9ed8\u8ba4\u5bf9\u65b9\u8fd9\u6b21\u5165\u4fb5\u80af\u5b9a\u8fd8\u662f\u65e0\u529f\u800c\u8fd4\u3002</p><p>\u201c\u771f\u9ebb\u70e6\uff0c\u600e\u4e48\u529e\uff1f\u201d</p><p>\u6240\u6709\u4eba\u805a\u96c6\u5230\u4e00\u8d77\u5546\u8bae\uff0c\u4fbf\u662f\u838e\u73de\u514b\u4e5f\u4ece\u5b9d\u77f3\u4e2d\u88ab\u91ca\u653e\u51fa\u6765\uff0c\u53cd\u6b63\u8eb2\u5728\u623f\u95f4\u91cc\uff0c\u4e0d\u865e\u5916\u4eba\u770b\u89c1\u3002\u4f46\u60f3\u6765\u60f3\u53bb\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u50fb\u5c45\u6574\u4e2a\u5927\u9646\u7684\u6781\u4e1c\uff0c\u4e00\u76f4\u68a6\u60f3\u7740\u897f\u6269\uff0c\u8fdc\u5c71\u5e02\u662f\u4ed6\u4eec\u5728\u6708\u6d77\u5730\u533a\u7684\u5934\u53f7\u636e\u70b9\uff0c\u65e2\u662f\u8d38\u6613\u90fd\u5e02\uff0c\u53c8\u662f\u4ea4\u901a\u67a2\u7ebd\uff0c\u5317\u53ef\u5c01\u9501\u6563\u5854\u6797\u4f1a\uff0c\u5357\u53ef\u6e17\u900f\u79d1\u66fc\u7d22\u548c\u6851\u6bd4\u4e9a\uff0c\u4e1c\u53ef\u901a\u8fc7\u6c34\u8def\u8fde\u63a5\u585e\u5c14\u672c\u571f\uff0c\u897f\u53ef\u547c\u5e94\u76df\u53cb\u9634\u9b42\u57ce\uff0c\u8fd9\u7b49\u6218\u7565\u8981\u5730\uff0c\u4ed6\u4eec\u81ea\u7136\u662f\u5927\u529b\u7ecf\u8425\uff0c\u57ce\u4e2d\u904d\u5e03\u7b3c\u7f69\u7740\u5404\u79cd\u5e9e\u5927\u7684\u9b54\u6cd5\u9635\uff0c\u5e73\u65f6\u8fd8\u4e0d\u89c9\u5f97\uff0c\u6212\u4e25\u4ee4\u4e00\u4e0b\u8fbe\uff0c\u6240\u6709\u6cd5\u9635\u5c3d\u6570\u542f\u52a8\uff0c\u5929\u7a7a\u4e2d\u5168\u662f\u5bc6\u5bc6\u9ebb\u9ebb\u95ea\u70c1\u751f\u8f89\u7684\u9b54\u6cd5\u7b26\u6587\uff0c\u90a3\u79cd\u65e0\u5f62\u7684\u51dd\u91cd\u5a01\u538b\u611f\uff0c\u5c31\u7b97\u662f\u73ca\u5609\u8fd9\u79cd\u53ea\u662f\u7565\u7565\u4f1a\u70b9\u620f\u6cd5\u7684\u666e\u901a\u4eba\u90fd\u80fd\u6e05\u695a\u610f\u8bc6\u5f97\u5230\u3002\u867d\u7136\u8fd8\u6ca1\u6709\u5177\u4f53\u5206\u6790\u8fd9\u4e9b\u6cd5\u9635\u7684\u6548\u679c\uff0c\u4f46\u81f3\u5c11\u4f20\u9001\u672f\u662f\u80af\u5b9a\u88ab\u5c01\u9501\u4e86\u3002</p><p>\u201c\u770b\u6765\u5982\u679c\u60f3\u8981\u63d0\u524d\u51fa\u57ce\uff0c\u5c31\u53ea\u80fd\u6309\u89c4\u77e9\u6765\uff0c\u627e\u90a3\u4e2a\u4ec0\u4e48\u57ce\u4e3b\u7b7e\u7279\u8bb8\u4ee4\u4e86\u3002\u201d</p><p>\u57ce\u4e3b\u5927\u4eba\u65e5\u7406\u4e07\u673a\uff0c\u4e0d\u662f\u968f\u4fbf\u4ec0\u4e48\u4eba\u90fd\u80fd\u89c1\u7684\uff0c\u5c24\u5176\u5982\u4eca\u8fd9\u79cd\u6218\u4e89\u65f6\u523b\u3002\u4f46\u743c\u6069\u597d\u6b79\u6709\u9634\u9b42\u57ce\u7279\u4f7f\u7684\u8eab\u4efd\uff0c\u9634\u9b42\u57ce\u548c\u585e\u5c14\u53c8\u662f\u76df\u53cb\uff0c\u585e\u5c14\u5219\u53c8\u662f\u8fdc\u5c71\u5e02\u7684\u9760\u5c71\u652f\u67f1\u2014\u2014\u53ea\u5e0c\u671b\u501f\u52a9\u8fd9\u5c42\u5173\u7cfb\uff0c\u57ce\u4e3b\u5927\u4eba\u80fd\u591f\u9ad8\u770b\u51e0\u5206\u5427\u3002</p><p>\u4e8b\u5b9e\u4f3c\u4e4e\u8bc1\u660e\u4e86\u8fd9\u79cd\u5224\u65ad\uff0c\u743c\u6069\u4e0a\u95e8\u6c42\u89c1\uff0c\u901a\u62a5\u8eab\u4efd\uff0c\u7acb\u523b\u5c31\u88ab\u8bf7\u4e86\u8fdb\u53bb\u3002\u4e00\u4e2a\u5934\u53d1\u82b1\u767d\u3001\u5927\u8179\u4fbf\u4fbf\u7684\u80d6\u8001\u4eba\u70ed\u60c5\u5730\u8fce\u4e0a\u6765\uff0c\u5475\u5475\u5927\u7b11\u7740\uff0c\u5f20\u5f00\u53cc\u81c2\u5c31\u60f3\u8981\u62e5\u62b1\u4ed6\uff0c\u743c\u6069\u88ab\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u8eab\u4e3a\u5ba2\u4eba\u53c8\u662f\u6709\u6c42\u4e8e\u4eba\uff0c\u4e0d\u597d\u8eb2\u907f\uff0c\u201c\u9601\u4e0b\uff0c\u60a8\u2026\u2026\u201d</p><p>\u201c\u597d\u4e45\u4e0d\u89c1\u554a\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u80d6\u8001\u4eba\u7b11\u7740\uff0c\u201c\u554a\uff0c\u60a8\u4e0d\u8bb0\u5f97\u6211\u4e86\u5417\uff0c\u5f17\u96f7\u65af\uff0c\u201d\u4ed6\u63d0\u9192\uff0c\u201c\u6211\u4eec\u524d\u5e74\u5728\u827e\u5c14\u5854\u4f2f\u89c1\u8fc7\u7684\u3002\u201d</p><p>\u827e\u5c14\u5854\u4f2f\u662f\u585e\u5c14\u7684\u9996\u90fd\uff0c\u743c\u6069\u786e\u5b9e\u53bb\u8fc7\uff0c\u4f46\u6ca1\u4ec0\u4e48\u719f\u4eba\u554a\uff0c\u6700\u719f\u7684\u51db\u5df2\u7ecf\u88ab\u62b1\u56de\u5bb6\uff0c\u5176\u6b21\u719f\u7684\u8428\u624e\u65af\u5766\u662f\u4e2a\u8001\u5deb\u5996\u3002\u5514\uff0c\u827e\u5c14\u5854\u4f2f\u81ea\u5df1\u4e00\u5171\u53bb\u8fc7\u4e24\u6b21\uff0c\u7b2c\u4e00\u6b21\u662f\u968f\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8c08\u5224\u7ed3\u76df\uff0c\u7b2c\u4e8c\u6b21\u662f\u53bb\u4e3a\u70bc\u91d1\u5b66\u9662\u8d2d\u4e70\u5974\u96b6\u2014\u2014\u603b\u7b97\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u80d6\u8001\u5934\u4e0d\u5c31\u662f\u5f53\u65f6\u90a3\u4e2a\u5974\u96b6\u5546\u4e48\uff0c\u4ed6\u5bb6\u91cc\u8fd8\u6709\u4e2a\u4f8d\u5973\u53eb\u83f2\u5a05\uff0c\u662f\u5df4\u5c14\u5b50\u55e3\uff0c\u88ab\u743c\u6069\u63a8\u5012\uff0c\u7ed3\u679c\u591c\u5973\u58eb\u6084\u6084\u964d\u4e34\u2026\u2026</p><p>\u201c\u554a\u554a\uff0c\u539f\u6765\u662f\u5f17\u96f7\u65af\u5148\u751f\u3002\u201d</p><p>\u65e2\u7136\u9047\u4e0a\u4e86\u719f\u4eba\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\uff0c\u53cc\u65b9\u7b80\u5355\u5bd2\u6684\uff0c\u743c\u6069\u5f97\u77e5\u539f\u6765\u8fd9\u4f4d\u5974\u96b6\u8d29\u5b50\u5c31\u662f\u5982\u4eca\u8fdc\u5c71\u57ce\u7684\u57ce\u4e3b\uff0c\u53bb\u5e74\u4e94\u6708\u4efd\u624d\u521a\u521a\u4e0a\u4efb\u7684\u3002\u8fd9\u4e5f\u5f88\u6b63\u5e38\uff0c\u5f17\u96f7\u65af\u53ef\u662f\u827e\u5c14\u5854\u4f2f\u603b\u7763\u7684\u53d4\u7236\uff0c\u672c\u6765\u5c31\u662f\u6709\u5b98\u65b9\u80cc\u666f\u7684\u4eba\u7269\uff0c\u6447\u8eab\u4e00\u53d8\u4ece\u5546\u4eba\u53d8\u6210\u57ce\u4e3b\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u53ea\u6015\u7ed9\u6240\u6709\u5974\u96b6\u6234\u9879\u5708\uff0c\u76d6\u516c\u7ae0\u4e0a\u724c\u53f7\u7684\u4e3b\u610f\uff0c\u4e5f\u662f\u8fd9\u4f4d\u524d\u4efb\u5974\u96b6\u8d29\u5b50\u7684\u624b\u7b14\u5427\u3002</p><p>\u5ba2\u5957\u534a\u664c\uff0c\u743c\u6069\u8bf4\u660e\u6765\u610f\uff0c\u5f17\u96f7\u65af\u6ee1\u53e3\u7b54\u5e94\uff0c\u8fd9\u4e0e\u4ed6\u800c\u8a00\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u7f62\u4e86\u3002\u56e0\u4e3a\u65f6\u5019\u5df2\u7ecf\u4e0d\u65e9\uff0c\u4ed6\u9080\u8bf7\u743c\u6069\u5171\u8fdb\u5348\u9910\uff0c\u88ab\u5a49\u8a00\u8c22\u7edd\u4e86\u3002\u743c\u6069\u4e00\u5fc3\u60f3\u7740\u8d76\u7d27\u79bb\u5f00\u8fd9\u5730\u65b9\uff0c\u54ea\u6709\u529f\u592b\u966a\u4ed6\u5403\u5348\u9910\u2014\u2014\u5c31\u7b97\u8981\u5171\u8fdb\u5348\u9910\uff0c\u4ed6\u4e5f\u5f53\u7136\u662f\u56de\u53bb\u627e\u8eab\u8fb9\u7684\u7f8e\u4eba\u513f\u3002</p><p>\u5f17\u96f7\u65af\u89c1\u4ed6\u53bb\u610f\u575a\u51b3\uff0c\u4e5f\u4e0d\u591a\u505a\u633d\u7559\uff0c\u53eb\u4e86\u79d8\u4e66\u8fc7\u6765\u6b63\u51c6\u5907\u7b7e\u53d1\u624b\u4ee4\uff0c\u7a81\u7136\u95f4\u4e00\u4e2a\u536b\u5175\u614c\u614c\u5f20\u5f20\u5730\u8dd1\u8fdb\u6765\uff0c\u201c\u57ce\u4e3b\u5927\u4eba\uff0c\u5927\u4e8b\u4e0d\u5999\uff0c\u201d\u4ed6\u6c14\u5598\u5401\u5401\u5730\u8bf4\uff0c\u201c\u6563\u63d0\u4eba\u8ddd\u79bb\u672c\u57ce\u5df2\u7ecf\u4e0d\u5230\u56db\u5341\u91cc\u4e86\u3002\u201d</p><p>\u5f17\u96f7\u65af\u5927\u60ca\u5931\u8272\uff0c\u201c\u600e\u4e48\u4f1a\u6765\u5f97\u8fd9\u4e48\u5feb\uff1f\u201d</p><p>\u8fd9\u4e2a\u4e16\u754c\u7684\u56db\u5341\u91cc\uff0c\u5927\u7ea6\u76f8\u5f53\u4e8e\u5730\u7403\u4e0a\u7684\u516d\u5341\u4f59\u516c\u91cc\uff0c\u5982\u679c\u4ee5\u9a91\u5175\u5168\u901f\u5954\u88ad\u7684\u8bdd\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u662f\u56db\u4e94\u4e2a\u5c0f\u65f6\u7684\u8def\u7a0b\uff0c\u8fd9\u53ef\u6bd4\u9884\u8ba1\u5f97\u53c8\u8981\u5feb\u8bb8\u591a\u2014\u2014\u672c\u6765\u4f30\u7b97\u6563\u63d0\u4eba\u81f3\u5c11\u8981\u660e\u5929\u624d\u80fd\u5230\u5462\u3002\u800c\u4e14\u8fd9\u6837\u4e00\u6765\uff0c\u743c\u6069\u7b49\u4eba\u4e5f\u522b\u60f3\u51fa\u57ce\u4e86\uff0c\u4ece\u8def\u7ebf\u4e0a\u770b\uff0c\u80af\u5b9a\u4f1a\u906d\u9047\u6563\u63d0\u5c14\u5821\u7684\u519b\u961f\u3002</p><p>\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u522b\u65e0\u4ed6\u6cd5\uff0c\u5f17\u96f7\u65af\u8981\u53bb\u57ce\u5899\u4e0a\u89c2\u770b\u654c\u60c5\uff0c\u90e8\u7f72\u9632\u5fa1\uff0c\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u4e3b\u52a8\u8981\u6c42\u548c\u4ed6\u540c\u53bb\u3002\u65e2\u7136\u907f\u4e0d\u5f00\uff0c\u53ea\u597d\u53bb\u9762\u5bf9\uff0c\u591a\u4e86\u89e3\u4e9b\u60c5\u51b5\uff0c\u8d77\u7801\u5fc3\u91cc\u6709\u5e95\u3002\u987a\u4fbf\u6d3e\u4e2a\u4ec6\u4eba\u53bb\u65c5\u5e97\u7ed9\u73ca\u5609\u5979\u4eec\u9001\u4fe1\uff0c\u514d\u5f97\u62c5\u5fc3\u3002</p><p>\u4e00\u8def\u4e0a\u5f17\u96f7\u65af\u663e\u5f97\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u5b8c\u5168\u6ca1\u6709\u90a3\u4e9b\u666e\u901a\u5e02\u6c11\u4eec\u7684\u8f7b\u677e\u81ea\u82e5\u3002\u743c\u6069\u5fc3\u4e2d\u5fe7\u8651\uff0c\u51fa\u8a00\u8be2\u95ee\uff0c\u624d\u77e5\u9053\u539f\u6765\u8fdc\u5c71\u57ce\u7684\u524d\u4efb\u57ce\u4e3b\uff0c\u4e43\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u539f\u5851\u80fd\u5b66\u6d3e\u9996\u5e2d\u8ff0\u5c14\u7684\u4eb2\u4fe1\u90e8\u4e0b\u3002\u4e0a\u6b21\u7ea2\u888d\u9996\u5e2d\u4eec\u5185\u8ba7\uff0c\u8ff0\u5c14\u7ad9\u9519\u4e86\u961f\u88ab\u6740\uff0c\u4ed6\u7684\u52bf\u529b\u4e5f\u968f\u4e4b\u88ab\u6e05\u7b97\u3002\u8fdc\u5c71\u57ce\u7684\u524d\u4efb\u57ce\u4e3b\u56e0\u4e3a\u548c\u8ff0\u5c14\u5173\u7cfb\u592a\u8fd1\uff0c\u6ca1\u6cd5\u53ca\u65f6\u8f6c\u53d8\u7acb\u573a\uff0c\u6216\u8005\u8bf4\u8f6c\u53d8\u7acb\u573a\u4e86\u4e0a\u9762\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u6700\u7ec8\u88ab\u8c03\u56de\u56fd\u5185\uff0c\u5b89\u6392\u4e86\u4e2a\u95f2\u804c\uff0c\u53e6\u6d3e\u5f17\u96f7\u65af\u524d\u6765\u63a5\u4efb\u3002\u5f17\u96f7\u65af\u662f\u4e2a\u6210\u529f\u7684\u5546\u4eba\uff0c\u505a\u751f\u610f\u5c24\u5176\u662f\u505a\u5974\u96b6\u8d38\u6613\u662f\u4e00\u628a\u597d\u624b\uff0c\u4f46\u6253\u4ed7\u5c31\u662f\u5b8c\u5168\u7684\u5916\u884c\u3002\u4ed6\u4e00\u4e2a\u7a7a\u964d\u57ce\u4e3b\uff0c\u521d\u6765\u4e4d\u5230\uff0c\u672c\u5c31\u6ca1\u6709\u4ec0\u4e48\u6839\u57fa\uff0c\u518d\u52a0\u4e0a\u4eba\u4e8b\u66f4\u66ff\uff0c\u6d3e\u7cfb\u6e05\u6d17\uff0c\u524d\u4efb\u57ce\u4e3b\u7684\u4eb2\u4fe1\u7eb7\u7eb7\u79bb\u53bb\u2014\u2014\u751a\u81f3\u6709\u4e0d\u5c11\u8f6c\u6295\u6563\u5854\u6797\u4f1a\u7684\uff0c\u6240\u4ee5\u6b64\u65f6\u7684\u8fdc\u5c71\u57ce\uff0c\u5176\u5b9e\u53ef\u4ee5\u7b97\u662f\u5386\u53f2\u4e0a\u6700\u865a\u5f31\u7684\u65f6\u671f\u3002</p><p>\u201c\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u90a3\u51e0\u4e2a\u519b\u961f\u7684\u5c06\u9886\u6211\u90fd\u6307\u6325\u4e0d\u52a8\uff0c\u201d\u5f17\u96f7\u65af\u5411\u743c\u6069\u62b1\u6028\uff0c\u201c\u56e4\u79ef\u7684\u519b\u68b0\u4e5f\u4e0d\u662f\u5f88\u8db3\uff0c\u6000\u7591\u6709\u4eba\u79c1\u81ea\u2026\u2026\u672c\u6765\u9884\u8ba1\u662f\u4eca\u5e74\u56db\u6708\u4efd\u4ece\u672c\u571f\u8865\u5145\u4e00\u6279\uff0c\u8c01\u6599\u6563\u63d0\u4eba\u6070\u597d\u8d76\u5728\u8fd9\u65f6\u5019\u8fdb\u653b\u2014\u2014\u592a\u8fc7\u5206\u4e86\uff0c\u73b0\u5728\u4ef2\u51ac\u8282\u90fd\u8fd8\u6ca1\u8fc7\u5462\u3002\u201d</p><p>\u4ef2\u51ac\u8282\u6ca1\u8fc7\u7b97\u4ec0\u4e48\uff0c\u6211\u8fd8\u77e5\u9053\u5386\u53f2\u4e0a\u67d0\u4e2a\u56fd\u5bb6\u4e13\u95e8\u8d81\u5bf9\u624b\u8fc7\u5723\u8bde\u8282\u7684\u65f6\u5019\u7a81\u88ad\u5462\uff0c\u743c\u6069\u5fc3\u91cc\u60f3\u7740\uff0c\u53e3\u4e2d\u5374\u5b89\u6170\u4ed6\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u81f3\u5c11\u57ce\u91cc\u7684\u9b54\u6cd5\u9632\u5fa1\u8fd8\u662f\u5f88\u5b8c\u5584\u7684\uff0c\u57ce\u5899\u4e5f\u5f88\u575a\u56fa\u3002\u201d</p><p>\u201c\u90a3\u5012\u662f\uff0c\u201d\u63d0\u8d77\u8fd9\u70b9\uff0c\u5f17\u96f7\u65af\u795e\u60c5\u8f7b\u677e\u4e86\u4e9b\uff0c\u201c\u8981\u8bba\u9b54\u6cd5\u6c34\u51c6\uff0c\u6563\u63d0\u4eba\u548c\u6211\u4eec\u585e\u5c14\u2014\u2014\u5f53\u7136\u4e5f\u5305\u62ec\u8d35\u56fd\u2014\u2014\u6839\u672c\u5c31\u4e0d\u662f\u4e00\u4e2a\u6863\u6b21\u4e0a\u7684\uff0c\u6240\u4ee5\u4e5f\u786e\u5b9e\u6ca1\u5fc5\u8981\u62c5\u5fc3\u5566\uff0c\u54c8\u54c8\uff0c\u201d\u4ed6\u7b11\u4e86\u4e24\u58f0\uff0c\u201c\u800c\u4e14\u6211\u4eec\u53c8\u4e0d\u8fdb\u653b\uff0c\u53ea\u8981\u80fd\u6321\u4f4f\u5c31\u591f\u3002\u6211\u5df2\u7ecf\u548c\u56fd\u5185\u53d6\u5f97\u8054\u7cfb\uff0c\u4ed6\u4eec\u6b63\u5728\u8c03\u96c6\u63f4\u519b\uff0c\u9884\u8ba1\u4e09\u5929\u540e\u5c31\u80fd\u5230\u8fbe\u3002\u201d</p><p>\u743c\u6069\u9644\u548c\u7740\u7b11\u4e86\u51e0\u58f0\uff0c\u201c\u5bf9\u4e86\uff0c\u542c\u8bf4\u6563\u63d0\u5c14\u4eba\u8fd9\u6b21\u7684\u7edf\u5e05\u662f\u4f4d\u5973\u5b50\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u800c\u4e14\u8fd8\u633a\u5e74\u8f7b\u7684\uff0c\u624d\u4e8c\u5341\u591a\u5c81\u5427\uff0c\u771f\u4e0d\u77e5\u9053\u5085\u4f50\u5c14\u662f\u4e0d\u662f\u8111\u5b50\u7a81\u7136\u70e7\u574f\u4e86\u3002\u201d</p><p>\u5085\u4f50\u5c14\u662f\u6563\u5854\u6797\u4f1a\u7684\u9886\u8896\uff0c\u66b4\u653f\u4e0e\u6050\u60e7\u4e4b\u795e\u73ed\u6069\u7684\u9009\u6c11\uff0c\u8fd9\u79cd\u4eba\u8111\u5b50\u7a81\u7136\u70e7\u574f\u7684\u6982\u7387\u2026\u2026\u6709\u662f\u6709\uff0c\u5927\u6982\u548c\u743c\u6069\u73b0\u5728\u80fd\u8f70\u6740\u9b54\u6cd5\u5973\u795e\u7684\u6982\u7387\u5dee\u4e0d\u591a\u5427\u3002</p><p>\u4ed6\u539f\u672c\u60f3\u63d0\u9192\u5f17\u96f7\u65af\u4e0d\u8981\u8f7b\u654c\uff0c\u60f3\u4e86\u60f3\u53c8\u95ed\u4e0a\u5634\u3002\u5927\u654c\u5f53\u524d\uff0c\u81ea\u5df1\u6bd5\u7adf\u53ea\u662f\u5ba2\u4eba\uff0c\u53c8\u4e0d\u8ba4\u8bc6\u5bf9\u65b9\u7684\u7edf\u5e05\uff0c\u6beb\u65e0\u4e86\u89e3\uff0c\u4e5f\u4e0d\u8fc7\u662f\u731c\u6d4b\u4e4b\u8bcd\u7f62\u4e86\uff0c\u5f80\u8f7b\u91cc\u8bf4\u662f\u80e1\u8a00\u4e71\u8bed\uff0c\u5f80\u91cd\u91cc\u8bf4\u8fd9\u5c31\u662f\u5728\u52a8\u6447\u519b\u5fc3\uff0c\u4f55\u5fc5\u81ea\u627e\u9ebb\u70e6\u5462\u3002</p><p>\u5230\u4e86\u57ce\u5899\u4e0a\uff0c\u53ea\u89c1\u4e00\u7247\u5fd9\u4e71\uff0c\u519b\u5b98\u4eec\u53d1\u53f7\u65bd\u4ee4\uff0c\u5927\u58f0\u5475\u65a5\uff0c\u5f80\u5f80\u5f7c\u6b64\u6307\u4ee4\u51b2\u7a81\uff0c\u4ee5\u81f3\u4e8e\u58eb\u5175\u4eec\u65e0\u6240\u9002\u4ece\uff0c\u50cf\u6ca1\u5934\u82cd\u8747\u4e00\u6837\u4e71\u649e\u3002\u5b88\u57ce\u5668\u68b0\u5012\u662f\u5df2\u7ecf\u57fa\u672c\u5230\u4f4d\uff0c\u519b\u961f\u4e5f\u96c6\u5408\u5b8c\u6bd5\u3002\u770b\u5230\u5f17\u96f7\u65af\u8fc7\u6765\uff0c\u4e00\u540d\u8138\u4e0a\u6709\u5947\u602a\u523a\u9752\u7684\u94f6\u7532\u5c06\u519b\u4e0a\u524d\u884c\u793c\uff0c\u795e\u60c5\u4e2d\u9887\u6709\u51e0\u5206\u5028\u50b2\u3002</p><p>\u5f17\u96f7\u65af\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u4ed6\u4e8e\u519b\u4e8b\u662f\u5b8c\u5168\u7684\u5916\u884c\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u95e8\u9053\uff0c\u968f\u53e3\u52c9\u52b1\u51e0\u53e5\uff0c\u95ee\u8d77\u654c\u60c5\u3002\u539f\u6765\u6563\u63d0\u4eba\u653b\u7834\u4fde\u62c9\u65af\u9547\u540e\uff0c\u5927\u519b\u7f13\u7f13\u63a8\u8fdb\uff0c\u5374\u6d3e\u4e86\u4e00\u652f\u524d\u950b\u6025\u901f\u5954\u6765\uff0c\u6cbf\u9014\u51fb\u6740\u65a5\u5019\uff0c\u6467\u6bc1\u54e8\u6240\uff0c\u4ee5\u81f3\u4e8e\u5230\u4e86\u79bb\u57ce\u4e0d\u8db3\u56db\u5341\u91cc\u7684\u65f6\u5019\uff0c\u624d\u6709\u4e00\u540d\u65a5\u5019\u62fc\u6b7b\u4f20\u56de\u6d88\u606f\u3002</p><p>\u201c\u4e0d\u8fc7\u9601\u4e0b\u65e0\u9700\u5bb3\u6015\uff0c\u201d\u94f6\u7532\u5c06\u519b\u7684\u53e3\u6c14\u4e2d\u9887\u6709\u51e0\u5206\u5632\u8bbd\u610f\u5473\uff0c\u201c\u654c\u519b\u7684\u524d\u950b\u90e8\u961f\u662f\u5b8c\u5168\u7684\u9a91\u5175\uff0c\u6570\u91cf\u5f88\u5c11\uff0c\u53c8\u6ca1\u6709\u643a\u5e26\u4efb\u4f55\u8f8e\u91cd\u5668\u68b0\uff0c\u5c31\u7b97\u662f\u5230\u4e86\u57ce\u4e0b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u6765\u9001\u6b7b\u7f62\u4e86\u3002\u9601\u4e0b\u4e0d\u5982\u5148\u56de\u5b98\u90b8\uff0c\u9759\u5019\u4f73\u97f3\u4fbf\u662f\u3002\u201d</p><p>\u5f17\u96f7\u65af\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u8138\u4e0a\u5374\u534a\u70b9\u6ca1\u6709\u53d1\u4f5c\uff0c\u53c8\u95ee\u4e86\u4e9b\u95ee\u9898\u3002\u94f6\u7532\u5c06\u519b\u4f3c\u4e4e\u4e0d\u8010\uff0c\u52c9\u5f3a\u966a\u4e86\u7247\u523b\uff0c\u4fbf\u627e\u4e2a\u501f\u53e3\u79bb\u53bb\u3002\u743c\u6069\u51b7\u773c\u65c1\u89c2\uff0c\u53ea\u89c9\u5df1\u65b9\u5f62\u52bf\u8d8a\u53d1\u4e0d\u5999\uff0c\u867d\u7136\u4ed6\u4e0d\u61c2\u519b\u4e8b\u4e5f\u660e\u767d\uff0c\u9a84\u5175\u608d\u5c06\uff0c\u4e0a\u4e0b\u79bb\u5fc3\uff0c\u6307\u6325\u6df7\u4e71\uff0c\u8fd9\u79cd\u519b\u961f\u5c31\u7b97\u88c5\u5907\u518d\u597d\uff0c\u6b66\u827a\u518d\u7cbe\uff0c\u90a3\u4e5f\u662f\u4e0d\u884c\u7684\u3002</p><p>\u6b63\u5982\u6b64\u60f3\u7740\uff0c\u5374\u89c1\u8fdc\u5904\u6709\u9ed1\u538b\u538b\u4e00\u7247\u4eba\u5f71\u6d8c\u6765\uff0c\u8d77\u521d\u8fd8\u4ee5\u4e3a\u662f\u654c\u519b\uff0c\u540e\u6765\u8fd1\u4e86\u624d\u53d1\u73b0\u662f\u4e00\u7fa4\u6276\u8001\u643a\u5e7c\u7684\u96be\u6c11\u3002\u4ed6\u4eec\u5230\u5f97\u57ce\u4e0b\uff0c\u548c\u5b88\u519b\u5c06\u9886\u7b54\u4e86\u51e0\u53e5\u8bdd\uff0c\u539f\u6765\u90fd\u662f\u5c45\u4f4f\u5728\u8fdc\u5c71\u57ce\u5468\u56f4\u6751\u5e84\u7684\u519c\u6c11\uff0c\u542c\u95fb\u6563\u63d0\u4eba\u5165\u4fb5\uff0c\u6240\u4ee5\u60f3\u8fdb\u57ce\u907f\u7978\u3002</p><p>\u6240\u8c13\u5175\u8fc7\u5982\u6d17\uff0c\u65e0\u5904\u4e0d\u7136\uff0c\u6563\u5854\u6797\u4f1a\u66f4\u4e0d\u662f\u4ec0\u4e48\u5584\u826f\u7ec4\u7ec7\u3002\u96be\u6c11\u8981\u6c42\u8fdb\u57ce\u907f\u4e71\uff0c\u4e5f\u662f\u5408\u60c5\u5408\u7406\u7684\u8981\u6c42\uff0c\u5b88\u57ce\u7684\u5c06\u519b\u60f3\u4e86\u7247\u523b\uff0c\u4e5f\u4e0d\u6c47\u62a5\u5f17\u96f7\u65af\uff0c\u7adf\u7136\u4fbf\u76f4\u63a5\u4e0b\u4ee4\u6253\u5f00\u57ce\u95e8\u3002\u603b\u7b97\u4ed6\u8fd8\u6709\u51e0\u5206\u8c28\u614e\uff0c\u6015\u8fd9\u4e9b\u4eba\u91cc\u6df7\u8fdb\u4e86\u654c\u519b\uff0c\u6240\u4ee5\u57ce\u95e8\u53ea\u5f00\u4e86\u72ed\u72ed\u4e00\u9053\uff0c\u8ba9\u96be\u6c11\u4eec\u4e00\u4e2a\u4e00\u4e2a\u5730\u8fdb\u6765\uff0c\u53c8\u9648\u91cd\u5175\u4e8e\u4e24\u65c1\u76d1\u89c6\uff0c\u4e00\u65e6\u53d1\u73b0\u8c01\u6709\u5f02\u52a8\u4fbf\u76f4\u63a5\u65a9\u6740\u3002\u82b1\u4e86\u534a\u5929\u529f\u592b\uff0c\u603b\u7b97\u628a\u6240\u6709\u7684\u96be\u6c11\u90fd\u653e\u8fdb\u4e86\u57ce\uff0c\u6b63\u51c6\u5907\u5173\u95ed\u57ce\u95e8\uff0c\u5c31\u89c1\u8fdc\u65b9\u5730\u5e73\u7ebf\u4e0a\u70df\u5c18\u5927\u8d77\uff0c\u906e\u5929\u853d\u65e5\uff0c\u5730\u9762\u4eff\u4f5b\u64c2\u9f13\u822c\u88ab\u9707\u5f97\u76f4\u98a4\uff0c\u4e00\u961f\u9ed1\u7532\u9a91\u5175\u4eff\u4f5b\u75be\u98ce\u6fc0\u6d41\u822c\uff0c\u671d\u7740\u57ce\u95e8\u76f4\u51b2\u800c\u6765\u3002</p><p>\u201c\u5173\u57ce\u95e8\uff01\u5173\u57ce\u95e8\uff01\u201d</p><p>\u5b88\u57ce\u5c06\u519b\u5927\u558a\uff0c\u58eb\u5175\u4eec\u614c\u5fd9\u52a8\u624b\uff0c\u603b\u7b97\u662f\u53ca\u65f6\u628a\u57ce\u95e8\u5173\u4e0a\uff0c\u843d\u4e0b\u91cd\u95f8\u3002\u654c\u519b\u773c\u89c1\u57ce\u95e8\u5df2\u95ed\uff0c\u4e5f\u4fbf\u8fdc\u8fdc\u505c\u4f4f\u3002\u743c\u6069\u8fd9\u624d\u770b\u5f97\u5206\u660e\uff0c\u539f\u6765\u8fd9\u4e00\u961f\u9a91\u5175\u5927\u7ea6\u6570\u5341\u4eba\uff0c\u4e2a\u4e2a\u5168\u90fd\u8eab\u62ab\u91cd\u7532\uff0c\u8170\u6302\u91cd\u5251\uff0c\u80cc\u630e\u957f\u5f13\uff0c\u800c\u80ef\u4e0b\u7684\u5750\u9a91\u4e5f\u6839\u672c\u4e0d\u662f\u9a6c\u5339\uff0c\u800c\u662f\u67d0\u79cd\u602a\u7269\uff0c\u5f62\u4f3c\u9ed1\u9a6c\uff0c\u5374\u8981\u9ad8\u5927\u8bb8\u591a\uff0c\u540c\u65f6\u4e00\u4e2a\u4e2a\u7626\u5f97\u76ae\u5305\u9aa8\u5934\uff0c\u9aa8\u9abc\u5d99\u5ccb\uff0c\u773c\u7736\u91cc\u6ca1\u6709\u773c\u73e0\uff0c\u800c\u662f\u6cdb\u7740\u70c1\u70c1\u7ea2\u5149\uff0c\u53e3\u9f3b\u4e2d\u4e0d\u65ad\u55b7\u51fa\u786b\u78fa\u785d\u70df\uff0c\u56db\u8e44\u8e0f\u706b\uff0c\u90aa\u6c14\u51db\u7136\u76f4\u9732\u3002</p><p>\u201c\u8fd9\u662f\u2026\u2026\u68a6\u9b47\uff01\u563f\uff0c\u6ca1\u60f3\u5230\u8fd8\u771f\u6709\u51e1\u4eba\u6562\u9a91\u68a6\u9b47\uff0c\u4e0d\u6015\u6574\u665a\u505a\u5669\u68a6\u4e48\u2014\u2014\u54a6\uff0c\u90a3\u4e2a\u5973\u4eba\u662f\u2026\u2026\u201d</p><p>\u743c\u6069\u6b63\u5728\u89c2\u5bdf\uff0c\u5c31\u89c1\u9ed1\u7532\u9a91\u58eb\u4e2d\uff0c\u4e00\u4f4d\u5973\u5b50\u7f13\u7f13\u7b56\u9a6c\u800c\u51fa\uff0c\u5979\u540c\u6837\u4e5f\u7a7f\u7740\u9ed1\u8272\u91cd\u7532\uff0c\u5374\u4f9d\u65e7\u63a9\u76d6\u4e0d\u4f4f\u7a88\u7a95\u7ea4\u7ec6\u7684\u8eab\u6bb5\uff0c\u8170\u60ac\u91d1\u8272\u91cd\u5251\uff0c\u80cc\u4e0a\u630e\u7740\u4e00\u5f20\u8910\u8272\u957f\u5f13\uff0c\u5374\u6ca1\u6234\u5934\u76d4\uff0c\u957f\u5f97\u60ca\u4eba\u7684\u7d2b\u53d1\u98d8\u82e5\u7f0e\u5e26\uff0c\u76f4\u53ca\u811a\u8e1d\u3002\u56e0\u4e3a\u8ddd\u79bb\u8fdc\uff0c\u743c\u6069\u770b\u4e0d\u6e05\u5979\u7684\u9762\u5bb9\uff0c\u5374\u6ca1\u6765\u7531\u5730\u89c9\u5f97\u6709\u51e0\u5206\u719f\u6089\uff0c\u751a\u81f3\u6709\u51e0\u5206\u4eb2\u5207\u611f\u3002\u663e\u7136\u6b64\u5973\u5c31\u662f\u90a3\u4f4d\u6563\u63d0\u519b\u7684\u65b0\u4efb\u7edf\u5e05\u4e86\uff0c\u548c\u90e8\u4e0b\u4e0d\u540c\uff0c\u5979\u7684\u5750\u9a91\u5012\u6bd4\u8f83\u6b63\u5e38\uff0c\u4e0d\u662f\u68a6\u9b47\uff0c\u770b\u8d77\u6765\u5c31\u662f\u4e00\u5339\u6bd4\u8f83\u795e\u9a8f\u4e9b\u7684\u9ed1\u9a6c\uff0c\u9a6c\u978d\u8fb9\u6302\u7740\u4e00\u67c4\u4e4c\u9ed1\u957f\u67aa\uff0c\u5e94\u8be5\u5c31\u662f\u5979\u7684\u6b66\u5668\u4e86\u3002</p><p>\u57ce\u4e0a\u57ce\u4e0b\u4e00\u7247\u5bc2\u9759\uff0c\u770b\u7740\u5979\u7b56\u9a6c\u8fd1\u524d\uff0c\u76f4\u5230\u8ddd\u79bb\u57ce\u5899\u4e24\u767e\u6b65\u7684\u5730\u65b9\u505c\u4f4f\u3002\u6b64\u65f6\u662f\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u9633\u5149\u5df2\u7ecf\u8fc7\u4e86\u6700\u76db\u7684\u65f6\u523b\uff0c\u4f46\u4f9d\u65e7\u660e\u4eae\uff0c\u4ece\u897f\u9762\u5929\u7a7a\u5c04\u8fc7\u6765\uff0c\u6620\u7167\u5728\u5973\u5b50\u7684\u957f\u957f\u7d2b\u53d1\u4e0a\uff0c\u6298\u5c04\u51fa\u4ee4\u4eba\u76ee\u7729\u795e\u6447\u7684\u7f8e\u4e3d\u5149\u6cfd\u3002\u5979\u7a0d\u7a0d\u4ef0\u8d77\u8138\uff0c\u62bf\u7740\u5634\u5507\uff0c\u76ee\u5149\u626b\u89c6\u7740\u57ce\u4e0a\u7684\u5b88\u519b\uff0c\u4e00\u65f6\u6ca1\u6709\u8bf4\u8bdd\u3002\u743c\u6069\u6b64\u65f6\u53ef\u4ee5\u770b\u6e05\u5979\u7684\u9762\u5bb9\uff0c\u5f88\u6f02\u4eae\uff0c\u800c\u4e14\u68f1\u89d2\u9c9c\u660e\uff0c\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6c14\u8d28\uff0c\u4eff\u4f5b\u662f\u65e2\u521a\u786c\u53c8\u67d4\u8f6f\uff0c\u65e2\u81ea\u8d1f\u53c8\u8c26\u900a\uff0c\u65e2\u7b80\u6d01\u53c8\u7cbe\u5de7\uff0c\u5b9e\u5728\u96be\u4ee5\u7528\u8a00\u8bed\u5f62\u5bb9\uff0c\u5374\u53c8\u83ab\u540d\u5176\u5999\u5730\u89c9\u5f97\u719f\u6089\uff0c\u4eff\u4f5b\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\u2014\u2014\u4f46\u743c\u6069\u53ef\u4ee5\u786e\u5b9a\u81ea\u5df1\u4e0d\u8ba4\u8bc6\u5979\u3002</p><p>\u201c\u5979\u6709\u70b9\u50cf\u827e\u5f25\u8587\u3002\u201d\u73ca\u5609\u7684\u58f0\u97f3\u7a81\u7136\u4ece\u65c1\u8fb9\u4f20\u6765\u3002</p><p>\u743c\u6069\u6b63\u770b\u5f97\u5165\u795e\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8f6c\u8eab\u770b\u53bb\u53d1\u73b0\u73ca\u5609\u5df2\u7ecf\u5230\u4e86\u57ce\u4e0a\u3002\u201c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u201d\u4ed6\u5947\u602a\uff0c\u7136\u540e\u624d\u610f\u8bc6\u5230\u73ca\u5609\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u201c\u59d0\u59d0\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5979\u6709\u70b9\u50cf\u827e\u5f25\u8587\uff0c\u201d\u73ca\u5609\u91cd\u590d\uff0c\u201c\u6709\u70b9\u50cf\u2026\u2026\u55ef\uff0c\u53c8\u4e0d\u592a\u4e00\u6837\u3002\u201d</p><p>\u88ab\u73ca\u5609\u8fd9\u4e00\u63d0\u9192\uff0c\u743c\u6069\u518d\u53bb\u770b\uff0c\u679c\u7136\u53d1\u89c9\u5982\u5979\u6240\u8a00\u3002\u8fd9\u4e2a\u5973\u5b50\u663e\u7136\u66f4\u5e74\u957f\u4e9b\uff0c\u770b\u9762\u5bb9\u5927\u6982\u4e8c\u5341\u516d\u4e03\u5c81\u5de6\u53f3\uff0c\u591a\u4e86\u51e0\u5206\u6210\u719f\u5473\u9053\uff0c\u6885\u83f2\u65af\u7ec8\u7a76\u8fd8\u662f\u4e0d\u514d\u6709\u4e9b\u7a1a\u6c14\uff0c\u4f46\u90a3\u79cd\u51db\u51db\u950b\u8292\u542b\u800c\u4e0d\u53d1\u7684\u6c14\u6d3e\uff0c\u5e73\u9759\u4e2d\u900f\u7740\u65a9\u9489\u622a\u94c1\u4e00\u5f80\u65e0\u524d\u7684\u575a\u51b3\uff0c\u5374\u5b9e\u5728\u662f\u795e\u4f3c\u5f97\u7d27\u3002</p><p>\u201c\u8fd9\u4e24\u4eba\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5173\u7cfb\u5427\u3002\u201d\u743c\u6069\u7a81\u7136\u5192\u51fa\u8fd9\u4e2a\u83ab\u540d\u5176\u5999\u7684\u5ff5\u5934\u3002</p><p>\u7136\u800c\u4e0d\u7b49\u4ed6\u731c\u6d4b\u51fa\u7ed3\u679c\uff0c\u90a3\u540d\u7d2b\u53d1\u5973\u5b50\u5df2\u7ecf\u5f00\u59cb\u8bf4\u8bdd\u3002</p><p>\u201c\u5f00\u57ce\u6295\u964d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u58f0\u97f3\u5e76\u4e0d\u54cd\u4eae\uff0c\u5374\u8ba9\u57ce\u5934\u4e0a\u6bcf\u4e2a\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5e94\u8be5\u662f\u501f\u52a9\u4e86\u67d0\u79cd\u6269\u97f3\u7684\u9b54\u6cd5\u88c5\u5907\uff0c\u201c\u6211\u662f\u6b4c\u66e6\u96c5\uff0c\u6563\u63d0\u519b\u7edf\u5e05\uff0c\u4fdd\u8bc1\u4f60\u4eec\u7684\u4eba\u8eab\u5b89\u5168\uff0c\u6211\u4e0d\u662f\u5f88\u559c\u6b22\u6740\u4eba\u3002\u201d</p><p>\u5979\u8bf4\u5f97\u4e0d\u75be\u4e0d\u5f90\uff0c\u5e73\u5e73\u6de1\u6de1\uff0c\u4eff\u4f5b\u53ea\u662f\u5728\u8bf4\u8fd9\u4e16\u754c\u4e0a\u6700\u5bfb\u5e38\u7684\u4e8b\u60c5\u3002\u57ce\u5934\u4e0a\u9759\u9ed8\u4e86\u51e0\u79d2\u949f\uff0c\u7136\u540e\u7206\u53d1\u51fa\u4e00\u9635\u54c4\u5802\u5927\u7b11\uff0c\u867d\u7136\u4f17\u4eba\u5927\u591a\u60ca\u8bb6\u4e8e\u8fd9\u81ea\u79f0\u6b4c\u66e6\u96c5\u7684\u7d2b\u53d1\u5973\u5b50\u7684\u7f8e\u8c8c\u6c14\u8d28\uff0c\u4ee5\u53ca\u5979\u7684\u7edf\u5e05\u8eab\u4efd\uff0c\u4f46\u4e5f\u5c31\u4ec5\u6b64\u800c\u5df2\uff0c\u5230\u5e95\u662f\u4e24\u519b\u9635\u524d\uff0c\u53cc\u65b9\u654c\u5bf9\uff0c\u54ea\u6709\u88ab\u5bf9\u65b9\u8bf4\u53e5\u8bdd\u5c31\u5f03\u68b0\u6295\u964d\u7684\u9053\u7406\u3002</p><p>\u57ce\u5934\u4e0a\u51e0\u4e2a\u5c06\u9886\u5546\u8bae\u7247\u523b\uff0c\u968f\u5373\u51b3\u5b9a\u6d3e\u4eba\u51fa\u6218\uff0c\u6740\u6740\u5bf9\u65b9\u7684\u6c14\u7130\uff0c\u63a5\u5230\u8fd9\u4e2a\u5149\u8363\u4efb\u52a1\u7684\uff0c\u4fbf\u662f\u65b9\u624d\u548c\u5f17\u96f7\u65af\u8bf4\u8bdd\u7684\u90a3\u4f4d\u94f6\u7532\u5c06\u519b\u3002\u540a\u95f8\u62c9\u8d77\uff0c\u57ce\u95e8\u6253\u5f00\uff0c\u94f6\u7532\u5c06\u519b\u7387\u9886\u4e00\u961f\u9a91\u5175\u51b2\u51fa\uff0c\u6c14\u52bf\u6c79\u6c79\u5730\u671d\u7740\u7d2b\u53d1\u5973\u5b50\u6251\u4e86\u8fc7\u6765\u3002\u65e2\u7136\u5bf9\u65b9\u4e3b\u5e05\u4eb2\u8eab\u524d\u6765\uff0c\u8eab\u8fb9\u53ea\u6709\u6570\u5341\u62a4\u536b\uff0c\u90a3\u8fd8\u5ba2\u6c14\u4ec0\u4e48\uff0c\u5982\u679c\u80fd\u591f\u5f53\u573a\u9635\u65a9\uff0c\u5c82\u4e0d\u662f\u649e\u5927\u8fd0\u4e86\u3002</p><p>\u6b4c\u66e6\u96c5\u9762\u65e0\u8868\u60c5\u5730\u770b\u7740\u4ed6\u4eec\uff0c\u4f38\u624b\u4ece\u978d\u4e0b\u53d6\u51fa\u957f\u67aa\uff0c\u9065\u9065\u4e00\u6307\u3002\u62c5\u4efb\u51b2\u9635\u950b\u77e2\u7684\u94f6\u7532\u5c06\u519b\u83ab\u540d\u5730\u611f\u89c9\u5230\u4e00\u9635\u5fc3\u614c\uff0c\u4f46\u4ed6\u4e5f\u662f\u8eab\u7ecf\u767e\u6218\u7684\u4eba\u7269\uff0c\u53d7\u5c01\u8fc7\u201c\u585e\u5c14\u9a91\u58eb\u201d\u7684\u79f0\u53f7\uff0c\u5e76\u975e\u5bfb\u5e38\u4e4b\u8f88\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u88ab\u6d3e\u51fa\u6765\u6253\u5934\u9635\u4e86\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u4ed6\u5df2\u7ecf\u51b2\u5230\u8fd1\u524d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5e73\u7aef\u624b\u4e2d\u7684\u7cbe\u91d1\u957f\u77db\u7167\u7740\u5bf9\u624b\u5f53\u80f8\u76f4\u523a\u3002</p><p>\u7136\u540e\u4ed6\u7684\u53cc\u773c\u9661\u7136\u77aa\u5927\u3002</p><p>\u6b4c\u66e6\u96c5\u7a0d\u7a0d\u5c06\u624b\u4e2d\u957f\u67aa\u4e00\u62ac\uff0c\u642d\u4e0a\u4e86\u585e\u5c14\u9a91\u58eb\u7684\u957f\u77db\uff0c\u987a\u52bf\u5f80\u4e0b\u4e00\u538b\u3002\u5979\u5e76\u6ca1\u6709\u7528\u591a\u5927\u529b\u6c14\uff0c\u4f46\u7cbe\u91d1\u957f\u77db\u5374\u7acb\u523b\u88ab\u8361\u5f00\u3002\u6b4c\u66e6\u96c5\u7684\u957f\u67aa\u968f\u5373\u5982\u6bd2\u86c7\u822c\u500f\u5ffd\u5f39\u8d77\uff0c\u55e4\u5730\u4e00\u58f0\u624e\u8fdb\u4e86\u5bf9\u624b\u7684\u54bd\u5589\u3002</p><p>\u201c\u6211\u4e0d\u662f\u5f88\u559c\u6b22\u6740\u4eba\u3002\u201d\u5979\u6de1\u6de1\u5730\u8bf4\uff0c\u62bd\u56de\u957f\u67aa\uff0c\u585e\u5c14\u9a91\u58eb\u7684\u54bd\u5589\u4e2d\u4e00\u9053\u8840\u7bad\u98d9\u5c04\u800c\u51fa\uff0c\u6574\u4e2a\u4eba\u6454\u843d\u9a6c\u4e0b\u3002\u6b4c\u66e6\u96c5\u5de6\u624b\u62d4\u51fa\u8170\u95f4\u91d1\u8272\u91cd\u5251\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5343\u4e07\u70b9\u8367\u8367\u7eff\u5149\u81ea\u5251\u8eab\u4e2d\u6d8c\u51fa\uff0c\u8f70\u7136\u7efd\u653e\u4eff\u4f5b\u78a7\u65e5\uff0c\u7167\u7740\u6b63\u8fce\u9762\u51b2\u6765\u7684\u8fdc\u5c71\u57ce\u9a91\u58eb\u4eec\u4e00\u6325\u800c\u4e0b\u3002</p><p>\u78a7\u5149\u5982\u6f6e\u6c34\u822c\u6c79\u6d8c\u51b2\u8fc7\uff0c\u5239\u90a3\u95f4\u5c06\u6240\u6709\u7684\u9a91\u58eb\u8fde\u4eba\u5e26\u9a6c\u90fd\u53d8\u6210\u4e86\u7c89\u672b\u3002</p></article>"}}}]);
