(window.webpackJsonp=window.webpackJsonp||[]).push([[1654],{1677:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516d\u8282 \u611f\u60c5\u95ee\u9898",content:"<article><h2>\u7b2c\u4e09\u5341\u516d\u8282 \u611f\u60c5\u95ee\u9898</h2><p>\u8fce\u63a5\u7740\u743c\u6069\u7684\u76ee\u5149\uff0c\u5f17\u96f7\u65af\u5c34\u5c2c\u5730\u5475\u5475\u7b11\u8d77\u6765\uff0c\u6293\u4e86\u6293\u82b1\u767d\u7684\u5934\u53d1\u3002\u201c\u8fd9\u4e2a\uff0c\u8d35\u6211\u4e24\u56fd\u7d20\u6765\u76df\u597d\uff0c\u5b88\u671b\u76f8\u52a9\u2026\u2026\u201d</p><p>\u201c\u94a5\u5319\u5728\u54ea\u91cc\uff1f\u201d\u743c\u6069\u5f84\u76f4\u6253\u65ad\u3002</p><p>\u521a\u624d\u7684\u5bf9\u8bdd\u4e2d\uff0c\u743c\u6069\u5df2\u7ecf\u77e5\u9053\u6b4c\u66e6\u96c5\u4e4b\u6240\u4ee5\u4e00\u8def\u8ffd\u6740\u5f17\u96f7\u65af\uff0c\u4e3a\u7684\u4fbf\u662f\u90a3\u679a\u201c\u94a5\u5319\u201d\u3002\u4f46\u5230\u5e95\u662f\u4ec0\u4e48\u94a5\u5319\uff0c\u505a\u4ec0\u4e48\u7528\uff0c\u73b0\u5728\u8fd8\u4e00\u65e0\u6240\u77e5\uff0c\u65e2\u7136\u80fd\u88ab\u53cc\u65b9\u5982\u6b64\u770b\u91cd\uff0c\u663e\u7136\u662f\u4ef6\u4e86\u4e0d\u8d77\u7684\u5b9d\u7269\u3002\u743c\u6069\u8d2a\u5ff5\u4e0d\u5f3a\uff0c\u5012\u6ca1\u6709\u593a\u5b9d\u7684\u6253\u7b97\uff0c\u4f46\u4ed6\u4e5f\u60f3\u77e5\u9053\u5230\u5e95\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u5426\u5219\u8fd9\u4e00\u67b6\u5c82\u4e0d\u662f\u6253\u5f97\u592a\u51a4\u6789\u3002</p><p>\u5f17\u96f7\u65af\u7a0d\u7a0d\u72b9\u8c6b\uff0c\u7136\u540e\u5f88\u4e0d\u60c5\u613f\u5730\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u4e2a\u7cbe\u81f4\u7684\u4e1d\u7ec7\u53e3\u888b\uff0c\u4ece\u4e2d\u6478\u51fa\u4e00\u679a\u91d1\u5149\u95ea\u95ea\u7684\u94a5\u5319\u9012\u8fc7\u6765\u3002\u743c\u6069\u63a5\u5728\u624b\u4e2d\uff0c\u5f00\u542f\u5965\u672f\u89c6\u89c9\uff0c\u7136\u540e\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u94a5\u5319\u4e0a\u6563\u53d1\u7740\u4e00\u5708\u53c8\u4e00\u5708\u660e\u4eae\u7075\u5149\uff0c\u786e\u5b9e\u662f\u4ef6\u4e0d\u9519\u7684\u9b54\u6cd5\u7269\u54c1\u3002</p><p>\u201c\u5b83\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u63a7\u5236\u8fdc\u5c71\u57ce\u9632\u5fa1\u6cd5\u9635\u7684\u6838\u5fc3\u90e8\u5206\uff0c\u201d\u5f17\u96f7\u65af\u89e3\u91ca\uff0c\u201c\u5f00\u542f\u3001\u5173\u95ed\u6216\u8005\u4fee\u6539\u6cd5\u9635\uff0c\u90fd\u5fc5\u987b\u8981\u5148\u62ff\u5230\u5b83\u624d\u884c\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u6563\u63d0\u5c14\u5821\u7684\u5927\u519b\u867d\u7136\u5360\u9886\u4e86\u8fdc\u5c71\u57ce\uff0c\u5374\u65e0\u6cd5\u63a5\u7ba1\u7ea2\u888d\u5deb\u5e08\u4eec\u6784\u5efa\u7684\u9632\u5fa1\u6cd5\u9635\uff0c\u800c\u4e0d\u80fd\u63a7\u5236\u9632\u5fa1\u6cd5\u9635\uff0c\u5c31\u5b9e\u5728\u7b97\u4e0d\u4e0a\u662f\u771f\u6b63\u5360\u9886\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u5728\u8eab\u8fb9\u7559\u4e0b\u5de8\u5927\u9690\u60a3\u3002\u6b4c\u66e6\u96c5\u4e3a\u6b64\u8ffd\u6740\u5f17\u96f7\u65af\uff0c\u5012\u4e5f\u6b63\u5e38\u3002\u800c\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u8fd9\u94a5\u5319\u57fa\u672c\u5c31\u662f\u5e9f\u7269\u4e86\uff0c\u4ed6\u53c8\u4e0d\u60f3\u5360\u9886\u8fdc\u5c71\u57ce\uff0c\u8981\u5b83\u4f55\u7528\u3002\u53ea\u4e0d\u8fc7\uff0c\u743c\u6069\u4f9d\u65e7\u6709\u4e9b\u6000\u7591\uff0c\u5f17\u96f7\u65af\u53c8\u4e0d\u662f\u6b63\u724c\u7684\u7ea2\u888d\u5deb\u5e08\uff0c\u5bf9\u585e\u5c14\u6709\u8fd9\u7b49\u5fe0\u5fc3\uff1f\u5c45\u7136\u80af\u5192\u7740\u4e22\u6389\u6027\u547d\u7684\u98ce\u9669\u4e5f\u8981\u4fdd\u62a4\u8fd9\u628a\u94a5\u5319\uff1f\u600e\u4e48\u770b\u8fd9\u6b7b\u80d6\u5b50\uff0c\u4e5f\u4e0d\u50cf\u662f\u8fd9\u7b49\u6709\u8d23\u4efb\u611f\u7684\u4eba\u554a\u3002</p><p>\u89c9\u5bdf\u5230\u743c\u6069\u6000\u7591\u7684\u76ee\u5149\uff0c\u5f17\u96f7\u65af\u644a\u5f00\u624b\uff0c\u8868\u793a\u65e0\u5948\u3002\u201c\u6211\u4e5f\u6ca1\u529e\u6cd5\u554a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u597d\u6b79\u6211\u662f\u8fdc\u5c71\u57ce\u7684\u57ce\u4e3b\uff0c\u73b0\u5728\u57ce\u6c60\u4e22\u4e86\uff0c\u8981\u662f\u8fde\u9b54\u6cd5\u9635\u7684\u94a5\u5319\u90fd\u4e22\u7ed9\u654c\u4eba\uff0c\u90a3\u6211\u5c31\u7b97\u56de\u5230\u585e\u5c14\u4e5f\u80af\u5b9a\u4f1a\u88ab\u9001\u4e0a\u519b\u4e8b\u6cd5\u5ead\u7684\u3002\u201d</p><p>\u201c\u96be\u9053\u4fdd\u4f4f\u94a5\u5319\u4f60\u5c31\u80fd\u4e0d\u4e0a\u519b\u4e8b\u6cd5\u5ead\uff1f\u201d\u743c\u6069\u53cd\u95ee\uff0c\u201c\u8eab\u4e3a\u57ce\u4e3b\uff0c\u4e34\u9635\u8131\u9003\uff0c\u5f03\u57ce\u800c\u9041\uff0c\u8fd9\u5df2\u7ecf\u662f\u91cd\u7f6a\u4e86\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u6bd5\u7adf\u6211\u4f84\u5973\u662f\u827e\u5c14\u5854\u4f2f\u7684\u603b\u7763\u561b\uff0c\u201d\u5f17\u96f7\u65af\u8bf4\uff0c\u201c\u6d3b\u52a8\u6d3b\u52a8\uff0c\u6253\u70b9\u6253\u70b9\uff0c\u591a\u4ea4\u70b9\u7f5a\u91d1\u7ed9\u90a3\u5e2e\u8001\u5bb6\u4f19\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u5f17\u96f7\u65af\u7684\u89e3\u91ca\uff0c\u542c\u8d77\u6765\u4e5f\u5408\u60c5\u5408\u7406\uff0c\u6ca1\u4ec0\u4e48\u7834\u7efd\uff0c\u4f46\u743c\u6069\u603b\u662f\u89c9\u5f97\u54ea\u91cc\u4e0d\u5bf9\u52b2\uff0c\u89c9\u5f97\u4ed6\u672a\u514d\u5766\u767d\u5f97\u592a\u5feb\uff0c\u662f\u4e0d\u662f\u9690\u7792\u4e86\u4ec0\u4e48\u66f4\u91cd\u8981\u7684\u4fe1\u606f\u3002\u5f53\u7136\u4e86\uff0c\u8fd9\u4e5f\u53ef\u80fd\u662f\u56e0\u4e3a\u8fd9\u6b7b\u80d6\u5b50\u521a\u521a\u5728\u6b4c\u66e6\u96c5\u9762\u524d\u4fe1\u53e3\u96cc\u9ec4\uff0c\u786c\u62c9\u743c\u6069\u505a\u6321\u7bad\u724c\u66ff\u6b7b\u9b3c\uff0c\u5bfc\u81f4\u4ed6\u7684\u4fe1\u8a89\u73b0\u5728\u5728\u5deb\u5e08\u7684\u5fc3\u76ee\u4e2d\u5df2\u7ecf\u5b8c\u5168\u7834\u4ea7\uff0c\u4e0d\u7ba1\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u771f\u5b9e\u6027\u90fd\u4f1a\u5927\u6253\u6298\u6263\u3002\u7136\u800c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u53ea\u6709\u6000\u7591\uff0c\u6ca1\u6709\u8bc1\u636e\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u7528\uff1f</p><p>\u5443\uff0c\u7b49\u4e00\u4e0b\uff0c\u6709\u4ef6\u4e8b\u60c5\u597d\u50cf\u641e\u9519\u4e86\uff0c\u6709\u6ca1\u6709\u8bc1\u636e\uff0c\u53c8\u6709\u4ec0\u4e48\u8981\u7d27\u5462\uff1f\u53ea\u8981\u6709\u6000\u7591\u4e0d\u5c31\u8db3\u591f\u4e86\u561b\u2014\u2014\u6211\u73b0\u5728\u53c8\u4e0d\u662f\u5f8b\u5e08\uff0c\u6211\u662f\u5deb\u5e08\u554a\u3002</p><p>\u743c\u6069\u7a81\u7136\u610f\u8bc6\u5230\u81ea\u5df1\u601d\u7ef4\u65b9\u5f0f\u51fa\u73b0\u4e86\u6839\u672c\u6027\u7684\u9519\u8bef\u3002\u8fd9\u662f\u4e00\u4e2a\u5947\u5e7b\u4e16\u754c\uff0c\u4e00\u4e2a\u9b54\u6cd5\u4e16\u754c\uff0c\u4e00\u4e2a\u5f3a\u8005\u4e3a\u5c0a\u5f31\u8005\u81e3\u670d\u7684\u4e16\u754c\uff0c\u800c\u4e14\u7edd\u5bf9\u4e0d\u662f\u6cd5\u6cbb\u793e\u4f1a\uff0c\u81f3\u5c11\u6b64\u5730\u4e0d\u662f\u3002\u743c\u6069\u4f5c\u4e3a\u9ad8\u9636\u5deb\u5e08\uff0c\u4f5c\u4e3a\u9b54\u6cd5\u7684\u8fd0\u7528\u8005\uff0c\u9762\u5bf9\u4e00\u4e2a\u5b64\u8eab\u843d\u5355\u7684\u5974\u96b6\u8d29\u5b50\uff0c\u4ed6\u62e5\u6709\u7edd\u5bf9\u7684\u529b\u91cf\u4f18\u52bf\u3002\u5982\u679c\u8bf4\u4e4b\u524d\u56e0\u4e3a\u53cc\u65b9\u4e00\u76f4\u4ee5\u793c\u76f8\u5f85\uff0c\u6240\u4ee5\u8bb2\u6587\u660e\uff0c\u8bb2\u89c4\u77e9\uff0c\u5ba2\u5ba2\u6c14\u6c14\u2014\u2014\u4f46\u73b0\u5728\u8fd8\u8ddf\u4ed6\u5ba2\u6c14\u4ec0\u4e48\uff1f\u76f4\u63a5\u7ed9\u5bf9\u65b9\u4e0b\u4e00\u4e2a\u7cbe\u795e\u63a7\u5236\u7c7b\u578b\u7684\u6cd5\u672f\uff0c\u81ea\u7136\u6709\u95ee\u5fc5\u7b54\uff0c\u60f3\u77e5\u9053\u4ec0\u4e48\u5c31\u6709\u4ec0\u4e48\uff0c\u518d\u7b80\u5355\u7701\u4e8b\u4e0d\u8fc7\u4e86\u3002</p><p>\u8fd9\u4e48\u7b80\u5355\u7684\u65b9\u6cd5\u90fd\u6ca1\u60f3\u5230\uff0c\u6211\u679c\u7136\u662f\u592a\u5584\u826f\u4e86\u4e48\u2026\u2026</p><p>\u4e00\u8fb9\u5728\u5fc3\u4e2d\u5bf9\u81ea\u5df1\u505a\u51fa\u8bc4\u4ef7\uff0c\u743c\u6069\u4e00\u8fb9\u6084\u6084\u5bf9\u5f17\u96f7\u65af\u91ca\u653e\u4e86\u4e00\u4e2a\u6697\u793a\u672f\u3002\u8fd9\u5e76\u4e0d\u662f\u4ec0\u4e48\u9ad8\u7ea7\u6cd5\u672f\uff0c\u4f46\u5bf9\u4ed8\u666e\u901a\u4eba\u5e94\u8be5\u662f\u8fdc\u8fdc\u8db3\u591f\u4e86\u3002\u7136\u800c\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u6697\u793a\u672f\u547d\u4e2d\u4e86\u76ee\u6807\uff0c\u5374\u76f4\u63a5\u50cf\u4e00\u9635\u5fae\u98ce\u822c\u62c2\u4f53\u800c\u8fc7\uff0c\u538b\u6839\u6ca1\u6709\u751f\u6548\u3002</p><p>\u743c\u6069\u6014\u4e86\u4e00\u6014\uff0c\u201c\u8fd9\u6b7b\u80d6\u5b50\u5c45\u7136\u80fd\u591f\u62b5\u5fa1\u6211\u7684\u6697\u793a\u672f\uff1f\u201d</p><p>\u9644\u9b54\u672f\u80fd\u5426\u6210\u529f\uff0c\u901a\u5e38\u53d6\u51b3\u4e8e\u65bd\u6cd5\u8005\u7684\u80fd\u529b\u9ad8\u4f4e\u5f3a\u5f31\u548c\u53d7\u672f\u8005\u7684\u610f\u5fd7\u575a\u5b9a\u7a0b\u5ea6\uff0c\u5f53\u7136\u4e5f\u4e0d\u6392\u9664\u50cf\u743c\u6069\u8fd9\u79cd\u56e0\u4e3a\u5f71\u706b\u7684\u5173\u7cfb\uff0c\u514d\u75ab\u4e00\u5207\u7cbe\u795e\u653b\u51fb\u7684\u7279\u4f8b\u5b58\u5728\u3002\u743c\u6069\u786e\u5b9e\u4e0d\u662f\u5f88\u7cbe\u901a\u9644\u9b54\u6cd5\u672f\uff0c\u4f46\u9020\u8be3\u4e5f\u4e0d\u7b97\u5dee\uff0c\u800c\u4e14\u4ed6\u6bd5\u7adf\u5df2\u7ecf\u662f\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u5bf9\u4ed8\u6885\u83f2\u65af\u8fd9\u79cd\u5fc3\u667a\u575a\u6bc5\u5982\u94c1\u7684\u4eba\u81ea\u7136\u6ca1\u6307\u671b\uff0c\u4f46\u9762\u5bf9\u4e00\u4e2a\u517b\u5c0a\u5904\u4f18\u7684\u5974\u96b6\u8d29\u5b50\u5c45\u7136\u4e5f\u4f1a\u5931\u624b\uff0c\u8fd9\u53ef\u5c31\u592a\u5947\u602a\u4e86\u3002</p><p>\u83ab\u975e\uff0c\u8fd9\u6b7b\u80d6\u5b50\u5e76\u975e\u50cf\u8868\u9762\u770b\u8d77\u6765\u90a3\u6837\u5dee\u52b2\uff0c\u800c\u662f\u4e2a\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u624b\uff1f</p><p>\u8fd9\u4e2a\u7a81\u7136\u5192\u51fa\u7684\u5ff5\u5934\u8ba9\u4ed6\u5fc3\u4e2d\u6697\u51db\uff0c\u800c\u4e14\u8d8a\u60f3\u8fd8\u771f\u8d8a\u6709\u53ef\u80fd\u3002\u6309\u7167\u5f17\u96f7\u65af\u4e4b\u524d\u7684\u8bf4\u6cd5\uff0c\u4ed6\u521a\u79bb\u57ce\u4e0d\u4e45\u5c31\u88ab\u53d1\u89c9\uff0c\u5f15\u6765\u8ffd\u6740\uff0c\u800c\u8fdc\u5c71\u7834\u57ce\u4e4b\u540e\u5148\u662f\u6212\u4e25\u4e86\u4e24\u5929\uff0c\u743c\u6069\u51fa\u57ce\u4e4b\u540e\u53c8\u8d70\u4e86\u4e09\u5929\u2014\u2014\u6362\u53e5\u8bdd\u8bf4\uff0c\u8fd9\u80d6\u8001\u5934\u5df2\u7ecf\u88ab\u8ffd\u6740\u4e86\u6574\u6574\u4e94\u5929\u3002\u9762\u5bf9\u6b4c\u66e6\u96c5\u8fd9\u79cd\u9a91\u7740\u72ec\u89d2\u517d\u7684\u5f3a\u608d\u7f8e\u4eba\uff0c\u4ed6\u80fd\u591f\u88ab\u8ffd\u6740\u4e94\u5929\u8fd8\u5b89\u7136\u65e0\u6059\uff0c\u867d\u7136\u6a21\u6837\u770b\u8d77\u6765\u72fc\u72c8\u4e0d\u582a\uff0c\u5176\u5b9e\u5374\u6839\u672c\u6ca1\u53d7\u4ec0\u4e48\u4f24\uff0c\u8fd8\u80fd\u9760\u4e24\u6761\u817f\u629b\u5f00\u5bf9\u624b\uff0c\u62a2\u5148\u4e00\u6b65\u8eb2\u8fdb\u743c\u6069\u7684\u623f\u5b50\u5927\u5403\u5927\u559d\u4e86\u987f\u2026\u2026\u8fd9\u6050\u6015\u5df2\u7ecf\u4e0d\u80fd\u7528\u8fd0\u6c14\u597d\u6765\u5f62\u5bb9\u4e86\u5427\u3002</p><p>\u743c\u6069\u5fc3\u5ff5\u7535\u8f6c\uff0c\u8138\u4e0a\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8fd8\u7ed9\u4f60\u5427\u3002\u201d\u4ed6\u8bf4\u7740\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u628a\u94a5\u5319\u629b\u4e86\u56de\u53bb\u3002\u5f17\u96f7\u65af\u4f38\u624b\u63a5\u8fc7\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u9661\u7136\u542c\u5f97\u743c\u6069\u4f4e\u559d\u4e00\u58f0\uff1a\u201c\u77f3\u5316\uff01\u201d</p><p>\u4e00\u9053\u7070\u8272\u5c04\u7ebf\u4ece\u5deb\u5e08\u6307\u5c16\u8ff8\u51fa\uff0c\u51c6\u786e\u51fb\u4e2d\u5f17\u96f7\u65af\uff0c\u77ac\u95f4\u5c06\u4ed6\u53d8\u6210\u4e86\u4e00\u5c0a\u77f3\u50cf\u3002</p><p>\u6210\u529f\u77f3\u5316\u76ee\u6807\uff0c\u743c\u6069\u6697\u81ea\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u4ed6\u73b0\u5728\u5bf9\u8fd9\u80d6\u8001\u5934\u8fd8\u771f\u6709\u51e0\u5206\u5fcc\u60ee\u3002\u5982\u679c\u8fd9\u4e2a\u77f3\u5316\u672f\u518d\u5931\u8d25\uff0c\u4ed6\u5c31\u4f1a\u7acb\u523b\u4e0b\u6740\u624b\u7528\u89e3\u79bb\u4e86\u3002\u5176\u5b9e\u4ee5\u6cd5\u672f\u5f3a\u5ea6\u800c\u8bba\uff0c\u89e3\u79bb\u548c\u77f3\u5316\u662f\u5dee\u4e0d\u591a\u7684\uff0c\u540c\u6837\u90fd\u662f\u8981\u4ece\u7b2c\u516d\u5c42\u9b54\u7f51\u4e2d\u63d0\u53d6\u9b54\u529b\u624d\u80fd\u65bd\u5c55\uff0c\u540c\u6837\u90fd\u662f\u4e00\u51fb\u5b9a\u80dc\u8d1f\u7684\u62db\u6570\u3002\u4f46\u89e3\u79bb\u66f4\u52a0\u51f6\u731b\uff0c\u800c\u77f3\u5316\u5219\u76f8\u5bf9\u201c\u6e29\u548c\u201d\u8bb8\u591a\uff0c\u6216\u8005\u8bf4\u6bd4\u8f83\u7559\u6709\u4f59\u5730\u3002\u88ab\u89e3\u79bb\u51fb\u4e2d\uff0c\u8f7b\u5219\u53d7\u4f24\uff0c\u91cd\u5219\u5316\u4f5c\u5fae\u5c18\uff1b\u88ab\u77f3\u5316\u51fb\u4e2d\uff0c\u5c31\u7b97\u662f\u6ca1\u80fd\u62b5\u6297\u8fc7\u53bb\uff0c\u53d8\u6210\u4e86\u77f3\u50cf\uff0c\u5982\u679c\u53ca\u65f6\u4f7f\u7528\u201c\u89e3\u9664\u77f3\u5316\u201d\u7684\u6cd5\u672f\uff0c\u7167\u6837\u80fd\u591f\u518d\u5ea6\u53d8\u56de\u8840\u8089\u4e4b\u8eaf\uff0c\u53ea\u662f\u4f1a\u5728\u4e00\u6bb5\u65f6\u95f4\u5185\u6781\u5ea6\u865a\u5f31\u3002\u743c\u6069\u5fc3\u4e2d\u9887\u6709\u7591\u60d1\uff0c\u8981\u4ece\u5f17\u96f7\u65af\u8eab\u4e0a\u5bfb\u627e\u7b54\u6848\uff0c\u6545\u6b64\u6ca1\u6709\u9009\u62e9\u89e3\u79bb\uff0c\u53ea\u662f\u6682\u65f6\u5148\u77f3\u5316\u8d77\u6765\uff0c\u518d\u4f5c\u6253\u7b97\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u957f\u5eca\u91cc\u4f20\u6765\u8f7b\u5fae\u54cd\u52a8\uff0c\u4e00\u6247\u623f\u95e8\u6084\u6084\u6253\u5f00\uff0c\u73ca\u5609\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u63a2\u51fa\u534a\u8fb9\u8138\uff0c\u201c\u5c0f\u5f1f\uff0c\u6253\u5b8c\u4e86\u4e48\uff1f\u201d\u5979\u95ee\u3002</p><p>\u521a\u624d\u743c\u6069\u548c\u6b4c\u66e6\u96c5\u8bf4\u50f5\u4e86\u52a8\u624b\uff0c\u5728\u5ba2\u5385\u91cc\u6253\u5f97\u567c\u91cc\u556a\u5566\uff0c\u73ca\u5609\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u81ea\u77e5\u63d2\u4e0d\u4e0a\u624b\uff0c\u51fa\u6765\u53ea\u4f1a\u6dfb\u4e71\uff0c\u6240\u4ee5\u90fd\u4e56\u4e56\u8eb2\u5728\u623f\u95f4\u91cc\u3002\u5982\u4eca\u542c\u89c1\u5916\u9762\u6253\u6597\u58f0\u5e73\u606f\uff0c\u52a8\u9759\u6e10\u5c0f\uff0c\u4f30\u8ba1\u4e8b\u6001\u5e73\u606f\uff0c\u4e8e\u662f\u51fa\u6765\u770b\u770b\u60c5\u51b5\u3002\u201c\u6ca1\u4e8b\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u4eec\u6050\u6015\u5f97\u8fde\u591c\u8d76\u8def\u4e86\u3002\u201d</p><p>\u6b4c\u66e6\u96c5\u53d7\u4f24\u8d25\u9000\uff0c\u5979\u88ab\u743c\u6069\u7684\u5f71\u706b\u51fb\u4e2d\uff0c\u4e00\u65f6\u534a\u523b\u662f\u80af\u5b9a\u65e0\u6cd5\u6062\u590d\u7684\u3002\u4f46\u5979\u4e0d\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u624b\u4e0b\u58eb\u5175\u65e0\u6570\uff0c\u96be\u4fdd\u4e0d\u4f1a\u5377\u571f\u91cd\u6765\uff0c\u5230\u65f6\u5019\u5982\u4f55\u62b5\u6321\u3002\u597d\u5728\u8fd9\u91cc\u8ddd\u79bb\u6851\u6bd4\u4e9a\u7684\u56fd\u5883\u53ea\u6709\u4e0d\u5230\u4e00\u5929\u7684\u8def\u7a0b\uff0c\u73b0\u5728\u52a8\u8eab\u7684\u8bdd\uff0c\u660e\u5929\u4e0a\u5348\u5c31\u80fd\u8d8a\u8fc7\u963f\u6c99\u5df4\u6cb3\uff0c\u5230\u65f6\u5019\u5c31\u65e0\u9700\u62c5\u5fc3\u8ffd\u5175\u4e86\u3002</p><p>\u73ca\u5609\u4e0d\u660e\u8be6\u60c5\uff0c\u4f46\u8fd8\u662f\u4e56\u4e56\u5730\u70b9\u70b9\u5934\uff0c\u8868\u793a\u660e\u767d\u3002\u867d\u7136\u4e24\u4eba\u79c1\u4e0b\u76f8\u5904\u65f6\uff0c\u73ca\u5609\u662f\u4e00\u526f\u59d0\u59d0\u505a\u6d3e\uff0c\u628a\u743c\u6069\u5f53\u5f1f\u5f1f\u770b\u5f85\uff0c\u4f46\u4e00\u65e6\u6d89\u53ca\u6b63\u4e8b\uff0c\u5979\u4fbf\u4f1a\u53d8\u6210\u6e29\u5a49\u61c2\u4e8b\u7684\u59bb\u5b50\uff0c\u6781\u5c11\u5bf9\u743c\u6069\u7684\u51b3\u5b9a\u8868\u793a\u5f02\u8bae\u3002\u83ab\u5c3c\u5361\u59d0\u59b9\u6b64\u65f6\u4e5f\u51fa\u4e86\u623f\u95f4\uff0c\u743c\u6069\u7b49\u4eba\u4e00\u8def\u884c\u6765\uff0c\u5750\u7740\u4ece\u8fdc\u5c71\u57ce\u96c7\u4f63\u7684\u9a6c\u8f66\uff0c\u662f\u6709\u8f66\u592b\u7684\uff0c\u6b64\u65f6\u6b63\u5728\u697c\u4e0a\u7684\u67d0\u95f4\u5367\u5ba4\u91cc\u7761\u89c9\uff0c\u4e8e\u662f\u8ba9\u8299\u857e\u72c4\u4e0a\u697c\u53bb\u53eb\u4ed6\u8d77\u5e8a\uff0c\u51c6\u5907\u8fde\u591c\u8d76\u8def\uff0c\u8299\u8389\u5a05\u4f3c\u4e4e\u5fd8\u4e86\u4ec0\u4e48\u4e1c\u897f\uff0c\u53c8\u56de\u5367\u5ba4\u53bb\u62ff\u3002\u5ba2\u5385\u91cc\u5c31\u5269\u4e0b\u59d0\u5f1f\u4e24\u4eba\uff0c\u73ca\u5609\u73af\u89c6\u4e00\u584c\u7cca\u6d82\u7684\u5468\u906d\uff0c\u7136\u540e\u770b\u89c1\u4e86\u5899\u89d2\u7684\u77f3\u50cf\uff0c\u201c\u8fd9\u91cc\u600e\u4e48\u4f1a\u591a\u4e86\u4e2a\u2014\u2014\u54a6\uff0c\u8fd9\u4e0d\u662f\u521a\u624d\u90a3\u4f4d\u5ba2\u4eba\u2026\u2026\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u5bb6\u4f19\u7684\u6765\u5386\u53ef\u80fd\u4e0d\u7b80\u5355\uff0c\u6211\u6253\u7b97\u8def\u4e0a\u597d\u597d\u5ba1\u8baf\u5ba1\u8baf\u4ed6\u2026\u2026\u59d0\u59d0\u4f60\u7a81\u7136\u7b11\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u73ca\u5609\u683c\u683c\u7b11\u7740\uff0c\u201c\u53ea\u662f\u6ca1\u60f3\u5230\uff0c\u6211\u7684\u5c0f\u5f1f\u9664\u4e86\u559c\u6b22\u6f02\u4eae\u5973\u5b69\u4e4b\u5916\uff0c\u5bf9\u80d6\u8001\u5934\u4e5f\u4f1a\u8fd9\u4e48\u6709\u5174\u8da3\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u522b\u5f00\u8fd9\u79cd\u73a9\u7b11\uff0c\u4f1a\u8ba9\u6211\u6709\u5fc3\u7406\u9634\u5f71\u7684\u554a\u2026\u2026\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\u5730\u677f\u8d77\u8138\uff0c\u201c\u6211\u53ea\u559c\u6b22\u7f8e\u5973\uff0c\u5bf9\u7537\u4eba\u53ef\u534a\u70b9\u6ca1\u5174\u8da3\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u548c\u827e\u5f25\u8587\u5728\u4e00\u8d77\u5f85\u5f97\u65f6\u95f4\u4e45\u4e86\uff0c\u6027\u53d6\u5411\u53d1\u751f\u53d8\u5316\u4e86\u5462\uff0c\u201d\u73ca\u5609\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u968f\u53e3\u8bf4\uff0c\u201c\u5979\u8981\u662f\u7a7f\u7537\u88c5\uff0c\u4e0d\u5c31\u5206\u660e\u662f\u4e2a\u5e05\u6c14\u7684\u7537\u5b69\u5b50\uff0c\u800c\u4e14\u4f60\u4eec\u8fd8\u4e00\u76f4\u90fd\u662f\u2026\u2026\u7528\u90a3\u79cd\u65b9\u5f0f\u3002\u7537\u4eba\u548c\u7537\u4eba\u4e0d\u5c31\u662f\u8fd9\u6837\u4e48\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u6cdb\u8d77\u4e00\u4e1d\u4e0d\u5feb\uff0c\u82e5\u662f\u522b\u4eba\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u4ed6\u7acb\u523b\u5c31\u8981\u5f53\u573a\u7ffb\u8138\uff1b\u4f46\u73b0\u5728\u8bf4\u8bdd\u7684\u662f\u73ca\u5609\uff0c\u4ed6\u4e5f\u53ea\u597d\u5fcd\u4e86\u3002\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u5728\u611f\u60c5\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\uff0c\u786e\u5b9e\u662f\u81ea\u5df1\u5bf9\u4e0d\u8d77\u59d0\u59d0\uff0c\u51fa\u95e8\u4e24\u8d9f\u5c31\u63a8\u5012\u4e86\u4e00\u7fa4\u5973\u5b69\u5b50\uff0c\u73ca\u5609\u53cd\u800c\u6210\u4e86\u6700\u540e\u4e00\u4e2a\uff0c\u5979\u5fc3\u91cc\u6709\u6028\u6c14\uff0c\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u56de\u51fb\uff0c\u201c\u522b\u603b\u662f\u8bf4\u827e\u5f25\u8587\uff0c\u4f60\u5982\u679c\u7a7f\u7537\u88c5\uff0c\u7167\u6837\u4e5f\u662f\u4e2a\u98ce\u5ea6\u7fe9\u7fe9\u7684\u7f8e\u5c11\u5e74\uff0c\u8d70\u5728\u8857\u4e0a\u80fd\u5438\u5f15\u4e00\u5927\u7fa4\u5973\u5b69\uff0c\u4fdd\u8bc1\u6bd4\u5979\u5f3a\u3002\u201d</p><p>\u8fd9\u5012\u786e\u5b9e\u662f\u5b9e\u8bdd\uff0c\u73ca\u5609\u548c\u743c\u6069\u76f8\u8c8c\u8fd1\u4f3c\uff0c\u8bf4\u8d77\u6765\u90fd\u6709\u4e9b\u504f\u4e2d\u6027\u3002\u4ee5\u7537\u6027\u7684\u6807\u51c6\u8861\u91cf\uff0c\u743c\u6069\u6709\u4e9b\u79c0\u6c14\uff0c\u4e0d\u591f\u9633\u521a\u5a01\u731b\uff1b\u800c\u4ee5\u5973\u6027\u7684\u6807\u51c6\u8861\u91cf\uff0c\u73ca\u5609\u5219\u82f1\u6c14\u52c3\u53d1\uff0c\u4e0d\u591f\u7ea4\u5de7\u5a07\u5f31\u3002\u7b49\u7b49\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u603b\u559c\u6b22\u8fd9\u79cd\u82f1\u6b66\u578b\u7684\u5973\u5b69\u5b50\uff0c\u6885\u83f2\u65af\u4e5f\u662f\u73ca\u5609\u4e5f\u662f\uff0c\u96be\u4e0d\u6210\u5f53\u771f\u5982\u73ca\u5609\u6240\u8a00\uff0c\u81ea\u5df1\u7684\u6f5c\u610f\u8bc6\u91cc\u6027\u53d6\u5411\u6709\u95ee\u9898\u2026\u2026\u5443\uff0c\u4e0d\u4f1a\u4e0d\u4f1a\uff0c\u8d77\u7801\u81ea\u5df1\u4e5f\u559c\u6b22\u8299\u857e\u72c4\u8fd9\u6837\u5a07\u6ef4\u6ef4\u7684\u5c0f\u7f8e\u4eba\u561b\u2014\u2014\u8fd9\u4e0d\u4f1a\u8bf4\u660e\u81ea\u5df1\u5176\u5b9e\u662f\u4e2a\u53cc\u6027\u604b\u5427\uff1f</p><p>\u8d8a\u60f3\u8d8a\u662f\u6050\u6016\uff0c\u743c\u6069\u8d76\u7d27\u6253\u65ad\u601d\u7eea\u3002\u73ca\u5609\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u521a\u624d\u51e0\u79d2\u79cd\u91cc\uff0c\u81ea\u5df1\u8fd9\u4e2a\u5f1f\u5f1f\u8111\u888b\u91cc\u8f6c\u8fc7\u4ec0\u4e48\u6837\u6050\u6016\u7684\u5ff5\u5934\u3002\u201c\u4eba\u5bb6\u624d\u6bd4\u4e0d\u8fc7\u827e\u5f25\u8587\u5462\uff0c\u201d\u5979\u9178\u6e9c\u6e9c\u5730\u8bf4\uff0c\u201c\u4ece\u8ba4\u8bc6\u8d77\u5c31\u5929\u5929\u5728\u4e00\u8d77\uff0c\u4e0d\u7ba1\u53bb\u54ea\u91cc\u90fd\u662f\u5f62\u5f71\u4e0d\u79bb\uff0c\u5c31\u8fde\u6df1\u6e0a\u5730\u72f1\u90fd\u4e00\u6837\u2014\u2014\u5c31\u7b97\u5076\u5c14\u5206\u5f00\u51e0\u5929\u4e86\uff0c\u4e5f\u6709\u6212\u6307\u53ef\u4ee5\u4f20\u8baf\uff0c\u8fd8\u80fd\u968f\u65f6\u4f20\u9001\u5e7d\u4f1a\u3002\u6211\u54ea\u91cc\u6709\u8fd9\u79cd\u5f85\u9047\uff0c\u53ea\u80fd\u7b49\u827e\u5f25\u8587\u79bb\u5f00\u4e86\u624d\u80fd\u586b\u8865\u7a7a\u7f3a\uff0c\u800c\u4e14\u4f60\u770b\uff0c\u4eca\u5929\u59d0\u59d0\u8eab\u4f53\u4e00\u4e0d\u65b9\u4fbf\uff0c\u9a6c\u4e0a\u67d0\u4e2a\u574f\u86cb\u5c31\u4e22\u4e0b\u6211\u4e0d\u7ba1\u5566\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u660e\u660e\u662f\u4f60\u81ea\u5df1\u4e0d\u8981\u6211\u966a\u4f60\u7684\uff0c\u201d\u743c\u6069\u65e0\u5948\uff0c\u201c\u4e0d\u80fd\u4e0d\u8bb2\u9053\u7406\u554a\u3002\u201d</p><p>\u201c\u6211\u662f\u8bf4\u4e0d\u8981\u4f60\u966a\u7740\uff0c\u4f46\u4f60\u96be\u9053\u5c31\u4e0d\u4f1a\u6b7b\u76ae\u8d56\u8138\u5730\u7559\u4e0b\u6765\uff0c\u7f20\u7740\u59d0\u59d0\uff0c\u8981\u59d0\u59d0\u5141\u8bb8\u4f60\u966a\u7740\u5417\uff1f\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u8fd9\u6709\u70b9\u51fa\u5c14\u53cd\u5c14\u5427\uff0c\u201d\u743c\u6069\u9690\u9690\u4e5f\u6709\u4e9b\u4e0d\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u7167\u505a\u4ec0\u4e48\uff0c\u7ed3\u679c\u5230\u5934\u6765\u53cd\u800c\u53d8\u6210\u6211\u4e0d\u5bf9\u4e86\uff1f\u201d</p><p>\u201c\u672c\u6765\u5c31\u662f\u4f60\u4e0d\u5bf9\uff0c\u201d\u73ca\u5609\u8bf4\u5f97\u7406\u76f4\u6c14\u58ee\uff0c\u201c\u6211\u662f\u5973\u5b69\u5b50\uff0c\u5973\u5b69\u5b50\u8bf4\u8bdd\u672c\u6765\u5c31\u4e0d\u80fd\u5168\u5f53\u771f\uff0c\u6211\u5728\u5e8a\u4e0a\u548c\u4f60\u8bf4\u4e0d\u8981\u7684\u65f6\u5019\uff0c\u4f60\u4f4f\u624b\u8fc7\u5417\uff1f\u81f3\u4e8e\u8bf4\u8bb2\u9053\u7406\u2014\u2014\u5973\u5b69\u5b50\u6bcf\u4e2a\u6708\u8eab\u4f53\u4e0d\u8212\u670d\u7684\u65f6\u5019\uff0c\u4f60\u672c\u6765\u5c31\u66f4\u5e94\u8be5\u966a\u5728\u8eab\u8fb9\u624d\u5bf9\u5427\uff0c\u8fd9\u79cd\u7b80\u5355\u7684\u9053\u7406\u4f60\u90fd\u4e0d\u61c2\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u5bf9\u4e0d\u8d77\uff0c\u201d\u6c89\u9ed8\u534a\u664c\uff0c\u743c\u6069\u9053\u6b49\uff0c\u201c\u59d0\u59d0\u4e0d\u8212\u670d\uff0c\u6211\u786e\u5b9e\u66f4\u5e94\u8be5\u966a\u7740\u624d\u5bf9\uff0c\u201d\u4ed6\u73af\u81c2\u5c06\u73ca\u5609\u6402\u5728\u6000\u91cc\uff0c\u201c\u662f\u6211\u4e0d\u5bf9\uff0c\u6211\u4fdd\u8bc1\u4ee5\u540e\u90fd\u966a\u7740\u59d0\u59d0\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u8bf4\u5f97\u597d\u542c\uff0c\u201d\u73ca\u5609\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u59d0\u59d0\u8fd9\u51e0\u5929\u4e0d\u65b9\u4fbf\uff0c\u4f60\u8981\u966a\u6211\uff0c\u53ef\u5c31\u8981\u81ea\u5df1\u618b\u7740\u4e86\u3002\u201d</p><p>\u201c\u90a3\u5012\u6ca1\u5173\u7cfb\uff0c\u201d\u743c\u6069\u7b11\u5f97\u5f88\u90aa\u6076\uff0c\u201c\u53cd\u6b63\u59d0\u59d0\u8eab\u4e0a\u8fd8\u6709\u6700\u540e\u4e00\u5757\u5904\u5973\u6ca1\u6709\u88ab\u5f00\u53d1\uff0c\u6b63\u597d\u8d81\u8fd9\u4e2a\u673a\u4f1a\u2026\u2026\u5dee\u70b9\u5fd8\u4e86\uff0c\u90a3\u4e32\u9879\u94fe\u8fd8\u4e00\u76f4\u6ca1\u7528\u8fc7\u5462\u3002\u201d</p><p>\u201c\u60f3\u5f97\u7f8e\uff0c\u201d\u73ca\u5609\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6ee1\u8111\u5b50\u5c31\u60f3\u7740\u600e\u4e48\u6b3a\u8d1f\u59d0\u59d0\uff0c\u4e16\u754c\u4e0a\u6709\u4f60\u8fd9\u6837\u7684\u5f1f\u5f1f\u4e48\u3002\u201d</p><p>\u201c\u53ef\u662f\u59d0\u59d0\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u5f1f\u5f1f\u8fd9\u79cd\u751f\u7269\uff0c\u5c31\u662f\u7528\u6765\u6b3a\u8d1f\u59d0\u59d0\u7684\u5440\u3002\u201d</p><p>\u201c\u90a3\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\u3002\u201d</p><p>\u201c\u4f46\u59d0\u59d0\u4e5f\u627f\u8ba4\u4e86\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u53bb\u4f60\u7684\uff0c\u201d\u73ca\u5609\u8f7b\u8f7b\u6376\u4e86\u4ed6\u4e00\u62f3\uff0c\u201c\u6709\u4f60\u8fd9\u6837\u7684\u5f1f\u5f1f\uff0c\u771f\u662f\u2026\u2026\u54a6\uff01\u201d</p><p>\u5979\u7a81\u7136\u60ca\u8bb6\u51fa\u58f0\uff0c\u743c\u6069\u4e0d\u77e5\u4f55\u6545\uff0c\u987a\u7740\u76ee\u5149\u770b\u53bb\uff0c\u53ea\u89c1\u65c1\u8fb9\u90a3\u5c0a\u80d6\u8001\u5934\u77f3\u50cf\uff0c\u4e0d\u77e5\u4f55\u65f6\u8868\u9762\u51fa\u73b0\u4e86\u65e0\u6570\u7ec6\u7ec6\u88c2\u7eb9\uff0c\u9690\u9690\u7684\u96f7\u9e23\u58f0\u5728\u8033\u8fb9\u54cd\u8d77\uff0c\u7a7a\u6c14\u6084\u65e0\u58f0\u606f\u7684\u5feb\u901f\u6d41\u8f6c\uff0c\u5f62\u6210\u5927\u5927\u5c0f\u5c0f\u7684\u6c14\u65cb\u3002\u968f\u7740\u88c2\u7eb9\u8fdb\u4e00\u6b65\u5ef6\u4f38\u6269\u5927\uff0c\u96f7\u9e23\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u539f\u672c\u8f7b\u67d4\u7684\u6c14\u65cb\u53d8\u6210\u4e86\u547c\u5578\u7684\u66b4\u98ce\uff0c\u5728\u5ba2\u5385\u4e2d\u75af\u72c2\u5730\u76d8\u65cb\u8086\u8650\u3002\u73ca\u5609\u80c6\u6218\u5fc3\u60ca\u5730\u6293\u7d27\u743c\u6069\u7684\u80f3\u818a\uff0c\u5979\u7684\u8138\u8272\u56e0\u4e3a\u60ca\u5413\u800c\u82cd\u767d\uff0c\u5a07\u8eaf\u5fae\u5fae\u98a4\u6296\uff0c\u201c\u600e\u2026\u2026\u600e\u4e48\u56de\u4e8b\uff0c\u5c0f\u5f1f\uff1f\u201d</p><p>\u743c\u6069\u6b32\u5f85\u8bf4\u8bdd\uff0c\u5c31\u542c\u5f97\u4e00\u58f0\u9707\u8033\u6b32\u804b\u7684\u96f7\u9e23\uff0c\u6574\u4e2a\u9752\u94dc\u623f\u5b50\u90fd\u5267\u70c8\u5730\u6643\u4e86\u6643\u3002\u77f3\u50cf\u8f70\u7136\u70b8\u88c2\uff0c\u707f\u70c2\u5f97\u4ee4\u4eba\u65e0\u6cd5\u6b63\u89c6\u7684\u5f3a\u5149\u4ece\u4e2d\u8ff8\u5c04\u51fa\u6765\uff0c\u743c\u6069\u4e0d\u7531\u81ea\u4e3b\u5730\u62ac\u624b\u906e\u4f4f\u773c\uff0c\u63fd\u7740\u73ca\u5609\u540e\u9000\u4e24\u6b65\uff0c\u4ed6\u672c\u80fd\u5730\u611f\u53d7\u5230\u4e86\u6251\u9762\u800c\u6765\u7684\u51db\u7136\u5a01\u538b\u3002</p><p>\u51e0\u79d2\u949f\u540e\uff0c\u5f3a\u5149\u6563\u53bb\uff0c\u4e00\u4e2a\u5168\u8eab\u9ec4\u91d1\u94e0\u7532\u7684\u9ad8\u5927\u8eab\u5f71\uff0c\u4ee5\u9876\u5929\u7acb\u5730\u7684\u6c14\u52bf\u7ad9\u7acb\u5728\u5ba2\u5385\u4e2d\u592e\uff0c\u53cc\u8db3\u79bb\u5730\uff0c\u60ac\u6d6e\u5728\u534a\u7a7a\uff0c\u8d64\u7ea2\u7684\u77b3\u5b54\u4e2d\u95ea\u8000\u7740\u94f6\u8272\u7535\u5149\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u671d\u743c\u6069\u548c\u73ca\u5609\u770b\u8fc7\u6765\uff0c\u5145\u6ee1\u7740\u9a84\u6a2a\u3001\u66b4\u8e81\u548c\u72c2\u5984\u81ea\u5927\u7684\u58f0\u97f3\u4ece\u4ed6\u53e3\u4e2d\u53d1\u51fa\uff0c\u5728\u6574\u4e2a\u623f\u5b50\u91cc\u8f70\u9e23\u56de\u8361\u7740\uff0c\u9707\u5f97\u4eba\u8033\u819c\u9690\u9690\u4f5c\u75bc\u3002</p><p>\u201c\u662f\u54ea\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u6742\u79cd\uff0c\u80c6\u6562\u6253\u6270\u672c\u738b\u7684\u7f8e\u68a6\uff01\u201d</p></article>"}}}]);
