(window.webpackJsonp=window.webpackJsonp||[]).push([[1656],{1679:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516b\u8282 \u73ca\u5609\u7684\u654f\u611f",content:"<article><h2>\u7b2c\u4e09\u5341\u516b\u8282 \u73ca\u5609\u7684\u654f\u611f</h2><p>\u4f5c\u4e3a\u8d39\u4f26\u5927\u9646\u7684\u540d\u57ce\u4e4b\u4e00\uff0c\u6b27\u675c\u6797\u5176\u5b9e\u662f\u4e00\u5ea7\u6bd4\u8f83\u5e74\u8f7b\u7684\u57ce\u5e02\uff0c\u5b83\u4ee5\u524d\u53eb\u505a\u6708\u8c37\uff0c\u6709\u4e00\u4e9b\u6751\u5e84\u548c\u96f6\u661f\u7684\u68ee\u6797\uff0c\u5c45\u4f4f\u7740\u4eba\u7c7b\u548c\u7cbe\u7075\u3002\u4e09\u767e\u5e74\u524d\uff0c\u6b64\u5730\u88ab\u6851\u6bd4\u4e9a\u541e\u5e76\uff0c\u56e0\u4e3a\u5730\u7406\u4f4d\u7f6e\u6781\u5ea6\u4f18\u8d8a\uff0c\u6545\u6b64\u88ab\u5f53\u65f6\u7684\u5927\u7edf\u9886\u548c\u603b\u5546\u4f1a\u770b\u4e2d\uff0c\u51b3\u5b9a\u5728\u6b64\u5efa\u90fd\u3002</p><p>\u6851\u6bd4\u4e9a\u653f\u5e9c\u6295\u5165\u4e86\u6d77\u91cf\u7684\u8d44\u91d1\uff0c\u8bf7\u4e86\u6700\u8457\u540d\u7684\u5efa\u7b51\u5e08\u548c\u8bbe\u8ba1\u5e08\uff0c\u82b1\u8d39\u6574\u6574\u56db\u5341\u5e74\u65f6\u95f4\uff0c\u6700\u7ec8\u5728\u5e73\u5730\u4e0a\u5efa\u8d77\u4e86\u8fd9\u5ea7\u5b8f\u4f1f\u58ee\u89c2\u7684\u57ce\u5e02\u3002\u5b83\u89c4\u5212\u4e25\u6574\uff0c\u683c\u5c40\u6e05\u6670\uff0c\u884c\u653f\u533a\u3001\u5c45\u6c11\u533a\u3001\u5546\u4e1a\u533a\u3001\u795e\u6bbf\u533a\u3001\u5de5\u4e1a\u533a\u90fd\u6709\u7740\u660e\u786e\u7684\u5212\u5206\uff0c\u56db\u901a\u516b\u8fbe\u7684\u4e0b\u6c34\u7ba1\u9053\u5168\u90e8\u7528\u5ca9\u77f3\u780c\u6210\uff0c\u6781\u5927\u63d0\u9ad8\u4e86\u57ce\u5e02\u7684\u536b\u751f\u6c34\u51c6\u3002\u5982\u679c\u4ece\u9ad8\u7a7a\u4fef\u77b0\uff0c\u6b27\u675c\u6797\u57ce\u5c31\u50cf\u662f\u4e00\u4e2a\u5149\u8292\u56db\u5c04\u7684\u5de8\u5927\u65ed\u65e5\uff0c\u53c8\u50cf\u662f\u5411\u5916\u6269\u6563\u7684\u65e0\u6570\u5706\u73af\u3002\u57ce\u5e02\u7684\u6700\u4e2d\u5fc3\u4f4d\u7f6e\u662f\u6b63\u5706\u5f62\u7684\u5927\u5e7f\u573a\uff0c\u4e09\u5ea7\u5efa\u7b51\u2014\u2014\u5927\u4f1a\u5802\uff08\u6700\u9ad8\u7acb\u6cd5\u548c\u884c\u653f\u673a\u5173\uff09\u3001\u5b88\u62a4\u4e4b\u95e8\uff08\u9020\u5e01\u5382\uff09\u548c\u536b\u5175\u5854\uff08\u56fd\u9632\u5927\u53a6\uff09\u2014\u2014\u5448\u54c1\u5b57\u5f62\u5ea7\u843d\u5e7f\u573a\u4e0a\uff0c\u6784\u6210\u7b49\u8fb9\u4e09\u89d2\u5f62\u3002\u5341\u4e8c\u6761\u5bbd\u9614\u5f97\u8db3\u591f\u516d\u8f86\u9a6c\u8f66\u5e76\u9a7e\u9f50\u9a71\u7684\u9c9c\u82b1\u5927\u9053\u4ee5\u5e7f\u573a\u4e3a\u5706\u5fc3\uff0c\u50cf\u9633\u5149\u822c\u671d\u56db\u9762\u516b\u65b9\u8f90\u5c04\u51fa\u53bb\uff0c\u6784\u6210\u4e86\u57ce\u5e02\u7684\u4e3b\u5e72\u9053\uff0c\u76f4\u62b5\u6b27\u675c\u6797\u7684\u5341\u4e8c\u5ea7\u57ce\u95e8\u3002\u5efa\u57ce\u65f6\u523b\u610f\u4fdd\u7559\u7684\u5c0f\u7247\u68ee\u6797\uff0c\u4eba\u5de5\u5f00\u51ff\u7684\u6e56\u6cca\uff0c\u70b9\u7f00\u5728\u57ce\u5e02\u7684\u6bcf\u4e00\u5904\uff0c\u5448\u73b0\u51fa\u6b23\u6b23\u5411\u8363\u7684\u6c14\u606f\u3002</p><p>\u743c\u6069\u4e00\u884c\u4eba\u4ece\u6b63\u5317\u95e8\u8fdb\u5165\u4e86\u6b27\u675c\u6797\u57ce\uff0c\u51fa\u5177\u4e86\u8bc1\u660e\u6587\u4ef6\u540e\uff0c\u4ed6\u4eec\u5728\u536b\u5175\u7684\u62a4\u9001\u4e0b\u76f4\u5954\u5927\u4f1a\u5802\uff0c\u89d0\u89c1\u6851\u6bd4\u4e9a\u7684\u65b0\u4efb\u5927\u7edf\u9886\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\u3002\u5979\u662f\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u3001\u68d5\u53d1\u3001\u58f0\u97f3\u6d2a\u4eae\u7684\u4e2d\u5e74\u5973\u5b50\uff0c\u53cc\u624b\u9aa8\u8282\u7c97\u5927\uff0c\u663e\u5f97\u975e\u5e38\u6709\u529b\u91cf\uff0c\u5f85\u4eba\u4eb2\u5207\u800c\u6709\u793c\u4eea\uff0c\u6709\u7740\u826f\u597d\u7684\u8d35\u65cf\u6559\u517b\uff0c\u4f46\u6216\u8bb8\u662f\u56e0\u4e3a\u521a\u521a\u63a5\u624b\u8fd9\u4e2a\u5e9e\u5927\u800c\u6df7\u4e71\u7684\u56fd\u5bb6\uff0c\u8bf8\u591a\u4e8b\u52a1\u7e41\u5fd9\u52b3\u7d2f\uff0c\u5979\u7684\u7cbe\u795e\u72b6\u6001\u5e76\u4e0d\u662f\u5f88\u597d\uff0c\u7709\u5b87\u95f4\u6709\u5f88\u91cd\u7684\u75b2\u60eb\u4e4b\u8272\uff0c\u8bf4\u8bdd\u7684\u65f6\u5019\u4e5f\u7ecf\u5e38\u8d70\u795e\u3002\u743c\u6069\u4e0d\u591a\u505a\u6253\u6270\uff0c\u793c\u8282\u6027\u5730\u5bd2\u6684\u7247\u523b\uff0c\u5373\u4fbf\u8d77\u8eab\u544a\u8f9e\u3002\u53e6\u6709\u5b98\u5458\u8fc7\u6765\u7ed9\u4ed6\u5f00\u5177\u7279\u522b\u901a\u884c\u8bc1\uff0c\u53c8\u5b89\u6392\u8def\u4e0a\u62a4\u9001\u7684\u8b66\u536b\uff0c\u743c\u6069\u672c\u60f3\u5a49\u62d2\uff0c\u8f6c\u5ff5\u4e00\u60f3\u591a\u51e0\u4e2a\u514d\u8d39\u4fdd\u9556\u4e5f\u4e0d\u9519\uff0c\u4e8e\u662f\u4fbf\u63a5\u53d7\u4e86\u3002</p><p>\u653f\u5e9c\u90e8\u95e8\u7684\u529e\u4e8b\u6548\u7387\u5929\u7136\u62d6\u6c93\uff0c\u5c31\u7b97\u662f\u771f\u5fc3\u60f3\u5feb\u4e5f\u5feb\u4e0d\u8d77\u6765\uff0c\u7b49\u6240\u6709\u7684\u4e8b\u60c5\u529e\u5b8c\uff0c\u5df2\u7ecf\u662f\u508d\u665a\u65f6\u5206\uff0c\u66ae\u8272\u7b3c\u7f69\uff0c\u5915\u9633\u5c06\u5782\u3002\u4ed6\u4eec\u7b97\u662f\u9634\u9b42\u57ce\u7684\u4f7f\u8005\uff0c\u81ea\u7136\u6709\u4eba\u9886\u8def\u53bb\u4e13\u95e8\u7684\u5bbe\u9986\u4e0b\u69bb\uff0c\u65e0\u9700\u81ea\u5df1\u627e\u65c5\u5e97\u3002</p><p>\u8fd9\u51e0\u65e5\u4e3a\u4e86\u8eb2\u907f\u8ffd\u5175\uff0c\u4e0d\u5206\u663c\u591c\u7684\u8fde\u7eed\u8d76\u8def\uff0c\u51e0\u4e4e\u662f\u4e00\u523b\u4e0d\u505c\uff0c\u5927\u5bb6\u90fd\u9887\u4e3a\u75b2\u5026\uff0c\u867d\u7136\u96c7\u7684\u9a6c\u8f66\u8f66\u53a2\u8db3\u591f\u5bbd\u5927\uff0c\u53ef\u4ee5\u8eba\u4e0b\u7761\u89c9\uff0c\u4f46\u7ec8\u7a76\u662f\u5728\u65c5\u9014\u4e2d\uff0c\u6bd4\u4e0d\u5f97\u771f\u6b63\u7684\u4f4f\u5bb6\u4f11\u606f\u3002\u8fd9\u4e2a\u4e16\u754c\u4e0a\u53c8\u6ca1\u6709\u6a61\u80f6\uff0c\u5f39\u7c27\u5012\u662f\u5b58\u5728\uff0c\u4e5f\u505a\u4e0d\u51fa\u771f\u6b63\u4f18\u826f\u7684\u907f\u9707\u7cfb\u7edf\uff0c\u9053\u8def\u518d\u5e73\u5766\uff0c\u98a0\u7c38\u4e5f\u662f\u5728\u6240\u96be\u514d\u3002\u597d\u4e0d\u5bb9\u6613\u5230\u4e86\u5bbe\u9986\uff0c\u4e0b\u4e86\u8f66\uff0c\u987f\u65f6\u5168\u90fd\u677e\u4e86\u53e3\u6c14\uff0c\u5306\u5306\u5fd9\u5fd9\u5403\u665a\u9910\u3001\u6d17\u6fa1\u6362\u4e0a\u7761\u8863\uff0c\u5404\u81ea\u56de\u623f\u95f4\u7761\u89c9\u3002</p><p>\u743c\u6069\u7cbe\u795e\u4e5f\u5f88\u5dee\uff0c\u6b63\u60f3\u62b1\u7740\u59d0\u59d0\u505a\u4e2a\u9999\u751c\u7f8e\u68a6\uff0c\u4e00\u8f6c\u8138\u770b\u89c1\u73ca\u5609\u6b63\u5750\u5728\u5e8a\u5934\uff0c\u82e5\u6709\u6240\u601d\u3002\u5979\u521a\u521a\u6d17\u5b8c\u6fa1\uff0c\u5934\u53d1\u592a\u957f\u592a\u6d53\u5bc6\uff0c\u6ca1\u80fd\u5b8c\u5168\u64e6\u5e72\uff0c\u8fd8\u6709\u4e9b\u6e7f\u6f09\u6f09\u7684\u6c34\u6c7d\uff0c\u7a7f\u7740\u4e00\u4ef6\u5929\u84dd\u8272\u7684\u68c9\u8d28\u886c\u886b\uff0c\u957f\u957f\u7684\u4e0b\u6446\u906e\u4f4f\u4e86\u81c0\u90e8\uff0c\u4e24\u6761\u4fee\u957f\u7b14\u633a\u7684\u7f8e\u817f\u5b8c\u5168\u88f8\u9732\u51fa\u6765\uff0c\u5728\u706f\u4e0b\u6cdb\u7740\u7389\u767d\u8272\u7684\u67d4\u548c\u5fae\u5149\uff0c\u7eaf\u51c0\u65e0\u6687\u3002\u743c\u6069\u722c\u5230\u65c1\u8fb9\uff0c\u4ece\u8eab\u540e\u5c06\u5979\u62b1\u4f4f\uff0c\u201c\u60f3\u4ec0\u4e48\u5462\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd8\u6709\uff0c\u600e\u4e48\u53c8\u7a7f\u6211\u7684\u886c\u886b\u554a\u3002\u201d</p><p>\u201c\u59d0\u59d0\u559c\u6b22\u554a\uff0c\u6709\u4f60\u7684\u5473\u9053\u561b\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5f88\u5e72\u51c0\uff0c\u8ba9\u4eba\u89c9\u5f97\u5b89\u5fc3\uff0c\u800c\u4e14\u5f88\u5927\uff0c\u76f4\u63a5\u5c31\u80fd\u5f53\u7761\u8863\uff0c\u4e0d\u7528\u518d\u7a7f\u522b\u7684\u4e86\u3002\u201d</p><p>\u201c\u90a3\u59d0\u59d0\u5728\u60f3\u4ec0\u4e48\u5462\uff1f\u201d\u743c\u6069\u53c8\u95ee\u3002</p><p>\u201c\u6211\u5728\u60f3\u90a3\u4e2a\u91d1\u95ea\u95ea\u7684\u5bb6\u4f19\u8bf4\u7684\u8bdd\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5c0f\u5f1f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff0c\u4ed6\u4e00\u5f00\u59cb\u770b\u89c1\u6211\uff0c\u8bf4\u4ec0\u4e48\u6545\u4eba\uff0c\u597d\u50cf\u4ed6\u548c\u6211\u4ee5\u524d\u8ba4\u8bc6\u4f3c\u7684\u3002\u201d</p><p>\u201c\u660e\u663e\u662f\u4ed6\u8ba4\u9519\u4eba\u5566\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u4f60\u8fd9\u7b97\u662f\u7b2c\u4e00\u6b21\u51fa\u9634\u9b42\u57ce\uff0c\u600e\u4e48\u53ef\u80fd\u8ba4\u8bc6\u90a3\u5bb6\u4f19\u3002\u800c\u4e14\u4f60\u6ca1\u542c\u4ed6\u8bf4\u5417\uff0c\u2018\u524d\u6b21\u4e00\u522b\uff0c\u500f\u5ffd\u5343\u5e74\u2019\uff0c\u6309\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u59d0\u59d0\u4e0d\u4f46\u548c\u4ed6\u8ba4\u8bc6\uff0c\u800c\u4e14\u8fd8\u662f\u81f3\u5c11\u4e00\u5343\u5e74\u524d\u5c31\u8ba4\u8bc6\u4e86\u2014\u2014\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u660e\u663e\u5c31\u662f\u4ed6\u5934\u6655\u773c\u82b1\uff0c\u8ba4\u9519\u4eba\u4e86\u3002\u201d</p><p>\u201c\u4f1a\u8ba4\u9519\u4eba\u5417\uff1f\u201d</p><p>\u201c\u4f1a\u554a\uff0c\u5e38\u8a00\u8bf4\uff0c\u7269\u6709\u8fd1\u4f3c\uff0c\u4eba\u6709\u96f7\u540c\uff0c\u4e16\u754c\u8fd9\u4e48\u5927\uff0c\u4eba\u8fd9\u4e48\u591a\uff0c\u957f\u5f97\u76f8\u50cf\u7684\u4e8b\u60c5\u591a\u5f97\u662f\uff0c\u4e0d\u8db3\u4e3a\u5947\u7684\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u201d\u73ca\u5609\u79c0\u7709\u5fae\u5fae\u8e59\u8d77\uff0c\u201c\u5947\u602a\u5c31\u5947\u602a\u5728\u8fd9\u91cc\u5462\uff0c\u5c0f\u5f1f\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u6211\u660e\u660e\u662f\u4e0d\u8bb0\u5f97\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4f46\u4ed6\u8bf4\u4e86\u4e4b\u540e\uff0c\u6211\u81ea\u5df1\u4e00\u8def\u4e0a\u56de\u60f3\uff0c\u53cd\u800c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u8fd8\u771f\u6709\u51e0\u5206\u719f\u6089\uff0c\u597d\u50cf\u771f\u7684\u5728\u54ea\u91cc\u89c1\u5230\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u4f60\u8fd9\u662f\u81ea\u6211\u6697\u793a\u5566\u3002\u4eba\u7ecf\u5e38\u4f1a\u8fd9\u6837\u7684\uff0c\u660e\u660e\u6ca1\u6709\u7684\u4e8b\u60c5\uff0c\u56e0\u4e3a\u522b\u4eba\u8bf4\u5f97\u591a\u4e86\uff0c\u81ea\u5df1\u4e5f\u5f00\u59cb\u6000\u7591\uff0c\u4e8e\u662f\u8d8a\u60f3\u8d8a\u89c9\u5f97\u771f\u6709\u90a3\u56de\u4e8b\u2014\u2014\u5176\u5b9e\u538b\u6839\u5c31\u662f\u5e7b\u89c9\u800c\u5df2\u3002\u201d</p><p>\u201c\u4f46\u6211\u5f53\u65f6\u770b\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u786e\u5b9e\u5fc3\u91cc\u7a81\u7136\u5c31\u6d8c\u8d77\u4e86\u4e00\u79cd\u975e\u5e38\u8ba8\u538c\uff0c\u975e\u5e38\u53cd\u611f\u7684\u60c5\u7eea\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5c31\u597d\u50cf\u770b\u5230\u4e86\u67d0\u4e2a\u8ba4\u8bc6\u5f88\u4e45\u5e76\u4e14\u6df1\u6076\u75db\u7edd\u7684\u4eba\u4e00\u6837\u2026\u2026\u5f53\u65f6\u771f\u7684\u5c31\u662f\u8fd9\u79cd\u611f\u89c9\uff0c\u5e76\u4e0d\u662f\u5e7b\u89c9\u5440\u3002\u201d</p><p>\u201c\u8fd9\u4e5f\u5f88\u6b63\u5e38\u554a\uff0c\u90a3\u4e2a\u5bb6\u4f19\u8bf4\u8bdd\u6b20\u6241\uff0c\u505a\u4e8b\u6b20\u6241\uff0c\u6837\u5b50\u6b20\u6241\uff0c\u5c24\u5176\u90a3\u5f20\u8138\uff0c\u957f\u5f97\u5c45\u7136\u6bd4\u6211\u8fd8\u5e05\uff0c\u4e00\u770b\u5c31\u6b20\u6241\u3002\u6211\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\u5c31\u6068\u4e0d\u5f97\u628a\u4ed6\u6253\u5012\u5728\u5730\uff0c\u987a\u4fbf\u8e0f\u4e0a\u4e00\u4e07\u53ea\u811a\u3002\u6240\u4ee5\u59d0\u59d0\u8ba8\u538c\u4ed6\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u4e86\u561b\u3002\u201d</p><p>\u73ca\u5609\u6447\u6447\u5934\uff0c\u201c\u4e0d\u662f\u8fd9\u6837\u7684\u3002\u201d</p><p>\u201c\u5c31\u662f\u8fd9\u6837\u5566\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u522b\u80e1\u601d\u4e71\u60f3\u4e86\uff0c\u59d0\u59d0\uff0c\u4f60\u662f\u8fd9\u51e0\u5929\u6ca1\u4f11\u606f\u597d\uff0c\u592a\u7d2f\u4e86\u3002\u6765\uff0c\u5230\u6211\u6000\u91cc\u4e56\u4e56\u7761\u4e00\u89c9\uff0c\u9192\u6765\u5c31\u4ec0\u4e48\u95ee\u9898\u90fd\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u4fa7\u8fc7\u8138\uff0c\u770b\u7740\u4ed6\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e8b\u60c5\u7792\u7740\u6211\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u4e00\u51db\uff0c\u5239\u90a3\u95f4\u80cc\u4e0a\u51b7\u6c57\u90fd\u6e17\u4e86\u51fa\u6765\uff0c\u201c\u8bf4\u4ec0\u4e48\u5462\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u52c9\u5f3a\u7b11\u7740\uff0c\u201c\u6211\u600e\u4e48\u4f1a\u6709\u4e8b\u7792\u7740\u59d0\u59d0\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u660e\u660e\u5c31\u6709\u4e8b\u7792\u7740\u6211\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5c31\u5728\u521a\u624d\u6211\u8bf4\u7684\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u5f97\u51fa\u8fd9\u4e2a\u7ed3\u8bba\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u7684\u8868\u73b0\u5f88\u53cd\u5e38\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u6211\u8bf4\u6211\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u771f\u7684\u8ba4\u8bc6\u90a3\u5bb6\u4f19\uff0c\u4f60\u80af\u5b9a\u4f1a\u5e2e\u6211\u5206\u6790\uff0c\u5e2e\u6211\u731c\u6d4b\uff0c\u60f3\u90a3\u5bb6\u4f19\u5230\u5e95\u662f\u8c01\u3002\u4f60\u4f1a\u5f88\u8ba4\u771f\u5730\u8003\u8651\u6211\u8bf4\u7684\u8bdd\uff0c\u54ea\u6015\u662f\u518d\u8352\u8c2c\u518d\u532a\u5937\u6240\u601d\u7684\u8bdd\uff0c\u53ea\u8981\u662f\u6211\u8bf4\u7684\uff0c\u4f60\u90fd\u4f1a\u7528\u5fc3\u53bb\u60f3\uff0c\u4e0d\u4f1a\u4e0d\u5f53\u4e00\u56de\u4e8b\u7684\u3002\u800c\u7edd\u4e0d\u662f\u50cf\u4eca\u5929\u8fd9\u6837\uff0c\u6781\u529b\u60f3\u8981\u907f\u5f00\u8fd9\u4e2a\u8bdd\u9898\uff0c\u6781\u529b\u60f3\u8ba9\u6211\u76f8\u4fe1\u2018\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\u2019\uff0c\u60f3\u628a\u5b83\u542b\u7cca\u8fc7\u53bb\uff0c\u201d\u5979\u76ef\u7740\u743c\u6069\u7684\u773c\u775b\uff0c\u76ee\u5149\u70af\u70af\uff0c\u201c\u8ddf\u6211\u8bf4\u5b9e\u8bdd\uff0c\u5c0f\u5f1f\uff0c\u4f60\u662f\u4e0d\u662f\u77e5\u9053\u4e86\u4ec0\u4e48\u4e1c\u897f\uff0c\u53c8\u4e0d\u60f3\u8ba9\u6211\u77e5\u9053\u7684\uff1f\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u672c\u6765\u5c31\u2018\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\u2019\u561b\uff0c\u662f\u4f60\u81ea\u5df1\u591a\u5fc3\u2026\u2026\u201d</p><p>\u73ca\u5609\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u662f\u9759\u9759\u770b\u7740\u4ed6\uff0c\u743c\u6069\u548c\u5979\u6e05\u6f88\u7684\u76ee\u5149\u4e00\u4ea4\uff0c\u90a3\u80a1\u65e0\u5f62\u7684\u6c14\u52bf\u8ba9\u4ed6\u540e\u9762\u534a\u622a\u8bdd\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u53ea\u80fd\u8bb7\u8bb7\u5730\u505c\u4e0b\u3002\u201c\u597d\u5427\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u6700\u540e\u6295\u964d\uff0c\u201c\u6211\u8ddf\u4f60\u8bf4\u5b9e\u8bdd\u5c31\u662f\u4e86\uff0c\u4f60\u522b\u90a3\u4e48\u770b\u7740\u6211\uff0c\u770b\u5f97\u6211\u5fc3\u91cc\u5bb3\u6015\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u73ca\u5609\u5782\u4e0b\u773c\uff0c\u8eab\u4f53\u540e\u4ef0\uff0c\u9760\u5728\u743c\u6069\u6000\u4e2d\uff0c\u201c\u8bf4\u5427\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\u3002\u201d</p><p>\u201c\u5176\u5b9e\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u201d\u743c\u6069\u6c89\u541f\u534a\u664c\uff0c\u6700\u540e\u8bf4\uff0c\u201c\u4e0d\u662f\u642a\u585e\uff0c\u59d0\u59d0\uff0c\u662f\u771f\u7684\u8bf4\u4e0d\u6e05\u695a\u3002\u4e8b\u60c5\u5230\u73b0\u5728\uff0c\u6211\u6240\u6709\u77e5\u9053\u7684\uff0c\u6240\u6709\u638c\u63e1\u7684\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u662f\u731c\u6d4b\uff0c\u662f\u81c6\u60f3\uff0c\u6ca1\u6709\u534a\u70b9\u771f\u51ed\u5b9e\u636e\uff0c\u6216\u8005\u62ff\u5f97\u51fa\u624b\u7684\u5b9e\u9645\u4e1c\u897f\u3002\u8bf4\u5230\u5e95\uff0c\u6211\u4e5f\u53ea\u662f\u5728\u62c5\u5fc3\u7f62\u4e86\u3002\u201d</p><p>\u201c\u62c5\u5fc3\u4ec0\u4e48\u5462\uff1f\u201d\u73ca\u5609\u67d4\u58f0\u95ee\u3002</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\u5462\uff0c\u6211\u8fde\u81ea\u5df1\u5230\u5e95\u5728\u62c5\u5fc3\u4ec0\u4e48\uff0c\u5176\u5b9e\u90fd\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u53ea\u662f\u5728\u62c5\u5fc3\u2026\u2026\u52c9\u5f3a\u8981\u8bf4\u7684\u8bdd\uff0c\u662f\u62c5\u5fc3\u59d0\u59d0\u4f1a\u5377\u5165\u5230\u4e00\u4e9b\u5f88\u9ebb\u70e6\u5f88\u5371\u9669\u7684\u4e8b\u60c5\u91cc\u53bb\uff0c\u62c5\u5fc3\u59d0\u59d0\u4f1a\u53d8\u6210\u4e0d\u662f\u59d0\u59d0\u5427\u3002\u201d</p><p>\u73ca\u5609\u7b11\u8d77\u6765\uff0c\u201c\u8bf4\u4ec0\u4e48\u50bb\u8bdd\u5462\u3002\u59d0\u59d0\u5c31\u662f\u59d0\u59d0\uff0c\u600e\u4e48\u4f1a\u53d8\u6210\u4e0d\u662f\u59d0\u59d0\uff1f\u201d</p><p>\u201c\u6240\u4ee5\u8bf4\uff0c\u6211\u81ea\u5df1\u90fd\u8bf4\u4e0d\u6e05\u695a\u561b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\uff0c\u6211\u4fdd\u8bc1\uff0c\u7b49\u6211\u628a\u4e8b\u60c5\u518d\u5f04\u6e05\u695a\u70b9\uff0c\u628a\u6709\u4e9b\u731c\u6d4b\u518d\u7a0d\u5fae\u786e\u5b9a\u70b9\uff0c\u6211\u5c31\u4e00\u5b9a\u544a\u8bc9\u4f60\uff0c\u597d\u4e0d\u597d\u3002\u73b0\u5728\u771f\u7684\u662f\u60f3\u8bf4\u90fd\u4e0d\u77e5\u9053\u4ece\u4f55\u8bf4\u8d77\uff0c\u832b\u65e0\u5934\u7eea\uff0c\u4e00\u56e2\u4e71\u9ebb\u3002\u201d</p><p>\u73ca\u5609\u70b9\u4e86\u70b9\u5934\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u623f\u95e8\u8f7b\u8f7b\u88ab\u6572\u54cd\u3002\u201c\u4e24\u4f4d\uff0c\u201d\u838e\u73de\u514b\u7684\u58f0\u97f3\u5728\u95e8\u5916\u54cd\u8d77\uff0c\u201c\u7761\u4e86\u4e48\uff1f\u6211\u8fd9\u91cc\u6709\u70b9\u8fdb\u5c55\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5728\u8def\u4e0a\uff0c\u743c\u6069\u6697\u4e2d\u4ea4\u5f85\u4e86\u838e\u73de\u514b\u4e00\u4ef6\u4efb\u52a1\uff0c\u5c31\u662f\u64ac\u5f00\u80d6\u8001\u5934\u5f17\u96f7\u65af\u7684\u5634\u5df4\uff0c\u5c3d\u53ef\u80fd\u5730\u5f04\u660e\u767d\u8fd9\u4e00\u5806\u83ab\u540d\u5176\u5999\u4e8b\u60c5\u7684\u524d\u56e0\u540e\u679c\u3002\u8fd9\u51e0\u5929\u8d76\u8def\uff0c\u5176\u4ed6\u4eba\u90fd\u7d2f\u5f97\u591f\u545b\uff0c\u5f17\u96f7\u65af\u5012\u662f\u6e10\u6e10\u6062\u590d\u8fc7\u6765\uff0c\u5982\u679c\u4e0d\u662f\u88ab\u7ed1\u7740\u7684\u8bdd\uff0c\u4f30\u8ba1\u90fd\u80fd\u8dd1\u8dd1\u8df3\u8df3\u4e86\u3002\u867d\u7136\u770b\u8d77\u6765\u662f\u4e2a\u517b\u5c0a\u5904\u4f18\u7684\u8001\u80d6\u5b50\uff0c\u6ca1\u60f3\u5230\u5374\u9aa8\u5934\u5f88\u786c\uff0c\u65e0\u8bba\u600e\u4e48\u5a01\u80c1\u5229\u8bf1\uff0c\u5c31\u662f\u534a\u53e5\u8bdd\u4e0d\u8bf4\uff0c\u7cbe\u795e\u63a7\u5236\u9b54\u6cd5\u5bf9\u4ed6\u4e5f\u5168\u65e0\u6548\u679c\uff0c\u743c\u6069\u6c14\u5f97\u7b80\u76f4\u90fd\u60f3\u4e25\u5211\u62f7\u6253\uff0c\u53ea\u662f\u7167\u987e\u73ca\u5609\u7b49\u5973\u5b69\u5b50\u5728\u65c1\u8fb9\uff0c\u4e0d\u65b9\u4fbf\u52a8\u624b\uff0c\u6700\u540e\u4fbf\u51b3\u5b9a\u628a\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u838e\u73de\u514b\u53bb\u529e\u3002\u4e00\u5230\u8fd9\u5ea7\u5bbe\u9986\uff0c\u743c\u6069\u5c31\u7279\u5730\u901a\u8fc7\u966a\u540c\u5b98\u5458\uff0c\u6682\u65f6\u201c\u501f\u7528\u201d\u4e86\u5730\u4e0b\u5ba4\u7ed9\u9b45\u9b54\u505a\u5211\u8baf\u903c\u4f9b\u7684\u573a\u5730\uff0c\u540c\u65f6\u544a\u8bc9\u5979\u201c\u653e\u624b\u65bd\u4e3a\u201d\uff0c\u9664\u4e86\u4e0d\u51c6\u8272\u8bf1\u4e4b\u5916\u522b\u65e0\u4efb\u4f55\u9650\u5236\u3002\u838e\u73de\u514b\u4ee5\u524d\u662f\u94c1\u738b\u5ea7\u7684\u5934\u53f7\u6740\u624b\uff0c\u540e\u6765\u53c8\u5728\u6076\u9b54\u904d\u5730\u7684\u6df1\u6e0a\u91cc\u8fdb\u4fee\u6df1\u9020\u8fc7\uff0c\u8fd9\u70b9\u533a\u533a\u5c0f\u4e8b\uff0c\u60f3\u5fc5\u96be\u4e0d\u5012\u5979\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\uff0c\u838e\u73de\u514b\u7684\u786e\u975e\u5e38\u64c5\u957f\u6b64\u9053\uff0c\u8fd9\u624d\u4e0d\u5230\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5c31\u5df2\u7ecf\u6709\u4e86\u8fdb\u5c55\u3002</p><p>\u743c\u6069\u6253\u5f00\u95e8\uff0c\u8ba9\u9b45\u9b54\u8fdb\u6765\uff0c\u201c\u8bf4\u6765\u542c\u542c\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4ed6\u90fd\u62db\u4e86\u4e9b\u4ec0\u4e48\u3002\u201d</p><p>\u838e\u73de\u514b\u6709\u4e9b\u60ca\u8bb6\uff0c\u5979\u539f\u672c\u4ee5\u4e3a\u743c\u6069\u4f1a\u81ea\u5df1\u51fa\u6765\uff0c\u79c1\u4e0b\u548c\u5979\u4ea4\u8c08\uff0c\u800c\u4e0d\u662f\u8bf7\u5979\u8fdb\u623f\u95f4\u3002\u5979\u770b\u4e86\u770b\u5750\u5728\u65c1\u8fb9\u7684\u73ca\u5609\uff0c\u7136\u540e\u7528\u76ee\u5149\u8be2\u95ee\u743c\u6069\uff1a\u201c\u4f60\u786e\u5b9a\u771f\u7684\u8981\u5f53\u7740\u5979\u7684\u9762\u8bf4\uff1f\u201d</p><p>\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u793a\u610f\u65e0\u59a8\u3002\u4ee5\u4ed6\u7684\u672c\u610f\u6765\u8bf4\uff0c\u786e\u5b9e\u662f\u6682\u65f6\u4e0d\u60f3\u8ba9\u73ca\u5609\u77e5\u9053\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u4f46\u521a\u624d\u59d0\u5f1f\u4fe9\u4e00\u5e2d\u8c08\u8bdd\uff0c\u73ca\u5609\u5df2\u7ecf\u5bf9\u4ed6\u7684\u505a\u6cd5\u8868\u793a\u4e0d\u6ee1\uff0c\u867d\u7136\u6682\u65f6\u5b89\u629a\u4e0b\u53bb\uff0c\u4f46\u743c\u6069\u5982\u679c\u4e0d\u60f3\u6fc0\u5316\u77db\u76fe\uff0c\u4e0d\u60f3\u60f9\u59d0\u59d0\u751f\u6c14\uff0c\u90a3\u603b\u4e5f\u5f97\u6709\u6240\u8ba9\u6b65\u624d\u662f\u3002</p><p>\u9b45\u9b54\u8038\u8038\u80a9\uff0c\u201c\u90a3\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u4ece\u540e\u8170\u6478\u51fa\u4e00\u672c\u7b14\u8bb0\u7ffb\u5f00\uff0c\u201c\u9996\u5148\uff0c\u636e\u90a3\u6b7b\u80d6\u5b50\u8bf4\uff0c\u540e\u6765\u8e66\u51fa\u6765\u7684\u90a3\u4e2a\u5168\u8eab\u91d1\u95ea\u95ea\u7684\u5bb6\u4f19\uff0c\u53eb\u505a\u5409\u52d2\u4eca\u3002\u201d</p><p>\u201c\u5409\u52d2\u4eca\uff1f\u201d</p><p>\u8fd9\u4e2a\u540d\u5b57\u743c\u6069\u5012\u4e0d\u964c\u751f\uff0c\u867d\u7136\u4e5f\u8c08\u4e0d\u4e0a\u5341\u5206\u719f\u6089\u3002\u4ee5\u524d\u5728\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u548c\u4ed6\u804a\u5929\u65f6\u66fe\u7ecf\u63d0\u53ca\u8fc7\uff0c\u706d\u4ea1\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u4e24\u5927\u52bf\u529b\uff0c\u4e00\u662f\u7a46\u7f55\u745e\u5fb7\uff0c\u4e00\u662f\u6069\u745f\uff0c\u5b83\u4eec\u540e\u6765\u5206\u522b\u5efa\u56fd\uff0c\u7531\u795e\u738b\u7edf\u6cbb\uff0c\u5176\u4e2d\u6069\u745f\u8bf8\u795e\u738b\u7684\u9886\u8896\u5c31\u662f\u5409\u52d2\u4eca\u3002\u53e6\u5916\u975e\u5e38\u503c\u5f97\u4e00\u63d0\u7684\u662f\uff0c\u8fd9\u4f4d\u5409\u52d2\u4eca\uff0c\u53c8\u540d\u5409\u5c14\u4f3d\u7f8e\u4ec0\uff0c\u548c\u5730\u7403\u82cf\u7f8e\u5c14\u795e\u8bdd\u4e2d\u9f0e\u9f0e\u5927\u540d\u7684\u82f1\u96c4\u738b\u540c\u540d\u2014\u2014\u751a\u81f3\u6781\u6709\u53ef\u80fd\u6839\u672c\u5c31\u662f\u540c\u4e00\u4e2a\u4eba\u3002</p><p>\u5c31\u76ee\u524d\u6240\u638c\u63e1\u7684\u8d44\u6599\u6765\u770b\uff0c\u867d\u7136\u8fd8\u7f3a\u4e4f\u771f\u6b63\u786e\u51ff\u7684\u8bc1\u636e\uff0c\u4f46\u743c\u6069\u5df2\u7ecf\u57fa\u672c\u5728\u5fc3\u91cc\u5224\u5b9a\uff1a\u8fd9\u4e2a\u4e16\u754c\u7684\u6069\u745f\u8bf8\u795e\uff0c\u5c31\u662f\u5730\u7403\u82cf\u7f8e\u5c14\u795e\u8bdd\u4e2d\u7684\u90a3\u4e9b\u795e\u660e\u3002\u81f3\u4e8e\u5409\u5c14\u4f3d\u7f8e\u4ec0\u5728\u5730\u7403\u795e\u8bdd\u91cc\u660e\u660e\u53ea\u662f\u4e2a\u51e1\u4eba\uff0c\u9876\u591a\u7b97\u4e2a\u534a\u795e\uff08\u51c6\u786e\u8bf4\u662f\u4e09\u5206\u4e4b\u4e8c\u795e\u7947\u8840\u7edf\uff0c\u4e09\u5206\u4e4b\u4e00\u51e1\u4eba\u8840\u7edf\uff09\uff0c\u548c\u5e0c\u814a\u795e\u8bdd\u91cc\u7684\u6d77\u683c\u529b\u65af\u4e4b\u6d41\u540c\u4e00\u7ea7\u522b\uff0c\u5230\u4e86\u8fd9\u91cc\u5374\u6210\u4e86\u8bf8\u795e\u9886\u8896\uff0c\u5176\u4e2d\u7f18\u7531\u5c31\u4e0d\u751a\u6e05\u695a\u4e86\u3002</p><p>\u95ee\u9898\u5728\u4e8e\uff0c\u8fd9\u4f4d\u8001\u5144\u4e0d\u662f\u660e\u660e\u5df2\u7ecf\u6302\u6389\u4e86\u5417\uff1f</p><p>\u743c\u6069\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u6069\u745f\u8bf8\u795e\u5148\u662f\u5728\u201c\u517d\u4eba\u95e8\u201d\u4e8b\u4ef6\u4e2d\uff0c\u88ab\u517d\u4eba\u795e\u660e\u6740\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u6b7b\u4f24\u60e8\u91cd\u3002\u540e\u6765\u5728\u5341\u516d\u5e74\u524d\u7684\u52a8\u8361\u5e74\u4ee3\u4e2d\uff0c\u5409\u52d2\u4eca\u53c8\u88ab\u63d0\u4e9a\u739b\u7279\u51fb\u6740\uff0c\u6807\u5fd7\u7740\u6574\u4e2a\u6069\u745f\u795e\u7cfb\u7684\u8986\u706d\u3002\u8fd9\u4e9b\u90fd\u662f\u6885\u83f2\u65af\u544a\u8bc9\u4ed6\u7684\uff0c\u5e94\u8be5\u4e0d\u4f1a\u6709\u9519\u3002</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5df4\u5c14\u4e5f\u662f\u5341\u516d\u5e74\u524d\u6b7b\u7684\uff0c\u5982\u4eca\u4e0d\u4e5f\u662f\u5e7d\u7075\u4e0d\u6563\u5417\u3002\u5409\u52d2\u4eca\u597d\u6b79\u4e5f\u662f\u4e00\u65b9\u4e3b\u795e\uff0c\u6b7b\u4e86\u7167\u6837\u8fd8\u80fd\u5076\u5c14\u51fa\u6765\u900f\u900f\u6c14\uff0c\u5f88\u6b63\u5e38\u7684\u3002\u201d</p><p>\u201c\u5514\uff0c\u4f60\u8fd9\u4e48\u8bf4\u5012\u4e5f\u6ca1\u9519\uff0c\u53ef\u662f\u2026\u2026\u201d</p><p>\u5c31\u5982\u838e\u73de\u514b\u6240\u8a00\uff0c\u5df4\u5c14\u540c\u6837\u4e5f\u662f\u6b7b\u4e86\u5341\u516d\u5e74\uff0c\u4f46\u4ed6\u8001\u4eba\u5bb6\u7684\u82f1\u59ff\u743c\u6069\u524d\u5e74\u8fd8\u5728\u5de8\u9b54\u5c71\u8109\u77bb\u4ef0\u8fc7\uff0c\u4f46\u90a3\u662f\u56e0\u4e3a\u6885\u83f2\u65af\u662f\u5df4\u5c14\u7684\u5973\u513f\uff0c\u4f53\u5185\u6ee1\u8574\u6740\u622e\u795e\u529b\u7684\u5173\u7cfb\u3002\u96be\u4e0d\u6210\u8fd9\u80d6\u8001\u5934\u5f17\u96f7\u65af\uff0c\u662f\u5409\u52d2\u4eca\u7684\u513f\u5b50\u4e0d\u6210\uff1f</p><p>\u201c\u5f17\u96f7\u65af\u662f\u4e0d\u662f\u5409\u52d2\u4eca\u7684\u513f\u5b50\uff0c\u8fd9\u4e2a\u6211\u5012\u662f\u4e0d\u6e05\u695a\uff0c\u201d\u838e\u73de\u514b\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u4f46\u73b0\u5728\u6211\u4eec\u6293\u5230\u7684\u8fd9\u4e2a\u4eba\u80af\u5b9a\u4e0d\u662f\u3002\u201d</p><p>\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u3002</p><p>\u201c\u610f\u601d\u5f88\u7b80\u5355\uff0c\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u5f17\u96f7\u65af\u3002\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u771f\u6b63\u7684\u5f17\u96f7\u65af\uff0c\u90a3\u4e2a\u585e\u5c14\u7684\u5974\u96b6\u8d29\u5b50\uff0c\u827e\u5c14\u4f2f\u5854\u603b\u7763\u7684\u53d4\u7236\uff0c\u65e9\u5c31\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6211\u4eec\u73b0\u5728\u770b\u89c1\u7684\u662f\u4e2a\u5192\u5145\u8005\uff0c\u201d\u838e\u73de\u514b\u8f7b\u8f7b\u5f39\u4e86\u5f39\u624b\u6307\uff0c\u201c\u4ed6\u53eb\u535a\u6c99\u8fe6\uff0c\u662f\u4e2a\u6069\u745f\u4eba\uff0c\u5409\u52d2\u4eca\u7684\u82db\u5f8b\u4f8d\u8005\u3002\u201d</p></article>"}}}]);
