(window.webpackJsonp=window.webpackJsonp||[]).push([[1658],{1681:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u8282 \u524a\u82f9\u679c",content:"<article><h2>\u7b2c\u56db\u5341\u8282 \u524a\u82f9\u679c</h2><p>\u5355\u7eaf\u4ece\u5229\u76ca\u6743\u8861\u4e0a\u8bf4\uff0c\u7559\u7740\u94a5\u5319\u6bd4\u73b0\u5728\u5c31\u541e\u566c\u66f4\u597d\u3002\u7559\u7740\u94a5\u5319\uff0c\u6709\u53ef\u9884\u671f\u7684\u5de8\u5927\u5229\u76ca\uff08\u738b\u4e4b\u5b9d\u5e93\uff09\uff0c\u5c31\u7b97\u627e\u4e0d\u5230\u5b9d\u5e93\u6216\u8005\u61d2\u5f97\u53bb\u627e\uff0c\u4ee5\u540e\u7167\u6837\u8fd8\u80fd\u541e\u566c\u878d\u5408\uff0c\u53c8\u4e0d\u662f\u8fc7\u4e86\u4eca\u5929\u94a5\u5319\u4e0a\u7684\u795e\u529b\u5c31\u5931\u6548\u6d88\u6563\u3002\u541e\u566c\u94a5\u5319\uff0c\u878d\u5408\u795e\u529b\uff0c\u771f\u6b63\u7684\u610f\u4e49\u53ea\u5728\u4e8e\u52a0\u5feb\u901f\u5ea6\uff0c\u8282\u7701\u4e00\u4e9b\u65f6\u95f4\u7f62\u4e86\u3002</p><p>\u4ece\u7406\u667a\u4e0a\u8bf4\uff0c\u743c\u6069\u5e94\u8be5\u9009\u62e9\u5c06\u94a5\u5319\u7559\u4e0b\uff0c\u4f46\u4ed6\u770b\u4e86\u770b\u73ca\u5609\uff0c\u968f\u5373\u505a\u51fa\u4e86\u76f8\u53cd\u7684\u51b3\u5b9a\u3002</p><p>\u743c\u6069\u6b64\u6b21\u5306\u5306\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u5176\u5b9e\u6700\u4e3b\u8981\u7684\u76ee\u7684\uff0c\u4e0d\u662f\u4e3a\u4e86\u53bb\u6851\u6bd4\u4e9a\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u548c\u6885\u83f2\u65af\u4f1a\u5408\uff0c\u800c\u662f\u4e3a\u4e86\u73ca\u5609\u3002\u8bf8\u591a\u7684\u8ff9\u8c61\u548c\u82e5\u9690\u82e5\u73b0\u7684\u7ebf\u7d22\uff0c\u51dd\u805a\u6210\u4e0d\u7965\u7684\u9884\u5146\uff0c\u4e00\u79cd\u5c71\u96e8\u6b32\u6765\u66b4\u98ce\u5c06\u81f3\u7684\u538b\u8feb\u611f\uff0c\u4eff\u4f5b\u4e4c\u4e91\u822c\u7b3c\u7f69\u5fc3\u5934\uff0c\u8ba9\u4ed6\u5fd0\u5fd1\u4e0d\u5b89\u3002\u59d0\u5f1f\u4fe9\u540c\u65f6\u505a\u5947\u602a\u7684\u68a6\uff0c\u6614\u65e5\u88ab\u8c0b\u6740\u7684\u738b\u540e\uff0c\u838e\u5c14\u795e\u6bbf\u91cc\u7684\u94c1\u9762\u4eba\uff0c\u548c\u73ca\u5609\u6c14\u8d28\u795e\u4f3c\u7684\u96d5\u50cf\uff0c\u6240\u6709\u7684\u8fd9\u4e9b\uff0c\u90fd\u4ee4\u743c\u6069\u5fc3\u795e\u4e0d\u5b9a\uff0c\u603b\u89c9\u5f97\u5de8\u5927\u7684\u5371\u9669\u50cf\u662f\u6f5c\u4f0f\u5728\u9ed1\u6697\u4e2d\u7684\u731b\u517d\uff0c\u6b63\u6084\u65e0\u58f0\u606f\u5730\u4e00\u6b65\u6b65\u903c\u8fd1\u3002</p><p>\u5e26\u73ca\u5609\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u907f\u514d\u5377\u5165\u6f29\u6da1\uff0c\u8fd9\u79cd\u65b9\u6cd5\u53ef\u884c\uff0c\u4f46\u5e76\u4e0d\u662f\u957f\u4e45\u6253\u7b97\u3002\u95ee\u9898\u65e2\u7136\u5b58\u5728\uff0c\u90a3\u5c31\u8981\u9762\u5bf9\uff0c\u8981\u89e3\u51b3\uff0c\u9003\u907f\u7ec8\u7a76\u4e0d\u662f\u529e\u6cd5\uff0c\u9876\u591a\u662f\u4e00\u65f6\u7684\u6743\u5b9c\u4e4b\u8ba1\u3002\u6885\u83f2\u65af\u4e5f\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u68cb\u624b\u4eec\u6b63\u5728\u89d2\u529b\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u98ce\u66b4\u5df2\u7ecf\u5f62\u6210\uff0c\u800c\u4e14\u5728\u5feb\u901f\u6269\u5927\uff0c\u743c\u6069\u538b\u6839\u907f\u65e0\u53ef\u907f\uff0c\u9003\u65e0\u53ef\u9003\uff0c\u53ea\u80fd\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u63d0\u5347\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u7136\u540e\u5f53\u5934\u8fce\u4e0a\u3002</p><p>\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\uff0c\u63d0\u5347\u81ea\u5df1\u7684\u529b\u91cf\u2014\u2014\u8fd9\u624d\u662f\u743c\u6069\u7684\u5f53\u52a1\u4e4b\u6025\uff0c\u5176\u4ed6\u7684\u4e00\u5207\uff0c\u90fd\u53ef\u4ee5\u5ef6\u540e\u518d\u8bae\u3002\u94a5\u5319\u4e0a\u7684\u90a3\u70b9\u795e\u529b\uff0c\u4e0d\u53ef\u80fd\u8ba9\u4ed6\u7acb\u523b\u6210\u957f\u4e3a\u9876\u7ea7\u5f3a\u8005\uff0c\u4f46\u80fd\u6293\u4f4f\u4e00\u70b9\uff0c\u4fbf\u662f\u4e00\u70b9\u5e0c\u671b\uff0c\u80fd\u591a\u5f97\u4e00\u5206\uff0c\u4fbf\u662f\u4e00\u5206\u673a\u4f1a\u3002</p><p>\u660e\u767d\u4e86\u8fd9\u4e00\u70b9\uff0c\u505a\u51fa\u9009\u62e9\u5c31\u5f88\u5bb9\u6613\u4e86\u3002\u541e\u566c\u94a5\u5319\u7684\u795e\u529b\uff0c\u867d\u7136\u6bd4\u8f83\u53ef\u60dc\uff0c\u5f88\u53ef\u80fd\u56e0\u6b64\u5c31\u5f7b\u5e95\u653e\u5f03\u4e86\u6253\u5f00\u201c\u738b\u4e4b\u5b9d\u5e93\u201d\u7684\u673a\u4f1a\uff0c\u800c\u4e14\u663e\u7136\u4f1a\u591a\u7ad6\u7acb\u4e00\u4e2a\u654c\u4eba\uff0c\u4fbf\u662f\u60f3\u8981\u5f97\u5230\u94a5\u5319\u7684\u5409\u52d2\u4eca\u738b\u540e\u2014\u2014\u4f46\u81f3\u5c11\u4e89\u53d6\u5230\u4e86\u65f6\u95f4\u3002\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u8fd9\u5c31\u5df2\u7ecf\u8db3\u591f\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5018\u82e5\u4e0d\u662f\u56e0\u4e3a\u5fcc\u60ee\u838e\u5c14\uff0c\u52a0\u4e0a\u5f71\u706b\u5728\u4f53\u5185\u592a\u8fc7\u9690\u853d\uff0c\u6f5c\u4f0f\u592a\u6df1\uff0c\u65e0\u4ece\u641c\u5bfb\uff0c\u4ed6\u90fd\u60f3\u628a\u5f71\u706b\u4e5f\u7ed9\u7ed9\u541e\u566c\u4e86\u3002</p><p>\u65e2\u7136\u505a\u51fa\u51b3\u5b9a\uff0c\u90a3\u4e48\u4e8b\u4e0d\u5b9c\u8fdf\u3002\u743c\u6069\u6536\u8d77\u94a5\u5319\uff0c\u544a\u8bc9\u73ca\u5609\u4ed6\u6709\u4e8b\u8981\u5904\u7406\uff0c\u4eca\u665a\u4e0d\u80fd\u966a\u5979\u4e86\u3002\u73ca\u5609\u4f3c\u4e4e\u5e76\u4e0d\u5728\u610f\uff0c\u4e5f\u4e0d\u591a\u95ee\uff0c\u201c\u53bb\u5427\u53bb\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u65e9\u70b9\u4f11\u606f\uff0c\u522b\u592a\u7d2f\u7740\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u9b45\u9b54\u4e5f\u968f\u4e4b\u8d77\u8eab\uff0c\u51c6\u5907\u548c\u743c\u6069\u4e00\u8d77\u544a\u8f9e\uff0c\u5374\u88ab\u73ca\u5609\u53eb\u4f4f\u4e86\u3002\u201c\u665a\u4e0a\u8fd8\u6709\u4e8b\u4e48\uff0c\u838e\u73de\u514b\uff0c\u201d\u5979\u7b11\u7740\u95ee\u3002</p><p>\u838e\u73de\u514b\u4e0d\u660e\u6240\u4ee5\u5730\u6447\u6447\u5934\uff0c\u201c\u6ca1\u4e8b\uff0c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u90a3\u966a\u6211\u7761\u597d\u4e0d\u597d\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u4e00\u4e2a\u4eba\u4f1a\u5bb3\u6015\u5462\u3002\u201d</p><p>\u838e\u73de\u514b\u770b\u7740\u743c\u6069\uff0c\u540e\u8005\u505a\u4e86\u4e2a\u9690\u853d\u7684\u624b\u52bf\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u7136\u540e\u8d70\u51fa\u623f\u95f4\u3002</p><p>\u73ca\u5609\u4e0d\u662f\u90a3\u79cd\u80c6\u602f\u6015\u9ed1\u7684\u5973\u5b69\u5b50\uff0c\u81ea\u4ece\u743c\u6069\u8fdb\u5165\u5deb\u5e08\u5b66\u6821\u4e4b\u540e\uff0c\u8fd9\u4e9b\u5e74\u6765\u805a\u5c11\u79bb\u591a\uff0c\u5979\u4e00\u76f4\u90fd\u662f\u4e00\u4e2a\u4eba\u7761\uff0c\u4e5f\u4ece\u6ca1\u542c\u5979\u8bf4\u5bb3\u6015\u8fc7\u3002\u6240\u4ee5\u8fd9\u53e5\u8bdd\u53ea\u662f\u6258\u8bcd\uff0c\u5979\u5e94\u8be5\u662f\u6709\u4ec0\u4e48\u8bdd\u8981\u548c\u838e\u73de\u514b\u5355\u72ec\u8bf4\u3002\u4e24\u4e2a\u5973\u5b69\u5b50\u95fa\u623f\u5bc6\u8bed\uff0c\u5341\u6709\u516b\u4e5d\u4f1a\u6d89\u53ca\u5230\u743c\u6069\uff0c\u4e0d\u8fc7\u65e0\u6240\u8c13\u4e86\uff0c\u838e\u73de\u514b\u806a\u660e\u72e1\u9ee0\uff0c\u9605\u5386\u4e30\u5bcc\uff0c\u4ec0\u4e48\u8bdd\u8be5\u8bf4\uff0c\u4ec0\u4e48\u8bdd\u4e0d\u8be5\u8bf4\uff0c\u5979\u81ea\u7136\u77e5\u9053\u5206\u5bf8\u3002</p><p>\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u5c06\u4ece\u80d6\u5b50\u8eab\u4e0a\u83b7\u5f97\u7684\u6240\u6709\u4e1c\u897f\u90fd\u653e\u5728\u684c\u4e0a\uff0c\u4e24\u679a\u94a5\u5319\uff0c\u4e00\u672c\u9b54\u6cd5\u4e66\u3002\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u628a\u5f00\u542f\u8fdc\u5c71\u9632\u5fa1\u6cd5\u9635\u7684\u94a5\u5319\u548c\u90a3\u672c\u8584\u8584\u7684\u9b54\u6cd5\u4e66\u90fd\u6254\u8fdb\u4e86\u6b21\u5143\u888b\uff0c\u5c06\u90a3\u628a\u201c\u738b\u4e4b\u5b9d\u5e93\u201d\u7684\u94a5\u5319\u62ff\u5728\u624b\u4e0a\uff0c\u4ed4\u7ec6\u7aef\u8be6\uff0c\u82e5\u6709\u6240\u601d\u3002</p><p>\u4ed6\u5df2\u7ecf\u6709\u8fc7\u4e24\u6b21\u541e\u566c\u795e\u529b\u7684\u7ecf\u9a8c\uff0c\u800c\u4e14\u4e24\u6b21\u90fd\u5f88\u6210\u529f\uff0c\u6240\u4ee5\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u8fd9\u6b21\u4f1a\u5931\u8d25\uff0c\u66f4\u4e0d\u62c5\u5fc3\u4f1a\u88ab\u5409\u52d2\u4eca\u5723\u8005\u9644\u4f53\u3002\u795e\u7947\u8981\u5723\u8005\u964d\u4e34\uff0c\u5fc5\u987b\u8981\u6709\u8db3\u591f\u5408\u9002\u7684\u201c\u5bb9\u5668\u201d\uff0c\u81f3\u5c11\u8981\u662f\u8654\u8bda\u7684\u4fe1\u5f92\u624d\u884c\uff0c\u80d6\u5b50\u4f1a\u53d8\u6210\u5723\u8005\uff0c\u56e0\u4e3a\u4ed6\u662f\u82db\u5f8b\u4f8d\u8005\uff0c\u662f\u9ad8\u9636\u796d\u53f8\uff0c\u743c\u6069\u548c\u5409\u52d2\u4eca\u538b\u6839\u4e0d\u8ba4\u8bc6\uff0c\u81ea\u7136\u65e0\u6240\u8c13\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u524d\u4e24\u6b21\u90fd\u662f\u5728\u5e8a\u4e0a\u548c\u5973\u5b69\u5b50\u4e91\u96e8\u6b22\u597d\uff0c\u8fd9\u6b21\u5374\u662f\u8981\u628a\u4e00\u628a\u8fd9\u4e48\u5927\u7684\u91d1\u5c5e\u94a5\u5319\u7ed9\u541e\u4e0b\u53bb\u2026\u2026\u8fd8\u771f\u662f\u6709\u70b9\u5fc3\u7406\u969c\u788d\u3002</p><p>\u743c\u6069\u72b9\u8c6b\u534a\u664c\uff0c\u5148\u63b7\u51fa\u4e24\u679a\u68cb\u5b50\uff0c\u5316\u4f5c\u4e24\u540d\u5353\u5c14\u6218\u58eb\u9b54\u50cf\uff0c\u5b88\u5728\u8eab\u8fb9\u514d\u5f97\u88ab\u4eba\u6253\u6270\u3002\u7136\u540e\u5750\u5728\u5e8a\u4e0a\uff0c\u5c06\u5fc3\u4e00\u6a2a\uff0c\u628a\u94a5\u5319\u653e\u8fdb\u5634\u91cc\uff0c\u6b63\u51c6\u5907\u5f80\u4e0b\u541e\u54bd\uff0c\u5374\u89c9\u94a5\u5319\u4eff\u4f5b\u5de7\u514b\u529b\u822c\u5165\u53e3\u5373\u5316\uff0c\u77ac\u95f4\u53d8\u6210\u6db2\u4f53\u987a\u7740\u54bd\u5589\u6d41\u4e0b\uff0c\u5728\u80f8\u8154\u91cc\u53c8\u7a81\u7136\u6a2a\u4e98\u4f4f\u4e86\uff0c\u50cf\u662f\u91cd\u65b0\u51dd\u6210\u94a5\u5319\uff0c\u5361\u5728\u90a3\u91cc\uff0c\u4e0d\u4e0a\u4e5f\u4e0d\u4e0b\u3002\u743c\u6069\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u4e5f\u4e0d\u614c\u5f20\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u7791\u76ee\u5185\u89c6\uff0c\u5f00\u59cb\u6309\u7167\u524d\u4e24\u6b21\u7684\u7ecf\u9a8c\u8fdb\u884c\u878d\u5408\u3002</p><p>\u51b0\u51b7\u800c\u5145\u6ee1\u6740\u610f\u7684\u529b\u91cf\u4ece\u743c\u6069\u8eab\u4f53\u6bcf\u4e2a\u89d2\u843d\u91cc\u5347\u8d77\uff0c\u4eff\u4f5b\u65e0\u6570\u6761\u7ec6\u7ec6\u7684\u9ed1\u8272\u4e1d\u7ebf\uff0c\u53c8\u82e5\u5636\u5636\u5410\u4fe1\u7684\u6e38\u86c7\uff0c\u8fc5\u6377\u53c8\u6084\u65e0\u58f0\u606f\u5730\u4ece\u56db\u9762\u516b\u65b9\u671d\u7740\u94a5\u5319\u6d8c\u6765\uff0c\u5c06\u5b83\u7262\u7262\u5305\u88f9\u5728\u5176\u4e2d\u3002\u8fd9\u4fbf\u662f\u743c\u6069\u524d\u4e24\u6b21\u5438\u53d6\u5230\u7684\u5df4\u5c14\u795e\u529b\uff0c\u5f53\u65f6\u4ed6\u4ee5\u4e3a\u88ab\u5b8c\u5168\u6d88\u878d\u4e86\uff0c\u8fde\u6885\u83f2\u65af\u90fd\u611f\u5e94\u4e0d\u5230\uff0c\u540e\u6765\u624d\u77e5\u9053\u795e\u529b\u672c\u8eab\u4ecd\u7136\u5b58\u5728\uff0c\u53ea\u662f\u5df4\u5c14\u7684\u6740\u622e\u5370\u8bb0\u88ab\u62b9\u9500\u4e86\u3002\u56e0\u4e3a\u62e5\u6709\u7684\u795e\u529b\u592a\u5fae\u5f31\u7684\u7f18\u6545\uff0c\u53ea\u76f8\u5f53\u4e8e\u4e00\u4e2a\u666e\u901a\u7684\u795e\u5b50\uff0c\u538b\u6839\u65e0\u6cd5\u5177\u73b0\u5916\u5316\uff0c\u4f46\u5728\u8fd9\u79cd\u201c\u5185\u90e8\u6d88\u5316\u201d\u7684\u65f6\u5019\uff0c\u8fd8\u662f\u5927\u6709\u4f5c\u7528\u7684\u3002\u501f\u52a9\u795e\u529b\u6765\u5bf9\u4ed8\u795e\u529b\uff0c\u6bd4\u8d77\u81ea\u5df1\u767d\u624b\u8d77\u5bb6\uff0c\u81ea\u7136\u4e8b\u534a\u529f\u500d\u3002</p><p>\u94a5\u5319\u4e0a\u7efd\u653e\u51fa\u91d1\u8272\u7684\u523a\u773c\u5149\u8292\uff0c\u5343\u4e07\u9053\u6781\u5fae\u5c0f\u7684\u95ea\u7535\u5728\u5176\u4e2d\u7a7f\u68ad\u8df3\u8dc3\uff0c\u6784\u6210\u7403\u5f62\u7684\u9632\u5fa1\u58c1\uff0c\u52aa\u529b\u62b5\u5fa1\u7740\u4fb5\u88ad\u3002\u7136\u800c\u8fd9\u4e00\u5207\u90fd\u662f\u5f92\u52b3\uff0c\u743c\u6069\u4e0d\u7d27\u4e0d\u6162\uff0c\u4e0d\u614c\u4e0d\u5fd9\uff0c\u9a71\u4f7f\u7740\u6740\u622e\u795e\u529b\u51b2\u523a\u3001\u649e\u51fb\u3001\u6e17\u900f\u3001\u6f5c\u5165\uff0c\u50cf\u91cd\u91cd\u4e4c\u4e91\u853d\u65e5\u822c\uff0c\u4e00\u70b9\u70b9\u5730\u63a9\u76d6\u7740\u3001\u4fb5\u8680\u7740\u94a5\u5319\u7684\u5149\u8292\uff0c\u8ba9\u5b83\u53d8\u5f97\u9eef\u6de1\u4e0b\u6765\u3002</p><p>\u8fd9\u4e2a\u8fc7\u7a0b\u6781\u5176\u6f2b\u957f\uff0c\u4f46\u743c\u6069\u4e5f\u6781\u6709\u8010\u5fc3\u3002\u6700\u7ec8\uff0c\u6240\u6709\u7684\u91d1\u5149\u548c\u95ea\u7535\u90fd\u88ab\u541e\u566c\uff0c\u4ec5\u7559\u4e0b\u94a5\u5319\u672c\u8eab\uff0c\u9759\u9759\u60ac\u6d6e\u5728\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5b83\u4e0a\u9762\u954c\u523b\u7684\u6954\u5f62\u6587\u5b57\u50cf\u661f\u8fb0\u4e00\u6837\u5f00\u59cb\u95ea\u95ea\u53d1\u5149\uff0c\u5f88\u5fae\u5f31\uff0c\u4f46\u5f88\u6e05\u6670\u3002\u7136\u540e\u94a5\u5319\u5f00\u59cb\u626d\u66f2\uff0c\u53d8\u6210\u4e00\u56e2\u91d1\u8272\u7403\u4f53\uff0c\u5b83\u6025\u901f\u5730\u5d29\u584c\u3001\u6536\u7f29\uff0c\u76f4\u5230\u53d8\u6210\u4e00\u4e2a\u51e0\u4e4e\u770b\u4e0d\u89c1\u7684\u70b9\u3002</p><p>\u7136\u540e\u5b83\u5267\u70c8\u5730\u7206\u70b8\u51fa\u6765\u3002</p><p>\u4eff\u4f5b\u98d3\u98ce\uff0c\u4eff\u4f5b\u6d77\u5578\uff0c\u6f8e\u6e43\u7684\u6c14\u6d6a\u88f9\u631f\u7740\u95ea\u7535\uff0c\u50cf\u5bc6\u96c6\u7684\u9ec4\u91d1\u7bad\u96e8\uff0c\u671d\u7740\u56db\u9762\u516b\u65b9\u6fc0\u5c04\uff0c\u5b83\u4eec\u77ac\u95f4\u7a7f\u900f\u4e86\u6740\u622e\u795e\u529b\u7f16\u7ec7\u7684\u4e4c\u4e91\uff0c\u76f4\u63a5\u653b\u51fb\u743c\u6069\u7684\u7cbe\u795e\u3002\u8fd9\u7b97\u4e0d\u4e0a\u731d\u7136\u751f\u53d8\uff0c\u4e0d\u8fc7\u662f\u6700\u540e\u7684\u6323\u624e\u7f62\u4e86\uff0c\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\u3002</p><p>\u82cd\u9752\u8272\u7684\u94c1\u5e55\u5728\u610f\u8bc6\u4e2d\u5f90\u5f90\u964d\u4e0b\uff0c\u963b\u9694\u4e86\u5168\u90e8\u91d1\u7bad\uff0c\u9ed1\u6697\u6c79\u6d8c\u800c\u81f3\uff0c\u541e\u566c\u4e86\u6240\u6709\u5149\u660e\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u743c\u6069\u518d\u6b21\u9192\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u7684\u4e0a\u5348\uff0c\u51ac\u65e5\u7684\u548c\u7166\u9633\u5149\u4ece\u7a97\u5916\u900f\u8fdb\uff0c\u7167\u5728\u5367\u5ba4\u7684\u6697\u9ec4\u8272\u5730\u677f\u4e0a\uff0c\u6cdb\u7740\u6e29\u6696\u7684\u5149\u6cfd\u3002\u623f\u95f4\u91cc\u975e\u5e38\u5b89\u9759\uff0c\u81ea\u5df1\u8eba\u5728\u5e8a\u4e0a\uff0c\u8eab\u4e0a\u76d6\u7740\u539a\u539a\u7684\u6bdb\u6bef\uff0c\u4e00\u4f4d\u808c\u80a4\u50cf\u725b\u5976\u822c\u767d\u7699\u7684\u8910\u53d1\u5c11\u5973\u5750\u5728\u5e8a\u8fb9\u7684\u6905\u5b50\u4e0a\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u628a\u5c0f\u5200\uff0c\u6b63\u795e\u60c5\u4e13\u6ce8\u5730\u5728\u524a\u82f9\u679c\u3002</p><p>\u968f\u7740\u950b\u5229\u7684\u5200\u950b\u79fb\u8f6c\uff0c\u679c\u76ae\u4e00\u5708\u5708\u5730\u4ece\u82f9\u679c\u4e0a\u5265\u843d\u4e0b\u6765\uff0c\u8fde\u7f00\u4e0d\u65ad\uff0c\u76f4\u5782\u5230\u5730\u3002\u773c\u770b\u5c31\u8981\u524a\u5b8c\uff0c\u5979\u7684\u795e\u6001\u8d8a\u53d1\u8ba4\u771f\uff0c\u8fd0\u5200\u66f4\u6162\uff0c\u663e\u7136\u4e0d\u60f3\u5728\u6700\u540e\u5173\u5934\u529f\u4e8f\u4e00\u7bd1\u3002\u743c\u6069\u770b\u5f97\u6709\u8da3\uff0c\u4e5f\u4e0d\u51fa\u58f0\u6253\u6270\uff0c\u9759\u9759\u5730\u770b\u7740\uff0c\u53ea\u89c1\u5979\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4e00\u70b9\u4e00\u70b9\uff0c\u6700\u7ec8\u5c06\u679c\u76ae\u5b8c\u5b8c\u6574\u6574\u6ca1\u6709\u65ad\u88c2\u5730\u524a\u4e86\u4e0b\u6765\uff0c\u7136\u540e\u8138\u4e0a\u9732\u51fa\u4e86\u5145\u6ee1\u5b69\u5b50\u6c14\u7684\u80dc\u5229\u7b11\u5bb9\uff0c\u7b80\u76f4\u628a\u743c\u6069\u770b\u5f97\u5446\u4e86\u3002</p><p>\u4ed6\u4ece\u672a\u60f3\u5230\u8fc7\uff0c\u773c\u524d\u7684\u5c11\u5973\uff0c\u4e5f\u4f1a\u6709\u8fd9\u6837\u5929\u771f\u800c\u5355\u7eaf\uff0c\u5374\u662f\u7f8e\u4e3d\u5f97\u60ca\u4eba\u7684\u7b11\u5bb9\u3002</p><p>\u6216\u8bb8\u662f\u611f\u53d7\u5230\u4e86\u65c1\u8fb9\u7684\u89c6\u7ebf\uff0c\u5c11\u5973\u8f6c\u8fc7\u5934\u6765\uff0c\u6b63\u548c\u743c\u6069\u56db\u76ee\u76f8\u5bf9\u3002\u201c\u9192\u4e86\uff1f\u201d\u5979\u7b11\u7740\u95ee\uff0c\u987a\u624b\u7528\u5200\u53c9\u7740\u82f9\u679c\uff0c\u9012\u4e86\u8fc7\u6765\uff0c\u201c\u6211\u521a\u524a\u597d\u7684\uff0c\u5c1d\u5c1d\u770b\u3002\u201d</p><p>\u743c\u6069\u5de6\u624b\u63a5\u8fc7\u82f9\u679c\uff0c\u5374\u6ca1\u6709\u5403\uff0c\u800c\u662f\u653e\u5728\u65c1\u8fb9\u67dc\u5b50\u4e0a\uff0c\u540c\u65f6\u53f3\u624b\u63e1\u7740\u5979\u7684\u624b\u8155\uff0c\u987a\u52bf\u4e00\u62c9\uff0c\u5c06\u5979\u62c9\u5230\u5e8a\u4e0a\u3002\u201c\u5e72\u5417\uff1f\u201d\u5979\u683c\u683c\u7b11\u7740\uff0c\u5374\u5e76\u4e0d\u53cd\u6297\uff0c\u4e56\u4e56\u4efb\u4ed6\u6446\u5e03\u3002</p><p>\u201c\u5148\u5403\u4f60\uff0c\u518d\u5403\u82f9\u679c\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u7537\u5b50\u7761\u68a6\u65b9\u9192\uff0c\u6b63\u662f\u8840\u6c14\u6700\u76db\u7684\u65f6\u5019\uff0c\u52a0\u4e0a\u8fd9\u51e0\u65e5\u4e00\u76f4\u5728\u5fd9\u7740\u8d76\u8def\u5954\u6ce2\uff0c\u65e9\u5c31\u6709\u4e9b\u6309\u637a\u4e0d\u4f4f\uff0c\u80ef\u4e0b\u6b63\u4e00\u67f1\u64ce\u5929\u3002\u800c\u878d\u5408\u795e\u529b\u7684\u6210\u529f\uff0c\u4e5f\u8ba9\u4ed6\u79ef\u90c1\u7684\u6b32\u706b\u8fdb\u4e00\u6b65\u81a8\u80c0\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u60f3\u8981\u53d1\u6cc4\u51fa\u6765\u3002\u73b0\u5728\u8eab\u8fb9\u5c31\u6b63\u6709\u4f4d\u5343\u5a07\u767e\u5a9a\u7684\u7f8e\u4eba\u513f\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u8fd8\u5ba2\u6c14\u4ec0\u4e48\uff0c\u5c31\u5f53\u662f\u5e86\u795d\u5427\u3002</p><p>\u4ed6\u629a\u6478\u7740\u5c11\u5973\u7684\u79c0\u53d1\uff0c\u547d\u4ee4\u5979\u8dea\u5728\u5e8a\u4e0a\uff0c\u7528\u53e3\u8154\u6e29\u67d4\u5730\u5305\u5bb9\u629a\u6170\u7740\u4ed6\u9ad8\u6da8\u7684\u6b32\u671b\u3002\u201c\u5341\u5206\u949f\uff0c\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u201c\u5426\u5219\u5c31\u8981\u63a5\u53d7\u60e9\u7f5a\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u5c11\u5973\u5e94\u4e86\u4e00\u58f0\uff0c\u5f00\u59cb\u57cb\u5934\u52aa\u529b\u5e72\u6d3b\uff0c\u5341\u5206\u949f\u7684\u65f6\u9650\u5f88\u77ed\uff0c\u5982\u679c\u662f\u6885\u83f2\u65af\u6216\u8005\u8299\u857e\u72c4\uff0c\u90a3\u80af\u5b9a\u662f\u7edd\u5bf9\u65e0\u6cd5\u5b8c\u6210\u7684\u4efb\u52a1\uff0c\u4f46\u5bf9\u4e8e\u5979\u800c\u8a00\u5012\u4e5f\u5e76\u4e0d\u7b97\u975e\u5e38\u8270\u5de8\uff0c\u81f3\u5c11\u8fd8\u6709\u505a\u5230\u7684\u53ef\u80fd\u3002\u5979\u4f7f\u51fa\u4e86\u6d51\u8eab\u89e3\u6570\uff0c\u6a31\u5507\u542e\u5438\uff0c\u9999\u820c\u8214\u8210\uff0c\u7ea4\u7ea4\u53cc\u624b\u4e5f\u52a0\u5165\u8fdb\u6765\uff0c\u5e2e\u5fd9\u629a\u6478\u63c9\u634f\uff0c\u4fee\u957f\u7389\u6307\u5728\u7537\u4eba\u817f\u80a1\u95f4\u8f7b\u8f7b\u52fe\u52d2\uff0c\u5728\u4ed6\u81c0\u540e\u6253\u7740\u8f6c\uff0c\u4e00\u70b9\u70b9\u523a\u5165\u3002\u743c\u6069\u6df1\u6df1\u5598\u606f\u7740\uff0c\u7528\u529b\u62c9\u626f\u7740\u5979\u7684\u8910\u8272\u957f\u53d1\uff0c\u6700\u7ec8\u75db\u75db\u5feb\u5feb\u5730\u5728\u5c11\u5973\u53e3\u4e2d\u7206\u53d1\u51fa\u6765\u3002</p><p>\u5c11\u5973\u7684\u5589\u7ba1\u98a4\u52a8\u7740\uff0c\u5495\u565c\u5495\u565c\u5730\u5c06\u6eda\u70eb\u7684\u725b\u5976\u5c3d\u6570\u541e\u54bd\u4e0b\u53bb\uff0c\u201c\u4e5d\u5206\u949f\u534a\uff0c\u8fd8\u6709\u534a\u5206\u949f\u3002\u201d\u5979\u770b\u4e86\u770b\u5e8a\u8fb9\u7684\u6c99\u6f0f\uff0c\u9887\u6709\u51e0\u5206\u5f97\u610f\u5730\u8bf4\u3002</p><p>\u8fd9\u4e2a\u7eaa\u5f55\u5728\u743c\u6069\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u4f30\u8ba1\u4ee5\u540e\u4e5f\u4e0d\u4f1a\u88ab\u6253\u7834\uff0c\u5979\u81ea\u7136\u6709\u9a84\u50b2\u7684\u8d44\u672c\u2014\u2014\u867d\u7136\u8fd9\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u9a84\u50b2\u7684\u3002\u743c\u6069\u4f11\u606f\u7247\u523b\uff0c\u5728\u5979\u7684\u5507\u820c\u670d\u4f8d\u4e0b\u518d\u5ea6\u91cd\u632f\u96c4\u98ce\uff0c\u4e8e\u662f\u547d\u4ee4\u5979\u8f6c\u8fc7\u8eab\uff0c\u81ea\u5df1\u8dea\u5728\u5979\u81c0\u540e\uff0c\u5f84\u76f4\u633a\u8170\u63d2\u4e86\u8fdb\u53bb\u3002\u5c11\u5973\u6e29\u987a\u5730\u8fce\u5408\u7740\uff0c\u5c06\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\uff0c\u626d\u52a8\u8170\u80a2\uff0c\u82b1\u5f84\u5185\u58c1\u968f\u7740\u547c\u5438\u7f13\u6162\u800c\u6709\u529b\u5730\u6536\u7f29\u7740\uff0c\u5589\u7ba1\u4e2d\u6324\u51fa\u65ad\u65ad\u7eed\u7eed\u7684\u547b\u541f\u58f0\uff0c\u50cf\u662f\u5728\u54c0\u9e23\u6c42\u9976\uff0c\u5374\u66f4\u52a0\u64a9\u62e8\u8d77\u7537\u4eba\u7684\u72c2\u66b4\u6b32\u671b\u3002</p><p>\u743c\u6069\u51f6\u731b\u8e42\u8e8f\u7740\u8eab\u524d\u7684\u5c11\u5973\uff0c\u6beb\u4e0d\u601c\u9999\u60dc\u7389\uff0c\u6bcf\u4e00\u4e0b\u90fd\u662f\u91cd\u91cd\u51b2\u649e\uff0c\u6df1\u5165\u5230\u5e95\u3002\u56e0\u4e3a\u65e0\u9700\u987e\u53ca\u5bf9\u65b9\u7684\u611f\u53d7\uff0c\u53ea\u8981\u6ee1\u8db3\u81ea\u5df1\u5373\u53ef\uff0c\u4e0d\u7528\u50cf\u5e73\u65f6\u90a3\u6837\u5fcd\u8010\uff0c\u53ea\u8fc7\u4e86\u4e0d\u5230\u4e00\u523b\u949f\uff0c\u4ed6\u4fbf\u518d\u5ea6\u5728\u5c11\u5973\u7684\u4f53\u5185\u53d1\u6cc4\u51fa\u6765\u3002</p><p>\u611f\u89c9\u5230\u7537\u4eba\u4ece\u81ea\u5df1\u82b1\u5f84\u5185\u7f13\u7f13\u9000\u51fa\uff0c\u5c11\u5973\u52c9\u529b\u6491\u8d77\u8eab\u4f53\uff0c\u6b63\u6253\u7b97\u6309\u7167\u4ee5\u5f80\u7684\u4e60\u60ef\uff0c\u8f6c\u56de\u8eab\u6765\u66ff\u4ed6\u7528\u5507\u820c\u201c\u6e05\u6d01\u201d\uff0c\u8170\u80cc\u5374\u88ab\u6709\u529b\u5730\u6309\u4f4f\u4e86\uff0c\u4ee4\u5979\u65e0\u6cd5\u52a8\u5f39\u3002\u201c\u522b\u52a8\u3002\u201d\u743c\u6069\u8bf4\uff0c\u7136\u540e\u5979\u611f\u89c9\u5230\u6eda\u70eb\u575a\u786c\u7684\u4e1c\u897f\u62b5\u5728\u81ea\u5df1\u80a1\u95f4\uff0c\u4e30\u6ee1\u67d4\u8f6f\u7684\u4e24\u74e3\u96ea\u767d\u81c0\u8089\u88ab\u63b0\u5f00\u4e86\uff0c\u9732\u51fa\u4e00\u6735\u7cbe\u81f4\u7ea4\u5de7\u7684\u7c89\u7ea2\u96cf\u83ca\uff0c\u653e\u5c04\u72b6\u7684\u7eb9\u8def\u6574\u6574\u9f50\u9f50\u5730\u6392\u5217\u7740\uff0c\u8bf1\u4eba\u81f3\u6781\u3002\u800c\u7537\u4eba\u7684\u5e9e\u7136\u5927\u7269\uff0c\u6b64\u65f6\u5c31\u6b63\u6740\u6c14\u817e\u817e\u5730\u9876\u5728\u83ca\u82b1\u854a\u4e0a\uff0c\u968f\u65f6\u51c6\u5907\u7834\u5173\u800c\u5165\u3002</p><p>\u201c\u90a3\u91cc\u4e0d\u884c\u2026\u2026\u7b49\u4e00\u7b49\u2026\u2026\u201d</p><p>\u89c9\u5bdf\u5230\u7537\u4eba\u7684\u610f\u56fe\uff0c\u5c11\u5973\u4f4e\u58f0\u5a07\u547c\uff0c\u626d\u52a8\u8170\u80a2\u60f3\u8981\u907f\u5f00\uff0c\u5979\u5012\u5e76\u4e0d\u662f\u5bf9\u8fd9\u79cd\u6b22\u597d\u65b9\u6cd5\u6709\u4ec0\u4e48\u6297\u62d2\uff0c\u4e8b\u5b9e\u4e0a\u5979\u633a\u559c\u6b22\uff0c\u800c\u4e14\u4e5f\u66fe\u7ecf\u4e50\u5728\u5176\u4e2d\u3002\u4f46\u81ea\u4ece\u62e5\u6709\u73b0\u5728\u8fd9\u5177\u8eaf\u4f53\u4ee5\u6765\uff0c\u90a3\u5730\u65b9\u8fd8\u662f\u5904\u5b50\uff0c\u4ece\u672a\u88ab\u5f00\u53d1\u8fc7\uff0c\u7d27\u81f4\u65e0\u6bd4\uff0c\u5982\u679c\u672a\u7ecf\u5145\u5206\u7684\u51c6\u5907\uff0c\u8d38\u7136\u201c\u4f7f\u7528\u201d\u7684\u8bdd\uff0c\u4f1a\u7ed9\u5979\u5e26\u6765\u5de8\u5927\u7684\u75db\u695a\u3002</p><p>\u201c\u665a\u4e0a\u597d\u4e0d\u597d\uff0c\u4e3b\u4eba\uff1f\u201d\u5979\u8f6f\u8bed\u54c0\u6c42\uff0c\u201c\u8ba9\u6211\u5148\u505a\u70b9\u51c6\u5907\uff0c\u4e0d\u7136\u4f1a\u5f88\u75bc\u7684\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u4e00\u65f6\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u4f46\u4e5f\u5e76\u6ca1\u6709\u79bb\u5f00\uff0c\u4f9d\u65e7\u9876\u5728\u5979\u8eab\u540e\u3002\u5c11\u5973\u5fc3\u4e2d\u614c\u5fd9\uff0c\u7a81\u7136\u7075\u5149\u4e00\u95ea\uff0c\u60f3\u8d77\u4ef6\u6551\u547d\u7a3b\u8349\u6765\uff0c\u201c\u4e3b\u4eba\uff0c\u201d\u5979\u8fde\u5fd9\u8bf4\uff0c\u201c\u6709\u4ef6\u975e\u5e38\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u5dee\u70b9\u5fd8\u4e86\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u6628\u665a\u906d\u9047\u4e86\u523a\u6740\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff01\u201d</p><p>\u743c\u6069\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u628a\u5c06\u5c11\u5973\u63a8\u5f00\uff0c\u8138\u8272\u5239\u90a3\u95f4\u53d8\u5f97\u60e8\u767d\u4e00\u7247\u3002\u5c11\u5973\u89c1\u4ed6\u795e\u8272\uff0c\u4e0d\u7531\u5f97\u4e5f\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u201c\u522b\u62c5\u5fc3\uff0c\u4e3b\u4eba\uff0c\u201d\u5979\u8d76\u5feb\u8865\u5145\uff0c\u201c\u5979\u6ca1\u4e8b\uff0c\u5b89\u7136\u65e0\u6059\uff0c\u522b\u62c5\u5fc3\u3002\u201d</p><p>\u95fb\u542c\u6b64\u8a00\uff0c\u743c\u6069\u7a0d\u7a0d\u7f13\u8fc7\u795e\u6765\uff0c\u53ea\u89c9\u80cc\u4e0a\u51c9\u98d5\u98d5\u7684\uff0c\u5df2\u7ecf\u6ee1\u662f\u51b7\u6c57\u3002\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4ed6\u95ee\uff0c\u6012\u89c6\u7740\u5c11\u5973\u3002</p><p>\u5c11\u5973\u4e0d\u6562\u6020\u6162\uff0c\u8fde\u5fd9\u8bb2\u8ff0\u4e8b\u60c5\u7ecf\u8fc7\u3002\u539f\u6765\u6628\u665a\u743c\u6069\u56de\u623f\u95f4\u540e\uff0c\u5979\u966a\u7740\u73ca\u5609\u8bf4\u8bdd\uff0c\u773c\u770b\u5df2\u7ecf\u591c\u6df1\uff0c\u4e24\u4eba\u4fbf\u7761\u5728\u4e00\u8d77\u3002\u53cd\u6b63\u5973\u5b69\u5b50\u540c\u5bdd\u540c\u7720\uff0c\u662f\u5f88\u6b63\u5e38\u7684\u73b0\u8c61\uff0c\u4e0d\u8db3\u4e3a\u5f02\u3002\u5230\u4e86\u534a\u591c\u65f6\u5206\uff0c\u7a81\u7136\u542c\u89c1\u95e8\u53e3\u5904\u4f3c\u4e4e\u6709\u5f02\u52a8\uff0c\u5979\u662f\u6740\u624b\u51fa\u8eab\uff0c\u8033\u76ee\u654f\u9510\u8fdc\u8d85\u5e38\u4eba\uff0c\u8b66\u60d5\u6027\u66f4\u662f\u6781\u9ad8\uff0c\u987f\u65f6\u4fbf\u7559\u4e86\u5fc3\u3002\u7b49\u5f85\u7247\u523b\uff0c\u679c\u7136\u6709\u4eba\u7528\u94a5\u5319\u6253\u5f00\u95e8\uff0c\u8e51\u624b\u8e51\u811a\u5730\u6f5c\u5165\uff0c\u624b\u6301\u94a2\u5200\uff0c\u671d\u7740\u5e8a\u8fb9\u800c\u6765\u3002\u5979\u65e9\u6709\u51c6\u5907\uff0c\u5f85\u4ed6\u8d70\u8fd1\uff0c\u4e00\u97ad\u5c06\u4ed6\u653e\u5012\u5728\u5730\uff0c\u81f3\u4e8e\u73ca\u5609\uff0c\u5979\u4e00\u76f4\u90fd\u5728\u719f\u7761\u4e2d\uff0c\u538b\u6839\u8fde\u60ca\u5413\u90fd\u6ca1\u53d7\u5230\u3002</p><p>\u542c\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u603b\u7b97\u662f\u5b8c\u5168\u653e\u4e0b\u5fc3\u6765\u3002\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u5c31\u662f\u8fd9\u5bbe\u9986\u91cc\u7684\u4e00\u4e2a\u4f8d\u8005\u3002\u201d</p><p>\u201c\u4f8d\u8005\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u201c\u6682\u65f6\u8fd8\u4e0d\u6e05\u695a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f46\u5982\u679c\u6211\u6ca1\u6599\u9519\u7684\u8bdd\uff0c\u4ed6\u5e94\u8be5\u662f\u6563\u5854\u6797\u4f1a\u7684\u95f4\u8c0d\u3002\u201d</p></article>"}}}]);
