(window.webpackJsonp=window.webpackJsonp||[]).push([[1659],{1682:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e00\u8282 \u4e0d\u5409\u5229\u7684\u57ce\u540d",content:"<article><h2>\u7b2c\u56db\u5341\u4e00\u8282 \u4e0d\u5409\u5229\u7684\u57ce\u540d</h2><p>\u7ad6\u7434\u624b\u540c\u76df\u65a5\u5019\u3001\u6563\u5854\u6797\u4f1a\u95f4\u8c0d\uff0c\u8fd9\u662f\u8d39\u4f26\u5927\u9646\u4e0a\u6700\u9f0e\u9f0e\u6709\u540d\u7684\u4e24\u5927\u7279\u5de5\u7ec4\u7ec7\uff0c\u5176\u58f0\u671b\u5927\u81f4\u76f8\u5f53\u4e8e\u5730\u7403\u4e0a\u7684\u4e2d\u592e\u60c5\u62a5\u5c40\u548c\u514b\u683c\u52c3\u3002\u5b83\u4eec\u65d7\u9f13\u76f8\u5f53\uff0c\u4f46\u4e5f\u5404\u6709\u4fa7\u91cd\uff0c\u76f8\u6bd4\u8d77\u6765\uff0c\u524d\u8005\u66f4\u64c5\u957f\u6f5c\u4f0f\u3001\u6e17\u900f\u3001\u6536\u96c6\u548c\u7a83\u53d6\u60c5\u62a5\uff0c\u800c\u540e\u8005\u66f4\u64c5\u957f\u98a0\u8986\u3001\u7834\u574f\u3001\u6697\u6740\u548c\u7b56\u53cd\u3002\u8fdc\u5c71\u57ce\u4e4b\u6240\u4ee5\u4f1a\u4e00\u5915\u9677\u843d\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c31\u662f\u90a3\u4e9b\u6563\u5854\u6797\u4f1a\u95f4\u8c0d\u7684\u529f\u52b3\u3002</p><p>\u743c\u6069\u4ece\u6b4c\u66e6\u96c5\u624b\u91cc\u6551\u4e0b\u5f17\u96f7\u65af\uff0c\u593a\u53d6\u4e86\u94a5\u5319\uff0c\u81ea\u77e5\u662f\u91cd\u91cd\u5f97\u7f6a\u4e86\u6563\u5854\u6797\u4f1a\uff0c\u88ab\u4eba\u627e\u4e0a\u95e8\u6765\uff0c\u5012\u4e5f\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u6ca1\u60f3\u5230\u4f1a\u8fd9\u4e48\u5feb\u3002\u201c\u90a3\u4e2a\u5bb6\u4f19\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6b7b\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u670d\u6bd2\u81ea\u6740\u3002\u201d</p><p>\u90a3\u540d\u95f4\u8c0d\u521a\u521a\u9760\u8fd1\u5e8a\u8fb9\uff0c\u4fbf\u88ab\u4e00\u97ad\u653e\u5012\uff0c\u838e\u73de\u514b\u5c06\u4ed6\u6293\u4f4f\uff0c\u6b63\u8981\u5ba1\u95ee\uff0c\u5374\u88ab\u5bf9\u65b9\u54ac\u7834\u85cf\u5728\u7259\u9f7f\u91cc\u7684\u6bd2\u836f\uff0c\u77ac\u95f4\u4fbf\u6c14\u7edd\u8eab\u4ea1\u3002\u838e\u73de\u514b\u662f\u9b45\u9b54\uff0c\u4e0d\u662f\u4ea1\u7075\u5e08\uff0c\u80fd\u64ac\u5f00\u6d3b\u4eba\u7684\u5634\u5df4\uff0c\u5374\u62ff\u6b7b\u4eba\u675f\u624b\u65e0\u7b56\u3002\u552f\u4e00\u7684\u7ebf\u7d22\uff0c\u5c31\u662f\u4ece\u6b7b\u8005\u8138\u4e0a\u53d1\u73b0\u4e86\u6613\u5bb9\u7684\u75d5\u8ff9\uff0c\u800c\u8fd9\u662f\u6563\u5854\u6797\u4f1a\u5e38\u7528\u7684\u624b\u6cd5\uff0c\u7ad6\u7434\u624b\u540c\u76df\u66f4\u591a\u662f\u672c\u8272\u6f14\u51fa\uff0c\u4e0d\u641e\u4f2a\u88c5\u3002\u81f3\u4e8e\u4ed6\u53d7\u4f55\u4eba\u6307\u4f7f\uff0c\u6709\u4f55\u76ee\u7684\uff0c\u662f\u5426\u8fd8\u6709\u540c\u4f19\u7b49\u7b49\uff0c\u5c31\u4e00\u6982\u4e0d\u77e5\u4e86\u3002\u731c\u6d4b\u8d77\u6765\uff0c\u5e94\u8be5\u662f\u63a5\u5230\u4e86\u8fdc\u5c71\u57ce\u90a3\u8fb9\u4f20\u6765\u7684\u6d88\u606f\uff0c\u8ba4\u51fa\u743c\u6069\u4e00\u884c\u4eba\u7684\u8eab\u4efd\uff0c\u60f3\u8981\u62ff\u5230\u94a5\u5319\u7f62\u4e86\u3002\u81f3\u4e8e\u4e3a\u4f55\u4f1a\u9009\u62e9\u73ca\u5609\u4f5c\u4e3a\u76ee\u6807\uff0c\u90a3\u5c31\u66f4\u597d\u7406\u89e3\uff0c\u743c\u6069\u7b49\u4eba\u4e2d\uff0c\u5c31\u6570\u73ca\u5609\u6700\u5f31\uff0c\u51e0\u4e4e\u6ca1\u6709\u81ea\u4fdd\u4e4b\u529b\uff0c\u504f\u504f\u5730\u4f4d\u53c8\u6700\u9ad8\uff0c\u53ea\u8981\u4ee5\u5979\u4e3a\u4eba\u8d28\uff0c\u4e0d\u6015\u743c\u6069\u4e0d\u4e56\u4e56\u4ea4\u51fa\u94a5\u5319\u3002</p><p>\u8fd9\u4e2a\u60f3\u6cd5\u4e0d\u53ef\u8c13\u4e0d\u6b63\u786e\uff0c\u53ea\u662f\u6ca1\u6599\u5230\u838e\u73de\u514b\u6070\u597d\u4eca\u665a\u4f1a\u548c\u73ca\u5609\u7761\u5728\u4e00\u8d77\uff0c\u7ed3\u679c\u529f\u4e8f\u4e00\u7bd1\u3002\u838e\u73de\u514b\u4ee5\u524d\u662f\u94c1\u738b\u5ea7\u7684\u738b\u724c\u6740\u624b\uff0c\u8fd9\u79cd\u6f5c\u4f0f\u6697\u6740\u7684\u4e8b\u60c5\uff0c\u5979\u662f\u4e13\u5bb6\u4e2d\u7684\u4e13\u5bb6\uff0c\u6240\u4ee5\u5bf9\u4ed8\u8fd9\u4e2a\u523a\u5ba2\u624d\u80fd\u9a7e\u8f7b\u5c31\u719f\uff0c\u624b\u5230\u64d2\u6765\u3002\u5426\u5219\u5c31\u7b97\u6362\u4e86\u8299\u857e\u72c4\u6216\u8005\u8299\u8389\u5a05\u966a\u7740\u73ca\u5609\uff0c\u53ea\u6015\u523a\u5ba2\u8fd8\u771f\u80fd\u5f97\u624b\u3002</p><p>\u4e00\u5ff5\u53ca\u6b64\uff0c\u7981\u4e0d\u4f4f\u80cc\u4e0a\u51b7\u6c57\u6d94\u6d94\uff0c\u201c\u90a3\u73b0\u5728\u73ca\u5609\u90a3\u8fb9\u5462\uff1f\u201d\u743c\u6069\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u600e\u4e48\u4e0d\u966a\u7740\u5979\uff0c\u5982\u679c\u8fd8\u6709\u540c\u515a\u600e\u4e48\u529e\u3002\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u73b0\u5728\u548c\u5979\u5728\u4e00\u8d77\uff0c\u6211\u4e5f\u901a\u77e5\u4e86\u5e02\u653f\u5e9c\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6d3e\u4e86\u4e00\u7fa4\u8b66\u536b\u8fc7\u6765\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u800c\u4e14\u6211\u8fd8\u628a\u4f60\u90a3\u4e24\u4e2a\u9b54\u50cf\u62a4\u536b\u7ed9\u6d3e\u8fc7\u53bb\u4e86\uff0c\u6b63\u5b88\u7740\u73ca\u5609\u59d0\u59d0\u5462\u3002\u201d</p><p>\u838e\u73de\u514b\u8fd9\u4e00\u8bf4\uff0c\u743c\u6069\u65b9\u624d\u53d1\u73b0\uff0c\u6628\u665a\u51c6\u5907\u541e\u566c\u94a5\u5319\u4e4b\u524d\uff0c\u4ed6\u51fa\u4e8e\u5b89\u5168\u8d77\u89c1\uff0c\u62db\u4e86\u4e24\u5c0a\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u4f5c\u4e3a\u62a4\u536b\uff0c\u5b88\u5728\u5e8a\u8fb9\uff0c\u9192\u6765\u5374\u90fd\u4e0d\u89c1\u4e86\uff0c\u53ea\u6709\u838e\u73de\u514b\u5750\u5728\u90a3\u91cc\u524a\u82f9\u679c\u3002\u53ea\u662f\u4ed6\u9192\u6765\u540e\u5fd9\u7740\u63a8\u5012\u7f8e\u5973\uff0c\u4e00\u76f4\u6ca1\u6709\u6ce8\u610f\u3002</p><p>\u201c\u4f60\u80fd\u6307\u6325\u5b83\u4eec\uff1f\u201d\u743c\u6069\u60ca\u8bb6\u3002</p><p>\u201c\u55ef\uff0c\u6211\u4e5f\u662f\u624d\u53d1\u73b0\u7684\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5f53\u65f6\u6ca1\u6ce8\u610f\uff0c\u968f\u53e3\u53eb\u5b83\u4eec\u8fc7\u53bb\uff0c\u6ca1\u60f3\u5230\u8fd8\u771f\u6709\u7528\u3002\u731c\u60f3\u5927\u6982\u662f\u56e0\u4e3a\u771f\u540d\u5951\u7ea6\u7684\u7f18\u6545\u5427\uff0c\u4f60\u6211\u7075\u9b42\u8054\u7ed3\uff0c\u88ab\u9b54\u50cf\u89c6\u4e3a\u540c\u4e00\u4e86\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u838e\u73de\u514b\uff0c\u786e\u5b9a\u5979\u5e76\u672a\u6492\u8c0e\uff0c\u4e8e\u662f\u653e\u4e0b\u5fc3\u6765\u3002\u771f\u540d\u5951\u7ea6\u7684\u5b58\u5728\uff0c\u751f\u6b7b\u4e0e\u5171\u7684\u675f\u7f1a\uff0c\u8ba9\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u838e\u73de\u514b\u4f1a\u53cd\u566c\u6216\u8005\u80cc\u53db\uff0c\u5982\u679c\u9b45\u9b54\u4e5f\u80fd\u6307\u6325\u9b54\u50cf\uff0c\u7b49\u4e8e\u989d\u5916\u591a\u4e86\u4e2a\u5e2e\u624b\uff0c\u53cd\u800c\u662f\u4ef6\u597d\u4e8b\u3002</p><p>\u5f97\u77e5\u73ca\u5609\u5b89\u7136\u65e0\u4e8b\uff0c\u743c\u6069\u5fc3\u4e2d\u5de8\u77f3\u843d\u5730\uff0c\u4f46\u88ab\u8fd9\u4e00\u60ca\u4e00\u5413\uff0c\u539f\u672c\u9ad8\u6da8\u7684\u6b32\u706b\u5df2\u7ecf\u5e73\u606f\u4e0b\u53bb\u3002\u201c\u4eca\u5929\u5148\u7b97\u4e86\uff0c\u7b49\u5230\u4e86\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u518d\u548c\u4f60\u7b97\u5e10\uff0c\u201d\u4ed6\u5728\u838e\u73de\u514b\u7684\u7fd8\u81c0\u4e0a\u556a\u5730\u6253\u4e86\u4e00\u4e0b\uff0c\u201c\u8bb0\u5f97\u505a\u597d\u51c6\u5907\u3002\u201d</p><p>\u201c\u9075\u547d\uff0c\u4e3b\u4eba\u3002\u201d</p><p>\u9b45\u9b54\u5a07\u58f0\u5e94\u547d\uff0c\u5a9a\u773c\u5982\u4e1d\uff0c\u8138\u4e0a\u795e\u60c5\u8bf4\u4e0d\u51fa\u7684\u8bf1\u60d1\u3002\u5979\u4e56\u4e56\u5730\u8db4\u4e0b\u6765\uff0c\u7528\u5c0f\u5634\u66ff\u7537\u4eba\u6e05\u6d01\u5b8c\u6bd5\uff0c\u7136\u540e\u670d\u4f8d\u4ed6\u7a7f\u4e0a\u8863\u670d\uff0c\u4eff\u4f5b\u6e29\u987a\u67d4\u5a49\u7684\u5c0f\u59bb\u5b50\u3002\u201c\u771f\u4e56\u3002\u201d\u743c\u6069\u5938\u5956\u3002</p><p>\u201c\u4f60\u559c\u6b22\u4e56\u7684\u5973\u5b69\u5b50\u561b\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u743c\u6069\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u4e0d\u8fc7\uff0c\u838e\u73de\u514b\uff0c\u8fd9\u6b21\u591a\u8c22\u4f60\u4e86\u3002\u201d</p><p>\u5018\u82e5\u4e0d\u662f\u6070\u597d\u6709\u5979\u5728\uff0c\u73ca\u5609\u8fd9\u6b21\u53ea\u6015\u5f53\u771f\u51f6\u591a\u5409\u5c11\u3002\u5728\u53cd\u7701\u81ea\u5df1\u8b66\u60d5\u6027\u5b9e\u5728\u592a\u4f4e\u7684\u540c\u65f6\uff0c\u743c\u6069\u4e5f\u786e\u5b9e\u5bf9\u838e\u73de\u514b\u5fc3\u6000\u611f\u6fc0\u2014\u2014\u5f53\u7136\uff0c\u611f\u6fc0\u5f52\u611f\u6fc0\uff0c\u8be5\u505a\u7684\u4e8b\u60c5\u8fd8\u662f\u8981\u505a\u5c31\u662f\u4e86\u3002</p><p>\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u743c\u6069\u5df2\u7ecf\u7a7f\u597d\u5916\u5957\uff0c\u838e\u73de\u514b\u4e5f\u5306\u5306\u6574\u7406\u4e86\u4e00\u4e0b\u8863\u670d\uff0c\u4e24\u4eba\u4e00\u524d\u4e00\u540e\u8d70\u51fa\u623f\u95f4\uff0c\u524d\u5f80\u73ca\u5609\u7684\u5367\u5ba4\u3002</p><p>\u5c31\u5982\u838e\u73de\u514b\u6240\u8a00\uff0c\u523a\u5ba2\u65e9\u5df2\u81ea\u6740\u8eab\u4ea1\uff0c\u5c38\u4f53\u90fd\u51b7\u5f97\u900f\u4e86\uff0c\u4ed6\u7684\u8eab\u4efd\u662f\u8fd9\u5bb6\u5bbe\u9986\u7684\u4f8d\u8005\uff0c\u5854\u745f\u8c37\u4eba\uff08\u81ea\u79f0\uff09\uff0c\u4eca\u5e74\u4e8c\u5341\u516d\u5c81\uff0c\u56db\u5e74\u524d\u6765\u5230\u6b27\u675c\u6797\uff0c\u65e0\u4eb2\u65e0\u6545\uff0c\u672a\u5a5a\uff0c\u65e0\u5b50\u5973\uff0c\u5e73\u65f6\u4e3a\u4eba\u5341\u5206\u4f4e\u8c03\uff0c\u6beb\u4e0d\u8d77\u773c\uff0c\u548c\u540c\u4e8b\u5173\u7cfb\u76f8\u5904\u4e5f\u90fd\u4e0d\u9519\uff0c\u603b\u4e4b\u5c31\u662f\u4e2a\u6c89\u9ed8\u8e0f\u5b9e\u4eba\u755c\u65e0\u5bb3\u7684\u5355\u8eab\u9752\u5e74\uff0c\u7a81\u7136\u505a\u51fa\u8fd9\u79cd\u4e8b\u60c5\u6765\uff0c\u5927\u5bb6\u4e5f\u90fd\u5f88\u60ca\u8bb6\u3002\u81f3\u4e8e\u4ed6\u8138\u4e0a\u6709\u6613\u5bb9\u7684\u75d5\u8ff9\uff0c\u56e0\u4e3a\u624b\u6cd5\u975e\u5e38\u5de7\u5999\u9690\u853d\uff0c\u9664\u4e86\u838e\u73de\u514b\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u662f\u6563\u5854\u6797\u4f1a\u7684\u95f4\u8c0d\u3002</p><p>\u201c\u90a3\u4e2a\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u6628\u5929\u9001\u743c\u6069\u7b49\u4eba\u6765\u5bbe\u9986\u7684\u5b98\u5458\u6413\u7740\u624b\uff0c\u867d\u7136\u662f\u5927\u51ac\u5929\uff0c\u4f46\u4ed6\u90a3\u5f20\u5706\u6eda\u6eda\u7684\u80d6\u8138\u4e0a\u70ed\u6c57\u76f4\u6d41\uff0c\u8bf4\u8bdd\u90fd\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21\uff0c\u201c\u5bf9\u4e8e\u6b64\u4e8b\uff0c\u6211\u4eec\u975e\u5e38\u62b1\u6b49\uff0c\u90a3\u4e2a\uff0c\u6df1\u8868\u6b49\u610f\u2026\u2026\u201d</p><p>\u9634\u9b42\u57ce\u548c\u6851\u6bd4\u4e9a\u662f\u76df\u56fd\uff0c\u751a\u81f3\u5728\u5408\u4f5c\u5f53\u4e2d\u8fd8\u9690\u7136\u5c45\u4e8e\u4e0a\u98ce\u4f4d\u7f6e\uff0c\u6bd5\u7adf\u73b0\u5728\u6851\u6bd4\u4e9a\u7684\u5927\u7edf\u9886\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\uff0c\u662f\u9760\u7740\u9634\u9b42\u57ce\u7684\u9f0e\u529b\u652f\u6301\u624d\u6253\u8d25\u7ade\u4e89\u5bf9\u624b\u94f6\u9e26\uff0c\u767b\u4e0a\u5b9d\u5ea7\u7684\u3002\u743c\u6069\u7b49\u4eba\u8eab\u4e3a\u9634\u9b42\u57ce\u7684\u4f7f\u8005\uff0c\u88ab\u5b89\u6392\u4f4f\u5728\u6851\u6bd4\u4e9a\u9996\u90fd\u7684\u5bbe\u9986\u91cc\uff0c\u7ed3\u679c\u5f53\u665a\u5c31\u906d\u9047\u6697\u6740\uff0c\u800c\u4e14\u523a\u5ba2\u8fd8\u662f\u5bbe\u9986\u7684\u5de5\u4f5c\u4eba\u5458\u2026\u2026\u867d\u8bf4\u8fd9\u4e2a\u4e16\u754c\u8fd8\u6ca1\u6709\u53d1\u5c55\u51fa\u6210\u719f\u7684\u56fd\u9645\u6cd5\uff0c\u4f46\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\uff0c\u6851\u6bd4\u4e9a\u65e0\u8bba\u5982\u4f55\u4e5f\u5f97\u7ed9\u4e2a\u4ea4\u4ee3\u3002\u7c73\u62c9\u8d1d\u5854\u592b\u4eba\u662f\u4e00\u56fd\u9886\u8896\uff0c\u81ea\u7136\u4e0e\u5979\u65e0\u5173\uff0c\u771f\u4f1a\u88ab\u62c9\u51fa\u6765\u5f53\u66ff\u7f6a\u7f8a\u7684\uff0c\u8fd8\u662f\u5177\u4f53\u7ecf\u529e\u7684\u4eba\u5458\u3002</p><p>\u597d\u5728\u743c\u6069\u5fc3\u77e5\u809a\u660e\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5176\u5b9e\u5e94\u8be5\u662f\u81ea\u5df1\u60f9\u51fa\u6765\u7684\uff0c\u548c\u6851\u6bd4\u4e9a\u5e76\u65e0\u5173\u7cfb\uff0c\u56e0\u6b64\u4e0d\u6253\u7b97\u8ffd\u7a76\uff0c\u6851\u6bd4\u4e9a\u653f\u5e9c\u5b98\u5458\u81ea\u7136\u66f4\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u53cc\u65b9\u8d70\u8d70\u8fc7\u573a\uff0c\u5c06\u4e8b\u60c5\u5b9a\u6027\u4e3a\u201c\u5bbe\u9986\u4f8d\u8005\u7a81\u7136\u7cbe\u795e\u5931\u5e38\uff0c\u884c\u51f6\u4f24\u4eba\u88ab\u5236\u6b62\u201d\uff0c\u53cd\u6b63\u4eba\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8d23\u4efb\u4e5f\u65e0\u4ece\u8ffd\u7a76\u8d77\uff0c\u5c31\u6b64\u7ed3\u6848\u5b8c\u4e8b\u3002\u743c\u6069\u4e00\u884c\u4eba\u6309\u7167\u539f\u8ba1\u5212\u52a8\u8eab\u51fa\u53d1\uff0c\u987a\u7740\u6851\u6bd4\u4e9a\u7684\u201c\u56fd\u5bb6\u5927\u9053\u201d\uff0c\u6d69\u6d69\u8361\u8361\u5730\u524d\u5f80\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u3002</p><p>\u4e4b\u6240\u4ee5\u8bf4\u6d69\u6d69\u8361\u8361\uff0c\u662f\u56e0\u4e3a\u51fa\u4e86\u8fd9\u6863\u5b50\u4e8b\u4e4b\u540e\uff0c\u76f8\u5173\u5b98\u5458\u7acb\u523b\u628a\u201c\u9634\u9b42\u57ce\u4f7f\u8005\u201d\u7684\u62a4\u536b\u6570\u91cf\u589e\u52a0\u4e86\u4e94\u500d\uff0c\u539f\u672c\u662f\u516b\u4e2a\uff0c\u73b0\u5728\u662f\u56db\u5341\u4e2a\u3002\u5e26\u7740\u56db\u5341\u4e2a\u4fdd\u9556\u524d\u547c\u540e\u62e5\uff0c\u62db\u6447\u8fc7\u5e02\uff0c\u8fd9\u611f\u89c9\u7740\u5b9e\u4e0d\u5dee\uff0c\u53ef\u60dc\u4e00\u8def\u4e0a\u518d\u4e5f\u6ca1\u6709\u9047\u5230\u654c\u4eba\uff0c\u7eaf\u7cb9\u6210\u4e86\u6446\u8bbe\u3002</p><p>\u6851\u6bd4\u4e9a\u5bcc\u5eb6\u7e41\u534e\uff0c\u8d22\u529b\u96c4\u539a\uff0c\u56fd\u5bb6\u5927\u9053\u4e5f\u5efa\u5f97\u5b9e\u5728\u662f\u5bbd\u655e\u5e73\u5766\uff0c\u4e0d\u8fc7\u4e24\u5929\u65f6\u95f4\uff0c\u743c\u6069\u4e00\u884c\u4eba\u5c31\u987a\u5229\u62b5\u8fbe\u4e86\u6b64\u884c\u7684\u6700\u7ec8\u76ee\u7684\u5730\uff1a\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u3002</p><p>\u203b\u203b\u203b</p><p>\u6bd4\u8d77\u751f\u6c14\u52c3\u52c3\u7684\u8fdc\u5c71\u548c\u6b27\u675c\u6797\uff0c\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u66f4\u50cf\u4e2a\u5b89\u9759\u7684\u8001\u8005\uff0c\u4f5c\u4e3a\u6851\u6bd4\u4e9a\u7684\u53e4\u57ce\u4e4b\u4e00\uff0c\u5b83\u5176\u5b9e\u4e5f\u6709\u63a5\u8fd1\u4e09\u4e07\u7684\u5c45\u6c11\u4eba\u53e3\uff0c\u79f0\u5f97\u4e0a\u662f\u5927\u90fd\u5e02\uff0c\u800c\u4e14\u5730\u7406\u4f4d\u7f6e\u4e0d\u9519\uff0c\u662f\u4f18\u826f\u7684\u6d77\u6e2f\uff0c\u6765\u6765\u5f80\u5f80\u7684\u5546\u8239\u7edc\u7ece\u4e0d\u7edd\u3002\u4f46\u6574\u4e2a\u57ce\u5e02\u4eff\u4f5b\u603b\u662f\u5448\u73b0\u7740\u4e00\u79cd\u6d45\u7070\u7684\u51b7\u8272\u8c03\uff0c\u5c45\u6c11\u4eec\u8863\u7740\u6734\u7d20\uff0c\u751f\u6d3b\u7b80\u5355\u800c\u89c4\u5f8b\uff0c\u65e5\u51fa\u800c\u4f5c\uff0c\u65e5\u843d\u800c\u606f\uff0c\u5efa\u7b51\u7269\u4e5f\u5927\u591a\u53e4\u65e7\u7834\u8d25\uff0c\u6ca1\u6709\u55a7\u95f9\u7684\u9152\u9986\uff0c\u6ca1\u6709\u95ea\u70c1\u7684\u9713\u8679\u706f\uff0c\u66f4\u770b\u4e0d\u89c1\u6000\u63e3\u68a6\u60f3\u548c\u70ed\u8840\u7684\u5192\u9669\u8005\u4eec\u3002\u57ce\u4e2d\u6709\u4e03\u516b\u4f4d\u795e\u7947\u7684\u6559\u4f1a\uff0c\u4f46\u89c4\u6a21\u90fd\u4e0d\u5927\uff0c\u795e\u6bbf\u4e5f\u5f88\u5c0f\uff0c\u4f4d\u7f6e\u504f\u50fb\u3002\u636e\u966a\u540c\u4eba\u5458\u4ecb\u7ecd\uff0c\u56e0\u4e3a\u67d0\u4e9b\u5386\u53f2\u539f\u56e0\uff0c\u4e4c\u62c9\u65af\u76ae\u5c14\u7684\u5c45\u6c11\u5bf9\u795e\u7947\u666e\u904d\u62b1\u6709\u4e00\u79cd\u51b7\u6de1\u7684\u6001\u5ea6\uff0c\u4e0d\u662f\u4e0d\u4fe1\uff0c\u4f46\u4e5f\u6781\u5c11\u8654\u4fe1\uff0c\u5927\u81f4\u53ef\u4ee5\u7b97\u662f\u6cdb\u4fe1\u2014\u2014\u743c\u6069\u603b\u7ed3\u4ed6\u7684\u610f\u601d\uff0c\u5176\u5b9e\u5c31\u662f\u4e00\u53e5\u8bdd\uff1a\u201c\u656c\u9b3c\u795e\u800c\u8fdc\u4e4b\u201d\u3002</p><p>\u9634\u9b42\u57ce\u4e4b\u524d\u6d3e\u6765\u8fdb\u884c\u201c\u6355\u635e\u201d\u5de5\u4f5c\u7684\u4eba\u5458\u5c45\u4f4f\u5728\u57ce\u5357\u7684\u7801\u5934\u533a\uff0c\u4e00\u5ea7\u722c\u6ee1\u4e86\u9752\u85e4\u7684\u53e4\u8001\u5b85\u9662\u91cc\uff0c\u8d1f\u8d23\u4eba\u53eb\u505a\u62c9\u52a0\u897f\u4e9a\u66fc\u7279\u65af\uff0c\u56e0\u4e3a\u540d\u5b57\u592a\u957f\uff0c\u4e00\u822c\u90fd\u53eb\u4ed6\u62c9\u52a0\uff0c\u662f\u70bc\u91d1\u5b66\u9662\u91cc\u7684\u4e00\u4f4d\u6559\u6388\uff0c\u548c\u743c\u6069\u8fd8\u7b97\u8ba4\u8bc6\u3002\u78b0\u9762\u4e4b\u540e\uff0c\u5148\u4ecb\u7ecd\u60c5\u51b5\uff0c\u7136\u540e\u743c\u6069\u624d\u77e5\u9053\uff0c\u539f\u6765\u9634\u9b42\u57ce\u5728\u6851\u6bd4\u4e9a\u6298\u817e\u4e86\u8fd9\u5927\u534a\u5e74\uff0c\u65e2\u4e0d\u662f\u60f3\u635e\u9c7c\uff0c\u4e5f\u4e0d\u662f\u60f3\u635e\u6c89\u8239\uff0c\u800c\u662f\u60f3\u635e\u4e00\u5ea7\u57ce\u5e02\u3002</p><p>\u4e00\u5ea7\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u65f6\u4ee3\u7684\u6d6e\u7a7a\u57ce\u3002</p><p>\u8010\u745f\u745e\u5c14\u5b58\u7eed\u4e09\u5343\u591a\u5e74\uff0c\u5386\u53f2\u4e0a\u51fa\u73b0\u8fc7\u7684\u5927\u5965\u672f\u5e08\u6570\u4ee5\u5343\u8ba1\uff0c\u53bb\u6389\u6b7b\u4ea1\u7684\uff0c\u53bb\u6389\u50cf\u5965\u6c83\u8fd9\u6837\u5c45\u4f4f\u5728\u5730\u9762\u7684\uff0c\u53bb\u6389\u970d\u6770\u54c8\u7eb3\u8fd9\u6837\u4f9d\u9644\u5176\u4ed6\u5927\u5965\u672f\u5e08\u7684\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u603b\u4e4b\u5728\u5e1d\u56fd\u9668\u706d\u7684\u524d\u4e00\u523b\uff0c\u5929\u7a7a\u4e2d\u5927\u6982\u4e00\u5171\u6f02\u6d6e\u7740\u51e0\u767e\u5ea7\u6d6e\u7a7a\u57ce\u3002\u7ed3\u679c\u5927\u707e\u53d8\u964d\u4e34\uff0c\u6d6e\u7a7a\u57ce\u5c3d\u6570\u5760\u6bc1\uff0c\u6454\u6210\u4e86\u788e\u6e23\uff0c\u53ea\u6709\u6781\u5c11\u6570\u8fd0\u6c14\u8d85\u597d\u7684\u4fa5\u5e78\u9003\u8131\u3002</p><p>\u9634\u9b42\u57ce\u6b64\u6b21\u60f3\u8981\u6253\u635e\u7684\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u540d\u4e3a\u8428\u5e93\u5c14\u65af\uff0c\u53c8\u540d\u201c\u661f\u9668\u57ce\u201d\uff0c\u5728\u8010\u745f\u65f6\u4ee3\u4e5f\u7b97\u662f\u9887\u6709\u540d\u6c14\uff0c\u539f\u4e3b\u4eba\u662f\u4e00\u4f4d\u6781\u64c5\u9884\u8a00\u9b54\u6cd5\u7684\u5927\u5965\u672f\u5e08\uff0c\u636e\u8bf4\u8fd8\u66fe\u7ecf\u5f53\u8fc7\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u7684\u8001\u5e08\u3002\u6839\u636e\u5e03\u96f7\u7eb3\u65af\u6536\u96c6\u5230\u7684\u5404\u79cd\u8d44\u6599\u6765\u5206\u6790\uff0c\u5927\u707e\u53d8\u53d1\u751f\u7684\u65f6\u5019\uff0c\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u6b63\u5728\u6851\u6bd4\u4e9a\u9644\u8fd1\u7684\u5760\u661f\u6d77\u4e0a\u7a7a\u6162\u60a0\u60a0\u5730\u98de\u884c\u2014\u2014\u7136\u540e\u6251\u901a\u4e00\u58f0\u5760\u5165\u4e86\u6d77\u4e2d\u3002</p><p>\u201c\u661f\u9668\u57ce\uff1f\u53d6\u8fd9\u4e48\u4e0d\u5409\u5229\u7684\u540d\u5b57\uff0c\u679c\u7136\u6700\u540e\u6389\u8fdb\u4e86\u5760\u661f\u6d77\u3002\u201d\u743c\u6069\u968f\u53e3\u8bf4\u3002</p><p>\u201c\u661f\u9668\u57ce\u7684\u610f\u601d\uff0c\u662f\u8bf4\u4ef0\u671b\u591c\u7a7a\uff0c\u770b\u6f2b\u5929\u661f\u8fb0\u5760\u843d\u5982\u96e8\u7684\u610f\u601d\uff0c\u201d\u62c9\u52a0\u5f88\u4e25\u8083\u5f88\u8ba4\u771f\u5730\u89e3\u91ca\uff0c\u201c\u4e0d\u662f\u8bf4\u4f1a\u6389\u5230\u5760\u661f\u6d77\u91cc\u53bb\u3002\u201d</p><p>\u201c\u2026\u2026\u53ea\u662f\u5f00\u4e2a\u73a9\u7b11\u800c\u5df2\u4e86\u3002\u201d</p><p>\u56e0\u4e3a\u662f\u5760\u5165\u6d77\u4e2d\uff0c\u6240\u4ee5\u5e94\u8be5\u6ca1\u6709\u50cf\u5176\u4ed6\u6d6e\u7a7a\u57ce\u90a3\u6837\u6454\u6210\u788e\u6e23\uff0c\u6362\u53e5\u8bdd\u8bf4\u5c31\u662f\u8fd8\u5927\u6709\u5269\u4f59\u4ef7\u503c\u53ef\u4f9b\u69a8\u53d6\u3002\u9634\u9b42\u57ce\u7684\u76ee\u7684\uff0c\u4fbf\u662f\u5c06\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u6253\u635e\u4e0a\u6765\uff0c\u81f3\u5c11\u662f\u628a\u91cc\u9762\u7684\u5b9d\u7269\u7ed9\u6253\u635e\u4e0a\u6765\u3002\u53ea\u662f\u5927\u6d77\u832b\u832b\uff0c\u8981\u627e\u4e00\u5ea7\u53e4\u57ce\u53ef\u4e0d\u5bb9\u6613\uff0c\u9634\u9b42\u57ce\u9b54\u6cd5\u53d1\u8fbe\uff0c\u5f3a\u8005\u4f17\u591a\uff0c\u4f46\u6f5c\u6c34\u5458\u8fd8\u771f\u627e\u4e0d\u5230\u51e0\u4e2a\uff0c\u6709\u76f8\u5173\u77e5\u8bc6\u7684\u4eba\u90fd\u5c11\u3002\u518d\u52a0\u4e0a\u6851\u6bd4\u4e9a\u5185\u6218\u7684\u5f71\u54cd\u6ce2\u53ca\uff0c\u5982\u4eca\u5df2\u7ecf\u5c0f\u534a\u5e74\u8fc7\u53bb\u4e86\uff0c\u9664\u4e86\u628a\u76ee\u6807\u8303\u56f4\u7f29\u5c0f\u5230\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\u9644\u8fd1\u7684\u6d77\u57df\u4e4b\u5916\uff0c\u76ee\u524d\u8fd8\u6ca1\u592a\u591a\u8fdb\u5c55\u3002</p><p>\u6ca1\u8fdb\u5c55\u4e0d\u8981\u7d27\uff0c\u53cd\u6b63\u8fd9\u4e0d\u5c5e\u4e8e\u743c\u6069\u7684\u5de5\u4f5c\uff0c\u4ed6\u8fd9\u6b21\u8fc7\u53bb\uff0c\u53ea\u662f\u5f85\u51e0\u5929\uff0c\u968f\u4fbf\u770b\u770b\u60c5\u51b5\uff0c\u56de\u6765\u4ea4\u4efd\u62a5\u544a\u4fbf\u7b97\u5b8c\u6210\u4efb\u52a1\u3002\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u4ed6\u8fd8\u6709\u66f4\u5173\u5fc3\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u5f17\u6717\u897f\u65af\u79d1\u5148\u751f\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u54e6\uff0c\u4ed6\u51fa\u6d77\u53bb\u4e86\u3002\u201d\u62c9\u52a0\u8bf4\u3002</p><p>\u6240\u8c13\u51fa\u6d77\uff0c\u610f\u601d\u5927\u6982\u5c31\u662f\u8bf4\u5230\u6d77\u91cc\u53bb\u641c\u7d22\u6d6e\u7a7a\u57ce\u5427\u3002\u201c\u90a3\u4ec0\u4e48\u65f6\u5019\u4f1a\u56de\u6765\u5462\uff1f\u201d\u743c\u6069\u53c8\u95ee\u3002</p><p>\u201c\u8fd9\u4e2a\u5c31\u4e0d\u597d\u8bf4\u4e86\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u201c\u6d77\u6d0b\u548c\u9646\u5730\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\uff0c\u884c\u7a0b\u5f88\u96be\u7531\u81ea\u5df1\u63a7\u5236\u3002\u5f17\u6717\u897f\u65af\u79d1\u5df2\u7ecf\u51fa\u53bb\u4e09\u5929\u4e86\uff0c\u6309\u7167\u4ee5\u5f80\u7684\u7ecf\u9a8c\uff0c\u957f\u5219\u516d\u4e03\u65e5\uff0c\u77ed\u5219\u4e24\u4e09\u65e5\uff0c\u4ed6\u5c31\u5e94\u8be5\u56de\u6765\u4e86\u2014\u2014\u627e\u4ed6\u6709\u4e8b\u5417\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u53ea\u662f\u4e00\u70b9\u79c1\u4e8b\uff0c\u60f3\u8981\u8bf7\u6559\u4ed6\u7f62\u4e86\u3002\u201d\u743c\u6069\u542b\u7cca\u7740\uff0c\u5c06\u8bdd\u9898\u626f\u5f00\u3002</p><p>\u65e2\u7136\u8981\u627e\u7684\u4eba\u4e0d\u5728\uff0c\u743c\u6069\u7b49\u4eba\u4fbf\u5148\u5b89\u987f\u4e0b\u6765\uff0c\u4e00\u8fb9\u4f11\u606f\u4e00\u8fb9\u7b49\u6d88\u606f\u3002\u773c\u770b\u53c8\u662f\u4e24\u5929\u8fc7\u53bb\u4e86\uff0c\u51fa\u6d77\u5bfb\u627e\u6d6e\u7a7a\u57ce\u7684\u9634\u9b42\u57ce\u4eba\u5458\u8fd8\u6ca1\u56de\u6765\uff0c\u743c\u6069\u4e5f\u4e0d\u7740\u6025\u3002\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\uff0c\u8fd9\u4e48\u591a\u5e74\u90fd\u7b49\u4e86\uff0c\u4e0d\u5728\u4e4e\u8fd9\u51e0\u5929\u3002</p><p>\u8fd9\u4e00\u65e5\u4e0b\u5348\uff0c\u4ed6\u5728\u81ea\u5df1\u7684\u623f\u95f4\u91cc\u6e29\u4e60\u9b54\u6cd5\u3002\u81ea\u4ece\u541e\u566c\u4e86\u5409\u52d2\u4eca\u795e\u529b\u4e4b\u540e\uff0c\u7ecf\u8fc7\u8fd9\u4e9b\u5929\u4e0d\u65ad\u6e29\u4e60\u5f3a\u5316\uff0c\u743c\u6069\u5df2\u7ecf\u80fd\u591f\u6210\u529f\u5e76\u7a33\u5b9a\u5730\u8054\u7ed3\u9b54\u7f51\u7684\u7b2c\u4e03\u5c42\u3002\u73b0\u5728\u8981\u505a\u7684\uff0c\u5c31\u662f\u5b66\u4e60\u4e0e\u4e4b\u76f8\u5339\u914d\u7684\u66f4\u9ad8\u6df1\u7684\u6cd5\u672f\u3002</p><p>\u4ed6\u7684\u9b54\u6cd5\u4e66\u4e2d\uff0c\u65e9\u5c31\u9884\u5148\u6284\u5f55\u4e86\u7cbe\u5fc3\u9009\u62e9\u7684\u5408\u9002\u6cd5\u672f\uff0c\u76f8\u5173\u8d44\u6599\u548c\u5185\u5bb9\u4e5f\u90fd\u5df2\u7ecf\u770b\u8fc7\u65e0\u6570\u904d\uff0c\u5177\u4f53\u7684\u7ec6\u5fae\u7591\u96be\u4e4b\u5904\u8fd8\u7279\u5730\u5411\u5965\u6c83\u8bf7\u6559\u8fc7\uff0c\u53ef\u8c13\u662f\u70c2\u719f\u4e8e\u5fc3\u3002\u7406\u8bba\u51c6\u5907\u65e2\u7136\u5df2\u7ecf\u5145\u5206\uff0c\u8f6c\u5316\u4e3a\u5b9e\u8df5\u4e5f\u5c31\u4e8b\u534a\u529f\u500d\u3002\u8fd9\u51e0\u5929\u4ed6\u5df2\u7ecf\u5b66\u4e60\u5e76\u57fa\u672c\u638c\u63e1\u4e86\u4e00\u4e2a\u53eb\u505a\u201c\u64cd\u63a7\u5929\u6c14\u201d\u7684\u6cd5\u672f\uff0c\u80fd\u591f\u5728\u77ed\u65f6\u95f4\u5185\u5f71\u54cd\u4e00\u5b9a\u8303\u56f4\u5185\u7684\u5929\u6c14\uff0c\u6ca1\u6709\u76f4\u63a5\u7684\u6740\u4f24\u529b\uff0c\u4f46\u8fd0\u7528\u5f97\u5de7\u5999\u7684\u8bdd\uff0c\u80fd\u591f\u5728\u5192\u9669\u6216\u6218\u6597\u4e2d\u53d1\u6325\u51fa\u5de8\u5927\u4f5c\u7528\u3002</p><p>\u5728\u8111\u4e2d\u518d\u5ea6\u6e29\u4e60\u6a21\u62df\u4e86\u4e24\u904d\u201c\u64cd\u63a7\u5929\u6c14\u201d\u6cd5\u672f\u540e\uff0c\u743c\u6069\u5c06\u76ee\u5149\u8f6c\u5411\u684c\u5b50\u7684\u53f3\u4fa7\u3002\u90a3\u91cc\u6b63\u653e\u7740\u4e00\u672c\u4e66\uff0c\u5f88\u8584\uff0c\u9876\u591a\u4e8c\u5341\u9875\uff0c\u7ea2\u8272\u7684\u5c01\u76ae\u4e0a\u6ca1\u6709\u4efb\u4f55\u6587\u5b57\uff0c\u88c5\u8ba2\u9887\u6709\u4e9b\u7c97\u7cd9\u3002\u8fd9\u662f\u4ed6\u4ece\u90a3\u4f4d\u5192\u5145\u5f17\u96f7\u65af\u7684\u82db\u5f8b\u4f8d\u8005\u8eab\u4e0a\u641c\u51fa\u7684\u6218\u5229\u54c1\u4e4b\u4e00\uff0c\u91cc\u9762\u8bb0\u8f7d\u4e86\u56db\u4e2a\u9887\u4e3a\u72ec\u7279\u7684\u9b54\u6cd5\u3002</p><p>\u201c\u5c3c\u67cf\u4e4b\u67d4\u548c\u63d0\u9192\u3001\u5c3c\u67cf\u4e4b\u6e29\u7f13\u8b66\u544a\u3001\u5c3c\u67cf\u4e4b\u4e25\u5389\u65a5\u8d23\u3001\u5c3c\u67cf\u4e4b\u6124\u6012\u60e9\u6212\u3002\u201d</p><p>\u867d\u7136\u662f\u56db\u4e2a\u540d\u5b57\uff0c\u5176\u5b9e\u672c\u8d28\u4e0a\u5c31\u662f\u540c\u4e00\u4e2a\uff0c\u7edf\u79f0\u4e3a\u201c\u5c3c\u67cf\u4e4b\u97ad\u7b1e\u672f\u201d\uff0c\u53ea\u4e0d\u8fc7\u662f\u8c03\u7528\u4e0d\u540c\u5c42\u7ea7\u7684\u9b54\u7f51\u80fd\u91cf\u5851\u6210\uff0c\u6240\u4ee5\u5a01\u529b\u4e0a\u6709\u6240\u533a\u522b\u7f62\u4e86\u3002\u7b80\u5355\u6765\u8bf4\uff0c\u8fd9\u6cd5\u672f\u7684\u4f5c\u7528\u5c31\u662f\u7528\u9b54\u529b\u5851\u6210\u4e00\u6761\u65e0\u5f62\u7684\u97ad\u5b50\uff0c\u5bf9\u53d7\u672f\u8005\u8fdb\u884c\u97ad\u7b1e\uff0c\u6548\u679c\u4ece\u6700\u8f7b\u5fae\u7684\u75bc\u75db\u548c\u9ebb\u75f9\uff08\u67d4\u548c\u63d0\u9192\uff09\u9010\u6b65\u589e\u5f3a\uff0c\u4e00\u76f4\u5230\u6700\u4e25\u91cd\u7684\u76f4\u63a5\u6b7b\u4ea1\uff08\u6124\u6012\u60e9\u6212\uff09\uff0c\u53d1\u660e\u8005\u5c3c\u67cf\u662f\u585e\u5c14\u5386\u53f2\u4e0a\u8457\u540d\u7684\u7ea2\u888d\u5deb\u5e08\uff0c\u66fe\u7ecf\u662f\u516b\u5927\u5b66\u6d3e\u9996\u5e2d\u4e4b\u4e00\uff08\u9644\u9b54\u9996\u5e2d\uff09\uff0c\u4ee5\u559c\u597d\u7528\u5404\u79cd\u522b\u51fa\u5fc3\u88c1\u7684\u624b\u6bb5\u6298\u78e8\u5974\u96b6\u800c\u201c\u540d\u5782\u9752\u53f2\u201d\u3002\u4ed6\u7684\u5f88\u591a\u8457\u4f5c\u548c\u8bba\u8ff0\uff0c\u90fd\u88ab\u585e\u5c14\u7684\u5974\u96b6\u8d29\u5b50\u5949\u4e3a\u7ecf\u5178\u6559\u6750\u52a0\u4ee5\u5b66\u4e60\u7684\u3002\u82db\u5f8b\u4f8d\u8005\u5192\u5145\u5f17\u96f7\u65af\uff0c\u4e0d\u77e5\u4ece\u4f55\u5904\u5f97\u5230\u8fd9\u51e0\u4e2a\u6cd5\u672f\u7684\u8d44\u6599\uff0c\u4f30\u8ba1\u662f\u60f3\u81ea\u5df1\u6709\u7a7a\u5b66\u4e60\uff0c\u6ca1\u60f3\u5230\u6700\u540e\u4fbf\u5b9c\u4e86\u743c\u6069\u3002</p><p>\u56e0\u4e3a\u653e\u5f03\u4e86\u5851\u80fd\u548c\u4ea1\u7075\u672f\uff0c\u743c\u6069\u7684\u9b54\u6cd5\u653b\u51fb\u529b\u4e0d\u8db3\u4e00\u76f4\u662f\u4e2a\u7f3a\u9677\uff0c\u867d\u7136\u6709\u4e86\u89e3\u79bb\u548c\u77f3\u5316\uff0c\u7ec8\u7a76\u8fd8\u662f\u592a\u5c11\u592a\u5355\u8c03\uff0c\u800c\u4e14\u89e3\u79bb\u592a\u8fc7\u51f6\u72e0\uff0c\u8f7b\u6613\u4e0d\u6562\u52a8\u7528\u3002\u6709\u4e86\u8fd9\u5c3c\u67cf\u4e4b\u97ad\u7b1e\u672f\uff0c\u4ee5\u540e\u5bf9\u654c\u7684\u65f6\u5019\uff0c\u53ef\u4f9b\u9009\u62e9\u7684\u4f59\u5730\u4e5f\u5c31\u5927\u5f88\u591a\u4e86\u3002</p><p>\u800c\u4e14\uff0c\u9664\u4e86\u5bf9\u654c\uff0c\u4f3c\u4e4e\u8fd8\u6709\u522b\u7684\u7528\u9014\uff0c\u6bd4\u5982\u95fa\u623f\u8c03\u6559\u2026\u2026</p><p>\u743c\u6069\u5fc3\u4e2d\u60f3\u7740\uff0c\u5c06\u76ee\u5149\u4e0b\u79fb\u3002\u6e29\u4e60\u9b54\u6cd5\u7684\u65f6\u5019\uff0c\u4ed6\u662f\u5750\u5728\u4e66\u684c\u524d\uff0c\u800c\u5728\u684c\u5b50\u4e0b\u9762\uff0c\u6b63\u8dea\u7740\u4e00\u4f4d\u59a9\u5a9a\u5a07\u4fcf\u7684\u8910\u53d1\u7f8e\u4eba\uff0c\u4e56\u5de7\u5730\u5c06\u8138\u57cb\u5728\u4ed6\u7684\u80ef\u95f4\uff0c\u5c0f\u5634\u5356\u529b\u541e\u5410\uff0c\u505a\u7740\u9999\u8273\u65d6\u65ce\u7684\u670d\u52a1\u3002\u8fd9\u662f\u743c\u6069\u7684\u8981\u6c42\uff0c\u5e76\u7f8e\u5176\u540d\u66f0\uff1a\u5e2e\u52a9\u4e3b\u4eba\u505a\u65bd\u6cd5\u4e13\u6ce8\u7684\u8bad\u7ec3\u3002</p><p>\u65e2\u7136\u662f\u8981\u5e2e\u52a9\u505a\u8bad\u7ec3\uff0c\u838e\u73de\u514b\u81ea\u7136\u4e0d\u4f1a\u5f53\u771f\u62ff\u51fa\u5168\u90e8\u672c\u4e8b\uff0c\u53ea\u662f\u6d45\u5c1d\u8f84\u6b62\uff0c\u5426\u5219\u4e16\u4e0a\u54ea\u91cc\u8fd8\u6709\u7537\u4eba\u80fd\u591f\u62b5\u6297\u5f97\u4f4f\uff0c\u66f4\u522b\u63d0\u8fd8\u770b\u4e66\u5b66\u4e60\u4e86\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u8fd8\u662f\u5f04\u5f97\u743c\u6069\u8212\u723d\u4e0d\u5df2\uff0c\u98d8\u98d8\u6b32\u4ed9\uff0c\u751a\u81f3\u60f3\u53e4\u4eba\u8bf4\u8bfb\u4e66\u4e4b\u4e50\uff0c\u5728\u4e8e\u7ea2\u8896\u6dfb\u9999\uff0c\u7136\u800c\u7ea2\u8896\u6dfb\u9999\uff0c\u53c8\u54ea\u91cc\u6bd4\u5f97\u4e0a\u6709\u7f8e\u4eba\u5439\u7bab\u2026\u2026\u8fd9\u5230\u5e95\u662f\u53e4\u4eba\u592a\u7eaf\u6d01\uff0c\u8fd8\u662f\u81ea\u5df1\u592a\u90aa\u6076\u3002</p><p>\u597d\u5427\uff0c\u4e0d\u60f3\u8fd9\u4e9b\u65e0\u804a\u7684\u95ee\u9898\u3002</p><p>\u4ed6\u62cd\u4e86\u62cd\u9b45\u9b54\u7684\u5934\uff0c\u793a\u610f\u5979\u4ece\u684c\u4e0b\u722c\u51fa\uff0c\u7136\u540e\u5750\u5728\u81ea\u5df1\u5927\u817f\u4e0a\u3002\u4e24\u4eba\u9762\u5bf9\u7740\u9762\uff0c\u4e0b\u4f53\u7d27\u5bc6\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u838e\u73de\u514b\u6402\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u4e00\u53cc\u957f\u817f\u7d27\u7d27\u5730\u6263\u7740\u4ed6\u7684\u8170\u3002\u8fd9\u4e2a\u59ff\u52bf\u5979\u5b8c\u5168\u65e0\u6cd5\u4e0a\u4e0b\u52a8\u4f5c\uff0c\u53ea\u80fd\u9759\u9759\u62b1\u7740\uff0c\u4f46\u7d27\u81f4\u7684\u82b1\u5f84\u5185\u58c1\u5374\u968f\u7740\u547c\u5438\u5267\u70c8\u5730\u62bd\u6410\u6536\u7f29\u7740\uff0c\u7ed9\u743c\u6069\u7684\u611f\u89c9\u5b8c\u5168\u4e0d\u4e9a\u4e8e\u771f\u6b63\u7684\u6fc0\u70c8\u4ea4\u5408\uff0c\u597d\u51e0\u6b21\u90fd\u9669\u9669\u8981\u7206\u53d1\u51fa\u6765\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u838e\u73de\u514b\u8fd9\u6837\u505a\uff0c\u8017\u8d39\u7684\u4f53\u529b\u4e5f\u8981\u8fdc\u8fdc\u8d85\u8fc7\u6b63\u5e38\u6b22\u597d\u3002\u5979\u7684\u5598\u606f\u4e0d\u77e5\u4e0d\u89c9\u5df2\u7ecf\u53d8\u5f97\u7c97\u91cd\u8d77\u6765\uff0c\u70ed\u70ed\u7684\u6c14\u606f\u5439\u5728\u743c\u6069\u8033\u6735\u91cc\uff0c\u4e00\u9635\u9635\u9ebb\u75d2\u4eff\u4f5b\u8f7b\u5fae\u7535\u6d41\u4f20\u904d\u5168\u8eab\uff0c\u6ee1\u8574\u6c34\u6c7d\u7684\u7f8e\u7738\u534a\u7741\u534a\u95ed\uff0c\u4fcf\u8138\u4e24\u988a\u5982\u9152\u9189\u822c\u9161\u7ea2\u3002\u743c\u6069\u9759\u9759\u770b\u7740\uff0c\u7136\u540e\u8f7b\u8f7b\u543b\u4e0a\u5979\u56e0\u4e3a\u5e72\u71e5\u800c\u683c\u5916\u9c9c\u8273\u7684\u5634\u5507\u3002</p><p>\u5507\u74e3\u4e00\u89e6\uff0c\u838e\u73de\u514b\u50cf\u662f\u53d7\u5230\u60ca\u5413\u822c\uff0c\u5934\u4e0b\u610f\u8bc6\u5730\u540e\u4ef0\uff0c\u773c\u775b\u4e5f\u7741\u5f97\u5927\u5927\uff0c\u4eff\u4f5b\u4e0d\u6562\u7f6e\u4fe1\u3002\u743c\u6069\u5374\u4e0d\u7ba1\uff0c\u53cc\u624b\u6367\u7740\u5c11\u5973\u7684\u8138\u86cb\uff0c\u8ba9\u5979\u65e0\u4ece\u8eb2\u907f\uff0c\u518d\u5ea6\u543b\u4e0a\u6a31\u5507\uff0c\u820c\u5c16\u67d4\u67d4\u6311\u9017\u7740\uff0c\u64ac\u5f00\u8d1d\u9f7f\uff0c\u6e38\u5165\u53e3\u8154\u3002\u838e\u73de\u514b\u53d1\u51fa\u201c\u5514\u3001\u5514\u201d\u7684\u4f4e\u541f\uff0c\u50cf\u662f\u6297\u62d2\uff0c\u9999\u820c\u5374\u662f\u4e0d\u7531\u81ea\u4e3b\u5730\u8fce\u5408\u4e0a\u6765\uff0c\u548c\u7537\u4eba\u505a\u7740\u6fc0\u70c8\u7684\u70ed\u543b\u3002</p><p>\u534a\u664c\u4e4b\u540e\uff0c\u4e24\u4eba\u5206\u5f00\uff0c\u838e\u73de\u514b\u770b\u7740\u743c\u6069\uff0c\u773c\u795e\u4e2d\u4eff\u4f5b\u591a\u4e86\u4e00\u4e1d\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\uff0c\u4ece\u672a\u6709\u8fc7\u7684\u610f\u5473\u3002\u201c\u743c\u6069\u3002\u201d\u5979\u8f7b\u8f7b\u53eb\u7740\u5deb\u5e08\u7684\u540d\u5b57\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4f60\u77e5\u9053\u4e48\uff0c\u201d\u5979\u8bf4\uff0c\u7ea2\u6655\u7684\u8138\u988a\u6cdb\u8d77\u7eaf\u771f\u7684\u7f9e\u6da9\uff0c\u201c\u8fd9\u662f\u6211\u7b2c\u4e00\u6b21\u8ba9\u7537\u4eba\u8fd9\u6837\u543b\u81ea\u5df1\u3002\u4ee5\u524d\u6211\u6700\u591a\u2026\u2026\u6700\u591a\u8ba9\u4ed6\u4eec\u4eb2\u4eb2\u5634\u5507\u800c\u5df2\uff0c\u4ece\u6765\u4e0d\u51c6\u4ed6\u4eec\u628a\u820c\u5934\u4f38\u8fdb\u6765\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u4f60\u76f8\u4fe1\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u838e\u73de\u514b\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\uff0c\u201c\u4e3a\u4ec0\u4e48\u4eca\u5929\u4f1a\u5bf9\u6211\u8fd9\u4e48\u6e29\u67d4\u554a\uff1f\u201d</p><p>\u201c\u600e\u4e48\uff1f\u4e0d\u559c\u6b22\uff1f\u201d</p><p>\u201c\u5f53\u7136\u559c\u6b22\uff0c\u6211\u53c8\u4e0d\u662f\u5fc3\u7406\u53d8\u6001\uff0c\u201d\u838e\u73de\u514b\u6492\u5a07\u4f3c\u5730\u6485\u8d77\u5634\uff0c\u201c\u80fd\u88ab\u7537\u4eba\u6e29\u67d4\u5730\u5475\u62a4\u5bf9\u5f85\uff0c\u53c8\u6709\u54ea\u4e2a\u5973\u5b69\u5b50\u4f1a\u4e0d\u559c\u6b22\u7684\u3002\u201d</p><p>\u201c\u559c\u6b22\u5c31\u884c\u4e86\uff0c\u95ee\u90a3\u4e48\u591a\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u5e73\u65f6\u4ece\u6765\u90fd\u5bf9\u6211\u5f88\u51f6\u554a\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4ece\u6765\u6ca1\u5bf9\u6211\u8fd9\u4e48\u6e29\u67d4\u8fc7\uff0c\u4eba\u5bb6\u4e0d\u4e60\u60ef\u561b\u3002\u201d</p><p>\u5979\u7684\u58f0\u97f3\u53c8\u8f6f\u53c8\u751c\uff0c\u4eff\u4f5b\u6709\u52fe\u9b42\u7684\u9b45\u60d1\u529b\uff0c\u542c\u5f97\u743c\u6069\u4e0b\u8eab\u4e00\u6da8\uff0c\u51e0\u4e4e\u8981\u55b7\u53d1\u51fa\u6765\uff0c\u8d76\u5feb\u6536\u655b\u5fc3\u795e\u624d\u4e0d\u81f3\u4e8e\u5c31\u6b64\u8d25\u9635\u3002\u201c\u6211\u5e73\u65f6\u5bf9\u4f60\u5f88\u51f6\uff1f\u6709\u5417\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u4f60\u5bf9\u5176\u4ed6\u4eba\u90fd\u5f88\u597d\uff0c\u628a\u5979\u4eec\u5f53\u60c5\u4eba\u770b\u5f85\uff0c\u522b\u8bf4\u827e\u5f25\u8587\u548c\u73ca\u5609\uff0c\u5c31\u7b97\u662f\u8299\u857e\u72c4\uff0c\u4f60\u90fd\u4f1a\u54c4\u5979\uff0c\u5ba0\u5979\uff0c\u601c\u60dc\u5979\uff0c\u6211\u5c31\u4ece\u6765\u6ca1\u8fd9\u79cd\u5f85\u9047\uff0c\u201d\u838e\u73de\u514b\u5e7d\u5e7d\u8bf4\uff0c\u201c\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u6211\u5c31\u50cf\u662f\u4e2a\u5de5\u5177\u4f3c\u7684\uff0c\u6253\u67b6\u7684\u65f6\u5019\u53ef\u4ee5\u5e2e\u624b\uff0c\u7a7a\u95f2\u7684\u65f6\u5019\u53ef\u4ee5\u804a\u5929\uff0c\u60f3\u8981\u5973\u4eba\u4e86\u53c8\u627e\u4e0d\u5230\u5176\u4ed6\u4eba\uff0c\u5c31\u5728\u6211\u8eab\u4e0a\u53d1\u6cc4\u3002\u6bcf\u6b21\u505a\u5b8c\u4e86\uff0c\u76f4\u63a5\u5c31\u79bb\u5f00\uff0c\u8fde\u4e00\u70b9\u5b8c\u4e8b\u540e\u7684\u6e29\u5b58\u90fd\u6ca1\u6709\u5462\u3002\u201d</p><p>\u743c\u6069\u8bed\u585e\uff0c\u4e00\u65f6\u65e0\u8bdd\u53ef\u8bf4\u3002</p><p>\u201c\u4ece\u6df1\u6e0a\u5f00\u59cb\uff0c\u6211\u8ddf\u7740\u4f60\u4e5f\u5df2\u7ecf\u5927\u534a\u5e74\u4e86\u5427\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e0d\u7ba1\u4e00\u5f00\u59cb\u56e0\u4e3a\u4ec0\u4e48\u539f\u56e0\uff0c\u6211\u603b\u80fd\u7b97\u662f\u5bf9\u4f60\u5c3d\u5fc3\u5c3d\u529b\u3002\u5df2\u7ecf\u7b7e\u4e86\u771f\u540d\u5951\u7ea6\uff0c\u6211\u4e5f\u5c31\u4ece\u4e0d\u6307\u671b\u8fd8\u80fd\u79bb\u5f00\u4f60\uff0c\u53ea\u60f3\u80fd\u8fc7\u5f97\u597d\u4e9b\u3002\u53ef\u662f\u4f60\u5bf9\u6211\u5462\uff0c\u65e0\u8bba\u6211\u505a\u5f97\u518d\u591a\uff0c\u505a\u5f97\u518d\u597d\uff0c\u5728\u4f60\u5fc3\u4e2d\uff0c\u6211\u4f9d\u65e7\u90fd\u8fd8\u662f\u5728\u70db\u5821\u7b2c\u4e00\u6b21\u89c1\u9762\u65f6\u7684\u90a3\u4e2a\u6740\u624b\u5427\u3002\u201d</p><p>\u201c\u838e\u73de\u514b\u2026\u2026\u201d</p><p>\u743c\u6069\u6b63\u60f3\u9053\u6b49\uff0c\u5c11\u5973\u7a81\u7136\u5657\u55e4\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\uff0c\u8138\u4e0a\u53c8\u9732\u51fa\u5e73\u65f6\u90a3\u79cd\u5996\u8273\u59a9\u5a9a\u7684\u795e\u60c5\uff0c\u201c\u8ddf\u4f60\u5f00\u73a9\u7b11\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd8\u771f\u76f8\u4fe1\u4e86\uff1f\u7b28\u7b28\u7b28\u7b28\uff0c\u522b\u5fd8\u4e86\u6211\u53ef\u662f\u9b45\u9b54\u554a\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u7b11\uff0c\u770b\u7740\u5979\uff0c\u201c\u6216\u8bb8\u4e0d\u662f\u73a9\u7b11\u5462\uff1f\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u73a9\u7b11\uff0c\u90a3\u4e5f\u6ca1\u5173\u7cfb\u554a\u3002\u603b\u662f\u4e00\u79cd\u73a9\u6cd5\u4e5f\u592a\u65e0\u804a\uff0c\u5f85\u4f60\u6e29\u67d4\u70b9\uff0c\u5c31\u5f53\u662f\u6362\u79cd\u82b1\u6837\uff0c\u6362\u4e2a\u53e3\u5473\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u9b45\u9b54\u683c\u683c\u5730\u5a07\u7b11\u7740\uff0c\u82b1\u5f84\u7684\u6709\u529b\u6536\u7f29\u539f\u672c\u56e0\u4e3a\u63a5\u543b\u548c\u8bf4\u8bdd\u800c\u505c\u6b62\u4e0b\u6765\uff0c\u73b0\u5728\u53c8\u518d\u5ea6\u6062\u590d\uff0c\u800c\u4e14\u901f\u5ea6\u52a0\u5feb\uff0c\u201c\u90a3\u4e48\uff0c\u4e3b\u4eba\uff0c\u8981\u4e0d\u8981\u518d\u6362\u79cd\u53e3\u5473\u5462\uff0c\u201d\u5979\u5a9a\u773c\u5982\u4e1d\uff0c\u5410\u6c14\u5982\u5170\uff0c\u58f0\u97f3\u4f4e\u5f97\u4eff\u4f5b\u542c\u4e0d\u89c1\uff0c\u5374\u53c8\u8ba9\u743c\u6069\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u201c\u9075\u7167\u60a8\u7684\u5429\u5490\uff0c\u90a3\u5730\u65b9\u5df2\u7ecf\u2026\u2026\u505a\u597d\u4e86\u51c6\u5907\u5462\u3002\u201d</p><p>\u90a3\u5f53\u7136\u662f\u8981\u7684\u4e86\uff0c\u4e3b\u52a8\u9001\u5230\u5634\u8fb9\u7684\u7f8e\u98df\uff0c\u5c82\u6709\u653e\u8fc7\u7684\u9053\u7406\u3002</p><p>\u743c\u6069\u53cc\u624b\u63e1\u7740\u9b45\u9b54\u7684\u7ea4\u8170\uff0c\u5c06\u5979\u6574\u4e2a\u4eba\u4ece\u8eab\u4e0a\u201c\u62d4\u201d\u51fa\u6765\u3002\u838e\u73de\u514b\u4e56\u5de7\u5730\u5c06\u4e0a\u534a\u8eab\u8db4\u5728\u4e66\u684c\u4e0a\uff0c\u9ad8\u9ad8\u7fd8\u8d77\u96ea\u767d\u7f8e\u81c0\uff0c\u53cc\u624b\u4e3b\u52a8\u63b0\u5f00\u81c0\u8089\uff0c\u9732\u51fa\u7ea4\u5de7\u96cf\u83ca\uff0c\u7b49\u5f85\u7740\u7537\u4eba\u7684\u5165\u4fb5\u3002\u743c\u6069\u505a\u4e86\u4e2a\u6df1\u547c\u5438\uff0c\u62b5\u4e0a\u5979\u7684\u8eab\u4f53\uff0c\u6b63\u8981\u8fdb\u5165\u3002\u7a81\u7136\u201c\u7830\u7830\u201d\u5730\u6572\u95e8\u58f0\u54cd\u8d77\uff0c\u63a5\u7740\u62c9\u52a0\u7684\u96be\u542c\u55d3\u97f3\u4ece\u5916\u9762\u4f20\u4e86\u8fdb\u6765\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u5728\u5417\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u6709\u5927\u53d1\u73b0\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u4e0d\u5728\uff01\u201d</p></article>"}}}]);
