(window.webpackJsonp=window.webpackJsonp||[]).push([[166],{189:function(p){p.exports={chapterTitle:null,title:"\u7b2c1144\u7ae0 \u80cc\u540e\u7684\u4ea4\u6613",content:"<article><h2>\u7b2c1144\u7ae0 \u80cc\u540e\u7684\u4ea4\u6613</h2><p>\u3000\u3000\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\u7684\u95ee\u9898\uff0c\u4e1c\u5927\u521b\u57df\u3001\u5353\u57df\u8fd9\u8fb9\u4e0d\u4f1a\u62d6\u6ce5\u5e26\u6c34\uff0c\u4f46\u6606\u817e\u5728\u7ebf\u6bd5\u7adf\u662f\u6d77\u5916\u4e0a\u5e02\u516c\u53f8\uff0c\u5982\u6b64\u5927\u5e45\u5ea6\u7684\u4e1a\u52a1\u8c03\u6574\uff0c\u9700\u8981\u7ecf\u8fc7\u8463\u4e8b\u4f1a\u7684\u6279\u51c6\u3002</p><p>\u3000\u3000\u867d\u7136\u6a61\u6811\u56ed\u662f\u7b2c\u4e00\u5927\u80a1\u4e1c\uff0c\u7ba1\u7406\u5c42\u6301\u80a1\u6bd4\u4f8b\u4e5f\u76f8\u5f53\u9ad8\uff0c\u4f46\u5916\u8d44\u80a1\u4e1c\u8981\u662f\u575a\u6301\u62b5\u5236\u7684\u8bdd\uff0c\u65b9\u6848\u8fd8\u662f\u65e0\u6cd5\u5b9e\u884c\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u8463\u4e8b\u4f1a\u6709\u4e24\u540d\u5916\u65b9\u8463\u4e8b\u4ee3\u8868\uff0c\u4e00\u662f\u4ee3\u8868\u534e\u767b\u7cfb\u98ce\u6295\u57fa\u91d1\uff0c\u4e00\u662f\u4ee3\u8868\u9ad8\u76db\u65d7\u4e0b\u7684\u98ce\u6295\u57fa\u91d1\uff0c\u6606\u817e\u5728\u7ebf\u5728\u77ed\u77ed\u4e09\u56db\u5e74\u65f6\u95f4\u91cc\u80fd\u6709\u8fd9\u4e48\u5927\u7684\u53d1\u5c55\u5e76\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\uff0c\u8fd9\u4e24\u5bb6\u98ce\u6295\u57fa\u91d1\u7684\u53c2\u4e0e\u529f\u4e0d\u53ef\u6ca1\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u4e0a\u5e02\u516d\u4e2a\u6708\u8fc7\u4e86\u89e3\u7981\u671f\uff0cESS\u575a\u51b3\u505a\u51fa\u51cf\u6301\u6240\u6301\u80a1\u7968\u7684\u51b3\u5b9a\u3002\u6606\u817e\u5728\u7ebf\u5f53\u65f6\u516c\u5f00\u53d1\u884c\u7684\u80a1\u7968\u603b\u91cf\u624d\u4e94\u767e\u4e94\u5341\u4e07\u80a1\uff0cESS\u7684\u51cf\u6301\u91cf\u5c31\u9ad8\u8fbe\u4e00\u5343\u4e03\u767e\u4e07\u80a1\uff0c\u534e\u767b\u4e0e\u9ad8\u76db\u4e5f\u4e0d\u662f\u4e0d\u60f3\u51cf\u6301\uff0c\u4f46\u662f\u4ed6\u4eec\u4e5f\u8981\u540c\u671f\u51cf\u6301\uff0c\u51cf\u6301\u603b\u91cf\u5c31\u8981\u518d\u589e\u52a0\u8fd1\u4e09\u5343\u4e07\u80a1\u3002\u5373\u4f7f\u5f53\u65f6\u7684\u5e02\u573a\u8fd8\u5904\u4e8e\u4e0a\u884c\u901a\u9053\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6709\u8fd9\u4e48\u575a\u5b9e\u7684\u5e02\u573a\u652f\u6491\uff0c\u72b9\u8c6b\u4e4b\u4e0b\uff0c\u534e\u767b\u4e0e\u9ad8\u76db\u90fd\u51b3\u5b9a\u6682\u65f6\u7ee7\u7eed\u6301\u6709\u6606\u817e\u5728\u7ebf\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u672a\u66fe\u60f3\u5230\u7b49ESS\u51cf\u6301\u5e72\u51c0\u3001\u4ed6\u4eec\u521a\u8981\u5b9e\u65bd\u51cf\u6301\u8ba1\u5212\u4e4b\u65f6\uff0c\u7eb3\u6307\u5c31\u9876\u4e0d\u4f4f\u4e86\u3002\u516b\u4e2a\u4ea4\u6613\u65e5\u5185\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u5c31\u51e0\u906d\u8170\u65a9\u3002\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u534e\u767b\u4e0e\u9ad8\u76db\u90fd\u5c1d\u8bd5\u51cf\u6301\uff0c\u7ed3\u679c\u53d1\u73b0\u652f\u6491\u592a\u5f31\uff0c\u4ed6\u4eec\u7a0d\u653e\u5f00\u4e9b\u53e3\u5b50\uff0c\u80a1\u4ef7\u5c31\u5927\u5e45\u63a2\u5e95\uff0c\u4e0d\u5f97\u4e0d\u5f7c\u6b64\u9ed8\u5951\u7684\u6682\u7f13\u51cf\u6301\u6b65\u4f10\uff0c\u7b49\u5f85\u5408\u9002\u7684\u65f6\u673a\u5230\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9ad8\u76db\u516c\u53f8\u4e9a\u6d32\u533a\u526f\u603b\u88c1\u66fe\u5b50\u8f89\u8fd8\u662f\u9996\u6b21\u5230\u5efa\u90ba\u6765\u3002</p><p>\u3000\u3000\u867d\u7136\u4e94\u6708\u4e0b\u65ec\u7684\u5efa\u90ba\u5df2\u7ecf\u662f\u521d\u590f\u7684\u5929\u6c14\uff0c\u6709\u4e9b\u708e\u70ed\uff0c\u8857\u5934\u5df2\u6709\u7a7f\u77ed\u88d9\u3001\u540a\u5e26\u886b\u7684\u5e74\u8f7b\u5973\u5b69\u5b50\u5149\u5f69\u8000\u4eba\u7684\u63a0\u593a\u884c\u4eba\u7684\u773c\u7403\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u4e34\u8857\u7684\u5496\u5561\u5385\u91cc\uff0c\u5f25\u6f2b\u7740\u6d53\u90c1\u7684\u5496\u5561\u9999\u6c14\uff0c\u900f\u660e\u7684\u843d\u5730\u73bb\u7483\u7a97\uff0c\u7a97\u5916\u68a7\u6850\u679d\u4e1b\u7e41\u8302\u7fe0\u7eff\uff0c\u4ece\u6811\u68a2\u95f4\u9699\u91cc\uff0c\u53ef\u4ee5\u5076\u5c14\u6355\u6349\u5230\u9752\u6625\u9753\u4e3d\u7684\u5e74\u8f7b\u5973\u5b69\u5b50\u7684\u5029\u5f71\uff0c\u66fe\u5b50\u8f89\u4e0d\u660e\u767d\u5f20\u606a\u4e3a\u4ec0\u4e48\u8981\u9009\u62e9\u5728\u8fd9\u95f4\u666e\u901a\u5496\u5561\u5385\u91cc\u8ddf\u4ed6\u89c1\u9762\uff0c\u4e0d\u8fc7\u611f\u89c9\u76f8\u5f53\u4e0d\u574f\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u4e0e\u9526\u6e56\u7684\u5176\u4ed6\u9ad8\u5c42\u63a5\u89e6\u8f83\u591a\uff0c\u4e0e\u5f20\u606a\u89c1\u9762\u624d\u7b2c\u4e09\u6b21\u3002\u4e00\u6b21\u662f\u5728\u5317\u4eac\uff0c\u662f\u4e3a\u6606\u817e\u5728\u7ebf\u5230\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u4e8b\u60c5\uff1b\u4e00\u6b21\u662f\u65e7\u91d1\u5c71\uff0c\u4e3a\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u80a1\u6743\u4ea4\u6613\u7684\u4e8b\u60c5\uff1b\u66fe\u5b50\u8f89\u4e5f\u662f\u5c11\u5e74\u5f97\u5fd7\u7684\u4eba\uff0c\u4ece\u54c8\u4f5b\u6bd5\u4e1a\u4e4b\u540e\u5c31\u8fdb\u5165\u9ad8\u76db\u5de5\u4f5c\uff0c\u4e09\u5341\u5c81\u5c31\u62c5\u4efb\u9ad8\u76db\u4e9a\u6d32\u533a\u526f\u603b\u88c1\u7684\u9ad8\u7ea7\u804c\u52a1\uff0c\u4ed6\u4e0d\u6e05\u695a\u773c\u524d\u8fd9\u4e2a\u6bd4\u81ea\u5df1\u5c0f\u5f97\u591a\u7684\u9752\u5e74\u8eab\u5904\u6b64\u65f6\u7684\u5730\u4f4d\u4f1a\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u6709\u90a3\u79cd\u9ad8\u9ad8\u5728\u4e0a\u3001\u89c6\u82b8\u82b8\u4f17\u751f\u5982\u65e0\u7269\u7684\u611f\u89c9\u5417\uff1f</p><p>\u3000\u3000\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u62ff\u7740\u4e0d\u9508\u94a2\u5236\u7684\u5c0f\u5319\u6405\u52a8\u7740\u676f\u4e2d\u7684\u5496\u5561\uff0c\u53ea\u662f\u5496\u5561\u676f\u672a\u5fc5\u592a\u5c0f\u4e86\u4e00\u4e9b\uff0c\u80fd\u540c\u65f6\u6293\u56db\u4e94\u53ea\u5728\u624b\u5fc3\u91cc\uff0c\u4e0d\u9508\u94a2\u5236\u7684\u5496\u5561\u58f6\u770b\u4e0a\u53bb\u8fd8\u6709\u5341\u4e94\u516d\u516c\u5206\u9ad8\uff0c\u4f46\u662f\u5bb9\u91cf\u5b9e\u5728\u6709\u9650\uff0c\u5f20\u606a\u966a\u66fe\u5b50\u8f89\u5750\u4e0b\u6765\u95f2\u626f\u5efa\u90ba\u7684\u98ce\u7269\u6ca1\u591a\u4e45\uff0c\u4ed6\u5c31\u8ba9\u4f8d\u5e94\u751f\u6dfb\u4e86\u4e24\u58f6\u5496\u5561\uff0c\u5168\u7136\u6ca1\u6709\u5e38\u4eba\u996e\u5496\u5561\u7684\u4f18\u96c5\u4e0e\u95f2\u6de1\u3002</p><p>\u3000\u3000\u201c\u65b0\u5149\u7eb8\u4e1a\u4e8e\u9999\u6e2f\u4e0a\u5e02\u52df\u8d44\u4e00\u4e8b\uff0c\u52b3\u70e6\u66fe\u603b\u5954\u6ce2\u52b3\u7d2f\uff0c\u6211\u8fd8\u60f3\u5728\u5efa\u90ba\u597d\u597d\u62db\u5f85\u4f60\uff0c\u6ca1\u60f3\u5230\u4f60\u7684\u884c\u7a0b\u8fd9\u4e48\u7d27\uff0c\u665a\u4e0a\u5c31\u8981\u8d76\u98de\u673a\u53bb\u5317\u4eac\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u770b\u7740\u5f20\u606a\u5c06\u5496\u5561\u6ee1\u53e3\u559d\u8fdb\u809a\u5b50\u91cc\uff0c\u7f51\u7403\u886b\u7684\u77ed\u8896\u4e0a\u8fd8\u6709\u64e6\u6c57\u7559\u4e0b\u7684\u75d5\u8ff9\uff0c\u77e5\u9053\u4ed6\u521a\u521a\u6253\u5b8c\u7f51\u7403\u6765\u89c1\u81ea\u5df1\uff0c\u5fc3\u91cc\u5f53\u7136\u7fa1\u6155\u4ed6\u7684\u60a0\u95f2\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6df7\u53e3\u996d\u5403\u4e0d\u5bb9\u6613\u2026\u2026\u4e00\u76f4\u60e6\u8bb0\u7740\u4e0a\u56de\u5728\u65e7\u91d1\u5c71\u65f6\u606a\u5c11\u8bf4\u8fc7\u7684\u8bdd\uff0c\u65e2\u7136\u6709\u4ece\u5efa\u90ba\u8f6c\u673a\u505c\u7559\u7684\u673a\u4f1a\uff0c\u4e5f\u60f3\u8fc7\u6765\u9886\u7565\u4e00\u4e0b\u5efa\u90ba\u7684\u95f2\u9002\uff0c\u53ea\u662f\u884c\u7a0b\u592a\u5306\u5306\u4e86\u2026\u2026\u7ea6\u597d\u665a\u4e0a\u4e0e\u4e54\u4f0a\u65af\u5148\u751f\u5728\u5317\u4eac\u89c1\u9762\uff0c\u4e0d\u8fc7\u5c31\u80fd\u591a\u505c\u7559\u4e00\u5929\u4e86\u3002\u201d</p><p>\u3000\u3000\u4ece\u9999\u6e2f\u98de\u5f80\u5317\u4eac\u7684\u822a\u73ed\uff0c\u603b\u8981\u6bd4\u98de\u5f80\u5efa\u90ba\u7684\u822a\u73ed\u5bc6\u5f97\u591a\uff0c\u66fe\u5b50\u8f89\u4e13\u7a0b\u5728\u5efa\u90ba\u4f5c\u77ed\u6682\u7684\u505c\u7559\u81ea\u7136\u6709\u5176\u4ed6\u7684\u610f\u56fe\u5728\uff1b\u4e54\u4f0a\u65af\u662f\u9ad8\u76db\u6d3e\u9a7b\u6606\u817e\u5728\u7ebf\u8463\u4e8b\u4f1a\u7684\u80a1\u6743\u8463\u4e8b\u4ee3\u8868\u3002</p><p>\u3000\u3000\u5728\u63a5\u5230\u66fe\u5b50\u8f89\u4ece\u9999\u6e2f\u4f20\u771f\u6765\u7684\u89c1\u9762\u8bf7\u6c42\uff0c\u5f20\u606a\u5c31\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u8fd9\u65f6\u5019\u89c1\u4ed6\u6311\u660e\u4e86\u610f\u56fe\uff0c\u4e5f\u8bf4\u9053\uff1a\u201c\u65e9\u542c\u8bf4\u4e54\u4f0a\u65af\u5148\u751f\u662f\u5f88\u6709\u8da3\u7684\u4e00\u4e2a\u4eba\uff0c\u4f60\u665a\u4e0a\u8ddf\u4ed6\u78b0\u9762\uff0c\u5e2e\u6211\u7a0d\u4e2a\u95ee\u5019\u7ed9\u4ed6\uff0c\u5e0c\u671b\u4ed6\u8fd9\u6b21\u7684\u4e2d\u56fd\u4e4b\u65c5\u6109\u5feb\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u8bf4\u7f8e\u56fd\u8bc1\u5238\u5e02\u573a\u7684\u76d1\u7ba1\u4f53\u7cfb\u4e25\u5bc6\uff0c\u4f46\u5bf9\u4e8e\u9ad8\u76db\u8fd9\u6837\u7684\u8d22\u9600\uff0c\u4e5f\u975e\u6ca1\u6709\u7f1d\u9699\u53ef\u94bb\u3002</p><p>\u3000\u3000\u8fd9\u6bb5\u65f6\u95f4\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5267\u70c8\u632f\u8361\uff0c\u7f51\u7edc\u80a1\u8fd8\u6ca1\u6709\u4f01\u7a33\u7684\u8ff9\u8c61\uff0c\u8dcc\u5e45\u6700\u6df1\u7684\u7f51\u7edc\u751a\u81f3\u8dcc\u638980\uff05\u7684\u5e02\u503c\uff0c\u4e5f\u5f00\u59cb\u6709\u4e00\u4e9b\u7f51\u7ad9\u652f\u6491\u4e0d\u4e0b\u53bb\u5173\u95ed\u3002\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u52c9\u5f3a\u7ef4\u6301\u5728\u6bcf\u80a120\u7f8e\u5143\uff0c\u4f46\u662f\u975e\u5e38\u7684\u8106\u5f31\uff0c\u534e\u767b\u3001\u9ad8\u76db\u90fd\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u7684\u51cf\u6301\uff0c\u4f46\u662f\u53c8\u6015\u62d6\u5ef6\u4e0b\u53bb\uff0c\u5c40\u52bf\u4f1a\u8fdb\u4e00\u6b65\u6076\u5316\uff0c\u5c06\u5f7b\u5e95\u4e27\u5931\u51cf\u6301\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8981\u662f\u8fd9\u65f6\u5019\u6606\u817e\u5728\u7ebf\u80fd\u6709\u5b9e\u8d28\u6027\u7684\u5229\u597d\uff0c\u5c06\u7ed9\u534e\u767b\u3001\u9ad8\u76db\u7684\u51cf\u6301\u8ba1\u5212\u63d0\u4f9b\u7edd\u4f73\u7684\u63a9\u62a4\u3002</p><p>\u3000\u3000\u9526\u6e56\u8981\u8c03\u6574\u4e1c\u5927\u521b\u57df\u3001\u5353\u57df\u4ee5\u53ca\u6606\u817e\u5728\u7ebf\u65d7\u4e0b\u7684\u7f51\u7edc\u8d44\u6e90\uff0c\u5b9e\u9645\u662f\u53ef\u4ee5\u5411\u6606\u817e\u5728\u7ebf\u8f93\u9001\u8f83\u5927\u7684\u5229\u76ca\u786c\u751f\u751f\u7684\u9020\u4e00\u4e2a\u5b9e\u8d28\u5229\u597d\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u53ea\u8981\u5411\u6606\u817e\u5728\u7ebf\u8f93\u9001\u4e24\u4e09\u5343\u4e07\u7f8e\u5143\u7684\u5229\u76ca\uff0c\u5c31\u80fd\u591f\u8ba9\u534e\u767b\u3001\u9ad8\u76db\u8fd1\u5341\u4ebf\u7f8e\u5143\u7684\u80a1\u7968\u5b89\u5168\u8131\u8eab\u2014\u2014\u8fd9\u4fbf\u662f\u66fe\u5b50\u8f89\u51fa\u5728\u8fd9\u91cc\u7684\u610f\u56fe\uff0c\u5f53\u7136\u8c01\u90fd\u4e0d\u80fd\u4fe1\u4efb\u8c01\uff0c\u6709\u4e9b\u8bdd\u610f\u601d\u70b9\u5230\u5c31\u884c\uff0c\u4e0d\u80fd\u8bf4\u900f\u4ee5\u514d\u7559\u4e0b\u628a\u67c4\u3002</p><p>\u3000\u3000\u7f8e\u56fd\u8bc1\u76d1\u4f53\u7cfb\u662f\u76d1\u7ba1\u4e0d\u5230\u8fd9\u79cd\u5c42\u9762\u7684\u64cd\u4f5c\u7684\uff0c\u8fd9\u4e5f\u662f\u7f8e\u56fd\u6c11\u4f17\u90fd\u75db\u6068\u5784\u65ad\u7684\u539f\u56e0\uff0c\u8bb8\u591a\u5546\u4e1a\u5784\u65ad\u884c\u4e3a\u5bf9\u666e\u901a\u6c11\u4f17\u7684\u63a0\u593a\u5e38\u5e38\u662f\u6cd5\u5f8b\u4f53\u7cfb\u76d1\u7ba1\u4e0d\u5230\u7684\u7a7a\u767d\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5546\u754c\u4ee5\u5229\u76ca\u4e3a\u5148\uff0c\u6ca1\u6709\u591a\u5c11\u4eba\u60c5\u53ef\u8bb2\u3002\u5f53\u521dESS\u9009\u62e9\u575a\u51b3\u51cf\u6301\uff0c\u800c\u534e\u767b\u3001\u9ad8\u76db\u6682\u7f13\uff0c\u5b9e\u9645\u662f\u7ed9ESS\u505a\u4e86\u63a9\u62a4\uff0c\u8ba9ESS\u6361\u4e86\u5927\u4fbf\u5b9c\u5728\u9ad8\u4f4d\u6210\u529f\u5957\u73b0\uff0c\u4f46\u662f\u6ca1\u6709\u8fd8\u4eba\u60c5\u7684\u5fc5\u8981\u3002</p><p>\u3000\u3000\u66f4\u4f55\u51b5\u66fe\u5b50\u8f89\u7684\u6b64\u884c\u672a\u5fc5\u662f\u4ee3\u8868\u80cc\u540e\u7684\u9ad8\u76db\u516c\u53f8\uff0c\u66f4\u5927\u7684\u53ef\u80fd\u662f\u4ee3\u8868\u67d0\u4e2a\u6709\u66fe\u5b50\u8f89\u53c2\u4e0e\u7684\u9ad8\u76db\u5185\u90e8\u56e2\u4f53\u3002\u9ad8\u76db\u9519\u8fc7\u51cf\u6301\u6606\u817e\u5728\u7ebf\u80a1\u7968\u7684\u826f\u673a\uff0c\u5bfc\u81f4\u6570\u4ebf\u7f8e\u5143\u7684\u9ad8\u989d\u5e94\u5f97\u5229\u6da6\u90fd\u53d8\u6210\u955c\u4e2d\u6708\u3001\u6c34\u4e2d\u82b1\uff0c\u52bf\u5fc5\u8981\u6709\u9ad8\u5c42\u4eba\u7269\u4e3a\u6b64\u627f\u62c5\u8d23\u4efb\u3002\u5506\u4f7f\u66fe\u5b50\u8f89\u51fa\u9762\uff0c\u8bf4\u4e0d\u5b9a\u662f\u9ad8\u76db\u5185\u90e8\u4e00\u4e9b\u4eba\u7684\u81ea\u4fdd\u884c\u52a8\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u66fe\u5b50\u8f89\u5f00\u4ef7\u4e5f\u4e0d\u83f2\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u6b63\u79ef\u6781\u51c6\u5907\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u4e0a\u5e02\u52df\u96c6\u8d44\u91d1\u4ee5\u542f\u52a8\u89c4\u6a21\u66f4\u52a0\u5e9e\u5927\u7684\u5236\u6d46\u9879\u76ee\u3002\u6070\u9022\u7f51\u7edc\u6ce1\u6cab\u7834\u706d\uff0c\u6e2f\u80a1\u53d7\u5f71\u54cd\u4e5f\u6781\u5927\uff0c\u800c\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u4e4b\u540e\uff0c\u7ea2\u7b79\u80a1\u4e00\u76f4\u5904\u4e8e\u4f4e\u8ff7\u72b6\u51b5\u4e4b\u4e2d\uff0c\u5373\u4f7f\u65b0\u5149\u7eb8\u4e1a\u8fd9\u51e0\u5e74\u6765\u7684\u8d22\u52a1\u72b6\u51b5\u4e00\u5411\u5f88\u597d\uff0c\u53c8\u6709\u9526\u6e56\u5728\u80cc\u540e\u652f\u6491\uff0c\u4f46\u662f\u4f5c\u4e3a\u56fd\u5185\u7684\u6c11\u8425\u4f01\u4e1a\u5230\u9999\u6e2f\u516c\u5f00\u4e0a\u5e02\u52df\u8d44\uff0c\u800c\u4e14\u52df\u8d44\u989d\u5ea6\u63a5\u5341\u4ebf\u7f8e\u5143\uff0c\u96be\u5ea6\u4f9d\u65e7\u5927\u5230\u8ba9\u4eba\u96be\u4ee5\u60f3\u8c61\u3002</p><p>\u3000\u3000\u53bb\u5e74\u4e2d\u6d77\u6cb9\u5728\u9999\u6e2f\u4e0a\u5e02\uff0c\u52df\u8d44\u989d\u5ea6\u4e5f\u4e0d\u8fc7\u5341\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u7684\u5229\u6da6\u6c34\u5e73\u867d\u7136\u9ad8\uff0c\u8fd9\u51e0\u5e74\u5229\u6da6\u6c34\u5e73\u4e00\u76f4\u5728\u6301\u7eed\u5feb\u901f\u7684\u589e\u6da8\uff0c\u4f46\u662f\u51c0\u8d44\u4ea7\u89c4\u6a21\u6bd5\u7adf\u8fd8\u662f\u6709\u4e9b\u5c0f\u4e86\u3002</p><p>\u3000\u3000\u8bb8\u591a\u6295\u8d44\u673a\u6784\u867d\u7136\u5bf9\u53c2\u52a0\u65b0\u5149\u7eb8\u4e1a\u7684\u80a1\u7968\u53d1\u884c\u611f\u5174\u8da3\uff0c\u4f46\u8fd8\u662f\u89c9\u5f97\u9526\u6e56\u7684\u5f00\u4ef7\u592a\u9ad8\uff0c\u4ee5\u9ad8\u76db\u3001\u56fd\u88d5\u3001\u6c47\u4e30\u4e3a\u9996\u7684\u6295\u8d44\u673a\u6784\uff0c\u5e0c\u671b\u65b0\u5149\u7eb8\u4e1a\u7684\u53d1\u884c\u4ef7\u80fd\u4e0b\u8c03\u4e09\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u8c08\u5224\u50f5\u6301\u5728\u90a3\u91cc\uff0c\u6295\u8d44\u673a\u6784\u3001\u627f\u9500\u5546\u90fd\u54ac\u5f97\u5f88\u7d27\uff0c\u4e00\u76f4\u90fd\u6ca1\u6709\u4ec0\u4e48\u8fdb\u5c55\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u4f5c\u4e3a\u9ad8\u76db\u4e9a\u6d32\u516c\u53f8\u526f\u603b\u88c1\u7684\u804c\u52a1\u76f8\u5f53\u5173\u952e\uff0c\u5728\u8fd9\u8fc7\u7a0b\u4e2d\u7684\u786e\u80fd\u63d0\u4f9b\u5f88\u5927\u7684\u65b9\u4fbf\uff0c\u751a\u81f3\u53ef\u4ee5\u8ba9\u9ad8\u76db\u7684\u6001\u5ea6\u5148\u52a8\u6447\uff0c\u4ece\u800c\u8ba9\u9526\u6e56\u8fd9\u8fb9\u5904\u4e8e\u4e3b\u52a8\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u8fd9\u52fe\u5f53\u8bf4\u767d\u4e86\u5c31\u662f\u5229\u76ca\u4ea4\u6362\uff0c\u5751\u7f8e\u56fd\u80a1\u6c11\u4e00\u628a\uff0c\u5f20\u606a\u5b8c\u5168\u6ca1\u6709\u5fc3\u7406\u8d1f\u62c5\uff0c\u4e5f\u8bb8\u7f8e\u56fd\u80a1\u6c11\u4f1a\u89c9\u5f97\u9526\u6e56\u662f\u5728\u7ed9\u4ed6\u4eec\u8f93\u9001\u5229\u76ca\u6765\u3002</p><p>\u3000\u3000\u6109\u5feb\u7684\u559d\u5b8c\u5496\u5561\uff0c\u5f20\u606a\u7279\u5730\u8ba9\u5085\u4fca\u6d3e\u90a3\u8f86\u52a0\u957f\u6797\u80af\u9001\u66fe\u5b50\u8f89\u53bb\u673a\u573a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6b21\u65e5\u9ec4\u660f\uff0c\u5f20\u606a\u5728\u56fe\u4e66\u9986\u91cc\u770b\u8d44\u6599\uff0c\u63a5\u5230\u675c\u98de\u6012\u6c14\u51b2\u51b2\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u7f8e\u56fd\u4f6c\u60f3\u94b1\u60f3\u75af\u4e86\uff0c\u201d\u675c\u98de\u5728\u7535\u8bdd\u6012\u6c14\u51b2\u51b2\u7684\u8bf4\u9053\uff0c\u201c\u6606\u817e\u5728\u7ebf\u7684\u8463\u4e8b\u4f1a\u8bae\u4e0a\uff0c\u9a6c\u5411\u4e1c\u5dee\u70b9\u8ddf\u9ad8\u76db\u7684\u4ee3\u8868\u52a8\u624b\u6253\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u6253\u8d77\u6765\u5427\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8bf4\u5dee\u70b9\u5417\uff1f\u7f8e\u56fd\u4f6c\u8981\u6c42\u5bf9\u65b9\u6848\u8fdb\u884c\u4fee\u6539\uff0c\u4f60\u77e5\u9053\u7f8e\u56fd\u4f6c\u6709\u591a\u8d2a\u5f97\u65e0\u538c\u5417\uff1f\u7535\u8bdd\u91cc\u8bf4\u4e0d\u6e05\u695a\uff0c\u4f60\u4eba\u5728\u54ea\u91cc\uff1f\u6211\u628a\u4f20\u771f\u4ef6\u7ed9\u4f60\u770b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u7ea6\u597d\u52301978\u78b0\u9762\uff0c\u4ed6\u5c06\u8d44\u6599\u6536\u62fe\u597d\uff0c\u6162\u60a0\u60a0\u7684\u8d70\u52301978\uff0c\u675c\u98de\u3001\u76db\u590f\u3001\u8d75\u6893\u7433\u3001\u65f6\u5b66\u658c\u4ee5\u53ca\u8c22\u5b50\u5609\u7b49\u4eba\u90fd\u5df2\u7ecf\u5728\u91cc\u9762\u7b49\u4ed6\u4e86\u3002\u8d75\u6893\u7433\u4e5f\u662f\u6606\u817e\u5728\u7ebf\u8463\u4e8b\u4f1a\u6210\u5458\uff0c\u53ea\u662f\u8fd9\u6b21\u7684\u4f1a\u8bae\u90fd\u59d4\u6258\u9a6c\u5411\u4e1c\u5168\u6743\u4ee3\u8868\u4e0e\u9ad8\u76db\u3001\u534e\u767b\u7684\u4ee3\u8868\u8ba8\u8bba\u8c03\u6574\u65b9\u6848\uff0c\u8d75\u6893\u7433\u5c31\u6ca1\u6709\u4e13\u7a0b\u8d76\u5230\u5317\u4eac\u53bb\u53c2\u52a0\u8463\u4e8b\u4f1a\u8bae\u3002</p><p>\u3000\u3000\u201c\u4f60\u770b\u770b\u7f8e\u56fd\u4f6c\u8d2a\u5f97\u65e0\u538c\uff01\u201d\u675c\u98de\u5c06\u9ad8\u76db\u8463\u4e8b\u4ee3\u8868\u63d0\u4ea4\u7684\u65b9\u6848\u4f20\u771f\u9012\u7ed9\u5f20\u606a\u770b\uff0c\u201c\u6606\u817e\u5728\u7ebf\u5c06MICQ\u4e1a\u52a1\u7f6e\u5165\u4e1c\u5927\u521b\u57df\u4ee5\u6362\u53d68\uff05\u7684\u80a1\u4efd\uff0c\u5df2\u7ecf\u662f\u76f8\u5f53\u5bbd\u677e\u7684\u6761\u4ef6\uff0c\u7f8e\u56fd\u4f6c\u7adf\u7136\u63d0\u51fa\u5c06\u6301\u80a1\u6bd4\u4f8b\u964d\u52306\uff05\uff0c\u4f46\u662f\u989d\u5916\u8981\u6c42\u4e1c\u5927\u521b\u57df\u5411\u6606\u817e\u5728\u7ebf\u652f\u4ed82000\u4e07\u7f8e\u5143\u7684\u73b0\u91d1\uff0c\u4f60\u8bf4\u4ed6\u4eec\u662f\u4e0d\u662f\u60f3\u94b1\u60f3\u75af\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6628\u5929\u5c31\u5f62\u6210\u9ed8\u5951\u7684\u65b9\u6848\u62ff\u5728\u624b\u91cc\u770b\u4e86\u770b\uff0c\u89c1\u675c\u98de\u4ed6\u4eec\u4e49\u6124\u586b\u81ba\u7684\u6a21\u6837\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e5f\u4e0d\u9519\u554a\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u4ee5\u4e3a\u4e1c\u5927\u521b\u57df2\uff05\u7684\u80a1\u6743\u5c31\u503c2000\u4e07\u7f8e\u91d1\u5462\uff0c\u201d\u4ed6\u4ece\u886c\u886b\u53e3\u888b\u91cc\u638f\u51fa\u94a2\u7b14\uff0c\u968f\u624b\u5c31\u5728\u4f20\u771f\u4ef6\u4e0a\u7b7e\u4e86\u5b57\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u6837\u597d\u4e86\uff0c\u6211\u8ba9\u7231\u8fbe\u96c6\u56e2\u6216\u8005\u9526\u6e56\u5546\u4e8b\u4ee52000\u4e07\u7f8e\u5143\u589e\u6301\u4e1c\u5927\u521b\u57df2\uff05\u7684\u80a1\u6743\uff0c\u8fd9\u4e00\u70b9\uff0c\u6682\u65f6\u6ca1\u6709\u5fc5\u8981\u901a\u77e5\u6606\u817e\u5728\u7ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u4e00\u65f6\u8fd8\u626d\u4e0d\u8fc7\u5f2f\u6765\uff0c\u8c22\u5b50\u5609\u5374\u4e0d\u5c51\u7684\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u54fc\uff0c\u53c8\u662f\u6697\u76d8\u4ea4\u6613\uff01\u201d</p><p>\u3000\u3000\u201c\u8c01\u8bf4\u91cc\u9762\u6709\u6697\u76d8\u4ea4\u6613\uff0c\u6211\u6253\u6b7b\u90fd\u4e0d\u627f\u8ba4\u7684\u3002\u65e2\u7136\u5168\u7403\u97f3\u4e50\u5728\u7ebf30\uff05\u7684\u80a1\u6743\u80fd\u5356\u51fa\u4e5d\u4ebf\u7f8e\u5143\u7684\u9ad8\u4ef7\uff0c\u4e1c\u5927\u521b\u57df\u7684\u5168\u90e8\u4ef7\u503c\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u503c10\u4ebf\u7f8e\u5143\uff1f\u54b1\u4eec\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\u3002\u201d\u5f20\u606a\u6d8e\u8138\u800c\u7b11\uff0c\u53c8\u8ddf\u675c\u98de\u8bf4\u9053\uff0c\u201c\u8fd9\u4ef6\u4e8b\u5c31\u8fd9\u4e48\u5b9a\u4e86\uff0c\u4f60\u5c06\u4f20\u771f\u53d1\u7ed9\u9a6c\u5411\u4e1c\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8ddf\u7f8e\u56fd\u5ba2\u4eba\u634b\u8896\u5b50\u5e72\u67b6\uff0c\u8ba9\u4ed6\u7ed9\u7f8e\u56fd\u5ba2\u4eba\u5728\u5317\u4eac\u5ea6\u4e00\u4e2a\u6109\u5feb\u7684\u5047\u671f\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5fae\u6485\u7740\u5634\uff0c\u77e5\u9053\u56e0\u4e3a\u81ea\u5df1\u5728\u573a\uff0c\u5f20\u606a\u624d\u4e0d\u628a\u8bdd\u8bf4\u900f\uff0c\u5fae\u6602\u7740\u5934\u770b\u77401978\u5929\u82b1\u677f\u4e0a\u6f02\u4eae\u7684\u65cb\u8f6c\u706f\u5e26\uff0c\u8f7b\u58f0\u7684\u5439\u7740\u53e3\u54e8\uff0c\u5979\u95ed\u4e0a\u4e24\u53ea\u773c\u775b\u90fd\u80fd\u731c\u5230\u80cc\u540e\u6709\u6697\u76d8\u4ea4\u6613\uff0c\u4e0d\u7136\u5f20\u606a\u54ea\u91cc\u53ef\u80fd\u4f1a\u5403\u8fd9\u4e2a\u4e8f\uff1f</p><p>\u3000\u3000\u4e92\u8054\u7f51\u6ce1\u6cab\u6ca1\u6709\u7834\u706d\u4e4b\u524d\uff0c\u4e1c\u5927\u521b\u57df2\uff05\u7684\u80a1\u6743\u4e5f\u8bb8\u80fd\u9a97\u56de\u4e24\u5343\u4e07\u7f8e\u5143\u6765\uff0c\u5e02\u503c\u552c\u5f04\u4eba\u5439\u4e2a\u5341\u4ebf\u3001\u516b\u4ebf\u7f8e\u5143\u4e5f\u4e0d\u7b97\u79bb\u8c31\uff0c\u4f46\u662f\u73b0\u5728\u7eb3\u65af\u8fbe\u514b\u7f51\u7edc\u80a1\u6700\u9ad8\u8dcc\u5e45\u8d85\u8fc780\uff05\uff0c\u8d85\u8fc7\u516b\u6210\u7684\u7f51\u7edc\u80a1\u80a1\u4ef7\u906d\u5230\u8170\u65a9\uff0c\u4e1c\u5927\u521b\u57df\u5c31\u65d7\u4e0b\u7684\u90a3\u4e9b\u8d44\u4ea7\u518d\u5c06\u6606\u817e\u5728\u7ebf\u7684MICQ\u4e1a\u52a1\u63a5\u7ba1\u8fc7\u6765\uff0c\u80fd\u5356\u51fa\u4e8c\u5341\u4ebf\u4eba\u6c11\u5e01\u5c31\u7b97\u662f\u5e78\u8fd0\u7684\u3002</p><p>\u3000\u3000\u4e0d\u80fd\u8fc7\u6cb3\u62c6\u6865\uff0c\u8c22\u5b50\u5609\u7684\u5efa\u8bae\u5bf9\u8fd9\u6b21\u4e92\u8054\u7f51\u4e1a\u52a1\u91cd\u7ec4\u65b9\u6848\u7684\u6700\u7ec8\u5f62\u6210\u6709\u7740\u5173\u952e\u6027\u7684\u542f\u53d1\u4f5c\u7528\uff0c\u8fd9\u6b21\u4e1c\u5927\u521b\u57df\u4e5f\u63a5\u53d7\u79d1\u738b\u9ad8\u79d1\u7684\u6ce8\u8d44\u3002\u79d1\u738b\u9ad8\u79d1\u5411\u4e1c\u5927\u521b\u57df\u6ce8\u8d44\u516b\u5343\u4e07\uff0c\u6362\u53d610\uff05\u7684\u80a1\u6743\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u63a5\u53d7\u79d1\u738b\u9ad8\u79d1\u6ce8\u8d44\u65f6\uff0c\u4e1c\u5927\u521b\u57df\u7684\u5e02\u503c\u53ea\u6298\u7b97\u4e0d\u5230\u516b\u4ebf\u4eba\u6c11\u5e01\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u4e5f\u53ea\u80fd\u5439\u5439\u53e3\u54e8\uff0c\u8868\u8fbe\u5185\u5fc3\u7684\u4e0d\u5c51\u8ddf\u4e0d\u6ee1\uff0c\u96be\u4e0d\u6210\u8fd8\u63ea\u7740\u5f20\u606a\u7684\u8033\u6735\u8ba9\u4ed6\u5410\u9732\u80cc\u540e\u5230\u5e95\u85cf\u7740\u600e\u6837\u7684\u6697\u76d8\u4ea4\u6613\uff1f\u8fd9\u6b21\u5bf9\u4e1c\u5927\u521b\u57df\u6765\u8bf4\uff0c\u53ea\u662f\u8fc7\u6865\u4ea4\u6613\u800c\u5df2\uff0c\u4e0d\u4f1a\u6709\u5b9e\u8d28\u7684\u597d\u5904\uff0c\u4e5f\u4e0d\u4f1a\u6709\u5b9e\u8d28\u574f\u5904\uff0c\u5373\u4f7f\u79d1\u738b\u9ad8\u79d1\u6210\u4e3a\u4e1c\u5927\u521b\u57df\u7684\u80a1\u6743\uff0c\u5979\u8c22\u5b50\u5609\u53c8\u4ee3\u8868\u79d1\u738b\u9ad8\u79d1\u5217\u5e2d\u4e1c\u5927\u521b\u57df\u7684\u7ba1\u7406\u56e2\u961f\uff0c\u6709\u4e9b\u4e8b\u60c5\u4e5f\u6ca1\u6709\u5fc5\u8981\u77e5\u4f1a\u5979\u3002</p><p>\u3000\u3000\u675c\u98de\u6487\u4e86\u6487\u5634\uff0c\u4e5f\u4e0d\u591a\u95ee\uff0c\u8ddf\u65f6\u5b66\u658c\u8bf4\u9053\uff1a\u201c\u4f60\u9a6c\u4e0a\u4ee3\u8868\u8fd9\u8fb9\u7ed9\u6606\u817e\u5728\u7ebf\u53d1\u4e00\u5c01\u4f20\u771f\u8fc7\u53bb\uff0c\u514d\u5f97\u9a6c\u5411\u4e1c\u8ddf\u7f8e\u56fd\u4f6c\u53c8\u5e72\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u8463\u4e8b\u4f1a\u4e0a\u9a6c\u5411\u4e1c\u8ddf\u9ad8\u76db\u4ee3\u8868\u4e89\u5435\uff0c\u4e5f\u662f\u8ba4\u5b9a\u9ad8\u76db\u4ee3\u8868\u63d0\u51fa\u7684\u65b9\u6848\u80af\u5b9a\u4e0d\u4f1a\u5f97\u5230\u8fd9\u8fb9\u7684\u8ba4\u53ef\uff0c\u8ba4\u5b9a\u9ad8\u76db\u4ee3\u8868\u5728\u6405\u5c40\uff0c\u6240\u4ee5\u624d\u5728\u8463\u4e8b\u4f1a\u8bae\u4e0a\u607c\u7f9e\u6210\u6012\u3001\u6076\u8bed\u76f8\u5411\u3002\u73b0\u5728\u8fd9\u8fb9\u8ba4\u540c\u9ad8\u76db\u4ee3\u8868\u63d0\u51fa\u7684\u65b9\u6848\uff0c\u6606\u817e\u5728\u7ebf\u8fd8\u662f\u80fd\u83b7\u5f97\u5b9e\u8d28\u7684\u5229\u76ca\uff0c\u9a6c\u5411\u4e1c\u9ad8\u5174\u8fd8\u6765\u4e0d\u5f97\uff0c\u81ea\u7136\u5c31\u80fd\u8ddf\u9ad8\u76db\u4ee3\u8868\u51b0\u91ca\u524d\u5acc\u4e86\u3002\u91ca\u4e0d\u91ca\u524d\u5acc\u4e5f\u4e0d\u91cd\u8981\uff0c\u5f85\u9ad8\u76db\u3001\u534e\u767b\u5c06\u6240\u6301\u80a1\u7968\u51cf\u6301\u5e72\u51c0\u4e4b\u540e\uff0c\u8463\u4e8b\u4f1a\u5fc5\u7136\u8981\u8fdb\u884c\u8c03\u6574\uff0c\u518d\u4e0d\u4f1a\u6709\u9ad8\u76db\u3001\u534e\u767b\u4ee3\u8868\u7684\u4f4d\u7f6e\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8868\uff0c\u8ddf\u675c\u98de\u3001\u76db\u590f\u8bf4\u9053\uff1a\u201c\u6211\u8bf7\u4f60\u4eec\u4fe9\u5403\u665a\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u6893\u7433\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u90fd\u544a\u8f9e\u79bb\u53bb\uff0c\u82e5\u662f\u6d89\u53ca\u5230\u4e0e\u9ad8\u76db\u7684\u6697\u76d8\u4ea4\u6613\uff0c\u8981\u4e48\u662f\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u7684\u6210\u5458\uff0c\u8981\u4e48\u662f\u675c\u98de\u8fd9\u4e9b\u8ddf\u5f20\u606a\u6709\u7740\u7279\u6b8a\u5173\u7cfb\u7684\u4eba\u624d\u6709\u8d44\u683c\u77e5\u6089\u8be6\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5915\u9633\u4f3c\u4e4e\u6d6e\u5728\u5c4b\u810a\u4e4b\u4e0a\uff0c\u68a7\u6850\u6811\u51a0\u4ee5\u4e0b\u7684\u5b66\u5e9c\u5df7\u7ed9\u7b3c\u7f69\u5728\u6781\u6de1\u7684\u9634\u5f71\u4e4b\u4e2d\uff0c\u8fd9\u65f6\u95f4\uff0c\u5b66\u5e9c\u5df7\u4e0a\u6700\u662f\u70ed\u95f9\uff0c\u4f3c\u4e4e\u9644\u8fd1\u9ad8\u6821\u7684\u5b66\u751f\u90fd\u6d8c\u4e86\u8fdb\u6765\u3002\u867d\u7136\u5b66\u5e9c\u5df7\u4e0a\u90fd\u662f\u9ad8\u7ea7\u9910\u5385\uff0c\u4f46\u662f\u9ad8\u6821\u5b66\u751f\u4e2d\u5bb6\u5ead\u5bcc\u88d5\u7684\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u66f4\u4f55\u51b5\u71d5\u5f52\u6e56\u9644\u8fd1\u767d\u9886\u90fd\u559c\u6b22\u5230\u8fd9\u91cc\u6765\u7528\u9910\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u3001\u76db\u590f\u8fde\u627e\u4e86\u51e0\u5bb6\u9910\u5385\uff0c\u90fd\u6ca1\u6709\u627e\u5230\u7a7a\u4f4d\uff0c\u4fbf\u6cbf\u7740\u5b66\u5e9c\u5df7\u5f80\u97f3\u4e50\u5b66\u9662\u65b9\u5411\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u66fe\u5b50\u8f89\u6628\u5929\u5728\u5efa\u90ba\u77ed\u6682\u505c\u7559\u7684\u4e8b\u60c5\u544a\u8bc9\u675c\u98de\uff0c\u8bf4\u9053\uff1a\u201c\u7ed9\u6606\u817e\u5728\u7ebf\u591a\u63d0\u4f9b\u4e00\u4e9b\u8d44\u91d1\uff0c\u4e5f\u591a\u51cf\u8f7b\u6389\u4e00\u4e9b\u76c8\u5229\u7684\u538b\u529b\uff1b\u53e6\u5916\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u4e0a\u5e02\uff0c\u4e5f\u8981\u5316\u89e3\u6389\u4e00\u4e9b\u538b\u529b\uff0c\u5236\u6d46\u9879\u76ee\u7b79\u5907\u4e5f\u6709\u56db\u4e94\u5e74\u4e86\uff0c\u603b\u4e0d\u80fd\u4e00\u76f4\u5c06\u997c\u753b\u5728\u90a3\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u8fb9\u5c06\u6843\u5b50\u6295\u51fa\u53bb\uff0c\u90a3\u8fb9\u4e5f\u5e94\u8be5\u8981\u7d27\u63a5\u7740\u62a5\u674e\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u4eec\u7684\u538b\u529b\u4e5f\u4e0d\u5c0f\u554a\uff0c\u201d\u675c\u98de\u7b11\u7740\u8bf4\uff0c\u201c\u81f3\u5c11\u5728\u8d22\u52a1\u4e0a\u4e1c\u5927\u521b\u57df\u662f\u4ee5\u4e24\u5343\u4e07\u7f8e\u5143\u7684\u73b0\u91d1\u52a06\uff05\u7684\u80a1\u6743\u4e3a\u4ee3\u4ef7\u5e76\u8d2d\u6606\u817e\u7684MICQ\u4e1a\u52a1\uff0c\u8981\u662f\u8fd0\u8425\u5f97\u592a\u5dee\uff0c\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u90a3\u91cc\u597d\u7cca\u5f04\uff0c\u9526\u6e56\u4e2d\u5c42\u7ba1\u7406\u4eba\u5458\u53ef\u662f\u4e0d\u660e\u771f\u76f8\u7684\u7fa4\u4f17\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u600e\u4e48\u80fd\u8ba9\u4f60\u4eec\u4e00\u70b9\u538b\u529b\u90fd\u6ca1\u6709\u5462\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u73b0\u5728\u624d\u662f\u4e24\u5343\u5e74\u7684\u521d\u590f\uff0c\u5168\u56fd\u4e92\u8054\u7f51\u7528\u6237\u6570\u91cf\u624d\u521a\u521a\u7a81\u7834\u4e24\u5343\u4e07\uff0c\u4e2d\u6587\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u5e02\u573a\u8fd8\u8fdc\u8fdc\u6ca1\u6709\u5206\u51fa\u80dc\u8d1f\u7684\u65f6\u5019\u2026\u2026\u4e0d\u8981\u8bf4MICQ\u8fd9\u4e2a\u53d8\u6570\u4e86\uff0c\u5fae\u8f6f\u4e5f\u7531\u4e8e\u53d7\u9526\u6e56\u7684\u5f71\u54cd\uff0c\u5bf9MSN\u53ca\u7535\u5b50\u90ae\u7bb1\u4e1a\u52a1\u5728\u4e2d\u56fd\u7684\u53d1\u5c55\u4e5f\u8f83\u66fe\u7ecf\u53d1\u751f\u7684\u5386\u53f2\u66f4\u52a0\u91cd\u89c6\u3002\u5fae\u8f6f\u5728\u4e2d\u56fd\u7684\u7814\u53d1\u4e2d\u5fc3\u4e3a\u4e92\u8054\u7f51\u6280\u672f\u4ea7\u54c1\u548c\u670d\u52a1\u65b0\u6210\u7acb\u7684\u4e00\u4e2a\u72ec\u7acb\u7684\u7814\u53d1\u90e8\u95e8\uff0c\u6bd4\u8d77\u5f20\u606a\u8bb0\u5fc6\u4e2d\u7684\u5386\u53f2\u63d0\u524d\u4e86\u597d\u51e0\u5e74\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u518d\u9891\u7e41\u51fa\u73b0MSN\u3001hotmail\u767b\u5f55\u4e0d\u4e0a\u3001\u670d\u52a1\u5668\u6389\u94fe\u5b50\u7684\u72b6\u51b5\uff0c\u4e5f\u8bb8MSN\u7684\u4e1a\u7ee9\u4e0d\u4f1a\u4e00\u5e74\u6bd4\u4e00\u5e74\u7cdf\u7cd5\uff0c\u5728\u5373\u65f6\u7f51\u7edc\u901a\u8baf\u5e02\u573a\uff0c\u4e1c\u5927\u521b\u57df\u81ea\u7136\u4e5f\u80fd\u4ece\u817e\u8baf\u5634\u91cc\u5206\u4e00\u676f\u7fb9\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u3001\u76db\u590f\u8d70\u5230\u5df7\u5b50\u53e3\uff0c\u51c6\u5907\u5f80\u4e1c\u62d0\u627e\u9910\u5385\uff0c\u770b\u7740\u674e\u99a8\u4e88\u62b1\u7740\u4e24\u672c\u4e66\u5728\u6000\u91cc\u8ddf\u4e24\u4e2a\u964c\u751f\u7684\u9752\u5e74\u8bf4\u8bdd\u3002\u5f20\u606a\u7ecf\u8fc7\u53bb\uff0c\u674e\u99a8\u4e88\u671d\u4ed6\u5ae3\u7136\u800c\u7b11\uff0c\u7b11\u5bb9\u6e05\u7eaf\u800c\u8ff7\u4eba\uff0c\u5979\u7a7f\u7740\u7ec6\u7eb9\u683c\u5b50\u7684\u534a\u8896\u886c\u886b\u3001\u76f4\u7b52\u7ec6\u7ba1\u7684\u725b\u4ed4\u88e4\uff0c\u4e60\u60ef\u6027\u7684\u62b1\u7740\u4e66\u6321\u5728\u80f8\u524d\uff0c\u8eab\u6750\u9ad8\u6311\u7ea4\u7ec6\uff0c\u9971\u6ee1\u7684\u80f8\u90e8\u5374\u7ed9\u53cc\u81c2\u6321\u4f4f\uff0c\u53ea\u6709\u4ece\u4fa7\u9762\u624d\u770b\u5230\u90a3\u51e0\u4e4e\u8981\u89e6\u53ca\u4eba\u5fc3\u7684\u52a8\u4eba\u66f2\u7ebf\uff0c\u662f\u90a3\u6837\u7684\u9971\u6ee1\uff0c\u9f13\u6da8\u7740\uff0c\u5145\u6ee1\u7740\u65e0\u7a77\u8bf1\u60d1\u7684\u5f39\u6027\u3002</p><p>\u3000\u3000\u542c\u7740\u674e\u99a8\u4e88\u8ddf\u4e24\u4e2a\u964c\u751f\u9752\u5e74\u7684\u5bf9\u8bdd\uff0c\u539f\u5148\u5979\u521a\u4ece\u56fe\u4e66\u9986\u51fa\u6765\uff0c\u7ed9\u8fd9\u4e24\u4e2a\u4eba\u62e6\u4f4f\u95ee\u8def\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u5bf9\u4e1c\u5927\u4e5f\u4e0d\u719f\u6089\uff0c\u8981\u4e48\u6211\u8bf7\u6258\u6211\u7684\u670b\u53cb\u9001\u4f60\u4eec\u8fc7\u53bb\u2026\u2026\u201d\u674e\u99a8\u4e88\u5f88\u6127\u759a\u7684\u8ddf\u4e24\u4e2a\u964c\u751f\u9752\u5e74\u9053\u6b49\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u8fd8\u662f\u8981\u8c22\u8c22\u4f60\u2026\u2026\u201d\u4e24\u4e2a\u95ee\u8def\u7684\u9752\u5e74\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u5c31\u6ca1\u8da3\u7684\u544a\u8f9e\u79bb\u5f00\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8981\u53bb\u4e1c\u5927\u54ea\u91cc\uff0c\u6211\u9001\u4f60\u4eec\u8fc7\u53bb\uff0c\u4e1c\u5927\u6211\u5f88\u591a\u5c0f\u5f1f\u7684\u2026\u2026\u201d\u770b\u7740\u4e24\u4e2a\u9752\u5e74\u79bb\u53bb\u7684\u80cc\u5f71\uff0c\u5f20\u606a\u7b11\u7740\u5466\u559d\uff0c\u53c8\u56de\u5934\u95ee\u674e\u99a8\u4e88\uff0c\u201c\u4f60\u6bcf\u5929\u4ece\u5b66\u5e9c\u5df7\u8d70\u8fc7\u53bb\u80fd\u9047\u5230\u591a\u5c11\u95ee\u8def\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u771f\u662f\u4e0d\u8ba4\u8bc6\u8def\uff01\u201d\u674e\u99a8\u4e88\u9762\u5e26\u5fae\u7f9e\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u54e6\uff0c\u6211\u4eec\u5c31\u627e\u4e0d\u5230\u4e00\u95f4\u6709\u7a7a\u4f4d\u7684\u9910\u5385\uff0c\u4e0d\u77e5\u9053\u4f60\u80fd\u4e0d\u80fd\u5e26\u6211\u4eec\u8fc7\u53bb\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u4f60\u4eec\u4e0d\u4ecb\u610f\u5403\u97e9\u56fd\u83dc\u7684\u8bdd\uff0c\u6211\u5012\u77e5\u9053\u4f60\u4eec\u5e94\u8be5\u53bb\u54ea\u91cc\u3002\u201d\u674e\u99a8\u4e88\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u97e9\u56fd\u9910\u5385\u8fd9\u65f6\u5019\u80fd\u6709\u7a7a\u4f4d\uff1f\u201d\u675c\u98de\u95ee\u9053\u3002</p><p>\u3000\u3000\u4e24\u5343\u5e74\uff0c\u97e9\u6d41\u6b63\u522e\u5f97\u5389\u5bb3\uff0c\u52a0\u4e0a\u5efa\u90ba\u7684\u97e9\u56fd\u7559\u5b66\u751f\u4e5f\u591a\uff0c\u5b66\u5e9c\u5df7\u51e0\u5bb6\u97e9\u56fd\u9910\u5385\u8fd9\u65f6\u95f4\u6bd4\u4e2d\u9910\u5385\u3001\u897f\u9910\u5385\u90fd\u8981\u62e5\u6324\u3002</p><p>\u3000\u3000\u201c\u5728\u5357\u7fe0\u56ed\u90a3\u8fb9\u65b0\u5f00\u4e86\u4e00\u5bb6\u9910\u5385\uff0c\u6211\u5728\u90a3\u91cc\u5403\u8fc7\uff0c\u611f\u89c9\u76f8\u5f53\u7684\u68d2\uff0c\u4e0d\u4ecb\u610f\u7684\u8bdd\uff0c\u6211\u8bf7\u4f60\u4eec\u8fc7\u53bb\u7528\u9910\u3002\u201d\u674e\u99a8\u4e88\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5357\u7fe0\u56ed\u662f\u7cbe\u5178\u5730\u4ea7\u501f\u6d77\u7c9f\u79d1\u6280\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u540d\u4e49\u5708\u5730\u5efa\u7684\u9ad8\u6863\u522b\u5885\u5c0f\u533a\uff0c\u5982\u6b64\u5df2\u7ecf\u6210\u4e3a\u71d5\u5f52\u6e56\u5357\u533a\u57df\u7684\u4ee3\u540d\u8bcd\u3002\u56e0\u4e3a\u5357\u7fe0\u56ed\u4f4f\u8fdb\u53bb\u4e00\u6279\u5bcc\u88d5\u4eba\u5bb6\uff0c\u9644\u8fd1\u7684\u9ad8\u7ea7\u9910\u5385\u4e5f\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u61d2\u5f97\u56de\u9752\u5e74\u516c\u5bd3\u53d6\u8f66\uff0c\u4e5f\u61d2\u5f97\u8ba9\u5de5\u4f5c\u4eba\u5458\u5f00\u8f66\u9001\u4ed6\u4eec\u8fc7\u53bb\uff0c\u5728\u8857\u4e0a\u62e6\u4e86\u8f86\u51fa\u79df\u8f66\u5c31\u5f80\u5357\u7fe0\u56ed\u800c\u53bb\u3002\u90a3\u662f\u4e00\u5bb6\u540d\u4e3a\u6c49\u5357\u6d1e\u9910\u5385\u7684\u97e9\u5f0f\u9910\u5385\uff0c\u5730\u9053\u7684\u97e9\u56fd\u88c5\u9970\uff0c\u5915\u9633\u90fd\u6ca1\u5230\u71d5\u5f52\u5c71\u80cc\u540e\uff0c\u9910\u5385\u91cc\u5ba2\u4eba\u4e0d\u591a\uff0c\u8d70\u8fdb\u53bb\u770b\u5230\u80e1\u91d1\u661f\u634b\u7740\u8896\u5b50\u7ad9\u5728\u4e34\u7a97\u7684\u5ea7\u4f4d\u4e0a\uff0c\u738b\u6d77\u7c9f\u4e0e\u9648\u52c7\u5750\u7740\u4e0d\u6653\u5f97\u5728\u8c08\u4ec0\u4e48\uff0c\u6b63\u56e0\u4e3a\u4ed6\u4eec\u804a\u5f97\u6295\u5165\uff0c\u4e4d\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\u8d70\u8fdb\u6765\uff0c\u5403\u60ca\u4e24\u4e2a\u5b57\u5c31\u5dee\u70b9\u523b\u8138\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u8fc7\u53bb\u51e0\u4e2a\u4ea4\u6613\u65e5\u91cc\uff0c\u56fd\u5185\u6caa\u6df1\u4e24\u5e02\u7684\u7f51\u7edc\u6982\u5ff5\u80a1\u90fd\u5728\u6301\u7eed\u7684\u505a\u63a2\u5e95\u52a8\u4f5c\uff0c\u6d77\u7c9f\u79d1\u6280\u8fd8\u662f\u7262\u7262\u5b88\u4f4f\u6bcf\u80a130\u5143\u7684\u5e95\u9650\u3002\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u6570\u636e\u5206\u6790\u90e8\u95e8\u76d1\u6d4b\u5230\u6d77\u7c9f\u79d1\u6280\u80a1\u7968\u7684\u96c6\u4e2d\u7a0b\u5ea6\u5728\u4e0d\u65ad\u7684\u63d0\u9ad8\uff0c\u800c\u4e14\u63d0\u9ad8\u7684\u901f\u5ea6\u975e\u5e38\u5feb\u3002\u8fd9\u8bf4\u660e\u5b8f\u4fe1\u5728\u80cc\u540e\u6b7b\u6491\u7740\u4e0d\u8ba9\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u8dcc\u7834\u666e\u901a\u80a1\u6c11\u7684\u5fc3\u7406\u5e95\u4ef7\u3002\u5373\u4f7f\u5f88\u96be\u8ffd\u5bdf\u5230\u5177\u4f53\u54ea\u4e9b\u662f\u5b8f\u4fe1\u7528\u4e8e\u5e55\u540e\u8fdb\u884c\u64cd\u4f5c\u7684\u62d6\u62c9\u673a\u8d26\u6237\uff0c\u4f46\u662f\u6839\u636e\u8bc1\u5238\u8425\u8fd0\u90e8\u7684\u96c6\u4e2d\u6d3b\u8dc3\u4ea4\u6613\u91cf\u4e5f\u80fd\u5927\u6982\u5224\u65ad\u51fa\u5b8f\u4fe1\u5728\u8fd9\u6bb5\u65f6\u95f4\u4e3a\u6258\u80a1\u4ef7\u88ab\u8feb\u5438\u7b79\u516d\u5230\u516b\u4ebf\u5143\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u9648\u52c7\u3001\u80e1\u91d1\u661f\u8138\u8272\u5f88\u5dee\uff0c\u4e5f\u4e0d\u96be\u7406\u89e3\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u5c31\u57284\u670826\u65e5\u4e92\u8054\u7f51\u6ce1\u6cab\u524d\u671f\uff0c\u4ed6\u4eec\u6b63\u8dbe\u9ad8\u6c14\u626c\u7684\u4e0e\u4e91\u6797\u8bc1\u5238\u7684\u5ba2\u6237\u7ecf\u7406\u4e00\u8d77\u5728\u5b66\u5e9c\u5df7\u5403\u996d\uff0c\u4ed6\u4eec\u5e94\u8be5\u662f\u60f3\u5728\u6d77\u7c9f\u79d1\u6280\u589e\u53d1\u524d\u51b2\u8fdb\u53bb\u635e\u6700\u540e\u4e00\u7b14\u5e76\u4e14\u6709\u5b9e\u9645\u52a8\u4f5c\u4e86\uff0c\u5927\u6982\u4e0d\u4f1a\u60f3\u5230\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u4e5f\u4f1a\u968f\u5927\u5c40\u503e\u584c\uff0c\u73b0\u5728\u4e5f\u53ea\u662f\u6709\u5b8f\u4fe1\u5728\u80cc\u540e\u6491\u7740\uff0c\u624d\u80fd\u6ca1\u6709\u7a81\u7834\u6bcf\u80a1\u4e09\u5341\u5143\u7684\u5fc3\u7406\u4ef7\u4f4d\u3002</p></article>"}}}]);
