(window.webpackJsonp=window.webpackJsonp||[]).push([[1663],{1686:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e94\u8282 \u516c\u5e73",content:"<article><h2>\u7b2c\u56db\u5341\u4e94\u8282 \u516c\u5e73</h2><p>\u518d\u5ea6\u9192\u6765\uff0c\u4e1c\u65b9\u5df2\u7ecf\u53d1\u767d\uff0c\u51ac\u65e5\u5929\u4eae\u5f97\u665a\uff0c\u5927\u6982\u662f\u65e9\u6668\u4e03\u70b9\u949f\u5de6\u53f3\u3002\u743c\u6069\u60f3\u5750\u8d77\u6765\u770b\u770b\u5e8a\u8fb9\u7684\u8ba1\u65f6\u6c99\u6f0f\uff0c\u5374\u53d1\u73b0\u6839\u672c\u65e0\u6cd5\u8d77\u8eab\uff0c\u4e24\u4f4d\u8d64\u88f8\u7684\u7f8e\u4eba\u513f\u4e00\u5de6\u4e00\u53f3\u5730\u8737\u5728\u4ed6\u6000\u91cc\uff0c\u8138\u8d34\u7740\u80f8\u819b\uff0c\u5747\u5300\u7684\u547c\u5438\u548c\u5b89\u9759\u7684\u9762\u5bb9\uff0c\u8868\u660e\u5979\u4eec\u90fd\u7761\u5f97\u6b63\u9999\u3002\u5fc5\u987b\u8bf4\uff0c\u8fd9\u662f\u975e\u5e38\u7f8e\u4e3d\u7684\u753b\u9762\uff0c\u5de6\u62e5\u53f3\u62b1\u66f4\u662f\u5929\u5e95\u4e0b\u6240\u6709\u7537\u4eba\u7684\u7f8e\u68a6\uff0c\u7136\u800c\u524d\u63d0\u662f\u5979\u4eec\u6ca1\u6709\u6574\u665a\u90fd\u62ff\u4f60\u7684\u80f3\u818a\u5f53\u6795\u5934\u3002</p><p>\u73ca\u5609\u7761\u5728\u743c\u6069\u7684\u5de6\u4fa7\uff0c\u6795\u7740\u4ed6\u7684\u5de6\u81c2\uff1b\u838e\u73de\u514b\u7761\u5728\u743c\u6069\u7684\u53f3\u4fa7\uff0c\u6795\u7740\u4ed6\u7684\u53f3\u81c2\u3002\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u9996\u5148\u662f\u743c\u6069\u65e0\u6cd5\u5728\u4e0d\u60ca\u52a8\u5979\u4eec\u7684\u60c5\u51b5\u4e0b\u8d77\u8eab\uff0c\u53ea\u80fd\u8eba\u7740\u4e00\u52a8\u4e0d\u52a8\uff1b\u5176\u6b21\u662f\u2014\u2014\u80f3\u818a\u771f\u7684\u597d\u9178\u597d\u9ebb\u597d\u6da8\uff0c\u90fd\u5df2\u7ecf\u5feb\u8981\u5931\u53bb\u77e5\u89c9\u4e86\u2026\u2026</p><p>\u743c\u6069\u8111\u6d77\u4e2d\u505a\u7740\u6fc0\u70c8\u7684\u601d\u60f3\u6597\u4e89\uff0c\u8003\u8651\u662f\u628a\u4e24\u4f4d\u7f8e\u4eba\u53eb\u9192\uff0c\u8fd8\u662f\u7ee7\u7eed\u5145\u5f53\u6795\u5934\u76f4\u5230\u5979\u4eec\u81ea\u5df1\u9192\u6765\u3002\u524d\u8005\u4f3c\u4e4e\u7f3a\u4e4f\u98ce\u5ea6\uff0c\u6253\u6270\u5973\u5b69\u5b50\u7761\u7f8e\u5bb9\u89c9\u66f4\u662f\u5927\u7f6a\uff1b\u4f46\u540e\u8005\u96be\u5ea6\u53c8\u5b9e\u5728\u592a\u9ad8\uff0c\u66f4\u4f55\u51b5\u4eca\u5929\u4e00\u65e9\u5c31\u8981\u51fa\u6d77\uff0c\u800c\u81ea\u5df1\u8fd8\u6ca1\u51c6\u5907\u9b54\u6cd5\u5462\u3002</p><p>\u72b9\u8c6b\u534a\u664c\uff0c\u5b9e\u5728\u662f\u80f3\u818a\u9178\u6da8\u5f97\u5389\u5bb3\uff0c\u6b63\u51c6\u5907\u4e00\u6a2a\u5fc3\u628a\u5979\u4eec\u53eb\u9192\uff0c\u6572\u95e8\u58f0\u7b03\u7b03\u54cd\u8d77\uff0c\u201c\u743c\u6069\uff0c\u201d\u8299\u857e\u72c4\u8f7b\u67d4\u7684\u55d3\u97f3\u4f20\u4e86\u8fdb\u6765\uff0c\u201c\u9192\u4e86\u5417\uff1f\u201d</p><p>\u743c\u6069\u8fd8\u6ca1\u56de\u7b54\uff0c\u53ea\u89c9\u53f3\u4fa7\u6000\u4e2d\u4e00\u52a8\uff0c\u838e\u73de\u514b\u5df2\u7136\u8b66\u9192\u3002\u6bd5\u7adf\u662f\u6740\u624b\uff0c\u867d\u7136\u6628\u591c\u73a9\u5f97\u6709\u4e9b\u8fc7\u706b\uff0c\u96be\u5f97\u5f7b\u5e95\u653e\u677e\uff0c\u4f46\u8b66\u60d5\u6027\u4f9d\u7136\u5728\u3002\u5979\u6175\u61d2\u5730\u652f\u8d77\u4e0a\u672c\u8eab\uff0c\u770b\u770b\u743c\u6069\u53c8\u770b\u770b\u73ca\u5609\uff0c\u968f\u5373\u660e\u767d\u60c5\u51b5\uff0c\u8d76\u5feb\u722c\u8fc7\u53bb\u5e2e\u5fd9\u62b1\u7740\u73ca\u5609\uff0c\u8ba9\u5979\u7ffb\u4e86\u4e2a\u8eab\uff0c\u7761\u5230\u771f\u6b63\u7684\u6795\u5934\u4e0a\u3002\u743c\u6069\u5982\u8499\u5927\u8d66\uff0c\u4e58\u673a\u62bd\u51fa\u80f3\u818a\uff0c\u56e0\u4e3a\u88ab\u538b\u5f97\u592a\u4e45\uff0c\u8840\u6db2\u9aa4\u7136\u56de\u6d41\uff0c\u4e00\u65f6\u95f4\u53cc\u81c2\u9178\u75bc\u65e0\u6bd4\uff0c\u4eff\u4f5b\u88ab\u5343\u4e07\u679a\u7ec6\u9488\u624e\u7740\u5173\u8282\u97e7\u5e26\u4f3c\u7684\uff0c\u53c8\u4e0d\u597d\u51fa\u58f0\uff0c\u53ea\u80fd\u54ac\u7259\u82e6\u5fcd\u7740\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u3002</p><p>\u771f\u662f\u7684\uff0c\u539f\u6765\u966a\u5973\u5b69\u5b50\u7761\u89c9\u8fd8\u6709\u8fd9\u79cd\u574f\u5904\uff0c\u53ef\u662f\u4ee5\u524d\u600e\u4e48\u4ece\u6765\u90fd\u6ca1\u53d1\u73b0\u5462\u2026\u2026</p><p>\u838e\u73de\u514b\u8d77\u8eab\u4e0b\u5e8a\uff0c\u4e5f\u4e0d\u7a7f\u8863\u670d\uff0c\u5168\u8eab\u8d64\u88f8\u7740\u8d70\u53bb\u5f00\u95e8\u8ba9\u8299\u857e\u72c4\u8fdb\u6765\uff0c\u5c0f\u5973\u5b69\u770b\u5230\u5979\u5413\u4e86\u4e00\u8df3\uff0c\u8fdb\u6765\u770b\u5230\u73ca\u5609\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u5927\u6982\u6ca1\u60f3\u5230\u73ca\u5609\u4f1a\u613f\u610f\u5e26\u7740\u838e\u73de\u514b\u4e00\u8d77\u548c\u743c\u6069\u73a9\u53cc\u98de\uff0c\u8fd9\u53ef\u662f\u8fde\u5979\u90fd\u6ca1\u6709\u4eab\u53d7\u8fc7\u7684\u5f85\u9047\u554a\u3002\u8981\u8bba\u8d77\u6765\uff0c\u96be\u9053\u5979\u4e0d\u6bd4\u838e\u73de\u514b\u8ba4\u8bc6\u73ca\u5609\u66f4\u4e45\uff0c\u5173\u7cfb\u66f4\u597d\u5417\uff1f\u51ed\u4ec0\u4e48\u8ba9\u8fd9\u4e2a\u521a\u5192\u51fa\u6765\u7684\u9b45\u9b54\u540e\u6765\u5c45\u4e0a\u5462\uff1f\u800c\u4e14\u8fd9\u6b21\u81ea\u4ece\u51fa\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u743c\u6069\u57fa\u672c\u6bcf\u5929\u665a\u4e0a\u90fd\u966a\u7740\u73ca\u5609\uff0c\u5b8c\u5168\u628a\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u51b7\u843d\u5728\u4e00\u65c1\uff0c\u8fd9\u5012\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8299\u857e\u72c4\u81ea\u77e5\u6ca1\u6cd5\u548c\u73ca\u5609\u76f8\u63d0\u5e76\u8bba\uff0c\u4e0d\u6562\u5962\u6c42\u2014\u2014\u4f46\u51ed\u4ec0\u4e48\u838e\u73de\u514b\u4e5f\u80fd\u6bd4\u5979\u6709\u4f18\u5148\u6743\uff1f</p><p>\u8fd9\u771f\u662f\u2026\u2026\u592a\u4e0d\u516c\u5e73\u4e86\uff01</p><p>\u5979\u5fc3\u601d\u5355\u7eaf\uff0c\u8111\u5b50\u91cc\u8fd9\u4e48\u60f3\uff0c\u8138\u4e0a\u795e\u60c5\u5c31\u6709\u53d8\u5316\u3002\u838e\u73de\u514b\u770b\u5728\u773c\u4e2d\uff0c\u53ea\u662f\u5fae\u7b11\u4e0d\u8bed\uff0c\u65c1\u82e5\u65e0\u4eba\u5730\u7a7f\u597d\u8863\u670d\u3002\u201c\u6211\u5148\u53bb\u505a\u51c6\u5907\u4e86\u3002\u201d\u5979\u5411\u743c\u6069\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u8d70\u51fa\u623f\u95f4\u3002</p><p>\u838e\u73de\u514b\u4e00\u8d70\uff0c\u73ca\u5609\u53c8\u6c89\u7761\u672a\u9192\uff0c\u8299\u857e\u72c4\u66f4\u4e0d\u5fc5\u63a9\u9970\uff0c\u5658\u7740\u5c0f\u5634\u671d\u743c\u6069\u770b\u8fc7\u6765\uff0c\u6ee1\u8138\u7684\u4e0d\u9ad8\u5174\u3002\u743c\u6069\u81ea\u89c9\u7406\u4e8f\uff0c\u5c34\u5c2c\u5730\u7b11\u4e86\u7b11\uff0c\u5306\u5306\u5fd9\u5fd9\u5957\u4e0a\u8863\u670d\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u6709\u4e8b\u627e\u6211\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u8299\u857e\u72c4\u5782\u4e0b\u773c\uff0c\u201c\u4f60\u8fd9\u4e48\u5fd9\uff0c\u6211\u54ea\u91cc\u4f1a\u6709\u4ec0\u4e48\u4e8b\u60c5\u6765\u6253\u6270\u4f60\u3002\u201d</p><p>\u2026\u2026\u5c0f\u4e2b\u5934\u771f\u751f\u6c14\u4e86\u3002</p><p>\u5e73\u65f6\u6e29\u987a\u67d4\u5f31\u7684\u4eba\uff0c\u4e00\u65e6\u771f\u6b63\u751f\u6c14\u8d77\u6765\uff0c\u53cd\u800c\u683c\u5916\u53ef\u6015\u3002\u743c\u6069\u5fc3\u4e2d\u53d1\u614c\uff0c\u4e00\u65f6\u95f4\u7adf\u7136\u624b\u8db3\u65e0\u63aa\uff0c\u90fd\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u5e94\u4ed8\u597d\u3002\u6240\u5e78\u8299\u857e\u72c4\u751f\u4e86\u4f1a\u95f7\u6c14\uff0c\u81ea\u5df1\u7f13\u548c\u8fc7\u6765\uff0c\u201c\u6ca1\u4e8b\uff0c\u5c31\u662f\u62c9\u52a0\u5148\u751f\u8bf7\u4f60\u8fc7\u53bb\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u8bf4\u662f\u4eca\u5929\u8981\u51fa\u6d77\uff0c\u5546\u91cf\u4e00\u4e0b\u5177\u4f53\u7684\u7ec6\u8282\u5b89\u6392\u3002\u201d</p><p>\u201c\u54e6\uff0c\u597d\uff0c\u597d\u7684\uff0c\u201d\u743c\u6069\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21\uff0c\u201c\u6211\u8fd9\u5c31\u53bb\u3002\u201d</p><p>\u4ed6\u8d70\u5230\u8299\u857e\u72c4\u8eab\u8fb9\uff0c\u4f38\u624b\u62b1\u5979\u3002\u5c0f\u5973\u5b69\u6ca1\u6709\u8eb2\u907f\uff0c\u4efb\u4ed6\u62b1\u5728\u6000\u91cc\uff0c\u8eab\u4f53\u5374\u662f\u786c\u90a6\u90a6\u7684\uff0c\u6c89\u7740\u8138\uff0c\u54ac\u7740\u5634\u5507\u3002\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u743c\u6069\u9053\u6b49\uff0c\u201c\u8fd9\u4e9b\u5929\u2026\u2026\u603b\u4e4b\u662f\u6211\u4e0d\u5bf9\uff0c\u522b\u751f\u6c14\u4e86\u597d\u4e0d\u597d\u3002\u201d</p><p>\u4ed6\u4e0d\u9053\u6b49\u5012\u8fd8\u7f62\u4e86\uff0c\u4ed6\u4e00\u9053\u6b49\uff0c\u8299\u857e\u72c4\u7684\u773c\u6cea\u987f\u65f6\u593a\u7736\u800c\u51fa\uff0c\u8fd9\u4e9b\u5929\u7d2f\u79ef\u7684\u59d4\u5c48\u5c3d\u6570\u6d8c\u4e0a\u5fc3\u5934\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u62bd\u6ce3\u8d77\u6765\u3002\u201c\u662f\u4f60\u8981\u6211\u966a\u7740\u4f60\u51fa\u6765\u7684\uff0c\u7ed3\u679c\u53c8\u628a\u6211\u6254\u5728\u4e00\u8fb9\u4e0d\u7406\u4e0d\u776c\uff0c\u201d\u5979\u54ed\u7740\u8bf4\uff0c\u201c\u6bcf\u5929\u665a\u4e0a\u90fd\u966a\u7740\u73ca\u5609\u59d0\u59d0\uff0c\u4e0d\u7136\u5c31\u662f\u6709\u4e8b\u2026\u2026\u65e2\u7136\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u4f60\u5f53\u65f6\u5e72\u561b\u8981\u6211\u966a\u4f60\u4e00\u8d77\u554a\u3002\u201d</p><p>\u743c\u6069\u65e0\u8a00\u4ee5\u5bf9\uff0c\u53ea\u80fd\u5c06\u5979\u62b1\u5728\u6000\u4e2d\uff0c\u201c\u5bf9\u4e0d\u8d77\u5bf9\u4e0d\u8d77\uff0c\u201d\u4ed6\u4e0d\u505c\u5730\u9053\u6b49\uff0c\u201c\u662f\u6211\u9519\u4e86\uff0c\u6211\u4fdd\u8bc1\u4ee5\u540e\u518d\u4e0d\u8fd9\u6837\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u8299\u857e\u72c4\u7591\u60d1\u5730\u62ac\u8d77\u8138\u5e9e\uff0c\u4e24\u988a\u4f9d\u65e7\u6302\u7740\u6e05\u6e05\u6cea\u75d5\u3002</p><p>\u201c\u771f\u7684\u3002\u201d\u743c\u6069\u4fdd\u8bc1\u3002</p><p>\u5f97\u5230\u4e86\u627f\u8bfa\uff0c\u5c0f\u5973\u5b69\u6ee1\u8db3\u5730\u7834\u6d95\u4e3a\u7b11\u3002\u201c\u5bf9\u4e86\uff0c\u5feb\u53bb\u5427\uff0c\u201d\u5979\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201c\u62c9\u52a0\u5148\u751f\u5728\u4f1a\u8bae\u5ba4\u91cc\u7b49\u7740\u4f60\u5462\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u548c\u62c9\u52a0\u8c08\u8fc7\u4e4b\u540e\uff0c\u743c\u6069\u56de\u5230\u81ea\u5df1\u623f\u95f4\u51c6\u5907\u9b54\u6cd5\uff0c\u56e0\u4e3a\u662f\u8981\u51fa\u6d77\uff0c\u5e73\u65f6\u5e38\u7528\u7684\u4e00\u4e9b\u6cd5\u672f\u5c31\u5fc5\u987b\u6362\u6389\uff0c\u5426\u5219\u7eaf\u7cb9\u6d6a\u8d39\u3002\u4e0a\u5348\u5341\u70b9\u949f\uff0c\u4e00\u884c\u4eba\u4ece\u603b\u90e8\u51fa\u53d1\uff0c\u524d\u5f80\u7801\u5934\u3002</p><p>\u5728\u743c\u6069\u539f\u672c\u7684\u8bbe\u60f3\u4e2d\uff0c\u8fd9\u6b21\u51fa\u6d77\u53ea\u5e26\u4e0a\u73ca\u5609\u548c\u838e\u73de\u514b\u3002\u59d0\u59d0\u662f\u575a\u6301\u8981\u53bb\u7684\uff0c\u6ca1\u529e\u6cd5\uff0c\u838e\u73de\u514b\u662f\u7528\u6765\u4fdd\u62a4\u59d0\u59d0\u7684\uff0c\u6015\u81ea\u5df1\u4e07\u4e00\u7167\u987e\u4e0d\u5468\u3002\u81f3\u4e8e\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\uff0c\u672c\u6253\u7b97\u662f\u8ba9\u5979\u4eec\u7559\u4e0b\u7684\uff0c\u4f46\u8299\u857e\u72c4\u5f97\u77e5\u8981\u53bb\u6d77\u5e95\u63a2\u9669\uff0c\u9887\u4e3a\u597d\u5947\uff0c\u7f20\u7740\u8981\u53bb\uff0c\u743c\u6069\u5bf9\u5979\u5fc3\u6709\u6127\u759a\uff0c\u4fbf\u4e0d\u597d\u62d2\u7edd\u3002\u59b9\u59b9\u65e2\u7136\u53bb\u4e86\uff0c\u603b\u4e0d\u597d\u5355\u5355\u7559\u4e0b\u59d0\u59d0\uff0c\u4f55\u51b5\u8299\u8389\u5a05\u662f\u4e2a\u7267\u5e08\uff0c\u5e26\u5728\u8eab\u8fb9\u4e5f\u80fd\u591a\u589e\u52a0\u51e0\u5206\u4fdd\u9669\u7cfb\u6570\u3002\u8fd9\u4e48\u4e00\u60f3\uff0c\u7ed3\u679c\u5c31\u662f\u743c\u6069\u5e26\u7740\u56db\u4f4d\u5973\u5b69\u5b50\u4e00\u8d77\u51fa\u52a8\u4e86\u3002\u6309\u7167\u743c\u6069\u7684\u8981\u6c42\uff0c\u73ca\u5609\u7279\u5730\u7a7f\u4e0a\u4e86\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u9001\u7684\u90a3\u4ef6\u6597\u7bf7\uff0c\u867d\u8bf4\u6b64\u884c\u5e94\u8be5\u6ca1\u591a\u5927\u5371\u9669\uff0c\u4f46\u6709\u5907\u65e0\u60a3\u603b\u662f\u597d\u7684\u3002</p><p>\u5230\u4e86\u7801\u5934\uff0c\u4fbf\u770b\u89c1\u4e00\u8258\u5e9e\u5927\u7684\u4e09\u6845\u6d77\u8239\uff0c\u505c\u6cca\u5728\u6d77\u4e2d\u4eff\u4f5b\u5de8\u9f99\u3002\u743c\u6069\u7b49\u4eba\u4e0a\u4e86\u8239\uff0c\u5374\u6ca1\u7acb\u523b\u51fa\u53d1\uff0c\u800c\u662f\u7ee7\u7eed\u7b49\u5f85\u4e86\u5927\u7ea6\u5341\u6765\u5206\u949f\u3002\u7a81\u7136\u8239\u7684\u53f3\u4fa7\u4e00\u9635\u6d77\u6f6e\u5982\u716e\u6cb8\u822c\u7ffb\u6eda\uff0c\u56db\u9053\u6c34\u67f1\u4ece\u4e2d\u51b2\u5929\u800c\u8d77\uff0c\u9876\u7aef\u7ad9\u7740\u56db\u4e2a\u4eba\u3002\u6c34\u67f1\u4e00\u76f4\u4e0a\u5347\uff0c\u6700\u540e\u4fdd\u6301\u4e0e\u7532\u677f\u9ad8\u5ea6\u6301\u5e73\uff0c\u7136\u540e\u743c\u6069\u770b\u6e05\u4e86\u4ed6\u4eec\u7684\u6478\u6837\u3002</p><p>\u90a3\u662f\u56db\u4e2a\u8eab\u7a7f\u84dd\u8272\u76ae\u8d28\u7d27\u8eab\u8863\u7684\u7537\u5b50\uff0c\u624b\u6301\u957f\u957f\u7684\u4e09\u53c9\u621f\uff0c\u9762\u5bb9\u67af\u7626\uff0c\u76ee\u5149\u5c16\u9510\uff0c\u867d\u7136\u662f\u521a\u4ece\u6c34\u4e2d\u94bb\u51fa\uff0c\u5168\u8eab\u4e0a\u4e0b\u5374\u6ca1\u534a\u70b9\u6c34\u6c7d\uff0c\u8fde\u5934\u53d1\u90fd\u662f\u5e72\u7684\u3002\u4ed6\u4eec\u671d\u7532\u677f\u4e0a\u770b\u4e86\u770b\uff0c\u671b\u89c1\u62c9\u52a0\uff0c\u968f\u5373\u8df3\u4e86\u8fc7\u6765\u3002\u201c\u5b89\u535a\u91cc\u6559\u4f1a\u7684\u4eba\u3002\u201d\u62c9\u52a0\u4f4e\u58f0\u5bf9\u65c1\u8fb9\u7684\u743c\u6069\u8bf4\u4e86\u53e5\uff0c\u968f\u5373\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u975e\u5e38\u5ba2\u6c14\u5730\u8fce\u4e86\u4e0a\u53bb\u3002</p><p>\u5b89\u535a\u91cc\u662f\u6d77\u6d0b\u5973\u795e\uff0c\u5728\u5185\u5730\u57ce\u5e02\u6ca1\u6709\u534a\u70b9\u5f71\u54cd\u529b\uff0c\u4f46\u5728\u6cbf\u6d77\u5730\u533a\u8fd8\u662f\u9887\u6709\u5a01\u540d\u7684\uff0c\u5927\u9646\u4e0a\u8457\u540d\u7684\u6e2f\u53e3\uff0c\u4f8b\u5982\u535a\u5f97\u4e4b\u95e8\u3001\u963f\u65af\u5361\u7279\u62c9\u3001\u8fdc\u5c71\u57ce\uff0c\u4ee5\u53ca\u8fd9\u5ea7\u4e4c\u62c9\u65af\u76ae\u5c14\uff0c\u90fd\u5efa\u6709\u5979\u7684\u795e\u6bbf\uff0c\u6240\u6709\u6e14\u6c11\u3001\u5546\u65c5\u51fa\u6d77\uff0c\u90fd\u5fc5\u987b\u8981\u5148\u5411\u795e\u6bbf\u732e\u4e0a\u4e30\u539a\u7684\u796d\u793c\uff0c\u5426\u5219\u96be\u514d\u906d\u9047\u75be\u98ce\u6076\u6d6a\uff0c\u843d\u4e2a\u8239\u6bc1\u4eba\u4ea1\u7684\u4e0b\u573a\u3002\u9634\u9b42\u57ce\u65e2\u7136\u6765\u5230\u6b64\u5730\uff0c\u53c8\u8981\u5728\u6d77\u4e2d\u641c\u5bfb\u661f\u9668\u57ce\uff0c\u81ea\u7136\u514d\u4e0d\u4e86\u8981\u548c\u5b89\u535a\u91cc\u6559\u4f1a\u6253\u4ea4\u9053\u3002\u53cc\u65b9\u7ecf\u8fc7\u51e0\u6b21\u5c0f\u89c4\u6a21\u7684\u51b2\u7a81\u4e4b\u540e\uff0c\u6700\u7ec8\u5750\u4e0b\u6765\u8c08\u5224\uff0c\u8fbe\u6210\u4e86\u534f\u8bae\uff1a\u9634\u9b42\u57ce\u548c\u5b89\u535a\u91cc\u6559\u4f1a\u5171\u540c\u5bfb\u627e\u661f\u9668\u57ce\uff0c\u6240\u83b7\u5f97\u7684\u8d22\u5b9d\uff0c\u5b89\u535a\u91cc\u6559\u4f1a\u4ece\u4e2d\u53d6\u516d\u6210\uff0c\u9634\u9b42\u57ce\u53d6\u56db\u6210\uff0c\u4f46\u6d6e\u7a7a\u57ce\u7684\u5bc6\u745f\u80fd\u6838\uff08\u65e0\u8bba\u662f\u5426\u635f\u574f\uff09\u5fc5\u987b\u5f52\u9634\u9b42\u57ce\u6240\u6709\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u5229\u76ca\u800c\u4e34\u65f6\u7ed3\u6210\u7684\u540c\u76df\u5173\u7cfb\uff0c\u5230\u5e95\u6709\u591a\u7262\u9760\uff0c\u90a3\u5c31\u4e0d\u597d\u8bf4\u4e86\u3002\u743c\u6069\u65e9\u4e0a\u548c\u62c9\u52a0\u5546\u8c08\u51fa\u6d77\u4e8b\u5b9c\uff0c\u63d0\u5230\u6b64\u4e8b\uff0c\u62c9\u52a0\u7684\u610f\u601d\u4fbf\u5f88\u6e05\u695a\uff1a\u4e00\u65e6\u4e8b\u6210\uff0c\u7acb\u523b\u7ffb\u8138\uff0c\u5e72\u6389\u8fd9\u51e0\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u3002\u4e4b\u6240\u4ee5\u8981\u7b49\u5230\u4e8b\u6210\u4e4b\u540e\uff0c\u800c\u4e0d\u662f\u73b0\u5728\u5c31\u52a8\u624b\uff0c\u4e43\u662f\u56e0\u4e3a\u8fd8\u8981\u501f\u52a9\u4ed6\u4eec\u7684\u529b\u91cf\u6765\u5bfb\u627e\u76ee\u6807\u3002\u81f3\u4e8e\u80dc\u8d25\u7684\u95ee\u9898\uff0c\u5012\u662f\u534a\u70b9\u4e0d\u7528\u62c5\u5fc3\uff0c\u5b89\u535a\u7406\u7684\u7267\u5e08\u64c5\u957f\u5404\u79cd\u5174\u98ce\u4f5c\u6d6a\u7684\u6cd5\u672f\uff0c\u4f46\u81ea\u8eab\u6218\u6597\u529b\u4e0d\u503c\u4e00\u63d0\uff0c\u4ed6\u4eec\u624b\u4e2d\u62ff\u7740\u7684\u4e09\u53c9\u621f\u770b\u4f3c\u660e\u6643\u6643\u7684\u5a01\u98ce\uff0c\u5176\u5b9e\u662f\u79cd\u6cd5\u5668\uff0c\u6839\u672c\u4e0d\u662f\u5175\u5203\u3002\u9634\u9b42\u57ce\u6b64\u884c\uff0c\u52a0\u4e0a\u743c\u6069\u4e00\u5171\u51fa\u52a8\u4e86\u5341\u4e8c\u540d\u5deb\u5e08\uff0c\u800c\u4e14\u5168\u90fd\u662f\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\uff0c\u5168\u526f\u6b66\u88c5\uff0c\u5404\u79cd\u9b54\u6cd5\u7269\u54c1\u643a\u5e26\u9f50\u5168\uff0c\u5c31\u7b97\u662f\u628a\u6574\u4e2a\u6851\u6bd4\u4e9a\u7684\u5b89\u535a\u91cc\u6559\u4f1a\u626b\u5e73\u4f30\u8ba1\u90fd\u6ca1\u95ee\u9898\u4e86\u3002</p><p>\u53cc\u65b9\u5ba2\u5957\u6bd5\uff0c\u4fbf\u5f00\u8239\u51fa\u53d1\u3002\u6b64\u5730\u8ddd\u79bb\u53d1\u73b0\u5929\u7a79\u4eea\u7684\u90a3\u7247\u6d77\u57df\u5e76\u4e0d\u5f88\u8fdc\uff0c\u5168\u901f\u884c\u9a76\u7684\u8bdd\uff0c\u9884\u8ba1\u516d\u4e2a\u5c0f\u65f6\u80fd\u591f\u5230\u8fbe\u3002\u8239\u662f\u76f4\u63a5\u7528\u9b54\u6cd5\u9a71\u52a8\uff0c\u5341\u5206\u5e73\u7a33\uff0c\u4f46\u9664\u4e86\u5b89\u535a\u7406\u7267\u5e08\u4e4b\u5916\uff0c\u9634\u9b42\u57ce\u8fd9\u8fb9\u7684\u4eba\u5927\u591a\u90fd\u4e45\u5c45\u9646\u5730\uff0c\u4e00\u8f88\u5b50\u6ca1\u51fa\u8fc7\u51e0\u6b21\u6d77\uff0c\u4e0d\u514d\u6216\u591a\u6216\u5c11\u90fd\u6709\u4e9b\u6655\u8239\uff0c\u5e78\u597d\u51fa\u53d1\u4e4b\u524d\uff0c\u62c9\u52a0\u5c31\u5df2\u7ecf\u5206\u53d1\u4e86\u62b5\u6297\u6655\u8239\u7684\u836f\u6c34\uff0c\u559d\u4e0b\u540e\u5f88\u5feb\u5373\u4fbf\u6062\u590d\u3002</p><p>\u6d77\u4e0a\u98ce\u666f\uff0c\u8f83\u4e4b\u9646\u5730\u53c8\u522b\u6709\u4e00\u756a\u4e0d\u540c\uff0c\u6ce2\u6d9b\u58ee\u9614\uff0c\u6d69\u8361\u65e0\u57a0\uff0c\u743c\u6069\u4e00\u884c\u4eba\u4e2d\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u548c\u838e\u73de\u514b\u4e4b\u5916\uff0c\u5176\u4ed6\u4e09\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u7b2c\u4e00\u6b21\u51fa\u6d77\uff0c\u770b\u5230\u8fd9\u524d\u6240\u672a\u89c1\u7684\u7f8e\u666f\uff0c\u65e2\u662f\u65b0\u5947\u53c8\u662f\u5174\u594b\uff0c\u5527\u5527\u55b3\u55b3\u8bf4\u4e2a\u4e0d\u505c\uff0c\u62c9\u7740\u838e\u73de\u514b\u95ee\u8fd9\u95ee\u90a3\u3002\u838e\u73de\u514b\u4ee5\u524d\u5728\u94c1\u738b\u5ea7\u7684\u65f6\u5019\uff0c\u5012\u662f\u7ecf\u5e38\u4e58\u8239\u5728\u6d77\u4e0a\u5f80\u6765\uff0c\u6bd4\u8f83\u719f\u6089\u3002\u743c\u6069\u9760\u5728\u8239\u8237\uff0c\u9759\u9759\u671b\u7740\u9065\u8fdc\u5904\u6c34\u5929\u76f8\u9694\u7684\u4e00\u7ebf\uff0c\u5e26\u7740\u8165\u5473\u7684\u6d77\u98ce\u5439\u5728\u8138\u4e0a\uff0c\u6709\u79cd\u9ebb\u9165\u9165\u7684\u611f\u89c9\u3002</p><p>\u201c\u54a6\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u6307\u7740\u6d77\u8239\u53f3\u524d\u65b9\uff0c\u8299\u857e\u72c4\u60ca\u5947\u5730\u53eb\u8d77\u6765\uff0c\u5176\u4ed6\u4eba\u987a\u7740\u5979\u624b\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u7247\u6d77\u57df\u4e0d\u77e5\u4f55\u6545\uff0c\u6570\u5341\u9053\u7eda\u70c2\u65e0\u6bd4\u7684\u5149\u67f1\u4ece\u6d77\u5e95\u6b64\u8d77\u5f7c\u4f0f\u5730\u63a5\u8fde\u5347\u8d77\uff0c\u76f4\u51b2\u5929\u9645\uff0c\u6709\u84dd\u8272\uff0c\u6709\u7eff\u8272\uff0c\u6709\u7d2b\u8272\uff0c\u6709\u7ea2\u8272\uff0c\u7f8e\u4e3d\u5f97\u8ba9\u4eba\u76ee\u7729\u3002\u53c8\u6709\u6210\u767e\u4e0a\u5343\u7684\u5149\u70b9\u4ece\u4e91\u5c42\u4e2d\u7a7f\u51fa\uff0c\u62d6\u7740\u8d64\u7ea2\u8272\u7684\u957f\u957f\u5c3e\u5df4\uff0c\u4ee5\u6781\u9ad8\u7684\u901f\u5ea6\u649e\u5165\u6d77\u4e2d\uff0c\u4eff\u4f5b\u5929\u4e0a\u7684\u661f\u8fb0\u5982\u96e8\u5760\u843d\u3002\u751a\u81f3\u8fd8\u6709\u97f3\u4e50\u58f0\u968f\u98ce\u9690\u9690\u4f20\u6765\uff0c\u4eff\u4f5b\u957f\u7b1b\uff0c\u4eff\u4f5b\u98ce\u7434\uff0c\u98d8\u98d8\u6e3a\u6e3a\uff0c\u60a0\u626c\u52a8\u542c\u3002</p><p>\u201c\u6781\u5149\uff1f\u4f46\u6781\u5149\u5e76\u4e0d\u662f\u5149\u67f1\u7684\u5f62\u6001\u554a\u3002\u201d</p><p>\u743c\u6069\u6b63\u81ea\u601d\u7d22\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u7ed9\u51fa\u4e86\u7b54\u6848\uff0c\u5979\u5176\u5b9e\u4e5f\u6ca1\u6765\u8fc7\u5760\u661f\u6d77\uff0c\u4f46\u89c1\u95fb\u5e7f\u535a\uff0c\u542c\u8bf4\u8fc7\u8fd9\u79cd\u666f\u8c61\u3002\u201c\u8fd9\u5c31\u662f\u2018\u5760\u661f\u96e8\u2019\u4e86\uff0c\u6e14\u6c11\u51fa\u6d77\u7684\u65f6\u5019\uff0c\u5982\u679c\u5929\u6c14\u7279\u522b\u6674\u597d\uff0c\u7ecf\u5e38\u80fd\u591f\u770b\u5230\u8fd9\u79cd\u666f\u8c61\uff0c\u5760\u661f\u6d77\u4e4b\u6240\u4ee5\u5f97\u540d\uff0c\u4fbf\u7531\u4e8e\u6b64\u3002\u201d</p><p>\u201c\u90a3\uff0c\u771f\u7684\u662f\u5929\u4e0a\u7684\u661f\u8fb0\u5728\u5760\u843d\u5417\uff1f\u201d</p><p>\u8299\u857e\u72c4\u8fd9\u4e2a\u95ee\u9898\u663e\u7136\u95ee\u5f97\u592a\u50bb\uff0c\u838e\u73de\u514b\u201c\u5475\u201d\u5730\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\uff0c\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6070\u6070\u76f8\u53cd\uff0c\u662f\u6d77\u91cc\u7684\u9b54\u602a\u5728\u4f5c\u795f\u5462\uff0c\u5b83\u4eec\u7528\u8fd9\u79cd\u7729\u76ee\u666f\u8272\u5438\u5f15\u98df\u7269\uff0c\u6e14\u6c11\u4eec\u6709\u65f6\u5019\u88ab\u5b83\u6240\u8bf1\u60d1\uff0c\u9760\u8fd1\u90a3\u7247\u6d77\u57df\uff0c\u7136\u540e\u5c31\u4f1a\u5c38\u9aa8\u65e0\u5b58\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u542c\u4e86\u838e\u73de\u514b\u7684\u89e3\u91ca\uff0c\u518d\u770b\u90a3\u7247\u6d77\u57df\uff0c\u539f\u672c\u7eda\u70c2\u7f8e\u4e3d\u7684\u666f\u8c61\u4e2d\uff0c\u4eff\u4f5b\u7a81\u7136\u589e\u52a0\u4e86\u51e0\u62b9\u72f0\u72de\u7684\u8840\u8272\u3002\u5b89\u535a\u7406\u7684\u7267\u5e08\u4eec\u4e45\u5c45\u6d77\u4e0a\uff0c\u81ea\u7136\u66f4\u6e05\u695a\u5176\u4e2d\u95e8\u9053\uff0c\u8f7b\u8f66\u719f\u8def\u5730\u6307\u6325\u7740\u6d77\u8239\u53d8\u5411\uff0c\u5212\u4e86\u4e2a\u5927\u5927\u7684\u5f27\u7ebf\uff0c\u4ece\u5760\u661f\u96e8\u65c1\u8fb9\u7ed5\u4e86\u8fc7\u53bb\uff0c\u7136\u540e\u7ee7\u7eed\u524d\u8fdb\u3002</p><p>\u6d77\u57df\u5e7f\u88a4\uff0c\u5176\u4e2d\u6f5c\u4f0f\u7684\u602a\u7269\u4f17\u591a\uff0c\u8f83\u4e4b\u9646\u5730\u4e0a\u7684\u540c\u7c7b\uff0c\u8be1\u8c32\u72e1\u8bc8\u72b9\u6709\u8fc7\u4e4b\u3002\u5760\u661f\u96e8\u8fd9\u79cd\uff0c\u56e0\u4e3a\u592a\u8fc7\u660e\u663e\uff0c\u5df2\u7ecf\u5c5e\u4e8e\u5f88\u4f4e\u7ea7\u7684\u624b\u6cd5\uff0c\u53ea\u8981\u7559\u795e\u5c31\u4e0d\u5bb9\u6613\u4e0a\u5f53\u3002\u8fd8\u6709\u66f4\u591a\u66f4\u51f6\u72e0\u7684\u6d77\u602a\uff0c\u65e0\u58f0\u65e0\u606f\u5730\u6f5c\u4f0f\u5728\u6c34\u9762\u4e0b\uff0c\u7b49\u5f85\u7740\u65f6\u673a\u53d1\u51fa\u81f4\u547d\u88ad\u51fb\u3002\u8fd9\u79cd\u65f6\u5019\uff0c\u540c\u884c\u7684\u56db\u4e2a\u5b89\u535a\u7406\u7267\u5e08\u5c31\u6d3e\u4e0a\u5927\u7528\u573a\u4e86\uff0c\u4ed6\u4eec\u53ea\u6d88\u7ad9\u5728\u8239\u5934\uff0c\u5c06\u624b\u4e2d\u7684\u4e09\u53c9\u621f\u63a2\u5165\u6d77\u4e2d\uff0c\u4fbf\u80fd\u591f\u6e05\u695a\u5730\u5f97\u77e5\u201c\u5de6\u524d\u65b9\u56db\u5341\u4e94\u5ea6\uff0c\u4e03\u91cc\uff0c\u6709\u5de8\u578b\u6d77\u86c7\u201d\uff0c\u6216\u8005\u201c\u6b63\u524d\u65b9\u5341\u4e8c\u91cc\uff0c\u6709\u7eb3\u8fe6\u7fa4\uff0c\u6570\u91cf\u8d85\u8fc7\u4e8c\u5341\u4e2a\u201d\uff0c\u6307\u5bfc\u8239\u53d8\u6362\u65b9\u5411\uff0c\u8eb2\u907f\u7740\u8fd9\u4e9b\u6d77\u602a\u3002\u6709\u65f6\u5019\u5b9e\u5728\u65e0\u6cd5\u7ed5\u5f00\uff0c\u8fd8\u80fd\u4ee5\u4e09\u53c9\u621f\u5475\u65a5\u6d77\u602a\u9000\u907f\uff0c\u4e00\u8def\u4e0a\u5c45\u7136\u4fbf\u5e73\u5e73\u5b89\u5b89\u5730\u5ea6\u8fc7\u3002</p><p>\u65f6\u95f4\u8fc7\u5f97\u5f88\u5feb\uff0c\u8f6c\u773c\u95f4\u5df2\u7ecf\u4e2d\u5348\u3002\u6d77\u4e0a\u666f\u8272\uff0c\u521d\u770b\u58ee\u4e3d\uff0c\u770b\u591a\u4e86\u4e5f\u4fbf\u89c9\u5f97\u5355\u8c03\uff0c\u5c24\u5176\u662f\u51fa\u6d77\u4e4b\u540e\uff0c\u89e6\u76ee\u6240\u53ca\u9664\u4e86\u5929\u7a7a\u5c31\u662f\u5927\u6d77\uff0c\u9664\u4e86\u4e91\u6735\u5c31\u662f\u6ce2\u6d9b\uff0c\u81f3\u4e8e\u90a3\u4e9b\u5760\u661f\u96e8\u3001\u6d77\u602a\uff0c\u521a\u5f00\u59cb\u867d\u7136\u65b0\u9c9c\uff0c\u91cd\u590d\u51e0\u6b21\u4e5f\u5c31\u6ca1\u611f\u89c9\u4e86\u3002\u743c\u6069\u770b\u770b\u65f6\u5019\u4e0d\u65e9\uff0c\u5728\u7532\u677f\u4e0a\u4e5f\u7ad9\u4e86\u534a\u5929\uff0c\u6709\u4e9b\u75b2\u5026\u4e86\uff0c\u4fbf\u51c6\u5907\u56de\u623f\u95f4\u4f11\u606f\u3002\u7ecf\u8fc7\u8239\u8237\u4e00\u4fa7\u7684\u8fc7\u9053\u65f6\uff0c\u6070\u597d\u770b\u5230\u90a3\u56db\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u8fce\u9762\u8d70\u8fc7\u6765\u3002</p><p>\u8fc7\u9053\u5e76\u4e0d\u5bbd\u655e\uff0c\u53ea\u80fd\u5bb9\u4e24\u4eba\u5e76\u884c\uff0c\u743c\u6069\u7ad9\u5728\u8239\u8237\u8fb9\u907f\u8ba9\uff0c\u56db\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u6392\u6210\u4e00\u7ebf\u4ece\u4ed6\u65c1\u8fb9\u7ecf\u8fc7\uff0c\u4ed6\u4eec\u8eab\u4e0a\u900f\u7740\u4e00\u80a1\u6d53\u6d53\u7684\u6d77\u8165\u5473\uff0c\u51b2\u5f97\u743c\u6069\u6697\u81ea\u76b1\u7709\u5934\u3002\u5f53\u6700\u540e\u4e00\u4e2a\u4eba\u548c\u4ed6\u64e6\u80a9\u800c\u8fc7\u7684\u65f6\u5019\uff0c\u7a7a\u6c14\u4e2d\u731b\u7136\u201c\u556a\u201d\u5730\u4e00\u58f0\u8106\u54cd\uff0c\u7206\u51fa\u4e00\u9053\u707f\u70c2\u7684\u94f6\u8272\u95ea\u7535\u5f27\u5149\u6765\u3002</p><p>\u8fd9\u9053\u95ea\u7535\u5f27\u5149\u51fa\u73b0\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u4e24\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u4e0b\u610f\u8bc6\u5730\u9f50\u9f50\u9000\u4e86\u534a\u6b65\uff0c\u5f7c\u6b64\u5bf9\u89c6\u7740\uff0c\u5fc3\u4e2d\u5404\u81ea\u60ca\u7591\u4e0d\u5b9a\u3002\u53e6\u5916\u4e09\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u5bdf\u89c9\u52a8\u9759\uff0c\u7591\u60d1\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u6700\u540e\u90a3\u540d\u7267\u5e08\u8bf4\uff0c\u4ece\u743c\u6069\u8eab\u8fb9\u4f4e\u5934\u8d70\u8fc7\u3002</p></article>"}}}]);
