(window.webpackJsonp=window.webpackJsonp||[]).push([[1664],{1687:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516d\u8282 \u53d1\u73b0\u661f\u9668\u57ce",content:"<article><h2>\u7b2c\u56db\u5341\u516d\u8282 \u53d1\u73b0\u661f\u9668\u57ce</h2><p>\u6d77\u8239\u7ee7\u7eed\u884c\u9a76\uff0c\u9884\u8ba1\u7684\u8def\u7a0b\u5df2\u7ecf\u8d70\u5b8c\u56db\u5206\u4e4b\u4e09\u3002\u73ca\u5609\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u7684\u65b0\u9c9c\u52b2\u603b\u7b97\u662f\u5b8c\u5168\u8fc7\u53bb\uff0c\u836f\u6c34\u867d\u7136\u80fd\u62b5\u6297\u6655\u8239\uff0c\u5374\u4e0d\u80fd\u6d88\u9664\u4e58\u8239\u7684\u75b2\u52b3\uff0c\u5979\u4eec\u73b0\u5728\u90fd\u4e56\u4e56\u4e0a\u5e8a\u4f11\u606f\u53bb\u4e86\u3002\u743c\u6069\u5219\u7ad9\u5728\u5916\u9762\u7532\u677f\u4e0a\uff0c\u6000\u4e2d\u6402\u7740\u9b45\u9b54\uff0c\u770b\u4e0a\u53bb\u5728\u8bf4\u7b11\u8c03\u60c5\uff0c\u4e00\u53ea\u624b\u8fd8\u5f88\u4e0d\u8001\u5b9e\u5730\u987a\u52bf\u6309\u5728\u5979\u7684\u4e30\u6ee1\u7fd8\u81c0\u4e0a\uff0c\u629a\u6478\u63c9\u634f\uff0c\u4f46\u4ed6\u7684\u773c\u5149\u5374\u59cb\u7ec8\u662f\u8d8a\u8fc7\u838e\u73de\u514b\u7684\u80a9\u5934\uff0c\u8fdc\u8fdc\u5730\u770b\u7740\u8239\u5934\u3002\u90a3\u91cc\u6709\u56db\u4f4d\u5b89\u535a\u7406\u7267\u5e08\u6b63\u8f6e\u6d41\u4e0a\u9635\uff0c\u4f7f\u7528\u4e09\u53c9\u621f\u63a2\u6d4b\u6d77\u602a\uff0c\u743c\u6069\u6240\u6ce8\u89c6\u7684\uff0c\u4fbf\u662f\u4e2a\u5934\u6700\u77ee\u5c0f\uff0c\u770b\u8d77\u6765\u6700\u4e0d\u8d77\u773c\u7684\u90a3\u4f4d\u3002</p><p>\u201c\u90a3\u4e2a\u5bb6\u4f19\u2026\u2026\u6709\u95ee\u9898\u554a\u3002\u201d</p><p>\u521a\u624d\u4e24\u4eba\u64e6\u80a9\u800c\u8fc7\uff0c\u83ab\u540d\u5176\u5999\u5730\u7206\u51fa\u4e00\u9053\u95ea\u7535\u5f27\uff0c\u867d\u7136\u4e00\u95ea\u5373\u901d\uff0c\u5176\u4ed6\u4eba\u90fd\u6ca1\u6ce8\u610f\uff0c\u4f46\u5f53\u4e8b\u4eba\u5374\u662f\u6e05\u6e05\u695a\u695a\u7684\u3002\u4e4b\u6240\u4ee5\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5947\u602a\u60c5\u5f62\uff0c\u743c\u6069\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u9690\u7ea6\u4e5f\u80fd\u731c\u5230\u51e0\u5206\u3002</p><p>\u5409\u52d2\u4eca\u662f\u6069\u745f\u7684\u4e3b\u795e\uff0c\u5177\u4f53\u6267\u638c\u4ec0\u4e48\uff0c\u743c\u6069\u5e76\u4e0d\u6e05\u695a\u3002\u4f46\u770b\u90a3\u5bb6\u4f19\u4e0a\u6b21\u51fa\u573a\u65f6\u7684\u60c5\u666f\uff0c\u72c2\u98ce\u547c\u5578\uff0c\u96f7\u9706\u5927\u4f5c\uff0c\u4f30\u8ba1\u5341\u6709\u516b\u4e5d\u548c\u5854\u6d1b\u65af\u4e00\u6837\uff0c\u6709\u98ce\u66b4\u3001\u96f7\u7535\u4e4b\u7c7b\u7684\u795e\u804c\u3002\u548c\u6885\u83f2\u65af\u5728\u4e00\u8d77\u4e45\u4e86\uff0c\u4ed6\u7684\u5b97\u6559\u5b66\u4e5f\u603b\u7b97\u662f\u6709\u6240\u8fdb\u6b65\uff0c\u77e5\u9053\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u51e1\u662f\u5c5e\u4e8e\u540c\u4e00\u4e2a\u795e\u7cfb\u7684\u795e\u7947\uff0c\u6267\u638c\u7684\u795e\u804c\u4e0d\u80fd\u91cd\u53e0\uff0c\u4f46\u4e0d\u540c\u795e\u7cfb\u4e4b\u95f4\u7684\u795e\u7947\u5219\u65e0\u6b64\u9650\u5236\u3002\u5f53\u7136\u5982\u679c\u795e\u7cfb\u878d\u5408\u2014\u2014\u8fd9\u79cd\u4e8b\u60c5\u5728\u5386\u53f2\u4e0a\u5c61\u6709\u53d1\u751f\u2014\u2014\u5177\u6709\u76f8\u540c\u795e\u804c\u7684\u795e\u660e\u4e4b\u95f4\u4fbf\u8981\u53d1\u751f\u6218\u4e89\uff0c\u51b3\u51fa\u552f\u4e00\u7684\u80dc\u8005\u3002</p><p>\u743c\u6069\u81ea\u4ece\u541e\u566c\u878d\u5408\u4e86\u5409\u52d2\u4eca\u7684\u795e\u529b\u540e\uff0c\u9690\u9690\u7ea6\u7ea6\u95f4\u4e5f\u80fd\u591f\u611f\u89c9\u5230\u4e00\u4e9b\u7ec6\u5fae\u53d8\u5316\uff0c\u6bcf\u6b21\u8fd0\u6c14\u547c\u5438\u7684\u65f6\u5019\uff0c\u4f53\u5185\u5c31\u4eff\u4f5b\u6709\u8f7b\u5fae\u7684\u7535\u6d41\u6d8c\u52a8\uff0c\u5f00\u59cb\u8fd8\u4ee5\u4e3a\u9519\u89c9\uff0c\u540e\u6765\u4fbf\u53d1\u73b0\u4e0d\u662f\u3002\u4ed6\u5c1d\u8bd5\u7740\u7528\u8c03\u606f\u7684\u65b9\u5f0f\u6765\u63a7\u5236\u8fd9\u4e9b\u7535\u6d41\uff0c\u5c45\u7136\u4e5f\u9887\u6709\u6210\u6548\uff0c\u867d\u7136\u56e0\u4e3a\u65f6\u95f4\u77ed\u7684\u5173\u7cfb\uff0c\u8fd8\u6ca1\u660e\u663e\u7a81\u7834\uff0c\u4f46\u5047\u4ee5\u65f6\u65e5\u7684\u8bdd\uff0c\u5e94\u8be5\u80fd\u591f\u5c06\u8fd9\u79cd\u7535\u6d41\u51dd\u805a\u5916\u5316\uff0c\u751a\u81f3\u50cf\u5409\u52d2\u4eca\u4e00\u6837\uff0c\u5851\u6210\u65e0\u6570\u795e\u5668\u7684\u6295\u5f71\u6765\u653b\u51fb\u654c\u4eba\uff0c\u53ef\u4ee5\u5927\u5927\u5f25\u8865\u653b\u51fb\u529b\u4e0d\u8db3\u7684\u95ee\u9898\u3002</p><p>\u7136\u800c\u6b64\u524d\u51fa\u73b0\u7684\u90a3\u9053\u95ea\u7535\u5f27\uff0c\u5374\u7edd\u975e\u743c\u6069\u523b\u610f\u63a7\u5236\u7684\u7ed3\u679c\uff0c\u800c\u662f\u4f53\u5185\u7535\u6d41\u81ea\u53d1\u7684\u5e94\u6fc0\u53cd\u5e94\u3002\u5c31\u50cf\u662f\u4e24\u4e2a\u6ee1\u8eab\u643a\u5e26\u7535\u8377\u7684\u4eba\u9760\u8fd1\uff0c\u4e8e\u662f\u4fbf\u6709\u4e86\u8fd9\u79cd\u60c5\u5f62\u3002\u8bf4\u5f97\u66f4\u660e\u767d\u70b9\uff0c\u743c\u6069\u80fd\u591f\u6e05\u695a\u611f\u5e94\u5230\uff0c\u5bf9\u65b9\u4f53\u5185\u540c\u6837\u4e5f\u8574\u542b\u7740\u96f7\u9706\u95ea\u7535\u7684\u529b\u91cf\u2014\u2014\u800c\u4e14\u5176\u5f3a\u5ea6\u8981\u8fdc\u8fdc\u8d85\u8fc7\u81ea\u5df1\uff0c\u81f3\u5c11\u662f\u51e0\u500d\u3002</p><p>\u53ef\u662f\uff0c\u5b89\u535a\u7406\u662f\u6d77\u795e\uff0c\u4e0d\u662f\u96f7\u795e\u5427\u3002</p><p>\u743c\u6069\u6697\u81ea\u6c89\u601d\u7740\uff0c\u8b66\u60d5\u8d77\u6765\u3002\u6839\u636e\u4e4b\u524d\u7684\u8ba1\u5212\uff0c\u4e00\u65e6\u627e\u5230\u661f\u9668\u57ce\uff0c\u8fd9\u51e0\u4e2a\u5b89\u535a\u7406\u7267\u5e08\u5c31\u518d\u65e0\u5229\u7528\u4ef7\u503c\uff0c\u76f4\u63a5\u201c\u6e05\u7406\u201d\u6389\u5b8c\u4e8b\u3002\u56e0\u4e3a\u4f17\u6240\u5468\u77e5\uff0c\u5b89\u535a\u7406\u7267\u5e08\u7684\u6218\u6597\u529b\u975e\u5e38\u4e4b\u5f31\uff0c\u5b8c\u5168\u4e0d\u80fd\u6784\u6210\u5a01\u80c1\uff0c\u6545\u6b64\u65e0\u9700\u62c5\u5fc3\u3002\u4f46\u5982\u679c\u8fd9\u4e2a\u4f17\u6240\u5468\u77e5\u7684\u5e38\u8bc6\u6709\u8bef\u5462\uff1f</p><p>\u81f3\u5c11\uff0c\u743c\u6069\u6240\u77e5\u9053\u7684\u5e38\u8bc6\u91cc\uff0c\u7edd\u6ca1\u6709\u534a\u53e5\u63d0\u53ca\u5b89\u535a\u7406\u7267\u5e08\u64c5\u957f\u64cd\u63a7\u96f7\u9706\u95ea\u7535\u4e4b\u529b\u7684\u8bf4\u6cd5\u3002</p><p>\u4ed6\u8003\u8651\u662f\u5426\u8981\u627e\u62c9\u52a0\u5546\u8bae\uff0c\u60f3\u4e86\u60f3\u53c8\u6253\u6d88\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4e0d\u592a\u5bb9\u6613\u89e3\u91ca\uff0c\u5c24\u5176\u662f\u5728\u4ed6\u7edd\u4e0d\u5e0c\u671b\u66b4\u9732\u81ea\u5df1\u80fd\u591f\u541e\u566c\u795e\u529b\u8fd9\u4ef6\u4e8b\u60c5\u7684\u524d\u63d0\u4e0b\u3002\u4e0d\u8fc7\u4e5f\u65e0\u6240\u8c13\u4e86\uff0c\u53cd\u6b63\u5bf9\u65b9\u4e00\u5171\u624d\u56db\u4e2a\u4eba\uff0c\u81ea\u5df1\u8fd9\u8fb9\u5341\u4e8c\u4e2a\u9ad8\u9636\u5deb\u5e08\uff0c\u600e\u4e48\u7b97\u4e5f\u662f\u80dc\u5238\u5728\u63e1\u3002\u800c\u4e14\u90a3\u56db\u4e2a\u5b89\u535a\u7406\u7267\u5e08\u4e2d\uff0c\u4e5f\u5c31\u6700\u540e\u8fd9\u4e2a\u6709\u53e4\u602a\u7f62\u4e86\uff0c\u5927\u4e0d\u4e86\u5429\u5490\u838e\u73de\u514b\u76ef\u4f4f\u4ed6\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u7b2c\u4e00\u4e2a\u5c31\u628a\u4ed6\u5e72\u6389\u3002</p><p>\u6b63\u76d8\u7b97\u95f4\uff0c\u8239\u5934\u5904\u7a81\u7136\u4f20\u6765\u4e00\u9635\u52a8\u9759\uff0c\u50cf\u662f\u4ece\u6d77\u4e2d\u6253\u635e\u8d77\u4ec0\u4e48\u4e1c\u897f\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4fbf\u770b\u89c1\u62c9\u52a0\u5e26\u7740\u51e0\u4e2a\u4eba\uff0c\u5306\u5306\u5fd9\u5fd9\u4ece\u8239\u8231\u4e2d\u94bb\u51fa\uff0c\u8d76\u4e86\u8fc7\u53bb\u3002\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u201c\u6211\u4eec\u4e5f\u53bb\u770b\u770b\u3002\u201d\u4ed6\u5bf9\u838e\u73de\u514b\u8bf4\u3002</p><p>\u8fd1\u524d\u4e00\u770b\uff0c\u5374\u662f\u4e09\u5177\u7537\u6027\u5c38\u4f53\uff0c\u663e\u7136\u5df2\u7ecf\u6b7b\u53bb\u5f88\u4e45\uff0c\u81f3\u5c11\u8d85\u8fc7\u4e00\u65e5\uff0c\u5168\u8eab\u90fd\u88ab\u6d77\u6c34\u6ce1\u5f97\u4e25\u91cd\u53d1\u80bf\uff0c\u8138\u90e8\u5b8c\u5168\u53d8\u5f62\uff0c\u8fa8\u8ba4\u4e0d\u51fa\u751f\u524d\u76f8\u8c8c\uff0c\u4f46\u4e00\u770b\u670d\u9970\u4fbf\u77e5\u662f\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u3002\u743c\u6069\u5fc3\u4e2d\u54af\u5654\u4e00\u8df3\uff0c\u4fa7\u8138\u770b\u53bb\uff0c\u53d1\u73b0\u62c9\u52a0\u4e5f\u6b63\u9762\u8272\u9634\u6c89\u5730\u671d\u4ed6\u770b\u8fc7\u6765\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u7136\u540e\u547d\u4ee4\u624b\u4e0b\u5c06\u5c38\u4f53\u62d6\u56de\u8239\u8231\u5730\u4e0b\u5ba4\u53bb\u505a\u68c0\u67e5\u3002</p><p>\u534a\u5c0f\u65f6\u540e\uff0c\u5c38\u68c0\u7ed3\u679c\u51fa\u6765\uff0c\u786e\u8ba4\u6b63\u662f\u9634\u9b42\u57ce\u6d3e\u51fa\u53bb\u641c\u7d22\u661f\u9668\u57ce\u7684\u4eba\u5458\u3002\u6b7b\u56e0\u662f\u88ab\u5927\u578b\u7684\u8f6f\u4f53\u602a\u7269\u7f20\u4f4f\uff0c\u5168\u8eab\u9aa8\u9abc\u88ab\u52d2\u5f97\u5bf8\u5bf8\u65ad\u88c2\uff0c\u6ca1\u6709\u660e\u663e\u5916\u4f24\uff0c\u4e5f\u6ca1\u6709\u4e2d\u6bd2\u8ff9\u8c61\u3002\u5c31\u76ee\u524d\u7684\u75d5\u8ff9\u63a8\u6d4b\uff0c\u5e94\u8be5\u662f\u5728\u641c\u5bfb\u6d6e\u7a7a\u57ce\u7684\u8fc7\u7a0b\u4e2d\u906d\u9047\u4e86\u4e4c\u8d3c\u4e4b\u7c7b\u7684\u6d77\u602a\uff0c\u88ab\u6740\u8eab\u4ea1\u3002</p><p>\u201c\u6d3e\u51fa\u53bb\u7684\u90a3\u961f\u4eba\u5171\u6709\u56db\u540d\uff0c\u4e00\u4eba\u6628\u65e5\u56de\u6765\u62a5\u4fe1\uff0c\u8fd9\u5c31\u662f\u5269\u4e0b\u90a3\u4e09\u4eba\u3002\u201d</p><p>\u62c9\u52a0\u9762\u8272\u4e0d\u8c6b\u5730\u5bf9\u743c\u6069\u8bf4\u3002\u6b64\u6b21\u201c\u6355\u635e\u201d\u5de5\u4f5c\u4e00\u5171\u5341\u4e94\u540d\u6210\u5458\uff0c\u5168\u90fd\u662f\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u5982\u4eca\u6298\u635f\u56db\u4eba\uff0c\u63a5\u8fd1\u4e09\u5206\u4e4b\u4e00\u3002\u8fd9\u6837\u7684\u635f\u5931\u9634\u9b42\u57ce\u867d\u7136\u627f\u53d7\u5f97\u8d77\uff0c\u4f46\u4f5c\u4e3a\u8d1f\u8d23\u4eba\u7684\u4ed6\uff0c\u96be\u514d\u8981\u53d7\u5904\u5206\u3002\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u53ea\u6709\u5c3d\u5feb\u627e\u5230\u661f\u9668\u57ce\uff0c\u624d\u80fd\u5c06\u529f\u6298\u7f6a\u4e86\u3002</p><p>\u5947\u602a\u7684\u662f\uff0c\u9634\u9b42\u57ce\u4eba\u5458\u5728\u51fa\u6d77\u4e4b\u524d\uff0c\u90fd\u4f1a\u643a\u5e26\u7279\u5236\u7684\u9b54\u6cd5\u901a\u8baf\u88c5\u5907\uff0c\u53ef\u4ee5\u968f\u65f6\u548c\u603b\u90e8\u4fdd\u6301\u8054\u7edc\uff0c\u521a\u624d\u5c38\u68c0\u7684\u7ed3\u679c\u8868\u660e\uff0c\u4e09\u4e2a\u6b7b\u8005\u8eab\u4e0a\u7684\u901a\u8baf\u88c5\u5907\u90fd\u5b8c\u597d\u65e0\u635f\u3002\u987b\u77e5\u4ed6\u4eec\u5e76\u975e\u662f\u88ab\u77ac\u95f4\u51fb\u6740\uff0c\u800c\u662f\u88ab\u6162\u6162\u52d2\u6b7b\u7684\uff0c\u5c31\u7b97\u4e0d\u80fd\u9003\u547d\uff0c\u81f3\u5c11\u8be5\u4f20\u56de\u8b66\u8baf\u624d\u662f\uff0c\u9b54\u6cd5\u901a\u8baf\u88c5\u5907\u53c8\u4e0d\u662f\u7535\u8bdd\uff0c\u8fd8\u9700\u8981\u624b\u6307\u6309\u952e\uff0c\u53ea\u8981\u52a8\u5ff5\u5373\u53ef\u542f\u52a8\u2014\u2014\u7136\u800c\u603b\u90e8\u5bf9\u6b64\u4e00\u65e0\u6240\u77e5\u3002</p><p>\u6216\u8005\u8bf4\uff0c\u4ed6\u4eec\u53d1\u51fa\u4e86\u8baf\u606f\uff0c\u53ea\u662f\u88ab\u67d0\u79cd\u529b\u91cf\u5c4f\u853d\u963b\u9694\u4e86\uff1f</p><p>\u201c\u8fd9\u5012\u4e5f\u6709\u53ef\u80fd\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u201c\u51fa\u53d1\u7684\u65f6\u5019\uff0c\u6bbf\u4e0b\u66fe\u7ecf\u63d0\u5230\u8fc7\uff0c\u6df1\u6d77\u4e2d\u6709\u7c7b\u4f3c\u4e8e\u5730\u8109\u8f90\u5c04\u7684\u4e1c\u897f\uff0c\u5bf9\u9884\u8a00\u6cd5\u672f\u6709\u626d\u66f2\u751a\u81f3\u5b8c\u5168\u5c4f\u853d\u7684\u6548\u679c\u3002\u82e5\u975e\u5982\u6b64\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u8fd9\u4e48\u4e45\u90fd\u627e\u4e0d\u5230\u661f\u9668\u57ce\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\u4ed6\u4eec\u662f\u5728\u6df1\u6d77\u4e2d\u88ab\u6740\u7684\uff1f\u201d</p><p>\u201c\u6216\u8bb8\u5427\u3002\u201d</p><p>\u743c\u6069\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u7684\u4e0d\u5b89\u611f\u5374\u8d8a\u6765\u8d8a\u91cd\u3002\u7a81\u7136\u51fa\u73b0\u7684\u5c38\u4f53\uff0c\u4eff\u4f5b\u4e0d\u7965\u7684\u9884\u5146\uff0c\u7ed9\u8fd9\u6b21\u51fa\u6d77\u4e4b\u884c\u7b3c\u7f69\u4e0a\u4e86\u9690\u9690\u7684\u4e4c\u4e91\u3002\u4e09\u4e2a\u9ad8\u9636\u5deb\u5e08\u6b7b\u4ea1\uff0c\u8bf4\u660e\u6d77\u6d0b\u4e16\u754c\u7684\u51f6\u9669\u53ea\u6015\u8d85\u51fa\u9884\u6599\uff0c\u800c\u4ed6\u4eec\u7684\u5c38\u4f53\u4f1a\u88ab\u6070\u597d\u9047\u5230\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u672c\u8eab\u5c31\u900f\u7740\u5341\u5206\u7684\u8be1\u5f02\u2014\u2014\u5927\u6d77\u832b\u832b\uff0c\u8fd9\u79cd\u6982\u7387\u8981\u4f4e\u5230\u4ec0\u4e48\u7a0b\u5ea6\u554a\u3002</p><p>\u4ed6\u5f00\u59cb\u6709\u4e9b\u540e\u6094\u8d77\u6765\uff0c\u53d1\u89c9\u4e0d\u8be5\u4e00\u65f6\u5fc3\u8f6f\uff0c\u540c\u610f\u73ca\u5609\u548c\u8299\u857e\u72c4\u7b49\u4eba\u4e00\u8d77\u51fa\u6d77\u7684\u8981\u6c42\u3002\u5982\u679c\u53ea\u6709\u4ed6\u81ea\u5df1\u7684\u8bdd\uff0c\u5c31\u7b97\u9047\u5230\u5371\u9669\uff0c\u8131\u8eab\u9003\u547d\u7684\u81ea\u4fe1\u603b\u8fd8\u662f\u6709\u7684\uff0c\u52a0\u4e0a\u838e\u73de\u514b\u5c31\u66f4\u4e07\u65e0\u4e00\u5931\u3002\u4f46\u8fd8\u8981\u7167\u987e\u4e09\u4e2a\u5973\u5b69\u5b50\uff0c\u90a3\u5c31\u4e0d\u597d\u8bf4\u4e86\u3002\u81ea\u5df1\u5f53\u65f6\u600e\u4e48\u5c31\u8111\u5b50\u53d1\u6655\uff0c\u7b54\u5e94\u4e86\u5462\u3002\u5982\u4eca\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u5c31\u7b97\u60f3\u56de\u5934\u4e5f\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4ef6\u4ee4\u4ed6\u9887\u4e3a\u90c1\u95f7\u7684\u4e8b\u60c5\uff0c\u4fbf\u662f\u8fd9\u6b7b\u4ea1\u7684\u4e09\u4eba\u4e2d\uff0c\u81ea\u7136\u4e5f\u5305\u62ec\u5f17\u6717\u897f\u65af\u79d1\u3002\u743c\u6069\u548c\u73ca\u5609\u4e4b\u6240\u4ee5\u8981\u6765\u6851\u6bd4\u4e9a\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u627e\u4ed6\uff0c\u5e0c\u671b\u4ece\u4ed6\u8eab\u4e0a\u4e86\u89e3\u5f53\u5e74\u7236\u6bcd\u6b7b\u4ea1\u7684\u771f\u76f8\u3002\u5982\u4eca\u5230\u4e86\u5730\u65b9\uff0c\u4eba\u5374\u6b7b\u4e86\uff0c\u597d\u4e0d\u5bb9\u6613\u83b7\u5f97\u7684\u7ebf\u7d22\u4fbf\u53c8\u65ad\u4e86\u3002</p><p>\u771f\u662f\u2026\u2026\u8ba8\u538c\u554a\u3002</p><p>\u6b63\u90c1\u95f7\u95f4\uff0c\u90a3\u56db\u540d\u5b89\u535a\u7406\u7267\u5e08\u4e2d\u4e3a\u9996\u7684\u4e00\u4e2a\u8d70\u8fc7\u6765\uff0c\u4f5c\u4e3a\u6d77\u6d0b\u751f\u7269\u5b66\u7684\u4e13\u5bb6\uff0c\u4ed6\u4eec\u521a\u624d\u4e5f\u53c2\u4e0e\u4e86\u9a8c\u5c38\u8fc7\u7a0b\u3002\u201c\u6211\u4eec\u53d1\u73b0\u4e86\u8fd9\u4e2a\uff0c\u201d\u7267\u5e08\u5bf9\u62c9\u52a0\u548c\u743c\u6069\u8bf4\uff0c\u644a\u5f00\u624b\uff0c\u638c\u5fc3\u662f\u51e0\u6839\u6697\u7d2b\u8272\u3001\u5e26\u6709\u8840\u7ea2\u6591\u70b9\u7684\u6d77\u8349\uff0c\u201c\u5728\u4ed6\u4eec\u7684\u9886\u53e3\u548c\u9774\u5b50\u91cc\u53d1\u73b0\u7684\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u62c9\u52a0\u95ee\u3002</p><p>\u201c\u6211\u4eec\u53eb\u5b83\u9ca8\u8840\u8349\uff0c\u201d\u5b89\u535a\u7406\u7267\u5e08\u8bf4\uff0c\u201c\u5f88\u7f55\u89c1\uff0c\u53ea\u80fd\u751f\u957f\u5728\u67d0\u4e9b\u7279\u5b9a\u7684\u6d77\u57df\u3002\u8fd9\u79cd\u5730\u65b9\uff0c\u6574\u4e2a\u5760\u661f\u6d77\u4e2d\u4e5f\u53ea\u6709\u5341\u6765\u5904\uff0c\u800c\u9644\u8fd1\u6070\u597d\u5c31\u6709\u4e00\u5904\u3002\u201d</p><p>\u4e5f\u5373\u662f\u8bf4\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u9047\u5bb3\u7684\u73b0\u573a\u4e86\u3002</p><p>\u201c\u8fd9\u79cd\u5730\u65b9\u6d77\u517d\u5f88\u591a\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6070\u6070\u76f8\u53cd\uff0c\u9ca8\u8840\u8349\u5bf9\u51e0\u4e4e\u6240\u6709\u7684\u6d77\u517d\uff0c\u5c24\u5176\u662f\u4e4c\u8d3c\u4e4b\u7c7b\u7684\u8f6f\u4f53\u52a8\u7269\u6709\u683c\u5916\u5f3a\u7684\u9a71\u9010\u6548\u679c\uff0c\u201d\u5b89\u535a\u7406\u7267\u5e08\u8bf4\uff0c\u201c\u6240\u4ee5\u8fd9\u4ef6\u4e8b\u60c5\u786e\u5b9e\u5f88\u5947\u602a\u3002\u201d</p><p>\u201c\u53bb\u770b\u770b\u3002\u201d\u62c9\u52a0\u6c89\u541f\u7247\u523b\uff0c\u505a\u51fa\u4e86\u51b3\u5b9a\u3002</p><p>\u5176\u4ed6\u4eba\u522b\u65e0\u5f02\u8bae\uff0c\u4e8e\u662f\u6d77\u8239\u5728\u5b89\u535a\u7406\u7267\u5e08\u7684\u6307\u5f15\u4e0b\u8f6c\u5411\uff0c\u671d\u7740\u751f\u957f\u9ca8\u8840\u8349\u7684\u90a3\u7247\u6d77\u57df\u524d\u8fdb\u3002\u4e00\u5c0f\u65f6\u4e4b\u540e\uff0c\u4ed6\u4eec\u62b5\u8fbe\u4e86\u76ee\u7684\u5730\u3002</p><p>\u201c\u9ca8\u8840\u8349\u751f\u957f\u5728\u6d77\u5e95\uff0c\u4ed6\u4eec\u5e94\u8be5\u662f\u5728\u6d77\u5e95\u9047\u5bb3\u7684\uff0c\u201d\u5b89\u535a\u7406\u7267\u5e08\u8bf4\uff0c\u201c\u6211\u4eec\u5f97\u4e0b\u6d77\u3002\u201d</p><p>\u4e0b\u6d77\u548c\u5728\u6d77\u4e0a\u4e58\u8239\u65c5\u884c\uff0c\u90a3\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\uff0c\u96be\u5ea6\u66f4\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\uff0c\u597d\u5728\u51fa\u53d1\u524d\u4fbf\u5df2\u7ecf\u9884\u6599\u5230\u8fd9\u79cd\u60c5\u5f62\uff0c\u65e9\u6709\u5145\u8db3\u51c6\u5907\u3002\u56db\u540d\u5b89\u535a\u7406\u7267\u5e08\u540c\u65f6\u4e3e\u8d77\u4e09\u53c9\u621f\u65bd\u6cd5\uff0c\u7ed9\u9634\u9b42\u57ce\u7684\u6bcf\u4e2a\u4eba\u90fd\u53e0\u52a0\u4e86\u201c\u6c34\u4e2d\u547c\u5438\u201d\u548c\u201c\u6c34\u4e0b\u6f2b\u6b65\u201d\u4e24\u9053\u6cd5\u672f\uff0c\u8ba9\u4ed6\u4eec\u80fd\u591f\u50cf\u9c7c\u4e00\u6837\u5728\u6c34\u4e2d\u547c\u5438\u5e76\u4e14\u81ea\u7531\u884c\u52a8\uff0c\u81f3\u4e8e\u4ed6\u4eec\u81ea\u5df1\u5012\u662f\u4e0d\u9700\u8981\uff0c\u5b89\u535a\u7406\u5973\u795e\u7684\u5e87\u4f51\uff0c\u65e9\u5c31\u8ba9\u4ed6\u4eec\u628a\u6d77\u6d0b\u5f53\u6210\u4e86\u968f\u610f\u8fdb\u51fa\u7684\u540e\u82b1\u56ed\u3002</p><p>\u9664\u4e86\u5b89\u535a\u7406\u7267\u5e08\u4e4b\u5916\uff0c\u8fd8\u6709\u4e2a\u4eba\u4e0d\u9700\u8981\u8fd9\u4e9b\u6cd5\u672f\uff0c\u4fbf\u662f\u73ca\u5609\u3002\u5979\u81ea\u7136\u6ca1\u6709\u6d77\u6d0b\u5973\u795e\u7684\u5e87\u4f51\uff0c\u5374\u6709\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8d60\u9001\u7684\u6597\u7bf7\u3002\u8bf4\u6765\u4e5f\u771f\u662f\u5947\u602a\uff0c\u8fd9\u6597\u7bf7\u662f\u9634\u9b42\u57ce\u4e3b\u9001\u7ed9\u513f\u5b50\u7684\u793c\u7269\uff0c\u4e0a\u9762\u5374\u9664\u4e86\u9644\u7740\u5927\u91cf\u9632\u5fa1\u3001\u5f3a\u5316\u9b54\u6cd5\u4e4b\u5916\uff0c\u8fd8\u6052\u5b9a\u4e86\u4e00\u4e2a\u201c\u6d77\u6d0b\u4e4b\u738b\u201d\uff0c\u8fd9\u662f\u79cd\u975e\u5e38\u5389\u5bb3\uff0c\u6216\u8005\u8bf4\u662f\u6781\u5176\u4e86\u4e0d\u8d77\u7684\u53d8\u5316\u5b66\u6d3e\u6cd5\u672f\uff0c\u8ba9\u4eba\u81ea\u52a8\u83b7\u5f97\u4e00\u7cfb\u5217\u4e0e\u6c34\u6709\u5173\u7684\u72b6\u6001\u80fd\u529b\uff0c\u4ec0\u4e48\u6c34\u4e2d\u547c\u5438\u3001\u6c34\u9762\u884c\u8d70\u3001\u6c34\u4e0b\u6f2b\u6b65\u3001\u6c34\u5e95\u89c6\u89c9\u3001\u6e38\u6cf3\u52a0\u901f\u3001\u62b5\u6297\u6c34\u538b\u7b49\u7b49\uff0c\u4e00\u6982\u56ca\u62ec\u6db5\u76d6\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u540c\u65f6\u53ea\u8981\u53d7\u672f\u8005\u8eab\u5728\u6c34\u4e2d\uff0c\u4fbf\u80fd\u591f\u81ea\u7531\u65bd\u5c55\u64cd\u63a7\u6c34\u4f4d\u3001\u6f29\u6da1\u672f\u3001\u6c34\u9762\u98d3\u98ce\u7b49\u5f3a\u529b\u6cd5\u672f\uff0c\u54ea\u6015\u4f60\u538b\u6839\u6ca1\u5b66\u8fc7\u3002</p><p>\u96be\u9053\u8bf4\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5c0f\u65f6\u5019\u5f88\u559c\u6b22\u6e38\u6cf3\u4e48\uff0c\u5426\u5219\u57ce\u4e3b\u5927\u4eba\u5728\u6597\u7bf7\u4e0a\u6052\u5b9a\u8fd9\u6cd5\u672f\u505a\u4ec0\u4e48\u2026\u2026</p><p>\u4e0d\u7ba1\u5982\u4f55\uff0c\u53cd\u6b63\u5165\u6d77\u4e4b\u540e\uff0c\u501f\u52a9\u6597\u7bf7\u7684\u5e2e\u52a9\uff0c\u73ca\u5609\u5f53\u771f\u662f\u5982\u9c7c\u5f97\u6c34\u2014\u2014\u6216\u8005\u8bf4\u5979\u5176\u5b9e\u5c31\u662f\u4e00\u6761\u707f\u70c2\u593a\u76ee\u7684\u7f8e\u4eba\u9c7c\uff0c\u5176\u4ed6\u4eba\u90fd\u65e0\u5f62\u95f4\u4eff\u4f5b\u6210\u4e86\u966a\u886c\u3002\u4e24\u540d\u5b89\u535a\u7406\u7267\u5e08\u5728\u961f\u4f0d\u7684\u6700\u524d\u65b9\u624b\u6301\u4e09\u53c9\u621f\u5f00\u8def\uff0c\u53e6\u5916\u4e24\u540d\u7267\u5e08\u5206\u522b\u5b88\u62a4\u7740\u5de6\u53f3\u4e24\u4fa7\uff0c\u9632\u6b62\u6709\u6d77\u517d\u88ad\u51fb\u3002\u968f\u7740\u9010\u6e10\u4e0b\u6f5c\uff0c\u5149\u7ebf\u4e5f\u8d8a\u6765\u8d8a\u6697\uff0c\u6700\u7ec8\u5df2\u7ecf\u662f\u5b8c\u5168\u6f06\u9ed1\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u62c9\u52a0\u4ece\u8170\u56ca\u4e2d\u6478\u51fa\u4e00\u7c92\u73bb\u7483\u73e0\u5b50\u5f39\u51fa\uff0c\u5316\u4f5c\u4e00\u4e2a\u7bee\u7403\u5927\u5c0f\u7684\u6a58\u7ea2\u8272\u5149\u56e2\uff0c\u4e0d\u5373\u4e0d\u79bb\u5730\u60ac\u6d6e\u5728\u4f17\u4eba\u5934\u9876\uff0c\u5c06\u5468\u906d\u4e94\u5341\u5c3a\u5185\u7167\u5f97\u660e\u6643\u6643\u4e00\u7247\uff0c\u4eff\u4f5b\u767d\u663c\u3002</p><p>\u4e00\u8def\u4e0a\u5927\u5bb6\u51e0\u4e4e\u90fd\u4e0d\u8bf4\u8bdd\uff0c\u9759\u6084\u6084\u7684\uff0c\u6c89\u95f7\u7684\u6c14\u6c1b\u8ba9\u73ca\u5609\u7b49\u5973\u5b69\u5b50\u4e5f\u53d7\u5230\u4e86\u611f\u67d3\uff0c\u95ed\u53e3\u4e0d\u8bed\u3002\u5176\u95f4\u906d\u9047\u4e86\u4e00\u4e9b\u6d77\u517d\uff0c\u5982\u6d77\u86c7\u3001\u7ae0\u9c7c\u4e4b\u7c7b\uff0c\u5168\u90fd\u88ab\u5b89\u535a\u7406\u7267\u5e08\u6301\u4e09\u53c9\u621f\u76f4\u63a5\u9a71\u9000\u4e86\u3002\u800c\u4e14\u8d8a\u5f80\u6df1\u5904\uff0c\u6d77\u517d\u8d8a\u5c11\uff0c\u8fde\u9c7c\u7c7b\u90fd\u6e10\u6e10\u770b\u4e0d\u89c1\uff0c\u5b89\u535a\u7406\u7267\u5e08\u8bf4\u8fd9\u662f\u56e0\u4e3a\u9ca8\u8840\u8349\u7684\u4f5c\u7528\uff0c\u8bf4\u660e\u8ddd\u79bb\u6d77\u5e95\u8d8a\u6765\u8d8a\u8fd1\u3002</p><p>\u501f\u52a9\u9b54\u6cd5\u7684\u5e2e\u52a9\uff0c\u4ed6\u4eec\u4e0b\u964d\u7684\u901f\u5ea6\u975e\u5e38\u5feb\uff0c\u4eff\u4f5b\u662f\u5750\u76f4\u5347\u7535\u68af\uff0c\u5927\u7ea6\u53ea\u82b1\u4e86\u4e24\u4e2a\u5c0f\u65f6\u4fbf\u5df2\u7ecf\u770b\u89c1\u6d77\u5e95\u3002\u6697\u7d2b\u8272\u7684\u6d77\u8349\u7ef5\u7ef5\u5bc6\u5bc6\u751f\u957f\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u4eff\u4f5b\u5728\u6d77\u5e95\u94fa\u4e0a\u4e86\u4e00\u5c42\u539a\u539a\u7684\u5730\u6bef\uff0c\u661f\u661f\u95ea\u95ea\u7684\u8840\u6591\u70b9\u7f00\u5176\u95f4\uff0c\u968f\u7740\u6ce2\u6d6a\u8d77\u4f0f\u6f02\u79fb\uff0c\u6b64\u5916\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u3002</p><p>\u201c\u770b\u4f60\u4eec\u4e86\uff0c\u201d\u4e3a\u9996\u7684\u5b89\u535a\u7406\u7267\u5e08\u5bf9\u62c9\u52a0\u8bf4\uff0c\u201c\u6211\u4eec\u80fd\u505a\u7684\u5230\u6b64\u4e3a\u6b62\u3002\u201d</p><p>\u62c9\u52a0\u70b9\u70b9\u5934\uff0c\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u4ed6\u8eab\u540e\u7684\u4e00\u4f4d\u9ed1\u888d\u5deb\u5e08\u540e\u9000\u4e24\u6b65\uff0c\u6597\u7bf7\u81ea\u52a8\u6380\u8d77\uff0c\u516d\u4e2a\u94f6\u8272\u5706\u76d8\u4ece\u4e2d\u98de\u51fa\uff0c\u5728\u8eab\u5468\u73af\u7ed5\u65cb\u8f6c\uff0c\u5176\u4e0a\u5149\u8292\u95ea\u70c1\uff0c\u541e\u5410\u4e0d\u5b9a\u3002\u5deb\u5e08\u624b\u4e2d\u63e1\u7740\u4ece\u6b7b\u8005\u8eab\u4e0a\u53d1\u73b0\u7684\u90a3\u51e0\u6839\u9ca8\u8840\u8349\uff0c\u95ed\u76ee\u9ed8\u8bf5\u5492\u6587\u3002\u8fc7\u4e86\u5341\u591a\u79d2\u79cd\uff0c\u7a81\u7136\u6709\u4e24\u53ea\u94f6\u76d8\u4e0a\u5149\u8292\u8f70\u7136\u7efd\u653e\uff0c\u800c\u5176\u4ed6\u4e09\u53ea\u94f6\u76d8\u540c\u65f6\u90fd\u9eef\u6de1\u4e0b\u6765\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u62c9\u52a0\u76b1\u7709\uff0c\u201c\u600e\u4e48\u4f1a\u4e24\u4e2a\u65b9\u5411\uff1f\u201d</p><p>\u201c\u5e72\u6270\u5f88\u5f3a\uff0c\u201d\u9ed1\u888d\u5deb\u5e08\u72fc\u72c8\u5730\u56de\u7b54\uff0c\u4ed6\u6ee1\u5934\u5927\u6c57\uff0c\u8138\u8272\u82cd\u767d\u5982\u7eb8\uff0c\u50cf\u662f\u521a\u521a\u8dd1\u5b8c\u4e00\u573a\u9a6c\u62c9\u677e\uff0c\u201c\u6211\u6ca1\u529e\u6cd5\u786e\u5b9a\u2026\u2026\u4f46\u5e94\u8be5\u5c31\u662f\u8fd9\u4e24\u4e2a\u65b9\u5411\u4e2d\u7684\u4e00\u4e2a\u3002\u201d</p><p>\u62c9\u52a0\u55ef\u4e86\u4e00\u58f0\uff0c\u8fd9\u540d\u9ed1\u888d\u5deb\u5e08\u662f\u4ed6\u7684\u5b66\u751f\uff0c\u4f5c\u4e3a\u5deb\u5e08\u800c\u8a00\u6574\u4f53\u80fd\u529b\u4e0d\u592a\u5408\u683c\uff0c\u4f46\u5728\u201c\u5b9a\u4f4d\u201d\u65b9\u9762\u5374\u6709\u7740\u6770\u51fa\u7684\u5929\u8d4b\uff0c\u8fde\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u90fd\u8d5e\u8bb8\u8fc7\u7684\u3002\u867d\u7136\u56e0\u4e3a\u6d77\u5e95\u8f90\u5c04\u7684\u5e72\u6270\uff0c\u9884\u8a00\u6cd5\u672f\u6ca1\u80fd\u83b7\u5f97\u6700\u4f73\u6548\u679c\uff0c\u4f46\u81f3\u5c11\u628a\u53ef\u80fd\u7684\u9009\u9879\u7f29\u51cf\u5230\u4e86\u4e24\u4e2a\uff0c\u603b\u6bd4\u6f2b\u65e0\u5934\u7eea\u597d\u5f97\u591a\u4e86\u3002</p><p>\u201c\u90a3\u5c31\u5148\u8d70\u8fd9\u8fb9\u8bd5\u8bd5\u770b\u5427\u3002\u201d</p><p>\u62c9\u52a0\u968f\u610f\u9009\u4e86\u5176\u4e2d\u4e00\u4e2a\u65b9\u5411\uff0c\u7136\u540e\u51fa\u53d1\u3002\u743c\u6069\u8ddf\u968f\u5728\u540e\uff0c\u838e\u73de\u514b\u7a81\u7136\u5feb\u884c\u51e0\u6b65\uff0c\u6084\u6084\u8d34\u5230\u8eab\u8fb9\uff0c\u201c\u6709\u95ee\u9898\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u521a\u624d\u62c9\u52a0\u9009\u62e9\u65b9\u5411\u7684\u65f6\u5019\uff0c\u90a3\u4e2a\u5bb6\u4f19\u7a81\u7136\u8be1\u79d8\u5730\u7b11\u4e86\u4e00\u7b11\u3002\u201d</p><p>\u9b45\u9b54\u6240\u8bf4\u7684\u201c\u90a3\u4e2a\u5bb6\u4f19\u201d\uff0c\u5c31\u662f\u6b64\u524d\u548c\u743c\u6069\u64e6\u80a9\u800c\u8fc7\uff0c\u7206\u51fa\u7535\u5f27\u7684\u90a3\u4f4d\u5b89\u535a\u7406\u7267\u5e08\u3002\u743c\u6069\u5bf9\u4ed6\u9887\u5177\u6212\u5fc3\uff0c\u6240\u4ee5\u6697\u4e2d\u547d\u4ee4\u838e\u73de\u514b\u76d1\u89c6\uff0c\u5982\u4eca\u770b\u6765\uff0c\u679c\u7136\u662f\u6709\u9b3c\u3002</p><p>\u83ab\u975e\u8fd9\u6761\u8def\u4e0a\u6709\u9677\u9631\uff1f\u6709\u57cb\u4f0f\uff1f</p><p>\u743c\u6069\u63d0\u9ad8\u8b66\u60d5\uff0c\u5c06\u6ce8\u610f\u529b\u96c6\u4e2d\u5728\u90a3\u540d\u5b89\u535a\u7406\u7267\u5e08\u8eab\u4e0a\uff0c\u85cf\u5728\u6597\u7bf7\u4e2d\u7684\u53f3\u624b\u6084\u6084\u63e1\u7d27\u4e86\u7d2b\u6c34\u6676\u6cd5\u6756\u3002\u838e\u73de\u514b\u66f4\u662f\u6709\u610f\u65e0\u610f\uff0c\u59cb\u7ec8\u4e0d\u79bb\u76ee\u6807\u8eab\u540e\uff0c\u968f\u65f6\u51c6\u5907\u7ed9\u4ed6\u4e00\u5251\u3002\u7136\u800c\u4e8b\u5b9e\u4f3c\u4e4e\u548c\u743c\u6069\u7684\u9884\u6599\u5b8c\u5168\u76f8\u53cd\uff0c\u4e00\u8def\u4e0a\u522b\u8bf4\u9677\u9631\u57cb\u4f0f\uff0c\u5c31\u8fde\u6d77\u86c7\u90fd\u6ca1\u9047\u5230\u534a\u6761\uff0c\u592a\u5e73\u65e0\u4e8b\uff0c\u5b89\u5b89\u9759\u9759\u3002</p><p>\u5947\u602a\u554a\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u5600\u5495\u7740\uff0c\u53d1\u73b0\u961f\u4f0d\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u7d27\u63a5\u7740\u4fbf\u662f\u4e00\u9635\u9a9a\u52a8\uff0c\u4ed6\u6b63\u5947\u602a\uff0c\u4e0d\u77e5\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4fbf\u542c\u5230\u524d\u9762\u5deb\u5e08\u53d1\u51fa\u7684\u4f4e\u4f4e\u60ca\u547c\u3002</p><p>\u201c\u661f\u9668\u57ce\uff01\u201d</p><p>\u743c\u6069\u8ba9\u8eab\u4f53\u5347\u9ad8\uff0c\u7136\u540e\u4ed6\u770b\u5230\u4e86\u4e00\u4e2a\u5e9e\u5927\u5230\u65e0\u6cd5\u60f3\u50cf\u7684\u900f\u660e\u7403\u4f53\uff0c\u4eff\u4f5b\u67d0\u79cd\u6cd5\u672f\u62a4\u7f69\u3002\u5728\u7403\u4f53\u4e2d\uff0c\u5012\u5706\u9525\u5f62\u7684\u9ed1\u8272\u5c71\u5cf0\u4e0a\uff0c\u4e00\u5ea7\u7389\u767d\u8272\u7684\u6d6e\u7a7a\u57ce\u6b63\u9759\u9759\u8e72\u8e1e\uff0c\u4ee5\u6781\u7f13\u6162\u7684\u901f\u5ea6\u987a\u65f6\u9488\u65b9\u5411\u8f6c\u52a8\u7740\u3002\u5728\u57ce\u5e02\u7684\u4e2d\u592e\uff0c\u4e00\u5ea7\u87ba\u65cb\u5f62\u7684\u9ad8\u5854\u6c14\u52bf\u903c\u4eba\u5730\u7a81\u5140\u77d7\u7acb\uff0c\u4eff\u4f5b\u9510\u5229\u7684\u5c16\u9488\u523a\u7834\u5929\u7a79\uff0c\u4fef\u77b0\u5168\u57ce\u3002\u743c\u6069\u4e0b\u6d77\u4e4b\u524d\u5c31\u5f00\u542f\u4e86\u5965\u672f\u89c6\u89c9\uff0c\u6b64\u65f6\u5728\u4ed6\u773c\u4e2d\uff0c\u6210\u5343\u4e0a\u4e07\u679a\u7480\u74a8\u751f\u8f89\u7684\u9b54\u6cd5\u7b26\u6587\u4eff\u4f5b\u6c34\u73e0\u822c\u81ea\u87ba\u65cb\u9ad8\u5854\u7684\u9876\u7aef\u6c69\u6c69\u6d8c\u51fa\uff0c\u81ea\u52a8\u6c47\u805a\uff0c\u6392\u5217\u6210\u4e03\u6761\u957f\u957f\u7684\u3001\u5149\u8292\u56db\u5c04\u7684\u5f69\u8272\u7f0e\u5e26\uff0c\u4ea4\u9519\u73af\u7ed5\u5305\u88f9\u7740\u6d6e\u7a7a\u57ce\u3002</p><p>\u8fd9\u662f\u8ff7\u9501\u3002</p><p>\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u51e0\u6b21\uff0c\u743c\u6069\u5bf9\u8fd9\u79cd\u7cbe\u7075\u65cf\u53d1\u660e\u7684\u8d85\u7ea7\u9b54\u6cd5\u9635\u5e76\u4e0d\u964c\u751f\uff0c\u4e00\u773c\u4fbf\u8ba4\u4e86\u51fa\u6765\u3002\u800c\u8ff7\u9501\u7684\u5b58\u5728\uff0c\u4ee5\u53ca\u57ce\u5e02\u4f9d\u7136\u8fd8\u80fd\u4fdd\u6301\u6d6e\u7a7a\u72b6\u6001\u7684\u4e8b\u5b9e\uff0c\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u5728\u573a\u7684\u9634\u9b42\u57ce\u5deb\u5e08\uff0c\u4eba\u4eba\u90fd\u4e00\u6e05\u4e8c\u695a\u3002</p><p>\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u7684\u5bc6\u745f\u80fd\u6838\u4f9d\u7136\u5b8c\u597d\u65e0\u635f\uff01</p><p>\u9634\u9b42\u57ce\u6b64\u6b21\u6253\u635e\u661f\u9668\u57ce\uff0c\u76ee\u7684\u662f\u5bfb\u5b9d\uff0c\u800c\u6240\u6709\u7684\u5b9d\u7269\u4e2d\uff0c\u771f\u6b63\u5fd7\u5728\u5fc5\u5f97\u7684\u4fbf\u662f\u5bc6\u745f\u80fd\u6838\u3002\u5965\u6c83\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u4f0a\u5965\u52d2\u59c6\u4e4b\u6240\u4ee5\u4f1a\u88ab\u5c0a\u79f0\u4e3a\u201c\u8010\u745f\u4e4b\u7236\u201d\uff0c\u5c31\u5728\u4e8e\u4ed6\u53d1\u660e\u4e86\u5bc6\u745f\u80fd\u6838\u3002\u8fd9\u79cd\u795e\u5947\u65e0\u6bd4\u7684\u9b54\u6cd5\u88c5\u7f6e\u80fd\u591f\u81ea\u52a8\u4ece\u9b54\u7f51\u7684\u201c\u6e90\u201d\u4e2d\u6c72\u53d6\u80fd\u91cf\uff0c\u865a\u62df\u51fa\u5c0f\u8303\u56f4\u7684\u7b2c\u4e8c\u91cd\u9b54\u7f51\uff0c\u8ba9\u9b54\u6cd5\u53d8\u5f97\u7b80\u6613\uff0c\u4ece\u5c11\u6570\u5929\u624d\u624d\u80fd\u638c\u63e1\u8fd0\u7528\u7684\u79d8\u827a\uff0c\u53d8\u6210\u4e86\u4eba\u4eba\u53ef\u7528\u7684\u5de5\u5177\uff0c\u4ece\u800c\u53d1\u5c55\u51fa\u4e86\u8010\u745f\u745e\u5c14\u8f89\u714c\u7684\u9b54\u6cd5\u6587\u660e\u3002\u800c\u5bf9\u4e8e\u6d6e\u7a7a\u57ce\u6765\u8bf4\uff0c\u6709\u4e86\u5bc6\u745f\u80fd\u6838\uff0c\u624d\u53ef\u80fd\u98de\u4e0a\u4e91\u7aef\uff0c\u624d\u53ef\u80fd\u7ef4\u6301\u8ff7\u9501\uff0c\u624d\u53ef\u80fd\u771f\u6b63\u5730\u7ff1\u7fd4\u5929\u7a79\uff0c\u4fef\u77b0\u5927\u5730\u3002</p><p>\u56e0\u4e3a\u8010\u745f\u5377\u8f74\u7684\u6563\u4f5a\uff0c\u4ee5\u53ca\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u5c01\u95ed\u4e86\u9b54\u7f51\u7684\u201c\u6e90\u201d\uff0c\u73b0\u5b58\u7684\u5927\u5965\u672f\u5e08\u4eec\u65e0\u6cd5\u518d\u5ea6\u5236\u9020\u51fa\u5bc6\u745f\u80fd\u6838\u3002\u4f46\u5965\u6c83\u4e5f\u66fe\u7ecf\u63d0\u5230\u8fc7\uff0c\u591c\u5973\u58eb\u838e\u5c14\u6084\u6084\u521b\u9020\u4e86\u9b54\u7f51\u7684\u4eff\u5236\u54c1\uff08\u5f71\u9b54\u7f51\uff09\uff0c\u62e5\u6709\u865a\u62df\u7684\u201c\u6e90\u201d\uff0c\u7528\u4ee5\u521b\u9020\u65b0\u7684\u5bc6\u745f\u80fd\u6838\u4e0d\u8db3\uff0c\u4f46\u7528\u6765\u652f\u6301\u73b0\u6709\u7684\u5bc6\u745f\u80fd\u6838\u5374\u662f\u80fd\u529e\u5230\u7684\u3002\u9634\u9b42\u57ce\u4e4b\u6240\u4ee5\u8fd8\u80fd\u591f\u6d6e\u7a7a\u98de\u884c\uff0c\u4fbf\u5728\u4e8e\u5b83\u7684\u5bc6\u745f\u80fd\u6838\uff0c\u8054\u7ed3\u7684\u662f\u5f71\u9b54\u7f51\u800c\u975e\u9b54\u7f51\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u9634\u9b42\u57ce\u53ea\u8981\u80fd\u627e\u5230\u5b8c\u597d\u65e0\u635f\u7684\u5bc6\u745f\u80fd\u6838\uff0c\u5c31\u80fd\u591f\u518d\u5efa\u4e00\u5ea7\u6d6e\u7a7a\u57ce\u3002</p><p>\u800c\u73b0\u5728\u662f\u76f4\u63a5\u627e\u5230\u4e86\u4e00\u5ea7\u5b8c\u597d\u7684\u6d6e\u7a7a\u57ce\u3002</p><p>\u6240\u6709\u7684\u9634\u9b42\u57ce\u5deb\u5e08\u90fd\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u6ca1\u60f3\u5230\u82e6\u82e6\u641c\u5bfb\u59cb\u7ec8\u4e0d\u89c1\u7684\u76ee\u6807\uff0c\u5374\u8fd9\u6837\u8f7b\u6613\u5730\u5c31\u51fa\u73b0\u5728\u9762\u524d\uff0c\u7b80\u76f4\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\u3002\u5c31\u8fde\u4e3a\u9996\u7684\u62c9\u52a0\uff0c\u60f3\u5230\u4eca\u65e5\u7acb\u6b64\u5927\u529f\uff0c\u56de\u57ce\u540e\u5fc5\u5b9a\u4f1a\u83b7\u5f97\u4e30\u539a\u5609\u5956\uff0c\u4e00\u5411\u6b7b\u677f\u7740\u7684\u8138\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u7b11\u5bb9\u6765\u3002\u4f46\u4ed6\u968f\u5373\u7b11\u610f\u4e00\u655b\uff0c\u773c\u4e2d\u5bd2\u5149\u6bd5\u9732\uff0c\u4f4e\u4f4e\u559d\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u52a8\u624b\uff01\u201d</p></article>"}}}]);
