(window.webpackJsonp=window.webpackJsonp||[]).push([[1665],{1688:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e03\u8282 \u4e11\u5973\u4eba",content:"<article><h2>\u7b2c\u56db\u5341\u4e03\u8282 \u4e11\u5973\u4eba</h2><p>\u5f53\u62c9\u52a0\u53d1\u51fa\u7b2c\u4e00\u4e2a\u97f3\u8282\u65f6\uff0c\u4e00\u7c92\u9ed1\u73cd\u73e0\u5728\u4ed6\u7684\u53f3\u624b\u638c\u5fc3\u88ab\u6084\u7136\u634f\u788e\uff0c\u7ec6\u7c89\u7c0c\u7c0c\u76f4\u4e0b\u3002\u9634\u9b42\u57ce\u8bf8\u4eba\u7684\u811a\u4e0b\u5239\u90a3\u95f4\u5149\u8292\u7206\u8d77\uff0c\u5341\u4e8c\u540d\u5deb\u5e08\u548c\u56db\u4e2a\u5973\u5b69\u5b50\u7684\u8eab\u4f53\u540c\u65f6\u53d8\u6210\u4e86\u534a\u900f\u660e\u72b6\uff0c\u770b\u8d77\u6765\u5c31\u662f\u7531\u6c34\u6784\u6210\u4f3c\u7684\u3002\u8fd9\u662f\u51fa\u53d1\u4e4b\u524d\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u4ed6\u7684\u79d8\u5bc6\u6b66\u5668\uff0c\u80fd\u591f\u8ba9\u4e00\u5b9a\u8303\u56f4\u5185\u6240\u6709\u6307\u5b9a\u5bf9\u8c61\uff08\u6570\u91cf\u4e0d\u8d85\u8fc7\u4e09\u5341\u4eba\uff09\u540c\u65f6\u88ab\u201c\u6d77\u4e4b\u62a4\u58c1\u201d\u6cd5\u672f\u6240\u7b3c\u7f69\u3002\u8fd9\u4e2a\u6cd5\u672f\u80fd\u4f7f\u53d7\u672f\u8005\u8eab\u4f53\u53d8\u5f97\u9002\u5408\u5728\u6c34\u4e2d\u6d3b\u52a8\uff0c\u80fd\u50cf\u9c7c\u4e00\u6837\u8fc7\u6ee4\u7a7a\u6c14\uff0c\u627f\u53d7\u6d77\u6c34\u7684\u4f4e\u6e29\u548c\u538b\u5f3a\uff0c\u800c\u4e14\u4f5c\u7528\u65f6\u95f4\u6781\u957f\uff0c\u9664\u975e\u4e2d\u9014\u88ab\u89e3\u6d88\uff0c\u5426\u5219\u80fd\u591f\u4e00\u76f4\u7ef4\u6301\u4e8c\u5341\u56db\u4e2a\u5c0f\u65f6\u3002\u6b63\u662f\u51ed\u501f\u7740\u5b83\uff0c\u62c9\u52a0\u6240\u4ee5\u6709\u6043\u65e0\u6050\uff0c\u6562\u5728\u8fd9\u6df1\u6d77\u4e0b\u548c\u5b89\u535a\u91cc\u7267\u5e08\u7ffb\u8138\u52a8\u624b\u3002</p><p>\u5176\u4f59\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u81ea\u7136\u4e5f\u65e9\u5c31\u505a\u597d\u51c6\u5907\uff0c\u201c\u52a8\u624b\u201d\u7684\u8bdd\u97f3\u672a\u843d\uff0c\u516b\u9053\u51f6\u72e0\u7684\u6740\u622e\u5492\u6cd5\u5df2\u7ecf\u540c\u65f6\u51fb\u4e2d\u4e86\u56db\u540d\u5b89\u535a\u91cc\u7267\u5e08\uff0c\u5e73\u5747\u6bcf\u4eba\u627f\u53d7\u4e86\u4e24\u9053\u3002\u9ad8\u9636\u5deb\u5e08\u53d1\u51fa\u7684\u84c4\u610f\u5077\u88ad\uff0c\u5a01\u529b\u786e\u5b9e\u975e\u540c\u5c0f\u53ef\uff0c\u4f55\u51b5\u6570\u91cf\u4e0a\u53c8\u5448\u73b0\u538b\u5012\u6027\u7684\u4f18\u52bf\u3002\u5176\u4e2d\u4e24\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u8fde\u53eb\u90fd\u6ca1\u53eb\u51fa\u4e00\u58f0\uff0c\u4fbf\u5df2\u7ecf\u88ab\u65e0\u5f62\u7684\u950b\u5229\u5200\u950b\u5237\u5237\u5207\u6210\u4e86\u5341\u51e0\u5757\uff0c\u9c9c\u8840\u987f\u65f6\u5c06\u6d77\u6c34\u67d3\u5f97\u7ea2\u901a\u901a\u4e00\u7247\u3002\u7b2c\u4e09\u4e2a\u5b89\u535a\u91cc\u7267\u5e08\u8fd0\u6c14\u7a0d\u597d\uff0c\u8fde\u625b\u4e24\u9053\u6cd5\u672f\u5c45\u7136\u8fd8\u672a\u6b7b\uff0c\u4ed6\u6012\u543c\u4e00\u58f0\uff0c\u6325\u52a8\u4e09\u53c9\u621f\u6b63\u8981\u53d1\u52a8\u62fc\u6b7b\u53cd\u51fb\uff0c\u4e00\u9053\u6697\u7070\u8272\u7684\u95ea\u7535\u675f\u4ece\u62c9\u52a0\u6307\u5c16\u5c04\u51fa\uff0c\u5c06\u4ed6\u77ac\u95f4\u53d8\u6210\u4e86\u7126\u70ad\u3002</p><p>\u4f46\u7b2c\u56db\u540d\u5b89\u535a\u91cc\u7267\u5e08\u9003\u8131\u4e86\u3002</p><p>\u4eff\u4f5b\u662f\u5bf9\u9634\u9b42\u57ce\u7684\u7ffb\u8138\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u62c9\u52a0\u521a\u521a\u559d\u4ee4\u653b\u51fb\uff0c\u4ed6\u4fbf\u540c\u65f6\u4e5f\u542f\u52a8\u4e86\u9632\u5fa1\uff0c\u4e00\u9053\u624b\u81c2\u7c97\u7ec6\u7684\u8000\u773c\u94f6\u8272\u95ea\u7535\u81ea\u4ed6\u7709\u5fc3\u8ff8\u51fa\uff0c\u77eb\u592d\u82e5\u9f99\uff0c\u6025\u901f\u76d8\u531d\uff0c\u5316\u4f5c\u4e00\u4ef6\u7cbe\u5149\u95ea\u95ea\u7684\u5168\u8eab\u94e0\u7532\u5c06\u7267\u5e08\u7262\u7262\u5305\u88f9\u3002\u4e00\u67c4\u9aa8\u77db\u548c\u4e94\u9897\u8d64\u7ea2\u98de\u5f39\u63a5\u8e35\u5c04\u6765\uff0c\u649e\u4e0a\u4e86\u95ea\u7535\u94e0\u7532\uff0c\u524d\u8005\u77ac\u95f4\u5316\u4f5c\u767d\u8272\u9aa8\u7c89\u6563\u53bb\uff0c\u540e\u8005\u5219\u88ab\u5b8c\u5168\u5438\u6536\uff0c\u6ca1\u6709\u5bf9\u5b89\u535a\u91cc\u7267\u5e08\u9020\u6210\u4efb\u4f55\u4f24\u5bb3\u3002</p><p>\u201c\u6cd5\u672f\u589e\u5e45\u2014\u2014\u89e3\u79bb\uff01\u201d</p><p>\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u4f9d\u6b21\u4eae\u8d77\uff0c\u9b54\u6cd5\u80fd\u91cf\u8282\u8282\u4f20\u9012\uff0c\u9010\u6b65\u5f3a\u5316\uff0c\u5728\u7d2b\u6c34\u6676\u6cd5\u6756\u7684\u9876\u7aef\u6c47\u805a\uff0c\u51dd\u6210\u78a7\u7eff\u7684\u5149\u7403\u7136\u540e\u6025\u901f\u8ff8\u51fa\u3002\u743c\u6069\u84c4\u8c0b\u5df2\u4e45\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u6700\u5f3a\u7684\u6740\u62db\uff0c\u5b89\u535a\u91cc\u7267\u5e08\u867d\u7136\u65e9\u6709\u51c6\u5907\uff0c\u4f9d\u65e7\u4e5f\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c\u55e4\u5730\u4e00\u58f0\uff0c\u95ea\u7535\u94e0\u7532\u88ab\u89e3\u79bb\u5c04\u7ebf\u76f4\u63a5\u51fb\u7a7f\uff0c\u7267\u5e08\u7684\u8179\u90e8\u51fa\u73b0\u4e00\u4e2a\u62f3\u5934\u5927\u5c0f\u7684\u9ed1\u6d1e\uff0c\u6df1\u53ef\u89c1\u8151\uff0c\u5374\u6ca1\u6709\u534a\u70b9\u8840\u6db2\u6d41\u51fa\u3002</p><p>\u201c\u77f3\u5f39\uff01\u201d</p><p>\u201c\u95ea\u7535\u675f\uff01\u201d</p><p>\u201c\u5bd2\u51b0\u9525\uff01\u201d</p><p>\u201c\u97f3\u6ce2\u77db\uff01\u201d</p><p>\u8fd8\u4e0d\u7b49\u5b89\u535a\u7406\u7267\u5e08\u53cd\u51fb\uff0c\u51e0\u9053\u6cd5\u672f\u5288\u5934\u76d6\u8138\u5730\u8f70\u70b8\u8fc7\u6765\uff0c\u56e0\u4e3a\u4e09\u4f4d\u540c\u4f34\u90fd\u5df2\u7ecf\u6b7b\u4ea1\uff0c\u4ed6\u4fbf\u6210\u4e86\u5168\u573a\u706b\u529b\u7684\u7126\u70b9\u3002\u95ea\u7535\u51dd\u6210\u7684\u94e0\u7532\u627f\u53d7\u4e0d\u4f4f\u8fd9\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u653b\u51fb\uff0c\u51e0\u79d2\u949f\u540e\u4fbf\u88c2\u6210\u4e86\u65e0\u6570\u788e\u7247\uff0c\u4f46\u5b83\u786e\u5b9e\u4e3a\u4e3b\u4eba\u4e89\u53d6\u5230\u4e86\u5598\u606f\u4e4b\u673a\u3002\u8d81\u7740\u9634\u9b42\u57ce\u5deb\u5e08\u653b\u51fb\u7684\u95f4\u9699\uff0c\u5b89\u535a\u7406\u7267\u5e08\u6643\u52a8\u624b\u4e2d\u7684\u4e09\u53c9\u621f\uff0c\u9ad8\u58f0\u8bf5\u5492\uff0c\u4e00\u56e2\u6fc0\u6d41\u6f29\u6da1\u5728\u621f\u7aef\u51fa\u73b0\uff0c\u8f70\u7136\u6da8\u5927\uff0c\u4eff\u4f5b\u8981\u5c06\u5468\u56f4\u6240\u6709\u4eba\u90fd\u541e\u566c\u8fdb\u53bb\u3002</p><p>\u7136\u540e\u8840\u5149\u4e00\u95ea\u3002</p><p>\u7267\u5e08\u5c16\u58f0\u60e8\u547c\uff0c\u53f3\u81c2\u8fde\u540c\u4e09\u53c9\u621f\u88ab\u4e00\u67c4\u8d64\u7ea2\u8272\u77ed\u5251\u4ece\u80a9\u8180\u4e0a\u6574\u9f50\u5730\u5378\u4e86\u4e0b\u6765\uff0c\u521a\u521a\u6210\u578b\u7684\u6cd5\u672f\u6f29\u6da1\u4e5f\u968f\u4e4b\u6d88\u6563\u3002\u838e\u73de\u514b\u7684\u7a88\u7a95\u8eab\u5f62\u4eff\u4f5b\u9b3c\u9b45\u822c\u5728\u4ed6\u8eab\u540e\u51fa\u73b0\uff0c\u867d\u7136\u4e00\u51fb\u5f97\u624b\uff0c\u5979\u5374\u7709\u5934\u5fae\u8e59\uff0c\u5fc3\u4e2d\u9690\u9690\u6709\u4e9b\u8be7\u5f02\u3002\u65b9\u624d\u90a3\u4e00\u5251\uff0c\u5979\u660e\u660e\u5df2\u7ecf\u662f\u7785\u51c6\u4e86\u7a7a\u9699\uff0c\u7b97\u51c6\u4e86\u65f6\u673a\uff0c\u672c\u62df\u80af\u5b9a\u80fd\u591f\u5c06\u5bf9\u624b\u4e00\u51fb\u6bd9\u6740\uff0c\u8c01\u6599\u51fa\u5251\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5b89\u535a\u7406\u7267\u5e08\u4eff\u4f5b\u5fc3\u6709\u611f\u5e94\u4f3c\u7684\uff0c\u4e0b\u610f\u8bc6\u5730\u8eab\u4f53\u5f80\u5de6\u4e00\u504f\uff0c\u7ed3\u679c\u53ea\u5378\u4e0b\u4e86\u4e00\u6761\u80f3\u818a\uff0c\u672a\u7adf\u5168\u529f\uff0c\u8fd9\u5728\u5979\u7684\u6740\u624b\u751f\u6daf\u4e2d\u8fd8\u662f\u5f88\u7f55\u89c1\u7684\u3002</p><p>\u53c8\u4e00\u652f\u9aa8\u77db\u52b2\u5c04\u800c\u6765\uff0c\u6b63\u4e2d\u7267\u5e08\u7684\u53f3\u80f8\u53e3\uff0c\u6df1\u6df1\u6ca1\u5165\uff0c\u5c06\u4ed6\u7684\u808b\u9aa8\u90fd\u523a\u65ad\u4e86\u4e24\u6839\u3002\u5b89\u535a\u7406\u7267\u5e08\u5f20\u5f00\u5634\uff0c\u4eff\u4f5b\u53d1\u51fa\u65e0\u58f0\u7684\u543c\u53eb\uff0c\u4ed6\u7684\u8eab\u4f53\u9661\u7136\u95f4\u88ab\u4e00\u5c42\u5929\u84dd\u8272\u7684\u5149\u8292\u7b3c\u7f69\uff0c\u7136\u540e\u6025\u901f\u5730\u626d\u66f2\u3001\u6da8\u5927\u3001\u53d8\u5f62\u3002\u534a\u79d2\u949f\u540e\uff0c\u7267\u5e08\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u53ea\u534a\u9ca8\u534a\u4eba\uff0c\u9ca8\u5934\u4eba\u8eab\u7684\u602a\u7269\u3002</p><p>\u201c\u543c\u543c\u543c\u543c\uff01\u201d</p><p>\u9ca8\u5934\u602a\u7269\u7684\u8840\u76c6\u5927\u53e3\u5f20\u5f00\uff0c\u53d1\u51fa\u9707\u8033\u6b32\u804b\u7684\u6012\u543c\uff0c\u65e0\u5f62\u7684\u9707\u8361\u6ce2\u63a8\u6d8c\u7740\u6d77\u6c34\u671d\u56db\u9762\u516b\u65b9\u5c42\u53e0\u6269\u6563\uff0c\u4eff\u4f5b\u5de8\u6d6a\u62cd\u6253\uff0c\u9634\u9b42\u57ce\u4f17\u4eba\u5168\u90fd\u7acb\u8db3\u4e0d\u7a33\uff0c\u88ab\u9707\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5305\u56f4\u987f\u65f6\u51fa\u73b0\u7a7a\u9699\u3002\u5f53\u4ed6\u4eec\u5b9a\u4f4f\u8eab\u5f62\uff0c\u6253\u7b97\u518d\u5ea6\u653b\u51fb\u65f6\uff0c\u9ca8\u5934\u602a\u7269\u5df2\u7ecf\u4ee5\u60ca\u4eba\u7684\u901f\u5ea6\u671d\u7740\u5927\u6d77\u6df1\u5904\u9003\u7a9c\u800c\u53bb\u3002</p><p>\u201c\u7b97\u4e86\u3002\u201d</p><p>\u62c9\u52a0\u62ac\u624b\u6b62\u4f4f\u624b\u4e0b\u524d\u5f80\u8ffd\u8d76\u7684\u6253\u7b97\uff0c\u5927\u6d77\u4e4b\u4e2d\uff0c\u6bd5\u7adf\u662f\u5b89\u535a\u7406\u7267\u5e08\u7684\u4e3b\u573a\u3002\u6b63\u9762\u5bf9\u654c\u7684\u8bdd\uff0c\u8fd9\u91cc\u7684\u9634\u9b42\u57ce\u5deb\u5e08\u8db3\u591f\u628a\u5168\u6851\u6bd4\u4e9a\u7684\u6d77\u6d0b\u5973\u795e\u6559\u4f1a\u76f4\u63a5\u63a8\u5e73\uff0c\u4f46\u8981\u5728\u832b\u832b\u5927\u6d77\u91cc\u8ffd\u8d76\u4e00\u5934\u9ca8\u9c7c\u2026\u2026\u8fd9\u96be\u5ea6\u8fd8\u662f\u672a\u514d\u592a\u9ad8\u4e86\u70b9\u3002\u53cd\u6b63\u5bf9\u65b9\u5df2\u7ecf\u8eab\u53d7\u91cd\u4f24\uff0c\u53ea\u5269\u4e0b\u534a\u6761\u547d\uff0c\u8ffd\u4e0d\u8ffd\u4e5f\u6ca1\u5dee\u522b\uff0c\u5de6\u53f3\u4e00\u4e2a\u5c0f\u89d2\u8272\uff0c\u4e0d\u5fc5\u653e\u5728\u5fc3\u4e0a\uff0c\u5982\u679c\u4e3a\u6b64\u803d\u8bef\u6b63\u4e8b\u5c31\u4e0d\u503c\u5f97\u4e86\u3002</p><p>\u4ef0\u671b\u7740\u9762\u524d\u7684\u661f\u9668\u57ce\uff0c\u62c9\u52a0\u6b63\u51c6\u5907\u7387\u4f17\u8fdb\u5165\uff0c\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\u7684\u743c\u6069\u7a81\u7136\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u7a0d\u7b49\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d\u62c9\u52a0\u505c\u4e0b\u811a\u6b65\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u6709\u4ec0\u4e48\u6307\u6559\u5417\uff1f\u201d</p><p>\u5728\u8fd9\u7fa4\u4eba\u4e2d\uff0c\u743c\u6069\u7684\u5730\u4f4d\u6709\u4e9b\u7279\u6b8a\uff0c\u65e2\u662f\u9634\u9b42\u57ce\u6d3e\u6765\u7763\u67e5\u7684\u4f7f\u8005\uff0c\u53c8\u6ca1\u6709\u76f4\u63a5\u63d2\u624b\u5e72\u6d89\u7684\u6743\u529b\uff0c\u7b97\u662f\u5ba2\u537f\u3002\u4ed6\u65e2\u7136\u8981\u8bf4\u8bdd\uff0c\u62c9\u52a0\u4e5f\u4e0d\u80fd\u4e0d\u542c\u542c\uff0c\u4ee5\u793a\u5c0a\u91cd\u3002</p><p>\u201c\u6307\u6559\u4e0d\u6562\uff0c\u53ea\u662f\u6709\u51e0\u4ef6\u4e8b\u60c5\u4e0d\u660e\u767d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7b2c\u4e00\u70b9\uff0c\u6d6e\u7a7a\u57ce\u4f9d\u7136\u5b58\u5728\uff0c\u5bc6\u745f\u80fd\u6838\u4f9d\u7136\u5b8c\u597d\uff0c\u8fd9\u662f\u5426\u610f\u5473\u7740\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\u4f9d\u65e7\u8fd8\u6d3b\u7740\uff1f\u201d</p><p>\u8fd9\u786e\u5b9e\u662f\u4e2a\u95ee\u9898\uff0c\u8010\u745f\u7684\u5927\u5965\u672f\u5e08\u4eec\u4e4b\u6240\u4ee5\u6b7b\u4f24\u6b86\u5c3d\uff0c\u76f4\u63a5\u539f\u56e0\u5c31\u662f\u56e0\u4e3a\u9b54\u6cd5\u5931\u63a7\uff0c\u6d6e\u7a7a\u57ce\u5c3d\u6570\u5760\u843d\uff0c\u4ed6\u4eec\u4e5f\u90fd\u968f\u4e4b\u6454\u6b7b\u4e86\u3002\u4f46\u5c31\u76ee\u524d\u7684\u60c5\u51b5\u6765\u770b\uff0c\u661f\u9668\u57ce\u867d\u7136\u5760\u5165\u6d77\u4e2d\uff0c\u5374\u5e76\u6ca1\u6709\u906d\u5230\u7834\u574f\uff0c\u90a3\u4e48\u57ce\u4e2d\u7684\u5c45\u6c11\uff0c\u5c24\u5176\u662f\u8eab\u4e3a\u5927\u5965\u672f\u5e08\u7684\u57ce\u4e3b\u5965\u5609\u83b1\u65af\u5973\u58eb\uff0c\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u786e\u5b9e\u662f\u6709\u5f88\u9ad8\u7684\u53ef\u80fd\u6027\u8fd8\u5b58\u6d3b\u7740\u7684\u3002</p><p>\u5018\u82e5\u5965\u5609\u83b1\u65af\u5973\u58eb\u8fd8\u6d3b\u7740\uff0c\u90a3\u4e48\u743c\u6069\u7b49\u4eba\u5f84\u76f4\u95ef\u5165\uff0c\u5c82\u4e0d\u662f\u592a\u5192\u5931\u4e86\u3002\u867d\u7136\u542c\u62c9\u52a0\u8bf4\u8fc7\uff0c\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u662f\u5965\u5609\u83b1\u65af\u7684\u5b66\u751f\uff0c\u5173\u7cfb\u5e94\u8be5\u662f\u4e0d\u9519\u7684\uff0c\u4f46\u5b66\u751f\u7684\u4e00\u7fa4\u90e8\u4e0b\u4e0d\u6253\u62db\u547c\u8dd1\u5230\u8001\u5e08\u5bb6\u91cc\uff0c\u800c\u4e14\u8fd8\u6253\u7b97\u62ff\u4e1c\u897f\u8fd0\u5b9d\u7269\u2026\u2026\u90a3\u8001\u5e08\u4f1a\u4f5c\u4f55\u53cd\u5e94\uff0c\u4f3c\u4e4e\u4e0d\u96be\u731c\u6d4b\u3002</p><p>\u542c\u4e86\u743c\u6069\u7684\u8d28\u7591\uff0c\u62c9\u52a0\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u591a\u8651\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\u5df2\u7ecf\u53bb\u4e16\uff0c\u8fd9\u70b9\u786e\u5b9a\u65e0\u7591\u3002\u201d</p><p>\u4ed6\u7684\u53e3\u6c14\u65a9\u9489\u622a\u94c1\uff0c\u663e\u7136\u6709\u5341\u8db3\u628a\u63e1\uff0c\u743c\u6069\u867d\u6709\u7591\u8651\uff0c\u4f46\u4e5f\u4e0d\u65b9\u4fbf\u518d\u591a\u8bf4\u4ec0\u4e48\u3002\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e48\u8fd8\u6709\u7b2c\u4e8c\u70b9\uff0c\u5373\u4fbf\u5965\u5609\u83b1\u65af\u5973\u58eb\u53bb\u4e16\uff0c\u4f46\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u7684\u8ff7\u9501\u4f9d\u7136\u5728\u8fd0\u4f5c\uff0c\u6211\u4eec\u8981\u600e\u6837\u624d\u80fd\u5b89\u5168\u8fdb\u5165\uff1f\u201d</p><p>\u8fd9\u53c8\u662f\u4e2a\u95ee\u9898\uff0c\u57fa\u672c\u4e0a\u6bcf\u5ea7\u6d6e\u7a7a\u57ce\u90fd\u6709\u81ea\u5df1\u7684\u8ff7\u9501\uff0c\u901a\u4fd7\u6765\u8bf4\u5c31\u662f\u8d85\u5f3a\u7684\u9632\u5fa1\u7cfb\u7edf\uff0c\u5176\u4e2d\u5fc5\u5b9a\u9690\u542b\u7740\u65e0\u6570\u5371\u9669\u7684\u673a\u5173\u9677\u9631\uff0c\u8fd9\u5374\u8981\u5982\u4f55\u624d\u80fd\u907f\u8fc7\u5462\uff1f</p><p>\u5bf9\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u5176\u4ed6\u9634\u9b42\u57ce\u5deb\u5e08\u4f3c\u4e4e\u4e5f\u90fd\u5fc3\u6709\u7591\u8651\uff0c\u867d\u7136\u4e0d\u6562\u9644\u548c\uff0c\u5374\u90fd\u4e00\u9f50\u770b\u7740\u62c9\u52a0\uff0c\u663e\u7136\u662f\u8981\u4ed6\u505a\u51fa\u5408\u7406\u7684\u89e3\u91ca\u3002\u62c9\u52a0\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u6240\u8a00\u6709\u7406\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5728\u6211\u4eec\u7684\u9884\u6599\u4e2d\uff0c\u4e00\u76f4\u90fd\u662f\u8ba4\u4e3a\u5c31\u7b97\u627e\u5230\u661f\u9668\u57ce\uff0c\u5fc5\u5b9a\u4e5f\u5df2\u7ecf\u6b8b\u7834\u4e0d\u582a\uff0c\u4e5f\u5c31\u6839\u672c\u4e0d\u5b58\u5728\u5982\u4f55\u5bf9\u4ed8\u8ff7\u9501\u7684\u95ee\u9898\u3002\u4e0d\u8fc7\u5e78\u8fd0\u7684\u662f\uff0c\u6bbf\u4e0b\u9ad8\u77bb\u8fdc\u77a9\uff0c\u601d\u8651\u5468\u5bc6\uff0c\u5bf9\u8fd9\u79cd\u60c5\u51b5\u65e9\u6709\u51c6\u5907\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u51c6\u5907\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u62c9\u52a0\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u4e00\u7269\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5411\u4f17\u4eba\u5c55\u793a\u3002\u90a3\u662f\u4e00\u4e2a\u7d2b\u8272\u7684\u5706\u76d8\uff0c\u6709\u70b9\u50cf\u838e\u5c14\u7684\u5723\u5fbd\uff0c\u4f46\u53c8\u4e0d\u540c\uff0c\u91cc\u9762\u662f\u4e00\u53ea\u76db\u5f00\u7684\u6de1\u78a7\u8272\u82b1\u6735\uff0c\u5206\u516d\u74e3\uff0c\u6bcf\u74e3\u7684\u5c16\u7aef\u90fd\u7f00\u7740\u4e00\u9897\u95ea\u95ea\u53d1\u5149\u7684\u94f6\u661f\u3002\u201c\u8fd9\u662f\u4fe1\u7269\uff0c\u201d\u62c9\u52a0\u89e3\u91ca\uff0c\u201c\u53ea\u8981\u6301\u6709\u5b83\uff0c\u6211\u4eec\u8fdb\u661f\u9668\u57ce\u5c31\u4e0d\u4f1a\u906d\u5230\u8ff7\u9501\u7684\u4efb\u4f55\u653b\u51fb\u6216\u538b\u5236\u3002\u201d</p><p>\u5176\u5b9e\u5c31\u662f\u901a\u884c\u8bc1\uff0c\u590f\u591a\u662f\u5965\u5609\u83b1\u65af\u7684\u5b66\u751f\uff0c\u6240\u4ee5\u9634\u9b42\u57ce\u4f1a\u6709\u8fd9\u79cd\u4e1c\u897f\u5012\u4e5f\u7b97\u6b63\u5e38\u3002\u53ea\u662f\u6784\u9020\u6709\u70b9\u5947\u602a\uff0c\u770b\u8d77\u6765\u50cf\u662f\u67d0\u79cd\u5fbd\u7ae0\uff0c\u800c\u590f\u591a\u7684\u6807\u5fd7\u662f\u4e00\u5934\u9ed1\u8272\u9f99\u72ee\uff0c\u548c\u82b1\u6735\u3001\u661f\u661f\u626f\u4e0d\u4e0a\u534a\u70b9\u8054\u7cfb\u3002</p><p>\u8fd9\u4e2a\u95ee\u9898\u6682\u4e14\u653e\u4e0b\uff0c\u53cd\u6b63\u62c9\u52a0\u62ff\u51fa\u4e86\u4fe1\u7269\uff0c\u743c\u6069\u7684\u7b2c\u4e8c\u4e2a\u95ee\u9898\u4fbf\u7b97\u662f\u5f97\u5230\u4e86\u89e3\u51b3\uff0c\u4ed6\u6c89\u541f\u4e86\u7247\u523b\uff0c\u7136\u540e\u95ee\u51fa\u4e86\u7b2c\u4e09\u4e2a\u95ee\u9898\u3002</p><p>\u201c\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u4e3a\u4ec0\u4e48\u73b0\u5728\u8fd8\u80fd\u6d6e\u7a7a\uff1f\u201d</p><p>\u62c9\u52a0\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6d6e\u7a7a\u57ce\u81ea\u7136\u662f\u80fd\u591f\u6d6e\u7a7a\u7684\uff0c\u8fd9\u8bf4\u660e\u5b83\u7684\u5bc6\u745f\u80fd\u6838\u4f9d\u65e7\u5b8c\u597d\uff0c\u4f9d\u7136\u80fd\u591f\u4ece\u9b54\u7f51\u2026\u2026\u201d</p><p>\u4ed6\u7684\u8bdd\u7a81\u7136\u987f\u4f4f\u4e86\u3002</p><p>\u4e0d\u4ec5\u4ec5\u662f\u4ed6\uff0c\u5176\u4ed6\u5deb\u5e08\u4e5f\u5168\u90fd\u6014\u4f4f\u4e86\u3002\u6b63\u5982\u62c9\u52a0\u6240\u8a00\uff0c\u6d6e\u7a7a\u57ce\u81ea\u7136\u80fd\u591f\u6d6e\u7a7a\uff0c\u8fd9\u662f\u5e38\u8bc6\uff0c\u800c\u5bf9\u4e8e\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u800c\u8a00\uff0c\u8fd9\u66f4\u662f\u5e38\u8bc6\u4e2d\u7684\u5e38\u8bc6\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6bcf\u5929\u5c31\u751f\u6d3b\u5728\u4e00\u5ea7\u98de\u5728\u5929\u4e0a\u7684\u57ce\u5e02\u91cc\u3002\u6b63\u56e0\u4e3a\u662f\u5e38\u8bc6\uff0c\u52a0\u4e0a\u610f\u5916\u53d1\u73b0\u4e00\u5ea7\u5b8c\u597d\u65e0\u635f\u7684\u6d6e\u7a7a\u57ce\uff0c\u6240\u6709\u5deb\u5e08\u90fd\u6781\u5ea6\u5174\u594b\u6fc0\u52a8\uff0c\u6240\u4ee5\u6709\u4e9b\u663e\u800c\u6613\u89c1\u7684\u9053\u7406\uff0c\u53cd\u800c\u88ab\u4e0b\u610f\u8bc6\u5730\u5ffd\u7565\u4e86\u3002</p><p>\u6d6e\u7a7a\u57ce\u80fd\u591f\u4e00\u76f4\u4fdd\u6301\u6d6e\u7a7a\u72b6\u6001\uff0c\u4f9d\u9760\u7684\u662f\u5bc6\u745f\u80fd\u6838\u4ece\u9b54\u7f51\u7684\u6e90\u4e2d\u6c72\u53d6\u80fd\u91cf\u3002\u4f46\u5982\u4eca\u9b54\u7f51\u7684\u6e90\u5df2\u7ecf\u88ab\u5c01\u95ed\u4e86\uff0c\u90a3\u4e48\u9762\u524d\u8fd9\u5ea7\u661f\u9668\u57ce\u4e3a\u4f55\u4ecd\u7136\u4e0d\u53d7\u5f71\u54cd\uff1f\u96be\u4e0d\u6210\u5b83\u4e5f\u548c\u9634\u9b42\u57ce\u4e00\u6837\uff0c\u501f\u52a9\u7684\u662f\u838e\u5c14\u7684\u5f71\u9b54\u7f51\u5417\uff1f\u8fd9\u5f53\u7136\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u5426\u5219\u9634\u9b42\u57ce\u4e5f\u5c31\u4e0d\u5fc5\u8fd9\u4e48\u8d39\u52b2\u4e86\uff0c\u76f4\u63a5\u8bf7\u5927\u796d\u53f8\u745e\u74e6\u5170\u51fa\u9762\uff0c\u8ddf\u5973\u795e\u6253\u4e2a\u62db\u547c\uff0c\u4e00\u5207\u641e\u5b9a\u3002</p><p>\u5e7b\u50cf\uff1f</p><p>\u5728\u4e00\u4e2a\u9b54\u6cd5\u4e16\u754c\u91cc\uff0c\u770b\u5230\u201c\u903b\u8f91\u4e0a\u4e0d\u53ef\u80fd\u5b58\u5728\u4e4b\u7269\u201d\uff0c\u6700\u5e38\u89c1\u7684\u53cd\u5e94\u4fbf\u662f\u201c\u8fd9\u662f\u5e7b\u672f\u201d\u3002\u7136\u800c\u9634\u9b42\u57ce\u6b64\u6b21\u51fa\u52a8\u7684\uff0c\u5168\u90fd\u662f\u9ad8\u9636\u9884\u8a00\u5e08\uff0c\u9884\u8a00\u672f\u53c8\u6b63\u662f\u5e7b\u672f\u7684\u514b\u661f\u3002\u5982\u679c\u8bf4\u9762\u524d\u6240\u89c1\u7684\u6d6e\u7a7a\u57ce\u662f\u4e2a\u5e9e\u5927\u5e7b\u8c61\uff0c\u7adf\u7136\u6b3a\u7792\u8fc7\u4e86\u5728\u573a\u7684\u6240\u6709\u5deb\u5e08\u2014\u2014\u8fd9\u79cd\u53ef\u80fd\u6027\u4e0d\u662f\u6ca1\u6709\uff0c\u4f46\u6982\u7387\u5b9e\u5728\u662f\u4f4e\u5f97\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u62c9\u52a0\u6478\u51fa\u4e00\u526f\u84dd\u5b9d\u77f3\u773c\u955c\u67b6\u5728\u9f3b\u6881\u4e0a\uff0c\u5bf9\u7740\u6d6e\u7a7a\u57ce\u518d\u4e09\u89c2\u5bdf\uff0c\u6700\u540e\u6447\u4e86\u6447\u5934\uff0c\u201c\u4e0d\u662f\u5e7b\u50cf\uff0c\u201d\u4ed6\u80af\u5b9a\u5730\u8bf4\uff0c\u201c\u662f\u771f\u5b9e\u3002\u201d</p><p>\u201c\u53ef\u662f\u8fd9\u5b8c\u5168\u4e0d\u7b26\u5408\u9053\u7406\u2026\u2026\u201d\u4e00\u540d\u9634\u9b42\u57ce\u5deb\u5e08\u8bf4\u3002</p><p>\u201c\u9053\u7406\u5fc5\u987b\u7b26\u5408\u4e8b\u5b9e\uff0c\u4e8b\u5b9e\u65e0\u9700\u7b26\u5408\u9053\u7406\uff0c\u201d\u62c9\u52a0\u6de1\u6de1\u8bf4\uff0c\u77ed\u6682\u7684\u5931\u6001\u540e\uff0c\u4ed6\u5df2\u7ecf\u7acb\u523b\u6062\u590d\u8fc7\u6765\uff0c\u795e\u8272\u5982\u5e38\uff0c\u201c\u65e2\u7136\u4e8b\u5b9e\u6446\u5728\u9762\u524d\uff0c\u90a3\u4e48\u5b83\u672c\u8eab\u5c31\u662f\u9053\u7406\u3002\u201d</p><p>\u201c\u90a3\u6211\u4eec\u2026\u2026\u201d</p><p>\u201c\u8fdb\u53bb\u770b\u770b\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u201c\u770b\u770b\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u201d\u4ed6\u7709\u89d2\u6311\u8d77\uff0c\u773c\u775b\u5fae\u5fae\u772f\u7f1d\uff0c\u9732\u51fa\u4e00\u4e1d\u51cc\u5389\u6740\u6c14\u6765\uff0c\u201c\u53cd\u6b63\u8fd9\u5ea7\u57ce\uff0c\u6211\u4eec\u662f\u5fd7\u5728\u5fc5\u5f97\uff0c\u8c01\u788d\u4e8b\uff0c\u5c31\u6740\u8c01\u3002\u201d</p><p>\u4ed6\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u4e00\u652f\u8c61\u7259\u6cd5\u6756\uff0c\u7d27\u4e86\u7d27\u6597\u7bf7\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u5176\u4f59\u9634\u9b42\u57ce\u5deb\u5e08\u7d27\u968f\u5176\u540e\uff0c\u4ed6\u4eec\u6212\u5907\u7740\uff0c\u624b\u4e2d\u63e1\u7d27\u6b66\u5668\uff0c\u6fc0\u6d3b\u4f69\u6234\u6216\u643a\u5e26\u7684\u9b54\u6cd5\u88c5\u5907\uff0c\u5404\u79cd\u7075\u5149\u6b64\u8d77\u5f7c\u4f0f\u5730\u7b3c\u7f69\u5168\u8eab\uff0c\u6784\u5efa\u8d77\u575a\u56fa\u7684\u9632\u5fa1\u3002\u743c\u6069\u6b63\u81ea\u72b9\u8c6b\uff0c\u73ca\u5609\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u201c\u6211\u4eec\u4e5f\u8fdb\u53bb\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u65e2\u7136\u5df2\u7ecf\u5230\u4e86\u8fd9\u91cc\uff0c\u5c31\u6ca1\u6709\u4e34\u9635\u9000\u7f29\u7684\u9053\u7406\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u73ca\u5609\u6240\u8a00\u4e5f\u6709\u9053\u7406\uff0c\u743c\u6069\u70b9\u5934\u7b54\u5e94\uff0c\u8ba9\u838e\u73de\u514b\u6ce8\u610f\u7167\u987e\u53cc\u80de\u80ce\u59d0\u59b9\uff0c\u81ea\u5df1\u63fd\u7740\u73ca\u5609\uff0c\u8ddf\u4e0a\u62c9\u52a0\u7b49\u4eba\u3002\u4ed6\u4eec\u7a7f\u8fc7\u7b3c\u7f69\u6d6e\u7a7a\u57ce\u7684\u900f\u660e\u7403\u5f62\u62a4\u7f69\uff0c\u7136\u540e\u60ca\u8bb6\u5730\u53d1\u73b0\u8fd9\u5c45\u7136\u662f\u4e2a\u8d85\u5927\u89c4\u6a21\u7684\u8fc7\u6ee4\u7ed3\u754c\uff0c\u91cc\u9762\u662f\u7eaf\u51c0\u7684\u65b0\u9c9c\u7a7a\u6c14\uff0c\u6d77\u6c34\u88ab\u5b8c\u5168\u963b\u9694\u5728\u5916\uff0c\u534a\u70b9\u65e0\u6cd5\u6e17\u5165\u3002</p><p>\u4e00\u8fdb\u5165\u6d6e\u7a7a\u57ce\u540e\uff0c\u6784\u6210\u8ff7\u9501\u7684\u4e03\u6761\u5f69\u8272\u5149\u5e26\u987f\u65f6\u4fbf\u6709\u53cd\u5e94\uff0c\u6570\u5341\u679a\u8000\u773c\u523a\u76ee\u7684\u9b54\u6cd5\u7b26\u6587\u4ece\u4e2d\u8131\u79bb\uff0c\u4eff\u4f5b\u661f\u8fb0\u822c\u9ad8\u901f\u5760\u843d\uff0c\u671d\u7740\u9634\u9b42\u57ce\u8bf8\u4eba\u5f53\u5934\u7838\u4e0b\u6765\u3002\u62c9\u52a0\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u7d2b\u8272\u5706\u76d8\uff0c\u516d\u9897\u94f6\u661f\u4ece\u4e2d\u98de\u51fa\uff0c\u8fce\u4e0a\u4e86\u7b26\u6587\u3002</p><p>\u4e00\u78b0\u4e4b\u4e0b\uff0c\u516d\u9897\u94f6\u661f\u81ea\u52a8\u7184\u706d\uff0c\u9b54\u6cd5\u7b26\u6587\u5219\u5f84\u76f4\u5f39\u56de\uff0c\u91cd\u65b0\u878d\u5165\u5f69\u8272\u5149\u5e26\u4e2d\uff0c\u8ff7\u9501\u7ee7\u7eed\u5982\u6cb3\u6d41\u822c\u9759\u9759\u8fd0\u52a8\uff0c\u4eff\u4f5b\u4ec0\u4e48\u90fd\u6ca1\u53d1\u751f\u8fc7\u3002\u62c9\u52a0\u6536\u56de\u5706\u76d8\uff0c\u4ece\u8896\u4e2d\u53d6\u51fa\u4e00\u4efd\u8be6\u7ec6\u5230\u6781\u81f4\u7684\u5730\u56fe\uff0c\u5bf9\u7167\u5468\u56f4\u770b\u4e86\u7247\u523b\uff0c\u6307\u4e86\u6307\u4e00\u4e2a\u65b9\u5411\u3002\u201c\u8d70\u8fd9\u8fb9\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u8fd9\u6761\u8def\u901a\u5f80\u54ea\u91cc\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u80fd\u6838\u5ba4\u3002\u201d</p><p>\u6240\u8c13\u80fd\u6838\u5ba4\uff0c\u987e\u540d\u601d\u4e49\uff0c\u5c31\u662f\u653e\u7f6e\u5bc6\u745f\u80fd\u6838\u7684\u5730\u65b9\uff0c\u9634\u9b42\u57ce\u6b64\u6765\uff0c\u771f\u6b63\u7684\u76ee\u7684\u4fbf\u662f\u4e3a\u4e86\u5bc6\u745f\u80fd\u6838\uff0c\u81f3\u4e8e\u5176\u4ed6\u90fd\u662f\u6b21\u8981\u3002\u51ed\u7740\u5730\u56fe\uff0c\u4e00\u884c\u4eba\u7545\u901a\u65e0\u963b\uff0c\u987a\u987a\u5229\u5229\u5730\u7a7f\u5802\u5165\u5ba4\uff0c\u4e0a\u9636\u4e0b\u697c\uff0c\u6700\u7ec8\u8fdb\u5165\u4e00\u4e2a\u50cf\u662f\u5bab\u6bbf\u7684\u8c6a\u534e\u5927\u5385\u4e2d\u3002</p><p>\u7136\u540e\u4ed6\u4eec\u770b\u89c1\u4e86\u4e00\u4e2a\u5973\u4eba\u3002</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u90a3\u662f\u4e00\u4f4d\u8863\u7740\u66b4\u9732\u7684\u4e11\u964b\u5973\u4eba\uff0c\u7a7f\u7740\u4e00\u5957\u7528\u7ec6\u788e\u8d1d\u58f3\u8fde\u7f00\u800c\u6210\u7684\u5206\u4f53\u5f0f\u6cf3\u8863\u3002\u84dd\u7eff\u8272\u7684\u76ae\u80a4\u4e0a\u5e03\u6ee1\u8936\u76b1\uff0c\u67af\u7626\u53cc\u624b\u4eff\u4f5b\u9e70\u722a\uff0c\u4e00\u53cc\u6b7b\u767d\u8272\u7684\u773c\u73e0\uff0c\u4eff\u4f5b\u6ca1\u6709\u77b3\u5b54\uff0c\u5934\u53d1\u5219\u662f\u6d3b\u751f\u751f\u7684\u6d77\u85fb\u3002\u5979\u6b63\u7ad9\u5728\u5927\u5385\u4e2d\u592e\u7684\u8fc7\u9053\u4e0a\uff0c\u77aa\u7740\u8fd9\u7fa4\u4e0d\u901f\u4e4b\u5ba2\uff0c\u795e\u60c5\u7d27\u5f20\u3002</p><p>\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u5979\u559d\u95ee\uff0c\u5374\u663e\u7136\u6ca1\u4ec0\u4e48\u6c14\u52bf\u3002</p><p>\u62c9\u52a0\u770b\u8d77\u6765\u663e\u7136\u4e5f\u5341\u5206\u7591\u60d1\uff0c\u4ed6\u6b63\u8981\u7b54\u8bdd\uff0c\u9661\u7136\u95f4\u53c8\u4e00\u4e2a\u5973\u6027\u58f0\u97f3\u81ea\u5934\u9876\u7a81\u7136\u54cd\u8d77\uff0c\u5728\u6574\u5ea7\u5927\u5385\u4e2d\u56de\u8361\u7740\uff0c\u5c16\u9510\u65e0\u6bd4\uff0c\u523a\u5f97\u6240\u6709\u4eba\u8033\u819c\u9690\u9690\u751f\u75bc\u3002</p><p>\u201c\u5567\u5567\uff0c\u7ec8\u4e8e\u6765\u4e86\u5417\uff0c\u590f\u591a\u90a3\u5bb6\u4f19\uff0c\u529e\u4e8b\u6548\u7387\u4e00\u5982\u65e2\u5f80\u7684\u5dee\u52b2\u554a\uff0c\u201d\u90a3\u4e2a\u58f0\u97f3\u4e0d\u6ee1\u5730\u53d1\u7740\u7262\u9a9a\uff0c\u201c\u7b97\u4e86\uff0c\u65e2\u7136\u90fd\u5230\u9f50\u4e86\uff0c\u90a3\u5c31\u8d76\u5feb\u5e72\u6d3b\u5427\u2014\u2014\u563f\uff0c\u8bf4\u4f60\u4eec\u5462\uff0c\u4e00\u7fa4\u7b28\u86cb\uff0c\u522b\u4e1c\u5f20\u897f\u671b\u7684\uff0c\u5feb\u70b9\u628a\u8fd9\u4e2a\u4e11\u5973\u4eba\u6740\u6389\uff0c\u6211\u8fd8\u6709\u4e00\u5927\u5806\u6b63\u4e8b\u8981\u7b49\u7740\u5904\u7406\u5462\u3002\u201d</p></article>"}}}]);
