(window.webpackJsonp=window.webpackJsonp||[]).push([[1666],{1689:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516b\u8282 \u6d77\u795e",content:"<article><h2>\u7b2c\u56db\u5341\u516b\u8282 \u6d77\u795e</h2><p>\u7a81\u7136\u5192\u51fa\u6765\u7684\u5973\u4eba\u58f0\u97f3\uff0c\u8bf4\u5f97\u662f\u7eaf\u6b63\u7684\u53e4\u8010\u745f\u8bed\uff0c\u4ed4\u7ec6\u542c\u7684\u8bdd\u97f3\u8272\u5176\u5b9e\u4e0d\u9519\uff0c\u6e05\u4eae\u660e\u4e3d\uff0c\u4f46\u5176\u4e2d\u90a3\u79cd\u98de\u626c\u8dcb\u6248\u9890\u6307\u6c14\u4f7f\u7684\u5473\u9053\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u6253\u5fc3\u5e95\u5730\u4e0d\u723d\u3002\u800c\u5979\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u66f4\u662f\u8ba9\u6240\u6709\u4eba\u90fd\u83ab\u540d\u5176\u5999\u3002</p><p>\u743c\u6069\u671d\u62c9\u52a0\u770b\u53bb\uff0c\u7528\u76ee\u5149\u8be2\u95ee\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u62c9\u52a0\u832b\u7136\u5730\u6447\u6447\u5934\uff0c\u8868\u793a\u4ed6\u4e5f\u662f\u4e00\u65e0\u6240\u77e5\u3002\u5faa\u7740\u58f0\u97f3\u4f20\u6765\u7684\u65b9\u5411\uff0c\u4ed6\u4eec\u62ac\u5934\u671d\u4e0a\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u9897\u6676\u83b9\u5254\u900f\u7684\u7403\u4f53\u6b63\u60ac\u6d6e\u5728\u5927\u6bbf\u7684\u7a79\u9876\uff0c\u76f4\u5f84\u7ea6\u4e94\u5c3a\uff0c\u707f\u707f\u751f\u5149\uff0c\u770b\u6a21\u6837\u5e76\u65e0\u4ec0\u4e48\u5947\u7279\uff0c\u90a3\u4e2a\u5c16\u9510\u7684\u5973\u58f0\u4fbf\u662f\u4ece\u4e2d\u53d1\u51fa\u7684\u3002</p><p>\u201c\u5bc6\u745f\u80fd\u6838\uff01\u201d</p><p>\u4e00\u9635\u4f4e\u4f4e\u7684\u8f7b\u547c\u58f0\u54cd\u8d77\uff0c\u5728\u573a\u7684\u9634\u9b42\u57ce\u5deb\u5e08\u5927\u591a\u90fd\u5728\u7b2c\u4e00\u65f6\u95f4\u8ba4\u51fa\u4e86\u5b83\u3002\u679c\u7136\u5982\u524d\u6240\u6599\uff0c\u8fd9\u5ea7\u661f\u9668\u57ce\u7684\u5bc6\u745f\u80fd\u6838\u4f9d\u7136\u5b8c\u597d\uff0c\u5e76\u672a\u6454\u788e\u3002\u53ea\u4e0d\u8fc7\u2026\u2026\u5bc6\u745f\u80fd\u6838\u867d\u7136\u6781\u5176\u5f3a\u5927\uff0c\u5374\u5e76\u975e\u667a\u80fd\u9b54\u6cd5\u7269\u54c1\uff0c\u4e0d\u5e94\u8be5\u4f1a\u8bf4\u8bdd\u5427\u3002</p><p>\u5982\u679c\u8bf4\u743c\u6069\u7b49\u4eba\u8fd8\u53ea\u662f\u7591\u60d1\uff0c\u90a3\u540d\u4e11\u964b\u7684\u84dd\u7eff\u76ae\u80a4\u5973\u4eba\u95fb\u8a00\u4fbf\u662f\u52c3\u7136\u53d8\u8272\u4e86\u3002\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff01\u201d\u5979\u5636\u54d1\u7740\u55d3\u5b50\u6012\u559d\u7740\uff0c\u58f0\u97f3\u4eff\u4f5b\u5608\u6742\u7684\u6d77\u6d6a\u62cd\u6253\u7901\u77f3\uff0c\u8f70\u8f70\u4f5c\u54cd\u3002</p><p>\u201c\u8fd9\u4e48\u663e\u800c\u6613\u89c1\u7684\u4e8b\u5b9e\uff0c\u96be\u9053\u8fd8\u9700\u8981\u89e3\u91ca\u5417\uff1f\u679c\u7136\u662f\u505a\u4e86\u592a\u4e45\u7684\u6d77\u795e\uff0c\u8111\u888b\u91cc\u90fd\u6d78\u6ee1\u6d77\u6c34\u4e86\u554a\uff0c\u201d\u90a3\u4e2a\u5c16\u9510\u7684\u58f0\u97f3\u518d\u5ea6\u4ece\u5bc6\u745f\u80fd\u6838\u4e2d\u4f20\u51fa\uff0c\u6beb\u4e0d\u7559\u60c5\u5730\u5632\u8bbd\u7740\uff0c\u201c\u4e5f\u662f\uff0c\u672c\u6765\u5c31\u4e0d\u5e94\u8be5\u6307\u671b\u4f60\u7684\u4f4e\u52a3\u667a\u529b\u80fd\u591f\u5f04\u61c2\u8fd9\u4e48\u590d\u6742\u7684\u95ee\u9898\u2014\u2014\u7b80\u5355\u70b9\u8bf4\uff0c\u4f60\u53ef\u4ee5\u53bb\u6b7b\u4e86\u3002\u201d</p><p>\u84dd\u7eff\u76ae\u80a4\u5973\u4eba\u6b7b\u6b7b\u76ef\u7740\u7a7a\u4e2d\u7684\u5bc6\u745f\u80fd\u6838\uff0c\u5634\u89d2\u626d\u66f2\uff0c\u4ece\u9f3b\u5b54\u4e2d\u53d1\u51fa\u51b7\u7b11\u58f0\uff0c\u201c\u539f\u6765\u5982\u6b64\u3002\u7136\u800c\u522b\u5fd8\u4e86\u4f60\u5728\u548c\u8c01\u8bf4\u8bdd\uff0c\u5965\u5609\u83b1\u65af\uff0c\u201d\u5979\u4f4e\u6c89\u5730\u5a01\u80c1\u7740\uff0c\u201c\u4f60\u5728\u85d0\u89c6\u4e00\u4f4d\u795e\u7947\u2026\u2026\u201d</p><p>\u201c\u597d\u4e86\u597d\u4e86\uff0c\u5c3d\u4f1a\u8bf4\u4e9b\u65e0\u804a\u7684\u5e9f\u8bdd\uff0c\u771f\u6ca1\u957f\u8fdb\uff0c\u201d\u5bc6\u745f\u80fd\u6838\u4e2d\u7684\u5973\u58f0\u4e0d\u8010\u70e6\u5730\u6253\u65ad\uff0c\u201c\u795e\u7947\u8fd9\u79cd\u5e9f\u7269\uff0c\u5728\u6211\u773c\u91cc\u5168\u90fd\u662f\u4e00\u5806\u6447\u6447\u6b32\u5760\u7684\u661f\u661f\uff0c\u4e0d\u662f\u4eca\u5929\u7184\u5c31\u662f\u660e\u5929\u706d\uff0c\u6709\u4ec0\u4e48\u597d\u70ab\u8000\u7684\u3002\u603b\u4e4b\u4f60\u914d\u5408\u70b9\uff0c\u8d76\u5feb\u53bb\u6b7b\u5427\uff0c\u8ba9\u6211\u4eec\u597d\u7ee7\u7eed\u8fdb\u884c\u4e0b\u4e00\u5e55\uff0c\u6211\u7684\u5267\u672c\u8fd8\u6709\u5f88\u957f\u7bc7\u5e45\u5462\u3002\u201d</p><p>\u56a3\u5f20\u81f3\u6781\u7684\u8bdd\u8bed\uff0c\u542c\u5f97\u84dd\u7eff\u76ae\u80a4\u5973\u4eba\u9762\u8272\u6570\u53d8\uff0c\u6012\u6781\u53cd\u7b11\uff0c\u201c\u6211\u82e5\u662f\u4e0d\u914d\u5408\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u201d\u5bc6\u745f\u80fd\u6838\u4e2d\u7684\u5973\u58f0\u8fdf\u7591\u5730\u987f\u4e86\u987f\uff0c\u201c\u8bf4\u8d77\u6765\uff0c\u6211\u53ea\u5199\u4e86\u4f60\u4f1a\u6b7b\uff0c\u5012\u662f\u5fd8\u4e86\u5199\u4f60\u600e\u4e48\u6b7b\u2026\u2026\u65e0\u6240\u8c13\u4e86\uff0c\u5c31\u8ba9\u8fd9\u5e2e\u7b28\u86cb\u628a\u4f60\u6740\u6389\u597d\u4e86\uff0c\u53ea\u8981\u7ed3\u679c\u4e00\u6837\u5c31\u6ca1\u95ee\u9898\u3002\u201d</p><p>\u201c\u5c31\u51ed\u8fd9\u4e9b\u877c\u8681\uff1f\u201d\u84dd\u7eff\u76ae\u80a4\u5973\u4eba\u50b2\u6162\u5730\u626b\u4e86\u743c\u6069\u7b49\u4eba\u4e00\u773c\uff0c\u201c\u4f60\u6307\u671b\u9760\u4ed6\u4eec\u6765\u6740\u6211\uff1f\u201d</p><p>\u201c\u5dee\u4e0d\u591a\u5427\uff0c\u603b\u4e4b\u5feb\u70b9\u5f00\u59cb\u5c31\u662f\u4e86\uff0c\u201d\u5bc6\u745f\u80fd\u6838\u91cc\u7684\u5973\u58f0\u5f88\u4e0d\u9ad8\u5174\u5730\u53f9\u7740\u6c14\uff0c\u201c\u771f\u662f\u7684\uff0c\u5267\u672c\u597d\u5199\uff0c\u6f14\u5458\u96be\u627e\uff0c\u4e00\u4e2a\u4e2a\u90fd\u6ca1\u534a\u70b9\u656c\u4e1a\u7cbe\u795e\u2014\u2014\u563f\uff0c\u8bf4\u4f60\u4eec\u5462\uff0c\u7b28\u86cb\u4eec\uff0c\u50bb\u7ad9\u7740\u505a\u4ec0\u4e48\uff0c\u96be\u9053\u6ca1\u542c\u5230\u6211\u8bf4\u8bdd\u5417\uff1f\u201d</p><p>\u4e24\u4e2a\u5973\u4eba\u7684\u4e00\u756a\u5bf9\u8bdd\uff0c\u6216\u8005\u51c6\u786e\u8bf4\u662f\u4e89\u5435\uff0c\u8ba9\u4f5c\u4e3a\u65c1\u89c2\u8005\u7684\u743c\u6069\u7b49\u4eba\u542c\u5f97\u76ee\u77aa\u53e3\u5446\u3002\u5965\u5609\u83b1\u65af\uff1f\u6d77\u795e\uff1f\u96be\u9053\u8bf4\u2026\u2026\u5bc6\u745f\u80fd\u6838\u91cc\u4f20\u51fa\u6765\u7684\u90a3\u4e2a\u5973\u4eba\u58f0\u97f3\uff0c\u4fbf\u662f\u8fd9\u5ea7\u661f\u9668\u57ce\u7684\u57ce\u4e3b\u5965\u5609\u83b1\u65af\u5973\u58eb\u53d1\u51fa\uff0c\u800c\u7ad9\u5728\u9762\u524d\u7684\u8fd9\u4f4d\u84dd\u7eff\u76ae\u80a4\u4e11\u964b\u5973\u4eba\uff0c\u5219\u662f\u6d77\u6d0b\u5973\u795e\u5b89\u535a\u7406\uff1f\u5443\uff0c\u8fd9\u4e48\u4e00\u8bf4\u7684\u8bdd\uff0c\u5012\u8fd8\u771f\u633a\u50cf\u7684\uff0c\u743c\u6069\u4e4b\u524d\u542c\u62c9\u52a0\u63d0\u8fc7\u4e00\u4e9b\u5b89\u535a\u7406\u7684\u8d44\u6599\uff0c\u5176\u4e2d\u4e5f\u7ea6\u7565\u8bf4\u5230\u8fd9\u4f4d\u5973\u795e\u7684\u5f62\u8c61\uff0c\u786e\u5b9e\u5c31\u662f\u8fd9\u526f\u6a21\u6837\u2026\u2026</p><p>\u867d\u8bf4\u8fdb\u5165\u6d6e\u7a7a\u57ce\u4e4b\u524d\uff0c\u9634\u9b42\u57ce\u8bf8\u4eba\u5fc3\u4e2d\u90fd\u505a\u597d\u4e86\u4f1a\u906d\u9047\u5f3a\u654c\u7684\u51c6\u5907\uff0c\u7136\u800c\u540c\u65f6\u78b0\u5230\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u548c\u4e00\u4f4d\u795e\u7947\uff0c\u8fd9\u8fd8\u662f\u592a\u51fa\u4eba\u610f\u6599\u4e86\u70b9\u3002\u5965\u5609\u83b1\u65af\u4e0d\u662f\u5df2\u7ecf\u6b7b\u4e86\u4e48\uff0c\u58f0\u97f3\u4e3a\u4f55\u53c8\u4f1a\u4ece\u5bc6\u745f\u80fd\u6838\u4e2d\u53d1\u51fa\uff1f\u800c\u4e14\u542c\u53e3\u6c14\uff0c\u5bf9\u743c\u6069\u7b49\u4eba\u7684\u5230\u6765\u4f3c\u4e4e\u65e9\u6709\u9884\u6599\u3002\u5b89\u535a\u7406\u53c8\u4e3a\u4ec0\u4e48\u4f1a\u5728\u6d6e\u7a7a\u57ce\u91cc\u2026\u2026\u4e0d\u5bf9\uff0c\u66f4\u51c6\u786e\u5730\u8bf4\u6cd5\u662f\uff0c\u5b89\u535a\u7406\u4e3a\u4f55\u4f1a\u51fa\u73b0\u5728\u7269\u8d28\u754c\uff1f</p><p>\u662f\u5316\u8eab\uff1f\u662f\u5723\u8005\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5b9a\uff0c\u548c\u62c9\u52a0\u9762\u9762\u76f8\u89d1\uff0c\u4e00\u65f6\u95f4\u5b8c\u5168\u641e\u4e0d\u6e05\u695a\u72b6\u51b5\u3002\u6309\u9053\u7406\u8bf4\uff0c\u65e2\u7136\u60c5\u52bf\u4e0d\u660e\uff0c\u6700\u6b63\u786e\u7684\u505a\u6cd5\u662f\u5148\u7f6e\u8eab\u4e8b\u5916\uff0c\u9759\u89c2\u5176\u53d8\u518d\u8bf4\u3002\u53ef\u60dc\u7684\u662f\uff0c\u8fd9\u79cd\u7406\u60f3\u72b6\u51b5\u6ce8\u5b9a\u662f\u4e0d\u4f1a\u51fa\u73b0\u7684\u3002</p><p>\u542c\u4e86\u5965\u5609\u83b1\u65af\u7684\u8bdd\uff0c\u5b89\u535a\u7406\u51b7\u7b11\u4e0d\u6b62\uff0c\u731b\u7136\u95f4\u5389\u559d\u4e00\u58f0\uff0c\u7709\u6bdb\u5012\u7ad6\uff0c\u53cc\u81c2\u5f20\u5f00\uff0c\u638c\u5fc3\u9065\u9065\u76f8\u5bf9\uff0c\u5341\u9053\u7ec6\u5c0f\u7684\u84dd\u8272\u6c34\u67f1\u81ea\u9e70\u722a\u822c\u7684\u6307\u5c16\u6fc0\u5c04\u800c\u51fa\uff0c\u55e4\u55e4\u7834\u7a7a\u4f5c\u54cd\uff0c\u9ad8\u901f\u649e\u5411\u60ac\u6d6e\u5728\u7a79\u9876\u7684\u5bc6\u745f\u80fd\u6838\u3002\u5979\u731d\u7136\u53d1\u96be\uff0c\u9634\u9b42\u57ce\u8bf8\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u4eec\u6b64\u884c\u7684\u9996\u8981\u76ee\u6807\u5c31\u662f\u5bc6\u745f\u80fd\u6838\uff0c\u5018\u82e5\u88ab\u8fd9\u4e11\u5973\u4eba\u7834\u574f\u7684\u8bdd\uff0c\u90a3\u5c82\u4e0d\u662f\u767d\u8dd1\u4e00\u8d9f\u3002\u867d\u7136\u4ece\u9b54\u6cd5\u5b66\u7406\u8bba\u4e0a\u8bf4\uff0c\u5bc6\u745f\u80fd\u6838\u53ea\u8981\u5904\u4e8e\u8fd0\u884c\u72b6\u6001\uff0c\u8054\u7ed3\u6c72\u53d6\u9b54\u7f51\u7684\u6e90\u529b\uff0c\u90a3\u5b83\u5c31\u662f\u65e0\u654c\u7684\uff0c\u65e0\u8bba\u662f\u9b54\u6cd5\u8fd8\u662f\u7269\u7406\u653b\u51fb\u90fd\u5bf9\u5b83\u65e0\u6548\uff0c\u5c31\u7b97\u662f\u7528\u5927\u88c2\u89e3\u6765\u8f70\u4e5f\u6ca1\u7528\u3002\u4f46\u5b89\u535a\u7406\u6bd5\u7adf\u662f\u795e\u660e\uff0c\u4e0d\u662f\u51e1\u4eba\uff0c\u4e0d\u80fd\u5b8c\u5168\u4ee5\u5e38\u7406\u6765\u5224\u65ad\u3002</p><p>\u94ff\uff01</p><p>\u5bc6\u745f\u80fd\u6838\u4e2d\u767d\u5149\u4e00\u95ea\uff0c\u968f\u5373\u4e03\u9053\u7531\u5343\u767e\u679a\u7b26\u6587\u51dd\u805a\u800c\u6210\u7684\u5f69\u8272\u5149\u5e26\u81ea\u865a\u7a7a\u4e2d\u5c55\u5f00\uff0c\u4eff\u4f5b\u6d6e\u7a7a\u57ce\u8ff7\u9501\u7684\u5fae\u7f29\u7248\uff0c\u4ea4\u9519\u7f20\u7ed5\uff0c\u5f62\u6210\u770b\u4f3c\u7262\u4e0d\u53ef\u7834\u7684\u9632\u5fa1\u3002\u5b89\u535a\u7406\u6307\u5c16\u5c04\u51fa\u7684\u6c34\u67f1\u649e\u4e0a\u5f69\u8272\u5149\u5e26\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\uff0c\u9b54\u6cd5\u7b26\u6587\u50cf\u706f\u76cf\u822c\u556a\u556a\u8f7b\u54cd\u7740\uff0c\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u63a5\u8fde\u7184\u706d\uff0c\u534a\u79d2\u949f\u5185\u4e03\u9053\u5149\u5e26\u4e2d\u7684\u4e00\u6761\u4fbf\u5b8c\u5168\u6d88\u5931\u4e86\u3002\u800c\u5341\u9053\u84dd\u8272\u6c34\u67f1\u5219\u50cf\u662f\u6d47\u5728\u4e86\u70e7\u7ea2\u7684\u94c1\u5757\u4e0a\uff0c\u5728\u534a\u79d2\u949f\u5185\u5316\u4f5c\u84b8\u817e\u6c34\u96fe\uff0c\u5728\u5f69\u8272\u5149\u5e26\u7684\u6620\u7167\u4e0b\u53d1\u51fa\u7f24\u7eb7\u7eda\u4e3d\u7684\u8679\u5149\uff0c\u968f\u5373\u6d88\u6563\u5728\u7a7a\u6c14\u4e2d\u3002</p><p>\u201c\u5537\u5537\uff0c\u201d\u5bc6\u745f\u80fd\u6838\u4e2d\uff0c\u5c16\u523b\u7684\u5973\u58f0\u518d\u5ea6\u53d1\u51fa\uff0c\u201c\u8981\u5b64\u6ce8\u4e00\u63b7\u5417\uff0c\u6d77\u795e\u965b\u4e0b\uff1f\u201d\u5979\u50cf\u662f\u5728\u60ca\u53f9\uff0c\u4f46\u66f4\u50cf\u662f\u5728\u8bbd\u523a\uff0c\u201c\u4f46\u662f\u522b\u5fd8\u4e86\uff0c\u5bc6\u745f\u80fd\u6838\u4e00\u6bc1\uff0c\u8ff7\u9501\u968f\u4e4b\u4fbf\u7834\uff0c\u90a3\u4f4d\u72ec\u773c\u9f99\u7684\u6253\u624b\u4eec\u53ef\u6b63\u5b88\u5728\u5916\u9762\u5bf9\u4f60\u864e\u89c6\u7708\u7708\u5462\u3002\u201d</p><p>\u201c\u90a3\u5c31\u4e00\u8d77\u540c\u5f52\u4e8e\u5c3d\u5427\uff0c\u80cc\u4fe1\u5f03\u4e49\u7684\u51e1\u4eba\uff01\u201d</p><p>\u5b89\u535a\u7406\u7684\u9762\u5bb9\u626d\u66f2\uff0c\u8d8a\u53d1\u663e\u5f97\u4e11\u964b\u72f0\u72de\uff0c\u6f8e\u6e43\u7684\u9b54\u529b\u5728\u865a\u7a7a\u4e2d\u65cb\u8f6c\u7740\uff0c\u51dd\u805a\u7740\uff0c\u5728\u5979\u7684\u6307\u5c16\u6c47\u96c6\uff0c\u84dd\u8272\u6c34\u73e0\u518d\u5ea6\u82e5\u9690\u82e5\u73b0\u5730\u6210\u578b\u3002\u7136\u800c\u4e0d\u7b49\u5979\u53d1\u51fa\u7b2c\u4e8c\u6b21\u653b\u51fb\uff0c\u9634\u9b42\u57ce\u8bf8\u4eba\u4fbf\u5df2\u7ecf\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u6740\u4e86\u5979\uff01\u201d\u62c9\u52a0\u4e00\u58f0\u4ee4\u4e0b\uff0c\u540c\u65f6\u6cd5\u6756\u75be\u6325\uff0c\u4e00\u70b9\u94f6\u5149\u4ece\u6756\u7aef\u5f39\u51fa\uff0c\u5728\u534a\u7a7a\u4e2d\u77ac\u95f4\u6da8\u5927\uff0c\u5316\u4f5c\u4e00\u5f20\u8000\u773c\u523a\u76ee\u7684\u96f7\u7535\u4e4b\u7f51\u3002</p><p>\u548c\u9634\u9b42\u57ce\u4e2d\u5927\u591a\u6570\u5deb\u5e08\u4e00\u6837\uff0c\u62c9\u52a0\u51fa\u8eab\u8d35\u65cf\u4e16\u5bb6\uff0c\u4ed6\u662f\u5e03\u96f7\u7eb3\u65af\u7684\u5b66\u751f\uff0c\u6700\u64c5\u957f\u9884\u8a00\u672f\uff0c\u4f46\u5bf9\u5176\u4ed6\u5404\u5b66\u6d3e\u7684\u9b54\u6cd5\u4e5f\u90fd\u6709\u7cbe\u7814\u3002\u5728\u62c9\u52a0\u7684\u5bb6\u65cf\u5386\u53f2\u4e0a\u66fe\u6709\u4e00\u4f4d\u5148\u7956\uff0c\u867d\u7136\u4e0d\u662f\u5927\u5965\u672f\u5e08\uff0c\u4f46\u5728\u5851\u80fd\u672f\u9886\u57df\u6709\u7740\u975e\u5e38\u9ad8\u7684\u9020\u8be3\uff0c\u8fd9\u9053\u201c\u96f7\u9706\u7f51\u201d\u6cd5\u672f\u4fbf\u662f\u4ed6\u7684\u53d1\u660e\uff0c\u5a01\u529b\u6781\u5927\uff0c\u6700\u8f89\u714c\u7684\u8bb0\u5f55\u662f\u66fe\u7ecf\u4e00\u51fb\u6bd9\u6740\u56db\u540d\u5c71\u5cad\u5de8\u4eba\uff0c\u7b97\u662f\u62c9\u52a0\u7684\u538b\u7bb1\u5e95\u6280\u80fd\uff0c\u5e73\u65f6\u4ece\u4e0d\u8f7b\u7528\u3002\u4f46\u5982\u4eca\u9762\u5bf9\u7684\u662f\u4e00\u4f4d\u795e\u7947\uff0c\u81ea\u7136\u662f\u4e00\u51fa\u624b\u5c31\u6beb\u65e0\u4fdd\u7559\u4e86\u3002</p><p>\u9762\u5bf9\u58f0\u52bf\u714a\u8d6b\u7684\u653b\u51fb\uff0c\u5b89\u535a\u7406\u4e0d\u7406\u4e0d\u776c\uff0c\u66f4\u4e0d\u8eb2\u907f\uff0c\u4eff\u4f5b\u5168\u7136\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u84dd\u8272\u6c34\u67f1\u5df2\u7ecf\u5728\u5979\u6307\u5c16\u51dd\u805a\u6210\u578b\uff0c\u773c\u770b\u5c31\u8981\u53d1\u51fa\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u9053\u707f\u70c2\u661f\u5149\u81ea\u7a79\u9876\u5c04\u4e0b\uff0c\u6b63\u597d\u6253\u5728\u96f7\u7535\u7f51\u4e0a\uff0c\u4ee4\u540e\u8005\u9661\u7136\u6da8\u5927\u4e86\u4e09\u500d\uff0c\u540c\u65f6\u4e00\u5c42\u9eef\u6de1\u865a\u5f71\u81ea\u4e2d\u5e7b\u51fa\uff0c\u77ac\u95f4\u51dd\u805a\u6210\u4e00\u5f20\u65b0\u7684\u96f7\u7535\u7f51\uff0c\u5c45\u4e8e\u539f\u672c\u7535\u7f51\u7684\u4fa7\u4e0b\u65b9\u3002\u4e24\u5f20\u7535\u7f51\u5728\u7a7a\u4e2d\u4ea4\u9519\u91cd\u53e0\uff0c\u94f6\u767d\u8272\u7684\u7535\u6d41\u5954\u6d8c\u817e\u8dc3\uff0c\u567c\u556a\u4f5c\u54cd\uff0c\u7167\u7740\u5b89\u535a\u7406\u5f53\u5934\u7f69\u4e0b\u3002</p><p>\u5b89\u535a\u7406\u8138\u8272\u9aa4\u53d8\uff0c\u6b32\u5f85\u518d\u95ea\u907f\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u5979\u5c16\u5578\u4e00\u58f0\uff0c\u6307\u5c16\u7684\u6c34\u67f1\u8f70\u7136\u56db\u6563\uff0c\u5316\u4f5c\u5343\u4e07\u9897\u7ec6\u5c0f\u6c34\u73e0\uff0c\u4eff\u4f5b\u4e00\u9053\u6c34\u96fe\u5c4f\u969c\u822c\u5c06\u81ea\u5df1\u7b3c\u7f69\u5728\u5176\u4e2d\u3002\u53ea\u542c\u5f97\u201c\u55de\u5566\u201d\u8fde\u58f0\uff0c\u94f6\u767d\u8272\u7684\u7c97\u5927\u95ea\u7535\u5728\u5b89\u535a\u7406\u8eab\u4e0a\u75af\u72c2\u6e38\u8d70\uff0c\u7f20\u7ed5\u5982\u86c7\uff0c\u4f46\u5374\u65e0\u6cd5\u5bf9\u5979\u9020\u6210\u534a\u70b9\u4f24\u5bb3\u3002</p><p>\u201c\u4f20\u8bf4\u4e2d\u7684\u6781\u6548\u590d\u5236\u5417\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u54ea\u3002\u201d\u53d7\u6b64\u610f\u5916\u4e00\u51fb\uff0c\u539f\u672c\u72c2\u6012\u7684\u5b89\u535a\u7406\u53cd\u800c\u53d8\u5f97\u9547\u9759\u4e0b\u6765\uff0c\u5979\u51b7\u7b11\u7740\uff0c\u53f3\u624b\u62ac\u8d77\u8f7b\u8f7b\u4e00\u62c9\uff0c\u4eff\u4f5b\u63b8\u7070\u822c\u5c06\u4e24\u5f20\u96f7\u7535\u7f51\u968f\u624b\u626f\u5f00\u3002\u7535\u7f51\u81ea\u5979\u8eab\u4e0a\u8131\u843d\uff0c\u8f70\u7136\u7834\u788e\uff0c\u5316\u505a\u65e0\u6570\u9053\u95ea\u7535\u94f6\u86c7\u98de\u6563\uff0c\u5728\u575a\u786c\u7684\u9ed1\u8000\u77f3\u5730\u9762\u4e0a\u7559\u4e0b\u4e86\u5927\u5927\u5c0f\u5c0f\u5192\u70df\u6d45\u5751\u3002</p><p>\u8f7b\u63cf\u6de1\u5199\u4fbf\u7834\u89e3\u6389\u9ad8\u9636\u5deb\u5e08\u7684\u5168\u529b\u4e00\u51fb\uff0c\u795e\u7947\u7684\u5a01\u80fd\u786e\u5b9e\u4e0d\u51e1\uff0c\u4f46\u8fd9\u4ec5\u4ec5\u53ea\u662f\u5f00\u59cb\u3002\u968f\u7740\u62c9\u52a0\u7684\u6307\u4ee4\uff0c\u540c\u884c\u7684\u5deb\u5e08\u9f50\u9f50\u51fa\u624b\uff0c\u5341\u9053\u6cd5\u672f\u767b\u65f6\u75be\u98ce\u66b4\u96e8\u822c\u671d\u7740\u5b89\u535a\u7406\u4ea4\u9519\u8f70\u6765\u3002</p><p>\u6ce2\u6ce2\u6ce2\u6ce2\u4e00\u9635\u8fde\u54cd\uff0c\u5341\u516d\u6839\u5bd2\u5149\u68ee\u68ee\u7684\u5c16\u9510\u9aa8\u523a\u81ea\u5730\u5e95\u751f\u51fa\uff0c\u7c97\u82e5\u624b\u81c2\uff0c\u77ac\u95f4\u5408\u62e2\uff0c\u6784\u5efa\u6210\u4e00\u53ea\u767d\u9aa8\u7262\u7b3c\uff0c\u5c06\u5b89\u535a\u7406\u56da\u7981\u5176\u4e2d\u3002\u56db\u4e2a\u5deb\u5e08\u540c\u65f6\u91ca\u653e\u8bc5\u5492\uff0c\u9ed1\u6697\u7684\u8d1f\u80fd\u91cf\u4ece\u865a\u7a7a\u4e2d\u6d8c\u51fa\uff0c\u56db\u9762\u516b\u65b9\u5730\u6d8c\u5411\u6d77\u795e\uff0c\u524a\u5f31\u5979\u7684\u4f53\u529b\uff0c\u6270\u4e71\u5979\u7684\u795e\u667a\uff0c\u964d\u4f4e\u5979\u5bf9\u9b54\u6cd5\u7684\u62b5\u6297\u529b\u3002\u7d27\u63a5\u7740\uff0c\u4e24\u53d1\u97f3\u6ce2\u77db\u3001\u4e24\u652f\u5bd2\u51b0\u9525\u548c\u4e00\u67c4\u529b\u573a\u5251\u9ad8\u901f\u523a\u7834\u7a7a\u6c14\uff0c\u53d1\u51fa\u5c16\u9510\u7684\u547c\u5578\u58f0\uff0c\u81ea\u767d\u9aa8\u7262\u7b3c\u7684\u95f4\u9699\u7a7f\u8fc7\uff0c\u6253\u7b97\u4e00\u4e3e\u5c06\u5bf9\u624b\u7f6e\u4e8e\u6b7b\u5730\u3002</p><p>\u5728\u573a\u7684\u9634\u9b42\u57ce\u5deb\u5e08\uff0c\u53bb\u6389\u743c\u6069\u4e4b\u5916\uff0c\u5305\u62ec\u8299\u857e\u72c4\u5728\u5185\u5168\u90fd\u662f\u9884\u8a00\u5e08\u3002\u9884\u8a00\u5b66\u6d3e\u7684\u9b54\u6cd5\u64c5\u957f\u7684\u662f\u63a2\u6d4b\u89c2\u5bdf\u3001\u5206\u6790\u63a8\u6572\u3001\u8baf\u606f\u4f20\u9012\uff0c\u51e0\u4e4e\u6ca1\u6709\u4efb\u4f55\u7528\u4e8e\u653b\u51fb\u7684\u6cd5\u672f\uff0c\u4f46\u5982\u679c\u56e0\u6b64\u8ba4\u4e3a\u9884\u8a00\u5e08\u5fc5\u5b9a\u4e0d\u64c5\u6218\u6597\uff0c\u90a3\u5c31\u662f\u5927\u9519\u7279\u9519\u4e86\u3002\u4e8b\u5b9e\u4e0a\u6070\u6070\u76f8\u53cd\uff0c\u6b63\u56e0\u4e3a\u9884\u8a00\u672f\u6781\u5ea6\u6b20\u7f3a\u6218\u6597\u529b\uff0c\u6240\u4ee5\u9884\u8a00\u5e08\u5f80\u5f80\u90fd\u7279\u522b\u7814\u4e60\u4e00\u4e9b\u72e0\u8fa3\u62db\u6570\u4f5c\u4e3a\u4fdd\u547d\u624b\u6bb5\uff0c\u901a\u5e38\u4f1a\u9009\u62e9\u4ea1\u7075\u672f\u6216\u8005\u5851\u80fd\u672f\u3002\u50cf\u8299\u857e\u72c4\u90a3\u6837\u771f\u6b63\u5b8c\u5168\u653e\u5f03\u6218\u6597\u80fd\u529b\uff0c\u4e13\u5fc3\u7814\u4e60\u6df1\u571f\u9884\u8a00\u7684\u60c5\u5f62\uff0c\u4e43\u662f\u7279\u4f8b\u4e2d\u7684\u7279\u4f8b\u3002</p><p>\u9634\u9b42\u57ce\u6ce8\u91cd\u7ec4\u7ec7\u5316\u548c\u7eaa\u5f8b\u6027\uff0c\u6240\u6709\u7684\u5deb\u5e08\u548c\u7267\u5e08\u5728\u5b66\u6821\u671f\u95f4\u90fd\u8981\u968f\u7740\u519b\u961f\u201c\u6f14\u4e60\u201d\uff0c\u63a5\u53d7\u4e25\u683c\u7684\u519b\u4e8b\u8bad\u7ec3\uff0c\u62ff\u5e7d\u5f71\u754c\u7684\u5404\u79cd\u5371\u9669\u602a\u7269\u7ec3\u624b\u3002\u8fd9\u6837\u57f9\u517b\u51fa\u6765\u7684\u65bd\u6cd5\u8005\uff0c\u5728\u5355\u5bf9\u5355\u7684\u6218\u6597\u4e2d\u8fd8\u4e0d\u89c9\u5f97\uff0c\u4e00\u65e6\u591a\u4eba\u6df7\u6218\uff0c\u4f18\u52bf\u4fbf\u987f\u65f6\u4f53\u73b0\u51fa\u6765\u3002\u9aa8\u9ab8\u7262\u7b3c\u7528\u4e8e\u9650\u5236\u5bf9\u624b\u7684\u884c\u52a8\uff0c\u4ea1\u7075\u8bc5\u5492\u6697\u4e2d\u524a\u5f31\u5bf9\u624b\u7684\u80fd\u529b\uff0c\u6700\u540e\u7528\u5927\u5a01\u529b\u7684\u5851\u80fd\u672f\u8f70\u70b8\u2014\u2014\u6574\u6574\u5341\u9053\u6cd5\u672f\uff0c\u5341\u4e2a\u5deb\u5e08\uff0c\u5f7c\u6b64\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u6ca1\u6709\u534a\u70b9\u7834\u7efd\u3002</p><p>\u552f\u4e00\u7684\u7834\u7efd\uff0c\u662f\u4ed6\u4eec\u8fd8\u4e0d\u591f\u5f3a\u3002</p><p>\u9762\u5bf9\u51e1\u4eba\u7684\u63a5\u8fde\u5192\u72af\uff0c\u5b89\u535a\u7406\u5f00\u59cb\u53d1\u52a8\u53cd\u51fb\uff0c\u5979\u88f8\u9732\u7684\u53f3\u80a9\u4e0a\u9f13\u52a8\u7740\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u6d3b\u7269\u5728\u84dd\u7eff\u8272\u7684\u76ae\u80a4\u4e0b\u9762\u722c\u884c\uff0c\u63a5\u7740\u4e00\u58f0\u7206\u54cd\uff0c\u4e00\u6761\u4e4c\u9ed1\u8272\u534a\u900f\u660e\u7684\u89e6\u624b\u81ea\u5973\u795e\u80a9\u90e8\u6025\u901f\u751f\u957f\u51fa\u6765\uff0c\u5b83\u8db3\u6709\u80f3\u818a\u7c97\u7ec6\uff0c\u957f\u6ee1\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u5012\u523a\uff0c\u4eff\u4f5b\u957f\u97ad\u822c\u7ed5\u8eab\u76d8\u65cb\u62bd\u6253\uff0c\u4e00\u51fb\u4fbf\u628a\u9aa8\u9ab8\u7262\u7b3c\u6253\u6210\u788e\u6e23\u3002\u97f3\u6ce2\u77db\u548c\u5bd2\u51b0\u9525\u63a5\u8fde\u5c04\u6765\uff0c\u5374\u88ab\u89e6\u624b\u73af\u8eab\u4e00\u626b\uff0c\u5c3d\u6570\u9707\u6210\u7c89\u788e\u3002\u552f\u4e00\u53d6\u5f97\u6218\u679c\u7684\u662f\u529b\u573a\u5251\uff0c\u5b83\u5728\u65bd\u6cd5\u8005\u7684\u64cd\u63a7\u4e0b\u9aa4\u7136\u53d8\u5411\uff0c\u51cc\u7a7a\u6a2a\u5207\uff0c\u89e6\u624b\u987f\u65f6\u88ab\u5212\u5f00\u4e00\u9053\u6df1\u6df1\u4f24\u53e3\uff0c\u9752\u84dd\u8272\u7684\u6c61\u8840\u98de\u6e85\u800c\u51fa\uff0c\u6ef4\u843d\u5728\u5730\uff0c\u53d1\u51fa\u6d53\u70c8\u7684\u8165\u81ed\u5473\u3002</p><p>\u201c\u627e\u6b7b\uff01\u201d</p><p>\u53d7\u4f24\u8ba9\u5b89\u535a\u7406\u518d\u5ea6\u72c2\u8e81\u8d77\u6765\uff0c\u6781\u5177\u5a01\u529b\u7684\u5492\u8a00\u81ea\u5979\u53e3\u4e2d\u5410\u51fa\uff0c\u5c06\u529b\u573a\u5251\u6253\u6210\u5341\u51e0\u5757\u788e\u7247\u3002\u540c\u65f6\u80a9\u90e8\u7684\u89e6\u624b\u50cf\u87d2\u86c7\u822c\u9ad8\u9ad8\u62ac\u8d77\uff0c\u968f\u5373\u547c\u5730\u4e00\u58f0\uff0c\u5728\u7a7a\u6c14\u4e2d\u5212\u51fa\u4e00\u9053\u4e4c\u9ed1\u5f27\u5149\uff0c\u7167\u7740\u4e00\u540d\u9634\u9b42\u57ce\u5deb\u5e08\u51f6\u731b\u62bd\u4e0b\u3002</p><p>\u556a\uff01</p><p>\u516d\u9762\u5149\u76fe\u63a5\u8fde\u5728\u865a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u6321\u5728\u5deb\u5e08\u7684\u9762\u524d\u2014\u2014\u7136\u540e\u5728\u4e0b\u4e00\u77ac\u95f4\u88ab\u6467\u67af\u62c9\u673d\u5730\u51fb\u6e83\u3002\u89e6\u624b\u6b63\u4e2d\u5deb\u5e08\u7684\u989d\u5934\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u7838\u6210\u4e86\u4e00\u6ee9\u6a21\u7cca\u8840\u8089\u3002\u89e6\u624b\u4f59\u52bf\u672a\u8870\uff0c\u5728\u5730\u9762\u4e0a\u4e00\u5f39\u800c\u8d77\uff0c\u671d\u7740\u62c9\u52a0\u62e6\u8170\u6a2a\u626b\u3002\u62c9\u52a0\u53f3\u624b\u4e94\u6307\u731b\u7136\u5f20\u5f00\uff0c\u5168\u8eab\u88ab\u4e00\u53ea\u95ea\u95ea\u53d1\u5149\u7684\u900f\u660e\u529b\u573a\u7403\u4f53\u6240\u7b3c\u7f69\u3002\u89e6\u624b\u7838\u4e0a\u529b\u573a\u7403\u4f53\u7684\u5916\u58c1\uff0c\u53d1\u51fa\u6c89\u95f7\u7684\u58f0\u54cd\uff0c\u5de8\u5927\u7684\u51b2\u51fb\u529b\u5c06\u62c9\u52a0\u50cf\u5f39\u73e0\u4e00\u822c\u62cd\u98de\u51fa\u53bb\uff0c\u91cd\u91cd\u649e\u4e0a\u4e86\u5927\u6bbf\u7684\u4e00\u6839\u67f1\u5b50\uff0c\u5657\u901a\u6454\u843d\u4e0b\u6765\uff0c\u4f46\u770b\u6a21\u6837\u534a\u70b9\u4e0d\u66fe\u53d7\u4f24\u3002</p><p>\u201c\u795e\u660e\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d</p><p>\u62c9\u52a0\u7ffb\u8eab\u8dc3\u8d77\uff0c\u51b7\u7b11\u7740\u64a4\u9500\u4e86\u7b3c\u7f69\u81ea\u8eab\u7684\u5c01\u7edd\u6cd5\u7403\u2014\u2014\u8fd9\u6cd5\u672f\u53ef\u4ee5\u7b97\u662f\u8679\u5149\u6cd5\u7403\u7684\u5f31\u5316\u7248\u672c\uff0c\u80fd\u963b\u9694\u591a\u79cd\u9b54\u6cd5\u548c\u7269\u7406\u653b\u51fb\uff0c\u4f46\u540c\u65f6\u4e5f\u9650\u5236\u4e86\u88ab\u4fdd\u62a4\u8005\u3002\u4ed6\u82e5\u4e0d\u64a4\u9500\u6cd5\u7403\uff0c\u81ea\u5df1\u5c31\u7b49\u4e8e\u662f\u88ab\u4e00\u76f4\u56f0\u5728\u4e86\u91cc\u9762\u3002\u201c\u5168\u4f53\u5206\u6563\uff0c\u6ce8\u610f\u9632\u5fa1\uff0c\u201d\u4ed6\u9ad8\u58f0\u547c\u559d\uff0c\u53d1\u51fa\u6307\u4ee4\uff0c\u201c\u53e0\u52a0\u8bc5\u5492\u3001\u53ec\u5524\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u89e6\u624b\u518d\u5ea6\u547c\u5578\u7740\u5377\u5730\u800c\u6765\uff0c\u52b2\u9053\u6bd4\u6b64\u524d\u66f4\u52a0\u731b\u70c8\u3002\u62c9\u52a0\u5374\u4e0d\u95ea\u907f\uff0c\u53f3\u624b\u63e1\u7740\u6cd5\u6756\u6a2a\u5728\u80f8\u524d\uff0c\u5de6\u624b\u9ad8\u9ad8\u626c\u8d77\uff0c\u638c\u5fc3\u5df2\u7ecf\u591a\u4e86\u4e00\u5f20\u5377\u8f74\u3002\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5e76\u4e0d\u5c55\u5f00\u9605\u8bfb\uff0c\u53ea\u662f\u4f4e\u4f4e\u559d\u4e86\u58f0\uff0c\u968f\u5373\u5377\u8f74\u4fbf\u731b\u7136\u70b8\u88c2\u5f00\u6765\uff0c\u4ece\u4e2d\u5c04\u51fa\u4e00\u9053\u9ed1\u8272\u5149\u675f\u3002</p><p>\u707f\u70c2\u661f\u5149\u518d\u6b21\u4ece\u7a79\u9876\u5c04\u4e0b\uff0c\u539f\u672c\u72ed\u957f\u7684\u9ed1\u8272\u5149\u675f\u9661\u7136\u6da8\u5927\uff0c\u540c\u65f6\u5316\u51fa\u590d\u5236\u4f53\u3002\u4e24\u9053\u9ed1\u5149\u547c\u5578\u7740\uff0c\u5982\u9f99\u86c7\u822c\u4ea4\u7f20\uff0c\u51dd\u805a\u6210\u950b\u8292\u5c16\u9510\u7684\u87ba\u65cb\u5f62\uff0c\u76f4\u76f4\u649e\u4e0a\u4e86\u8fce\u9762\u626b\u6765\u7684\u89e6\u624b\u3002</p><p>\u6eda\u6eda\u9ed1\u70df\u817e\u8d77\uff0c\u539f\u672c\u6c14\u52bf\u60ca\u4eba\u7684\u5de8\u5927\u89e6\u624b\u5728\u8d1f\u80fd\u91cf\u7684\u4fb5\u8680\u4e0b\u6025\u901f\u5e72\u67af\u3001\u840e\u7f29\uff0c\u76ae\u8089\u5e72\u762a\u584c\u9677\uff0c\u4e4c\u9ed1\u8272\u7684\u5149\u6ed1\u8868\u76ae\u4e0a\u4e5f\u5145\u6ee1\u4e86\u76b1\u7eb9\u548c\u5f00\u88c2\uff0c\u4eff\u4f5b\u51ac\u65e5\u7684\u8001\u6811\u76ae\uff0c\u7136\u540e\u6574\u4e2a\u89e6\u624b\u4e00\u8282\u8282\u5730\u65ad\u88c2\u5f00\u6765\uff0c\u6389\u843d\u5728\u5730\u3002\u5b89\u535a\u7406\u53d1\u51fa\u523a\u8033\u7684\u5c16\u53eb\u58f0\uff0c\u4e11\u964b\u7684\u9762\u5bb9\u56e0\u4e3a\u6124\u6012\u548c\u75db\u695a\u800c\u663e\u5f97\u65e0\u6bd4\u72f0\u72de\uff0c\u5979\u5f20\u5f00\u53cc\u81c2\uff0c\u6b63\u8981\u53d1\u52a8\u53cd\u51fb\uff0c\u4e00\u9053\u78a7\u7eff\u5c04\u7ebf\u4ece\u773c\u524d\u63a0\u8fc7\uff0c\u51c6\u786e\u65e0\u8bef\u5730\u547d\u4e2d\u4e86\u5979\u7684\u7709\u5fc3\u3002</p></article>"}}}]);
