(window.webpackJsonp=window.webpackJsonp||[]).push([[1668],{1691:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u8282 \u661f\u5760",content:"<article><h2>\u7b2c\u4e94\u5341\u8282 \u661f\u5760</h2><p>\u4ece\u82cf\u4f26\u548c\u838e\u5c14\u7684\u5149\u6697\u4e4b\u6218\uff0c\u5bfc\u81f4\u5929\u5730\u5f00\u8f9f\u65f6\u8ba1\u7b97\u8d77\uff0c\u4e00\u76f4\u5230\u8010\u745f\u745e\u5c14\u5efa\u56fd\u4e4b\u524d\uff0c\u5927\u7ea6\u6709\u4e00\u4e07\u4e94\u5343\u5e74\u3002\u5728\u8fd9\u6f2b\u957f\u7684\u65f6\u5149\u91cc\uff0c\u5de8\u9f99\u5728\u5929\u9645\u7ff1\u7fd4\uff0c\u7cbe\u7075\u5728\u68ee\u6797\u4e2d\u6e38\u8361\uff0c\u4e0d\u53ef\u8ba1\u6570\u7684\u795e\u7947\u81ea\u6df7\u6c8c\u4e2d\u8bde\u751f\uff0c\u5f7c\u6b64\u5f81\u6218\uff0c\u5f52\u4e8e\u6d88\u4ea1\uff0c\u4eba\u7c7b\u5219\u8fd8\u672a\u771f\u6b63\u767b\u4e0a\u5386\u53f2\u7684\u821e\u53f0\uff0c\u53ea\u5728\u9065\u8fdc\u7684\u5927\u9646\u6781\u4e1c\uff0c\u5982\u4eca\u88ab\u5730\u7406\u5b66\u5bb6\u79f0\u4e3a\u201c\u7edd\u5883\u4e1c\u57df\u201d\u7684\u5730\u65b9\uff0c\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\u5efa\u7acb\u8d77\u4e86\u4f20\u8bf4\u4e2d\u7684\u9b54\u6cd5\u738b\u671d\uff0c\u540e\u6765\u88ab\u6765\u81ea\u5f02\u754c\u7684\u795e\u738b\u4eec\u6467\u6bc1\u3002\u201c\u5927\u5730\u5148\u77e5\u201d\u963f\u8f9b\u591a\u5728\u4ed6\u7f16\u64b0\u7684\u53f2\u4e66\u4e2d\uff0c\u5c06\u8fd9\u6bb5\u65f6\u95f4\u7edf\u79f0\u4e3a\u201c\u7b2c\u4e00\u7eaa\u201d\uff0c\u540e\u6765\u88ab\u8010\u745f\u4eba\u5e7f\u6cdb\u6cbf\u7528\u3002\u73b0\u5982\u4eca\u56fd\u5ea6\u5185\u7684\u795e\u660e\uff0c\u5927\u7ea6\u6709\u5341\u5206\u4e4b\u4e00\u662f\u5728\u7b2c\u4e00\u7eaa\u65f6\u8bde\u751f\uff0c\u4f8b\u5982\u82cf\u4f26\u3001\u838e\u5c14\u3001\u5bc6\u65af\u62c9\u3001\u88f3\u63d0\u963f\u7b49\u7b49\uff0c\u8fd9\u4e9b\u795e\u7947\u5728\u5b97\u6559\u5b66\u4e0a\uff0c\u901a\u5e38\u53c8\u79f0\u4e4b\u4e3a\u201c\u8fdc\u53e4\u795e\u660e\u201d\uff0c\u533a\u522b\u4e8e\u63d0\u5c14\u3001\u73ed\u6069\u3001\u5e0c\u745e\u514b\u3001\u514b\u5170\u6c83\u8fd9\u4e9b\u540e\u6765\u8005\u3002\u5b89\u535a\u7406\u4f5c\u4e3a\u6d77\u795e\uff0c\u4e5f\u662f\u7b2c\u4e00\u7eaa\u65f6\u8bde\u751f\u7684\u3002</p><p>\u8fd9\u4e9b\u5386\u53f2\u548c\u5b97\u6559\u77e5\u8bc6\uff0c\u743c\u6069\u672a\u5fc5\u591a\u4e48\u719f\u6089\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u611f\u5174\u8da3\uff0c\u73ca\u5609\u5374\u662f\u5f88\u6e05\u695a\u7684\u3002\u5979\u6115\u7136\u56de\u795e\uff0c\u8fd9\u624d\u53d1\u73b0\u4e00\u4f4d\u5973\u5b50\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u9759\u9759\u7ad9\u5728\u8eab\u4fa7\uff0c\u770b\u4e0a\u53bb\u5e74\u9f84\u5927\u7ea6\u5728\u4e09\u56db\u5341\u5c81\u5de6\u53f3\uff0c\u7070\u8272\u5deb\u5e08\u888d\uff0c\u7070\u767d\u5934\u53d1\uff0c\u7d2b\u8272\u53cc\u76ee\u4e2d\u6709\u8fb0\u5149\u7480\u74a8\u95ea\u70c1\uff0c\u4eff\u4f5b\u661f\u8292\uff0c\u8eab\u6750\u6781\u9ad8\u3002\u73ca\u5609\u5728\u5973\u5b50\u4e2d\u5df2\u7ecf\u5c5e\u4e8e\u975e\u5e38\u9ad8\u6311\u7684\uff0c\u6bd4\u5979\u5374\u8fd8\u662f\u4f4e\u4e86\u534a\u4e2a\u5934\u3002\u7070\u888d\u5973\u5b50\u539f\u672c\u770b\u7740\u5927\u6bbf\u4e2d\u592e\u7684\u6218\u5c40\uff0c\u611f\u89c9\u5230\u73ca\u5609\u7684\u6ce8\u89c6\uff0c\u4fa7\u8fc7\u8138\u6765\u5fae\u5fae\u4e00\u7b11\u3002\u5979\u5634\u5507\u5f88\u8584\uff0c\u4e0b\u5df4\u5c16\u957f\uff0c\u7ec6\u957f\u7684\u773c\u89d2\u5f80\u4e0a\u6311\uff0c\u4e94\u5b98\u68f1\u89d2\u5206\u660e\uff0c\u6a21\u6837\u539f\u672c\u6709\u4e9b\u51b7\u5cfb\uff0c\u4f46\u8fd9\u4e00\u7b11\u5374\u662f\u6e29\u67d4\u65e0\u6bd4\uff0c\u900f\u7740\u53d1\u81ea\u5185\u5fc3\u7684\u5584\u610f\u548c\u559c\u7231\u3002\u73ca\u5609\u4e3a\u5979\u6240\u611f\u67d3\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u4e5f\u62a5\u4ee5\u6e29\u5a49\u4e00\u7b11\uff0c\u70b9\u5934\u793a\u610f\u3002\u53ea\u662f\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u5947\u602a\uff0c\u4e3a\u4f55\u6709\u964c\u751f\u4eba\u9760\u8fd1\uff0c\u4e24\u5c0a\u9b54\u50cf\u5374\u90fd\u6ca1\u6709\u534a\u70b9\u53cd\u5e94\uff0c\u4f9d\u65e7\u9759\u9759\u5446\u5728\u539f\u5730\uff0c\u50cf\u662f\u5bf9\u5979\u89c6\u800c\u4e0d\u89c1\u4f3c\u7684\u3002\u800c\u4e14\u5979\u660e\u660e\u7ad9\u5728\u81ea\u5df1\u8eab\u4fa7\uff0c\u600e\u4e48\u4f1a\u8bf4\u8bdd\u58f0\u76f4\u63a5\u4fbf\u5728\u8111\u4e2d\u54cd\u8d77\u3002</p><p>\u201c\u60a8\u597d\uff0c\u201d\u73ca\u5609\u8f7b\u58f0\u8bf4\uff0c\u201c\u60a8\u662f\u2026\u2026\u554a\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\uff1f\u201d</p><p>\u5979\u731b\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u521a\u624d\u8fd9\u4f4d\u7070\u888d\u5973\u5b50\u7684\u8bf4\u8bdd\uff0c\u4e0d\u5c31\u662f\u6b64\u524d\u5bc6\u745f\u80fd\u6838\u4e2d\u4f20\u51fa\u7684\u90a3\u4e2a\u58f0\u97f3\u4e48\u3002\u542c\u4e86\u73ca\u5609\u7684\u8bdd\uff0c\u7070\u888d\u5973\u5b50\u7684\u773c\u4e2d\u95ea\u8fc7\u4e00\u62b9\u5931\u843d\uff0c\u968f\u5373\u6d88\u901d\uff0c\u201c\u771f\u7684\u4e0d\u8ba4\u8bc6\u6211\u4e86\u5417\uff1f\u201d\u5979\u4f4e\u58f0\u95ee\u3002</p><p>\u201c\u554a\uff1f\u201d\u73ca\u5609\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u662f\u6211\u5fd8\u4e86\u2014\u2014\u55ef\uff0c\u6211\u5c31\u662f\u5965\u5609\u83b1\u65af\uff0c\u201d\u5973\u5b50\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u53eb\u73ca\u5609\u662f\u5427\uff0c\u6211\u542c\u4ed6\u8fd9\u4e48\u53eb\u4f60\uff0c\u201d\u5979\u8bf4\u7740\uff0c\u6307\u7740\u743c\u6069\uff0c\u201c\u4ed6\u5c31\u662f\u4f60\u559c\u6b22\u7684\u4eba\uff1f\u201d</p><p>\u73ca\u5609\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f46\u53ea\u662f\u7f9e\u602f\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u5bf9\u65b9\u7684\u95ee\u9898\u5192\u5931\u800c\u751f\u6c14\uff0c\u201c\u55ef\u3002\u201d\u5979\u6700\u540e\u70b9\u70b9\u5934\uff0c\u7b97\u662f\u627f\u8ba4\u3002</p><p>\u201c\u770b\u8d77\u6765\u4e0d\u600e\u4e48\u6837\u554a\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bc4\u4ef7\uff0c\u201c\u4f60\u5728\u9009\u62e9\u7537\u4eba\u65b9\u9762\u7684\u773c\u5149\u600e\u4e48\u603b\u662f\u6ca1\u8fdb\u6b65\u5462\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d\u542c\u5230\u5bf9\u65b9\u8d2c\u4f4e\u743c\u6069\uff0c\u73ca\u5609\u65e2\u662f\u4e0d\u60a6\uff0c\u53c8\u6709\u4e9b\u7591\u60d1\uff0c\u201c\u603b\u662f\u6ca1\u8fdb\u6b65\uff1f\u201d</p><p>\u6211\u4e5f\u53ea\u9009\u62e9\u8fc7\u8fd9\u4e00\u4e2a\u7537\u4eba\u5427\u3002</p><p>\u201c\u8bf4\u9519\u4e86\uff0c\u201d\u610f\u8bc6\u5230\u81ea\u5df1\u7684\u5931\u8a00\uff0c\u5965\u5609\u83b1\u65af\u968f\u53e3\u6577\u884d\uff0c\u201c\u957f\u76f8\u5012\u8fd8\u8fc7\u5f97\u53bb\uff0c\u9a6c\u9a6c\u864e\u864e\u5427\u2026\u2026\u4ed6\u65c1\u8fb9\u90a3\u4e09\u4e2a\u5973\u4eba\u662f\u8c01\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u662f\u4ed6\u7684\u670b\u53cb\u3002\u201d</p><p>\u201c\u670b\u53cb\uff1f\u201d\u5965\u5609\u83b1\u65af\u53cd\u95ee\u3002</p><p>\u73ca\u5609\u6c89\u9ed8\u3002</p><p>\u201c\u7537\u4eba\u679c\u7136\u90fd\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u201d\u5965\u5609\u83b1\u65af\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u518d\u8ffd\u7a76\u8fd9\u4e2a\u95ee\u9898\uff0c\u8f6c\u8fc7\u8138\u53bb\u7ee7\u7eed\u89c2\u770b\u6218\u5c40\u3002\u62c9\u52a0\u5df2\u7ecf\u63a5\u8fde\u53c8\u4f7f\u7528\u4e86\u4e24\u5f20\u51cb\u6b7b\u672f\u5377\u8f74\uff0c\u751a\u81f3\u8fd8\u53d1\u51fa\u4e86\u4e00\u51fb\u9633\u708e\u7206\uff0c\u743c\u6069\u4e5f\u52a8\u7528\u4e86\u6700\u540e\u4e00\u53d1\u89e3\u79bb\u672f\u3002\u4ed6\u4eec\u7684\u8054\u624b\u653b\u51fb\u6210\u529f\u5730\u5c06\u89e6\u624b\u602a\u7684\u56db\u5206\u4e4b\u4e00\u8eab\u8eaf\u5316\u4f5c\u98de\u7070\u2014\u2014\u4f46\u5c31\u5728\u73ca\u5609\u548c\u5965\u5609\u83b1\u65af\u8bf4\u8bdd\u8fd9\u70b9\u65f6\u95f4\uff0c\u5b83\u5c45\u7136\u53c8\u957f\u4e86\u56de\u6765\u3002</p><p>\u5b83\u7684\u518d\u751f\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\u4e86\u3002</p><p>\u5deb\u5e08\u4eec\u8fd8\u5728\u62fc\u547d\u653b\u51fb\uff0c\u4f46\u706b\u529b\u7684\u731b\u70c8\u7a0b\u5ea6\u660e\u663e\u4e0d\u5982\u4ee5\u524d\uff0c\u800c\u86c7\u9b54\u4e5f\u6e10\u6e10\u62b5\u6321\u4e0d\u4f4f\u89e6\u624b\u7684\u8f6e\u756a\u7ede\u6740\uff0c\u6b66\u5668\u6325\u821e\u7684\u901f\u5ea6\u4e5f\u6162\u4e86\u4e0b\u6765\uff0c\u751a\u81f3\u88ab\u89e6\u624b\u593a\u8d70\u4e86\u4e00\u67c4\u6218\u65a7\u3002\u7167\u8fd9\u79cd\u5f62\u52bf\u53d1\u5c55\u4e0b\u53bb\uff0c\u518d\u8fc7\u51e0\u5206\u949f\uff0c\u53ea\u6015\u9634\u9b42\u57ce\u4e00\u65b9\u5c31\u8981\u4e00\u8d25\u6d82\u5730\u4e86\u3002</p><p>\u201c\u5567\uff0c\u771f\u5dee\u52b2\u54ea\uff0c\u201d\u5965\u5609\u83b1\u65af\u89c2\u770b\u7740\u6218\u5c40\uff0c\u4e8b\u4e0d\u5173\u5df1\u5730\u8bc4\u4ef7\uff0c\u201c\u8fd9\u4e48\u591a\u4eba\uff0c\u8fde\u53ea\u79bb\u6c34\u4e0a\u5cb8\u7684\u4e4c\u8d3c\u90fd\u641e\u4e0d\u5b9a\uff0c\u8fd8\u597d\u610f\u601d\u81ea\u79f0\u5deb\u5e08\uff0c\u4e0d\u5982\u65e9\u65e9\u649e\u5899\u81ea\u6740\u7b97\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u90a3\u5bb6\u4f19\u5f88\u96be\u5bf9\u4ed8\u554a\uff01\u201d\u73ca\u5609\u4e0d\u6ee1\u5730\u6297\u8bae\uff0c\u201c\u6bd5\u7adf\u662f\u795e\u7947\u3002\u201d</p><p>\u201c\u55e4\uff0c\u795e\u7947\u7b97\u4ec0\u4e48\uff0c\u6211\u53c8\u4e0d\u662f\u6ca1\u6740\u8fc7\uff0c\u5f53\u5e74\u6211\u548c\u4f60\u2026\u2026\u7b97\u4e86\uff0c\u4e0d\u63d0\u90a3\u4e2a\u6df7\u86cb\uff0c\u201d\u5965\u5609\u83b1\u65af\u4e0d\u5c51\u5730\u8bf4\uff0c\u201c\u4f55\u51b5\u5b89\u535a\u7406\u73b0\u5728\u53ea\u662f\u5723\u8005\uff0c\u6211\u53c8\u53d6\u8d70\u4e86\u5979\u4e03\u6210\u4ee5\u4e0a\u7684\u795e\u529b\u6765\u652f\u6301\u5bc6\u745f\u80fd\u6838\uff0c\u8fd8\u5b8c\u5168\u963b\u9694\u4e86\u6d77\u6c34\u2014\u2014\u7b97\u4e86\uff0c\u662f\u6211\u7684\u9519\uff0c\u672c\u6765\u5c31\u4e0d\u5e94\u8be5\u5bf9\u590f\u591a\u7684\u624b\u4e0b\u62b1\u4ec0\u4e48\u671f\u671b\u624d\u5bf9\u3002\u201d</p><p>\u201c\u90a3\u4e48\u60a8\u51c6\u5907\u4eb2\u81ea\u4e0a\u573a\u4e86\u5417\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u867d\u7136\u4e00\u5f00\u59cb\u7684\u5370\u8c61\u826f\u597d\uff0c\u4f46\u5728\u968f\u540e\u7684\u4ea4\u8c08\u4e2d\uff0c\u5965\u5609\u83b1\u65af\u4e0b\u610f\u8bc6\u5c55\u9732\u51fa\u6765\u7684\u90a3\u79cd\u50b2\u6162\u2014\u2014\u4e3b\u8981\u662f\u9488\u5bf9\u743c\u6069\u7684\u50b2\u6162\u2014\u2014\u8ba9\u73ca\u5609\u5fc3\u751f\u4e0d\u5feb\u3002\u5979\u4e5f\u4e0d\u559c\u6b22\u743c\u6069\u8eab\u8fb9\u6709\u522b\u7684\u5973\u5b69\u5b50\uff0c\u5979\u4e5f\u5e0c\u671b\u838e\u73de\u514b\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u4fe9\u4ece\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6d88\u5931\uff0c\u6700\u597d\u4ece\u6765\u4e0d\u5b58\u5728\u2014\u2014\u4f46\u5973\u5b69\u5b50\u7684\u5171\u901a\u5fc3\u7406\u662f\uff1a\u6211\u7684\u7537\u670b\u53cb\uff0c\u6211\u80fd\u8bf4\u5f97\uff0c\u4f60\u8bf4\u4e0d\u5f97\u3002</p><p>\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u5979\u7684\u95ee\u8bdd\u8bed\u6c14\u4e5f\u5c31\u6709\u4e9b\u786c\u90a6\u90a6\uff0c\u751a\u81f3\u8fd8\u7565\u5e26\u51e0\u5206\u8ba5\u8bbd\u3002\u5965\u5609\u83b1\u65af\u81ea\u7136\u4e0d\u4f1a\u542c\u4e0d\u51fa\u6765\uff0c\u5979\u5bf9\u743c\u6069\u548c\u5176\u4ed6\u4eba\u6beb\u4e0d\u5ba2\u6c14\uff0c\u5bf9\u73ca\u5609\u5374\u4f3c\u4e4e\u662f\u5341\u5206\u4e4b\u5bbd\u5bb9\uff0c\u95fb\u8a00\u4e00\u7b11\uff0c\u50cf\u662f\u542c\u5230\u5b69\u5b50\u8bdd\u822c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u201c\u6211\u4e48\uff0c\u73b0\u5728\u5012\u662f\u5df2\u7ecf\u6ca1\u6cd5\u52a8\u624b\u4e86\u5462\uff0c\u201d\u5979\u975e\u5e38\u5766\u767d\u5730\u8bf4\uff0c\u201c\u770b\u4e0d\u51fa\u6765\u5417\uff0c\u51fa\u73b0\u5728\u4f60\u9762\u524d\u7684\u6211\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u865a\u5f71\u7f62\u4e86\u3002\u201d</p><p>\u88ab\u5979\u4e00\u63d0\u9192\uff0c\u73ca\u5609\u5b9a\u775b\u518d\u770b\uff0c\u679c\u7136\u53d1\u73b0\u5982\u6b64\u3002\u6b63\u5982\u5965\u5609\u83b1\u65af\u6240\u8a00\uff0c\u5979\u5e76\u975e\u5b9e\u4f53\uff0c\u53ea\u662f\u865a\u5f71\uff0c\u96be\u602a\u65c1\u8fb9\u7684\u9b54\u50cf\u90fd\u6ca1\u6709\u53cd\u5e94\u3002\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\uff0c\u4f60\u4e0d\u662f\u8fd8\u6709\u5bc6\u745f\u80fd\u6838\u5417\uff1f\u201d\u73ca\u5609\u50cf\u662f\u6293\u4f4f\u4e86\u6700\u540e\u7684\u673a\u4f1a\uff0c\u201c\u4f60\u662f\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u7684\u4e3b\u4eba\uff0c\u4f60\u8fd8\u53ef\u4ee5\u8fd0\u7528\u5bc6\u745f\u80fd\u6838\u7684\u529b\u91cf\u5bf9\u5427\u3002\u201d</p><p>\u201c\u53ef\u4ee5\u662f\u53ef\u4ee5\uff0c\u4f46\u53ea\u80fd\u662f\u5f88\u4f4e\u7a0b\u5ea6\u5730\u8c03\u7528\u5b83\u7684\u529b\u91cf\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u8981\u5e72\u6389\u8fd9\u4e2a\u4e11\u5973\u4eba\uff0c\u6700\u9002\u5408\u7684\u5c31\u662f\u5760\u661f\u672f\uff0c\u4e00\u51fb\u5c31\u80fd\u8ba9\u5979\u53d8\u6210\u7070\u70ec\u3002\u4f46\u662f\u661f\u9668\u672f\u6240\u9700\u8981\u7684\u5f3a\u5ea6\u592a\u9ad8\uff0c\u6211\u6ca1\u529e\u6cd5\u53d1\u52a8\u5462\u3002\u201d</p><p>\u201c\u7136\u540e\u5462\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u5979\u7f3a\u4e4f\u5192\u9669\u548c\u6218\u6597\u7ecf\u9a8c\uff0c\u5374\u4e0d\u7f3a\u4e4f\u806a\u6167\u7684\u5934\u8111\uff0c\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5965\u5609\u83b1\u65af\u4f1a\u548c\u5979\u8bf4\u8fd9\u4e9b\uff0c\u4e0d\u53ef\u80fd\u771f\u7684\u662f\u95f2\u6781\u65e0\u804a\u6ca1\u8bdd\u627e\u8bdd\uff0c\u5fc5\u5b9a\u662f\u6709\u6240\u7528\u610f\u3002\u201c\u90a3\u4e48\u600e\u4e48\u6837\u624d\u80fd\u8ba9\u4f60\u80fd\u591f\u53d1\u52a8\u5760\u661f\u672f\u5462\uff1f\u201d\u73ca\u5609\u53c8\u95ee\u3002</p><p>\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5965\u5609\u83b1\u65af\u5e72\u8106\u5229\u843d\u5730\u5426\u51b3\u4e86\uff0c\u201c\u4ec0\u4e48\u529e\u6cd5\u90fd\u6ca1\u6709\uff0c\u6211\u65e9\u5df2\u7ecf\u6b7b\u53bb\uff0c\u5355\u9760\u7075\u9b42\u662f\u4e0d\u53ef\u80fd\u771f\u6b63\u64cd\u63a7\u5bc6\u745f\u80fd\u6838\u7684\u2014\u2014\u4e0d\u8fc7\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff0c\u201d\u5979\u7b11\u7740\u8bf4\uff0c\u201c\u7531\u4f60\u6765\u53d1\u52a8\u5c31\u884c\u4e86\u561b\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u6211\u6839\u672c\u4e0d\u4f1a\u4ec0\u4e48\u5760\u661f\u672f\u2026\u2026\u201d</p><p>\u201c\u4f60\u4f1a\u3002\u201d\u5965\u5609\u83b1\u65af\u6253\u65ad\u3002</p><p>\u73ca\u5609\u77aa\u5927\u773c\u775b\u770b\u7740\u5979\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\u5979\u653e\u5f03\u4e86\u4e89\u8fa9\uff0c\u201c\u5f88\u597d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90a3\u4e48\u544a\u8bc9\u6211\u600e\u4e48\u505a\u3002\u201d</p><p>\u9971\u542b\u7740\u6ee1\u610f\u548c\u8d5e\u8bb8\u7684\u7b11\u5bb9\u5728\u5965\u5609\u83b1\u65af\u7684\u8138\u4e0a\u7efd\u5f00\uff0c\u201c\u5c31\u662f\u8fd9\u6837\uff0c\u5b69\u5b50\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u8fd9\u624d\u662f\u4f60\u771f\u6b63\u7684\u6837\u5b50\u3002\u201d</p><p>\u73ca\u5609\u6ca1\u6709\u660e\u767d\u8fd9\u53e5\u8bdd\u7684\u542b\u4e49\uff0c\u6216\u8005\u8bf4\u5979\u6ca1\u6709\u771f\u6b63\u5730\u660e\u767d\uff0c\u201c\u544a\u8bc9\u6211\u600e\u4e48\u505a\uff01\u201d\u5979\u50ac\u4fc3\u7740\u3002</p><p>\u201c\u505a\u4f60\u6700\u62ff\u624b\u7684\u4e8b\u60c5\u5c31\u884c\u3002\u201d\u5965\u5609\u83b1\u65af\u56de\u7b54\u3002</p><p>\u203b\u203b\u203b</p><p>\u5c31\u5728\u8fd9\u77ed\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u5927\u6bbf\u4e2d\u592e\u7684\u5c40\u52bf\u53c8\u5df2\u7ecf\u53d1\u751f\u4e86\u6539\u53d8\uff0c\u5b89\u535a\u7406\u5b8c\u5168\u5360\u636e\u4e0a\u98ce\u3002\u86c7\u9b54\u63a5\u8fde\u53d7\u521b\uff0c\u4e09\u53ea\u624b\u81c2\u5df2\u7ecf\u88ab\u89e6\u624b\u4ece\u8eab\u4f53\u4e0a\u786c\u751f\u751f\u5730\u6495\u6389\uff0c\u5018\u82e5\u4e0d\u662f\u88ab\u9b54\u6cd5\u7ea6\u675f\uff0c\u53d7\u6b64\u91cd\u521b\uff0c\u5979\u65e9\u5c31\u5df2\u7ecf\u8f6c\u8eab\u9003\u56de\u6df1\u6e0a\u4e86\u3002\u5c3d\u7ba1\u88ab\u53ec\u5524\u800c\u6765\u7684\u53ea\u662f\u6295\u5f71\uff0c\u4f46\u5018\u82e5\u88ab\u6467\u6bc1\u7684\u8bdd\uff0c\u540c\u6837\u4e5f\u662f\u4f1a\u5bf9\u6df1\u6e0a\u4e2d\u7684\u672c\u4f53\u9020\u6210\u5de8\u5927\u4f24\u5bb3\u548c\u524a\u5f31\u7684\u3002</p><p>\u5657\u5657\u5657\u5657\u5657\u5657\u5657\u5657\uff01</p><p>\u4e00\u8fde\u4e32\u55b7\u5c04\u58f0\u54cd\u8d77\uff0c\u5341\u51e0\u6761\u89e6\u624b\u7684\u5c16\u7aef\u9aa4\u7136\u5f20\u5f00\uff0c\u9752\u9ed1\u8272\u7684\u58a8\u6c41\u4ece\u4e2d\u98d9\u5c04\u800c\u51fa\u3002\u86c7\u9b54\u548c\u4e24\u540d\u5deb\u5e08\u8eb2\u95ea\u4e0d\u53ca\uff0c\u88ab\u55b7\u4e2a\u6b63\u7740\uff0c\u58a8\u6c41\u843d\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u4eff\u4f5b\u6c34\u73e0\u6ef4\u5728\u6c99\u4e2d\uff0c\u77ac\u95f4\u4fbf\u6e17\u900f\u76ae\u80a4\uff0c\u8fdb\u5165\u4f53\u5185\u3002\u51e0\u79d2\u949f\u540e\uff0c\u5e26\u7740\u6d53\u70c8\u8165\u6c14\u7684\u9ed1\u8840\u4ece\u4ed6\u4eec\u53e3\u4e2d\u3001\u9f3b\u4e2d\u3001\u773c\u4e2d\u3001\u8033\u4e2d\u6c69\u6c69\u6d41\u6dcc\u800c\u51fa\u3002\u4e24\u540d\u5deb\u5e08\u7684\u8eab\u4f53\u53d8\u5f97\u6d6e\u80bf\u53d1\u767d\uff0c\u6323\u624e\u6447\u6643\u7740\u5012\u5730\uff0c\u6d3b\u50cf\u662f\u88ab\u6eba\u6b7b\u7684\u5c38\u4f53\uff0c\u800c\u86c7\u9b54\u5728\u63a5\u8fde\u53d7\u521b\u540e\uff0c\u7ec8\u4e8e\u4e5f\u65e0\u6cd5\u7ee7\u7eed\u7ef4\u6301\u5728\u7269\u8d28\u754c\u7684\u6295\u5f71\u5b58\u5728\uff0c\u4e00\u9635\u5149\u8292\u95ea\u8fc7\uff0c\u5979\u88ab\u653e\u9010\u56de\u4e86\u6df1\u6e0a\u3002</p><p>\u5931\u53bb\u4e86\u8fd9\u4e2a\u5f3a\u529b\u8089\u76fe\uff0c\u9634\u9b42\u57ce\u4e00\u65b9\u7684\u5904\u5883\u66f4\u52a0\u5c8c\u5c8c\u53ef\u5371\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u8d25\u5c40\u5df2\u5b9a\u3002\u62c9\u52a0\u63a5\u8fde\u4f7f\u7528\u4e86\u4e09\u4e2a\u73cd\u8d35\u7684\u9b54\u6cd5\u9053\u5177\uff0c\u7f16\u7ec7\u6210\u4e00\u9053\u70c8\u7130\u548c\u95ea\u7535\u4e4b\u7f51\uff0c\u6682\u65f6\u5c06\u5b89\u535a\u7406\u7684\u6240\u6709\u89e6\u624b\u90fd\u963b\u6321\u56de\u53bb\u3002\u201c\u9000\uff01\u201d\u4ed6\u72fc\u72c8\u5730\u8bf4\uff0c\u6307\u6325\u5176\u4ed6\u5deb\u5e08\u4ea4\u66ff\u63a9\u62a4\u65bd\u6cd5\uff0c\u671d\u7740\u5927\u6bbf\u95e8\u53e3\u9000\u53bb\u3002\u8fd9\u4e00\u6218\u4ed6\u53c8\u6298\u635f\u4e86\u56db\u540d\u90e8\u4e0b\uff0c\u52a0\u4e0a\u6b64\u524d\u4e09\u4eba\uff0c\u5df2\u7ecf\u635f\u5931\u4e86\u4e03\u540d\u9ad8\u9636\u5deb\u5e08\u3002\u4f5c\u4e3a\u8d1f\u8d23\u4eba\uff0c\u4ed6\u56de\u53bb\u4e4b\u540e\u88ab\u4e25\u5389\u5904\u5206\u5728\u6240\u96be\u514d\uff0c\u4f46\u6b64\u65f6\u4e5f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\uff0c\u6027\u547d\u6700\u8981\u7d27\u3002</p><p>\u743c\u6069\u62bf\u7740\u5634\u5507\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u968f\u4e4b\u540e\u9000\u3002\u4ed6\u5012\u4e0d\u5fc5\u62c5\u5fc3\u88ab\u8ffd\u7a76\u8d23\u4efb\u4e4b\u7c7b\u7684\u95ee\u9898\uff0c\u4f46\u4e5f\u5b9e\u5728\u6709\u4e9b\u4e0d\u7518\u5fc3\uff0c\u773c\u524d\u7684\u8fd9\u4f4d\u6d77\u795e\u5b89\u535a\u7406\uff0c\u4e0d\u77e5\u9053\u56e0\u4e3a\u4ec0\u4e48\u539f\u56e0\uff0c\u603b\u4e4b\u5168\u7136\u6ca1\u6709\u795e\u660e\u5e94\u6709\u7684\u5a01\u80fd\uff0c\u66f4\u591a\u50cf\u662f\u4e2a\u5927\u602a\u7269\uff0c\u653b\u51fb\u529b\u5176\u5b9e\u4e0d\u5f3a\uff0c\u5c31\u662f\u8eaf\u4f53\u592a\u5927\uff0c\u518d\u751f\u80fd\u529b\u592a\u53d8\u6001\uff0c\u901a\u4fd7\u5730\u8bf4\uff0c\u5c31\u662f\u8840\u91cf\u8d85\u591a\uff0c\u56de\u8840\u901f\u5ea6\u8d85\u5feb\uff0c\u600e\u4e48\u90fd\u6253\u4e0d\u6b7b\u3002\u88ab\u8fd9\u79cd\u5bf9\u624b\u6253\u8d25\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u5f88\u90c1\u95f7\u7684\u4e8b\u60c5\u3002</p><p>\u7136\u800c\u4ed6\u65e0\u53ef\u5948\u4f55\u3002</p><p>\u89e3\u79bb\u672f\u6839\u672c\u65e0\u6cd5\u6467\u6bc1\u5982\u6b64\u5e9e\u5927\u7684\u602a\u7269\uff0c\u77f3\u5316\u672f\u4e5f\u57fa\u672c\u4e0d\u53ef\u80fd\u594f\u6548\uff0c\u800c\u4e14\u4ed6\u4eca\u5929\u4e5f\u6839\u672c\u6ca1\u6709\u51c6\u5907\u8fd9\u6cd5\u672f\u3002\u878d\u5408\u4e86\u5409\u52d2\u4eca\u7684\u795e\u529b\u4e4b\u540e\uff0c\u743c\u6069\u5df2\u7ecf\u80fd\u591f\u6210\u529f\u8054\u7ed3\u5230\u7b2c\u4e03\u5c42\u9b54\u7f51\uff0c\u8fd9\u8ba9\u4ed6\u53ef\u4ee5\u6c72\u53d6\u8fd0\u4f7f\u66f4\u5145\u6c9b\u7684\u9b54\u529b\uff0c\u5851\u9020\u6210\u5a01\u529b\u66f4\u5f3a\u5927\u7684\u6cd5\u672f\u2014\u2014\u4f46\u8fd9\u4e9b\u6cd5\u672f\u4ed6\u8fd8\u6ca1\u6765\u5f97\u53ca\u53bb\u5b66\u4e60\u638c\u63e1\u5462\u3002\u5965\u6c83\u5012\u662f\u7559\u7ed9\u4e86\u4ed6\u4e00\u5f20\u4fdd\u547d\u738b\u724c\uff0c\u90a3\u4e2a\u9ed1\u8272\u7684\u5c0f\u5c0f\u9ab7\u9ac5\u5934\u9aa8\uff0c\u53ea\u8981\u634f\u788e\u5c31\u4f1a\u91ca\u653e\u51fa\u4e00\u9762\u9b54\u6cd5\u62a4\u76fe\uff0c\u80fd\u591f\u5b8c\u5168\u9006\u8f6c\u5bf9\u624b\u7684\u4e0b\u4e00\u6b21\u653b\u51fb\u3002\u7528\u6e38\u620f\u672f\u8bed\u6253\u6bd4\u65b9\uff0c\u5bf9\u624b\u51c6\u5907\u53d1\u52a8\u4e00\u6b21\u4f24\u5bb3\u503c\u4e00\u5343\u7684\u653b\u51fb\uff0c\u53ea\u8981\u743c\u6069\u62a2\u5148\u634f\u788e\u9ab7\u9ac5\u5934\u9aa8\uff0c\u90a3\u4e48\u8fd9\u4f24\u5bb3\u503c\u4e00\u5343\u7684\u653b\u51fb\u5c31\u4f1a\u5c3d\u6570\u9006\u8f6c\u53cd\u5f39\u5230\u5bf9\u624b\u81ea\u5df1\u8eab\u4e0a\u3002\u8fd9\u786e\u5b9e\u662f\u975e\u5e38\u5f3a\u529b\u7684\u6cd5\u672f\u2014\u2014\u95ee\u9898\u662f\uff0c\u773c\u524d\u8fd9\u4e2a\u5927\u602a\u7269\uff0c\u5c31\u7b97\u88ab\u81ea\u5df1\u7684\u89e6\u624b\u62bd\u4e0a\u4e2a\u5341\u4e03\u516b\u6b21\uff0c\u4f30\u8ba1\u4e5f\u8fd8\u662f\u6b7b\u4e0d\u4e86\u5427\u3002</p><p>\u4e0d\u8fc7\u5f88\u5947\u602a\u554a\uff0c\u743c\u6069\u9661\u7136\u60f3\u8d77\u6765\uff0c\u8fd9\u5ea7\u661f\u9668\u57ce\u7684\u57ce\u4e3b\u5965\u5609\u83b1\u65af\u5973\u58eb\u5728\u505a\u4ec0\u4e48\u5462\uff1f\u9634\u9b42\u57ce\u548c\u6d77\u795e\u5b89\u535a\u7406\u7684\u51b2\u7a81\uff0c\u8bf4\u8d77\u6765\u8fd8\u662f\u5979\u6311\u8d77\u7684\uff0c\u5728\u6218\u6597\u7684\u4e00\u5f00\u59cb\uff0c\u5979\u8fd8\u62b5\u5fa1\u4e86\u5b89\u535a\u7406\u5bf9\u5bc6\u745f\u80fd\u6838\u7684\u653b\u51fb\uff0c\u4ee5\u53ca\u64cd\u7eb5\u8ff7\u9501\u5728\u62c9\u52a0\u91ca\u653e\u7684\u4e24\u9053\u6cd5\u672f\u4e0a\u53e0\u52a0\u4e86\u201c\u6781\u6548\u590d\u5236\u201d\uff0c\u7136\u540e\u2026\u2026\u7136\u540e\u5c31\u6084\u65e0\u58f0\u606f\u5730\u518d\u6ca1\u534a\u70b9\u52a8\u9759\u4e86\u3002</p><p>\u5c31\u76ee\u524d\u6240\u89c2\u5bdf\u5230\u7684\u4fe1\u606f\u6765\u5224\u65ad\uff0c\u5965\u5609\u83b1\u65af\u548c\u5b89\u535a\u7406\u4f3c\u4e4e\u66fe\u7ecf\u4e00\u5ea6\u5904\u4e8e\u76df\u53cb\u5173\u7cfb\u3002\u6d77\u795e\u50cf\u662f\u4e3a\u4e86\u8eb2\u907f\u67d0\u4e2a\u5f3a\u5927\u7684\u654c\u4eba\uff08\u5e94\u8be5\u5c31\u662f\u5965\u5609\u83b1\u65af\u53e3\u4e2d\u6240\u8bf4\u7684\u201c\u72ec\u773c\u9f99\u201d\uff09\u8fdb\u5165\u6b64\u5730\uff0c\u63a5\u53d7\u4e86\u661f\u9668\u57ce\u57ce\u4e3b\u7684\u5e87\u62a4\uff0c\u53cd\u8fc7\u6765\u8bf4\uff0c\u661f\u9668\u57ce\u7684\u5bc6\u745f\u80fd\u6838\u5728\u9b54\u7f51\u7684\u201c\u6e90\u201d\u88ab\u5c01\u95ed\u7684\u60c5\u51b5\u4e0b\u8fd8\u80fd\u7ee7\u7eed\u8fd0\u4f5c\uff0c\u53ea\u6015\u4e5f\u548c\u5b89\u535a\u7406\u6709\u5173\u3002\u4f46\u73b0\u5728\u56e0\u4e3a\u9634\u9b42\u57ce\u5deb\u5e08\u7684\u5230\u6765\uff0c\u5965\u5609\u83b1\u65af\u5df2\u7ecf\u516c\u5f00\u5ba3\u5e03\u548c\u5b89\u535a\u7406\u51b3\u88c2\uff0c\u6240\u4ee5\u5b89\u535a\u7406\u624d\u4f1a\u65a5\u8d23\u8bf4\u201c\u80cc\u4fe1\u5f03\u4e49\u7684\u51e1\u4eba\u201d\uff0c\u5e76\u4e14\u4f18\u5148\u51fa\u624b\u653b\u51fb\u5979\uff0c\u76f4\u5230\u88ab\u9634\u9b42\u57ce\u8bf8\u4eba\u6240\u6253\u65ad\u3002</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u5965\u5609\u83b1\u65af\u6ca1\u9053\u7406\u4f1a\u8896\u624b\u65c1\u89c2\u624d\u5bf9\uff0c\u5426\u5219\u9634\u9b42\u57ce\u8bf8\u4eba\u4e00\u8d25\u9000\uff0c\u63a5\u4e0b\u6765\u5b89\u535a\u7406\u80af\u5b9a\u5c31\u8981\u628a\u76ee\u6807\u8f6c\u5411\u5979\u4e86\u3002</p><p>\u60f3\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u62ac\u5934\u770b\u5411\u7a79\u9876\uff0c\u7136\u540e\u60ca\u8bb6\u5730\u53d1\u73b0\u90a3\u9897\u5149\u8292\u523a\u76ee\u7684\u5bc6\u745f\u80fd\u6838\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u53d8\u5f97\u9eef\u6de1\uff0c\u4f53\u79ef\u4e5f\u56e0\u6b64\u50cf\u662f\u7f29\u5c0f\u4e86\u8bb8\u591a\uff0c\u539f\u672c\u73af\u7ed5\u5305\u88f9\u5b83\u7684\u516d\u9053\u5f69\u8272\u7b26\u6587\u5149\u5e26\u90fd\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u5c0f\u56e2\u4e00\u5c0f\u56e2\u7684\u900f\u660e\u6c34\u6c7d\uff0c\u4e91\u6735\u822c\u5206\u6563\u5f25\u6f2b\u7740\uff0c\u98ce\u4ece\u4e2d\u5439\u51fa\uff0c\u5939\u6742\u7740\u7ec6\u5c0f\u7684\u95ea\u7535\u548c\u706b\u82b1\u3002\u8fd9\u79cd\u53d8\u5316\u5230\u5e95\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u743c\u6069\u4e00\u65e0\u6240\u77e5\uff0c\u8fde\u63e3\u6d4b\u90fd\u65e0\u4ece\u63e3\u6d4b\u8d77\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u719f\u6089\u7684\u60a0\u626c\u7b1b\u58f0\u81ea\u8eab\u540e\u4f20\u6765\u3002</p><p>\u743c\u6069\u6115\u7136\u56de\u8eab\uff0c\u53ea\u89c1\u88ab\u4e24\u5c0a\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u4fdd\u62a4\u5728\u4e2d\u95f4\u7684\u73ca\u5609\uff0c\u4e0d\u77e5\u4f55\u65f6\u53d6\u51fa\u4e86\u4ed6\u9001\u7684\u90a3\u652f\u957f\u7b1b\uff0c\u8d34\u5728\u5507\u8fb9\u6b63\u5168\u795e\u8d2f\u6ce8\u5730\u5439\u594f\u7740\u3002\u90a3\u662f\u300a\u5929\u7a79\u300b\uff0c\u8010\u745f\u5927\u5965\u672f\u5e08\u201c\u65c5\u8005\u201d\u6b27\u8d1d\u4f26\u4e3a\u5973\u513f\u7684\u5341\u5c81\u751f\u65e5\u6240\u7279\u522b\u521b\u4f5c\u7684\u540d\u66f2\uff0c\u743c\u6069\u66fe\u7ecf\u542c\u59d0\u59d0\u5439\u594f\u8fc7\u5f88\u591a\u6b21\uff0c\u786e\u5b9e\u662f\u767e\u542c\u4e0d\u538c\uff0c\u4f46\u8fd9\u6b21\u663e\u7136\u683c\u5916\u4e0d\u540c\u3002</p><p>\u94f6\u8272\u7684\u91d1\u5c5e\u957f\u7b1b\u95ea\u70c1\u7740\u6e05\u6ce0\u7684\u5149\u6cfd\uff0c\u516d\u9053\u7b26\u6587\u5149\u5e26\u50cf\u5f69\u6761\u822c\u7f20\u7ed5\u7740\u5b83\uff0c\u968f\u7740\u73ca\u5609\u7684\u5439\u594f\uff0c\u5e9e\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u9b54\u529b\u4ece\u7b1b\u5b54\u4e2d\u6ea2\u51fa\uff0c\u51dd\u805a\u6210\u4e91\u96fe\u72b6\u7ffb\u6eda\u6d8c\u52a8\uff0c\u5373\u4fbf\u5173\u95ed\u5965\u672f\u89c6\u89c9\uff0c\u5355\u7eaf\u7528\u8089\u773c\u90fd\u80fd\u770b\u5f97\u5206\u660e\u3002\u65cb\u98ce\u4ece\u5730\u9762\u6276\u6447\u800c\u8d77\uff0c\u5305\u88f9\u7740\u73ca\u5609\u7684\u8eab\u8eaf\uff0c\u6258\u7740\u5979\u5189\u5189\u4e0a\u5347\uff0c\u76f4\u81f3\u534a\u7a7a\u3002\u5c11\u5973\u4f3c\u4e4e\u5bf9\u81ea\u5df1\u7684\u5904\u5883\u604d\u7136\u672a\u89c9\uff0c\u53ea\u662f\u51dd\u795e\u5439\u594f\uff0c\u5979\u539f\u672c\u675f\u8d77\u7684\u957f\u53d1\u5b8c\u5168\u88ab\u5439\u6563\u5f00\u6765\uff0c\u88d9\u88fe\u968f\u98ce\u730e\u730e\u98d8\u626c\uff0c\u7f8e\u4e3d\u7684\u9762\u5bb9\u4e0a\u8868\u60c5\u5e73\u9759\u800c\u575a\u6bc5\uff0c\u900f\u7740\u51db\u7136\u7684\u5a01\u4e25\uff0c\u4eff\u4f5b\u9ad8\u8d35\u7684\u5973\u738b\uff0c\u4ee4\u4eba\u4e0d\u6562\u76f4\u89c6\u3002</p><p>\u5305\u62ec\u743c\u6069\u5728\u5185\uff0c\u6240\u6709\u7684\u9634\u9b42\u57ce\u5deb\u5e08\u90fd\u4e0d\u7531\u81ea\u4e3b\u5730\u505c\u4f4f\u4e86\u64a4\u9000\u7684\u811a\u6b65\uff0c\u76d8\u8e1e\u5728\u5730\u677f\u4e0a\u7684\u602a\u7269\u4f3c\u4e4e\u4e5f\u610f\u8bc6\u5230\u4e86\u5371\u673a\uff0c\u5b83\u7684\u6240\u6709\u89e6\u624b\u90fd\u9ad8\u9ad8\u5730\u6602\u4e86\u8d77\u6765\uff0c\u50cf\u5636\u5636\u5410\u4fe1\u7684\u86c7\u7fa4\uff0c\u51c6\u5907\u5411\u5439\u7b1b\u5c11\u5973\u53d1\u51fa\u81f4\u547d\u7684\u4e00\u51fb\u3002</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u591c\u5e55\u7a81\u7136\u964d\u4e34\u4e86\u3002</p><p>\u91d1\u78a7\u8f89\u714c\u7684\u5bab\u6bbf\u7a79\u9876\u6d88\u5931\uff0c\u5360\u636e\u89c6\u91ce\u7684\u662f\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u7480\u74a8\u7e41\u661f\u70b9\u7f00\u5176\u4e0a\uff0c\u4eff\u4f5b\u4e00\u9897\u9897\u5b8c\u7f8e\u65e0\u7455\u7684\u5b9d\u77f3\uff0c\u95ea\u70c1\u7740\u94f6\u767d\u8272\u7684\u51b7\u5cfb\u5149\u8292\u3002\u800c\u624b\u6267\u957f\u7b1b\u7684\u5c11\u5973\u8eab\u59ff\uff0c\u5df2\u7ecf\u88ab\u8fd9\u7470\u4e3d\u7684\u591c\u8272\u6240\u63a9\u76d6\uff0c\u53c8\u50cf\u662f\u88ab\u6eb6\u89e3\uff0c\u4e0e\u4e4b\u878d\u5408\uff0c\u4e0e\u4e4b\u540c\u5728\u3002</p><p>\u4e0d\u77e5\u4ece\u4f55\u5904\uff0c\u98ce\u5e26\u6765\u4e86\u5979\u8f7b\u67d4\u7684\u4f4e\u8bed\u58f0\u3002</p><p>\u201c\u661f\u8fb0\u2014\u2014\u5760\u843d\u5427\uff01\u201d</p><p>\u6570\u4ee5\u5343\u4e07\u8ba1\u7684\u661f\u8fb0\u540c\u65f6\u5728\u6c89\u6c89\u591c\u5e55\u4e2d\u9707\u52a8\u8d77\u6765\uff0c\u5b83\u4eec\u88ab\u5de8\u5927\u7684\u529b\u91cf\u7275\u5f15\uff0c\u81ea\u865a\u7a7a\u4e2d\u5212\u51fa\u707f\u70c2\u5f97\u4ee4\u4eba\u7729\u76ee\u7684\u8f68\u8ff9\uff0c\u50cf\u66b4\u98ce\u9aa4\u96e8\u822c\u5bc6\u96c6\u5730\u5760\u843d\uff0c\u7838\u5411\u5730\u9762\u4e0a\u7684\u89e6\u624b\u602a\uff0c\u90a3\u79cd\u60ca\u4eba\u7684\u78c5\u7934\u6c14\u52bf\uff0c\u5373\u4fbf\u662f\u743c\u6069\u89c1\u8fc7\u7684\u6700\u534e\u4e3d\u7684\u6d41\u661f\u96e8\u4e5f\u65e0\u6cd5\u4e0e\u4e4b\u76f8\u6bd4\u62df\u3002\u5c16\u9510\u523a\u8033\u7684\u60e8\u568e\u81ea\u602a\u7269\u4f53\u5185\u53d1\u51fa\uff0c\u51e0\u5341\u53ea\u89e6\u624b\u72c2\u4e71\u5730\u7ffb\u6eda\u62bd\u6253\u7740\u56db\u5468\uff0c\u6323\u624e\u7740\u8bd5\u56fe\u9003\u79bb\u661f\u8fb0\u5760\u843d\u7684\u8303\u56f4\uff0c\u4f46\u8fd9\u662f\u5f92\u52b3\u7684\uff0c\u6ca1\u6709\u6d77\u6c34\u5979\u6839\u672c\u65e0\u6cd5\u79fb\u52a8\u3002</p><p>\u201c\u554a\u554a\u554a\u554a\u554a\u554a\uff01\u201d</p><p>\u6fc0\u5149\u822c\u70ab\u76ee\u7684\u5c04\u7ebf\u81ea\u5b89\u535a\u91cc\u7684\u56db\u9897\u773c\u7403\u4e2d\u5c04\u51fa\uff0c\u5728\u524d\u65b9\u7684\u865a\u7a7a\u4e2d\u4e0a\u4e0b\u4ea4\u7ec7\uff0c\u5207\u5f00\u4e00\u9053\u952f\u9f7f\u72b6\u9ed1\u8272\u88c2\u9699\uff0c\u6025\u901f\u6269\u5927\u5230\u4e24\u4eba\u9ad8\u3002\u4eff\u4f5b\u5927\u5824\u6e83\u7a74\uff0c\u6ed4\u6ed4\u767d\u6d6a\u81ea\u88c2\u9699\u4e2d\u6c79\u6d8c\u800c\u51fa\uff0c\u53d1\u51fa\u9707\u8033\u6b32\u804b\u7684\u58f0\u54cd\uff0c\u8981\u5c06\u8fd9\u5ea7\u5927\u6bbf\uff0c\u4e43\u81f3\u6574\u5ea7\u6d6e\u7a7a\u57ce\u4e2d\u7684\u4e00\u5207\u541e\u566c\u6df9\u6ca1\u3002\u8fd9\u662f\u6765\u81ea\u6c34\u5143\u7d20\u4f4d\u9762\u6700\u6838\u5fc3\u7684\u201c\u6e90\u6c34\u201d\uff0c\u80fd\u591f\u6eb6\u89e3\u4e00\u5207\u7269\u8d28\u5b58\u5728\uff0c\u4efb\u4f55\u4eba\u53ea\u8981\u88ab\u5b83\u6df9\u5230\u2014\u2014\u4e0d\uff0c\u53ea\u8981\u88ab\u5b83\u6cbe\u4e0a\uff0c\u4fbf\u4f1a\u5728\u51e0\u79d2\u949f\u5185\u6eb6\u5316\u6210\u6db2\uff0c\u52a0\u5165\u5b83\u7684\u884c\u5217\u3002</p><p>\u81ea\u77e5\u5fc5\u6b7b\u7684\u6d77\u795e\u79ef\u805a\u8d77\u6700\u540e\u5168\u90e8\u529b\u91cf\uff0c\u6253\u5f00\u4e86\u540c\u5f52\u4e8e\u5c3d\u7684\u901a\u9053\u3002\u5979\u7684\u5e9e\u5927\u8eab\u8eaf\u4e0a\u8986\u76d6\u7740\u4e00\u5c42\u6de1\u84dd\u8272\u8584\u819c\u5149\u7f69\uff0c\u5728\u6c34\u4e2d\u534a\u6d6e\u534a\u6c89\u7740\uff0c\u6b47\u65af\u5e95\u91cc\u5730\u75af\u72c2\u5927\u7b11\uff0c\u4f5c\u4e3a\u6d77\u795e\uff0c\u5979\u5bf9\u6c34\u6709\u7740\u65e0\u4e0e\u4f26\u6bd4\u7684\u4eb2\u548c\u4e0e\u9a7e\u9a6d\u80fd\u529b\uff0c\u4e0d\u81f3\u4e8e\u88ab\u7acb\u523b\u201c\u6eb6\u5316\u201d\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u4e5f\u4e0d\u53ef\u80fd\u771f\u6b63\u62b5\u6321\u201c\u6e90\u6c34\u201d\uff0c\u6b7b\u4ea1\u53ea\u662f\u65f6\u95f4\u95ee\u9898\u800c\u5df2\u3002\u4f46\u5728\u6b64\u4e4b\u524d\uff0c\u5979\u5c06\u4f1a\u4eb2\u773c\u770b\u5230\u654c\u4eba\u7684\u6bc1\u706d\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u7136\u800c\u5979\u5e76\u6ca1\u6709\u5982\u613f\u3002</p><p>\u661f\u5149\u81ea\u591c\u7a79\u4ea4\u9519\u5c04\u4e0b\uff0c\u52fe\u52d2\u51fa\u4e66\u7684\u8f6e\u5ed3\uff0c\u7136\u540e\u8f6e\u5ed3\u5316\u4e3a\u5b9e\u4f53\u3002\u90a3\u662f\u4e00\u672c\u5de8\u5927\u7684\u4e66\uff0c\u5e73\u5e73\u60ac\u6d6e\u7740\uff0c\u6d45\u7eff\u8272\u7684\u5c01\u9762\u671d\u4e0b\uff0c\u95ea\u70c1\u51b0\u51b7\u7684\u91d1\u5c5e\u5149\u6cfd\uff0c\u4e0a\u9762\u753b\u7740\u4e00\u652f\u7389\u767d\u8272\u6800\u5b50\u82b1\u3002\u65e0\u5f62\u7684\u624b\u5feb\u901f\u7ffb\u52a8\u7740\u5b83\uff0c\u6253\u5f00\u5176\u4e2d\u4e00\u9875\uff0c\u9ed1\u8272\u7684\u6f29\u6da1\u5728\u4e66\u9875\u7684\u6b63\u4e2d\u5fc3\u51fa\u73b0\uff0c\u4ee5\u65e0\u6cd5\u60f3\u8c61\u7684\u9ad8\u901f\u65cb\u8f6c\u6269\u5927\uff0c\u77ac\u95f4\u5360\u6ee1\u4e86\u6574\u5f20\u7eb8\u3002</p><p>\u731b\u70c8\u7684\u98d3\u98ce\u4ece\u6f29\u6da1\u4e2d\u51b2\u51fa\uff0c\u8fce\u4e0a\u6c79\u6d8c\u800c\u6765\u7684\u5de8\u6d6a\uff0c\u98ce\u548c\u6c34\u7ede\u7f20\u5728\u4e00\u8d77\uff0c\u5f62\u6210\u6570\u9053\u9f99\u86c7\u822c\u7684\u87ba\u65cb\u6c34\u67f1\uff0c\u6276\u6447\u76f4\u4e0a\uff0c\u88ab\u9ed1\u6d1e\u5b8c\u5168\u541e\u566c\u8fdb\u53bb\u3002\u9ed1\u6d1e\u968f\u4e4b\u6d88\u5931\uff0c\u4e66\u9875\u4e5f\u81ea\u52a8\u5408\u8d77\uff0c\u7389\u767d\u8272\u7684\u5149\u8292\u4ece\u5c01\u9762\u7684\u6800\u5b50\u82b1\u56fe\u6848\u4e2d\u7efd\u653e\u51fa\u6765\uff0c\u901a\u5f80\u6c34\u5143\u7d20\u4f4d\u9762\u7684\u88c2\u9699\u88ab\u6324\u538b\u3001\u5408\u62e2\uff0c\u6700\u7ec8\u5b8c\u5168\u5c01\u95ed\uff0c\u6d88\u5931\u5728\u865a\u7a7a\u4e2d\u3002</p><p>\u957f\u7b1b\u58f0\u518d\u5ea6\u54cd\u8d77\uff0c\u66f4\u591a\u7684\u661f\u8fb0\u5728\u6c89\u6c89\u591c\u5e55\u4e2d\u51fa\u73b0\uff0c\u7136\u540e\u707f\u70c2\u5730\u5760\u843d\uff0c\u4eff\u4f5b\u6c38\u65e0\u6b62\u5c3d\u3002\u89e6\u624b\u602a\u5e9e\u5927\u8eab\u8eaf\u88ab\u707c\u70e7\uff0c\u88ab\u7c89\u788e\uff0c\u88ab\u8fd9\u70ab\u76ee\u5149\u8f89\u51c0\u5316\uff0c\u8f6c\u773c\u95f4\u6e6e\u706d\u65e0\u8ff9\u3002</p></article>"}}}]);
