(window.webpackJsonp=window.webpackJsonp||[]).push([[167],{190:function(p){p.exports={chapterTitle:null,title:"\u7b2c167\u7ae0 \u56fd\u5bb6\u5e72\u90e8",content:"<article><h2>\u7b2c167\u7ae0 \u56fd\u5bb6\u5e72\u90e8</h2><p>\u3000\u3000\u65bd\u536b\u5fe0\u3001\u8bb8\u6d77\u5c71\u56de\u6765\uff0c\u8eab\u4f53\u8863\u670d\u6cbe\u6ee1\u6ce5\u6e0d\uff0c\u6ee1\u9762\u6101\u5bb9\uff1b\u5085\u4fca\u8eab\u4e0a\u5012\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u8863\u670d\u90fd\u6ca1\u6709\u8d77\u76b1\uff0c\u542c\u8bf4\u90a3\u4e24\u540d\u6df7\u5e10\u5382\u957f\u4ece\u6cb3\u91cc\u722c\u4e0a\u6765\uff0c\u641e\u4e86\u4e00\u8eab\u9ed1\u6ce5\uff0c\u81ed\u6c14\u718f\u5929\uff0c\u6ca1\u6709\u8138\u518d\u7559\u4e0b\u6765\u5403\u996d\uff0c\u8d1f\u6c14\u8d70\u4e86\u3002\u4e34\u65f6\u8fd8\u4e0d\u5fd8\u4e22\u4e0b\u51e0\u53e5\u72e0\u8bdd\uff0c\u5927\u5bb6\u90fd\u5728\u60f3\u4ed6\u4eec\u8fd9\u6837\u5b50\u5230\u8857\u4e0a\u4e5f\u62e6\u4e0d\u4e0b\u8f66\u5b50\u6765\u3002</p><p>\u3000\u3000\u5218\u82ac\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u8d77\u6765\uff0c\u5634\u91cc\u8bf4\uff1a\u201c\u8fd9\u4e9b\u4eba\u5c31\u8be5\u8fd9\u6837\u6cbb\u6cbb\u4ed6\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5947\u671d\u4ed6\u8001\u5a46\u77aa\u4e86\u77aa\u773c\uff1a\u201c\u80e1\u95f9\u4ec0\u4e48\u2026\u2026\u201d\u6324\u6324\u773c\u775b\uff0c\u8ba9\u5979\u770b\u65bd\u536b\u5fe0\u3001\u8bb8\u6d77\u5c71\u5fe7\u5fc3\u5fe1\u5fe1\u7684\u6837\u5b50\uff0c\u8fd9\u4e48\u8bf4\u4e0d\u662f\u7ed9\u4ed6\u4eec\u5fc3\u91cc\u6dfb\u5835\u5417\uff1f</p><p>\u3000\u3000\u5218\u82ac\u7b11\u7740\u8bf4\uff1a\u201c\u591a\u5927\u7684\u4e8b\uff0c\u519c\u673a\u5382\u4e0d\u662f\u8fde\u5de5\u8d44\u90fd\u4e0d\u80fd\u8db3\u6708\u53d1\uff1f\u8fd9\u6837\u7684\u5382\u957f\u65e9\u5c31\u5e94\u8be5\u7ed9\u8d76\u4e0b\u53f0\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u6d77\u5c71\u53f9\u4e86\u4e00\u53e3\u6c14\u8bf4\uff1a\u201c\u8be5\u4e0d\u8be5\u4e0b\u53f0\uff0c\u4e0d\u662f\u6211\u4eec\u8fd9\u4e9b\u5c0f\u5de5\u4eba\u80fd\u8bf4\u5f97\u7b97\u4e86\u2026\u2026\u201d\u6ca1\u60f3\u7740\u53bb\u8d23\u5907\u5f20\u606a\uff0c\u5374\u671d\u8bb8\u601d\u77aa\u4e86\u77aa\u773c\u775b\uff0c\u602a\u5979\u6ca1\u6709\u53ca\u65f6\u5236\u6b62\u5f20\u606a\u778e\u95f9\uff0c\u201c\u5509\uff0c\u4e8b\u60c5\u5df2\u7ecf\u8fd9\u6837\u5b50\u4e86\uff0c\u5148\u5403\u996d\u5427\uff0c\u8ba9\u8fd9\u4e24\u4e2a\u738b\u516b\u9f9f\u86cb\u6298\u817e\u5230\u73b0\u5728\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u8bb8\u6d77\u5c71\u3001\u65bd\u536b\u5fe0\u5230\u524d\u9762\u53bb\uff0c\u5f20\u606a\u7b11\u7740\u5bf9\u8bb8\u601d\u8bf4\uff1a\u201c\u542c\u4f60\u7238\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u4e5f\u6709\u5c06\u8fd9\u4e24\u4eba\u6e23\u4e22\u6cb3\u91cc\u53bb\u7684\u51b2\u52a8\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u5c0f\u8001\u767e\u59d3\u4e2a\u4e2a\u90fd\u8ddf\u4f60\u4f3c\u7684\u6562\u80e1\u4f5c\u975e\u4e3a\u2026\u2026\u201d\u4ece\u5f20\u606a\u819d\u76d6\u4e0a\u62b1\u8fc7\u4e00\u4e2a\u5b69\u5b50\u653e\u5728\u81ea\u5df1\u819d\u76d6\u4e0a\uff0c\u5979\u8ba4\u4e0d\u51fa\u54ea\u4e2a\u662f\u60dc\u5bb9\uff0c\u54ea\u4e2a\u662f\u60dc\u7fbd\u3002</p><p>\u3000\u3000\u9664\u4e86\u65bd\u536b\u5fe0\u3001\u8bb8\u6d77\u5c71\u4e24\u4eba\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u5176\u4ed6\u4eba\u5bf9\u8fd9\u6837\u7684\u63d2\u66f2\u90fd\u89c9\u5f97\u5927\u5feb\u4eba\u5fc3\uff0c\u4e5d\u5341\u5e74\u4ee3\u7684\u9152\u5e2d\u83dc\u80b4\u6ca1\u6709\u5341\u51e0\u5e74\u540e\u90a3\u4e48\u4e30\u5bcc\uff0c\u4e0d\u8fc7\u6597\u9152\u7684\u98ce\u6c14\u8981\u6d53\u91cd\u5f97\u591a\uff0c\u4f46\u662f\u4eca\u5929\u8bb8\u591a\u597d\u9152\u7684\u4eba\u90fd\u538b\u7740\u6027\u5b50\uff0c\u9152\u5e2d\u5230\u4e5d\u70b9\u949f\u5c31\u6563\u4e86\u3002</p><p>\u3000\u3000\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u5c0f\u4e2b\u5934\u65e9\u5c31\u5f00\u59cb\u6253\u778c\u7761\uff0c\u5085\u4fca\u592b\u5987\u6ca1\u6709\u65f6\u95f4\uff0c\u90fd\u5c06\u5c0f\u5b69\u5b50\u4ea4\u7ed9\u4ed6\u5988\u770b\u7740\uff0c\u4ed6\u4eec\u73b0\u5728\u6362\u4e86\u4f4f\u5904\uff0c\u8fd9\u4e48\u665a\u4e86\u5c31\u4e0d\u4fbf\u5c06\u5c0f\u5b69\u5b50\u9001\u5230\u7236\u6bcd\u5bb6\u53bb\uff0c\u5218\u82ac\u5c31\u8ba9\u4e24\u4e2a\u5c0f\u5973\u5b69\u5b50\u5148\u5230\u5983\u84c9\u5e8a\u4e0a\u7761\u89c9\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u5148\u8d70\uff0c\u5750\u5728\u9648\u5983\u84c9\u5bb6\u5802\u5c4b\u91cc\u559d\u8336\uff0c\u9648\u5947\u4e0e\u4e34\u65f6\u8bf7\u6765\u7684\u53a8\u5e08\u3001\u5c0f\u5de5\u624d\u5f97\u7a7a\u5403\u996d\uff0c\u9648\u5983\u84c9\u5e2e\u7740\u8bb8\u601d\u8ddf\u5979\u5988\u5988\u6536\u62fe\u6b8b\u5e2d\uff0c\u7b49\u65bd\u536b\u5fe0\u5c06\u4eb2\u621a\u670b\u53cb\u90fd\u9001\u8d70\uff0c\u9648\u5983\u84c9\u3001\u8bb8\u601d\u5979\u4eec\u4e5f\u6536\u62fe\u4e86\u5dee\u4e0d\u591a\uff0c\u9648\u5947\u4e5f\u5403\u597d\u996d\uff0c\u5c06\u4e34\u65f6\u8bf7\u6765\u7684\u53a8\u5e08\u3001\u5c0f\u5de5\u9001\u8d70\uff0c\u8fd8\u8ba9\u4ed6\u4eec\u5e26\u4e86\u4e9b\u5269\u83dc\u56de\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u5e74\u5934\u5bb6\u91cc\u6709\u51b0\u7bb1\u7684\u4eba\u5bb6\u5c11\uff0c\u5929\u6c14\u53c8\u70ed\u4e86\u8d77\u6765\uff0c\u9152\u5e2d\u591a\u4e0b\u7684\u83dc\u80b4\u591a\u534a\u8ba9\u4eba\u5206\u8d70\u3002</p><p>\u3000\u3000\u4e8b\u60c5\u65e2\u7136\u53d1\u751f\u4e86\uff0c\u8fc7\u4e86\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u65bd\u536b\u5fe0\u5fc3\u601d\u4e5f\u5b9a\u4e86\u4e0b\u6765\uff0c\u4ed4\u7ec6\u60f3\u4e86\u60f3\u5f20\u606a\u8bf4\u8fc7\u7684\u8bdd\uff0c\u771f\u8981\u80fd\u9694\u5929\u8ba9\u75e4\u75ae\u75a4\u8138\u72d7\u5c4e\u90fd\u4e0d\u662f\uff0c\u90a3\u771f\u662f\u6ca1\u4ec0\u4e48\u597d\u987e\u5fcc\u7684\u3002</p><p>\u3000\u3000\u8ba4\u8bc6\u5f20\u606a\u4e5f\u5c06\u8fd1\u4e00\u5e74\u4e86\uff0c\u53bb\u5e74\u516b\u6708\u8bb8\u601d\u5bb6\u91cc\u6b63\u4e3a\u4e8c\u5341\u51e0\u4e07\u7684\u6b20\u503a\u6025\u5f97\u9e21\u98de\u72d7\u8df3\uff0c\u65bd\u536b\u5fe0\u4e5f\u4e3a\u6b64\u4e8b\u5954\u8d70\u4e0d\u505c\uff0c\u6c42\u7237\u7237\u544a\u5976\u5976\u5230\u516b\u6708\u4e2d\u8fd8\u77ed\u7f3a\u5341\u4e8c\u4e07\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u51fa\u73b0\u5728\u8bb8\u601d\u5bb6\u4eba\u9762\u524d\uff0c\u4e00\u6296\u624b\u5c31\u5c06\u5dee\u989d\u8865\u8db3\uff0c\u7ed9\u4eba\u7684\u5370\u8c61\u5f25\u8db3\u73cd\u8d35\u3002</p><p>\u3000\u3000\u8ba4\u8bc6\u5c06\u8fd1\u4e00\u5e74\uff0c\u5f20\u606a\u5bb6\u91cc\u7684\u60c5\u51b5\uff0c\u65bd\u536b\u5fe0\u8ddf\u8bb8\u601d\u7236\u6bcd\u5374\u662f\u4e00\u70b9\u90fd\u4e0d\u6e05\u695a\uff0c\u53ea\u4ece\u5f20\u606a\u4e3a\u4ed6\u5bb6\u91cc\u505a\u7684\u4e00\u4e9b\u4e8b\u4e0a\u770b\u51fa\u4ed6\u5bb6\u91cc\u975e\u5bcc\u5373\u8d35\uff0c\u800c\u4e14\u662f\u5927\u5bcc\u5927\u8d35\u7684\u90a3\u79cd\uff0c\u8981\u662f\u8ddf\u8fd9\u79cd\u7684\u4eba\u5bb6\u7ed3\u4e0b\u5173\u7cfb\uff0c\u81ea\u7136\u4e0d\u7528\u62c5\u5fc3\u4ec0\u4e48\u3002\u5173\u952e\u662f\u5f20\u606a\u4ed6\u4eca\u5e74\u624d\u5341\u4e03\u516b\u5c81\uff0c\u8fd8\u662f\u534a\u5927\u672a\u6210\u5e74\u7684\u5c11\u5e74\uff0c\u4ed6\u5bb6\u91cc\u4e5f\u672a\u5fc5\u5c06\u4ed6\u5f53\u56de\u4e8b\u554a\uff0c\u4f46\u662f\u60f3\u5230\u8bb8\u6d77\u5c71\u8ddf\u4ed6\u8bf4\u8fc7\uff0c\u8fde\u57ce\u5357\u533a\u533a\u59d4\u4e66\u8bb0\u5b8b\u57f9\u660e\u90fd\u8ba9\u5f20\u606a\u4f7f\u5524\u7740\u89e3\u51b3\u5218\u82ac\u516c\u8d39\u533b\u7597\u7684\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u771f\u80fd\u5e2e\u4e0a\u5fd9\u3002</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u8ddf\u8bb8\u6d77\u5c71\u56de\u5230\u9648\u5947\u5bb6\u7684\u9662\u5b50\u91cc\uff0c\u770b\u89c1\u5f20\u606a\u3001\u5218\u82ac\u3001\u9648\u5947\u3001\u5085\u4fca\u8fd8\u6709\u8bb8\u601d\u5750\u5728\u9648\u5947\u5bb6\u7684\u5802\u5c4b\u91cc\u559d\u8336\uff0c\u8d70\u8fdb\u53bb\u6b63\u542c\u5f20\u606a\u8ddf\u9648\u5947\u8be6\u7ec6\u8bf4\u529e\u6d41\u6c34\u5e2d\u7684\u4e8b\u60c5\uff1a\u201c\u4f60\u6bcf\u5929\u5c31\u62ff\u516b\u6837\u5927\u83dc\u51fa\u6765\uff0c\u95e8\u5916\u6302\u62db\u724c\u5c31\u79f0\u2018\u516b\u5927\u7897\u2019\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u65bd\u536b\u5fe0\u3001\u8bb8\u6d77\u5c71\u8fdb\u6765\uff0c\u5c06\u4ed6\u72ec\u5750\u7684\u4e00\u5f20\u6761\u51f3\u5f80\u5916\u632a\u4e86\u632a\uff0c\u8ba9\u65bd\u536b\u5fe0\u5750\u4e0b\uff0c\u8bb8\u6d77\u5c71\u8ddf\u4ed6\u5973\u513f\u8bb8\u601d\u5750\u4e00\u8d77\u3002\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u8205\u6709\u6ca1\u6709\u602a\u6211\u60f9\u662f\u751f\u975e\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u65bd\u536b\u5fe0\u5fc3\u91cc\u867d\u7136\u62c5\u5fe7\uff0c\u9762\u5b50\u5012\u4e0d\u4f1a\u670d\u8f6f\uff0c\u201c\u519c\u673a\u5382\u4e5f\u6ca1\u4ec0\u4e48\u597d\u5e72\u7684\uff0c\u5916\u9762\u6709\u5382\u5b50\u4e00\u4e2a\u6708\u4e00\u5343\u7684\u5de5\u8d44\u8bf7\u6211\u8fc7\u53bb\uff0c\u6ca1\u7b54\u5e94\u3002\u5c31\u56fe\u5728\u519c\u673a\u5382\u5b89\u7a33\u4e9b\uff0c\u867d\u7136\u5de5\u8d44\u4f4e\u4e00\u4e9b\uff0c\u4f46\u597d\u6b79\u662f\u56fd\u5bb6\u5e72\u90e8\u7684\u8eab\u4efd\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u82ac\u55e4\u9f3b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u5bb6\u5e72\u90e8\uff1f\u4f60\u4e00\u4e2a\u6708\u62ff\u591a\u5c11\u94b1\u6562\u8bf4\u81ea\u5df1\u662f\u56fd\u5bb6\u5e72\u90e8\u2026\u2026\u201d</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u4e0d\u5e72\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u60dc\u7684\uff0c\u4e0d\u7834\u4e0d\u7acb\uff0c\u4e5f\u4e0d\u6101\u627e\u4e0d\u5230\u65b0\u996d\u7897\uff0c\u8bf4\u4e0d\u5b9a\u5bf9\u6211\u4e5f\u662f\u4e00\u6b21\u673a\u4f1a\u3002\u201d</p><p>\u3000\u3000\u201c\u519c\u673a\u5382\u4e0d\u662f\u5728\u641e\u5382\u957f\u7ade\u8058\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u6d77\u5dde\u63a7\u80a1\u90a3\u8fb9\uff0c\u6211\u80fd\u6253\u5f97\u4e0a\u62db\u547c\uff0c\u5c0f\u8205\u60f3\u7ade\u8058\u526f\u5382\u957f\u7684\u4e8b\uff0c\u6211\u542c\u8bb8\u601d\u59d0\u8bf4\u8fc7\uff0c\u76f8\u4fe1\u5c0f\u8205\u7684\u80fd\u529b\uff0c\u6211\u5c31\u6ca1\u60f3\u7740\u8981\u53bb\u591a\u5634\u591a\u820c\uff0c\u4f46\u662f\u5c0f\u8205\u4f60\u653e\u5fc3\uff0c\u90a3\u4e24\u4e2a\u5bb6\u4f19\u7edd\u5bf9\u4e0d\u4f1a\u51fa\u73b0\u5728\u7ade\u8058\u4eba\u7684\u540d\u5355\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u65bd\u536b\u5fe0\u8fd8\u6709\u4e9b\u7591\u8651\uff0c\u201c\u4ed6\u4eec\u5f88\u4f1a\u94bb\u8425\uff0c\u8ddf\u519c\u673a\u5c40\u7684\u51e0\u4e2a\u5c40\u957f\u5173\u7cfb\u90fd\u5f88\u597d\uff0c\u6d77\u5dde\u63a7\u80a1\u90a3\u8fb9\u5bf9\u4eba\u4e8b\u7684\u5b89\u6392\u4e5f\u8981\u542c\u519c\u673a\u5c40\u7684\u610f\u89c1\u5427\uff1f\u201d</p><p>\u3000\u3000\u5218\u82ac\u7b11\u7740\u8bf4\uff1a\u201c\u6562\u60c5\u4f60\u771f\u60f3\u5f53\u519c\u673a\u5382\u7684\u5382\u957f\uff0c\u5e78\u4e8f\u6211\u8ddf\u4f60\u59d0\u5173\u7cfb\u90a3\u4e48\u597d\uff0c\u4f60\u8fd8\u7792\u7740\u4e0d\u8bf4\uff0c\u6539\u5929\u8fd8\u8981\u5728\u6211\u8fd9\u91cc\u8bf7\u5ba2\u5403\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u82e6\u7b11\u7740\uff1a\u201c\u516b\u5b57\u90fd\u6ca1\u6709\u4e00\u6487\uff0c\u53ea\u662f\u5fc3\u91cc\u60f3\u60f3\u800c\u5df2\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6562\u60f3\u5c31\u8981\u6562\u4e3a\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u4e86\uff0c\u5916\u9762\u5382\u957f\u6709\u4eba\u51fa\u4e00\u5343\u7684\u6708\u5de5\u8d44\u8bf7\u4f60\uff0c\u505a\u4e0d\u6210\u519c\u673a\u5382\u7684\u5382\u957f\uff0c\u51fa\u53bb\u6253\u5de5\u96be\u9053\u4f1a\u6bd4\u5728\u519c\u673a\u5382\u5dee\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u5f53\u4e0a\u526f\u5382\u957f\uff0c\u8fd8\u8981\u4ea4\u4e09\u4e07\u591a\u7684\u4fdd\u8bc1\u91d1\uff0c\u6211\u54ea\u91cc\u62ff\u5f97\u51fa\u8fd9\u94b1\uff1f\u201d\u65bd\u536b\u5fe0\u7b11\u4e86\u7b11\uff0c\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5916\u4eba\uff0c\u4e5f\u4e0d\u85cf\u7740\u6396\u7740\u3002\u4ed6\u8ddf\u7231\u4eba\u7684\u5de5\u4f5c\u90fd\u4e0d\u7b97\u5dee\uff0c\u519c\u673a\u5382\u867d\u7136\u4e0d\u80fd\u8db3\u6708\u53d1\u5de5\u8d44\uff0c\u4f46\u662f\u4ed6\u4eec\u8f66\u95f4\u4e3b\u4efb\u4e00\u5c42\u7ba1\u7406\u4eba\u5458\u5012\u4e0d\u4f1a\u7f3a\uff0c\u5bb6\u91cc\u539f\u4e5f\u6709\u4e9b\u79ef\u84c4\uff0c\u4e3a\u8bb8\u601d\u53bb\u5e74\u7684\u4e8b\u60c5\uff0c\u5c06\u79ef\u84c4\u90fd\u62ff\u4e86\u51fa\u6765\uff0c\u8fd8\u5e2e\u8bb8\u601d\u501f\u4e86\u8bb8\u591a\u94b1\u3002</p><p>\u3000\u3000\u5218\u82ac\u77e5\u9053\u8bb8\u601d\u5bb6\u60c5\u51b5\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7b49\u4f60\u5f53\u4e0a\u519c\u673a\u5382\u7684\u5382\u957f\uff0c\u8fd8\u6101\u501f\u4e0d\u5230\u8fd9\u4e9b\u94b1\uff1f\u201d\u666e\u901a\u8001\u767e\u59d3\u501f\u94b1\u603b\u662f\u96be\u7684\uff0c\u5e72\u90e8\u501f\u94b1\u603b\u662f\u5f88\u65b9\u4fbf\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c31\u7559\u4e0b\u6765\u8bf4\u53e5\u5b89\u6170\u65bd\u536b\u5fe0\u7684\u8bdd\uff0c\u89c1\u4ed6\u8fd9\u65f6\u5019\u8fd8\u6709\u5fc3\u601d\u53bb\u60f3\u7ade\u8058\u7684\u4e8b\u60c5\uff0c\u77e5\u9053\u4ed6\u4e0d\u4f1a\u5c06\u4eca\u5929\u7684\u4e8b\u60c5\u592a\u653e\u5fc3\u91cc\u53bb\uff0c\u5bf9\u5085\u4fca\u8bf4\uff1a\u201c\u6211\u4eec\u8d70\u5427\u2026\u2026\u201d\u8d77\u8eab\u8d70\u8fdb\u9648\u5983\u84c9\u7684\u623f\u95f4\u91cc\uff0c\u4e24\u4e2b\u5934\u5e76\u6392\u7761\u5728\u5e8a\u4e0a\uff0c\u62ff\u6bdb\u5dfe\u88ab\u538b\u7740\u809a\u5b50\uff0c\u60dc\u5bb9\u624b\u634f\u7740\u62f3\u5934\uff0c\u6446\u5728\u7c89\u561f\u561f\u7684\u5c0f\u8138\u4e24\u4fa7\uff0c\u60dc\u7fbd\u7761\u89c9\u559c\u6b22\u76b1\u7740\u7709\u5934\uff0c\u5a07\u6da6\u7684\u5c0f\u5634\u5507\u8fd8\u5fae\u5fae\u7684\u5658\u8d77\u7740\u3002\u9648\u5983\u84c9\u8fd8\u5750\u5728\u4e66\u684c\u524d\u6e29\u4e60\u529f\u8bfe\uff0c\u53f0\u706f\u6cc4\u4e0b\u7684\u660e\u9ec4\u8272\u7684\u5149\u8292\u7f69\u5728\u5979\u7684\u4e0a\u534a\u8eab\uff0c\u5979\u611f\u89c9\u5230\u6709\u4eba\u8fdb\u5c4b\uff0c\u4fa7\u5934\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u662f\u5f20\u606a\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53c8\u4f4e\u5934\u770b\u5979\u7684\u4e66\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6367\u7740\u60dc\u5bb9\u7684\u5c0f\u8138\u86cb\uff0c\u8f7b\u8f7b\u5f80\u4e0a\u6324\u7740\uff0c\u5c06\u5c0f\u5bb6\u4f19\u641e\u9192\uff0c\u60dc\u5bb9\u7761\u773c\u60fa\u5fea\uff0c\u5c0f\u624b\u62fc\u547d\u60f3\u62e8\u5f00\u5f20\u606a\u7684\u624b\uff0c\u6323\u624e\u4e86\u4e00\u4f1a\u513f\uff0c\u5374\u4e0d\u4f9d\u4e0d\u9976\u7684\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u50cf\u6811\u736d\u4e00\u6837\u7f20\u5230\u4ed6\u7684\u8eab\u5b50\u4e0a\u3002\u5085\u4fca\u8f7b\u624b\u8f7b\u811a\u7684\u5c06\u60dc\u7fbd\u62b1\u8d77\u6765\uff0c\u60dc\u7fbd\u8fde\u7761\u59ff\u90fd\u6ca1\u6709\u53d8\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c31\u4e0d\u4f1a\u53bb\u5435\u9192\u60dc\u5bb9\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u634f\u4e86\u634f\u60dc\u5bb9\u7684\u5c0f\u8138\u86cb\uff1a\u201c\u4f60\u662f\u59b9\u59b9\uff0c\u76b1\u7709\u5934\u90fd\u6ca1\u6709\u7528\u3002\u201d\u8f6c\u8fc7\u8eab\uff0c\u770b\u89c1\u9648\u5983\u84c9\u6b63\u6536\u655b\u8d77\u7b11\u5bb9\uff0c\u6e05\u4e3d\u8131\u4fd7\u7684\u7cbe\u81f4\u8138\u86cb\u655b\u8d77\u7b11\u5bb9\uff0c\u773c\u775b\u91cc\u7684\u7b11\u610f\u8fd8\u6ca1\u6709\u5b8c\u5168\u6563\u6389\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u8fc7\u4e24\u5929\u5510\u5a67\u751f\u65e5\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u8f6c\u8fc7\u8eab\u53bb\u7ee7\u7eed\u770b\u4e66\uff0c\u6ca1\u6709\u7406\u5f20\u606a\u3002</p><p>\u3000\u3000\u60dc\u5bb9\u626f\u7740\u5f20\u606a\u7684\u8033\u6735\u8bf4\uff1a\u201c\u5f20\u53d4\u53d4\u5f88\u6ca1\u6709\u7528\u5462\uff0c\u8fde\u5c0f\u8f89\u54e5\u54e5\u90fd\u6709\u5973\u5b69\u966a\u4ed6\u73a9\u3002\u201d\u5c0f\u8f89\u662f\u5085\u4fca\u5927\u54e5\u5bb6\u7684\u7537\u5b69\uff0c\u624d\u5341\u4e94\u5c81\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u62b1\u7740\u60dc\u5bb9\u3001\u5085\u4fca\u62b1\u7740\u60dc\u7fbd\u51fa\u4e86\u5c4b\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bb6\u4eba\u90fd\u5728\uff0c\u5f20\u606a\u6ca1\u6562\u8ddf\u5979\u592a\u4eb2\u6635\uff0c\u544a\u8f9e\u79bb\u5f00\uff0c\u8f66\u4ece\u6c99\u7530\u524d\u8857\u5f80\u897f\u5f00\uff0c\u8fd8\u6ca1\u6709\u6c99\u7530\u8857\u5e02\u53e3\uff0c\u5c31\u542c\u89c1\u9ad8\u97f3\u5587\u53ed\u5608\u6742\u7684\u58f0\u97f3\uff0c\u6df1\u66f4\u534a\u591c\u6b63\u5ba3\u8bb2\u5e02\u91cc\u7684\u62c6\u8fc1\u653f\u7b56\uff0c\u5085\u4fca\u7b11\u7740\u8bf4\uff1a\u201c\u9ad8\u97f3\u5587\u53ed\u7ad6\u8d77\u6765\u6709\u4e24\u5929\u4e86\uff0c\u949f\u697c\u89d2\u90a3\u91cc\u90fd\u9690\u7ea6\u542c\u5f97\u5230\uff0c\u4f4f\u8fd9\u9644\u8fd1\u7684\u4eba\u5bb6\u591c\u91cc\u5435\u5f97\u6839\u672c\u7761\u4e0d\u7740\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u903c\u7740\u62c6\u8fc1\u6237\u642c\u8d70\u5462\uff0c\u4f60\u770b\u7740\u5427\uff0c\u4ee5\u540e\u7684\u624b\u6bb5\u8fd8\u662f\u5c42\u51fa\u4e0d\u7a77\u3002\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u8857\u5e02\u53e3\u524d\u6bb5\u65f6\u95f4\u7684\u706b\u707e\uff0c\u867d\u7136\u5e02\u91cc\u4ee5\u53ca\u50cf\u90b5\u81f3\u521a\u8fd9\u4e9b\u4eba\u90fd\u5bf9\u9526\u57ce\u5730\u4ea7\u6709\u6240\u6000\u7591\uff0c\u4f46\u662f\u90fd\u76f8\u5f53\u9ed8\u5951\u7684\u4fdd\u6301\u6c89\u9ed8\uff0c\u56e0\u4e3a\u8fd9\u53ea\u662f\u5f00\u5934\uff0c\u4ed6\u4eec\u4f30\u8ba1\u6c99\u7530\u897f\u7247\u7684\u62c6\u8fc1\u5f88\u5feb\u5c31\u4f1a\u542f\u52a8\uff0c\u8fd9\u65f6\u5019\u7ed9\u9526\u57ce\u5236\u9020\u62c6\u8fc1\u4e0a\u7684\u56f0\u96be\uff0c\u5f88\u5feb\u56f0\u96be\u5c31\u4f1a\u8f6e\u5230\u4ed6\u4eec\u7684\u5934\u4e0a\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u666e\u901a\u5e02\u6c11\uff0c\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u5e02\u91cc\u5212\u51fa\u8fd9\u5757\u5730\uff0c\u5e76\u5c06\u8fd9\u5757\u5730\u5212\u62d4\u7ed9\u9526\u57ce\u5730\u4ea7\u662f\u706b\u707e\u53d1\u751f\u4e4b\u540e\u7684\u4e8b\u60c5\uff0c\u6ca1\u6709\u660e\u773c\u4eba\u70b9\u62e8\uff0c\u666e\u901a\u5e02\u6c11\u600e\u4e48\u4f1a\u6000\u7591\u5230\u9526\u57ce\u5730\u4ea7\u5934\u4e0a\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u8857\u5e02\u53e3\uff0c\u4e34\u65f6\u5357\u8857\u7ed9\u706b\u707e\u70e7\u6bc1\u7684\u5730\u65b9\u7ad6\u8d77\u9ad8\u6746\uff0c\u60ac\u6302\u7740\u9ad8\u5c04\u706f\uff0c\u5c06\u56db\u5468\u7167\u5f97\u4eae\u5982\u767d\u663c\uff0c\u5076\u5c14\u6709\u73bb\u7483\u7ed9\u7838\u788e\u7684\u58f0\u97f3\uff0c\u770b\u6765\u5728\u62c6\u8fc1\u6237\u6ca1\u6709\u5b8c\u5168\u642c\u79bb\u4e4b\u524d\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4f1a\u4e00\u76f4\u5ef6\u7eed\u4e0b\u53bb\u3002</p></article>"}}}]);
