(window.webpackJsonp=window.webpackJsonp||[]).push([[167],{189:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u4f9d\u7136\u662f\u5723\u6b66\u58eb",content:"<article><h2>\u7b2c\u56db\u8282 \u4f9d\u7136\u662f\u5723\u6b66\u58eb</h2><p>\u731b\u7136\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u743c\u6069\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u867d\u7136\u65e9\u60f3\u8fc7\u9ece\u660e\u4e4b\u77f3\u548c\u8389\u62c9\u516c\u7235\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u6050\u6015\u6709\u4e9b\u9ebb\u70e6\uff0c\u4f46\u5374\u4e5f\u6ca1\u60f3\u5230\u4f1a\u6709\u8fd9\u6837\u4e25\u91cd\u7684\u540e\u679c\u3002\u743c\u6069\u662f\u4e2a\u6cdb\u4fe1\u8005\uff0c\u5bf9\u795e\u7947\u5bf9\u5b97\u6559\u5bf9\u4fe1\u4ef0\u4ec0\u4e48\u7684\u6ca1\u591a\u5927\u611f\u89c9\uff0c\u4f46\u4ed6\u81f3\u5c11\u5f88\u6e05\u695a\uff0c\u5bf9\u4e8e\u6885\u83f2\u65af\u6765\u8bf4\uff0c\u5265\u593a\u5723\u6b66\u58eb\u8eab\u4efd\uff0c\u5f00\u9664\u6559\u7c4d\uff0c\u8fd9\u662f\u591a\u4e48\u6c89\u91cd\u7684\u6253\u51fb\u3002</p><p>\u5947\u602a\uff0c\u542c\u6885\u83f2\u65af\u7684\u8bed\u6c14\uff0c\u90a3\u4f4d\u8ff7\u65af\u5353\u8bfa\u7684\u5927\u4e3b\u6559\u5e94\u8be5\u5bf9\u5979\u975e\u5e38\u4fe1\u4efb\u624d\u662f\uff0c\u600e\u4e48\u4f1a\u8fd9\u4e48\u5306\u5fd9\u5c31\u53d1\u5e03\u6555\u4ee4\u2026\u2026\u7b49\u7b49\u2026\u2026</p><p>\u5230\u5e95\u4e8b\u4e0d\u5173\u5df1\uff0c\u743c\u6069\u867d\u7136\u60ca\u8bb6\uff0c\u8111\u5b50\u5374\u8fd8\u6e05\u9192\uff0c\u5ff5\u5934\u8f6c\u4e86\u51e0\u8f6c\uff0c\u4ed6\u7a81\u7136\u53d1\u89c9\u4e0d\u5bf9\u52b2\u3002\u6885\u83f2\u65af\u8bf4\u5979\u521a\u628a\u4fe1\u4f20\u8fc7\u53bb\uff0c\u90a3\u8fb9\u5c31\u4f20\u6765\u4e86\u6555\u4ee4\u2014\u2014\u63d0\u5c14\u6559\u4f1a\u7684\u529e\u4e8b\u6548\u7387\u8fd9\u4e48\u60ca\u4eba\u4e48\uff1f</p><p>\u6885\u83f2\u65af\u6447\u5934\u3002</p><p>\u201c\u9ece\u660e\u4e4b\u77f3\u4e0e\u8ff7\u65af\u5353\u8bfa\u7684\u8ff7\u9501\u76f8\u8054\u63a5\uff0c\u201d\u5979\u89e3\u91ca\u8bf4\uff0c\u201c\u5723\u7269\u4e00\u6bc1\uff0c\u8ff7\u9501\u81ea\u7136\u5c31\u6709\u53cd\u5e94\u3002\u201d</p><p>\u8ff7\u65af\u5353\u8bfa\u90a3\u8fb9\u77e5\u9053\u5723\u7269\u88ab\u6bc1\uff0c\u81ea\u7136\u5927\u60ca\u5931\u8272\uff0c\u7acb\u523b\u8981\u67e5\u51fa\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u7267\u5e08\u4eec\u6216\u8005\u5411\u795e\u7947\u7948\u7977\uff0c\u8bf7\u6c42\u795e\u8c15\uff0c\u6216\u8005\u4f7f\u7528\u5360\u535c\u672f\uff0c\u603b\u4e4b\uff0c\u53ea\u8981\u6709\u8db3\u591f\u5f3a\u529b\u7684\u4eba\u624d\uff0c\u8981\u5f04\u6e05\u695a\u8fd9\u8fb9\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e5f\u5e76\u4e0d\u662f\u5f88\u96be\u3002\u6240\u4ee5\uff0c\u8ff7\u65af\u5353\u8bfa\u90a3\u8fb9\u5176\u5b9e\u65e9\u5c31\u5df2\u7ecf\u5f97\u77e5\u60c5\u7531\uff0c\u8fd9\u4efd\u6555\u4ee4\u4e5f\u5e76\u4e0d\u662f\u6536\u5230\u6885\u83f2\u65af\u7684\u4f20\u8baf\u540e\u624d\u4f5c\u51fa\u7684\uff0c\u53ea\u4e0d\u8fc7\u6070\u597d\u2014\u2014\u4e5f\u53ef\u80fd\u662f\u6545\u610f\u2014\u2014\u8fd9\u65f6\u5019\u53d1\u51fa\u6765\u7f62\u4e86\u3002</p><p>\u4f46\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f</p><p>\u201c\u9ece\u660e\u4e4b\u77f3\u7684\u4e8b\u60c5\u5c31\u7f62\u4e86\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u8389\u62c9\u516c\u7235\u662f\u6211\u6740\u7684\uff0c\u600e\u4e48\u6555\u4ee4\u91cc\u8bf4\u662f\u4f60\u6740\u7684\u3002\u201d</p><p>\u201c\u4e5f\u6ca1\u4ec0\u4e48\u533a\u522b\u5427\u3002\u201d\u6885\u83f2\u65af\u6de1\u6de1\u5e94\u7740\uff0c\u4f3c\u4e4e\u6709\u4e9b\u75b2\u5026\u7684\u6a21\u6837\u3002</p><p>\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u7ec8\u7a76\u8fd8\u662f\u4e0d\u540c\u7684\uff0c\u5982\u679c\u5927\u4e3b\u6559\u5b58\u4e86\u7ef4\u62a4\u6885\u83f2\u65af\u7684\u5fc3\u601d\uff0c\u5b8c\u5168\u53ef\u4ee5\u987a\u7406\u6210\u7ae0\u5730\u628a\u6740\u4eba\u8fd9\u6869\u4e8b\u60c5\u63a8\u5728\u743c\u6069\u8eab\u4e0a\uff0c\u8fd9\u672c\u6765\u4e5f\u5c31\u662f\u4e8b\u5b9e\uff1a\u5728\u743c\u6069\u548c\u8389\u62c9\u516c\u7235\u5bf9\u51b3\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u5b8c\u5168\u6ca1\u63d2\u624b\uff0c\u4e8b\u540e\u624d\u8dd1\u8fc7\u6765\u3002</p><p>\u201c\u4f60\u4e0d\u6740\u5979\uff0c\u6211\u4e5f\u6740\u5979\uff0c\u7ed3\u679c\u90fd\u4e00\u6837\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u53ef\u662f\u8fd9\u4ef6\u4e8b\u672c\u6765\u4f60\u4e5f\u5c31\u6ca1\u505a\u9519\u554a\uff0c\u201d\u4ed6\u8be7\u5f02\u7740\uff0c\u201c\u8389\u62c9\u516c\u7235\u5236\u9020\u761f\u75ab\uff0c\u84c4\u8c0b\u6467\u6bc1\u9ece\u660e\u4e4b\u77f3\uff0c\u672c\u6765\u5c31\u7f6a\u65e0\u53ef\u6055\uff0c\u6740\u4e86\u5979\u4e5f\u662f\u7406\u6240\u5e94\u5f53\u3002\u5927\u4e3b\u6559\u4ed6\u4eec\u65e2\u7136\u80fd\u77e5\u9053\u8fd9\u8fb9\u7684\u4e8b\u60c5\uff0c\u7406\u5f53\u4e5f\u77e5\u9053\u5185\u60c5\u624d\u662f\uff0c\u4f55\u51b5\u4f60\u8fd8\u53d1\u4e86\u4fe1\u8fc7\u53bb\u3002\u201d</p><p>\u6885\u83f2\u65af\u4f4e\u5934\u5782\u76ee\uff0c\u9759\u9759\u770b\u7740\u81ea\u5df1\u5149\u83b9\u5982\u7389\u7684\u53cc\u624b\uff0c\u5e76\u4e0d\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u72b9\u81ea\u5947\u602a\uff0c\u8111\u4e2d\u7a81\u7136\u7075\u5149\u4e00\u95ea\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002\u95ee\u9898\u6070\u6070\u5c31\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u65e2\u7136\u8ff7\u65af\u5353\u8bfa\u7684\u5927\u4e3b\u6559\u80fd\u63a2\u77e5\u5343\u91cc\u4e4b\u5916\u7684\u4e8b\u60c5\uff0c\u4e0d\u53ef\u80fd\u4e0d\u77e5\u9053\u5185\u60c5\uff0c\u800c\u4e14\u542c\u6885\u83f2\u65af\u7684\u8bf4\u6cd5\uff0c\u5927\u4e3b\u6559\u5bf9\u5979\u7406\u5f53\u662f\u5341\u5206\u4fe1\u4efb\u7684\uff0c\u5e94\u8be5\u77e5\u9053\u6885\u83f2\u65af\u4e0d\u4f1a\u80e1\u4e71\u6740\u4eba\u2014\u2014\u90a3\u4e48\u4e5f\u5c31\u662f\u8bf4\uff0c\u5927\u4e3b\u6559\u660e\u77e5\u6885\u83f2\u65af\u5e76\u65e0\u8fc7\u9519\uff0c\u81f3\u5c11\u6ca1\u6709\u4e25\u91cd\u8fc7\u9519\uff0c\u4f46\u5374\u8fd8\u662f\u53d1\u4e86\u8fd9\u5c01\u4e25\u5389\u7684\u6555\u4ee4\u3002</p><p>\u5265\u593a\u5723\u6b66\u58eb\u8eab\u4efd\uff0c\u5f00\u9664\u6559\u7c4d\uff0c\u610f\u5473\u7740\u6885\u83f2\u65af\u548c\u63d0\u5c14\u6559\u4f1a\u8131\u79bb\u5e72\u7cfb\uff0c\u8fd9\u5f88\u5bb9\u6613\u8ba9\u743c\u6069\u4ea7\u751f\u4e00\u79cd\u8054\u60f3\uff1a\u5927\u4e3b\u6559\u5176\u5b9e\u662f\u56e0\u4e3a\u67d0\u79cd\u539f\u56e0\uff0c\u628a\u6885\u83f2\u65af\u727a\u7272\u4e86\u3002</p><p>\u81f3\u4e8e\u4ec0\u4e48\u539f\u56e0\u2026\u2026\u53ea\u6015\u662f\u653f\u6cbb\u56e0\u7d20\u5427\uff0c\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u6740\u4e86\u535a\u5f97\u4e4b\u95e8\u7684\u5927\u516c\u7235\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u4e2a\u4eba\u884c\u4e3a\uff0c\u800c\u662f\u4e25\u91cd\u7684\u653f\u6cbb\u95ee\u9898\u4e86\u3002\u5982\u679c\u8981\u4fdd\u6885\u83f2\u65af\uff0c\u63d0\u5c14\u6559\u4f1a\u52bf\u5fc5\u8981\u548c\u535a\u5f97\u4e4b\u95e8\u53d1\u751f\u51b2\u7a81\uff0c\u751a\u81f3\u5f15\u53d1\u4e00\u8fde\u4e32\u7684\u95ee\u9898\uff0c\u743c\u6069\u867d\u7136\u662f\u4e2a\u653f\u6cbb\u76f2\uff0c\u4f46\u4e5f\u80fd\u60f3\u8c61\u5230\u8fd9\u70b9\u3002</p><p>\u743c\u6069\u61ca\u6094\u81ea\u5df1\u771f\u662f\u53cd\u5e94\u8fdf\u949d\uff0c\u5c45\u7136\u5230\u73b0\u5728\u624d\u60f3\u660e\u767d\u8fc7\u6765\u3002\u6885\u83f2\u65af\u5fc5\u5b9a\u65e9\u5df2\u7ecf\u60f3\u901a\u8fd9\u70b9\uff0c\u6240\u4ee5\u624d\u8fd9\u822c\u90c1\u90c1\u4e0d\u4e50\u3002\u4e00\u65b9\u9762\uff0c\u56fa\u7136\u662f\u56e0\u4e3a\u88ab\u5265\u593a\u5723\u6b66\u58eb\u8eab\u4efd\uff0c\u5f00\u9664\u6559\u7c4d\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u662f\u56e0\u4e3a\u5979\u6240\u5b8c\u5168\u4fe1\u4efb\u7684\u5927\u4e3b\u6559\uff0c\u5374\u5c31\u8fd9\u6837\u5c06\u5979\u727a\u7272\u6389\u4e86\u5427\u3002</p><p>\u4ed6\u5fc3\u5934\u5347\u8d77\u4e00\u9635\u601c\u60dc\uff0c\u8d70\u4e0a\u524d\uff0c\u8f7b\u8f7b\u63fd\u4f4f\u5979\u7684\u9999\u80a9\uff0c\u5c06\u5979\u62b1\u5728\u6000\u4e2d\u3002\u201c\u6ca1\u4e8b\u4e86\uff0c\u827e\u5f25\u8587\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u53ea\u662f\u4e00\u65f6\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u539f\u672c\u5c31\u6ca1\u591a\u5c11\u5b89\u6170\u5973\u5b69\u5b50\u7684\u7ecf\u9a8c\uff0c\u4f55\u51b5\u5982\u4eca\u53c8\u662f\u8fd9\u7b49\u5927\u4e8b\u3002</p><p>\u6885\u83f2\u65af\u987a\u4ece\u5730\u4efb\u743c\u6069\u62b1\u7740\uff0c\u5c06\u8138\u8d34\u5728\u4ed6\u80f8\u53e3\uff0c\u201c\u4e5f\u6ca1\u4ec0\u4e48\u4e86\uff0c\u201d\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u6162\u6162\u5f00\u53e3\u8bf4\uff0c\u201c\u8fd9\u6b21\u7684\u4e8b\u60c5\uff0c\u539f\u672c\u6211\u4e5f\u5c31\u6709\u8d23\u4efb\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u672c\u6765\u5c31\u4ec0\u4e48\u90fd\u6ca1\u505a\u9519\u3002\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u5fae\u6447\u5934\uff0c\u6ca1\u6709\u4e89\u8bba\u8fd9\u4e2a\u95ee\u9898\uff0c\u201c\u6682\u65f6\uff0c\u4e5f\u5c31\u8fd9\u6837\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u7b49\u56de\u5230\u8ff7\u65af\u5353\u8bfa\uff0c\u6211\u518d\u548c\u5927\u4e3b\u6559\u4ed4\u7ec6\u8c08\u8c08\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u8be7\u5f02\uff0c\u201c\u4f60\u8fd8\u8981\u56de\u8ff7\u65af\u5353\u8bfa\u53bb\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6211\u603b\u5f97\u56de\u53bb\u590d\u547d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u867d\u7136\u6211\u5df2\u7ecf\u88ab\u5f00\u9664\u6559\u7c4d\uff0c\u4f46\u8fd9\u6b21\u7684\u4efb\u52a1\u5374\u8fd8\u6ca1\u6709\u5b8c\u6210\u3002\u201d</p><p>\u743c\u6069\u4e5f\u6ca1\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u53cd\u6b63\u539f\u672c\u4e5f\u5c31\u9884\u8ba1\u8981\u53bb\u4e00\u8d9f\u8ff7\u65af\u5353\u8bfa\uff0c\u201c\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u966a\u4f60\u53bb\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u5bf9\u6211\u5f88\u597d\u5462\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u56e0\u4e3a\u6211\u559c\u6b22\u4f60\u554a\u3002\u201d</p><p>\u201c\u4e5f\u559c\u6b22\u522b\u7684\u5973\u5b69\u5b50\u662f\u4e48\uff0c\u201d\u6885\u83f2\u65af\u626c\u4e86\u626c\u7709\uff0c\u201c\u6bd4\u5982\u90a3\u4f4d\u8389\u6cd5\u513f\u5c0f\u59d0\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5c34\u5c2c\uff0c\u4e0d\u77e5\u9053\u5982\u4f55\u56de\u7b54\u3002\u65f6\u95f4\u8fc7\u53bb\u51e0\u5929\uff0c\u4ed6\u672c\u4ee5\u4e3a\u6885\u83f2\u65af\u5df2\u7ecf\u5dee\u4e0d\u591a\u8be5\u5fd8\u4e86\uff0c\u81f3\u5c11\u6d88\u6c14\u4e86\uff0c\u5982\u4eca\u770b\u8d77\u6765\uff0c\u663e\u7136\u81ea\u5df1\u662f\u518d\u6b21\u4f4e\u4f30\u4e86\u5973\u5b69\u5b50\u5728\u8fd9\u65b9\u9762\u7684\u8bb0\u5fc6\u529b\u3002</p><p>\u6885\u83f2\u65af\u8f7b\u58f0\u7b11\u4e86\u8d77\u6765\uff0c\u5979\u4ef0\u8d77\u8138\uff0c\u770b\u7740\u743c\u6069\uff0c\u78a7\u7eff\u7684\u53cc\u7738\u91cc\u95ea\u70c1\u8fc7\u4e00\u4e1d\u72e1\u9ee0\u7684\u7b11\u610f\u3002\u201c\u5176\u5b9e\u554a\uff0c\u743c\u6069\uff0c\u6211\u5f88\u597d\u5947\u4e00\u4ef6\u4e8b\u60c5\u5462\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u81ea\u4ece\u6211\u4eec\u5728\u6c99\u6f20\u91cc\u8ba4\u8bc6\uff0c\u201d\u5979\u5fae\u5fae\u7b11\u7740\uff0c\u770b\u7740\u743c\u6069\uff0c\u201c\u4f60\u597d\u50cf\u6b3a\u8d1f\u8fc7\u6211\u5f88\u591a\u6b21\u5462\u3002\u201d</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62c5\u5fc3\u6211\u4f1a\u6740\u4e86\u4f60\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u662f\u5723\u6b66\u58eb\u561b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u6211\u8fde\u4e2a\u5723\u6b66\u58eb\u90fd\u4e0d\u76f8\u4fe1\uff0c\u90a3\u8fd8\u80fd\u76f8\u4fe1\u8c01\u5462\u3002\u201d</p><p>\u201c\u6211\u53ef\u662f\u4f1a\u51fa\u5c14\u53cd\u5c14\u7684\u4eba\u5462\u3002\u201d</p><p>\u201c\u770b\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5bf9\u4ec0\u4e48\u4eba\u7f62\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u4e0d\u4f1a\uff0c\u6240\u4ee5\u5c31\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6709\u81ea\u4fe1\u5417\uff1f\u201d\u6885\u83f2\u65af\u51b7\u7b11\u8d77\u6765\uff0c\u201c\u5c31\u90a3\u4e48\u80fd\u80af\u5b9a\u6211\u7684\u60f3\u6cd5\uff1f\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u4f60\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u5982\u679c\u6211\u8fde\u4f60\u5fc3\u91cc\u60f3\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd8\u600e\u4e48\u6307\u671b\u80fd\u5f97\u5230\u4f60\u5462\uff0c\u201d\u4ed6\u62b1\u7d27\u6000\u91cc\u7684\u5c11\u5973\uff0c\u201c\u6211\u60f3\u5f97\u5230\u4f60\uff0c\u81ea\u7136\u8981\u6e05\u695a\u4f60\u7684\u60f3\u6cd5\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u90a3\u4f60\u77e5\u9053\u6211\u73b0\u5728\u5fc3\u91cc\u60f3\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u6211\u53ef\u731c\u4e0d\u5230\u3002\u201d</p><p>\u201c\u6211\u5728\u60f3\uff0c\u4f60\u5c61\u6b21\u6b3a\u8d1f\u6211\uff0c\u5374\u4e0d\u62c5\u5fc3\u6211\u4f1a\u6740\u4f60\uff0c\u662f\u56e0\u4e3a\u6211\u4eec\u6709\u534f\u8bae\uff0c\u56e0\u4e3a\u6211\u662f\u5723\u6b66\u58eb\uff0c\u5bf9\u5427\uff0c\u201d\u6885\u83f2\u65af\u987a\u4ece\u5730\u4efb\u7531\u743c\u6069\u62b1\u7740\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u4e00\u5b57\u4e00\u987f\uff0c\u9690\u9690\u51b0\u51b7\uff0c\u201c\u90a3\u4e48\uff0c\u73b0\u5728\u6211\u597d\u50cf\u5df2\u7ecf\u4e0d\u662f\u5723\u6b66\u58eb\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u968f\u5373\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\uff0c\u4f38\u624b\u629a\u6478\u7740\u5c11\u5973\u7684\u8138\u86cb\uff0c\u201c\u5c0f\u4e2b\u5934\u3002\u201d\u4ed6\u8bf4\uff0c\u770b\u7740\u5979\u90a3\u78a7\u7eff\u5e7d\u6df1\u7684\u53cc\u773c\uff0c\u4f4e\u5934\u5728\u5979\u989d\u5934\u4e0a\u8f7b\u8f7b\u543b\u4e86\u4e00\u4e0b\uff0c\u6885\u83f2\u65af\u6ca1\u6709\u53cd\u6297\uff0c\u5b89\u5b89\u9759\u9759\u5730\u9760\u5728\u4ed6\u6000\u91cc\u3002\u201c\u9a97\u4eba\u90fd\u4e0d\u4f1a\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u7b11\u7740\u770b\u5979\uff0c\u201c\u4e0b\u6b21\u5e94\u8be5\u88c5\u5f97\u66f4\u50cf\u70b9\u3002\u201d</p><p>\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u4e8b\u5b9e\u5417\uff1f\u201d\u6885\u83f2\u65af\u5e73\u9759\u5730\u8bf4\uff0c\u201c\u6211\u786e\u5b9e\u5df2\u7ecf\u4e0d\u662f\u5723\u6b66\u58eb\u4e86\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u76ef\u7740\u5c11\u5973\u7684\u773c\u775b\uff0c\u201c\u4f60\u6211\u90fd\u6e05\u695a\u8fd9\u4e00\u70b9\u7684\u3002\u201d</p><p>\u201c\u6211\u88ab\u6559\u4f1a\u5265\u593a\u8d44\u683c\u4e86\u3002\u201d</p><p>\u201c\u90a3\u6709\u5173\u7cfb\u4e48\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u201c\u6ca1\u5173\u7cfb\u5417\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u662f\u5723\u6b66\u58eb\uff0c\u6240\u4ee5\u795e\u7947\u624d\u4f1a\u8ba4\u53ef\u4f60\uff0c\u6240\u4ee5\u6559\u4f1a\u624d\u4f1a\u518c\u5c01\u4f60\u2014\u2014\u5374\u4e0d\u662f\u53cd\u8fc7\u6765\uff0c\u56e0\u4e3a\u795e\u7947\u8ba4\u53ef\uff0c\u56e0\u4e3a\u6559\u4f1a\u518c\u5c01\uff0c\u6240\u4ee5\u4f60\u624d\u662f\u5723\u6b66\u58eb\u554a\uff0c\u201d\u743c\u6069\u8ba4\u771f\u5730\u8bf4\uff0c\u201c\u6211\u867d\u7136\u6c38\u8fdc\u4e0d\u4f1a\u662f\u4e00\u4e2a\u5723\u6b66\u58eb\uff0c\u4f46\u6211\u76f8\u4fe1\u6211\u8bf4\u7684\u9053\u7406\u6ca1\u9519\u3002\u6ca1\u6709\u4e86\u6559\u4f1a\u7684\u518c\u5c01\uff0c\u4f60\u81f3\u5c11\u8fd8\u6ca1\u6709\u5931\u53bb\u795e\u7947\u7684\u8ba4\u53ef\u2014\u2014\u5c31\u7b97\u8fde\u795e\u7947\u7684\u8ba4\u53ef\u4e5f\u90fd\u5931\u53bb\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f60\u672c\u6765\u5c31\u662f\u5723\u6b66\u58eb\uff0c\u8fd9\u662f\u786e\u5b9a\u65e0\u7591\u7684\u4e8b\u5b9e\uff0c\u4e0d\u9700\u8981\u4efb\u4f55\u4eba\u3001\u4efb\u4f55\u795e\u7947\u3001\u4efb\u4f55\u5b58\u5728\u6765\u627f\u8ba4\u7684\u5440\u3002\u201d</p><p>\u6885\u83f2\u65af\u5b9a\u5b9a\u5730\u770b\u7740\u743c\u6069\uff0c\u4e24\u4eba\u76ee\u5149\u4ea4\u63a5\u7740\uff0c\u770b\u7740\u5f7c\u6b64\u773c\u4e2d\u7684\u81ea\u5df1\uff0c\u201c\u4f60\u5f88\u4f1a\u5b89\u6170\u4eba\u5462\uff0c\u743c\u6069\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u6216\u8bb8\u5427\uff0c\u4f46\u6211\u521a\u624d\u5e76\u4e0d\u662f\u5728\u5b89\u6170\u4f60\u554a\uff0c\u6211\u53ea\u662f\u5728\u9648\u8ff0\u4e8b\u5b9e\u3002\u201d</p><p>\u201c\u662f\u4e8b\u5b9e\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u662f\u4e8b\u5b9e\u5462\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u76f8\u4fe1\u662f\uff0c\u4f60\u4e5f\u76f8\u4fe1\u662f\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u683c\u683c\u7b11\u4e86\u8d77\u6765\uff0c\u8fde\u80a9\u5934\u90fd\u8f7b\u5fae\u6296\u52a8\u7740\uff0c\u743c\u6069\u4ece\u6ca1\u89c1\u5979\u7b11\u5f97\u8fd9\u4e48\u5f00\u5fc3\u8fc7\uff0c\u53ea\u89c9\u5f97\u660e\u8273\u65e0\u4fe6\uff0c\u5a07\u7f8e\u53ef\u7231\uff0c\u4e00\u65f6\u90fd\u6709\u4e9b\u770b\u5f97\u5446\u4e86\u3002\u201c\u6211\u559c\u6b22\u542c\u5230\u8fd9\u4e2a\u4e8b\u5b9e\u5462\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u4f38\u81c2\u73af\u62b1\u7740\u743c\u6069\u7684\u8116\u5b50\uff0c\u5634\u5507\u8d34\u8fd1\u4ed6\u7684\u8033\u8fb9\uff0c\u201c\u90a3\u4e48\uff0c\u4f5c\u4e3a\u56de\u62a5\uff0c\u6211\u51b3\u5b9a\u539f\u8c05\u4f60\u4e86\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u5979\u8bf4\u5f97\u53c8\u8f7b\u53c8\u5feb\uff0c\u743c\u6069\u6ca1\u542c\u6e05\u695a\uff0c\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u8bf4\uff0c\u201d\u6885\u83f2\u65af\u4e00\u5b57\u4e00\u987f\u5730\u8bf4\uff0c\u201c\u6211\u3001\u539f\u3001\u8c05\u3001\u4f60\u3001\u4e86\uff01\u201d</p><p>\u201c\u54e6\u3002\u201d\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u770b\u6837\u5b50\uff0c\u603b\u7b97\u662f\u5ea6\u8fc7\u4e00\u52ab\u4e86\u3002</p><p>\u201c\u53e6\u5916\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u521a\u624d\u5e72\u561b\u53eb\u6211\u5c0f\u4e2b\u5934\u554a\u3002\u201d</p><p>\u201c\u4e0d\u559c\u6b22\u4e48\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u5f53\u65f6\u89c9\u5f97\u4f60\u8d85\u7ea7\u53ef\u7231\uff0c\u5c31\u8131\u53e3\u800c\u51fa\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5176\u5b9e\uff0c\u6211\u597d\u50cf\u633a\u559c\u6b22\u4f60\u8fd9\u4e48\u53eb\u6211\u5462\u2026\u2026\u4f60\u6bd4\u6211\u5927\u5417\uff1f\u201d</p><p>\u201c\u6211\u5341\u4e94\u5c81\uff0c\u4f60\u5462\u3002\u201d</p><p>\u201c\u5341\u56db\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u6709\u4e9b\u9eef\u7136\uff0c\u5979\u5e73\u65f6\u770b\u8d77\u6765\uff0c\u65e2\u575a\u5f3a\u53c8\u6c89\u7a33\uff0c\u521a\u786c\u5014\u5f3a\uff0c\u5b8c\u5168\u5c31\u662f\u4e2a\u8eab\u7ecf\u767e\u6218\u7ecf\u9a8c\u4e30\u5bcc\u7684\u5723\u6b66\u58eb\uff0c\u8c01\u80fd\u770b\u51fa\u5176\u5b9e\u624d\u662f\u4e2a\u5341\u56db\u5c81\u7684\u5c0f\u5973\u5b69\u5462\u3002</p><p>\u4e00\u4e2a\u4eba\u627f\u53d7\u8fd9\u4e9b\uff0c\u60f3\u5fc5\u5f88\u8f9b\u82e6\u5427\u3002</p><p>\u4ed6\u601c\u7231\u5730\u629a\u6478\u7740\u5c11\u5973\u7684\u91d1\u53d1\uff0c\u4eb2\u543b\u7740\u5979\u7684\u8138\uff0c\u201c\u5c0f\u4e2b\u5934\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u4f60\u3002\u201d</p></article>"}}}]);
