(window.webpackJsonp=window.webpackJsonp||[]).push([[1675],{1698:function(p){p.exports={chapterTitle:"\u7b2c\u5341\u5377 \u9634\u5f71\u8c37\u7bc7",title:"\u5e8f\u7ae0 \uff08\u4e0a\uff09\u591a\u8299",content:"<article><h1>\u7b2c\u5341\u5377 \u9634\u5f71\u8c37\u7bc7</h1><h2>\u5e8f\u7ae0 \uff08\u4e0a\uff09\u591a\u8299</h2><p>\u5728\u827e\u8bfa\u5965\u514b\u5927\u6c99\u6f20\u548c\u98ce\u66b4\u53f7\u89d2\u5c71\u8109\u4e4b\u95f4\uff0c\u6709\u4e00\u7247\u692d\u5706\u5f62\u7684\u72ed\u957f\u5730\u5e26\uff0c\u89e6\u76ee\u6240\u53ca\u5c3d\u662f\u8d2b\u7620\u6c99\u5316\u7684\u571f\u58e4\u3001\u5149\u79c3\u79c3\u7684\u5c71\u5cad\u548c\u88ab\u72c2\u98ce\u4fb5\u8680\u7684\u5ca9\u77f3\uff0c\u770b\u4e0d\u5230\u4efb\u4f55\u68ee\u6797\u6216\u8005\u5176\u4ed6\u7eff\u8272\u690d\u88ab\uff0c\u53ea\u8981\u98ce\u4e00\u8d77\u5c31\u4f1a\u522e\u8d77\u6f2b\u5929\u5c18\u6c99\u3002\u5728\u5730\u56fe\u4e0a\uff0c\u5b83\u88ab\u7b80\u5355\u5730\u79f0\u4e4b\u4e3a\u201c\u5ca9\u77f3\u4e4b\u5730\u201d\uff0c\u56e0\u4e3a\u65e2\u975e\u4ea4\u901a\u8981\u9053\uff08\u76f8\u53cd\u662f\u975e\u5e38\u4e4b\u504f\u50fb\uff09\uff0c\u81ea\u7136\u73af\u5883\u53c8\u5982\u6b64\u6076\u52a3\uff08\u8fde\u5730\u7cbe\u90fd\u5f88\u96be\u627e\u5230\u51e0\u53ea\uff09\uff0c\u66f4\u6ca1\u6709\u4efb\u4f55\u540d\u80dc\u53e4\u8ff9\u6216\u8005\u85cf\u5b9d\u4f20\u8bf4\uff08\u4e5f\u4e0d\u53ef\u80fd\u5438\u5f15\u90a3\u4e9b\u65e0\u6240\u4e0d\u5728\u7684\u5192\u9669\u8005\uff09\uff0c\u6240\u4ee5\u8352\u51c9\u81f3\u6781\uff0c\u53ea\u6709\u51e0\u5ea7\u96f6\u96f6\u661f\u661f\u7684\u5c0f\u6751\u5e84\uff0c\u6570\u767e\u540d\u5c45\u6c11\u7956\u7956\u8f88\u8f88\u5c45\u4f4f\u4e8e\u6b64\uff0c\u51e0\u4e4e\u88ab\u5916\u754c\u6240\u9057\u5fd8\u3002</p><p>\u8fd9\u4e00\u65e5\u508d\u665a\u65f6\u5206\uff0c\u65e5\u5df2\u897f\u5782\uff0c\u82cd\u832b\u66ae\u8272\u7b3c\u7f69\u56db\u91ce\uff0c\u5d0e\u5c96\u4e0d\u5e73\u7684\u5c71\u8def\u4e0a\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u5f71\u3002\u90a3\u662f\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u7684\u5973\u5b50\uff0c\u80a9\u8180\u5bbd\u9614\uff0c\u8170\u7a84\u817f\u957f\uff0c\u6b65\u4f10\u77eb\u5065\u800c\u8f7b\u5feb\uff0c\u9ed1\u8272\u76ae\u5236\u957f\u9774\u8e29\u5728\u5ca9\u77f3\u548c\u6c99\u783e\u4e0a\uff0c\u53ea\u53d1\u51fa\u975e\u5e38\u8f7b\u5fae\u7684\u54cd\u52a8\u3002\u5979\u7a7f\u7740\u7070\u8272\u7684\u886c\u886b\u548c\u7d27\u8eab\u957f\u88e4\uff0c\u5916\u9762\u5957\u7740\u4e00\u4ef6\u8910\u8272\u76ae\u7532\uff0c\u76ae\u80a4\u5fae\u9ed1\u4e2d\u6cdb\u7ea2\uff0c\u663e\u7136\u5e38\u5e74\u5954\u6ce2\u5728\u5916\uff0c\u88ab\u98ce\u5439\u65e5\u6652\u6240\u81f4\uff0c\u4e94\u5b98\u4e5f\u7b97\u4e0d\u4e0a\u7cbe\u81f4\u79c0\u4e3d\uff0c\u4f46\u5982\u5200\u524a\u65a7\u51ff\uff0c\u7ebf\u6761\u521a\u786c\uff0c\u68f1\u89d2\u5206\u660e\uff0c\u4e00\u53cc\u84dd\u8272\u7684\u5927\u773c\u775b\u91cc\u900f\u7740\u52c3\u52c3\u82f1\u6c14\uff0c\u957f\u957f\u7684\u94f6\u53d1\u88ab\u4e1d\u5e26\u675f\u8d77\uff0c\u68b3\u6210\u9a6c\u5c3e\uff0c\u5e72\u8106\u5229\u843d\u3002\u770b\u5979\u7684\u88c5\u675f\uff0c\u50cf\u662f\u4f4d\u7ecf\u9a8c\u8001\u5230\u7684\u5192\u9669\u8005\uff0c\u4f46\u5374\u6ca1\u6709\u643a\u5e26\u4efb\u4f55\u6b66\u5668\uff0c\u4e5f\u6ca1\u6709\u884c\u56ca\u5305\u88f9\uff0c\u4e24\u624b\u7a7a\u7a7a\u884c\u8d70\u5728\u8fd9\u8352\u5c71\u91ce\u5730\u4e4b\u4e2d\uff0c\u50cf\u662f\u7a81\u7136\u5192\u51fa\u6765\u4f3c\u7684\u3002</p><p>\u201c\u4e0d\u65e9\u4e86\u5462\u3002\u201d\u94f6\u53d1\u5973\u5b50\u62ac\u5934\u770b\u770b\u5915\u9633\uff0c\u81ea\u8a00\u81ea\u8bed\u3002</p><p>\u8fd9\u6761\u8def\u5979\u8d70\u8fc7\u5f88\u591a\u6b21\uff0c\u5e76\u4e0d\u964c\u751f\uff0c\u77e5\u9053\u7ffb\u8fc7\u8fd9\u9053\u5c71\u6881\u4fbf\u80fd\u770b\u5230\u4e00\u5ea7\u6751\u5e84\uff0c\u5c45\u4f4f\u7740\u51e0\u5341\u6237\u4eba\u5bb6\uff0c\u4eca\u665a\u53ef\u4ee5\u5728\u90a3\u91cc\u501f\u5bbf\u3002\u660e\u65e5\u4e00\u65e9\u542f\u7a0b\uff0c\u665a\u4e0a\u4fbf\u80fd\u62b5\u8fbe\u63d0\u51e1\u987f\u57ce\uff0c\u540e\u65e5\u4e0b\u5348\u5c31\u80fd\u56de\u5230\u5bb6\u4e86\u3002</p><p>\u5979\u52a0\u5feb\u811a\u6b65\uff0c\u7247\u523b\u95f4\u4fbf\u4e0a\u4e86\u5c71\u9876\uff0c\u90a3\u5ea7\u5c0f\u6751\u5e84\u6b63\u9759\u9759\u5730\u5ea7\u843d\u5728\u5c71\u811a\u4e0b\uff0c\u708a\u70df\u8885\u8885\uff0c\u663e\u7136\u8f9b\u52e4\u52b3\u7d2f\u4e86\u4e00\u5929\u7684\u6751\u6c11\u4eec\u6b63\u5728\u51c6\u5907\u665a\u9910\u3002\u89c1\u6b64\u60c5\u5f62\uff0c\u94f6\u53d1\u5973\u5b50\u7684\u8138\u4e0a\u9732\u51fa\u5fae\u5fae\u7b11\u610f\uff0c\u6b63\u8981\u8d77\u7a0b\u4e0b\u5c71\uff0c\u9661\u7136\u95f4\u6014\u4e86\u6014\uff0c\u62ac\u5934\u671d\u4e1c\u5357\u65b9\u5411\u7684\u5929\u7a7a\u671b\u53bb\u3002</p><p>\u51e0\u5341\u9053\u707f\u70c2\u7684\u767d\u5149\u5212\u7834\u5929\u9645\uff0c\u53d1\u51fa\u9690\u9690\u96f7\u9e23\u58f0\uff0c\u4ee5\u6781\u9ad8\u7684\u901f\u5ea6\u5760\u843d\u4e0b\u6765\uff0c\u6ca1\u5165\u9065\u8fdc\u7684\u5730\u5e73\u7ebf\u3002\u56e0\u4e3a\u592a\u9633\u5c1a\u672a\u843d\u5c71\uff0c\u5929\u8272\u8fd8\u4eae\uff0c\u8fd9\u767d\u5149\u5e76\u4e0d\u662f\u5341\u5206\u5f15\u4eba\u6ce8\u76ee\uff0c\u800c\u4e14\u5f88\u591a\u4eba\u5c31\u7b97\u770b\u5230\u4e86\u4e5f\u4e0d\u5728\u610f\uff0c\u53ea\u5f53\u4f5c\u6d41\u661f\uff0c\u4f46\u94f6\u53d1\u5973\u5b50\u7684\u7709\u5934\u5374\u5fae\u5fae\u76b1\u4e86\u8d77\u6765\u3002</p><p>\u56e0\u4e3a\uff0c\u6d41\u661f\u548c\u6d41\u661f\uff0c\u4e5f\u662f\u4e0d\u4e00\u6837\u7684\u3002</p><p>\u201c\u53c8\u6709\u54ea\u4f4d\u795e\u660e\u88ab\u6467\u6bc1\u4e86\u5417\uff1f\u201d\u5979\u601d\u5fd6\u7740\uff0c\u4e0d\u8fc7\u5e76\u4e0d\u5982\u4f55\u62c5\u5fc3\uff0c\u66f4\u591a\u53ea\u662f\u597d\u5947\u800c\u5df2\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4e00\u4e1d\u7d27\u5f20\u611f\u4e5f\u4ece\u5fc3\u5e95\u6084\u6084\u5347\u4e86\u8d77\u6765\u3002</p><p>\u7b2c\u56db\u7eaa\u679c\u7136\u5df2\u7ecf\u6084\u7136\u964d\u4e34\u4e86\u554a\u3002</p><p>\u5979\u7ad9\u5728\u5c71\u9876\u4e0a\u6c89\u601d\u4e86\u534a\u664c\uff0c\u7b49\u56de\u8fc7\u795e\u6765\u7684\u65f6\u5019\uff0c\u592a\u9633\u5df2\u7ecf\u843d\u5c71\u4e86\uff0c\u591c\u5e55\u5f00\u59cb\u7b3c\u7f69\u5927\u5730\uff0c\u56e0\u4e3a\u5149\u7ebf\u9eef\u6de1\uff0c\u89c6\u89c9\u88ab\u5927\u5927\u524a\u5f31\u7684\u7f18\u6545\uff0c\u539f\u672c\u5c31\u5d0e\u5c96\u9661\u5ced\u7684\u5c71\u8def\u53d8\u5f97\u66f4\u52a0\u9669\u5cfb\uff0c\u4f55\u51b5\u53c8\u662f\u4e0b\u5c71\u3002\u94f6\u53d1\u5973\u5b50\u5374\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5979\u8038\u4e86\u8038\u80a9\uff0c\u4e00\u9635\u75be\u98ce\u5439\u8fc7\uff0c\u8eab\u4f53\u88ab\u65e0\u5f62\u7684\u529b\u91cf\u626d\u66f2\uff0c\u7728\u773c\u95f4\u53d8\u5f62\u6210\u4e86\u4e00\u53ea\u82cd\u9e70\uff0c\u7136\u540e\u5f20\u5f00\u5bbd\u9614\u53cc\u7ffc\uff0c\u5982\u5229\u7bad\u822c\u8d34\u7740\u5c71\u4f53\u4fef\u51b2\u800c\u4e0b\u3002</p><p>\u77ed\u77ed\u51e0\u79d2\u949f\uff0c\u82cd\u9e70\u4fbf\u98de\u5230\u4e86\u6751\u5e84\u7684\u5165\u53e3\uff0c\u7136\u540e\u5b83\u518d\u5ea6\u53d8\u56de\u94f6\u53d1\u5973\u5b50\uff0c\u6b63\u51c6\u5907\u8fc8\u6b65\u8fdb\u5165\uff0c\u7a81\u7136\u8eab\u5f62\u786c\u751f\u751f\u5730\u987f\u4f4f\uff0c\u50cf\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\u8e4a\u8df7\u4f3c\u7684\uff0c\u7d27\u63a5\u7740\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u4e24\u56e2\u706b\u7130\u81ea\u53cc\u624b\u638c\u5fc3\u8df3\u8dc3\u800c\u51fa\uff0c\u51dd\u805a\u6210\u4e24\u628a\u96ea\u4eae\u7684\u519b\u5200\uff0c\u5979\u7684\u77b3\u5b54\u4e2d\u94f6\u5149\u95ea\u70c1\uff0c\u8138\u8272\u53d8\u5f97\u524d\u6240\u672a\u6709\u7684\u51dd\u91cd\u3002</p><p>\u7136\u540e\u5979\u7f13\u7f13\u8d70\u8fdb\u6751\u5e84\u3002</p><p>\u591c\u8272\u8d8a\u52a0\u6df1\u6c89\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u4f46\u94f6\u53d1\u5973\u5b50\u7684\u89c6\u89c9\u5df2\u7ecf\u5b8c\u5168\u4e0d\u53d7\u5f71\u54cd\uff0c\u751a\u81f3\u6bd4\u767d\u663c\u65f6\u8fd8\u8981\u66f4\u5f3a\u51e0\u5206\u3002\u6751\u5e84\u91cc\u7a7a\u8361\u8361\u7684\uff0c\u5bc2\u9759\u65e0\u58f0\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u5f71\uff0c\u5979\u4e00\u8def\u884c\u6765\uff0c\u53ea\u770b\u89c1\u4e86\u7834\u635f\u7684\u623f\u5c4b\u95e8\u7a97\uff0c\u770b\u89c1\u4e86\u5730\u9762\u548c\u5899\u58c1\u4e0a\u7684\u8840\u8ff9\uff0c\u95fb\u5230\u4e86\u7a7a\u6c14\u4e2d\u7684\u8840\u8165\u5473\uff0c\u8fd8\u6709\u4e00\u80a1\u6df7\u5408\u7740\u5404\u79cd\u96be\u95fb\u6c14\u5473\u7684\u6076\u81ed\uff0c\u90a3\u662f\u91ce\u5916\u602a\u7269\u8eab\u4e0a\u6240\u5171\u6709\u7684\u4f53\u5473\u3002</p><p>\u94f6\u53d1\u5973\u5b50\u7acb\u523b\u5c31\u660e\u767d\u8fd9\u91cc\u66fe\u7ecf\u53d1\u751f\u4e86\u4ec0\u4e48\uff1a\u4e00\u7fa4\u51f6\u731b\u7684\u602a\u7269\u88ad\u51fb\u4e86\u8fd9\u5ea7\u6751\u5e84\uff0c\u6740\u6b7b\u4e86\u6751\u6c11\uff0c\u4ece\u7559\u4e0b\u7684\u6c14\u5473\u6765\u5224\u65ad\uff0c\u81f3\u5c11\u5305\u62ec\u8c7a\u72fc\u4eba\u3001\u98df\u4eba\u9b54\u548c\u725b\u5934\u602a\u8fd9\u4e09\u79cd\uff0c\u5176\u4ed6\u6682\u65f6\u8fd8\u4e0d\u80fd\u786e\u5b9a\uff0c\u53ef\u80fd\u6709\u5de8\u9b54\u6216\u8005\u718a\u5730\u7cbe\u3002\u5979\u662f\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6e38\u4fa0\uff0c\u6210\u5e74\u7d2f\u6708\u5728\u5c71\u6797\u91ce\u5916\u5954\u6ce2\uff0c\u5bf9\u81ea\u5df1\u5728\u8fd9\u65b9\u9762\u7684\u5224\u65ad\u6781\u6709\u81ea\u4fe1\u3002\u7136\u800c\u66f4\u5927\u7684\u7591\u60d1\u968f\u4e4b\u4ea7\u751f\uff1a\u602a\u7269\u4ece\u4f55\u800c\u6765\uff1f\u987b\u77e5\u6b64\u5730\u5982\u6b64\u8d2b\u7620\uff0c\u8fde\u751f\u547d\u529b\u6700\u987d\u5f3a\u7684\u5730\u7cbe\u90fd\u4e0d\u600e\u4e48\u80fd\u517b\u6d3b\uff0c\u66f4\u9051\u8bba\u5176\u4ed6\u51f6\u731b\u54c1\u79cd\uff1f\u5979\u4e5f\u5e38\u5e38\u8def\u8fc7\u6b64\u5730\uff0c\u53ef\u4ee5\u786e\u5b9a\u65b9\u5706\u767e\u91cc\u4e4b\u5185\uff0c\u7edd\u5bf9\u6ca1\u6709\u4ec0\u4e48\u98df\u4eba\u9b54\u6216\u8005\u8c7a\u72fc\u4eba\u7684\u5de2\u7a74\u3002</p><p>\u9000\u4e00\u6b65\u8bf4\uff0c\u5c31\u7b97\u771f\u662f\u6b64\u5730\u6709\u602a\u7269\uff0c\u6216\u8005\u522b\u5904\u7684\u602a\u7269\u6210\u7fa4\u7ed3\u961f\u8def\u8fc7\uff0c\u5c60\u6740\u4e86\u6751\u6c11\uff0c\u90a3\u4e48\u5c38\u4f53\u53bb\u54ea\u91cc\u4e86\uff1f\u94f6\u53d1\u5973\u5b50\u5df2\u7ecf\u4ed4\u7ec6\u67e5\u770b\u8fc7\uff0c\u4ece\u8840\u8ff9\u4e0a\u770b\uff0c\u5168\u6751\u7684\u767e\u4f59\u53e3\u4eba\u57fa\u672c\u65e0\u5e78\uff0c\u4f46\u5374\u6ca1\u6709\u770b\u5230\u4efb\u4f55\u4e00\u5177\u5c38\u4f53\u3002\u5f53\u7136\uff0c\u602a\u7269\u662f\u4f1a\u5403\u4eba\u7684\u2014\u2014\u95ee\u9898\u662f\u5c31\u7b97\u6700\u51f6\u6b8b\u7684\u98df\u4eba\u9b54\uff0c\u603b\u4e5f\u662f\u8981\u5410\u9aa8\u5934\u7684\u3002\u80fd\u591f\u5403\u4eba\u4e0d\u5410\u9aa8\u5934\u7684\uff0c\u53ea\u6709\u4eba\u7c7b\u81ea\u8eab\uff0c\u602a\u7269\u4eec\u666e\u904d\u667a\u5546\u504f\u4f4e\uff0c\u8fd8\u6ca1\u953b\u70bc\u8fdb\u5316\u51fa\u8fd9\u79cd\u80fd\u529b\u3002\u603b\u4e0d\u53ef\u80fd\u662f\u5b83\u4eec\u6740\u5b8c\u4eba\u4e4b\u540e\uff0c\u8fd8\u628a\u5c38\u4f53\u90fd\u85cf\u533f\u8d77\u6765\u6216\u8005\u642c\u8d70\u4e86\u5427\uff0c\u4e14\u4e0d\u8bf4\u602a\u7269\u4eec\u7684\u5b57\u5178\u91cc\u6709\u6ca1\u6709\u201c\u6bc1\u5c38\u706d\u8ff9\u201d\u8fd9\u4e2a\u8bcd\uff0c\u5c31\u7b97\u771f\u6709\u8fd9\u4e2a\u6982\u5ff5\uff0c\u5355\u5355\u628a\u5c38\u4f53\u5f04\u8d70\u53c8\u6709\u4ec0\u4e48\u7528\u3002</p><p>\u5979\u7f13\u7f13\u5410\u4e86\u53e3\u6c14\uff0c\u4e24\u67c4\u519b\u5200\u81ea\u624b\u4e2d\u6d88\u5931\uff0c\u94f6\u8272\u706b\u7130\u4ece\u4f53\u5185\u6563\u53d1\u51fa\u6765\uff0c\u51dd\u805a\u6210\u51e0\u5341\u53ea\u7fbd\u6bdb\u6d01\u767d\u7684\u5c0f\u9e1f\uff0c\u6251\u68f1\u7740\u7fc5\u8180\u671d\u56db\u9762\u516b\u65b9\u98de\u53bb\uff0c\u6ca1\u5165\u6c89\u6c89\u591c\u8272\u4e2d\u3002\u5927\u7ea6\u8fc7\u4e86\u4e03\u516b\u5206\u949f\uff0c\u5c0f\u9e1f\u4eec\u9646\u9646\u7eed\u7eed\u53c8\u98de\u4e86\u56de\u6765\uff0c\u5927\u90e8\u5206\u76f4\u63a5\u94bb\u5165\u5973\u5b50\u7684\u8eab\u4f53\uff0c\u4f46\u6709\u51e0\u53ea\u5c0f\u9e1f\u53e3\u4e2d\u5374\u8854\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5149\u7403\uff0c\u8272\u6cfd\u9eef\u6de1\uff0c\u900f\u7740\u660e\u663e\u7684\u6b7b\u6c14\u3002\u94f6\u53d1\u5973\u5b50\u4f38\u51fa\u624b\uff0c\u63a5\u8fc7\u90a3\u4e9b\u5c0f\u5149\u7403\uff0c\u6258\u5728\u638c\u5fc3\u4e2d\u4ed4\u7ec6\u7aef\u8be6\uff0c\u7f8e\u4e3d\u7684\u84dd\u8272\u773c\u775b\u91cc\u5df2\u7ecf\u7b3c\u7f69\u4e0a\u4e86\u4e00\u5c42\u6012\u610f\uff0c\u98de\u9e1f\u5e26\u56de\u7684\u8baf\u606f\uff0c\u5b8c\u5168\u5370\u8bc1\u4e86\u5979\u7684\u731c\u6d4b\uff1a\u8fd9\u5ea7\u6751\u5e84\u91cc\uff0c\u6b8b\u7559\u7740\u90aa\u6076\u7684\u4ea1\u7075\u672f\u6c14\u606f\u3002</p><p>\u88ad\u51fb\u6751\u5e84\u7684\uff0c\u4e0d\u4ec5\u4ec5\u662f\u602a\u7269\uff0c\u8fd8\u6709\u4ea1\u7075\u5deb\u5e08\u3002\u6751\u6c11\u4eec\u7684\u5c38\u4f53\u5e76\u4e0d\u662f\u88ab\u85cf\u533f\u6216\u8005\u88ab\u8fd0\u8d70\uff0c\u800c\u662f\u81ea\u5df1\u8d70\u7684\u2014\u2014\u4ed6\u4eec\u90fd\u88ab\u53d8\u6210\u4e86\u4ea1\u7075\u3002</p><p>\u8fd9\u4e2a\u7ed3\u8bba\u4ee4\u5979\u65e0\u53ef\u6291\u5236\u5730\u6124\u6012\uff0c\u4f46\u540c\u65f6\u4e5f\u9690\u9690\u8b66\u60d5\u8d77\u6765\u3002\u5c60\u6740\u65e0\u8f9c\u7684\u6751\u6c11\uff0c\u8fd8\u5c06\u4ed6\u4eec\u5168\u90e8\u53d8\u6210\u4ea1\u7075\u5e26\u8d70\u2014\u2014\u5982\u6b64\u90aa\u6076\u7684\u884c\u4e3a\uff0c\u5e76\u4e0d\u662f\u5bfb\u5e38\u7684\u4ea1\u7075\u5e08\u6240\u80fd\u505a\u51fa\u6765\u7684\u3002\u5982\u679c\u5012\u9000\u51e0\u767e\u5e74\uff0c\u8fd9\u79cd\u60c5\u5f62\u8fd8\u80fd\u7b97\u662f\u5e38\u89c1\uff0c\u5c24\u5176\u662f\u8428\u739b\u65af\u7279\u521b\u7acb\u9f99\u5deb\u6559\u4e4b\u540e\uff0c\u904d\u9080\u5927\u9646\u5404\u5730\u4ea1\u7075\u5e08\u52a0\u76df\uff0c\u7814\u7a76\u5404\u79cd\u9ed1\u6697\u90aa\u672f\uff0c\u6700\u7ec8\u5f53\u771f\u5f04\u51fa\u4e86\u201c\u9f99\u5deb\u5996\u201d\u8fd9\u79cd\u9707\u53e4\u70c1\u4eca\u7684\u53d8\u6001\u5b58\u5728\uff0c\u4ea1\u7075\u5e08\u7684\u5a01\u540d\u4e00\u65f6\u95f4\u4e5f\u968f\u4e4b\u62b5\u8fbe\u5386\u53f2\u7684\u5dc5\u5cf0\uff0c\u4ed6\u4eec\u6a2a\u884c\u65e0\u5fcc\uff0c\u6c14\u7130\u56a3\u5f20\uff0c\u4eff\u4f5b\u4e4c\u4e91\u822c\u7b3c\u7f69\u4e16\u754c\u3002\u7136\u800c\u597d\u666f\u4e0d\u957f\uff0c\u8428\u739b\u65af\u7279\u5f88\u5feb\u8d25\u4ea1\uff0c\u9f99\u5deb\u6559\u5728\u5404\u5927\u52bf\u529b\u7684\u8054\u624b\u6253\u538b\u4e0b\u8fd1\u4e4e\u5d29\u6e83\uff0c\u5f88\u591a\u8457\u540d\u7684\u4ea1\u7075\u5e08\u90fd\u88ab\u527f\u6740\uff0c\u6b8b\u5b58\u8005\u4e5f\u90fd\u8f6c\u4e3a\u5730\u4e0b\uff0c\u5939\u8d77\u5c3e\u5df4\u4f4e\u8c03\u505a\u4eba\uff0c\u867d\u7136\u76d7\u5893\u6316\u575f\u7684\u4e8b\u60c5\u80af\u5b9a\u7ecf\u5e38\u5e72\uff0c\u6740\u4eba\u7f6a\u884c\u4e5f\u4e0d\u53ef\u80fd\u5b8c\u5168\u675c\u7edd\uff0c\u4f46\u786e\u5b9e\u662f\u666e\u904d\u6536\u655b\u4e86\u5f88\u591a\u3002\u50cf\u8fd9\u79cd\u5c60\u6740\u5168\u6751\u53c8\u5168\u90e8\u53d8\u6210\u4ea1\u7075\u7684\u4e8b\u60c5\uff0c\u5df2\u7ecf\u662f\u5f88\u4e45\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u4e86\u3002</p><p>\u6216\u8bb8\uff0c\u662f\u56e0\u4e3a\u89c9\u5f97\u6b64\u5730\u592a\u8fc7\u504f\u50fb\uff0c\u6240\u4ee5\u8086\u65e0\u5fcc\u60ee\u5427\u3002\u4f46\u65e2\u7136\u88ab\u81ea\u5df1\u649e\u4e0a\uff0c\u90a3\u4e48\u5c31\u662f\u5bf9\u65b9\u7684\u597d\u8fd0\u6c14\u5230\u5934\u4e86\u3002</p><p>\u867d\u7136\u4f9d\u65e7\u5947\u602a\u4ea1\u7075\u5e08\u4e3a\u4f55\u4f1a\u548c\u4e00\u7fa4\u602a\u7269\u6405\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u662f\u4e00\u7fa4\u6742\u724c\u602a\u7269\uff0c\u4f46\u94f6\u53d1\u5973\u5b50\u6ca1\u6709\u6253\u7b97\u7ee7\u7eed\u505c\u5728\u539f\u5730\u601d\u8003\uff0c\u8981\u77e5\u9053\u7b54\u6848\uff0c\u76f4\u63a5\u8ffd\u4e0a\u53bb\u4eb2\u773c\u770b\u770b\u4fbf\u662f\u6700\u597d\u7684\u65b9\u6cd5\u3002\u81f3\u4e8e\u5371\u9669\uff0c\u8fd9\u4ece\u6765\u5c31\u4e0d\u5728\u5979\u7684\u8003\u8651\u4e4b\u4e2d\u3002</p><p>\u65cb\u98ce\u518d\u5ea6\u4ece\u5730\u9762\u817e\u8d77\uff0c\u5c06\u5973\u5b50\u9ad8\u5927\u7684\u8eab\u5f62\u5b8c\u5168\u541e\u566c\uff0c\u7d27\u63a5\u7740\u4e00\u53ea\u77eb\u5065\u7684\u730e\u8c79\u4ece\u65cb\u98ce\u4e2d\u51b2\u51fa\uff0c\u5168\u8eab\u7ebf\u6761\u5145\u6ee1\u4e86\u529b\u91cf\u4e0e\u7f8e\u611f\u3002\u5979\u5954\u8dd1\u8d77\u6765\uff0c\u4eff\u4f5b\u4e00\u9053\u91d1\u8272\u95ea\u7535\u6ca1\u5165\u591c\u8272\u4e4b\u4e2d\uff0c\u6cbf\u7740\u602a\u7269\u548c\u4ea1\u7075\u7559\u4e0b\u7684\u6c14\u606f\u4e00\u8def\u8ffd\u8e2a\u800c\u53bb\u3002</p><p>\u547c\u5578\u7684\u98ce\u58f0\u4ece\u8033\u8fb9\u63a0\u8fc7\uff0c\u5c71\u8def\u4e24\u65c1\u7684\u98ce\u666f\u98de\u901f\u5012\u9000\uff0c\u730e\u8c79\u5c06\u5954\u8dd1\u901f\u5ea6\u53d1\u6325\u5230\u4e86\u6781\u81f4\uff0c\u5927\u7ea6\u8fc7\u4e86\u5341\u591a\u5206\u949f\uff0c\u5979\u53d1\u73b0\u4e86\u76ee\u6807\u3002\u6b63\u5982\u4e8b\u5148\u5224\u65ad\u7684\u90a3\u6837\uff0c\u4e00\u7fa4\u91ce\u5916\u602a\u7269\uff0c\u5305\u62ec\u8c7a\u72fc\u4eba\u3001\u98df\u4eba\u9b54\u3001\u5de8\u9b54\u3001\u718a\u5730\u7cbe\u3001\u725b\u5934\u602a\uff0c\u8fd8\u6709\u51e0\u53ea\u517d\u4eba\uff0c\u6b63\u5435\u5435\u56b7\u56b7\uff0c\u4e92\u76f8\u63a8\u6421\uff0c\u4ee5\u4e00\u79cd\u6563\u4e71\u5f97\u51e0\u4e4e\u4e0d\u6210\u9635\u5217\u7684\u961f\u5f62\u671d\u7740\u4e1c\u65b9\u524d\u8fdb\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u8ddf\u5728\u602a\u7269\u540e\u9762\u7684\u4ea1\u7075\u4eec\u5c31\u6709\u89c4\u77e9\u5f97\u591a\uff0c\u5b83\u4eec\u6c89\u9ed8\u4e0d\u8bed\uff0c\u6b65\u4f10\u6574\u9f50\u5212\u4e00\uff0c\u9664\u4e86\u8863\u886b\u8934\u891b\u4e4b\u5916\uff0c\u7eaa\u5f8b\u6027\u7b80\u76f4\u53ef\u4ee5\u4e0e\u6b63\u89c4\u519b\u5ab2\u7f8e\u3002</p><p>\u730e\u8c79\u653e\u8f7b\u811a\u6b65\uff0c\u9759\u6084\u6084\u5730\u8ddf\u968f\u5728\u540e\u9762\uff0c\u4fdd\u6301\u7740\u4e00\u6bb5\u8ddd\u79bb\uff0c\u89c2\u5bdf\u8fd9\u652f\u5947\u602a\u7684\u961f\u4f0d\u3002\u4ea1\u7075\u4eec\u6b63\u662f\u521a\u521a\u4e0d\u5e78\u9047\u5bb3\u7684\u6751\u6c11\uff0c\u8fd9\u70b9\u4ece\u4ed6\u4eec\u7684\u8863\u7740\u5c31\u80fd\u6e05\u6670\u5224\u65ad\u51fa\u6765\u3002\u81f3\u4e8e\u602a\u7269\u5219\u662f\u79cd\u7c7b\u6781\u5176\u6742\u4e71\uff0c\u57fa\u672c\u4e0a\u91ce\u5916\u5e38\u89c1\u7684\u54c1\u79cd\u5168\u90e8\u90fd\u5305\u62ec\u4e86\uff0c\u6570\u91cf\u4e0d\u7b97\u5f88\u591a\uff0c\u4e5f\u5c31\u767e\u4f59\u540d\uff0c\u4f46\u5374\u90fd\u662f\u5168\u526f\u6b66\u88c5\uff0c\u7a7f\u7740\u81f3\u5c11\u7b97\u662f\u52c9\u5f3a\u5408\u8eab\u7684\u76ae\u7532\u751a\u81f3\u94e0\u7532\uff0c\u62ff\u7740\u94c1\u5236\u7684\u957f\u77db\u3001\u6218\u65a7\uff0c\u4e24\u5934\u5305\u7740\u91d1\u5c5e\u7684\u5927\u6728\u68d2\uff0c\u751a\u81f3\u8fd8\u6709\u51e0\u53ea\u8c7a\u72fc\u4eba\u80cc\u7740\u590d\u5408\u957f\u5f13\u548c\u7bad\u56ca\uff0c\u91cc\u9762\u63d2\u7740\u6ee1\u6ee1\u5f53\u5f53\u7684\u7c07\u65b0\u7bad\u77e2\u2014\u2014\u8fd9\u53ef\u5c31\u5b9e\u5728\u592a\u4e0d\u5bfb\u5e38\u4e86\u3002</p><p>\u201c\u4ea1\u7075\u5deb\u5e08\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u730e\u8c79\u6f5c\u4f0f\u5728\u9ed1\u6697\u4e2d\uff0c\u7528\u9510\u5229\u7684\u76ee\u5149\u6765\u56de\u641c\u7d22\u7740\uff0c\u602a\u7269\u4e5f\u597d\u4ea1\u7075\u4e5f\u7f62\uff0c\u5979\u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u6709\u4ea1\u7075\u5e08\u624d\u662f\u771f\u6b63\u7684\u76ee\u6807\u3002\u5deb\u5e08\u4e0d\u4f1a\u7ed9\u81ea\u5df1\u5934\u4e0a\u8d34\u6807\u7b7e\uff0c\u4f46\u4ee5\u5979\u7684\u9605\u5386\u89c1\u8bc6\uff0c\u65e0\u8bba\u4f2a\u88c5\u6210\u4ec0\u4e48\u6a21\u6837\uff0c\u54ea\u6015\u53d8\u5f62\u7136\u540e\u6df7\u8fdb\u602a\u7269\u7fa4\u4e2d\uff0c\u53ea\u8981\u770b\u89c1\u5c31\u4e00\u5b9a\u80fd\u591f\u8ba4\u51fa\u6765\uff0c\u7136\u800c\u5bfb\u627e\u4e86\u534a\u664c\uff0c\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u81ea\u5df1\u7684\u5224\u65ad\u5931\u8bef\uff0c\u5e76\u4e0d\u5b58\u5728\u4ea1\u7075\u5e08\uff1f\u54c8\uff0c\u8fd9\u5f53\u7136\u662f\u5728\u5f00\u73a9\u7b11\uff0c\u7edd\u65e0\u53ef\u80fd\u3002\u90a3\u540d\u53ea\u5269\u4e0b\u4e24\u79cd\u89e3\u91ca\uff1a\u6216\u8005\uff0c\u4ea1\u7075\u5e08\u6070\u597d\u79bb\u5f00\u4e86\uff0c\u4e0d\u5728\u8fd9\u652f\u961f\u4f0d\u91cc\uff1b\u6216\u8005\uff0c\u4ea1\u7075\u5e08\u548c\u5979\u4e00\u6837\uff0c\u4e5f\u6b63\u9690\u9041\u5728\u6697\u5904\u3002</p><p>\u6b63\u601d\u5fd6\u95f4\uff0c\u4e00\u80a1\u523a\u9aa8\u5bd2\u610f\u9661\u7136\u81ea\u80cc\u540e\u88ad\u6765\uff0c\u201c\u88ab\u653b\u51fb\u4e86\uff01\u201d\u610f\u8bc6\u77ac\u95f4\u505a\u51fa\u5224\u65ad\uff0c\u8fd9\u662f\u5979\u8eab\u7ecf\u767e\u6218\u6240\u953b\u70bc\u51fa\u6765\u7684\u76f4\u89c9\u3002\u730e\u8c79\u6309\u4e86\u6309\u722a\u5b50\uff0c\u731b\u5730\u5f80\u5de6\u4fa7\u8df3\u5f00\uff0c\u4e00\u9053\u9b3c\u706b\u7f20\u7ed5\u7684\u7070\u6697\u5c04\u7ebf\u4ece\u5979\u7684\u76ae\u80a4\u582a\u582a\u64e6\u8fc7\uff0c\u6ca1\u5165\u571f\u4e2d\u3002</p><p>\u730e\u8c79\u56de\u8fc7\u8eab\u6765\uff0c\u770b\u89c1\u539f\u672c\u865a\u65e0\u7684\u9ed1\u6697\u4e2d\u73b0\u51fa\u4e00\u4e2a\u7626\u524a\u7684\u4eba\u5f62\u8f6e\u5ed3\uff0c\u6162\u6162\u6e05\u6670\uff0c\u90a3\u662f\u4e2a\u7070\u888d\u7537\u5b50\uff0c\u6234\u7740\u515c\u5e3d\uff0c\u4f4e\u7740\u5934\uff0c\u770b\u4e0d\u6e05\u695a\u9762\u5bb9\uff0c\u53cc\u624b\u67af\u7626\u4eff\u4f5b\u9e1f\u722a\uff0c\u6307\u5c16\u6e38\u79bb\u7740\u7eff\u5e7d\u5e7d\u7684\u9b3c\u706b\uff0c\u661f\u661f\u70b9\u70b9\u3002\u773c\u89c1\u730e\u8c79\u8eb2\u5f00\u81ea\u5df1\u7684\u5077\u88ad\uff0c\u4ed6\u9634\u9634\u5730\u7b11\u7740\uff0c\u58f0\u97f3\u4eff\u4f5b\u591c\u67ad\u822c\u523a\u8033\u3002</p><p>\u201c\u662f\u54ea\u4f4d\u670b\u53cb\u5728\u4e00\u8def\u8ddf\u8e2a\u6211\u4eec\uff1f\u201d</p><p>\u4e00\u9635\u72c2\u98ce\u522e\u8fc7\uff0c\u730e\u8c79\u6062\u590d\u6210\u4eba\u5f62\uff0c\u94f6\u53d1\u5973\u5b50\u53cc\u7709\u9ad8\u6311\uff0c\u51b7\u51b7\u903c\u89c6\u7740\u7070\u888d\u7537\u5b50\u3002\u201c\u6211\u662f\u6765\u81ea\u9634\u5f71\u8c37\u7684\u591a\u8299\xb7\u9e70\u624b\uff0c\u201d\u5979\u9ad8\u58f0\u8bf4\uff0c\u201c\u662f\u4f60\u6740\u4e86\u8fd9\u4e9b\u6751\u6c11\uff0c\u628a\u4ed6\u4eec\u53d8\u6210\u4e86\u4ea1\u7075\uff1f\u201d</p><p>\u542c\u5230\u5bf9\u65b9\u81ea\u62a5\u540d\u53f7\uff0c\u7070\u888d\u7537\u5b50\u660e\u663e\u754f\u7f29\u4e86\u4e00\u4e0b\uff0c\u4f46\u7acb\u523b\u6062\u590d\u9547\u5b9a\uff0c\u201c\u6b63\u662f\u9119\u4eba\uff0c\u201d\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u56de\u7b54\uff0c\u201c\u4e0d\u77e5\u5c0a\u656c\u7684\u5973\u58eb\u6709\u4f55\u89c1\u6559\uff1f\u201d</p><p>\u201c\u53ea\u6709\u4e00\u53e5\uff0c\u201d\u591a\u8299\u5389\u559d\uff0c\u53cc\u624b\u4e00\u7ffb\uff0c\u638c\u5fc3\u5df2\u7ecf\u63e1\u4f4f\u4e86\u4e24\u67c4\u519b\u5200\uff0c\u7136\u540e\u4e0b\u4e00\u77ac\u95f4\u5979\u5df2\u7ecf\u5982\u79bb\u5f26\u4e4b\u7bad\u822c\u7b14\u76f4\u524d\u51b2\uff0c\u201c\u4e0b\u5730\u72f1\u53bb\u5427\uff01\u201d</p><p>\u7070\u888d\u7537\u5b50\u5fae\u5fae\u62ac\u8d77\u8138\uff0c\u5ff5\u4e86\u53e5\u5492\u8bed\uff0c\u4e24\u652f\u9aa8\u77db\u51ed\u7a7a\u51fa\u73b0\uff0c\u4e00\u524d\u4e00\u540e\u671d\u7740\u591a\u8299\u98de\u5c04\u800c\u6765\uff0c\u4f46\u5374\u5168\u90fd\u6253\u7a7a\u4e86\u3002\u94f6\u53d1\u5973\u5b50\u7684\u6b65\u6cd5\u975e\u5e38\u8be1\u5f02\uff0c\u770b\u4f3c\u76f4\u7ebf\u524d\u51b2\uff0c\u5176\u5b9e\u5374\u5728\u4e0d\u65ad\u5730\u5de6\u53f3\u53d8\u5e7b\u65b9\u4f4d\uff0c\u8ba9\u5bf9\u624b\u538b\u6839\u65e0\u6cd5\u9501\u5b9a\u76ee\u6807\u3002\u7070\u888d\u7537\u5b50\u53e3\u4e2d\u561f\u56d4\u4e86\u53e5\u4ec0\u4e48\uff0c\u9ed1\u6c14\u4ece\u6307\u5c16\u5c04\u51fa\uff0c\u6269\u6563\u6210\u4e00\u5f20\u5de8\u578b\u9b3c\u8138\uff0c\u98d8\u98d8\u5f80\u524d\uff0c\u5f20\u5f00\u8840\u76c6\u5927\u53e3\uff0c\u7167\u7740\u591a\u8299\u4e00\u53e3\u54ac\u8fc7\u53bb\u3002</p><p>\u591a\u8299\u7eb5\u58f0\u5927\u7b11\uff0c\u94f6\u706b\u4ece\u5979\u7684\u53cc\u773c\u4e2d\u55b7\u6d8c\u800c\u51fa\uff0c\u77ac\u95f4\u5c06\u9b3c\u8138\u711a\u70e7\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u534a\u70b9\u9ed1\u6c14\u4e0d\u5269\u3002\u5979\u903c\u8fd1\u8ddf\u524d\uff0c\u53f3\u624b\u6325\u5200\u780d\u4e0b\uff0c\u7070\u888d\u7537\u5b50\u5306\u5fd9\u95f4\u6765\u4e0d\u53ca\u95ea\u907f\uff0c\u53ea\u80fd\u62ac\u8d77\u5de6\u81c2\u4e00\u6321\u3002\u201c\u5654\u201d\u5730\u4e00\u58f0\u95f7\u54cd\uff0c\u519b\u5200\u780d\u5728\u624b\u81c2\u4e0a\uff0c\u5c45\u7136\u672a\u80fd\u5c06\u5b83\u780d\u65ad\uff0c\u53cd\u800c\u88ab\u5f39\u4e86\u56de\u6765\u3002\u591a\u8299\u5fc3\u4e2d\u8be7\u5f02\uff0c\u52a8\u4f5c\u5374\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5de6\u624b\u519b\u5200\u7167\u7740\u8116\u9888\u6a2a\u626b\u8fc7\u6765\u3002\u4e00\u9762\u767d\u9aa8\u76fe\u724c\u81ea\u7a7a\u6c14\u4e2d\u53ca\u65f6\u51fa\u73b0\uff0c\u6321\u4e0b\u653b\u51fb\uff0c\u4f46\u591a\u8299\u731b\u7136\u62ac\u8d77\u817f\uff0c\u91cd\u91cd\u4e00\u811a\u8e39\u5728\u7070\u888d\u7537\u5b50\u7684\u8179\u90e8\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u8e39\u98de\u4e86\u51fa\u53bb\u3002</p><p>\u7070\u888d\u7537\u5b50\u5728\u5730\u9762\u4e0a\u6323\u624e\u7740\uff0c\u52aa\u529b\u60f3\u8981\u722c\u8d77\u8eab\u53cd\u51fb\uff0c\u4f46\u591a\u8299\u8fd9\u4e00\u811a\u8e39\u5f97\u6781\u91cd\uff0c\u4e94\u810f\u516d\u8151\u90fd\u50cf\u662f\u79fb\u4e86\u4f4d\uff0c\u56db\u80a2\u6839\u672c\u7528\u4e0d\u51fa\u534a\u70b9\u52b2\uff0c\u547c\u5438\u90fd\u56f0\u96be\uff0c\u66f4\u522b\u8bf4\u5ff5\u5492\u3002\u591a\u8299\u51b7\u7b11\u7740\uff0c\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u201c\u8bf4\u51fa\u4f60\u6b64\u884c\u7684\u76ee\u7684\uff0c\u201d\u5979\u5c45\u9ad8\u4e34\u4e0b\u5730\u4fef\u89c6\u7740\u5bf9\u624b\uff0c\u201c\u5426\u5219\u2026\u2026\u201d</p><p>\u201c\u5f88\u4e45\u4e0d\u89c1\u4e86\uff0c\u591a\u8299\u3002\u201d</p><p>\u82cd\u8001\u7684\u58f0\u97f3\u7a81\u7136\u5728\u65c1\u8fb9\u54cd\u8d77\uff0c\u7d27\u63a5\u7740\u4e00\u4e2a\u6d88\u7626\u8eab\u5f71\u4ece\u9ed1\u6697\u4e2d\u6162\u6162\u8e31\u51fa\u6765\uff0c\u4ed6\u54b3\u55fd\u7740\uff0c\u8170\u80cc\u4f5d\u507b\uff0c\u50cf\u662f\u4e2a\u75be\u75c5\u7f20\u8eab\u7684\u8001\u8005\uff0c\u7136\u800c\u591a\u8299\u4e00\u89c1\u4fbf\u9762\u8272\u5927\u53d8\uff0c\u4e0b\u610f\u8bc6\u5730\u5f80\u540e\u9000\u4e86\u4e24\u6b65\u3002</p><p>\u201c\u8428\u739b\u65af\u7279\uff01\u201d</p><p>\u94f6\u706b\u81ea\u5979\u4f53\u5185\u55b7\u6d8c\u800c\u51fa\uff0c\u6c47\u805a\u6210\u718a\u718a\u71c3\u70e7\u7684\u70c8\u7130\u98ce\u66b4\uff0c\u51b2\u7740\u8001\u8005\u547c\u5578\u5e2d\u5377\u800c\u53bb\u3002\u591a\u8299\u81ea\u5df1\u5374\u4e00\u8dc3\u800c\u8d77\uff0c\u671d\u76f8\u53cd\u7684\u65b9\u5411\u5168\u529b\u5954\u8dd1\u3002\u5979\u81ea\u77e5\u4e0d\u662f\u8428\u739b\u65af\u7279\u7684\u5bf9\u624b\uff0c\u4f46\u4ee5\u94f6\u706b\u4e3a\u5c4f\u969c\uff0c\u8981\u9003\u8dd1\u8fd8\u662f\u6709\u628a\u63e1\u7684\u3002\u7136\u800c\u521a\u521a\u51b2\u51fa\u51e0\u6b65\uff0c\u9661\u7136\u4e00\u80a1\u68ee\u68ee\u5bd2\u610f\u8fce\u9762\u8feb\u6765\uff0c\u5c06\u5979\u5b8c\u5168\u7b3c\u7f69\u3002\u591a\u8299\u987f\u4f4f\u811a\u6b65\uff0c\u62ac\u8d77\u5934\uff0c\u53ea\u89c1\u4e00\u4f4d\u5168\u8eab\u9ed1\u8863\u7684\u77ed\u53d1\u5c11\u5973\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\uff0c\u6597\u7bf7\u730e\u730e\u5f20\u5f00\uff0c\u4eff\u4f5b\u7fbd\u7ffc\uff0c\u53cc\u624b\u63e1\u7740\u4e00\u67c4\u5de8\u5927\u7684\u8840\u7ea2\u8272\u9570\u5200\uff0c\u950b\u5203\u4e0a\u6cdb\u7740\u6e05\u6ce0\u6708\u5149\uff0c\u6b63\u9762\u65e0\u8868\u60c5\u5730\u4fef\u89c6\u7740\u5979\u3002</p><p>\u7136\u540e\u5979\u4e00\u6325\u800c\u4e0b\uff0c\u5c06\u591a\u8299\u7684\u8eab\u4f53\u65a9\u6210\u4e24\u6bb5\u3002</p></article>"}}}]);
