(window.webpackJsonp=window.webpackJsonp||[]).push([[1678],{1701:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u627f\u8bfa",content:"<article><h2>\u7b2c\u4e8c\u8282 \u627f\u8bfa</h2><p>\u548c\u5965\u5609\u83b1\u65af\u7684\u4ea4\u6d89\u65e0\u529f\u800c\u8fd4\uff0c\u8fd9\u5176\u5b9e\u4e5f\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u5f3a\u8005\u4e3a\u5c0a\uff0c\u5b9e\u529b\u81f3\u4e0a\uff0c\u8c01\u7684\u62f3\u5934\u5927\u8c01\u7684\u9053\u7406\u5c31\u66f4\u6b63\u786e\uff0c\u800c\u743c\u6069\u663e\u7136\u6253\u4e0d\u8fc7\u5965\u5609\u83b1\u65af\u3002\u5c31\u7b97\u4e0d\u8c08\u529b\u91cf\uff0c\u4ec5\u8bba\u9053\u7406\uff0c\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u5c31\u5982\u5965\u5609\u83b1\u65af\u6240\u8a00\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u662f\u5979\u548c\u53cc\u80de\u80ce\u4e4b\u95f4\u7684\u4ea4\u6613\uff0c\u53cc\u65b9\u4e92\u8d1f\u5c65\u884c\u4e49\u52a1\uff0c\u73b0\u5728\u5965\u5609\u83b1\u65af\u5df2\u7ecf\u6551\u6d3b\u4e86\u743c\u6069\uff0c\u5982\u679c\u53cc\u80de\u80ce\u4e0d\u80af\u79bb\u5f00\uff0c\u81ea\u7136\u5c31\u662f\u8fdd\u7ea6\u3002\u81f3\u4e8e\u743c\u6069\uff0c\u4ed6\u662f\u6240\u8c13\u7684\u7b2c\u4e09\u4eba\uff0c\u6392\u9664\u5728\u534f\u8bae\u53cc\u65b9\u5f53\u4e8b\u4eba\u4e4b\u5916\u3002\u8fd9\u662f\u57fa\u672c\u7684\u6cd5\u5f8b\u5e38\u8bc6\uff0c\u743c\u6069\u4e0a\u8f88\u5b50\u5c31\u662f\u5b66\u8fd9\u4e2a\u7684\uff0c\u60f3\u5426\u8ba4\u90fd\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u53e3\u3002</p><p>\u5f53\u7136\uff0c\u771f\u8981\u73a9\u6cd5\u5f8b\uff0c\u743c\u6069\u6709\u4e00\u767e\u79cd\u529e\u6cd5\u53ef\u4ee5\u7ed5\u8fc7\u8fd9\u4e2a\u95ee\u9898\u3002\u6bd4\u5982\u8bf4\u4ed6\u53ea\u6d88\u5ba3\u79f0\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u90fd\u662f\u672a\u6210\u5e74\u4eba\uff0c\u4ec0\u4e48\u534f\u8bae\u81ea\u7136\u90fd\u4f5c\u5e9f\u3002\u53cc\u80de\u80ce\u4eca\u5e74\u5341\u516d\u5c81\uff0c\u6309\u7167\u5927\u9646\u7684\u901a\u884c\u89c4\u5219\uff0c\u5341\u516d\u5c81\u5df2\u7ecf\u6210\u5e74\u4e86\uff0c\u4f46\u662f\u6309\u7167\u8010\u745f\u7684\u89c4\u77e9\uff0c\u5341\u516b\u5c81\u624d\u7b97\u771f\u6b63\u6210\u5e74\uff0c\u53cc\u80de\u80ce\u548c\u5965\u5609\u83b1\u65af\u90fd\u662f\u8010\u745f\u4eba\uff0c\u6b64\u5730\u662f\u8010\u745f\u5e1d\u56fd\u7684\u6d6e\u7a7a\u57ce\uff0c\u65e0\u8bba\u662f\u5c5e\u4eba\u539f\u5219\u8fd8\u662f\u5c5e\u5730\u539f\u5219\uff0c\u90fd\u5e94\u8be5\u9075\u5b88\u8010\u745f\u7684\u89c4\u77e9\u3002\u518d\u6bd4\u5982\u4ed6\u53ef\u4ee5\u5ba3\u79f0\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u662f\u4ed6\u7684\u4f8d\u5973\uff0c\u6ca1\u6709\u4eba\u8eab\u81ea\u7531\uff0c\u65e0\u6743\u51b3\u5b9a\u81ea\u5df1\u7684\u53bb\u7559\uff0c\u6240\u4ee5\u8fd9\u4efd\u534f\u8bae\u538b\u6839\u5c31\u65e0\u6548\uff0c\u6599\u60f3\u53cc\u80de\u80ce\u4e5f\u4e0d\u4f1a\u4e0d\u914d\u5408\u3002\u4f46\u8fd9\u4e9b\u5176\u5b9e\u90fd\u6ca1\u6709\u610f\u4e49\uff0c\u5965\u5609\u83b1\u65af\u7684\u8bdd\u5df2\u7ecf\u8bf4\u5f97\u5f88\u660e\u767d\uff0c\u8fd9\u4e2a\u4ea4\u6613\u5c31\u662f\u7ed9\u743c\u6069\u7559\u4e2a\u53f0\u9636\uff0c\u5982\u679c\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u8bc6\u76f8\u70b9\uff0c\u4e56\u4e56\u79bb\u53bb\uff0c\u90a3\u4e48\u7686\u5927\u6b22\u559c\uff0c\u5982\u679c\u4e0d\u8bc6\u76f8\uff0c\u90a3\u5979\u5c31\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u81f3\u4e8e\u5982\u4f55\u4e0d\u5ba2\u6c14\u6cd5\u2026\u2026\u6599\u60f3\u603b\u4e0d\u4f1a\u662f\u8bf7\u53bb\u559d\u8336\u5427\u3002</p><p>\u743c\u6069\u4e0d\u662f\u7a77\u51f6\u6781\u6076\u86ee\u4e0d\u8bb2\u7406\u4e4b\u8f88\uff0c\u4f46\u4e5f\u4e0d\u662f\u6e29\u548c\u8c26\u900a\u514b\u5df1\u5b88\u793c\u7684\u541b\u5b50\uff0c\u7edd\u5927\u591a\u6570\u60c5\u51b5\u4e0b\u4ed6\u90fd\u613f\u610f\u8bb2\u9053\u7406\uff0c\u5b88\u89c4\u77e9\uff0c\u4f46\u800d\u8d56\u4e0d\u8ba4\u8d26\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4ed6\u4e5f\u4e0d\u662f\u505a\u4e0d\u51fa\u6765\u3002\u5965\u5609\u83b1\u65af\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u4ed6\u81ea\u7136\u611f\u6fc0\uff0c\u4f46\u56e0\u6b64\u5c31\u8981\u4ed6\u7684\u5973\u4eba\u79bb\u5f00\uff0c\u8fd9\u5374\u662f\u6ca1\u6cd5\u63a5\u53d7\u7684\u3002\u4ea4\u6613\u4e5f\u597d\uff0c\u534f\u8bae\u4e5f\u7f62\uff0c\u4ed6\u90fd\u4e0d\u53ef\u80fd\u8ba4\u53ef\u2014\u2014\u95ee\u9898\u5c31\u5728\u4e8e\uff0c\u4e0d\u8ba4\u53ef\uff0c\u90a3\u4e5f\u662f\u8981\u6709\u4e0d\u8ba4\u53ef\u7684\u5b9e\u529b\u7684\u3002</p><p>\u800c\u4ed6\u6ca1\u6709\u8fd9\u4e2a\u5b9e\u529b\u3002</p><p>\u4e0d\u5230\u5341\u516b\u5c81\uff0c\u5df2\u7ecf\u662f\u80fd\u591f\u8054\u7ed3\u9b54\u7f51\u7b2c\u4e03\u5c42\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u8fd9\u79cd\u8fdb\u6b65\u901f\u5ea6\u4e0d\u8bf4\u524d\u65e0\u53e4\u4eba\u540e\u65e0\u6765\u8005\uff0c\u81f3\u5c11\u4e5f\u662f\u767e\u5e74\u4e0d\u9047\u7684\uff0c\u743c\u6069\u6709\u65f6\u5019\u60f3\u8d77\u6765\u90fd\u4e0d\u7981\u6709\u4e9b\u6cbe\u6cbe\u81ea\u559c\u3002\u4f46\u8fd9\u4e2a\u4e16\u754c\u4e0a\u53d8\u6001\u5b9e\u5728\u662f\u592a\u591a\uff0c\u4ed6\u53c8\u8fd0\u6c14\u592a\u5dee\uff0c\u603b\u662f\u80fd\u591f\u78b0\u5230\uff0c\u906d\u9047\u7684\u4e0d\u662f\u51e0\u767e\u5c81\u7684\u9009\u6c11\u5c31\u662f\u5343\u5e74\u7684\u5deb\u5996\uff0c\u53c8\u6216\u8005\u51e0\u5343\u5c81\u7684\u6076\u9b54\uff0c\u518d\u6216\u8005\u4e0a\u4e07\u5c81\u7684\u5973\u795e\u59d0\u59d0\u2026\u2026\u5c31\u62ff\u5965\u5609\u83b1\u65af\u6765\u8bf4\uff0c\u8bba\u8eab\u4efd\u662f\u53e4\u8010\u745f\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\uff0c\u8bba\u8f88\u5206\u662f\u9634\u9b42\u57ce\u4e3b\u7684\u5e08\u957f\uff0c\u624b\u91cc\u90a3\u672c\u4e66\u867d\u7136\u4e0d\u77e5\u5176\u540d\uff0c\u5374\u7edd\u5bf9\u662f\u795e\u5668\uff0c\u5c31\u7b97\u73b0\u5728\u6210\u4e86\u5e7d\u7075\uff0c\u5b9e\u529b\u5927\u635f\uff0c\u90a3\u4e5f\u4e0d\u662f\u743c\u6069\u80fd\u591f\u6311\u6218\u7684\u3002\u5982\u679c\u5979\u771f\u8981\u7ffb\u8138\u5bf9\u53cc\u80de\u80ce\u4e0b\u6740\u624b\uff0c\u743c\u6069\u60f3\u963b\u62e6\u90fd\u62e6\u4e0d\u4f4f\u3002</p><p>\u6240\u4ee5\u5c31\u53ea\u80fd\u5c48\u670d\u4e86\u3002</p><p>\u743c\u6069\u65e0\u7cbe\u6253\u91c7\u5730\u56de\u5230\u623f\u95f4\uff0c\u8299\u857e\u72c4\u539f\u672c\u8fd8\u62b1\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u89c1\u4ed6\u795e\u60c5\u4fbf\u77e5\u9053\u7ed3\u679c\uff0c\u773c\u4e2d\u795e\u91c7\u987f\u65f6\u9eef\u6de1\u4e0b\u53bb\uff0c\u201c\u6ca1\u5173\u7cfb\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u79bb\u5f00\u4e5f\u597d\uff0c\u514d\u5f97\u2026\u2026\u514d\u5f97\u2026\u2026\u201d</p><p>\u5979\u6897\u54bd\u7740\uff0c\u54ac\u7740\u5634\u5507\uff0c\u52aa\u529b\u4e0d\u8ba9\u773c\u6cea\u6d41\u51fa\uff0c\u8bed\u4e0d\u6210\u58f0\u3002\u743c\u6069\u62b1\u7740\u5979\uff0c\u5fc3\u4e2d\u4e00\u9635\u9635\u5730\u96be\u53d7\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u522b\u8bf4\u5bf9\u4e0d\u8d77\uff0c\u743c\u6069\uff0c\u201d\u4e8b\u5230\u4e34\u5934\uff0c\u4e00\u5411\u67d4\u5f31\u7684\u5c0f\u5973\u5b69\u53cd\u800c\u53d8\u5f97\u575a\u5f3a\u8d77\u6765\uff0c\u5979\u7528\u624b\u80cc\u64e6\u4e86\u64e6\u6cea\u6c34\uff0c\u52c9\u5f3a\u7efd\u5f00\u7b11\u5bb9\uff0c\u201c\u4f60\u4ee5\u524d\u6559\u8fc7\u6211\u7684\uff0c\u4eba\u751f\u5728\u4e16\uff0c\u8981\u8d1f\u8d23\u4efb\uff0c\u4f5c\u51fa\u4e86\u9009\u62e9\uff0c\u5c31\u8981\u627f\u62c5\u540e\u679c\uff0c\u4e0d\u6028\u5929\uff0c\u4e0d\u5c24\u4eba\u3002\u6211\u7b54\u5e94\u4e86\u8d70\uff0c\u90a3\u5c31\u8d70\uff0c\u8bf4\u8bdd\u8981\u7b97\u6570\u561b\u3002\u201d</p><p>\u201c\u2026\u2026\u7b28\u86cb\u3002\u201d</p><p>\u743c\u6069\u53ea\u89c9\u80f8\u53e3\u5835\u5f97\u614c\uff0c\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u3002\u5176\u5b9e\u4ed6\u5fc3\u77e5\u809a\u660e\uff0c\u4ee5\u5965\u5609\u83b1\u65af\u6240\u8868\u73b0\u51fa\u6765\u7684\u5bf9\u73ca\u5609\u7684\u91cd\u89c6\uff0c\u5c31\u7b97\u662f\u5f53\u65f6\u8299\u857e\u72c4\u4e0d\u6073\u6c42\uff0c\u4e0d\u7b54\u5e94\u4ec0\u4e48\u4ea4\u6613\uff0c\u5965\u5609\u83b1\u65af\u4e5f\u4e0d\u4f1a\u771f\u770b\u7740\u743c\u6069\u6302\u6389\uff0c\u5341\u6709\u516b\u4e5d\u8fd8\u662f\u8981\u51fa\u624b\u76f8\u52a9\u7684\u3002\u4f46\u8299\u857e\u72c4\u54ea\u91cc\u4f1a\u77e5\u9053\u8fd9\u4e9b\u5462\uff0c\u4e8e\u5979\u800c\u8a00\uff0c\u5728\u5f53\u65f6\u7684\u60c5\u5883\u4e0b\uff0c\u5df2\u7ecf\u662f\u505a\u51fa\u4e86\u6700\u597d\u7684\u9009\u62e9\uff0c\u65e0\u6cd5\u88ab\u8d23\u5907\u3002\u743c\u6069\u8bf4\u201c\u7b28\u86cb\u201d\uff0c\u4e5f\u6ca1\u6709\u4e1d\u6beb\u8d23\u602a\u7684\u610f\u601d\uff0c\u52c9\u5f3a\u8981\u8bf4\u7684\u8bdd\uff0c\u53ea\u662f\u65e0\u53ef\u5948\u4f55\u7684\u53f9\u606f\u5427\u3002</p><p>\u201c\u5176\u5b9e\u554a\uff0c\u743c\u6069\uff0c\u201d\u5c0f\u5973\u5b69\u5782\u7740\u773c\u5e18\uff0c\u201c\u5c31\u7b97\u5965\u5609\u83b1\u65af\u5973\u58eb\u4e0d\u8bf4\uff0c\u6211\u5fc3\u91cc\u5176\u5b9e\u4e5f\u6709\u8fc7\u79bb\u5f00\u7684\u60f3\u6cd5\u5462\uff0c\u201d\u5979\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u201c\u6240\u4ee5\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u4e0d\u5168\u662f\u56e0\u4e3a\u5979\u2026\u2026\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u7981\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u6211\u53d1\u73b0\uff0c\u81ea\u5df1\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u554a\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u8981\u51fa\u95e8\uff0c\u9080\u8bf7\u6211\u540c\u884c\uff0c\u6211\u597d\u9ad8\u5174\u597d\u9ad8\u5174\u3002\u53ef\u662f\u63a5\u7740\u5c31\u53d1\u73b0\uff0c\u4f60\u6709\u73ca\u5609\u59d0\u59d0\uff0c\u8fd8\u6709\u838e\u73de\u514b\uff0c\u6bcf\u5929\u5fd9\u90fd\u5fd9\u4e0d\u8fc7\u6765\u3002\u8fd9\u6b21\u51fa\u6d77\uff0c\u6211\u4e4b\u6240\u4ee5\u4e3b\u52a8\u8981\u6c42\u4e00\u8d77\u6765\uff0c\u5c31\u662f\u5e0c\u671b\u80fd\u591f\u5e2e\u4e0a\u70b9\u5fd9\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u7ed3\u679c\u8ba9\u6211\u660e\u767d\uff0c\u539f\u6765\u6211\u662f\u6700\u591a\u4f59\u7684\u90a3\u4e00\u4e2a\u3002\u8299\u8389\u5a05\u80fd\u591f\u4e3a\u4f60\u6cbb\u7597\uff0c\u838e\u73de\u514b\u80fd\u591f\u66ff\u4f60\u62b5\u6321\u654c\u4eba\uff0c\u5c31\u8fde\u73ca\u5609\u59d0\u59d0\uff0c\u5c31\u8fde\u5979\u2026\u2026\u672c\u6765\u660e\u660e\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u7684\uff0c\u7ed3\u679c\u90fd\u80fd\u5e72\u6389\u90a3\u4e2a\u6d77\u795e\uff0c\u201d\u5979\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u4f4e\uff0c\u201c\u53ea\u6709\u6211\uff0c\u4ec0\u4e48\u5fd9\u90fd\u5e2e\u4e0d\u4e0a\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u505a\u4e0d\u5230\uff0c\u7ad9\u5728\u90a3\u91cc\u89c9\u5f97\u81ea\u5df1\u597d\u591a\u4f59\u554a\uff0c\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u4eba\uff0c\u88ab\u629b\u5f03\u4e86\u4e00\u6837\u3002\u201d</p><p>\u201c\u6211\u4e5f\u5df2\u7ecf\u5f88\u52aa\u529b\u4e86\u5440\uff0c\u660e\u660e\u2026\u2026\u660e\u660e\u5c31\u5df2\u7ecf\u5f88\u52aa\u529b\u5f88\u52aa\u529b\u4e86\u5440\uff0c\u201d\u5979\u62bd\u6ce3\u7740\uff0c\u773c\u6cea\u5fcd\u4e0d\u4f4f\u5730\u593a\u7736\u800c\u51fa\uff0c\u201c\u4f60\u8eab\u8fb9\u90a3\u4e9b\u5973\u5b69\u5b50\uff0c\u4e00\u4e2a\u4e2a\u90fd\u90a3\u4e48\u5947\u602a\uff0c\u5929\u751f\u7684\u4e86\u4e0d\u8d77\uff0c\u6211\u600e\u4e48\u6bd4\u90fd\u6bd4\u4e0d\u4e0a\uff0c\u6211\u77e5\u9053\u751a\u81f3\u53ef\u80fd\u4e00\u8f88\u5b50\u90fd\u6bd4\u4e0d\u4e0a\uff0c\u53ef\u662f\u6211\u8fd8\u662f\u62b1\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u6211\u771f\u7684\u662f\u5728\u52aa\u529b\u554a\u3002\u4f60\u5728\u6211\u8eab\u8fb9\u7684\u65f6\u5019\uff0c\u6211\u5168\u5fc3\u5168\u610f\u8ba8\u4f60\u6b22\u5fc3\uff1b\u4f60\u4e0d\u5728\u6211\u8eab\u8fb9\u7684\u65f6\u5019\uff0c\u6bcf\u4e00\u5206\uff0c\u6bcf\u4e00\u79d2\uff0c\u6211\u90fd\u5728\u770b\u4e66\uff0c\u5728\u6e29\u4e60\uff0c\u5728\u953b\u70bc\u9b54\u6cd5\uff0c\u5c3d\u4e00\u5207\u53ef\u80fd\u63d0\u5347\u81ea\u5df1\u7684\u80fd\u529b\u3002\u4f60\u8981\u6211\u5b66\u6df1\u571f\u9884\u8a00\uff0c\u6211\u5c31\u7167\u4f60\u7684\u8bdd\u53bb\u505a\uff0c\u90a3\u4efd\u7b14\u8bb0\u4e0a\u7684\u6240\u6709\u5185\u5bb9\u6211\u90fd\u5df2\u7ecf\u638c\u63e1\u4e86\uff0c\u751a\u81f3\u6bcf\u4e2a\u5b57\u6211\u90fd\u80fd\u80cc\u4e0b\u6765\uff0c\u800c\u4e14\u6211\u90fd\u5df2\u7ecf\u5feb\u8981\u80fd\u591f\u611f\u5e94\u5230\u7b2c\u56db\u5c42\u9b54\u7f51\u4e86\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u7ed3\u679c\u8fd8\u662f\u4e0d\u884c\uff0c\u5df2\u7ecf\u7aed\u5c3d\u5168\u529b\u4e86\uff0c\u5374\u8fd8\u662f\u4e0d\u582a\u4e00\u51fb\u3002\u6211\u89c9\u5f97\uff0c\u201d\u5979\u7684\u53cc\u624b\u7ede\u7f20\u5728\u4e00\u8d77\uff0c\u6307\u8282\u56e0\u4e3a\u8fc7\u5ea6\u7528\u529b\u800c\u53d1\u767d\uff0c\u201c\u89c9\u5f97\u81ea\u5df1\u771f\u7684\u597d\u5931\u8d25\u554a\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\u65e0\u8bed\u3002</p><p>\u4ed6\u6700\u521d\u4e4b\u6240\u4ee5\u5c06\u90a3\u4efd\u6df1\u571f\u9884\u8a00\u7684\u7b14\u8bb0\u4ea4\u7ed9\u8299\u857e\u72c4\uff0c\u4e00\u65b9\u9762\u662f\u73b0\u5b9e\u9700\u8981\uff0c\u5f53\u65f6\u5927\u5bb6\u8981\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u6025\u7f3a\u4e00\u4e2a\u5411\u5bfc\u7c7b\u578b\u7684\u961f\u5458\uff0c\u800c\u8299\u857e\u72c4\u4f5c\u4e3a\u552f\u4e00\u7684\u9884\u8a00\u5e08\uff0c\u662f\u5b66\u4e60\u5b83\u7684\u6700\u4f73\u4eba\u9009\u3002\u53e6\u4e00\u65b9\u9762\u5374\u4e5f\u672a\u5c1d\u6ca1\u6709\u4e00\u70b9\u79c1\u5fc3\uff0c\u4ed6\u5927\u7565\u770b\u8fc7\u90a3\u672c\u7b14\u8bb0\uff0c\u4ece\u5b66\u672f\u7684\u89d2\u5ea6\u800c\u8a00\uff0c\u6df1\u571f\u9884\u8a00\u786e\u5b9e\u9887\u4e3a\u795e\u5999\uff0c\u522b\u51fa\u5fc3\u88c1\uff0c\u4f46\u5bf9\u6218\u6597\u80fd\u529b\u7684\u63d0\u5347\u5374\u6ca1\u591a\u5c11\u5e2e\u52a9\uff0c\u6bd5\u7adf\u5b83\u88ab\u53d1\u660e\u51fa\u6765\u7684\u672c\u610f\u5c31\u4e0d\u662f\u4e3a\u4e86\u6218\u9635\u6740\u4f10\uff0c\u800c\u662f\u63a2\u91d1\u6316\u77ff\u3002\u90a3\u65f6\u5019\u4ed6\u8fd8\u6ca1\u548c\u8299\u857e\u72c4\u590d\u5408\uff0c\u5766\u767d\u5730\u8bf4\uff0c\u5fc3\u5e95\u7ec8\u7a76\u8fd8\u662f\u5b58\u4e86\u4e00\u4e1d\u82a5\u8482\uff0c\u7559\u4e86\u4e00\u9053\u9632\u5907\uff0c\u6240\u4ee5\u8ba9\u5979\u5b66\u8fd9\u79cd\u51e0\u4e4e\u6ca1\u6709\u6218\u6597\u80fd\u529b\u7684\u6280\u827a\uff0c\u4e5f\u6709\u201c\u9884\u9632\u4e07\u4e00\u201d\u7684\u610f\u601d\u3002\u8299\u857e\u72c4\u4e0d\u7b97\u7edd\u9876\u806a\u660e\uff0c\u5374\u4e5f\u4e0d\u662f\u7b28\u86cb\uff0c\u8fd9\u5c42\u610f\u601d\u5979\u80af\u5b9a\u731c\u5f97\u51fa\u6765\uff0c\u4f46\u53cc\u65b9\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4ece\u4e0d\u8bf4\u7834\u3002\u540e\u6765\u53cc\u65b9\u611f\u60c5\u6e10\u597d\uff0c\u743c\u6069\u539f\u8c05\u4e86\u5979\u66fe\u7ecf\u7684\u80cc\u53db\uff0c\u771f\u6b63\u518d\u6b21\u4ece\u5fc3\u5e95\u63a5\u7eb3\u5979\uff0c\u8fd9\u79cd\u62c5\u5fc3\u81ea\u7136\u6ca1\u4e86\u5fc5\u8981\uff0c\u4f46\u4e8b\u5b9e\u5df2\u7ecf\u53d1\u751f\uff0c\u4e5f\u53ea\u597d\u7ee7\u7eed\u4e0b\u53bb\u4e86\u3002</p><p>\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u8299\u857e\u72c4\u4e4b\u6240\u4ee5\u663e\u5f97\u201c\u5f31\u201d\uff0c\u7814\u4e60\u7684\u4e13\u4e1a\u662f\u4e00\u4e2a\u65b9\u9762\uff0c\u4f46\u66f4\u91cd\u8981\u7684\u539f\u56e0\u5219\u662f\u5982\u5979\u81ea\u5df1\u521a\u624d\u6240\u8bf4\u7684\u90a3\u6837\uff1a\u743c\u6069\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\uff0c\u4e00\u4e2a\u4e2a\u90fd\u662f\u532a\u5937\u6240\u601d\u7684\u5b58\u5728\u3002\u6885\u83f2\u65af\u662f\u795e\u5b50\uff0c\u51db\u662f\u9f99\u5973\uff0c\u838e\u73de\u514b\u4e5f\u66fe\u7ecf\u662f\u795e\u5b50\uff0c\u73b0\u5728\u662f\u9b45\u9b54\uff0c\u5c31\u8fde\u201c\u660e\u660e\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u201d\u7684\u73ca\u5609\uff0c\u53d1\u8d77\u5a01\u6765\u4e5f\u4e00\u51fb\u8f70\u6740\u4e86\u795e\u660e\u3002\u5979\u4eec\u5df2\u7ecf\u662f\u8d85\u51fa\u4e86\u5e38\u7406\u7684\u8303\u7574\uff0c\u8299\u857e\u72c4\u4e00\u4e2a\u666e\u901a\u4eba\u7c7b\u5c11\u5973\uff0c\u5c31\u7b97\u662f\u518d\u52aa\u529b\uff0c\u4e5f\u662f\u6839\u672c\u6ca1\u6cd5\u6bd4\u7684\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u4e27\u6c14\u7684\u3002\u53ea\u662f\u9053\u7406\u5982\u6b64\uff0c\u6210\u5929\u770b\u5230\u5979\u4eec\uff0c\u5fc3\u91cc\u6ca1\u6cd5\u4e0d\u81ea\u5351\u5427\u3002\u800c\u5728\u6218\u6597\u4e2d\uff0c\u8fd9\u79cd\u611f\u89c9\u5c31\u5c24\u5176\u660e\u663e\uff0c\u54ea\u6015\u662f\u62fc\u5c3d\u5168\u529b\uff0c\u8fde\u751f\u547d\u90fd\u613f\u610f\u727a\u7272\uff0c\u4f46\u4f9d\u7136\u8fd8\u662f\u6ca1\u6cd5\u963b\u6321\u654c\u4eba\u3002\u8fd9\u79cd\u6df1\u6df1\u7684\u65e0\u529b\u611f\uff0c\u8db3\u4ee5\u8ba9\u518d\u575a\u5f3a\u7684\u4eba\u5fc3\u7070\u610f\u51b7\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4ec5\u4ec5\u662f\u529b\u91cf\u4e0a\u7684\u5f31\u5c0f\uff0c\u5e94\u8be5\u8fd8\u4e0d\u8db3\u4ee5\u8ba9\u8299\u857e\u72c4\u60f3\u5230\u8981\u79bb\u5f00\u3002\u6bd4\u8d77\u201c\u6218\u6597\u65f6\u7684\u540c\u4f34\u201d\uff0c\u5979\u663e\u7136\u66f4\u613f\u610f\u62c5\u4efb\u201c\u60c5\u4eba\u201d\u8fd9\u4e2a\u89d2\u8272\u3002\u4f46\u743c\u6069\u7684\u6076\u52a3\u884c\u5f84\u663e\u7136\u518d\u6b21\u8ba9\u5979\u4f24\u5fc3\u4e86\uff0c\u4e3b\u52a8\u9080\u8bf7\u5979\u540c\u884c\uff0c\u7ed3\u679c\u51fa\u57ce\u540e\u4e00\u76f4\u5c06\u5979\u667e\u5728\u4e00\u8fb9\uff0c\u53ea\u987e\u7740\u966a\u4f34\u73ca\u5609\u2014\u2014\u5982\u679c\u8bf4\u8fd9\u90fd\u8fd8\u52c9\u5f3a\u80fd\u591f\u5bb9\u5fcd\uff0c\u90a3\u4e48\u838e\u73de\u514b\u7684\u6210\u529f\u52a0\u5165\u663e\u7136\u6210\u4e86\u538b\u65ad\u9a86\u9a7c\u7684\u6700\u540e\u4e00\u6839\u7a3b\u8349\uff0c\u5f53\u573a\u5c31\u5dee\u70b9\u8981\u5d29\u6e83\u3002\u867d\u7136\u88ab\u743c\u6069\u54c4\u4f4f\u4e86\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\u53ea\u4f1a\u8d8a\u60f3\u8d8a\u6c14\uff0c\u8d8a\u60f3\u8d8a\u4e0d\u7518\u5fc3\uff0c\u7ec8\u7a76\u8fd8\u662f\u8981\u7206\u53d1\u51fa\u6765\u3002</p><p>\u62b1\u7740\u6000\u4e2d\u5782\u6cea\u7684\u5973\u5b69\uff0c\u743c\u6069\u5fc3\u91cc\u7684\u540e\u6094\u65e0\u6cd5\u7528\u8a00\u8bed\u6765\u5f62\u5bb9\u3002\u5982\u679c\u80fd\u591f\u65f6\u95f4\u5012\u9000\uff0c\u80fd\u591f\u56de\u5230\u5341\u51e0\u5929\u524d\uff0c\u751a\u81f3\u51e0\u5929\u524d\uff0c\u54ea\u6015\u4f1a\u60f9\u5f97\u73ca\u5609\u4e0d\u9ad8\u5174\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u8981\u62bd\u51fa\u65f6\u95f4\u6765\u966a\u4f34\u8299\u857e\u72c4\uff0c\u4e0d\u8ba9\u5979\u53d7\u5230\u8fd9\u6837\u7684\u51b7\u843d\u3002\u7136\u800c\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u8bf4\u8fd9\u4e9b\u8fd8\u6709\u4ec0\u4e48\u610f\u4e49\u5462\u3002</p><p>\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u548c\u4f60\u5728\u4e00\u8d77\u8fd9\u4e9b\u65f6\u95f4\uff0c\u6211\u771f\u7684\u5f88\u9ad8\u5174\uff0c\u6bcf\u6b21\u770b\u5230\u4f60\uff0c\u6bcf\u6b21\u4f60\u966a\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u5c31\u597d\u5f00\u5fc3\u597d\u5f00\u5fc3\u5462\u3002\u5c31\u7b97\u4ee5\u540e\u4e0d\u80fd\u5728\u4e00\u8d77\u4e86\uff0c\u4e5f\u4f1a\u6c38\u8fdc\u90fd\u8bb0\u5f97\u5427\uff0c\u201d\u5c11\u5973\u4ef0\u8d77\u8138\uff0c\u52aa\u529b\u5730\u9732\u51fa\u751c\u7f8e\u7b11\u5bb9\uff0c\u8138\u988a\u5374\u8fd8\u6302\u7740\u6e05\u6e05\u6cea\u75d5\uff0c\u5979\u8ba4\u8ba4\u771f\u771f\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u8bf4\uff0c\u201c\u743c\u6069\uff0c\u6211\u7231\u4f60\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u543b\u4e0a\u5979\u7684\u524d\u989d\uff0c\u5728\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u4e0b\u5b9a\u4e86\u51b3\u5fc3\u3002</p><p>\u201c\u6211\u4e0d\u4f1a\u653e\u5f03\u4f60\uff0c\u8299\u857e\u72c4\u3002\u201d\u4ed6\u8f7b\u58f0\u4f46\u575a\u5b9a\u5730\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u6211\u5728\u6b64\u8d77\u8a93\uff0c\u4ee5\u6211\u7684\u771f\u540d\u8d77\u8a93\uff0c\u201d\u743c\u6069\u51dd\u89c6\u7740\u5979\u7684\u53cc\u7738\uff0c\u201c\u4eca\u65e5\u867d\u7136\u6682\u522b\uff0c\u4f46\u6211\u4e00\u5b9a\u4f1a\u63a5\u4f60\u56de\u6765\uff0c\u4efb\u4f55\u4eba\u90fd\u4f11\u60f3\u963b\u62e6\uff0c\u4efb\u4f55\u4eba\u3002\u4f60\u7b49\u6211\u4e09\u2026\u2026\u4e0d\uff0c\u4e24\u5e74\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u5728\u6b64\u627f\u8bfa\uff0c\u8299\u857e\u72c4\uff0c\u6700\u591a\u7b49\u6211\u4e24\u5e74\u3002\u5728\u4f60\u5341\u516b\u5c81\u751f\u65e5\u7684\u90a3\u4e00\u5929\uff0c\u5ac1\u7ed9\u6211\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u5c11\u5973\u770b\u7740\u4ed6\uff0c\u539f\u672c\u9eef\u6de1\u7684\u773c\u795e\u4e2d\u6709\u4e86\u5149\u5f69\uff0c\u201c\u4e00\u8a00\u4e3a\u5b9a\uff1f\u201d\u5979\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d\u743c\u6069\u8bf4\u3002</p></article>"}}}]);
