(window.webpackJsonp=window.webpackJsonp||[]).push([[168],{191:function(p){p.exports={chapterTitle:null,title:"\u7b2c168\u7ae0 \u7236\u5b50\u591c\u8c0b",content:"<article><h2>\u7b2c168\u7ae0 \u7236\u5b50\u591c\u8c0b</h2><p>\u3000\u3000\u90b5\u81f3\u521a\u505a\u9910\u996e\u4e4b\u524d\uff0c\u66fe\u5728\u519c\u673a\u5382\u5f53\u5382\u957f\uff0c\u90a3\u65f6\u519c\u673a\u5382\u7684\u6548\u76ca\u8fd8\u4e0d\u9519\uff1b\u8fdb\u5165\u4e5d\u5341\u5e74\u4ee3\uff0c\u4e3a\u4e86\u9002\u65f6\u7684\u8ba9\u519c\u4e1a\u751f\u4ea7\u5411\u89c4\u6a21\u5316\u3001\u4ea7\u4e1a\u5316\u53d1\u5c55\uff0c\u56fd\u5185\u5f00\u59cb\u52a0\u5927\u5bf9\u519c\u673a\u8865\u8d34\u7684\u529b\u5ea6\uff0c\u4fc3\u4f7f\u519c\u6c11\u8d2d\u7f6e\u519c\u673a\u548c\u5e94\u7528\u65b0\u6280\u672f\u3002\u5728\u8fd9\u79cd\u4ea7\u4e1a\u80cc\u666f\u4e0b\uff0c\u53d7\u519c\u673a\u5c40\u5206\u7ba1\u7684\u519c\u673a\u5382\u4f1a\u53d7\u5230\u6700\u5927\u9650\u5ea6\u7684\u7167\u987e\uff0c\u519c\u673a\u5382\u5374\u5728\u8fd9\u51e0\u5e74\u9677\u8fdb\u5165\u4e0d\u6577\u51fa\u7684\u56f0\u5883\uff0c\u7ba1\u7406\u4e0a\u7684\u95ee\u9898\u76f8\u5f53\u4e25\u91cd\u3002</p><p>\u3000\u3000\u674e\u660e\u5b66\u662f\u575a\u51b3\u8981\u6c42\u5c06\u519c\u673a\u5382\u7684\u9886\u5bfc\u73ed\u5b50\u5168\u90e8\u6362\u6389\uff0c\u4f46\u5728\u4e0e\u5e02\u519c\u673a\u5c40\u7684\u8c08\u5224\u4e2d\uff0c\u91c7\u53d6\u5382\u957f\u7ade\u8058\u7684\u6298\u4e2d\u65b9\u6cd5\uff0c\u6709\u8d44\u683c\u53c2\u52a0\u7684\u7ade\u8058\u4eba\u9009\u4e5f\u53ea\u9650\u4e8e\u539f\u519c\u673a\u5382\u9886\u5bfc\u5c42\uff0c\u53c8\u5728\u7ade\u8058\u4eba\u5458\u7684\u8d44\u683c\u4e0a\u52a0\u4ee5\u9650\u5236\uff0c\u79cd\u79cd\u6761\u4ef6\u9650\u5236\uff0c\u519c\u673a\u5382\u7684\u5382\u957f\u4e5f\u53ea\u80fd\u5728\u539f\u6765\u51e0\u4e2a\u5382\u957f\u3001\u526f\u5382\u957f\u4e4b\u95f4\u62e9\u51fa\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5085\u4fca\u5148\u8ba9\u4ed6\u56de\u5bb6\uff0c\u4e0b\u8f66\u65f6\u770b\u7740\u4e24\u4e2a\u5c0f\u4e2b\u5934\u90fd\u5728\u540e\u5ea7\u7761\u7740\u4e86\uff0c\u7b11\u7740\u5bf9\u5085\u4fca\u8bf4\uff1a\u201c\u82b7\u5f64\u8fd9\u4e2a\u5468\u672b\u4f1a\u5230\u6d77\u5dde\u6765\uff0c\u770b\u5230\u4f60\u5bb6\u7684\u4e2b\u5934\uff0c\u4e00\u5b9a\u4f1a\u9ad8\u5174\u574f\u4e86\u3002\u201d</p><p>\u3000\u3000\u56de\u5230\u5bb6\uff0c\u7238\u7238\u4e5f\u521a\u5230\u5bb6\u91cc\uff0c\u5988\u5988\u5750\u5728\u6c99\u53d1\u4e0a\u770b\u7535\u89c6\uff0c\u5f20\u606a\u63a2\u5934\u770b\u4e86\u770b\uff0c\u662f\u738b\u5fd7\u6587\u4e0e\u59dc\u73ca\u4e3b\u6f14\u7684\u300a\u8fc7\u628a\u763e\u300b\uff0c\u8fd9\u7535\u5267\u89c6\u4e5d\u4e09\u5e74\u5c31\u5728\u6d77\u5dde\u7535\u89c6\u53f0\u64ad\u8fc7\uff0c\u6ca1\u60f3\u5230\u4f1a\u6709\u91cd\u64ad\uff0c\u770b\u624b\u8868\u90fd\u5feb\u5341\u4e00\u70b9\uff1a\u201c\u600e\u4e48\u71ac\u591c\u770b\u8fd9\u4e2a\uff1f\u201d\u5f20\u606a\u8ddf\u7238\u7238\u90fd\u4e60\u60ef\u665a\u7761\u65e9\u8d77\uff0c\u5988\u5988\u5c31\u4e0d\u884c\uff0c\u5c5e\u4e8e\u90a3\u79cd\u55dc\u7761\u578b\u7684\u4e2d\u5e74\u5987\u5973\uff0c\u4eca\u5e74\u624d\u56db\u5341\u4e8c\u5c81\uff0c\u6bd4\u540c\u9f84\u4eba\u8981\u5e74\u8f7b\u5f97\u591a\uff0c\u5f20\u606a\u4e5f\u662f\u9057\u4f20\u7236\u6bcd\u6f02\u4eae\u57fa\u56e0\u7684\u7f18\u6545\uff0c\u957f\u76f8\u5f88\u8ba8\u5973\u5b69\u5b50\u559c\u6b22\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u8bf4\u9053\uff1a\u201c\u4f60\u7238\u73b0\u5728\u6390\u7740\u8fd9\u4e2a\u70b9\u56de\u6765\uff0c\u4e8c\u5957\u8fd9\u6bb5\u65f6\u95f4\u5728\u5341\u70b9\u540e\u7684\u7535\u89c6\u5267\u90fd\u4e0d\u9519\uff0c\u8fc7\u6bb5\u65f6\u95f4\u8981\u91cd\u64ad\u300a\u5c04\u96d5\u82f1\u96c4\u4f20\u300b\u2026\u2026\u201d\u5c06\u7535\u89c6\u62a5\u62ff\u7ed9\u5f20\u606a\u770b\uff0c\u6709\u4e8c\u5957\u5341\u70b9\u6863\u7535\u89c6\u5267\u91cd\u64ad\u5b89\u6392\u7684\u4ecb\u7ecd\uff0c\u8fd8\u6709\u300a\u4e0a\u6d77\u6ee9\u300b\uff0c\u90fd\u662f\u4e0d\u9519\u7684\u7535\u89c6\uff0c\u5f20\u606a\u8981\u6709\u65f6\u95f4\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u91cd\u6e29\u4e00\u4e0b\uff0c\u300a\u5c04\u96d5\u82f1\u96c4\u4f20\u300b\u8fd8\u662f\u516b\u4e09\u5e74\u9ec4\u65e5\u534e\u3001\u7fc1\u7f8e\u73b2\u7248\u672c\uff0c\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u5341\u5e74\u540e\u56fd\u5185\u7ffb\u62cd\u7684\u7248\u672c\u7b80\u76f4\u5c31\u662f\u6e23\u6ed3\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e5f\u6709\u5174\u81f4\u5750\u4e0b\u6765\u770b\u4e00\u4f1a\u513f\u7535\u89c6\uff0c\u5f20\u606a\u4ece\u51b0\u7bb1\u91cc\u62ff\u4e86\u74f6\u996e\u6599\uff0c\u6ca1\u6709\u63d0\u65bd\u536b\u5fe0\u8bf7\u5ba2\u7684\u4e8b\u60c5\uff0c\u8bf4\u4e0d\u5b9a\u7238\u7238\u5bf9\u65bd\u536b\u5fe0\u4f1a\u6709\u5370\u8c61\uff0c\u53ea\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u5728\u9f99\u534e\u5bbe\u9986\u770b\u5230\u519c\u673a\u5c40\u7684\u4e24\u4e2a\u9886\u5bfc\u5728\u90a3\u91cc\u8d4c\u94b1\uff0c\u4f5c\u966a\u7684\u597d\u50cf\u662f\u519c\u673a\u5382\u7684\u5382\u957f\uff0c\u8138\u4e0a\u662f\u75e4\u75ae\u75a4\u7684\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5468\u5065\u3001\u5468\u9ebb\u5b50\u2026\u2026\u201d\u5f20\u77e5\u884c\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u519c\u673a\u5c40\u4e0d\u80af\u5c06\u519c\u673a\u5382\u7684\u4eba\u4e8b\u6743\u653e\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eba\u4e8b\u4e0d\u52a8\uff0c\u6240\u8c13\u7684\u4f53\u5236\u6539\u9769\u8981\u6253\u591a\u5c11\u6298\u6263\uff1f\u201d\u5f20\u606a\u77e5\u9053\u6709\u4e9b\u65f6\u5019\u5404\u65b9\u52bf\u529b\u5e73\u8861\u4e0b\u6765\u7684\u7ed3\u679c\u5f88\u8ba9\u4eba\u65e0\u8010\uff0c\u201c\u65e2\u7136\u662f\u53d8\u9769\uff0c\u5c31\u4e0d\u80fd\u53ea\u8003\u8651\u5e73\u8861\u5404\u65b9\u9762\u7684\u5229\u76ca\uff0c\u5f97\u7f6a\u4e00\u90e8\u4eba\u662f\u96be\u514d\u7684\uff0c\u6211\u770b\u6d77\u5dde\u63a7\u80a1\u8981\u66f4\u52a0\u7684\u5f3a\u52bf\uff0c\u625b\u7740\u5e02\u91cc\u7684\u5927\u65d7\uff0c\u96be\u9053\u8981\u8ba9\u519c\u673a\u5c40\u6321\u4f4f\u8def\uff1f\u5c31\u62ff\u519c\u673a\u5382\u5f00\u5200\uff0c\u674e\u660e\u5b66\u4e0d\u662f\u5e0c\u671b\u5c06\u519c\u673a\u5382\u7684\u9886\u5bfc\u90fd\u6362\u6389\u5427\uff0c\u8ba9\u4ed6\u653e\u624b\u53bb\u505a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u600e\u4e48\u4e3b\u52a8\u5173\u5fc3\u8d77\u6d77\u5dde\u63a7\u80a1\u7684\u4e8b\u60c5\u6765\uff1f\u201d\u5f20\u77e5\u884c\u5947\u602a\u7684\u95ee\uff0c\u201c\u522b\u662f\u5728\u5916\u9762\u53c8\u60f9\u4e8b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u5dde\u63a7\u80a1\u662f\u5e02\u91cc\u7684\u8bd5\u70b9\uff0c\u6ca1\u6709\u653e\u592a\u591a\u7684\u6743\uff0c\u4f60\u4eec\u8981\u81ea\u5df1\u4e89\u53d6\uff0c\u4eba\u4e8b\u6743\u4e0e\u8d22\u6743\u6293\u5728\u624b\u91cc\uff0c\u624d\u80fd\u771f\u6b63\u8c08\u7ecf\u8425\u6743\u7684\u95ee\u9898\uff0c\u6211\u4e5f\u662f\u96be\u5f97\u652f\u6301\u4f60\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u5df2\u7ecf\u8ddf\u519c\u673a\u5c40\u8c08\u59a5\u4eba\u4e8b\u53d8\u66f4\u7684\u65b9\u6848\uff0c\u7ba1\u7406\u5c42\u91c7\u53d6\u7ade\u8058\u4e0a\u5c97\u7684\u65b9\u6cd5\uff0c\u7a81\u7136\u53d8\u5316\uff0c\u53ea\u6015\u4e0d\u592a\u597d\u2026\u2026\u201d\u5f20\u77e5\u884c\u6709\u4e9b\u72b9\u8c6b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u8bb0\u5f97\u4e0a\u6b21\u5728\u56db\u51e4\u6865\u90a3\u8fb9\u5403\u591c\u5bb5\u65f6\uff0c\u5750\u6211\u5de6\u624b\u7684\u90a3\u4e2a\u90b5\u81f3\u521a\uff0c\u4ed6\u73b0\u5728\u662f\u4e16\u7eaa\u9910\u996e\u7684\u603b\u7ecf\u7406\uff0c\u4ed6\u4e4b\u524d\u662f\u519c\u673a\u5382\u7684\u5382\u957f\uff0c\u5c31\u662f\u7ed9\u8fd9\u4e2a\u5468\u9ebb\u5b50\u641e\u4e0b\u53f0\u7684\uff0c\u6211\u60f3\u4ed6\u624b\u91cc\u4f1a\u6709\u4e00\u4e9b\u6750\u6599\uff0c\u6750\u6599\u65e0\u8bba\u4ea4\u5230\u5e02\u4fe1\u8bbf\u5c40\u8fd8\u662f\u68c0\u5bdf\u9662\u4fe1\u8bbf\u63a5\u5f85\u5c40\uff0c\u53ea\u9700\u8981\u5728\u6750\u6599\u8f6c\u4ea4\u7ed9\u519c\u673a\u5c40\u4e4b\u524d\u62bd\u51fa\u6765\u5f97\u5230\u8ba4\u771f\u7684\u5904\u7406\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u901a\u5e38\u7684\u4e3e\u62a5\u7a0b\u5e8f\u90fd\u662f\u4fe1\u8bbf\u90e8\u95e8\u6536\u5230\u76f8\u5173\u4e3e\u62a5\u6750\u6599\u8f6c\u9001\u6216\u4ea4\u529e\u76f8\u5173\u5355\u4f4d\u5904\u7406\uff0c\u6240\u8c13\u7684\u76f8\u5173\u5355\u4f4d\u7edd\u5927\u591a\u6570\u662f\u88ab\u6295\u62c6\u4eba\u6216\u90e8\u95e8\u7684\u4e3b\u7ba1\u5355\u4f4d\uff0c\u4e3e\u62a5\u6750\u6599\u8bf4\u4e0d\u5b9a\u9694\u5929\u5c31\u843d\u5230\u88ab\u4e3e\u62a5\u4eba\u6216\u90e8\u95e8\u7684\u624b\u91cc\uff0c\u63a5\u7740\u5c31\u662f\u4e3e\u62a5\u4eba\u53d7\u5230\u6253\u51fb\u3001\u62a5\u590d\u3002</p><p>\u3000\u3000\u5728\u8fd9\u6837\u7684\u80cc\u666f\uff0c\u5f88\u5c11\u6709\u4eba\u6562\u5b9e\u540d\u4e3e\u62a5\uff0c\u533f\u540d\u4e3e\u62a5\uff0c\u591a\u534a\u4e0d\u4f1a\u88ab\u8ba4\u771f\u5904\u7406\uff0c\u8fd9\u4fbf\u662f\u4e5d\u5341\u5e74\u4ee3\u56fd\u5185\u4e00\u4e9b\u5730\u65b9\u4fe1\u8bbf\u5de5\u4f5c\u7684\u72b6\u51b5\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u624b\u629a\u7740\u989d\u5934\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u884c\uff0c\u81f3\u5c11\u88ab\u8c03\u67e5\u671f\u95f4\uff0c\u53ef\u4ee5\u5c06\u4ed6\u4eec\u4ece\u7ade\u8058\u4eba\u540d\u5355\u4e0a\u62ff\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u4fa7\u8fc7\u5934\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u519c\u673a\u5c40\u4e0d\u4f1a\u8981\u6c42\u63a8\u8fdf\u7ade\u8058\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u770b\u91d1\u53d4\u5728\u6d77\u5dde\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u4e8b\u60c5\u505a\uff0c\u4ed6\u4eb2\u81ea\u6293\u8fd9\u4ef6\u4e8b\uff0c\u519c\u673a\u5c40\u7684\u9886\u5bfc\u8981\u987e\u5fcc\u4f1a\u4e0d\u4f1a\u5f15\u706b\u70e7\u8eab\uff0c\u8c01\u4f1a\u8df3\u51fa\u6765\u8981\u6c42\u63a8\u8fdf\u7ade\u8058\uff1f\u5c31\u7b97\u519c\u673a\u5c40\u771f\u6709\u4eba\u8fd9\u6837\u8981\u6c42\uff0c\u96be\u9053\u6d77\u5dde\u63a7\u80a1\u5c31\u6ca1\u6709\u81ea\u5df1\u7684\u7acb\u573a\uff1f\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u63a8\u7740\u4e08\u592b\u8ddf\u513f\u5b50\u7684\u80a9\u8180\uff1a\u201c\u4f60\u4eec\u5750\u9910\u684c\u4e0a\u8ba8\u8bba\u4f60\u4eec\u7684\u9634\u8c0b\u8be1\u8ba1\u53bb\uff0c\u6211\u770b\u7535\u89c6\u5267\u90fd\u8ba9\u4f60\u5e72\u6270\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u8ddf\u7238\u7238\u4ece\u6c99\u53d1\u4e0a\u7ad9\u8d77\u6765\uff0c\u5750\u5230\u9910\u684c\u8fb9\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8bf4\uff1a\u201c\u8fd9\u4e5f\u53ea\u662f\u6743\u5b9c\u4e4b\u8ba1\uff0c\u5176\u4ed6\u4f01\u4e1a\u90fd\u8fd9\u4e48\u7ba1\uff0c\u975e\u4e71\u5957\u4e0d\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u56fd\u6709\u4f01\u4e1a\u4f53\u5236\u8f6c\u53d8\u76ee\u524d\u6700\u91cd\u8981\u7684\u5c31\u662f\u884c\u653f\u64a4\u7f16\uff0c\u90a3\u4e9b\u5382\u957f\u5230\u5e95\u662f\u4f01\u4e1a\u7684\u7ecf\u8425\u8005\u8fd8\u662f\u653f\u5e9c\u6d3e\u51fa\u7684\u5b98\uff1f\u8fd9\u4e9b\u5b98\u4e0d\u4e00\u4e2a\u4e2a\u575a\u51b3\u7684\u780d\u6389\uff0c\u4f53\u5236\u600e\u4e48\u8f6c\u53d8\u8fc7\u6765\uff1f\u653f\u5e9c\u8981\u5bf9\u4f01\u4e1a\u76d1\u7763\u53ef\u4ee5\uff0c\u4f46\u90fd\u8981\u96c6\u4e2d\u5230\u6d77\u5dde\u63a7\u80a1\u91cc\u9762\uff0c\u4e0d\u80fd\u4efb\u610f\u5ef6\u4f38\u5230\u4e0b\u9762\u7684\u4f01\u4e1a\u53bb\uff0c\u4e0d\u7136\u76d1\u7763\u6210\u672c\u592a\u9ad8\uff0c\u8fd8\u4f1a\u5e72\u6270\u4f01\u4e1a\u7684\u6b63\u5e38\u7ecf\u8425\uff0c\u5c31\u7b97\u5728\u6d77\u5dde\u63a7\u80a1\u91cc\u9762\uff0c\u7ecf\u8425\u6743\u4e0e\u76d1\u7763\u6743\u4e5f\u8981\u622a\u7136\u5206\u79bb\uff0c\u53ef\u80fd\u7684\u8bdd\uff0c\u6d77\u5dde\u63a7\u80a1\u9700\u8981\u8f6c\u4e3a\u80a1\u4efd\u516c\u53f8\uff0c\u674e\u660e\u5b66\u51fa\u4efb\u603b\u7ecf\u7406\uff0c\u7238\u7238\u4f60\u51fa\u4efb\u8463\u4e8b\u957f\u5c31\u53ef\u4ee5\u4e86\uff0c\u76d1\u7763\u6743\u53ea\u80fd\u9650\u5236\u5728\u8463\u4e8b\u4f1a\u4e0e\u76d1\u4e8b\u4f1a\u5185\u90e8\uff0c\u800c\u4e14\u5728\u8463\u4e8b\u4f1a\u91cc\uff0c\u653f\u5e9c\u7684\u76d1\u7763\u6743\u4e5f\u4e0d\u80fd\u4e0d\u53d7\u9650\u5236\uff0c\u6709\u4e24\u4e09\u4e2a\u8463\u4e8b\u7531\u5e02\u91cc\u76f4\u63a5\u4efb\u547d\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u8f6c\u53d8\u4f1a\u4e0d\u4f1a\u592a\u6fc0\u8fdb\uff1f\u201d\u5f20\u77e5\u884c\u62c5\u5fe7\u7684\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\u5927\u6982\u8fd8\u6709\u8fc7\u4e00\u6bb5\u65f6\u95f4\u4e2d\u592e\u624d\u4f1a\u660e\u786e\u63d0\u51fa\u5bf9\u56fd\u6709\u4f01\u4e1a\u8fdb\u884c\u201c\u7ecf\u8425\u6743\u4e0e\u6240\u6709\u6743\u5206\u79bb\u7684\u201d\u73b0\u4ee3\u4f01\u4e1a\u5236\u5ea6\u8f6c\u5316\uff0c\u4f46\u662f\u5728\u56fd\u6709\u4f01\u4e1a\u7684\u4ea7\u6743\u95ee\u9898\u8981\u62d6\u5230\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\u4e4b\u540e\u624d\u4f1a\u6253\u7834\u4f20\u7edf\u601d\u7ef4\u7684\u575a\u51b0\uff0c\u73b0\u5728\u5f88\u591a\u5730\u65b9\u53ea\u505a\u4e0d\u8bf4\uff0c\u4f46\u5927\u4e2d\u578b\u56fd\u6709\u4f01\u4e1a\u7684\u4ea7\u6743\u95ee\u9898\u7edd\u5bf9\u662f\u96f7\u533a\u3002\u6d77\u5dde\u63a7\u80a1\u867d\u7136\u6ca1\u6709\u6d89\u53ca\u6781\u5176\u654f\u611f\u7684\u4ea7\u6743\u95ee\u9898\uff0c\u4f46\u662f\u4f9d\u5f20\u606a\u7684\u601d\u8def\uff0c\u4f1a\u6781\u5927\u7684\u9650\u5236\u653f\u5e9c\u5bf9\u4f01\u4e1a\u7684\u63a7\u5236\u6743\u3002</p><p>\u3000\u3000\u865a\u7f6e\u56fd\u6709\u4f01\u4e1a\u6240\u6709\u6743\u5728\u4e5d\u4e94\u5e74\u4f9d\u7136\u662f\u76f8\u5f53\u654f\u611f\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u8bd5\u70b9\uff0c\u5c31\u8981\u8fdb\u5165\u96f7\u533a\uff0c\u89e6\u53ca\u654f\u611f\u95ee\u9898\u90a3\u4e5f\u662f\u8feb\u4e0d\u5f97\u5df2\u7684\u4e8b\u60c5\uff0c\u674e\u660e\u5b66\u5bf9\u8fd9\u4e2a\u6709\u7814\u7a76\uff0c\u4f60\u53ea\u8981\u575a\u5b9a\u4e0d\u79fb\u7684\u652f\u6301\u4ed6\uff0c\u771f\u8981\u6709\u96f7\u9706\u52a0\u8eab\uff0c\u4e5f\u8ba9\u4ed6\u5148\u5e2e\u4f60\u625b\u4e00\u4f1a\u513f\uff0c\u4f60\u8fd8\u62c5\u5fc3\u4f60\u7684\u4ed5\u9014\u56e0\u6b64\u53d7\u963b\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u624b\u63fd\u8fc7\u5f20\u606a\u7684\u8111\u888b\uff0c\u72e0\u72e0\u7684\u63c9\u4e86\u63c9\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u4e00\u5473\u7684\u52fe\u5fc3\u6597\u89d2\uff0c\u8fd8\u4e0d\u5982\u65e9\u51e0\u5e74\u9000\u4f11\uff0c\u4e5f\u6ca1\u5fc5\u8981\u50cf\u4f60\u8bf4\u7684\u90a3\u6837\uff0c\u62ff\u674e\u660e\u5b66\u5f53\u6321\u7bad\u724c\u3002\u201d</p><p>\u3000\u3000\u201c\u6307\u4e0d\u5b9a\u674e\u660e\u5b66\u81ea\u5df1\u4e50\u610f\u5462\uff0c\u201d\u5934\u76ae\u8ba9\u4ed6\u7238\u6413\u5f97\u751f\u75bc\uff0c\u5f20\u606a\u5934\u504f\u7740\u6323\u624e\u5f00\uff0c\u201c\u519c\u673a\u5382\u7684\u4e8b\u60c5\u5c31\u8fd9\u4e48\u5b9a\u4e86\uff0c\u6211\u9a6c\u4e0a\u7ed9\u4eba\u6253\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5230\u623f\u95f4\u7ed9\u90b5\u81f3\u521a\u6253\u7535\u8bdd\uff0c\u542c\u7535\u8bdd\u91cc\u6c89\u95f7\u7684\u97f3\u4e50\u58f0\uff0c\u4f30\u8ba1\u4ed6\u8fd8\u5728\u966a\u4e07\u52c7\u5728\u5916\u9762\u6d88\u8c34\uff0c\u6653\u5f97\u90b5\u81f3\u521a\u8fd9\u4eba\u5fc3\u673a\u6df1\u6c89\uff0c\u8868\u9762\u6577\u884d\u4e07\u52c7\u5012\u6709\u53ef\u80fd\uff0c\u7edd\u4e0d\u4f1a\u8f7b\u6613\u5012\u5411\u4e07\u52c7\u90a3\u8fb9\uff0c\u5728\u7535\u8bdd\u91cc\u76f4\u63a5\u63d0\u5230\u519c\u673a\u5382\u5382\u957f\u5468\u5065\u5f97\u7f6a\u81ea\u5df1\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7ed9\u8d76\u51fa\u5e02\u519c\u673a\u7cfb\u7edf\uff0c\u5f53\u7136\u5bf9\u5468\u5065\u4e00\u76f4\u6000\u6068\u5728\u5fc3\uff0c\u4ed6\u6ca1\u6709\u80fd\u5c06\u5468\u5065\u4e00\u4e3e\u6273\u5012\u7684\u6750\u6599\uff0c\u4f46\u662f\u5f20\u606a\u53ea\u9700\u8981\u7a0d\u5fae\u6709\u4e9b\u771f\u6750\u5b9e\u6599\u7684\u6750\u6599\uff0c\u5148\u5c06\u5468\u5065\u4e0e\u51e0\u4e2a\u526f\u5382\u957f\u4ece\u7ade\u8058\u8005\u540d\u5355\u91cc\u5212\u6389\uff0c\u4e4b\u540e\u518d\u6162\u6162\u8c03\u67e5\u4ed6\u4eec\u7684\u95ee\u9898\uff0c\u53cd\u6b63\u91d1\u56fd\u6d77\u5230\u6d77\u5dde\u6302\u804c\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u8ffd\u4e00\u4e24\u4ef6\u6848\u5b50\uff0c\u6b63\u597d\u5e2e\u4ed6\u6253\u53d1\u65f6\u95f4\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5fd9\u4e0d\u8fed\u7684\u7b54\u5e94\uff0c\u8fd9\u4e9b\u6750\u6599\u4ed6\u624b\u5934\u65e9\u5c31\u51c6\u5907\u597d\uff0c\u53ea\u662f\u60f3\u7740\u4e0d\u80fd\u4e00\u4e0b\u5b50\u5c06\u5468\u5065\u6273\u5012\uff0c\u5c31\u662f\u5c06\u8fd9\u4e9b\u6750\u6599\u9001\u5230\u5e02\u91cc\uff0c\u6700\u7ec8\u4e5f\u4f1a\u8f6c\u5230\u519c\u673a\u5c40\u5185\u90e8\u5904\u7406\uff0c\u8bf4\u4e0d\u5b9a\u519c\u673a\u5c40\u6700\u540e\u8fd8\u662f\u4f1a\u8ba9\u5468\u5065\u81ea\u5df1\u5904\u7f6e\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u63d2\u624b\u8fd9\u4e8b\u5c31\u4f1a\u8fe5\u7136\u4e0d\u540c\uff0c\u53ea\u8981\u4e3e\u62a5\u6750\u6599\u6700\u540e\u4e0d\u8f6c\u5230\u519c\u673a\u5c40\u5185\u90e8\u5904\u7406\u3001\u4e0d\u8ba9\u4ed6\u4eec\u5b98\u5b98\u76f8\u62a4\uff0c\u5f20\u606a\u8981\u63ea\u5468\u5065\uff0c\u5c31\u51ed\u8fd9\u4e9b\u6750\u6599\uff0c\u4e5f\u8db3\u591f\u7ed9\u5468\u5065\u4e00\u4e2a\u64a4\u804c\u5904\u5206\uff0c\u63a5\u4e0b\u6765\u8fd8\u4e0d\u5f97\u8f6e\u5230\u81ea\u5df1\u6162\u6162\u6536\u62fe\u4ed6\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u591a\u95ee\u4e86\u5f20\u606a\u4e00\u53e5\uff1a\u201c\u5468\u9ebb\u5b50\u600e\u4e48\u5f97\u7f6a\u4f60\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u63d0\u53ca\u6d77\u5dde\u63a7\u80a1\u4e0e\u519c\u673a\u5c40\u5bf9\u519c\u673a\u5382\u7684\u4eba\u4e8b\u4e4b\u4e89\uff0c\u53ea\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u4e86\u4eca\u5929\u95f9\u9152\u7684\u4e8b\u60c5\uff0c\u8ba9\u5085\u4fca\u5c06\u5468\u5065\u4e22\u758f\u6e2f\u6cb3\u91cc\u7684\u4e8b\u60c5\uff0c\u4e34\u4e86\u53c8\u8bf4\uff1a\u201c\u542c\u8bf4\u4f60\u4e5f\u7ed9\u4ed6\u6446\u4e86\u4e00\u9053\uff0c\u987a\u4fbf\u5e2e\u4f60\u5904\u7406\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u77e5\u9053\u5f20\u606a\u4e0d\u662f\u90a3\u79cd\u60f9\u662f\u751f\u975e\u3001\u775a\u7726\u5fc5\u62a5\u7684\u4eba\uff0c\u66f4\u4e0d\u77e5\u9053\u5f53\u4e2d\u8fd8\u6d89\u53ca\u5230\u5f20\u606a\u60f3\u5e2e\u8bb8\u601d\u5c0f\u8205\u65bd\u536b\u5fe0\u4e00\u628a\uff0c\u53ea\u5f53\u5f20\u606a\u771f\u662f\u8003\u8651\u4ed6\u7684\u65e7\u4ec7\uff0c\u5fc3\u91cc\u81ea\u7136\u5341\u5206\u627f\u60c5\u3002</p></article>"}}}]);
