(window.webpackJsonp=window.webpackJsonp||[]).push([[1685],{1708:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e5d\u8282 \u9b54\u9b42\u5c38",content:"<article><h2>\u7b2c\u4e5d\u8282 \u9b54\u9b42\u5c38</h2><p>\u6b63\u5982\u743c\u6069\u6240\u6599\uff0c\u4ed6\u524d\u65e5\u5728\u661f\u9668\u57ce\u91cc\u9047\u5230\u7684\u9ed1\u732b\u5e76\u4e0d\u662f\u5bfb\u5e38\u732b\u54aa\uff0c\u800c\u662f\u90a3\u4f4d\u540d\u53eb\u742a\u5a05\xb7\u706b\u5203\u7684\u732b\u5973\u3002\u5979\u968f\u7740\u5149\u5934\u7267\u5e08\u5361\u65af\u56fe\u524d\u6765\u661f\u9668\u57ce\uff0c\u539f\u672c\u6253\u7b97\u662f\u8d81\u7740\u9634\u9b42\u57ce\u548c\u5b89\u535a\u91cc\u62fc\u5f97\u4e24\u8d25\u4ff1\u4f24\u4e4b\u9645\uff0c\u5750\u6536\u6e14\u7fc1\u4e4b\u5229\uff0c\u7ed3\u679c\u5374\u88ab\u601d\u601d\u51bb\u6210\u4e86\u51b0\u96d5\u3002\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u5979\u4f1a\u88ab\u6740\u6389\uff0c\u6ca1\u60f3\u5230\u53cd\u800c\u88ab\u5965\u5609\u83b1\u65af\u653e\u4e86\u51fa\u6765\uff0c\u5e76\u4e14\u8fd8\u5f53\u4f5c\u793c\u7269\u9001\u7ed9\u4e86\u73ca\u5609\u3002</p><p>\u742a\u5a05\u662f\u732b\u5973\uff0c\u517d\u5316\u75c7\u7684\u6df1\u5ea6\u611f\u67d3\u4e0d\u4ec5\u8ba9\u5979\u7684\u5916\u8c8c\u53d8\u5f97\u90e8\u5206\u5177\u6709\u732b\u7684\u7279\u5f81\uff08\u5c16\u8033\u3001\u957f\u5c3e\u3001\u7360\u7259\uff09\uff0c\u800c\u4e14\u80fd\u591f\u81ea\u7531\u53d8\u8eab\u6210\u4e00\u53ea\u9ed1\u732b\u3002\u5965\u5609\u83b1\u65af\u5c06\u5979\u9001\u7ed9\u73ca\u5609\u6709\u4e24\u4e2a\u7528\u9014\uff1a\u5e73\u65f6\u505a\u5ba0\u7269\uff0c\u966a\u73ca\u5609\u89e3\u95f7\uff0c\u5fc5\u8981\u65f6\u505a\u4fdd\u9556\uff0c\u62a4\u536b\u73ca\u5609\u7684\u5b89\u5168\u3002</p><p>\u201c\u5f00\u2026\u2026\u5f00\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d\u5f97\u77e5\u6b64\u4e8b\uff0c\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u5dee\u70b9\u4ee5\u4e3a\u5965\u5609\u83b1\u65af\u7684\u795e\u667a\u51fa\u4e86\u95ee\u9898\uff0c\u201c\u5979\u8981\u662f\u4f24\u5bb3\u59d0\u59d0\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u5979\u4e0d\u4f1a\u3002\u201d\u5965\u5609\u83b1\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u5982\u6b64\u56de\u7b54\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4f1a\uff0c\u201d\u743c\u6069\u4e0d\u5feb\uff0c\u201c\u4e07\u4e00\u5462\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u4e07\u4e00\uff0c\u201d\u5965\u5609\u83b1\u65af\u6de1\u6de1\u8bf4\uff0c\u201c\u5974\u96b6\u4e0d\u4f1a\u53cd\u6297\u4e3b\u4eba\u3002\u201d</p><p>\u201c\u7b11\u8bdd\uff0c\u8fd9\u4e16\u754c\u4e0a\u5974\u96b6\u9020\u53cd\u7684\u4e8b\u60c5\u4e00\u5927\u5806\uff0c\u968f\u5904\u53ef\u89c1\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u8eab\u4f53\u4e0a\u7684\u5974\u96b6\u81ea\u7136\u4f1a\u9020\u53cd\uff0c\u201d\u5965\u5609\u83b1\u65af\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u5fc3\u7075\u4e0a\u7684\u5974\u96b6\u5c31\u4e0d\u4f1a\u3002\u201d</p><p>\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u8010\u745f\u8bed\u4f60\u90fd\u542c\u4e0d\u61c2\u4e86\uff1f\u5c31\u662f\u5b57\u9762\u610f\u601d\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u65e2\u7136\u4f60\u8fd9\u4e48\u80af\u5b9a\uff0c\u90a3\u6211\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\u3002\u201d</p><p>\u542c\u5965\u5609\u83b1\u65af\u7684\u53e3\u6c14\uff0c\u50cf\u662f\u7ed9\u732b\u5973\u4e0b\u4e86\u4ec0\u4e48\u9644\u9b54\u672f\uff0c\u8ba9\u5979\u5728\u7cbe\u795e\u548c\u610f\u8bc6\u4e0a\u670d\u4ece\u4e8e\u73ca\u5609\uff0c\u5949\u5176\u4e3a\u4e3b\u4eba\u3002\u7136\u800c\u743c\u6069\u65e9\u5c31\u4ed4\u7ec6\u89c2\u5bdf\u8fc7\uff0c\u742a\u5a05\u795e\u60c5\u51b0\u51b7\uff0c\u4e0d\u559c\u4e0d\u6012\uff0c\u4e0d\u8a00\u4e0d\u7b11\uff0c\u4f46\u76ee\u5149\u51dd\u805a\u4e0d\u6563\uff0c\u773c\u795e\u6e05\u6f88\u4e0d\u6d51\uff0c\u4e0d\u50cf\u662f\u88ab\u63a7\u5236\u4e86\u795e\u667a\u7684\u6837\u5b50\uff0c\u81f3\u5c11\u4ed6\u6240\u77e5\u9053\u7684\u50ac\u7720\u672f\u3001\u6697\u793a\u672f\u3001\u6307\u4f7f\u672f\u4e4b\u7c7b\u90fd\u8fbe\u4e0d\u5230\u8fd9\u79cd\u6548\u679c\uff0c\u800c\u4e14\u6548\u679c\u4e5f\u4e0d\u53ef\u80fd\u4e00\u76f4\u6301\u7eed\uff0c\u4f46\u5965\u5609\u83b1\u65af\u4e0d\u80af\u900f\u9732\u5230\u5e95\u662f\u7528\u4e86\u4ec0\u4e48\u624b\u6cd5\u3002</p><p>\u53cd\u6b63\u5979\u4e5f\u662f\u4f60\u5973\u513f\uff0c\u4f60\u603b\u4e0d\u4f1a\u6545\u610f\u5bb3\u5979\u5427\u2014\u2014\u62b1\u7740\u8fd9\u79cd\u60f3\u6cd5\uff0c\u743c\u6069\u52c9\u5f3a\u70b9\u5934\u540c\u610f\uff0c\u4e8e\u662f\u73ca\u5609\u591a\u4e86\u4e00\u53ea\u5ba0\u7269\u732b\u517c\u5973\u4fdd\u9556\u3002\u59d0\u59d0\u5bf9\u6b64\u4e8b\u5012\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u867d\u7136\u5979\u5bf9\u732b\u554a\u72d7\u554a\u4e4b\u7c7b\u7684\u5c0f\u52a8\u7269\u4e0d\u662f\u7279\u522b\u7684\u559c\u7231\uff0c\u4e0d\u81f3\u4e8e\u4e00\u770b\u5230\u5c31\u4e24\u773c\u653e\u5149\u8981\u5f80\u6000\u91cc\u62b1\uff0c\u4f46\u95f2\u7740\u6ca1\u4e8b\u9017\u5f04\u9017\u5f04\u8fd8\u662f\u633a\u6709\u8da3\u7684\uff0c\u4f55\u51b5\u742a\u5a05\u786e\u5b9e\u633a\u6f02\u4eae\uff0c\u65e0\u8bba\u662f\u4eba\u5f62\u8fd8\u662f\u732b\u5f62\u3002</p><p>\u800c\u4e14\uff0c\u5979\u786e\u5b9e\u662f\u975e\u5e38\u5f3a\u529b\u7684\u4fdd\u9556\u3002</p><p>\u968f\u7740\u73ca\u5609\u4e00\u58f0\u4ee4\u4e0b\uff0c\u732b\u5973\u8dc3\u51fa\u9a6c\u8f66\uff0c\u6062\u590d\u4eba\u5f62\uff0c\u95ea\u7535\u822c\u671d\u7740\u90a3\u56db\u4e2a\u50f5\u5c38\u51b2\u8fc7\u53bb\u3002\u50f5\u5c38\u662f\u975e\u5e38\u4f4e\u7ea7\u7684\u4ea1\u7075\uff0c\u65e2\u7b28\u62d9\u53c8\u8fdf\u7f13\uff0c\u9664\u4e86\u529b\u6c14\u5927\uff0c\u76ae\u539a\u6297\u6253\uff0c\u522b\u7684\u4e00\u65e0\u662f\u5904\u3002\u732b\u5973\u539f\u672c\u6240\u7528\u7684\u722a\u5957\u662f\u795e\u5668\uff0c\u88ab\u5965\u5609\u83b1\u65af\u6beb\u4e0d\u5ba2\u6c14\u5730\u541e\u6ca1\u4e86\uff0c\u4ece\u81ea\u5df1\u7684\u6536\u85cf\u91cc\u53e6\u627e\u4e86\u526f\u722a\u5957\u914d\u7ed9\u5979\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u539f\u7248\u7684\u5389\u5bb3\uff0c\u4e5f\u7b97\u662f\u5f88\u9ad8\u7ea7\u7684\u9b54\u6cd5\u6b66\u5668\u4e86\uff0c\u81f3\u5c11\u780d\u50f5\u5c38\u6ca1\u95ee\u9898\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u732b\u5973\u5df2\u7ecf\u987a\u5229\u6467\u6bc1\u4e86\u4e09\u5177\u50f5\u5c38\uff0c\u8db3\u5c16\u8f7b\u8f7b\u70b9\u5730\uff0c\u8eab\u4f53\u5df2\u7ecf\u4e0d\u53ef\u601d\u8bae\u5730\u6a2a\u5411\u8f6c\u6298\uff0c\u671d\u7740\u7b2c\u56db\u5177\u50f5\u5c38\u6251\u53bb\u3002</p><p>\u5530\uff01</p><p>\u4e00\u6761\u6697\u7ea2\u8272\u3001\u7ec6\u7ec6\u957f\u957f\u7684\u5e26\u72b6\u7269\u500f\u5730\u4ece\u8def\u65c1\u8349\u4e1b\u4e2d\u5c04\u51fa\uff0c\u7b14\u76f4\u5730\u4ece\u4fa7\u9762\u523a\u5411\u742a\u5a05\u3002\u8fd9\u4e00\u51fb\u5feb\u5f97\u60ca\u4eba\uff0c\u800c\u4e14\u65e0\u8bba\u89d2\u5ea6\u3001\u65f6\u673a\u90fd\u9009\u62e9\u5f97\u6070\u5230\u597d\u5904\uff0c\u663e\u7136\u662f\u84c4\u8c0b\u5df2\u4e45\u7684\u5077\u88ad\u3002\u732b\u5973\u8eab\u5728\u534a\u7a7a\uff0c\u65e0\u6cd5\u8eb2\u907f\uff0c\u5979\u731b\u7136\u7ea4\u8170\u53cd\u5f13\uff0c\u6574\u4e2a\u4eba\u50cf\u5f39\u7c27\u88ab\u65e0\u5f62\u7684\u624b\u6298\u4e86\u4e00\u6298\uff0c\u7136\u540e\u677e\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u7a7a\u6c14\u53d1\u51fa\u8f7b\u5fae\u7206\u54cd\uff0c\u751a\u81f3\u51fa\u73b0\u6b8b\u5f71\uff0c\u732b\u5973\u786c\u751f\u751f\u5730\u5728\u6beb\u65e0\u501f\u529b\u7684\u60c5\u51b5\u4e0b\uff0c\u8ba9\u8eab\u4f53\u51ed\u7a7a\u5f80\u540e\u5e73\u79fb\u4e86\u534a\u5c3a\u3002\u6697\u7ea2\u7ec6\u5e26\u8d34\u7740\u732b\u5973\u7684\u8179\u90e8\u64e6\u8fc7\uff0c\u6ca1\u6709\u51fb\u4e2d\u76ee\u6807\uff0c\u5530\u5730\u4e00\u4e0b\u53c8\u6536\u4e86\u56de\u53bb\u3002</p><p>\u55b5\u545c\uff01</p><p>\u732b\u5973\u843d\u4e0b\u5730\u6765\uff0c\u53cc\u722a\u5206\u5f00\uff0c\u6446\u51fa\u6212\u5907\u7684\u59ff\u52bf\uff0c\u671d\u7740\u8349\u4e1b\u4e2d\u6697\u7ea2\u7ec6\u5e26\u51fa\u73b0\u7684\u4f4d\u7f6e\u5389\u53eb\u4e00\u58f0\uff0c\u8fd9\u662f\u6124\u6012\u7684\u8868\u793a\u3002\u8349\u4e1b\u4e2d\u53d1\u51fa\u5495\u5495\u602a\u7b11\uff0c\u7136\u540e\u53c8\u4e00\u5177\u50f5\u5c38\u4ece\u4e2d\u722c\u8d77\uff0c\u548c\u540c\u7c7b\u4e0d\u4e00\u6837\u7684\u662f\uff0c\u5b83\u7684\u8111\u888b\u51fa\u5947\u7855\u5927\uff0c\u5149\u79c3\u79c3\u7684\uff0c\u80f8\u8179\u90e8\u4f4d\u6ca1\u6709\u76ae\u8089\uff0c\u9732\u51fa\u91cc\u9762\u767d\u68ee\u68ee\u7684\u9aa8\u67b6\uff0c\u6697\u7ea2\u8272\u7684\u5e26\u72b6\u7269\u50cf\u86c7\u4e00\u6837\u7f20\u7ed5\u5176\u4e0a\uff0c\u6b63\u662f\u521a\u624d\u88ad\u51fb\u732b\u5973\u4e4b\u7269\u3002</p><p>\u201c\u9b54\u9b42\u5c38\uff1f\u201d</p><p>\u8f66\u53a2\u91cc\uff0c\u539f\u672c\u8212\u670d\u5730\u6795\u5728\u59d0\u59d0\u5927\u817f\u4e0a\u60a0\u95f2\u89c2\u770b\u73b0\u573a\u76f4\u64ad\u7684\u743c\u6069\u5403\u4e86\u4e00\u60ca\uff0c\u4e0b\u610f\u8bc6\u5730\u5750\u8d77\u8eab\u6765\u3002\u201c\u600e\u4e48\u4e86\uff1f\u201d\u73ca\u5609\u4e0d\u89e3\u5730\u95ee\uff0c\u201c\u9b54\u9b42\u5c38\u662f\u4ec0\u4e48\uff1f\u5f88\u5389\u5bb3\u5417\uff1f\u201d</p><p>\u201c\u5389\u5bb3\u5012\u4e5f\u8c08\u4e0d\u4e0a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ef\u662f\u2026\u2026\u8fd9\u662f\u8428\u739b\u65af\u7279\u7684\u72ec\u5bb6\u53d1\u660e\u554a\u3002\u201d</p><p>\u8428\u739b\u65af\u7279\u662f\u4e2a\u5929\u624d\u7684\u4ea1\u7075\u5e08\u517c\u53d1\u660e\u5bb6\uff0c\u53d1\u660e\u4e86\u6570\u4ee5\u767e\u8ba1\u7684\u5947\u7279\u4ea1\u7075\uff0c\u9664\u4e86\u9f99\u5deb\u5996\u8fd9\u79cd\u9f0e\u9f0e\u5927\u540d\u7684\u5b58\u5728\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5927\u7968\u4e0d\u600e\u4e48\u51fa\u540d\u6216\u8005\u5b8c\u5168\u4e0d\u51fa\u540d\u7684\uff0c\u9b54\u9b42\u5c38\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5b83\u662f\u4ece\u50f5\u5c38\u53d8\u5f02\u800c\u6765\uff0c\u4f46\u6bd4\u50f5\u5c38\u5f3a\u5927\u5f97\u591a\uff0c\u5177\u5907\u4e00\u5b9a\u7684\u667a\u529b\uff0c\u8fd8\u6709\u51e0\u9879\u7279\u6b8a\u7684\u672c\u9886\uff0c\u5176\u4e2d\u6700\u4ee4\u4eba\u5934\u75bc\u7684\u4fbf\u662f\u201c\u5c38\u6bd2\u611f\u67d3\u201d\u2014\u2014\u51e1\u662f\u88ab\u9b54\u9b42\u5c38\u6740\u6b7b\u7684\u6d3b\u4eba\uff0c\u90fd\u4f1a\u88ab\u81ea\u52a8\u8f6c\u5316\u4e3a\u50f5\u5c38\uff0c\u800c\u4e14\u8fd9\u4e9b\u50f5\u5c38\u5f53\u4e2d\uff0c\u53c8\u6709\u4e00\u5b9a\u7684\u6982\u7387\u80fd\u53d8\u5f02\u6210\u9b54\u9b42\u5c38\u3002\u636e\u8bf4\u8428\u739b\u65af\u7279\u5c31\u66fe\u7ecf\u505a\u8fc7\u5b9e\u9a8c\uff0c\u628a\u4e00\u53ea\u9b54\u9b42\u5c38\u6254\u8fdb\u6751\u5e84\uff0c\u534a\u65e5\u4e4b\u5185\u5c31\u51ed\u7a7a\u5236\u9020\u51fa\u4e86\u51e0\u767e\u53f7\u50f5\u5c38\uff0c\u8fd9\u53ef\u6bd4\u81ea\u5df1\u6162\u6162\u65bd\u6cd5\u53ec\u5524\u8981\u5feb\u6377\u5341\u500d\uff0c\u7701\u529b\u767e\u500d\u3002\u56e0\u4e3a\u9b54\u9b42\u5c38\u6709\u4e00\u5b9a\u667a\u529b\u7684\u5173\u7cfb\uff0c\u8fd8\u80fd\u591f\u4f5c\u4e3a\u5934\u76ee\u6765\u6307\u6325\u50f5\u5c38\uff0c\u5b8c\u6210\u6307\u4ee4\uff0c\u65e0\u9700\u4ea1\u7075\u5e08\u4eb2\u81ea\u8d39\u5fc3\u64cd\u63a7\uff0c\u975e\u5e38\u4e4b\u597d\u7528\u3002\u540e\u6765\u8428\u739b\u65af\u7279\u88ab\u6668\u66e6\u4e4b\u795e\u51fb\u6740\uff0c\u9f99\u5deb\u6559\u968f\u4e4b\u5206\u5d29\u79bb\u6790\uff0c\u4ed6\u7684\u4ea1\u7075\u5927\u519b\u4e5f\u5728\u5404\u5927\u52bf\u529b\u7684\u8054\u624b\u7ede\u6740\u4e0b\u5316\u4f5c\u5c18\u571f\uff0c\u6b64\u540e\u5c31\u518d\u6ca1\u542c\u8bf4\u8fc7\u9b54\u9b42\u5c38\u7684\u6d88\u606f\u4e86\u3002</p><p>\u96be\u4e0d\u6210\u8428\u739b\u65af\u7279\u5728\u9644\u8fd1\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u8ba9\u743c\u6069\u6b62\u4e0d\u4f4f\u5730\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u4ed6\u548c\u8428\u739b\u65af\u7279\u53ef\u662f\u6709\u6df1\u4ec7\u5927\u6068\u7684\u3002\u5f53\u65e5\u5728\u6df1\u6e0a\u65ad\u57df\u9547\uff0c\u8428\u739b\u65af\u7279\u4e3a\u4e86\u5bf9\u4ed8\u6b23\u5e03\u548c\u8475\u9732\uff0c\u66fe\u7ecf\u62c9\u62e2\u743c\u6069\uff0c\u751a\u81f3\u8bb8\u4e0b\u91cd\u8bfa\uff0c\u7ed3\u679c\u743c\u6069\u5f53\u9762\u7b54\u5e94\uff0c\u8f6c\u8eab\u5c31\u7ffb\u6094\uff0c\u8fd8\u8bbe\u4e0b\u5708\u5957\u8ba9\u8428\u739b\u65af\u7279\u786c\u751f\u751f\u5403\u4e86\u4e00\u8bb0\u708e\u9b54\u711a\u8eab\u7206\u3002\u5982\u679c\u8fd9\u5177\u9b54\u9b42\u5c38\u5f53\u771f\u548c\u8428\u739b\u65af\u7279\u6709\u5173\uff0c\u751a\u81f3\u8001\u5deb\u5996\u5c31\u5728\u9644\u8fd1\uff0c\u90a3\u743c\u6069\u7684\u9ebb\u70e6\u53ef\u5c31\u5927\u4e86\u3002</p><p>\u4ed6\u601d\u5fd6\u4e4b\u95f4\uff0c\u5916\u9762\u732b\u5973\u5df2\u7ecf\u548c\u9b54\u9b42\u5c38\u6218\u5728\u4e00\u8d77\u3002\u4f5c\u4e3a\u50f5\u5c38\u7684\u53d8\u79cd\uff0c\u9b54\u9b42\u5c38\u7684\u884c\u52a8\u4f9d\u65e7\u7b28\u62d9\u8fdf\u7f13\uff0c\u4f46\u5b83\u8179\u8154\u91cc\u76d8\u531d\u7684\u90a3\u6761\u86c7\u72b6\u6697\u7ea2\u7ec6\u5e26\uff0c\u52a8\u8f84\u4fbf\u4ece\u53e3\u4e2d\u5410\u51fa\uff0c\u50cf\u662f\u957f\u957f\u7684\u820c\u5934\uff0c\u4f38\u7f29\u5982\u7535\uff0c\u8fc5\u6377\u5f02\u5e38\uff0c\u4e0a\u9762\u8fd8\u5305\u88f9\u7740\u7a20\u72b6\u7c98\u6db2\uff0c\u4e00\u65e6\u6cbe\u4e0a\u76ae\u80a4\uff0c\u4fbf\u4f1a\u8fc5\u901f\u6e17\u5165\u4f53\u5185\uff0c\u9ebb\u75f9\u795e\u7ecf\u3002\u732b\u5973\u4e0d\u6562\u5927\u610f\uff0c\u5148\u5e72\u6389\u6700\u540e\u4e00\u5177\u50f5\u5c38\uff0c\u7136\u540e\u5168\u529b\u4e0e\u4e4b\u5468\u65cb\uff0c\u63a5\u8fde\u56f4\u7740\u9b54\u9b42\u5c38\u7ed5\u4e86\u51e0\u4e2a\u5708\uff0c\u7ec8\u4e8e\u6293\u4f4f\u4e00\u4e2a\u7834\u7efd\uff0c\u4e00\u722a\u523a\u5165\u5b83\u7684\u80cc\u90e8\u3002</p><p>\u562d\uff01</p><p>\u732b\u5973\u624b\u4e0a\u7528\u529b\uff0c\u6b63\u5f85\u5c06\u5bf9\u624b\u7684\u810a\u67f1\u626d\u65ad\uff0c\u731b\u7136\u95f4\u9b54\u9b42\u5c38\u7684\u80f8\u8154\u5185\u4eae\u8d77\u4e00\u56e2\u7ea2\u5149\uff0c\u767d\u68ee\u68ee\u7684\u9aa8\u67b6\u4e0a\u9661\u5730\u6d6e\u73b0\u51fa\u5341\u51e0\u4e2a\u7b26\u6587\uff0c\u7ec4\u6210\u4e00\u4e2a\u5c0f\u578b\u9b54\u6cd5\u9635\uff0c\u7d27\u63a5\u7740\u6574\u4e2a\u8eab\u8eaf\u8f70\u7136\u7206\u5f00\u3002\u732b\u5973\u731d\u4e0d\u53ca\u9632\uff0c\u8ddd\u79bb\u53c8\u8fd1\uff0c\u767b\u65f6\u88ab\u9707\u98de\u4e86\u51fa\u53bb\u3002</p><p>\u201c\u54a6\uff01\u201d</p><p>\u743c\u6069\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u662f\u9605\u8bfb\u8fc7\u9b54\u9b42\u5c38\u7684\u8d44\u6599\u7684\uff0c\u4e0d\u8bb0\u5f97\u8fd9\u602a\u7269\u7684\u80fd\u529b\u91cc\u6709\u201c\u81ea\u7206\u201d\u8fd9\u4e00\u9879\u554a\u3002\u5e78\u597d\u732b\u5973\u521a\u4e00\u843d\u5730\uff0c\u968f\u5373\u8dc3\u8d77\u8eab\u6765\uff0c\u663e\u7136\u5e76\u672a\u53d7\u4ec0\u4e48\u4f24\u3002\u743c\u6069\u4e0b\u8f66\u67e5\u770b\u4ea1\u7075\u6b8b\u9ab8\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u8fd4\u56de\u8f66\u53a2\u4e2d\uff0c\u8138\u8272\u5fae\u5fae\u6709\u4e9b\u9634\u6c89\u3002</p><p>\u201c\u5f13\u8c37\u6050\u6015\u51fa\u4e8b\u4e86\u3002\u201d\u4ed6\u5bf9\u73ca\u5609\u8bf4\u3002</p><p>\u90a3\u56db\u5177\u50f5\u5c38\u867d\u7136\u5df2\u7ecf\u88ab\u732b\u5973\u94a2\u722a\u6495\u788e\uff0c\u4f46\u901a\u8fc7\u68c0\u67e5\u8863\u7269\u788e\u7247\u548c\u9970\u54c1\uff0c\u8fd8\u662f\u80fd\u591f\u5224\u65ad\u51fa\u751f\u524d\u7684\u8eab\u4efd\u662f\u5f13\u8c37\u5c45\u6c11\uff0c\u5176\u4e2d\u6709\u4e00\u540d\u662f\u6c11\u5175\uff0c\u4f3c\u4e4e\u8fd8\u662f\u4e2a\u526f\u961f\u957f\u3002\u81f3\u4e8e\u90a3\u540d\u9b54\u9b42\u5c38\u4e4b\u6240\u4ee5\u4f1a\u81ea\u7206\uff0c\u662f\u56e0\u4e3a\u5b83\u7684\u9aa8\u67b6\u4e0a\u523b\u6709\u4e00\u4e2a\u7206\u88c2\u5fbd\u8bb0\uff0c\u4e00\u65e6\u89e6\u53d1\u5c31\u4f1a\u81ea\u52a8\u7206\u70b8\uff0c\u8fd9\u5f53\u7136\u4e0d\u4f1a\u662f\u5b83\u81ea\u5df1\u5e72\u7684\uff0c\u53ea\u53ef\u80fd\u662f\u67d0\u4f4d\u4ea1\u7075\u5e08\u7684\u624b\u7b14\u3002</p><p>\u5f13\u8c37\u88ab\u4ea1\u7075\u5165\u4fb5\u4e86\uff1f</p><p>\u73b0\u6709\u7684\u7ebf\u7d22\u592a\u5c11\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u5f97\u51fa\u786e\u5b9a\u7684\u7ed3\u8bba\uff0c\u4f46\u743c\u6069\u5df2\u7ecf\u4f5c\u51fa\u4e86\u4e0d\u7965\u7684\u731c\u6d4b\u3002\u5018\u82e5\u5728\u5e73\u65f6\uff0c\u660e\u77e5\u9053\u524d\u65b9\u53ef\u80fd\u6709\u5371\u9669\uff0c\u4ed6\u80af\u5b9a\u6389\u5934\u5c31\u8d70\uff0c\u95ee\u9898\u662f\u4ed6\u60f3\u53bb\u5854\u745f\u8c37\u89c1\u6885\u83f2\u65af\u548c\u51db\uff0c\u800c\u8981\u53bb\u5854\u745f\u8c37\uff0c\u5c31\u5fc5\u987b\u7ecf\u8fc7\u5f13\u8c37\uff0c\u9664\u975e\u4ed6\u613f\u610f\u82b1\u4e00\u4e24\u4e2a\u6708\u65f6\u95f4\u7ed5\u4e2a\u8d85\u7ea7\u5927\u5f2f\u3002</p><p>\u60f3\u4e86\u4e00\u60f3\uff0c\u743c\u6069\u51b3\u5b9a\u8fd8\u662f\u7ee7\u7eed\u4e0a\u8def\uff0c\u5148\u770b\u770b\u60c5\u51b5\u518d\u8bf4\uff0c\u73ca\u5609\u81ea\u65e0\u5f02\u8bae\u3002\u9a6c\u8f66\u7ee7\u7eed\u4e0a\u8def\uff0c\u732b\u5973\u56de\u5230\u8f66\u53a2\u4e2d\uff0c\u4f46\u6ca1\u6709\u518d\u53d8\u6210\u732b\u5f62\uff0c\u800c\u662f\u5750\u5728\u89d2\u843d\u91cc\uff0c\u968f\u65f6\u51c6\u5907\u51fa\u51fb\u3002\u5979\u521a\u624d\u88ab\u7206\u88c2\u7b26\u6587\u8fd1\u8ddd\u79bb\u70b8\u4e2d\uff0c\u4f46\u8eab\u4e0a\u7684\u76ae\u7532\u5378\u6389\u4e86\u7edd\u5927\u90e8\u5206\u51b2\u51fb\u529b\uff0c\u6240\u4ee5\u53ea\u53d7\u4e86\u70b9\u8f7b\u5fae\u64e6\u4f24\uff0c\u57fa\u672c\u65e0\u788d\u3002</p><p>\u201c\u8ba9\u838e\u73de\u514b\u51fa\u6765\u5427\u3002\u201d\u73ca\u5609\u5bf9\u743c\u6069\u8bf4\u3002</p><p>\u4ed6\u4eec\u51fa\u53d1\u7684\u65f6\u5019\u662f\u4e94\u4e2a\u4eba\uff0c\u540e\u6765\u5965\u5609\u83b1\u65af\u8bf4\u8981\u953b\u70bc\u4ec0\u4e48\u5b9d\u7269\uff0c\u8fdb\u5165\u4e86\u547d\u8fd0\u957f\u591c\u4e4b\u4e2d\uff0c\u7136\u540e\u5c31\u518d\u6ca1\u51fa\u6765\uff1b\u742a\u5a05\u53d8\u6210\u732b\u8737\u7f29\u5728\u89d2\u843d\u91cc\u6253\u547c\u565c\uff0c\u838e\u73de\u514b\u89c1\u72b6\uff0c\u8bc6\u8da3\u5730\u56de\u5230\u8d1d\u88d8\u91cc\u5b9d\u77f3\u540a\u5760\u91cc\uff0c\u514d\u5f97\u7ed9\u8fd9\u5bf9\u59d0\u5f1f\u4fe9\u5f53\u7535\u706f\u6ce1\u3002\u5982\u4eca\u60c5\u5f62\u4e0d\u5bf9\u52b2\uff0c\u8fd9\u7535\u706f\u6ce1\u4e5f\u53ea\u597d\u7ee7\u7eed\u5f53\u4e86\u3002</p><p>\u5728\u63a5\u4e0b\u6765\u7684\u8def\u4e0a\uff0c\u4ed6\u4eec\u63a5\u8fde\u53c8\u906d\u9047\u4e86\u4e09\u62e8\u4ea1\u7075\uff0c\u6bcf\u6b21\u90fd\u662f\u9b54\u9b42\u5c38\u5e26\u9886\u7740\u50f5\u5c38\uff0c\u4f46\u8d8a\u5230\u540e\u9762\u6570\u91cf\u8d8a\u591a\uff0c\u743c\u6069\u5fc3\u4e2d\u4e5f\u662f\u8d8a\u6765\u8d8a\u4e0d\u5b89\u3002\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u51fb\u6740\u7b2c\u4e09\u62e8\u4ea1\u7075\u4e4b\u540e\uff0c\u9053\u8def\u53c8\u91cd\u65b0\u53d8\u5f97\u592a\u5e73\u8d77\u6765\uff0c\u4e00\u76f4\u5230\u4ed6\u4eec\u62b5\u8fbe\u5f13\u6865\u9547\uff08\u5f13\u8c37\u7684\u9996\u5e9c\uff09\u4e3a\u6b62\uff0c\u518d\u6ca1\u9047\u4e0a\u534a\u4e2a\u654c\u4eba\u3002</p><p>\u5f13\u6865\u9547\u867d\u7136\u540d\u4e3a\u9547\uff0c\u5176\u5b9e\u5df2\u7ecf\u7b97\u5f97\u4e0a\u662f\u4e00\u5ea7\u4e2d\u578b\u57ce\u5e02\uff0c\u4eba\u53e3\u903e\u4e07\uff0c\u662f\u6574\u4e2a\u8c37\u5730\u5730\u533a\u6700\u7e41\u534e\u7684\u5730\u65b9\uff0c\u800c\u4e14\u56e0\u4e3a\u9760\u8fd1\u6851\u6bd4\u4e9a\u7684\u7f18\u6545\uff0c\u5546\u4e1a\u975e\u5e38\u5174\u76db\u53d1\u8fbe\u3002\u743c\u6069\u7b49\u4eba\u8fdb\u57ce\u65f6\uff0c\u4fbf\u89c1\u57ce\u5899\u4e0a\u8d34\u4e86\u544a\u793a\uff0c\u5927\u610f\u662f\u8bf4\u8fd1\u671f\u53d1\u73b0\u6709\u4ea1\u7075\u62e6\u622a\u5546\u9053\uff0c\u529d\u8c15\u6765\u5f80\u5546\u65c5\u6682\u65f6\u7559\u5728\u5f13\u6865\u9547\u5185\uff0c\u201c\u4e09\u5251\u201d\u9886\u4e3b\u4f1a\u5373\u65e5\u6d3e\u519b\u53bb\u527f\u706d\u4ea1\u7075\uff0c\u7b49\u7b49\u3002</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u6697\u81ea\u5600\u5495\u7740\uff0c\u544a\u793a\u4e0a\u8bf4\u5f97\u5f88\u7b80\u5355\uff0c\u53e3\u6c14\u4e5f\u8f7b\u63cf\u6de1\u5199\uff0c\u4f3c\u4e4e\u5e76\u672a\u771f\u6b63\u91cd\u89c6\uff0c\u53ea\u5f53\u505a\u662f\u4e2a\u5c0f\u9ebb\u70e6\u2014\u2014\u4f46\u65e2\u7136\u9b54\u9b42\u5c38\u8fd9\u79cd\u4e1c\u897f\u90fd\u51fa\u6765\u4e86\uff0c\u9ebb\u70e6\u5c31\u80af\u5b9a\u4e0d\u4f1a\u5c0f\u3002\u4e0d\u8fc7\u8fd9\u79cd\u4e8b\u60c5\u4ed6\u5c31\u61d2\u5f97\u64cd\u5fc3\u4e86\uff0c\u5148\u53bb\u5854\u745f\u8c37\u518d\u8bf4\u3002</p><p>\u9a6c\u8f66\u4ece\u9547\u4e2d\u5fc3\u7684\u96c6\u5e02\u7a7f\u8fc7\uff0c\u743c\u6069\u968f\u610f\u4ece\u8f66\u7a97\u91cc\u5411\u5916\u770b\uff0c\u7a81\u7136\u50cf\u662f\u770b\u5230\u4e86\u67d0\u4e2a\u719f\u4eba\u7684\u8eab\u5f71\u3002\u4ed6\u6014\u4e86\u6014\uff0c\u5b9a\u775b\u518d\u770b\uff0c\u53d1\u73b0\u4e00\u4e2a\u5356\u5c0f\u9970\u54c1\u7684\u644a\u4f4d\u524d\uff0c\u6b63\u7ad9\u7740\u4e00\u4f4d\u5973\u5b50\uff0c\u56e0\u4e3a\u89d2\u5ea6\u7684\u5173\u7cfb\u770b\u4e0d\u5230\u8138\uff0c\u4f46\u90a3\u4e00\u5934\u957f\u5f97\u60ca\u4eba\uff0c\u6d41\u7011\u822c\u5782\u81ea\u811a\u8e1d\u7684\u7eda\u4e3d\u7d2b\u53d1\uff0c\u5374\u8ba9\u743c\u6069\u5239\u90a3\u95f4\u4fbf\u56de\u60f3\u8d77\u5979\u7684\u540d\u5b57\u3002</p></article>"}}}]);
