(window.webpackJsonp=window.webpackJsonp||[]).push([[1688],{1711:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u82cd\u767d\u9886\u4e3b",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u82cd\u767d\u9886\u4e3b</h2><p>\u5ced\u58c1\u4e0a\u9690\u853d\u7684\u6d1e\u7a9f\u4e2d\uff0c\u8010\u8bfa\u5168\u795e\u8d2f\u6ce8\u5730\u53cd\u590d\u5ff5\u8bf5\u7740\u5492\u6587\uff0c\u9ed1\u8272\u7684\u9b54\u529b\u4eff\u4f5b\u6ce2\u7eb9\u822c\u4ece\u4ed6\u7684\u9aa8\u6756\u4e0a\u5c42\u5c42\u6ea2\u51fa\uff0c\u6e17\u5165\u6ce5\u571f\uff0c\u5c06\u6c89\u7720\u5728\u5730\u5e95\u7684\u9aa8\u9ab8\u5524\u8d77\uff0c\u52a0\u5165\u65e0\u7a77\u65e0\u5c3d\u7684\u4ea1\u7075\u519b\u56e2\u3002\u4fef\u77b0\u7740\u81ea\u5df1\u7684\u6770\u4f5c\uff0c\u4ea1\u7075\u5e08\u5fc3\u4e2d\u5145\u6ee1\u7740\u4ece\u672a\u6709\u8fc7\u7684\u5de8\u5927\u6210\u5c31\u611f\uff0c\u4eff\u4f5b\u81ea\u5df1\u4fbf\u662f\u4f20\u8bf4\u4e2d\u7684\u4f1f\u5927\u82f1\u96c4\uff0c\u6b63\u6307\u6325\u7740\u5343\u519b\u4e07\u9a6c\u653b\u57ce\u7565\u5730\uff0c\u6240\u5411\u62ab\u9761\u3002</p><p>\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u540c\u65f6\u63a7\u5236\u6570\u5343\u540d\u4ea1\u7075\uff0c\u53e4\u5f80\u4eca\u6765\u7684\u4ea1\u7075\u5e08\u4eec\uff0c\u8fd8\u6709\u6bd4\u81ea\u5df1\u66f4\u5f3a\u7684\u5417\uff1f</p><p>\u5deb\u5e08\u5927\u591a\u90fd\u80fd\u53ec\u5524\u4ea1\u7075\uff0c\u4ea1\u7075\u5e08\u66f4\u662f\u7cbe\u7814\u6b64\u9053\uff0c\u4f46\u6bd5\u7adf\u90fd\u662f\u6709\u5176\u9650\u5ea6\u7684\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u80fd\u591f\u72ec\u529b\u53ec\u5524\u5e76\u63a7\u5236\u6570\u5341\u540d\u4e0a\u767e\u540d\u4ea1\u7075\uff0c\u4fbf\u8db3\u4ee5\u88ab\u79f0\u4e3a\u5927\u5deb\u5e08\u4e86\u3002\u7136\u800c\u201c\u5929\u8d4b\u201d\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e16\u754c\u4e0a\u4e5f\u662f\u5b58\u5728\u7684\uff0c\u8010\u8bfa\u4fbf\u5728\u201c\u53ec\u5524\u4ea1\u7075\u201d\u4e0a\u6709\u7740\u60ca\u4eba\u7684\u624d\u534e\u3002\u5c24\u5176\u662f\u5f53\u5e74\u5076\u9047\u8428\u739b\u65af\u7279\uff0c\u52a0\u5165\u9f99\u5deb\u6559\uff0c\u6210\u4e3a\u201c\u82cd\u767d\u9886\u4e3b\u201d\u4e4b\u540e\uff0c\u518d\u8f85\u4ee5\u5404\u79cd\u9b54\u6cd5\u88c5\u5907\u7684\u5f3a\u5316\uff0c\u5c24\u5176\u662f\u624b\u4e0a\u8fd9\u67c4\u6b21\u7b49\u795e\u5668\u6cd5\u6756\u201c\u62db\u9b42\u4e4b\u722a\u201d\uff0c\u4ed6\u5728\u8fd9\u65b9\u9762\u7684\u80fd\u529b\u5f97\u5230\u4e86\u6210\u500d\u589e\u957f\uff0c\u5230\u8fbe\u4e86\u532a\u5937\u6240\u601d\u7684\u5730\u6b65\u3002\u5c31\u7b97\u662f\u8428\u739b\u65af\u7279\u4eb2\u81f3\uff0c\u53ea\u6015\u4e5f\u4e0d\u80fd\u6bd4\u4ed6\u505a\u5f97\u66f4\u597d\u4e86\u3002</p><p>\u56e0\u4e3a\u89d2\u5ea6\u7684\u5173\u7cfb\uff0c\u88ab\u56f4\u653b\u8005\u65e0\u6cd5\u770b\u5230\u4ed6\u8fd9\u4e2a\u4ea1\u7075\u5e08\uff0c\u4f46\u53cd\u8fc7\u6765\uff0c\u8010\u8bfa\u5374\u80fd\u51ed\u501f\u9b54\u6cd5\u6e05\u695a\u5730\u770b\u5230\u4ed6\u4eec\u3002\u5706\u9635\u770b\u4e0a\u53bb\u4f9d\u65e7\u8fd8\u662f\u5f88\u575a\u56fa\uff0c\u4f46\u5728\u4ea1\u7075\u4eec\u65e0\u4f11\u65e0\u6b62\u7684\u653b\u51fb\u4e0b\u5df2\u7ecf\u88ab\u538b\u7f29\uff0c\u6b66\u58eb\u4eec\u660e\u663e\u5f00\u59cb\u75b2\u5026\uff0c\u800c\u7267\u5e08\u7684\u795e\u672f\u5e94\u8be5\u4e5f\u6240\u5269\u4e0d\u591a\u4e86\uff0c\u6700\u68d8\u624b\u7684\u5fa1\u5929\u4f7f\u4e5f\u88ab\u51fb\u706d\uff0c\u4e00\u5207\u90fd\u5728\u638c\u63e1\u4e4b\u4e2d\uff0c\u80dc\u5229\u5373\u5c06\u5230\u6765\u3002</p><p>\u201c\u770b\u6765\u7528\u4e0d\u7740\u51fa\u52a8\u90a3\u4e24\u4e2a\u5927\u5bb6\u4f19\u4e86\uff0c\u201d\u4ea1\u7075\u5e08\u60f3\uff0c\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d</p><p>\u6b63\u5fd7\u5f97\u610f\u6ee1\u95f4\uff0c\u9661\u7136\u5fc3\u5934\u95ea\u8fc7\u8b66\u5146\uff0c\u4ed6\u611f\u89c9\u5230\u4e00\u80a1\u5f3a\u70c8\u7684\u9b54\u6cd5\u6ce2\u52a8\u5728\u8eab\u540e\u53d1\u751f\uff0c\u94f6\u8272\u4f20\u9001\u95e8\u6b63\u5728\u5feb\u901f\u6210\u5f62\uff0c\u663e\u7136\u6709\u4eba\u8981\u4ece\u522b\u5904\u4f20\u9001\u800c\u6765\u3002\u4ea1\u7075\u5e08\u201c\u5484\u201d\u5730\u4f4e\u559d\u4e00\u58f0\uff0c\u6682\u65f6\u4e2d\u6b62\u4e86\u53ec\u5524\u672f\uff0c\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u7578\u5f62\u53f3\u81c2\u62ac\u8d77\uff0c\u7167\u7740\u4f20\u9001\u95e8\u7684\u51fa\u53e3\u6a2a\u626b\u8fc7\u53bb\u3002</p><p>\u201c\u7830\uff01\u201d</p><p>\u81ea\u4ece\u53d1\u73b0\u82cd\u767d\u9886\u4e3b\u85cf\u8eab\u4e4b\u5904\u7684\u90a3\u4e00\u523b\u8d77\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u5728\u5fc3\u4e2d\u63a8\u6f14\u8981\u5982\u4f55\u53d1\u52a8\u88ad\u51fb\uff0c\u53ef\u8c13\u662f\u80f8\u6709\u6210\u7af9\u3002\u8fd8\u6ca1\u8e0f\u51fa\u4f20\u9001\u95e8\uff0c\u4ed6\u4fbf\u5df2\u7ecf\u53d6\u51fa\u4e86\u8679\u5f69\u9f99\u9cde\u76fe\u6321\u5728\u8eab\u4fa7\u3002\u8010\u8bfa\u7684\u53f3\u81c2\u626b\u6765\uff0c\u88ab\u76fe\u724c\u6321\u4f4f\uff0c\u5de8\u5927\u7684\u51b2\u51fb\u529b\u8ba9\u743c\u6069\u7acb\u8db3\u4e0d\u7a33\uff0c\u4ed6\u501f\u52bf\u659c\u8de8\u4e24\u6b65\uff0c\u810a\u80cc\u8d34\u4e0a\u575a\u786c\u7684\u77f3\u58c1\uff0c\u6325\u624b\u6254\u51fa\u4e09\u679a\u68cb\u5b50\u3002</p><p>\u4e09\u56e2\u70df\u96fe\u817e\u8d77\uff0c\u4e24\u53ea\u901a\u4f53\u9c9c\u7ea2\u7684\u98df\u4eba\u9b54\u5f53\u5148\u4ece\u4e2d\u51b2\u51fa\uff0c\u4ea1\u7075\u5e08\u731d\u4e0d\u53ca\u9632\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u4f5c\u51fa\u95ea\u907f\uff0c\u4e24\u6839\u7c97\u5927\u7684\u72fc\u7259\u68d2\u5c31\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u51e0\u4e4e\u540c\u65f6\u7838\u4e2d\u4e86\u4ed6\u7684\u80f8\u53e3\u3002\u6b63\u5e38\u4eba\u82e5\u662f\u6328\u4e86\u8fd9\u4e00\u51fb\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u9aa8\u65ad\u7b4b\u6298\u5410\u8840\u8eab\u4ea1\u7684\u4e0b\u573a\u2014\u2014\u7136\u800c\u82cd\u767d\u9886\u4e3b\u5e76\u4e0d\u662f\u6b63\u5e38\u4eba\uff0c\u7ecf\u8fc7\u9b54\u6cd5\u5f3a\u5316\u7684\u4ea1\u7075\u5668\u5b98\u5927\u91cf\u79fb\u690d\uff0c\u8ba9\u4ed6\u62e5\u6709\u8fdc\u8fdc\u8d85\u51fa\u4eba\u7c7b\u6240\u80fd\u60f3\u8c61\u7684\u6297\u6253\u51fb\u80fd\u529b\u3002</p><p>\u4ea1\u7075\u5e08\u53ea\u662f\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u968f\u5373\u4fbf\u7ad9\u7a33\u811a\u8ddf\uff0c\u624b\u4e2d\u9aa8\u6756\u5f80\u5730\u4e0a\u4e00\u6572\uff0c\u6570\u5341\u53ea\u767d\u68ee\u68ee\u7684\u9aa8\u523a\u4ece\u5730\u9762\u5494\u5494\u5f39\u51fa\uff0c\u4ea4\u9519\u5408\u62e2\uff0c\u6784\u6210\u4e24\u5ea7\u767d\u9aa8\u7262\u7b3c\uff0c\u5c06\u98df\u4eba\u9b54\u9b54\u50cf\u56f0\u5728\u5176\u4e2d\u3002\u4ed6\u6b63\u51c6\u5907\u987a\u52bf\u53cd\u51fb\uff0c\u7a81\u7136\u95f4\u4e00\u9053\u707f\u70c2\u70c8\u7130\u76f4\u76f4\u55b7\u6765\uff0c\u4ea1\u7075\u5e08\u4e0b\u610f\u8bc6\u5730\u4e3e\u81c2\u4e00\u6321\uff0c\u4ed6\u7684\u53f3\u81c2\u79fb\u690d\u81ea\u5de8\u9f99\uff0c\u7ecf\u8fc7\u591a\u79cd\u9b54\u6cd5\u53cd\u590d\u5f3a\u5316\uff0c\u5bf9\u6240\u6709\u7684\u70c8\u7130\u3001\u5bd2\u51b0\u3001\u95ea\u7535\u7b49\u5143\u7d20\u653b\u51fb\u90fd\u80fd\u5b8c\u5168\u514d\u75ab\uff0c\u672c\u8be5\u80fd\u987a\u5229\u6321\u4e0b\u653b\u51fb\u3002\u7136\u800c\u90a3\u9053\u70c8\u7130\u975e\u6bd4\u5bfb\u5e38\uff0c\u5176\u4e2d\u8574\u542b\u7740\u5f3a\u70c8\u7684\u795e\u5723\u6c14\u606f\uff0c\u5c45\u7136\u786c\u751f\u751f\u5728\u9aa8\u81c2\u4e0a\u70e7\u7a7f\u4e86\u4e00\u4e2a\u62f3\u5934\u5927\u5c0f\u7684\u6d1e\u3002\u4ea1\u7075\u5e08\u62ac\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u6210\u719f\u7f8e\u8273\u7684\u5353\u5c14\u5973\u796d\u53f8\u6b63\u8eb2\u5728\u4e24\u5c0a\u98df\u4eba\u9b54\u9b54\u50cf\u7684\u80cc\u540e\uff0c\u8718\u86db\u5723\u5fbd\u4e0a\u767d\u5149\u95ea\u70c1\uff0c\u65b0\u7684\u795e\u672f\u6b63\u84c4\u52bf\u5f85\u53d1\u3002</p><p>\u201c\u683c\uff01\u201d</p><p>\u6e05\u8106\u7684\u9aa8\u9abc\u6469\u64e6\u649e\u51fb\u58f0\u81ea\u82cd\u767d\u9886\u4e3b\u7684\u5589\u5934\u53d1\u51fa\uff0c\u4ed6\u77ac\u95f4\u6fc0\u53d1\u4e86\u4e00\u4e2a\u5f3a\u529b\u6cd5\u672f\u3002\u5353\u5c14\u5973\u796d\u53f8\u8eab\u524d\u7684\u7a7a\u6c14\u88ab\u6025\u5267\u538b\u7f29\uff0c\u7136\u540e\u77ac\u95f4\u7206\u5f00\uff0c\u5c06\u5979\u70b8\u5f97\u5f80\u540e\u6454\u98de\u51fa\u53bb\u3002\u4e58\u6b64\u673a\u4f1a\uff0c\u743c\u6069\u8bd5\u63a2\u6027\u5730\u63b7\u51fa\u4e00\u679a\u77f3\u5f39\uff0c\u51c6\u786e\u547d\u4e2d\u76ee\u6807\uff0c\u4f46\u6ca1\u6709\u9020\u6210\u4f24\u5bb3\uff1b\u4ed6\u63a5\u7740\u4f7f\u7528\u8fdf\u7f13\u672f\uff0c\u8bd5\u56fe\u524a\u5f31\u5bf9\u624b\uff0c\u7136\u540e\u518d\u6b21\u5931\u8d25\u4e86\u3002</p><p>\u201c\u89e3\u79bb\uff01\u201d</p><p>\u4e24\u6b21\u8bd5\u63a2\u65e0\u529f\u800c\u8fd4\uff0c\u743c\u6069\u518d\u4e0d\u72b9\u8c6b\uff0c\u5f84\u76f4\u4f7f\u7528\u4e86\u6700\u5177\u6740\u4f24\u529b\u7684\u6cd5\u672f\u3002\u4ea1\u7075\u5e08\u5306\u5fd9\u8eb2\u907f\uff0c\u78a7\u7eff\u5c04\u7ebf\u64e6\u7740\u4ed6\u7684\u8170\u63a0\u8fc7\uff0c\u5c06\u5927\u534a\u4e2a\u8179\u8154\u90fd\u5316\u4f5c\u7c89\u672b\u3002\u82e5\u662f\u6d3b\u4eba\uff0c\u8fd9\u65f6\u5019\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6700\u591a\u5269\u4e0b\u534a\u53e3\u6c14\uff0c\u4f46\u5bf9\u5927\u534a\u4e2a\u8eab\u8eaf\u5305\u62ec\u4e94\u810f\u516d\u8151\u90fd\u4ea1\u7075\u5316\u7684\u82cd\u767d\u9886\u4e3b\u6765\u8bf4\uff0c\u5374\u5b8c\u5168\u53ef\u4ee5\u4e0d\u5f53\u4e00\u56de\u4e8b\u3002</p><p>\u4ea1\u7075\u5e08\u968f\u5373\u53d1\u52a8\u53cd\u51fb\uff0c\u4ed6\u7684\u9aa8\u722a\u62ac\u8d77\uff0c\u9065\u9065\u6307\u5411\u743c\u6069\uff0c\u53d1\u52a8\u4e86\u90a3\u9053\u8457\u540d\u7684\u4ea1\u7075\u672f\u201c\u6b7b\u4ea1\u4e00\u6307\u201d\u3002\u743c\u6069\u6ca1\u6709\u8eb2\u95ea\uff0c\u800c\u662f\u76f4\u63a5\u542f\u52a8\u5f71\u706b\uff0c\u6cf0\u7136\u81ea\u82e5\u5730\u627f\u53d7\u4e86\u8fd9\u4e00\u51fb\u3002\u4ea1\u7075\u5e08\u7684\u773c\u7736\u4e2d\u9b3c\u706b\u95ea\u70c1\uff0c\u4f3c\u4e4e\u4e0d\u6562\u7f6e\u4fe1\uff0c\u4f46\u52a8\u4f5c\u6beb\u4e0d\u8fdf\u7591\uff0c\u4e00\u9053\u8000\u773c\u7684\u95ea\u7535\u4ece\u6307\u5c16\u8ff8\u51fa\uff0c\u7d27\u63a5\u7740\u662f\u9aa8\u77db\u548c\u8870\u5f31\u5c04\u7ebf\u3002\u743c\u6069\u907f\u8fc7\u95ea\u7535\uff0c\u7528\u9f99\u9cde\u76fe\u6321\u4f4f\u9aa8\u77db\uff0c\u8870\u5f31\u5c04\u7ebf\u547d\u4e2d\u4e86\u4ed6\uff0c\u4f46\u88ab\u65e9\u5df2\u542f\u52a8\u7684\u6cd5\u672f\u9006\u8f6c\u6212\u53cd\u5f39\u56de\u53bb\u3002</p><p>\u53cc\u65b9\u4f60\u6765\u6211\u5f80\u4e86\u516d\u4e03\u4e2a\u56de\u5408\uff0c\u8c01\u4e5f\u5948\u4f55\u4e0d\u5f97\u8c01\uff0c\u5c40\u9762\u6682\u65f6\u50f5\u6301\u4f4f\u4e86\uff0c\u4f46\u743c\u6069\u77e5\u9053\u81ea\u5df1\u5176\u5b9e\u5df2\u7ecf\u5904\u4e8e\u4e0b\u98ce\u3002\u8bba\u9b54\u6cd5\u9020\u8be3\uff0c\u8bba\u5bf9\u9635\u7ecf\u9a8c\uff0c\u743c\u6069\u90fd\u8fdc\u8fdc\u4e0d\u5982\u5bf9\u624b\uff0c\u4ed6\u6240\u501a\u4ed7\u7684\u4e3b\u8981\u662f\u5f71\u706b\u548c\u6cd5\u672f\u9006\u8f6c\u6212\uff0c\u524d\u8005\u80fd\u591f\u8ba9\u4ed6\u514d\u75ab\u4e00\u5207\u5373\u6b7b\u6cd5\u672f\uff08\u4f8b\u5982\u6b7b\u4ea1\u4e00\u6307\u3001\u5973\u5996\u4e4b\u568e\u4e4b\u7c7b\uff09\uff0c\u540e\u8005\u5219\u80fd\u53cd\u5f39\u653b\u51fb\u6cd5\u672f\uff0c\u8fd9\u4e24\u8005\u7ed3\u5408\uff0c\u4ee4\u4ea1\u7075\u5e08\u7684\u6570\u6b21\u8fdb\u653b\u90fd\u65e0\u529f\u800c\u8fd4\u3002\u4f46\u743c\u6069\u5bf9\u5f71\u706b\u7684\u638c\u63e1\u5c1a\u4e0d\u7eaf\u719f\uff0c\u7528\u4ee5\u9632\u5fa1\u53ef\u4ee5\uff0c\u653b\u51fb\u5c31\u6709\u4e9b\u52c9\u5f3a\uff0c\u800c\u4e14\u5f71\u706b\u662f\u51b0\u51b7\u5e7d\u5bd2\u7684\u529b\u91cf\uff0c\u9634\u90aa\u3001\u6b7b\u706d\uff0c\u7528\u6765\u5bf9\u4ed8\u6d3b\u4eba\u975e\u5e38\u4f18\u79c0\uff0c\u4f46\u5bf9\u4ed8\u4ea1\u7075\u5c31\u672a\u514d\u6548\u679c\u8981\u6253\u6298\u6263\uff1b\u81f3\u4e8e\u6cd5\u672f\u9006\u8f6c\u6212\u867d\u7136\u5f3a\u52b2\uff0c\u5374\u7ec8\u7a76\u662f\u6709\u53cd\u5f39\u9650\u5ea6\u7684\u3002</p><p>\u9632\u5fa1\u53ea\u662f\u6682\u65f6\u7a33\u56fa\uff0c\u8fdb\u653b\u4e5f\u540c\u6837\u4e0d\u5360\u4f18\u52bf\u3002\u56e0\u4e3a\u6781\u5ea6\u4ea1\u7075\u5316\uff0c\u82cd\u767d\u9886\u4e3b\u7684\u8eaf\u4f53\u4e0d\u4f46\u575a\u903e\u91d1\u94c1\uff0c\u800c\u4e14\u80fd\u591f\u8f7b\u677e\u62b5\u6321\u591a\u79cd\u653b\u51fb\u6cd5\u672f\uff0c\u751a\u81f3\u514d\u75ab\u77f3\u5316\uff0c\u9644\u9b54\u672f\u5bf9\u4ed6\u4e5f\u57fa\u672c\u65e0\u6548\u3002\u800c\u4e14\u4ed6\u7684\u9b54\u6cd5\u9020\u8be3\u660e\u663e\u9ad8\u4e8e\u743c\u6069\uff0c\u6709\u51e0\u4e2a\u6cd5\u672f\u751a\u81f3\u76f4\u63a5\u5c31\u53cd\u5236\u6389\u4e86\u3002\u81f3\u4e8e\u8428\u74e6\u68cb\u9b54\u50cf\uff0c\u4e24\u5c0a\u98df\u4eba\u9b54\u88ab\u767d\u9aa8\u7262\u7b3c\u6b7b\u6b7b\u56f0\u4f4f\uff0c\u65e0\u6cd5\u6323\u8131\uff0c\u800c\u5353\u5c14\u5973\u796d\u53f8\u63a5\u8fde\u51e0\u6b21\u60f3\u518d\u7528\u5723\u7130\u672f\u653b\u51fb\uff0c\u90fd\u88ab\u4ea1\u7075\u5e08\u76f4\u63a5\u9707\u98de\u51fa\u53bb\uff0c\u91cd\u590d\u51e0\u6b21\u4e4b\u540e\uff0c\u9b54\u50cf\u4f3c\u4e4e\u53d7\u521b\u592a\u91cd\uff0c\u8db4\u5728\u5730\u4e0a\u4e0d\u8d77\u6765\u4e86\u3002</p><p>\u201c\u5567\uff0c\u771f\u96be\u5bf9\u4ed8\u554a\u3002\u201d</p><p>\u743c\u6069\u5600\u5495\u7740\uff0c\u4ed6\u539f\u672c\u8fd8\u60f3\u72ec\u81ea\u5e72\u6389\u82cd\u767d\u9886\u4e3b\uff0c\u4e5f\u5728\u6885\u83f2\u65af\u9762\u524d\u70ab\u8000\u70ab\u8000\uff0c\u5982\u4eca\u770b\u6765\u96be\u5ea6\u592a\u9ad8\u4e86\uff0c\u8fd8\u662f\u4e0d\u8981\u5f04\u5de7\u6210\u62d9\u7684\u597d\u3002\u4ed6\u542f\u52a8\u4e86\u5b9d\u77f3\u8df3\u8dc3\u6212\u6307\u4e0a\u7684\u4f20\u8baf\u672f\uff0c\u5374\u5e76\u672a\u8bf4\u8bdd\uff0c\u53ea\u662f\u7528\u6307\u5c16\u5feb\u901f\u6572\u51fb\u4e86\u4e09\u4e0b\uff0c\u8fd9\u662f\u4ed6\u548c\u6885\u83f2\u65af\u7ea6\u5b9a\u7684\u6697\u53f7\uff0c\u610f\u601d\u662f\uff1a\u6211\u9047\u5230\u654c\u4eba\uff0c\u6709\u9ebb\u70e6\uff0c\u8d76\u5feb\u7a7f\u7532\u62ff\u5251\u8fc7\u6765\u5e2e\u5fd9\u5427\u3002</p><p>\u4ea1\u7075\u5e08\u4e0d\u77e5\u9053\u743c\u6069\u5728\u505a\u4ec0\u4e48\uff0c\u8d81\u7740\u8fd9\u4e2a\u7a7a\u9699\uff0c\u4ed6\u637b\u52a8\u624b\u6307\uff0c\u9ed8\u8bf5\u5492\u6587\uff0c\u56db\u56e2\u9eef\u6de1\u9ed1\u5f71\u4ece\u5730\u9762\u722c\u8d77\uff0c\u626d\u66f2\u7740\uff0c\u51dd\u805a\u6210\u6d88\u7626\u7684\u4eba\u5f62\uff0c\u5f20\u7259\u821e\u722a\u5730\u671d\u743c\u6069\u6251\u6765\u3002\u8fd9\u662f\u9eef\u5f71\uff0c\u8fd1\u4f3c\u4e8e\u5e7d\u7075\uff0c\u4f46\u6ca1\u6709\u795e\u667a\uff0c\u56e0\u6b64\u4e5f\u66f4\u52a0\u51f6\u623e\uff0c\u4e43\u662f\u5deb\u5e08\u901a\u8fc7\u90aa\u6076\u7684\u4eea\u5f0f\u6740\u4eba\uff0c\u7136\u540e\u6444\u53d6\u727a\u7272\u8005\u7684\u7075\u9b42\uff0c\u4ee5\u4ea1\u7075\u672f\u70bc\u5236\u800c\u6210\uff0c\u4f5c\u4e3a\u81ea\u5df1\u7684\u8d34\u8eab\u62a4\u536b\u4f7f\u7528\u3002\u743c\u6069\u5728\u8eab\u524d\u5f20\u5f00\u4e00\u9762\u6b63\u80fd\u91cf\u62a4\u76fe\uff0c\u6682\u65f6\u6321\u4f4f\u591c\u5f71\uff0c\u4e89\u53d6\u65f6\u95f4\u3002</p><p>\u51e0\u79d2\u79cd\u540e\uff0c\u7a7a\u6c14\u8f7b\u5fae\u6ce2\u6298\uff0c\u91d1\u53d1\u94f6\u7532\u7684\u5c11\u5973\u51fa\u73b0\u5728\u8eab\u65c1\u3002\u51db\u6ca1\u6709\u968f\u5979\u4e00\u8d77\u6765\uff0c\u8fd9\u8ba9\u743c\u6069\u6709\u70b9\u5947\u602a\uff0c\u4f46\u4e5f\u65e0\u6687\u591a\u95ee\uff0c\u5927\u6982\u662f\u6070\u597d\u4e0d\u5728\u6885\u83f2\u65af\u8eab\u8fb9\u5427\uff0c\u65e0\u6240\u8c13\u4e86\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u743c\u6069\u3002\u201d</p><p>\u7b80\u5355\u7684\u95ee\u5019\uff0c\u8ba9\u4e24\u4eba\u4e4b\u95f4\u56e0\u4e3a\u5206\u522b\u591a\u65e5\u800c\u4ea7\u751f\u7684\u6de1\u6de1\u9694\u819c\u77ac\u95f4\u6d88\u878d\uff0c\u90a3\u79cd\u66fe\u7ecf\u65e0\u6570\u6b21\u5e76\u80a9\u5bf9\u654c\u800c\u5f62\u6210\u7684\u719f\u6089\u611f\uff0c\u50cf\u6e29\u6696\u7684\u6cc9\u6c34\u822c\u6d41\u8fc7\u5fc3\u5934\u3002\u6885\u83f2\u65af\u77a5\u4e86\u4e00\u773c\u654c\u4eba\uff0c\u201c\u82cd\u767d\u9886\u4e3b\uff1f\u201d\u5979\u7565\u7565\u6709\u4e9b\u8bb6\u5f02\uff0c\u4f46\u624b\u4e2d\u7684\u94f6\u5251\u5374\u6beb\u4e0d\u8fdf\u7591\u5730\u9ad8\u9ad8\u4e3e\u8d77\u3002</p><p>\u201c\u7834\u90aa\uff01\u201d</p><p>\u70ab\u76ee\u767d\u5149\u4ece\u6885\u83f2\u65af\u7684\u5251\u4e0a\u7efd\u653e\u51fa\u6765\uff0c\u4eff\u4f5b\u6b63\u5348\u7684\u592a\u9633\uff0c\u539f\u672c\u9634\u6697\u7684\u6d1e\u7a74\u88ab\u7167\u8000\u5f97\u4eae\u5802\u5802\u4e00\u7247\u3002\u88ab\u8fd9\u795e\u5723\u7684\u5149\u8292\u7167\u5c04\uff0c\u56db\u53ea\u591c\u5f71\u7684\u9eef\u6de1\u8eab\u8eaf\u50cf\u96ea\u4e00\u822c\u878d\u89e3\uff0c\u8f6c\u773c\u95f4\u4fbf\u5f7b\u5e95\u6d88\u706d\u3002\u5c11\u5973\u6e05\u559d\u4e00\u58f0\uff0c\u6325\u5251\u4e0b\u5288\uff0c\u767d\u5149\u4ece\u5251\u5203\u4e2d\u8ff8\u53d1\uff0c\u5982\u6c79\u6d8c\u6f6e\u6c34\u822c\u51b2\u5411\u4ea1\u7075\u5e08\uff0c\u77ac\u95f4\u5c06\u4ed6\u4e11\u964b\u7684\u8eab\u8eaf\u541e\u566c\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002\u51e0\u79d2\u79cd\u540e\uff0c\u767d\u5149\u6563\u53bb\uff0c\u4ea1\u7075\u5e08\u5df2\u7ecf\u4e0d\u89c1\u8e2a\u5f71\uff0c\u8fde\u5e26\u56f0\u4f4f\u98df\u4eba\u9b54\u9b54\u50cf\u7684\u4e24\u5ea7\u9aa8\u7262\u90fd\u88ab\u5f7b\u5e95\u6467\u6bc1\u3002</p><p>\u201c\u771f\u5389\u5bb3\uff0c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u5938\u5956\u8bf4\uff0c\u6536\u56de\u4e09\u679a\u68cb\u5b50\uff0c\u201c\u4e0d\u8fc7\u4f60\u4e5f\u522b\u4e00\u4e0a\u6765\u5c31\u653e\u5927\u62db\u554a\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u534a\u5929\u90fd\u6ca1\u641e\u5b9a\uff0c\u4f60\u4e00\u51fa\u624b\u5c31\u89e3\u51b3\u4e86\uff0c\u8fd9\u6837\u5c82\u4e0d\u662f\u663e\u5f97\u6211\u5f88\u5dee\u52b2\uff0c\u201d\u743c\u6069\u534a\u771f\u534a\u5047\u5730\u8bf4\uff0c\u201c\u806a\u660e\u7684\u5973\u4eba\uff0c\u5e94\u8be5\u61c2\u5f97\u7ed9\u81ea\u5df1\u7537\u4eba\u7559\u70b9\u9762\u5b50\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u6211\u5e94\u8be5\u88c5\u6a21\u4f5c\u6837\u5148\u548c\u4ed6\u6253\u4e0a\u5341\u5206\u949f\uff0c\u7136\u540e\u518d\u8270\u96be\u65e0\u6bd4\u5730\u628a\u4ed6\u5e72\u6389\uff1f\u201d</p><p>\u201c\u8bf4\u5f97\u597d\uff0c\u4e0b\u6b21\u5c31\u8fd9\u4e48\u505a\u5427\u2014\u2014\u6216\u8005\u628a\u6700\u540e\u4e00\u51fb\u7559\u7ed9\u6211\uff0c\u90a3\u5c31\u66f4\u5b8c\u7f8e\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u9661\u5730\u8138\u8272\u4e00\u53d8\u3002\u743c\u6069\u6b63\u81ea\u5947\u602a\uff0c\u4fbf\u89c9\u811a\u4e0b\u5730\u52a8\u5c71\u6447\uff0c\u731b\u70c8\u7684\u9707\u8361\u8ba9\u4ed6\u7acb\u8db3\u4e0d\u7a33\uff0c\u51e0\u4e4e\u6454\u5012\u3002\u575a\u786c\u7684\u5ca9\u58c1\u4e0a\u51fa\u73b0\u6ce2\u6298\u88c2\u7f1d\uff0c\u6025\u901f\u8513\u5ef6\uff0c\u773c\u770b\u6574\u4e2a\u5c71\u6d1e\u90fd\u8981\u584c\u9677\u3002\u94ff\u5730\u4e00\u58f0\uff0c\u6885\u83f2\u65af\u6325\u5251\u4ece\u4ed6\u5934\u4e0a\u626b\u8fc7\uff0c\u7838\u5f00\u4e00\u5757\u843d\u4e0b\u7684\u77f3\u5934\u3002</p><p>\u201c\u4f60\u5148\u51fa\u53bb\uff01\u201d\u5979\u671d\u743c\u6069\u558a\u3002</p><p>\u743c\u6069\u8dc3\u51fa\u5c71\u6d1e\uff0c\u542f\u52a8\u4e86\u98de\u884c\u672f\uff0c\u6885\u83f2\u65af\u4e5f\u968f\u5373\u8ddf\u4e86\u51fa\u6765\uff0c\u70b9\u70b9\u94f6\u5149\u81ea\u5979\u6307\u5c16\u6d41\u6cfb\uff0c\u53d8\u6210\u4e00\u53ea\u94f6\u8272\u72ec\u89d2\u517d\uff0c\u8f7d\u7740\u5979\u98de\u4e0a\u534a\u7a7a\u3002\u4e24\u4eba\u4e92\u76f8\u770b\u770b\uff0c\u5fc3\u4e2d\u6697\u81ea\u60ca\u8bb6\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c31\u89c1\u539f\u672c\u50b2\u7136\u77d7\u7acb\u7684\u65ad\u5d16\u4eff\u4f5b\u788e\u7802\u822c\u6eda\u6eda\u76f4\u4e0b\uff0c\u70df\u5c18\u5f25\u6f2b\u4e2d\u9732\u51fa\u4e24\u5177\u5e9e\u5927\u65e0\u6bd4\u7684\u9f99\u5f62\u9aa8\u67b6\uff0c\u5b83\u4eec\u7f13\u7f13\u62ac\u8d77\u5934\uff0c\u7a7a\u6d1e\u7684\u773c\u7736\u4e2d\u7ea2\u5149\u70c1\u70c1\uff0c\u4e00\u5de6\u4e00\u53f3\u5730\u77aa\u7740\u743c\u6069\u548c\u6885\u83f2\u65af\u3002</p><p>\u201c\u9aa8\u2026\u2026\u9aa8\u9f99\uff01\u201d</p></article>"}}}]);
