(window.webpackJsonp=window.webpackJsonp||[]).push([[169],{192:function(p){p.exports={chapterTitle:null,title:"\u7b2c169\u7ae0 \u82b7\u5f64\u5f00\u53e3\u8bf4\u8bdd",content:"<article><h2>\u7b2c169\u7ae0 \u82b7\u5f64\u5f00\u53e3\u8bf4\u8bdd</h2><p>\u3000\u3000\u90b5\u81f3\u521a\u4ed6\u867d\u7136\u4eca\u5929\u624d\u52aa\u529b\u8ddf\u4e07\u52c7\u63a5\u89e6\u5230\uff0c\u4f46\u5bf9\u5e02\u91cc\u9519\u7efc\u590d\u6742\u7684\u5173\u7cfb\u5f88\u6e05\u695a\uff0c\u518d\u8bf4\u5e74\u524d\u5f20\u606a\u5728\u76db\u9752\u7684\u9152\u5427\u91cc\u902e\u5230\u673a\u4f1a\u5c31\u6559\u8bad\u4e07\u52c7\u4ed6\u513f\u5b50\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e5f\u4e0d\u662f\u4e0d\u77e5\u9053\u3002\u90b5\u81f3\u521a\u4e0d\u77e5\u9053\u5f20\u606a\u770b\u5230\u4ed6\u8ddf\u4e07\u52c7\u63a5\u89e6\u7684\u4e8b\u60c5\uff0c\u5305\u53a2\u95e8\u7684\u9694\u97f3\u6548\u679c\u4e0d\u662f\u5f88\u597d\uff0c\u6572\u6253\u5728\u4eba\u5fc3\u810f\u4e0a\u4f3c\u7684\u5f3a\u70c8\u97f3\u4e50\u4ece\u95e8\u91cc\u4f20\u51fa\u6765\uff0c\u90b5\u81f3\u521a\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u5b89\uff0c\u5fc3\u60f3\u5f20\u606a\u5728\u7535\u8bdd\u90a3\u5934\u4e5f\u80fd\u542c\u5f97\u89c1\uff0c\u5c31\u6015\u5f20\u606a\u5728\u7535\u8bdd\u95ee\u4ed6\u5728\u505a\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\u90b5\u81f3\u521a\u80fd\u8ddf\u4e07\u901a\u63a5\u89e6\u4e0a\uff0c\u8fd8\u662f\u4e16\u7eaa\u9910\u996e\u6709\u9650\u516c\u53f8\u4e0a\u9762\u625b\u7740\u9526\u6e56\u7684\u62db\u724c\uff1b\u90b5\u81f3\u521a\u4e5f\u6e05\u695a\u77e5\u9053\u8fd9\u4e00\u70b9\uff0c\u6ca1\u6709\u9526\u6e56\uff0c\u4ed6\u8fd8\u63a5\u89e6\u4e0d\u5230\u4e07\u52c7\u8fd9\u5c42\u6b21\u7684\u4eba\u7269\u3002</p><p>\u3000\u3000\u4e07\u52c7\u518d\u9a84\u6a2a\uff0c\u4e5f\u4e0d\u6562\u4e0d\u628a\u5f90\u5b66\u5e73\u5f53\u56de\u4e8b\uff0c\u4ed6\u751a\u81f3\u5e0c\u671b\u8c22\u665a\u6674\u7684\u9526\u6e56\u516c\u53f8\u4e5f\u6765\u53c2\u4e0e\u5230\u6c99\u7530\u65e7\u57ce\u6539\u9020\u9879\u76ee\u4e2d\u6765\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u4ed6\u5c31\u53ef\u4ee5\u4ece\u7701\u91cc\u5f97\u5230\u66f4\u591a\u7684\u597d\u5904\uff0c\u53ef\u60dc\u90b5\u81f3\u521a\u53ea\u4ee3\u8868\u4ed6\u4e2a\u4eba\uff0c\u6240\u5e78\u4f3a\u5019\u8fd8\u7b97\u5468\u5230\u3002</p><p>\u3000\u3000\u5728\u4e07\u52c7\u770b\u6765\uff0c\u5f20\u77e5\u884c\u662f\u5510\u5b66\u8c26\u5fe0\u5b9e\u7684\u8ffd\u968f\u8005\uff0c\u4f46\u662f\u6d77\u5dde\u5e02\u91cc\u4efb\u4f55\u77e5\u9053\u5f20\u77e5\u884c\u4e0e\u5f90\u5b66\u5e73\u5173\u7cfb\u7684\u4eba\uff0c\u90fd\u4e0d\u5e0c\u671b\u4e0e\u5f20\u77e5\u884c\u7684\u5173\u7cfb\u641e\u50f5\uff0c\u518d\u8bf4\u4ed6\u628a\u5f20\u606a\u6b3a\u8d1f\u4ed6\u513f\u5b50\u4e07\u5929\u624d\u7684\u884c\u4e3a\u53ea\u5f53\u6210\u5c11\u5e74\u4eba\u4e4b\u95f4\u7684\u4e89\u6267\uff0c\u5e76\u6ca1\u6709\u5c06\u8fd9\u95ee\u9898\u770b\u5f97\u591a\u4e25\u91cd\uff0c\u4ed6\u4e5f\u4e0d\u8ba4\u4e3a\u5510\u5b66\u8c26\u4e0e\u4ed6\u4e07\u5bb6\u7684\u5173\u7cfb\u5c31\u4e00\u5b9a\u6c34\u706b\u4e0d\u5bb9\uff0c\u81f3\u5c11\u5468\u5bcc\u660e\u4e0e\u4ed6\u4e07\u5bb6\u5173\u7cfb\u4e00\u70b9\u6ca1\u53d7\u5230\u5e74\u524d\u7d27\u5f20\u5173\u7cfb\u7684\u59a8\u788d\uff0c\u81f3\u5c11\u5468\u5bcc\u660e\u4e0e\u5510\u5b66\u8c26\u8868\u9762\u90fd\u5341\u5206\u5ba2\u6c14\u3002</p><p>\u3000\u3000\u8bf4\u767d\u4e86\uff0c\u5728\u4e07\u52c7\u7684\u5b57\u5178\u91cc\uff0c\u6ca1\u6709\u6240\u8c13\u7684\u539f\u5219\u4e0e\u7acb\u573a\uff0c\u4e00\u5207\u7684\u590d\u6742\u5173\u7cfb\u90fd\u662f\u56e0\u4e3a\u5229\u76ca\u7684\u7ea0\u845b\u3002</p><p>\u3000\u3000\u66ff\u9526\u6e56\u8d1f\u8d23\u4e16\u7eaa\u9910\u996e\u3001\u524d\u6bb5\u65f6\u95f4\u56e0\u4e3a\u56db\u51e4\u6865\u996e\u98df\u5e7f\u573a\u800c\u540d\u95fb\u6d77\u5dde\u7684\u90b5\u81f3\u521a\u4e0d\u4e5f\u6c42\u5230\u4ed6\u95e8\u4e0b\u4e86\u5417\uff1f</p><p>\u3000\u3000\u8c01\u77e5\u9053\u90b5\u81f3\u521a\u4f1a\u4e0d\u4f1a\u662f\u9526\u6e56\u7684\u95ee\u8def\u77f3\uff1f</p><p>\u3000\u3000\u8fde\u5f20\u77e5\u884c\u4e4b\u524d\u90fd\u7ed9\u7792\u5728\u9f13\u91cc\uff0c\u4e07\u52c7\u66f4\u4e0d\u53ef\u80fd\u4f1a\u77e5\u9053\u5f20\u606a\u5728\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u7684\u91cd\u8981\u4f4d\u7f6e\uff0c\u5f53\u7136\u6ca1\u6709\u4e00\u70b9\u63d0\u9632\u7684\u5fc3\uff0c\u770b\u7740\u90b5\u81f3\u521a\u62ff\u7740\u624b\u673a\u8fdb\u6765\uff0c\u95ee\u4ed6\uff1a\u201c\u8c01\u554a\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7b11\u7740\u8bf4\uff1a\u201c\u56db\u51e4\u6865\u90a3\u8fb9\u7684\u7535\u8bdd\uff0c\u6709\u4e9b\u5c0f\u4e8b\u60c5\uff0c\u4e0d\u5173\u7d27\u7684\uff0c\u4e0d\u8981\u8fd9\u5c0f\u4e8b\u6253\u6270\u4e07\u5e02\u7684\u597d\u5174\u81f4\u3002\u201d</p><p>\u3000\u3000\u201c\u65e2\u7136\u4f60\u90a3\u8fb9\u6709\u4e8b\u60c5\uff0c\u90a3\u4eca\u5929\u5c31\u5230\u8fd9\u91cc\u5427\u2026\u2026\u201d\u4e07\u52c7\u4e5f\u4e0d\u60f3\u521d\u6b21\u5c31\u7ed9\u90b5\u81f3\u521a\u7559\u4e0b\u4e0d\u901a\u60c5\u7406\u7684\u5370\u8c61\uff0c\u6bd5\u7adf\u90b5\u81f3\u521a\u540e\u9762\u7ad9\u7740\u9526\u6e56\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5f53\u7136\u6025\u4e8e\u8131\u8eab\uff0c\u6273\u5012\u5468\u5065\u7684\u673a\u4f1a\u4e0d\u662f\u968f\u65f6\u90fd\u7b49\u7740\u4ed6\uff0c\u867d\u7136\u4ed6\u4e0d\u518d\u5728\u4e4e\u519c\u673a\u5382\u7684\u5382\u957f\u4e00\u804c\uff0c\u4f46\u662f\u95f7\u5728\u5fc3\u91cc\u7684\u6076\u6c14\u4e0d\u80fd\u4e0d\u51fa\uff0c\u5634\u91cc\u8bf4\u201c\u4e07\u5e02\u957f\u4f53\u8c05\u6211\u4eec\u8fd9\u4e9b\u82e6\u547d\u7684\u201d\uff0c\u968f\u540e\u5c06\u8fd9\u8fb9\u7684\u8d39\u7528\u7ed3\u4e86\uff0c\u8ba9\u4e09\u540d\u5c0f\u59d0\u966a\u7740\u4e07\u52c7\u8ddf\u4ed6\u7684\u79d8\u4e66\u7ee7\u7eed\u7559\u5728\u8fd9\u91cc\u5feb\u6d3b\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u51fa\u4e86\u591c\u591c\u7b19\u6b4c\u7684\u591c\u603b\u4f1a\uff0c\u9a71\u8f66\u76f4\u63a5\u56de\u5bb6\uff0c\u4e3a\u4e86\u5f20\u606a\u80fd\u987a\u5229\u5c06\u4e3e\u62a5\u6750\u6599\u5728\u8f6c\u9001\u519c\u673a\u5c40\u4e4b\u524d\u622a\u4e0b\u6765\u8fdb\u6765\u8c03\u67e5\uff0c\u90b5\u81f3\u521a\u5728\u51c6\u5907\u5df2\u4e45\u7684\u51e0\u4efd\u6750\u6599\u4e0a\u90fd\u7f72\u4e0a\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u7b2c\u4e8c\u5929\u65e9\u6668\u4eb2\u81ea\u9a71\u8f66\u5206\u522b\u9001\u5230\u5e02\u4fe1\u8bbf\u5c40\u3001\u5e02\u68c0\u67e5\u9662\u4fe1\u8bbf\u63a5\u5f85\u5ba4\u3001\u5e02\u7eaa\u59d4\u4fe1\u8bbf\u63a5\u5f85\u5ba4\u3002\u5e02\u4fe1\u8bbf\u5c40\u5c5e\u4e8e\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u5206\u7ba1\uff0c\u540e\u6765\u5f20\u77e5\u884c\u5fd9\u4e8e\u6d77\u5dde\u63a7\u80a1\uff0c\u624d\u7531\u4e00\u540d\u526f\u79d8\u4e66\u957f\u534f\u7ba1\uff1b\u91d1\u56fd\u6d77\u6628\u5929\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u4ed6\u8fd9\u4e2a\u653f\u6cd5\u59d4\u4e66\u8bb0\u4e5f\u662f\u4e00\u5927\u65e9\u5c31\u5230\u5e02\u68c0\u67e5\u9662\u4fe1\u8bbf\u63a5\u5f85\u5ba4\u89c6\u5bdf\u5de5\u4f5c\uff1b\u5f20\u606a\u63a5\u5230\u91d1\u56fd\u6d77\u7684\u7535\u8bdd\uff0c\u4ed6\u5df2\u7ecf\u6307\u793a\u5e02\u68c0\u67e5\u9662\u90a3\u8fb9\u4e13\u95e8\u7acb\u6848\u8c03\u67e5\u6b64\u4e8b\uff0c\u5f20\u606a\u4fbf\u7ed9\u7238\u7238\u6253\u7535\u8bdd\uff0c\u5e02\u4fe1\u8bbf\u5c40\u90a3\u91cc\u7684\u90a3\u4efd\u6750\u6599\u5c31\u53ef\u4ee5\u4ee5\u6b63\u5e38\u7684\u624b\u7eed\u6d41\u5411\u519c\u673a\u5c40\uff0c\u8ba9\u5e02\u4fe1\u8bbf\u5c40\u8ffd\u8e2a\u519c\u673a\u5c40\u5904\u7406\u5c31\u53ef\u4ee5\u4e86\uff0c\u6295\u5411\u5e02\u7eaa\u59d4\u7684\u90a3\u4efd\u6750\u6599\u53bb\u8e2a\uff0c\u4e5f\u65e0\u9700\u5173\u5fc3\u4e86\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u51c6\u5907\u7684\u6750\u6599\u91cc\u6d89\u53ca\u5230\u5468\u5065\u6e0e\u804c\u72af\u7f6a\u7684\u5acc\u7591\uff0c\u8fd9\u5c5e\u4e8e\u68c0\u5bdf\u9662\u76f4\u63a5\u7acb\u6848\u4fa6\u67e5\u7684\u8303\u56f4\uff0c\u5468\u5065\u88ab\u8bf7\u5230\u5e02\u68c0\u5bdf\u9662\u63a5\u53d7\u8c03\u67e5\u4e4b\u524d\u4e0d\u4e45\uff0c\u624d\u4ece\u519c\u673a\u5c40\u90a3\u91cc\u77e5\u9053\u4ed6\u7ed9\u90b5\u81f3\u521a\u7f72\u540d\u4e3e\u62a5\u4e86\uff0c\u8fd9\u662f\u4ed6\u7ed9\u4e22\u5230\u758f\u6e2f\u6cb3\u3001\u809a\u5b50\u7a9d\u7740\u4e00\u809a\u706b\u7ede\u5c3d\u8111\u6c41\u60f3\u7740\u5982\u4f55\u627e\u56de\u8138\u9762\u7684\u7b2c\u4e09\u5929\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7684\u8fd9\u4efd\u6750\u6599\uff0c\u9664\u4e86\u5468\u5065\uff0c\u8fd8\u6d89\u53ca\u519c\u673a\u5382\u56db\u540d\u526f\u5382\u957f\u4e2d\u7684\u4e09\u4eba\uff0c\u5982\u5f20\u606a\u6240\u6599\uff0c\u674e\u660e\u5b66\u63d0\u51fa\u4ece\u519c\u673a\u5382\u57fa\u5c42\u518d\u9009\u62d4\u56db\u4eba\u53c2\u4e0e\u8fd9\u6b21\u5382\u957f\u7ade\u8058\uff0c\u5e02\u519c\u673a\u5c40\u6ca1\u6709\u4eba\u8df3\u51fa\u6765\u53cd\u5bf9\uff0c\u5e02\u519c\u673a\u5c40\u91cc\u7684\u5934\u5934\u8111\u8111\u53ea\u5e0c\u671b\u68c0\u5bdf\u9662\u7684\u8c03\u67e5\u9002\u53ef\u800c\u6b62\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u6687\u5173\u6ce8\u8fd9\u4e9b\u7ec6\u8282\uff0c\u8fd9\u4e2a\u5468\u672b\uff0c\u8c22\u665a\u6674\u6ca1\u6709\u8d76\u56de\u7701\u57ce\uff0c\u800c\u662f\u7531\u5468\u53d4\u60e0\u5e26\u7740\u82b7\u5f64\u5230\u6d77\u5dde\u6765\u3002\u5176\u5b50\u5f90\u5fd7\u660e\u4e27\u751f\u6d77\u5dde\u4e4b\u540e\uff0c\u5468\u53d4\u60e0\u8fd8\u6ca1\u6709\u518d\u8e0f\u5165\u6d77\u5dde\u7684\u571f\u5730\u3002\u5e74\u521d\u521a\u6ee1\u4e94\u5341\u4e94\u5c81\u7684\u5468\u53d4\u60e0\u4e5f\u6ca1\u6709\u5230\u653f\u534f\u3001\u7701\u4eba\u5927\u4efb\u95f2\u804c\u5c31\u76f4\u63a5\u9000\u4f11\u56de\u5bb6\u7167\u987e\u82b7\u5f64\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5085\u4fca\u5bb6\u4e24\u4e2b\u5934\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e22\u5c0f\u9526\u6e56\u522b\u5885\u91cc\uff0c\u5728\u90a3\u91cc\u5403\u4e86\u987f\u4e2d\u996d\uff0c\u5c31\u53bb\u76ca\u9686\u9547\u5fd9\u7231\u8fbe\u7535\u5b50\u7684\u4e8b\u60c5\uff0c\u7231\u8fbe\u7535\u5b50\u6b63\u5728\u7b79\u5907\u8bd5\u751f\u4ea7\u7684\u4e8b\u60c5\uff0c\u5343\u5934\u4e07\u7eea\u3002\u867d\u7136\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e0d\u5206\u767d\u5929\u9ed1\u591c\u7684\u505a\u8bd5\u9a8c\u3001\u8c03\u8bd5\u3001\u7ec4\u7ec7\u751f\u4ea7\uff0c\u5f20\u606a\u4e5f\u4e0d\u662f\u4ec5\u4ec5\u9f13\u52b1\u4ed6\u4eec\uff0c\u4e5f\u8ddf\u7740\u4e00\u591c\u4e00\u591c\u7684\u7761\u4e0d\u7740\uff0c\u4e00\u6709\u7a7a\u5c31\u5f80\u5382\u5b50\u91cc\u8dd1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5382\u5b50\u91cc\u8017\u4e86\u4e00\u4e0b\u5348\uff0c\u665a\u4e0a\u8fd8\u8d76\u5230\u5c0f\u9526\u6e56\u522b\u5885\u5403\u996d\uff0c\u5374\u770b\u89c1\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u59d0\u59b9\u8ddf\u82b7\u5f64\u73a9\u5979\u4eec\u4e60\u60ef\u7684\u90a3\u4e00\u5957\u8bc6\u4eba\u6e38\u620f\uff0c\u4e24\u59d0\u59b9\u85cf\u8d77\u6765\u4e00\u9635\u5b50\u7136\u540e\u8df3\u51fa\u6765\uff0c\u95ee\u82b7\u5f64\uff1a\u201c\u8c01\u662f\u60dc\u5bb9\uff1f\u201d\u4e5f\u4e0d\u6653\u5f97\u82b7\u5f64\u4ece\u54ea\u91cc\u770b\u51fa\u6765\uff0c\u53ea\u8981\u54ac\u7740\u5634\u5507\u6c89\u9ed8\u4e00\u4f1a\u513f\uff0c\u5c31\u624b\u6307\u7740\u60dc\u5bb9\u3002\u4e24\u59d0\u59b9\u89c1\u7ed9\u8bc6\u7834\uff0c\u9a6c\u4e0a\u53c8\u8eb2\u8d77\u6765\uff0c\u8fd8\u6362\u4e86\u8863\u670d\u51fa\u6765\uff0c\u95ee\u82b7\u5f64\uff1a\u201c\u8c01\u662f\u60dc\u7fbd\uff1f\u201d\u82b7\u5f64\u54ac\u7740\u624b\u6307\u5934\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u624b\u6307\u7740\u60dc\u7fbd\u3002</p><p>\u3000\u3000\u5468\u53d4\u60e0\u7b11\u7740\u8bf4\uff1a\u201c\u5979\u4eec\u90fd\u73a9\u4e00\u4e0b\u5348\u4e86\uff0c\u8fd8\u73a9\u5f97\u8fd9\u4e48\u8d77\u52b2\uff0c\u8fd9\u4e24\u5c0f\u4e2b\u5934\uff0c\u6211\u504f\u504f\u8ba4\u4e0d\u51fa\u54ea\u4e2a\u662f\u59d0\u59d0\u54ea\u4e2a\u662f\u59b9\u59b9\uff0c\u82b7\u5f64\u5012\u662f\u4e00\u773c\u90fd\u770b\u5f97\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u51e0\u6b21\uff0c\u624d\u6653\u5f97\u4ece\u4e24\u59d0\u59b9\u7684\u95ee\u8bdd\u91cc\u770b\u51fa\u7834\u7efd\uff0c\u60dc\u5bb9\u53ea\u4f1a\u95ee\uff1a\u201c\u8c01\u662f\u60dc\u7fbd\uff1f\u201d\u60dc\u7fbd\u4e00\u76f4\u95ee\uff1a\u201c\u8c01\u662f\u60dc\u5bb9\uff1f\u201d\u770b\u7740\u82b7\u5f64\u4ece\u4e24\u59d0\u59b9\u5f00\u53e3\u95ee\u8bdd\u5c31\u77e5\u9053\u8c01\u662f\u59d0\u59d0\u8c01\u662f\u59b9\u59b9\uff0c\u5979\u504f\u504f\u8981\u88c5\u4f5c\u601d\u8003\u4e00\u4f1a\u513f\u3002\u5f97\uff0c\u82b7\u5f64\u5c31\u662f\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5012\u4e0d\u662f\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u59d0\u59b9\u8001\u5b9e\uff0c\u5f20\u606a\u6233\u7a7f\u5979\u8bc6\u7834\u4e24\u59d0\u59b9\u7684\u7a8d\u95e8\uff0c\u60dc\u5bb9\u4e00\u8138\u4e0d\u7f6e\u4fe1\u7684\u95ee\uff1a\u201c\u771f\u7684\uff0c\u4f60\u771f\u8fd9\u6837\u8ba4\u51fa\u6211\u4eec\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u82b7\u5f64\u5a07\u7f9e\u7684\u85cf\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u60dc\u5bb9\u62c9\u7740\u60dc\u7fbd\u5230\u4e00\u65c1\u7ee7\u7eed\u7422\u78e8\u9a97\u4eba\u7684\u6280\u5de7\uff0c\u5085\u4fca\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u518d\u8fd9\u4e48\u4e0b\u53bb\uff0c\u8fde\u5979\u4eec\u5988\u5988\u90fd\u8981\u8ba4\u4e0d\u51fa\u5979\u4eec\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u8981\u53bb\u5317\u4eac\u5f00\u4f1a\uff0c\u5468\u53d4\u60e0\u4e0e\u82b7\u5f64\u8981\u5728\u6d77\u5dde\u591a\u7559\u4e24\u5929\uff0c\u5979\u5e0c\u671b\u5085\u4fca\u5c06\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u5c0f\u59d0\u59b9\u7559\u4e0b\u6765\u966a\u82b7\u5f64\uff0c\u867d\u7136\u5927\u4eba\u518d\u5c06\u82b7\u5f64\u7167\u987e\u597d\uff0c\u6ca1\u6709\u540c\u9f84\u7684\u73a9\u4f34\uff0c\u90fd\u4e0d\u53ef\u907f\u514d\u7684\u611f\u5230\u5b64\u5355\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5e02\u5c31\u6ca1\u6709\u6bd4\u5c0f\u9526\u6e56\u522b\u5885\u533a\u8fd9\u8fb9\u6cbb\u5b89\u66f4\u597d\u7684\u793e\u533a\u4e86\uff0c\u4f46\u662f\u7701\u957f\u592b\u4eba\u5468\u53d4\u60e0\u5c0f\u4f4f\u4e4b\u540e\uff0c\u5e02\u91cc\u8fd8\u662f\u4e13\u95e8\u5b89\u6392\u4e86\u8b66\u536b\u3002</p><p>\u3000\u3000\u5468\u53d4\u60e0\u5bf9\u4e24\u5c0f\u59d0\u59b9\u4e5f\u559c\u6b22\u5f97\u4e0d\u5f97\u4e86\uff0c\u8ba9\u5085\u4fca\u53bb\u63a5\u7231\u4eba\u4e00\u8d77\u6765\u5403\u665a\u996d\u3002\u5f20\u606a\u8ba9\u5085\u4fca\u5feb\u53bb\u63a5\u4eba\uff0c\u968f\u8def\u5c06\u5510\u5a67\u76f4\u63a5\u4ece\u5b66\u6821\u91cc\u4e5f\u63a5\u8fc7\u6765\u5403\u996d\u3002\u8fd9\u4e2a\u793e\u4f1a\u8c01\u90fd\u65e0\u6cd5\u8131\u4fd7\uff0c\u5085\u4fca\u4ed6\u7231\u4eba\u60f3\u5230\u5a18\u5bb6\u4eba\u7684\u539f\u8c05\uff0c\u91cd\u65b0\u4fee\u590d\u7834\u88c2\u591a\u5e74\u7684\u7236\u5973\u5173\u7cfb\uff0c\u6700\u597d\u7684\u6377\u5f84\u5c31\u662f\u6500\u4e0a\u7701\u957f\u5bb6\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u674e\u660e\u745c\u8fd8\u5728\u5e02\u4e2d\u836f\u5382\u4e0a\u4e2d\u73ed\uff0c\u5085\u4fca\u8d76\u5230\u5382\u5b50\u91cc\u53bb\u63a5\u4eba\u6765\u5403\u665a\u996d\uff0c\u5f20\u606a\u8d81\u7740\u8fd9\u7a7a\u5f53\u513f\uff0c\u5c06\u5085\u4fca\u4e0e\u4ed6\u7231\u4eba\u4e8b\u60c5\u8bf4\u7ed9\u5468\u53d4\u60e0\u542c\u3002\u5468\u53d4\u60e0\u4eba\u5fc3\u8f6f\uff0c\u8d76\u7740\u5085\u4fca\u3001\u674e\u660e\u745c\u8fc7\u6765\uff0c\u773c\u5708\u90fd\u7ea2\u4e86\uff0c\u770b\u7740\u674e\u660e\u745c\u6807\u81f4\u9e45\u86cb\u8138\u4e0a\u51e0\u9053\u75a4\u75d5\u89c9\u5f97\u5341\u5206\u7684\u75db\u60dc\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u6ca1\u5728\u5e02\u91cc\u803d\u6401\uff0c\u5230\u4e0b\u73ed\u65f6\u95f4\u5c31\u8ddf\u59bb\u5b50\u6881\u683c\u73cd\u8d76\u8fc7\u6765\u5403\u665a\u996d\uff0c\u987a\u8def\u628a\u987e\u5efa\u840d\u63a5\u8fc7\u6765\u3002\u5510\u5b66\u8c26\u665a\u4e0a\u8981\u8d74\u5e02\u4f01\u4e1a\u5bb6\u534f\u8bae\u7ec4\u7ec7\u7684\u665a\u5bb4\uff0c\u53ea\u8bf4\u90a3\u91cc\u7ed3\u675f\u5f97\u65e9\u5c31\u8d76\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u770b\u5230\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u5c0f\u59d0\u59b9\uff0c\u5b8c\u5168\u6ca1\u60f3\u5230\u4f1a\u6709\u957f\u76f8\u3001\u8bf4\u8bdd\u4e00\u6a21\u4e00\u6837\u7684\u53cc\u80de\u80ce\uff0c\u8fd8\u90fd\u8fd9\u4e48\u6f02\u4eae\u53ef\u7231\uff0c\u4eff\u4f5b\u4e00\u5bf9\u590d\u5236\u7684\u82ad\u8298\u5a03\u5a03\uff0c\u559c\u6b22\u5f97\u7d27\uff0c\u8fd9\u65f6\u5019\u60dc\u7fbd\u3001\u60dc\u5bb9\u9a97\u4eba\u7684\u6280\u5de7\u53c8\u63d0\u9ad8\u7684\u4e00\u622a\uff0c\u82b7\u5f64\u4e00\u65f6\u534a\u4f1a\u4e5f\u8ba4\u4e0d\u51fa\u6765\uff0c\u8001\u731c\u9519\uff0c\u5510\u5a67\u770b\u4e86\u534a\u5929\uff0c\u4e5f\u5c31\u8499\u5bf9\u4e86\u4e24\u4e09\u6b21\uff0c\u522b\u4eba\u5750\u5728\u697c\u4e0b\u7684\u5ba2\u5385\u91cc\u8bf4\u8bdd\uff0c\u5979\u62c9\u7740\u82b7\u5f64\u8ff7\u4e0a\u8fd9\u4e2a\u75af\u72c2\u7684\u8fa8\u4eba\u6e38\u620f\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u7238\u7238\u8bf4\u674e\u660e\u745c\u8ddf\u674e\u660e\u5b66\u662f\u5802\u5144\u59b9\uff0c\u7565\u8fc7\u90a3\u4e9b\u8ba9\u6c89\u75db\u7684\u5f80\u4e8b\uff0c\u53ea\u8bf4\u674e\u660e\u745c\u4e3a\u4e86\u8ddf\u5085\u4fca\u7ed3\u5a5a\uff0c\u8ddf\u5bb6\u91cc\u65ad\u4e86\u5f80\u6765\u3002\u542c\u5230\u5085\u4fca\u7adf\u7136\u662f\u52b3\u6539\u91ca\u653e\u5206\u5b50\uff0c\u6881\u683c\u73cd\u591a\u5c11\u6709\u4e9b\u4e0d\u4e50\u610f\uff0c\u5f20\u77e5\u884c\u6027\u60c5\u8c41\u8fbe\uff0c\u867d\u7136\u5f20\u606a\u6ca1\u6709\u63d0\u539f\u56e0\uff0c\u80fd\u8ba9\u4e00\u4e2a\u59d1\u5a18\u6bc1\u5bb9\u7b49\u5019\uff0c\u81ea\u6709\u60b2\u58ee\u52a8\u4eba\u7684\u6545\u4e8b\u5728\u91cc\u9762\uff0c\u7b11\u7740\u8bf4\u6539\u5929\u8ba9\u674e\u660e\u5b66\u529d\u529d\u5085\u4fca\u5cb3\u7236\u6bcd\u3002</p><p>\u3000\u3000\u4eba\u6d3b\u4e00\u4e16\uff0c\u8981\u662f\u8ba9\u4eb2\u751f\u7236\u6bcd\u89c6\u4e3a\u4ec7\u654c\uff0c\u4e5f\u592a\u53ef\u60b2\u4e86\u3002</p><p>\u3000\u3000\u53a8\u5e08\u5728\u53a8\u623f\u91cc\u51c6\u5907\u83dc\u80b4\uff0c\u5927\u5bb6\u5750\u5728\u5ba2\u5385\u91cc\u62c9\u5bb6\u5e38\uff0c\u7b49\u8c22\u665a\u6674\u4ece\u516c\u53f8\u91cc\u5f80\u56de\u8d76\uff0c\u5510\u5a67\u7a81\u7136\u8df3\u7740\u5954\u4e0b\u697c\u68af\uff0c\u4eba\u5728\u697c\u68af\u53e3\u5c31\u5f00\u59cb\u558a\uff1a\u201c\u82b7\u5f64\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff0c\u82b7\u5f64\u5f00\u53e3\u8bf4\u8bdd\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u6123\u5728\u90a3\u91cc\uff0c\u4e00\u65f6\u95f4\u90fd\u6ca1\u6709\u53cd\u5e94\u8fc7\u6765\uff0c\u5f20\u606a\u773c\u775b\u7728\u4e86\u4e24\u4e0b\uff0c\u5c31\u8df3\u4e86\u8d77\u6765\uff0c\u5174\u594b\u7684\u62c9\u7740\u5510\u5a67\u5c31\u5f80\u697c\u4e0a\u8dd1\uff0c\u4ed6\u90fd\u4e0d\u76f8\u4fe1\u5510\u5a67\u8bf4\u7684\u662f\u4e8b\u5b9e\u3002</p></article>"}}}]);
