(window.webpackJsonp=window.webpackJsonp||[]).push([[1690],{1713:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u5341\u79d2\u949f",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u5341\u79d2\u949f</h2><p>\u743c\u6069\u5e76\u4e0d\u754f\u60e7\u9aa8\u9f99\u2014\u2014\u6216\u8005\u8bf4\uff0c\u4e0d\u662f\u5341\u5206\u754f\u60e7\u3002\u5728\u4ed6\u7684\u6982\u5ff5\u91cc\uff0c\u6ca1\u6709\u7075\u89c9\u6ca1\u6709\u667a\u529b\u7684\u4e1c\u897f\uff0c\u5c31\u7b97\u662f\u518d\u5f3a\u4e5f\u5f3a\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u90fd\u5c5e\u4e8e\u201c\u53ef\u4ee5\u60f3\u529e\u6cd5\u641e\u5b9a\u201d\u7684\u8303\u7574\u3002\u4f46\u5982\u679c\u591a\u4e86\u4e00\u540d\u4ea1\u7075\u5e08\u6307\u6325\u64cd\u63a7\uff0c\u90a3\u5c31\u5b8c\u5168\u662f\u4e24\u4e2a\u6982\u5ff5\u4e86\u3002</p><p>\u9f7f\u556e\u3001\u722a\u6495\u3001\u5c3e\u51fb\uff0c\u751a\u81f3\u76f4\u63a5\u7528\u8eab\u4f53\u51b2\u649e\uff0c\u9aa8\u9f99\u867d\u7136\u7b28\u62d9\uff0c\u4f46\u6bcf\u4e00\u51fb\u90fd\u8db3\u4ee5\u788e\u77f3\u88c2\u91d1\uff0c\u8fd9\u5df2\u7ecf\u5b8c\u5168\u8d85\u51fa\u4e86\u4eba\u529b\u6240\u80fd\u62b5\u6297\u7684\u8303\u7574\uff0c\u53ea\u8981\u88ab\u6253\u4e2d\u4e00\u4e0b\u5c31\u80af\u5b9a\u4f1a\u5b8c\u86cb\u3002\u9ed1\u6c14\u4e0d\u65ad\u4ece\u5b83\u4eec\u53e3\u4e2d\u55b7\u51fa\uff0c\u5c06\u5927\u534a\u4e2a\u5929\u7a7a\u90fd\u53d8\u6210\u4e86\u7070\u8499\u8499\u4e00\u7247\uff0c\u867d\u7136\u4e0d\u8db3\u4ee5\u4fb5\u8680\u9732\u4e1d\u96c5\u7684\u5723\u6d01\u5149\u73af\uff0c\u4f46\u5374\u6210\u529f\u5730\u8d77\u5230\u4e86\u7275\u5236\u5e72\u6270\u7684\u4f5c\u7528\u3002\u82cd\u767d\u9886\u4e3b\u4fbf\u501f\u7740\u8fd9\u9ed1\u96fe\u7684\u63a9\u62a4\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u63a5\u8fde\u4e0d\u65ad\u53d1\u8d77\u5077\u88ad\u3002</p><p>\u8f70\uff01\u8f70\uff01\u8f70\uff01</p><p>\u4e09\u9897\u706b\u7403\u4ece\u9ed1\u6c14\u4e2d\u8fde\u73af\u98de\u51fa\uff0c\u5c04\u5411\u6b63\u5728\u9ad8\u901f\u524d\u51b2\u7684\u72ec\u89d2\u517d\uff0c\u88ab\u743c\u6069\u521b\u9020\u51fa\u7684\u5143\u7d20\u62a4\u76fe\u6321\u4e86\u4e0b\u6765\u3002\u9b54\u6cd5\u70c8\u7130\u5c06\u9ed1\u96fe\u6682\u65f6\u51b2\u6563\uff0c\u82cd\u767d\u9886\u4e3b\u7684\u8eab\u5f62\u4ece\u4e2d\u663e\u73b0\u51fa\u6765\uff0c\u5de6\u624b\u7684\u9aa8\u6756\u4e0a\u5149\u8292\u9eef\u6de1\uff0c\u4eff\u4f5b\u6210\u4e86\u5e9f\u54c1\uff0c\u53f3\u624b\u5374\u6301\u7740\u4e00\u67c4\u706b\u7ea2\u7684\u86c7\u5f62\u77ed\u6756\uff0c\u521a\u624d\u90a3\u4e09\u53d1\u706b\u7403\u4fbf\u662f\u4ece\u4e2d\u8ff8\u51fa\u3002\u743c\u6069\u6b63\u5f85\u53cd\u51fb\uff0c\u5c31\u89c1\u4ea1\u7075\u5e08\u558b\u558b\u602a\u7b11\u4e24\u58f0\uff0c\u6025\u901f\u540e\u9000\uff0c\u8f6c\u773c\u95f4\u53c8\u9690\u6ca1\u5728\u4e86\u6c89\u6c89\u9ed1\u96fe\u4e2d\u3002</p><p>\u201c\u8fd9\u6837\u4e0b\u53bb\u4e0d\u884c\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f97\u7acb\u523b\u5e72\u6389\u90a3\u4e2a\u4ea1\u7075\u5e08\u3002\u201d</p><p>\u4f9d\u8d56\u7740\u5929\u754c\u72ec\u89d2\u517d\u7684\u5353\u7edd\u673a\u52a8\u529b\uff0c\u5c40\u9762\u4e00\u65f6\u8fd8\u6ca1\u5230\u5c71\u7a77\u6c34\u5c3d\u7684\u5730\u6b65\uff0c\u4f46\u4e5f\u5df2\u7ecf\u79f0\u5f97\u4e0a\u662f\u9ebb\u70e6\u4e86\u2014\u2014\u800c\u4e14\u4f1a\u53d8\u5f97\u8d8a\u6765\u8d8a\u9ebb\u70e6\uff0c\u56e0\u4e3a\u4e3b\u52a8\u6743\u5df2\u7ecf\u5b8c\u5168\u88ab\u5bf9\u65b9\u638c\u63e1\u3002\u4efb\u4f55\u6709\u57fa\u672c\u6218\u6597\u5e38\u8bc6\u7684\u4eba\u90fd\u660e\u767d\uff0c\u4e00\u76f4\u88ab\u52a8\u6328\u6253\u7684\u8bdd\uff0c\u4e0b\u573a\u53ea\u53ef\u80fd\u662f\u6b7b\u8def\u4e00\u6761\u3002\u800c\u8981\u53cd\u51fb\u7684\u8bdd\uff0c\u9996\u5148\u5e72\u6389\u82cd\u767d\u9886\u4e3b\u65e0\u7591\u662f\u6700\u4f73\u9009\u62e9\uff0c\u9aa8\u9f99\u81ea\u8eab\u6ca1\u6709\u667a\u529b\uff0c\u53ea\u8981\u6ca1\u4e86\u64cd\u63a7\u8005\uff0c\u5a01\u80c1\u81f3\u5c11\u51cf\u8f7b\u4e00\u534a\uff0c\u90a3\u5c31\u597d\u5bf9\u4ed8\u591a\u4e86\u3002</p><p>\u95ee\u9898\u662f\u600e\u4e48\u5e72\u6389\u4ed6\u5462\uff1f</p><p>\u901a\u8fc7\u6b64\u524d\u7684\u4ea4\u624b\uff0c\u743c\u6069\u5df2\u7ecf\u786e\u5b9a\u5bf9\u65b9\u7684\u9b54\u6cd5\u9020\u8be3\u7a33\u7a33\u80dc\u8fc7\u81ea\u5df1\uff0c\u5728\u6240\u4ea4\u624b\u8fc7\u7684\u5bf9\u8c61\u91cc\uff0c\u5927\u6982\u548c\u74dc\u91cc\u5fb7\u65af\u57ce\u9996\u5e2d\u5927\u5deb\u5e08\u4e9a\u5f53\u65af\u5c5e\u4e8e\u540c\u4e00\u6c34\u5e73\uff0c\u751a\u81f3\u7565\u6709\u8fc7\u4e4b\u3002\u5982\u679c\u743c\u6069\u548c\u6885\u83f2\u65af\u8054\u624b\u5408\u653b\uff0c\u8981\u5e72\u6389\u4ed6\u5012\u4e5f\u5e76\u4e0d\u7b97\u5f88\u96be\uff0c\u80dc\u7b97\u81f3\u5c11\u6709\u4e03\u6210\uff0c\u4f46\u65c1\u8fb9\u90a3\u4e24\u5934\u9aa8\u9f99\u4e5f\u5e76\u4e0d\u662f\u6446\u8bbe\u3002\u743c\u6069\u6781\u529b\u601d\u7d22\u7740\uff0c\u5404\u79cd\u53ef\u80fd\u7684\u65b9\u6848\u5728\u8111\u4e2d\u6025\u901f\u95ea\u8fc7\uff0c\u6700\u7ec8\u5b9a\u683c\u5230\u5176\u4e2d\u4e00\u79cd\u3002\u6709\u5e0c\u671b\uff0c\u4f46\u98ce\u9669\u5f88\u9ad8\uff0c\u6240\u4ee5\u4ed6\u4e00\u65f6\u6709\u4e9b\u72b9\u8c6b\u4e0d\u51b3\u3002</p><p>\u66f4\u591a\u7684\u706b\u7403\u4ece\u9ed1\u96fe\u4e2d\u5c04\u6765\uff0c\u82cd\u767d\u9886\u4e3b\u6beb\u65e0\u987e\u60dc\u5730\u6325\u970d\u7740\u9b54\u6756\u7684\u50a8\u5b58\uff0c\u4ed6\u5e76\u4e0d\u6307\u671b\u9760\u8fd9\u79cd\u7b80\u5355\u7684\u653b\u51fb\u5c31\u80fd\u591f\u5e72\u6389\u773c\u524d\u7684\u5bf9\u624b\uff0c\u53ea\u662f\u7528\u4ee5\u6d88\u8017\u9632\u5fa1\u7f62\u4e86\uff0c\u771f\u6b63\u7684\u6740\u62db\u7559\u5728\u540e\u9762\u3002\u4e24\u5934\u9aa8\u9f99\u5728\u4ea1\u7075\u5e08\u7684\u6307\u6325\u4e0b\u63a5\u8fde\u653b\u6765\uff0c\u867d\u7136\u90fd\u88ab\u72ec\u89d2\u517d\u5de7\u5999\u907f\u8fc7\uff0c\u4f46\u5b83\u7684\u52a8\u4f5c\u4e5f\u660e\u663e\u53d8\u5f97\u6709\u4e9b\u8fdf\u7f13\u4e86\uff0c\u6bd5\u7adf\u80cc\u4e0a\u627f\u8f7d\u7740\u4e24\u4e2a\u4eba\u7684\u5206\u91cf\u3002</p><p>\u53ea\u80fd\u5192\u9669\u4e00\u640f\u4e86\u3002</p><p>\u518d\u5ea6\u907f\u5f00\u9aa8\u9f99\u7684\u8ffd\u51fb\uff0c\u540c\u65f6\u743c\u6069\u7528\u4e24\u679a\u77f3\u5f39\u6682\u65f6\u51fb\u9000\u4ea1\u7075\u5e08\uff0c\u4e58\u6b64\u7a7a\u9699\uff0c\u9732\u4e1d\u96c5\u7ec8\u4e8e\u51b2\u51fa\u4e86\u9ed1\u96fe\u3002\u5018\u82e5\u8981\u9003\u8dd1\u7684\u8bdd\uff0c\u73b0\u5728\u662f\u552f\u4e00\u7684\u673a\u4f1a\uff0c\u4f46\u65e0\u8bba\u662f\u743c\u6069\u8fd8\u662f\u6885\u83f2\u65af\u90fd\u6ca1\u6709\u8fd9\u4e2a\u6253\u7b97\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u7d27\u76ef\u7740\u4e0b\u65b9\u7ffb\u817e\u4e0d\u606f\u7684\u9ed1\u96fe\uff0c\u201c\u4f60\u4e00\u4e2a\u4eba\uff0c\u80fd\u5e72\u6389\u90a3\u4e2a\u82cd\u767d\u9886\u4e3b\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u201c\u4f60\u6709\u529e\u6cd5\u6321\u4f4f\u9aa8\u9f99\uff1f\u201d</p><p>\u201c\u6211\u6765\u8bd5\u8bd5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6700\u591a\u5341\u4e94\u79d2\u949f\uff0c\u53ef\u4ee5\u4e48\uff1f\u201d</p><p>\u5c11\u5973\u67f3\u7709\u4e00\u6311\uff0c\u82f1\u6c14\u81ea\u7f8e\u4e3d\u7684\u78a7\u7eff\u773c\u7738\u4e2d\u51db\u51db\u900f\u51fa\uff0c\u201c\u5341\u79d2\u949f\u5c31\u8db3\u591f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u597d\u3002\u201d</p><p>\u743c\u6069\u7b80\u77ed\u5730\u56de\u7b54\uff0c\u4ed6\u4e5f\u53ea\u6765\u5f97\u53ca\u8bf4\u8fd9\u4e00\u53e5\uff0c\u56e0\u4e3a\u4e24\u5934\u9aa8\u9f99\u5df2\u7ecf\u631f\u7740\u6ee1\u8eab\u9ed1\u6c14\u51b2\u6765\uff0c\u4ea1\u7075\u5e08\u4f9d\u65e7\u9690\u9041\u5728\u6697\u5904\uff0c\u4f3a\u673a\u800c\u52a8\u3002\u743c\u6069\u51b7\u51b7\u4e00\u7b11\uff0c\u7eb5\u8eab\u4ece\u72ec\u89d2\u517d\u7684\u80cc\u4e0a\u8dc3\u8d77\uff0c\u5b9a\u5b9a\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u4ed6\u95ed\u4e0a\u773c\uff0c\u518d\u7741\u5f00\uff0c\u539f\u672c\u9ed1\u8272\u7684\u77b3\u5b54\u5728\u5239\u90a3\u95f4\u5df2\u7ecf\u53d8\u6210\u4e86\u94f6\u767d\uff0c\u95ea\u70c1\u7740\u660e\u4eae\u7684\u7535\u5149\uff0c\u9759\u9759\u5782\u5728\u8eab\u4f53\u4e24\u4fa7\u7684\u53cc\u81c2\u540c\u65f6\u62ac\u8d77\uff0c\u5e73\u4f38\uff0c\u7136\u540e\u5927\u559d\u4e00\u58f0\u3002</p><p>\u201c\u98d3\u98ce\uff01\u201d</p><p>\u731b\u70c8\u7684\u6c14\u6d41\u81ea\u53cc\u624b\u638c\u5fc3\u4e2d\u8f70\u7136\u6d8c\u51fa\uff0c\u7ffb\u6eda\u7f20\u7ed5\u7740\uff0c\u75af\u72c2\u5438\u626f\u7740\u5468\u56f4\u7684\u7a7a\u6c14\uff0c\u77ac\u95f4\u51dd\u805a\u6210\u4e24\u9053\u6491\u5929\u62c4\u5730\u7684\u98d3\u98ce\uff0c\u9ad8\u901f\u5411\u4e24\u65c1\u79fb\u52a8\u8fc7\u53bb\u3002\u9aa8\u9f99\u4e00\u5de6\u4e00\u53f3\u6251\u6765\uff0c\u6b63\u597d\u88ab\u98d3\u98ce\u5f53\u5934\u8fce\u4e0a\uff0c\u65e0\u53ef\u6bd4\u62df\u7684\u5e9e\u5927\u5438\u529b\u77ac\u95f4\u5c06\u5b83\u4eec\u62c9\u626f\u8fdb\u6765\u3002\u9aa8\u9f99\u53d1\u51fa\u5636\u54d1\u7684\u543c\u53eb\u58f0\uff0c\u52aa\u529b\u632f\u52a8\u80cc\u540e\u7684\u9aa8\u7ffc\u60f3\u8981\u9003\u8131\uff0c\u7136\u800c\u5b83\u4eec\u73b0\u5728\u5df2\u7ecf\u662f\u4ea1\u7075\uff0c\u80fd\u591f\u98de\u884c\u9760\u7684\u5e76\u4e0d\u662f\u7fc5\u8180\u6247\u52a8\u7a7a\u6c14\uff0c\u800c\u662f\u9b54\u6cd5\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b83\u4eec\u529b\u6c14\u518d\u5927\uff0c\u518d\u52aa\u529b\u632f\u52a8\u9aa8\u7ffc\uff0c\u5176\u5b9e\u4e5f\u53ea\u662f\u4e00\u79cd\u751f\u524d\u7684\u4e60\u60ef\uff0c\u5e76\u6ca1\u6709\u534a\u70b9\u771f\u6b63\u7684\u4f5c\u7528\u3002\u743c\u6069\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u53cc\u773c\u4e2d\u7535\u5149\u5927\u76db\uff0c\u98d3\u98ce\u4eff\u4f5b\u540c\u65f6\u53d7\u5230\u9f13\u821e\uff0c\u8f6c\u52a8\u901f\u5ea6\u548c\u62c9\u626f\u529b\u9053\u9661\u7136\u589e\u5f3a\u4e00\u500d\uff0c\u4e24\u5934\u9aa8\u9f99\u6beb\u65e0\u60ac\u5ff5\u5730\u4fbf\u88ab\u5377\u4e86\u8fdb\u53bb\u3002</p><p>\u201c\u6210\u529f\u4e86\u3002\u201d</p><p>\u4e00\u51fb\u5f97\u624b\uff0c\u743c\u6069\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u72c2\u559c\uff0c\u8fd9\u662f\u4ed6\u6700\u8fd1\u624d\u521a\u521a\u638c\u63e1\u7684\u65b0\u6cd5\u672f\uff0c\u9700\u8981\u8c03\u7528\u7b2c\u4e03\u5c42\u9b54\u7f51\u80fd\u91cf\u624d\u80fd\u5851\u6210\u7684\u201c\u64cd\u63a7\u5929\u6c14\u201d\uff0c\u53ef\u4ee5\u5728\u4e00\u6bb5\u65f6\u95f4\u5185\u6539\u53d8\u533a\u57df\u6027\u7684\u5929\u6c14\u72b6\u51b5\uff0c\u4f8b\u5982\u6674\u8f6c\u591a\u4e91\u3001\u591a\u4e91\u8f6c\u9634\u4e4b\u7c7b\uff0c\u6216\u8005\u4eba\u4e3a\u521b\u9020\u8bf8\u5982\u66b4\u96e8\u3001\u51b0\u96f9\u3001\u98d3\u98ce\u4e4b\u7c7b\u7684\u7279\u6b8a\u81ea\u7136\u666f\u8c61\uff0c\u4f46\u6301\u7eed\u65f6\u95f4\u4f1a\u975e\u5e38\u77ed\u3002\u4f5c\u4e3a\u76ee\u524d\u6240\u80fd\u638c\u63e1\u7684\u6700\u9ad8\u9636\u6cd5\u672f\uff0c\u5176\u5a01\u529b\u81ea\u7136\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u4ec5\u4ec5\u5982\u6b64\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u56f0\u4f4f\u4e24\u5934\u9aa8\u9f99\u3002\u743c\u6069\u8fd9\u6b21\u4e4b\u6240\u4ee5\u80fd\u591f\u6210\u529f\uff0c\u8fd8\u6709\u4e00\u4e2a\u975e\u5e38\u91cd\u8981\u7684\u539f\u56e0\uff0c\u5c31\u662f\u4ed6\u5728\u65bd\u6cd5\u7684\u540c\u65f6\u6fc0\u53d1\u4e86\u4f53\u5185\u8574\u542b\u7684\u96f7\u9706\u795e\u529b\u3002</p><p>\u4ece\u8fdc\u5c71\u57ce\u80d6\u57ce\u4e3b\u5f17\u96f7\u65af\u90a3\u91cc\u593a\u6765\u7684\u94a5\u5319\uff0c\u662f\u6069\u745f\u4e3b\u795e\u5409\u52d2\u4eca\u7684\u795e\u529b\u76f4\u63a5\u51dd\u805a\u800c\u6210\uff0c\u672c\u8d28\u4e0a\u5e76\u4e0d\u662f\u4e00\u4ef6\u7269\u4f53\uff0c\u53ea\u662f\u5177\u73b0\u4e3a\u8fd9\u79cd\u5f62\u6001\u800c\u5df2\u3002\u743c\u6069\u541e\u566c\u4e86\u5b83\uff0c\u4ece\u800c\u83b7\u5f97\u4e86\u4e00\u70b9\u5409\u52d2\u4eca\u795e\u529b\uff0c\u5bf9\u4e8e\u5b83\u7684\u9a7e\u9a6d\u8fd0\u7528\uff0c\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u95f4\u7684\u4e0d\u61c8\u953b\u70bc\uff0c\u4e5f\u5df2\u7ecf\u6709\u4e86\u521d\u6b65\u6210\u6548\u3002\u8981\u60f3\u51dd\u805a\u5916\u5316\uff0c\u5851\u6210\u65e0\u6570\u795e\u5668\u6295\u5f71\u6765\u653b\u51fb\u5bf9\u624b\uff0c\u6682\u65f6\u8fd8\u662f\u529e\u4e0d\u5230\u7684\u2014\u2014\u661f\u9668\u57ce\u4e2d\u5bf9\u5361\u65af\u56fe\u90a3\u6b21\uff0c\u786e\u5b9e\u662f\u6253\u51fa\u4e86\u8fd9\u4e00\u51fb\uff0c\u4f46\u82cf\u9192\u540e\u65e0\u8bba\u600e\u4e48\u8bd5\u9a8c\u90fd\u6ca1\u6cd5\u518d\u6b21\u6210\u529f\uff0c\u4e0d\u8fc7\u743c\u6069\u5374\u53d1\u73b0\uff0c\u5728\u91ca\u653e\u67d0\u4e9b\u6cd5\u672f\u7684\u65f6\u5019\uff0c\u5982\u679c\u7528\u795e\u529b\u878d\u5408\u6e17\u900f\uff0c\u4f1a\u8d77\u5230\u51fa\u4e4e\u610f\u6599\u7684\u5f3a\u5316\u6548\u679c\uff0c\u201c\u64cd\u63a7\u5929\u6c14\u201d\u5c31\u662f\u6700\u5178\u578b\u7684\u4f8b\u5b50\u3002\u731c\u6d4b\u8d77\u6765\uff0c\u5409\u52d2\u4eca\u5e94\u8be5\u548c\u5854\u6d1b\u65af\u4e00\u6837\uff0c\u90fd\u662f\u98ce\u66b4\u96f7\u9706\u4e4b\u795e\uff0c\u6240\u4ee5\u4ed6\u7684\u795e\u529b\u5bf9\u8fd9\u79cd\u7c7b\u578b\u7684\u6cd5\u672f\u6709\u989d\u5916\u589e\u5e45\u4f5c\u7528\u5427\u3002</p><p>\u5494\uff01\u5494\uff01\u5494\uff01\u5494\uff01</p><p>\u63a5\u8fde\u4e0d\u65ad\u7684\u7206\u88c2\u58f0\u4ece\u98d3\u98ce\u4e2d\u4f20\u51fa\uff0c\u90a3\u662f\u9aa8\u9f99\u7684\u9aa8\u9abc\u8eaf\u4f53\u4e0e\u5f3a\u5927\u7684\u6c14\u538b\u76f8\u5bf9\u6297\u6240\u4ea7\u751f\uff0c\u5b83\u4eec\u5728\u62fc\u547d\u6323\u624e\uff0c\u60f3\u8981\u6323\u8131\u675f\u7f1a\u3002\u743c\u6069\u7aed\u5c3d\u5168\u529b\u63a7\u5236\u7740\u98d3\u98ce\uff0c\u4e0d\u65ad\u5c06\u4f53\u5185\u7684\u96f7\u9706\u795e\u529b\u6ce8\u5165\uff0c\u4fee\u8865\u7740\u88ab\u9aa8\u9f99\u6240\u6253\u7834\u7684\u201c\u6f0f\u6d1e\u201d\uff0c\u4f46\u4ed6\u77e5\u9053\u81ea\u5df1\u575a\u6301\u4e0d\u4e86\u591a\u4e45\u3002</p><p>\u201c\u770b\u4f60\u4e86\uff0c\u827e\u5f25\u8587\uff01\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u5c06\u76ee\u5149\u8fdc\u8fdc\u6295\u5411\u90a3\u4e2a\u91d1\u53d1\u94f6\u7532\u7684\u7f8e\u4e3d\u8eab\u5f71\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u743c\u6069\u53d1\u51fa\u98d3\u98ce\u7684\u540c\u65f6\uff0c\u5929\u754c\u72ec\u89d2\u517d\u8f7d\u7740\u6885\u83f2\u65af\uff0c\u4eff\u4f5b\u4e00\u9053\u94f6\u5149\u5212\u7834\u5929\u9645\uff0c\u9ad8\u901f\u51b2\u5411\u56e0\u4e3a\u9ed1\u96fe\u88ab\u98d3\u98ce\u626b\u8361\u800c\u663e\u9732\u51fa\u8e2a\u8ff9\u7684\u82cd\u767d\u9886\u4e3b\u3002</p><p>\u201c\u5438\u80fd\u672f\uff01\u201d</p><p>\u8010\u8bfa\u76f4\u63a5\u6254\u6389\u706b\u7403\u9b54\u6756\uff0c\u62ac\u624b\u5bf9\u51c6\u6885\u83f2\u65af\u4e00\u6307\uff0c\u53d1\u51fa\u81ea\u5df1\u6240\u638c\u63e1\u7684\u6700\u5f3a\u6cd5\u672f\u3002\u8fd9\u662f\u8010\u745f\u5927\u5965\u672f\u5e08\u62c9\u6c83\u514b\u7684\u5f97\u610f\u53d1\u660e\uff0c\u80fd\u591f\u76f4\u63a5\u5438\u53d6\u76ee\u6807\u7684\u529b\u91cf\uff0c\u5e76\u4e14\u4e34\u65f6\u8f6c\u5316\u4e3a\u81ea\u5df1\u6240\u6709\uff0c\u81f3\u4e8e\u5177\u4f53\u80fd\u591f\u5438\u6536\u591a\u5c11\uff0c\u8f6c\u5316\u591a\u5c11\uff0c\u90a3\u5c31\u8981\u770b\u65bd\u6cd5\u8005\u7684\u672c\u9886\u4e86\u3002\u8fd9\u9053\u6cd5\u672f\u5728\u5386\u53f2\u4e0a\u7684\u6700\u5f3a\u6218\u7ee9\u662f\u62c9\u6c83\u514b\u672c\u4eba\u521b\u9020\u7684\uff0c\u5728\u4e00\u6b21\u51b3\u6597\u4e2d\u7528\u5b83\u76f4\u63a5\u5c06\u5bf9\u624b\uff08\u4e00\u540d\u65b0\u664b\u5927\u5965\u672f\u5e08\uff09\u6253\u56de\u5230\u76f8\u5f53\u4e8e\u743c\u6069\u76ee\u524d\u7684\u6c34\u51c6\uff0c\u81ea\u5df1\u5219\u5728\u516d\u5206\u949f\u5185\u6240\u6709\u9b54\u6cd5\u90fd\u6548\u679c\u7ffb\u500d\u3002\u8010\u8bfa\u81ea\u7136\u6ca1\u8fd9\u4e48\u5f3a\uff0c\u4f46\u4ed6\u76f8\u4fe1\u8fd9\u4e00\u51fb\u53d1\u51fa\uff0c\u81f3\u5c11\u4e5f\u80fd\u524a\u5f31\u6389\u5bf9\u65b9\u4e09\u5206\u4e4b\u4e00\u751a\u81f3\u4e00\u534a\u7684\u5b9e\u529b\u5427\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\u4ed6\u9519\u4e86\u3002</p><p>\u5438\u80fd\u672f\u7cbe\u51c6\u5730\u547d\u4e2d\u4e86\u76ee\u6807\uff0c\u4f46\u5374\u88ab\u5c11\u5973\u8eab\u4f53\u8868\u9762\u4e00\u5c42\u65e0\u5f62\u5c4f\u969c\u76f4\u63a5\u5f39\u5f00\uff0c\u6ca1\u6709\u8d77\u5230\u534a\u70b9\u4f5c\u7528\u3002\u8010\u8bfa\u5927\u5403\u4e00\u60ca\uff0c\u786e\u5b9e\u548c\u5f88\u591a\u4ea1\u7075\u672f\u4e00\u6837\uff0c\u8eab\u4f53\u975e\u5e38\u5f3a\u5065\u7684\u4eba\u662f\u6709\u53ef\u80fd\u786c\u751f\u751f\u625b\u4e0b\u5438\u80fd\u672f\uff0c\u4f46\u5c31\u7b97\u5982\u6b64\u4e5f\u8fd8\u662f\u4f1a\u53d7\u4e00\u5b9a\u4f24\u5bb3\u7684\uff0c\u4e0d\u53ef\u80fd\u5b8c\u5168\u6beb\u53d1\u65e0\u635f\u3002</p><p>\u201c\u6b7b\u4ea1\u4e00\u6307\uff01\u201d</p><p>\u201c\u9ad8\u7b49\u964d\u5492\uff01\u201d</p><p>\u201c\u8c03\u6b7b\u672f\uff01\u201d</p><p>\u60c5\u6025\u4e4b\u4e0b\uff0c\u82cd\u767d\u9886\u4e3b\u5168\u529b\u65bd\u5c55\uff0c\u501f\u52a9\u4ed6\u6240\u4f69\u6234\u7684\u201c\u77ac\u53d1\u6307\u73af\u201d\uff0c\u4e09\u9053\u5a01\u529b\u65e0\u6bd4\u7684\u4ea1\u7075\u672f\u51e0\u4e4e\u662f\u540c\u65f6\u88ab\u91ca\u653e\u51fa\u6765\uff0c\u5e9e\u5927\u5230\u6781\u81f4\u7684\u8d1f\u80fd\u91cf\u5df2\u7ecf\u8fd1\u4e4e\u51dd\u805a\u6210\u5b9e\u4f53\uff0c\u4eff\u4f5b\u4e00\u6761\u9ed1\u9f99\u5f20\u7259\u821e\u722a\u5730\u671d\u6885\u83f2\u65af\u6251\u53bb\u3002</p><p>\u7136\u540e\u5728\u4ea1\u7075\u5e08\u7684\u60ca\u9a87\u76ee\u5149\u4e2d\u88ab\u5f39\u5f00\uff0c\u9707\u6563\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u201c\u5929\u8d4b\u9b54\u6297\u2026\u2026\u4e0d\u5bf9\uff0c\u8fd9\u7b80\u76f4\u662f\u9b54\u6cd5\u514d\u75ab\u4e86\uff01\u201d</p><p>\u63a5\u8fde\u56db\u9053\u5f3a\u5927\u7684\u4ea1\u7075\u672f\uff0c\u5176\u4e2d\u5438\u80fd\u672f\u5df2\u7ecf\u662f\u4ea1\u7075\u672f\u4e2d\u7684\u5dc5\u5cf0\uff0c\u76f8\u5f53\u4e8e\u65f6\u95f4\u505c\u6b62\u3001\u5927\u88c2\u89e3\u5728\u5404\u81ea\u5b66\u6d3e\u4e2d\u7684\u5730\u4f4d\uff0c\u9ad8\u7b49\u964d\u5492\u548c\u8c03\u6b7b\u672f\u4ec5\u4ec5\u7565\u900a\u4e00\u7b79\uff0c\u5373\u4fbf\u662f\u6700\u5f31\u7684\u6b7b\u4ea1\u4e00\u6307\uff0c\u8c03\u7528\u7684\u4e5f\u662f\u9b54\u7f51\u7b2c\u4e03\u5c42\u80fd\u91cf\uff0c\u800c\u4e14\u82cd\u767d\u9886\u4e3b\u7684\u7279\u6027\u8fd8\u5bf9\u5b83\u6709\u989d\u5916\u7684\u5f3a\u5316\u4f5c\u7528\u3002\u5b83\u4eec\u7684\u4f5c\u7528\u5f62\u5f0f\u4e5f\u5404\u6709\u533a\u522b\uff0c\u6709\u76f4\u63a5\u706d\u6740\u7684\uff0c\u6709\u95f4\u63a5\u81f4\u547d\u7684\uff0c\u6709\u8bc5\u5492\u524a\u5f31\u7684\uff0c\u6709\u7684\u8003\u9a8c\u610f\u5fd7\uff0c\u6709\u7684\u4fb5\u88ad\u4f53\u9b44\uff0c\u6709\u7684\u76f4\u63a5\u653b\u51fb\u7075\u9b42\u2014\u2014\u7136\u800c\u5b83\u4eec\u5168\u90fd\u65e0\u4e00\u4f8b\u5916\u5730\u5931\u8d25\u4e86\u3002\u5c31\u7b97\u662f\u50cf\u743c\u6069\u90a3\u6837\u501f\u52a9\u5f71\u706b\uff0c\u4e5f\u662f\u7edd\u5bf9\u505a\u4e0d\u5230\u8fd9\u70b9\u7684\u3002</p><p>\u4f1a\u51fa\u73b0\u8fd9\u79cd\u6548\u679c\uff0c\u53ea\u6709\u4e00\u79cd\u53ef\u80fd\u6027\uff0c\u4fbf\u662f\u8fd9\u4f4d\u82f1\u6c14\u903c\u4eba\u7684\u7f8e\u4e3d\u5c11\u5973\u6709\u7c7b\u4f3c\u4e8e\u5353\u5c14\u90a3\u79cd\u5929\u8d4b\u6297\u9b54\u529b\uff0c\u6709\u6982\u7387\u5c06\u4e00\u5207\u6cd5\u672f\u65e0\u6548\u5316\u2014\u2014\u800c\u4e14\u8fd9\u79cd\u6297\u9b54\u529b\u5e94\u8be5\u662f\u975e\u5e38\u975e\u5e38\u4e4b\u9ad8\uff0c\u9ad8\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u5730\u6b65\uff0c\u53ea\u6015\u4e0a\u53e4\u5de8\u9f99\u4e5f\u4e0d\u8fc7\u5982\u6b64\u4e86\u3002</p><p>\u907f\u8ba9\uff01</p><p>\u77ac\u606f\u4e4b\u95f4\uff0c\u4ea1\u7075\u5e08\u7acb\u523b\u505a\u51fa\u4e86\u6b63\u786e\u5224\u65ad\u3002\u5bf9\u65b9\u65e2\u7136\u6709\u5982\u6b64\u5f3a\u608d\u7684\u6297\u9b54\u529b\uff0c\u7b80\u76f4\u5c31\u662f\u4e00\u5207\u5deb\u5e08\u7684\u68a6\u9b47\uff0c\u6b63\u9762\u5bf9\u6297\u80af\u5b9a\u662f\u6b7b\u8def\u4e00\u6761\uff0c\u53ea\u6709\u5148\u907f\u5176\u950b\u8292\uff0c\u518d\u4f3a\u673a\u53cd\u6251\u3002\u53cd\u6b63\u9664\u4e86\u76f4\u63a5\u653b\u51fb\uff0c\u4ed6\u8fd8\u53ef\u4ee5\u53ec\u5524\u602a\u7269\uff0c\u5927\u4e0d\u4e86\u7528\u65e0\u7a77\u65e0\u5c3d\u7684\u70ae\u7070\u628a\u5bf9\u65b9\u5806\u6b7b\u4fbf\u662f\u4e86\u3002</p><p>\u94f6\u8272\u4f20\u9001\u95e8\u6b64\u8d77\u5f7c\u4f0f\u5730\u4e0d\u65ad\u5728\u7a7a\u4e2d\u5212\u5f00\uff0c\u4ea1\u7075\u5e08\u63a5\u8fde\u53d8\u6362\u65b9\u4f4d\uff0c\u8eb2\u907f\u6885\u83f2\u65af\u7684\u88ad\u51fb\uff0c\u540c\u65f6\u4e89\u53d6\u7a7a\u9699\u53ec\u5524\u602a\u7269\u3002\u9732\u4e1d\u96c5\u867d\u7136\u98de\u884c\u5982\u7535\uff0c\u7ec8\u7a76\u6bd4\u4e0d\u4e0a\u9b54\u6cd5\u4f20\u9001\u7684\u5feb\u6377\uff0c\u6bcf\u6b21\u90fd\u6251\u4e86\u4e2a\u7a7a\u3002\u65f6\u95f4\u98de\u901d\uff0c\u8f6c\u773c\u95f4\u5df2\u7ecf\u8fc7\u53bb\u4e86\u4e03\u516b\u79d2\u949f\uff0c\u6885\u83f2\u65af\u4f9d\u7136\u8fd8\u672a\u80fd\u63a5\u8fd1\u654c\u4eba\uff0c\u53ea\u5e72\u6389\u4e86\u51e0\u53ea\u53ec\u5524\u602a\u7269\uff0c\u7136\u800c\u5979\u6beb\u65e0\u7126\u6025\u4e4b\u8272\uff0c\u53cd\u800c\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\u3002</p><p>\u90a3\u662f\u80dc\u5238\u5728\u63e1\u7684\u7b11\u610f\u3002</p><p>\u201c\u7834\u90aa\uff01\u201d</p><p>\u968f\u7740\u5c11\u5973\u7684\u5a07\u53f1\uff0c\u707f\u70c2\u593a\u76ee\u7684\u5149\u8292\u4ece\u5251\u5203\u4e2d\u5954\u6d8c\u800c\u51fa\uff0c\u50cf\u6d41\u661f\u822c\u8f70\u7136\u5c04\u5411\u4ea1\u7075\u5e08\u3002\u8010\u8bfa\u65e9\u6709\u63d0\u9632\uff0c\u4e00\u89c1\u6885\u83f2\u65af\u5251\u4e0a\u653e\u5149\uff0c\u7acb\u523b\u8eab\u5f62\u4e00\u6643\uff0c\u4ece\u7a7a\u6c14\u4e2d\u51ed\u7a7a\u6d88\u5931\uff0c\u4e0b\u4e00\u77ac\u95f4\u4ece\u5de6\u4fa7\u4e09\u5341\u5c3a\u5916\u7684\u4e00\u6247\u4f20\u9001\u95e8\u4e2d\u8d70\u51fa\uff0c\u4ed6\u6025\u901f\u62ac\u4e86\u62ac\u624b\u6307\uff0c\u53ec\u5524\u51fa\u4e24\u53ea\u7ffc\u9f99\uff0c\u6b63\u8981\u547d\u4ee4\u5b83\u4eec\u53bb\u653b\u51fb\u524d\u65b9\u7684\u5bf9\u624b\uff0c\u7136\u540e\u4ed6\u6115\u7136\u53d1\u73b0\u5c11\u5973\u7684\u8eab\u5f71\u4e0d\u77e5\u4f55\u65f6\u4ece\u89c6\u91ce\u4e2d\u6d88\u5931\u4e86\u3002</p><p>\u51b0\u5bd2\u523a\u9aa8\u7684\u5371\u673a\u611f\u5239\u90a3\u95f4\u7b3c\u7f69\u5fc3\u5934\uff0c\u4ee4\u4ed6\u7684\u4ea1\u7075\u8eaf\u4f53\u90fd\u7981\u4e0d\u4f4f\u6218\u6817\u8d77\u6765\u3002\u5b8c\u5168\u662f\u4e0b\u610f\u8bc6\u5730\uff0c\u8010\u8bfa\u518d\u5ea6\u53d1\u52a8\u4f20\u9001\uff0c\u4ed6\u4e0d\u77e5\u9053\u8981\u4f20\u9001\u5230\u4f55\u5904\uff0c\u53ea\u662f\u672c\u80fd\u5730\u77e5\u9053\u8981\u9003\u79bb\u5f53\u524d\u7684\u4f4d\u7f6e\u3002</p><p>\u7136\u800c\u5df2\u7ecf\u592a\u8fdf\u4e86\u3002</p><p>\u4e00\u67c4\u70ab\u76ee\u7684\u94f6\u5251\u81ea\u80cc\u540e\u523a\u5165\uff0c\u4ece\u80f8\u53e3\u900f\u51fa\uff0c\u5c06\u4ea1\u7075\u5e08\u751f\u751f\u201c\u9489\u201d\u4f4f\u3002\u201c\u6b7b\u5427\uff01\u201d\u5c11\u5973\u4f4e\u559d\uff0c\u795e\u5723\u7684\u767d\u5149\u4ece\u5251\u4e0a\u7efd\u653e\u51fa\u6765\uff0c\u8f70\u7136\u56db\u5c04\u3002\u4ea1\u7075\u5e08\u53d1\u51fa\u51c4\u5389\u7684\u568e\u53eb\u58f0\uff0c\u626d\u66f2\u6323\u624e\u7740\uff0c\u60f3\u8981\u9003\u8131\uff0c\u4f46\u8fd9\u662f\u5f92\u52b3\u7684\u3002\u534a\u79d2\u949f\u540e\uff0c\u4ed6\u90a3\u5177\u7ecf\u8fc7\u65e0\u6570\u9b54\u6cd5\u5f3a\u5316\uff0c\u8db3\u4ee5\u5ab2\u7f8e\u7cbe\u91d1\u7684\u534a\u8840\u8089\u534a\u4ea1\u7075\u8eaf\u4f53\u5728\u5929\u5802\u5723\u5149\u7167\u8000\u4e0b\u5316\u4f5c\u7247\u7247\u98de\u7070\uff0c\u968f\u98ce\u6563\u53bb\u3002</p><p>\u76f4\u5230\u610f\u8bc6\u6d88\u6563\u7684\u6700\u540e\u5239\u90a3\uff0c\u4ea1\u7075\u5e08\u4e5f\u4f9d\u7136\u6ca1\u6709\u660e\u767d\uff0c\u6885\u83f2\u65af\u662f\u600e\u4e48\u5230\u4e86\u81ea\u5df1\u8eab\u540e\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u4f20\u9001\u9b54\u6cd5\uff0c\u800c\u4e14\u5e76\u4e0d\u7b97\u7f55\u89c1\uff0c\u5deb\u5e08\u4f1a\uff0c\u67d0\u4e9b\u7267\u5e08\u4e5f\u4f1a\uff0c\u751a\u81f3\u5f88\u591a\u975e\u65bd\u6cd5\u8005\u90fd\u53ef\u80fd\u501f\u52a9\u9b54\u6cd5\u7269\u54c1\u5b8c\u6210\u4f20\u9001\uff0c\u4f8b\u5982\u5b9d\u77f3\u8df3\u8dc3\u6212\u6307\u3002\u79cd\u7c7b\u4e5f\u5f88\u591a\uff0c\u5305\u62ec\u6700\u5e38\u89c1\u7684\u4f20\u9001\u672f\u548c\u4efb\u610f\u95e8\uff0c\u5305\u62ec\u4ea1\u7075\u5e08\u521a\u624d\u4f7f\u7528\u7684\u201c\u9732\u5179\u5b89\u4e4b\u9891\u7e41\u79fb\u52a8\u201d\uff0c\u5305\u62ec\u51db\u64c5\u957f\u7684\u706b\u4e2d\u8df3\u8dc3\uff0c\u7b49\u7b49\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u65e0\u8bba\u54ea\u4e00\u79cd\u4f20\u9001\uff0c\u65e0\u8bba\u7528\u4ec0\u4e48\u65b9\u5f0f\u77ac\u79fb\uff0c\u672c\u8d28\u4e0a\u90fd\u662f\u9b54\u6cd5\u7684\u4f5c\u7528\u7ed3\u679c\uff0c\u90fd\u4f1a\u5f3a\u70c8\u5730\u5e72\u6d89\u9b54\u7f51\uff0c\u5f15\u53d1\u5927\u5e45\u5ea6\u7684\u7075\u529b\u6ce2\u52a8\uff0c\u9707\u8361\u7a7a\u6c14\uff0c\u4ece\u800c\u88ab\u9ad8\u660e\u7684\u5deb\u5e08\u6216\u8005\u7ecf\u9a8c\u4e30\u5bcc\u7684\u5192\u9669\u8005\u6240\u5bdf\u89c9\u3002\u6b64\u524d\u743c\u6069\u53c2\u6218\u65f6\uff0c\u4f20\u9001\u5230\u4ea1\u7075\u5e08\u6240\u85cf\u8eab\u7684\u6d1e\u7a9f\uff0c\u4fbf\u662f\u7b2c\u4e00\u65f6\u95f4\u88ab\u8010\u8bfa\u53d1\u73b0\u4e86\u3002</p><p>\u7136\u800c\u6885\u83f2\u65af\u77ac\u79fb\u5230\u8eab\u540e\uff0c\u4ed6\u5374\u534a\u70b9\u6ca1\u6709\u5bdf\u89c9\u3002\u8fd9\u53ea\u6709\u4e24\u79cd\u53ef\u80fd\uff0c\u6216\u8005\u6885\u83f2\u65af\u662f\u9b54\u6cd5\u9020\u8be3\u80dc\u8fc7\u4ed6\u5341\u500d\u7684\u5927\u5deb\u5e08\uff0c\u5f3a\u884c\u62b9\u6d88\u4e86\u4f20\u9001\u6240\u5f15\u53d1\u7684\u6ce2\u52a8\uff1b\u6216\u8005\uff0c\u5979\u4f7f\u7528\u7684\u6839\u672c\u5c31\u4e0d\u662f\u9b54\u6cd5\u2014\u2014\u7136\u800c\u4e0d\u7528\u9b54\u6cd5\u53c8\u5982\u4f55\u5b9e\u73b0\u77ac\u79fb\uff1f\u9000\u4e00\u6b65\u8bf4\uff0c\u5373\u4fbf\u6885\u83f2\u65af\u5176\u5b9e\u662f\u53ea\u7075\u5438\u602a\uff0c\u4f7f\u7528\u4f20\u8bf4\u4e2d\u7684\u7075\u80fd\uff0c\u4e0d\u9700\u8981\u501f\u52a9\u9b54\u7f51\uff0c\u4f46\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\u51fa\u73b0\u5728\u8eab\u540e\uff0c\u8010\u8bfa\u603b\u8be5\u6709\u6240\u5bdf\u89c9\u624d\u5bf9\uff0c\u4ed6\u662f\u5df2\u7ecf\u534a\u4ea1\u7075\u5316\u7684\u4ea1\u7075\u5e08\uff0c\u5bf9\u751f\u8005\u7684\u6c14\u606f\u6781\u4e3a\u654f\u611f\uff0c\u4e0d\u5e94\u8be5\u7b49\u5230\u5251\u90fd\u63d2\u8fdb\u80f8\u819b\u4e86\u624d\u9192\u609f\u3002\u9664\u975e\u662f\u4e16\u754c\u4e0a\u6700\u9876\u7ea7\u7684\u523a\u5ba2\uff0c\u624d\u53ef\u80fd\u8fd9\u6837\u65e0\u58f0\u65e0\u606f\u5730\u63a5\u8fd1\u4ed6\u5427\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u7684\u60f3\u6cd5\u5e76\u6ca1\u6709\u9519\u3002\u56e0\u4e3a\u6b64\u65f6\u6b64\u523b\uff0c\u6885\u83f2\u65af\u4fbf\u662f\u4e16\u754c\u4e0a\u6700\u9876\u7ea7\u7684\u523a\u5ba2\u3002</p></article>"}}}]);
