(window.webpackJsonp=window.webpackJsonp||[]).push([[1691],{1714:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u610f\u5916",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u610f\u5916</h2><p>\u5728\u6885\u83f2\u65af\u6210\u529f\u65a9\u6740\u82cd\u767d\u9886\u4e3b\u7684\u90a3\u4e00\u523b\uff0c\u743c\u6069\u7684\u52aa\u529b\u7ec8\u4e8e\u5230\u4e86\u6700\u540e\u6781\u9650\u3002</p><p>\u501f\u52a9\u96f7\u9706\u795e\u529b\u7684\u5f3a\u5316\uff0c\u98d3\u98ce\u6210\u529f\u5730\u56f0\u4f4f\u4e86\u9aa8\u9f99\uff0c\u4f46\u8fd9\u53ea\u662f\u6682\u65f6\u7684\u3002\u201c\u64cd\u63a7\u5929\u6c14\u201d\u6240\u521b\u9020\u7684\u98d3\u98ce\u539f\u672c\u5c31\u53ea\u80fd\u6301\u7eed\u7247\u523b\uff0c\u800c\u9aa8\u9f99\u7684\u6323\u624e\u53cd\u6297\u8fdb\u4e00\u6b65\u7f29\u77ed\u4e86\u8fd9\u4e2a\u65f6\u95f4\u3002\u743c\u6069\u6700\u521d\u9884\u8ba1\u662f\u80fd\u575a\u6301\u5341\u4e94\u79d2\u949f\uff0c\u5df2\u7ecf\u662f\u6bd4\u8f83\u4fdd\u5b88\u7684\u4f30\u8ba1\u4e86\uff0c\u73b0\u5728\u770b\u8d77\u6765\uff0c\u8fd9\u4e2a\u6570\u5b57\u8fd8\u8981\u518d\u6253\u4e2a\u6298\u6263\u3002</p><p>\u201c\u5341\u4e94\u79d2\u90fd\u4e0d\u80fd\u575a\u6301\uff0c\u8fd9\u5206\u660e\u662f\u65e9\u6cc4\u554a\u2026\u2026\u65e0\u6240\u8c13\u4e86\uff0c\u80fd\u6491\u8fc7\u5341\u79d2\u5c31\u884c\u3002\u201d</p><p>\u6885\u83f2\u65af\u8bf4\u8fc7\uff0c\u53ea\u8981\u5341\u79d2\u949f\u5979\u5c31\u80fd\u5e72\u6389\u82cd\u767d\u9886\u4e3b\u3002\u867d\u7136\u743c\u6069\u521a\u624d\u62bd\u7a7a\u770b\u4e86\u4e00\u773c\uff0c\u53d1\u73b0\u4e24\u4eba\u8fd8\u5728\u8ffd\u9010\u6218\u5f53\u4e2d\u7ea0\u7f20\uff0c\u6885\u83f2\u65af\u6ca1\u6709\u534a\u70b9\u8981\u53d6\u80dc\u7684\u8ff9\u8c61\uff0c\u4f46\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u3002\u65e2\u7136\u5979\u8bf4\u5341\u79d2\u949f\u80fd\u641e\u5b9a\uff0c\u90a3\u4e48\u5c31\u4e0d\u4f1a\u6709\u95ee\u9898\uff0c\u81ea\u5df1\u6240\u9700\u8981\u505a\u7684\uff0c\u4fbf\u662f\u7aed\u5c3d\u5168\u529b\u6321\u4f4f\u8fd9\u4e24\u5934\u9aa8\u9f99\uff0c\u81f3\u5c11\u6321\u4f4f\u5341\u79d2\u949f\u3002</p><p>\u201c\u2026\u2026\u4e94\u3001\u516d\u3001\u4e03\u3001\u516b\u2026\u2026\u201d</p><p>\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5df2\u7ecf\u6709\u4e86\u949f\u8868\uff0c\u4e00\u540d\u5ca9\u4f8f\u5112\u673a\u68b0\u5e08\u53d1\u660e\u7684\uff0c\u4f46\u975e\u5e38\u7c97\u7cd9\uff0c\u5f88\u4e0d\u7cbe\u51c6\uff0c\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u548c\u5de5\u5320\u4eec\u6b63\u5728\u52aa\u529b\u8bd5\u56fe\u6539\u8fdb\uff0c\u8fd8\u6ca1\u6709\u5e7f\u6cdb\u63a8\u884c\u3002\u4f46\u5bf9\u4e8e\u7ecf\u8fc7\u6b63\u89c4\u8bad\u7ec3\u7684\u5deb\u5e08\u6765\u8bf4\uff0c\u5fc3\u7b97\u6d4b\u79d2\u53ea\u662f\u57fa\u672c\u529f\u4e2d\u7684\u57fa\u672c\u529f\uff0c\u7edd\u4e0d\u4f1a\u6709\u534a\u70b9\u504f\u5dee\u3002</p><p>\u773c\u770b\u76ee\u6807\u5373\u5c06\u8fbe\u6210\uff0c\u4e24\u5934\u9aa8\u9f99\u9661\u7136\u95f4\u66b4\u543c\u8d77\u6765\uff0c\u62fc\u547d\u6323\u624e\uff0c\u539f\u672c\u6210\u578b\u7684\u98d3\u98ce\u987f\u65f6\u88ab\u6405\u5f97\u5343\u75ae\u767e\u5b54\uff0c\u773c\u770b\u5c31\u8981\u5d29\u6e83\u3002\u743c\u6069\u53ea\u89c9\u53cc\u81c2\u4e0a\u4f20\u6765\u7684\u538b\u529b\u9661\u7136\u52a0\u5927\uff0c\u60c5\u77e5\u518d\u4e5f\u65e0\u6cd5\u7ef4\u6301\uff0c\u4ed6\u5f53\u673a\u7acb\u65ad\uff0c\u79ef\u6512\u8d77\u4f53\u5185\u6b8b\u5b58\u7684\u6700\u540e\u5168\u90e8\u96f7\u9706\u795e\u529b\uff0c\u5168\u90e8\u704c\u6ce8\u5230\u98d3\u98ce\u4e4b\u4e2d\uff0c\u7136\u540e\u5f3a\u884c\u5207\u65ad\u4e86\u6cd5\u672f\u8054\u7ed3\u3002</p><p>\u5927\u7206\u70b8\u53d1\u751f\u4e86\u3002</p><p>\u523a\u773c\u7729\u76ee\u7684\u5f3a\u5149\u4ece\u5185\u90e8\u900f\u51fa\uff0c\u743c\u6069\u6ce8\u5165\u7684\u96f7\u9706\u795e\u529b\u5316\u4f5c\u65e0\u6570\u9053\u7ea4\u7ec6\u94f6\u4e1d\uff0c\u5728\u6c14\u65cb\u4e2d\u7eb5\u6a2a\u6e38\u8d70\uff0c\u539f\u672c\u5728\u9ad8\u901f\u65cb\u8f6c\u7684\u98d3\u98ce\u9661\u7136\u95f4\u987f\u4f4f\uff0c\u50cf\u662f\u65f6\u95f4\u5239\u90a3\u505c\u6ede\u4e00\u822c\uff0c\u7136\u540e\u5728\u4e00\u58f0\u9707\u8033\u6b32\u804b\u7684\u5de8\u54cd\u4e2d\u5f7b\u5e95\u5d29\u6e83\uff0c\u5316\u4f5c\u731b\u70c8\u7684\u6c14\u6d41\u56db\u6563\u5439\u5f00\u3002\u4e24\u5934\u9aa8\u9f99\u7ec8\u4e8e\u4ece\u4e2d\u8131\u56f0\u800c\u51fa\uff0c\u4f46\u5b83\u4eec\u7684\u6a21\u6837\u975e\u5e38\u72fc\u72c8\uff0c\u5176\u4e2d\u4e00\u5934\u88ab\u70b8\u788e\u4e86\u5de6\u524d\u722a\u548c\u6574\u4e2a\u5c3e\u9aa8\uff0c\u53e6\u5916\u4e00\u5934\u5219\u662f\u5c0f\u534a\u4e2a\u5934\u9aa8\u5b8c\u5168\u7c89\u788e\uff0c\u9aa8\u9abc\u8eaf\u4f53\u4e0a\u4e5f\u5230\u5904\u90fd\u662f\u70b8\u88c2\u7684\u4f24\u53e3\uff0c\u6df1\u6d45\u4e0d\u4e00\u3002</p><p>\u743c\u6069\u878d\u5408\u4e86\u5409\u52d2\u4eca\u7684\u795e\u529b\uff0c\u4f46\u8fd9\u53ea\u662f\u6539\u53d8\u4e86\u6240\u6709\u6743\uff0c\u5e76\u4e0d\u80fd\u6539\u53d8\u5176\u6027\u8d28\u3002\u8fd9\u5c31\u50cf\u662f\u4e00\u679a\u91d1\u5e01\uff0c\u5370\u8bb0\u88ab\u62b9\u6d88\u4e86\uff0c\u751a\u81f3\u53ef\u80fd\u56de\u7089\u91cd\u94f8\uff0c\u53d8\u6210\u5176\u4ed6\u5f62\u72b6\uff0c\u4f46\u9ec4\u91d1\u8fd8\u662f\u9ec4\u91d1\uff0c\u4e0d\u4f1a\u56e0\u6b64\u53d8\u6210\u767d\u94f6\u9752\u94dc\uff0c\u57fa\u672c\u5c5e\u6027\u4f9d\u65e7\u4fdd\u7559\u3002\u8fd9\u4e5f\u662f\u4ed6\u4f1a\u53d8\u8eab\u6210\u6740\u622e\u8005\u7684\u539f\u56e0\uff0c\u541e\u566c\u7684\u90a3\u4e9b\u6740\u622e\u795e\u529b\uff0c\u6240\u6709\u6743\u662f\u6539\u53d8\u4e86\uff0c\u5df4\u5c14\u7684\u795e\u6027\u70d9\u5370\u88ab\u62b9\u6d88\u4e86\uff0c\u4f46\u795e\u529b\u7684\u672c\u8d28\u8fd8\u662f\u6ca1\u6709\u53d8\u7684\u3002\u96f7\u9706\u795e\u529b\u5c31\u5177\u6709\u201c\u7206\u88c2\u201d\u7684\u5c5e\u6027\uff0c\u4e00\u65e6\u8131\u79bb\u63a7\u5236\uff0c\u5b83\u4fbf\u4f1a\u81ea\u884c\u81a8\u80c0\u3001\u6269\u5f20\uff0c\u7136\u540e\u5f15\u53d1\u8d85\u4e4e\u60f3\u50cf\u7684\u7206\u70b8\u3002\u743c\u6069\u6b63\u662f\u501f\u52a9\u8fd9\u70b9\uff0c\u5c06\u795e\u529b\u6ce8\u5165\u98d3\u98ce\u4e2d\uff0c\u4ece\u800c\u7ed9\u4e24\u5934\u9aa8\u9f99\u9020\u6210\u4e86\u5de8\u5927\u7684\u4f24\u5bb3\u3002</p><p>\u552f\u4e00\u7684\u95ee\u9898\u5728\u4e8e\uff0c\u4f5c\u4e3a\u65bd\u6cd5\u8005\uff0c\u8fd9\u6837\u5f3a\u884c\u5207\u65ad\u6cd5\u672f\u8054\u7ed3\uff0c\u81ea\u8eab\u4e5f\u4f1a\u906d\u5230\u5f3a\u70c8\u7684\u53cd\u566c\u3002\u5c31\u5728\u98d3\u98ce\u5d29\u6e83\u7684\u540c\u65f6\uff0c\u743c\u6069\u7684\u80f8\u53e3\u4eff\u4f5b\u88ab\u91cd\u91cd\u7838\u4e86\u4e00\u9524\uff0c\u5267\u75db\u51e0\u4e4e\u4ee4\u4ed6\u7a92\u606f\uff0c\u98de\u884c\u672f\u518d\u4e5f\u7ef4\u6301\u4e0d\u4f4f\uff0c\u767b\u65f6\u4fbf\u5f80\u4e0b\u6454\u843d\u3002</p><p>\u4ed6\u6b64\u65f6\u8eab\u5904\u8fd1\u5343\u5c3a\u7684\u9ad8\u7a7a\uff0c\u771f\u8981\u6454\u5230\u5730\u4e0a\uff0c\u552f\u4e00\u7684\u4e0b\u573a\u5c31\u662f\u7c89\u8eab\u788e\u9aa8\u3002\u56e0\u4e3a\u6cd5\u672f\u88ab\u5f3a\u884c\u6253\u7834\uff0c\u743c\u6069\u4f5c\u4e3a\u65bd\u6cd5\u8005\u81ea\u8eab\u4e5f\u906d\u5230\u53cd\u566c\uff0c\u8fde\u610f\u8bc6\u90fd\u6709\u4e9b\u6da3\u6563\uff0c\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u518d\u5ea6\u65bd\u5c55\u9b54\u6cd5\u6551\u81ea\u5df1\uff0c\u4e0d\u8fc7\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u3002</p><p>\u56e0\u4e3a\u5341\u79d2\u949f\u5df2\u7ecf\u8fc7\u4e86\u3002</p><p>\u4e0b\u5760\u7684\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\uff0c\u51cc\u5389\u7684\u5f3a\u98ce\u5df2\u7ecf\u538b\u8feb\u5f97\u4ed6\u65e0\u6cd5\u547c\u5438\uff0c\u53ea\u770b\u89c1\u5730\u9762\u5728\u9ad8\u901f\u5411\u81ea\u5df1\u63a5\u8fd1\uff0c\u4eff\u4f5b\u4e0b\u4e00\u79d2\u949f\u5c31\u4f1a\u771f\u6b63\u649e\u4e0a\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u743c\u6069\u8ddd\u79bb\u5730\u9762\u5df2\u7ecf\u4e0d\u5230\u4e94\u5341\u82f1\u5c3a\uff0c\u7136\u540e\u4e00\u9053\u94f6\u5149\u95ea\u7535\u822c\u63a0\u8fc7\uff0c\u5c06\u4ed6\u6551\u4e86\u8d77\u6765\u3002</p><p>\u201c\u6ca1\u4e8b\u5427\u3002\u201d\u5c11\u5973\u5173\u5207\u5730\u95ee\u3002</p><p>\u201c\u8fd8\u597d\u3002\u201d</p><p>\u743c\u6069\u5b9a\u4e86\u5b9a\u795e\uff0c\u52c9\u5f3a\u56de\u7b54\uff0c\u4ed6\u7684\u80f8\u53e3\u6c14\u8840\u7ffb\u817e\uff0c\u773c\u524d\u8fd8\u662f\u4e00\u9635\u9635\u53d1\u9ed1\uff0c\u5589\u5934\u6709\u79cd\u8165\u751c\u7684\u611f\u89c9\uff0c\u96be\u53d7\u4e4b\u6781\u3002\u6885\u83f2\u65af\u6709\u4e9b\u62c5\u5fc3\uff0c\u4f46\u4e0d\u6562\u518d\u591a\u8bf4\uff0c\u56e0\u4e3a\u4e24\u5934\u9aa8\u9f99\u53c8\u5df2\u7ecf\u5982\u5f71\u968f\u5f62\u5730\u6251\u4e86\u4e0a\u6765\u3002</p><p>\u4e27\u5931\u4e86\u6307\u6325\u8005\u7684\u9aa8\u9f99\u66f4\u52a0\u51f6\u66b4\uff0c\u4f46\u6bd5\u7adf\u53ea\u662f\u4f9d\u636e\u672c\u80fd\u884c\u4e8b\uff0c\u6ca1\u6709\u4efb\u4f55\u8ba1\u5212\u548c\u6218\u672f\u53ef\u8a00\uff0c\u5e94\u4ed8\u8d77\u6765\u8981\u8f7b\u677e\u5f97\u591a\u3002\u63a5\u8fde\u4e09\u6b21\u8f6c\u6298\uff0c\u9732\u4e1d\u96c5\u4fbf\u6210\u529f\u51b2\u51fa\u4e86\u5305\u56f4\u5708\uff0c\u5c06\u4e24\u5934\u9aa8\u9f99\u8fdc\u8fdc\u629b\u5f00\u3002\u743c\u6069\u6162\u6162\u7f13\u8fc7\u52b2\u6765\uff0c\u4f38\u624b\u4ece\u80cc\u540e\u62b1\u7740\u6885\u83f2\u65af\uff0c\u610f\u5916\u5730\u53d1\u73b0\u5979\u7684\u5a07\u8eaf\u5728\u5fae\u5fae\u98a4\u6296\uff0c\u50cf\u662f\u6253\u5bd2\u6218\u4e00\u822c\uff0c\u91d1\u53d1\u88ab\u72c2\u98ce\u5439\u6563\uff0c\u9732\u51fa\u534a\u8fb9\u5f02\u5e38\u82cd\u767d\u7684\u8138\u988a\uff0c\u987f\u65f6\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u60ca\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u827e\u5f25\u8587\uff1f\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u5c11\u5973\u4f4e\u58f0\u56de\u7b54\uff0c\u201c\u8eab\u4e0a\u6709\u70b9\u51b7\u2026\u2026\u8fc7\u4f1a\u5c31\u597d\u4e86\u3002\u201d</p><p>\u4ed6\u4eec\u6b64\u65f6\u5728\u9ad8\u7a7a\u4e2d\uff0c\u51db\u98ce\u547c\u5578\uff0c\u89c9\u5f97\u51b7\u539f\u672c\u662f\u5f88\u6b63\u5e38\u7684\uff0c\u4f46\u6885\u83f2\u65af\u6240\u8868\u73b0\u51fa\u6765\u7684\u72b6\u51b5\u660e\u663e\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\uff0c\u66f4\u50cf\u662f\u5927\u75c5\u4e00\u573a\u4e4b\u540e\u7684\u865a\u8131\u3002\u5979\u65a9\u6740\u82cd\u767d\u9886\u4e3b\u7684\u65f6\u5019\uff0c\u743c\u6069\u6b63\u5168\u795e\u8d2f\u6ce8\u5730\u5f15\u53d1\u795e\u529b\u7206\u70b8\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u5177\u4f53\u7ecf\u8fc7\uff0c\u4f46\u731c\u60f3\u8d77\u6765\uff0c\u5e94\u8be5\u662f\u7528\u4e86\u4ec0\u4e48\u6781\u8017\u7cbe\u529b\u7684\u5927\u62db\uff0c\u6240\u4ee5\u4f1a\u53d8\u6210\u8fd9\u6837\u5427\u3002</p><p>\u67d4\u548c\u7684\u767d\u5149\u4ece\u9732\u4e1d\u96c5\u7684\u72ec\u89d2\u4e0a\u53d1\u51fa\uff0c\u7167\u5c04\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u5c11\u5973\u7684\u8138\u8272\u7a0d\u7a0d\u597d\u8f6c\uff0c\u6709\u4e86\u51e0\u5206\u8840\u8272\uff0c\u5979\u8f7b\u8f7b\u62cd\u4e86\u62cd\u72ec\u89d2\u517d\u7684\u8116\u9888\uff0c\u793a\u610f\u611f\u8c22\u3002\u201c\u9732\u4e1d\u96c5\u575a\u6301\u4e0d\u4e86\u591a\u4e45\u4e86\uff0c\u201d\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u5fc5\u987b\u901f\u6218\u901f\u51b3\u3002\u201d</p><p>\u81ea\u4ece\u4ea4\u6218\u4ee5\u6765\uff0c\u4e3a\u4e86\u8eb2\u907f\u9aa8\u9f99\u7684\u88ad\u51fb\uff0c\u9732\u4e1d\u96c5\u59cb\u7ec8\u662f\u4ee5\u6700\u9ad8\u901f\u98de\u884c\uff0c\u53ea\u6709\u6781\u77ed\u7684\u95f4\u9699\u4f11\u606f\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u4e00\u4e2a\u4eba\u5728\u4e0d\u65ad\u5730\u8fdb\u884c\u7740\u767e\u7c73\u51b2\u523a\u2014\u2014\u800c\u4e14\u80cc\u4e0a\u8fd8\u627f\u8f7d\u7740\u4e24\u4e2a\u4eba\u7684\u5206\u91cf\u3002\u4e3a\u4e86\u62b5\u5fa1\u9aa8\u9f99\u5410\u51fa\u7684\u9ed1\u6c14\uff0c\u5979\u5fc5\u987b\u4e00\u76f4\u7ef4\u6301\u7740\u5723\u6d01\u5149\u73af\uff0c\u521a\u624d\u53c8\u5bf9\u6885\u83f2\u65af\u91ca\u653e\u4e86\u52a0\u901f\u6062\u590d\u7cbe\u529b\u7684\u795e\u672f\uff0c\u8fd9\u4e9b\u540c\u6837\u90fd\u4e25\u91cd\u6d88\u8017\u4e86\u4f53\u529b\u3002\u743c\u6069\u548c\u5979\u6253\u4ea4\u9053\u5c11\uff0c\u8fd8\u6ca1\u53d1\u89c9\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u6e05\u695a\u5730\u610f\u8bc6\u5230\u4e86\u8fd9\u70b9\u3002</p><p>\u4f46\u662f\u8981\u901f\u6218\u901f\u51b3\u5e76\u4e0d\u5bb9\u6613\u3002</p><p>\u9aa8\u9f99\u6700\u96be\u5bf9\u4ed8\u7684\u5730\u65b9\uff0c\u4e0d\u5728\u4e8e\u522b\u7684\uff0c\u5728\u4e8e\u5b83\u591f\u5927\u3002\u743c\u6069\u7684\u89e3\u79bb\u672f\u80fd\u591f\u5c06\u4e00\u5207\u547d\u4e2d\u4e4b\u7269\u5316\u4f5c\u7c89\u672b\uff0c\u4f46\u9762\u5bf9\u8fd9\u79cd\u5e9e\u7136\u5de8\u7269\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u5f53\u7136\uff0c\u5982\u679c\u771f\u8981\u86ee\u5e72\u7684\u8bdd\uff0c\u8fde\u8f70\u5341\u53d1\u89e3\u79bb\u672f\uff0c\u9aa8\u9f99\u80af\u5b9a\u4e5f\u5c31\u6210\u6e23\u4e86\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u8fde\u8f70\u5341\u53d1\u89e3\u79bb\u672f\u8fd9\u79cd\u4f20\u8bf4\u4e2d\u7684\u4e8b\u60c5\uff0c\u522b\u8bf4\u743c\u6069\uff0c\u5c31\u7b97\u662f\u4ed6\u8001\u5e08\u5965\u6c83\u4e5f\u672a\u5fc5\u80fd\u529e\u5230\u5427\u2014\u2014\u5982\u679c\u4e0d\u501f\u52a9\u4ed6\u90a3\u4e00\u5806\u5b9d\u7269\u5f3a\u5316\u7684\u8bdd\u3002</p><p>\u5514\uff1f</p><p>\u60f3\u5230\u5965\u6c83\u548c\u4ed6\u7684\u85cf\u5b9d\uff0c\u743c\u6069\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u5fd8\u4e86\u67d0\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\u60c5\u3002\u4ed6\u6014\u4e86\u534a\u79d2\u949f\uff0c\u7136\u540e\u60f3\u4e86\u8d77\u6765\uff0c\u8fde\u5fd9\u4f38\u624b\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u6837\u4e1c\u897f\uff0c\u66ff\u6885\u83f2\u65af\u6234\u5728\u9888\u4e0a\u3002\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u5c11\u5973\u7d27\u76ef\u7740\u9ad8\u901f\u51b2\u6765\u7684\u9aa8\u9f99\uff0c\u65e0\u6687\u53bb\u770b\uff0c\u53ea\u662f\u53e3\u4e2d\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u4e00\u679a\u62a4\u8eab\u7b26\u3002\u201d\u743c\u6069\u8bf4\u7740\uff0c\u624b\u811a\u9ebb\u5229\u5730\u6263\u4e0a\u91d1\u94fe\u7684\u6302\u94a9\u3002</p><p>\u5728\u4ed6\u6307\u5c16\u79bb\u5f00\u91d1\u94fe\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u6885\u83f2\u65af\u7684\u8eab\u4f53\u5fae\u5fae\u4e00\u9707\uff0c\u50cf\u662f\u611f\u5e94\u5230\u4e86\u4ec0\u4e48\u4f3c\u7684\uff0c\u4e0d\u6562\u7f6e\u4fe1\u5730\u4f4e\u5934\u671b\u53bb\u3002\u201c\u8f89\u9633\u62a4\u7b26\uff1f\u201d\u5979\u8f7b\u58f0\u8bf4\u3002</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u62a4\u7b26\u4e0a\u9661\u7136\u7206\u53d1\u51fa\u5343\u4e07\u9053\u523a\u773c\u70ab\u76ee\u7684\u91d1\u5149\uff0c\u65e0\u5f62\u7684\u67d4\u548c\u529b\u91cf\u5c06\u5979\u6258\u8d77\uff0c\u5189\u5189\u5347\u4e0a\u9ad8\u7a7a\u3002\u66f4\u52a0\u5f3a\u70c8\u7684\u91d1\u5149\u4ece\u5c11\u5973\u80f8\u53e3\u7684\u62a4\u7b26\u4e2d\u8ff8\u5c04\u51fa\u6765\uff0c\u660e\u4eae\u5f97\u65e0\u6cd5\u7528\u8bed\u8a00\u6765\u5f62\u5bb9\uff0c\u5979\u6574\u4e2a\u4eba\u4eff\u4f5b\u53d8\u6210\u4e86\u4e00\u9897\u65b0\u7684\u592a\u9633\u3002\u5149\u660e\u5411\u56db\u9762\u516b\u65b9\u6269\u6563\u800c\u53bb\uff0c\u8986\u76d6\u5927\u5730\uff0c\u7167\u8000\u5929\u7a7a\uff0c\u6b63\u5728\u4e0b\u65b9\u6fc0\u6218\u7684\u4eba\u7c7b\u6b22\u547c\u632f\u594b\uff0c\u4ea1\u7075\u4eec\u5219\u5927\u7247\u5927\u7247\u5730\u7070\u98de\u70df\u706d\uff0c\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u4e00\u5c42\u9eef\u6de1\u5149\u819c\u4ece\u4f53\u5185\u6d8c\u51fa\uff0c\u7b3c\u7f69\u5168\u8eab\uff0c\u963b\u9694\u7740\u91d1\u5149\u7684\u7167\u5c04\uff0c\u800c\u90a3\u4e24\u5934\u9aa8\u9f99\u4fbf\u6ca1\u8fd9\u4e48\u5e78\u8fd0\uff0c\u5b83\u4eec\u5636\u543c\u7740\u3001\u60e8\u568e\u7740\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u5728\u7a7a\u4e2d\u731b\u70c8\u7ffb\u6eda\uff0c\u62fc\u547d\u6323\u624e\uff0c\u4e0d\u65ad\u6709\u9aa8\u9abc\u5316\u4f5c\u9752\u70df\u6563\u5f00\uff0c\u8f6c\u773c\u4e4b\u95f4\u5e9e\u5927\u7684\u8eab\u8eaf\u7f29\u6c34\u4e86\u5c06\u8fd1\u4e00\u534a\u3002</p><p>\u707f\u70c2\u5149\u660e\u4e4b\u4e2d\uff0c\u5c11\u5973\u7684\u5029\u5f71\u5a01\u51db\u4eff\u4f5b\u5929\u795e\uff0c\u5979\u63d0\u8d77\u94f6\u5251\uff0c\u53cc\u624b\u63e1\u67c4\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u63d0\u5c14\u7684\u72ec\u81c2\u76f2\u773c\u865a\u50cf\u5728\u80cc\u540e\u51fa\u73b0\uff0c\u7d27\u63a5\u7740\u5374\u53c8\u53d8\u6210\u4e00\u4f4d\u6ee1\u5934\u94f6\u53d1\u7684\u8001\u8005\uff0c\u84c4\u7740\u77ed\u77ed\u7684\u94f6\u767d\u9aed\u987b\uff0c\u624b\u4e2d\u63e1\u7740\u7070\u70df\u8272\u6743\u6756\uff0c\u6000\u62b1\u4e00\u672c\u5de8\u5927\u7684\u4e66\uff0c\u5c01\u9762\u50cf\u662f\u767d\u91d1\u6240\u94f8\uff0c\u4e00\u8f6e\u65ed\u65e5\u5728\u4ed6\u7684\u989d\u5934\u4e0a\u51fa\u73b0\uff0c\u95ea\u95ea\u53d1\u5149\u3002</p><p>\u201c\u51e1\u4e00\u5207\u90aa\u6076\uff0c\u7686\u5f52\u6d88\u706d\uff1b\u51e1\u4e00\u5207\u4ea1\u7075\uff0c\u7686\u5f52\u5c18\u571f\u3002\u201d</p><p>\u5c11\u5973\u6c89\u7a33\u5730\u5ba3\u793a\u7740\uff0c\u4e00\u5251\u6325\u4e0b\u3002</p><p>\u70bd\u70c8\u5230\u6781\u81f4\u7684\u5149\u4ece\u5251\u5203\u4e2d\u6d8c\u51fa\uff0c\u8d2f\u5f7b\u5929\u5730\uff0c\u5212\u7834\u957f\u7a7a\uff0c\u4e24\u5934\u9aa8\u9f99\u8fd8\u672a\u505a\u51fa\u534a\u70b9\u6323\u624e\uff0c\u4fbf\u88ab\u8fd9\u5149\u8292\u5f7b\u5e95\u541e\u566c\uff0c\u5e9e\u5927\u7684\u9aa8\u9abc\u8eab\u8eaf\u4ee5\u8089\u773c\u770b\u5f97\u5230\u7684\u901f\u5ea6\u5206\u89e3\u3001\u6d88\u878d\uff0c\u6700\u7ec8\u5316\u4e3a\u865a\u65e0\uff0c\u6d88\u5931\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4eff\u4f5b\u4ece\u6765\u4e0d\u66fe\u5b58\u5728\u8fc7\u4e00\u822c\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u963f\u66fc\u7eb3\u5854\u4e4b\u6c38\u6052\u70bd\u9633\u2026\u2026\u4eca\u5929\u7684\u60ca\u559c\uff0c\u8fd8\u771f\u662f\u610f\u5916\u7684\u591a\u554a\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u8fdc\u8fdc\u5730\u6ce8\u89c6\u7740\u8fd9\u573a\u6218\u6597\uff0c\u5979\u5df2\u7ecf\u79bb\u5f00\u4e86\u8679\u5149\u6cd5\u7403\uff0c\u6b63\u60a0\u95f2\u7ad9\u7acb\u5728\u7a7a\u4e2d\uff0c\u8fde\u6d6e\u7a7a\u672f\u90fd\u4e0d\u9700\u8981\uff0c\u8fd9\u5c31\u662f\u8eab\u4e3a\u5e7d\u7075\u7684\u597d\u5904\u4e86\u3002\u300a\u547d\u8fd0\u957f\u591c\u300b\u6258\u5728\u5979\u7684\u624b\u4e2d\uff0c\u8367\u8367\u5fae\u5149\u4ece\u5c01\u9762\u4e2d\u900f\u51fa\uff0c\u7f16\u7ec7\u6210\u4e00\u4e2a\u7cbe\u5de7\u7684\u5e7b\u672f\uff0c\u8ba9\u5979\u5468\u56f4\u4e94\u5341\u5c3a\u5185\u90fd\u5b8c\u5168\u9690\u5f62\uff0c\u522b\u4eba\u671b\u8fc7\u6765\u53ea\u662f\u84dd\u5929\u767d\u4e91\uff0c\u7a7a\u65e0\u4e00\u4eba\u3002\u770b\u5230\u743c\u6069\u7528\u98d3\u98ce\u56f0\u4f4f\u9aa8\u9f99\uff0c\u5979\u5e76\u672a\u60ca\u8bb6\uff0c\u4eff\u4f5b\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\uff1b\u4f46\u5f53\u6885\u83f2\u65af\u5b8c\u6210\u795e\u5947\u7684\u77ac\u79fb\uff0c\u6210\u529f\u65a9\u6740\u82cd\u767d\u9886\u4e3b\u65f6\uff0c\u5979\u7684\u7709\u5934\u5fae\u5fae\u76b1\u4e86\u8d77\u6765\uff1b\u800c\u5f53\u8f89\u9633\u62a4\u7b26\u7684\u5a01\u529b\u663e\u73b0\uff0c\u5c06\u5c11\u5973\u53d8\u6210\u4e00\u8f6e\u707f\u70c2\u70c8\u65e5\u65f6\uff0c\u5927\u5965\u672f\u5e08\u6df1\u6df1\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u6bd4\u6211\u60f3\u8c61\u5f97\u8fd8\u8981\u5f3a\u4e48\uff0c\u679c\u7136\u5148\u884c\u8bd5\u63a2\u7684\u6218\u7565\u662f\u6b63\u786e\u7684\u3002\u6709\u8fd9\u6837\u5f3a\u5927\u7684\u60c5\u654c\uff0c\u5973\u513f\u4f60\u8fd8\u771f\u662f\u4e0d\u5e78\u5462\uff0c\u201d\u5965\u5609\u83b1\u65af\u4f4e\u58f0\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u4e0d\u8fc7\u5e78\u8fd0\u7684\u662f\uff0c\u4f60\u6709\u4e00\u4e2a\u597d\u6bcd\u4eb2\u3002\u201d</p><p>\u5979\u5c55\u5f00\u4e66\u9875\uff0c\u53e3\u4e2d\u4f4e\u58f0\u5ff5\u8bf5\u5197\u957f\u7684\u5492\u6587\uff0c\u65e0\u6570\u7d2b\u5149\u95ea\u95ea\u7684\u8c61\u5f62\u6587\u5b57\u6d6e\u73b0\u51fa\u6765\uff0c\u98de\u821e\u76d8\u65cb\u7740\uff0c\u6700\u7ec8\u73af\u7ed5\u5728\u5979\u8eab\u4f53\u5468\u56f4\uff0c\u4eff\u4f5b\u4e00\u88ad\u534e\u8d35\u62ab\u98ce\u3002\u4f34\u968f\u7740\u9f99\u541f\u6e05\u5578\uff0c\u4e03\u9053\u91d1\u5149\u81ea\u4e66\u4e2d\u5c04\u51fa\uff0c\u6c47\u805a\u51dd\u7ed3\u6210\u4e00\u67c4\u5f0f\u6837\u53e4\u6734\u7684\u9ec4\u91d1\u5de8\u5251\uff0c\u5e73\u7a33\u5730\u60ac\u6d6e\u5728\u7a7a\u4e2d\u3002\u5bbd\u9614\u7684\u5251\u8eab\u5149\u6ed1\u5982\u955c\uff0c\u6ca1\u6709\u4efb\u4f55\u82b1\u7eb9\u88c5\u9970\uff0c\u4f46\u4efb\u4f55\u4eba\u7684\u76ee\u5149\u4e00\u65e6\u6ce8\u89c6\u5b83\uff0c\u4fbf\u4f1a\u4eff\u4f5b\u7f6e\u8eab\u4e8e\u6d69\u6dfc\u661f\u7a7a\u4e4b\u4e2d\uff0c\u4e07\u5343\u661f\u8fb0\u68ee\u7f57\u68cb\u5e03\uff0c\u4e00\u9053\u707f\u70c2\u94f6\u6cb3\u5728\u5934\u9876\u6a2a\u8d2f\u5929\u5b87\uff0c\u6062\u5f18\u65e0\u6bd4\u3002</p><p>\u5965\u5609\u83b1\u65af\u4f38\u624b\u63e1\u4f4f\u5251\u67c4\uff0c\u5403\u529b\u5730\u5c06\u5b83\u4e3e\u8d77\uff0c\u5251\u5c16\u9694\u7740\u9065\u8fdc\u7684\u8ddd\u79bb\u6307\u5411\u521a\u521a\u6467\u6bc1\u4e86\u9aa8\u9f99\uff0c\u4f9d\u65e7\u9759\u9759\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u91d1\u53d1\u5c11\u5973\u3002\u7d2b\u8272\u6587\u5b57\u63a5\u4e8c\u8fde\u4e09\u5730\u6ce8\u5165\u5251\u4e2d\uff0c\u539f\u672c\u6709\u4e9b\u9eef\u6de1\u7684\u5251\u5203\u53d8\u5f97\u660e\u4eae\u8d77\u6765\uff0c\u51b0\u51b7\u7684\u91d1\u8272\u5149\u6cfd\u95ea\u70c1\u8df3\u8dc3\u7740\uff0c\u6c47\u805a\u5728\u4e00\u8d77\uff0c\u8f6e\u5ed3\u5df2\u7ecf\u9690\u7ea6\u6210\u578b\uff0c\u4f3c\u4e4e\u662f\u4e00\u6761\u80cc\u751f\u53cc\u7ffc\u7684\u873f\u8712\u9f99\u86c7\u3002</p><p>\u5f53\u5b83\u5b8c\u5168\u663e\u73b0\u65f6\uff0c\u9707\u64bc\u5929\u5730\u7684\u4e00\u51fb\u4fbf\u4f1a\u53d1\u51fa\u3002</p><p>\u4ece\u73ca\u5609\u53e3\u4e2d\uff0c\u5965\u5609\u83b1\u65af\u5df2\u7ecf\u5f97\u77e5\u6885\u83f2\u65af\u7684\u5b58\u5728\uff0c\u4e5f\u77e5\u9053\u8fd9\u662f\u4e00\u4f4d\u5f3a\u5927\u7684\u5723\u6b66\u58eb\uff0c\u751a\u81f3\u77e5\u9053\u5979\u662f\u795e\u5b50\uff0c\u662f\u67d0\u4f4d\u540d\u53f7\u4e3a\u5df4\u5c14\u7684\u6740\u622e\u4e4b\u795e\u7684\u5973\u513f\uff08\u5df4\u5c14\u662f\u8010\u745f\u5e1d\u56fd\u706d\u4ea1\u4e4b\u540e\u624d\u7531\u51e1\u4eba\u5c01\u795e\uff0c\u5965\u5609\u83b1\u65af\u90a3\u65f6\u5019\u5df2\u7ecf\u6f5c\u5165\u6df1\u6d77\uff0c\u5e76\u4e0d\u4e86\u89e3\uff09\u3002\u5965\u5609\u83b1\u65af\u4e5f\u5e76\u672a\u5f53\u771f\u6307\u671b\u82cd\u767d\u9886\u4e3b\u548c\u4e24\u5934\u9aa8\u9f99\u5c31\u80fd\u591f\u5e72\u6389\u6885\u83f2\u65af\uff0c\u4ed6\u4eec\u7684\u4f5c\u7528\u53ea\u662f\u6295\u77f3\u95ee\u8def\uff0c\u5148\u884c\u8bd5\u63a2\u7f62\u4e86\u3002\u5c3d\u7ba1\u5df2\u7ecf\u6709\u4e86\u5fc3\u7406\u51c6\u5907\uff0c\u4f46\u6885\u83f2\u65af\u7684\u5f3a\u5927\uff0c\u8fd8\u662f\u8fdc\u8fdc\u8d85\u51fa\u4e86\u5979\u7684\u9884\u8ba1\u3002\u540c\u65f6\u5979\u4e5f\u6ca1\u60f3\u5230\uff0c\u743c\u6069\u8eab\u4e0a\u5c45\u7136\u8fd8\u643a\u5e26\u4e86\u8f89\u9633\u62a4\u7b26\u8fd9\u79cd\u52d8\u79f0\u662f\u4ea1\u7075\u514b\u661f\u7684\u795e\u5668\u3002\u743c\u6069\u89c1\u8bc6\u4e0d\u8db3\uff0c\u4e0d\u77e5\u9053\u8f89\u9633\u62a4\u7b26\u7684\u73cd\u8d35\uff0c\u5965\u6c83\u8bf4\u7684\u65f6\u5019\u4e5f\u662f\u8f7b\u63cf\u6de1\u5199\uff0c\u4eff\u4f5b\u4e0d\u5f53\u56de\u4e8b\uff0c\u4f46\u5965\u5609\u83b1\u65af\u5374\u5f88\u6e05\u695a\uff0c\u90a3\u662f\u5f53\u5e74\u8010\u745f\u5e1d\u56fd\u65f6\u671f\u963f\u66fc\u7eb3\u5854\u6559\u4f1a\u7684\u5723\u7269\uff0c\u7531\u201c\u795e\u5723\u4e0e\u6b63\u4e49\u6267\u6cd5\u8005\u534f\u4f1a\u201d\uff08\u963f\u66fc\u7eb3\u5854\u6559\u4f1a\u4e09\u5927\u5206\u652f\u4e4b\u4e00\uff09\u7684\u9886\u8896\u6240\u6301\u6709\uff0c\u5728\u6559\u4f1a\u5185\u90e8\u5730\u4f4d\u4ec5\u6b21\u4e8e\u70bd\u9633\u5723\u676f\u3001\u9884\u8a00\u4e4b\u6756\u548c\u88c1\u51b3\u4e4b\u4e66\u8fd9\u4e09\u5927\u5723\u5668\u3002\u4e0d\u77e5\u600e\u4e48\u4f1a\u843d\u5230\u743c\u6069\u624b\u91cc\uff0c\u5982\u4eca\u53c8\u7ed9\u4e86\u6885\u83f2\u65af\u3002</p><p>\u201c\u5341\u6709\u516b\u4e5d\uff0c\u662f\u4ed6\u90a3\u4e2a\u6b7b\u80d6\u5b50\u8001\u5e08\u7ed9\u4ed6\u7684\u5427\u3002\u201d</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u5965\u5609\u83b1\u65af\u5e76\u4e0d\u5341\u5206\u5728\u610f\u3002\u8f89\u9633\u62a4\u7b26\u662f\u5bf9\u4ed8\u4ea1\u7075\u7684\u5229\u5668\uff0c\u4f46\u5e76\u6ca1\u6709\u592a\u5f3a\u7684\u9632\u5fa1\u80fd\u529b\uff0c\u53ea\u8981\u5979\u8fd9\u4e00\u5251\u6325\u4e0b\uff0c\u90a3\u4f4d\u5723\u6b66\u58eb\u5c11\u5973\u4fbf\u4f1a\u5f7b\u5e95\u4ece\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6d88\u5931\u5427\u3002</p><p>\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u5927\u5965\u672f\u5e08\u6709\u7edd\u5bf9\u7684\u4fe1\u5fc3\u3002\u56e0\u4e3a\u5979\u624b\u4e2d\u7684\u9ec4\u91d1\u5de8\u5251\uff0c\u4e43\u662f\u6614\u65e5\u4f0a\u739b\u65af\u5361\u7b2c\u4e09\u79d8\u5668\u201c\u661f\u8fb0\u5251\u201d\u7684\u4eff\u5236\u54c1\uff0c\u4ec5\u4ee5\u653b\u51fb\u529b\u800c\u8bba\uff0c\u548c\u6b63\u54c1\u5176\u5b9e\u76f8\u5dee\u4e0d\u8fdc\uff0c\u6b64\u524d\u53ea\u52a8\u7528\u8fc7\u4e24\u6b21\uff0c\u7b2c\u4e00\u6b21\u65a9\u6740\u4e86\u6069\u745f\u4e3b\u795e\u6602\u5229\u5c14\uff0c\u7b2c\u4e8c\u6b21\u91cd\u521b\u4e86\u591c\u5973\u58eb\u838e\u5c14\u7684\u5316\u8eab\u3002\u533a\u533a\u4e00\u540d\u4eba\u7c7b\uff0c\u662f\u7edd\u65e0\u53ef\u80fd\u62b5\u6321\u5b83\u7684\u3002</p><p>\u7d2b\u8272\u6587\u5b57\u773c\u770b\u4fbf\u8981\u5c3d\u6570\u6ca1\u5165\u5251\u4e2d\uff0c\u78c5\u7934\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u529b\u91cf\u9686\u9686\u9707\u52a8\u7740\uff0c\u84c4\u52bf\u5f85\u53d1\u3002\u5965\u5609\u83b1\u65af\u6b63\u5f85\u53d1\u52a8\u653b\u51fb\uff0c\u9661\u7136\u6014\u4e86\u6014\uff0c\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u5730\u56de\u8fc7\u8eab\u6765\u3002</p><p>\u201c\u73ca\u5609\uff1f\u201d</p><p>\u4e03\u5f69\u6d41\u6ea2\u7684\u5149\u7403\u4e2d\uff0c\u4e00\u9053\u8d64\u7130\u8f70\u7136\u817e\u8d77\uff0c\u70c8\u70c8\u71c3\u70e7\u7740\uff0c\u77ac\u95f4\u7a7f\u900f\u4e86\u7406\u8bba\u4e0a\u80fd\u963b\u9694\u4e00\u5207\u9b54\u6cd5\u7684\u8679\u5149\u58c1\uff0c\u51b2\u4e0a\u534a\u7a7a\uff0c\u5316\u4f5c\u4e00\u5934\u7f8e\u4e3d\u5230\u4ee4\u4eba\u7729\u76ee\u7684\u706b\u51e4\u51f0\u3002\u5965\u5609\u83b1\u65af\u9762\u8272\u5927\u53d8\uff0c\u5306\u5fd9\u5c06\u9ec4\u91d1\u5de8\u5251\u6536\u5165\u4e66\u4e2d\uff0c\u7136\u540e\u6253\u5f00\u3002\u5979\u521a\u521a\u7ffb\u5230\u5176\u4e2d\u4e00\u9875\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u6e05\u9e23\uff0c\u4e0a\u8fbe\u4e5d\u9704\uff0c\u51e4\u51f0\u5c55\u5f00\u53cc\u7ffc\uff0c\u5468\u8eab\u5149\u7130\u4e07\u4e08\uff0c\u7167\u7740\u5965\u5609\u83b1\u65af\u8fc5\u731b\u5c04\u6765\u3002</p></article>"}}}]);
