(window.webpackJsonp=window.webpackJsonp||[]).push([[1694],{1717:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u6885\u83f2\u65af\u7684\u5bb6",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u6885\u83f2\u65af\u7684\u5bb6</h2><p>\u7279\u52a0\u5c14\u9547\u4e0d\u5927\uff0c\u5c45\u6c11\u4e5f\u5f88\u5c11\uff0c\u73b0\u5728\u53c8\u5df2\u7ecf\u662f\u6df1\u591c\uff0c\u8857\u9053\u4e0a\u66f4\u662f\u7a7a\u7a7a\u8361\u8361\u7684\uff0c\u534a\u4e2a\u4eba\u5f71\u90fd\u6ca1\u6709\u3002\u9a6c\u8f66\u653e\u7f13\u901f\u5ea6\uff0c\u6162\u6162\u8fdb\u4e86\u9547\u5b50\uff0c\u6309\u7167\u6885\u83f2\u65af\u7684\u6307\u70b9\uff0c\u6cbf\u7740\u4e2d\u5fc3\u5927\u9053\u4e00\u8def\u671d\u5317\u9a70\u53bb\u3002</p><p>\u201c\u55d2\u55d2\uff01\u55d2\u55d2\uff01\u201d</p><p>\u9a6c\u8e44\u78b0\u649e\u5730\u9762\u7684\u6e05\u8106\u54cd\u58f0\u4ece\u540e\u9762\u4f20\u6765\uff0c\u5728\u5bc2\u9759\u7684\u6df1\u591c\u4e2d\u683c\u5916\u6e05\u695a\uff0c\u6b63\u5728\u548c\u73ca\u5609\u8bf4\u8bdd\u7684\u6885\u83f2\u65af\u6014\u4e86\u6014\uff0c\u6253\u5f00\u8f66\u53a2\u7684\u95e8\u671d\u5916\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u91d1\u53d1\u7537\u5b50\u7b56\u9a6c\u5954\u6765\uff0c\u6b63\u662f\u4e0d\u4e45\u524d\u521a\u521a\u89c1\u8fc7\u7684\u5723\u6b66\u58eb\u5e0c\u6b27\u3002\u201c\u54c8\uff0c\u827e\u5f25\u8587\uff0c\u603b\u7b97\u627e\u5230\u4f60\u4e86\uff0c\u201d\u5e0c\u6b27\u8ffd\u4e0a\u9a6c\u8f66\uff0c\u7b11\u7740\u671d\u5979\u626c\u4e86\u626c\u624b\uff0c\u201c\u770b\u5230\u8fd9\u4e48\u6709\u578b\u7684\u9a6c\u8f66\uff0c\u6211\u5c31\u60f3\u80af\u5b9a\u662f\u4f60\u4eec\uff0c\u679c\u7136\u88ab\u6211\u731c\u4e2d\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u6885\u83f2\u65af\u76f4\u622a\u4e86\u5f53\u5730\u95ee\u3002</p><p>\u201c\u8001\u5934\u5b50\u627e\u4f60\uff0c\u201d\u5e0c\u6b27\u8bf4\uff0c\u201c\u4ec0\u4e48\u4e8b\u60c5\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d</p><p>\u201c\u73b0\u5728\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u73b0\u5728\u3002\u201d</p><p>\u6885\u83f2\u65af\u7565\u7565\u6c89\u541f\uff0c\u793a\u610f\u9a6c\u8f66\u505c\u4e0b\uff0c\u201c\u6211\u8981\u53bb\u4e00\u8d9f\u795e\u6bbf\uff0c\u201d\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u5927\u4e3b\u6559\u627e\u6211\uff0c\u5e94\u8be5\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u548c\u73ca\u5609\u59d0\u59d0\u5148\u53bb\u6211\u5bb6\u5427\u3002\u201d</p><p>\u201c\u53ef\u6211\u4e0d\u8ba4\u8bc6\u8def\u554a\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u6709\u73b0\u6210\u7684\u5411\u5bfc\u4e48\u2014\u2014\u62dc\u6258\u4f60\u4e86\uff0c\u5e0c\u6b27\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u201d\u91d1\u53d1\u7537\u5b50\u6297\u8bae\uff0c\u201c\u4e0d\u8981\u968f\u4fbf\u6293\u5dee\u554a\uff0c\u6211\u8fd8\u6709\u8981\u4e8b\u5728\u8eab\u5462\u3002\u4eca\u5929\u662f\u59ae\u53ef\u7684\u5341\u4e03\u5c81\u751f\u65e5\uff0c\u6211\u7b54\u5e94\u8981\u966a\u5979\u4e00\u8d77\u5ea6\u8fc7\u7684\u3002\u201d</p><p>\u201c\u73b0\u5728\u624d\u521a\u521a\u5341\u4e00\u70b9\uff0c\u8ddd\u79bb\u660e\u5929\u8fd8\u6709\u5c06\u8fd1\u4e00\u4e2a\u5c0f\u65f6\uff0c\u6765\u5f97\u53ca\u7684\u2014\u2014\u7b49\u4e00\u4e0b\uff0c\u59ae\u53ef\uff1f\u6211\u8bb0\u5f97\u4f60\u4eec\u4e0d\u662f\u65e9\u5c31\u5206\u624b\u4e86\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u5206\u624b\u8fd8\u662f\u53ef\u4ee5\u590d\u5408\u561b\u3002\u201d</p><p>\u201c\u90a3\u8309\u8389\u5a05\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u6211\u5728\u52aa\u529b\u6559\u5979\u6570\u5b66\uff0c\u8ba9\u5979\u660e\u767d\u2018\u4e09\u89d2\u5f62\u624d\u662f\u6700\u7a33\u5b9a\u7684\u2019\u8fd9\u4e2a\u771f\u7406\u2026\u2026\u201d</p><p>\u201c\u6210\u6548\u5982\u4f55\uff1f\u201d\u6885\u83f2\u65af\u4f3c\u7b11\u975e\u7b11\u3002</p><p>\u5e0c\u6b27\u53f9\u6c14\uff0c\u4e0d\u8bed\uff0c\u4f38\u624b\u6307\u4e86\u6307\u773c\u89d2\u4e00\u5757\u5d2d\u65b0\u7684\u9752\u80bf\u3002</p><p>\u6885\u83f2\u65af\u5927\u7b11\u8d77\u6765\uff0c\u53ec\u5524\u51fa\u9732\u4e1d\u96c5\uff0c\u4f11\u606f\u4e86\u8fd9\u4e48\u4e45\uff0c\u5b83\u770b\u8d77\u6765\u7cbe\u795e\u597d\u4e86\u4e0d\u5c11\uff0c\u201c\u6211\u5148\u8d70\u4e86\u3002\u201d\u5979\u8bf4\uff0c\u9a91\u4e0a\u72ec\u89d2\u517d\u671d\u897f\u8fb9\u8dd1\u53bb\uff0c\u94f6\u8272\u7684\u8eab\u5f71\u5f88\u5feb\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u3002\u5e0c\u6b27\u65e0\u5948\u5730\u8038\u4e86\u8038\u80a9\uff0c\u7b11\u7740\u4e0e\u743c\u6069\u7b49\u4eba\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u201c\u8d70\u5427\uff0c\u4e09\u4f4d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u968f\u6211\u6765\u3002\u201d</p><p>\u5e0c\u6b27\u7b56\u9a6c\u5728\u524d\u5f15\u8def\uff0c\u5f15\u7740\u743c\u6069\u7b49\u4eba\u7a7f\u8fc7\u9547\u5b50\uff0c\u51fa\u4e86\u5317\u95e8\u4fbf\u89c1\u4e00\u6761\u6cb3\uff0c\u6cb3\u9762\u9887\u4e3a\u5bbd\u9614\uff0c\u4e0a\u9762\u67b6\u7740\u4e00\u5ea7\u957f\u957f\u6728\u6865\uff0c\u4eff\u4f5b\u98de\u8679\u3002\u8fc7\u4e86\u8fd9\u5ea7\u6865\uff0c\u53c8\u8d70\u4e86\u51e0\u5206\u949f\uff0c\u4fbf\u89c1\u4e00\u5ea7\u5b85\u9662\u72ec\u7acb\u5728\u8fd9\u8352\u90ca\u91ce\u5916\u4e4b\u4e2d\u3002\u4e24\u5c42\u5c0f\u697c\uff0c\u5360\u5730\u4e0d\u5927\uff0c\u6837\u5f0f\u53e4\u6734\u800c\u7cbe\u81f4\uff0c\u51e0\u682a\u9752\u8272\u8513\u85e4\u60a0\u60a0\u95f2\u95f2\u5730\u6500\u722c\u5728\u56f4\u5899\u4e0a\uff0c\u5728\u6de1\u6de1\u6708\u5149\u4e0b\u770b\u8d77\u6765\u9887\u6709\u51e0\u5206\u8d85\u9038\u51fa\u5c18\u7684\u7f8e\u611f\u3002\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u743c\u6069\u603b\u89c9\u5f97\u8fd9\u5ea7\u5efa\u7b51\u6709\u4e9b\u602a\u602a\u7684\uff0c\u4f46\u5230\u5e95\u662f\u4ec0\u4e48\u5730\u65b9\u5947\u602a\uff0c\u4ed6\u4e00\u65f6\u53c8\u8bf4\u4e0d\u4e0a\u6765\u3002\u5018\u82e5\u662f\u767d\u5929\uff0c\u4ed6\u53ef\u80fd\u8fd8\u4f1a\u6709\u5174\u81f4\u4ed4\u7ec6\u7814\u7a76\uff0c\u4f46\u73b0\u5728\u5df2\u7ecf\u662f\u6df1\u591c\uff0c\u8fd9\u51e0\u5929\u63a5\u8fde\u8d76\u8def\uff0c\u743c\u6069\u548c\u838e\u73de\u514b\u5012\u7f62\u4e86\uff0c\u73ca\u5609\u5374\u5df2\u7ecf\u660e\u663e\u6709\u4e9b\u652f\u6301\u4e0d\u4f4f\uff0c\u8fd8\u662f\u8d76\u7d27\u4f11\u606f\u5427\uff0c\u4e00\u5207\u4e8b\u60c5\u90fd\u7b49\u660e\u5929\u518d\u8bf4\u3002</p><p>\u4e00\u884c\u4eba\u5728\u95e8\u524d\u4e0b\u9a6c\uff0c\u73ca\u5609\u6309\u7167\u5965\u5609\u83b1\u65af\u6559\u7684\u65b9\u6cd5\uff0c\u5c06\u6574\u5ea7\u9a6c\u8f66\u6536\u5165\u4e66\u4e2d\u3002\u5e0c\u6b27\u7a7f\u8fc7\u9662\u5b50\uff0c\u8d70\u5230\u95e8\u524d\u62ac\u8d77\u624b\uff0c\u50cf\u662f\u8981\u62cd\u95e8\uff0c\u5374\u53c8\u987f\u4f4f\u4e86\uff0c\u56de\u8fc7\u5934\u95ee\u743c\u6069\uff0c\u201c\u4eca\u5929\u662f\u661f\u671f\u51e0\uff1f\u201d</p><p>\u201c\u661f\u671f\u4e5d\u3002\u201d</p><p>\u201c\u661f\u671f\u4e5d\uff0c\u5443\uff0c\u661f\u671f\u4e5d\u662f\u4ec0\u4e48\u6765\u7740\uff0c\u201d\u5e0c\u6b27\u6293\u6293\u5934\u53d1\uff0c\u201c\u597d\u4e45\u6ca1\u6765\uff0c\u8bb0\u4e0d\u6e05\u53e3\u4ee4\u4e86\uff0c\u201d\u4ed6\u4f38\u624b\u5728\u95e8\u4e0a\u4e00\u62cd\uff0c\u201c\u90c1\u91d1\u9999\uff01\u201d</p><p>\u6ca1\u6709\u53cd\u5e94\u3002</p><p>\u201c\u73ab\u7470\uff01\u201d</p><p>\u4f9d\u7136\u6ca1\u6709\u53cd\u5e94\u3002</p><p>\u201c\u5947\u602a\uff0c\u827e\u5f25\u8587\u4e0d\u4f1a\u6539\u53e3\u4ee4\u4e86\u5427\u2026\u2026\u5bf9\u4e86\uff0c\u98ce\u4fe1\u5b50\uff01\u201d</p><p>\u95e8\u6084\u65e0\u58f0\u606f\u5730\u5f00\u4e86\uff0c\u540c\u65f6\u706f\u5149\u81ea\u52a8\u4eae\u8d77\uff0c\u5e0c\u6b27\u8eac\u8eab\u505a\u4e86\u4e2a\u8bf7\u7684\u59ff\u52bf\uff0c\u9886\u7740\u5ba2\u4eba\u4eec\u8fdb\u5165\u6b63\u5385\uff0c\u7136\u540e\u4e00\u4e00\u6307\u70b9\u5ba2\u623f\u3001\u6d74\u5ba4\u3001\u536b\u751f\u95f4\u7b49\u6240\u5728\u3002\u201c\u6ca1\u4ec0\u4e48\u522b\u7684\u4e8b\uff0c\u6211\u5c31\u5148\u544a\u8f9e\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u8d70\u51fa\u4e24\u6b65\u53c8\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u827e\u5f25\u8587\u7684\u5367\u5ba4\u662f\u5728\u4e8c\u697c\uff0c\u9760\u4e1c\u8fb9\u6700\u91cc\u9762\u7684\u90a3\u4e2a\u623f\u95f4\uff0c\u4e0d\u8981\u8d70\u9519\u4e86\u3002\u201d</p><p>\u201c\u591a\u8c22\u3002\u201d</p><p>\u743c\u6069\u6de1\u6de1\u5e94\u4e86\u4e00\u58f0\uff0c\u9001\u5e0c\u6b27\u51fa\u95e8\uff0c\u4ed6\u5fc3\u4e2d\u5fae\u5fae\u6709\u4e9b\u4e0d\u5feb\u3002\u6b64\u5730\u662f\u6885\u83f2\u65af\u7684\u4f4f\u5904\uff0c\u800c\u8fd9\u4e2a\u5bb6\u4f19\u4e0d\u4f46\u77e5\u9053\u8fdb\u95e8\u7684\u53e3\u4ee4\uff0c\u8fd8\u5341\u5206\u719f\u6089\u5176\u5185\u90e8\u7ed3\u6784\uff0c\u751a\u81f3\u8fde\u6885\u83f2\u65af\u5367\u5ba4\u7684\u4f4d\u7f6e\u90fd\u6e05\u695a\u2014\u2014\u4e0d\u662f\u5173\u7cfb\u975e\u5e38\u4eb2\u8fd1\u7684\u4eba\uff0c\u4e0d\u5e38\u6765\u5e38\u5f80\uff0c\u4e0d\u4f1a\u77e5\u9053\u5f97\u8fd9\u4e48\u8be6\u7ec6\u5427\u3002</p><p>\u201c\u4ed6\u4eec\u662f\u6559\u4f1a\u540c\u50da\uff0c\u53c8\u90fd\u662f\u5723\u6b66\u58eb\uff0c\u5f7c\u6b64\u6765\u5f80\u591a\u4e9b\u5f88\u6b63\u5e38\uff0c\u201d\u73ca\u5609\u4e0d\u77e5\u4f55\u65f6\u8d70\u5230\u4ed6\u8eab\u65c1\uff0c\u8f7b\u58f0\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u5149\u660e\u5766\u8361\uff0c\u4e0d\u50cf\u5bfb\u5e38\u5973\u5b69\u5b50\u7ec6\u817b\uff0c\u5c0f\u5f1f\u4f60\u4e0d\u7528\u591a\u5fc3\u3002\u201d</p><p>\u201c\u6ca1\u6709\u591a\u5fc3\u2026\u2026\u5443\uff0c\u201d\u743c\u6069\u6014\u4e86\u6014\uff0c\u8138\u4e0a\u5fae\u7ea2\uff0c\u201c\u59d0\u59d0\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\u3002\u201d</p><p>\u73ca\u5609\u4e00\u7b11\uff0c\u201c\u5982\u679c\u4f60\u60f3\u4ec0\u4e48\u6211\u90fd\u731c\u4e0d\u5230\uff0c\u90a3\u4e5f\u4e0d\u591f\u683c\u505a\u4f60\u59d0\u59d0\u4e86\u5427\u3002\u201d</p><p>\u201c\u4e5f\u662f\u554a\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u90a3\u4e2a\uff0c\u59d0\u59d0\uff0c\u6211\u5012\u4e0d\u662f\u6000\u7591\u827e\u5f25\u8587\u4ec0\u4e48\u5566\uff0c\u77e5\u9053\u90a3\u6839\u672c\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d</p><p>\u201c\u53ea\u4e0d\u8fc7\uff0c\u77e5\u9053\u81ea\u5df1\u5973\u670b\u53cb\u548c\u522b\u7684\u7537\u4eba\u5173\u7cfb\u4eb2\u5bc6\uff0c\u7ec8\u7a76\u8fd8\u662f\u5fc3\u91cc\u4e0d\u592a\u8212\u670d\uff0c\u201d\u73ca\u5609\u66ff\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u201c\u662f\u5427\uff1f\u201d</p><p>\u743c\u6069\u4e0d\u597d\u610f\u601d\u5730\u7b11\u4e86\u7b11\uff0c\u201c\u59d0\u59d0\uff0c\u6211\u662f\u4e0d\u662f\u5f88\u5c0f\u5fc3\u773c\u554a\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5c0f\u5fc3\u773c\uff0c\u662f\u81ea\u79c1\uff0c\u201d\u73ca\u5609\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u827e\u5f25\u8587\u53ea\u662f\u548c\u522b\u7684\u7537\u4eba\u719f\u6089\u4e9b\uff0c\u4f60\u5c31\u5fc3\u91cc\u4e0d\u9ad8\u5174\u3002\u90a3\u4f60\u6574\u5929\u548c\u522b\u7684\u5973\u5b69\u5b50\u9b3c\u6df7\uff0c\u5c31\u4e0d\u8003\u8651\u827e\u5f25\u8587\u7684\u611f\u53d7\uff1f\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u548c\u6211\u9b3c\u6df7\u7684\u8fd9\u4e9b\u2018\u522b\u7684\u5973\u5b69\u5b50\u2019\uff0c\u5176\u4e2d\u5c31\u6709\u4f60\u554a\u3002\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u4e86\uff0c\u201d\u73ca\u5609\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u73b0\u5728\u662f\u5728\u8bf4\u9053\u7406\u95ee\u9898\uff0c\u53ea\u8bba\u5bf9\u9519\uff0c\u4e0d\u8bba\u7acb\u573a\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u5934\u75bc\uff0c\u5148\u56de\u53bb\u7761\u4e86\u3002\u201d</p><p>\u743c\u6069\u662f\u771f\u7684\u5934\u75bc\uff0c\u522b\u7684\u4ec0\u4e48\u4e8b\u60c5\u90fd\u597d\u89e3\u51b3\uff0c\u552f\u6709\u8fd9\u4e2a\u95ee\u9898\u96be\u529e\uff0c\u6216\u8005\u8bf4\u538b\u6839\u5c31\u662f\u65e0\u89e3\uff0c\u8ba9\u4ed6\u672c\u80fd\u5730\u56de\u907f\uff0c\u4e0d\u613f\u610f\u63d0\u53ca\u3002\u73ca\u5609\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u6e29\u67d4\u5730\u4ece\u8eab\u540e\u62b1\u7740\u4ed6\uff0c\u5c06\u8138\u8d34\u5728\u4ed6\u7684\u80cc\u4e0a\u3002</p><p>\u201c\u5c0f\u5f1f\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4eca\u5929\u7684\u4e8b\u60c5\uff0c\u5bf9\u4e0d\u8d77\uff0c\u201d\u5c11\u5973\u4f4e\u58f0\u8bf4\uff0c\u201c\u8001\u5e08\u7684\u505a\u6cd5\u592a\u8fc7\u5206\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6ca1\u4e8b\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u4ed6\u8bed\u6c14\u5f88\u5e73\u6de1\uff0c\u4f46\u73ca\u5609\u77e5\u9053\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u5e73\u9759\u3002\u4e16\u754c\u4e0a\u7684\u4eba\uff0c\u9664\u975e\u771f\u662f\u5929\u751f\u51b7\u8840\u7684\u602a\u80ce\uff0c\u5426\u5219\u90fd\u4f1a\u52a8\u6012\uff0c\u90fd\u4f1a\u751f\u6c14\uff0c\u743c\u6069\u4e5f\u4e0d\u4f8b\u5916\u3002\u4f46\u5927\u591a\u6570\u4eba\u751f\u6c14\u65f6\u662f\u75be\u8a00\u5389\u8272\uff0c\u9762\u7ea2\u8033\u8d64\uff0c\u6012\u610f\u4eff\u4f5b\u718a\u718a\u70c8\u706b\u52c3\u53d1\uff0c\u4ed6\u5374\u662f\u6070\u597d\u76f8\u53cd\uff0c\u5fc3\u4e2d\u8d8a\u662f\u751f\u6c14\uff0c\u8bed\u6c14\u53cd\u800c\u8d8a\u5e73\u6de1\uff0c\u6012\u610f\u5230\u6781\u70b9\uff0c\u6574\u4e2a\u4eba\u5c31\u53d8\u6210\u4e86\u4e00\u5757\u5bd2\u51b0\u3002\u73ca\u5609\u5f88\u6e05\u695a\u5730\u660e\u767d\u8fd9\u70b9\uff0c\u4e0d\u4ec5\u4ec5\u56e0\u4e3a\u5341\u51e0\u5e74\u7684\u76f8\u5904\uff0c\u66f4\u56e0\u4e3a\u5979\u81ea\u5df1\u4e5f\u662f\u8fd9\u79cd\u7c7b\u578b\u7684\u4eba\u2014\u2014\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u4ed6\u4eec\u8fd8\u771f\u662f\u5f53\u4e4b\u65e0\u6127\u7684\u59d0\u5f1f\u3002</p><p>\u81ea\u4ece\u770b\u89c1\u9aa8\u9f99\uff0c\u743c\u6069\u5c31\u77e5\u9053\u4e0a\u4e86\u5965\u5609\u83b1\u65af\u7684\u5f53\uff0c\u800c\u4e14\u4ed6\u80fd\u591f\u6e05\u695a\u731c\u5230\u5bf9\u65b9\u7684\u76ee\u7684\uff0c\u5e76\u4e0d\u662f\u9488\u5bf9\u4ed6\uff0c\u800c\u662f\u9488\u5bf9\u6885\u83f2\u65af\u3002\u5176\u5b9e\u5982\u679c\u5965\u5609\u83b1\u65af\u8981\u6740\u4ed6\uff0c\u743c\u6069\u53cd\u5012\u4e0d\u4f1a\u591a\u4e48\u607c\u6012\uff0c\u751a\u81f3\u770b\u5728\u73ca\u5609\u7684\u4efd\u4e0a\uff0c\u6216\u8bb8\u8fd8\u80fd\u5bb9\u5fcd\u8fc7\u8fd9\u4e00\u6b21\uff1b\u4f46\u5965\u5609\u83b1\u65af\u8981\u6740\u6885\u83f2\u65af\uff0c\u90a3\u743c\u6069\u5c31\u4e0d\u53ef\u80fd\u8fd8\u5f53\u4f5c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u53d1\u751f\u8fc7\u4e86\u3002</p><p>\u8fd9\u6b21\u8eb2\u8fc7\u4e86\uff0c\u90a3\u4e0b\u4e00\u6b21\u5462\uff1f\u8fd9\u6b21\u662f\u9aa8\u9f99\uff0c\u8fd8\u7b97\u5bb9\u6613\u641e\u5b9a\uff0c\u4e0b\u6b21\u6362\u4e24\u53ea\u9f99\u5deb\u5996\u6765\u5462\uff1f</p><p>\u743c\u6069\u4e0d\u662f\u775a\u7726\u5fc5\u62a5\u7684\u5c0f\u4eba\uff0c\u5374\u4e5f\u4e0d\u662f\u4ee5\u5fb7\u62a5\u6028\u7684\u541b\u5b50\u3002\u4ed6\u627f\u8ba4\u81ea\u5df1\u662f\u5bf9\u4e0d\u8d77\u73ca\u5609\uff0c\u6240\u4ee5\u5982\u679c\u5965\u5609\u83b1\u65af\u56e0\u6b64\u8981\u6765\u627e\u4ed6\u7b97\u5e10\uff0c\u4ed6\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u4e8b\u60c5\u505a\u4e86\uff0c\u5c31\u8981\u627f\u62c5\u8d23\u4efb\u3002\u4f46\u5965\u5609\u83b1\u65af\u5bf9\u6885\u83f2\u65af\u4e0b\u624b\uff0c\u90a3\u5c31\u5b8c\u5168\u8d85\u8d8a\u4ed6\u7684\u6700\u540e\u5e95\u7ebf\u3002</p><p>\u73ca\u5609\u7d27\u7d27\u62b1\u4f4f\u743c\u6069\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u8f6f\u8bed\u592e\u6c42\uff0c\u201c\u6211\u77e5\u9053\u4f60\u5f88\u751f\u6c14\uff0c\u6211\u4e5f\u5f88\u4e0d\u9ad8\u5174\u3002\u6211\u5df2\u7ecf\u6b63\u5f0f\u8b66\u544a\u8fc7\u5979\uff0c\u4ee5\u540e\u518d\u4e5f\u4e0d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u4e8b\u60c5\u3002\u6240\u4ee5\uff0c\u4f60\u5c31\u518d\u539f\u8c05\u8fd9\u4e00\u6b21\uff0c\u5c31\u8fd9\u4e00\u6b21\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u201c\u59d0\u59d0\u3002\u201d\u4ed6\u8f7b\u58f0\u53eb\u73ca\u5609\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u59d0\u59d0\u771f\u7684\u2026\u2026\u771f\u7684\u5f88\u559c\u6b22\u5979\u4e48\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u73ca\u5609\u8bda\u5b9e\u5730\u56de\u7b54\uff0c\u201c\u4f46\u662f\u4ece\u770b\u5230\u5979\u7b2c\u4e00\u773c\u8d77\uff0c\u6211\u5c31\u89c9\u5f97\u5f88\u4eb2\u5207\uff0c\u6709\u79cd\u5f88\u6e29\u6696\u7684\u611f\u89c9\u3002\u6211\u77e5\u9053\u5979\u813e\u6c14\u5f88\u574f\uff0c\u505a\u4e8b\u60c5\u5f88\u8fc7\u5206\uff0c\u4f46\u662f\u5979\u5bf9\u6211\u771f\u7684\u5f88\u597d\u5462\u3002\u5927\u6982\u5c31\u50cf\u5979\u81ea\u5df1\u8bf4\u7684\uff0c\u6211\u548c\u5979\u7684\u5973\u513f\u5f88\u50cf\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u5979\u8bf4\u4f60\u548c\u5979\u7684\u5973\u513f\u5f88\u50cf\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u8001\u5e08\u8bf4\uff0c\u5979\u66fe\u7ecf\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u76f8\u8c8c\u548c\u6027\u683c\u90fd\u548c\u6211\u633a\u50cf\u7684\u3002\u6240\u4ee5\u6211\u60f3\uff0c\u5979\u5176\u5b9e\u5728\u67d0\u79cd\u7a0b\u5ea6\u4e0a\uff0c\u662f\u628a\u6211\u5f53\u5979\u7684\u5973\u513f\u770b\u5f85\u5462\u3002\u201d</p><p>\u743c\u6069\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u73ca\u5609\u5fd0\u5fd1\u4e0d\u5b89\u5730\u7b49\u5f85\u7740\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u542c\u89c1\u4ed6\u957f\u957f\u51fa\u4e86\u53e3\u6c14\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u9762\u5bf9\u9762\u5730\u770b\u7740\u73ca\u5609\uff0c\u201c\u6700\u540e\u4e00\u6b21\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8f7b\u58f0\u4f46\u6e05\u695a\u5730\u8bf4\uff0c\u201c\u518d\u6ca1\u6709\u4e0b\u6b21\u4e86\u3002\u201d</p><p>\u201c\u6700\u540e\u4e00\u6b21\uff0c\u6211\u4fdd\u8bc1\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u5728\u73ca\u5609\u7684\u5507\u4e0a\u8f7b\u8f7b\u4e00\u543b\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u65e9\u70b9\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u73ca\u5609\u6e29\u67d4\u5730\u7b11\u7740\uff0c\u4fcf\u76ae\u5730\u7728\u4e86\u7728\u773c\u775b\uff0c\u201c\u53ef\u662f\u59d0\u59d0\u7d2f\u4e86\uff0c\u8d70\u4e0d\u52a8\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u4e5f\u7b11\u8d77\u6765\uff0c\u5f2f\u8170\u5c06\u5979\u6a2a\u62b1\u5728\u80f8\u524d\uff0c\u671d\u5367\u5ba4\u4e2d\u8d70\u53bb\u3002\u73ca\u5609\u62ac\u8d77\u53cc\u81c2\u6402\u7740\u4ed6\u7684\u8116\u9888\uff0c\u201c\u6709\u4e2a\u5f1f\u5f1f\u771f\u597d\u5462\uff0c\u201d\u5979\u683c\u683c\u7b11\u7740\uff0c\u201c\u4e0d\u60f3\u8d70\u8def\u7684\u65f6\u5019\uff0c\u90fd\u53ef\u4ee5\u6709\u4eba\u62b1\u7740\u3002\u201d</p><p>\u201c\u59d0\u59d0\u559c\u6b22\u8fd9\u6837\u88ab\u62b1\u7740\uff1f\u201d</p><p>\u201c\u559c\u6b22\uff0c\u611f\u89c9\u81ea\u5df1\u50cf\u4e2a\u5c0f\u516c\u4e3b\u3002\u201d</p><p>\u201c\u90a3\u6211\u4ee5\u540e\u8981\u591a\u8fd9\u6837\u62b1\u59d0\u59d0\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u7ecf\u8fdb\u4e86\u5367\u5ba4\u3002\u623f\u5185\u7684\u9648\u8bbe\u5f88\u7b80\u5355\uff0c\u4f46\u5904\u5904\u900f\u7740\u4e00\u79cd\u7cbe\u81f4\u611f\u3002\u743c\u6069\u5c0f\u5fc3\u5730\u5c06\u73ca\u5609\u653e\u5230\u5e8a\u4e0a\uff0c\u66ff\u5979\u8131\u4e0b\u5916\u5957\uff0c\u892a\u53bb\u88d9\u889c\uff0c\u53ea\u5269\u8d34\u8eab\u5185\u8863\uff0c\u7136\u540e\u76d6\u4e0a\u6bdb\u6bef\u3002</p><p>\u201c\u665a\u5b89\uff0c\u59d0\u59d0\u3002\u201d\u4ed6\u8bf4\uff0c\u5728\u73ca\u5609\u7684\u989d\u5934\u4e0a\u8f7b\u8f7b\u4e00\u543b\u3002</p><p>\u201c\u665a\u5b89\u3002\u201d\u5c11\u5973\u62a5\u4ee5\u5fae\u7b11\u3002</p><p>\u5979\u5fc3\u4e2d\u5fae\u5fae\u6709\u4e9b\u82e6\u6da9\uff0c\u81ea\u4ece\u743c\u6069\u53bb\u5e74\u5e74\u5e95\u56de\u5230\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u9664\u4e86\u6781\u5c11\u6570\u4f8b\u5916\uff0c\u51e0\u4e4e\u6bcf\u4e2a\u665a\u4e0a\u90fd\u5b88\u5728\u5979\u8eab\u8fb9\uff0c\u966a\u5979\u5165\u7761\u3002\u4f46\u4eca\u665a\u663e\u7136\u4e0d\u884c\u2014\u2014\u800c\u4e14\u4eca\u665a\u53ea\u662f\u5f00\u59cb\uff0c\u4ece\u4eca\u5f80\u540e\uff0c\u53ea\u6015\u90fd\u5f88\u96be\u4e86\u3002</p><p>\u56e0\u4e3a\u591a\u4e86\u4e00\u4e2a\u6885\u83f2\u65af\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u6ca1\u6709\u8868\u73b0\u5f97\u4e0d\u9ad8\u5174\uff0c\u4e5f\u4e0d\u4f1a\u8981\u6c42\u6216\u8005\u6697\u793a\u743c\u6069\u7559\u4e0b\u6765\u3002\u5979\u6e05\u695a\u5730\u77e5\u9053\uff0c\u81ea\u5df1\u5bf9\u743c\u6069\u4f9d\u7136\u6709\u7740\u5de8\u5927\u7684\u5f71\u54cd\u529b\uff0c\u65c1\u4eba\u65e0\u53ef\u6bd4\u62df\uff0c\u5373\u4fbf\u662f\u6885\u83f2\u65af\u4e5f\u6bd4\u4e0d\u4e0a\u3002\u5979\u4e5f\u6e05\u695a\u5730\u76f8\u4fe1\uff0c\u5982\u679c\u81ea\u5df1\u771f\u7684\u5f00\u53e3\uff0c\u743c\u6069\u6700\u7ec8\u8fd8\u662f\u4f1a\u987a\u4ece\u5979\u7684\u610f\u601d\u3002\u4f46\u5979\u4ece\u4e0d\u6253\u7b97\u8fd9\u4e48\u505a\uff0c\u56e0\u4e3a\u6ca1\u6709\u610f\u4e49\u3002</p><p>\u5979\u4ece\u6765\u4e0d\u559c\u6b22\u505a\u6ca1\u6709\u610f\u4e49\u7684\u4e8b\u60c5\u3002</p><p>\u773c\u770b\u743c\u6069\u5df2\u7ecf\u51c6\u5907\u63a8\u95e8\u51fa\u53bb\uff0c\u73ca\u5609\u53eb\u4f4f\u4e86\u4ed6\uff0c\u201c\u5bf9\u4e86\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u5dee\u70b9\u5fd8\u4e86\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u8001\u5e08\u8bf4\uff0c\u6211\u7684\u57fa\u7840\u7406\u8bba\u5df2\u7ecf\u57fa\u672c\u638c\u63e1\uff0c\u63a5\u4e0b\u6765\u8981\u8fdb\u5165\u5b9e\u6218\u8bad\u7ec3\u9636\u6bb5\uff0c\u4ee5\u540e\u6bcf\u5929\u81f3\u5c11\u8981\u82b1\u516b\u4e2a\u5c0f\u65f6\u4ee5\u4e0a\u6765\u5b66\u4e60\uff0c\u665a\u4e0a\u8fd8\u8981\u5b66\u4e60\u5360\u661f\u672f\uff0c\u6240\u4ee5\uff0c\u201d\u5979\u7b11\u4e86\u7b11\uff0c\u201c\u6050\u6015\u6709\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\uff0c\u59d0\u59d0\u90fd\u6ca1\u4ec0\u4e48\u7a7a\u966a\u4f60\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u55ef\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\u5c31\u56de\u53bb\u4f11\u606f\u5427\uff0c\u201d\u73ca\u5609\u7b11\u7740\u6446\u6446\u624b\uff0c\u201c\u4e8c\u697c\u9760\u4e1c\u8fb9\u6700\u91cc\u9762\u7684\u623f\u95f4\uff0c\u4e0d\u8981\u8d70\u9519\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u8d70\u9519\u623f\u95f4\u3002</p><p>\u51e0\u4e4e\u662f\u548c\u5ba2\u623f\u540c\u6837\u7684\u5e03\u5c40\uff0c\u7b80\u6d01\u800c\u7cbe\u81f4\uff0c\u5e72\u5e72\u51c0\u51c0\uff0c\u7ea4\u5c18\u4e0d\u67d3\u3002\u5e8a\u5f88\u8f6f\uff0c\u88ab\u8925\u548c\u6795\u5934\u4e0a\u900f\u7740\u4e00\u79cd\u719f\u6089\u7684\u5473\u9053\uff0c\u662f\u6885\u83f2\u65af\u7684\u4f53\u9999\uff0c\u6e05\u5e7d\u6de1\u96c5\uff0c\u4eff\u4f5b\u767e\u5408\uff0c\u5176\u4e2d\u53c8\u6709\u4e9b\u8bb8\u751c\u9999\uff0c\u743c\u6069\u60f3\u4e86\u51e0\u79d2\u949f\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u90a3\u662f\u51db\u7684\u5473\u9053\u2026\u2026\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u5979\u52a8\u60c5\u65f6\u82b1\u871c\u7684\u5473\u9053\u3002</p><p>\u201c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u2026\u2026\u592a\u8fc7\u5206\u4e86\uff0c\u8d81\u6211\u4e0d\u5728\u80af\u5b9a\u5929\u5929\u73a9\u767e\u5408\uff0c\u4e00\u5b9a\u8981\u597d\u597d\u60e9\u6212\u3002\u201d</p><p>\u8fd8\u6ca1\u7b49\u4ed6\u60f3\u51fa\u5982\u4f55\u60e9\u6212\uff0c\u7761\u610f\u4fbf\u5df2\u7ecf\u6084\u6084\u88ad\u6765\u3002\u767d\u5929\u90a3\u4e00\u6218\uff0c\u4ed6\u52a8\u7528\u96f7\u9706\u795e\u529b\u52c9\u5f3a\u56f0\u4f4f\u9aa8\u9f99\uff0c\u5176\u540e\u66f4\u5f3a\u884c\u5207\u65ad\u6cd5\u672f\u8054\u7ed3\uff0c\u867d\u7136\u6210\u529f\u5f15\u53d1\u7206\u70b8\u51fb\u4f24\u5bf9\u624b\uff0c\u81ea\u8eab\u5374\u4e5f\u906d\u53d7\u5f3a\u70c8\u53cd\u566c\uff0c\u52c9\u5f3a\u652f\u6491\u5230\u73b0\u5728\uff0c\u5b9e\u5728\u662f\u6709\u4e9b\u9876\u4e0d\u4f4f\u4e86\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4fbf\u6c89\u6c89\u7761\u53bb\u3002\u6726\u6726\u80e7\u80e7\u4e4b\u4e2d\uff0c\u9690\u7ea6\u89c9\u5f97\u50cf\u662f\u6709\u4eba\u8fdb\u4e86\u623f\u95f4\uff0c\u53c8\u4e0a\u4e86\u5e8a\uff0c\u94bb\u8fdb\u6000\u91cc\u3002\u4ed6\u56f0\u5026\u81f3\u6781\uff0c\u773c\u775b\u90fd\u61d2\u5f97\u7741\u5f00\uff0c\u53ea\u89c9\u6000\u4e2d\u6e29\u9999\u8f6f\u7389\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u4fbf\u62b1\u4f4f\uff0c\u7ee7\u7eed\u547c\u547c\u5927\u7761\u3002</p><p>\u8fd9\u4e00\u89c9\u7761\u5f97\u5f88\u6c89\uff0c\u5f53\u4ed6\u518d\u5ea6\u9192\u6765\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u4e0a\u5348\u4e5d\u70b9\u949f\u3002\u743c\u6069\u7741\u5f00\u773c\uff0c\u7136\u540e\u770b\u89c1\u4e86\u6885\u83f2\u65af\uff0c\u5c11\u5973\u6795\u7740\u4ed6\u7684\u80f3\u818a\uff0c\u80cc\u8d34\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u72b9\u81ea\u6c89\u7761\u672a\u9192\u3002\u6668\u5149\u4ece\u7a97\u5e18\u7684\u7f1d\u9699\u95f4\u900f\u8fdb\u6765\uff0c\u6d12\u5728\u5979\u7684\u6de1\u91d1\u79c0\u53d1\u4e0a\uff0c\u6298\u5c04\u51fa\u7f8e\u4e3d\u7684\u5149\u6cfd\u3002\u5927\u7ea6\u662f\u56e0\u4e3a\u611f\u89c9\u5230\u743c\u6069\u7684\u52a8\u9759\uff0c\u5979\u542b\u7cca\u5730\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u7a0d\u7a0d\u4fa7\u4e86\u4fa7\u8eab\uff0c\u8fd9\u4e2a\u52a8\u4f5c\u4ee4\u5979\u8eab\u4e0a\u7684\u6bef\u5b50\u7a0d\u7a0d\u4e0b\u6ed1\uff0c\u9732\u51fa\u96ea\u767d\u7c89\u5ae9\u7684\u8116\u9888\u548c\u53cc\u80a9\uff0c\u743c\u6069\u7a0d\u7a0d\u62ac\u8d77\u5934\uff0c\u773c\u5149\u843d\u5728\u5c11\u5973\u7684\u9501\u9aa8\u4e0a\uff0c\u6cbf\u7740\u7cbe\u5de7\u6d41\u7545\u7684\u66f2\u7ebf\u5f80\u4e0b\uff0c\u9690\u9690\u7ea6\u7ea6\u770b\u89c1\u80f8\u524d\u4e00\u5bf9\u8bf1\u4eba\u7389\u5cf0\u3002</p><p>\u4ed6\u770b\u5f97\u6026\u7136\u5fc3\u52a8\uff0c\u6b32\u5ff5\u65e2\u8d77\uff0c\u8eab\u4f53\u4fbf\u6709\u53cd\u5e94\uff0c\u4f55\u51b5\u53c8\u662f\u6e05\u6668\u65f6\u5206\uff0c\u56e0\u4e3a\u662f\u4ece\u80cc\u540e\u62b1\u7740\uff0c\u4e00\u6839\u706b\u70ed\u6eda\u70eb\u7684\u94c1\u68cd\u6602\u7136\u633a\u8d77\uff0c\u4fbf\u6b63\u62b5\u5728\u5c11\u5973\u81c0\u80a1\u95f4\u3002\u6885\u83f2\u65af\u539f\u672c\u7761\u5f97\u9999\u751c\uff0c\u611f\u89c9\u6709\u4e1c\u897f\u4ece\u540e\u9762\u9876\u7740\u81ea\u5df1\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4f38\u624b\u5c31\u53bb\u6478\uff0c\u7136\u540e\u4e60\u60ef\u6027\u5730\u4fbf\u63e1\u4f4f\u2014\u2014\u7136\u540e\u7ec8\u4e8e\u60ca\u9192\u8fc7\u6765\u3002</p><p>\u201c\u5514\u2026\u2026\u65e9\uff0c\u743c\u6069\u3002\u201d\u5979\u53e3\u9f7f\u4e0d\u6e05\u5730\u8bf4\u3002</p></article>"}}}]);
